[4463] = {title = "Libram of Rumination", objective = "Present the components, mortal.\n\nI will also require payment in the form of thirty gold pieces for this creation.", description = nil, progress = "Present the components, mortal.\n\nI will also require payment in the form of thirty gold pieces for this creation.", completion = "Take your trinket, fool. Leave me to my studies!", rewards = "You will receive:\n\nLesser Arcanum of Rumination"},
