[7815] = {title = "Snapjaws, Mon!", objective = "Katoom the Angler at Revantusk Village in the Hinterlands wants you to kill 15 Saltwater Snapjaw turtles. Return to him when you have completed this task.", description = "Don't let their big eyes fool you, mon! Those saltwater snapjaws be a menace. I can't fish for five minutes without getting a broken line from one of those oversized fish vacuums stealing my bait.\n\nDo me and the people of Revantusk Village a service and kill em, mon.\n\nKill the whole lot of em!", progress = "How goes the hunt?", completion = "Oh, mon! I can't thank you enough! Hopefully this here fishing pole will be reward enough for the job.", rewards = "You will receive:\n\nNat Pagle's Extreme Angler FC-5000"},
[7816] = {title = "Gammerita, Mon!", objective = "Katoom the Angler at Revantusk Village in the Hinterlands wants you to kill Gammerita and retrieve Katoom's Best Lure from her corpse.\n\nKatoom mentioned that Gammerita hangs out on the coast with the other turtles.", description = "Yo! There be a turtle out there not like the others. She be the Gammerita, leader of the saltwater snapjaws - a downright dirty thievin' reptile.\n\nMon, she stole my best lure. Snapped it right off my line! Since then, the fish just aren't biting the same.\n\nFind Gammerita and get my lure out of her stomach.", progress = "Did you get it yet?", completion = "MON! This is fantastic! With my best lure and the snapjaw population down, the fish will pile up.", rewards = "The following spell will be cast on you:\n\nBurst of Experience\n\nYou will receive:\n\n"},
