[7926] = {title = "The Darkmoon Faire", objective = "Deliver the Free Ticket Voucher to Gelvas Grimgate, located inside the Darkmoon Faire carnival.", description = "The Darkmoon Faire is coming!  Be sure to experience all the wonders and excitement of it when it comes to town!\n\nIf you act right now, you can redeem this voucher for free prize tickets!  Just hand your voucher to Gelvas Grimgate located inside the carnival.\n\nRight now, the Faire is just outside Thunder Bluff, in Mulgore.  Don't miss out!  Go today!", progress = "Yes friend, may I help you?  Do you have a voucher that needs redeeming?", completion = "Welcome to the Darkmoon Faire!  This voucher is good for FIVE, yes, FIVE prize tickets!\n\nWhen you accumulate prize tickets, you'll want to speak to me about redeeming them for valuable prizes.  The more tickets you get, the more prizes you can win.  Huzzah!", rewards = "You will receive:\n\nDarkmoon Faire Prize Ticket"},
[7927] = {title = "Darkmoon Portals Deck", objective = "Return the Portals Deck to the Darkmoon Faire when it comes to town.", description = "Now that you have gathered all of the Portal cards together into a deck, a ninth card mysteriously appears with the same backing as the first eight.   On this card is inscribed, \"Return these cards to the Darkmoon Faire and be rewarded.\"   You have heard that this faire alternately stops at Mulgore and Goldshire every few months, so you will have to wait until they are here next.", progress = "I see that you have managed to complete a deck of portals!   Congratulations!", completion = "So you have put together a deck of Portals.   Your gesture of returning it to us will create a tie between you and the Darkmoon that will not soon be forgotten.   Allow me to present you with one of the greater darkmoon cards as a small token of our appreciation.", rewards = "You will receive:\n\nDarkmoon Card: Twisting Nether"},
