[7930] = {title = "5 Tickets - Darkmoon Flower", objective = "Step right up, step right up!  If you have tickets from the Darkmoon Faire you'd like redeemed, then just say so!  You can redeem tickets in various denominations for wondrous and fantastic prizes.  Don't be shy, give it a try!", description = nil, progress = "Step right up, step right up!  If you have tickets from the Darkmoon Faire you'd like redeemed, then just say so!  You can redeem tickets in various denominations for wondrous and fantastic prizes.  Don't be shy, give it a try!", completion = "Here you go, here you go!  A prize fit for a king!  Congratulations to you, my friend!\n\nIf you have more tickets you'd like to turn in, then please let me know!  For as long as the Darkmoon Faire is here, I'll redeem your tickets.", rewards = "You will receive:\n\nDarkmoon Flower"},
