[8168] = {title = "The Battle for Arathi Basin!", objective = "Assault the mine, the lumber mill, the blacksmith and the farm, then return to Field Marshal Oslight in Refuge Pointe.", description = "<name>, the battle for Arathi Basin is a battle for resources and territory.  Key points in the area must be held and operated in order to supply our forces with much needed raw materials and supplies.  I charge you to assault the points farthest from our Arathi Basin base.\n\nAssault the farm, the mine, the lumber mill and the blacksmith when they are controlled by the enemy.  Find the enemy's banner and rip it from its flag post!\n\nWhen you have done this to all four points, return to me.", progress = nil, completion = "I received reports of your exploits in Arathi Basin, &lt;name&gt;.  Well done!  With those like you joining the struggles here, I have faith that we will one day retake our homelands.", rewards = "You will receive:\n\n"},
[8169] = {title = "The Battle for Arathi Basin!", objective = "Assault the Arathi Basin mine, lumber mill, blacksmith and stable, and then return to Deathmaster Dwire in Hammerfall.", description = "As you no doubt have heard, the League of Arathor is sending large numbers of troops into Arathi Basin.  Eager for food and supplies, these Alliance fools are intent to take and control the rich resources there.  We must show them that Arathi will never again be a home for humans!\n\nGo to Arathi Basin and assault the mine, the lumber mill, the blacksmith and the stables.  Pull down the enemies' banners, declaring those territories for the Horde.\n\nGo, <name>.  Report back to me when this task is complete.", progress = nil, completion = "A Defiler scout came to me with a report of your success, &lt;name&gt;.  Well done.\n\nAs you will learn, to win the battle for Arathi Basin, we must be ever vigilant and crush any attempts at Alliance expansion.  Do this, and our victory here is ensured.", rewards = "You will receive:\n\n"},
[8170] = {title = "The Battle for Arathi Basin!", objective = "Assault the Arathi Basin mine, lumber mill, blacksmith and stable, and then return to Deathmaster Dwire in Hammerfall.", description = "As you no doubt have heard, the League of Arathor is sending large numbers of troops into Arathi Basin.  Eager for food and supplies, these Alliance fools are intent to take and control the rich resources there.  We must show them that Arathi will never again be a home for humans!\n\nGo to Arathi Basin and assault the mine, the lumber mill, the blacksmith and the stables.  Pull down the enemies' banners, declaring those territories for the Horde.\n\nGo, <name>.  Report back to me when this task is complete.", progress = nil, completion = "A Defiler scout came to me with a report of your success, &lt;name&gt;.  Well done.\n\nAs you will learn, to win the battle for Arathi Basin, we must be ever vigilant and crush any attempts at Alliance expansion.  Do this, and our victory here is ensured.", rewards = "You will receive:\n\n"},
[8171] = {title = "The Battle for Arathi Basin!", objective = "Assault the Arathi Basin mine, lumber mill, blacksmith and stable, and then return to Deathmaster Dwire in Hammerfall.", description = "As you no doubt have heard, the League of Arathor is sending large numbers of troops into Arathi Basin.  Eager for food and supplies, these Alliance fools are intent to take and control the rich resources there.  We must show them that Arathi will never again be a home for humans!\n\nGo to Arathi Basin and assault the mine, the lumber mill, the blacksmith and the stables.  Pull down the enemies' banners, declaring those territories for the Horde.\n\nGo, <name>.  Report back to me when this task is complete.You will receive:\n\nUpon completion of this quest you will gain:\n\n", progress = nil, completion = nil, rewards = "You will receive:\n\n"},
[8181] = {title = "Confront Yeh'kinya", objective = "Speak with Yeh'kinya.", description = "My delvings into the Mosh'aru Tablets bear grim news, <name>.  Hakkar's true form, the god himself, can be born again through the very egg into which you captured him.  Yeh'kinya told you it was to contain his evil, but it is to recreate it!  The troll has led you astray!\n\nGo to Yeh'kinya and demand the egg from him.  Pray that it is not too late to stop Hakkar's return!", progress = nil, completion = "So, you have been speaking with that old dwarf Ironboot and uncovered the lost Mosh'aru Tablets, have you?  That meddling prospector...\n\nNo matter.  It is too late to save this world.  Hakkar has been reborn, and even now grows in power... deep in the ancient troll kingdom of Zul'Gurub!  He has come!  And he will rule in blood and terror!\n\nThank you, &lt;name&gt;.  Your help has sped the return of the Soulflayer!", rewards = "You will be able to choose one of these rewards:\n\nFaded Hakkari CloakTattered Hakkari Cape\n\nYou will also receive:\n\n"},
[8182] = {title = "The Hand of Rastakhan", objective = "Speak with Molthor in Stranglethorn.", description = "<name>, our worst fears have come.  Legend speaks of the Soulflayer ruling the ancient troll world through evil and terror, and his power was absolute.  If he cannot be stopped now, then his new reign may spread across all of Azeroth!\n\nThere is a contingent of Zandalarian trolls who may help us stem this tide of darkness.  They have camped on Yojamba Isle, off the coast of Stranglethorn, west of the ruins of Zul'Kunda.  Speak with their leader, Molthor, and pray he knows how to defeat Hakkar.", progress = nil, completion = "You were sent by the prospector, Ironboot?  His kind are wise, and wiser still to bring you to me, for I and the Zandalarian trolls may know a way to defeat the Soulflayer.\n\nSpeak with the trolls of this isle, heed their words, and prepare yourself, &lt;name&gt;, for the task before you is soaked in blood.", rewards = "You will receive:\n\n"},
[8183] = {title = "The Heart of Hakkar", objective = "\nBring the Heart of Hakkar to Molthor on Yojamba Isle.", description = "The Heart of Hakkar burns from within.  Although the Soulflayer is defeated, the kernel of his power yet remains in this cursed organ.", progress = "<name>, the reek of an evil soul surrounds you.  Have you been cursed by the Soulflayer?", completion = "Ah, the Heart of Hakkar.  So the Soulflayer has been slain!  Our world is saved!\n\n&lt;name&gt;, your service to us cannot be overstated.  You truly are a hero of this realm!", rewards = "You will be able to choose one of these rewards:\n\nZandalarian Hero BadgeZandalarian Hero CharmZandalarian Hero Medallion\n\nYou will also receive:\n\n"},
[8184] = {title = "Presence of Might", objective = "Zanza can create the ancient troll enchantments with the right components.\n\nThe first is a primal Hakkari idol that must be taken from the most powerful of the Hakkari lieutenants: Jin'do and the Bloodlord.\n\nThe second is a punctured voodoo doll. These can be found in the piles of refuse that litter this foul place. Look inside the jinxed hoodoo piles.\n\nBring me these things and I will create a powerful enchantment for you!", description = nil, progress = "Zanza can create the ancient troll enchantments with the right components.\n\nThe first is a primal Hakkari idol that must be taken from the most powerful of the Hakkari lieutenants: Jin'do and the Bloodlord.\n\nThe second is a punctured voodoo doll. These can be found in the piles of refuse that litter this foul place. Look inside the jinxed hoodoo piles.\n\nBring me these things and I will create a powerful enchantment for you!", completion = "Use it well, &lt;class&gt;. Show the Hakkari no mercy!", rewards = "You will receive:\n\nPresence of Might"},
