[8264] = {title = "Defiler's Standard Care Package", objective = "You've certainly begun to prove yourself to us, <name>.  As a reward, please take this care package!  Inside it you'll find rations and bandages that are usable only inside Arathi Basin.\n\nAs you continue to prove yourself inside the Basin, you'll find that these items will become available to you for purchase.  Rations become available when you are Honored with us, and bandages when you are Revered.", description = nil, progress = nil, completion = nil, rewards = "You will receive:\n\nDefiler's Standard Care Package"},
[8265] = {title = "Defiler's Advanced Care Package", objective = "You've certainly begun to prove yourself to us, <name>.  As a reward, please take this care package!  Inside it you'll find rations and bandages that are usable only inside Arathi Basin.\n\nAs you continue to prove yourself inside the Basin, you'll find that these items will become available to you for purchase.  Rations become available when you are Honored with us, and bandages when you are Revered.", description = nil, progress = nil, completion = nil, rewards = "You will receive:\n\nDefiler's Advanced Care Package"},
