[8384] = {title = "Claiming Arathi Basin", objective = "The war against the Horde is quickly depleting our supplies, <name>.  It is vital to maintain control of Arathi Basin to keep the stream of resources flowing!  Have you lent a hand in Arathi yet?", description = nil, progress = "The war against the Horde is quickly depleting our supplies, <name>.  It is vital to maintain control of Arathi Basin to keep the stream of resources flowing!  Have you lent a hand in Arathi yet?", completion = "Excellent work, &lt;class&gt;!  Your efforts will help to provide the Alliance with the resources it needs.", rewards = "The following spell will be cast on you:\n\nHonor Points +82"},
[8385] = {title = "Concerted Efforts", objective = "Ah, <name>.  I hope you continue to lend your experience to the different battlefronts.  A lot of our lads and lasses in the field look up to seasoned veterans like you.", description = nil, progress = "Ah, <name>.  I hope you continue to lend your experience to the different battlefronts.  A lot of our lads and lasses in the field look up to seasoned veterans like you.", completion = "You live up to yer reputation, &lt;name&gt;.  Without people like you, the Alliance wouldn't stand a chance against the savage forces of the Horde.", rewards = "The following spell will be cast on you:\n\nHonor Points +2388"},
[8386] = {title = "Fight for Warsong Gulch", objective = "Don't forget the fight for Warsong Gulch is about more than just protecting trees.  It's about keeping a natural barrier between Ashenvale and the Barrens.  Do you bring any news from the battlefield, <name>?", description = nil, progress = "Don't forget the fight for Warsong Gulch is about more than just protecting trees.  It's about keeping a natural barrier between Ashenvale and the Barrens.  Do you bring any news from the battlefield, <name>?", completion = "Good work, &lt;name&gt;.  Thanks to you, our natural defenses in Ashenvale will remain intact for another day.", rewards = "The following spell will be cast on you:\n\nHonor Points +50"},
[8387] = {title = "Invaders of Alterac Valley", objective = "The battle for Alterac rages on!  You must return to Alterac Valley and once more drive the invaders out of Frostwolf territory, <name>!", description = nil, progress = "The battle for Alterac rages on!  You must return to Alterac Valley and once more drive the invaders out of Frostwolf territory, <name>!", completion = "Good work, &lt;name&gt;!  You should be proud of yourself.  The Alliance invaders must be expelled from Alterac!", rewards = nil},
[8388] = {title = "For Great Honor", objective = "Many brave fighters look up to you, <name>.  You continue to be an example for all in the fight against the Alliance.  What news do you bring from your journeys?", description = nil, progress = "Many brave fighters look up to you, <name>.  You continue to be an example for all in the fight against the Alliance.  What news do you bring from your journeys?", completion = "You continue to honor the Horde, &lt;name&gt;.  I'm glad to see you haven't turned soft.", rewards = "The following spell will be cast on you:\n\nHonor Points +2388\n\nYou will receive:\n\n"},
[8389] = {title = "Battle of Warsong Gulch", objective = "The battle in Warsong Gulch rages on, <name>.  Do your part to drive the Silverwing Sentinels out of our lands!", description = nil, progress = "The battle in Warsong Gulch rages on, <name>.  Do your part to drive the Silverwing Sentinels out of our lands!", completion = "Well done, &lt;class&gt;!  You've helped ensure the Horde's ownership over Warsong Gulch!", rewards = "The following spell will be cast on you:\n\nHonor Points +50"},
[8390] = {title = "Conquering Arathi Basin", objective = "The war against the Alliance scum demands resources, <name>! We cannot allow our stockpiles to run dry in the middle of a battle.  Continue to ensure Arathi Basin remains in our hands!", description = nil, progress = "The war against the Alliance scum demands resources, <name>! We cannot allow our stockpiles to run dry in the middle of a battle.  Continue to ensure Arathi Basin remains in our hands!", completion = "Excellent work, &lt;name&gt;!  As long as Arathi Basin stays under our control our supplies will be well stocked.", rewards = nil},
