[8436] = {title = "Conquering Arathi Basin", objective = "Bring 3 Arathi Basin Mark of Honor to a Horde Warbringer outside the battlegrounds.", description = "Our conflict with the Alliance has escalated to a true war.  No longer are false pretenses to enter into battle used or expected by either side at this point.\n\nThe battle for the Arathi Basin is one of resources, plain and simple.  Either the Alliance gets the resources and uses them against us or we get them and use them to keep our war efforts going.  Help the Defilers maintain the flow of resources we need, <name>!", progress = "You've come back with news from Arathi, <class>?", completion = "Securing our resources in Arathi is an important task!  You've done well, &lt;name&gt;.", rewards = "The following spell will be cast on you:\n\nHonor Points +138\n\nYou will receive:\n\n"},
[8437] = {title = "Conquering Arathi Basin", objective = "Bring 3 Arathi Basin Mark of Honor to a Horde Warbringer outside the battlegrounds.", description = "Our conflict with the Alliance has escalated to a true war.  No longer are false pretenses to enter into battle used or expected by either side at this point.\n\nThe battle for the Arathi Basin is one of resources, plain and simple.  Either the Alliance gets the resources and uses them against us or we get them and use them to keep our war efforts going.  Help the Defilers maintain the flow of resources we need, <name>!", progress = "You've come back with news from Arathi, <class>?", completion = "Securing our resources in Arathi is an important task!  You've done well, &lt;name&gt;.", rewards = "The following spell will be cast on you:\n\nHonor Points +228\n\nYou will receive:\n\n"},
