[8440] = {title = "Conquering Arathi Basin", objective = "The war against the Alliance scum demands resources, <name>! We cannot allow our stockpiles to run dry in the middle of a battle.  Continue to ensure Arathi Basin remains in our hands!", description = nil, progress = "The war against the Alliance scum demands resources, <name>! We cannot allow our stockpiles to run dry in the middle of a battle.  Continue to ensure Arathi Basin remains in our hands!", completion = "Excellent work, &lt;name&gt;!  As long as Arathi Basin stays under our control our supplies will be well stocked.", rewards = nil},
