[8635] = {title = "Splitrock the Elder", objective = "The battles of ages past are seeds from which legends grow.  Yours is a time of heroes, young one.  May your legend take root, thrive.\n\nI bid you well, <name>, and offer you this token...", description = nil, progress = nil, completion = nil, rewards = "The following spell will be cast on you:\n\nForce Self - Bow\n\nYou will receive:\n\nCoin of Ancestry"},
