[8687] = {title = "Target: Hive'Zora Tunnelers", objective = "Slay 30 Hive'Zora Tunnelers and report back to Commander Mar'alith at Cenarion Hold in Silithus. You must also bring Combat Task Briefing VII in order to complete this quest.", description = "Reinforcements have been called on to join the assault on Hive'Zora.  You have been selected to target Hive'Zora tunnelers.  Report back to Commander Mar'alith after completing your task.", progress = "You have something to tell me, <class>?", completion = "Excellent work, &lt;name&gt;.  Without their tunnelers, the silithid will have a hard time repairing any damage inflicted to their hive's structure.  I assume you'll want a new task.", rewards = "You will receive:\n\nCenarion Combat Badge"},
[8688] = {title = "Windrun the Elder", objective = "The heavens, in their countless stars, hold answers to earthly mysteries, <name>.  Might, then, the wise, and the lucky, gaze up and find truth?", description = nil, progress = nil, completion = nil, rewards = "The following spell will be cast on you:\n\nForce Self - Bow\n\nYou will receive:\n\nCoin of Ancestry"},
[8689] = {title = "Shroud of Infinite Wisdom", objective = "Bring 1 Qiraji Martial Drape, 2 Jasper Idols, 5 Gold Scarabs and 5 Clay Scarabs to Keyl Swiftclaw in Silithus. You must also obtain Revered reputation with Cenarion Circle to complete this quest.", description = "I'm glad to hear you're aiding us in our attack on Ahn'Qiraj, <name>.  I can make you a protective item that'll be of assistance in the fight against our enemy, <class>.\n\nI shall need a Qiraji drape, which I'll resize for you of course.  I'll also need idols and scarabs which I'll use to create a suitable clasp.\n\nI promise you won't be disappointed.", progress = "Did you bring the materials for the cloak, <name>?", completion = "Ah yes... this drape is immaculate.\n\nTake this cloak, &lt;name&gt;.  May it protect you from the foul magic of the Qiraji.", rewards = "You will receive:\n\nShroud of Infinite Wisdom"},
