[8697] = {title = "Ring of Infinite Wisdom", objective = "Bring 1 Qiraji Ceremonial Ring, 2 Obsidian Idols, 5 Silver Scarabs and 5 Bone Scarabs to Windcaller Yessendra in Silithus. You must also attain Honored reputation with Cenarion Circle to complete this quest.", description = "The rings worn by the Qiraji lieutenants are rumored to grant them formidable abilities.  The corrupted gems that adorn them could, in theory, be swapped out with more pure materials to create uncorrupted versions of the rings.\n\nThe gems that adorn Qiraji idols would work quite well, as their quality is superior.\n\nBring me the ring and idols and I'll create a powerful ring for you, <class>.", progress = "Did you bring me the ring and materials, <name>?", completion = "I will swap the gem on the ring for you.  You'll find the effect is much more... agreeable.", rewards = "You will receive:\n\nRing of Infinite Wisdom"},
