[9111] = {title = "Robe of Faith", objective = "Father Inigo Montoy at Light's Hope Chapel in the Eastern Plaguelands will make a Robe of Faith if you bring him the following: 1 Desecrated Robe, 25 Wartorn Cloth Scraps, 4 Mooncloth and 2 Nexus Crystals.", description = "<Father Montoy makes some strange gestures in the air in front of you.>\n\nBlessed <boy/girl>, have you found the vestments of the fallen? The poor souls whose last breaths were taken on the field of battle - stripped of everything including their dignity.\n\nBring me the desecrated remnants of the departed along with reagents of purification and you shall know faith.", progress = "Have you brought me the reagents, child?", completion = "The vestments are ready, &lt;name&gt;. Smite those that would harm our world in your righteous fire.", rewards = "You will receive:\n\nRobe of Faith"},
