[9246] = {title = "Icy Scale Breastplate", objective = "Craftsman Wilhelm at Light's Hope Chapel in the Eastern Plaguelands wants 8 Frozen Runes, 24 Heavy Scorpid Scale, 7 Essence of Water, 5 Cured Rugged Hides and 300 gold. You must also be of Exalted reputation with the Argent Dawn.", description = "Thanks to you and Omarion (May he rest in peace), I am now able to create an Icy Scale breastplate. All you need to do is bring me the materials and pay a small crafting fee.You will receive:\n\n", progress = nil, completion = nil, rewards = "You will receive:\n\nIcy Scale Breastplate"},
[9247] = {title = "The Keeper's Call", objective = "Take the Letter from the Keeper of the Rolls to him at Light's Hope Chapel in the Eastern Plaguelands.", description = "<name>,\n\nIn times past you have served the interests of the Argent Dawn, always to the best of your abilities. Now, once again, we sound the clarion call in our time of greatest need.\n\n<name>, please report to the Keeper of the Rolls at Light's Hope Chapel in the Eastern Plaguelands if you are willing to aid us once more.", progress = "Ah, <name>, it is good to see you.", completion = "Simply by answering the call, we are in your debt, &lt;name&gt;. The Argent Dawn is certain that through your shining example, others that are here to aid us will gain great insight into how best to throw back this Scourge Invasion!", rewards = "You will be able to choose one of these rewards:\n\nBlessed Wizard OilConsecrated Sharpening Stone\n\nYou will also receive:\n\n"},
[9248] = {title = "A Humble Offering", objective = "Aurel Goldleaf at Cenarion Hold in Silithus wants you to bring her 1 Abyssal Scepter. You must also be Honored with the Cenarion Circle.", description = "Ishnu-alah, <race>. Show me proof of the demise of a Lord of the Abyssal Council and I shall reward you with an item that should aid you in your battles against the Qiraji.", progress = "Fandu-dath-belore? Oh, pardon me, <name>. I did not recognize you. Have you the scepter?", completion = "Ande'thoras-ethil. It means; 'May your troubles be diminished,' in my native tongue.\n\nWear the band proudly and in good health, &lt;name&gt;.", rewards = "You will receive:\n\nBand of Cenarius"},
[9249] = {title = "40 Tickets - Schematic: Steam Tonk Controller", objective = nil, description = nil, progress = nil, completion = nil, rewards = "You will receive:\n\nSchematic: Steam Tonk Controller"},
[9250] = {title = "Frame of Atiesh", objective = "Find someone that will help you in recovering the last two pieces of the staff and ridding it of evil.", description = "With the frame formed, Atiesh is nearly whole. Only the head and base of the staff are now missing. There is also a minor issue regarding the staff being a conduit for Sargeras's evil.\n\nFind someone that can assist you in locating the remaining pieces and exorcising the evil from the staff.", progress = "It was her time, mortal.", completion = "The archmages have died.\n\nDo not mourn their passing, &lt;name&gt;. All such things are fated. Just as you holding the staff of the Guardian was fated...", rewards = "You will receive:\n\n"},
[9251] = {title = "Atiesh, the Befouled Greatstaff", objective = "Anachronos at the Caverns of Time in Tanaris wants the Staff Head of Atiesh and the Base of Atiesh.", description = "You question why you are still alive. After all, you have in your possession an item of great evil. The answer is not clear, <name>. I only do as I must.\n\nKel'Thuzad sits atop his throne in Naxxramas, plotting... He seeks that which you hold and holds that which you seek.\n\nAs for the base of the staff; the dwarf, Brann Bronzebeard, held it when he entered Ahn'Qiraj. He has left Ahn'Qiraj but the base of Atiesh remains.\n\nShould you succeed in recovering the pieces, return them to me.", progress = "In its current form, the staff is far too dangerous to be in the hands of a mortal.", completion = "There is a being within... a being not of this world.", rewards = "You will receive:\n\n"},
[9257] = {title = "Atiesh, Greatstaff of the Guardian", objective = "Anachronos at the Caverns of Time in Tanaris wants you to take Atiesh, Greatstaff of the Guardian to Stratholme and use it on Consecrated Earth. Defeat the entity that is exorcised from the staff and return to him.", description = "This final task will be the most difficult of all, <name>. You have faced and defeated both an Old God and the Lich King's General, but now you must face the hand of Sargeras.\n\nTake the staff to Stratholme. It is there that you will find a piece of consecrated earth: ground where the greatest knights of Lordaeron were murdered. Place the befouled staff upon the holy ground and prepare for an entity of immense power to break from within... Defeat the demonic being that controls the staff and return to me.You will receive:\n\n", progress = nil, completion = nil, rewards = "You will receive:\n\nAtiesh, Greatstaff of the Guardian"},
[9259] = {title = "Traitor to the Bloodsail", objective = "That mangy dog, Revilgaz, has offered to pardon any former Bloodsails willing to turn on their cap'n!  That bilge sucker asked me to go an' off me ole' mates and to bring back their bandannas as proof.\n\nI don't have it in me heart to do that, nor will I ask ye to.  Bring me some silk cloth and red dye and I'll make suitable replicas.  I'll tell the Baron you went turncoat, but be warned... the Bloodsails are not gonna like it!", description = nil, progress = "That mangy dog, Revilgaz, has offered to pardon any former Bloodsails willing to turn on their cap'n!  That bilge sucker asked me to go an' off me ole' mates and to bring back their bandannas as proof.\n\nI don't have it in me heart to do that, nor will I ask ye to.  Bring me some silk cloth and red dye and I'll make suitable replicas.  I'll tell the Baron you went turncoat, but be warned... the Bloodsails are not gonna like it!", completion = "Let us observe a minute of silence for our fallen mates, &lt;name&gt;.\n\n\n\nBeing a traitor is one thing, murdering yer own buckos... that'd be just downright despicable, aye!", rewards = "The following spell will be cast on you:\n\nReputation - Booty Bay +500"},
[9260] = {title = "Investigate the Scourge of Stormwind", objective = "Collect three Dim Necrotic Stones from the Scourge outside Stormwind and investigate the glowing runic circles nearby their encampment.", description = "The Scourge is camped upon our very doorstep; we cannot remain idle! Surely you will step up to defend Stormwind from these accursed invaders? \n\nThere are strange runic circles outside, glowing with the same energy that surrounds the undead and the Necropolis above. They have significance, I'm certain, and I want you to find out what. Thin the numbers of those aberrations while you're at it; bring me proof of their death along with your results, and I will reward you.  Go!", progress = "Have you investigated the invasion yet? There's no time for dawdling!", completion = "Hmm. From what you've told me, the voices within the circles... they must be used for communication of some kind. I shudder to think what unnatural minds are contacted through such magic. Regardless, you seem to have emerged whole and sane.  The Argent Dawn appreciates your service.", rewards = "You will receive:\n\n"},
