[9310] = {title = "Faint Necrotic Crystal", objective = "Bring the Faint Necrotic Crystal to Lieutenant Rukag in the courtyard of the Undercity.", description = "On the corpse of the fallen wight, you find a crystal that is faintly radiating necrotic energy.", progress = "Can I help you with something?", completion = "You say this was taken from a wight attacking the city?\n\nWe have received word about the necropolis base camps that are being summoned by the Scourge. I suspect they were trying to create one in the Undercity. We will have to be vigilant in the future.", rewards = "You will be able to choose one of these rewards:\n\nBlessed Wizard OilConsecrated Sharpening Stone"},
[9317] = {title = "Consecrated Sharpening Stones", objective = "If you bring me eight necrotic runes from the Scourge invaders, I can give you a consecrated sharpening stone. It should be of great help in your battle against the minions of the Lich King.", description = nil, progress = "If you bring me eight necrotic runes from the Scourge invaders, I can give you a consecrated sharpening stone. It should be of great help in your battle against the minions of the Lich King.", completion = "Thank you, &lt;name&gt;. Here are your consecrated sharpening stones. Use them prudently.", rewards = "You will receive:\n\nConsecrated Sharpening Stone"},
[9318] = {title = "Blessed Wizard Oil", objective = "In exchange for the necrotic runes of the Scourge invaders, the Argent Dawn will give you one vial of blessed wizard oil.", description = nil, progress = "In exchange for the necrotic runes of the Scourge invaders, the Argent Dawn will give you one vial of blessed wizard oil.", completion = "I hope that you will find these items useful in future battles.", rewards = "You will receive:\n\nBlessed Wizard Oil"},
[9319] = {title = "A Light in Dark Places", objective = "Find and touch the bonfires located within Blackrock Spire, Dire Maul, Scholomance, and Stratholme, then return to the Festival Flamekeeper.", description = "We are not the only ones who celebrate this holiday, <name>. All creatures, all cultures, are touched by the flame. Even in the darkest places, the fires are being lit.\n\nIf you wish to see them for yourself, travel into the depths. Seek out the bonfires burning within Stratholme, Scholomance, among the ogres of Dire Maul, and the halls of Blackrock Spire itself; I will await your return.", progress = nil, completion = "Your essence sings with the energy of the flames you found, &lt;name&gt;. The fire you encountered is potent, and with the right knowledge, its power can be harnessed...\n\n\n\nAh! Here we are. May this light your path, no matter where you tread.", rewards = "You will receive:\n\nCaptured FlameBurning Blossom"},
[9320] = {title = "Major Mana Potion", objective = "In addition to our other supplies, we also have a number of major mana potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", description = nil, progress = "In addition to our other supplies, we also have a number of major mana potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", completion = "Here you are, &lt;name&gt;. Be careful out there. Our numbers are not so large we can afford many battlefield casualties.", rewards = "You will receive:\n\nMajor Mana Potion"},
[9321] = {title = "Major Healing Potion", objective = "In addition to our other supplies, we also have a number of major healing potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", description = nil, progress = "In addition to our other supplies, we also have a number of major healing potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", completion = "Here you are, &lt;name&gt;. Be careful out there. Our numbers are not so large we can afford many battlefield casualties.", rewards = "You will receive:\n\nMajor Healing Potion"},
[9322] = {title = "Wild Fires in Kalimdor", objective = "Visit the Midsummer camps located in Azshara, Silithus, Un'Goro Crater, and Winterspring, then return to the Festival Flamekeeper.", description = "The Midsummer Fire Festival is celebrated everywhere in Azeroth, and the diverse land of Kalimdor is no exception. You look fit to travel -- perhaps a journey is in order.\n\nMidsummer cannot be truly appreciated unless you've seen the blistering blue fires abroad. The celebrants have lit fires in chilly western Winterspring, northeastern Azshara, near Valor's Rest in the wastes of Silithus, and by the entrance to lush Un'Goro. Return once your journey is complete.", progress = nil, completion = "Your spirit glows with the warmth of the Kalimdor fires, friend. Is the heat not invigorating?\n\n Once the power fades from you, perhaps these will help rekindle the feeling.", rewards = "You will receive:\n\nCinder BracersBurning Blossom"},
[9323] = {title = "Wild Fires in the Eastern Kingdoms", objective = "Visit the Fire Festival camps located in Blasted Lands, Eastern Plaguelands, the Hinterlands and Searing Gorge, then speak with the Festival Flamekeeper.", description = "Our celebration of the flame is spread throughout the land, <class>. Not a single place remains untouched by the heat of this holiday, and the strongest fires burn a brilliant blue. Seek them out.\n\nYou'll find one just north of Blackrock Spire. The others are located deep within the Eastern Plaguelands near a decrepit town, along the road to the cliffs within the mountainous Hinterlands, and on the way to the Dark Portal within the Blasted Lands.\n\n Return once your journey is done.", progress = nil, completion = "Ah, here you are. A little wearier, a bit dustier, but enlightened! The heat touches us all during this season and unites us; your very being glows with the power of the fires you touched.\n\nAh, but these days enlightenment is taken as a rather poor reward for being sent on a task, I suppose. Take these, then, and enjoy the celebration.", rewards = "You will receive:\n\nMantle of the Fire FestivalBurning Blossom"},
[9324] = {title = "Stealing Orgrimmar's Flame", objective = "Return the Flame of Orgrimmar to the Festival Loremaster.", description = "This flame, stolen from the heart of Orgrimmar itself, floods you with warmth. This raw power is foreign to you, but the Loremaster might know more...", progress = "You're practically glowing, <race>. What do you have there?", completion = "What's this? Unless I'm mistaken, this flame has been taken from the heart of Orgrimmar itself! The fierce hearts of the orcs and trolls are reflected in its searing heat. What a fine addition to our collection--how brave you are, stealing into the enemy's lair to retrieve the sacred flame!\n\nI wonder what power lurks in the other cities...", rewards = "You will receive:\n\nBurning BlossomBurning Blossom"},
[9325] = {title = "Stealing Thunder Bluff's Flame", objective = "Return the Flame of Thunder Bluff to the Festival Loremaster.", description = "This flame, stolen from the shaman of Thunder Bluff, floods you with warmth. This raw power is foreign to you, but the Loremaster might know more...", progress = "You're practically glowing, <race>. What do you have there?", completion = "Unless I miss my guess, this fire has been taken from the cliffs of Thunder Bluff! How it sings with the power of the spirits! You are quite the thief, &lt;name&gt;. Take this as just payment for your skill. Now, if only we had the flames of all three cities...", rewards = "You will receive:\n\nFiery Festival BrewBurning Blossom"},
[9326] = {title = "Stealing the Undercity's Flame", objective = "Return the Flame of the Undercity to the Festival Loremaster.", description = "This flame, stolen from the ruins above the Undercity, floods you with warmth. This raw power is foreign to you, but the Loremaster might know more...", progress = "You're practically glowing, <race>. What do you have there?", completion = "Hm... what a strange fire! It is subdued, nearly smoldering, but the heat is intense. And you received this from the Undercity, you say? Fascinating. With the flames of all three cities, who knows what we could accomplish?", rewards = "You will be able to choose one of these rewards:\n\nElderberry PieFire-toasted BunMidsummer SausageToasted Smorc\n\nYou will also receive:\n\nBurning Blossom"},
[9330] = {title = "Stealing Stormwind's Flame", objective = "Return the Flame of Stormwind to the Festival Talespinner.", description = "This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the Talespinner might know more...", progress = "You're practically glowing, <race>. What do you have there?", completion = "And this brightly-burning fire... you say it came from Stormwind? Remarkable! That must have been quite an adventure! I'll pay you for this, of course. If you find anything else like this, be sure to speak with me again.", rewards = "You will receive:\n\nFiery Festival BrewBurning Blossom"},
[9331] = {title = "Stealing Ironforge's Flame", objective = "Return the Flame of Ironforge to the Festival Talespinner.", description = "This flame, stolen from the heart of Ironforge itself, floods you with warmth. This raw power is foreign to you, but the Talespinner might know more...", progress = "You're practically glowing, <race>. What do you have there?", completion = "This hearty flame must be from the halls of Ironforge; it's as stalwart as the dwarves themselves. A fine catch, &lt;name&gt;. If I had the fires of the other cities... ah, but never mind. Here is your payment.", rewards = "You will receive:\n\nBurning BlossomBurning Blossom"},
[9332] = {title = "Stealing Darnassus's Flame", objective = "Return the Flame of Darnassus to the Festival Talespinner.", description = "This flame, stolen from the shores of Teldrassil, floods you with warmth. This raw power is foreign to you, but the Talespinner might know more...", progress = "You're practically glowing, <race>. What do you have there?", completion = "This fire aches of ancient sorrow, &lt;name&gt;. You pulled this from the roots of Teldrassil, you say? Fascinating! Here is a reward for your skill. Return to me should you find anything else like this; I may have a use for it.", rewards = "You will be able to choose one of these rewards:\n\nElderberry PieFire-toasted BunMidsummer SausageToasted Smorc\n\nYou will also receive:\n\nBurning Blossom"},
[9333] = {title = "Argent Dawn Gloves", objective = "If you bring me thirty necrotic runes, I will give you your choice of hand protection in return. These should prove valuable in our fight against the undead Scourge.", description = nil, progress = "If you bring me thirty necrotic runes, I will give you your choice of hand protection in return. These should prove valuable in our fight against the undead Scourge.", completion = "You've done well, &lt;name&gt;. Take your pick of hand protection in thanks for your efforts.", rewards = "You will be able to choose one of these rewards:\n\nGloves of Undead CleansingHandwraps of Undead SlayingHandguards of Undead SlayingGauntlets of Undead Slaying"},
[9334] = {title = "Blessed Wizard Oil", objective = "In exchange for the necrotic runes of the Scourge invaders, the Argent Dawn will give you one vial of blessed wizard oil.", description = nil, progress = "In exchange for the necrotic runes of the Scourge invaders, the Argent Dawn will give you one vial of blessed wizard oil.", completion = "I hope that you will find these items useful in future battles.", rewards = "You will receive:\n\nBlessed Wizard Oil"},
[9335] = {title = "Consecrated Sharpening Stones", objective = "If you bring me eight necrotic runes from the Scourge invaders, I can give you a consecrated sharpening stone. It should be of great help in your battle against the minions of the Lich King.", description = nil, progress = "If you bring me eight necrotic runes from the Scourge invaders, I can give you a consecrated sharpening stone. It should be of great help in your battle against the minions of the Lich King.", completion = "Thank you, &lt;name&gt;. Here are your consecrated sharpening stones. Use them prudently.", rewards = "You will receive:\n\nConsecrated Sharpening Stone"},
[9336] = {title = "Major Healing Potion", objective = "In addition to our other supplies, we also have a number of major healing potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", description = nil, progress = "In addition to our other supplies, we also have a number of major healing potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", completion = "Here you are, &lt;name&gt;. Be careful out there. Our numbers are not so large we can afford many battlefield casualties.", rewards = "You will receive:\n\nMajor Healing Potion"},
[9337] = {title = "Major Mana Potion", objective = "In addition to our other supplies, we also have a number of major mana potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", description = nil, progress = "In addition to our other supplies, we also have a number of major mana potions you may find useful. I will give you one in exchange for fifteen necrotic runes.", completion = "Here you are, &lt;name&gt;. Be careful out there. Our numbers are not so large we can afford many battlefield casualties.", rewards = "You will receive:\n\nMajor Mana Potion"},
[9338] = {title = "Allegiance to Cenarion Circle", objective = "Even if you have no need for the material rewards we offer, these badges still mark our mutual trust and friendship.\n\nBring them to me.  I'll make due note of your service should your performance go above and beyond the call of duty.", description = nil, progress = "Even if you have no need for the material rewards we offer, these badges still mark our mutual trust and friendship.\n\nBring them to me.  I'll make due note of your service should your performance go above and beyond the call of duty.", completion = "You honor us, &lt;name&gt;.  With your continued efforts we will overcome all obstacles.", rewards = nil},
[9339] = {title = "A Thief's Reward", objective = "Return the Flame of Stormwind to (NAME).This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the (NAME) might know more...", description = "This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the (NAME) might know more...", progress = nil, completion = "You've proven yourself today, &lt;name&gt;.\n\nWhile I have little else to give you, I do have this crown; a fitting gift for a conqueror. May it light your way.", rewards = "You will receive:\n\nCrown of the Fire Festival"},
[9341] = {title = "Tabard of the Argent Dawn", objective = "You have aided our cause greatly, <name>. If you wish, I can make available the tabard of the Argent Dawn. We are proud to have you among our allies.", description = nil, progress = "You have aided our cause greatly, <name>. If you wish, I can make available the tabard of the Argent Dawn. We are proud to have you among our allies.", completion = "Take this tabard and wear it proudly. Perhaps your deeds might inspire others to take up arms in support of our fight against the Scourge.", rewards = "You will receive:\n\nTabard of the Argent Dawn"},
[9343] = {title = "Tabard of the Argent Dawn", objective = "You have aided our cause greatly, <name>. If you wish, I can make available the tabard of the Argent Dawn. We are proud to have you among our allies.", description = nil, progress = "You have aided our cause greatly, <name>. If you wish, I can make available the tabard of the Argent Dawn. We are proud to have you among our allies.", completion = "Take this tabard and wear it proudly. Perhaps your deeds might inspire others to take up arms in support of our fight against the Scourge.", rewards = "You will receive:\n\nTabard of the Argent Dawn"},
[9362] = {title = "Warlord Krellian", objective = "Retrieve the Prismatic Shell for Archmage Xylem. The Archmage resides in a tower atop the cliffs of Azshara.", description = "There isn't much time, <name>!  My scrying spells have revealed a powerful source of magic.  I cannot leave the tower, and would ask you to kill the vile creature in possession of this artifact.\n\nThe pathetic little cretin is known as Warlord Krellian, hiding behind his guards in the Temple of Zin-Malor.  Burn him to ashes and take the prismatic shell he covets so dearly.", progress = "There is not time to waste!  Have you the shell?", completion = "I must say, this shell has a strange aura.  It seems only fitting that part of your reward for retrieving it would be to test its magical capabilities...", rewards = "You will receive:\n\n"},
[9364] = {title = "Fragmented Magic", objective = "Polymorph the Spitelash of Azshara and kill the clones that appear several seconds later. When you have slain 50 Polymorph Clones, return to Archmage Xylem in Azshara.", description = "My research has revealed a unique purpose for the prismatic shell.\n\nCarry the shell and cast your polymorph spell on the Spitelash in Azshara.  If you wait a few moments, the chaotic aura of the shell will cause them to split into several smaller clones for a few moments.  Kill these clones as quickly as possible, and the shell will collect their essence.\n\nWhen the shell is full, return it to me.", progress = "Have you collected the magic?", completion = "Splendid!  Splendid!\n\nNow we are able to reform this magic into a new spell.  Behold!", rewards = "You will learn:\n\nPolymorph\n\nThe following spell will be cast on you:\n\nPolymorph: Pig\n\nYou will also receive:\n\n"},
[9365] = {title = "A Thief's Reward", objective = "Return the Flame of Stormwind to (NAME).This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the (NAME) might know more...", description = "This flame, stolen from the heart of Stormwind itself, floods you with warmth. This raw power is foreign to you, but the (NAME) might know more...", progress = nil, completion = "You've proven yourself today, &lt;name&gt;.\n\nWhile I have little else to give you, I do have this crown; a fitting gift for a conqueror. May it light your way.", rewards = "You will receive:\n\nCrown of the Fire Festival"},
[9367] = {title = "The Festival of Fire", objective = "Touch the bonfires within Stormwind, Ironforge, and Darnassus, then speak to a Festival Loremaster within the capital cities.", description = "Midsummer is upon us once again!\n\nEach year, as tradition dictates, Flamekeepers are chosen to tend the bonfires within our capitals. While fires are burning throughout Azeroth, it is imperative that ours be the hottest and brightest, to properly pay homage to the season.\n\nIn fact, I have a task for you, if you don't mind. I've not yet heard if the fires in all capitals are properly burning. Travel there yourself and warm yourself by the fires--make sure they're hot!", progress = nil, completion = "Excellent! It seems we've appointed dutiful Flamekeepers this year if the bonfires are as well-kept as you claim. Thank you for inspecting them for me; here, take this as a small token of my esteem.", rewards = "You will receive:\n\nBurning BlossomFiery Festival Brew"},
[9368] = {title = "The Festival of Fire", objective = "Touch the bonfires within Orgrimmar, Thunder Bluff, and the Undercity, then speak to a Festival Talespinner within the capital cities.", description = "Midsummer is upon us once again!\n\nEach year, as tradition dictates, Flamekeepers are chosen to tend the bonfires within our capitals. While fires are burning throughout Azeroth, it is imperative that ours be the hottest and brightest, to properly pay homage to the season.\n\nIn fact, I have a task for you, if you don't mind. I've not yet heard if the fires in all capitals are properly burning. Travel there yourself and warm yourself by the fires--make sure they're hot!", progress = "Are the fires burning?", completion = "Excellent! It seems we've appointed dutiful Flamekeepers this year if the bonfires are as well-kept as you claim. Thank you for inspecting them for me; here, take this as a small token of my esteem.", rewards = "You will receive:\n\nBurning BlossomFiery Festival Brew"},
[9386] = {title = "A Light in Dark Places", objective = "Find and touch the bonfires located within Blackrock Spire, Dire Maul, Scholomance, and Stratholme, then speak with the Festival Flamekeeper.", description = "An eager one, aren't you? If you're willing to enter the depths once more, I'll gladly reward you.\n\nSeek out the bonfires burning within Stratholme, Scholomance, Dire Maul, and the halls of Blackrock Spire itself; I look forward to your return, <name>.You will receive:\n\n", progress = nil, completion = nil, rewards = "You will receive:\n\nBurning Blossom"},
[9388] = {title = "Flickering Flames in Kalimdor", objective = "Visit the bonfires within Ashenvale, the Barrens, Darkshore, and Stonetalon Mountains, then speak with the Festival Flamekeeper.", description = "There are many flames burning throughout the world today, <name>. The trickiest fires to tend are those that burn green; they do not stay lit long without supervision. Make certain the emerald fires still burn on Kalimdor for our Flamekeeper, eh?\n\nYou will find them near Ratchet in the Barrens, close to the Master's Glaive in Darkshore, in the forest of Ashenvale by the bridge near Silverwing Outpost, and near the road to Windshear Crag along the main road in Stonetalon Mountains.", progress = nil, completion = "Ahhh, so they do still burn. I am glad; it would not do for those fires to burn out before their time. You have done well, &lt;name&gt;. Take this with my blessing.", rewards = "You will be able to choose one of these rewards:\n\nElderberry PieFire-toasted BunMidsummer SausageToasted Smorc\n\nYou will also receive:\n\n"},
[9389] = {title = "Flickering Flames in the Eastern Kingdoms", objective = "Visit the bonfires within Hillsbrad Foothills, Silverpine Forest, Westfall, and the Wetlands, then speak with the Festival Flamekeeper.", description = "The Midsummer Fire Festival traditionally lights up the land with a myriad of flames. Some argue that the green fires are the finest, as they are the hardest to maintain and have such a distinctive hue.\n\nMake sure they still burn in the Eastern Kingdoms for our Flamekeeper. They sit near Dun Modr in the Wetlands, close to Pyrewood Village within Silverpine Forest, on the cliffs overlooking the lighthouse in Westfall, and by the ruined tower in Hillsbrad Foothills.", progress = "Have you seen to your task?", completion = "So you've returned, and you've seen to the emerald flames of the continent. What do you think, mm? A rare and wonderful sight, to be certain. I am aware, however, that tangible rewards are better appreciated... so please, take this for your trouble.", rewards = "You will be able to choose one of these rewards:\n\nElderberry PieFire-toasted BunMidsummer SausageToasted Smorc\n\nYou will also receive:\n\n"},
[9415] = {title = "Report to Marshal Bluewall", objective = "Speak with Marshal Bluewall at the Alliance encampment near Hive'Zora.", description = "Are you Marshal Bluewall's new recruits? Of course you are.\n\nWhat are you waiting for? Good soldiers are dying in the desert. Aren't you excited to join them?\n\nNow, get going and don't dry out on the way.", progress = nil, completion = "The Alliance needs every able body they can get. The Horde has been receiving a steady supply of reinforcements from the northern part of the continent, and it's been a struggle to keep up.\n\nLet me explain the situation to you.", rewards = nil},
[9416] = {title = "Report to General Kirika", objective = "Speak with General Kirika at the Horde encampment near Hive'Regal.", description = "You must be one of General Kirika's new recruits. It's about time you showed up. Bodies wear down quick in the desert.\n\nThe dwarves have been getting reinforcements daily, and we can't afford to lose ground in our struggle for resources in Silithus.\n\nGet your instructions from the General. It's not my job to train you, just find you.", progress = nil, completion = "It's good that you're hear, but there's no time to waste making small talk. You need instructions, and then you need to get out there.\n\nListen up.", rewards = nil},
[9419] = {title = "Scouring the Desert", objective = "Find and deliver Silithyst to the Silithyst Extractor, then speak with Marshal Bluewall at the Alliance encampment near Hive'Zora.", description = "We recently found out that the Horde has been scavenging the Silithus desert for resources, but paid them little mind. We thought it could not be worth it to station large numbers in this sweltering desert.\n\nHowever, it was discovered the importance of the resources, and now we are scrambling to catch up.\n\nIf I have your assistance, you must go into the desert and recover the silithyst and return it here.", progress = "Be careful out there, but also be quick. We have no time to lose.", completion = "Thank the Light you were successful.\n\nNow if we could only have started this sooner, our need would not be so desperate. We must keep the bounty of the desert from falling into the hands of the Horde.", rewards = "You will be able to choose one of these rewards:\n\nMajor Healing PotionMajor Mana Potion\n\nYou will also receive:\n\n"},
[9422] = {title = "Scouring the Desert", objective = "Find and deliver Silithyst to the Silithyst Extractor then speak with General Kirika at the Horde encampment near Hive'Regal.", description = "For some time now, we have been tapping the crystal resources of the Silithus desert. The only dangers we faced were from the creatures that made this place their home and the oppressive sand and dust and heat.\n\nBut now the Alliance seems to have caught on to what we have found here. It is now a race between us to capture the natural resources, and keep them from the other.\n\nI need every set of hands that is available. I hope I have yours.", progress = "Be wary of the dangers of the desert, <name>. With the Alliance lurking in the dunes, any step could be your last.", completion = "You've done well, &lt;name&gt;. These resources will be added to our stockpiles, and every bit is to our gain and our enemy's loss.", rewards = "You will be able to choose one of these rewards:\n\nMajor Healing PotionMajor Mana Potion\n\nYou will also receive:\n\n"},
[9556] = {title = "To The Victor...", objective = "Collect a bottle of Stratholme Holy Water from within the city of Stratholme and deliver it to the Lordaeron Mage within the Mage Quarter of Stormwind or the Undercity when your side is victorious in the Eastern Plaguelands.", description = "You've no doubt been fighting long and hard among the towers, <race>. We would like to give something back to you in return for your defense of these lands, but first, you must prove to us you understand the weight of the tragedies that took place here.\n\nWalk the streets of Stratholme and find a vial of the holy water. Once you have it, travel to the mage quarter of Stormwind or the Undercity... There, if you are victorious within these lands, you will find my colleague.You will receive:\n\nUpon completion of this quest you will gain:\n\n", progress = nil, completion = nil, rewards = "You will receive:\n\n"},
[9664] = {title = "Establishing New Outposts", objective = "Capture Crown Guard Tower, Eastwall Tower, Northpass Tower and Plaguewood Tower in the Eastern Plaguelands, then return to Emmisary Whitebeard at Light's Hope Chapel.", description = "Welcome to the Plaguelands, <name>. As you know, the Forsaken and their allies hold the lands to the west of here against our best efforts. The Alliance commanders have decided that here in the east, we will establish new fortifications to tighten the noose around our enemies.\n\nTowers long abandoned since the days of Lordaeron are to be seized and rebuilt for this purpose. These lands are dangerous, and we could use your help.", progress = nil, completion = "Very good, &lt;name&gt;. It will be important for us to hold these towers against future attacks, and for that we may need your assistance until reinforcements can be brought in to secure the area.", rewards = "You will receive:\n\n"},
[9665] = {title = "Bolstering Our Defenses", objective = "Capture Crown Guard Tower, Eastwall Tower, Northpass Tower and Plaguewood Tower in the Eastern Plaguelands, then return to Emmisary Gormok at Light's Hope Chapel.", description = "Agents of the Forsaken uncovered an Alliance plot to establish new bases here in the Eastern Plaguelands, at abandoned towers throughout the area.\n\nIt is imperative that we not allow them to establish a significant presence on our eastern borders, and so we must stave off this Alliance incursion. Our current plans are to drive them out of any towers they may have already seized, and occupy those that we have under our control against further attacks.", progress = nil, completion = "This is good news, &lt;name&gt;. It will be a constant struggle here in the Plaguelands until we have fully driven back the forces of the Alliance.\n\nUntil that day, I hope that we will have your continued assistance.", rewards = "You will receive:\n\n"},
[60863] = {title = nil, objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[63769] = {title = nil, objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[65593] = {title = "Hearts of the Lovers", objective = "Bring the hearts of Avelina Lilly and Isaac Pearson to Carendin Halgar in the Temple of the Damned.", description = "An incubus will be drawn to the hearts of lovers, and the most aching love will create the most irresistible lure.\n\nMy scrying has revealed two that should serve exceptionally.\n\nIn Silverpine Forest, Avelina Lilly provides her tailoring services to the fallen wizards at Ambermill. In the Barrens, Isaac Pearson hopes to make his mark in the seaside town of Ratchet.\n\nThey toil on opposite ends of the world, yet their hearts yearn only for each other. Bring those hearts to Carendin.", progress = "Ah, <name>, you return. Were you able to procure the lovers' hearts?", completion = "As expected. Excellently done, &lt;name&gt;.\n\n\n\nIdiots.", rewards = "You will receive:\n\n"},
[65597] = {title = "The Binding", objective = "Using the Lovers' Hearts, summon and subdue an incubus, then return the Lovers' Hearts to Carendin Halgar in the Magic Quarter of the Undercity.", description = "The time has come at last. Bring the hearts to the summoning circle below. Place them at its center and call forth the incubus.\n\nThe demon will not be pleased when it realizes that you have tempted it into a trap. Do not fall prey to its beguilement, <name>. You must subdue it fully.\n\nOnly then can I teach you the spell to bind it to your will.", progress = "The incubus possesses martial prowess, but its illusory charms are its most potent weapon. Falter for even a moment, and the demon will seize upon the opportunity to enthrall you.\n\nDo not let it, <name>.", completion = "Impressive. Very impressive. The sayaad specialize in preying upon mortals. Many a &lt;class&gt; has thought themselves strong enough to command one, only to find themselves being commanded instead.\n\nThis trial did not break you, &lt;name&gt;, and your efforts have earned your reward. I will teach you the spell to summon the incubus.", rewards = "You will learn:\n\nSummon Incubus\n\nThe following spell will be cast on you:\n\nTeach Summon Incubus\n\nYou will also receive:\n\nSmall Soul Pouch"},
[65602] = {title = "What Is Love?", objective = "Retrieve the Wooden Figurine and bring it to Gakin the Darkbinder in the Mage Quarter of Stormwind.", description = "You will need a powerful reagent to tempt an incubus -- an object that represents great devotion and love.\n\nWith my scrying, I have located a small figurine that should be suitable for your ritual.\n\nThousands of years ago, the figurine belonged to a young couple. The original owners are no more, but their keepsake has survived to the present day.\n\nFollow the road north into Ashenvale, then west and past Maestra's Post. You will see a house nestled amongst the trees. There you will find what you seek.", progress = "You've returned. Successfully, I assume?", completion = "&lt;Gakin turns the wooden figurine over in his hand.&gt;\n\nI sense the emotional energy residing within this artifact, undiminished by the passing of time. The incubus will be drawn to it like a fly to honey.\n\nTakar never disappoints.", rewards = "You will receive:\n\n"},
[65603] = {title = "The Binding", objective = "Using the Wooden Figurine, summon and subdue an incubus, then return the Wooden Figurine to Gakin the Darkbinder in the Slaughtered Lamb.", description = "The time has come. Take the figurine and descend into the crypts below. A summoning circle has been prepared, and you must place the figurine at its center.\n\nThen the gateway will open, and the incubus will cross into our world.\n\nA sayaad is not to be underestimated. The demon will not hesitate to attack when it realizes your ruse. You must draw upon all you have been taught to defeat it.\n\nIf you succeed, return to me and I shall bind the creature to your will. Fail, and your soul is forfeit.", progress = "The incubus will try to lower your guard through beguilement and deception. The demon is adept at swaying the hearts of mortals.\n\nRemember that anything it may offer you is false. To the demon, you are merely prey.", completion = "Not bad, &lt;name&gt;. Your have bested the fiend, and your will remains unbroken.\n\nMany a &lt;class&gt; has believed themselves strong enough to bind a sayaad, only to become the demon's next meal instead. I am pleased to see you are not so foolish.\n\nWatch closely, and I will teach you the invocation to call upon your newly bound incubus.", rewards = "You will learn:\n\nSummon Incubus\n\nThe following spell will be cast on you:\n\nTeach Summon Incubus\n\nYou will also receive:\n\nSmall Soul Pouch"},
[65604] = {title = "The Binding", objective = "Using the Withered Scarf, summon and subdue an incubus, then return the Withered Scarf to Gan'rul Bloodeye in Orgrimmar.", description = "Yes... I see. I feel the residual emotions bound within these threads. Love is woven into its stitches. The incubus will not be able to resist.\n\nTake this to the summoning circle in Neeru's tent, <name>. Summon forth a malevolent incubus, then show it your mastery. Do not expect it to be subdued quietly. Ready your magic and prepare yourself for a fight. Underestimating a demon has been many a warlock's final mistake.\n\nWhen you have defeated it, return the cloth to me. I will let Magar know what you found.", progress = "The sayaad are dominators of minds and torturers of souls. Their visage brings the heart joy, leaving you defenseless when they rend your flesh with whip and claw.\n\nDo not entertain the incubus' charms, <name>. Slay it quickly and let it be done.", completion = "Hah! Victorious, as expected!\n\nThe incubus is subdued, and it will be my pleasure to teach you to summon the demon. You strength is growing, and I have no doubt that this is a mere stepping stone to even greater powers.\n\nI will return the scarf to Magar. I have known that orc for many years, and never did I think he would care for someone the way he cared for Krilge.\n\nIt is a reminder of what separates us from the demons. We wield their darkness, but they will never wield us.", rewards = "You will learn:\n\nSummon Incubus\n\nThe following spell will be cast on you:\n\nTeach Summon Incubus\n\nYou will also receive:\n\nSmall Soul Pouch"},
[65610] = {title = "Wish You Were Here", objective = "Investigate Fallen Sky Lake in Ashenvale and report your findings to Gan'rul Bloodeye in Orgrimmar.", description = "I met Krilge while making Mooncloth in Ashenvale. He was stationed there to protect civilians from danger, until he disappeared...\n\nThey've stopped looking for him and consider him a deserter, but I don't believe it.\n\nWe would often meet at Fallen Sky Lake before it became overrun by those aggressive bog beasts.\n\nI'm not typically one to ask favors, but if you could go to Ashenvale and look for any sign of him, I'd appreciate it.", progress = "Yes, what is it?", completion = "What is it you have there, &lt;name&gt;? A scarf made of... Mooncloth? You best not be seeking to romance me.", rewards = "You will receive:\n\n"},
[76156] = {title = "Stalk With The Earthmother", objective = "Sneak into the Venture Company mine southeast of Thunder Bluff and collect 5 Seaforium Mining Charges from carts of blasting supplies, then return them to Boarton Shadetotem in Thunder Bluff.", description = "I sense in you a hidden talent for the subtle arts, shaman.  Use this mask to infilrate the nearby Venture Company mine and steal their supplies.  It may not seem like a convincing disguise, but a skilled rogue can slip past any guard with naught but a winning smile and a cunning distraction.  I believe this capability lies within you as well.", progress = "We'll need to work on your stealth skills, <class>.  I heard you approaching as soon as you set foot on the mesa.", completion = "Hmm... perhaps disguises are not your strength.", rewards = "You will receive:\n\n"},
[76160] = {title = "Stalk With The Earthmother", objective = "Gather 8 Windfury Cones north of Thunder Bluff, use them with the Mortar and Pestle, then return to Boarton Shadetotem in Thunder Bluff with the result.", description = "I speak to you from your future, when the Horde faces threats you cannot imagine.  Since the others cannot see me, I rely on you to learn my skills and pass them to the tribe.\n\nMaybe your skills lie with poisons, not disguises.  The Harpies to the north nest in a tree which produces a resin of potent toxicity.  Gather the fallen cones around the nests, then crush them into a salve.  When applied to a blade this salve will sear your opponent's wounds into their flesh and turn their blood to salt.", progress = "Be careful with the salve once you are finished, <class>.  Even skin contact could be quite painful.", completion = "&lt;class&gt;, this is not the venomous salve I asked for.  What you have brought me is pine nut butter.  Are you sure you followed my instructions?\n\nThis will be of far more use spread on some spice bread than on a dagger.  Although you would be quite formidable against a foe with a nut allergy.", rewards = "You will receive:\n\nPine Nut Butter"},
[76240] = {title = "Stalk With The Earthmother", objective = "Buy or catch a Raw Brilliant Smallfish, then use the Knife Set to fillet it in front of Boarton Shadetotem in Thunder Bluff.", description = "I have one last idea that may lead us to success.  I still feel there is hidden talent in you, but let us try and coax it out with a less direct approach.\n\nGo and procure a Raw Brilliant Smallfish, then bring it back here.  Use these daggers to clean and fillet it.  Do not think too hard - do what comes naturally.", progress = "Do you have something for me?", completion = "That was quite a display, &lt;class&gt;.  Not the rogueish precision I hoped for, but you possess an impressive ambidexterity.\n\nI see now that you will not be the one to train my people in the subtle arts, but there is one thing I can teach you.  Good luck on your travels, &lt;class&gt;.  The Horde will need heroes like you.", rewards = "You will learn:\n\nDual Wield\n\nThe following spell will be cast on you:\n\nEngrave Gloves - Lava Lash\n\nYou will also receive:\n\n"},
[77568] = {title = "A Hunter's Strength", objective = "Defeat Githyiss the Vile in Shadowthread Cave and recover the lost rune. Learn the rune, then report back to Ayanna Everstride in Shadowglen.", description = "A hunter draws strength from their connection and respect for all creatures, not just their pets. You will hunt and slay many beasts in service to the Alliance, but even in battle you must respect your foe.\n\nMy previous apprentice used a mystical rune to augment her skills, but foolishly attempted to practice on the largest of the spiders in the cave to the north, a creature we have named Githyiss the Vile. Her disrespect led to her demise.\n\nSlay Githyiss - but do not underestimate its strength.", progress = "When you have slain Githyiss, recover the rune and learn its magic.", completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nCracked Leather Gloves"},
[77571] = {title = "Relics of the Kaldorei", objective = "Recover the relic from the grellkin in Shadowglen. Follow the relic's guidance to learn a new ability, then report back to Mardant Strongoak.", description = "The kaldorei are an ancient people, and our lost relics can be found across Azeroth. Through these relics, the spirits can teach you ways to strengthen your connection to the Dream.\n\nThe grellkin have stolen one such relic. Slay them and retrieve the relic, then follow its guidance to learn a new combat technique. Return to me when you have done this.", progress = "What has the relic taught you?", completion = "Well done, &lt;name&gt;. Applying the rune you have learned to your equipment will grant you powerful new abilities.\n\nSeek out more of our lost relics along your travels.", rewards = "You will receive:\n\nCracked Leather Vest"},
[77573] = {title = "Second-Story Work", objective = "Retrieve the rune from the hidden idol, then use it to learn a new ability. Afterwards, return to Frahun Shadewhisper.", description = "I have prepared a training exercise for you, <name>. There is an idol of an owl on the roof of this building. You should be able to see it from the south entrance. Inside the idol is a hidden compartment containing an engraved rune.\n\nMake your way to the roof, find and memorize the rune hidden in the idol, and return to me.", progress = "Have you found the rune?", completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nCracked Leather Gloves"},
[77574] = {title = "Meditation on Elune", objective = "Kneel at the moonwell to meditate on Elune, then use the memory to learn a new ability. Afterwards, return to Shanda in Shadowglen.", description = "Joining the Alliance has strengthened our people, but we must not forget that we are Kaldorei. Our connection to Elune is at the core of our being.\n\nClear your mind, <name>, and focus on my voice. I will share with you an ancient memory of our kind. Go to the moonwell to the north and kneel in the sacred waters to meditate on Elune's mysteries. Once you have focused your thoughts, you will be able to make sense of this memory and learn a new technique. Return to me when you have done this.", progress = nil, completion = "Elune will likely grant you more memories along your travels. You can meditate at the moonwells across Azeroth to clear your thoughts and interpret these memories.\n\nUnderstanding more complex visions may require the help of our fellow priests from the Alliance. Do not be afraid to learn from their faith, &lt;name&gt;. Tyrande teaches that the Holy Light worshipped by the humans and the Light of Elune are closely related.", rewards = "You will receive:\n\nTattered Cloth Gloves"},
[77582] = {title = "A Trial of Fitness", objective = "Find the Hidden Cache in the cliffs behind Frang. Learn the rune inside, then report back to Frang in the Valley of Trials.", description = "Above all else, a warrior of the Horde must be of strong body. On your journey, you will undergo many trials of stamina and endurance. \n\nTo this end, I have hidden a cache among the cliffs behind me. You can see it if you stand by the fire in the center of the village. Reaching it will be no easy feat. Within the cache is a rune of great power.\n\nYour test is to find this rune, and return when you have learned its secrets.", progress = "Have you found the rune, <name>?", completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nTarnished Chain Gloves"},
[77583] = {title = "Atop the Cliffs", objective = "Find the Hidden Cache in the cliffs surrounding the Valley of Trials. Learn the rune inside, then report back to Rwag in The Den.", description = "I have prepared a training exercise for you, <name>. I have hidden a cache among the cliffs nearby. You can see it if you stand by the fire in the center of the village. Within the cache is a rune of great power.\n\nYour test is to acquire this rune, and return when you have learned its secrets.", progress = nil, completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nCracked Leather Gloves"},
[77583] = {title = "Atop the Cliffs", objective = "Find the Hidden Cache in the cliffs surrounding the Valley of Trials. Learn the rune inside, then report back to Rwag in The Den.", description = "I have prepared a training exercise for you, <name>. I have hidden a cache among the cliffs nearby. You can see it if you stand by the fire in the center of the village. Within the cache is a rune of great power.\n\nYour test is to acquire this rune, and return when you have learned its secrets.", progress = nil, completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nCracked Leather Gloves"},
[77584] = {title = "Hunt for the Rune", objective = "Find the lost rune by slaying scorpids in the Valley of Trials. Learn the rune's power, then report back to Jen'sahn.", description = "A hunter draws strength from their connection to the natural world. On your journey, you will track quarry through all manner of harsh terrain, surviving only by your wits.\n\nNot long ago, another fledgling hunter sadly failed in this. He went out to hunt scorpids, and never returned. He was experimenting with engraving mystical runes onto his equipment to improve his combat prowess.\n\nFind his rune among the scorpids nearby, and return when you have learned its secrets.", progress = nil, completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nCracked Leather Gloves"},
[77585] = {title = "Icons of Power", objective = "Find the stolen icon by slaying scorpids in the Valley of Trials. Learn its power, then report back to Shikrik.", description = "Shamanism is an ancient practice, <name>. Many artifacts, full of spiritual energy, can be found across Azeroth. I had recently discovered an icon of notable power myself, but it was stolen by those pesky scorpids.\n\nOne of them is likely carrying it around, thinking it a shiny curio to be used as a decoration for their nest. \n\nWhen you find the icon, learn its power and return to me.", progress = "Have you found the icon, <name>?", completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nCracked Leather Vest"},
[77586] = {title = "Stolen Power", objective = "Find the stolen rune inside of the Burning Blade Coven. Learn its power, then report back to Nartok in The Den.", description = "I've been experimenting with a new type of magic - a rune which can channel fel energies to fuel powerful spells. I would share this power with you, but it was stolen by one of those pesky Burning Blade familiars.\n\nGo to their coven, to the north, and see if you can find my rune stashed among their possessions. In my experience, the familiars tend to hide their plunder in hard to find places. \n\nOnce you find the rune, learn its magic and report back to me. I would like to see my experiment put to us", progress = "Have you found the rune, <name>?", completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nTattered Cloth Gloves"},
[77587] = {title = "Icons of Power", objective = "Find the stolen icon by slaying scorpids in the Valley of Trials. Learn its power, then report back to Shikrik.", description = "Shamanism is an ancient practice, <name>. Many artifacts, full of spiritual energy, can be found across Azeroth. I had recently discovered an icon of notable power myself, but it was stolen by those pesky scorpids.\n\nOne of them is likely carrying it around, thinking it a shiny curio to be used as a decoration for their nest. \n\nWhen you find the icon, learn its power and return to me.", progress = "Have you found the icon, <name>?", completion = "Well done, &lt;name&gt;. Applying the rune to your equipment will grant you powerful new abilities.\n\nThere are many such runes throughout Azeroth. Seek them out, as they will help along your journey.", rewards = "You will receive:\n\nCracked Leather Vest"},
