[407627] = {name = "Furia recta", additional_info = "InstanteRequiere Paladín\n[q]Aumenta la amenaza generada por tus hechizos Sagrados un 80% y, cuando recibes una sanación de hechizos de objetivos amistosos, obtienes una cantidad de maná equivalente a un 25% de la cantidad sanada. Además, reduce un 20% todo el daño recibido que reduzca tu salud por debajo de un 35%. Dura until cancelled."},
[407628] = {name = "Furia recta", additional_info = "Instante\n[q]Se obtiene maná de la sanación recibida."},
[407631] = {name = "Mano de expiación", additional_info = "Alcance de ataques cuerpo a cuerpoInstante 10 seg de reutilización\nRequiere Paladín\n[q]Provoca al objetivo para que te ataque, pero no tiene efecto si ya te está atacando.\n\n Cuando conoces esta facultad, el bonus de amenaza de Furia recta aumenta un 80% y Furia recta hace que, al recibir una sanación, obtengas una cantidad de maná equivalente a un 25% de la cantidad sanada. Además, mientras Furia recta está activa, reduce un 35% todo el daño recibido que reduzca tu salud por debajo de un 20%. Furia recta permanecerá activa hasta que se cancele."},
[407653] = {name = "Relámpago bifurcado", additional_info = "Alcance de 30 m\nInstante\n[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a otro enemigo cercano. Afecta a un máximo de 0 objetivos e inflige daño de Naturaleza a cada uno."},
[407667] = {name = "Relámpago bifurcado", additional_info = "Alcance de 30 m\nInstante\n[q]Golpea al enemigo con una Descarga de relámpagos que se extiende a todos los objetivos cercanos y les inflige daño de Naturaleza."},
[407669] = {name = "Escudo de vengador", additional_info = "26% del maná base Alcance de 30 m\nInstante 30 seg de reutilización\nRequiere Paladín\n[q]Lanza un escudo Sagrado al enemigo, inflige (366 */ 100 + 0.091 * Spell power + 0.091 * Attack power)-(448 */ 100 + 0.091 * Spell power + 0.091 * Attack power) p. de daño Sagrado y lo deja atontado. A continuación, salta a los enemigos cercanos. Afecta a 3 objetivos en total. Dura 10 sec."},
[407676] = {name = "Golpe de cruzado", additional_info = "Alcance de ataques cuerpo a cuerpoInstante 6 seg de reutilización\nRequiere Paladín\nRequiere Arma cuerpo a cuerpo\n[q]Un golpe instantáneo que inflige un 75% de daño con arma y regenera un 5% de tu maná máximo."},
