[407778] = {name = "Tormenta divina", additional_info = "12% del maná baseInstante 10 seg de reutilización\nRequiere Paladín\nRequiere Arma cuerpo a cuerpo\n[q]Un ataque instantáneo con arma que inflige un 110% de daño con arma hasta un total de 4 enemigos que se encuentren en un radio de 8 m. La Tormenta divina sana a hasta 3 miembros del grupo o banda con un total del 25% del daño infligido."},
[407782] = {name = "Tormenta divina", additional_info = "Alcance de 100 m\nInstanteRequiere Paladín\n[q]El daño infligido con Tormenta divina sana a los objetivos amistosos."},
