[407798] = {name = "Sello de martirio", additional_info = "4% del maná base\nInstanteRequiere Paladín\n[q]Te colma con el espíritu sagrado durante 30 sec, lo que hace que tus ataques cuerpo a cuerpo inflijan un 40% de daño con arma a tu objetivo, pero pierdes una cantidad de salud equivalente a un 10% del daño infligido. Mientras el sello está activo, los miembros de tu grupo y banda que se encuentran en un radio de 40 m ganan una cantidad de maná equivalente a un 20% del daño que recibes de este sello.\n\n Al liberar la energía de este sello, un enemigo resulta juzgado y recibe al instante un 70% de daño con arma con un coste de salud equivalente al 10% del daño infligido."},
[407799] = {name = "Sello de martirio", additional_info = nil},
[407800] = {name = "Sello de martirio", additional_info = "InstanteRequiere Paladín\n[q]El daño de Sello de martirio también inflige daño al paladín."},
[407802] = {name = "Sello de martirio", additional_info = "Alcance de 40 m\nInstante\n[q]Se gana maná a partir del daño que recibe el paladín."},
[407803] = {name = "Sentencia de martirio", additional_info = nil},
[407804] = {name = "Sacrificio divino", additional_info = "Instante 2 min de reutilización\nRequiere Paladín\n[q]Un 30% de todo el daño recibido por los miembros del grupo en un radio de 30 m se redirige al paladín durante 10 sec. El daño que reduzca la salud del paladín por debajo del 20% romperá el efecto y aumentará un 10% el daño infligido y la sanación realizada durante 10 sec. No puedes usar Sacrificio divino si estás bajo los efectos de Bendición de protección, Escudo divino o Protección divina y además evita que puedas ser objetivo de dichas facultades mientras el efecto está activo."},
[407805] = {name = "Sacrificio de redención", additional_info = "Instante 3 min de reutilización\nRequiere Paladín\n[q]El daño y la sanación aumentan tras tu sacrificio."},
[407811] = {name = "Rajar", additional_info = "Alcance de ataques cuerpo a cuerpo\nSiguiente cuerpo a cuerpo\n[q]Inflige daño con arma más 23 to 27 p. a un enemigo y a sus aliados cercanos. Afecta hasta a 3 objetivos."},
