[407973] = {name = "Golpe descontrolado", additional_info = "Alcance de 100 m\nInstante\n[q]Ataque extra."},
[407975] = {name = "Golpes descontrolados", additional_info = "Instante\n[q]Los miembros del grupo en un radio de 20 m obtienen mayor ferocidad en combate. Cada golpe cuerpo a cuerpo tiene una probabilidad del 20% de otorgar al atacante un ataque extra con un 20% de poder de ataque adicional. No tiene efecto si el miembro de grupo ya se está beneficiando de Tótem Viento Furioso.\n(Probabilidad de que accione: 20%, 1.5s de reutilización)"},
[407977] = {name = "Golpes descontrolados", additional_info = "[q]Mientras estás en forma felina, forma de oso o forma de oso temible, los miembros del grupo en un radio de 20 m obtienen mayor ferocidad en combate. Cada golpe cuerpo a cuerpo tiene una probabilidad del 20% de otorgar al atacante un ataque extra con un 20% de poder de ataque adicional. No tiene efecto si el miembro de grupo ya se está beneficiando de Tótem Viento Furioso."},
[407988] = {name = "Rugido salvaje", additional_info = "25 Energía Alcance de 100 m\nInstante\n[q]Remate que aumenta el daño físico infligido en forma felina un 30%. Se prolonga por cada punto de combo:\n 1 p.: 14 s\n 2 p.: 19 s\n 3 p.: 24 s\n 4 p.: 29 s\n 5 p.: 34 s"},
[407989] = {name = "Rugido salvaje", additional_info = "Instante\nRequiere Forma felina\n[q]Remate que aumenta el daño físico infligido un 30%. Se prolonga por cada punto de combo:\n 1 p.: 14 s\n 2 p.: 19 s\n 3 p.: 24 s\n 4 p.: 29 s\n 5 p.: 34 s"},
[407993] = {name = "Destrozar", additional_info = "Destrozar Felino\n40 Energía Alcance de ataques cuerpo a cuerpo\nInstante\nRequiere Forma felina\n[q]Destroza al enemigo, le inflige un 270% de daño normal y hace que el objetivo reciba un 30% de daño extra de los efectos de sangrado y de Triturar durante 1 min. Otorga 1 punto de combo. Esta facultad se beneficia de todos los efectos relacionados con Zarpa y Magullar y también los activa."},
[407995] = {name = "Destrozar", additional_info = "Destrozar Oso\n15 Ira Alcance de ataques cuerpo a cuerpo\nHechizo instantáneo 6 seg de reutilización\nRequiere Forma de oso, Forma de oso nefasto\n[q]Destroza al enemigo, le inflige un 160% de daño normal y hace que el objetivo reciba un 30% de daño extra de los efectos de sangrado y de Triturar durante 1 min. Esta facultad se beneficia de todos los efectos relacionados con Zarpa y Magullar y también los activa."},
