[408120] = {name = "Crecimiento salvaje", additional_info = "56% del maná base Alcance de 40 m\nInstante 6 seg de reutilización\n[q]Sana (34 / 100 * 7 *) p. a todos los miembros del grupo del jugador objetivo en un radio de 40 m durante 7 sec. La cantidad sanada se aplica con rapidez al principio y va disminuyendo a medida que surte efecto Crecimiento salvaje."},
[408124] = {name = "Flor de vida", additional_info = "28% del maná base Alcance de 40 m\nInstante\n[q]Sana al objetivo (4 / 100 * 7 *) p. durante 7 sec. Cuando Flor de vida completa su duración o se disipa, el objetivo se sana (57 / 100 *) p., y el druida recupera la mitad del coste del hechizo. Este efecto puede acumularse hasta 3 veces sobre el mismo objetivo."},
[408245] = {name = "Flor de vida", additional_info = "Alcance de 40 m\nInstante\n[q]Sana al objetivo durante 7 s. Cuando Flor de vida completa su duración o se disipa, el objetivo se sana de forma instantánea. Este efecto puede acumularse hasta 3 veces sobre el mismo objetivo."},
