[408309] = {name = "Gracia de Elune", additional_info = "Instante 2 min de reutilización\n[q]Aumenta tu daño con hechizos el equivalente a un 55% de la diferencia entre tu poder de sanación y tu daño con hechizos. Dura 30 sec."},
[408438] = {name = "Sobrecarga", additional_info = "[q]Otorga a tus hechizos Descarga de relámpagos, Cadena de relámpagos, Sanación en cadena, Ola de sanación y Ráfaga de lava un 50% de probabilidad de lanzar un segundo hechizo similar sobre el mismo objetivo sin coste extra, que inflige la mitad de daño o sanación y no genera amenaza.\n(Probabilidad de que accione: 50%)"},
