[408510] = {name = "Escudo de agua", additional_info = "Instante\n[q]El taumaturgo queda rodeado por 3 esferas de agua que otorgan un 1% del maná máximo cada 5 s. Cada hechizo o ataque cuerpo a cuerpo o a distancia que golpee al taumaturgo restaura un 4% del maná máximo y consume una de las esferas. Solo se activará una esfera cada varios segundos. Dura 10 min. Un chamán solo puede tener un Escudo elemental activo a la vez.\n(3.5s de reutilización)"},
[408511] = {name = "Escudo de agua", additional_info = "Instante\n[q]Obtiene maná si recibe un golpe con Escudo de agua activo."},
[408514] = {name = "Escudo de tierra", additional_info = "15% del maná base Alcance de 40 m\nInstante\n[q]Protege al objetivo con un escudo de tierra que reduce el tiempo perdido de lanzamiento o canalización cuando recibe daño un 30% y hace que los ataques sanen al objetivo escudado 100 p. Este efecto solo puede tener lugar cada pocos segundos. 3 cargas. Dura 10 min. Escudo de tierra solo puede utilizarse con un objetivo a la vez y solo puede haber un escudo elemental activo a la vez sobre el objetivo.\n(3.5s de reutilización)"},
[408519] = {name = "Escudo de tierra", additional_info = "Instante\n[q]Sanado por Escudo de tierra."},
[408524] = {name = "Maestría con escudos", additional_info = "[q]Cada vez que bloqueas, regeneras una cantidad de maná equivalente a un 8% de tu maná máximo y obtienes armadura equivalente a un 30% del valor de armadura de tu escudo. Se acumula hasta 5 veces. Además, siempre aumentas un 10% la probabilidad de bloqueo y un 15% el valor de bloqueo."},
[408525] = {name = "Maestría con escudos", additional_info = "Instante\n[q]Aumenta la armadura un 30% del valor de armadura de tu escudo durante 15 sec. Se acumula hasta 5 veces."},
[408531] = {name = "Camino de la tierra", additional_info = "[q]Cuando Arma Muerdepiedras está activa en el arma de la mano principal, generas un 50% más de amenaza, obtienes un 30% más de salud, recibes un 10% menos de daño, reduces un 6% la probabilidad de recibir golpes críticos por ataques cuerpo a cuerpo, y Choque de tierra provoca a los objetivos para que te ataquen. Además, su tiempo de reutilización es independiente del resto de hechizos de choque, pero el alcance se reduce a cuerpo a cuerpo."},
[408680] = {name = "Camino de la tierra", additional_info = "Instante\n[q]Cuando Arma Muerdepiedras está activa en el arma de la mano principal, generas un 50% más de amenaza, obtienes un 30% más de salud, recibes un 10% menos de daño, reduce un 6% la probabilidad de recibir golpes críticos por ataques cuerpo a cuerpo y Choque de tierra provoca a los objetivos para que te ataquen. Además, su tiempo de reutilización es independiente del resto de hechizos de choque."},
[408681] = {name = "Choque de tierra", additional_info = "30 Maná Alcance de ataques cuerpo a cuerpo\nInstante 6 seg de reutilización\n[q]Choque instantáneo con fuerza de conmoción que inflige 19 to 22 p. de daño de Naturaleza al objetivo. También interrumpe el lanzamiento de hechizos e impide que se lance cualquier hechizo de esa misma escuela durante 2 sec. Genera una gran cantidad de amenaza y provoca al objetivo."},
