[409428] = {name = "Maestro tirador", additional_info = "[q]Aumenta 5% tu probabilidad de golpe crítico y reduce un 25% el coste de maná de todas tus facultades de disparo."},
[409433] = {name = "Disparo de quimera", additional_info = "6% del maná base Alcance de 8 - 35 m\n6 seg de reutilización\nRequiere Arma a distancia\n[q]Infliges un 100% de daño con arma, reinicias la Picadura actual sobre tu objetivo y activas un efecto:\n\n Picadura de serpiente: inflige instantáneamente un 40% del daño infligido por tu Picadura de serpiente.\n\n Picadura de víbora: te restaura instantáneamente el equivalente a un 60% de la cantidad de maná drenada por tu Picadura de víbora.\n\n Picadura de escórpido: intenta desarmar al objetivo durante 10 sec. Este efecto no puede tener lugar más de una vez por minuto."},
[409493] = {name = "Disparo de quimera: serpiente", additional_info = "Alcance de 100 m\n[q]Infliges un 100% de daño con arma, reinicias la Picadura actual sobre tu objetivo y activas un efecto:\n\n Picadura de serpiente: inflige instantáneamente un 40% del daño infligido por tu Picadura de serpiente.\n\n Picadura de víbora: te restaura instantáneamente el equivalente a un 60% de la cantidad de maná drenada por tu Picadura de víbora.\n\n Picadura de escórpido: intenta desarmar al objetivo durante 10 sec. Este efecto no puede tener lugar más de una vez por minuto."},
[409494] = {name = "Disparo de quimera: víbora", additional_info = "Alcance de 100 m\n[q]Infliges un 100% de daño con arma, reinicias la Picadura actual sobre tu objetivo y activas un efecto:\n\n Picadura de serpiente: inflige instantáneamente un 40% del daño infligido por tu Picadura de serpiente.\n\n Picadura de víbora: te restaura instantáneamente el equivalente a un 60% de la cantidad de maná drenada por tu Picadura de víbora.\n\n Picadura de escórpido: intenta desarmar al objetivo durante 10 sec. Este efecto no puede tener lugar más de una vez por minuto."},
[409495] = {name = "Disparo de quimera: escórpido", additional_info = "Alcance de 100 m\n[q]Infliges un 100% de daño con arma, reinicias la Picadura actual sobre tu objetivo y activas un efecto:\n\n Picadura de serpiente: inflige instantáneamente un 40% del daño infligido por tu Picadura de serpiente.\n\n Picadura de víbora: te restaura instantáneamente el equivalente a un 60% de la cantidad de maná drenada por tu Picadura de víbora.\n\n Picadura de escórpido: intenta desarmar al objetivo durante 10 sec. Este efecto no puede tener lugar más de una vez por minuto."},
[409510] = {name = "Trampa congelante", additional_info = "50 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Escarcha en una ubicación cercana que congela al primer enemigo que se acerca. El objetivo no podrá realizar ningún tipo de acción durante un máximo de 10 sec. Cualquier daño infligido romperá el hielo. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409512] = {name = "Trampa congelante", additional_info = "75 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Escarcha en una ubicación cercana que congela al primer enemigo que se acerca. El objetivo no podrá realizar ningún tipo de acción durante un máximo de 15 sec. Cualquier daño infligido romperá el hielo. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409519] = {name = "Trampa congelante", additional_info = "100 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Escarcha en una ubicación cercana que congela al primer enemigo que se acerca. El objetivo no podrá realizar ningún tipo de acción durante un máximo de 20 sec. Cualquier daño infligido romperá el hielo. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409520] = {name = "Trampa de Escarcha", additional_info = "60 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Escarcha en una ubicación cercana que crea una mancha de hielo durante 30 sec cuando el primer enemigo se acerca. Todos los enemigos en un radio de 10 m se ralentizarán un 60% mientras estén en el área de efecto. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409521] = {name = "Trampa de inmolación", additional_info = "50 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que quemará al primer enemigo que se acerque y le infligirá 105 p. de daño de Fuego durante 15 sec. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409524] = {name = "Trampa de inmolación", additional_info = "90 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que quemará al primer enemigo que se acerque y le infligirá 215 p. de daño de Fuego durante 15 sec. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409526] = {name = "Trampa de inmolación", additional_info = "135 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que quemará al primer enemigo que se acerque y le infligirá 340 p. de daño de Fuego durante 15 sec. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409528] = {name = "Trampa de inmolación", additional_info = "190 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que quemará al primer enemigo que se acerque y le infligirá 510 p. de daño de Fuego durante 15 sec. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409530] = {name = "Trampa de inmolación", additional_info = "245 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que quemará al primer enemigo que se acerque y le infligirá 690 p. de daño de Fuego durante 15 sec. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409532] = {name = "Trampa explosiva", additional_info = "275 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que explota cuando un enemigo se acerca, inflige 104 to 135 p. de daño de Fuego y 150 p. de daño de Fuego más durante 20 sec a todos los que estén a 10 m. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409534] = {name = "Trampa explosiva", additional_info = "395 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que explota cuando un enemigo se acerca, inflige 145 to 193 p. de daño de Fuego y 240 p. de daño de Fuego más durante 20 sec a todos los que estén a 10 m. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
[409535] = {name = "Trampa explosiva", additional_info = "520 Maná Alcance de 35 m\nInstante 15 seg de reutilización\n[q]Coloca una trampa de Fuego en una ubicación cercana que explota cuando un enemigo se acerca, inflige 208 to 265 p. de daño de Fuego y 330 p. de daño de Fuego más durante 20 sec a todos los que estén a 10 m. La trampa dura 1 min. Solo puede haber una trampa activa a la vez."},
