[415460] = {name = "Consunción", additional_info = "[q]Consigues la facultad Consunción:\n\n\nAumenta tu probabilidad de golpe crítico con todos los hechizos un 15%, pero ahora tus golpes críticos con hechizos no periódicos tienen un coste de maná extra igual al 1% de tu maná base."},
[415467] = {name = "Regeneración en masa", additional_info = "[q]Consigues la facultad Regeneración en masa:\n\n\nSana (* 42 / 100 * 3) p. de salud a todos los miembros del grupo del jugador objetivo en un radio de 30 m durante 3 sec y aplica Baliza temporal a cada objetivo durante 15 s.\n\n Baliza temporal\n\nRegistra la posición espaciotemporal del sujeto. Un 80% de todo el daño Arcano infligido por el taumaturgo se convierte en sanación cronomántica sobre cada uno de los objetivos actuales de su Baliza temporal. La sanación se reduce un 50% sobre uno mismo, y un 80% cuando se inflige daño con hechizos Arcanos que afectan a varios objetivos."},
[415599] = {name = "Furia enfocada", additional_info = "[q]Consigues la facultad Furia enfocada:\n\n\nCon la doble empuñadura, tu daño físico y tu velocidad de movimiento aumentan un 10%."},
[415604] = {name = "Aflicción eterna", additional_info = "[q]Consigues la facultad Aflicción eterna:\n\n\nDrenar vida, Drenar alma, Descarga de las Sombras, Cuchilla de las Sombras, Dolor abrasador, Incinerar y Poseer restablecen la duración de tu Corrupción en el objetivo."},
[415605] = {name = "Tácticas demoníacas", additional_info = "[q]Consigues la facultad Tácticas demoníacas:\n\n\nAumenta un 10% tu probabilidad de golpe crítico cuerpo a cuerpo y tu probabilidad de golpe crítico con hechizos, así como las de tu mascota."},
[415709] = {name = "Supervivencia del más fuerte", additional_info = "[q]Consigues la facultad Supervivencia del más fuerte:\n\n\nReduce la probabilidad de recibir un golpe crítico en ataques cuerpo a cuerpo un 6% y reduce todo el daño que recibes un 10%. Daño recibido reducido un 10% más mientras estás en forma de oso o forma de oso temible."},
[415714] = {name = "Lluvia de sanación", additional_info = "[q]Consigues la facultad Lluvia de sanación:\n\n\nSelecciona un área de 15 m alrededor del jugador seleccionado y sana (* 15 / 100) p. cada segundo a todos los miembros de su grupo que estén en su interior."},
[415729] = {name = "Esclarecimiento", additional_info = "[q]Consigues la facultad Esclarecimiento:\n\n\nInfliges un 10% más de daño si tienes más de un 70% de maná. Mientras tu maná está por debajo del 30%, un 10% de la regeneración de maná continúa al lanzar hechizos."},
[415737] = {name = "Serendipia", additional_info = "[q]Consigues la facultad Serendipia:\n\n\nSanar con Sanación relámpago reduce el tiempo de lanzamiento de tu próximo Sanación inferior, Sanar, Sanación superior o Rezo de sanación un 20% durante 20 sec. Se acumula hasta 3 veces."},
[415738] = {name = "Abrasamiento mental", additional_info = "[q]Consigues la facultad Abrasamiento mental:\n\n\nProvoca una deflagración de magia de las Sombras alrededor del objetivo enemigo, lo que inflige de (70 / 100 *) a (78 / 100 *) p. de daño de las Sombras cada 1 s durante 5 sec a todos los enemigos en un radio de 10 m alrededor del objetivo."},
