[698] = {name = "Rituel d'invocation", additional_info = "Mana : 300 30 m de portée\n5 s d'incantation\nComposants:\nFragment d'âme\n[q]Entame un rituel qui invoque le membre du groupe ciblé. Nécessite la présence du lanceur de sorts et deux membres du groupe supplémentaires pour mener le rituel à son terme. Pour y prendre part, les joueurs doivent être hors combat, cliquer avec le bouton droit de la souris sur le portail et ne pas bouger tant que le rituel n'est pas terminé."},
[699] = {name = "Drain de vie", additional_info = "Mana : 85 20 m de portée\nCanalisée (5 s d'incantation)\n[q]Transfère 17 points de vie de la cible vers le lanceur de sorts toutes les secondes. Dure 5 sec."},
[700] = {name = "Sommeil", additional_info = "Mana : 60 30 m de portée\n1.5 s d'incantation\n[q]Endort la cible ennemie pour un maximum de 20 sec. Si la cible subit des dégâts, elle est réveillée. Vous ne pouvez endormir qu'une cible à la fois."},
