[1510] = {name = "Salve", additional_info = "Mana : 350 8 - 35 m de portée\nCanalisée (6 s d'incantation) 1 min de recharge\nRequiert Arme à distance\n[q]Tire une salve sur la zone ciblée. Inflige 50 points de dégâts des Arcanes aux cibles ennemies qui se trouvent dans cette zone de 8 mètres de rayon toutes les secondes, pendant 6 sec."},
[1513] = {name = "Effrayer une bête", additional_info = "Mana : 35 10 m de portée\n1.5 s d'incantation 30 s de recharge\n[q]Effraie une bête et l'oblige à fuir pendant 10 sec, d'éventuels dégâts abrègent les effets du sort. On ne peut effrayer qu'une seule bête à la fois."},
[1515] = {name = "Apprivoise une bête", additional_info = "60% de la mana de base 30 m de portée\nCanalisée (20 s d'incantation)\n[q]Apprivoise une bête pour qu'elle devienne votre compagnon. Votre armure est réduite de 100% pendant que vous vous focalisez sur la bête pendant 20 sec. Si vous perdrez l'attention de la bête pour une quelconque raison, le processus échoue. Une fois domptée la bête sera mécontente et déloyale. Nourrissez-la immédiatement pour la rendre heureuse."},
[1516] = {name = "Tir réflexe", additional_info = "5 - 30 m de portée\n1 min de recharge\nRequiert Arme à distance\n[q]Déclenche instantanément votre arme et inflige 5 points de dégâts supplémentaires."},
[1528] = {name = "Libérer le Maître", additional_info = "5 m de portée\nInstantanée\n[q]Libère votre maître de tous ses liens."},
