[14514] = {name = "Transfert", additional_info = "Mana : 75\nInstantanée\n[q]Téléporte le lanceur de sorts 20 mètres vers l'avant, à moins qu'un objet se trouve sur son chemin. Le lanceur de sorts est libéré de tout emprisonnement."},
[14515] = {name = "Emprise", additional_info = "Mana : 30 20 m de portée\n2 s d'incantation\n[q]Prend le contrôle d'un ennemi humanoïde de niveau 25 ou inférieur, pendant 15 sec."},
[14516] = {name = "Frappe", additional_info = "Allonge\nProchaine attaque en mêlée\nRequiert Arme de mêlée\n[q]Frappe un ennemi et lui inflige des dégâts de mêlée augmentés."},
[14517] = {name = "Inquisition", additional_info = "Mana : 40 Allonge\nInstantanée\nRequiert Arme de mêlée\n[q]Inflige 27 points de dégâts à un ennemi et augmente les points de dégâts du Sacré qu'il subit de 10 par attaque Inquisition. Peut être appliquée jusqu'à 5 fois. Dure 30 sec."},
[14518] = {name = "Inquisition", additional_info = "Mana : 40 Allonge\nInstantanée\nRequiert Arme de mêlée\n[q]Inflige 50 points de dégâts à un ennemi et augmente les points de dégâts du Sacré qu'il subit de 20 par attaque Inquisition. Peut être appliquée jusqu'à 5 fois. Dure 30 sec."},
[14520] = {name = "Sagacité", additional_info = "[q]Réduit le coût en mana de vos sorts instantanés de 2%."},
[14521] = {name = "Méditation", additional_info = "[q]Vous confère 5% de votre vitesse de récupération du mana normale pendant l'incantation."},
[14522] = {name = "Volonté inflexible", additional_info = "[q]Augmente vos chances de résister aux effets d'étourdissement, de peur et de silence de 3%."},
[14523] = {name = "Résolution silencieuse", additional_info = "[q]Diminue le niveau de menace généré par vos sorts de 4%."},
[14524] = {name = "Spécialisation Baguette", additional_info = "[q]Augmente les dégâts que vous infligez avec les baguettes de 5%."},
[14525] = {name = "Spécialisation Baguette", additional_info = "[q]Augmente les dégâts que vous infligez avec les baguettes de 10%."},
[14526] = {name = "Spécialisation Baguette", additional_info = "[q]Augmente les dégâts que vous infligez avec les baguettes de 15%."},
[14527] = {name = "Spécialisation Baguette", additional_info = "[q]Augmente les dégâts que vous infligez avec les baguettes de 20%."},
[14528] = {name = "Spécialisation Baguette", additional_info = "[q]Augmente les dégâts que vous infligez avec les baguettes de 25%."},
[14530] = {name = "Vitesse", additional_info = "Instantanée\n[q]Augmente la vitesse de course de 40% pendant 10 sec."},
[14531] = {name = "Martyre", additional_info = "[q]Vous confère 50% de chances de bénéficier de l'effet Incantation focalisée pendant 6 sec après avoir été victime d'un coup critique en mêlée ou à distance. Cet effet vous évite, lors de l'incantation d'un sort, d'être interrompu lorsque vous subissez des dégâts. Il augmente aussi la résistance aux effets d'interruption de 10%.\n(Chance d'occurence :50%)"},
[14532] = {name = "Venin de rampant", additional_info = "2 s d'incantation\n[q]Augmente les points de dégâts physiques subis par les ennemis qui se trouvent à proximité de 30% pendant 5 min."},
[14533] = {name = "Disjonction", additional_info = "Instantanée\n[q]Augmente les points de dégâts de Feu, de givre et des Arcanes que les ennemis proches subissent de 50% pendant 5 min."},
[14534] = {name = "Dard acéré", additional_info = "Instantanée\n[q]Augmente le temps entre les attaques des ennemis proches de 54% pendant 5 min."},
[14535] = {name = "Maladie térébrante", additional_info = "Instantanée\n[q]Réduit toutes les caractéristiques des ennemis qui se trouvent à proximité de 25% pendant 5 min."},
[14537] = {name = "Sac des six démons", additional_info = "30 m de portée\nInstantanée\n[q]Frappe les ennemis se trouvant devant vous avec la puissance du vent, du feu et de toutes ces choses !"},
[14538] = {name = "Horion sonore", additional_info = "Instantanée\n[q]Réduit la vitesse d'incantation de tous les ennemis proches de 35% pendant 5 min."},
[14539] = {name = "Enzyme putride", additional_info = "Instantanée\n[q]Augmente les points de dégâts de Nature subis par les ennemis proches de 50% et augmente de 50% les points de dégâts d'ombre qu'ils subissent. Dure 5 min."},
