[381599] = {name = "Invocation d’un palefroi squelettique", additional_info = "3 s d'incantation\n[q]Invoque et renvoie un palefroi squelettique, qui obéit au personnage qui l’invoque et peut être utilisé comme monture à Naxxramas. Toute action hostile envers le cavalier fait fuir le palefroi."},
[384135] = {name = "Rune de bonne fortune", additional_info = "Instantanée\n[q]Confère aux membres du raid une chance de découvrir des récompenses supplémentaires sur Kel’Thuzad et ses sbires.\n\n Cet effet est cumulable et dure une semaine."},
[398061] = {name = "Brassards de redirection", additional_info = "[q]Vous gagnez 1 point de combo chaque fois que vous évitez une attaque ou un sort d’une cible ennemie qui n’est pas votre cible actuelle.\n(1sec de recharge)"},
[398115] = {name = "Ceinture d’inoculation", additional_info = "Instantanée\n[q]Subit 1 % de dégâts pour se débarrasser d’un effet de maladie ou de poison."},
[398117] = {name = "Poches pleines de friandises", additional_info = "20 m de portée\nInstantanée\n[q]Ouvre un sac de friandises et en glisse dans la poche de votre cible, ce qui augmente les dégâts subis par les bêtes de 20."},
[398196] = {name = "Tireur rapide", additional_info = "Energie : 25 20 m de portée\nInstantanée 10 s de recharge\nRequiert Arme à distance\n[q]Vous dégainez une arme à distance pour tirer rapidement sur l’adversaire, ce qui lui inflige les dégâts normaux de l’arme à distance et réduit sa vitesse de déplacement de 50 % pendant 6 sec. Octroie 1 point de combo.\n\n Tireur rapide bénéficie de tous les talents et effets qui sont déclenchés par ou modifient Attaque pernicieuse."},
[398197] = {name = "Tireur rapide", additional_info = "[q]Vous obtenez la technique Tireur rapide :\n\n\nVous dégainez une arme à distance pour tirer rapidement sur l’adversaire, ce qui lui inflige les dégâts normaux de l’arme à distance et réduit sa vitesse de déplacement de 50 % pendant 6 sec. Octroie 1 point de combo.\n\n Tireur rapide bénéficie de tous les talents et effets qui sont déclenchés par ou modifient Attaque pernicieuse."},
[398198] = {name = "Tempête d’épées", additional_info = "Energie : 20 100 m de portée\nInstantanée\n[q]Coup de grâce qui augmente vos chances de Parade. La durée et les chances de parer dépendent du nombre de points de combo :\n 1 point : 10 s, 12 % de parade\n 2 points : 15 s, 14 % de parade\n 3 points : 20 s, 16 % de parade\n 4 points : 25 s, 18 % de parade\n 5 points : 30 s, 20 % de parade"},
[398199] = {name = "Tunique de fatuité", additional_info = nil},
[398538] = {name = "Gravure de brassard (Déviation mineure)", additional_info = "5 s d'incantation\nRequiert Brassards\nOutils:\nBâtonnet runique en cuivre\nComposants:\nEssence de magie inférieure , Poussière étrange\n[q]Enchante définitivement des bracelets. Ces derniers augmentent de 1 la Défense."},
[398603] = {name = "Gravure runique", additional_info = "Instantanée\n[q]Attache de puissantes runes à votre armure."},
[398604] = {name = "Gravure runique", additional_info = nil},
[399954] = {name = "Technique de rune de torse", additional_info = "Instantanée\n[q]Équipe un emplacement d’objet de torse avec une rune gravée pour activer une technique à cet emplacement."},
[399956] = {name = "Estropier", additional_info = "Energie : 40 Allonge\nInstantanée\n[q]Attaque instantanément avec les deux armes et inflige 100 % des dégâts des armes plus [100 / 100 * (5.741530 - 0.255683 * Level + 0.032656 * Level * Level)] avec chacune. Les dégâts sont augmentés de 20 % contre les cibles empoisonnées. Vous gagnez 2 points de combo.\n\n Estropier bénéficie de tous les talents et effets qui se déclenchent à partir de ou modifient Attaque sournoise, sauf indication contraire."},
[399960] = {name = "Estropier", additional_info = "Allonge\nInstantanée\n[q]Attaque instantanément avec les deux armes et inflige 100 % des dégâts des armes plus [100 / 100 * (5.741530 - 0.255683 * Level + 0.032656 * Level * Level)] avec chacune. Les dégâts sont augmentés de 20 % contre les cibles empoisonnées. Vous gagnez 2 points de combo."},
[399961] = {name = "Estropier", additional_info = "Allonge\nInstantanée\n[q]Attaque instantanément avec les deux armes et inflige 100 % des dégâts des armes plus $[100 / 100 * (5.741530 - 0.255683 * Level + 0.032656 * Level * Level)] avec chacune. Les dégâts sont augmentés de 20 % contre les cibles empoisonnées. Vous gagnez 2 points de combo."},
[399962] = {name = "Estropier", additional_info = "[q]Vous obtenez la technique Estropier :\n\n\nAttaque instantanément avec les deux armes et inflige 100 % des dégâts des armes plus [100 / 100 * (5.741530 - 0.255683 * Level + 0.032656 * Level * Level)] avec chacune. Les dégâts sont augmentés de 20 % contre les cibles empoisonnées. Vous gagnez 2 points de combo.\n\n Estropier bénéficie de tous les talents et effets qui se déclenchent à partir de ou modifient Attaque sournoise, sauf indication contraire."},
[399963] = {name = "Envenimer", additional_info = "Energie : 35 Allonge\nInstantanée\nRequiert Arme de mêlée\n[q]Coup de grâce qui inflige des dégâts de poison instantanément en fonction de vos doses de Poison mortel sur la cible. Après l’attaque Envenimer, vous avez 75 % de chances supplémentaires d’appliquer Poison instantané pendant 1 s plus 1 s supplémentaire par point de combo. Une dose est activée par point de combo :\n 1 dose : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 1 + Attack power * 0.09] points de dégâts\n 2 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 2 + Attack power * 0.18] points de dégâts\n 3 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 3 + Attack power * 0.27] points de dégâts\n 4 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 4 + Attack power * 0.36] points de dégâts\n 5 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 5 + Attack power * 0.45] points de dégâts."},
[399965] = {name = "Breuvage mortel", additional_info = "[q]Confère plusieurs améliorations à vos poisons :\n\n Lorsque vous infligez un autre poison à une cible, vous lui infligez également Poison mortel.\n\n Si vous n’avez pas appliqué de poison sur votre arme, elle a une chance de déclencher Poison instantané comme s’il avait été appliqué.\n\n Votre puissance d’attaque augmente à présent les dégâts de Poison mortel et Poison instantané."},
[399966] = {name = "Technique de rune de pantalon", additional_info = "Instantanée\n[q]Équipe un emplacement d’objet de jambes avec une rune gravée pour activer une technique à cet emplacement."},
[399967] = {name = "Technique de rune de gants", additional_info = "Instantanée\n[q]Équipe un emplacement d’objet de mains avec une rune gravée pour activer une technique à cet emplacement."},
[399968] = {name = "Envenimer", additional_info = "[q]Vous obtenez la technique Envenimer :\n\n\nCoup de grâce qui inflige des dégâts de poison instantanément en fonction de vos doses de Poison mortel sur la cible. Après l’attaque Envenimer, vous avez 75 % de chances supplémentaires d’appliquer Poison instantané pendant 1 s plus 1 s supplémentaire par point de combo. Une dose est activée par point de combo :\n 1 dose : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 1 + Attack power * 0.09] points de dégâts\n 2 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 2 + Attack power * 0.18] points de dégâts\n 3 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 3 + Attack power * 0.27] points de dégâts\n 4 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 4 + Attack power * 0.36] points de dégâts\n 5 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 5 + Attack power * 0.45] points de dégâts."},
[399969] = {name = "Breuvage mortel", additional_info = "[q]Vous obtenez la technique Breuvage mortel :\n\n\nConfère plusieurs améliorations à vos poisons :\n\n Lorsque vous infligez un autre poison à une cible, vous lui infligez également Poison mortel.\n\n Si vous n’avez pas appliqué de poison sur votre arme, elle a une chance de déclencher Poison instantané comme s’il avait été appliqué.\n\n Votre puissance d’attaque augmente à présent les dégâts de Poison mortel et Poison instantané."},
[399985] = {name = "Frappe-ténèbres", additional_info = "Energie : 20 25 m de portée\nInstantanée\nRequiert Arme de mêlée\nRequiert Camouflage\n[q]Vous téléporte derrière la cible et la frappe, ce qui inflige 150 % des dégâts de l’arme. Vous devez être camouflé(e). Octroie 1 point de combo."},
[400009] = {name = "Entre les deux yeux", additional_info = "Energie : 35 20 m de portée\n20 s de recharge\nRequiert Arme à distance\n[q]Coup de grâce à distance qui inflige des dégâts en fonction du nombre de points de combo, augmenté par la puissance d’attaque et étourdit la cible :\n\n 1 point : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level))] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level))] points de dégâts, 1 s d’étourdissement\n 2 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 2)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 2)] points de dégâts, 2 s d’étourdissement\n 3 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 3)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 3)] points de dégâts, 3 s d’étourdissement\n 4 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 4)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 4)] points de dégâts, 4 s d’étourdissement\n 5 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 5)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 5)] points de dégâts, 5 s d’étourdissement\n\n Temps de recharge partagé avec Aiguillon perfide."},
[400012] = {name = "Danse des lames", additional_info = "Energie : 25 100 m de portée\nInstantanée\n[q]Coup de grâce qui augmente les chances de parade de 10 %. La durée dépend du nombre de points de combo :\n\n 1 point : 14 s\n 2 points : 18 s\n 3 points : 22 s\n 4 points : 26 s\n 5 points : 30 s"},
[400014] = {name = "Juste une égratignure", additional_info = "[q]Réduit les dégâts physiques subis de 20 % lorsque Danse des lames est active. De plus, réduit les chances de subir un coup critique en mêlée de 6 %, augmente considérablement la menace générée par toutes les actions et remplace Feinte par Persifler, qui force la cible à vous attaquer.\n\n\nProvoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer."},
[400024] = {name = "Trompe-la-mort", additional_info = "Instantanée\n[q]Toute attaque qui devrait normalement vous tuer réduit à la place vos points de vie à 10 % de leur maximum. De plus, les dégâts subis sont réduits d’au maximum 90 % pendant 3 sec. Cet effet ne peut se produire plus d’une fois par minute."},
[400031] = {name = "Frappe-ténèbres", additional_info = "[q]Vous obtenez la technique Frappe-ténèbres :\n\n\nVous téléporte derrière la cible et la frappe, ce qui inflige 150 % des dégâts de l’arme. Vous devez être camouflé(e). Octroie 1 point de combo."},
[400033] = {name = "Entre les deux yeux", additional_info = "[q]Vous obtenez la technique Entre les deux yeux :\n\n\nCoup de grâce à distance qui inflige des dégâts en fonction du nombre de points de combo, augmenté par la puissance d’attaque et étourdit la cible :\n\n 1 point : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level))] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level))] points de dégâts, 1 s d’étourdissement\n 2 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 2)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 2)] points de dégâts, 2 s d’étourdissement\n 3 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 3)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 3)] points de dégâts, 3 s d’étourdissement\n 4 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 4)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 4)] points de dégâts, 4 s d’étourdissement\n 5 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 5)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 5)] points de dégâts, 5 s d’étourdissement\n\n Temps de recharge partagé avec Aiguillon perfide."},
[400038] = {name = "Danse des lames", additional_info = "[q]Vous obtenez la technique Danse des lames :\n\n\nCoup de grâce qui augmente les chances de parade de 10 %. La durée dépend du nombre de points de combo :\n\n 1 point : 14 s\n 2 points : 18 s\n 3 points : 22 s\n 4 points : 26 s\n 5 points : 30 s"},
[400039] = {name = "Juste une égratignure", additional_info = "[q]Vous obtenez la technique Juste une égratignure :\n\n\nRéduit les dégâts physiques subis de 20 % lorsque Danse des lames est active. De plus, réduit les chances de subir un coup critique en mêlée de 6 %, augmente considérablement la menace générée par toutes les actions et remplace Feinte par Persifler, qui force la cible à vous attaquer.\n\n\nProvoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer."},
[400080] = {name = "Gravure de plastron (Breuvage mortel)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Breuvage mortel :\n\n\nConfère plusieurs améliorations à vos poisons :\n\n Lorsque vous infligez un autre poison à une cible, vous lui infligez également Poison mortel.\n\n Si vous n’avez pas appliqué de poison sur votre arme, elle a une chance de déclencher Poison instantané comme s’il avait été appliqué.\n\n Votre puissance d’attaque augmente à présent les dégâts de Poison mortel et Poison instantané."},
[400081] = {name = "Gravure de pantalon (Entre les deux yeux)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Entre les deux yeux :\n\n\nCoup de grâce à distance qui inflige des dégâts en fonction du nombre de points de combo, augmenté par la puissance d’attaque et étourdit la cible :\n\n 1 point : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level))] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level))] points de dégâts, 1 s d’étourdissement\n 2 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 2)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 2)] points de dégâts, 2 s d’étourdissement\n 3 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 3)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 3)] points de dégâts, 3 s d’étourdissement\n 4 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 4)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 4)] points de dégâts, 4 s d’étourdissement\n 5 points : de [53 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 5)] à [81 / 100 * ((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) + (8.740728 - 0.415787 * Level + 0.051973 * Level * Level) * 5)] points de dégâts, 5 s d’étourdissement\n\n Temps de recharge partagé avec Aiguillon perfide."},
[400082] = {name = "Gravure de plastron (Juste une égratignure)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Juste une égratignure :\n\n\nRéduit les dégâts physiques subis de 20 % lorsque Danse des lames est active. De plus, réduit les chances de subir un coup critique en mêlée de 6 %, augmente considérablement la menace générée par toutes les actions et remplace Feinte par Persifler, qui force la cible à vous attaquer.\n\n\nProvoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer."},
[400094] = {name = "Gravure de gants (Estropier)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Estropier :\n\n\nAttaque instantanément avec les deux armes et inflige 100 % des dégâts des armes plus [100 / 100 * (5.741530 - 0.255683 * Level + 0.032656 * Level * Level)] avec chacune. Les dégâts sont augmentés de 20 % contre les cibles empoisonnées. Vous gagnez 2 points de combo.\n\n Estropier bénéficie de tous les talents et effets qui se déclenchent à partir de ou modifient Attaque sournoise, sauf indication contraire."},
[400095] = {name = "Gravure de plastron (Tireur rapide)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Tireur rapide :\n\n\nVous dégainez une arme à distance pour tirer rapidement sur l’adversaire, ce qui lui inflige les dégâts normaux de l’arme à distance et réduit sa vitesse de déplacement de 50 % pendant 6 sec. Octroie 1 point de combo.\n\n Tireur rapide bénéficie de tous les talents et effets qui sont déclenchés par ou modifient Attaque pernicieuse."},
[400099] = {name = "Gravure de pantalon (Danse des lames)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Danse des lames :\n\n\nCoup de grâce qui augmente les chances de parade de 10 %. La durée dépend du nombre de points de combo :\n\n 1 point : 14 s\n 2 points : 18 s\n 3 points : 22 s\n 4 points : 26 s\n 5 points : 30 s"},
[400102] = {name = "Gravure de pantalon (Envenimer)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Envenimer :\n\n\nCoup de grâce qui inflige des dégâts de poison instantanément en fonction de vos doses de Poison mortel sur la cible. Après l’attaque Envenimer, vous avez 75 % de chances supplémentaires d’appliquer Poison instantané pendant 1 s plus 1 s supplémentaire par point de combo. Une dose est activée par point de combo :\n 1 dose : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 1 + Attack power * 0.09] points de dégâts\n 2 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 2 + Attack power * 0.18] points de dégâts\n 3 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 3 + Attack power * 0.27] points de dégâts\n 4 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 4 + Attack power * 0.36] points de dégâts\n 5 doses : [((5.741530 - 0.255683 * Level + 0.032656 * Level * Level) * 100 / 100) * 5 + Attack power * 0.45] points de dégâts."},
[400105] = {name = "Gravure de gants (Frappe-ténèbres)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Frappe-ténèbres :\n\n\nVous téléporte derrière la cible et la frappe, ce qui inflige 150 % des dégâts de l’arme. Vous devez être camouflé(e). Octroie 1 point de combo."},
[400573] = {name = "Déflagration des arcanes", additional_info = "Instantanée 3 min de recharge\n[q]Frappe la cible avec de l’énergie et lui inflige des dégâts des arcanes. Chaque fois que vous lancez Déflagration des arcanes, les dégâts et les soins de tous les sorts des arcanes sont augmentés de 15 %, mais le coût en mana de Déflagration des arcanes augmente de 175 %. Effet cumulable jusqu’à 4 fois. Dure 6 sec."},
[400574] = {name = "Déflagration des arcanes", additional_info = "7% de la mana de base 30 m de portée\n2.5 s d'incantation\n[q]Frappe la cible avec de l’énergie et lui inflige de [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 453 / 100] à [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 527 / 100] points de dégâts des arcanes. Chaque fois que vous lancez Déflagration des arcanes, les dégâts et les soins de tous les autres sorts des arcanes sont augmentés de 15 %, mais le coût en mana de Déflagration des arcanes augmente de 175 %. Effet cumulable jusqu’à 4 fois. Dure 6 sec ou jusqu’à ce qu’un sort de dégâts d’arcanes ou de soins soit lancé."},
[400586] = {name = "Déflagration des arcanes", additional_info = "Instantanée 3 min de recharge\n[q]Frappe la cible avec de l’énergie et lui inflige des dégâts des arcanes. Chaque fois que vous lancez Déflagration des arcanes, les dégâts de tous les autres sorts des arcanes sont augmentés de 15 %, mais le coût en mana de Déflagration des arcanes augmente de 175 %. Effet cumulable jusqu’à 4 fois. Dure 6 sec."},
[400613] = {name = "Bombe vivante", additional_info = "22% de la mana de base 35 m de portée\nInstantanée\n[q]La cible devient une bombe vivante qui inflige [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 85 / 100 * 4] points de dégâts de feu en 12 sec. Au bout de 12 sec ou quand le sort est dissipé, la cible explose en infligeant [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 171 / 100] points de dégâts de feu aux ennemis à moins de 10 mètres"},
[400614] = {name = "Bombe vivante", additional_info = "100 m de portée\nInstantanée\n[q]La cible devient une bombe vivante qui inflige [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 85 / 100 * 4] points de dégâts de feu en until cancelled. Au bout de until cancelled ou quand le sort est dissipé, la cible explose en infligeant [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 171 / 100] points de dégâts de feu aux ennemis à moins de 10 mètres."},
