[401765] = {name = "Gravure de plastron (Doigts de givre)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Doigts de givre :\n\n\nDonne à vos effets d’engourdissement 15 % de chances de déclencher l’effet Doigts de givre, qui permet à vos 2 prochains sorts d’agir comme si la cible était Gelée. Dure 15 sec."},
[401767] = {name = "Gravure de plastron (Régénération)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Régénération :\n\n\nRend à la cible [(38.258376 + 0.904195 * Level + 0.161311 * Level * Level) * 42 / 100 * 3] points de vie en 3 sec et applique Balise temporelle pendant 30 s.\n\n Balise temporelle\n\nEnregistre la position spatio-temporelle de la cible. 80 % de tous les dégâts des arcanes infligés par le lanceur ou la lanceuse seront convertis en soins chronomantiques pour chaque cible actuelle de Balise temporelle du lanceur ou de la lanceuse. Ces soins sont réduits de 50 % sur vous-même, et également réduits de 80 % lorsque les dégâts sont infligés par des sorts des arcanes qui blessent plusieurs cibles."},
[401768] = {name = "Gravure de pantalon (Flamme vivante)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Flamme vivante :\n\n\nInvoque une flamme magique qui se dirige vers la cible en laissant une traînée de feu-sorcier. Cette traînée inflige [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 100 / 100] dégâts de feu-sorcier toutes les secondes aux ennemis proches. Dure 20 sec."},
[401798] = {name = "Nouvelle cache enfouie", additional_info = nil},
[401847] = {name = "Combiner les éléments de carte", additional_info = "3 s d'incantation\nComposants:\nMorceau supérieur droit de carte , Morceau supérieur gauche de carte , Morceau inférieur droit de carte , Morceau inférieur gauche de carte\n[q]Combiner avec 3 autres morceaux pour former la carte complète."},
[401859] = {name = "Prière de guérison", additional_info = "15% de la mana de base 40 m de portée\nInstantanée 10 s de recharge\n[q]Place sur la cible un sort qui lui rend [(38.258376 + 0.904195 * Level + 0.161311 * Level * Level) * 74 / 100] points de vie la prochaine fois qu’elle subit des dégâts. Chaque fois que les soins sont déclenchés, la Prière de guérison passe à une autre cible du groupe ou raid se trouvant à moins de 20 mètres. Change de cible 5 fois au maximum et dure 30 sec après chaque changement. Ce sort ne peut être placé que sur une cible à la fois."},
[401863] = {name = "Prière de guérison", additional_info = "Portée illimitée\nInstantanée\n[q]Place un sort sur la cible qui la soigne la prochaine fois qu’elle subit des dégâts. Quand elle est soignée, la Prière de guérison passe à un autre membre du groupe ou raid se trouvant à moins de 0 mètres. Rebondit jusqu’à 0 fois et dure until cancelled après chaque rebond. Ce sort ne peut être lancé que sur une cible à la fois."},
[401877] = {name = "Prière de guérison", additional_info = "Portée illimitée\nInstantanée\n[q]Place un sort sur la cible qui la soigne la prochaine fois qu’elle subit des dégâts ou reçoit des soins. Quand elle est soignée, la Prière de guérison passe à un autre membre du groupe ou raid se trouvant à moins de 0 mètres. Rebondit jusqu’à 0 fois et dure 30 sec après chaque rebond. Ce sort ne peut être lancé que sur une cible à la fois."},
