[407614] = {name = "Guide de la lumière", additional_info = "60 m de portée\nInstantanée\n[q]La cible devient un Guide de lumière pour toutes les cibles se trouvant dans un rayon de 60 mètres. Tous les soins que vous lancez sur ces cibles soignent également le guide pour un montant de points de vie égal à 100 % des soins prodigués."},
[407615] = {name = "Guide de lumière", additional_info = "40 m de portée\nInstantanée\n[q]La cible devient un Guide de lumière pour toutes les cibles se trouvant dans un rayon de 40 mètres. Tous les soins que vous lancez sur ces cibles soignent également le guide pour un montant de points de vie égal à 1 % des soins prodigués. Une seule cible à la fois peut être le Guide de lumière. Dure until cancelled."},
[407627] = {name = "Fureur vertueuse", additional_info = "Instantanée\n[q]Augmente la menace générée par vos sorts du sacré de 80 % et vous donne du mana lorsque des cibles alliées vous soignent, pour 25 % des soins reçus. De plus, les dégâts qui vous font descendre sous les 35 % de points de vie sont réduits de 20 %. Dure until cancelled."},
[407628] = {name = "Fureur vertueuse", additional_info = "Instantanée\n[q]Gagne du mana grâce aux soins reçus."},
[407631] = {name = "Main de rétribution", additional_info = "AllongeInstantanée 10 s de recharge\n[q]Provoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer.\n\n Pendant que vous connaissez cette technique, le bonus de menace de Fureur vertueuse passe à 80 % et Fureur vertueuse vous fait gagner du mana lorsque d’autres personnages vous soignent, pour 25 % du montant des soins. De plus, tant que Fureur vertueuse est actif, les dégâts qui vous font descendre sous 35 % de points de vie sont réduits de 20 %. Fureur vertueuse reste actif jusqu’à annulation."},
[407653] = {name = "Fourche d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Un éclair frappe un ennemi et rebondit sur un autre proche. Le sort affecte jusqu’à 0 cibles et inflige des dégâts de nature à chacune."},
[407667] = {name = "Fourche d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Envoie sur un adversaire un éclair qui bifurque vers toutes les cibles proches et leur inflige des dégâts de nature à chacune."},
[407669] = {name = "Bouclier du vengeur", additional_info = "26% de la mana de base 30 m de portée\nInstantanée 30 s de recharge\n[q]Lance sur un ennemi un bouclier sacré qui inflige de [366 * (9.046514 + 0.676562 * Level + 0.019349 * Level * Level) / 100 + 0.091 * Spell power + 0.091 * Attack power] à [448 * (9.046514 + 0.676562 * Level + 0.019349 * Level * Level) / 100 + 0.091 * Spell power + 0.091 * Attack power] points de dégâts du sacré, l’hébète et rebondit ensuite sur des ennemis proches. Le sort frappe 3 cibles au total. Dure 10 sec."},
[407676] = {name = "Frappe du croisé", additional_info = "AllongeInstantanée 6 s de recharge\nRequiert Arme de mêlée\n[q]Une attaque instantanée qui inflige 75 % des dégâts de l’arme sous forme de dégâts du sacré et régénère 5 % de votre maximum de mana."},
[407720] = {name = "Fourche d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Un éclair frappe un ennemi et rebondit sur un autre proche. Le sort affecte jusqu’à 0 cibles et inflige des dégâts de nature à chacune."},
[407736] = {name = "Fourche d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Un éclair frappe un ennemi et rebondit sur un autre proche. Le sort affecte jusqu’à 0 cibles et inflige des dégâts de nature à chacune."},
[407745] = {name = "Fourche d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Envoie sur un adversaire un éclair qui bifurque vers toutes les cibles proches et leur inflige des dégâts de nature à chacune."},
