[407774] = {name = "Main de rétribution", additional_info = nil},
[407778] = {name = "Tempête divine", additional_info = "12% de la mana de baseInstantanée 10 s de recharge\nRequiert Arme de mêlée\n[q]Une attaque instantanée avec une arme qui inflige 110 % des dégâts de l’arme à un maximum de 4 ennemis se trouvant à moins de 8 mètres. Tempête divine soigne jusqu’à 3 membres du groupe ou raid pour un total de 25 % des dégâts infligés."},
[407782] = {name = "Tempête divine", additional_info = "100 m de portée\nInstantanée\n[q]Les dégâts de Tempête divine soignent les cibles alliées."},
[407784] = {name = "Tempête divine", additional_info = "Portée illimitée\nInstantanée\n[q]Les dégâts de Tempête divine soignent les cibles alliées."},
[407791] = {name = "Rage d’Aku’mai", additional_info = "Instantanée\n[q]Augmente les dégâts physiques infligés par le lanceur ou la lanceuse de 25 et la vitesse de ses attaques de 30 % pendant 15 sec."},
[407794] = {name = "Triple perforation", additional_info = "Allonge\nInstantanée\n[q]Inflige des dégâts physiques à un ennemi toutes les 3 s. pendant 15 sec.\n(1sec de recharge)"},
[407798] = {name = "Sceau du martyr", additional_info = "4% de la mana de base\nInstantanée\n[q]Vous remplit d’esprit sacré pendant 30 sec. Chacune de vos attaques de mêlée inflige 40 % des dégâts d’armes à votre cible, mais vous perdez des points de vie pour 10 % des dégâts infligés. Tant que ce sceau est actif, chaque membre du groupe ou du raid à moins de 40 m gagne un montant de mana égal à 20 % des dégâts que ce sceau vous inflige.\n\n Lorsque l’énergie de ce sceau est déchaînée, un personnage adverse est jugé, ce qui lui inflige instantanément 70 % de dégâts de l’arme en sacrifiant vos points de vie pour 10 % des dégâts infligés."},
[407799] = {name = "Sceau du martyr", additional_info = nil},
[407800] = {name = "Sceau du martyr", additional_info = "Instantanée\n[q]Les dégâts de Sceau du martyr blessent également le paladin."},
[407802] = {name = "Sceau du martyr", additional_info = "40 m de portée\nInstantanée\n[q]Le paladin gagne du mana en subissant des dégâts."},
[407803] = {name = "Jugement de martyr", additional_info = nil},
[407804] = {name = "Sacrifice divin", additional_info = "Instantanée 2 min de recharge\n[q]30 % des dégâts subis par les membres du groupe à moins de 30 m sont redirigés vers le paladin ou la paladine pendant 10 sec. Les dégâts qui le ou la font passer sous 20 % de points de vie interrompent l’effet et augmentent ses dégâts et soins de 10 % pendant 10 sec. Vous ne pouvez pas utiliser Sacrifice divin sous l’effet de Bénédiction de protection, Bouclier divin ou Protection divine, ni être la cible de ces techniques tant que Sacrifice divin est actif."},
[407805] = {name = "Sacrifice racheté", additional_info = "Instantanée 3 min de recharge\n[q]Dégâts et soins augmentés après votre sacrifice."},
[407811] = {name = "Enchaînement", additional_info = "Allonge\nProchaine attaque en mêlée\n[q]Inflige les dégâts de l’arme plus 23 to 27 à un ennemi et à ses alliés les plus proches, affecte jusqu’à 3 cibles."},
[407812] = {name = "Immunité à Flèche givrante", additional_info = nil},
[407813] = {name = "Flèche de givre", additional_info = "5 - 30 m de portée\n1 s d'incantation\nRequiert Arme à distance\n[q]Tire sur un ennemi et lui inflige des dégâts physiques."},
[407819] = {name = "Flèche de givre", additional_info = "50 m de portée\n1.5 s d'incantation\nRequiert Arme à distance\n[q]Tire sur un ennemi et lui inflige des dégâts physiques."},
[407821] = {name = "Flèche de givre", additional_info = "50 m de portée\nInstantanée\nRequiert Arme à distance\n[q]Tire sur un ennemi et lui inflige des dégâts physiques."},
[407822] = {name = "Flèche de givre", additional_info = nil},
[407880] = {name = "Source d’inspiration", additional_info = "2% de la mana de base\nInstantanée\n[q]Votre présence inspirante dissipe périodiquement les effets de peur et de sommeil sur les membres du groupe proches."},
[407881] = {name = "Source d’inspiration", additional_info = "Instantanée\n[q]Votre présence inspirante dissipe périodiquement les effets de peur et de sommeil sur les membres du groupe proches."},
[407973] = {name = "Frappe féroce", additional_info = "100 m de portée\nInstantanée\n[q]Attaque supplémentaire."},
[407975] = {name = "Frappes féroces", additional_info = "Instantanée\n[q]Les membres du groupe à moins de 20 mètres gagnent une férocité accrue en combat. Chaque coup en mêlée a 20 % de chances de conférer à l’attaquant une attaque supplémentaire avec 20 % de puissance d’attaque supplémentaire. Aucun effet si le membre du groupe bénéficie déjà de Totem Furie-des-vents.\n(Chance d'occurence :20%, 1.5sec de recharge)"},
[407977] = {name = "Frappes féroces", additional_info = "[q]Lorsque vous êtes en forme de félin, d’ours ou d’ours redoutable, les membres du groupe à moins de 20 mètres gagnent une férocité accrue en combat. Chaque coup en mêlée a 20 % de chances de conférer à l’attaquant une attaque supplémentaire avec 20 % de puissance d’attaque supplémentaire. Aucun effet si le membre du groupe bénéficie déjà de Totem Furie-des-vents."},
[407988] = {name = "Rugissement sauvage", additional_info = "Energie : 25 100 m de portée\nInstantanée\n[q]Coup de grâce qui augmente les dégâts physiques infligés de 30 % en forme de félin. La durée dépend du nombre de points de combo :\n 1 point : 14 s\n 2 points : 19 s\n 3 points : 24 s\n 4 points : 29 s\n 5 points : 34 s"},
[407989] = {name = "Rugissement sauvage", additional_info = "Instantanée\nRequiert Forme de félin\n[q]Coup de grâce qui augmente les dégâts physiques infligés de 30 %. La durée dépend du nombre de points de combo :\n 1 point : 14 s\n 2 points : 19 s\n 3 points : 24 s\n 4 points : 29 s\n 5 points : 34 s"},
[407993] = {name = "Mutilation", additional_info = "Mutilation Félin\nEnergie : 40 Allonge\nInstantanée\nRequiert Forme de félin\n[q]Mutile la cible, ce qui lui inflige 270 % des dégâts normaux. De plus, les effets de saignement et Lambeau lui infligent 30 % de dégâts supplémentaires pendant 1 min. Vous gagnez 1 point de combo. Cette technique bénéficie de tous les effets associés à Griffe et Mutiler et les déclenche."},
[407995] = {name = "Mutilation", additional_info = "Mutilation Ours\nRage : 15 Allonge\nIncantation immédiate 6 s de recharge\nRequiert Forme d’ours, Forme d’ours redoutable\n[q]Mutile la cible, ce qui lui inflige 160 % des dégâts normaux. De plus, les effets de saignement et Lambeau lui infligent 30 % de dégâts supplémentaires pendant 1 min. Cette technique bénéficie de tous les effets associés à Griffe et Mutiler et les déclenche."},
[408013] = {name = "Générateur d’élite de Brassenoire", additional_info = nil},
[408014] = {name = "Faire apparaître une élite de Brassenoire", additional_info = nil},
[408030] = {name = "Gravure runique : enseigne la gravure de Siphon d’âme", additional_info = nil},
[408035] = {name = "Damien Kane – Échanger des âmes contre la rune", additional_info = nil},
[408120] = {name = "Croissance sauvage", additional_info = "56% de la mana de base 40 m de portée\nInstantanée 6 s de recharge\n[q]Rend [34 / 100 * 7 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie en 7 sec aux membres du groupe du personnage-joueur ciblé à moins de 40 m de lui. La quantité de soins prodiguée est importante au début, puis diminue à mesure que Croissance sauvage atteint sa durée maximale."},
[408124] = {name = "Fleur de vie", additional_info = "28% de la mana de base 40 m de portée\nInstantanée\n[q]Rend [4 / 100 * 7 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie à la cible en 7 sec. Quand Fleur de vie parvient au terme de sa durée ou est dissipée, la cible reçoit instantanément [57 / 100 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie et le lanceur ou la lanceuse récupère la moitié du coût du sort. Cet effet est cumulable jusqu’à 3 fois sur la même cible."},
[408245] = {name = "Fleur de vie", additional_info = "40 m de portée\nInstantanée\n[q]Soigne la cible en 7 s. Quand Fleur de vie arrive au terme de sa durée ou est dissipée, la cible est soignée instantanément. Cet effet est cumulable jusqu’à 3 fois sur la même cible."},
[408246] = {name = "Fleur de vie", additional_info = "Instantanée\n[q]Gagne du mana à l’expiration de Fleur de vie."},
[408307] = {name = "Fureur de la nature", additional_info = "Instantanée 2 min de recharge\n[q]Augmente la puissance de soins de 90 % des dégâts des sorts de nature pendant 30 sec."},
[408309] = {name = "Grâce d’Élune", additional_info = "Instantanée 2 min de recharge\n[q]Augmente les dégâts des sorts de 55 % de la différence entre votre puissance de soins et vos dégâts des sorts. Dure 30 sec."},
[408438] = {name = "Surcharge", additional_info = "[q]Donne à vos sorts Éclair, Chaîne d’éclairs, Salve de guérison, Vague de soins et Explosion de lave 50 % de chances de lancer un second sort semblable sur la même cible sans coût supplémentaire. Ce sort inflige la moitié des dégâts et ne génère pas de menace.\n(Chance d'occurence :50%)"},
[408439] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 7 to 9 points de dégâts de nature."},
[408440] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 14 to 17 points de dégâts de nature."},
[408441] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 23 to 29 points de dégâts de nature."},
[408442] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 43 to 51 points de dégâts de nature."},
[408443] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 65 to 75 points de dégâts de nature."},
[408472] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 89 to 101 points de dégâts de nature."},
[408473] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 117 to 133 points de dégâts de nature."},
[408474] = {name = "Éclair", additional_info = "30 m de portée\n3 s d'incantation\n[q]Lance un éclair de foudre sur la cible et lui inflige 145 to 163 points de dégâts de nature."},
[408475] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 178 to 201 points de dégâts de nature."},
[408477] = {name = "Éclair", additional_info = "30 m de portée\nInstantanée\n[q]Lance un éclair de foudre sur la cible et lui inflige 213 to 239 points de dégâts de nature."},
[408479] = {name = "Chaîne d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Lance sur un ennemi un éclair qui inflige 99 to 114 points de dégâts de nature et rebondit sur les ennemis proches. Chaque rebond inflige 30 % de dégâts en moins. Le sort frappe 3 cibles au total."},
[408481] = {name = "Chaîne d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Lance sur un ennemi un éclair qui inflige 143 to 162 points de dégâts de nature et rebondit sur les ennemis proches. Chaque rebond inflige 30 % de dégâts en moins. Le sort frappe 3 cibles au total."},
[408482] = {name = "Chaîne d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Lance sur un ennemi un éclair qui inflige 195 to 219 points de dégâts de nature et rebondit sur les ennemis proches. Chaque rebond inflige 30 % de dégâts en moins. Le sort frappe 3 cibles au total."},
[408484] = {name = "Chaîne d’éclairs", additional_info = "30 m de portée\nInstantanée\n[q]Lance sur un ennemi un éclair qui inflige 252 to 283 points de dégâts de nature et rebondit sur les ennemis proches. Chaque rebond inflige 30 % de dégâts en moins. Le sort frappe 3 cibles au total."},
[408490] = {name = "Explosion de lave", additional_info = "10% de la mana de base 30 m de portée\n2 s d'incantation 8 s de recharge\n[q]Vous lancez de la lave en fusion sur la cible, lui infligeant [469 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] à [605 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] points de dégâts de feu. Si Horion de flammes est actif sur la cible, Explosion de lave sera un coup critique."},
[408491] = {name = "Explosion de lave", additional_info = "30 m de portée\nInstantanée\n[q]Vous lancez de la lave en fusion sur la cible, lui infligeant [234 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] à [303 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] points de dégâts de feu. Si Horion de flammes est actif sur la cible, Explosion de lave sera un coup critique."},
[408496] = {name = "Spécialisation Ambidextrie", additional_info = "[q]Augmente les chances de toucher avec les sorts et les attaques de mêlée de 10 % lorsque vous utilisez deux armes. Frappe-tempête frappe à présent avec les deux armes lorsque vous en utilisez deux. Augmente les dégâts de l’arme en main gauche de 50 %."},
[408501] = {name = "Maelström prêt !", additional_info = nil},
[408507] = {name = "Fouet de lave", additional_info = "1% de la mana de base Allonge\nInstantanée 6 s de recharge\nRequiert Arme de mêlée\n[q]Vous chargez votre arme en main gauche de lave, infligeant instantanément 150 % des dégâts de l’arme en main gauche. Les dégâts sont augmentés de 50 % si votre arme en main gauche est enchantée avec Langue de feu."},
[408510] = {name = "Bouclier d’eau", additional_info = "Instantanée\n[q]Le lanceur ou la lanceuse s’entoure de 3 globes d’eau, ce qui rend 1 % du maximum de mana toutes les 5 s. À toute attaque en mêlée ou à distance ou à tout sort subis, rend 4 % du maximum de mana. Un globe d’eau est alors dépensé. Un seul globe peut s’activer toutes les quelques secondes. Dure 10 min. Un seul Bouclier élémentaire peut être actif sur le chaman à la fois.\n(3.5sec de recharge)"},
[408511] = {name = "Bouclier d’eau", additional_info = "Instantanée\n[q]Gain de mana lorsque Bouclier d’eau est actif."},
[408514] = {name = "Bouclier de terre", additional_info = "15% de la mana de base 40 m de portée\nInstantanée\n[q]Protège la cible avec un bouclier de terre, qui réduit de 30% le temps d’incantation ou de canalisation de sort perdu quand elle subit des dégâts. Les attaques rendent (100 */ 100) points de vie à la cible protégée. Cet effet ne peut se produire qu’une fois toutes les quelques secondes. 3 charges. Dure 10 min. Bouclier de terre ne peut être placé que sur une cible à la fois et un seul Bouclier élémentaire peut être actif sur une cible à la fois.\n(3.5sec de recharge)"},
[408519] = {name = "Bouclier de terre", additional_info = "Portée illimitée\nInstantanée\n[q]Soins de bouclier de terre."},
[408524] = {name = "Maîtrise du bouclier", additional_info = "[q]Chaque fois que vous bloquez, vous régénérez un montant de mana égal à 8 % de votre maximum et votre Armure est augmentée de 30 % de la valeur d’Armure de votre bouclier, cumulable jusqu’à 5 fois. Vous gagnez également 10 % de chances de bloquer et 15 % de valeur de blocage en permanence."},
[408525] = {name = "Maîtrise du bouclier", additional_info = "Instantanée\n[q]Augmente l’Armure de 30 % de la valeur d’Armure de votre bouclier pendant 15 sec, cumulable jusqu’à 5 fois."},
[408531] = {name = "Voie de la terre", additional_info = "[q]Tant qu’Arme Croque-roc est active sur votre arme principale, vous générez 50 % de menace supplémentaire, vous gagnez 30 % de points de vie, vous subissez des dégâts réduits de 10 %, réduit la probabilité que vous soyez touché par un coup critique infligé par une attaque en mêlée de 6 % et Horion de terre provoque les cibles pour qu’elles vous attaquent avec un temps de recharge distinct des autres horions, mais sa portée est réduite à la mêlée."},
[408680] = {name = "Voie de la terre", additional_info = "Instantanée\n[q]Tant qu’Arme Croque-roc est active sur votre arme principale, vous générez 50 % de menace supplémentaire, vous gagnez 30 % de points de vie, vous subissez des dégâts réduits de 10 %, réduit la probabilité que vous soyez touché par un coup critique infligé par une attaque en mêlée de 6 % et Horion de terre provoque les cibles pour qu’elles vous attaquent avec un temps de recharge distinct des autres horions."},
[408681] = {name = "Horion de terre", additional_info = "Mana : 30 Allonge\nInstantanée 6 s de recharge\n[q]Frappe instantanément la cible avec force et inflige 19 to 22 points de dégâts de nature. Interrompt les incantations de sorts et interdit l’utilisation de sorts de cette école de magie pendant 2 sec. Génère un niveau élevé de menace et provoque la cible."},
[408683] = {name = "Horion de terre", additional_info = "Mana : 50 Allonge\nInstantanée 6 s de recharge\n[q]Frappe instantanément la cible avec force et inflige 35 to 38 points de dégâts de nature. Interrompt les incantations de sorts et interdit l’utilisation de sorts de cette école de magie pendant 2 sec. Génère un niveau élevé de menace et provoque la cible."},
[408685] = {name = "Horion de terre", additional_info = "Mana : 85 Allonge\nInstantanée 6 s de recharge\n[q]Frappe instantanément la cible avec force et inflige 65 to 69 points de dégâts de nature. Interrompt les incantations de sorts et interdit l’utilisation de sorts de cette école de magie pendant 2 sec. Génère un niveau élevé de menace et provoque la cible."},
[408687] = {name = "Horion de terre", additional_info = "Mana : 145 Allonge\nInstantanée 6 s de recharge\n[q]Frappe instantanément la cible avec force et inflige 126 to 134 points de dégâts de nature. Interrompt les incantations de sorts et interdit l’utilisation de sorts de cette école de magie pendant 2 sec. Génère un niveau élevé de menace et provoque la cible."},
