[409368] = {name = "Maîtrise des bêtes", additional_info = "[q]Les dégâts et points de vie de votre familier sont augmentés de 20 % et sa régénération de focalisation de 50 %. En outre, Grondement provoque également la cible pour qu’elle l’attaque pendant 3 sec."},
[409372] = {name = "Grondement", additional_info = "Allonge\nInstantanée\n[q]Provoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer."},
[409379] = {name = "Ordre de tuer", additional_info = "1.5% de la mana de base 45 m de portée\nInstantanée 1 min de recharge\n[q]Donne l’ordre de tuer. Les dégâts infligés par votre familier avec Griffe et Morsure sont augmentés de 60% pendant 30 sec. Chaque attaque Griffe ou Morsure effectuée par le familier réduit le bonus de dégâts de 20%."},
[409396] = {name = "Ordre de tuer", additional_info = "45 m de portée\nInstantanée\n[q]Donne l’ordre de tuer. Les dégâts infligés par votre familier avec Griffe et Morsure sont augmentés de 60 % pendant 30 sec. Chaque attaque Griffe ou Morsure effectuée par le familier réduit le bonus de dégâts de 20 %."},
[409418] = {name = "Ordre de tuer", additional_info = "Portée illimitéeInstantanée 1 min de recharge\n[q]Donne l’ordre de tuer. Les dégâts infligés par votre familier avec Griffe et Morsure sont augmentés de 60 % pendant 30 sec. Chaque attaque Griffe ou Morsure effectuée par le familier réduit le bonus de dégâts de 20 %."},
