[409428] = {name = "Maître tireur", additional_info = "[q]Augmente vos chances de coup critique de 5 % et réduit le coût en mana de toutes vos techniques de tir de 25 %."},
[409433] = {name = "Tir de la chimère", additional_info = "6% de la mana de base 8 - 35 m de portée\n6 s de recharge\nRequiert Arme à distance\n[q]Infligez 100 % des dégâts de l’arme, réinitialise la piqûre ou morsure actuelle sur la cible et déclenche un effet :\n\n Morsure de serpent – Inflige instantanément 40 % des dégâts de Morsure de serpent.\n\n Morsure de vipère – Rend instantanément un montant de mana égal à 60 % du total drainé par Morsure de vipère.\n\n Piqûre de scorpide – Tente de désarmer la cible pendant 10 sec. Cet effet ne peut se produire plus d’une fois par minute."},
[409493] = {name = "Tir de la chimère – Serpent", additional_info = "100 m de portée\n[q]Infligez 100 % des dégâts de l’arme, réinitialise la piqûre ou morsure actuelle sur la cible et déclenche un effet :\n\n Morsure de serpent – Inflige instantanément 40 % des dégâts de Morsure de serpent.\n\n Morsure de vipère – Rend instantanément un montant de mana égal à 60 % du total drainé par Morsure de vipère.\n\n Piqûre de scorpide – Tente de désarmer la cible pendant 10 sec. Cet effet ne peut se produire plus d’une fois par minute."},
[409494] = {name = "Tir de la chimère – Vipère", additional_info = "100 m de portée\n[q]Infligez 100 % des dégâts de l’arme, réinitialise la piqûre ou morsure actuelle sur la cible et déclenche un effet :\n\n Morsure de serpent – Inflige instantanément 40 % des dégâts de Morsure de serpent.\n\n Morsure de vipère – Rend instantanément un montant de mana égal à 60 % du total drainé par Morsure de vipère.\n\n Piqûre de scorpide – Tente de désarmer la cible pendant 10 sec. Cet effet ne peut se produire plus d’une fois par minute."},
[409495] = {name = "Tir de la chimère – Scorpide", additional_info = "100 m de portée\n[q]Infligez 100 % des dégâts de l’arme, réinitialise la piqûre ou morsure actuelle sur la cible et déclenche un effet :\n\n Morsure de serpent – Inflige instantanément 40 % des dégâts de Morsure de serpent.\n\n Morsure de vipère – Rend instantanément un montant de mana égal à 60 % du total drainé par Morsure de vipère.\n\n Piqûre de scorpide – Tente de désarmer la cible pendant 10 sec. Cet effet ne peut se produire plus d’une fois par minute."},
[409510] = {name = "Piège givrant", additional_info = "Mana : 50 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de givre qui gèle le premier ennemi qui s’en approche et l’empêche d’agir pendant 10 sec au maximum. Tous les dégâts subis brisent la glace. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409512] = {name = "Piège givrant", additional_info = "Mana : 75 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de givre qui gèle le premier ennemi qui s’en approche et l’empêche d’agir pendant 15 sec au maximum. Tous les dégâts subis brisent la glace. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409519] = {name = "Piège givrant", additional_info = "Mana : 100 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de givre qui gèle le premier ennemi qui s’en approche et l’empêche d’agir pendant 20 sec au maximum. Tous les dégâts subis brisent la glace. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409520] = {name = "Piège de givre", additional_info = "Mana : 60 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de givre qui crée autour de lui une coulée de givre d’une durée de 30 sec lorsque le premier ennemi s’en approche. Les ennemis à moins de 10 mètres sont ralentis de 60 %. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409521] = {name = "Piège d’immolation", additional_info = "Mana : 50 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui brûle le premier ennemi qui approche et lui inflige 105 points de dégâts de feu en 15 sec. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409524] = {name = "Piège d’immolation", additional_info = "Mana : 90 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui brûle le premier ennemi qui approche et lui inflige 215 points de dégâts de feu en 15 sec. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409526] = {name = "Piège d’immolation", additional_info = "Mana : 135 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui brûle le premier ennemi qui approche et lui inflige 340 points de dégâts de feu en 15 sec. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409528] = {name = "Piège d’immolation", additional_info = "Mana : 190 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui brûle le premier ennemi qui approche et lui inflige 510 points de dégâts de feu en 15 sec. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409530] = {name = "Piège d’immolation", additional_info = "Mana : 245 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui brûle le premier ennemi qui approche et lui inflige 690 points de dégâts de feu en 15 sec. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409532] = {name = "Piège explosif", additional_info = "Mana : 275 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui explose lorsqu’un ennemi s’en approche. Il inflige 104 to 135 points de dégâts de feu, et 150 supplémentaires en 20 sec dans un rayon de 10 mètres. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409534] = {name = "Piège explosif", additional_info = "Mana : 395 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui explose lorsqu’un ennemi s’en approche. Il inflige 145 to 193 points de dégâts de feu, et 240 supplémentaires en 20 sec dans un rayon de 10 mètres. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
[409535] = {name = "Piège explosif", additional_info = "Mana : 520 35 m de portée\nInstantanée 15 s de recharge\n[q]Place à un emplacement proche un piège de feu qui explose lorsqu’un ennemi s’en approche. Il inflige 208 to 265 points de dégâts de feu, et 330 supplémentaires en 20 sec dans un rayon de 10 mètres. Le piège reste en place pendant 1 min. Un seul piège peut être actif à la fois."},
