[409580] = {name = "Cœur de lion", additional_info = "8% de la mana de base\nInstantanée\n[q]Invoque le cœur d’un lion et augmente le total des caractéristiques des personnages alliés proches de 10 %, et le total de celles du chasseur ou de la chasseuse de 10 % supplémentaires."},
[409583] = {name = "Cœur de lion", additional_info = "40 m de portée\nInstantanée\n[q]Invoque le cœur d’un lion et augmente le total des caractéristiques des personnages alliés proches de 10 %, et le total de celles du chasseur ou de la chasseuse de 10 % supplémentaires."},
