[409752] = {name = "Attaque du raptor", additional_info = "Allonge\nInstantanée\nRequiert Arme de mêlée\n[q]Une attaque puissante qui augmente de 40 les dégâts infligés en mêlée."},
[409754] = {name = "Attaque du raptor", additional_info = "Allonge\nInstantanée\nRequiert Arme de mêlée\n[q]Une attaque puissante qui augmente de 55 les dégâts infligés en mêlée."},
[409755] = {name = "Attaque du raptor", additional_info = "Allonge\nInstantanée\nRequiert Arme de mêlée\n[q]Une attaque puissante qui augmente de 70 les dégâts infligés en mêlée."},
[409805] = {name = "Frappes féroces", additional_info = "[q]Vous obtenez la technique Frappes féroces :\n\n\nLorsque vous êtes en forme de félin, d’ours ou d’ours redoutable, les membres du groupe à moins de 20 mètres gagnent une férocité accrue en combat. Chaque coup en mêlée a 20 % de chances de conférer à l’attaquant une attaque supplémentaire avec 20 % de puissance d’attaque supplémentaire. Aucun effet si le membre du groupe bénéficie déjà de Totem Furie-des-vents."},
[409810] = {name = "Croissance sauvage", additional_info = "[q]Vous obtenez la technique Croissance sauvage :\n\n\nRend [34 / 100 * 7 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie en 7 sec aux membres du groupe du personnage-joueur ciblé à moins de 40 m de lui. La quantité de soins prodiguée est importante au début, puis diminue à mesure que Croissance sauvage atteint sa durée maximale."},
[409819] = {name = "Rugissement sauvage", additional_info = "[q]Vous obtenez la technique Rugissement sauvage :\n\n\nCoup de grâce qui augmente les dégâts physiques infligés de 30 % en forme de félin. La durée dépend du nombre de points de combo :\n 1 point : 14 s\n 2 points : 19 s\n 3 points : 24 s\n 4 points : 29 s\n 5 points : 34 s"},
[409824] = {name = "Fleur de vie", additional_info = "[q]Réduit le temps de recharge global de vos techniques Récupération et Fleur de vie de 1,1 s et vous obtenez la technique Fleur de vie :\n\n\nRend [4 / 100 * 7 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie à la cible en 7 sec. Quand Fleur de vie parvient au terme de sa durée ou est dissipée, la cible reçoit instantanément [57 / 100 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie et le lanceur ou la lanceuse récupère la moitié du coût du sort. Cet effet est cumulable jusqu’à 3 fois sur la même cible."},
[409826] = {name = "Fureur de la nature", additional_info = "[q]Vous obtenez la technique Fureur de la nature :\n\n\nAugmente la puissance de soins de 90 % des dégâts des sorts de nature pendant 30 sec."},
[409828] = {name = "Mutilation", additional_info = "[q]Vous obtenez la technique Mutilation (ours) et remplacez votre technique Griffe par Mutilation (félin). Cette technique bénéficie de tous les effets associés à Griffe et Mutiler et les déclenche.\n\n\nMutile la cible, ce qui lui inflige 160 % des dégâts normaux. De plus, les effets de saignement et Lambeau lui infligent 30 % de dégâts supplémentaires pendant 1 min. Cette technique bénéficie de tous les effets associés à Griffe et Mutiler et les déclenche.\n\n\nMutile la cible, ce qui lui inflige 270 % des dégâts normaux. De plus, les effets de saignement et Lambeau lui infligent 30 % de dégâts supplémentaires pendant 1 min. Vous gagnez 1 point de combo. Cette technique bénéficie de tous les effets associés à Griffe et Mutiler et les déclenche."},
[409832] = {name = "Fureur de Hurlorage", additional_info = "[q]Vous obtenez la technique Fureur de Hurlorage :\n\n\nRéduit le coût en mana de Courroux de 100 % et chaque fois que vous infligez des dégâts avec Courroux, vous avez 12 % de chances que votre prochaine incantation de Toucher guérisseur dans les 15 sec soit instantanée."},
[409856] = {name = "Rituel d’esprit", additional_info = "30 m de portée\nCanalisée (10 min d'incantation)\n[q]Commence un rituel pour libérer l’esprit d’un aventurier défunt."},
[409858] = {name = "Dummy Trigger", additional_info = nil},
[409906] = {name = "Guide de lumière", additional_info = "[q]Vous obtenez la technique Guide de lumière :\n\n\nLa cible devient un Guide de lumière pour tous les membres de votre groupe ou raid se trouvant dans un rayon de 40 m. Tous les soins que vous lancez sur ces cibles soignent également le guide pour un montant de points de vie égal à 100 % des soins prodigués. Une seule cible à la fois peut être le Guide de lumière. Dure 1 min."},
[409911] = {name = "Main de rétribution", additional_info = "[q]Vous obtenez la technique Main de rétribution :\n\n\nProvoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer.\n\n Pendant que vous connaissez cette technique, le bonus de menace de Fureur vertueuse passe à 80 % et Fureur vertueuse vous fait gagner du mana lorsque d’autres personnages vous soignent, pour 25 % du montant des soins. De plus, tant que Fureur vertueuse est actif, les dégâts qui vous font descendre sous 35 % de points de vie sont réduits de 20 %. Fureur vertueuse reste actif jusqu’à annulation."},
[409914] = {name = "Frappe du croisé", additional_info = "[q]Vous obtenez la technique Frappe du croisé :\n\n\nUne attaque instantanée qui inflige 75 % des dégâts de l’arme sous forme de dégâts du sacré et régénère 5 % de votre maximum de mana."},
[409918] = {name = "Inspiration (Jugement)", additional_info = nil},
[409920] = {name = "Apprendre la relique (Jugement)", additional_info = nil},
[409924] = {name = "Tempête divine", additional_info = "[q]Vous obtenez la technique Tempête divine :\n\n\nUne attaque instantanée avec une arme qui inflige 110 % des dégâts de l’arme à un maximum de 4 ennemis se trouvant à moins de 8 mètres. Tempête divine soigne jusqu’à 3 membres du groupe ou raid pour un total de 25 % des dégâts infligés."},
[409925] = {name = "Sceau du martyr", additional_info = "[q]Vous obtenez la technique Sceau du martyr :\n\n\nVous remplit d’esprit sacré pendant 30 sec. Chacune de vos attaques de mêlée inflige 40 % des dégâts d’armes à votre cible, mais vous perdez des points de vie pour 10 % des dégâts infligés. Tant que ce sceau est actif, chaque membre du groupe ou du raid à moins de 40 m gagne un montant de mana égal à 20 % des dégâts que ce sceau vous inflige.\n\n Lorsque l’énergie de ce sceau est déchaînée, un personnage adverse est jugé, ce qui lui inflige instantanément 70 % de dégâts de l’arme en sacrifiant vos points de vie pour 10 % des dégâts infligés."},
[409930] = {name = "Libram de jugement", additional_info = nil},
[409933] = {name = "Bouclier du vengeur", additional_info = "[q]Vous obtenez la technique Bouclier du vengeur :\n\n\nLance sur un ennemi un bouclier sacré qui inflige de [366 * (9.046514 + 0.676562 * Level + 0.019349 * Level * Level) / 100 + 0.091 * Spell power + 0.091 * Attack power] à [448 * (9.046514 + 0.676562 * Level + 0.019349 * Level * Level) / 100 + 0.091 * Spell power + 0.091 * Attack power] points de dégâts du sacré, l’hébète et rebondit ensuite sur des ennemis proches. Le sort frappe 3 cibles au total. Dure 10 sec."},
[409935] = {name = "Sacrifice divin", additional_info = "[q]Vous obtenez la technique Sacrifice divin :\n\n\n30 % des dégâts subis par les membres du groupe à moins de 30 m sont redirigés vers le paladin ou la paladine pendant 10 sec. Les dégâts qui le ou la font passer sous 20 % de points de vie interrompent l’effet et augmentent ses dégâts et soins de 10 % pendant 10 sec. Vous ne pouvez pas utiliser Sacrifice divin sous l’effet de Bénédiction de protection, Bouclier divin ou Protection divine, ni être la cible de ces techniques tant que Sacrifice divin est actif."},
[409936] = {name = "Source d’inspiration", additional_info = "[q]Vous obtenez la technique Source\n d’inspiration :\n\n\nVotre présence inspirante dissipe périodiquement les effets de peur et de sommeil sur les membres du groupe proches."},
[409940] = {name = "Spécialisation Ambidextrie", additional_info = "[q]Vous obtenez la technique Spécialisation Ambidextrie :\n\n\nAugmente les chances de toucher avec les sorts et les attaques de mêlée de 10 % lorsque vous utilisez deux armes. Frappe-tempête frappe à présent avec les deux armes lorsque vous en utilisez deux. Augmente les dégâts de l’arme en main gauche de 50 %."},
[409941] = {name = "Bouclier d’eau", additional_info = "[q]Vous obtenez la technique Bouclier d’eau :\n\n\nLe lanceur ou la lanceuse s’entoure de 3 globes d’eau, ce qui rend 1 % du maximum de mana toutes les 5 s. À toute attaque en mêlée ou à distance ou à tout sort subis, rend 4 % du maximum de mana. Un globe d’eau est alors dépensé. Un seul globe peut s’activer toutes les quelques secondes. Dure 10 min. Un seul Bouclier élémentaire peut être actif sur le chaman à la fois."},
[409943] = {name = "Maîtrise du bouclier", additional_info = "[q]Vous obtenez la technique Maîtrise du bouclier :\n\n\nChaque fois que vous bloquez, vous régénérez un montant de mana égal à 8 % de votre maximum et votre Armure est augmentée de 30 % de la valeur d’Armure de votre bouclier, cumulable jusqu’à 5 fois. Vous gagnez également 10 % de chances de bloquer et 15 % de valeur de blocage en permanence."},
[409944] = {name = "Soutien ancestral", additional_info = "[q]Vous obtenez la technique Soutien ancestral :\n\n\nPendant 10 sec, 25 % de vos dégâts sont convertis en soins pour jusqu’à 3 membres du groupe proches, et 100 % de vos soins sont convertis en dégâts vers la cible la plus récente de votre Horion de flammes."},
[409945] = {name = "Surcharge", additional_info = "[q]Vous obtenez la technique Surcharge ! :\n\n\nDonne à vos sorts Éclair, Chaîne d’éclairs, Salve de guérison, Vague de soins et Explosion de lave 50 % de chances de lancer un second sort semblable sur la même cible sans coût supplémentaire. Ce sort inflige la moitié des dégâts et ne génère pas de menace."},
[409947] = {name = "Bouclier de terre", additional_info = "[q]Vous obtenez la technique Bouclier de terre :\n\n\nProtège la cible avec un bouclier de terre, qui réduit de 30% le temps d’incantation ou de canalisation de sort perdu quand elle subit des dégâts. Les attaques rendent (100 */ 100) points de vie à la cible protégée. Cet effet ne peut se produire qu’une fois toutes les quelques secondes. 3 charges. Dure 10 min. Bouclier de terre ne peut être placé que sur une cible à la fois et un seul Bouclier élémentaire peut être actif sur une cible à la fois."},
[409952] = {name = "Explosion de lave", additional_info = "[q]Vous obtenez la technique Explosion de lave :\n\n\nVous lancez de la lave en fusion sur la cible, lui infligeant [469 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] à [605 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] points de dégâts de feu. Si Horion de flammes est actif sur la cible, Explosion de lave sera un coup critique."},
[409953] = {name = "Fouet de lave", additional_info = "[q]Vous obtenez la technique Fouet de lave :\n\n\nVous chargez votre arme en main gauche de lave, infligeant instantanément 150 % des dégâts de l’arme en main gauche. Les dégâts sont augmentés de 50 % si votre arme en main gauche est enchantée avec Langue de feu."},
[409955] = {name = "Voie de la terre", additional_info = "[q]Vous obtenez la technique Voie de la terre :\n\n\nTant qu’Arme Croque-roc est active sur votre arme principale, vous générez 50 % de menace supplémentaire, vous gagnez 30 % de points de vie, vous subissez des dégâts réduits de 10 %, réduit la probabilité que vous soyez touché par un coup critique infligé par une attaque en mêlée de 6 % et Horion de terre provoque les cibles pour qu’elles vous attaquent avec un temps de recharge distinct des autres horions, mais sa portée est réduite à la mêlée."},
[409958] = {name = "Maître tireur", additional_info = "[q]Vous obtenez la technique Maître tireur :\n\n\nAugmente vos chances de coup critique de 5 % et réduit le coût en mana de toutes vos techniques de tir de 25 %."},
[409960] = {name = "Cœur de lion", additional_info = "[q]Vous obtenez la technique Cœur de lion :\n\n\nInvoque le cœur d’un lion et augmente le total des caractéristiques des personnages alliés proches de 10 %, et le total de celles du chasseur ou de la chasseuse de 10 % supplémentaires."},
[409962] = {name = "Maîtrise des bêtes", additional_info = "[q]Vous obtenez la technique Maîtrise des bêtes :\n\n\nLes dégâts et points de vie de votre familier sont augmentés de 20 % et sa régénération de focalisation de 50 %. En outre, Grondement provoque également la cible pour qu’elle l’attaque pendant 3 sec."},
[409963] = {name = "Invocation d’un esprit", additional_info = nil},
[409974] = {name = "Ordre de tuer", additional_info = "[q]Vous obtenez la technique Ordre de tuer :\n\n\nDonne l’ordre de tuer. Les dégâts infligés par votre familier avec Griffe et Morsure sont augmentés de 60% pendant 30 sec. Chaque attaque Griffe ou Morsure effectuée par le familier réduit le bonus de dégâts de 20%."},
[409976] = {name = "Tir de la chimère", additional_info = "[q]Vous obtenez la technique Tir de la chimère :\n\n\nInfligez 100 % des dégâts de l’arme, réinitialise la piqûre ou morsure actuelle sur la cible et déclenche un effet :\n\n Morsure de serpent – Inflige instantanément 40 % des dégâts de Morsure de serpent.\n\n Morsure de vipère – Rend instantanément un montant de mana égal à 60 % du total drainé par Morsure de vipère.\n\n Piqûre de scorpide – Tente de désarmer la cible pendant 10 sec. Cet effet ne peut se produire plus d’une fois par minute."},
[409978] = {name = "Tir explosif", additional_info = nil},
[409979] = {name = "Loup solitaire", additional_info = "[q]Vous obtenez la technique Loup solitaire :\n\n\nVous infligez 30 % de dégâts supplémentaires avec toutes les attaques tant que vous n’avez pas de familier actif."},
[409988] = {name = "Lancement forcé de Bannissement retardé de personnage-joueur", additional_info = nil},
[409999] = {name = "Gravure de gants (Guide de lumière)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Guide de lumière :\n\n\nLa cible devient un Guide de lumière pour tous les membres de votre groupe ou raid se trouvant dans un rayon de 40 m. Tous les soins que vous lancez sur ces cibles soignent également le guide pour un montant de points de vie égal à 100 % des soins prodigués. Une seule cible à la fois peut être le Guide de lumière. Dure 1 min."},
[410001] = {name = "Gravure de gants (Main de rétribution)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Main de rétribution :\n\n\nProvoque la cible et la force à vous attaquer. Aucun effet si elle est déjà en train de vous attaquer.\n\n Pendant que vous connaissez cette technique, le bonus de menace de Fureur vertueuse passe à 80 % et Fureur vertueuse vous fait gagner du mana lorsque d’autres personnages vous soignent, pour 25 % du montant des soins. De plus, tant que Fureur vertueuse est actif, les dégâts qui vous font descendre sous 35 % de points de vie sont réduits de 20 %. Fureur vertueuse reste actif jusqu’à annulation."},
[410002] = {name = "Gravure de gants (Frappe du croisé)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Frappe du croisé :\n\n\nUne attaque instantanée qui inflige 75 % des dégâts de l’arme sous forme de dégâts du sacré et régénère 5 % de votre maximum de mana."},
[410008] = {name = "Gravure de pantalon (Bouclier du vengeur)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Bouclier du vengeur :\n\n\nLance sur un ennemi un bouclier sacré qui inflige de [366 * (9.046514 + 0.676562 * Level + 0.019349 * Level * Level) / 100 + 0.091 * Spell power + 0.091 * Attack power] à [448 * (9.046514 + 0.676562 * Level + 0.019349 * Level * Level) / 100 + 0.091 * Spell power + 0.091 * Attack power] points de dégâts du sacré, l’hébète et rebondit ensuite sur des ennemis proches. Le sort frappe 3 cibles au total. Dure 10 sec."},
[410010] = {name = "Gravure de pantalon (Sacrifice divin)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Sacrifice divin :\n\n\n30 % des dégâts subis par les membres du groupe à moins de 30 m sont redirigés vers le paladin ou la paladine pendant 10 sec. Les dégâts qui le ou la font passer sous 20 % de points de vie interrompent l’effet et augmentent ses dégâts et soins de 10 % pendant 10 sec. Vous ne pouvez pas utiliser Sacrifice divin sous l’effet de Bénédiction de protection, Bouclier divin ou Protection divine, ni être la cible de ces techniques tant que Sacrifice divin est actif."},
[410011] = {name = "Gravure de pantalon (Source d’inspiration)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Source d’inspiration :\n\n\nVotre présence inspirante dissipe périodiquement les effets de peur et de sommeil sur les membres du groupe proches."},
[410014] = {name = "Gravure de plastron (Tempête divine)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine la rune Tempête divine :\n\n\nUne attaque instantanée avec une arme qui inflige 110 % des dégâts de l’arme à un maximum de 4 ennemis se trouvant à moins de 8 mètres. Tempête divine soigne jusqu’à 3 membres du groupe ou raid pour un total de 25 % des dégâts infligés."},
[410015] = {name = "Gravure de plastron (Sceau du martyr)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine la rune Sceau du martyr :\n\n\nVous remplit d’esprit sacré pendant 30 sec. Chacune de vos attaques de mêlée inflige 40 % des dégâts d’armes à votre cible, mais vous perdez des points de vie pour 10 % des dégâts infligés. Tant que ce sceau est actif, chaque membre du groupe ou du raid à moins de 40 m gagne un montant de mana égal à 20 % des dégâts que ce sceau vous inflige.\n\n Lorsque l’énergie de ce sceau est déchaînée, un personnage adverse est jugé, ce qui lui inflige instantanément 70 % de dégâts de l’arme en sacrifiant vos points de vie pour 10 % des dégâts infligés."},
[410021] = {name = "Gravure de plastron (Frappes féroces)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Frappes féroces :\n\n\nLorsque vous êtes en forme de félin, d’ours ou d’ours redoutable, les membres du groupe à moins de 20 mètres gagnent une férocité accrue en combat. Chaque coup en mêlée a 20 % de chances de conférer à l’attaquant une attaque supplémentaire avec 20 % de puissance d’attaque supplémentaire. Aucun effet si le membre du groupe bénéficie déjà de Totem Furie-des-vents."},
[410023] = {name = "Gravure de pantalon (Rugissement sauvage)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Rugissement sauvage :\n\n\nCoup de grâce qui augmente les dégâts physiques infligés de 30 % en forme de félin. La durée dépend du nombre de points de combo :\n 1 point : 14 s\n 2 points : 19 s\n 3 points : 24 s\n 4 points : 29 s\n 5 points : 34 s"},
[410025] = {name = "Gravure de gants (Mutilation)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Mutilation :\n\n\nMutile la cible, ce qui lui inflige 160 % des dégâts normaux. De plus, les effets de saignement et Lambeau lui infligent 30 % de dégâts supplémentaires pendant 1 min. Cette technique bénéficie de tous les effets associés à Griffe et Mutiler et les déclenche."},
[410028] = {name = "Gravure de gants (Croissance sauvage)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Croissance sauvage :\n\n\nRend [34 / 100 * 7 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie en 7 sec aux membres du groupe du personnage-joueur ciblé à moins de 40 m de lui. La quantité de soins prodiguée est importante au début, puis diminue à mesure que Croissance sauvage atteint sa durée maximale."},
[410033] = {name = "Gravure de pantalon (Fleur de vie)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Fleur de vie :\n\nRéduit le temps de recharge global de vos techniques Récupération et Fleur de vie de 0.5,1 s et vous obtenez la technique Fleur de vie :\n\n\nRend [4 / 100 * 7 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie à la cible en 7 sec. Quand Fleur de vie parvient au terme de sa durée ou est dissipée, la cible reçoit instantanément [57 / 100 * (38.949830 + 0.606705 * Level + 0.167780 * Level * Level)] points de vie et le lanceur ou la lanceuse récupère la moitié du coût du sort. Cet effet est cumulable jusqu’à 3 fois sur la même cible."},
[410061] = {name = "Gravure de plastron (Fureur de Hurlorage)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Fureur de Hurlorage :\n\n\nRéduit le coût en mana de Courroux de 100 % et chaque fois que vous infligez des dégâts avec Courroux, vous avez 12 % de chances que votre prochaine incantation de Toucher guérisseur dans les 15 sec soit instantanée."},
[410094] = {name = "Gravure de plastron (Surcharge)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Surcharge :\n\n\nDonne à vos sorts Éclair, Chaîne d’éclairs, Salve de guérison, Vague de soins et Explosion de lave 50 % de chances de lancer un second sort semblable sur la même cible sans coût supplémentaire. Ce sort inflige la moitié des dégâts et ne génère pas de menace."},
[410095] = {name = "Gravure de gants (Explosion de lave)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Explosion de lave :\n\n\nVous lancez de la lave en fusion sur la cible, lui infligeant [469 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] à [605 / 100 * (7.583798 + 0.471881 * Level + 0.036599 * Level * Level)] points de dégâts de feu. Si Horion de flammes est actif sur la cible, Explosion de lave sera un coup critique."},
[410096] = {name = "Gravure de plastron (Spécialisation Ambidextrie)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Spécialisation Ambidextrie :\n\n\nAugmente les chances de toucher avec les sorts et les attaques de mêlée de 10 % lorsque vous utilisez deux armes. Frappe-tempête frappe à présent avec les deux armes lorsque vous en utilisez deux. Augmente les dégâts de l’arme en main gauche de 50 %."},
[410097] = {name = "Gravure de gants (Bouclier d’eau)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Bouclier d’eau :\n\n\nLe lanceur ou la lanceuse s’entoure de 3 globes d’eau, ce qui rend 1 % du maximum de mana toutes les 5 s. À toute attaque en mêlée ou à distance ou à tout sort subis, rend 4 % du maximum de mana. Un globe d’eau est alors dépensé. Un seul globe peut s’activer toutes les quelques secondes. Dure 10 min. Un seul Bouclier élémentaire peut être actif sur le chaman à la fois."},
[410098] = {name = "Gravure de plastron (Maîtrise du bouclier)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune bouclier Maîtrise du bouclier :\n\n\nChaque fois que vous bloquez, vous régénérez un montant de mana égal à 8 % de votre maximum et votre Armure est augmentée de 30 % de la valeur d’Armure de votre bouclier, cumulable jusqu’à 5 fois. Vous gagnez également 10 % de chances de bloquer et 15 % de valeur de blocage en permanence."},
