[415460] = {name = "Ardeur épuisante", additional_info = "[q]Vous obtenez la technique Ardeur épuisante :\n\n\nAugmente les chances de coup critique avec tous les sorts de 15 % mais vos coups critiques de sorts non-périodiques coûtent 1 % de votre mana de base en plus."},
[415467] = {name = "Régénération de masse", additional_info = "[q]Vous obtenez la technique Régénération de masse :\n\n\nRend aux membres du groupe du personnage-joueur ciblé à moins de 30 m de lui [(38.258376 + 0.904195 * Level + 0.161311 * Level * Level) * 42 / 100 * 3] points de vie en 3 sec et applique Balise temporelle à chaque cible pendant 15 s.\n\n Balise temporelle\n\nEnregistre la position spatio-temporelle de la cible. 80 % de tous les dégâts des arcanes infligés par le lanceur ou la lanceuse seront convertis en soins chronomantiques pour chaque cible actuelle de Balise temporelle du lanceur ou de la lanceuse. Ces soins sont réduits de 50 % sur vous-même, et également réduits de 80 % lorsque les dégâts sont infligés par des sorts des arcanes qui blessent plusieurs cibles."},
[415599] = {name = "Fureur obsessionnelle", additional_info = "[q]Vous obtenez la technique Fureur obsessionnelle :\n\n\nLorsque vous utilisez deux armes, vos dégâts physiques et votre vitesse de déplacement sont augmentés de 10 %."},
[415604] = {name = "Affliction éternelle", additional_info = "[q]Vous obtenez la technique Affliction éternelle :\n\n\nDrain de vie, Drain d’âme, Trait d’ombre, Enchaînement d’ombre, Douleur brûlante, Incinérer et Hanter rafraîchissent la durée de votre Corruption sur la cible à sa durée maximale."},
[415605] = {name = "Tactique démoniaque", additional_info = "[q]Vous obtenez la technique Tactique démoniaque :\n\n\nAugmente de 10% les chances de coup critique en mêlée ou avec les sorts pour votre familier et vous."},
[415709] = {name = "Survie du plus fort", additional_info = "[q]Vous obtenez la technique Survie du plus fort :\n\n\nRéduit de 6 % les chances de subir un coup critique en mêlée et réduit de 10 % tous les dégâts subis. Les dégâts subis sont réduits de 10 % supplémentaires lorsque vous êtes en Forme d’ours ou Forme d’ours redoutable."},
[415714] = {name = "Pluie guérisseuse", additional_info = "[q]Vous obtenez la technique Pluie guérisseuse :\n\n\nSélectionne une zone de 15 m autour du personnage-joueur ciblé et rend à tous les membres de son groupe dans cette zone [(29.888200 + 0.690312 * Level + 0.136267 * Level * Level) * 15 / 100] points de vie par seconde."},
[415729] = {name = "Illumination", additional_info = "[q]Vous obtenez la technique Illumination :\n\n\nVous infligez 10 % de dégâts supplémentaires lorsque vous avez plus de 70 % de votre mana. Sous 30 % de mana, 10 % de votre régénération de mana se poursuit pendant l’incantation."},
[415737] = {name = "Heureux hasard", additional_info = "[q]Vous obtenez la technique Heureux hasard :\n\n\nSoigner une cible avec Soins rapides réduit le temps d’incantation de votre prochain sort Soins inférieurs, Soins, Soins supérieurs ou Prière de soins de 20 % pendant 20 sec. Cumulable jusqu’à 3 fois."},
[415738] = {name = "Incandescence mentale", additional_info = "[q]Vous obtenez la technique Incandescence mentale :\n\n\nProvoque une explosion de magie des ombres autour de la cible ennemie et inflige [78 / 100 * (9.456667 + 0.635108 * Level + 0.039063 * Level * Level)] à [70 / 100 * (9.456667 + 0.635108 * Level + 0.039063 * Level * Level)] points de dégâts d’ombre toutes les 1 s. pendant 5 sec à tous les ennemis se trouvant à moins de 10 m de la cible."},
[415739] = {name = "Force d’âme", additional_info = "[q]Soins inférieurs, Soins, Soins supérieurs et Soins rapides réduisent la durée restante d’Âme affaiblie sur les cibles soignées de 4 s. De plus, les cibles de votre Mot de pouvoir : Bouclier gagnent de la rage en subissant des dégâts même si ces dégâts sont absorbés et Fureur vertueuse est déclenchée par l’absorption de dégâts de votre Mot de pouvoir : Bouclier comme s’il s’agissait de soins."},
[415740] = {name = "Force d’âme", additional_info = "[q]Vous obtenez la technique Force d’âme :\n\n\nSoins inférieurs, Soins, Soins supérieurs et Soins rapides réduisent la durée restante d’Âme affaiblie sur les cibles soignées de 4 s. De plus, les cibles de votre Mot de pouvoir : Bouclier gagnent de la rage en subissant des dégâts même si ces dégâts sont absorbés et Fureur vertueuse est déclenchée par l’absorption de dégâts de votre Mot de pouvoir : Bouclier comme s’il s’agissait de soins."},
[415750] = {name = "Incinérer", additional_info = "[q]Vous obtenez la technique Incinérer :\n\n\nBrûle votre adversaire en lui infligeant [222 / 100 * (6.568597 + 0.672028 * Level + 0.031721 * Level * Level)] à [258 / 100 * (6.568597 + 0.672028 * Level + 0.031721 * Level * Level)] points de dégâts et augmente les dégâts de feu que vous infligez de 25 % pendant 15 sec."},
[415756] = {name = "Exorciste", additional_info = "[q]Vous obtenez la technique Exorciste :\n\n\nExorcisme peut maintenant être lancé sur n’importe quelle cible et a 100 % de chances de coup critique supplémentaires contre les morts-vivants et les démons."},
[415759] = {name = "Coup de crâne", additional_info = "[q]Vous obtenez la technique Coup de crâne :\n\n\nVous chargez une cible jusqu’à 13 m et lui fracassez le crâne, ce qui interrompt son incantation et l’empêche de lancer tout sort de la même école pendant 2 sec. Partage le temps de recharge de Charge farouche."},
[415760] = {name = "Lacérer", additional_info = "[q]Vous obtenez la technique Lacérer :\n\n\nLacère la cible, ce qui la fait saigner de [(9.183105 + 0.616405 * Level + 0.028608 * Level * Level) * 20 / 100 * 5] points de dégâts en 15 sec plus 20 % des dégâts de l’arme par application existante de Lacérer sur la cible. Génère une quantité de menace élevée. Cet effet se cumule jusqu’à 5 fois sur la même cible."},
[415761] = {name = "Graine de vie", additional_info = "[q]Vous obtenez la technique Graine de vie :\n\n\nQuand vous réussissez des soins critiques avec un sort de soins, vous plantez une Graine de vie sur la cible pour un montant égal à 30 % des points de vie rendus. La Graine de vie fleurira lors de la prochaine attaque sur la cible. Dure 15 sec."},
[415818] = {name = "Entraînement de sniper", additional_info = "[q]Vous obtenez la technique Entraînement de sniper :\n\n\nVos techniques de Tir gagnent 10 % de chances de coup critique supplémentaires lorsque vous n’avez pas bougé depuis 6 s."},
[415936] = {name = "Gravure de gants (Bombe vivante)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Bombe vivante :\n\n\nLa cible devient une bombe vivante qui inflige [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 85 / 100 * 4] points de dégâts de feu en 12 sec. Au bout de 12 sec ou quand le sort est dissipé, la cible explose en infligeant [(13.828124 + 0.018012 * Level + 0.044141 * Level * Level) * 171 / 100] points de dégâts de feu aux ennemis à moins de 10 mètres"},
[415939] = {name = "Gravure de pantalon (Régénération de masse)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre ceinture la rune Régénération de masse :\n\n\nRend aux membres du groupe du personnage-joueur ciblé à moins de 30 m de lui [(38.258376 + 0.904195 * Level + 0.161311 * Level * Level) * 42 / 100 * 3] points de vie en 3 sec et applique Balise temporelle à chaque cible pendant 15 s.\n\n Balise temporelle\n\nEnregistre la position spatio-temporelle de la cible. 80 % de tous les dégâts des arcanes infligés par le lanceur ou la lanceuse seront convertis en soins chronomantiques pour chaque cible actuelle de Balise temporelle du lanceur ou de la lanceuse. Ces soins sont réduits de 50 % sur vous-même, et également réduits de 80 % lorsque les dégâts sont infligés par des sorts des arcanes qui blessent plusieurs cibles."},
[415942] = {name = "Gravure de plastron (Illumination)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Illumination :\n\n\nVous infligez 10 % de dégâts supplémentaires lorsque vous avez plus de 70 % de votre mana. Sous 30 % de mana, 10 % de votre régénération de mana se poursuit pendant l’incantation."},
