[415996] = {name = "Gravure de gants (Incandescence mentale)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Incandescence mentale :\n\n\nProvoque une explosion de magie des ombres autour de la cible ennemie et inflige [78 / 100 * (9.456667 + 0.635108 * Level + 0.039063 * Level * Level)] à [70 / 100 * (9.456667 + 0.635108 * Level + 0.039063 * Level * Level)] points de dégâts d’ombre toutes les 1 s. pendant 5 sec à tous les ennemis se trouvant à moins de 10 m de la cible."},
[415997] = {name = "Gravure de plastron (Force d’âme)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Force d’âme :\n\n\nSoins inférieurs, Soins, Soins supérieurs et Soins rapides réduisent la durée restante d’Âme affaiblie sur les cibles soignées de 4 s. De plus, les cibles de votre Mot de pouvoir : Bouclier gagnent de la rage en subissant des dégâts même si ces dégâts sont absorbés et Fureur vertueuse est déclenchée par l’absorption de dégâts de votre Mot de pouvoir : Bouclier comme s’il s’agissait de soins."},
[416003] = {name = "Gravure de gants (Fureur obsessionnelle)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Fureur obsessionnelle :\n\n\nLorsque vous utilisez deux armes, vos dégâts physiques et votre vitesse de déplacement sont augmentés de 10 %."},
[416008] = {name = "Gravure de pantalon (Affliction éternelle)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Affliction éternelle :\n\n\nDrain de vie, Drain d’âme, Trait d’ombre, Enchaînement d’ombre, Douleur brûlante, Incinérer et Hanter rafraîchissent la durée de votre Corruption sur la cible à sa durée maximale."},
[416009] = {name = "Gravure de plastron (Tactique démoniaque)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Tactique démoniaque :\n\n\nAugmente de 10% les chances de coup critique en mêlée ou avec les sorts pour votre familier et vous."},
[416015] = {name = "Gravure de pantalon (Incinérer)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Incinérer :\n\n\nBrûle votre adversaire en lui infligeant [222 / 100 * (6.568597 + 0.672028 * Level + 0.031721 * Level * Level)] à [258 / 100 * (6.568597 + 0.672028 * Level + 0.031721 * Level * Level)] points de dégâts et augmente les dégâts de feu que vous infligez de 25 % pendant 15 sec."},
[416037] = {name = "Gravure de pantalons (Exorciste)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Exorciste :\n\n\nExorcisme peut maintenant être lancé sur n’importe quelle cible et a 100 % de chances de coup critique supplémentaires contre les morts-vivants et les démons."},
[416042] = {name = "Gravure de plastron (Survie du plus fort)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Survie du plus fort :\n\n\nRéduit de 6 % les chances de subir un coup critique en mêlée et réduit de 10 % tous les dégâts subis. Les dégâts subis sont réduits de 10 % supplémentaires lorsque vous êtes en Forme d’ours ou Forme d’ours redoutable."},
[416044] = {name = "Gravure de gants (Éclat solaire)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune Éclat solaire :\n\n\nBrûle l’ennemi et lui inflige [130 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] à [152 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] points de dégâts de nature puis [65 * 4 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] points de dégâts de nature supplémentaires en 12 sec.\n\n De plus, lorsque vous êtes en forme d’ours, de félin ou d’ours redoutable, votre Éclat lunaire est remplacé par Éclat solaire (ours) ou Éclat solaire (félin). Ce sort bénéficie et déclenche tous les effets associés à Éclat lunaire.\n\n Éclat solaire (ours)\nBrûle l’ennemi et lui inflige [130 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] à [152 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] points de dégâts de nature puis [65 * 4 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] points de dégâts de nature supplémentaires en 12 sec.\n\n Éclat solaire (félin)\nBrûle l’ennemi et lui inflige [130 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] à [152 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] points de dégâts de nature puis [65 * 4 * (9.183105 + 0.616405 * Level + 0.028608 * Level * Level) / 100] points de dégâts de nature supplémentaires en 12 sec. Octroie 1 point de combo."},
[416046] = {name = "Gravure de pantalon (Coup de crâne)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Coup de crâne :\n\n\nVous chargez une cible jusqu’à 13 m et lui fracassez le crâne, ce qui interrompt son incantation et l’empêche de lancer tout sort de la même école pendant 2 sec. Partage le temps de recharge de Charge farouche."},
[416049] = {name = "Gravure de gants (Lacérer)", additional_info = "3 s d'incantation\nRequiert Gants\n[q]Grave sur vos gants la rune\n Lacérer :\n\n\nLacère la cible, ce qui la fait saigner de [(9.183105 + 0.616405 * Level + 0.028608 * Level * Level) * 20 / 100 * 5] points de dégâts en 15 sec plus 20 % des dégâts de l’arme par application existante de Lacérer sur la cible. Génère une quantité de menace élevée. Cet effet se cumule jusqu’à 5 fois sur la même cible."},
[416050] = {name = "Gravure de plastron (Graine de vie)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Graine de vie :\n\n\nQuand vous réussissez des soins critiques avec un sort de soins, vous plantez une Graine de vie sur la cible pour un montant égal à 30 % des points de vie rendus. La Graine de vie fleurira lors de la prochaine attaque sur la cible. Dure 15 sec."},
[416057] = {name = "Gravure de plastron (Pluie guérisseuse)", additional_info = "3 s d'incantation\nRequiert Torse, Torse\n[q]Grave sur votre poitrine ou votre robe la rune Pluie guérisseuse :\n\n\nSélectionne une zone de 15 m autour du personnage-joueur ciblé et rend à tous les membres de son groupe dans cette zone [(29.888200 + 0.690312 * Level + 0.136267 * Level * Level) * 15 / 100] points de vie par seconde."},
[416091] = {name = "Gravure de pantalon (Entraînement de sniper)", additional_info = "3 s d'incantation\nRequiert Pantalons\n[q]Grave sur votre pantalon la rune Entraînement de sniper :\n\n\nVos techniques de Tir gagnent 10 % de chances de coup critique supplémentaires lorsque vous n’avez pas bougé depuis 6 s."},
