local addonName, addonTable = ...

addonTable.questData['es'] = {
[1] = {title = "The \"Chow\" Quest (123)aa", objective = "Kill Kobold Vermin, 2 of em.$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAINTe van a lanzar el siguiente hechizo:\n\n", description = "$Tpunk;!  Kill Kobold Vermin, 2 of em. NEW TEST AGAINTe van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nPollo otro\n\nRecibirás:\n\n"},
[2] = {title = "La garra de Garrafilada", objective = "Llévale la garra de Garrafilada a Senani Truenozón en el Puesto del Hachazo, Vallefresno.", description = "El poderoso hipogrifo Garrafilada ha sido ejecutado, con la garra de la bestia derribada como testimonio de tu victoria. Seguro que Senani Truenozón, del Puesto del Hachazo, estará interesado en ver este trofeo que prueba tus actos.", progress = "Sí, poderoso <clase>, he presentido tu llegada. Confío que tienes más noticias que darme sobre tu caza.", completion = "De lo más impresionante, &lt;nombre&gt;... ¡no puede haber sido un paseo conseguir la garra de Garrafilada! ¡La Caza de Vallefresno te está yendo bien! \n\nGarrafilada lleva muchos años aterrorizando a los peones de los aserraderos cuando se trasladan a Puesto del Hachazo y se cruzan en su ruta. No lo dudes, cuando se corra la voz de que doblegaste a ese monstruo, ¡se escucharán muchas canciones alabando tu valor en los campamentos y aserraderos de todo Vallefresno!", rewards = "Recibirás:\n\n"},
[5] = {title = "La hambruna de Gato", objective = "Habla con el Chef Grual.", description = "¡Llevo semanas escondiéndome en esta ciudad fantasma y no he comido más que larvas y hierbajos! Necesito algo de comida decente. Estoy en disposición de pagar bien.\n\nSi me traes un festín, te pagaré generosamente.\n\nHe oído que el chef Grual del Mesón del Cuervo Escarlata de Villa Oscura hace unas estupendas croquetas de cangrejo...", progress = nil, completion = "Necesitas croquetas de cangrejo, ¿verdad? Bueno, quizás pueda cocinarte algunas...", rewards = "Recibirás:\n\n"},
[6] = {title = "La recompensa por Garrick Piesuaves", objective = "Mata a Garrick Piesuaves y lleva su cabeza al ayudante Willem a la Abadía de Villanorte.", description = "Garrick Piesuaves es un maleante que lleva semanas acosando a granjeros y mercaderes. Ha sido visto en una choza cerca de los viñedos, cruzando el puente al este de la abadía. Tráeme la cabeza de ese villano y podrás quedarte con su botín.\n\nPero ve con cuidado, <nombre>. Garrick anda con una pandilla de matones. Puede que no te sea fácil llegar hasta él.", progress = "¿Has acabado con Garrick?", completion = "¡Lo has conseguido! ¡Acabas de ganarte una hermosa recompensa!", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones estrechosTúnica a capasCapa insignia\n\nTambién recibirás:\n\nTúnica a capasCapa insigniaPantalones estrechos"},
[7] = {title = "La limpieza del campamento kóbold", objective = "Mata 10 alimañas kobold y ve a hablar con el alguacil McBride.", description = "<nombre>, lo primero que tienes que hacer es limpiar; un clan de kobolds ha invadido los bosques del norte. Ve y liquida a todos los kobolds que encuentres; si reducimos sus filas podremos echarlos de Villanorte.", progress = nil, completion = "Buen trabajo. Los kobolds son ladrones y cobardes, pero en gran número suponen una amenaza más para los humanos de Ventormenta.\n\nTe agradezco lo que has hecho.", rewards = "Recibirás:\n\n"},
[8] = {title = "Un trato de pícaro", objective = "Lleva la carta indefinida a Claros de Tirisfal y entrégasela a la tabernera Renee.", description = "¡Eh, colega! ¿Le harías un favor a un joven hombre que ha combatido a más zombis y arañas de los que puede contar?\n\nTengo una carta que tiene que llegar a Rémol... Es para una tabernera llamada Renee no sé qué. No creo que importe cómo se apellida, la verdad.\n\nPero el sitio es muy acogedor y está lleno de víctimas de la Plaga que intentan abrirse paso en el mundo. Es el sitio ideal para descansar... si es que necesitas hacerlo. Anímate... si vas, te pagaré bien.", progress = "¿Sí? ¿Sí? ¿Qué hay?\n\nEsos Renegados sí que tienen estómagos grandes. Tú ponte cómodo... Da un respiro a tu agotado esqueleto. Me llamo Renee, por si quieres algo.", completion = "¿Eh? ¿Una carta? ¡Qué bien! Estaba esperando noticias de mi querida madre, que está en Camposanto. Es una santa, eso es lo que es. Me pregunto qué tal estará.\n\n¿Qué? ¿Qué? ¿Sueno demasiado alegre? No te he engañado, ¿eh?\n\nPues me da igual. Y además, tampoco te importa de quién es la carta. ¡Hala! ¡Aire!\n\nVuelve cuando tengas dinero que gastar, holgazán.\n\nYa volverás, ya, cuando necesites descansar después de unos días de aventuras o de alguna estupidez parecida.", rewards = "Podrás elegir una de estas recompensas:\n\nChampiñón de bosqueAgua de manantial refrescante\n\nTambién recibirás:\n\nAgua de manantial refrescanteChampiñón de bosque"},
[9] = {title = "Campos de la muerte", objective = "El granjero Saldean quiere que mates 20 Vigilantes de la cosecha.", description = "¡Mira lo que le ha ocurrido a este lugar! Estas tierras estaban antes en manos de buenos granjeros. Pero los condenados ladrones les han ahuyentado a todos. ¡Pero no a mí! Eso sí, parece que algunos Vigilantes de la cosecha han invadido los campos.\n\nSi estás <preparado/preparada> me gustaría que fueras y mataras a 20 de ellos. Vuelve cuando hayas terminado y te pagaré. Si terminas con los de mi campo, puedes limpiar también los campos vecinos.", progress = nil, completion = "Buen trabajo amigo. Te has ganado bien tu sueldo. Quién sabe, quizás los Páramos de Poniente vuelvan a ser prósperos.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones de cosechadorToga de cosechador\n\nTambién recibirás:\n\nToga de cosechadorPantalones de cosechador"},
[10] = {title = "La redención de Patagolpe", objective = "Descubre qué le ha ocurrido al perito adjunto Patagolpe y llévalo a él o su equipo de análisis ante el perito jefe Picoflojo, que está en Gadgetzan.", description = "¡Oh, esto es horrible! Envié a mi mejor ayudante, el perito adjunto Patagolpe, al desierto situado al sudeste de aquí, una zona que se conoce como Sima Abierta. Bien, pues ha desaparecido y su último informe contenía más información acerca de esos extraños bichos que había visto por la zona. ¿Es posible que ya hayan infestado dos grandes regiones del desierto?\n\nPor favor, ¡necesito que vayas a Sima Abierta en busca de Patagolpe! Y si no lo encuentras a él, al menos a ver si aparece su equipo de análisis. Me temo que pueda ser lo único que quede de ese pobre chico.", progress = "Y bien, ¿qué novedades traes? Parece que te hayan dado una paliza... ¿Patagolpe está vivo?", completion = "El joven Patagolpe era un buen goblin. Esperemos que, gracias a los datos registrados en su equipo de análisis, su muerte no haya sido en vano.\n\nParece que ahora el problema con estos bichos está en nuestras manos. Son demasiado astutos y las investigaciones realizadas hasta el momento señalan la posibilidad de que estén controlados por una tercera persona. Si eso es cierto, ¡no le espera nada bueno ni a Gadgetzan ni al desierto en general!", rewards = "Recibirás:\n\n"},
[11] = {title = "Recompensas por los gnolls Zarparrío", objective = "Lleva 8 brazaletes gnoll violáceos al ayudante del alguacil Rainer al Cuartel.", description = "En las fronteras del Bosque de Elwynn se han avistado gnolls, unas criaturas salvajes que seguramente no están allí por trabajo. Un grupo enorme de ellos, uno más numeroso del que podemos contener solos, ha infestado los bosques que hay al sur de la torre de vigilancia. Otro grupo ha infestado las áreas cercanas al Lago del Hito, al este.\n\nEl ejército de Ventormenta elogiará a quienquiera que le ayude a matarlos. Tráeme sus brazaletes gnoll violáceos como prueba de tu hazaña.", progress = "Salve, <nombre>. ¿Has estado matando gnolls...?", completion = "¡Has estado muy ocupado! Muchas gracias, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nRodela de miliciaPantalones de Urchin\n\nTambién recibirás:\n\nPantalones de UrchinRodela de milicia"},
[12] = {title = "Las Milicias del Pueblo", objective = "Gryan Mantorrecio quiere que mates 15 tramperos Defias y 15 contrabandistas Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.", description = "Las Milicias del Pueblo solo tienen un objetivo: defender las tierras de los Páramos de Poniente y traer la paz a nuestro entorno. Por desgracia, el precio de la paz suele pagarse en sangre.\n\nSegún el informe de uno de mis rastreadores, una banda de tramperos Defias ha sido vista cerca de la Mina de Jango al noreste de aquí, en La Granja de Molsen y en la Plantación de Calabazas de Cejade. Si quieres unirte a nuestras filas, acaba con 15 tramperos Defias y con 15 contrabandistas Defias y vuelve a verme.", progress = "Quizás no me he explicado bien. Para demostrar que mereces servir a las Milicias del Pueblo y a la Luz, tienes que matar a 15 tramperos Defias y a 15 contrabandistas Defias. Vuelve aquí cuando lo hayas hecho.", completion = "Bien hecho, &lt;nombre&gt;. Mi explorador ha sido testigo de tus hazañas. Estás dando muy buenas pruebas de tu valía.", rewards = "Recibirás:\n\n"},
[13] = {title = "Las Milicias del Pueblo", objective = "Gryan Mantorrecio quiere que mates 15 saqueadores Defias y 15 despojadores Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.", description = "Una banda de despiadados saqueadores Defias ha sido vista saqueando la Mina de la Costa de Oro, el Arroyo de la Luna y la Hacienda de Alexston. Las Milicias del Pueblo no tolerarán ese comportamiento. Avisa inmediatamente, <nombre>, y haz conocer la presencia de la Luz en los Páramos de Poniente. La Mina de la Costa de Oro está cerca de la costa, al oeste de la torre. El siguiente paso de tu instrucción consistirá en acabar con 15 de esos saqueadores Defias y con 15 despojadores Defias.", progress = nil, completion = "Hasta ahora tus hazañas no han dejado de probar tu valía para las Milicias del Pueblo.", rewards = "Recibirás:\n\n"},
[14] = {title = "Las Milicias del Pueblo", objective = "Gryan Mantorrecio quiere que mates 15 bandoleros Defias y 5 acechacaminos Defias. Cuando lo hayas hecho vuelve a verle en la Colina del Centinela.", description = "Algunos Defias nos han esquivado. Según informa mi mejor rastreador, los Defias han estado saqueando y expoliando el campo, todo el sureste de los Páramos de Poniente. Creemos que se están ocultando en Las Colinas Afiladas, planeando sus siguientes pasos. Acaba con esos miserables en nombre de Las Milicias del Pueblo.", progress = nil, completion = "Cuando dejé las tierras malditas de Lordaeron, regresé para encontrar mi patria sumida en una situación desalentadora. Pero aún hay esperanza para los Páramos de Poniente. Has demostrado tu valor en la batalla, y es evidente que sirves con honor a nuestra causa. Me es por ello un gran honor ordenarte como miembro de Las Milicias del Pueblo. Que la Luz guíe siempre tu camino.", rewards = "Podrás elegir una de estas recompensas:\n\nFilo de las Milicias del puebloPuño de la Milicia del puebloChispa de la Milicia del Pueblo\n\nTambién recibirás:\n\nPuño de la Milicia del puebloFilo de las Milicias del puebloChispa de la Milicia del Pueblo"},
[15] = {title = "Investigar la Mina del Eco", objective = "Mata 10 obreros kobold y ve a hablar con el alguacil McBride.", description = "<nombre>, los rastreadores dicen que hay más kobolds de lo que creíamos. Un grupo de obreros ha acampado cerca de la Mina del Eco, al norte de aquí.\n\nVe a la mina y liquídalos; sabemos que hay al menos diez. Comprueba que no quede ninguno más y vuelve.", progress = nil, completion = "No me gusta que haya kobolds en la mina; eso no puede ser bueno. Bien, acepta esto como pago, y vuelve a hablar conmigo cuando quieras; puede que tengas que volver a la mina...", rewards = "Recibirás:\n\n"},
[16] = {title = "Dale de beber a Gerard", objective = "El trabajo en la granja es agotador, por eso intento siempre tener una buena reserva de agua de manantial que ayude a soportarlo.\n\nSi tienes algo de agua, podríamos hacer un intercambio.", description = nil, progress = "El trabajo en la granja es agotador, por eso intento siempre tener una buena reserva de agua de manantial que ayude a soportarlo.\n\nSi tienes algo de agua, podríamos hacer un intercambio.", completion = "¡Gracias, &lt;nombre&gt;! Y vuelve si quieres hacer otro intercambio.", rewards = "Recibirás:\n\nManzana roja lustrosa"},
[17] = {title = "Componentes de Uldaman", objective = "Lleva 12 setas moradas a Ghak Sanadón a Thelsamar.", description = "Hace poco me hablaron de un organismo recién descubierto, las setas moradas. Crecen arracimadas en lo más profundo de la excavación de Uldaman; es el único sitio del que tengo noticia. Quiero ver qué usos potenciales pueden tener en la alquimia, por eso necesito tu ayuda.\n\nTráeme una docena de setas moradas. Es posible que se defiendan con esporas venenosas; ten cuidado.\n\n¡A cambio, te daré uno de mis famosos elixires vigorizantes!", progress = "¿Me traes las setas moradas? ¡Si no, no te pago!", completion = "¡Has hecho un buen trabajo otra vez, &lt;nombre&gt;! Esto va a ser muy interesante.\n\nY como te prometí, aquí tienes uno de mis mejores elixires vigorizantes.\n\nSi te interesa la alquimia, puedo ofrecerte otros trabajos. En todo caso, te agradezco tu ayuda.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nPoción reconstituyente"},
[18] = {title = "Hermandad de ladrones", objective = "Lleva 12 pañuelos de tela de saco rojo al ayudante Willem cerca de la Abadía de Villanorte.", description = "Hace poco llegó una banda de maleantes a Villanorte. Se hacen llamar la Hermandad Defias; se les ha visto cruzando el río hacia el este.\n\nNo sé qué están haciendo, ¡pero seguro que no es nada bueno! Tráeme sus pañuelos y te daré un arma como recompensa.", progress = "¿Me traes los pañuelos?", completion = "Ah, traes unos pañuelos. El ejército de Ventormenta te agradece tu ayuda.", rewards = "Podrás elegir una de estas recompensas:\n\nDaga de miliciaMartillo de miliciaEspada corta de miliciaMartillo de guerra de miliciaBastón de intendente de milicia\n\nTambién recibirás:\n\nBastón de intendente de miliciaEspada corta de miliciaDaga de miliciaMartillo de guerra de miliciaMartillo de milicia"},
[19] = {title = "Tharil'zun", objective = "Lleva la cabeza de Tharil'zun al alguacil Marris a Crestagrana.", description = "Hay avanzados y renegados Roca Negra tendiendo emboscadas entre aquí y el Fuerte de Petravista. El jefe de los avanzados se llama Tharil'zun y lo queremos muerto. ¡Tráeme su cabeza!", progress = "Los orcos de Roca Negra siguen atacando. ¿Has acabado con Tharil'zun?", completion = "Era fuerte y cruel; seguro que no fue fácil derrotarlo. Gracias; aquí tienes tu recompensa.", rewards = "Podrás elegir una de estas recompensas:\n\nRodela endurecida con fuegoTriturador orco\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nRodela endurecida con fuegoTriturador orco"},
[20] = {title = "La amenaza de los Roca Negra", objective = "Lleva 10 restos de hacha de guerra al alguacil Marris a Villa del Lago.", description = "Los orcos Roca Negra llegaron desde Las Estepas Ardientes y han invadido el Fuerte de Petravista, al norte de aquí. Andan merodeando por el norte de Villa del Lago y tienen a los ciudadanos encerrados como a ganado. \n\nLibra a Villa del Lago de esta amenaza. Mata a los orcos y tráeme 10 restos de hacha de guerra como prueba. \n\nAunque nos amenazan desde el norte, sabemos que hay más al sureste de Crestagrana. Si los encuentras, mátalos también.", progress = "¿Has estado liquidando orcos, <nombre>? Demuéstramelo.", completion = "Ahora hay muchos menos orcos de los que ocuparse; gracias.", rewards = "Recibirás:\n\n"},
[21] = {title = "Escarmuza en la Mina del eco", objective = "Mata 12 peones kobold y ve a hablar con el alguacil McBride a la Abadía de Villanorte.", description = "Ha quedado demostrado que tenemos que purgar la Mina del Eco. Vuelve allí y ayuda a eliminar a los kobolds.\n\nDate prisa, <nombre>. Cuanto más tiempo dejemos a los kobolds en la mina, más nos costará expulsarlos de Villanorte.", progress = nil, completion = "Has vuelto a ganarte mi respeto y la gratitud del ejército de Ventormenta. Enviaré a otro equipo para ver si quedan kobolds en la mina; para ti tengo otras misiones.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de modistoBotas de modistoGuantes de modisto\n\nTambién recibirás:\n\nCinturón de modistoBotas de modistoGuantes de modisto"},
[22] = {title = "Pastel de hígado de dentosangre", objective = "Salma Saldean necesita 8 hígados de dentosangre para hacer un pastel de hígado de dentosangre.", description = "Las cebollas están peladas. El ajo machacado. La corteza está tostada. Las hierbas de eneldo picadas. ¡Lo único que me falta para mi famosa empanada de carne son 8 hígados de dentosangre!", progress = "¡Todo lo que necesito para mi famosa empanada de carne son 8 hígados de dentosangre!", completion = "¡Son perfectos, &lt;nombre&gt;! Muchas gracias. El granjero Saldean y yo vamos a darnos un gran festín. Toma, esto es para ti, por todo el trabajo. No pensarías que iba a dejar que un &lt;clase&gt; como tú se fuera con hambre, ¿eh?", rewards = "Recibirás:\n\nPastel de hígado de DentosangreReceta: tarta de hígado de Dentosangre"},
[23] = {title = "La zarpa de Ursangous", objective = "Llévale la zarpa de Ursangous a Senani Truenozón en el Puesto del Hachazo, Vallefresno.", description = "La Zarpa del poderoso oso Ursangous es tu trofeo para esta parte de la Caza de Vallefresno. La pequeña media luna de su zarpa es un emblema cuyo significado sigue siendo un misterio.\n\nDeberías llevar la zarpa a Senani Truenozón, en el Puesto del Hachazo, para demostrar que has vencido a la poderosa criatura y has completado esta parte de la caza.", progress = "Ah, <nombre>, ¡precisamente estaba pensando en ti! ¿Cómo te va la caza?", completion = "¡Ah, la zarpa de Ursangous! Tu talento me impresiona, &lt;clase&gt;; muchos han perecido bajo su ira. La media luna de su zarpa no deja lugar a dudas, los elfos de la noche que merodean por Vallefresno lo amaestraron. Ursangous es... era el oso más poderoso que podías encontrarte por estos lares. Derrotar a semejante criatura es toda una muestra de tu empeño y determinación.\n\n¡Tus antepasados estarían orgullosos de ti!", rewards = "Recibirás:\n\n"},
[24] = {title = "La cabeza de Shadumbra", objective = "Llévale la cabeza de Shadumbra a Senani Truenozón en el Puesto del Hachazo, Vallefresno.", description = "El otrora veloz Shadumbra yace ahora inerte ya que te atribuyes esta victoria. La cabeza del sable de la noche caído es un trofeo digno para este combate de la Caza de Vallefresno. Senani Truenozón, del Puesto del Hachazo, querrá ver tal trofeo para darte lo que te corresponde por esta parte de la Caza de Vallefresno.", progress = "¡Saludos de nuevo, <nombre>! ¿Has venido a mostrarme la prueba de tu caza?", completion = "¡Una victoria para &lt;nombre&gt; en la Caza de Vallefresno! Muy bien, la cabeza de Shadumbra es sin lugar a dudas un trofeo apropiado para atribuirse tan impresionante lucha.\n\nLa ferocidad de Shadumbra era de sobras conocida en toda la avanzada. Cualquier viaje hacia el oeste, especialmente a pie para salir de La Ensenada de Zoram, conllevaba mucho riesgo debido a este tigre. Gracias a ti, este peligro ya no existe.", rewards = "Recibirás:\n\n"},
[25] = {title = "Paso del Espolón bloqueado", objective = "Envía 12 elementales de agua podrida al Lago Mystral que esperan al este de El Paso del Espolón y al suroeste del Puesto del Hachazo.\n\nExplora la glorieta que está en el Lago Mystral y que domina la cercana avanzada de la Alianza.\n\nVuelve junto a Mastok Sibilante en el Puesto del Hachazo, Vallefresno.", description = "Si quieres ser de utilidad, ¿por qué no haces algo para remediar la situación cerca de El Paso del Espolón?\n\nEl transporte entre Vallefresno y El Espolón está paralizado porque el lago está cobrando vida y ataca a cualquiera que se acerque... lo que significa a cualquiera que utilice el paso. Lo que sería aún más útil que poner firmes a los elementales de agua allí sería tener a alguien rastreando la glorieta del Lago Mystral, la que tiene una vista ininterrumpida de la avanzada de la Alianza en ese lugar.\n\n¿Te apetece ser útil?", progress = nil, completion = "Sí, he oído que alguien había abierto el lago como un demonio de polvo por Los Baldíos. Parece que podríamos establecer un punto de observación por allí para mantener un ojo sobre nuestros amigos de la Alianza. Bueno, eso si conseguimos mantenerlo al nivel mínimo...\n\nParece que has resultado ser bastante útil después de todo; ahora el transporte entre este lugar y Espolón deberían ser menos accidentados. Aquí tienes, te lo has ganado.", rewards = "Recibirás:\n\n"},
[26] = {title = "Una lección que aprender", objective = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Dendrite Rafagaestelar.", description = "<nombre>, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en <un valioso protector/una valiosa protectora> de la naturaleza.\n\nHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenario nos ha bendecido. Una forma que podemos adoptar y nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna y hablar con Dendrite Rafagaestelar, del Círculo Cenarion. Él será tu guía.", progress = nil, completion = "Saludos, joven &lt;clase&gt;, ha llegado el momento de que conozcas nuestra forma acuática. Te permitirá moverte libre e indefinidamente por el agua. Encontrarás muy útil este aspecto, pronto te darás cuenta de que es una bendición. Pero debes ganártelo antes de dominarlo.\n\nDeberás superar dos pruebas. La primera probará tu capacidad para trabajar en el agua bajo presión. La segunda, tu resolución y tu comprensión de los aspectos de la forma acuática.\n\nEstate preparado, el momento de la prueba se acerca.", rewards = "Recibirás:\n\n"},
[27] = {title = "Una lección que aprender", objective = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Dendrite Rafagaestelar.", description = "<nombre>, he seguido tus progresos con gran gozo. Sin duda te estás convirtiendo en <un valioso protector/una valiosa protectora> de la naturaleza. \n\nHa llegado el momento de que aprendas más sobre una de las formas animales con las que Cenario nos ha bendecido. Una forma que podemos adoptar y nos permite controlar el agua. Tendrás que viajar al poblado Amparo de la Noche, que está en Claro de la Luna, y hablar con Dendrite Rafagaestelar del Círculo Cenarion. Él será tu guía.", progress = nil, completion = "Saludos, joven &lt;clase&gt;, ha llegado el momento de que conozcas nuestra forma acuática. Se trata de una técnica que te permitirá moverte libre e indefinidamente por el agua. Encontrarás muy útil este aspecto y pronto te darás cuenta de que es una bendición. Pero debes ganártelo antes de dominarlo...\n\nDeberás superar dos pruebas. La primera probará tu capacidad para trabajar en el agua bajo presión. La segunda, tu resolución y tu comprensión de los aspectos de la forma acuática.\n\nPrepárate pues el momento de la prueba se acerca.", rewards = "Recibirás:\n\n"},
[28] = {title = "La prueba del lago", objective = "Lleva un adorno de altar del Lago Elune'ara al Santuario de Remulos, al noroeste de Claro de la Luna. Usa el adorno en el santuario. \n\nDespués, habla con Tajarri en el santuario para completar la prueba.", description = "En tu primera prueba, debes ir a las profundidades del Lago Elune'ara y encontrar un adorno de altar. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Rémulos. Coloca el adorno en el altar y habla con Tajarri; es una de las guardianas más apreciadas de Claro de la Luna.\n\nTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.\n\nSuerte, <nombre>.", progress = "Para completar la Prueba del Lago, tienes que usar el adorno de altar en el santuario. Hasta que no lo hagas, no completarás la prueba.", completion = "¡Otro &lt;clase&gt; que completa la Prueba del Lago! Buen trabajo.\n\nMe llamo Tajarri y tengo el honor de ser el guardián de este santuario, un tributo a la conservación de la naturaleza y su equilibrio. Este adorno señala la importancia de este lugar para el Círculo Cenarion.", rewards = "Recibirás:\n\n"},
[29] = {title = "La prueba del lago", objective = "Lleva un adorno de altar del Lago Elune'ara al Santuario de Remulos, al noroeste de Claro de la Luna. Usa el adorno en el santuario. \n\nDespués, habla con Tajarri en el santuario para completar la prueba.", description = "En tu primera prueba, debes ir a las profundidades del Lago Elune'ara y encontrar un adorno de altar. Se descompone rápidamente, por lo que deberás ir inmediatamente al Santuario de Rémulos. Coloca el adorno en el altar y habla con Tajarri; es una de las guardianas más apreciadas de Claro de la Luna.\n\nTienes un tiempo limitado para completar la tarea y es probable que tengas que competir con otros druidas en busca de adornos. Necesitarás toda tu destreza mental y física.\n\nSuerte, <nombre>.", progress = nil, completion = "¡Otro &lt;clase&gt; que completa la Prueba del Lago! Buen trabajo.\n\nMe llamo Tajarri y tengo el honor de ser el guardián de este santuario, un tributo a la conservación de la naturaleza y su equilibrio. Este adorno señala la importancia de este lugar para el Círculo Cenarion.", rewards = "Recibirás:\n\n"},
[30] = {title = "La prueba del león marino", objective = "Busca el colgante de Agilidad acuática y el colgante de Resistencia acuática. Habla con la gente de Claro de la Luna para conseguir pistas sobre la ubicación de los colgantes. \n\nForma el colgante del león marino con los otros dos cuando estés cerca del Santuario de Remulos. \n\nLleva el colgante completo a Dendrite Rafagaestelar a Amparo de la Noche, en Claro de la Luna.", description = "En tu segunda prueba debes encontrar las dos mitades del colgante del león marino. Una obtiene su poder de la agilidad del león marino cuando se desliza en el agua, la otra lo obtiene de su increíble capacidad de resistencia. Los druidas se alimentan de ambos rasgos para asumir su forma acuática.\n\nHabla con la gente de Claro de la Luna para averiguar dónde están las mitades y tráemelas para que las unamos. Luego llevarás el colgante a Dendrite Rafagaestelar.", progress = "¿Traes el colgante, <nombre>? Si no encuentras las piezas, habla con la gente del lugar. Encontrarás lo que buscas y podrás explorar este lugar sagrado.", completion = "Enhorabuena por completar la Prueba del León Marino, &lt;nombre&gt;.\n\nHas aprendido que necesitas agilidad y resistencia para moverte en armonía en el agua, y que para ello debes abrazar el aspecto del león marino.\n\nEstas lecciones te serán muy útiles cuando ganes tu forma acuática.", rewards = "Recibirás:\n\n"},
[31] = {title = "Forma acuática", objective = "Vuelve a Cima del Trueno y enséñale el colgante del león marino a Turak Tótem Rúnico.", description = "Has completado tu formación para la adopción de una forma acuática. Ve a ver a tu instructor, Turak Tótem Rúnico, a Cima del Trueno. Enséñale el colgante y demuéstrale que puedes aprender lo que tenga que enseñarte. Él completará tu formación para que puedas fundirte con el agua.\n\nBuena suerte, joven druida. Volveremos a vernos.", progress = "Tenía noticia de tu regreso a Cima del Trueno, pero debes demostrarme lo que has aprendido para que te enseñe tu forma acuática.\n\n¿Traes el colgante del león marino?", completion = "Impresionante, &lt;nombre&gt;; tus antepasados pueden estar orgullosos. Me llena de alegría poder enseñarte tu Forma acuática.\n\nAcepta esto como premio por tus logros en las dos pruebas; espero que te ayude en tu objetivo de proteger la naturaleza y su equilibrio.", rewards = "Te van a lanzar el siguiente hechizo:\n\nForma acuática\n\nRecibirás:\n\nCinturón acuario"},
[32] = {title = "La sublevación de los silítidos", objective = "Entrégale el informe a Zilzibin Drumlore en Orgrimmar.", description = "Aquí tienes el informe. Te ruego que se lo entregues a alguien que tenga conocimientos de este tema.\n\nConozco a un erudito trol llamado Zilzibin Drumlore que ha dedicado mucho tiempo al estudio de los seres vivos que solo se encuentran en Azeroth. ¡Es un historiador de los pies a la cabeza! Entrégale este informe si quieres que te ayude a entender a qué te enfrentas y el modo de detener esta amenaza. Vamos a necesitar toda la ayuda que podamos conseguir.\n\nZil vive en una casa en el segundo nivel de La Calle Mayor en Orgrimmar.", progress = "Lo siento, no te conozco... pero por lo que parece, tú sí has oído hablar de mí. ¿En qué puedo ayudarte? ¿Tienes algo para mí?", completion = "Este informe... Yo ya he visto antes a estas criaturas. Picoflojo ha hecho muy bien al enviarte aquí con el informe. \n\nEstas criaturas destruirán el Desierto de Tanaris con rapidez. Se trata de una de las mayores amenazas a la que jamás nos hemos enfrentado. Familiarízate con este nombre, &lt;nombre&gt;: silítidos. Los insectos de los que habla este informe son los silítidos y representan uno de los mayores peligros a los que tanto la Horda como la Alianza se enfrentan. \n\nSi hubiéramos sido capaces de descubrirlo antes...", rewards = "Recibirás:\n\n"},
[33] = {title = "Los lobos cruzan la frontera", objective = "Lleva 8 piezas de carne dura de lobo a Eagan Desollapieles junto a la Abadía de Villanorte.", description = "¡Condenados lobos Fauces de Madera! Pero me gustan los filetes de lobo, la verdad. Tráeme carne dura de lobo y te la cambiaré por algo que te será útil.\n\nConseguirás la carne de los lobos y lobeznos Fauces de Madera que andan por la campiña de Villanorte.", progress = "Oye, <nombre>, tengo hambre... ¿Y la carne dura de lobo?", completion = "¡Estupendo! Qué ganas de hincarle el diente...\n\nEn pago, elige lo que quieras de aquí.", rewards = "Podrás elegir una de estas recompensas:\n\nZapatos de pelaje suaveBrazales de piel de lobo\n\nTambién recibirás:\n\nZapatos de pelaje suaveBrazales de piel de lobo"},
[34] = {title = "Una visita poco grata", objective = "Martie Jainrose de Villa del Lago quiere que mates a Panzallena. LLévale un colmillo como prueba.", description = "Han vuelto a arrasar mi jardín, y sé quién es el responsable. Es ese maldito jabalí, ese al que el mariscal Marris llama Bellygrub. \n\nYa es demasiado tarde para replantar narcisos. Ese puerco ha devastado mis flores. ¡Acaba con ese bicharraco! Si me traes un colmillo suyo, te recompensaré. Dicen que anda husmeando por los campos que hay al suroeste de Villa del Lago, pero de vez en cuando viene a mi jardín.", progress = "¿Has acabado ya con Panzallena?", completion = "¡Por fin! Gracias, &lt;nombre&gt;, me has hecho un favor enorme. ¡Este año el jardín estará precioso!", rewards = "Recibirás:\n\nRamo de begoñas escarlata"},
[35] = {title = "Más preocupaciones", objective = "El alguacil Dughan quiere que hables con el guardia Thomas.", description = "Si te preocupa que los rumores sobre los murlocs sean ciertos, viaja al puente del este de Elwynn y habla con el guardia Thomas. Esta última semana se encuentra emplazado en el puente y sabrá cuál es el estado de la situación.\n\nTráeme su informe.", progress = nil, completion = "Sí, los murlocs se han asentado a lo largo de los ríos del este de Elwynn. No sabemos por qué están ahí, pero son agresivos y, además, ¡son hasta un poco inteligentes!", rewards = "Recibirás:\n\n"},
[36] = {title = "Estofado de los Páramos de Poniente", objective = "Verna Furlbrow quiere que le entregues su receta del estofado de los Páramos de Poniente a Salma Saldean.", description = "Nunca pensé que llegaría el día en el que tuviera que dejar la granja. Pero los campos están llenos de ladrones y es demasiado peligroso que sigamos aquí. En cuanto el granjero Furlbrow consiga arreglar el carro nos pondremos en marcha.\n\n¿Podrías hacerme un favor? Deja que te escriba mi receta del estofado de los Páramos de Poniente. Por favor, llévasela a Salma Saldean, estará en su granja, nada más pasar la bifurcación del camino.", progress = "Voy a echar mucho de menos a Verna Furlbrow. ¿No la verías por casualidad cuando estuviste por ahí, verdad?", completion = "Esa Verna siempre fue una muchacha encantadora. La echaré de menos en los Páramos de Poniente pero, entre tú y yo, es una chica de ciudad y en Ventormenta estará muy a gusto. ¡Pero basta de chácharas! ¡Tenemos que hacer el estofado de los Páramos de Poniente!", rewards = "Recibirás:\n\n"},
[37] = {title = "Encuentra a los guardias perdidos", objective = "El guardia Thomas quiere que viajes al norte, río arriba, y busques a sus dos guardias, Rolf y Malakai.", description = "Hace unos días mandé a dos guardias, Rolf y Malakai, a investigar el río. Aún no han regresado. Para completar mi informe necesito saber qué les ocurrió a esos hombres.\n\nViaja al norte a lo largo del río y encuentra a mis guardias... o lo que quede de ellos.", progress = nil, completion = "Aunque el cuerpo ha sido saqueado, cerca de él hay un medallón que nadie ha recogido. Tiene una inscripción: \"Lacayo Malakai Roca\".", rewards = "Recibirás:\n\n"},
[38] = {title = "Estofado de los Páramos de Poniente", objective = "Salma Saldean quiere 3 carnes fibrosas de buitre, 3 morros de dentosangre, 3 ojos de múrloc y 3 okras.", description = "¡Ayúdame a preparar el estofado de los Páramos de Poniente! Vuelve con estos ingredientes:\n\n3 piezas de carne fibrosa de buitre\n3 morros de dentosangre\n3 ojos de múrloc\n3 okras", progress = "Vuelve con estos ingredientes:\n\n3 piezas de carne fibrosa de buitre\n3 morros de dentosangre\n3 ojos de múrloc\n3 okras", completion = "¡Esa okra servirá para dar algo de consistencia al caldo, perfecto! Ahora solo nos falta por añadir carne fibrosa de buitre, unos cuantos ojos de múrloc y esos deliciosos morros de dentosangre... ¡y ya estará listo! Por toda tu ayuda, &lt;nombre&gt;, ¡quiero que te lleves el primer lote de estofado de los Páramos de Poniente!", rewards = "Recibirás:\n\nEstofado de Páramos de PonienteReceta: estofado de Páramos de PonienteManoplas de SalmaCuchillo de cocina afilado"},
[39] = {title = "Entrega el informe de Thomas", objective = "Preséntate ante el alguacil Dughan en Villadorada.", description = "Habla al alguacil Dughan de la muerte de Malakai y Rolf y después infórmale de que no podrá contener a los murlocs del este de Elwynn con sus tropas.\n\nSé que no tenemos bastantes soldados, quizás Dughan pueda encontrar alguno más.", progress = nil, completion = "Mmm, son noticias preocupantes. Nuestras defensas ya están bastante mermadas y, ahora que hemos perdido a Rolf y Malakai a manos de esos murlocs, estamos incluso peor.\n\nSi las cosas no mejoran, para el final de la semana habrá un combate en Villadorada.", rewards = "Recibirás:\n\n"},
[40] = {title = "Un peligro con olor a pescado", objective = "Remy Dos Veces quiere que hables con el alguacil Dughan de Villadorada.", description = "¡Hay una nueva amenaza en el Bosque de Elwynn, <nombre>! ¡Los murlocs están subiendo por los ríos del este de Elwynn, ahuyentando a los peces y atacando a las buenas gentes de allí!\n\nAvisé al alguacil Dughan, pero le preocupan más los gnolls y los bandidos. No cree que los murlocs sean una amenaza.\n\n¡Por favor, <nombre>! ¡Habla con Dughan y convéncele de que envíe más tropas al este!", progress = nil, completion = "Sí, hablé con Remy. Lo respeto como mercader, aunque los informes de los murlocs del este han sido, por decirlo suave, demasiado superficiales.\n\nTomo nota de tu preocupación pero, si no recibo un informe militar sobre la amenaza múrloc, no puedo enviar más tropas al Este.", rewards = "Recibirás:\n\n"},
[45] = {title = "Descubre el destino de Rolf", objective = "Busca a Rolf o alguna prueba de su muerte en el poblado múrloc.", description = "En tu exploración del área encuentras huellas palmeadas que llevan al este por la costa del Lago del Hito. Hacia el este, en la distancia, adivinas la silueta de una aldea múrloc.\n\nQuizás Rolf encontró allí su destino...", progress = nil, completion = "Alrededor del cuello del cadáver hay un medallón de metal con una inscripción que reza: \"Lacayo Rolf Hartford\".", rewards = "Recibirás:\n\n"},
[46] = {title = "Recompensas por los murlocs", objective = "Lleva 8 aletas de múrloc rotas al guardia Thomas al puente oriental de Elwynn.", description = "El ejército de Ventormenta ha puesto precio a la cabeza de los rondadores y batidores múrloc de Elwynn. Mátalos y tráeme sus ancas. El ejército de Ventormenta te recompensará bien.\n\nLos murlocs han construido una aldea en el Lago del Hito, al norte de aquí, y otra en el sur, donde se divide el río.", progress = "¿Cómo va la caza, <nombre>?", completion = "¿Tienes las ancas? ¡Genial! El alguacil Dughan está ansioso por recibir noticias del estado de la situación en Elwynn y me gustaría decirle que empezamos a controlar la situación.\n\nTus acciones han contribuido a que sea posible.", rewards = "Podrás elegir una de estas recompensas:\n\nBayonetaGarrote de metal sólidoEspada bien usada\n\nTambién recibirás:\n\nEspada bien usadaGarrote de metal sólidoBayoneta"},
[47] = {title = "Intercambio de polvo de oro", objective = "Lleva 10 unidades de polvo de oro a Remy Dos Veces a Villadorada. Puedes obtener polvo de oro de los kobolds del Bosque de Elwynn.", description = "Los kobolds de esa zona a veces llevan polvo de oro. Y me vendría muy bien. Consígueme un montón de polvo de oro y te pagaré el mejor precio de la ciudad, ¡el mejor!\n\nPuedes encontrar kobolds en la Mina Abisal, al sur. Y también alrededor de la Cantera de Jaspe, al noreste.", progress = "¡Psst! ¿Tienes ese polvo de oro... para mí?", completion = "Gracias por el polvo de oro, &lt;nombre&gt;. Aquí tienes tu dinero y... este detalle de mis socios. Puede que lo encuentres útil... útil.", rewards = "Recibirás:\n\nBolsa de canicas"},
[48] = {title = "Dulce ámbar", objective = "Lleva agua sagrada de manantial a Tragoamargo Cebatruenos.", description = "Allá en el Sur donde las zarzas irritan\nHay una fuente de aguas umbrías\nPura como la sagrada Hija de la Luz\nAgua sagrada de manantial me traerás tú.", progress = "¡Hip! ¡Jo jo! ¿Quieres otra?\n\nAllá en el Sur donde las zarzas irritan\nHay una fuente de aguas umbrías\nPura como la sagrada Hija de la Luz\nAgua sagrada de manantial me traerás tú.", completion = "¡Hip! ¡Jo jo!", rewards = "Recibirás:\n\n"},
[49] = {title = "Dulce ámbar", objective = "Tragoamargo Cebatruenos quiere un saco de cebada, un saco de centeno y un saco de maíz.", description = "Ámbar es el color del amor de mi vida\nComo las luces de un final del día\nRecupéralo y el amor volverá a mí\nUn saco de cebada, uno de centeno y otro de maíz.", progress = "¿Otra? ¡Claro!\n\nÁmbar es el color del amor de mi vida\nComo las luces de un final del día\nRecupéralo y el amor volverá a mí\nUn saco de cebada, uno de centeno y otro de maíz.", completion = "¡Hip! ¡Jo jo!", rewards = "Recibirás:\n\n"},
[50] = {title = "Dulce ámbar", objective = "Tragoamargo Cebatruenos quiere veraplata.", description = "Mezclaré y mezclaré y un puré será todo\nSerá, amigo, nuestro más querido tesoro\nHaré un alambique fuerte y resistente\nDame veraplata y no daré la lata.", progress = "¿Un bis? ¡Claro que sí!\n\nMezclaré y mezclaré y un puré será todo\nSerá, amigo, nuestro más querido tesoro\nHaré un alambique fuerte y resistente\nDame veraplata y no daré la lata.", completion = "¡Hip! ¡Jo jo!", rewards = "Recibirás:\n\n"},
[51] = {title = "Dulce ámbar", objective = "Lleva una rama de sicomoro a Tragoamargo Cebatruenos.", description = "Recién destilada, aunque aún no esté filtrada\nComo tierra sin semilla esperando a ser arada\nAhora haré nuestra pila de carbón\nUna rama de sicomoro es tu próxima misión.", progress = "¿Repetimos? ¡Claro, lo que quieras!\n\nRecién destilada, aunque aún no esté filtrada\nComo tierra sin semilla esperando a ser arada\nAhora haré nuestra pila de carbón\nUna rama de sicomoro es tu próxima misión.", completion = "¡Hip! ¡Jo jo!", rewards = "Recibirás:\n\n"},
[52] = {title = "Proteger la frontera", objective = "Mata a 8 merodeadores y a 5 osos del bosque jóvenes y vuelve con el guardia Thomas al puente oriental de Elwynn.", description = "Saludos, <nombre>. Los animales salvajes son más agresivos cuanto más nos alejamos de Villadorada. El Aserradero de la Vega del Este sufre constantes ataques de lobos y osos.\n\nNos vendría bien tu ayuda ahí fuera.", progress = nil, completion = "Gracias por tu ayuda, &lt;nombre&gt;. Debe de haber algo en el bosque que está agitando a esos animales.\n\nSea lo que sea, espero que no salga de allí.", rewards = "Recibirás:\n\nPoción de Curación inferior"},
[53] = {title = "Dulce ámbar", objective = "Lleva un fajo de roble quemado a Tragoamargo Cebatruenos.", description = "Es un líquido meloso que se vuelve delicioso\nDulce líquido ambarino que merece mi cariño\nPero algo necesito para darle mis cuidados\nUn magnífico tonel del mejor roble quemado.", progress = "¡Claro que te la canto otra vez, amigo!\n\nEs un líquido meloso que se vuelve delicioso\nDulce líquido ambarino que merece mi cariño\nPero algo necesito para darle mis cuidados\nUn magnífico tonel del mejor roble quemado.", completion = "Envejecida en el barril y filtrada con carbón\nAcaricia tus labios y va directa al corazón.\nBrindemos por ti y verás que bueno\nAlzad vuestras jarras por Tragoamargo Cebatruenos", rewards = "Recibirás:\n\nPetaca de Cebatruenos"},
[54] = {title = "Informa en Villadorada", objective = "Lleva los documentos del alguacil McBride al alguacil Dughan a Villadorada.", description = "<nombre>, has demostrado que te preocupa la seguridad de Villanorte. Tu siguiente misión será proteger el Bosque de Elwynn.\n\nSi aceptas, te daré unos papeles para el alguacil Dughan, que está en Villadorada. Para ir, sigue el camino hacia el sur, pasada la frontera.", progress = "Villanorte es un jardín comparada con el Bosque de Elwynn; me pregunto qué querrá McBride.\n\nDame los documentos.", completion = "Dice que te han nombrado ayudante del alguacil de Ventormenta. Enhorabuena.\n\nY buena suerte, el trabajo no será fácil ahora que el ejército parece que anda ocupado con la nobleza.\n\nQué misteriosa es la política en estos tiempos difíciles...", rewards = "Recibirás:\n\nEscudo de hombre pico"},
[55] = {title = "Morbent Vil", objective = "Utiliza la Pesadilla de Morbent contra Morbent Vil, mata a Morbent Vil y luego ve al campamento de Sven y devuélvele la Pesadilla de Morbent.", description = "Morbent Vil se esconde en una casa que cuelga de la cima de la montaña que hay al este, una casa que da al Cementerio del Cerro del Cuervo. Su tiempo en esta tierra está llegando a su fin...\n\nUtiliza la pesadilla de Morbent contra él. Inutilizará su magia protectiva.\n\nY mátalo. Mátalo y sálvanos de su maldad. ¡Sé el instrumento de mi venganza, y conviértete en <el héroe/la heroína> del Bosque del Ocaso!", progress = "¡¿Has derrotado a Morbent Vil?!", completion = "Morbent Vil ha sido derrotado. Los jinetes oscuros aún andan por ahí, pero ya ha ocurrido una de las dos cosas que esperaba con desesperación.\n\nHas hecho lo imposible y tu hazaña perdurará en las leyendas del Bosque del Ocaso.\n\nVe, &lt;nombre&gt;. Quizás un día, el destino vuelva a traerte a mí...", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones de guardia nocturnoCapa de maestro vigíaAlmófar de metal brillante\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAntorcha de Llama sagradaAlmófar de metal brillanteCapa de maestro vigíaPantalones de guardia nocturno"},
[56] = {title = "Vigilancia nocturna", objective = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 8 guerreros esqueléticos y 6 magos esqueléticos.", description = "Como Comandante de La Guardia Nocturna, es mi deber proteger a los ciudadanos de Villa Oscura. Ventormenta ha retirado a los guardias que estaban aquí destacados. Ahora tenemos que arreglárnoslas solos.\n\nSi crees estar a la altura de la tarea, nos vendría bien tu ayuda. Ayuda a La Guardia Nocturna aventurándote en el Jardín Sereno, al sur de Villa Oscura y matando a 8 guerreros esqueléticos y a 6 magos esqueléticos. ¡Hemos de acabar con la amenaza de los no-muertos!", progress = nil, completion = "Espléndido, &lt;nombre&gt;. Serás recompensado por los servicios prestados a la gente de Villa Oscura.", rewards = "Recibirás:\n\n"},
[57] = {title = "Vigilancia nocturna", objective = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 15 mefistos esqueléticos y 15 horrores esqueléticos.", description = "<nombre>, me están llegando informes de una infestación de no-muertos en el bosque. Si estás a la altura, viaja al Cementerio del Cerro del Cuervo y mata a 15 de esos demonios esqueléticos y a 15 horrores esqueléticos. También me han llegado noticias de la presencia de esqueletos al sur de Villa Oscura, en el Cementerio del Jardín Sereno y en sus alrededores. ¡Hemos de expulsar a los no-muertos del Bosque del Ocaso!", progress = nil, completion = "Las gentes de Villa Oscura te dan las gracias, &lt;nombre&gt;. Has demostrado ser un gran aliado de la Guardia Nocturna.", rewards = "Recibirás:\n\n"},
[58] = {title = "Vigilancia nocturna", objective = "La comandante Althea Cerranegro de Villa Oscura quiere que mates a 20 propagadores de la Peste en el mausoleo oriental de Cerro del Cuervo.", description = "La situación de Cerro del Cuervo sigue siendo desalentadora. La seguridad de Villa Oscura está en tus manos, <nombre>. Yo no tengo suficientes guardias para mantener la villa a salvo. Necesitamos que ayudes a Cerro del Cuervo una vez más y que liberes el mausoleo oriental de 20 propagadores de la plaga.", progress = nil, completion = "Te doy las gracias de parte de las gentes de Villa Oscura y de la Guardia Nocturna, &lt;nombre&gt;, por tu valor y dedicación. Que la Luz ilumine tu camino.", rewards = "Podrás elegir una de estas recompensas:\n\nBandolera de la Guardia NocturnaCarcaj de la Guardia NocturnaSaco de armas de la Guardia Nocturna\n\nTambién recibirás:\n\nSaco de armas de la Guardia NocturnaBandolera de la Guardia NocturnaCarcaj de la Guardia Nocturna"},
[59] = {title = "Armadura de tela y cuero", objective = "Entrega a Sara Timberlain el marcador de armaduras de Ventormenta.", description = "Tengo aquí una señal, un premio a tu habilidad y tu valor. Podrás cambiarla por una buena armadura.. Quiero que se la lleves a Sara Timberlain al Aserradero de la Vega del Este. Dale la señal y ella te forjará una armadura. Y cuando la recibas, <nombre>, úsala para defender Elwynn.\n\nEl Aserradero de la Vega del Este está más allá del puesto de vigilancia del guardia Thomas, al este.", progress = "El ejército de Ventormenta me ha encargado que suministre a sus tropas tela y armaduras de cuero.\n\nSi tienes una señal para mí, estaré encantado de hacerte algo.", completion = "Ah, gracias por la señal. Por favor, elige la armadura que quieras.\n\nBuena suerte, valiente &lt;clase&gt;. Espero que esta armadura te sirva bien.", rewards = "Podrás elegir una de estas recompensas:\n\nToga bien cosidaPantalones con remiendos\n\nTambién recibirás:\n\nToga bien cosidaPantalones con remiendos"},
[60] = {title = "Las velas de los kobolds", objective = "Lleva 8 velas grandes a William Mortero a Villadorada.", description = "Hola, <chico/chica>. ¿Tienes un momento?\n\nMi hermano y yo tenemos un herbolario en Ventormenta. Yo estoy aquí para conseguir velas grandes, por la cera, ya me entiendes. ¿Puedes ayudarme?\n\nPuedes conseguir velas grandes de los kobolds. He oído rumores de que están infestando las minas de Elwynn... La Mina Abisal del sur y la Cantera de Jaspe del este. Puedes empezar la búsqueda en esos lugares.", progress = "¿Has reunido las velas ya?", completion = "Estabas ocupado cazando algún kóbold que otro, ¿eh? Gracias por las velas, &lt;nombre&gt;, aquí tienes tu recompensa.", rewards = "Recibirás:\n\nPalo de cera resplandeciente"},
[61] = {title = "Un envío para Ventormenta", objective = "Lleva el envío de William a Morgan Mortero al Distrito de Mercaderes de Ventormenta.", description = "Mi hermano Morgan espera mi envío de velas en Ventormenta. No tengo tiempo para hacer el viaje en persona, pero si quisieras llevarle el envío, te pagaría bien.\n\nHe empaquetado las velas. Encontrarás a Morgan en nuestra tienda, Herbolario Mortero, en el Distrito de Mercaderes de Ventormenta.", progress = "Oh, ¿un envío de mi hermano? ¡Espléndido! ¡Hoy la Fortuna me es favorable!", completion = "Ten, tu pago. Y, si quieres echar un vistazo ahora que estás aquí, ¡adelante! Seguro que tengo alguna poción o algún abalorio que te venga bien.", rewards = "Podrás elegir una de estas recompensas:\n\nCohete explosivoAceite de OlafElixir de fuerza de león\n\nTambién recibirás:\n\nAceite de OlafCohete explosivoElixir de fuerza de león"},
[62] = {title = "La Mina Abisal", objective = "Explora la Mina Abisal y vuelve junto al alguacil Dughan a Villadorada.", description = "¡La mina de Villanorte no es la única que tiene problemas! Según mis informes, la Mina Abisal de Elwynn también ha sido ocupada por los kobolds.\n\nExplora la mina y comprueba la veracidad de mis informes. Luego vuelve aquí. La mina está hacia el norte de Villadorada, entre La Granja Pedregosa y la granja Maclure.", progress = nil, completion = "Son malas noticias. ¡¿Qué vendrá luego?! ¡¿Dragones?! Tendremos que reforzar las patrullas de esa mina. Gracias por tu ayuda, &lt;nombre&gt;. Espera un momento... podría tener otra misión para ti.", rewards = "Recibirás:\n\n"},
[63] = {title = "La llamada del agua", objective = "Derrota la manifestación de agua corrupta y coloca los brazales de manifestación corrupta con las gotas restantes del agua más pura en el Blandón de Siemprefuente en el Bosque de Argénteos.", description = "Por la costa del Bosque de Argénteos, al oeste de El Sepulcro, verás un altar infestado por la Peste. Los espíritus de ese lugar ya no comunican con nuestro pueblo y de ti depende acabar con esa manifestación de la Plaga y limpiar el estanque.\n\nLlévate los brazales de elemental y las gotas restantes de la poción de Brine y colócalos en el blandón. El ritual debería bastar para conjurar a un espíritu puro con el que comunicar.\n\nTráeme una prueba de haber completado la tarea y te recompensaré con un tótem.", progress = "El blandón aún refleja la luz ambiente del lugar y la llama tiembla levemente cuando te acercas.\n\nLa corrupción de las colinas parece haber mantenido las distancias y, además de los componentes perdidos, el blandón parece estar listo para el ritual de conjura de una forma de agua limpia.", completion = "Colocas los brazales sobre el blandón y entonces las gotas restantes de la poción de Brine en la llama. Las gotas harán chisporrotear la llama y empiezas el conjuro con las sencillas palabras que Islen te enseñó....", rewards = "Recibirás:\n\n"},
[64] = {title = "La reliquia olvidada", objective = "El granjero Furlbrow quiere que recuperes el reloj de bolsillo que dejó olvidado en el armario de su granja en la plantación de calabazas del Oeste.", description = "¡Fue horrible! Verna me despertó cuando oyó jaleo en los campos. Estaban llenos de gamberros. Nos fuimos corriendo y olvidé recoger mi reloj de bolsillo. Me lo dio el padre de Verna el día de nuestra boda y es horrible saber que está en manos de esos ladrones. Dejé el reloj de bolsillo en el armario de la granja. Busca el campo de calabazas que está al Oeste... no tiene pérdida. ¡Si me lo devuelves te estaré muy agradecido!", progress = "Por casualidad, ¿no conseguirías encontrar mi reloj, verdad?", completion = "¡Mi reloj! ¡Muchas gracias, amable señor!\n\nSolo somos unos pobres granjeros y hemos perdido nuestras tierras, pero, por favor, acepta esta recompensa como una muestra de nuestro aprecio.", rewards = "Recibirás:\n\nPan recién horneadoLeche fría como el hielo"},
[65] = {title = "La hermandad de los Defias", objective = "Gryan Mantorrecio quiere que hables con Wiley en Villa del Lago.", description = "La banda de desgraciados responsable de que las gentes de los Páramos de Poniente abandonaran sus tierras se llama a sí misma la Hermandad Defias. Necesito que te infiltres en este clan de matones. Debemos averiguar quién lidera el grupo y dónde se esconden. En Villa del Lago, en la posada, encontrarás a un pícaro que responde al nombre de Wiley; me debe un favor.\n\nViaja a Villa del Lago, en las Montañas Crestagrana, al este de Elwynn, y averigua todo lo que puedas.", progress = nil, completion = "¿Así que vienes de parte de Mantorrecio? Bien, estoy en deuda con él.", rewards = "Recibirás:\n\n"},
[66] = {title = "La leyenda de Stalvan", objective = "Busca al dependiente Daltry en el Concejo de Villa Oscura.", description = "La otra noche, un terrible tumulto me recorrió las venas. Sentí que mi nieta Alyssa estaba en grave peligro. Consulté las cartas y vi a la Muerte contemplándome desde la mesa.\n\nBusqué la luz en un largo y oscuro trance... intentaba encontrar algo concreto que me ayudara a calmar el espíritu. Y un nombre vino a mí. Stalvan.\n\nAcude al secretario del Concejo o e intenta averiguar algo más sobre ese nombre. Temo por todos nosotros.", progress = nil, completion = "Ah, así que te manda Lady Eva...", rewards = "Recibirás:\n\n"},
[67] = {title = "La leyenda de Stalvan", objective = "Viaja a la escuela de Arroyo de la Luna y lleva cualquier información que descubras sobre Stalvan al dependiente Daltry.", description = "Stalvan, ¿eh? Deja que consulte el registro.\n\nStalvan... Stalvan... Veamos... ¡Ah! ¡Aquí está! Tengo un señor Stalvan Mantoniebla. Según consta aquí, su última dirección es la escuela de Arroyo de la Luna. ¡Pero este dato es de hace mucho tiempo!\n\nHazme un favor, ¿quieres? Si pasas por Arroyo de la Luna, hazme saber si consigues información más reciente sobre este tipo. Me gusta llevar los registros al día.", progress = nil, completion = "El viejo baúl se abre con un crujido...", rewards = "Recibirás:\n\n"},
[68] = {title = "La leyenda de Stalvan", objective = "Lleva la carta polvorienta sin enviar al dependiente Daltry.", description = "En el viejo arcón encuentras una carta polvorienta sin enviar.", progress = "Me acuerdo de ti... Eres el que estaba preguntando por ese tal Stalvan. ¿Encontraste lo que estabas buscando?", completion = "Interesante... Así que ese individuo sí que pasó un tiempo en Arroyo de la Luna, después de todo. Resulta extraño que la carta nunca llegara a entregarse. En cualquier caso, voy a actualizar el registro.", rewards = "Recibirás:\n\n"},
[69] = {title = "La leyenda de Stalvan", objective = "Ve a la Posada Orgullo de León a ver si alguien ha oído el nombre de Stalvan.", description = "¡Ay, ay! Esto se me debió de pasar la primera vez. En el registro, justo debajo de la primera dirección de Stalvan, hay otra dirección. Está tachada... Parece que se dirigía a la Posada Orgullo de León, en Villadorada. Quizás quieras comprobarlo, <nombre>.", progress = nil, completion = "¿Stalvan? Desde luego, me resulta familiar.", rewards = "Recibirás:\n\n"},
[70] = {title = "La leyenda de Stalvan", objective = "Recupera la carta sin enviar de Stalvan del cofre y entrégala en el distrito de Los Canales de Ventormenta.", description = "Stalvan... ese nombre me suena de algo. ¡Ya sé de qué!\n\nUna noche de tormenta de hace muchos años, llegó aquí un mensajero que buscaba refugio para la noche. Casi a medianoche, el hombre se precipitó escaleras abajo gritando, pálido de terror. Desapareció en la tormenta aún en pijama.\n\nCon las prisas, se olvidó sus cartas en el arcón de arriba. Y nunca volvió a buscarlas. Una de ellas es de ese tal Stalvan. Iba dirigida al distrito de Los Canales de Ventormenta. Puedes quedártela.", progress = "¿Qué tienes ahí? Deja que le eche un vistazo...", completion = "¿Cómo tienes la cara de traer esto aquí?", rewards = "Recibirás:\n\n"},
[71] = {title = "Informa a Thomas", objective = "Entrega los medallones de Rolf y Malakai al guardia Thomas. Lo encontrarás en el puente oriental de Elwynn.", description = "Ahora que tienes los dos medallones, llévaselos al guardia Thomas al puente. Así sabrá que sus guardias murieron asesinados.", progress = "Hola, <nombre>. ¿Has averiguado qué suerte corrieron Rolf y Malakai?", completion = "Has confirmado mis temores, &lt;nombre&gt;. Los murlocs son una amenaza que no podemos ignorar.", rewards = "Recibirás:\n\n"},
[72] = {title = "La leyenda de Stalvan", objective = "Busca una pista entre los objetos empaquetados.", description = "Mi padre era el custodio de la propiedad mucho antes que yo. Tuvo que limpiar la sangre después de la masacre. Pero eso ahora no viene al caso.\n\nEl fondo de Flintridge se ha agotado. Ahora, las últimas pertenencias de la familia serán subastadas. Es culpa de esos buitres de los recaudadores de impuestos. Como veo que te mueres por saber más, creo que puedes mirar entre toda esta basura. Quién sabe lo que puedes encontrar.", progress = nil, completion = "Levantas lentamente la tapa de la caja...", rewards = "Recibirás:\n\n"},
[74] = {title = "La leyenda de Stalvan", objective = "La página arrancada del diario que descubriste es tu siguiente pista.", description = "Dentro de la caja encuentras varios objetos: reliquias familiares mohosas, unos trofeos de caza, un retrato de familia y algunos libros antiguos. Al fondo, debajo de un jarrón, descubres una página arrancada de un diario.", progress = "¿Qué tienes ahí? No lo veo. Tengo una vista muy mala. Pónmelo en las manos.", completion = "Apenas distingo las letras. Pero la caligrafía me recuerda a algo que vi una vez, cuando aún tenía buena vista.", rewards = "Recibirás:\n\n"},
[75] = {title = "La leyenda de Stalvan", objective = "El alguacil Haggard quiere que busques la página del diario descolorida en el cofre que hay arriba.", description = "Cuando me mudé aquí había un fajo de pergaminos en el cofre de arriba. Empecé a examinarlos, pero tuve que dejarlo cuando la niebla empezó a nublarme la vista.\n\nHazle un favor a un pobre anciano ciego. Sube y busca en el cofre cualquier cosa que te pueda ayudar a descubrir más sobre ese Stalvan. Sé que hay una página de un diario que, aunque está algo descolorida, podría interesarte mucho. Tráemela. Yo te ayudaré en lo que pueda.", progress = "¿Has encontrado la página de la que te hablé, <nombre>?", completion = "¡La has encontrado!", rewards = "Recibirás:\n\n"},
[76] = {title = "La Cantera de Jaspe", objective = "Explora la Cantera de Jaspe y vuelve a informar al alguacil Dughan a Villadorada.", description = "Gracias a ti, ahora sabemos que la Mina Abisal está infestada de kobolds. Ahora necesitamos un rastreador que investigue la Cantera de Jaspe, que está más lejos.\n\nExplora la Cantera de Jaspe e infórmame si ves algún kóbold. Para llegar a la mina, viaja hacia el este por el camino hasta que llegues a la Torre de Azora. Cuando llegues allí, dirígete hacia el norte. Encontrarás la mina al pie de las colinas.", progress = nil, completion = "¿Dices que hay kobolds en la Cantera de Jaspe? ¡Maldita sea! ¡La situación empeora cada minuto!\n\nGracias por el informe, &lt;nombre&gt;. Aunque habría preferido que trajeras buenas noticias, claro.", rewards = "Recibirás:\n\n"},
[77] = {title = "Una situación peliaguda", objective = "Recoge 10 botellas de calimocho de miel de las Tierras del Interior de las cajas de la base abandonada de la Horda en Roca Oculta.", description = "Tengo material de empaquetado original para el calimocho que quiere Dran Droffers. Te ayudaré a prepararlo para enviárselo a ese gruñón del padre de Malton... a cambio de dos botellas del mismo. Están en unas cajas en la base abandonada de la Horda cerca de Roca Oculta.\n\nSerá muy dificultoso; habrá babosas y mocos repugnantes por las ruinas. Dicen que su líder es una especie de jefe moco. Pero da igual; consigue el calimocho y vuelve aquí para empaquetarlo.", progress = "Vienes a entregar el calimocho, ¿verdad?", completion = "Excelente trabajo, &lt;nombre&gt;. Veo que has tratado el calimocho con cuidado; no hay nada peor que el calimocho agitado. \n\nBueno, pasemos al mágico proceso del embotellado para conservar este tesoro alcohólico. En realidad el proceso no tiene nada de mágico; lo único que tengo son unos contenedores de calimocho dulce de las Tierras del Interior vacíos. Nuestras triquiñuelas convertirán estas botellas en un tesoro alcohólico con solera.\n\n¡Salud!", rewards = "Recibirás:\n\n"},
[78] = {title = "La leyenda de Stalvan", objective = "Lleva la página de diario descolorida al tabernero Smitts.", description = "Sé de alguien que probablemente podrá ayudarte. Cuando yo comandaba la guardia de Ventormenta, iba con mis hombres a beber al Mesón del Cuervo Escarlata de Villa Oscura. El posadero, Smitts, era todo un experto en las tradiciones de la zona. Enséñale esta página y escucha lo que te pueda decir sobre ella.", progress = "¿Te envía el mariscal Haggard? ¿Y por qué no lo has dicho antes?\n\nAy, el viejo Haggard... El pobre se quedará ciego dentro de poco. En fin, déjame ver lo que tienes ahí.", completion = "¡Por la Luz! Claro que reconozco esa caligrafía.", rewards = "Recibirás:\n\n"},
[79] = {title = "La leyenda de Stalvan", objective = "Lleva la página embarrada a la comandante Cerranegro.", description = "Seguí la leyenda del tal Stalvan durante años. Hace unos años, cuando asesinaron a aquellos nobles, yo fui con Haggard a investigar el asunto. Encontré estas páginas llenas de barro, pero nunca conseguimos relacionar la caligrafía con ese lunático. Tus pruebas demuestran su culpabilidad. Lleva esto a la comandante Ebonlocke de inmediato e infórmale de lo que has descubierto.", progress = "Espero que sea importante, <clase>. Déjame ver lo que traes y cuéntame tu historia. Pero, por la Luz, date prisa. La defensa de Villa Oscura es mi prioridad. No tengo tiempo para tonterías.", completion = "Vaya, desde luego, tienes madera de detective, &lt;clase&gt;.", rewards = "Recibirás:\n\nUna página de un diario manchada de sangre"},
[80] = {title = "La leyenda de Stalvan", objective = "Enseña la página del diario manchada de sangre al dependiente Daltry.", description = "Estuve vigilando a ese asqueroso de Stalvan durante mucho tiempo. Si está página fue escrita por la misma mano... tenemos una prueba de su culpabilidad más allá de una duda razonable.\n\nEl secretario Daltry, a lo largo de sus años en el registro, se ha convertido en un experto calígrafo. Muéstrale esta página y pídele que compruebe si la caligrafía coincide con la de la firma del registro.", progress = "¿Tú otra vez, <nombre>? ¿Qué te trae aquí de nuevo?", completion = "¡Pues sí! La caligrafía de esta página coincide con las firmas del registro.", rewards = "Recibirás:\n\n"},
[81] = {title = "Entrega el calimocho", objective = "Entrega el barril a Dran Droffers en Orgrimmar.", description = "He puesto la caja de calimocho y las otras botellas que se lleva Dran en el barril para que no se muevan. Él no me cae muy bien, pero la verdad es que no hay nada peor que el calimocho agitado.\n\nLlévale el barril a su local en Orgrimmar. Y transmite mi afectuoso saludo a Malton; dile que si alguna vez tiene sed de bebida y de buena compañía, nos honraría mucho su visita.", progress = "Si no has venido a comprar o a entregar nada, me estás haciendo perder el tiempo. ¡Y eso no me gusta nada! ¡Más te vale traer el calimocho!", completion = "¡Veamos el barril! \n\nSí, está todo. Ten tu pago y buen trabajo. ¡En la taberna me habría cansado mucho más, ja, ja! \n\nGuardaré estas botellas para una ocasión especial. Me gusta tomarlo con champán; lo llamo champimocho.", rewards = "Recibirás:\n\n"},
[82] = {title = "Investigación en la Guarida Ponzoñosa", objective = "Llévale 5 trozos de insectos centipaar de La Guarida Ponzoñosa al alquimista Morterozugg de Gadgetzan.", description = "A ver, en cuanto a estos informes sobre los bichos... lo que sabemos es que están drenando las pozas de agua que antes estaban donde se ubica La Guarida Ponzoñosa. Lo que NO sabemos es... bueno, todo lo demás.\n\n¡Descubramos en primer lugar a quién nos enfrentamos! Quiero que vayas a La Guarida Ponzoñosa y que obtengas algunos trozos de esos bichos. Consigue 5 trozos de centipaar y llévaselos al alquimista Morterozugg para que se encargue de ellos. Con su ayuda podremos descubrir qué son.", progress = "Eres el perito adjunto, ¿no es cierto? ¡Rápido, estamos en una situación muy delicada! ¡¿Tienes los trozos de insecto que Picoflojo dijo que tendrías?!", completion = "Buen trabajo, ¡empezaré ahora mismo con el análisis! A primera vista, puedo decirte que no se parecen a ningún tipo de insecto que haya visto antes. Parece que estos bichos están controlados por una fuerza externa o algo similar.\n\nDebes ir a hablar con Picoflojo inmediatamente. ¡Hay algo urgente para lo que necesita tu ayuda! No estoy seguro pero creo que tiene que ver con los bichos del Desierto de Tanaris.", rewards = "Recibirás:\n\n"},
[83] = {title = "Pañuelos de lino rojo", objective = "Lleva 6 vendas de lino rojas a Sara Timberlain al Aserradero de la Vega del Este.", description = "La banda Defias de Villanorte lleva máscaras de tela de saco, pero los Defias de Elwynn las llevan de lino... Esas me vendrían bien para confeccionar prendas.\n\nSi me traes pañuelos de lino rojo, los usaré para hacerte algo bonito.\n\nLa banda Defias tiene campamentos por todo Elwynn.", progress = "Me estoy quedando sin lino, <nombre>. ¿No tendrás tú algo de lino que me puedas dar?", completion = "Ah, qué pañuelos tan estupendos... son un poco ásperos, pero...\n\n¡Ten!", rewards = "Recibirás:\n\nCamisa de lino rojaFajín de lino rojo"},
[84] = {title = "Vuelve con Billy", objective = "Lleva el pastel de tripa de cerdo a Billy Maclure a Los Viñedos de Maclure.", description = "Aquí tienes. Y cuando le des el pastel a Billy, ¡dile que espero que se atragante con él!", progress = "Ay... ¡Me muero de hambre! ¿Me has traído el pastel, <nombre>?", completion = "¡Mm, qué rico! ¡Este pastel es lo mejor que he comido en mi vida!\n\nCreo que me está volviendo la memoria...", rewards = "Recibirás:\n\n"},
[85] = {title = "El collar perdido", objective = "Habla con Billy Maclure.", description = "He perdido mi collar... creo que me lo robó ese granuja de Billy Maclure. Normalmente pasa el día escondido como una rata en Los Viñedos de Maclure, al este de aquí.\n\nRecupera mi collar y darás una alegría al corazón de una anciana viuda.", progress = nil, completion = "¿Que has perdido qué? Pues yo no he robado ningún collar, ¡no soy un ladrón!\n\nCreo que sé quién lo hizo, pero...&lt;sonríe&gt;tengo demasiada hambre para que me funcione la memoria.", rewards = "Recibirás:\n\n"},
[86] = {title = "Un pastel para Billy", objective = "Lleva 4 trozos de carne de jabalí a la tía Bernice Pedregosa a La Granja Pedregosa.", description = "Si me dieras un pastel, puede que te diga quién tiene el collar. Bernice, esa anciana de la otra granja, hace unos pasteles de tripa de cerdo increíbles...\n\nSi le llevas algo de carne de jabalí de los jabalíes que rondan por nuestras granjas, y le dices para qué es, seguro que te hace un pastel.", progress = "No creo que esté bien dar de comer al pillo que me robó el collar, pero si es lo que hay que hacer para recuperar lo que es mío, ¡habrá que hacerlo!\n\n¿Tienes la carne de jabalí?", completion = "Esta carne de jabalí está un poco dura pero, si la hierves lo suficiente, ¡quedará estupenda!", rewards = "Recibirás:\n\n"},
[87] = {title = "Dientes de oro", objective = "Lleva el collar de Bernice a tía Bernice Pedregosa a La Granja Pedregosa.", description = "Estaba jugando cerca de la Mina Abisal y creo que se me cayó... esto... quiero decir, creo que vi el collar de la señora. No me preguntes cómo llegó allí, ¡porque no lo sé!\n\nPues eso, que vi a un enorme kóbold con dientes de oro que lo recogía y entraba corriendo a la mina. Si encuentras a ese kóbold, encontrarás el collar, ¡lo juro!", progress = "Hola, <nombre>. ¿Has encontrado mi collar?", completion = "¡Oh, lo has encontrado! ¡Gracias, muchas gracias!\n\nTen, toma esto. Era de mi marido. Siempre dijo que le traía suerte. ¡Es una pena que no lo llevara en su última campaña! *snif*", rewards = "Recibirás:\n\nGuantes con impronta de león"},
[88] = {title = "¡Princesa debe morir!", objective = "Mata a Princesa, coge su collar y llévaselo a la vieja Pedregosa a La Granja Pedregosa.", description = "Los Brackwell tienen una cerda que ganaría cualquier concurso. Se llama Princesa. Está ENORME, ¡y es gracias a mí! ¡Porque se alimenta de mis verduras!\n\n¡Princesa debe morir antes de que llegue a nuestros campos! Tráeme su collar como prueba de que está muerta y te daré algo para recompensarte.\n\nPrincesa suele estar en la Plantación de Calabazas de Brackwell. Está al este, más allá de la granja de los Maclure. ¡Ve! ¡Antes de que le entre hambre y vuelva!", progress = "¿La has visto ya? ¿Te has ocupado de ella?", completion = "¡Menos mal! ¡Esa cerda se estaba poniendo tan gorda que se habría comido toda nuestra cosecha! Gracias, &lt;nombre&gt;.\n\nMira a ver si algo de esto te vale.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas deterioradasBrazales de tachas de latónBotas de granjero\n\nTambién recibirás:\n\nBotas deterioradasBrazales de tachas de latónBotas de granjero"},
[89] = {title = "La reconstrucción del puente", objective = "Lleva 5 picas y 5 roblones de hierro al supervisor Oslow a Villa del Lago.", description = "Durante la última invasión orca tuvimos que usar todo el hierro para fabricar balas, espadas y armaduras. Hicimos un pedido de materia prima a Ventormenta, pero unos gnolls Crestagrana secuestraron la caravana y se ocultaron en las colinas de detrás de Villa del Lago. \n\nY ahora estamos sin material para reconstruir el puente. Te recompensaré si me traes cinco picas y cinco roblones de hierro. \n\nVenga, ¡a trabajar!", progress = "¡El puente no va a construirse solo! ¿Dónde están las picas y los roblones?", completion = "Buen trabajo, &lt;nombre&gt;. Con esto podemos ir avanzando los trabajos del puente.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones de SmithGuantes de puenteGuantes de jinete\n\nTambién recibirás:\n\nGuantes de puenteGuantes de jinetePantalones de Smith"},
[90] = {title = "Kebab de lobo salpimentado", objective = "Reúne 10 ijadas magras de lobo y hierbas aromáticas de Ventormenta y vuelve a Villa Oscura, a ver al Chef Grual.", description = "¡El kebab de lobo salpimentado es el especial de la casa! ¡Claro que te puedo preparar un poco! Pero necesitaré algunos productos. Tráeme 10 ijadas magras de lobo y algunas hierbas aromatizadas de Ventormenta. En el bosque hay muchísimos lobos; encontrar ijadas no será un problema. Para las hierbas, busca a Felicia Gump. Tiene una floristería en el distrito de Los Canales de Ventormenta.", progress = "¿Has conseguido ya las 10 ijadas magras de lobo? ¿Y qué hay de las hierbas de Felicia de Ventormenta?", completion = "¡Buen trabajo, &lt;nombre&gt;! ¡Disfruta esta delicatessen llamada kebab de lobo salpimentado!", rewards = "Recibirás:\n\nKebab de lobo salpimentadoReceta: kebab de lobo salpimentado"},
[91] = {title = "Ley de Salomón", objective = "Lleva 10 colgantes Pellejo Negro a Bailiff Conacher al Concejo de Villa del Lago.", description = "Se ha instalado un enemigo de Ventormenta en las laderas orientales de Crestagrana. Ese bellaco usa lo arcano para extender el terror entre nuestra gente. Se oculta bajo el nombre de Morganth. \n\nHa esclavizado al clan gnol Pellejo Negro. De acuerdo con las leyes de Villa del Lago, asistir a un enemigo del Reino está penado con la muerte. \n\n¡Esos miserables gnolls deben morir! \n\nTráeme diez colgantes Pellejo Negro y te recompensaré.", progress = "Tráeme 10 colgantes Pellejo Negro y te recompensaré.", completion = "El Tribunal de Villa del Lago reconoce tu entrega a la justicia, &lt;nombre&gt;. Gracias por ayudar a mantener la ley y el orden en el Reino.", rewards = "Recibirás:\n\n"},
[92] = {title = "Estofado de Crestagrana", objective = "El chef Breanna de Villa del Lago quiere 5 piezas de carne dura de cóndor, 5 morros de dentosangre grandes y 5 raciones de carne de araña crujiente.", description = "Me encantaría prepararte un estofado de Crestagrana. Pero tengo un problemita; durante los disturbios con los orcos alimenté a toda la guarnición de Ventormenta. No me queda nada en la despensa, pero si tú me traes los ingredientes, cocinaré para ti. \n\nTráeme cinco filetes de carne dura de cóndor, cinco morros de dentosangre grandes y cinco raciones de carne de araña crujiente.", progress = "Tráeme 5 filetes de carne dura de cóndor, 5 morros de dentosangre grandes y 5 raciones de carne de araña crujiente.", completion = "Muchas gracias, &lt;nombre&gt;. Aquí tienes el delicioso estofado de Crestagrana prometido.", rewards = "Recibirás:\n\nEstofado CrestagranaReceta: estofado Crestagrana"},
[93] = {title = "Las croquetas de cangrejo", objective = "Reúne 6 patas pegajosas de araña y llévaselas al Chef Grual a Villa Oscura.", description = "Te voy a contar un secreto: ¡las croquetas de \"cangrejo\", en realidad se hacen con patas de araña! Sé que es un poco asqueroso, pero las patas de araña son muy sabrosas y las croquetas quedan estupendas. Tráeme patas pegajosas de araña y yo te preparo algunas croquetas.\n\nHe oído que las arañas de hilo venenoso son las mejores para cocinar. Están al norte, entre las laderas de las colinas y el río.", progress = "Salve, <nombre>. ¿Has conseguido esas patas pegajosas de araña ya?", completion = "¡Tienes un bonito ramillete de patas ahí! Deja que las condimente con mis especias secretas (¡no mires!) y las saltearé en una sartén...\n\nY, aunque las croquetas de cangrejo son mi especialidad y no te daré nunca la receta, sí te daré la de un plato que es casi igual de bueno.", rewards = "Recibirás:\n\nReceta: pastel de araña pegajosa"},
[94] = {title = "Un ojo vigilante", objective = "Lleva el Glifo de Azora a la Estatua Leonida, cerca de la Torre de Ilgalar, en Crestagrana.", description = "Mi rival, Morganth, es un malvado mago de gran poder que vive en la Torre de Ilgalar, en Crestagrana. He creado un dispositivo que me permitirá espiar sus movimientos: el Ojo de Azora. Ahora puedo verlo desde lejos.\n\nPero no puedo saber lo que piensa... Necesito más ayuda.\n\nSi quieres ayudarme tú, lleva mi Glifo de Azora a la Torre de Ilgalar. Colócalo sobre la Estatua Leonida, cerca de la Torre.\n\nCuando lo hayas hecho, el poder de Azora me permitirá ver los planes de Morganth para el futuro.", progress = "Esta Estatua Leonida ha estado aquí décadas, vigilando la infame Torre de Ilgalar.\n\nHay una hendidura en la base de la estatua en la que parece que podría acoplarse una gema o un glifo...", completion = "Colocas el Glifo de Azora en la piedra. Se adhiere a ella y luego oyes la voz incorpórea de Theocritus:\n\nTienes toda mi gratitud, joven &lt;nombre&gt;. Puedes hablar conmigo a través de este Glifo. Es posible que en un futuro tenga trabajos para ti... cuando haya sondeado los pensamientos de mi rival.", rewards = "Recibirás:\n\n"},
[95] = {title = "La venganza de Sven", objective = "Ve a la granja de Sven y averigua qué había enterrado detrás del viejo tocón.", description = "Yo antes trabajaba en una granja al sureste... hasta que un día que tuve que ausentarme, los Jinetes Oscuros del Paso de la Muerte arrasaron mi granja y asesinaron a mi familia.\n\nCuando regresé vi una figura misteriosa cerca de mi granero... enterrando algo. Huyó antes de que pudiera llegar a él y tampoco quería entretenerme: quería ir tras los Jinetes Oscuros. Así que nunca descubrí qué se escondía allí.\n\nTe agradecería que encontraras lo que enterró aquella figura misteriosa. El lugar en el que debes buscar está detrás del tocón que hay cerca de mi granero.", progress = nil, completion = "Hay un montón de tierra, apilada con prisas, como si alguien se hubiera visto obligado a esconder algo en poco tiempo.", rewards = "Recibirás:\n\n"},
[96] = {title = "La llamada del agua", objective = "Llévale el fragmento de agua a Islen Vident'aqua en Los Baldíos.", description = "Llevar fragmento. Llevar fragmento y dar vida.\n\nDar vida y entender. Entender que el agua es vida... la corrupción acaba con la vida.\n\nDas vida y la proteges con el poder del agua.", progress = "Has vuelto. ¡Fantástico!\n\n¿Qué tal fue tu viaje a Filargenta? ¿Te llevó mucho tiempo?\n\nMe cuesta discernir si te fue bien porque la corrupción alcanza gran parte de la tierra. Su hedor supera cualquier bien que puedas haber hecho. Pero en ocasiones los pequeños éxitos son tan importantes como los grandes. La purificación del agua fue decisiva para ti y para todos los chamanes.", completion = "Esta es la prueba que pedí. Lo has hecho muy bien, &lt;nombre&gt;.\n\nMe complace ser el primero en felicitarte y te hago entrega de este… de tu tótem de agua.\n\nÚsalo bien y suerte.\n\nPasará cierto tiempo hasta tu próxima prueba, así que aprende bien las lecciones. El agua pura no solo devuelve la vida a los heridos, sino que puede tener muchos más efectos. Te sugiero que busques un instructor &lt;clase&gt; cuando tengas tiempo. Podrías aprender más hechizos..", rewards = "Aprenderás:\n\nTótem de corriente curativa\n\nTe van a lanzar el siguiente hechizo:\n\nTótem de corriente curativa\n\nTambién recibirás:\n\nTótem de agua"},
[97] = {title = "La leyenda de Stalvan", objective = "Informa a la comandante Cerranegro.", description = "¡Informa de inmediato a la comandante Ebonlocke de que las caligrafías coinciden, <nombre>! ¡Sus sospechas han resultado ser ciertas!", progress = nil, completion = "¡Lo sabía! ¡Buen trabajo, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[98] = {title = "La leyenda de Stalvan", objective = "Mata a Stalvan Mantoniebla y muestra a Lady Eva su anillo familiar.", description = "Stalvan Mantoniebla llevó una vida de depravación. Su mano sesgó innumerables vidas inocentes. Es, sin duda alguna, culpable de incontables crímenes. Ahora ese lunático amenaza Villa Oscura. Solo la Luz sabe qué sórdidas acciones está planeando. Debes ejecutar a Stalvan, <clase>. Lo encontrarás en su casa, al norte de la ciudad.\n\nCuando lo hayas hecho, busca a Lady Eva y enséñale este anillo familiar. Después de todo, fue su premonición lo que nos guió a este terrible descubrimiento. Gracias a ella, Villa Oscura ahora es más segura.", progress = "¿Sí, <nombre>?", completion = "Lo sé, &lt;nombre&gt;: Stalvan está muerto. Percibí una oleada de esperanza que atravesaba el bosque contaminado. El Bosque del Ocaso está libre, por fin, de su sed de sangre. Aunque mi alegría se ensombrece cuando pienso en aquellos que cayeron a manos de esa horrible bestia. En cualquier caso, has sido valiente y astuto, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCreciente de los Espíritus desoladosAnillo de espíritus desapacibles\n\nTambién recibirás:\n\nAnillo de espíritus desapaciblesCreciente de los Espíritus desolados"},
[99] = {title = "La locura de Arugal", objective = "Lleva 6 grilletes piroleños a Dalar Tejalba en El Sepulcro.", description = "Según aumenta mi conocimiento sobre la magia de Arugal, aumenta también mi desdén por el desafortunado necio. Estoy a punto de completar la investigación de su Remedio... Bonito nombre...\n\nMi conocimiento será completo cuando descubra qué encantamiento está causando el extraño comportamiento en la Aldea Piroleña. Por el día, los aldeanos parecen ser humanos. Pero, cuando el sol desaparece, se convierten en ferocanis Furia Lunar.\n\nNecesito extraer energía de los grilletes encantados que Arugal utilizó con ellos. Tráeme seis grilletes Piroleños encantados, <nombre>.", progress = "¿Has asegurado ya los grilletes piroleños, <nombre>?", completion = "Tu persistencia es encomiable. Con el conocimiento que me has ayudado a reunir, Arugal caerá y su magia temeraria se anulará. Brindo por ti, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nPartetroncosMano de mortero de huesosVarita de ceniza\n\nTe van a lanzar el siguiente hechizo:\n\nIntelecto Arcano\n\nTambién recibirás:\n\nMano de mortero de huesosPartetroncosVarita de ceniza"},
[100] = {title = "La llamada del agua", objective = "Habla con la manifestación de agua menor en el Bosque de Argénteos.", description = "Hay un destello de luz cuando pronuncias las últimas palabras del conjuro. Se desvanece el sonido de las gotas de agua sobre la llama y la emoción embarga tus sentidos conforme se destruyen los espíritus corruptos que te rodean.", progress = nil, completion = "Gracias a ti. Gracias a los chamanes.\n\nLa corrupción retrocede pero la vida sigue disminuyendo. La protección de la vida depende de los chamanes", rewards = nil},
[101] = {title = "El Tótem de castigo", objective = "Consigue 10 colmillos de necrófago, 10 dedos de esqueleto y 5 viales de veneno arácnido para Madame Eva en Villa Oscura.", description = "A medida que la corrupción mística invade el bosque, la necesidad de autoprotección se hace innegable, <nombre>. El viento me susurra y me cuenta de grandes peligros que el futuro te depara.\n\nSi deseas protegerte, noble <clase>, tráeme 10 colmillos de necrófago, 10 dedos de esqueleto y 5 viales de veneno arácnido. Te encantaré un tótem de castigo que dañará a aquellos que intenten causarte daño a ti.", progress = "Tráeme 10 colmillos de necrófago, 10 dedos de esqueleto y 5 frascos de veneno de araña. Así haré un encantamiento a un Tótem de castigo que dañará a aquellos que intenten causarte daño a ti.", completion = "Muy inteligente por tu parte, &lt;nombre&gt;. Este Tótem de castigo te protegerá del mal que acecha por todo Azeroth y más allá. Cuando te sientas amenazado, confórtate con el aura mágica de este tótem. Sé valiente, &lt;clase&gt;. Necesitarás todo el valor del que puedas hacer acopio, pues veo mucha sangre en tu futuro.", rewards = "Recibirás:\n\nTótem de Inflicción"},
[102] = {title = "En patrulla por Páramos de Poniente", objective = "Lleva 8 zarpas de gnoll al capitán Danuvin en la Colina del Centinela.", description = "Ventormenta nos ha abandonado. Nauseabundos aires de depravación soplan por las llanuras de los Páramos de Poniente. Este era mi hogar y no daré la espalda a los ciudadanos que decidieron permanecer aquí. Nosotros, los antiguos granjeros, ofreceremos resistencia.\n\nTu tarea, si decidieras aceptar, consistirá en patrullar las praderas de los Páramos de Poniente. Localiza y aniquila a los infames gnolls que parecen colaborar con los ladrones de Las Minas de la Muerte. Tráeme 8 zarpas de gnoll y sabré recompensar tu valentía.", progress = "¿Ya has reunido 8 zarpas de esos gnolls traidores?", completion = "Bien hecho, &lt;nombre&gt;. Contando con valientes aventureros como tú luchando del lado de Las Milicias del Pueblo, los Páramos de Poniente podrán volver a ser el próspero granero que una vez fueron. Por favor, acepta esto como reconocimiento por tus incansables esfuerzos.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de las Milicias del puebloBrazales de las Milicias del pueblo\n\nTambién recibirás:\n\nBrazales de las Milicias del puebloCinturón de las Milicias del pueblo"},
[103] = {title = "Guardián de la Llama", objective = "Lleva 5 botellas de aceite al capitán Grisillo en el Faro de los Páramos de Poniente.", description = "La noche en que murió la familia del guardián del Faro fue horrible. Contemplé, impotente, como el Viejo Ojosombrío lideraba el ataque. Pero lo pasado pasado está y lo que ahora me preocupa son las vidas de los que navegan por el Mare Magnum y se acercan demasiado a las peligrosas rocas de la costa. Como no hay nadie que vigile la llama, la responsabilidad es ahora mía.\n\nAyúdame a mantener la antorcha iluminada trayéndome 5 botellas de aceite de los monstruos de la cosecha.", progress = "La llama no arderá mucho tiempo sin aceite, <nombre>.", completion = "¡Loado seas, valiente &lt;clase&gt;. Las rocas de la Costa de los Páramos de Poniente estarán iluminadas gracias a ti. Mientras la antorcha esté iluminada se salvarán muchas vidas.\n\nMorí innecesariamente en esta cosa. Sigo vivo más allá de la muerte para que nadie más siga mi destino.", rewards = "Podrás elegir una de estas recompensas:\n\nPoción de maná menorPoción de curación menor\n\nTambién recibirás:\n\nPergamino de IntelectoPergamino de AguantePoción de curación menorPoción de maná menor"},
[104] = {title = "La amenaza costera", objective = "Lleva una escama del Viejo Ojosombrío al capitán Grisillo en el Faro de los Páramos de Poniente.", description = "Cuando acabó mi vida contra las rocas, no tenía ni idea de lo que me esperaba en el más allá. El Faro estaba sumido en la oscuridad aquella noche, ya que el Viejo Ojosombrío había asustado a la familia del guarda. Lograron regresar y volver a encender la llama, pero el Viejo Ojosombrío coaccionó a los múrlocs de mente más débil para que asaltaran el Faro con él de nuevo. La segunda vez, la familia no corrió la misma suerte y pereció inútilmente ante mis ojos.\n\nMata al Viejo Ojosombrío si lo ves por la costa y tráeme una de sus escamas, te recompensaré.", progress = "¿Aún no has conseguido aniquilar a la amenaza conocida como Viejo Ojosombrío? Ha sido visto recorriendo la costa de los Páramos de Poniente.\n\nVuelve a verme cuando esa bestia esté muerta.", completion = "Así que el infecto pagano Ojosombrío está muerto. Bien hecho, &lt;nombre&gt;. Tus manos han dado fin a una vida, pero han podido salvar muchas otras. El Mare Magnum, aun tan lleno de peligros como está, será algo más seguro esta noche gracias a tus hazañas.", rewards = "Podrás elegir una de estas recompensas:\n\nAntorcha de GraysonRodela de los MaresVarita antorcha\n\nTambién recibirás:\n\nAntorcha de GraysonRodela de los MaresVarita antorcha"},
[105] = {title = "Ah, Andorhal", objective = "Lleva el Fragmento de Filacteria de Araj al sumo ejecutor Derrington en El Baluarte, en las Tierras de la Peste del Oeste.", description = "¡Ay, se acerca el tiempo de atacar Andorhal y de hacer salir al exánime que lo controla!\n\nEs en el interior de las ruinas de la ciudad donde el exánime, Araj el Invocador, tiene sus dominios. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes es realmente formidable. Necesitarás muchos aliados para tener alguna oportunidad de enfrentarte a él con éxito.\n\n¡Destruye a Araj, <nombre>, y tráeme un Fragmento de su Filacteria como prueba!", progress = "<nombre>, ya te ha sido asignada tu tarea. ¿Tienes un trozo de la filacteria del exánime como prueba de que has terminado tu tarea?", completion = "¡Lo has conseguido! ¡Araj... el exánime ha sido derrotado! \n\nEn el día de hoy has prestado un enorme favor a la Horda, &lt;nombre&gt;. Tu valentía y perseverancia son testimonio de lo que significa ser un auténtico héroe. Por favor, acepta esto como muestra para que todos vean y sepan que has dado una victoria a la Horda que nunca podrá ser olvidada.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nMarca de Resolución"},
[106] = {title = "Jóvenes enamorados", objective = "Entrega a Tommy Joe Pedregosa la carta de amor de Maybell.", description = "¡Oh, estoy maldita! He entregado mi corazón a Tommy Joe Stonefield, pero nuestras familias se odian a muerte. Así que no puedo verlo, ¡aunque me duelan los ojos por no poder mirar su hermoso rostro!\n\nTe lo ruego, lleva esta carta a Tommy Joe. Suele estar en el río que está al oeste de La Granja Pedregosa, al oeste de aquí.", progress = "¡¿Que tienes qué?! Maybell es la luz que ilumina mi tediosa vida. ¡Déjame ver su carta, deprisa!", completion = "¡Ah! No puedo soportar estar separado de ella. ¡¡Tengo que verla!!", rewards = "Recibirás:\n\n"},
[107] = {title = "Una nota para William", objective = "Lleva la nota de la Abuela Pedregosa a William Mortero.", description = "¡Seguro que William Mortero tiene una poción que reúna a los dos jóvenes amantes!\n\nTen, llévale esta nota a William. Está en la Posada Orgullo de León, en Villadorada.", progress = "Así que tienes una nota de la abuelita Pedregosa, ¿eh? ¡Hace años que no veo a Mildred! Me pregunto qué dirá...", completion = "Cómo compadezco a esas pobres almas... Maybell y Tommy Joe. También yo fui joven y también conocí el amor... una vez.\n\n¡Debe de haber algo que pueda hacer para ayudarles! Deja que piense...", rewards = "Recibirás:\n\n"},
[109] = {title = "Habla con Gryan Mantorrecio", objective = "Habla con Gryan Mantorrecio. Puedes encontrarlo en la torre de piedra de la Colina del Centinela, saliendo de la carretera que cruza los Páramos de Poniente.", description = "Parece que has visto bastantes combates en tu vida, <clase>. Si no has tenido suficiente, busca a Gryan Mantorrecio. Comanda las Milicias del Pueblo que protegen las granjas de los Páramos de Poniente. Seguro que le vendrá bien tu ayuda. Puedes encontrarlo en la torre de piedra de la Colina del Centinela, saliendo de la carretera que cruza los Páramos de Poniente.", progress = nil, completion = "Así que te ha enviado aquí mi amigo, ¿eh? Qué amable por su parte. \n\nBien, te diré que la monarquía de Ventormenta nos ha abandonado. Ahora son Las Milicias del Pueblo las que mantienen la tierra a salvo de la corrupción. Si nuestra causa te interesa, puedo ofrecer a tus habilidades de combate un trabajo al servicio de la libertad.", rewards = "Recibirás:\n\n"},
[110] = {title = "Análisis de trozos de insecto", objective = "Busca al alquimista Morterozugg en Gadgetzan y comprueba su análisis de los trozos de insecto.", description = "Voy a estudiar el equipo de análisis de Patagolpe y a ordenar sus efectos personales. Tengo la extraña sensación de que esta no es la última pérdida que sufriremos ante estos bichos.\n\nQuiero que vayas a ver al alquimista Morterozugg y compruebes si ya ha tenido oportunidad de procesar los trozos de insecto que le enviamos. Si es así, vuelve aquí.", progress = nil, completion = "Sí, tengo los resultados aquí mismo. Son... inquietantes, por no decir otra cosa.\n\nCreemos que es cierta nuestra sospecha inicial de que los bichos están bajo el control de una fuerza externa. De ser así, nos enfrentamos a un poder desconocido y, lo que es más, maligno. ¡A estos bichos los han convertido en devastadores! Nunca había visto nada parecido.", rewards = "Recibirás:\n\n"},
[111] = {title = "Habla con la abuelita", objective = "Habla con la Abuela Pedregosa.", description = "Por favor, <nombre>, habla con mi abuelita. Si alguien puede encontrar la forma de reunirme con Maybell, esa es ella.\n\nEstá en nuestra casa, al este de aquí.", progress = nil, completion = "Mientras nuestras familias sigan siendo enemigas, Tommy Joe y Maybell no tendrán futuro. Pero quizás podamos reunirlos, aunque sea por poco tiempo.\n\nMm, ¿qué podríamos hacer?", rewards = "Recibirás:\n\n"},
[112] = {title = "La recolección de Kelp", objective = "Lleva 4 hojas de kelp cristalino William Mortero a Villadorada.", description = "Puedo hacer un licor de invisibilidad para Maybell, para que pueda salir de Los Viñedos de Maclure y reunirse con Tommy Joe. Pero para hacer el licor necesito kelp cristalino.\n\nAunque el kelp normalmente crece en el océano, también se puede conseguir de los murlocs. Ve a ver si los murlocs que hay cerca del Lago de Cristal tienen algo. El Lago de Cristal está al este de Villadorada.", progress = "¿Tienes ya el kelp cristalino? Maybell estará ansiosa por ver a su amado...", completion = "Lo tienes. ¡Muy bien! Dame solo un momento para preparar la poción.", rewards = "Recibirás:\n\n"},
[113] = {title = "Análisis de trozos de insecto", objective = "Entrégale el informe al perito jefe Picoflojo de Gadgetzan. Asegúrate de que te da una copia del informe, tal y como el alquimista Morterozugg ha pedido.", description = "El perito jefe Picoflojo tiene que ver este informe inmediatamente. En realidad... todo el que sepa leer debería echarle un vistazo. Nos enfrentamos a una grave y seria amenaza no solo para Tanaris y Gadgetzan, sino también para toda la zona meridional de Kalimdor si no le ponemos solución pronto. ¡Qué diablos! Incluso es posible que afecte a la totalidad de Azeroth pues no hay ningún indicio de que vaya a mejorar la situación.\n\nPídele a Picoflojo una copia del informe para poder distribuirlo. ¡Todos deben conocer la existencia de esta amenaza!", progress = "Y bien, perito adjunto, ¿Morterozugg tiene el informe listo?", completion = "Gracias por el informe, perito adjunto &lt;nombre&gt;. Aquí tienes lo que te corresponde por el gran servicio prestado a la compañía de aguas de Gadgetzan.\n\nAunque no suelo entregar nuestra valiosa información de manera desinteresada a aquellos que con toda seguridad pagarían bien por ella, hace años que conozco a Morterozugg. Si él recomienda que todos han de conocer esta información, que así sea. Dame un momento y te haré una copia.", rewards = "Recibirás:\n\n"},
[114] = {title = "La fuga", objective = "Lleva el licor de invisibilidad a Maybell Maclure.", description = "Lleva este licor de invisibilidad a la joven Maybell. Debería durar lo suficiente para que haga una visita a Tommy Joe.", progress = "¿Entregaste mi carta a Tommy Joe? ¿Y qué dijo?", completion = "¡Ay! Me siento muy culpable por engañar a mi familia, pero no puedo ignorar lo que siento por Tommy Joe.\n\nGracias, &lt;nombre&gt;. Beberé este licor en cuanto tenga la oportunidad de escaparme para ir a ver a mi amado.\n\nPor favor, acepta esto.", rewards = "Recibirás:\n\nPoción de curación menor"},
[115] = {title = "Magia de las Sombras", objective = "Lleva 3 orbes de medianoche al alguacil Marris a Villa del Lago.", description = "Los Roca Negra han traído nigromantes a los ataques en Crestagrana. Tienen unos objetos de poder oscuro, los orbes de medianoche. Estos orbes han malherido a muchos de los que luchan en Crestagrana; tenemos que hacernos con esos objetos demoníacos. \n\nTráeme los orbes de medianoche de los cadáveres de nigromantes Roca Negra. Me encargaré de destruirlos; el mundo estará mejor sin ellos.", progress = "¿Cómo va con los taumaturgos umbríos? ¿Tienes los orbes de medianoche?", completion = "Gracias, &lt;nombre&gt;. Acabaré con ellos de inmediato.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[116] = {title = "Tiempos de sequía", objective = "Lleva 1 barril de cerveza Cebatruenos, 1 barril de vino dulce, 1 botella de licor de luna y 1 botella de ron dulce al posadero Daniels a Villa del Lago.", description = "Tengo un problema, <nombre>. Nos estamos quedando sin bebida y el envío se está retrasando mucho. Esta invasión orca ha sido terrible. \n\n¿Buscas trabajo? \n\nNecesito que vayas a recoger un barril de cerveza Cebatruenos de Tragoamargo Cebatruenos, que está en las colinas de Páramos de Poniente, uno de Merlot en Ventormenta, una botella de licor de luna en Villa Oscura y una de ron dulce en Villadorada. Tráeme todo eso y te ganarás una recompensa.", progress = "Necesito que vayas a recoger un barril de cerveza Cebatruenos de Tragoamargo Cebatruenos, que está en las colinas de Páramos de Poniente, uno de vino dulce en Ventormenta, una botella de licor de luna en Villa Oscura y una de ron dulce en Villadorada. Tráeme todo eso y te ganarás una recompensa.", completion = "¡Buen trabajo, &lt;nombre&gt;! Sé qué caminos has tenido que recorrer para traer este licor. ¡Esto complacerá a mis patronos! \n\nMi esposa cose muy bien; acepta esta capa en agradecimiento por tus servicios.", rewards = "Recibirás:\n\nCapa tejida con cuidadoUn monedero abultado"},
[117] = {title = "Cebatruenos", objective = "Entrega a Tragoamargo Cebatruenos 5 lúpulos para que pueda terminar su cerveza especial.", description = "Rápido, <amigo/amiga> y date prisa\nNuestra cerveza está de risa\nMás cerveza y menos comida,\nDadnos lúpulo para hacer birra.", progress = "Rápido, amigo y date prisa\nNuestra cerveza está de risa\nMás cerveza y menos comida,\nDadnos lúpulo para hacer birra.", completion = "Lúpulo y cebada, malta y levadura\nNi te imaginas cómo se disfruta,\n\nDeja de pedir, deja de gemir\n¡La cerveza Cebatruenos ya viene en su barril!", rewards = "Recibirás:\n\nBarril de Cebatruenos"},
[118] = {title = "El precio de unas herraduras", objective = "Lleva la nota de Verner al herrero Argus a Villadorada.", description = "¡He necesitado tanto hierro para abastecer a la guardia de Ventormenta que no tengo bastante para hacer herraduras! \n\nVe a Villadorada y dale esta nota al herrero Argus; le cuento mi problema y le hago un pedido de herraduras.", progress = "¿Qué dices? ¿Que vienes de parte de Verner? Pues dame la nota. ¡Y habla más alto!", completion = "Así que necesita herraduras, ¿eh?", rewards = "Recibirás:\n\n"},
[119] = {title = "Vuelve con Verner", objective = "Vuelve a Crestagrana y entrega la caja de herraduras a Verner Osgood.", description = "Muy bien, 50 herraduras... \n\n¡Aquí las tienes, listas para llevar! Dile a Verner que ha sido un placer y que lea la nota que he pegado a la caja.", progress = "¡Me alegro de verte! ¿Traes las herraduras?", completion = "Gracias, me hacía mucha falta.\n\nAh, hay una nota... ¿Cómo? ¡¿Argus quiere que le pague?! ¡Bah!\n\nBueno, gracias por tu ayuda.", rewards = "Recibirás:\n\n"},
[120] = {title = "Mensajero a Páramos de Poniente", objective = "El magistrado Solomon te ha dado un informe para el general Marcus de Ventormenta. Quiere que vuelvas en cuanto lo hayas entregado.", description = "Estamos en tiempos difíciles, <clase>. Vivimos bajo un asedio constante. Si no llegan refuerzos, solo nos queda la derrota. El mensaje que te confío es de la mayor importancia. Llévaselo al general Marcus Jonathan de Ventormenta inmediatamente. Después vuelve, ya sean tus noticias buenas o malas. \n\n¡Apresúrate!", progress = "¿Qué llevas ahí?", completion = "Descansa, &lt;clase&gt;.\n\nEl magistrado es un líder de los nobles; siempre atiendo a sus palabras. Voy a reunirme con el Rey para exponerle la situación. Dile al magistrado que enviaremos refuerzos en cuanto Su Majestad dé la orden.", rewards = "Recibirás:\n\n"},
[121] = {title = "Mensajero a Páramos de Poniente", objective = "Lleva la respuesta del general Marcus Jonathan al magistrado Solomon a Villa del Lago.", description = "Mientras espero la respuesta del Rey, llévale esta carta al magistrado Solomon. \n\nPuedes retirarte, <clase>.", progress = "¿Qué noticias envía el General? ¿Vienen refuerzos?", completion = "Gracias por todo, &lt;clase&gt;. Acepta estas monedas por tus servicios a Villa del Lago y Ventormenta.\n\nY ahora disculpa, pero esta correspondencia me intriga; está pasando algo turbio. Temo que se avecine otro conflicto.", rewards = "Recibirás:\n\n"},
[122] = {title = "Escamas de barriga", objective = "Lleva 6 escamas de barriga de cachorro de dragón negro a Verner Osgood a Crestagrana.", description = "Necesito escamas de barriga de cachorro de dragón negro para pagarle las herraduras a Argus. Si me traes 6 escamas, tendré para pagarle y también para hacerte un regalo. \n\nEsos bichos vuelan al sur de Villa del Lago, pero a menudo se alejan. Tendrás que estar ojo avizor para cazarlos en cuanto los veas.", progress = "Si no le envío las escamas a Argus, mandará a alguien a buscarlas. ¿Las tienes o no?", completion = "Gracias, con esto pagaré a Argus.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de cría negraGuantes de cría negra\n\nTambién recibirás:\n\nGuantes de cría negraBotas de cría negra"},
[123] = {title = "El Coleccionista", objective = "Busca al alguacil Dughan en Villadorada y dale la lista de coleccionista.", description = "Esta nota es una lista de los días y las horas a las que una persona –descrita como \"el Coleccionista\"– recibirá cargamentos de oro provenientes de las minas del Bosque de Elwynn.\n\nPor lo que pone en la nota, parece que el Coleccionista vive cerca de la Plantación de Calabazas de Brackwell, al este de Elwynn.\n\nParece importante. Deberías informar al alguacil Dughan de Villadorada.", progress = "¡¿Qué?! ¡Pero si nuestra gente lleva meses sin trabajar en las minas de Elwynn!\n\nDéjame ver esa nota...", completion = "Mm... He oído hablar de ese Coleccionista, pero no sé para quién trabaja. Gracias por la información. Nos ayudará a resolver este misterio.", rewards = "Recibirás:\n\n"},
[124] = {title = "El aullido de los gnolls", objective = "Mata a 10 brutos y 8 místicos Crestagrana y ve a ver a Verner Osgood.", description = "¡Como si no tuviera bastante con los orcos! Ahora tengo brutos y místicos gnolls en la cresta que hay al norte de mi establo y se llevan mis caballos cuando se pierden. \n\nSi nos libras de los gnolls, mis caballos y yo te lo agradeceremos eternamente.", progress = nil, completion = "Gracias por tu ayuda, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[125] = {title = "Las herramientas perdidas", objective = "El supervisor Oslow de Villa del Lago quiere que recojas su caja de herramientas del fondo del Lago Sempiterno.", description = "Échame una mano, <nombre>. Con todo esto del asedio, es difícil conseguir suministros. Mis herramientas venían de camino de Villadorada, pero hicieron explotar el puente. Pusimos las herramientas en una barca, pero los orcos atacaron con una catapulta y todo acabó en el fondo del lago. \n\nRecupera mi caja de herramientas; te ganarás una buena recompensa.", progress = "¿Me traes las herramientas?", completion = "Excelente trabajo, &lt;nombre&gt;. Pensé que no volvería a ver mis herramientas.", rewards = "Recibirás:\n\nJuego de armadura medio"},
[126] = {title = "El aullido de las colinas", objective = "Lleva la zarpa de Ululante a Verner Osgood a Villa del Lago.", description = "Los gnolls siguen ahí. Los oigo aullar mientras cazan en las colinas de Villa del Lago. El que más fuerte aúlla es Yowler, el jefe. \n\n¡Mátalo! ¡Su manada se asustará y huirá! Tráeme su zarpa como prueba. \n\nY ten cuidado; si es el jefe, será por algo. Ya sabes lo que dicen de los perros viejos.", progress = "Ululante es peligroso, <nombre>. Entenderé que no seas capaz de enfrentarte a él.", completion = "Gracias de nuevo. El ejército de Ventormenta está tan ocupado con los orcos que no hay tropas para proteger Crestagrana de otras amenazas, como los gnolls.", rewards = "Podrás elegir una de estas recompensas:\n\nAnillo de Voluntad de hierroBotas de milicia de oro\n\nTambién recibirás:\n\nAnillo de Voluntad de hierroBotas de milicia de oro"},
[127] = {title = "Pescado a la venta", objective = "Lleva 10 peces luna moteados al maestro de embarcadero Baren a Villa del Lago.", description = "El Lago Sempiterno es famoso por su pez luna moteado. Tiene mucha demanda, ¡pero me estoy quedando sin existencias! \n\nTráeme 10 peces luna moteados; te pagaré bien. Y si no sabes pescar, intenta robárselo a los murlocs.", progress = "¿Qué me traes?", completion = "¡Ha sido un placer!", rewards = "Recibirás:\n\nSopa de aleta de múrlocReceta: sopa de aleta de múrlocAceite de hígado de pescado"},
[128] = {title = "La recompensa de Roca Negra", objective = "Mata a 15 campeones Roca Negra y el guardia Howe te recompensará en Villa del Lago.", description = "Tenemos que acabar con esos Roca Negra. El enemigo ha invadido nuestra tierra. Estas montañas pertenecen al rey de Ventormenta. Nuestras fuentes nos dicen que el jefe de cada unidad del clan se llama campeón. Encontrarás su campamento hacia el norte, justo al oeste del camino a Las Estepas Ardientes. \n\nEl magistrado Solomon quiere que mueran. Acaba con 15 campeones Roca Negra y vuelve a verme.", progress = nil, completion = "Has mostrado gran valor frente a los Roca Negra, &lt;clase&gt;. Nos has prestado un gran servicio.", rewards = "Recibirás:\n\n"},
[129] = {title = "Una comida gratis", objective = "Llévale su comida al guardia Parker. Está de patrulla en el camino a Villa Oscura.", description = "¿Puedes hacerme un favor? Le he preparado la comida al guardia Parker, pero ha salido de patrulla. Él puede salir sin miedo, pero para mí es demasiado peligroso. \n\n¡Si le llevas la comida, cuando vuelvas te invitaré a comer! ¿Qué me dices? Está de patrulla en el camino a Bosque del Ocaso.", progress = "¿Que Darcy me envía la comida? Pues venga, ¡a mover el bigote!", completion = "Gracias, me muero de hambre. ¡Proteger la ciudad de los orcos y los gnolls es un trabajo duro!", rewards = "Recibirás:\n\n"},
[130] = {title = "Una visita al herborista", objective = "Habla con Martie Jainrose, la herborista de Crestagrana.", description = "¿Te importa entregarle unas flores a Darcy de mi parte? Cómpralas en el herbolario de Villa del Lago, en el extremo oeste de la ciudad. \n\nPero no vayas a decir en la tienda para quién son o quién las envía...", progress = nil, completion = "¿Un ramo de flores? ¿Acabas de llegar y ya estás con romances?\n\nNo es por chafardear, pero los amores juveniles son tan bonitos... sobre todo en tiempos de guerra.", rewards = "Recibirás:\n\n"},
[131] = {title = "Un ramo de narcisos", objective = "Dale el ramo de narcisos a Darcy.", description = "Aquí tienes el ramo. Lo he hecho de narcisos, mis flores favoritas.", progress = "Hola, <nombre>. ¿Le gustó la comida a Parker?", completion = "¡Qué bonitas! ¡Y qué bien huelen!\n\nPero son las flores favoritas de Martie. ¿No me digas que Parker te mandó a comprarle las flores a esa bruja? No le dijiste que eran para mí, ¿verdad? Porque si lo sabía, seguro que puso veneno.\n\nEn fin, no es culpa tuya. Gracias, y aquí tienes la comida.", rewards = "Recibirás:\n\nPezsol salteado"},
[132] = {title = "La hermandad de los Defias", objective = "Lleva la Nota de Wiley a Gryan Mantorrecio en los Páramos de Poniente.", description = "Lo que voy a contarte puede costarme la vida. La banda Defias está preparando algo grande. Lo último que oí fue que estaban colaborando con varios gnolls, kobolds e incluso goblins.\n\nLlévale esta nota a Mantorrecio. Explica todo lo que sé sobre este asunto.", progress = "Me alegro de verte de vuelta, <nombre>. ¿Qué tenía que decir Wiley?", completion = "¡Bah! Debería haber acabado con esa escoria cuando tuve oportunidad de hacerlo. Pero esta información es crucial. Buen trabajo.", rewards = "Recibirás:\n\n"},
[133] = {title = "Un espantapájaros para necrófagos", objective = "Reúne 7 costillas de necrófago y llévaselas a Abercrombie a su cabaña.", description = "Vivo tan lejos de la protección de la ciudad que es un milagro que los necrófagos y los muertos andantes no me hayan devorado. ¡De hecho, ayer, sin ir más lejos, una manada de masticahuesos intentó tirar abajo las paredes de mi casa!\n\nQuiero hacer un monigote... una especie de espantapájaros para necrófagos. Y para hacerlo necesito costillas de necrófago. Puedes conseguirlos de comecarnes, comehuesos, comecerebros, podridos y propagadores de la plaga.\n\nConsígueme esas costillas y yo te pagaré.", progress = "Creo que estoy oyendo a una panda de necrófagos en la parte trasera de la casa... ¡tengo que colocar ese espantapájaros ya! ¿Me conseguiste las costillas de necrófago?", completion = "Ah, gracias. ¡Son justo lo que necesito!", rewards = "Recibirás:\n\n"},
[134] = {title = "Ladrones ogros", objective = "Devuelve a Abercrombie la caja de Abercrombie.", description = "Hace unas pocas semanas, mientras recogía hierbas lejos de mi casa, me atacó una manada de ogros. Huí a toda prisa y tuve que dejar atrás una caja llena de valiosas herramientas y hierbas.\n\nLos ogros volvieron a su túmulo del sur del Bosque del Ocaso pavoneándose de su hazaña. Tengo la seguridad de que mi caja está cerca de ese túmulo.\n\nPor favor, <nombre>, recupérame esa caja, la necesito.", progress = "¿Tienes mi caja? Si la tienes, ¡dámela! ¡Rápido!", completion = "Gracias al Nec... no, no, ¡gracias a TI, &lt;nombre&gt;! Te has ganado tu recompensa.\n\n¡Aah! ¡Qué felicidad! ¡¡Qué noches me esperan!!", rewards = "Recibirás:\n\n"},
[135] = {title = "La hermandad de los Defias", objective = "Lleva la Nota de Wiley a Mathias Shaw de Ventormenta.", description = "Me pregunto qué querría decir Wiley cuando habló de los Albañiles. Quizás fue un lapsus. ¿Podría estar relacionada la banda Defias con los Albañiles? El único que podría saberlo es Mathias Shaw, líder del IS:7. Enséñale la nota de Wiley, tal vez tenga algo que añadir a este misterio. Si tienes problemas para encontrarlo, busca en el Cuartel, en el Casco Antiguo.", progress = "¿Qué asuntos tienes conmigo? Soy un hombre muy ocupado...", completion = "Creo que este asunto es mucho más complicado de lo que cree Mantorrecio.", rewards = "Recibirás:\n\n"},
[136] = {title = "El tesoro escondido del capitán Sanders", objective = "Encuentra el baúl del capitán Sanders y busca la siguiente pista.", description = "Si estás leyendo esto, el capitán Sanders estará sepultado bajo el agua. Así que mi tesoro es tuyo, solo tienes que seguir las pistas.\n\nPrimero tienes que encontrar mi baúl. Seguramente estará enterrado en la arena, en la costa Oeste de los Páramos de Poniente, cerca de los restos de naufragios. Habrá muchos restos, pero solo un ancla oxidada en la costa. ¡Encuentra el ancla y encontrarás mi baúl! Allí encontrarás también la siguiente pista.", progress = nil, completion = "El baúl se abre lentamente. Solo parece estar lleno de agua y arena. ¡Pero espera! ¡Está saliendo un pequeño cangrejo con una pista del tesoro entre las pinzas!", rewards = "Recibirás:\n\n"},
[138] = {title = "El tesoro escondido del capitán Sanders", objective = "Busca el viejo barril junto a la chimenea en ruinas y busca la siguiente pista.\n\nObjeto provisto: \n", description = "Esto es lo que dice la siguiente pista: ¡Buen trabajo, compañero! Ahora tienes que ir al Este. Al Este hacia los riscos, al Este hacia el camino. Busca las ruinas de chimeneas a orillas del camino. Allí encontrarás un viejo barril con la siguiente pista.", progress = nil, completion = "¡Buen trabajo cazatesoros!", rewards = "Recibirás:\n\n"},
[139] = {title = "El tesoro escondido del capitán Sanders", objective = "Busca la jarra vacía junto al molino de viento para encontrar la siguiente pista.\n\nObjeto provisto: \n", description = "Buscando en el barril descubres otro fragmento de pergamino. En este se puede leer: Desde este barril, ve hacia el Norte. Tan recto como vuela el cuervo, anda hasta que veas una jarra vacía junto al solitario molino de viento sobre los riscos del mar. Si buscas alrededor de la jarra encontrarás lo que estás buscando.", progress = nil, completion = "¡Te estás acercando al premio gordo, buscatesoros!", rewards = "Recibirás:\n\n"},
[140] = {title = "El tesoro escondido del capitán Sanders", objective = "Localiza el cofre del capitán Sanders y ábrelo para obtener tu recompensa.\n\nObjeto provisto: \n", description = "Ciertamente, dentro de la Vieja jarra hay otra pista sobre el tesoro de Sander. La tinta se ha borrado en algunas partes y el papel huele a whisky, pero aún puedes entender algo: Ahora que has encontrado mi vieja jarra de whisky ¡casi has llegado al tesoro! Dirígete hacia el Oeste desde la botella y baja hasta la costa. Cuando llegues al agua, ¡sigue andando! ¡Nada directo hacia el Oeste hasta encontrar la isla con el cofre del tesoro!", progress = nil, completion = "Las bisagras del viejo cofre están oxidadas, pero todavía funcionan. Fuerzas el cofre, lo abres y coges el botín.\n\n¡Felicidades!", rewards = "Recibirás:\n\nLingote de plataCamisa del capitán SanderFajín del capitán SanderBolsa de botín del capitán Sander"},
[141] = {title = "La hermandad de los Defias", objective = "Lleva el informe de Shaw a Gryan Mantorrecio de los Páramos de Poniente.", description = "La Hermandad de los Albañiles estaba dirigida por un tal Edwin VanCleef. VanCleef fue responsable de la reconstrucción de Ventormenta cuando la ciudad fue arrasada por los orcos en la Primera Guerra. Al parecer, VanCleef y sus hombres no estaban satisfechos con el tratamiento que les brindó el rey cuando terminó la reconstrucción. Pero eso solo explicaría algunas cosas.\n\nHe redactado un informe más detallado para tu maestro en los Páramos de Poniente. ¡Llévaselo de una vez!", progress = "¿Ha conseguido aclarar algo el maestro Shaw?", completion = "Edwin VanCleef... conozco muy bien ese nombre. Me desconcierta pensar que un hombre con tanto talento y tan trabajador pudiera convertirse en tal escoria. Necesitaré más pruebas antes de creerlo.", rewards = "Recibirás:\n\n"},
[142] = {title = "La hermandad de los Defias", objective = "Localiza al mensajero Defias de los Páramos de Poniente y lleva su mensaje a Mantorrecio.", description = "Tenemos que descubrir la localización de la Ladronera Defias. <nombre>, según los informes de mi rastreador, se ha visto a un mensajero Defias en los caminos entre el Arroyo de la Luna, la Mina de la Costa de Oro y la Mina de Jango. Quiero que le captures. Si ofrece resistencia, mátalo y tráeme lo que esté llevando.", progress = "<nombre>, ¿pudiste reunir alguna información? ¿Localizaste al mensajero?", completion = "Esto es sin duda alguna una prueba clara de que VanCleef está al mando. Ahora todo lo que tenemos que saber es dónde se oculta la banda Defias.\n\nTuvimos un golpe de suerte mientras estabas fuera. Capturamos a un ladrón que intentaba robar el carro de Saldean. Ha prometido guiarnos hasta la ladronera a cambio de su vida. Quiero que defiendas al traidor para que pueda enseñarnos la ladronera. Vuelve a verme cuando hayas descubierto su localización.", rewards = "Recibirás:\n\n"},
[143] = {title = "Mensajero a Páramos de Poniente", objective = "El magistrado Solomon quiere que lleves su petición a Gryan Mantorrecio a Páramos de Poniente.", description = "En el ejército de Ventormenta pasa algo raro; ya deberían haber llegado. No tenemos tiempo que perder. No voy a quedarme a mirar cómo se sacrifica la gente de Villa del Lago sin intentar conseguir ayuda. He oído que en Páramos de Poniente se ha formado una Milicia del Pueblo. \n\nLleva nuestra súplica a su jefe, Gryan Mantorrecio; puede que sus hombres nos ayuden hasta que llegue la Reserva.", progress = "Saludos, <clase>. ¿Qué te trae a hablar con Las Milicias del Pueblo?", completion = "Has servido bien a tu maestro.", rewards = "Recibirás:\n\n"},
[144] = {title = "Mensajero a Villa Oscura", objective = "Lleva la respuesta de Gryan Mantorrecio al magistrado Solomon.", description = "Me duele leer la nota del magistrado Solomon. Evidentemente, no sabe que hay guerra en Páramos de Poniente, si no, no pediría ayuda a la Milicia. Si Ventormenta no nos hubiera abandonado también a nosotros, no necesitaríamos una milicia. \n\nLlévale la respuesta a tu señor en Crestagrana, <clase>. Dile que lamento la pérdida de tantos hombres buenos.", progress = "¿Están Las Milicias del Pueblo en camino? ¿Traes buenas noticias de Gryan Mantorrecio?", completion = "Esto no es nada bueno. No me había dado cuenta de que Mantorrecio está igual que nosotros. ¿Qué campaña es tan importante como para enviar al ejército de Ventormenta de su gente? Lo sabré a su debido tiempo.\n\nTen, acepta estas monedas en pago por tus servicios Puede que vuelva a contratarte.", rewards = "Recibirás:\n\n"},
[145] = {title = "Mensajero a Villa Oscura", objective = "El magistrado Solomon quiere que lleves una carta a Lord Ebonlocke a Villa Oscura.", description = "Necesito tu ayuda una vez más. He escrito a Lord Ebonlocke para pedir que mande a su Guardia Nocturna como refuerzo de la defensa de Villa del Lago. Es imprescindible que reciba este mensaje cuanto antes, pero debes ser extremadamente prudente. Ebonlocke es el alcalde de Villa Oscura, en pleno corazón del Bosque del Ocaso. No te salga de los caminos.", progress = "Parece que vienes de lejos, <clase>. ¿Qué te trae a Villa Oscura?", completion = "Gracias por venir hasta aquí. Esta información es muy importante.", rewards = "Recibirás:\n\n"},
[146] = {title = "Mensajero a Villa Oscura", objective = "Lleva la respuesta de Lord Ebonlocke al magistrado Solomon.", description = "Si no conociera a Solomon, diría que se ha vuelto loco. ¿Que envíe a mi Guardia Nocturna a Villa del Lago porque Ventormenta no envía ayuda? Formé la guardia porque Ventormenta nos abandonó cuando nos infestó esta magia inmunda. Si envío a la guardia, esta ciudad sería invadida antes de que mis guardias se perdieran en el horizonte. \n\nLLévale la respuesta a tu señor en Villa del Lago.", progress = "Me alegro de verte. ¿Debo informar de que La Guardia Nocturna ha salido de Villa Oscura?", completion = "¡Por la Luz! El ejército de Ventormenta se ha retirado de Páramos de Poniente. ¿La Guardia ya no protege Villa Oscura? Aquí hay algo muy siniestro.\n\nAh, quería recompensarte, &lt;clase&gt;; toma unas monedas. Disculpa, pero tengo que averiguar qué está pasando en Ventormenta.", rewards = "Recibirás:\n\n"},
[147] = {title = "La persecución", objective = "Encuentra y mata al Coleccionista y vuelve junto al alguacil Dughan con la sortija de coleccionista.", description = "Si el Coleccionista se está llevando oro de nuestras minas, ¡está robando al Rey! Lleva al Coleccionista ante la justicia. Y tráeme a mí el anillo que se mencionaba en la nota. Podría indicarnos para quién trabaja el Coleccionista...\n\nLa nota dice que el Coleccionista se esconde en la Plantación de Calabazas de Brackwell. Deberías buscarlo allí.\"", progress = "¿Has encontrado al Coleccionista? ¿Has averiguado para quién trabaja?", completion = "¿Lo has encontrado? Bien hecho, &lt;nombre&gt;. ¡Ya no volverá a \"coleccionar\"!\n\nY... este anillo que encontraste es muy interesante. Es un anillo de la antigua Hermandad de albañiles de Ventormenta. ¿Cómo habrá llegado el anillo de una hermandad de artesanos a manos de un mísero ladrón? ¿Y por qué los ladrones Defias se llevan dinero de nuestras minas?\n\nSon preguntas difíciles de responder. Pero espero que algún día encontraré esas respuestas.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de anillas de VentormentaMuñequeras elásticas\n\nTambién recibirás:\n\nMuñequeras elásticasGuantes de anillas de Ventormenta"},
[148] = {title = "Suministros de Villa Oscura", objective = "Habla con Lady Eva.", description = "Ay, ¿qué puede hacer un anciano como yo? Necesito suministros de Villa Oscura, pero está tan lejos y yo soy tan viejo y tan débil... No sobreviviría al viaje.\n\n¿Podrías ir a Villa Oscura y traerme un poco de hilo de cabello de fantasma? Puedes conseguirlo de Lady Eva. Ve a su casa de Villa Oscura.", progress = nil, completion = "Ah, necesitas Hilo de pelo de fantasma, ¿no es así? Pues me temo que no tengo ninguno en el almacén, pero puedo hacértelo, si tú me proporcionas el pelo de fantasma.", rewards = "Recibirás:\n\n"},
[149] = {title = "Hilo de pelo de fantasma", objective = "Lleva el peine espectral a Mary la Ciega.", description = "Mary la ciega es un desgraciado y triste espíritu que mora en una vieja granja en las colinas del sur de Villa Oscura.\n\nTen, coge este peine espectral y pide a la ciega Mary que se peine el cabello. Luego tráeme el peine y yo extraeré de él el cabello de fantasma que necesito para crear el hilo.\n\nY ten cuidado... cerca de la casa acechan seres oscuros...", progress = "<Snif> Oh, por favor, ¡no me mires! ¡Arranqué estos ojos con mis propias manos, pero puedo sentir tu mirada fija en mí!\n\n¡Soy un monstruo! ¡Un monstruo horrible! ¡¡Por favor, no me mires!!", completion = "¿Qué es eso? ¿Un peine? ¡Es precioso! Y se desliza como seda por mi cabello, como si no fuera el alambroso y grasiento horror que es.\n\nAy, si tan solo tuviera un espejo...", rewards = "Recibirás:\n\n"},
[150] = {title = "Cazadores furtivos", objective = "Lleva 8 ancas múrloc al maestro de embarcadero Baren a Villa del Lago.", description = "Los murlocs nos están complicando la vida. Se están instalando en las orillas del Lago Sempiterno y pescan en cantidades increíbles. ¡Tenemos que echarlos o acabarán con todos los peces del lago! \n\n¿Te apetece cazar murlocs? Tráeme 8 ancas y te llevarás algo a cambio...", progress = "¿Tienes las aletas? ¡Tenemos que echar a los múrlocs del lago!", completion = "Buen trabajo, &lt;nombre&gt;. \n\nEs raro que estén tan lejos del mar; puede que estuvieran huyendo de algo...", rewards = "Recibirás:\n\nCaña de pescar enana"},
[151] = {title = "La pobre y vieja Blanchy", objective = "Verna Furlbrow de los Páramos de Poniente quiere que le lleves 8 puñados de avena.", description = "¡Pobre Vieja Blanchy! La bestia está agotada después de todo el trabajo que le dimos. Le di de comer antes de dejar la granja, pero no esperábamos que se rompiera el carro. Si pudieras traerle algunos puñados de avena te estaría muy agradecida.\n\nSeguro que podrás encontrar algunos por las granjas de los Páramos de Poniente, si puedes librarte de esas horribles máquinas que los han invadido. Hay varias granjas al suroeste de aquí.", progress = "La Vieja Blanchy está en las últimas. ¿Has podido encontrar algo de avena?", completion = "¡Muchísimas gracias, &lt;nombre&gt;! ¡La pobre Vieja Blanchy se pondrá tan contenta!", rewards = "Recibirás:\n\nManta del viejo BlanchyFaltriquera del viejo Blanchy"},
[152] = {title = "Hay moros en la costa", objective = "Mata 7 cazamareas, 7 guerreros, 7 oráculos y 7 correcostas y vuelve con el capitán Grisillo en el Faro de los Páramos de Poniente.", description = "Seguramente habrás visto todos los restos de naufragios a lo largo de la costa. El Mare Magnum es traicionero. La costa de los Páramos de Poniente tiene que estar limpia, de ese modo si algún marinero llega hasta nuestras costas, no correrá ningún peligro. Pero los murlocs son un gran problema.\n\nMata 7 cazamareas, 7 guerreros, 7 oráculos y 7 correcostas y procuraré que seas <recompensado/recompensada>.", progress = nil, completion = "Bien hecho, &lt;nombre&gt;. Tienes talento para el combate. Gracias a ti, la Costa de los Páramos de Poniente es un lugar más seguro.", rewards = "Recibirás:\n\n"},
[153] = {title = "Pañuelos rojos de cuero", objective = "Lleva 15 pañuelos de cuero rojo al explorador Galiaan en la Colina del Centinela.", description = "La tapadera de los Defias cambia constantemente. He estado siguiendo sus movimientos desde hace algún tiempo. Aunque, recuerda, he averiguado que muchos miembros de la banda se identifican por sus pañuelos de cuero rojo.\n\nTráeme 15 de esos pañuelos y procuraré que seas <recompensado/recompensada>.", progress = "Tráeme 15 pañuelos de cuero rojo y te pagaré bien.", completion = "Buen trabajo, &lt;raza&gt;. Por favor, acepta uno de estos objetos como pago por tu trabajo.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de las Milicias del puebloGrebas de las Milicias del puebloLeotardos de las Milicias del pueblo\n\nTambién recibirás:\n\nCapa de las Milicias del puebloGrebas de las Milicias del puebloLeotardos de las Milicias del pueblo"},
[154] = {title = "Devuelve el peine", objective = "Lleva el peine de fantasma a Lady Eva a Villa Oscura.", description = "¡¡NO!!\n\n¡Aleja ese peine de mí!\n\n¡Soy un monstruo! ¡Nadie volverá a pensar que soy guapa!", progress = "Tengo la sensación de que has visto a Mary la Ciega. ¿Tienes mi peine...?", completion = "Con esto puedo hacerte un carrete de Hilo de pelo de fantasma y todavía me sobrarían algunas hebras. Ten, acepta estas monedas a cambio de esas hebras.", rewards = "Recibirás:\n\n"},
[155] = {title = "La hermandad de los Defias", objective = "Escolta al Traidor de los Defias a la ladronera secreta de la Hermandad Defias. Cuando el Traidor de los Defias te enseñe dónde se esconden VanCleef y sus hombres, regresa con Gryan Mantorrecio con toda la información.", description = "¿Así que Mantorrecio envía a un <raza> enclenque para protegerme? Supongo que tendrás que hacerlo. Pero será mejor que traigas algunos amigos.\n\n¿Ya conoces el trato, verdad? Tú me guardas las espaldas y yo te llevo hasta la Ladronera Defias. La banda Defias busca mi cabeza, así que si me ven contigo, intentarán matarme.\n\nAvísame cuando tú y los amigos que puedas reunir estéis listos para emprender la marcha.", progress = nil, completion = "¡Excelente, &lt;nombre&gt;! Ahora que sabemos dónde se oculta, VanCleef ya es nuestro.", rewards = "Recibirás:\n\n"},
[156] = {title = "Busca flores podridas", objective = "Recoge 8 flores podridas y llévaselas al tabernero Smitts a Villa Oscura.", description = "Si me traes algunas flores podridas, podré prepararte algo de zumo de zombi.\n\nLas flores podridas crecen en lugares extraños. Se dice que se encuentran en el interior de las calaveras de los horrores esqueléticos y de los demonios esqueléticos. Claro que nunca lo he comprobado...\n\nEn el Cementerio del Cerro del Cuervo y en el Jardín Sereno encontrarás esqueletos de esos a montones. Mientras buscas flores podridas, podrías aprovechar para deshacerte de algunos de ellos. Villa Oscura te estaría muy agradecida.", progress = "Hola, <nombre>. ¿Me has conseguido ya esas flores podridas?", completion = "Bien, tienes las flores podridas. Y le diré a Althea Ebonlocke de La Guardia Nocturna que has estado matando esqueletos.\n\nSalvaguardar a las gentes de Villa Oscura es un tremendo peso para La Guardia Nocturna... siempre se alegran de saber que hay otros que ayudan a contener a esas bestias del Bosque del Ocaso.", rewards = "Recibirás:\n\n"},
[157] = {title = "Devolver el pelo", objective = "Lleva el hilo de pelo de fantasma a Abercrombie, a su choza al norte del Cementerio del Cerro del Cuervo.", description = "Aquí tienes tu hilo, <nombre>. Entre muchas otras cosas, sirve para atar espíritus malvados o para coser la vida en objetos inanimados.", progress = "Necesito colocar el hilo de cabello de fantasma en mis puertas y ventanas para mantener alejados a esos indeseables espíritus. ¿Me lo conseguiste?", completion = "¡Delicioso! Esto funcionará de maravilla.\n\nTen, &lt;nombre&gt;, acepta esto como pago por tu honorable acción.", rewards = "Recibirás:\n\n"},
[158] = {title = "Zumo de zombi", objective = "Havla con el tabernero Smitts.", description = "Necesito una botella de licor. De zumo de zombi. El tabernero Smitts, del Mesón del Cuervo Escarlata de Villa Oscura, debería tener alguna botella.\n\nPor favor, date prisa, <nombre>. ¡Las noches empiezan a ser frías y necesito ese fuego en mis venas!", progress = nil, completion = "Necesitas un poco de zumo de zombi, ¿verdad? Mmm... es un líquido muy fuerte... no me lo piden muy a menudo.", rewards = "Recibirás:\n\n"},
[159] = {title = "Entrega de zumo", objective = "Lleva el zumo de zombi a Abercrombie a su choza.", description = "Aquí tienes tu zumo de zombi...\n\nY... ten cuidado con él. ¡Es tan fuerte que resucitaría a un muerto!", progress = "Necesito ese zumo de zombi para mantenerme caliente en estas noches frías y oscuras. ¿Me lo conseguiste?", completion = "Miles de gracias, &lt;nombre&gt;. Has dado una alegría al corazón de este pobre viejo con este gesto estúpid... digo, ¡con este gesto estupendo!\n\nTen, amigo. Acepta esto como muestra de mi agradecimiento.", rewards = "Recibirás:\n\n"},
[160] = {title = "Nota para el alcalde", objective = "Lleva la carta de Abercrombie a Ello Cerranegro, el alcalde de Villa Oscura.", description = "Lleva esta nota a Ello Ebonlocke, el alcalde de Villa Oscura.\n\nÉl y yo somos viejos amigos. Seguro que cuando la lea y sepa lo que has hecho por mí... ¡estará tan contento como yo! ¡Ja, ja, ja, ja!", progress = "¿Abercrombie...? No conozco a nadie con ese nombre. ¿Dices que tienes una carta para mí de ese tal Abercrombie?\n\nBueno, pues dámela...", completion = "&lt;Ello mira la carta...&gt;\n\n¿Qué idioma es este? Parece antiguo... No puedo leerlo.", rewards = "Recibirás:\n\n"},
[161] = {title = "Se aproxima una oscura amenaza", objective = "El ingeniero jefe Hinderweir quiere que lleves los polvos a Ashlan Risapétrea, el experto en explosivos de Dun Modr.", description = "Si mis sospechas son correctas, esto es un explosivo. Debemos hacerlo analizar por un experto. Ashlan Risapétrea fue asignado al regimiento que cubre Dun Modr y el Puente Thandol. Vas a tener que emprender un viaje peligroso, <nombre>.\n\nCruza la Puerta de Algaz, sigue el camino que cruza Los Humedales y busca a Risapétrea en Dun Modr. Un consejo: ¡no te salgas del camino y no te detengas por nada!", progress = "¿Decías algo? No oigo nada, me pitan los oídos. ¿Qué es eso que me traes?", completion = "Sí, Hinderweir tiene motivos para preocuparse.", rewards = "Recibirás:\n\n"},
[162] = {title = "La sublevación de los silítidos", objective = "Dirígete a Darnassus y entrégale el informe del análisis del insecto a Gracina Poderespíritu.", description = "Te ruego que le entregues este informe a alguien que tenga conocimientos de este tema.\n\nConozco a una erudita elfa de la noche llamada Gracina Poderespíritu que ha dedicado mucho tiempo al estudio de los seres vivos que solo se encuentran en Azeroth. ¡Es una historiadora de los pies a la cabeza! Entrégale este informe si quieres que te ayude a entender a qué te enfrentas y, lo más importante, el modo de detener esta amenaza. Necesitaremos ayuda.\n\nLo último que he sabido de ella es que residía en el Templo de la Luna, ubicado en Darnassus.", progress = "Que la paz y la tranquilidad sean contigo, forastero. ¿En qué necesitas hoy mi consejo?", completion = "Este informe... nosotros, los elfos de la noche, conocemos muy bien el terror del que en él se habla. Picoflojo ha hecho muy bien al enviarte aquí con el informe.\n\nEstas criaturas destruirán el Desierto de Tanaris con rapidez. Se trata de una de las mayores amenazas a la que jamás nos hemos enfrentado. Familiarízate con este nombre, &lt;nombre&gt;: silítidos. Los insectos de los que habla este informe son los silítidos y representan uno de los mayores peligros a los que tanto la Horda como la Alianza se enfrentan.\n\nSi hubiéramos sido capaces de descubrirlo antes...", rewards = "Recibirás:\n\n"},
[163] = {title = "Cerro del Cuervo", objective = "Averigua qué está acechando Cerro del Cuervo.", description = "Algo ocurre en Cerro del Cuervo, <nombre>. Calor jura que alguien –o algo– está rondando los edificios de la zona. Ha visto sombras moviéndose dos noches seguidas y dice que hace tres noches vio luz en una de las ventanas.\n\nSi, como dices, te preocupa la seguridad del Bosque del Ocaso, averigua qué monstruo acecha el Cerro del Cuervo.", progress = nil, completion = "¡No! ¡Por favor, no me mates! Soy solo yo... ¡¡Alterio!! Están por todas partes... ¡No puedo escapar! ¿Monstruos en Cerro del Cuervo? No, no, no... no puede haber ninguno. Aquí solo está Alterio, el inocente Alterio.", rewards = "Recibirás:\n\n"},
[164] = {title = "Entrega a Sven", objective = "Entrega los suministros a Sven.", description = "¿Conoces a Sven? ¡Pues él sí que trabaja para la Luz! Él y sus seguidores están acampados en la linde noroccidental del Bosque del Ocaso.\n\nPor desgracia, son muy pocos, y no pueden permitirse enviar a nadie a buscar suministros a la ciudad. ¿No podrías llevarles tú estos suministros?", progress = "Tienes pinta de ser fuerte, <clase>. ¿Has venido a unirte a nuestra lucha?", completion = "Estos suministros serán muy útiles. La ayuda de la familia Carevin es siempre bienvenida. Aquí tienes un algo por tus molestias.", rewards = "Recibirás:\n\n"},
[165] = {title = "El ermitaño", objective = "Ve a ver cómo está el anciano de la choza que hay cerca del Cementerio del Cerro del Cuervo.", description = "Antes venía por aquí un anciano... venía a comprar suministros con bastante frecuencia, pero hace un tiempo que no lo veo. Vive en una choza cerca del Cementerio del Cerro del Cuervo, si no recuerdo mal. ¿Podrías ir a ver si le ha ocurrido algo?", progress = nil, completion = "¿Elaine Carevin? Je, je, sí, claro... los Carevin. \n\nNo, no pasa nada. Es que ya no puedo ir a la villa, el viaje es demasiado para mí. Los caminos son peligrosos y cada día estoy más viejo. ¿Podrías hacer algunos favores a un pobre viejo?", rewards = "Recibirás:\n\n"},
[166] = {title = "La hermandad de los Defias", objective = "Mata a Edwin VanCleef y lleva su cabeza a Gryan Mantorrecio.", description = "Todavía hay una tarea que debes cumplir. Edwin VanCleef debe ser asesinado. Aunque me entristece condenar a alguien a la pena de muerte, el bien común de las gentes de los Páramos de Poniente exige que VanCleef descanse de una vez por todas. Tráeme la cabeza de ese villano cuando hayas terminado tu hazaña.", progress = "¿Qué tal va la caza y captura de Edwin Van Cleef?", completion = "&lt;nombre&gt;, tu valentía es extraordinaria. Las Milicias del Pueblo te agradecen los servicios prestados a las gentes de los Páramos de Poniente. Ahora que VanCleef ha muerto, estamos ante el comienzo del fin de la Hermandad Defias. Esperemos que en un día no muy lejano la paz volverá a bendecir los llanos de estas tierras.\n\n¡Saludos, Señor!", rewards = "Podrás elegir una de estas recompensas:\n\nAlbarca de Páramos de PonienteTúnica de Páramos de PonienteBastón de Páramos de Poniente\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTúnica de Páramos de PonienteBastón de Páramos de PonienteAlbarca de Páramos de Poniente"},
[167] = {title = "Oh hermano. . .", objective = "Lleva la insignia de la Liga de Exploradores del supervisor Cardortiga a Wilder Cardortiga, en Ventormenta.", description = "Estábamos en las profundidades de una gran mina en Páramos de Poniente, oculta tras un granero en el Arroyo de la Luna. No tenemos ni idea de dónde salieron esos ladrones. En fin, el túnel se derrumbó sobre nosotros. Conseguí salir, pero los otros... bueno... de los otros no sé nada.\n\nCreo que tú podrías tener más suerte ahí abajo. Si pudieras buscar a mi hermano, te lo agradecería mucho. Siempre llevaba su insignia de la Liga de Exploradores. Si no puedes encontrarle, al menos la insignia me daría algo de paz mental.", progress = "¿Has encontrado alguna pista de mi hermano? ¿Hay alguna esperanza después de todo este tiempo?", completion = "¡Mis sospechas eran ciertas! Mi pobre hermano... Bueno, gracias, al menos ahora tengo la conclusión del misterio, incluso aunque la conclusión sea trágica.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nRevancha de minero"},
[168] = {title = "La recolección de Tarjetas del Sindicato Minero", objective = "Recupera 4 Tarjetas del Sindicato Minero y llévaselas a Wilder Cardortiga, en Ventormenta.", description = "Muchos de mis amigos murieron ese horrible día cuando se derrumbó el túnel de la mina. Si tus aventuras te llevan de casualidad a esa parte de la mina en el Arroyo de la Luna, por favor, busca alguna señal que pueda identificarles. Si te encuentras con alguna de sus Tarjetas del Sindicato Minero, tráemelas y me aseguraré de que sus familias reciban la resolución de este horrible accidente.", progress = "¿Conseguiste recuperar alguna de las Tarjetas del Sindicato Minero de mis colegas?", completion = "Gracias por esto, &lt;nombre&gt;. Tus esfuerzos contribuirán a traer la paz a los caídos en los comienzos de esta tragedia. Me aseguraré de que se informe a las familias.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de tuneladorGuantes de minería polvorientos\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nGuantes de minería polvorientosBotas de tunelador"},
[169] = {title = "Se busca: Gath'Ilzogg", objective = "Mata a Gath'Ilzogg y lleva su cabeza al magistrado Solomon a Villa del Lago para recibir tu recompensa.", description = "Se busca a Gath'Ilzogg por dirigir los ataques Roca Negra al Fuerte de Petravista y las incursiones en Villa del Lago. \n\nGath'Ilzogg es extremadamente peligroso. Se encuentra en el Fuerte maquinando nuevos crímenes del clan contra la humanidad. Debemos sofocar la infestación de orcos cueste lo que cueste. Se ofrece recompensa a quien presente la cabeza de Gath'Ilzogg al magistrado Solomon.", progress = "¿Qué llevas ahí, <raza>?", completion = "¡Buen trabajo, &lt;nombre&gt;! Gath'Ilzogg es responsable de la muerte de muchos humanos. Nos hemos vengado. ¡Estamos un paso más cerca de reconquistar el castillo!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[170] = {title = "Una nueva amenaza", objective = "Balir Martilloescarcha quiere que mates a 6 troggs Comepiedras y a 6 troggs Comepiedras fornidos.", description = "Espero que estés aquí para echar una mano, <clase>. Después del último ataque de los troggs, nos vendría bien cualquier ayuda.\n\nHe oído que esos malnacidos han estado apareciendo por todas partes y parece que el Valle de Crestanevada no es una excepción. Han sido vistos en los montes del sureste y cerca del lago helado.\n\nY eso no es todo. Hace unas noches atacaron e invadieron nuestro campamento del oeste.\n\nNecesitamos ayuda, <muchacho/muchacha>. Necesitamos brazos fuertes para hacer retroceder a los troggs.", progress = nil, completion = "Si los problemas que hemos tenido aquí son una muestra de lo que está ocurriendo en el resto de nuestra tierra, ¡tendremos muchos problemas! Solo puedo esperar que el Rey y el Senado estén tomando medidas para acabar con la amenaza que los troggs representan.", rewards = "Podrás elegir una de estas recompensas:\n\nChal de osoCinturón rústicoBotas de nieve\n\nTambién recibirás:\n\nCinturón rústicoBotas de nieveChal de oso"},
[171] = {title = "Alcaide de la Alianza", objective = "Lleva a tu pupilo de vuelta al orfanato de Ventormenta entregándole el silbato de huérfano humano a la supervisora de huérfanos Ruiseñor en el Distrito de la Catedral.", description = "Has sido muy <bueno/buena> conmigo, <nombre>. Nunca había soñado con divertirme tanto en la Semana de los Niños. Gracias. No quiero ponerme triste por lo que me pasó a mí y a todos nosotros, mis amigos siempre me dicen que debo sobreponerme a todo lo malo. Pero ya sabes, cuando crezca seré un héroe... como tú.\n\nMe da pena decir esto, pero es hora de que vuelva a casa. ¡Volvamos y le diré a la supervisora que eres una persona maravillosa!Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nCollar de PigletJaula de ratasCarcasa de tortugaPago de Curmudgeon\n\nTambién recibirás:\n\nCarcasa de tortugaCollar de PigletJaula de ratasPago de Curmudgeon"},
[172] = {title = "Semana de los Niños", objective = "Usa el silbato de huérfano para hablar con el niño al que cuides durante la Semana de los Niños.\n\nObjeto provisto: \n", description = "¿Quieres ayudarnos? Muchísimas gracias, necesitamos muchas manos durante la Semana de los Niños.\n\nToma este silbato. Solo podrás usarlo durante esta semana, y cuando lo uses, podrás hablar con el niño al que estés cuidando. Si vuelves a usarlo, el niño se irá.\n\nY ahora, ¡ve a conocer a tu protegido! Seguro que lo pasaréis muy bien juntos.", progress = nil, completion = "¡Hola! Me llamo Grunth, ¿me vas a cuidar tú? Me alegro de conocerte. Mis papás también eran aventureros, me han dicho que murieron honrosamente en combate.\n\nEspero que juguemos mucho; la matrona me ha dicho que esta semana serás como mi hermano mayor, qué bien.", rewards = nil},
[173] = {title = "Ferocanis en el bosque", objective = "Mata a 6 ferocanis Tejesombras nocturno para Calor de Villa Oscura.", description = "La oscuridad se siente inexorablemente atraída hacia el Bosque del Ocaso. El maestro Carevin lucha por erradicar el mal y la herejía. Y con nuestros esfuerzos mantenemos a salvo a las gentes de Villa Oscura.\n\n¿Te crees <merecedor/merecedora> de unirte a nosotros?\n\nHace tiempo pensaba como tú. La complacencia de la Guardia me decepcionó y acabé uniéndome al maestro Carevin. Si quieres probar tu valía, no utilices las palabras.\n\nMide tus habilidades con un ferocanis delirante tejesombras en la Arboleda del Destello y en El Vergel Pútrido.", progress = nil, completion = "Impresionante, &lt;nombre&gt;. Parece que sabes desenvolverte solo. Quizás podamos encontrar un desafío más adecuado para una de tus habilidades.\n\nPero es curioso... los ferocanis aparecieron como de la nada. En fin, con todas las extrañas criaturas que han cruzado nuestras fronteras, supongo que esa es solo una más de mis preocupaciones.", rewards = "Recibirás:\n\n"},
[174] = {title = "Mira las estrellas", objective = "Viktori Prism'Antras de Villa Oscura quiere que le lleves un tubo de bronce.", description = "Cog, mi amigo gnomo, me ha prometido ayuda para crear un instrumento que me servirá para ver las estrellas. Más allá de la magia hay una verdad que puede explicar las extrañas transformaciones del bosque.\n\nTráeme un tubo de bronce creado por un ingeniero cualificado. Ese es el primer paso según el manual de Cog.", progress = "¿Has conseguido encontrar a un ingeniero que te haga un tubo de bronce?", completion = "Es perfecto, &lt;nombre&gt;. ¡Ya hemos completado el primer paso!", rewards = "Recibirás:\n\n"},
[175] = {title = "Mira las estrellas", objective = "Localiza a Mary y convéncela de que te dé una superficie reflectora para el proyecto de Viktori.", description = "Según el manual de Cog, ahora necesito encontrar algo para reflejar la luz. Me acuerdo de una mujer llamada Mary que se hizo muy famosa por aquí porque siempre llevaba un espejo consigo. Quizás puedas encontrar a Mary y pedirle que nos preste una superficie reflectante para este proyecto... Vive al sur del Bosque del Ocaso. Al menos, es donde vivía antes...", progress = nil, completion = "Pues sí, sí que tenía un espejo.", rewards = "Recibirás:\n\n"},
[176] = {title = "Se busca: Hogger", objective = "Mata al gnoll Hogger y trae su enorme garra gnoll al alguacil Dughan.", description = "Se busca: Hogger.\n\nUn gnoll enorme, Hogger, está al acecho en el bosque al suroeste de Elwynn. Ha sofocado todos los intentos de capturarlo.\n\nEl ejército de Ventormenta ofrece una generosa recompensa por atrapar al gnoll. Para ganarla, los caza recompensas tendrán que traer una prueba de la derrota de Hogger al alguacil Dughan de Villadorada.", progress = "Sí, Hogger ha sido un auténtico estorbo para mí y para mis hombres. ¿Tienes algo de que informar sobre la bestia?", completion = "¡Ja! ¡Muy bien! ¡Empezaba a creer que nadie conseguiría abatir a ese monstruo!\n\nAquí tienes, &lt;nombre&gt;. Y gracias, ese gnoll era un dolor de cabeza tan monumental como la Cumbre de Roca Negra!", rewards = "Podrás elegir una de estas recompensas:\n\nTúnica de lacayoLeotardos de guardia de VentormentaPalo de lucha equilibrado\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nLeotardos de guardia de VentormentaTúnica de lacayoPalo de lucha equilibrado"},
[177] = {title = "Mira las estrellas", objective = "Quita el espejo de Mary la Ciega al necrófago demente y llévaselo a Viktori a Villa Oscura.", description = "El espejo era uno de mis tesoros más preciados hasta... el incidente. Después de lo que ocurrió, lo escondí detrás del granero. Antes venía por aquí un necrófago... Quizás lo cogió él. Se dice que esa bestia inmunda vive en el Cementerio del Jardín Sereno.", progress = "Le he enseñado a Cog nuestra máquina de las estrellas. Bueno, lo que hemos hecho hasta ahora. Se quedó muy impresionado. ¿Encontraste el instrumento reflectante?", completion = "¡Excelente, excelente! ¡Es perfecto! ¡Muchas gracias!", rewards = "Recibirás:\n\n"},
[178] = {title = "Theocritus recupera el colgante", objective = "Lleva el colgante Pellejo Negro a Theocritus el Mago.", description = "<Oyes una voz...> \n\nNo te asustes; soy Theocritus, Mago Supremo de la Torre de Azora, en el Bosque de Elwynn. El colgante es un medio de comunicación entre los gnolls Pellejo Negro y su señor, Morganth. \n\nHe investigado durante meses y creo que yo también puedo usar los colgantes. Si oyes este mensaje, mi hechizo ha funcionado. \n\nTráeme el colgante y te recompensaré.", progress = "¿Que te hablé mediante un colgante? Mmm, dámelo, quiero estudiarlo.", completion = "Gracias, &lt;nombre&gt;. Hace semanas que envié el mensaje, creí que no se había transmitido. Pero parece que el hechizo llegó a algunos colgantes, aunque tarde.\n\nEspero aprender a enviar ataques para inmovilizar a los gnolls que los lleven puestos.\n\nPero me queda mucha investigación por delante para llegar a eso.", rewards = "Recibirás:\n\nBálsamo restablecedor"},
[179] = {title = "Los enanos modistos", objective = "Sten Brazorrecio quiere 8 piezas de carne dura de lobo.", description = "¿Qué tenemos aquí? Tienes pinta de necesitar algo para mantener las manos calientes, ¿mm?\n\nYo te diré lo que necesitas: un par de estupendos guantes bien calentitos. Y, como soy un alma generosa, me encantaría proporcionarte ese par de guantes. Pero con una condición.\n\nNecesito que me consigas carne de lobo. Es un buen trato, ¿mm? Tú me traes un poco de carne de lobo y yo evito que pierdas los dedos por congelación. ¿Y bien? ¿Qué dices?", progress = "¿Qué? ¿Los lobos te están dando problemillas? Ya sabes que tienes que evitar colmillos y patas y otras cosas afiladas, ¿verdad?", completion = "¡Ah! Estupendo. Esta carne de lobo me vendrá muy bien. Oh, no te preocupes, &lt;nombre&gt;, no he olvidado mi parte del trato. Ten, alguno de estos debería valerte.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de cuidador de conejosGuantes de cuidador de lobosGuantes de cuidador de jabalíes\n\nTambién recibirás:\n\nGuantes de cuidador de conejosGuantes de cuidador de jabalíesGuantes de cuidador de lobos"},
[180] = {title = "Se busca: teniente Fangore", objective = "Mata al teniente Fangore y lleva su zarpa al magistrado Solomon a Villa del Lago.", description = "Se busca al teniente Fangore \n\nSe acusa al jefe del clan gnoll Pellejo Negro de colaborar con el enemigo del Reino Morganth. El magistrado Solomon ha decretado la pena de muerte para este traidor a Ventormenta. Su zarpa se considerará prueba de su muerte.", progress = "¿Qué nuevas nos traes?", completion = "¿Así que el malvado Fangore ha muerto? Pues buen viaje. Admiro tu valor, &lt;clase&gt;. Tienes la gratitud de Villa del Lago.", rewards = "Recibirás:\n\n"},
[181] = {title = "Mira las estrellas", objective = "Localiza a Zzarc'Vul en el montículo meridional del Bosque del Ocaso y llévale su monóculo a Viktori a Villa Oscura.", description = "Necesito un objeto más para completar el instrumento. Según el manual de Cog necesito una lente de algún tipo. La única lente que conozco, aunque sería lo suficientemente grande, es difícil de conseguir.\n\nSé de un ogro que tiene un monóculo... se llama Zzarc'Vul y vive en el montículo que hay al sur del Bosque del Ocaso. Si puedes, tráeme su monóculo y lo usaremos como lente.", progress = "¿Conseguiste convencer a Zzarc'Vul de que nos preste su monóculo para nuestro experimento?", completion = "¡Al fin! ¡Ya hemos completado el instrumento de las estrellas! Gracias, &lt;nombre&gt;. Ya puedo seguir con mis estudios...", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes zodíacoCinturón de las Estrellas\n\nTambién recibirás:\n\nGuantes zodíacoCinturón de las Estrellas"},
[182] = {title = "La cueva de los trols", objective = "Grelin Barbablanca quiere que mates a 14 crías de trol Peloescarcha.", description = "Mi hermano Senir y yo fuimos enviados a diferentes zonas de Dun Morogh para investigar la amenaza de los trols. El Senado ya está muy ocupado con los troggs, no necesita más preocupaciones.\n\nPor lo que he visto, no hay muchos trols en el Valle de Crestanevada y la mayoría se concentran en la cueva del sur. No creo que vayamos a necesitar al ejército. Unos cuantos brazos fuertes serán más que suficiente.\n\nQuizás quieras ayudar en esta empresa... Tengo autoridad para ofrecerte una compensación por tu ayuda.", progress = nil, completion = "¡Aaah! ¡Esos malditos trols!\n\n&lt;Respira hondo unas pocas veces y parece calmarse... un poco.&gt;\n\n¡Vino un grupo! ¡Por la noche! ¡Vinieron y me robaron mi diario! No tenía que haberme fiado de esa inutilidad de apren...", rewards = "Podrás elegir una de estas recompensas:\n\nHacha de mano YunquemarMartillo YunquemarCuchillo YunquemarTrineo Yunquemar\n\nTambién recibirás:\n\nHierba curativaHacha de mano YunquemarMartillo YunquemarCuchillo YunquemarTrineo Yunquemar"},
[183] = {title = "Cazador de jabalíes", objective = "Talin Ojoagudo quiere que mates a 12 jabalíes del risco pequeños.", description = "No hay nada como pasar un día cazando jabalíes, ¿eh?\n\nAunque con la de jabalíes que tenemos en el Valle de Crestanevada, la cosa pierde un poco de gracia. Ni siquiera hay que provocarlos. Ya están todos enfadados e inquietos, no necesitan ayuda para eso. De hecho, últimamente hay tantos jabalíes en la zona que mi caza diaria se está convirtiendo en una actividad peligrosa.\n\nPara abreviar, si pudieras ayudarme a matar algunos jabalíes, te lo agradecería mucho.", progress = nil, completion = "¡Fantástico! Ya puedo volver a mi caza diaria tranquilamente. Gracias, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCalzones de paño enanoPantalones de cuero enano\n\nTambién recibirás:\n\nPantalones de cuero enanoCalzones de paño enano"},
[184] = {title = "La escritura de Furlbrow", objective = "Lleva al granjero Furlbrow su escritura.", description = "Esta es la escritura de una extensión de tierras de labranza de los Páramos de Poniente. Está firmada por un tal Theodore Furlbrow y por su mujer, Verna. En el reverso de la escritura hay unas palabras garabateadas con gran prisa:\n\n\"Presionamos a Furlbrow y conseguimos su escritura. Pensé que te vendría bien si querías falsificar una para ti.\n\nLos Furlbrow no nos darán problemas. La última vez que los vi estaban saliendo de los Páramos de Poniente con un carro roto.\"\n\nCrees que probablemente los Furlbrow querrán recuperar su escritura...", progress = "¿¿Tienes la escritura de mi granja?? ¡Qué buena noticia! Me la robaron unos rufianes hace unos días... ¡La daba por perdida!\n\nPor favor, deja que la coja. Estamos saliendo de los Páramos de Poniente y no vamos a volver pronto, pero si lo hacemos, necesitaré esos papeles...", completion = "¡Muchas gracias, &lt;nombre&gt;! Como te decía, este ya no es lugar para gente honrada como nosotros. Pero si en un futuro las cosas mejoran, esta escritura nos permitirá reclamar nuestras tierras.\n\nNo tengo mucho que ofrecer, tan solo esto.", rewards = "Recibirás:\n\nSandía maduraCalabaza pequeña"},
[185] = {title = "Domina el tigre", objective = "Ajeck Rouack de la Expedición de Nesingwary, quiere que mates 10 tigres jóvenes de Tuercespina.", description = "Cuando yo era una niña, mi padre me enseñó a cazar tigres.\n\nVerás que los felinos jóvenes son mucho más fáciles de seguir y dar caza. Por esa razón, comenzaremos con lo fácil. Es fácil encontrar tigres jóvenes cerca del campamento de expedición.\n\nPon a prueba tus habilidades de rastreo y veamos si puedes dar caza a alguna de las bestias.", progress = nil, completion = "Muy bien, &lt;nombre&gt;. Puedes seguir adelante.", rewards = "Recibirás:\n\n"},
[186] = {title = "Domina el tigre", objective = "Ajeck Rouack de la Expedición de Nesingwary, quiere que mates 10 tigres de Tuercespina.", description = "Estás aprendiendo, <clase>. Probemos ahora tus habilidades con los felinos maduros. Mata a 10 tigres de Tuercespina.", progress = nil, completion = "Me impresionas, &lt;clase&gt;. Estás progresando mucho.", rewards = "Recibirás:\n\n"},
[187] = {title = "Domina el tigre", objective = "Ajeck Rouack de la Expedición de Nesingwary, quiere que mates 10 tigres de Tuercespina mayores.", description = "Ahora voy a subir las apuestas y a poner a prueba realmente tu habilidad. Demuéstrame que puedes matar 10 tigres de Tuercespina mayores. Si puedes hacerlo, estarás <listo/lista> para el desafío final antes de poder declararte Maestro Cazatigres en presencia del maestro Decautela.\n\nLos tigres de Tuercespina mayores no solo son los más difíciles de encontrar, sino también los más difíciles de vencer.", progress = nil, completion = "Impresionante. Creo que casi estás preparado.", rewards = "Recibirás:\n\n"},
[188] = {title = "Domina el tigre", objective = "Ajeck Rouack de la Expedición de Nesingwary quiere que mates a Sin'Dall y vuelvas con su zarpa.", description = "Este es el desafío final al que tienes que enfrentarte. Llevamos semanas rastreando a una tigresa muy evasiva. Llamamos a la bestia, Sin'Dall. Veamos si puedes hacer lo que ningún <raza> ha logrado: dar caza y matar a Sin'Dall. Tráeme su zarpa como prueba.\n\nRastrearla no será una tarea fácil.", progress = "¿Qué tal va la caza de Sin'Dall?", completion = "¡Absolutamente impresionante! ¡Eres un auténtico maestro cazador de tigres, &lt;nombre&gt;!", rewards = "Recibirás:\n\nGuantes de cazador de tigre"},
[189] = {title = "Orejas de Sangrapellejo", objective = "Consigue 15 orejas Sangrapellejo y llévaselas a Kebok en la Bahía del Botín.", description = "¡Malditos trols! ¡Han estado matando a nuestros agentes en la jungla! Ya he perdido a un buen número de mis mejores guardias en esas escaramuzas. ¡Los costes se multiplican! ¿Cómo se lo voy a explicar al Barón Revilgaz?\n\nAcción decisiva... Piensa, Kebok... no te asignaron al puesto que tienes hoy por estar de brazos cruzados... ¡Lo tengo! Tú... <nombre>, ¿verdad? Me ayudarás, ¿no? ¡Hay dinero en juego!\n\nEste es el trato: tráeme las orejas de quince trols Sangrapellejo y te compensaré generosamente.", progress = "Espero que hayas tenido éxito... Hay trols por todas partes. Asquerosos demonios.\n\n¡Por cierto, eso me recuerda algo! Asegúrate de decírselo a tus amigos, ¿vale? ¡Necesitamos mucha ayuda!", completion = "Oh excelente, excelente. Esto ayudará un poco, por ahora...\n\n&lt;Su voz se hace casi imperceptible, masculla algo sobre margen de beneficio y costes...&gt;\n\n¿Qué? ¿Todavía estáis aquí? ¿Recompensa? Oh, vale... claro, aquí tienes... debería ser más que suficiente.", rewards = "Recibirás:\n\nCaña de pescar goblin"},
[190] = {title = "Domina la pantera", objective = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que mates 10 panteras jóvenes.", description = "Si quieres ser parte de la partida de caza tan estupenda que Hemet ha reunido, vas a tener que demostrar que se te da bien cazar panteras. Empezaremos por lo fácil, no te preocupes. Veamos, para empezar, mata 10 panteras jóvenes.\n\nRastrearlas es solo la mitad del desafío...", progress = "Un verdadero cazador de panteras estaría ahí fuera, acechando a su presa. Muestra algo de compromiso, <clase>, vuelve ahí fuera.", completion = "¡Que tengas buena suerte, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[191] = {title = "Domina la pantera", objective = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que mates 10 panteras.", description = "Creo que podemos subir un poco el nivel. Si quieres demostrar que estás <preparado/preparada> para socializar con esos grandes cazadores, vas a tener que demostrar que puedes matar 10 panteras.\n\nSon bastante duras. No resulta tan fácil matar a esas panteras como a las más jóvenes que mataste antes.", progress = "¡Vete a matar 10 panteras! ¡Enséñanos lo que tienes!", completion = "¡Una ejecución perfecta, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[192] = {title = "Domina la pantera", objective = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que mates 10 panteras Faucesombrías.", description = "Y ahora vamos con lo difícil. Un verdadero cazador de panteras puede demostrar su habilidad matando panteras Faucesombrías. Demuéstranos que puedes matar a 10 de estas bestias.\n\nSi te parece difícil rastrearlas, espera a intentar matar a una...", progress = nil, completion = "¡Lo has logrado, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[193] = {title = "Domina la pantera", objective = "Sir S. J. Erlgadin de la Expedición de Nesingwary, quiere que le lleves el colmillo de Bhag'thera.", description = "Casi has demostrado ser un maestro cazador de panteras. Hay una pantera llamada Bhag'thera merodeando por la jungla. Hasta ahora, ha conseguido esquivar a nuestro grupo. Veamos si puedes usar tus habilidades para matar a la gran Bhag'thera.\n\n¡Tráeme el colmillo de Bhag'thera y conseguirás mi respeto!", progress = "Bhag'thera puede ser una bestia muy esquiva. ¿Cómo va la caza?", completion = "¡Así que la gran Bhag'thera está muerta! Enhorabuena, &lt;nombre&gt;. ¡Eres un poderoso &lt;clase&gt;!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nLeotardos de cazador de panteras"},
[194] = {title = "Domina el raptor", objective = "Hemet Decautela quiere que mates 10 raptores de Tuercespina.", description = "¿Así que crees que tus habilidades de caza están en buena forma? Veamos si es cierto. Vete a la jungla y mata 10 raptores de Tuercespina. Veremos lo <buen cazador/buena cazadora> que eres.\n\n¡Y no, no te diré dónde encontrarlos! Localizar a las bestias es la mitad del desafío.", progress = nil, completion = "Un trabajo magistral, amigo.", rewards = "Recibirás:\n\n"},
[195] = {title = "Domina el raptor", objective = "Hemet Decautela quiere que mates 10 raptores colazote.", description = "Ahora aumentemos la dificultad del desafío y veamos si estás a la altura. Entra en la jungla y mata 10 de esos asquerosos raptores colazote. ¡Veamos si puedes!", progress = nil, completion = "¡Así es como se consiguen un par de trofeos para poner encima de la chimenea! ¡Bien hecho, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[196] = {title = "Domina el raptor", objective = "Hemet Decautela quiere que mates 10 acechadores de la selva.", description = "Veamos qué tal lo haces en este desafío. Ahí fuera hay una raza muy reservada de raptores. Los llamamos acechadores de la selva. Son mucho más difíciles de rastrear y matar que las otras razas que has probado hasta ahora. Mata 10 acechadores de la selva y te enseñaré la mejor parte de la caza.", progress = nil, completion = "¡¡Jo, jo!! ¡Tenemos un auténtico cazaraptores! Un trabajo magistral, amigo.", rewards = "Recibirás:\n\n"},
[197] = {title = "Domina el raptor", objective = "Hemet Decautela quiere que mates a Tethis, un esquivo y peligroso raptor de Tuercespina.", description = "Como has demostrado ser invencible en la selva, te hablaré de un feroz raptor que ni siquiera yo he conseguido matar. Ajeck llama a ese astuto raptor, Tethis.\n\nSi quieres demostrar que eres un verdadero maestro, mata a Tethis y tráeme su garra. Sería un logro que ningún otro cazador ha conseguido.", progress = "¿Cómo va la caza de Tethis? ¿Has conseguido seguirle la pista de vuelta al campamento, no?", completion = "Maestro &lt;nombre&gt;, ¡el Cazaraptores!\n\n¿A que suena bien?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nTúnica de cazador de raptor"},
[198] = {title = "Suministros para el soldado Thorsen", objective = "Entrega los suministros goblin variados al soldado Thorsen.", description = "Mmm. Sí, de hecho, tengo algo que podrías hacer. El teniente Doren y sus seguidores que están al norte, necesitan que les enviemos sus suministros.\n\nTodavía no lo hemos podido hacer y, con el ataque de los trols, necesito que alguien lo haga. El campamento de Doren está en la parte noroeste del camino, en la entrada de Tuercespina desde el Bosque del Ocaso. Lleva los suministros al soldado Thorsen, si no está en el campamento, estará patrullando la zona sur. Él se encargará de todo.\n\nPor cierto, si fallas, me deberás una indemnización.", progress = "¿Sí? ¿Qué quieres? Oh, suministros de los goblins. Es extraño ver a un <raza> entregándolos, generalmente mandan a alguien de los suyos. No pasa nada... ¿no los habrás tocado, verdad? Bien, bien... No querríamos que nada pasase en nuestras minas. No, no, no.", completion = "Estas minas terrestres nos ayudan a defendernos de ese animal Kurzen y de sus hombres. Gracias por traerlas. Si buscas más trabajo, probablemente pueda encontrar algo para ti. Y no tendrás que tratar con esos goblins, ¿mm?", rewards = "Recibirás:\n\n"},
[199] = {title = "Se aproxima una oscura amenaza", objective = "Enséñale la pista al ingeniero jefe Hinderweir.", description = "Coges una muestra de esos polvos de olor extraño.", progress = "¿Encontraste alguna pista, <nombre>?", completion = "¡Esto tiene muy mala pinta!", rewards = "Recibirás:\n\n"},
[200] = {title = "Bookie Herod", objective = "Encuentra los registros de Bookie Herod.", description = "Si Kurzen sabe lo del secreto de Thorsen, significa que tenemos un espía. ¡Debemos determinar cómo se filtró el secreto!\n\nEl contacto de Thorsen en la Base de Kurzen era Bookie Herod. Herod es el oficial de logística de Kurzen y lo sabe todo sobre la Base de Kurzen. Si consigues hacerte con las notas o los registros de Herod, quizás podamos saber cómo descubrieron a Thorsen.\n\nCuando todavía estábamos con Kurzen, la oficina de Bookie estaba la parte de arriba de la casa de dos pisos de la Base. Probablemente, sus registros sigan allí.", progress = nil, completion = "Estas son las notas y los registros de Bookie Herod.\n\nEntre los papeles hay: una lista de suministros del campamento, pedidos de material del coronel Kurzen, y varias cartas.", rewards = "Recibirás:\n\n"},
[201] = {title = "Investiga el campamento", objective = "Encuentra el campamento de los cazadores e informa sobre su ubicación a Krazek en la Bahía del Botín.", description = "He recibido informes sobre un grupo de cazadores que ha establecido un campamento río arriba del campamento minero de Ventura y Cía., en el Lago Nazferiti. En circunstancias normales, no me importaría, pero he oído que Hemet Decautela forma parte de ese grupo. Está muy bien conectado, ya me entiendes, y nunca se puede estar demasiado seguro.\n\n¿Por qué no vas a echar un vistazo al campamento, <nombre>, a ver qué están tramando?", progress = nil, completion = "Mmm, así que mi información era correcta, como siempre. Bueno, no creo que representen amenaza alguna de interferencia con nuestra operación, pero quizás necesiten alguno de los extraordinarios productos que hemos producido. Mmm...", rewards = "Recibirás:\n\n"},
[202] = {title = "Coronel Kurzen", objective = "Mata 6 unidades de élite Kurzen y 4 subjefes Kurzen. Lleva la cabeza de Kurzen al teniente Doren en el Asentamiento Rebelde.", description = "Has demostrado tu valía otra vez y tus recursos parecen rivalizar con los del mismísimo Kurzen. Quizás seas la salvación de nuestro grupo de rebeldes... si consigues enfrentarte a Kurzen y vencer.\n\nLidera a sus hombres desde las profundidades de Reservas. Debes entrar en la cueva, derrotar a sus defensores y enfrentarte a Kurzen y a sus líderes.\n\nAdiós, <nombre>. Mi esperanza, y el destino de mis hombres, van contigo.", progress = "¿Me atrevo a creer que Kurzen y sus defensores están muertos, <nombre>?", completion = "No me atrevía a creer los informes pero nos traes la prueba… Han derrotado a Kurzen y a sus líderes. ¡Nos has salvado!\n\nEstos bosques están llenos de amenazas de todo tipo, pero ninguna tenía la perversa inteligencia de Kurzen. Todos estaremos más tranquilos ahora que ya no está.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nLanzatralla"},
[203] = {title = "La segunda rebelión", objective = "Mata 15 combatientes de la jungla Kurzen.\n\nVuelve a ver al sargento Yohwa en al Asentamiento Rebelde.", description = "Estamos luchando una batalla perdida con el coronel Kurzen. No solo es un estratega brillante, sino que nos supera en número, ¡al menos 10 a 1! Para sobrevivir, tenemos que usar sus propias tácticas de cobertura contra él.\n\nVete a su campamento del este y mata a sus hombres. A muchos de ellos. Y sal de ahí antes de que los refuerzos te alcancen.", progress = nil, completion = "Bien hecho. La derrota de esos combatientes de la jungla aliviará algo la presión sobre nuestro campamento.\n\nPero ahora que has catado el tipo de fuerza al que nos enfrentamos, ¿aún deseas echar el resto con nosotros?", rewards = "Recibirás:\n\n"},
[204] = {title = "Mala medicina", objective = "Lleva 7 remedios de la jungla y 1 extracto de veneno de helecho al sargento Yohwa en el Asentamiento Rebelde.", description = "Para tener una oportunidad contra Kurzen, necesitamos neutralizar su fuerza de curación. Ha reunido a un gran número de auxiliadores que curarán a sus tropas con remedios de la jungla y envenenarán a las nuestras con extractos de veneno de helecho.\n\nNecesitamos esos remedios para curar a los nuestros, y una muestra de esos extractos para poder estudiarla y crear nuestros propios remedios. Busca ambas cosas en la Base de Kurzen. Sus auxiliadores tendrán los remedios y, probablemente, los extractos están en algunas de las cajas de suministros del campamento.", progress = "Estoy esperando los remedios y el extracto, <nombre>.", completion = "Bien hecho, &lt;nombre&gt;. Estos remedios conseguirán mantener a más de nosotros con vida y en buena forma. Y los extractos nos ayudarán a crear nuestros propios remedios.", rewards = "Podrás elegir una de estas recompensas:\n\nManto de fronda de palmeraCuchilla de guerrilla\n\nTambién recibirás:\n\nCuchilla de guerrillaManto de fronda de palmera"},
[205] = {title = "Brujería trol", objective = "Lleva 4 fetiches Machacacráneos al hermano Nimetz en el Asentamiento Rebelde.", description = "Mientras estudio esas leyendas, necesito que consigas instrumentos de magia trol maligna. Entra en el territorio de los trols Machacacráneos, la segunda tribu del sur, y consigue fetiches Machacacráneos de sus médicos brujos y místicos.", progress = "¿Tienes esos fetiches, <nombre>? Quizás guarden el secreto de la degradación de Kurzen... ¡Debo saberlo!", completion = "Gracias. Estudiaré estos fetiches y veré si puedo descubrir el secreto de la corrupción de Kurzen.", rewards = "Recibirás:\n\n"},
[206] = {title = "Mai'Zoth", objective = "Lleva el Ojo de la Mente al hermano Nimetz en el Asentamiento Rebelde.", description = "Quizás Kurzen esté muerto, pero... eso que le volvió maligno sigue vivo. Gracias a mis estudios he descubierto esto: hay un mago ogro, Mai'Zoth, que vive en un Túmulo Ogro en las montañas del este de Tuercespina. Se dice que utiliza magia de control mental y creo que fue él quien corrompió a Kurzen.\n\nSi permitimos que Mai'Zoth siga con vida, su magia podría influenciar a otros y quizás incluso provocar que apareciese otro Kurzen.\n\nMátale y tráeme su Ojo de la Mente, el artefacto que utiliza para corromper.", progress = "Te doy mi bendición. El mal aquí contenido es el único rival posible para la fuerza de Mai'Zoth.", completion = "¡Ajá! ¡Lo has conseguido! Eres una gran fuerza para el bien, &lt;nombre&gt;, ¡que las fuerzas nunca te fallen!\n\nEstudiaré este Ojo de la Mente y veré si puede usarse para buenos fines. Si no… habrá que destruirlo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nOrbe tranquilo"},
[207] = {title = "El misterio de Kurzen", objective = "Encuentra las 4 tablillas y lleva las leyendas al hermano Nimetz.", description = "El coronel Kurzen fue un gran hombre de gran nobleza, ¡debo descubrir qué hizo que cayese en la depravación! Quizás está bajo el influjo de alguna magia trol. Para confirmarlo, debo estudiar algunas de sus leyendas.\n\nHay 4 tablillas escondidas en las ruinas trol de Tuercespina. ¡Necesito leer esas tablillas! Transcribe las tablillas y tráeme las transcripciones.\n\nLa primera tablilla está en las Ruinas de Bal'lal, la segunda en El Arrecife Mortal, y la tercera y cuarta en las Ruinas de Zul'Kunda.", progress = "¡<nombre>! ¿Tienes las leyendas trols? ¡Tengo que saber sus secretos!", completion = "Gracias, &lt;nombre&gt;. Estudiaré estas leyendas para ver si hay algún tipo de unión entre los trols y el mal que ha poseído al coronel Kurzen.", rewards = "Recibirás:\n\n"},
[208] = {title = "Caza mayor", objective = "Hemet Decautela quiere que le lleves la cabeza del rey Bangalash, el gran tigre blanco.", description = "<nombre>, eres <un buen cazador/una buena cazadora>, mereces estar en nuestro grupo. Desde los campos de batalla de Lordaeron hasta las profundidades de la jungla, muy pocas veces he visto tal destreza con las armas como de la que has hecho gala.\n\nHas matado al feroz Sin'Dall, a la esquiva Bag'thera y al traicionero Tethis. Pero el verdadero premio es la cabeza del rey Bangalash. Ese tigre blanco es la razón por la que estoy aquí. Llevo bastante tiempo detrás de él. Mata al rey Bangalash y tu dominio de la cacería será insuperable.", progress = "Veo que has vuelto, viejo amigo. El rey Bangalash me ha obligado a volver arrastrándome al campamento demasiadas veces. Espera un momento.", completion = "¡Pero qué maravilla! ¡Eso es! Enhorabuena, &lt;nombre&gt;, por salir victorioso ahí donde otros fracasaron.\n\nEs un honor para mí contarte entre mis camaradas de caza mayor. ¡Nadie podrá poner en duda tu pericia en la caza y el rastreo!\n\n¡Levanto mi copa por ti, salud! ¡Salud!", rewards = "Podrás elegir una de estas recompensas:\n\nArco de maestro cazadorRifle de maestro cazador\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nArco de maestro cazadorRifle de maestro cazador"},
[209] = {title = "Colmillos de Machacacráneos", objective = "Consigue 18 colmillos de Machacacráneos y llévaselos a Kebok.", description = "Matar los suficientes tigres para mantener la exportación de colmillos de tigre a las islas de los Mares del Sur, es caro y complicado. Por suerte, hemos desarrollado una técnica que nos permite coger objetos normales en forma de cuerno y convertirlos en falsificaciones perfectas.\n\nLo más parecido que hemos encontrado, sorprendentemente, son los colmillos de trols Machacacráneos.\n\nEh, antes de que digas nada, ojo de comprador que no ve, corazón de tonto que no siente, ¿tengo razón? Tráeme muchos colmillos para que podamos ponernos a trabajar en el pedido del mes.", progress = "Unos bichos duros, ¿verdad? ¿Y bien? ¿Has tenido suerte? Perdona mi impaciencia, ¡pero esto ha sido una enorme espina clavada...! ¿¿Y bien??", completion = "Ah, ¡buen trabajo, buen trabajo! Ejem... aunque podrías haber hecho una labor de limpieza más minuciosa... en fin, no importa. Te lo agradezco, &lt;nombre&gt;, gracias a ti, nuestra operación tendrá un resultado mejor y un aumento de productividad. Bien, bien, pasemos a otros asuntos...", rewards = "Recibirás:\n\n"},
[210] = {title = "La cocina de Krazek", objective = "Lleva la olla mecánica de Krazek al goblin Krazek.", description = "Yo me encargo de la mayor parte de la cocina del campamento. Y solía ser un montón de trabajo, hasta que le compré una olla mecánica a un mercader goblin en la Bahía del Botín. ¡Esa olla es una maravilla! Me ha ahorrado un montón de horas... ¡Pero se ha roto!\n\n¿Podrías llevar la olla al goblin para que me la pueda arreglar? Se llama Krazek.", progress = "¡Hola! ¿Me has traído algo?", completion = "Oh, ya veo, es la olla de barro de Krazek. Un objeto muy ingenioso, pero, de vez en cuando, se le salta algún muelle que otro. Veamos qué le pasa...\n\n¡Ajá! El cargador de vapor está oxidado, tiene una junta rota y le faltan dientes al fobulador. ¡Me sorprende que la olla haya sido capaz incluso de cocer un nabo en este estado!\n\n¿Puedo arreglarla? Pues claro que sí.\n\nPero todo en esta vida tiene un precio. Oh, sí, siempre hay que pagar un precio...", rewards = "Recibirás:\n\n"},
[211] = {title = "Ah, Andorhal", objective = "Lleva el Fragmento de Filacteria de Araj al comandante Ashlam Puñovalor del Campamento del Orvallo, en las Tierras de la Peste del Oeste.", description = "¡Ay, se acerca el tiempo de atacar Andorhal y de hacer salir al exánime que lo controla!\n\nEs en el interior de las ruinas de la ciudad donde el exánime, Araj el Invocador, tiene sus dominios. Está custodiado por numerosos miembros de la Plaga que rodean los restos del centro de la ciudad, y su séquito personal de guardianes es realmente formidable. Necesitarás muchos aliados para tener alguna oportunidad de enfrentarte a él con éxito.\n\n¡Destruye a Araj, <nombre>, y tráeme un Fragmento de su Filacteria como prueba!", progress = "<nombre>, ya te ha sido asignada tu tarea. ¿Tienes un Fragmento de la Filacteria del exánime como prueba de que has terminado tu tarea?", completion = "¡Lo has conseguido! ¡Araj... el exánime ha sido derrotado!\n\nEn el día de hoy has prestado un enorme favor a la Alianza, &lt;nombre&gt;. Tu valentía y perseverancia son testimonio de lo que significa ser un auténtico héroe. Por favor, acepta esto como muestra para que todos vean y sepan que has dado una victoria a la Alianza que nunca podrá ser olvidada.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nMarca de Resolución"},
[212] = {title = "Un plato bien frío", objective = "Mata un Basilisco Ojogélido, consigue un anca de basilisco helado y llévasela a Angus Stern en El Ermitaño Azul.\n\n<Para tener éxito en esta misión, no puedes soltar tu espíritu.>", description = "Uno de mis platos más famosos es basilisco helado en vinagreta de lima. Pero el problema es que es imposible mantener fresca la carne y tengo que servir el plato esta noche en el cumpleaños de un noble.\n\nSi pudieras traerme un anca de basilisco helado de un Basilisco Ojogélido recién muerto y rápidamente... ¡estaré en deuda contigo!\n\nLos Basiliscos Ojogélido suelen vagar por las dunas de la playa al noroeste de la Vega de Tuercespina, así que te espera un largo viaje. ¡Y rápido!", progress = "¡<nombre>! ¿Has conseguido el anca ya? Debo cocinar pronto, ¡o el banquete será un desastre total!", completion = "Ah, ¡esto vendrá de perlas! ¡Muchas, pero que muchísimas gracias!\n\nAhora, si me disculpas, tengo que picar esta pierna, marinarla y preparar la guarnición para el banquete.", rewards = "Recibirás:\n\n"},
[213] = {title = "OPA hostil", objective = "Consigue 8 cristales girados y llévaselos a Kebok en la Bahía del Botín.", description = "¡Lo ha hecho! Ya es bastante malo que Gelriz se meta por el medio de los magnates que nombraron los príncipes mercantes como para que ahora intente meterse con el pirata más famoso.\n\nRevilgaz no lo tolerará y me ha dicho que me encargue del problema a mi manera.\n\n¿Mi manera? El robo. Los geólogos de Ventura y Cía. que están cerca del Lago Nazferiti están muy interesados en esos extraños cristales azules que han encontrado en las minas. Tráeme muestras de los cristales de los geólogos, no me importa lo que tengas que hacer para conseguirlas.", progress = "Necesito esos cristales, <nombre>. ¡Haz lo que sea para conseguirlo! Hazlo, no me des detalles.\n\nTodo se trata del resultado, <nombre>, nada más y nada menos.", completion = "Fascinante. El que los cristales se hayan caído con este proceso solo puede indicar que piensan usarlos para algún tipo de combustible o catalizador. Eso es todo lo que sé. Habrá que enviar muestras al químico jefe Gigglefont.", rewards = "Recibirás:\n\nGuantes con gemas"},
[214] = {title = "Pañuelos rojos de seda", objective = "La exploradora Riell de la Colina del Centinela quiere que le lleves 10 pañuelos de seda roja.", description = "No es ningún secreto que la marca distintiva de la banda Defias son los pañuelos rojos. Pero hemos averiguado que el material del que están hechos los pañuelos sirve para identificar el rango del miembro.\n\nDicho esto, quiero que elimines tantos miembros Defias de alto rango como puedas. Podrás encontrarlos en la ladronera secreta de VanCleef. Cuando hayas terminado tráeme 10 pañuelos de seda roja y sabré recompensarte. Buena suerte, <nombre>.", progress = "¿Has estado defendiendo nuestra tierra de la banda Defias? Si es así enséñame 10 pañuelos de seda roja como prueba.", completion = "¡Bien hecho, &lt;nombre&gt;! En nombre de Las Milicias del Pueblo te rindo homenaje por todos tus esfuerzos. Pronto, y gracias a la ayuda de valientes como tú, devolveremos esta tierra a su pueblo.", rewards = "Podrás elegir una de estas recompensas:\n\nHoja corta sólidaDaga para tallarHacha atravesadora\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nHoja corta sólidaDaga para tallarHacha atravesadora"},
[215] = {title = "Los secretos de la jungla", objective = "Habla con el teniente Doren en el Asentamiento Rebelde.", description = "Gracias por salvarme, <nombre>. Sé lo que parece esto, pero... deja que te lo explique.\n\nEl teniente Doren me encargó alistarme con los hombres de Kurzen para que pudiera espiarles y llevar información a los rebeldes. Lo que has visto es lo que yo pensaba que sería mi oportunidad para infiltrarme en la Base de Kurzen. Me equivoqué y, otra vez, gracias por salvarme.\n\nEsto era una misión de alto secreto, así que no se lo puedes decir a nadie. Pero si hablas con Doren, te lo explicará.", progress = nil, completion = "Sí, lo que te contó Thorsen es cierto. Debes tener discreción con este asunto. Hubo un tiempo, cuando los rebeldes éramos absolutamente leales al coronel Kurzen. Pero debido al grado de corrupción de Kurzen, nuestra pasión se convirtió en ira. Si se supiera que el corsario Thorsen ha tenido algo que ver con los hombres de Kurzen, cualquier noche se encontraría con el pescuezo cortado.\n\nPor lo que yo sé, solo la sargento Yohwa, Thorsen y yo conocíamos la existencia de la misión de Thorsen. Pero debe de haber alguien más. Un traidor.", rewards = "Recibirás:\n\n"},
[216] = {title = "Entre una roca y un Piel de Cardo", objective = "Ejecuta a 12 vengadores Piel de Cardo y a 12 chamanes Piel de Cardo; la mayoría se encuentran al este de La Ensenada de Zoram en el Poblado Piel de Cardo. Cuando lo hayas logrado, vuelve junto a Karang Amakkar en la Avanzada de Zoram'gar, Vallefresno.", description = "¡Los furbolgs Piel de Cardo son una amenaza para nuestra expansión! El Poblado Piel de Cardo, al este, obstruye nuestro paso entre este lugar y el Puesto del Hachazo. Es un camino que rodearía el pueblo de Astranaar; sin él, ofrecemos a la Alianza una vía más para la expansión.\n\n<nombre>, no podemos permitir... ¡un hueco en el camino de expansión!\n\nVe al Poblado Piel de Cardo y acaba con los furbolgs. Recibirás una compensación por esta tarea y lo que es más importante, ¡estarás cumpliendo con tu deber para con la Horda!", progress = nil, completion = "Muy bien, &lt;clase&gt;, ¡pero que muy bien! Seguiré mandando tropas para diezmar a los furbolgs, pero al menos cuento con una ruta diferente que estará libre de enredos con la Alianza. ¡La conquista de Vallefresno por la Horda está bien encarrilada!\n\nTe has ganado el jornal, héroe. ¡Te saludo!", rewards = "Recibirás:\n\n"},
[217] = {title = "En defensa de las tierras del rey", objective = "Mata al jefe trol, Grawmug, y a sus dos matones, Rechín y Camorrista. Informa al capitán Rugelfuss en la torre de vigilancia sur.", description = "<nombre>, nuestros rasteadores han identificado al jefe de la incursión trogg. Parece que estas bestias de Loch Modan siguen a un jefe llamado Grawmug. Y lo tienen muy bien protegido. Siempre va acompañadp de dos de sus mejores guardias, Gnasher y Brawler.\n\nSi te vez capaz, <nombre>, quiero que dirijas el asesinato de Grawmug y sus dos matones. Si hacemos caer al líder trogg, puede que tengamos una oportunidad de rechazar a estas bestias.", progress = nil, completion = "¡Excelente, &lt;nombre&gt;! Gracias por eliminar a Gromug; ahora tenemos más posibilidades de erradicar a los troggs.", rewards = "Podrás elegir una de estas recompensas:\n\nCalzones de fronteraDefensor enanoPantalones de la suerte\n\nTambién recibirás:\n\nCalzones de fronteraPantalones de la suerteDefensor enano"},
[218] = {title = "El diario robado", objective = "Grelin Barbablanca quiere que mates a Grik'nir el Frío y que recuperes su diario.", description = "¡Mi diario! Se lo llevaron a la cueva. El que lo cogió... era una bestia enorme con unas extrañas marcas en la cara... bueno, en todo el cuerpo. No conseguí ver mucho más.\n\nA ti se te dan bien los trols, quizás podrías ir a recuperar mi diario...", progress = "¡<nombre>! ¿Ha habido suerte?", completion = "¡Excelente, &lt;nombre&gt;! Estoy muy agradecido por recuperar mi cuaderno. Bueno, parece que la situación con los trols aquí en el Valle de Crestanevada está bajo control y no hay por qué preocuparse.\n\nDespués de incluir los últimos detalles en mi informe, necesitaré que alguien se lo lleve a mi hermano Senir.", rewards = "Podrás elegir una de estas recompensas:\n\nEscudo cometa enanoBastón de apoyo liso\n\nTambién recibirás:\n\nBastón de apoyo lisoEscudo cometa enano"},
[219] = {title = "Desaparecido en combate", objective = "Protege al cabo Keeshan en el camino a Crestagrana.", description = "¡Al fin, ayuda para la Alianza! \n\nAyúdame... \n\nLo último que recuerdo es que esos malditos orcos nos tenían rodeados. Los mataron a casi todos. A mí me dieron un golpe en la cabeza y perdí el conocimiento, y ahora me encuentro cautivo. Ayúdame, estoy malherido. \n\nTienes que ayudarme a volver... Por favor, eres mi única esperanza.", progress = nil, completion = "Has prestado un gran servicio al Reino, &lt;clase&gt;. Gracias por ayudar al cabo Keeshan a huir de los Roca Negra. Recibirás una recompensa por tus heroicas acciones.", rewards = "Podrás elegir una de estas recompensas:\n\nToga de SolomonAbrigo de anillas de diputadoCuero con huesos\n\nTambién recibirás:\n\nAbrigo de anillas de diputadoCuero con huesosToga de Solomon"},
[220] = {title = "La llamada del agua", objective = "Llévale el vial del agua más pura a Islen Vident'aqua en Los Baldíos.", description = "Islen tendrá una tarea más para ti antes de recompensarte con tu siguiente tótem. Sea cual sea esa tarea, te hará falta el vial de agua que te he dado.\n\nAhora vete y encuéntrala. Volveremos a encontrarnos algún día.", progress = "Hola de nuevo, <nombre>. Tienes buen aspecto.\n\n¿Qué te trae por aquí tan pronto?", completion = "Ah, un vial de agua creado y bendecido por Brine y reunido por ti, nada menos.\n\nEl líquido de este vial será vital en la creación de sapta para ti pero también se usará en otra tarea.\n\nLa sapta que cree para ti también servirá para ver los espíritus del agua del altar que se encuentra en el Bosque de Argénteos, al sur de Entrañas. En cuanto a las gotas restantes, pues, te lo contaré si crees que estás preparado.\n\n¿Estás preparado para otro viaje, &lt;nombre&gt;?", rewards = "Recibirás:\n\nSapta de agua"},
[221] = {title = "Ferocanis en el bosque", objective = "Mata a 12 ferocanis correoscuros nocturnos para Calor de Villa Oscura.", description = "Quizás te hayas fijado en esos enormes ferocanis que deambulan por los bosques con los tejesombras... Por lo que sabemos, esos corredores oscuros son el grueso de las filas de los ferocanis.\n\nEn mis patrullas también he notado que han invadido El Vergel Pútrido al suroeste de la ciudad. Esos ferocanis son más fuertes que los últimos a los que te enfrentaste.\n\nNo bajes la guardia.", progress = nil, completion = "Te has desenvuelto bien contra los Correoscuros, &lt;nombre&gt;. Pero parece que su número es irreducible. Cada vez que hacemos un progreso, llegan más a tomar el relevo.\n\n¿Qué maléfico poder es el que les trae aquí? ¿Por qué han irrumpido de esta manera en nuestro infeliz reino?\n\nTendré fe en el maestro Carevin. Sin duda, él llegará hasta el fondo del asunto.", rewards = "Recibirás:\n\n"},
[222] = {title = "Ferocanis en el bosque", objective = "Mata 8 Colmillovil nocturnos y 8 ferocanis corruptos nocturnos para Calor de Villa Oscura.", description = "Tus anteriores hazañas me han hecho pensar que ya estás <listo/lista> para afrontar a los ferocanis más duros del bosque.\n\nDe todos los ferocanis que han establecido aquí su morada, los Colmillo Vil y los Mancillados han demostrado ser los más peligrosos. Se han instalado cerca de las cuevas y en la mina que hay al sur.\n\nEl fuego de sus hogueras puede verse desde muy lejos...", progress = nil, completion = "A decir verdad, &lt;nombre&gt;, no creía que llegarías tan lejos. Pero está claro que me equivoqué contigo. De hecho, si deseas unirte formalmente a la lucha del maestro Carevin, será un placer escribirte una carta de recomendación.", rewards = "Recibirás:\n\n"},
[223] = {title = "Ferocanis en el bosque", objective = "Lleva la nota de Calor a Jonathan Carevin.", description = "Ten, <nombre>. Lleva este mensaje al maestro Carevin.\n\n<Con un rápido movimiento saca un pergamino descolorido y te lo tiende.>\n\n¡Unos pocos más como tú y superaremos en número a La Guardia Nocturna! Quizás así podríamos acabar entre todos el trabajo que hacemos nosotros.", progress = "¿Sí? ¿Qué quieres? Habla deprisa, tengo asuntos urgentes que atender.", completion = "Perdona mi frío recibimiento, &lt;nombre&gt;, pero ya te habrás dado cuenta de que soy un hombre muy ocupado. Veo que has impresionado a Calor, cosa que no es en absoluto fácil de conseguir, y veo que te ha dado una recomendación.\n\nHemos de afrontar seres de lo más desagradable, &lt;nombre&gt;, y necesitamos la ayuda de cualquiera que haya demostrado su valía. Combatimos contra demonios, no-muertos y contra aquellos que les proporcionan ayuda. Mantente alerta, sé precavido y no confíes en nadie que no colabore con nuestra causa.\n\nGloria bajo la Luz.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de la FeEscudo de la Fe\n\nTambién recibirás:\n\nVarita sagrada consagradaEscudo de la FeCapa de la Fe"},
[224] = {title = "En defensa de las tierras del rey", objective = "El montaraz Pedernal, de la torre de vigilancia sur, quiere que mates a 10 trogg y 10 rastreadores Rompecantos.", description = "¡Tenemos que proteger Loch Modan, <nombre>! Casi todas nuestras tropas están luchando en otros frentes; nos hemos quedado desprotegidos. ¡Están saliendo trogg de debajo de las piedras! Esta invasión supone la mayor amenaza para Forjaz. Tenemos que acabar con esos asquerosos mutantes.\n\nNecesitamos a alguien valiente como tú para acabar con esos seres. Acaba con diez trogg Rompecantos y diez rastreadores Rompecantos. Después vuelve.", progress = nil, completion = "¡Buen trabajo, &lt;nombre&gt;! Has demostrado un valor excepcional en combate; nos has ayudado mucho en nuestra lucha contra los troggs.\n\nSi quieres buscarte más aventuras, ve a hablar con el montaraz Gravagau; seguro que agradece la ayuda de alguien tan capaz como tú. Está en la torre.", rewards = "Recibirás:\n\nPincho Dalaran"},
[225] = {title = "La lápida desgastada", objective = "Ve a Villa Oscura y habla con Sirra Von'Indi.", description = "La lápida, desgastada por el tiempo, reza sencillamente:\n\nMORGAN LADIMOR\n\n<Cuando te acercas más, te das cuenta de que la tierra que hay sobre la tumba ha sido removida recientemente; parte de ella ha sido extraída...>", progress = nil, completion = "¿Morgan Ladimor?\n\nAh, sí, claro. La suya es una historia larga y llena de desgracias. Yo lo conocía bien, bueno, antes de que se fuera a al guerra, pero esa fue la última vez que lo vi. Era un buen hombre, un hombre noble, pero tuvo un trágico final.\n\nTen, tengo algo aquí que podría contarte su historia mejor que yo.", rewards = "Recibirás:\n\n"},
[226] = {title = "Con los lobos en los talones", objective = "Mata a 12 lobos feroces hambrientos y a 8 lobos feroces rabiosos y vuelve al campamento de Sven en la frontera oeste de Bosque del Ocaso a buscar a Lars.", description = "A Sven y a mí nos esperan días peligrosos, con el Necromántico al este... y aquí solos... tenemos que cazar nuestra propia comida. Y cada vez que vuelvo al campamento con algo de carne, aparecen lobos hambrientos o rabiosos buscando un bocado. ¡Vivir aquí es peligroso, no hace falta que lo jure!\n\nPero si consiguieras librarnos de algunos de esos lobos, nos facilitarías mucho la cosa. Se concentran sobre todo al norte y al este de aquí, cerca del río.", progress = nil, completion = "Oh, bien. Has diezmado la manada. Ahora que no tendremos a esos lobos pisándonos los talones, podremos centrarnos en Morbent Vil.\n\nTen, coge algunos de estos manojos destellantes. Los hacen en el pueblo y muchos han sido donados a Sven, para ayudar a su causa.", rewards = "Recibirás:\n\nCarga cegadora"},
[227] = {title = "Morgan Ladimor", objective = "Ve a Villa Oscura y habla con la comandante Althea Cerranegro.", description = "<Busca entre las estanterías y vuelve con un libro encuadernado en cuero.>\n\nSi quieres saber algo más, podrías preguntar a Althea. Ella ha sido la que ha... tratado el... problema... con él.", progress = nil, completion = "¿Morgan Ladimor? No sé quién es...\n\nAh, espera, ¡te refieres a Mor'Ladim! Supongo que no has oído la historia.\n\nPues te contaré la versión resumida. Un no-muerto que se llama a sí mismo Mor'Ladim ha estado deambulando por el Bosque del Ocaso. Por lo que sé, parece ser el cuerpo no-muerto de Morgan Ladimor, que es la persona que buscas. Nos ha causado todo tipo de problemas, ha estado atacando a los vigilantes que patrullaban la zona y ha matado a gente.\n\n&lt;Te mira de arriba a abajo.&gt;\n\nParece que tú podrías proporcionarle descanso.", rewards = "Recibirás:\n\nLa historia de Morgan Ladimor"},
[228] = {title = "Mor'Ladim", objective = "Mata a Mor'Ladim y luego lleva su calavera a la comandante Althea Cerranegro a Villa Oscura.", description = "Por lo que me dicen los rastreadores, Mor'Ladim deambula por el Bosque del Ocaso; recorre un extraño y serpenteante camino por el cementerio. Lo enterramos pasada la casa de la colina, ¿sabes dónde digo?\n\nNo hay nada que pueda hacer para ayudarte, pero te deseo buena suerte.", progress = "No me extraña que te esté dando problemas, <nombre>. Algunos de nuestros guardias más fuertes han perdido la vida a manos de Mor'Ladim.", completion = "¿Lo mataste? ¡Es toda una hazaña, &lt;nombre&gt;! Te doy las gracias de parte de las gentes de Villa Oscura y de parte de La Guardia Nocturna.\n\nAh... pero hay una cosilla...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[229] = {title = "La hija superviviente", objective = "Ve a Villa Oscura y habla con la vigilante Sarah Ladimor.", description = "Tendría que haberte dicho esto antes...\n\nVeamos cómo podría explicarlo...\n\nMorgan cree que toda su familia está muerta pero, de hecho, su hija Sarah Ladimor ahora es una guardia. Siempre ha estado atormentada por las... circunstancias que rodearon la muerte de su padre. Quizás podrías ir a llevarle estas noticias.", progress = nil, completion = "¿Sí? Mi padre...\n\n&lt;Baja la mirada, abatida..&gt;\n\nOjalá hubiera podido hacer algo por él... Si hubiera hablado con él antes de...", rewards = "Recibirás:\n\n"},
[230] = {title = "El campamento de Sven", objective = "Lleva el libro de la granja de Sven a Sven.", description = "Cavando en el montículo encuentras un pequeño libro manchado de tierra.\n\nNo tiene título pero... contiene información que a Sven le gustaría tener.", progress = "¿Que has encontrado qué? Déjame verlo, por favor...", completion = "&lt;Sven lee las páginas del libro y luego lo cierra de un golpe.&gt;\n\n¡Esa figura misteriosa que vi fue testigo de la muerte de mi familia! Hay preguntas que necesito hacerle a ese hombre... ¡si tan solo supiera quién es!", rewards = "Recibirás:\n\n"},
[231] = {title = "El amor de una hija", objective = "Lleva el anillo de Sarah Ladimor a la tumba de Morgan Ladimor.", description = "Ten, lleva esto a su tumba. Quizás... de alguna forma, él sepa que estoy bien y que nadie le hace responsable de lo que ocurrió.\n\n<Los ojos se le llenan de lágrimas mientras se quita el anillo de la mano derecha y lo coloca en tus manos.>\n\nY... gracias, <nombre>.", progress = "Déjame...", completion = "&lt;El viento trae una voz fantasmal...&gt;\n\n¿Esto es...? ¿Sarah? ¿Es posible que siga con vida? Qué peso me has quitado de encima...\n\n&lt;nombre&gt;. Toma mi espada, Archeus. Mi alma descansará. Ya no volveré a necesitarla. Fue forjada para hacer el bien y, aunque yo he demostrado no ser merecedor de portarla, quizás tú consigas acercarla a la Luz.\n\nLys, mi amor...", rewards = "Recibirás:\n\nArcheus"},
[232] = {title = "Una recado para la boticaria Zinge", objective = "Llévale la orden de compra a Alessandro Luca, que está en Entrañas.", description = "La Sociedad Real de Boticarios necesita tu ayuda. Escucha bien esto, y puede que ambos podamos beneficiarnos con mi plan.\n\nEl boticario Keever, en la sala contigua, puede que esté... un poco tocado... pero su investigación promete dar muy buenos resultados, sobre todo si la condujera un investigador más inteligente. Afortunadamente, yo soy investigador. Quiero que lleves esta orden de compra a Alessandro Luca, el propietario de Cachivaches Luna Azul; él te dará un equipo de estudio. Cuando lo tengas, tráemelo y yo me encargaré de explicarte tu encomienda con todo detalle.", progress = "Dime, ¿en qué puedo ayudarte? ¿Tienes algo que tratar con la Sociedad Real de Boticarios o has venido simplemente a alegrarme el día?", completion = "¿Conque Zinge necesita un equipo? Me entristece que Zinge no me honre con su dulce presencia. \n\nBueno, te procuraremos un equipo.", rewards = "Recibirás:\n\n"},
[233] = {title = "Entrega de correo en el Valle de Crestanevada", objective = "Entrega el fajo de cartas a Talin Ojoagudo.", description = "Mm, oye, tú no querrás hacerme un favor... ¿o sí? Hoy ha llegado un fajo de cartas a través del paso, pero no tengo tiempo para entregarlas.\n\nEstán todas dirigidas a Talin Ojoagudo. Lo encontrarás al oeste. Solo tienes que seguir el camino. Ha establecido su campamento en la orilla del lago helado.\n\n¿Qué respondes?", progress = "¿Sí? ¿Tienes algo para mí?", completion = "Gracias, llevo un tiempo esperando estas cartas...\n\nPor desgracia, no son todas para mí. Esta es para Grelin Barbablanca. No está muy lejos de aquí. Si quieres entregársela...", rewards = "Recibirás:\n\n"},
[234] = {title = "Entrega de correo en el Valle de Crestanevada", objective = "Entrega la carta a Grelin Barbablanca.", description = "Si no recuerdo mal, el campamento de Grelin está al sureste, por el camino. Estará ansioso por recibir su correo.", progress = "¿Puedo ayudarte en algo?", completion = "Aah, excelente. Hace tiempo que no recibo noticias de Forjaz.", rewards = "Recibirás:\n\n"},
[235] = {title = "La Caza de Vallefresno", objective = "Habla con Senani Truenozón en Puesto del Hachazo, en Vallefresno.", description = "¡Amantes de la aventura, sabed que esta os espera en Vallefresno!\n\nLa Horda se está asentando en las tierras norteñas de Los Baldíos. ¡Queremos llevar la gloria a la Horda desde Puesto del Hachazo y Avanzada de la Ensanada de Zoram! Si deseáis probar vuestra valía, acudid allí. Nota: Senani Astatrueno, Puesto del Hachazo, al norte de Los Baldíos, busca amantes de la aventura para una gran cacería en Vallefresno.", progress = nil, completion = "Te doy la bienvenida a la nueva frontera, &lt;nombre&gt;. Vallefresno es una tierra de oportunidades, sobre todo para alguien como tú. Echa un vistazo y ve a La Ensenada de Zoram; allí hay otra avanzada de la Horda.\n\nTu presencia me dice que has venido a aprender sobre la caza. Escucha con atención y yo compartiré mis secretos contigo.", rewards = "Recibirás:\n\n"},
[236] = {title = "Combustible para los demoledores", objective = "El maestro de asedio Durasa, en la Fortaleza de Conquista del Invierno, quiere que arrebates 10 ascuas eternas a los jugadores de la Horda en El Caldero de Llamas.", description = "¡<nombre>! Ya era hora de que aparecieras. Puede que esta batalla haya terminado, ¡pero aún tenemos que prepararnos para la siguiente! Viaja hasta El Caldero de Llamas, al sureste de Conquista del Invierno, y recoge algunas ascuas eternas.\n\nLas ascuas están mezcladas con carbón para repostar el demoledor y las máquinas de asedio.\n\nOh, y si ves a algún miembro de la Horda por El Caldero, no dudes en matarlo y quedarte con sus ascuas. No podemos dejar que nos saquen ventaja en la próxima batalla.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[237] = {title = "En defensa de las tierras del rey", objective = "El montaraz Gravagau, de la torre de vigilancia sur, quiere que mates a 10 aplastacráneos y 10 profetas Rompecantos. Luego ve a informarle.", description = "El montaraz Pedernal habla muy bien de ti, <raza>. Por eso he decidido confiarte una misión de extrema importancia. Tenemos que mantener a raya a los trogg invasores hasta que nuestros hermanos regresen de luchar por la Alianza.\n\nDirígete a las colinas del sur y mata a diez aplastacráneos y diez profetas Rompecantos. Eso nos hará ganar tiempo. Regresa cuando termines la misión.", progress = nil, completion = "Buen trabajo, &lt;raza&gt;.\n\nPero los refuerzos prometidos no han llegado; muchos de los nuestros yacen en el campo de batalla. Amargo es el dolor, pero debemos seguir adelante, por la Alianza.\n\nHabla con el montaraz Muroplof para que te informe de tu nuevo destino.", rewards = "Recibirás:\n\nPoción de Curación inferior"},
[238] = {title = "Una recado para la boticaria Zinge", objective = "Llévale el equipo de recogida de muestras de campo a la boticaria Zinge, que está en Entrañas.", description = "Este equipo está listo, <nombre>. No cabe duda de que a la boticaria Zinge le gustaría recibirlo lo antes posible. Llévaselo cuanto antes, y dile que le cargaré los costes del equipo a la Sociedad. De paso, por favor, hazle llegar mi más afectuoso saludo.\n\nBueno, no te quedes ahí. Esa es la vida de un simple <mensajero/mensajera>, ¿no?", progress = "Sí, al igual que tú creo que Luca es desconcertante. No importa, dame el equipo de prueba de campo. Tengo que hacerle algunos ajustes para poder recoger los materiales adecuados.\n\nTienes el equipo, ¿verdad?", completion = "Me alegra que no me hagas perder el tiempo. \n\nCuando haya realizado los ajustes, podrás utilizar este equipo para recoger la información que precises. \n\nEspera un momento a que acabe y, entre tanto, ¿por qué no vas pensando en qué te gustaría llevar contigo al desierto? \n\nSí, sí, he dicho al desierto.", rewards = "Recibirás:\n\n"},
[239] = {title = "El Cuartel Arroyoeste necesita ayuda", objective = "Ve al Cuartel de Arroyoeste y habla con el ayudante del alguacil Rainer.", description = "La plaza fuerte de la frontera occidental informa de un aumento de actividad de gnolls y ladrones. Solicitan que enviemos más soldados de Ventormenta, ¡pero no podemos prescindir de ninguno!\n\nTu ayuda nos vendría bien. Habla con el ayudante del alguacil Rainer en el Cuartel Arroyoeste y que te diga qué necesita.\n\nLa plaza fuerte está al oeste, bajando por el camino. Encontrarás un puente sobre un arroyo. Crúzalo. La plaza fuerte estará a la derecha.", progress = nil, completion = "Te envía el alguacil Dughan, ¿eh? No eres del ejército, pero bueno, si te manda Dughan, me basta como referencia.\n\nNuestra situación es, como mínimo, apurada. Espero que puedas echarnos una mano.", rewards = "Recibirás:\n\n"},
[240] = {title = "Entrega para Gato", objective = "Vuelve a donde Alterio con las croquetas de cangrejo.", description = "¡¡Y voilà!! ¡Croquetas de \"cangrejo\" à la Grual!", progress = "<nombre>, ¡estás aquí! ¡¿Tienes mi comida?!", completion = "¡Ajá! Gracias, &lt;nombre&gt;. ¡Qué bien sientan estas cosas!\n\n¡Y ten, deja que te pague!", rewards = "Recibirás:\n\n"},
[243] = {title = "In situ", objective = "Ve a Gadgetzan y encuentra al ingeniero jefe Silvaina para que te explique cómo obtener una fuente de energía para el equipo de estudio.\n\nObjeto provisto: \n", description = "Dirígete al Desierto de Tanaris, y allí comprueba la consistencia del tejido de los basiliscos, escórpidos y de las hienas autóctonas del lugar. Consigue muestras de tejido con el equipo de estudio que llevas; eso sí, hazte a la idea de que parte de las muestras serán inservibles. Necesito 8 muestras buenas de cada tipo de criatura.\n\nAdemás, los goblins de Gadgetzan tienen una fuente de energía que debe emplearse con este tipo de equipo modificado. Habla con el ingeniero jefe Silvaina y consigue una para el equipo.\n\nVuelve a hablar conmigo cuando la tengas.", progress = nil, completion = "¡Ah, nuestras fuentes de alimentación, modelo 4711-FTZ! Solemos utilizarlas para el funcionamiento de los pozos de agua, pero GRACIAS A LOS VILES NÓMADAS, tenemos exceso de existencias ahora mismo. \n\nAsí que, seguro que puedo conseguirte una fuente de alimentación para el nefario experimento científico que tratas de ocultarme, sea el que sea. Aunque no será gratis, &lt;raza&gt;. Nada es gratis en Gadgetzan.", rewards = "Recibirás:\n\n"},
[244] = {title = "Gnolls invasores", objective = "Preséntate ante el ayudante Feldon.", description = "Saludos, <clase>. Estoy patrullando esta parte del camino. De momento todo está en calma, pero he visto campamentos gnoll al norte y al este de aquí. \n\n¡Debes informar de esto en Villa del Lago! Preséntate ante el ayudante Feldon y dile lo de los gnolls. Seguro que te paga sueldo de rastreador. \n\nLo encontrarás al sur del puente a Villa del Lago.", progress = nil, completion = "¿Hay gnolls de Crestagrana tan cerca de Elwynn? Puede que planeen una invasión... ¡Los de Villa del Lago ya no serán los únicos humanos bajo asedio!\n\nAquí tienes el pago, pese a las noticias que traes. Y en qué mal momento: carecemos de recursos.", rewards = "Recibirás:\n\n"},
[245] = {title = "Las amenazas de ocho patas", objective = "Mata a 15 telarácnidas pigmeovenenosas y vuelve al Bosque del Ocaso a informar al vigilante Dodds.", description = "Salve, <clase>. ¿Has visto esas arañas que han invadido la frontera occidental? Esas amenazas de ocho patas son demasiado para nosotros pero el Comandante no tiene suficientes hombres. No puede enviar a nadie tan lejos de Villa Oscura.\n\nOdio tener que pedirte esto pero, parece que tú podrías manejar a esos bichos. No puedo prometerte una gran recompensa, pero si nos ayudas a erradicar a esos arácnidos, encontraré la forma de compensarte.\n\nAh, y deja que te dé un consejo: evita su veneno a toda costa.", progress = nil, completion = "¡Ah! Has vuelto. Y no demasiado agotado, por lo que veo. La Guardia Nocturna está agradecida por tu trabajo, &lt;nombre&gt;, y yo te prometí una recompensa, aquí la tienes.", rewards = "Recibirás:\n\nGuanteletes de guardia nocturno"},
[246] = {title = "Evaluar la amenaza", objective = "Mata a 10 bastardos y 6 furtivos Crestagrana e informa al ayudante Feldon en Villa del Lago.", description = "Andamos escasos de tropas, pero hay que hacer algo con esos gnolls.\n\nRastrea el sur de Crestagrana en busca de gnolls. Espíalos, mata a tantos como puedas y vuelve para informarme de cuántos son y si suponen una amenaza. Sus campamentos están por todo el camino meridional de Crestagrana. \n\nEl ejército de Ventormenta te recompensará, <nombre>.", progress = nil, completion = "&lt;El ayudante del alguacil Feldon escucha tu informe...&gt;\n\nAsí que hay un grupo numeroso de gnolls de los duros. Seguro que no te ha sido fácil recopilar esta información.\n\nTen, &lt;nombre&gt;, y gracias por tu ayuda.\n\nHabla con el alguacil Marris y el magistrado Salomón en Villa del Lago. Necesitarán tu ayuda; la situación empeora por momentos.", rewards = "Recibirás:\n\n"},
[247] = {title = "El fin de la Caza", objective = "<nombre>, has dado muerte a las tres bestias que comprenden la caza de Vallefresno. Me has traído prueba suficiente de su derrota; no cabe la menor duda acerca de tu fuerza y tenacidad. Por tus venas corre la sangre de un cazador ¡y mereces los más elevados elogios!\n\nTe ruego aceptes este adorno como muestra de reconocimiento a tus descomunales esfuerzos realizados en Vallefresno para la Horda hoy. ¡Que tus ancestros estén contigo!", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCapa de cazador salvaje"},
[248] = {title = "Ver más allá", objective = "Sube a la atalaya intacta que hay junto al Fuerte de Petravista. \n\nEncuentra un objeto en el que esconder el Glifo de Azora.", description = "He estado observando la Torre de Ilgalar; Morganth está en conflicto con los orcos del Fuerte de Petravista. Desconozco los motivos, pero tú me ayudarás a descubrirlos.\n\nCoge otro Glifo de Azora y sube a la atalaya intacta que hay junto al fuerte.\n\nEncuentra un objeto en el que depositar el glifo. Un cofre o un tarro iría bien, un lugar en el que nadie se fije en él.", progress = "Este tarro contenía un licor pestilente, pero ahora está vacío, tirado entre dos barriles.", completion = "Es una vasija adecuada para el Glifo de Azora, que permite a Theocritus explorar las mentes que hay en el Fuerte de Petravista.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[249] = {title = "Morganth", objective = "Derrota a Morganth. \n\nLleva el colgante de la Sombra a Theocritus a la Torre de Azora, en el Bosque de Elwynn.", description = "Después de mucha observación, he descubierto los planes de Morganth. \n\nBusca la Guadaña de Elune, ¡y es posible que ya la haya encontrado! Si ese es el caso, pronto las Montañas Crestagrana y el Reino de Ventormenta sentirán su siniestro poder. Debes ir a la Torre de Ilgalar y enfrentarte a Morganth. \n\nDerrótalo y tráeme su colgante de la Sombra. El reino quedará en deuda contigo.", progress = "¡<nombre>, tienes que evitar que Morganth extienda su poder!", completion = "Muy bien, &lt;nombre&gt;. Al derrotar a Morganth, has librado a Ventormenta de un grave peligro.\n\nAcepta esta recompensa. Te diría que fueras en paz, pero estos no son tiempos de paz.\n\nEn lugar de eso, te deseo suerte en tus pruebas y victoria en combates futuros.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nManto rosa"},
[250] = {title = "Se aproxima una oscura amenaza", objective = "Investiga los alrededores de la rampa este de la Presa y lleva al ingeniero jefe Hinderweir alguna pista sobre lo que traman los Hierro Negro.", description = "Menos mal que alguien se interesa por el bienestar de la Presa. ¡Primero la destrucción del Puente Thandol y después el saqueo de Dun Modr! No dudo que el objetivo del siguiente ataque de los Hierro Negro será la Presa. Casi todos los agentes de seguridad se han destinado a luchar con la Alianza, incluido mi mejor inspector.\n\nPor eso te necesito, <nombre>. He visto algunos zapadores Hierro Negro en la rampa oriental de la Presa. Investiga la zona y trae una pista.", progress = nil, completion = "En el barril hay unos polvos de olor extraño.", rewards = "Recibirás:\n\n"},
[251] = {title = "Traduce la nota de Abercrombie", objective = "Lleva la carta de Ello a Sirra Von'Indi.", description = "Lleva la nota que me diste a Sirra Von'Indi. Es un experto en idiomas y artes antiguas. Si alguien puede traducir este mensaje, ese alguien es él.", progress = "Una nota de Abercrombie el ermitaño, ¿eh? Pues no sé quién es... Pero desde luego, si vive en el cementerio, ¡debe de tener el cerebro podrido!", completion = "&lt;Sirra empieza a traducir la nota...&gt;", rewards = "Recibirás:\n\n"},
[252] = {title = "Una traducción para Ello", objective = "Lleva la carta traducida a Lord Ello Cerranegro.", description = "¡Ay, ay! Si lo que dice esta nota es cierto, Abercrombie ha hecho algo... indescriptible.\n\nSerá mejor que le lleves la nota traducida a Ello.\n\nY date prisa. Temo que una maldición se acerca hacia nosotros...", progress = "Ah, ¿la nota está traducida? Déjame verla, por favor...", completion = "&lt;Ello lee la carta... y palidece. Luego te mira a ti...&gt;\n\nAunque cuando ayudaste a Abercrombie lo hiciste motivado por tu bondad, con tus acciones has condenado a nuestro pueblo.\n\n&lt;Ello se pierde en sus pensamientos...&gt;", rewards = "Recibirás:\n\nCarta traducida de El Embalsamador"},
[253] = {title = "La novia del embalsamador", objective = "Encuentra la tumba de Eliza. Quítale el corazón del embalsamador y luego vuelve a Ello Cerranegro.", description = "El Embalsamador es el nombre de un personaje de uno de nuestros cuentos para niños. Era un alquimista bueno que enloqueció tras la muerte de su esposa Eliza. Para recuperarla, utilizó magia negra y colocó su propio corazón en el pecho de su mujer.\n\nEliza resucitó... pero con un hambre insaciable de carne humana y eso obligó al Embalsamador a mantenerla enterrada.\n\nSu corazón es su poder. Encuentra la tumba de su esposa, quítale el corazón y tráemelo a mí mientras aún lata. Hazlo y romperás su poder.", progress = "¿Has encontrado la tumba de Eliza? ¿Tienes el corazón del embalsamador?", completion = "¡Lo has conseguido! La magia que contiene su corazón nos protegerá contra el mal del embalsamador y será un poderoso auspicio para Villa Oscura.\n\nHas hecho una gran cosa para nuestra gente, &lt;nombre&gt;. Tu nombre permanecerá para siempre en nuestros anales.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nManto de HonorEmblema de Villa Oscura"},
[254] = {title = "Escavar la tierra", objective = "Excavas entre la tierra suelta que hay sobre la tumba... y encuentras los restos de un féretro.\n\nEn el interior del féretro puedes sentir la vibración de unos dedos arañando y el rechinar de unos dientes podridos...\n\n¿Abrirás el féretro y extraerás el corazón del embalsamador?", description = nil, progress = nil, completion = nil, rewards = nil},
[255] = {title = "Mercenarios", objective = "El magistrado Romonariz de Thelsamar te ha contratado para matar a 4 ogros, 4 brutos y 4 gorilas Mo'grosh.", description = "¡Hemos esperado dos meses a que Forjaz nos conceda unos cuantos soldados para la defensa, y tardarán otros dos meses en llegar! Ya es bastante malo que la ciudad esté desprotegida, pero la excavación y la presa son muy vulnerables.\n\nParece que no tengo elección. Necesito mercenarios.\n\n¿Qué tal tú? Creo que lo harás bien, <clase>. Un grupo de ogros se ha instalado al noreste del lago. ¿Qué tal si reclutas a unos cuantos tipos y os ocupáis de esos ogros?", progress = nil, completion = "Estupendas noticias. Quiero agradecerte lo que has hecho en nombre de Thelsamar. Y resuelto el problema de los ogros, podremos concentrarnos en los troggs.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[256] = {title = "SE BUSCAN: Chok'sul", objective = "Mata a Chok'sul y lleva su cabeza al magistrado Romonariz a Thelsamar.", description = "Por orden del magistrado de Thelsamar:\n\nse busca muerto a Chok'sul, ogro culpable de dirigir los ataques contra la ciudad de Thelsamar, la Presa de las Tres Cabezas y la excavación.\n\nSe ofrece recompensa a quien presente al magistrado una prueba de la muerte de Chok'sul.\n\nFue visto por última vez en el campamento ogro situado al noreste de Loch Modan.", progress = "¿Sí? ¿Necesitas algo?", completion = "¡Puaj! ¿Qué es eso?\n\nPor la Luz, ¿es la cabeza del ogro? ¡Excelente! Ten la recompensa y gracias en nombre de Thelsamar.", rewards = "Podrás elegir una de estas recompensas:\n\nHombreras de anillas durablesBotas Kimbra\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAnillo de mediación menorHombreras de anillas durablesBotas Kimbra"},
[257] = {title = "La fanfarronada de un cazador", objective = "Mata 6 águilas ratoneras montesas y ve a ver a Daryl el Jovenzuelo a la Cabaña del Errante en menos de 15 minutos.", description = "Crees que estás a la altura de Daryl el Audaz, ¿eh? ¡No lo creo! Por supuesto, puedes ponerte a prueba. Te propongo un desafío que seguro estará por encima de tus posibilidades, así que no te sientas mal si no eres capaz de superarlo. ¿Qué tal si acabas con algunas de esas bestias? Si lo consigues en 15 minutos, te daré mi arco o mi pistola.", progress = nil, completion = "¿Que lo has conseguido?\n\nBah, tampoco hay para tanto; hasta un crío con un arco de juguete podría matar a estas bestezuelas. Y no te emociones matando águilas ratoneras, que no es cuestión de que se extingan. \n\nBueno, pues... En fin, Daryl el Audaz siempre cumple su palabra.", rewards = "Podrás elegir una de estas recompensas:\n\nArco de caza de DarylRifle de caza de Daryl\n\nTambién recibirás:\n\nArco de caza de DarylRifle de caza de Daryl"},
[258] = {title = "El reto de un cazador", objective = "Mata 5 jabalís monteses mayores y ve a ver a Daryl el Jovenzuelo a la Cabaña del Errante en menos de 12 minutos.", description = "Así que eres <nombre>, ¿eh? Te llena de orgullo haber pasado la primera prueba, ¿verdad? Como dije antes, tampoco hay para tanto.\n\nA ver si te atreves a cazar un jabalí. Esta no será una cacería como las del Valle de Crestanevada. Será mejor que tengas cuidado. Esta vez te doy 12 minutos.\n\nNo te sientas mal si no lo consigues. ¡Si lo haces, te daré mi camisa!\n\n¿Te he contado cómo me hice mi famosa cicatriz? ¿No? Fue hace dos años...", progress = nil, completion = "Esos jabalís te han dejado para el arrastre, ¿eh? No te preocupes, que no se lo diré a nadie; bastante mal lo estás pasando.\n\nAh, ¿que sí los has matado? Pues..., bueno, es normal. Era un juego de niños...\n\n¿Apuesta? ¿Qué apuesta?", rewards = "Recibirás:\n\nCamisa de paño refinadoEspada corta de Daryl"},
[261] = {title = "El sendero Escarlata", objective = "Destruye 30 devastadores no-muertos y luego ve a buscar al hermano Antón a Punta de Nijel.", description = "Te hablaré con franqueza. Estamos en guerra con la Plaga. Es un mal que corrompe a nuestra gente e infesta nuestra tierra. Hay que detenerlo antes de que nos arrolle y arrastre a nuestro mundo a las sombras. Los miembros de la Cruzada Escarlata hemos jurado combatir a la Plaga en cuerpo y alma.\n\nSi estás con nosotros, reúne valor y prueba tu lealtad: destruye a los devastadores no-muertos de Desolace. Búscalos en el Valle de los Huesos, al sur.\n\nHazlo y la Cruzada te acogerá con los brazos abiertos.", progress = nil, completion = "Has demostrado tu resolución y la Cruzada Escarlata elogia tu honor.\n\nSi no te arredras ante las matanzas justas, quizás tu camino esté con nosotros...", rewards = "Recibirás:\n\n"},
[262] = {title = "La figura en las sombras", objective = "Llévate el libro que encontraste en la granja de Sven a Villa Oscura y enséñaselo a sus habitantes.", description = "¡Debí adivinar quién era esa figura que vi en las sombras la noche en que mi familia fue asesinada!\n\nPor favor, <nombre>, has demostrado ser <un hombre/una mujer> de recursos. Te lo ruego, encuentra a ese ser misterioso. ¡He de saber qué secretos guarda!\n\nNo sé nada que tú no sepas ya pero, ten, coge el libro que encontraste en mi granja. Quizás alguien del pueblo pueda decirte a quién pertenece el libro.", progress = "Si tienes algo que pertenezca a la figura misteriosa que buscas, enséñamelo. Quizás pueda averiguar algo sobre su dueño.", completion = "Mmm... Alguien ha manipulado este libro hace poco... alguien guiado por la venganza. Pero las impresiones que hay en él, al menos las impresiones del dueño original, son débiles.\n\nPero sí puedo decirte que el dueño original de este libro no es oriundo de Villa Oscura.", rewards = "Recibirás:\n\n"},
[263] = {title = "En defensa de las tierras del rey", objective = "El montaraz Pedernal, de la torre de vigilancia sur, quiere que mates a 10 chamanes y 10 quebrantahuesos Rompecantos.", description = "Los montaraces me dicen que eres valiente y capaz, <nombre>. Nos hace falta alguien así. La situación con los trogg no mejora. La reserva está en combate; estamos solos. Pero con tu experiencia como <clase>, la cosa va a cambiar.\n\nLiquida a diez chamanes y diez quebrantahuesos Rompecantos. A ver si los trogg aprenden a temerte.", progress = nil, completion = "Lo supe en cuanto te vi, &lt;nombre&gt;. Sabía que Forjaz se enorgullecería de ti. Has servido bien al rey Magni. Ahora que has demostrado ser un ayudante leal del reino, quizás deberías hablar con el capitán para que te confíe más detalles.\n\nTe saludo, &lt;nombre&gt;.", rewards = "Recibirás:\n\nPiedra de afilar burda"},
[264] = {title = "Hasta que la muerte nos separe", objective = "Coloca el colgante de Clarice sobre la tumba de Yuriv en Argénteos.", description = "¡Sucia escoria! Me abandona por su maldita cruzada porque \"la Luz es lo más importante que tenemos contra la amenaza que representan los no-muertos.\"\n\n¡¿Y sus hijos?! ¡¿Y YO?! Noche tras noche me quedaba esperando por él... ¡siempre en segundo lugar después de sus condenadas obligaciones!\n\nBueno, ¡mira lo que te depara la \"justicia\", <nombre>! ¡Él está muerto y yo tengo precisamente lo que estaba intentando detener!\n\nCoge esto y ponlo encima de su tumba en El Sepulcro. ¡No quiero tener nunca más nada que ver con eso... ni con él!", progress = "La piedra está fría y su estado demuestra que se han ensañado con ella. La zona está llena de basura, el féretro presenta golpes y grietas y las malas hierbas empiezan a crecer a su alrededor.\n\nA nadie le importa quién está enterrado aquí, sobre todo a ninguna de las víctimas de la peste.", completion = "Colocas el colgante sobre la tumba y observas que la gema parece perder su brillo.\n\nCuando te incorporas para marcharte, miras de nuevo el collar, que yace inánime en las manos de la escultura que preside el féretro. Una ráfaga de viento frío recorre El Sepulcro, interrumpiendo así tus pensamientos. Por un momento, el silencio reina a tu alrededor.", rewards = "Recibirás:\n\n"},
[265] = {title = "La búsqueda de la figura continúa", objective = "Averigua si otros habitantes tienen pistas sobre la figura misteriosa.", description = "Te devuelvo tu libro, <nombre>. Y te deseo mucha suerte. Si preguntas a la gente del pueblo, quizás puedas conseguir más pistas.\n\nEmpezaré por darte yo una: aunque el autor de este libro no es de Villa Oscura, el libro fue comprado aquí.", progress = "A veces vendo libros en blanco a estudiosos y magos, o a cualquiera que quiera escribir un diario.\n\nRecuerdo perfectamente a todos los que me compraron libros en los últimos meses...", completion = "Ah, sí. Vendí este libro hace un mes a una persona... algo sombría. No me dio su nombre, pero era un tipo nervioso que no paraba de mirar por encima de su hombro.\n\nEspero que eso te ayude...", rewards = "Recibirás:\n\n"},
[266] = {title = "Busqueda en la posada", objective = "Busca pistas sobre la figura misteriosa en la posada.", description = "Ten tu libro. Y buena suerte con tus pesquisas.\n\nQuizás quieras continuar la búsqueda en la posada. Si la persona a la que buscas pasó algo de tiempo en el pueblo, es probable que alguien le viera allí.", progress = "El Cuervo Escarlata ya no es lo que solía ser pero aún pasan por aquí algunos viajeros.\n\nDescribirme al tipo al que buscas, quizás lo recuerde.", completion = "Ah, sí, recuerdo este libro. Su dueño alquiló una habitación por unos días. Se pasaba las noches en el bar, escribiendo.\n\nDejó el pueblo a toda prisa, murmurando algo de que le perseguían... La última vez que lo vi se dirigía hacia el oeste... buscaba un nuevo lugar en el que esconderse.\n\nEl primer lugar al que llegaría yendo hacia el oeste sería Cerro del Cuervo...", rewards = "Recibirás:\n\n"},
[267] = {title = "La amenaza trogg", objective = "Lleva 8 petrodientes de trogg al capitán Rugelfuss a la torre de vigilancia sur.", description = "<clase>, puede que sepas que los trogg amenazan las tierras de los enanos. Ahora que la Reserva de Forjaz está combatiendo por la Alianza, nos hemos quedado con una fracción de las fuerzas defensivas que necesitamos. Mi regimiento está destinado a vigilar la puerta. No podemos abandonar el puesto porque tememos una invasión.\n\nHay que ahuyentar a esos malditos trogg que acechan en las colinas. Si te ves capaz, organiza un asalto a los trogg. Tráeme ocho petrodientes de trogg como prueba.", progress = "¿Me traes los 8 petrodientes de trogg? Si no, no sé qué haces aquí, <nombre>.", completion = "Impresionante, &lt;nombre&gt;. Los enanos quedan en deuda contigo. ¡El rey Magni Barbabronce estaría orgulloso!", rewards = "Recibirás:\n\n"},
[268] = {title = "Entrega para Sven", objective = "Lleva a Sven el diario completo de Alterio.", description = "El libro que encontraste no estaba acabado. Tuve que dejarlo en la granja de Sven cuando huí...\n\nTen. Lleva mi diario completo a Sven. Describe con más detalle lo que ocurrió en su granja.\n\nAunque este diario ha sido mi única compañía todas estas semanas, si renunciando a él me evito tener que afrontar la ira de Sven... ¡te lo doy encantado!", progress = "¿Has averiguado quién era la figura misteriosa?", completion = "Este diario me explica muchas cosas ¡y me indica el camino para enfrentarme a los jinetes oscuros! He visto a esos jinetes en Bosque del Ocaso, y les he visto hablando con el zahorí Morbent Vil, en la Loma Inhóspita.", rewards = "Recibirás:\n\n"},
[269] = {title = "En busca de sabiduría", objective = "Entrega el diario completo de Alterio al obispo Lontananza de Ventormenta.", description = "Ve a la ciudad de Ventormenta y habla con el obispo Farthing. Búscalo en la Catedral de la Luz. Dale el diario del Gato y dile que deseas enfrentarte a Morbent Vil.\n\nAunque Ventormenta ha abandonado a Villa Oscura, Farthing es un alma buena y sabia. Puede que te ayude a salvarnos.\n\nTu misión es peligrosa, pero date prisa, <nombre>. Las sombras del Bosque del Ocaso son más cada vez más oscuras.", progress = "Descansa, hermano. Se ve que has viajado lejos y que llevas un peso enorme. ¿Puedes decirme qué es lo que proyecta sobre ti esa sombra tan oscura?", completion = "&lt;Después de leer el diario y de oír tu intención...&gt;\n\nUna malvada mácula se ha hecho con el ya asediado Bosque del Ocaso. Doy gracias a la Luz porque ahora tenemos a un héroe como tú para hacerle frente.\n\nPero primero debes prepararte para la prueba que te espera, pues el poder del Necromántico Morbent Vil es inconmensurable y además, ha lanzado sobre sí mismo oscuros encantamientos que lo protegen de ataques físicos. Si quieres derrotarle, necesitarás un arma que pueda contrarrestar estas protecciones.", rewards = "Recibirás:\n\n"},
[270] = {title = "La flota maldita", objective = "Vete al Puerto de Menethil en Los Humedales y habla con Glorin Frenteacerada.", description = "Años atrás, la Tercera Flota de Kul'Tiras se hundió en la costa de Los Humedales, una expansión pantanosa al norte del reino enano de Forjaz. Abordo del barco había un cargamento de hierro Forjaluz.\n\nEste metal es muy valioso para nosotros, porque los objetos forjados con él, golpean con toda la verdad sagrada. Si te vas a enfrentar a Morbent Vil, necesitarás un arma así.\n\nVete al Puerto de Menethil y habla con mi colega enano Glorin Frenteacerada. Si es posible encontrar ese metal perdido, él sabrá dónde.", progress = nil, completion = "El hierro Forjaluz es un metal precioso, pero parece que tu misión es muy peligrosa. Has hecho un largo camino, hermano. Con suerte, encontrarás lo que buscas.\n\nY, si consigues el hierro, sé quién puede forjar un arma para luchar contra ese Necromántico del que hablas del Bosque del Ocaso.", rewards = "Recibirás:\n\n"},
[271] = {title = "La venganza de Vyrin", objective = "Mata a Ol' Sooty y llévale una prueba a Daryl el Jovenzuelo en la Cabaña del Errante.", description = "Supongo que querrás vengarte de ese bocazas. Me duele admitirlo, pero Daryl tiene su talento.\n\nHace un año, toda la Cabaña del Errante andaba detrás de un oso enorme, pero nadie conseguía cazarlo. Y Daryl decidió intentarlo. Lo único que consiguió fue esa fea cicatriz.\n\nSi matas al oso dejarás a todo el mundo boquiabierto, incluido Daryl.\n\nEl oso se llama Ol' Sooty y vive en las colinas de Thelsamar.", progress = "¿Ya estás aquí, <nombre>? ¿Qué tal la caza? ¡No te preocupes si no ha ido bien, ya irás mejorando!\n\nTampoco es que pudieras hacerlo peor que ahora...", completion = "¿Qué... Qué es esto? Parece una cabeza de oso.\n\n&lt;Se toca la cicatriz sin darse cuenta.&gt;\n\nInteresante. Pero claro, no puede ser aquel oso...\n\n&lt;Se queda callado y empieza a temblar.&gt;\n\n¡Llévatela! ¡Fuera de mi vista!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[272] = {title = "La prueba del león marino", objective = "Busca el colgante de Agilidad acuática y el colgante de Resistencia acuática. Habla con la gente de Claro de la Luna para conseguir pistas sobre la ubicación de los colgantes. \n\nForma el colgante del león marino con los otros dos cuando estés cerca del Santuario de Remulos. \n\nLleva el colgante completo a Dendrite Rafagaestelar a Amparo de la Noche, en Claro de la Luna.", description = "En tu segunda prueba debes encontrar las dos mitades del colgante del león marino. Una obtiene su poder de la agilidad del león marino cuando se desliza en el agua, la otra lo obtiene de su increíble capacidad de resistencia. Los druidas se alimentan de ambos rasgos para asumir su forma acuática.\n\nHabla con la gente de Claro de la Luna para averiguar dónde están las mitades y tráemelas para que las unamos. Luego llevarás el colgante a Dendrite Rafagaestelar.", progress = "¿Traes el colgante, <nombre>? Si no encuentras las piezas, habla con la gente del lugar. Encontrarás lo que buscas y podrás explorar este lugar sagrado.", completion = "Enhorabuena por completar la Prueba del León Marino, &lt;nombre&gt;.\n\nHas aprendido que necesitas agilidad y resistencia para moverte en armonía en el agua, y que para ello debes abrazar el aspecto del león marino.\n\nEstas lecciones te serán muy útiles cuando ganes tu forma acuática.", rewards = "Recibirás:\n\n"},
[273] = {title = "Aprovisionar la excavación", objective = "Habla con Huldar.", description = "Pero aún hay tiempo, ayúdanos a buscar a Huldar. No puede estar muy lejos; aunque son fuertes, no creo que Miran y Saean puedan ir muy rápido cargando con esos barriles.\n\nEstán siguiendo la ruta normal, hacia el sur y siguiendo la curva de Loch Modan.\n\nSi todavía no han llegado, los encontrarás.", progress = nil, completion = "¡Malditos Hierro Negro...!\n\nY que Saean esté conchabado con ellos... Mira que hemos trabajado juntos un año y no me había percatado de nada.\n\nBah, qué más da.", rewards = "Recibirás:\n\n"},
[274] = {title = "Se aproxima una oscura amenaza", objective = "Ashlan Risapétrea quiere que informes de la naturaleza de los polvos al ingeniero jefe Hinderweir.", description = "Esto no es un explosivo normal y corriente. Mira estos cristalitos de plata. ¡Y este olor tan peculiar! Está clarísimo que esto es polvo de seforio. Es bastante inofensivo, pero si se moja, podría borrar Forjaz del mapa.\n\nPara neutralizar la reacción química necesito cuatro ingredientes: veneno de rondador, cristal de Mo'Grosh triturado, una lágrima de crocolisco y este coloide desactivador. ¡Avisa a Hinderweir antes de que sea tarde!", progress = "¿Qué te ha dado Ashlan para mí?", completion = "¿Un coloide desactivador para polvo de seforio?", rewards = "Recibirás:\n\n"},
[275] = {title = "Ampollas en la tierra", objective = "Mata 12 Trepadores del cubil y regresa con Rethiel el Guardaverde en Los Humedales.", description = "No sé la causa pero una auténtica podredumbre está creciendo en Los Humedales. Han aparecido Trepadores del cubil. Son burbujas enfermas y febriles en el país, y para detener la fiebre deben poder a asentarse de nuevo en los pantanos.\n\nLos Trepadores del cubil se arrastran por los canales de Los Humedales.", progress = nil, completion = "Tus esfuerzos han aliviado a esta tierra, recibirás algo muy provechoso.", rewards = "Podrás elegir una de estas recompensas:\n\nToga de guardia de pantanoCadena de bosqueFitohoja\n\nTambién recibirás:\n\nCadena de bosqueFitohojaToga de guardia de pantano"},
[276] = {title = "Patas sucias", objective = "Mata 15 gnolls Pellejomusgo y 10 bastardos Pellejomusgo y regresa con Rethiel el Guardaverde en Los Humedales.", description = "Los gnolls Pellejomusgo han vivido en paz en Los Humedales durante mucho tiempo, pero ahora su número está creciendo. Sus pies pisotean las flores y arbustos del pantano y sus hachas no dejan de talar árboles para alimentar sus fuegos.\n\nHan dejado de estar en equilibrio con la tierra. Para que sobrevivan Los Humedales, debemos reducir a los gnolls.\n\nMata a 15 gnolls Pellejomusgo y a 10 bastardos Pellejomusgo en el Sur, junto a la puerta enana de Algaz. Cuando lo hayas hecho vuelve aquí.", progress = "Aún tienes que completar tu misión, joven <clase>.", completion = "Al reducir el número de gnolls has cuidado de la salud de Los Humedales. Aunque mi fe en tu pueblo fue puesta a prueba en el pasado, &lt;raza&gt;, quizás ahora se recupere.", rewards = "Recibirás:\n\n"},
[277] = {title = "No al fuego", objective = "Lleva 9 pedernales primitivos a Rethiel el Guardaverde.", description = "Los Pellejomusgo están talando los árboles de Los Humedales, y utilizan su carne para darse calor. ¡No seguiré sufriendo este dolor!\n\nArrebata a los Pellejomusgo los pedernales primitivos que utilizan para hacer fuego. Todos los Pellejomusgo llevan esos pedernales primitivos, excepto los pequeños gnolls y bastardos Pellejomusgo.", progress = "¿Tienes las herramientas gnoll para hacer fuego?", completion = "... Gracias. Romperé estos pedernales y los lanzaré al mar para que nunca más puedan ser usados para prender fuego a madera viva.", rewards = "Recibirás:\n\nColmenilla esponjosa"},
[278] = {title = "Se aproxima una oscura amenaza", objective = "El ingeniero jefe Hinderweir quiere que le lleves veneno de rondador, 1 cristal de Mo'grosh y 1 lágrima de crocolisco.", description = "Ponte a buscar los ingredientes de inmediato, <nombre>. Puedes sacar el veneno de las arañas de Loch Modan. También encontrarás lágrimas de crocolisco allí. El cristal de Mo'Grosh ya será más difícil de conseguir. Los extraen los ogros del noreste, pero son muy raros de encontrar.\n\n¡Tenemos que prepararnos para rechazar un ataque de los Hierro Negro! Vuelve cuando tengas todos los ingredientes.", progress = "¿Tienes lo que te pedí? ¡Es muy urgente!", completion = "¡Justo a tiempo, &lt;nombre&gt;!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[279] = {title = "Zarpas de las profundidades", objective = "Mata a 12 murlocs Branquiazul.\n\nMata a Engullidor y coge su cabeza.\n\nLleva la cabeza de Engullidor a Karl Boran en Puerto de Menethil.", description = "Los murlocs están saliendo de las aguas profundas y construyendo sus poblados en la costa. Acosan a nuestros pescadores y mercaderes y hay que detenerlos.\n\nUno de esos murlocs, Engullidor, merodea el camino en compañía de otros murlocs Branquiazul y acosan a los mercaderes, refugiándose luego en la seguridad de las casuchas múrloc cercanas. Nuestros mercaderes están en peligro y pagaremos por su seguridad.\n\nEngullidor merodea la cala del río al norte de aquí. Da con él, mata a los suyos y tráeme su cabeza.", progress = "Nuestros mercaderes no estarán a salvo hasta que nos libremos de Engullidor y de esos murlocs.", completion = "Ah, bien hecho, &lt;nombre&gt;.\n\nAcepta esto como pago por haber cumplido con tu contrato con los marinos mercantes.", rewards = "Recibirás:\n\n"},
[280] = {title = "Se aproxima una oscura amenaza", objective = "El ingeniero jefe Hinderweir quiere que nades hasta la base de la presa, localices el barril de pólvora y disuelvas en él la mezcla neutralizadora para evitar que explote.", description = "Antes he visto a unos cuantos insurgentes Hierro Negro nadando hacia la Presa con un barril grande. ¡No había ningún montaraz para detenerlos! ¡Creo que han colocado el seforio en la base de la Presa y va a explotar de un momento a otro! Voy a combinar todos los ingredientes para crear la mezcla neutralizadora. Aquí tienes.\n\n¡Ahora tienes que verter la mezcla en el barril! ¡Date prisa!", progress = "La tapa del barril se desliza lentamente.", completion = "El barril crepita un poco al verter la mezcla neutralizadora.", rewards = "Recibirás:\n\n"},
[281] = {title = "Recuperación de bienes", objective = "Busca los cajones y barriles en los campamentos múrloc de la costa.", description = "No hace mucho los murlocs atacaron una nave de mercaderes, La princesa ciega. Aunque el barco escapó intacto, esas inmundas criaturas sacaron gran parte de su cargamento por la borda, incluida una valiosa estatuilla de Terenas Menethil. Queremos recuperar la estatuilla.\n\nRegistra las casuchas múrloc de la costa en busca de cajones, barriles o cualquier otra evidencia del cargamento perdido. Encuentra la estatuilla y vuelve a verme.", progress = nil, completion = "El cajón está cubierto de arañazos y está dañado por el agua. Cuando lo abres descubres paja empapada y podrida, como si hubiera habido algo de valor guardado en el cajón. Buscas por la paja...\n\n... y encuentras un puñado de botellas de color rojo.", rewards = "Recibirás:\n\nPoción de curación"},
[282] = {title = "Las observaciones de Senir", objective = "Habla con el montaraz Thalos.", description = "Tenía intención de enviar a mi aprendiz a que entregara a mi hermano Senir mi informe, pero por mi tranquilidad de espíritu... preferiría confiarlo a manos más seguras. Si es que no te importa, claro.\n\nMmm... tendrás que atravesar el túnel para llegar a Kharanos.\n\nHabla con el montaraz Thalos antes de adentrarte en el túnel. Ahora está infestado de troggs.\n\nSigue la carretera para regresar a Yunquemar y luego ve hacia el este hasta llegar al túnel. Thalos no andará lejos.", progress = "¡Saludos! Ten cuidado, <nombre>, el túnel de Dun Morogh está infestado de troggs. No es seguro atravesarlo.\n\nSi no tienes asuntos urgentes en Dun Morogh, te aconsejo que te quedes en Yunquemar hasta que el túnel sea más seguro.", completion = "Mm, bueno, si Barbablanca te ha mandado a Dun Morogh para asuntos importantes, yo no puedo detenerte, ¿no? \n\nDeja que te dé algún consejo e indicaciones.", rewards = "Recibirás:\n\n"},
[283] = {title = "Se aproxima una oscura amenaza", objective = "Parece que la mezcla neutralizadora funciona. Ve a darle la buena noticia al ingeniero jefe Hinderweir.", description = "El barril crepita.", progress = nil, completion = "¡Gracias, &lt;nombre&gt;! Sin tu ayuda, la presa habría saltado en pedazos. Gracias en nombre del Reino y de la Alianza, &lt;clase&gt;.\n\nEstamos a salvo... al menos de momento.", rewards = "Podrás elegir una de estas recompensas:\n\nCorta árboles enanaEspada espinada\n\nTambién recibirás:\n\nCorta árboles enanaEspada espinada"},
[284] = {title = "La búsqueda continúa", objective = "Busca la Estatuilla de Menethil.", description = "Registras a fondo el grupo de casuchas múrloc y no encuentras ni un rastro de la Estatuilla de Menethil.\n\nTal vez encuentres más pistas en las casuchas del Norte y del Noreste.", progress = nil, completion = "El barril no ha sido tocado en mucho tiempo. Lo abres...\n\n... y te abruma un fuerte olor a queso. Está un poco mojado, ¡pero aún está bueno!", rewards = "Recibirás:\n\nCerveza enana"},
[285] = {title = "Inspecciona más casuchas", objective = "Sigue buscando la Estatuilla de Menethil en las casuchas de los murlocs.", description = "Este grupo de casuchas de múrloc, tras inspeccionarlo a fondo, no revela nada.\n\nPe... pero estás <seguro/segura> de que la próxima pista que encuentres será muy fructífera.\n\n¡La Estatuilla de Menethil debe estar en alguna de las casuchas del Norte!", progress = nil, completion = "El barril está casi enterrado entre el barro y la hierba...", rewards = "Recibirás:\n\n"},
[286] = {title = "Devuelve la Estatuilla", objective = "Lleva la Estatuilla de Menethil a Karl Boran.", description = "Después de sacar el barril de toda la mugre y abrirlo, descubres una figurilla plateada empaquetada entre paja. Representa a un joven paladín de pelo largo, con un martillo alzado.\n\n¡Tiene que ser la Estatuilla de Menethil! Debes regresar con Karl Boran en el Puerto de Menethil.", progress = "¿Tienes la estatuilla?", completion = "El capitán de La princesa ciega se sentirá aliviado al saber que has recuperado su precioso cargamento. Te has ganado el pago por tus servicios.\n\nEs bueno encontrar a gente fiable como tú, especialmente en tiempos difíciles como estos.", rewards = "Podrás elegir una de estas recompensas:\n\nVara carámbanoBotas de marinero\n\nTambién recibirás:\n\nBotas de marineroVara carámbano"},
[287] = {title = "Poblado Peloescarcha", objective = "Explora el Poblado Peloescarcha y mata a 5 rebanacabezas Peloescarcha para Senir Barbablanca de Kharanos.", description = "Llevo mucho retraso con mi informe y me vendría bien tu ayuda, <nombre>. Conseguí averiguar dónde se esconden los trols, pero vi tantos de ellos que me dio miedo entrar.\n\nPor eso... necesito que entres en la cueva, eches un vistazo y mates a algunos de los trols. Luego vuelves aquí.\n\nSigue la carretera norte que sale de Kharanos, cuando llegues al puente, sigue el río helado hacia el oeste hasta que llegues al Lago Glacial. En la orilla oeste encontrarás Las Birras. La cueva está al suroeste del poblado.", progress = nil, completion = "¡Ah, &lt;nombre&gt;! Pensé que no tendrías problemas para encontrar el lugar, doy excelentes indicaciones, ya me entiendes...\n\nDeja que acabe mi informe.\n\n\n\n¡Ya está! Vaya, ja, ja, esto sí que es divertido... Supongo, &lt;nombre&gt;, que no te importará hacerme un último favor...", rewards = "Podrás elegir una de estas recompensas:\n\nToga de invierno cálidaRodela de piedra\n\nTambién recibirás:\n\nRodela de piedraToga de invierno cálida"},
[288] = {title = "La Tercera Flota", objective = "Compra un cántaro de hidromiel para el primer oficial Fistimos.", description = "En su día fui el primero de abordo del Dueña de las olas, una nave de alto bordo de la Tercera Flota de Kul Tiras. La flota maldita. Maldita, porque en su último viaje fue atacada por dragones rojos. ¡Dragones controlados por orcos!\n\nNo teníamos ninguna oportunidad contra el fuego de los dragones... y nuestra flota ardió y se hundió en esta costa. Soy el único superviviente de todas esas desgraciadas naves...\n\n... ¡Oh me va a estallar la cabeza! Tengo que beber algo fresco si quiero acabar con esta resaca y seguir con mi historia.", progress = "Oh, ¡tengo mucha sed! ¿No quieres comprarle algo de beber a este pobre y lamentable idiota?", completion = "¡Ah! Ah, me sienta de perlas y suelta la lengua. ¡Gracias colega!", rewards = "Recibirás:\n\n"},
[289] = {title = "La tripulación maldita", objective = "Mata 13 marineros malditos, 5 marinos malditos y al primer oficial Esnelim. Lleva la caja de tabaco de Enelim al primer oficial Fistimos en Puerto de Menethil.", description = "<Algo recuperado Fistimos sigue contando su historia...>\n\nSoy el único superviviente de la Tercera Flota de Kul Tiras, naufragada en la costa del Norte. ¡Pero aunque soy el único miembro de la flota que sigue con vida, no soy el único que sigue por aquí! Uno de nuestros barcos ocultaba un gran y terrible tesoro y su espeluznante poder tiene atrapadas las almas de mis hermanos entre los descompuestos cascos de los barcos.\n\nSi quieres liberar a algunos de mis hermanos tal vez puedas romper su maldición.", progress = "¡En mis sueños a veces escucho los gemidos de mis hermanos! Por favor señor, libéralos de sus ataduras y termina con sus lamentos.", completion = "Muchas gracias, &lt;nombre&gt;. Recibe también la gratitud de mis hermanos malditos.", rewards = "Recibirás:\n\n"},
[290] = {title = "Purga de la maldición", objective = "Obtén la caja de la caja fuerte de El intrépido del capitán Halyndor.\n\nUtiliza la llave para abrir la caja fuerte de El intrépido.", description = "Para romper la maldición y liberar a todos mis hermanos, hay que recuperar y purificar la... cosa... que guardaba mi barco. No sé lo que era, pero sé que estaba almacenada en la caja fuerte del barco. El capitán Halyndor era el único que tenía la llave.\n\nMi buen capitán sigue presente en mis sueños. Sé que sigue en el barco, El intrépido. Hazte con la llave y utilízala para abrir la caja fuerte de El intrépido.\n\nEntonces, cuando tengas esa maldita cosa, llévasela al sacerdote Glorin Frenteacerada en la ciudad.", progress = "Este cofre está bien cerrado.", completion = "La llave está oxidada pero entra perfectamente en el candado.", rewards = "Recibirás:\n\n"},
[291] = {title = "Los informes", objective = "Entrega el informe de Senir al senador Barin Rocarroja de Forjaz.", description = "¡Fantástico! Lleva mi informe al senador Barin Rocarroja. Es un tipo muy agrio; procura que no te contagie su desbordante alegría. Está en Forjaz, en la cámara en la que el rey Magni celebra sus audiencias.\n\n¿Que no sabes llegar a Forjaz? Sigue la carretera norte que sale de Kharanos, cruza el puente, sigue el camino del este que verás bordeado de estandartes, y que sube por la ladera de la montaña.\n\nPor cierto... ¿podrías no mencionar eso de que me has ayudado? No me gustaría que pensaran que no trabajo duro, ya me entiendes.", progress = "Vaya... Menos mal que dije a los guardias que no dejaran pasar mendigos... ¿Y bien? ¿Qué quieres? Habla rápido.", completion = "&lt;Examina rápidamente el informe.&gt; \n\n¡¿Autoridad del REY?! ¡Magni ha perdido totalmente la cabeza! Y ese maldito Barbablanca... Se ha encargado del asunto en persona, ¿eh? Al Senado no le gustará oír esto. Ni una pizca. \n\n¿Pero aún estás aquí? ¡Vete antes de que llame a la guardia!", rewards = "Recibirás:\n\n"},
[292] = {title = "El ojo de Paleth", objective = "Lleva el Ojo maldito de Paleth a Glorin Frenteacerada.", description = "Al abrir el cofre descubres un orbe ennegrecido, que parpadea con una luz espeluznante. Es el tesoro del que hablaba Fistimos: un Ojo maldito de Paleth.\n\nLleva el Ojo maldito de Paleth al sacerdote enano Glorin Frenteacerada, en el Puerto de Menethil. Pregúntale como eliminar la maldición del Ojo.", progress = "¿Te envía el borracho de Fistimos? Puedes decirle que no tengo más cerveza. ¡Tendrá que ir mendigarle a otro!", completion = "¿Un ojo de Paleth? ¡Fascinante! Umm... algo va mal con este. ¿Lo encontraste en uno de los restos de naufragio? ¡Eso lo explica todo! Pero... no sabía que tuvieran un ojo así en la Tercera Flota...\n\nNo me extraña que Fistimos estuviera tan asustado. Encontrarse con un Ojo maldito de Paleth no es ninguna alegría.", rewards = "Recibirás:\n\n"},
[293] = {title = "Limpieza del ojo", objective = "Lleva el Ojo maldito de Paleth al arzobispo Benedictus. Benedictus se encuentra en la Catedral de la Luz, en la ciudad de Ventormenta.", description = "No puedo borrar la maldición del ojo de Paleth. Los ojos fueron creados por sacerdotes humanos, así que tendrás que ir a Ventormenta para encontrar a alguien que pueda ayudarte. Habla con el Arzobispo Benedictus. Está al frente de la Catedral de la Luz de Ventormenta. El podrá ayudarte.\n\nEso espero, por tu bien.", progress = "¿Qué es ese orbe? Parece... dañado.", completion = "Un ojo de Paleth. Pero este ha visto demasiado dolor... debe haber sido testigo de una terrible, terrible tragedia.\n\nDéjame purificarlo, los ojos fueron creados para iluminar y sus poderes pueden ser muy peligrosos si están malditos como este.", rewards = "Recibirás:\n\nOjo de Paleth"},
[294] = {title = "La venganza de Ormer", objective = "Ormer Trenzaferro de la Excavación de Whelgar quiere que mates 10 estridadores jaspeados y 10 raptores jaspeados.", description = "La situación es grave, ciertamente. Cuando descubrimos estos huesos acudieron los raptores, atraídos por ellos. Esas bestias inmundas mataron a mis hermanos y nos atraparon a mí, a Merrin y al pobre prospector.\n\nLimpia Los Humedales de esos raptores, <nombre>. Encontrarás raptores jaspeados y estridadores jaspeados al oeste de este risco. Mata 10 ejemplares de cada, si puedes.\n\nSerá un buen comienzo para la venganza que he planeado.", progress = nil, completion = "Matar es un negocio sucio, pero has hecho muy bien tu trabajo, &lt;nombre&gt;. Si no tuviera que permanecer junto al prospector y a Merrin estaría a tu lado vertiendo sangre.", rewards = "Recibirás:\n\n"},
[295] = {title = "La venganza de Ormer", objective = "Ormer Trenzaferro quiere que mates 10 raptores segadores jaspeados y 10 raptores tajobuches jaspeados y que vuelvas a verle en la Excavación de Whelgar.", description = "Ha llegado el momento de que esos pavorosos raptores lamenten su sed de sangre. Aquí debajo podrás encontrar decenas de segadores jaspeados y tajobuches jaspeados. Haz pagar todas sus deudas a esas terribles criaturas matando a 10 de cada clase.", progress = nil, completion = "¡Hiciste un trabajo fenomenal eliminando a esos raptores, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[296] = {title = "La venganza de Ormer", objective = "Ormer Trenzaferro de la Excavación de Whelgar quiere que mates a Sarldente y que le lleves una de sus garras cuando completes la tarea.", description = "Mientras estabas ahí abajo pude observar que una de esas bestias destacaba sobre el resto. Era más grande y amenazadora. Apuesto a que es la que condujo hasta aquí al resto para atacar la excavación.\n\nTe pido una última tarea, <nombre>. Hay que hacer justicia con ese Sarldente, y de acuerdo a la gravedad de sus crímenes, justicia no significa otra cosa que pena de muerte. Tráeme una de sus garras como prueba de su muerte.", progress = "¿Está muerto Sarldente? ¿Has redimido la memoria de los caídos?", completion = "Gracias, &lt;nombre&gt;. No olvidaremos tus esfuerzos. Muchos enanos inocentes murieron en este lugar mientras se limitaban a hacer su trabajo. Has hecho que la venganza caiga sobre esas malvadas criaturas. Se lo pensarán dos veces antes de volver a atacar a los súbditos del rey Magni.", rewards = "Podrás elegir una de estas recompensas:\n\nFin de raptorArmadura AterraptorVara de excavación\n\nTambién recibirás:\n\nReceta: tortilla con un sabor curiosoFin de raptorArmadura AterraptorVara de excavación"},
[297] = {title = "Recoger ídolos", objective = "Lleva 8 ídolos de piedra tallada a Magmar Tallavil.", description = "Justo antes de que los trogg invadieran la excavación, habíamos descubierto una gran cantidad de ídolos tallados muy extraños. Pero no pudimos estudiarlos porque los trogg nos echaron de las ruinas. Parece que los ídolos tienen un efecto extraño en los trogg. ¡Se ponen como locos!\n\nTráeme ocho ídolos; quiero estudiarlos ¡y quiero quitárselos a los trogg! Los trogg que han invadido la excavación tienen los ídolos.", progress = "¿Me traes los ídolos? Tengo que estudiarlos y enviar un informe a Forjaz.", completion = "¡Buen trabajo, &lt;nombre&gt;!\n\nY no parece que te hayan machacado mucho... Tú prometes.", rewards = "Podrás elegir una de estas recompensas:\n\nPalo de fuego enanoRebanadora troggHacha de Thelsamar\n\nTambién recibirás:\n\nHacha de ThelsamarPalo de fuego enanoRebanadora trogg"},
[298] = {title = "Un informe sobre el avance de la excavación", objective = "Lleva el informe de Vetaferro a Jern Cornamenta a Thelsamar.", description = "Tengo que enviar un informe a Thelsamar. ¡Eso, e insistir en mi pedido de pólvora! Este es el informe. Llévaselo a Jern Cornamenta, es nuestro contacto en Thelsamar.\n\nNo es un encargo difícil, pero no tengo a nadie más para hacerlo.", progress = "¿Me traes el informe? ¡Excelente!", completion = "Me pregunto si Vetaferro quiere la pólvora para la excavación o para luchar contra los troggs...\n\n¡Ambos usos me parecen bien!\n\nLo raro es que le mandé un cargamento hace unas semanas... ¿Dónde habrá ido a parar?", rewards = "Recibirás:\n\n"},
[299] = {title = "Desvelando el pasado", objective = "El prospector Whelgar quiere que te adentres en la excavación en busca de los 4 fragmentos de tablilla perdidos: Ados, Modr, Golm y Neru.", description = "Justo antes de la invasión descubrí una tablilla, era la llamada piedra Goaz. El texto traducido se interrumpe en cuatro puntos. Se habla de un \"plan divino\" y de una \"profecía maldita.\"\n\nTemo que nos estemos quedando sin tiempo. Soy demasiado viejo para ir a enfrentarme con esas bestias. Pero tú eres fuerte.\n\nRegistra la excavación y encuentra los 4 fragmentos perdidos de la tablilla: Ados, Modr, Golm y Neru. Búscalos en artefactos antiguos o donde veas tierra suelta. ¡Tráemelos y podré empezar a descifrar este misterio!", progress = "¿Ya has descubierto los cuatro fragmentos, <nombre>? Ados, Modr, Golm y Neru...", completion = "¡Ah, la piedra Goaz está completa! Buen trabajo, &lt;nombre&gt;. Buen trabajo, sin duda. Ahora puedo seguir con la trascripción de este escrito de los Titanes. ¡Ya puedo descifrar nuevas palabras! Umm... aquí dice, \"Dioses antiguos... encadenados bajo la tierra.\" Esto debe ser el comienzo de algún relato épico.", rewards = "Recibirás:\n\nManto de Gamn de seda"},
[301] = {title = "Informe a Forjaz", objective = "Lleva el informe de Vetaferro al prospector Pico Tormenta.", description = "Necesitamos autorización para llevar más pólvora a Vetaferro. Lleva este informe a la oficina central de la Liga de Exploradores, en Forjaz.\n\nAquí tienes el informe. Dáselo al prospector Pico Tormenta y no olvides agacharte en cuanto lo hagas. Pico Tormenta tiene muy mal genio y no le va a gustar lo lento que va Vetaferro.\n\nLo encontrarás en la Asamblea de Exploradores.", progress = "A los Pico Tormenta no nos gusta perder el tiempo, <clase>. Espero que lo que tengas que decir merezca la pena.", completion = "¡Maldito sea Vetaferro! Le envié un cargamento de pólvora hace semanas y no lo menciona en el informe.\n\n¿Dónde está la pólvora?", rewards = "Recibirás:\n\n"},
[302] = {title = "Pólvora para Vetaferro", objective = "Habla con Jern Cornamenta en Thelsamar.", description = "¡Si Vetaferro necesita más pólvora, por Magni que la va a tener! Ya le he enviado la autorización a Jern Cornamenta a Thelsamar. Él preparará el envío para Vetaferro.\n\nY otra cosa: quiero que te ocupes personalmente de que la pólvora llega a su destino. El último envío no llegó, así que mantén los ojos abiertos.", progress = nil, completion = "¿Que Pico Tormenta quiere que te encargues tú del envío a Vetaferro?\n\nPor mí bien, pero hace apenas un rato que se lo llevó Huldar con los porteadores, Miran y Saean.", rewards = "Recibirás:\n\n"},
[303] = {title = "La guerra contra los Hierro Negro", objective = "Motley Garmason de Dun Modr quiere que mates 15 enanos Hierro Negro, 5 tuneladores Hierro Negro, 5 saboteadores Hierro Negro y 5 demoledores Hierro Negro.", description = "La guerra arde aquí en casa y en el extranjero. Mientras los grandes soldados enanos dan sus vidas en tierras lejanas en nombre de la Alianza, nuestras propias tierras caen víctimas de la depravación de los enanos Hierro Negro. Arrasaron el Puente Thandol y Dun Modr pronto caerá.\n\nTe necesitamos para reunir fuerzas y hacer retroceder el frente. Si puedes matar a 15 enanos Hierro Negro, 5 tuneladores, 5 saboteadores y 5 demoledores, tal vez la reserva consiga llegar a tiempo para terminar la ofensiva.\n\n¡Date prisa!", progress = nil, completion = "Cumples bien tus órdenes, &lt;clase&gt;. Has demostrado ser un valiente soldado y un leal servidor del rey Magni, ¡te rindo homenaje! Gracias a la presión que ejerciste sobre el campamento Hierro Negro tenemos ahora una oportunidad para expulsarlos definitivamente de nuestras tierras... si tan solo aparecieran las condenadas fuerzas de reserva...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[304] = {title = "Una misión hedionda", objective = "Mata a Balgaras el Hediondo y lleva su oreja a Largatrenza el Nefasto a las afueras de Dun Modr.", description = "Muchos de mis soldados murieron en la batalla de Dun Modr. Mi propio hermano murió cuando fue destruido el Paso Oeste del Puente Thandol. Sí señor, los enanos Hierro Negro son una raza diabólica. A partir de lo que Roggo pudo reunir, el líder de esta banda de matones en Los Humedales es un brujo llamado Balgaras el Hediondo. Pero es un auténtico cobarde. Envía a sus soldados para que cumplan sus órdenes y él se queda escondido, evitando el peligro.\n\nSi puedes encontrar a esa escoria escurridiza, mátalo y tráeme su oreja. Sabré recompensarte, <clase>.", progress = "¿Ya has localizado a Balgaras el Hediondo, <clase>? Se nos ha estado escapando demasiado tiempo.", completion = "Balgaras el Hediondo por fin está muerto. Aunque su muerte no devolverá la vida a todos los enanos inocentes que murieron innecesariamente a sus manos, ayudará a devolver la paz a los que sobrevivieron. Con Balgaras muerto, debería ser fácil acabar con el resto de escoria Hierro Negro. Ahora debemos exterminarlos antes de que sea enviado un nuevo líder. \n\nLo has hecho bien, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nEscudo de león de oroAnillo tranquilo\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nEscudo de león de oroAnillo tranquilo"},
[305] = {title = "La búsqueda del equipo de excavación", objective = "Tarrel Tejepiedra quiere que viajes a la Excavación y que contactes con Merrin Tejepiedra.", description = "¡Oh, debes ser <el rastreador enviado/la rastreadora enviada> por Largatrenza! No puedes imaginar lo preocupado que he estado.\n\nDurante un mes, mi mujer, Merrin, me ha estado enviando cartas cada semana. Forma parte del equipo de la Excavación de Whelgar, pero hace casi dos semanas que he dejado de recibir sus cartas. ¡Seguro que algo va mal!\n\nLargatrenza dijo que si no recibíamos noticias del equipo enviaría a un rastreador para comprobar cómo va todo. Supongo que tú eres ese explorador, <nombre>. ¡Busca a Merrin en la excavación, al Este!", progress = nil, completion = "Oh, ¡loados sean los cielos! ¡Estás aquí!", rewards = "Recibirás:\n\n"},
[306] = {title = "La búsqueda del equipo de excavación", objective = "Lleva a Tarrel Tejepiedra la nota de Merrin.", description = "¡Fue horrible! Estábamos desenterrando unos extraños artefactos cuando dimos con estos grandes huesos.\n\nAntes de poder saber qué sucedía, los raptores jaspeados inundaron la excavación. Se perdieron muchas vidas.\n\nOrmer... tan valiente... nos salvó al prospector y a mí. Nos defendió mientras corríamos a buscar refugio. Ahora estamos atrapados y todo nuestro trabajo está perdido.\n\nToma, lleva esta nota a mi querido Tarrel. En ella le explico cuál es nuestra situación.", progress = "¿Has podido encontrar a Merrin? ¿Ha enviado algún mensaje?", completion = "¡Qué noticias tan horribles! Al menos tres sobrevivieron. ¿Cuándo terminarán las muertes? Anhelo el día en el que los súbditos del rey Magni puedan vivir libres de miedo.", rewards = "Recibirás:\n\n"},
[307] = {title = "Zarpas asquerosas", objective = "Ve a la Mina de Fuenteplata y recoge 4 cargas de equipamiento de minero. \n\nLuego ve a ver al montaraz Pico Tormenta.", description = "La Mina de Fuenteplata, que cae al este, se agotó hace tiempo. La Liga de Mineros la usaba como almacén, pero ahora se han instalado unos kobolds allí, ¡con esas magníficas herramientas enanas a su alcance!\n\nLos expulsaremos, pero la Liga quiere que vaya alguien a recoger el material antes de que lleguemos los soldados a romperlo todo. Es muy arriesgado; será mejor que lleves compañía.\n\nEl material está en unas cajas con la marca de la Liga de Mineros. Suerte.", progress = "¿Traes el equipamiento de minero, <nombre>?", completion = "¿Había muchos kobolds? A ver si desaparecen pronto... Me pongo malo de pensar que andan por nuestra mina.", rewards = "Podrás elegir una de estas recompensas:\n\nCadena coraceroToga del VigilanteRodela de placas de hierro\n\nTambién recibirás:\n\nRodela de placas de hierroToga del VigilanteCadena coracero"},
[308] = {title = "Distraer a Jarven", objective = "¡Vaya! Con toda la bebida que hay aquí abajo... ¡Y tengo órdenes estrictas de no tocarla! Si tan solo pudiera probar un poco de nuestra Cerveza del Trueno... ¡agudiza el ingenio, y no es broma!", description = nil, progress = "¡Vaya! Con toda la bebida que hay aquí abajo... ¡Y tengo órdenes estrictas de no tocarla! Si tan solo pudiera probar un poco de nuestra Cerveza del Trueno... ¡agudiza el ingenio, y no es broma!", completion = "¿Es para mí? ¡Eres un héroe, &lt;nombre&gt;!", rewards = nil},
[309] = {title = "Proteger el envío", objective = "Asegúrate de que Miran y la pólvora llegan a la excavación y luego informa al prospector Vetaferro.", description = "¿Vamos, <nombre>?\n\nLo primero es llevarle la pólvora a Vetaferro. Es una carga muy pesada y esta zona es peligrosa. Además, quién sabe qué más me tienen preparado los Hierro Negro.\n\nMe tranquiliza bastante que vengas conmigo.", progress = nil, completion = "¡Por fin! Pero eso que los Hierro Negro tengan ayuda interna para conseguir material es muy inquietante.\n\nPero no soy yo quien debe ocuparse de eso; yo voy a ocuparme de la pólvora.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de supervisorVapuleador de barro\n\nTambién recibirás:\n\nCinturón de supervisorVapuleador de barro"},
[310] = {title = "Cerveza rivales", objective = "Ve al sótano de la Destilería Cebatruenos de Kharanos y sustituye un barril de cerveza del Trueno con uno de brebaje de Cebadiz.", description = "Tengo que reconocer que esos Cebatruenos preparan unas bebidas estupendas. ¡Pero hay que bajarles los humos! ¡La suya no es la única bebida decente del mundo! Quizás tú puedas ayudarme a darles una lección...\n\nTen, coge este barril de brebaje de Cebadiz. Cuélate en el sótano de la Destilería Cebatruenos de Kharanos y cámbialo por uno de sus barriles de Cerveza del Trueno. ¡Y ya veremos qué brebaje les gusta más a los clientes!\n\nY si encuentras guardias en Los Baldíos, intenta distraerlos...", progress = "La fecha del barril te indica que no tardará en ser abierto.", completion = "Colocas rápidamente el barril de Marleth de brebaje de Cebadiz entre los barriles de Cebatruenos.", rewards = "Recibirás:\n\n"},
[311] = {title = "Vuleve con Marleth", objective = "Lleva el barril de cerveza del Trueno a Marleth Cebadiz a Las Birras.", description = "Ya has dejado el barril del brebaje de Marleth en el almacén de la destilería de Kharanos y recoges un barril de Cerveza del Trueno como prueba de que has cumplido...", progress = "¿Diste el cambiazo?", completion = "¡No está mal, &lt;nombre&gt;! ¿Te costó mucho superar a su guardia?\n\nNo importa. Muchas gracias. Y oye, si alguna vez necesitas una buena cervezota, ¡no te olvides de los brebajes de Cebadiz!", rewards = "Recibirás:\n\n"},
[312] = {title = "La reserva robada de Tundra MacGrann", objective = "Ve a la cueva del Viejo Barbahielo y recupera la carne seca de Tundra MacGrann de la caja fuerte robada.", description = "Tenía carne seca para un mes, salada y bien guardada para la estación fría. ¡Estaba bajo llave para que los osos no se la comieran! Pero cuando salí a cazar ciervos, esa bestia, el Viejo Barbahielo, se las arregló para romper la cerradura. Eso sí, no podrá con el torio, seguro. Claro que yo me moriré de hambre si no recupero mi carne.\n\nSeguro que tú te mueves con más sigilo que yo. ¿No podrías ir a recuperar mi alijo? Con esta pierna coja no conseguiré entrar y salir de esa cueva con suficiente rapidez.", progress = "¿Conseguiste entrar en mi almacén de carne, <nombre>?", completion = "¡Qué amabilidad la tuya, &lt;nombre&gt;! ¡Y qué valor! Gracias a ti no me moriré de hambre cuando llegue la estación fría.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales de hierro forjadoMitones de lana\n\nTambién recibirás:\n\nBrazales de hierro forjadoMitones de lana"},
[313] = {title = "El Cubil Pardo", objective = "Reúne 8 crines de wendigo y llévaselas al piloto Roscapiedra.", description = "No todo el mundo puede conducir una máquina de asedio. Hay que tener brazos de hierro y nervios de acero. Yo, por suerte ¡tengo ambas cosas! ¿Y tú? ¿Te crees a la altura? ¿Quieres demostrármelo?\n\nBien... Estoy intentando hacer algo más cómoda mi máquina, Potenciatrol. ¿Qué tal si me consigues unas cuantas crines de wendigo de El Cubil Pardo, al oeste de Kharanos? ¡Serían una alfombrilla estupenda para mi Potenciatrol!", progress = "Eh, <nombre>, ¿ya has estado en El Cubil Pardo? Esos wendigos pueden llegar a ser muy feroces.", completion = "¡Ah, sí, estas crines me vendrán estupendamente! Tienes agallas, &lt;clase&gt;. Apuesto a que te veremos protagonizar numerosas hazañas.", rewards = "Recibirás:\n\n"},
[314] = {title = "La protección del rebaño", objective = "Rudra Cieloámbar quiere que mates a Vagash y que le lleves su colmillo al rancho de carneros.", description = "Oímos los gritos en medio de la noche. Y esta mañana faltan dos cabezas de ganado. Esa ruin bestia conocida como Vagash ha estado alimentándose de nuestro sustento. La mayor parte del ejército del rey Magni está en tierras lejanas, luchando con la Alianza. No queda nadie para mantener a raya a Vagash.\n\nQuizás tú tengas el valor necesario para buscar a ese animal y matarlo. Tráeme uno de sus colmillos y te daré una recompensa. Vagash acecha desde encima de este rancho. Ten cuidado, es peligroso.", progress = "¡Vagash sigue aterrorizando al ganado! Ayúdanos, por favor, ¡mata a esa maldita bestia!", completion = "¡Muy bien hecho! Veron se pondrá muy contento cuando oiga la noticia. Matar a Vagash no es tarea fácil. Imagino que un día combatirás en el frente de la Alianza, entre los hombres del rey el Magni.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones de rancheroTúnica de cuero suaveMartillo Crestanevada\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTúnica de cuero suaveMartillo CrestanevadaPantalones de ranchero"},
[315] = {title = "La cerveza perfecta", objective = "Trae 6 cardaluz a Rejold Cebadiz en Las Birras.", description = "Mi misión consiste en conseguir la cerveza perfecta. Sé que puedo hacerlo, la cebada está en mi sangre. Necesito encontrar la receta adecuada...\n\nLos trols Peloescarcha cultivan una planta llamada cardaluz, en las alturas del este. La utilizan para rituales extraños y tribales. Nosotros los enanos casi no la usamos, pero tiene un sabor único... y quisiera experimentar con él en mis cervezas.\n\nTráeme un poco de cardaluz de los profetas Peloescarcha o róbala de sus canastas.", progress = "¿Tienes la cardaluz ya? Ya casi tengo todo listo para la próxima cerveza y quisiera probar esa plantita en la mezcla.", completion = "¡Genial! Estoy deseando utilizarla en mis recetas. Estoy pensando en una en concreto a la que le irá de maravilla.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de pelaje de cabraBrazales tejidos con hiedra\n\nTambién recibirás:\n\nBrazales tejidos con hiedraCapa de pelaje de cabra"},
[317] = {title = "Los suministros del reactor a vapor", objective = "Reúne 4 piezas de carne de jabalí y 2 pieles gruesas de oso y llévaselas al piloto Bramiz al Almacén de Brasacerada.", description = "Me estoy preparando para empezar una misión para la Brigada de Asedio. Es una misión larga y tengo que cargar un mes de suministros en mi reactor a vapor. Así que, mientras Brasacerada trabaja en mi tanque, quizás podrías ir de caza por mí...\n\nNecesito pieles para hacerme una cama y carne para comer. Puedes cazar jabalíes para la carne y osos para las pieles... ve a los campos nevados que hay al sur de El Cubil Pardo.", progress = "Los preparativos van bien. ¿Cómo va tu caza?", completion = "Por la barba de Magni, ¡sí que has cazado! Gracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[318] = {title = "La tajada", objective = "Consigue un barril de tajada de Rejold Cebadiz de Las Birras.", description = "Rejold Barleybrew experimenta con sus brebajes. Algunos saben bien, otros mal y otros... bueno, esos tumbarían al enano más fuerte.\n\nVive en Las Birras, al oeste de Kharanos. Y lo que quiero es una de sus bebidas: tajada. Me mantendrá caliente en los caminos más fríos.\n\nY... ¡ese bendito líquido quema tanto que también puedo usarlo para aumentar la potencia de mi reactor a vapor! Me ha sacado de un apuro más de una vez.", progress = nil, completion = "¿Que Bramira necesita un poco de mi tajada? Mm... está bien.", rewards = "Recibirás:\n\n"},
[319] = {title = "Un favor a cambio de un barril de tajada", objective = "Mata a 6 osos zarpahelada, 8 jabalíes del risco mayores y 8 leopardos de la nieve y luego vuelve a Las Birras con Rejold Cebadiz.", description = "Tengo un barril de tajada, pero... ¿podrías hacerme un favor? En Las Birras vendría bien un poco de ayuda con los animales salvajes que deambulan por las nieves de los alrededores. A veces se acercan demasiado.\n\nNo es que tengamos miedo de los osos, los gatos ni los jabalíes... ningún enano que se precie lo tiene. Pero si tú pudieras ocuparte de esos animales, tendríamos más tiempo para trabajar y hacer cerveza.", progress = nil, completion = "Gracias, &lt;nombre&gt;. Será estupendo trabajar sin esos gritos y aullidos de fondo.", rewards = "Recibirás:\n\n"},
[320] = {title = "Vuelve con Bramiz", objective = "Entrega el barril de tajada al piloto Bramiz que está en el Almacén de Brasacerada.", description = "Aquí tienes tu barril de tajada, <nombre>. Y, una vez más, gracias por la caza.", progress = "¿Conseguiste esa tajada?", completion = "Vale, esta me vale. Creo que abriré una botella ahora mismo para probarla...", rewards = "Podrás elegir una de estas recompensas:\n\nHacha afiladaBastón nudoso cortoCuchillo de camping\n\nTambién recibirás:\n\nBastón nudoso cortoHacha afiladaCuchillo de camping"},
[321] = {title = "Hierro Forjaluz", objective = "Busca los restos del Pigargo volador en Los Humedales.", description = "Los restos de naufragios que puedes ver en nuestra costa son la Tercera Flota maldita de Kul Tiras, incendiada y hundida por los dragones rojos durante la última Gran Guerra.\n\nLa flota procedía de Lordaeron, pero un destructor élfico, el Pigargo volador, estaba con ella. Transportaba un cargamento de hierro Forjaluz y se unió a la Tercera Flota en busca de protección. Irónicamente lo hundieron los mismos dragones...\n\nPero basta de hablar. El Pigargo volador naufragó al sur del Puerto de Menethil. Si encuentras el hierro Forjaluz, tráemelo.", progress = nil, completion = "El cofre está empapado y el revestimiento está roto. El interior y el exterior del cofre están dañados con zarpazos... como si los murlocs hubieran estado rebuscando y hubieran robado su tesoro.\n\nPero escondido en el cofre descubres un Lingote Forjaluz. Por desgracia se necesitan muchos más para conseguir el metal necesario para forjar un arma.", rewards = "Recibirás:\n\nLingote Forjaluz"},
[322] = {title = "Brazo bendito", objective = "Lleva el cajón de lingotes Forjaluz a Grimand Elmore en Ventormenta.", description = "Hay un armero enano en el Distrito de los Enanos de Ventormenta que puede trabajar con este hierro Forjaluz. Su nombre es Grimand Elmore.\n\nHe empaquetado en un cajón el hierro que encontraste. Llévaselo a Grimand y cuéntale toda tu historia. Estoy seguro de que estará muy agradecido a alguien comprometido con una búsqueda como la tuya y te hará un arma... ¡un arma que haría temblar hasta al mismísimo Morbent Vil!", progress = "¿En qué te podrían servir mis conocimientos, <clase>?", completion = "¡Menuda historia la tuya! ¡Y tienes hierro Forjaluz! Hace años que no trabajo con ese metal. Será un honor trabajar con él otra vez. Vuelve a verme luego para contarme la historia de tu batalla contra el Necromántico.\n\nPero estamos yendo demasiado deprisa. Antes de nada, tu arma contra Morbent Vil...", rewards = "Recibirás:\n\n"},
[323] = {title = "Prueba de valor", objective = "Mata a 15 asaltantes esqueléticos, 3 sanadores esqueléticos y 3 guardas esqueléticos y vuelve a donde Sven.", description = "Morbent Vil es un Necromántico y un aliado de los Jinetes Oscuros. Te enfrentaría al poder de Morbent Vil, pero no te enviaré a luchar contra ese demonio sin conocer tu fuerza.\n\nYa me has probado tu valentía pero, si de verdad quieres enfrentarte al Necromántico, tendrás que probar primero que puedes derrotar a sus esbirros.", progress = nil, completion = "Tu habilidad en combate, &lt;nombre&gt;, es indiscutible. ¡Y tu resolución podría ayudarnos a presenciar el final de Morbent Vil!", rewards = "Recibirás:\n\n"},
[324] = {title = "Los lingotes perdidos", objective = "Reúne 5 lingotes Forjaluz y vuelve con Glorin Frenteacerada en Los Humedales.", description = "Después de sacar un único lingote Forjaluz del cofre anegado, está claro que los murlocs robaron el resto. Da caza a los asaltantes múrloc que rodean los restos del Pigargo volador y busca el resto del hierro Forjaluz perdido.", progress = "¿Has encontrado el hierro Forjaluz que necesitas?", completion = "¡Ah, los encontraste! ¡Vamos a fundir este hierro Forjaluz en algo útil!, ¿qué te parece?", rewards = "Recibirás:\n\n"},
[325] = {title = "Armado y listo", objective = "Lleva la Pesadilla de Morbent a Sven en el Bosque del Ocaso.", description = "Aquí tienes el arma para vencer al demonio ese del que me hablaste. Ya estás <preparado/preparada>, <nombre>. Te deseo mucha suerte, ¡y esperaré con ansia escuchar la leyenda de tu gloria!", progress = "Hola, <nombre>. No hace tanto que te fuiste y, sin embargo, veo un fuego en tus ojos que no había visto antes.", completion = "Ah, ¡esto será perfecto para el malvado Morbent Vil! ¡Bien hecho!\n\nYa estás preparado. Morbent Vil caerá.", rewards = "Recibirás:\n\n"},
[328] = {title = "La llave oculta", objective = "Utiliza la pequeña llave de latón para abrir la caja fuerte de Bookie Herod.", description = "Si bien en los registros no aparece ninguna mención al soldado Thorsen, escondida en la tapa de uno de los libros de contabilidad de Herod hay una pequeña llave de latón.\n\nY, tras estudiar en profundidad sus notas, aparecen algunas referencias entre varios papeles, a una \"caja fuerte segura\". La caja está en algún lugar de Reservas, el complejo de cuevas que está en la parte trasera de la Base de Kurzen.\n\nSi encontramos la caja fuerte y la pequeña llave de latón puede abrirla, quizás descubramos el misterio tras la detección de Thorsen.", progress = "La caja fuerte es de bronce. Muy sólida y pesada.", completion = "Queda claro que, la caja fuerte, es donde Herod guarda sus monedas y objetos más valiosos.", rewards = "Recibirás:\n\nCetro de corredor de apuestas"},
[329] = {title = "¡Espía descubierto!", objective = "Lleva la nota sucia al teniente Doren en el Asentamiento Rebelde.", description = "Entre los objetos de la caja fuerte hay una nota sucia llena de garabatos. Aunque casi es imposible de leer, parece ser un itinerario para el soldado Thorsen. Detalla sus rutas de patrulla, con quién habla y cómo pasa su tiempo con los rebeldes. Está firmada con una letra: M.\n\nPero quien fuera que escribiera esta nota debe de haber estado muy cerca de Thorsen. Quien escribió esta nota es el traidor del Asentamiento Rebelde.", progress = "¿Has descubierto quién sabía el secreto de Thorsen? ¿Hay un traidor entre nosotros?", completion = "Gracias, &lt;nombre&gt;. Esta nota es la pista que necesitábamos para dar con el traidor.", rewards = "Recibirás:\n\n"},
[330] = {title = "Horarios de patrulla", objective = "Habla con el cabo Sethman.", description = "Esa nota que me diste está firmada con una \"M\"... No sé lo que puede significar.\n\nPero... la nota también dice que esta persona compartió las rutas de patrulla con Thorsen la semana pasada. Así que, quien compartió las rutas de patrulla con Thorsen la semana pasada es el traidor.\n\nVete a ver al cabo Sethman. Se encarga de hacer los horarios de patrulla y podrá decirte quién estaba con Thorsen.", progress = nil, completion = "Ah, sí, claro que puedo comprobar eso. Veamos... la semana pasada era...\n\n...por qué..., el soldado Merle estaba de guardia con Thorsen. Tenían la misma hora de patrulla durante toda la semana pasada.", rewards = "Recibirás:\n\n"},
[331] = {title = "Informa a Doren", objective = "Habla con el teniente Doren.", description = "¿Te pidió el teniente Doren que volvieses a informarle sobre las patrullas?", progress = nil, completion = "Me duele descubrir que fue Merle quien nos traicionó. Siempre ha sido un buen soldado y yo creí en su lealtad. Pero me alivia haberle descubierto... con un espía como él entre nosotros... no habríamos durado mucho contra el coronel Kurzen.", rewards = "Podrás elegir una de estas recompensas:\n\nSandalias para la selvaEspaldares de metal escarchados\n\nTambién recibirás:\n\nEspaldares de metal escarchadosSandalias para la selva"},
[332] = {title = "Publicidad de la bodega", objective = "Ve a la Bodega Gallina y lleva a Suzetta Gallina el cupón de vino para una botella gratis.", description = "Si hablas sobre vino con alguien, sabrás que nosotros, en Gallinas, vendemos el mejor vino de Ventormenta. Y no estamos lejos, seguimos en el Distrito de Mercaderes, en los preciosos canales de la ciudad.\n\nToma, coge este panfleto. Llévaselo a mi hermana Suzetta a nuestra tienda, a cambio te dará una botella de regalo de nuestro famoso pinot noir. ¡No lo lamentarás!", progress = "Saludos y bienvenido, señor. ¿Has probado ya alguno de nuestros vinos?", completion = "¿Entonces has visto a Renato? Aquí estamos, caballero. Una botella de nuestro pinot noir especial. ¡No encontrarás nada mejor en todo Azeroth!", rewards = "Recibirás:\n\nBotella de Dalaran Noir"},
[333] = {title = "Harlan necesita un nuevo suministro", objective = "Ve a Trajes y Arreglos a Medida y lleva a Rema Schneider el paño que ha pedido Harlan Bagley.", description = "Últimamente el negocio ha ido bien. Parece que todo el mundo está comprando armaduras y ropas resistentes. Es casi como si esperasen una época dura y fría...\n\nPero esas son preocupaciones futuras. Mi preocupación del día es que me estoy quedando sin ropas tejidas que vender. Necesito otro cargamento de nuestro suministrador.\n\nSi puedes llevar este pedido a Rema Schneider en Trajes y Arreglos a Medida, te lo agradecería mucho.", progress = "¿Traes noticias de Bagley?", completion = "Dice aquí que el negocio de Harlan está floreciendo. Me alegra oír eso, pero me pregunto por qué la gente necesita tantas armaduras. No he oído nada sobre una declaración de guerra... ¿Acaso los nobles nos están ocultando información?\n\nEn fin, gracias por traer el pedido. Aquí tienes el pago, me encargaré de que Harlan reciba sus suministros.", rewards = "Recibirás:\n\n"},
[334] = {title = "Un paquete para Thurman", objective = "Ve a Diseños Larson en el Barrio de los Magos de Ventormenta y entrega a Thurman Schneider sus herramientas de coser.", description = "Mi hijo Thurman es un aprendiz en Diseños Larson, en el Barrio de los Magos. Hoy salió con prisa y se olvidó su trasquiladora y las agujas. Sé que un gran <clase> como tú debe de tener importantes tareas entre manos, ¡pero sin sus herramientas, Thurman no puede hacer su trabajo de aprendiz!\n\nPor favor, <nombre>. ¿Podrías llevar las herramientas de coser a mi hijo? Diseños Larson es una de las dos tiendas de ropa en el Barrio de los Magos, la que está más al fondo, cerca de la torre de los magos.", progress = "¿Has venido a comprar ropa?", completion = "¡Oh, maldición! Creí que al haberme olvidado las herramientas, no tendría que trabajar. Aunque supongo que ahora tendré que ayudar a los Larson cosiendo...\n\nEn fin... Supongo que tendré que esperar hasta más tarde para divertirme.", rewards = "Recibirás:\n\n"},
[335] = {title = "Una noble cerveza", objective = "Zardeth de los Zarpanegra, quiere que le lleves una lágrima de Tilloa de Villa Oscura y una zibética de Los Humedales.", description = "Lord Wishock está causando gran agitación en la Casa de Nobles. Se ha enterado de lo de nuestro pequeño \"establecimiento\" aquí abajo y está presionando para que se realice una investigación en toda regla. Tengo un plan para mantener a Wishock \"preocupado\", pero necesito tu ayuda.\n\nEn el Jardín de Stalvan, a las afueras de Villa Oscura, crece una flor. Esta pequeña flor blanca es conocida como la lágrima de Tilloa. También necesitaré una zibética, que solo se puede encontrar en la base de las cascadas bajo la Presa de Fraguapiedra, en Los Humedales.", progress = "Pronto, Wishock conseguirá suficientes refuerzos y suministros como para causarnos muchos problemas. ¿Has conseguido obtener la lágrima de Tilloa o la zibética ya?", completion = "¡Soberbio! ¡Por fin tengo lo que necesitaba!", rewards = "Recibirás:\n\n"},
[336] = {title = "Una noble cerveza", objective = "Lleva la cerveza Zarpanegra a Lord Wishock en la Sala de los Ruegos.", description = "Lord Wishock, como todos los hombres, tiene sus puntos débiles. Y, resulta que su punto más débil es el licor. La hipocresía es asombrosa. El pobre necio pasa sus días intentando cerrar nuestra pequeña \"taberna\" y las noches arriba, bebiendo y aumentando una cuenta que nunca paga. Bueno, veamos qué le parece a Wishock este brebaje especial que le he preparado: cerveza Zarpanegra. Es bastante fuerte, <nombre>.\n\nLlévasela y veamos si le gusta. Lo más probable es que el viejo tonto esté dando vueltas por la Sala de los Ruegos.", progress = "¿No ves que estoy con mucho lío? ¡Escucha mis palabras! ¡Soy muy importante para el futuro de este reino!\n\nOh... ¿Qué tienes ahí? Porque parece una bebida refrescante...", completion = "Ah, gracias por traerme esta estupenda cerveza negra, sirviente. Aquí tienes una moneda de cobre. Y ahora, ¡lárgate y déjame disfrutar mi bebida!", rewards = "Recibirás:\n\n"},
[337] = {title = "Un libro de Historia viejo", objective = "Devuelve el libro: La Historia de Ventormenta a La Biblioteca de Ventormenta.", description = "Aunque la cubierta de este libro está llena de arañazos y de barro, aún se pueden leer las palabras del lomo: La Historia de Ventormenta. Al abrir el libro se ve claramente el sello de la biblioteca de Ventormenta en la primera página.\n\nParece que alguien sacó este libro y no lo llegó a devolver...", progress = "¡Ooh! ¿Qué es ese libro viejo que tienes ahí? ¿Puedo verlo?", completion = "¡Ay, ay! Este libro no descansa en estos estantes desde antes de la reconstrucción de Ventormenta. Habrá que restaurarlo, claro, está en un estado lamentable, pero me honraría colocarlo entre los otros libros raros de nuestra colección.\n\nGracias, señor. ¡Acepta esto como pago por la devolución del libro!", rewards = "Recibirás:\n\n"},
[338] = {title = "Las verdes colinas de Tuercespina", objective = "Reúne las páginas perdidas del manuscrito Las verdes colinas de Tuercespina. Cuando los cuatro capítulos estén completos, llévaselos a Barnil.", description = "¡Oh, el maestro Decautela se sentirá muy disgustado si lo descubre!\n\nConfiaba en mí para revisar el manuscrito de su novela, Las verdes colinas de Tuercespina. Todo iba bien, pero un golpe de viento pasó por el campamento y las páginas quedaron dispersas por todas partes.\n\nConseguí reunir la mayor parte de las páginas, pero hay muchas todavía por la jungla. Reúne las páginas para completar los cuatro capítulos.\n\nYo te ayudaré a organizarlas. Devuélveme los capítulos cuando tengas los cuatro.", progress = "¿Tienes ya los capítulos, <nombre>?", completion = "Me has ahorrado muchos problemas, &lt;nombre&gt;. No puedo agradecértelo lo suficiente. No tardaremos mucho en ver el libro de Nesingwary en las bibliotecas de todo el mundo.", rewards = "Recibirás:\n\nBotas de la selvaComplemento grueso para armaduraPoción de curación excelente"},
[339] = {title = "Capítulo I", objective = "Lleva las páginas 1, 4, 6 y 8 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo I.", description = "¡El capítulo I es fascinante! El maestro Decautela establece el telón de fondo de la historia y menciona a sus compañeros de caza, ¡incluyéndome a mí! \n\nPero eso no viene al caso. Déjame ver las páginas que faltan. Parece ser que las páginas 1, 4, 6 y 8 siguen todavía por algún lado en la jungla.", progress = "¿Supongo que no has conseguido las páginas 1, 4, 6 y 8? Necesitamos esas cuatro para completar el capítulo I.", completion = "¡Al fin! El capítulo I está completo.", rewards = "Recibirás:\n\nLas verdes colinas de Tuercespina: Capítulo I"},
[340] = {title = "Capítulo II", objective = "Lleva las páginas 10, 11, 14 y 16 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo II.", description = "El capítulo II está lleno de historias excitantes sobre la caza de panteras y tigres. ¡Me patearía la cara por haber dejado que las páginas volaran! \n\n Parece ser que conseguí recuperar todas las páginas menos las 10, 11, 14, y 16.", progress = "Seguimos necesitando las páginas 10, 11, 14 y 16 para completar el capítulo II, <nombre>.", completion = "El capítulo II está completo. ¡Bien hecho!", rewards = "Recibirás:\n\nLas verdes colinas de Tuercespina: Capítulo II"},
[341] = {title = "Capítulo III", objective = "Lleva las páginas 18, 20, 21 y 24 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo III.", description = "El capítulo III es mi favorito. Habla de la primera caza de Sir Erlgadin y Lady Rouack con un rifle enano. Y, al final del capítulo III, ¡menudo suspense! \n\nMe parece que todavía necesitamos las páginas 18, 20, 21 y 24 para terminar el capítulo. Esas páginas están ahí fuera, en algún lugar...", progress = "¿Has tenido suerte con las páginas 18, 20, 21 o 24? Son las únicas que faltan para completar el capítulo III.", completion = "¡Puff! Buen trabajo, &lt;nombre&gt;. El capítulo III está completo.", rewards = "Recibirás:\n\nLas verdes colinas de Tuercespina: Capítulo III"},
[342] = {title = "Capítulo IV", objective = "Lleva las páginas 25, 26, y 27 de Las verdes colinas de Tuercespina de Decautela a Barnil Jarropetra para completar el capítulo IV.", description = "Ah, el capítulo IV, ¡el gran final! ¡Y menudo final! \n\nSolo nos faltan tres páginas de este capítulo. Necesitamos las páginas 25, 26 y 27 para finalizar el capítulo IV.", progress = "Solo necesitamos tres páginas para acabar el capítulo IV, <nombre>. ¿Tienes las páginas 25, 26 o 27?", completion = "¡Excelente! ¡Ya tenemos el capítulo IV!", rewards = "Recibirás:\n\nLas verdes colinas de Tuercespina: Capítulo IV"},
[343] = {title = "Hablando de fortaleza", objective = "Ve al Archivo Real en el Castillo de Ventormenta y habla con Milton Gavilla. Podrá encontrar el libro sobre metalurgia que el hermano Kristoff necesita para su charla.", description = "Voy a dar un discurso sobre la fortaleza de espíritu y me gustaría recitar unos pasajes de un libro sobre metalurgia, \"La tensión del hierro\". En el Archivo Real, en el Castillo de Ventormenta debería haber una copia del libro. ¿Podrías ir a buscarlo para mí? Si lo haces, ¡que las bendiciones de la Luz te acompañen!\n\nEl Archivo Real es enorme, pero el bibliotecario jefe, Milton Sheaf, conoce todas las estanterías como la palma de su mano. Habla con él, te dará el libro.", progress = nil, completion = "¿Metalurgia? Ah, sí, tenemos muchos libros sobre eso. Estoy encantado de poder ayudar al hermano Kristoff con su discurso. Y ahora... ¿Qué libro en particular necesita el buen hermano?", rewards = "Recibirás:\n\n"},
[344] = {title = "Hermano Paxton", objective = "Habla con el hermano Paxton en la Abadía de Villanorte.", description = "¿La tensión del hierro? Sí, conozco el libro. Es bastante bueno, lo escribió el minero enano Margulf Blaggon hace unas décadas. Pasó la mayor parte de su vida en las profundidades de Khaz Modan, extrayendo minerales. ¡Un tipo listo!\n\n¿Por dónde iba? ¡Oh, sí, La tensión del hierro! Me temo que el libro se llevó a la Abadía de Villanorte. El encargado de la biblioteca de la Abadía, el hermano Paxton, quería hacer una copia del libro para su biblioteca.\n\nPero eso fue hace meses. Me pregunto por qué estará tardando tanto...", progress = nil, completion = "¿Gavilla se pregunta dónde está su libro? ¡Qué vergüenza! Tengo el libro, pero sigo haciendo la copia y todavía no puedo darte el original.", rewards = "Recibirás:\n\n"},
[345] = {title = "Suministros de tinta", objective = "Habla con el supervisor Oslow en Villa del Lago y pregúntale sobre el mineral de hierro de Rethban.", description = "Quería terminar mi copia de La tensión del hierro, pero...\n\nBueno, verás, hago mi propia tinta para los libros que copio. Es mi propia receta. No se corre ni mancha y se seca muy rápido. Por desgracia, ¡se seca demasiado rápido! Lo última que me quedaba se secó antes de que pudiera acabar la copia y no he tenido oportunidad de hacer más tinta.\n\nPara hacer más, necesito mineral de hierro de Rethban, de las Montañas Crestagrana. El supervisor Oslow, en Villa del Lago, es quien me lo suele suministrar.", progress = nil, completion = "¡¿Que necesitas qué?! Por si no te has dado cuenta, estamos muy ocupados fortificando las defensas de Villa del Lago y reparando el daño sufrido por los ataques orcos. Si necesitas mineral de hierro de Rethban, ¡vetea buscarlo!", rewards = "Recibirás:\n\n"},
[346] = {title = "Vuelve con Kristoff", objective = "Vuelve a hablar con el hermano Kristoff en la Plaza de la Catedral. Entrégale el libro La tensión del hierro.", description = "Ahora que he terminado de copiar el libro, aquí tienes el original de La tensión del hierro. Gracias por tu paciencia y, si ves a Milton, por favor, dile que siento haberme retrasado tanto.", progress = "¿Has conseguido encontrar el libro?", completion = "¡Exacto! Gracias, &lt;nombre&gt;. Que la Luz brille sobre tu cuerpo y tu espíritu.", rewards = "Recibirás:\n\nBotas deambulantes"},
[347] = {title = "Mineral de Rethban", objective = "Reúne 5 montones de mineral de hierro de Rethban y llévaselos al hermano Paxton en la biblioteca de Villanorte.", description = "El mineral de Rethban tiene ese nombre por el hombre que lo descubrió, Bart Rethban. Cuando se funde, no es tan fuerte como el hierro, pero tiene un color oscuro que algunos escribas utilizan en sus tintes. Se puede encontrar en las Cavernas de Rethban, al norte de aquí.\n\nGeneralmente lo tenemos almacenado, pero estamos demasiado ocupados preparándonos para los atacantes orcos que no hemos tenido tiempo de recogerlo. Y, además, los gnolls de Crestagrana viven ahora en las Cavernas de Rethban y sus esclavos se encargan de recoger el mineral.\n\nAsí que si necesitas recogerlo, tendrás que ir a por él.", progress = "¿Tienes el mineral de hierro de Rethban?", completion = "¡Oh, excelente! Puedo usar este mineral para hacer la tinta y después terminar de copiar La tensión del hierro.\n\n¡Gracias, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[348] = {title = "La fiebre de Tuercespina", objective = "Busca al médico brujo Unbagwa y haz que invoque a Mokk el Salvaje. Lleva el corazón de Mokk a Fin Fizqueta.", description = "M... me... m... muero...\n\n... T... tengo la fiebre de Tuercespina... un virus m... mortal...\n\n... La única cura... es... comer el corazón de M... mokk el Salvaje... una esquiva bestia blanca...\n\n... El médico brujo Unbagwa... es el único que p... puede invocar...\n\n... Busca a Unbagwa en una cueva oculta en el cabo... del sur...\n\n... Rápido... m... me... muero...", progress = "... ¿Madre?\n\n... tanto frío...\n\n... hacia El Vacío Abismal... a-abrázame, madre...", completion = "... Siento que... la fuerza... vuelve a mí...\n\n¿Dónde estoy? ¿Bahía del Botín? ¡Cielos! He tenido un sueño de lo más extraño...\n\n... todavía me sigo sintiendo débil...", rewards = "Recibirás:\n\nManta medicinal"},
[349] = {title = "La fiebre de Tuercespina", objective = "temp text 02 - log", description = "temp text 02 - description", progress = "El médico brujo Unbagwa sabe lo que gustar a Mokk el Salvaje. ¡Sí, sí!\n\nYo hablar con espíritus y hacer que venga, sí, sí.\n\nPero haber otro espíritu como de gorila hablando también. Tú proteger al médico brujo Unbagwa y yo hacer que él venir.\n\nEl médico brujo Unbagwa quiere algo primero. ¡Trae 10 colmillos de gorila y yo empezar a hablar con espíritus!", completion = "¡Al médico brujo Unbagwa gustarle colmillos de gorila! Por ti yo hablar con espíritus. Haz que venga Mokk el Salvaje.\n\nPero tú proteger al médico brujo Unbagwa de otra bestia gorila, ¡o no hay Mokk!", rewards = nil},
[350] = {title = "Mira por un viejo amigo", objective = "Habla con Elling Trias en Ventormenta.", description = "Ese Marzon tenía que contactar con un miembro de los Defias y llegar a un acuerdo con Lescovar...\n\nCon VanCleef y Thredd muertos, no sería fácil llevar a Lescovar ante la justicia, ya que las pruebas no sirven de nada sin sus testimonios. Y eso, sin considerar que Lescovar es un noble, y está muy bien conectado. Efectivamente, eso hace que esté por encima de la ley.\n\nVas a necesitar más ayuda. Odio hacer esto, pero conozco a alguien que nos ayudará. Busca a Trias en el Distrito de Mercaderes de la ciudad.", progress = nil, completion = "¿Lo dices en serio? ¿Shaw quiere mi ayuda otra vez? Menuda desfachatez...\n\n¿La Hermandad Defias, dices? Bueno, ciertamente traes muy buenas noticias, ¿eh? Lo próximo que me vas a decir es que Alamuerte sigue con vida y está atacando la ciudad.\n\nMuy bien, cuéntamelo todo, desde el principio. Y no te dejes nada que creas que no es importante. Necesito saberlo todo.", rewards = "Recibirás:\n\n"},
[351] = {title = "¡Encontrar a OOX-17/TN!", objective = "Llévale el localizador de emergencia al robot mensajero de Oglethorpe.", description = "Has descubierto una especie de dispositivo metálico extraño con forma de huevo. Al tocar uno de sus botones el huevo cobra vida y descubres que se trata de algún tipo de artilugio robótico de invención gnómica. A continuación, una voz resuena en su interior.\n\n\"Mi nombre es Oglethorpe y he perdido a mi robot mensajero. Si lo encuentras te daré una recompensa. Por favor, llévale este localizador al robot. ¡Genial, acabo de obtener las coordenadas! ¡Parece que se estropeó cerca de Sima Abierta en Tanaris!\"", progress = "\"Parece que has descubierto los restos de... ¿un pollo robótico? La voz que proviene del huevo vuelve de nuevo a la vida:\n\n\"¡Sí, un trabajo excelente! Este es sin duda mi robot mensajero, aunque mis sensores indican que necesita un empujón para que pueda volar de regreso a Bahía del Botín para ser reparado. Coloca el localizador en el interior del robot. ¡El localizador hará todo lo demás!\"\"", completion = "Colocas el localizador de emergencia con forma de... huevo en el interior del... pollo robótico.\n\nMientras el robot lucha por ponerse en pie, se escuchan una serie de zumbidos provenientes de su interior. Cuando el robot cobra vida, se escucha una vez más la voz de Oglethorpe, en esta ocasión desde el interior del robot:\n\n\"OOX-17/TN funciona otra vez pero necesita ciertas reparaciones. Es demasiado pesado para que puedas traerlo... pero se me ocurre una idea. ¿Te apetece seguir cuidando de él?\"", rewards = "Recibirás:\n\n"},
[353] = {title = "Una entrega a Pico Tormenta", objective = "Entrega el paquete Pico Tormenta al montaraz Pico Tormenta en Loch Modan.", description = "Los Pico Tormenta son unos enanos muy respetados y famosos por su exigente gusto. Así que no es sorprendente que Gringer Pico Tormenta, un montaraz de Forjaz, me encargase que forjase un arma para él.\n\nHe terminado el arma, pero... el montaraz Pico Tormenta está muy lejos, en Loch Modan. Si tienes planeado viajar al norte, ¿podrías entregarle este paquete?\n\nEl último mensaje que recibí del montaraz Pico Tormenta, decía que estaba estacionado en la torre de vigilancia del norte, en Loch Modan.", progress = "¿Has venido hasta aquí desde Ventormenta? He oído que las cosas se están poniendo feas en las tierras humanas, con forajidos y orcos campando a sus anchas. ¡Un lugar perfecto para que un <clase> demuestre su valía!", completion = "¡Ajá! ¿Así que Grimand ha terminado mi hacha por fin? ¡Me muero por probarla con algunos troggs y kobolds!\n\nMuchas gracias, &lt;nombre&gt;. Has hecho un largo viaje para entregármela. Acepta unas monedas por tu trabajo.", rewards = "Recibirás:\n\n"},
[354] = {title = "Muertes en la familia", objective = "Llévale los restos de Gregor, los restos de Nissa y los restos de Thurman a Coleman Lontananza, que está en Rémol.", description = "Los Agamand eran una de las familias más prósperas de los Claros de Tirisfal. Yo solía trabajar en sus molinos... antes de la Peste.\n\nCuando la Plaga apareció por primera vez, los Agamand fortificaron su casa y convencieron a todos sus empleados para que se quedaran y les ayudaran a defenderse. Fuimos unos insensatos pero, al menos, fuimos unos insensatos leales.\n\nLos Agamand, a causa de su orgullo, nos abocaron a todos a la desgracia. ¡Y ahora son esbirros de la Plaga!\n\nSirve a los Renegados y derrota a los Agamand, que sucumbieron a la Peste. Sírveme a mí y tráeme sus restos.", progress = "¿Tienes los restos de la familia Agamand? ¿Has acabado al fin con esas malditas bestias?", completion = "La venganza es dulce, ¿no crees? Cuando acabaste con los Agamand, ¿eran conscientes de lo que estaba pasando? Eso espero. Espero que sintieran miedo antes de pasar a mejor vida.\n\nYa sé que es un deseo absurdo pero no puedo evitar pensar en ello.", rewards = "Recibirás:\n\n"},
[355] = {title = "Habla con Sevren", objective = "Habla con el magistrado Sevren de Rémol.", description = "Me has entregado los restos de los Agamand y con ello, has satisfecho mis deseos de venganza. Pero los Molinos de Agamand encierran una amenaza para todos los Renegados, no solo para mí.\n\nHe informado al magistrado Sevren acerca de tus hazañas contra los Agamand y desea hablar contigo.", progress = nil, completion = "Coleman habla maravillas de ti, &lt;nombre&gt;, sobre todo de tu exitosa incursión en los Molinos de Agamand.\n\nLos Renegados necesitan tu ayuda una vez más...", rewards = "Recibirás:\n\n"},
[356] = {title = "Patrulla de la retaguardia", objective = "Mata a 8 horrores sangrantes y a 8 espíritus deambulantes, y ve a informar a Linnea a su campamento.", description = "Los defensores de El Baluarte que protegen Tirisfal de las Tierras de la Peste están en alerta constante pero, aun así, en ocasiones la Plaga consigue colarse.\n\nNuestro éxito en El Baluarte depende de una batalla de un solo frente. No podemos, pues, permitir un ataque desde la retaguardia ni dejar que corten la línea de abastecimiento de El Baluarte.\n\nPresta ayuda en El Baluarte. Inspecciona en dirección este y mata a todo miembro de la Plaga que encuentres. Céntrate sobre todo en la Hacienda Balnir, situada al este, pues parece que se ha convertido en el refugio de numerosos miembros de la Plaga.", progress = nil, completion = "Muy bien. Con tus esfuerzos ayudas a mantener a raya a la Plaga. Enviaré una mención honorífica a mi superior, el ejecutor Zygand.", rewards = "Recibirás:\n\n"},
[357] = {title = "La identidad del exánime", objective = "Llévale el libro de hechizos del exánime a Bethor Duroyelo, que está en Entrañas.", description = "Hay un exánime que mora en la isla Lago Aguasclaras, en el norte. Aunque es un Renegado independiente, cree que los otros no-muertos son esclavos de la Plaga por lo que sus esbirros atacan a todo aquel que se acerca a él.\n\nPero, dado que es un Renegado, la Reina lo reclama. Y si tiene conocimientos de necromancia, estos serán... muy útiles para nosotros.\n\nPara descubrir su identidad y talentos, debo ver su libro de hechizos. Irrumpe en su campamento, róbalo y tráemelo aquí.", progress = "<nombre>, ¿has conseguido el libro?", completion = "Muy bien, &lt;nombre&gt;. Con este libro podré descubrir la identidad de ese exánime. Has servido con diligencia a la Reina.\n\n\n\n¡No puedo creerlo! ¡El libro pertenece nada más y nada menos que a Gunther Arcanos!\n\nEn vida, Gunther era un gran Necromántico. Ambos éramos compañeros de armas antes de la llegada de la Peste. A juzgar por lo que veo aquí, parece que tras su muerte se hizo todavía más poderoso.\n\n¿Sabes? Sus facultades nos vendrían muy bien.", rewards = "Recibirás:\n\n"},
[358] = {title = "Profanadores", objective = "Mata a los asaltatumbas Putrepellejo y a los bastardos Putrepellejo. \n\nLlévale 8 icores de embalsamar al magistrado Sevren de Rémol.", description = "Las fosas comunes, al suroeste de la Granja de Garren situada hacia el norte de aquí, se hicieron para albergar la impresionante cantidad de cadáveres de aquellos que perecieron en Tirisfal cuando la Peste golpeó por primera vez. \n\nDe estas tumbas no había salido ni un solo no-muerto hasta el momento, pero ahora la Plaga envía a gnolls Putrepellejo a recoger cadáveres para que engrosen las filas de sus ejércitos. ¡Esto es inaceptable!\n\nTienes una doble tarea que cumplir: mata a los Putrepellejo de las fosas comunes y la Granja de Garren, y quítales el icor de embalsamar que les da vida.", progress = "¿Has finalizado tu tarea? ¿Has destruido a esa especie de perros y obtenido su icor?", completion = "Muy bien hecho, &lt;nombre&gt;. La Plaga se equivoca si cree que podrá usar esos cuerpos en nuestra contra. Nuestros boticarios estudiarán el fluido que has recogido de los esclavos Putrepellejo pues es posible que albergue secretos que podamos utilizar contra ellos.\n\nComo ya he dicho, lo has hecho muy bien pero nuestra lucha continúa y con seguridad podrás seguir demostrando tu valía a los Renegados.", rewards = "Podrás elegir una de estas recompensas:\n\nGuanteletes de acero fríoBotas de piel de zombi\n\nTambién recibirás:\n\nBotas de piel de zombiGuanteletes de acero frío"},
[359] = {title = "Las obligaciones de los Renegados", objective = "Habla con la guardia de la muerte Linnea.", description = "Necesito conocer las últimas noticias de nuestro puesto del Guardia de la Muerte situado al este de aquí.\n\nTengo que saber si las fuerzas de la Plaga han pasado por El Baluarte y han entrado en Tirisfal. Debemos mantener la vigilancia en la frontera con las Tierras de la Peste pues no podemos permitir que la Plaga afiance su presencia aquí.\n\nViaja hacia el sur siguiendo el camino y cuando este se bifurque, continúa en dirección este hasta el puesto. Habla con la guardia de la muerte Linnea. Ella tiene la información que necesito.", progress = nil, completion = "Conque el magistrado Sevren quiere un informe, ¿eh? Muy bien: los defensores de El Baluarte están conteniendo a la mayor parte de los miembros de la Plaga aunque alguno siempre se las arregla para colarse.\n\nHemos detectado cierta actividad esporádica de la Plaga al este de aquí, especialmente importante en Hacienda Balnir.", rewards = "Recibirás:\n\n"},
[360] = {title = "Vuelve con el magistrado", objective = "Ve a ver de nuevo al magistrado Sevren de Rémol.", description = "Ahora llévale la información que te he dado al magistrado Sevren. Le gustará recibirla lo antes posible así que date prisa.\n\nY si ves a algún miembro de la Plaga por el camino, acaba con él. No podemos permitir que vaguen por nuestras tierras a su antojo.", progress = nil, completion = "Es preocupante saber que la Plaga consigue superar la seguridad de El Baluarte. El rey Exánime y sus esbirros no cesan en sus ataques.\n\nPero no nos rendiremos. ¡Nunca volverá a cogernos!", rewards = "Recibirás:\n\n"},
[361] = {title = "Una carta sin entregar", objective = "Encuentra a Yvette Lontananza y entrégale la carta de Thurman Agamand.", description = "Esta carta la escribió Thurman, el hijo mayor de Gregor Agamand. Está dirigida a una tal Yvette Lontananza, aunque parece que nunca llegó a recibirla...", progress = "¿Sí?", completion = "¡Oh... mi pobre Thurman! Le supliqué que abandonara Molinos de Agamand conmigo y mi padre pero su lealtad a la familia era demasiado fuerte. ¡Lo sabía! ¡Sabía que Brand quería hacerle daño! ¡Maldita sea la Peste y maldita sea la Plaga!\n\n&lt;Yvette se calma y se pone seria.&gt; \n\nPero lamentarse es de débiles. Como Renegada tengo nuevas metas y el amor no está entre ellas. En otro tiempo, te hubiera dado las gracias por haberme entregado esta carta y brindarme la oportunidad de saber qué había sido de mi antiguo amor.\n\nPero esa vida se acabó para siempre.", rewards = "Recibirás:\n\n"},
[362] = {title = "Los molinos fantasma", objective = "Acaba con Devlin Agamand y llévale los restos de Devlin a Coleman Lontananza, que está en Rémol.", description = "Devlin Agamand era el más joven de los dos hijos de la familia Agamand y en vida ambos no podían ser más diferentes. Thurman era alto y amable mientras que su hermano pequeño era débil y tenía la lengua muy afilada.\n\nCuando la Peste llegó a los Molinos de Agamand, no me sorprendió de que Devlin sucumbiera casi de inmediato. Sus desbarres todavía pueden oírse cerca del camino que conduce a los Molinos de Agamand.\n\nEstoy recogiendo lo que queda de los Agamand y quiero al pobre Devlin. Encuéntralo, destrúyelo y tráeme sus huesos.", progress = "¿Has encontrado a Devlin?", completion = "Gracias. Los huesos de Devlin quedarán de maravilla en mi manto.\n\nLo único que puede consolar a mi pobre corazón es ver el fin de los Agamand. Ellos nos fallaron a mi familia y a mí cuando llegó la Peste... ¡juro acabar con todos ellos!", rewards = "Recibirás:\n\n"},
[363] = {title = "Desvelo brutal", objective = "Habla con el sacerdote de las Sombras Sarvis.", description = "Ya era hora de que despertaras. Estábamos a punto de arrojarte al fuego con los otros y, mira por dónde, ya no tenemos que hacerlo.\n\nSoy Mordo, el custodio de la cripta de Camposanto. Y tú ya no eres uno de los esclavos del rey Exánime.\n\nHabla con el sacerdote de las Sombras Sarvis. Está en la capilla, al pie de la colina. Él te dirá lo que debes saber.", progress = nil, completion = "Mm, otro muerto que camina... Ha debido de ser un shock... despertarse en la cripta con el frío y Mordo como única compañía...\n\nVeo en tu cara que estás muy confuso. Deja que te explique nuestra... situación.\n\nHemos sido liberados del control del rey Exánime por nuestra nueva líder, Lady Sylvanas. La Dama Oscura nos guía en una guerra contra la odiada Plaga y contra los humanos que aún no aceptan la situación y nos acosan a cada paso.", rewards = "Recibirás:\n\n"},
[364] = {title = "Los Autómatas", objective = "El sacerdote de las Sombras Sarvis quiere que mates a 8 zombis descerebrados y a 8 zombis desdichados.", description = "Los Renegados estamos en guerra con el ejército del rey Exánime, la Plaga, un ejército de no-muertos alzados con necromancia, bestias inmundas del norte y espectros atormentados.\n\nLa parte norte del poblado ha sido invadida por los Autómatas. Deben ser destruidos. Acaba con ellos. Y no tengas piedad, aunque antes fueran nuestros hermanos y hermanas. Los Caídos no son más que esclavos del rey Exánime.", progress = nil, completion = "Es una pena que no podamos hacer entrar en razón a los de la Plaga. Nos vendrían bien para futuras batallas, son muy numerosos.\n\nPero no se unirán a nosotros, así que no tenemos más opción que destruirlos.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de lienzoCapa maltrecha\n\nTambién recibirás:\n\nGuantes de lienzoCapa maltrecha"},
[365] = {title = "Campos de pena", objective = "Roba 10 calabazas de la plantación que encontrarás en dirección oeste, justo al norte de Camposanto, y llévaselas al boticario Johaan a Rémol.", description = "¿Qué tenemos aquí? Pareces un poco <novato/novata>. Si quieres demostrar tu valía a la Dama Oscura, tendrás que aprender los caminos de los Renegados.\n\nEncontrarás una plantación en dirección oeste. Los humanos infestan la tierra al igual que el moho un cuerpo en descomposición. Y lo que es peor, la Cruzada Escarlata vigila la zona desde su torre. Dales una lección a esos canallas y roba 10 de sus preciadas calabazas.\n\nCuando tengas 10, llévaselas al boticario Johaan a Rémol.", progress = "El guardia de la muerte Simmer me dijo que me traerías los componentes que tanto anhelo. <nombre>, ¿has podido reunir 10 calabazas?", completion = "Has cumplido con tu cometido a la perfección, joven &lt;clase&gt;. Estás demostrando ser todo un hallazgo para el ejército de la Dama Oscura.", rewards = "Recibirás:\n\n"},
[366] = {title = "Devuelve el libro", objective = "Devuélvele el libro de hechizos de Gunther a su dueño, que se encuentra en la isla del Refugio de Gunther.", description = "Hace tiempo, Gunther y yo, junto con el ahora necromántico de la Plaga Thule Corvozarpa, fuimos amigos y estudiamos juntos en el Kirin Tor (una sociedad de magos de Dalaran).\n\nTanto Gunther como yo sucumbimos a la Peste pero creí que él estaría muerto o sirviendo al rey Exánime. ¡Es estupendo saber que está libre!\n\nTenemos que conseguir que se una a nosotros. He lanzado un hechizo sobre su libro de forma que, cuando lo vea, sepa que yo también estoy libre del rey Exánime. Cuando se entere de esto, espero que se una a nosotros.", progress = "¡Tus maestros están locos si piensan que volveré a ser un esclavo de la Plaga!", completion = "¡Mi libro! ¡Así que tú me lo robaste!\n\nPero... este hechizo... ¿¡Bethor!? ¿Está en Lordaeron?\n\nEso significa que quizás también haya escapado del rey Exánime...", rewards = "Recibirás:\n\n"},
[367] = {title = "Una nueva Peste", objective = "El boticario Johaan de Rémol quiere que recojas 5 viales de sangre de can oscuro.", description = "Lady Sylvanas ha recurrido a la Sociedad Real de Boticarios. La Dama Oscura cree que nuestro conocimiento, unido a la recién descubierta magia, proporcionará la clave para derrotar a Arthas. Nos ha instado a idear una nueva peste, una peste mucho más mortífera que cualquier otra dolencia conocida en Azeroth. Esta nueva enfermedad llevará a la ruina al ejército de la Plaga de Arthas.\n\nMis estudios revelan que la clave podría estar en la sangre de las bestias. Tráeme 5 viales de sangre de can oscuro para que pueda comprobar mi teoría.", progress = "<nombre>, ¿tienes ya 5 viales de sangre de can oscuro? ¡El tiempo se agota!", completion = "Lo has hecho muy bien, &lt;nombre&gt; y te agradezco tus esfuerzos.", rewards = "Recibirás:\n\nPoción de sangre trol débil"},
[368] = {title = "Una nueva Peste", objective = "El boticario Johaan de Rémol necesita 5 escamas de Anca Vil de los murlocs de los Claros de Tirisfal.", description = "Mientras recogías las muestras, me he dado cuenta gracias a mis experimentos de que serán necesarios más componentes para que esta nueva enfermedad se propague como es debido. Envenenar a unas cuantas víctimas es un juego de niños pero extender una plaga por todo un mundo es algo más complicado.\n\nNecesitaré 5 escamas de Anca Vil de los murlocs de la zona. Encontrarás a estas criaturas en la costa hacia el norte o el oeste.", progress = "<nombre>, ¿has conseguido 5 escamas de Anca Vil de los murlocs?", completion = "Las escamas son perfectas, &lt;nombre&gt;. Son justo lo que necesitaba para el brebaje.", rewards = "Recibirás:\n\nArena de sueño"},
[369] = {title = "Una nueva Peste", objective = "El boticario Johaan de Rémol quiere que le lleves 4 muestras de veneno de nocturácnida sanguinaria.", description = "Mientras recogías lo que te pedí, descubrí un viejo texto en uno de mis tomos que explica cómo una antigua peste arrasó con miles de víctimas inocentes. Más tarde se descubrió que el agente mortal de dicha peste estaba presente en el veneno de las nocturácnidas.\n\nTráeme un poco de veneno de una nocturácnida sanguinaria para completar este experimento. Quiero comprobar si el elemento contagioso presente en el veneno servirá para mi nuevo brebaje. Se dice que hay muchas arañas en los Claros de Tirisfal del Este.", progress = "<nombre>, ¿tienes ya el veneno de araña nocturácnida? Es el último componente que necesito para completar mi experimento.", completion = "Ah, este veneno será perfecto, &lt;nombre&gt;. Ya he añadido y cocido los demás ingredientes de mi brebaje. ¡Al fin puedo probar este nuevo agente mortal!", rewards = "Recibirás:\n\nFajín de aprendiz"},
[370] = {title = "En guerra con la Cruzada Escarlata", objective = "El ejecutor Zygand de Rémol quiere que mates al capitán Perrine, 3 integristas Escarlata y 3 misioneros Escarlata.", description = "\"Los documentos del ejecutor Arren contienen exactamente todos los detalles que necesitamos para erradicar la infestación humana de nuestras tierras. De acuerdo con esta información, la Cruzada Escarlata ha enviado al capitán Perrine y a una brigada a las ruinas de la torre situada al suroeste de Rémol.\n\nMata a Perrine, a 3 fanáticos y a 3 misioneros y regresa aquí.", progress = nil, completion = "La muerte del capitán Perrine en verdad complacerá a la Dama Oscura. Has hecho un buen trabajo, &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[371] = {title = "En guerra con la Cruzada Escarlata", objective = "El ejecutor Zygand de Rémol te ha encargado que mates al capitán Vachon y a 5 frailes Escarlata.", description = "Seguimos en guerra y la Cruzada Escarlata se hace cada vez más fuerte. El informe que el ejecutor Arren me ha enviado indica que la Cruzada Escarlata ha estado atacando desde las ruinas de la torre situada al sudeste de Tirisfal, cerca de la Hacienda Balnir, bajo las órdenes del capitán Vachon.\n\nMata a Vachon y a 5 frailes Escarlata. ¡Será un duro golpe para la Cruzada!", progress = nil, completion = "La muerte del capitán Vachon frenará algo el avance de la Cruzada Escarlata en Tirisfal, pero se avecinan otras amenazas.", rewards = "Recibirás:\n\n"},
[372] = {title = "En guerra con la Cruzada Escarlata", objective = "El ejecutor Zygand de Rémol quiere que asesines al capitán Melrache y a sus dos guardaespaldas.", description = "La Cruzada Escarlata ha estado atacando desde las ruinas de la torre situada al norte de Tirisfal, más allá Tumba de Faol. De acuerdo con los datos que tenemos, están bajo las órdenes del implacable capitán Melrache.\n\nTe voy a confiar una misión tan poco habitual como peligrosa. Mata a Melrache y a sus dos guardaespaldas en nombre de la Dama Oscura.", progress = nil, completion = "Lo has hecho muy, muy bien, &lt;nombre&gt;. Con luchadores despiadados como tú actuando en nombre de los Renegados, nuestra raza está cada vez más cerca de derrotar a Arthas de una vez por todas. Tengo un buen presentimiento respecto a ti, camarada.", rewards = "Podrás elegir una de estas recompensas:\n\nCuchillo ceremonialHachuela de artillero\n\nTambién recibirás:\n\nHachuela de artilleroCuchillo ceremonial"},
[373] = {title = "La carta sin enviar", objective = "Entrega la carta destinada al arquitecto jefe a Baros Alexston en Ventormenta.", description = "Registrando a Edwin VanCleef descubres, entre otras cosas, una carta sin enviar. Va dirigida a Baros Alexston, el arquitecto jefe de Ventormenta, Ayuntamiento, Plaza de la Catedral.\n\nParece ser que ha sido escrita y sellada recientemente.", progress = "¿Sí? ¿Puedo ayudarte en algo?", completion = "¿Edwin VanCleef, dices? Sería como recibir una carta de mi abuela muerta... ¿Así que le has matado? Perdona que te lo diga, pero me sorprende bastante. En su juventud, fue un luchador incomparable. Veamos por qué decidió escribirme después de tantos años.\n\n&lt;Baros lee detenidamente la carta.&gt;\n\nEdwin... Veo que los años no te han cambiado, sigues siendo un idealista y un romántico. No le importa hacer daño, &lt;nombre&gt;. La venganza le ha consumido. Aunque no estoy seguro de poder culparle por ello.", rewards = "Recibirás:\n\n"},
[374] = {title = "Prueba de la muerte", objective = "Llévale 10 anillos con la insignia escarlata al guardia de la muerte Burgess de Rémol.", description = "El ejecutor Zygand me dice que has estado matando a miembros de la Cruzada Escarlata. Los Claros de Tirisfal están plagados de ellos.\n\nVarimathras, teniente de la Dama Oscura, se está encargando personalmente de exterminar a la escoria de Azeroth: la raza de los humanos. Aquí en los Claros de Tirisfal nos enfrentamos a un nuevo género de humanos: la Cruzada Escarlata.\n\nCorre el riesgo y lanza una ofensiva contra la Cruzada Escarlata, <clase>. Tráeme 10 anillos con la insignia Escarlata como muestra de tu lealtad a Varimathras y a la Dama Oscura.", progress = "<clase>, ¿has reunido ya 10 anillos con la insignia escarlata?", completion = "Varimathras se sentiría orgulloso pues sirves con diligencia a tus líderes, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales de piel de zombiCinturón con brocheGuantes de red\n\nTambién recibirás:\n\nBrazales de piel de zombiCinturón con brocheGuantes de red"},
[375] = {title = "El frío de la muerte", objective = "Llévale 5 pieles de murciumbrío y algunos hilos gruesos a Gretchen Dedmar, que está en Rémol.", description = "Tengo tanto frío... La Peste de los no-muertos corre por mis venas como una serpiente de hielo. Pronto me embargará el estado de automatismo pero nada me impedirá que sirva a nuestra Dama Oscura. En su momento yo mismo cosí bolsas de cadáveres para los soldados caídos del poderoso ejército de Sylvanas.\n\nPero ahora mis manos tiemblan de frío. Si me trajeras 5 pieles de murciumbrío y un poco de hilo grueso, podría coser yo mismo una manta. Ayúdame, <nombre>, para que así pueda seguir sirviendo a la causa.", progress = "<nombre>, ¿tienes 5 pieles de murciumbrío y algunos hilos gruesos?", completion = "Aprecio tus esfuerzos, &lt;nombre&gt;. Ojalá Sylvanas reconozca algún día tu valentía.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de adeptoGuantes de costura\n\nTambién recibirás:\n\nCapa de adeptoGuantes de costura"},
[376] = {title = "Los Malditos", objective = "El novicio Elreth necesita 6 patas de carroñero y 6 alas de murciumbrío.", description = "Mis deberes son atender a nuestros guerreros heridos, suministrar ropa y armaduras y asistir al sacerdote de las Sombras Sarvis en lo que necesite.\n\nParece que tú también vas a acabar a su servicio... dando caza a los Autómatas. Seguro, sé cómo piensa. Si quieres seguir de una pieza, que seguro que sí, quizás pueda ayudarte. Me estoy quedando sin patas y alas. Si me traes algunas, te buscaré una armadura. Encontrarás muchos lobos y murciélagos hacia el sur.", progress = "Intenta ser prudente y no resultar herido hasta que te consiga una armadura.", completion = "Gracias, &lt;nombre&gt;. Esta armadura te servirá de ayuda.\n\nEspero que, al menos, te dé más suerte que a su último portador...", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales de lienzoCinturón de cuero viejo\n\nTambién recibirás:\n\nCinturón de cuero viejoBrazales de lienzo"},
[377] = {title = "Crimen y castigo", objective = "El consejero Tallolino de Villa Oscura quiere que le lleves la mano de Dextren Tutor.", description = "Como si no tuviéramos suficiente con la negligencia de los residentes del Bosque del Ocaso, ahora la Casa de Nobles escupe en el ojo al Consejo de Villa Oscura con su decisión de encarcelar a Dextren Tutor en Ventormenta en lugar de decapitarlo como establecía la sentencia de Lord Ebonlocke.\n\nTutor fue descubierto vendiendo cadáveres del cementerio a Morbent Vil, y ese es un delito que se castiga con la muerte. Y sin embargo, Ventormenta lo reclama como prisionero.\n\nAsesina a Tutor en las Mazmorras de Ventormenta. Tráeme su mano y yo te recompensaré.", progress = "Cuando se deja vivir a un profanador como Dextren Tutor, se traiciona a la justicia. Vuelve a mí cuando se haya cumplido la sentencia de muerte que Lord Ebonlocke dictó contra ese rapiñador de Tutor. Vamos a dar a las familias de los muertos la satisfacción que merecen y, además, enviaremos un claro mensaje a la Casa de Nobles de Ventormenta.", completion = "Así que finalmente, Dextren Tutor pagó por sus crímenes contra la humanidad... Eso sí que es hacer limpieza. ¡Y enhorabuena, amigo! No solo has dado a las familias de los muertos la tranquilidad de espíritu que merecen, además has enviado un contundente mensaje a esos burócratas corruptos de la Casa de Nobles. Ventormenta debe escuchar las necesidades de la gente del Bosque del Ocaso o nos independizaremos y nos liberaremos de su tiranía.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de embajadorLeotardos de malla Villa Oscura\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBotas de embajadorLeotardos de malla Villa Oscura"},
[378] = {title = "La furia mora en las profundidades", objective = "Motley Garmason quiere que le lleves la cabeza de Kam Furiahonda a Dun Modr.", description = "Has demostrado tu lealtad al rey Magni, <clase>. Y tu odio por la escoria Hierro Negro es tan grande como el mío.\n\nHay una tarea que me gustaría hacer por mí mismo, pero el honor me obliga a permanecer junto a Largatrenza. Roggo ha conseguido reunir datos que demuestran que Kam Furiahonda fue miembro de la conspiración para el ataque del Puente Thandol. Fueron los planes de Furiahonda los que hicieron perder a Largatrenza a toda su familia.\n\nFuriahonda está retenido, por razones políticas, en las Mazmorras de Ventormenta. Le quiero muerto, <nombre>. ¡Por Largatrenza! ¡Tráeme su cabeza!", progress = "No dejaré que la estúpida burocracia humana interfiera en los asuntos de los enanos, <nombre>. Se ha demostrado que Kam Furiahonda conspiró en el ataque al Puente Thandol. Las gentes del rey Magni perdieron sus vidas por las mentiras y la traición de Furiahonda. Puede que los humanos se hayan quedado satisfechos dejando que Furiahonda se pudra en Las Mazmorras, pero yo no dormiré tranquilo hasta que Furiahonda sea asesinado.", completion = "¿Así que Kam Furiahonda por fin supo lo que es mirar a la muerte a los ojos? Bien. Esa cobarde escoria Hierro Negro lo tenía bien merecido. Lo has hecho bien, &lt;clase&gt;. Las víctimas del ataque al Puente Thandol fueron unas víctimas más de un mundo torturado por la guerra y el caos. Sus familias tendrán la paz de saber que Furiahonda recibió el castigo que merecía. La muerte del hermano de Largatrenza ha sido vengada.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de ConfirmaciónAzota cabezas\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAzota cabezasCinturón de Confirmación"},
[379] = {title = "Aplacar esa sed", objective = "Llévale 5 bolsas de agua de los Deambuladores al ingeniero jefe Silvaina, que está en Gadgetzan.", description = "Si quieres una fuente de calor, yo puedo ayudarte a conseguirla. ¿Y sabes qué quiero yo? ¡Algo de beber! Tráeme cinco bolsas de agua de los Deambuladores y yo te daré la 4711-FTZ.\nYa solo te falta saber dónde puedes encontrar bolsas de agua de los Deambuladores. ¿Por qué no te diriges al este en busca de nómadas Deambuladores y les pides una? Ya me contarás cómo resulta.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nFuente de poder modelo 4711-FTZ"},
[380] = {title = "Hoya Nocturácnidas", objective = "El ejecutor Arren quiere que mates a 10 nocturácnidas jóvenes y a 8 nocturácnidas.", description = "Obtener los recursos naturales que necesitamos para sobrevivir es una de nuestras luchas más arduas. El oro escaseaba incluso en los tiempos en los que el poder de la Alianza estaba en su cumbre.\n\nHay una mina de oro al noroeste que ha sido invadida por arañas. Necesitamos el oro de esa mina, pero no podemos extraerlo mientras las arañas estén por ahí. No tengo mucho personal para la tarea, así que tendremos que encargarnos de ellas poco a poco.\n\nSube allí y a ver si puedes hacer algo por nosotros, <nombre>.", progress = nil, completion = "Mmm, bueno, es un comienzo. Llevará algunas semanas, o incluso meses, limpiar por completo el mal. Después de eso, tendremos que bajar ahí con algunas antorchas para quemar las telarañas.\n\nHas cumplido con tu deber, &lt;nombre&gt;, seguro que encontraré algo más de trabajo para ti.", rewards = "Podrás elegir una de estas recompensas:\n\nJubón de lienzoJubón de malla bastaLeotardos de piel de zombi\n\nTambién recibirás:\n\nJubón de lienzoLeotardos de piel de zombiJubón de malla basta"},
[381] = {title = "La Cruzada Escarlata", objective = "Lleva al ejecutor Arren 12 brazaletes Escarlata de convertidos Escarlata y de iniciados Escarlata.", description = "Te alegrará saber que estamos haciendo algunos progresos en la mina, gracias, en gran parte, a tus esfuerzos. Ahora podemos centrarnos en otros problemas.\n\nMis exploradores me han comunicado que un destacamento de la Cruzada Escarlata está estableciendo un campamento al sureste de aquí. La Cruzada Escarlata es una despreciable organización que nos da caza. No descansarán hasta que hayan destruido a todos los no-muertos, sean de la Plaga del rey Exánime o no. ¡Debemos atacar primero!\n\nTen cuidado, su impuro celo los convierte en adversarios muy peligrosos.", progress = "Esos insensatos... Se cavan tan contentos sus propias tumbas.", completion = "Ay, si se atuvieran a razones, ¿eh, &lt;nombre&gt;? Quizás podríamos sentarnos alrededor de una mesa para dialogar... ¡Ja!\n\nEsos esperpentos cegados por la Luz...", rewards = "Podrás elegir una de estas recompensas:\n\nDaga de los RenegadosGran maza de los RenegadosEspada corta de los RenegadosEspada bastarda de los Renegados\n\nTambién recibirás:\n\nAgua de manantial refrescanteEspada corta de los RenegadosDaga de los RenegadosGran maza de los RenegadosEspada bastarda de los Renegados"},
[382] = {title = "El mensajero rojo", objective = "Mata a Meven Korgal, el mensajero, en el campamento de los Cruzados y vuelve a comunicar al ejecutor Arren cualquier información que encuentres.", description = "Los informes que me envían mis superiores desde Rémol indican que un agente de la Cruzada Escarlata fue enviado desde el Monasterio Escarlata hasta el Camposanto. Sabemos que ese mensajero transporta una información muy importante. No tengo más detalles pero, lleve lo que lleve, ¡debemos conseguirlo!\n\nEres la persona más cualificada a la que puedo enviar, <nombre>. Encuentra a ese mensajero y tráeme cualquier información que encuentres.", progress = "¿Cómo va tu misión, <nombre>?", completion = "Mmm... Veamos lo que traes...\n\n&lt;Empieza a rebuscar entre los documentos que le has entregado.&gt;\n\nInformes de sus hazañas... contra nosotros... Esto no vale nada. Informes de rastreadores... Nuevas órdenes: \"Seguir construyendo un campamento cerca de la ciudad en ruinas...\" Nada que no supiéramos ya, la verdad... Espera, ¿qué es esto? ¡Un mapa con los destinos de algunos de sus comandantes de campo y agentes! Esto sí nos sirve.", rewards = "Podrás elegir una de estas recompensas:\n\nBastón de verdugoRodela de Guardia de la Muerte\n\nTambién recibirás:\n\nRodela de Guardia de la MuerteBastón de verdugo"},
[383] = {title = "Información decisiva", objective = "Ve a Rémol y entrega los documentos de la Cruzada Escarlata al ejecutor Zygand.", description = "Hay que hacer llegar esto a mi superior, el ejecutor Zygand, en Rémol. Con esta información podremos asestar un golpe definitivo a la Cruzada Escarlata. Has sido muy útil en Camposanto, pero necesito que alguien lleve esto a Zygand y además, creo que él podrá asignarte tareas más adecuadas.\n\nCuando salgas de Camposanto, sigue la carretera del norte. Poco después de entrar en Tirisfal, encontrarás un cruce. Toma el sendero del este y sigue en esa dirección. En tu camino pasarás por la Mansión Friogar.", progress = "¿Sí? ¿Qué quieres?", completion = "Esto está muy bien. Has hecho bien en conseguir esta información, &lt;nombre&gt;. Mmm... los nombres de los oficiales de los Claros de Tirisfal...\n\nImagina el golpe que podríamos asestar a la moral de esos malditos fanáticos si consiguiéramos matar a todos sus jefazos de un golpe.", rewards = "Recibirás:\n\n"},
[384] = {title = "Costillas de jabalí a la cerveza", objective = "Ragnar Cebatruenos de Kharanos quiere 6 costillas de jabalí del risco y una jarra de malta rapsódica.", description = "¡Nada les gusta más a mis clientes que unas ricas costillas de jabalí a la cerveza! El problema es que el trampero local que solía traerme los suministros, se ha alistado en el ejército del Rey para ayudar en el frente de la Alianza.\n\nQuizás puedas ayudarme tú... Si me traes seis costillas de jabalí montés y algo de malta rapsódica de la taberna, compartiré contigo una receta familiar: mis famosas costillas de jabalí a la cerveza. ¡Y te daré una muestra gratis! ¡El secreto está en la malta!", progress = "Voy a necesitar seis costillas de jabalíes del risco y una jarra de malta rapsódica, <nombre>.", completion = "La malta está lista y los jabalíes muertos\nY antes de acabar y de ponernos a hablar\nTendremos que luchar por ver quién empieza\n¡estas sabrosas costillas de jabalí a la cerveza!", rewards = "Recibirás:\n\nCostillas de jabalí a la cervezaReceta: costillas de jabalí a la cerveza"},
[385] = {title = "La caza de crocoliscos", objective = "Lleva 5 piezas de carne de crocolisco y 6 pieles de crocolisco a Marek Coracero, que está en la Cabaña del Errante.", description = "Vienen muchos cazadores a Loch Modan por los crocoliscos. Los mercaderes compran pieles de crocolisco para hacer ropa y armaduras, e incluso hay a quien le gusta su carne.\n\nHacemos negocio, pero no mucho, porque esos bichos son muy feroces y se esconden en las islas del lago. Pero tú no te prives, enfrentarse a esos dientes es toda una experiencia.\n\nMe acuerdo de una vez que...", progress = "¿Te he contado lo del croco que casi se me come la mano? Tenía unos dientes como puñales, así... Pero le bloqueé la mandíbula con el cuchillo. Creo que todavía lo tengo por aquí...", completion = "Buen trabajo, &lt;nombre&gt;. Esto es de excelente calidad.", rewards = "Recibirás:\n\nReceta: chuleta de crocoliscoManteo basto"},
[386] = {title = "Lo que sucece alrededor…", objective = "Lleva la cabeza de Targor el Horror al guardia Berton a Villa del Lago.", description = "Targorr el Pavoroso sirvió bajo las órdenes de Gath'llzogg como Ejecutor supremo. Sus métodos eran los más despiadados, incluso para los orcos. No dudó en torturar a hombres que habían servido lealmente en la defensa del Reino. Ahora se encuentra recluso en las Mazmorras de Ventormenta, a la espera de que se cumpla su sentencia de muerte. Aún así, hay algo que no encaja. Uno de los nobles burócratas ha detenido su ejecución. Estoy seguro de que se trata de una maniobra oscura.\n\nTienes que acabar con Targorr el Pavoroso, <nombre>. Viaja a las Mazmorras y decapítalo antes de que logre escapar a su destino.", progress = "¿Ya has estado en las Mazmorras de Ventormenta? Temo que el poder que ha mantenido a Targor con vida acabe por conseguir liberarlo. Fue sentenciado a muerte, no a ser un títere político.", completion = "Targor el Pavoroso ha caído al fin. Me alegro. Buen trabajo, &lt;nombre&gt;. A veces solo se puede hacer justicia al margen de los tribunales y los políticos.", rewards = "Podrás elegir una de estas recompensas:\n\nEspada larga LucineBastón de raíz endurecido\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBastón de raíz endurecidoEspada larga Lucine"},
[387] = {title = "Detener el motín", objective = "El alcaide Thelagua de Ventormenta quiere que mates 10 prisioneros Defias, 8 presidiarios Defias y 8 insurgentes Defias en las Mazmorras.", description = "¡Hay que detener el motín! Necesito tu ayuda. ¡Si alguien se entera de que la prisión es un caos perderé mi trabajo! Esto es juego sucio. Pero lo importante es obligar a los prisioneros a rendirse, por la seguridad de Ventormenta.\n\nEl castigo por sublevación es la muerte. Mata a alguno de esos desviados. Y verás cómo vuelven al buen camino...", progress = nil, completion = "Tus acciones en las Mazmorras fueron muy valientes, &lt;nombre&gt;. Es obvio que este problema nos sobrepasa. Pero lo has hecho muy bien y, por ello, te doy las gracias.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[388] = {title = "El color de la sangre", objective = "Nikova Raskol, de Ventormenta, quiere que consigas 10 pañuelos de lana roja.", description = "El resentimiento corre por mis venas como una corriente fría. Mi nieto, Mac, era un chico muy bueno. 14 años recién cumplidos. Cuando el ejército fue mandado al frente, Mac cogió las armas para defender la ciudad. Fue un guardia de las Mazmorras, donde está encerrada la banda Defias. Mac reprendió a algunos de los prisioneros por romper sus mantas de lana para hacer bufandas, un símbolo de la banda. Por hacerlo, recibió una puñalada en la espalda.\n\nQuiero venganza. Tráeme 10 de sus valiosos pañuelos de lana. Son del color de la sangre.", progress = "Veo que has vuelto, <clase>. ¿Has conseguido 10 pañuelos de lana roja de esa escoria Defias de las Mazmorras?", completion = "¿Y estos pañuelos, símbolos asquerosos de corrupción, son por lo que mi Mac tuvo que morir? Qué gran pérdida. Qué trágico sacrificio. \n\nPero en fin, no puedo vivir en el pasado. Has de saber, &lt;nombre&gt;, que tus actos han conseguido que se haga justicia con mi familia.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[389] = {title = "Bazil Thredd", objective = "Habla con el alcaide Thelagua en las Mazmorras.", description = "VanCleef y yo fuimos miembros de la Hermandad de los Albañiles. Nuestro primer proyecto después de la Guerra era reconstruir Ventormenta.\n\nCuando terminamos, nos estafaron. Los nobles se negaron a pagarnos. A algunos de los Albañiles mayores les ofrecieron trabajos gubernamentales, pero VanCleef y yo los rechazamos debido a nuestra lealtad con los Albañiles. Él lideró un motín y abandonó la ciudad, jurando venganza.\n\nEs posible que su teniente, Bazil Thredd, sepa más sobre los planes de VanCleef. Está prisionero en las Mazmorras.", progress = nil, completion = "En el nombre de la Luz, ¿qué quieres? ¿No ves que estamos en medio de una crisis?\n\n¡¿Bazil Thredd?! ¿Por qué quieres hablar con ese bastardo? ¿Cómo se supone que voy a saber que no eres uno de sus amigos que ha venido a ayudarle con este maldito motín? Si las malditas celdas no estuvieran abiertas, ¡te metería en una!", rewards = "Recibirás:\n\n"},
[391] = {title = "El motín de las Mazmorras", objective = "Mata a Bazil Thredd y lleva su cabeza al alcaide Thelagua en las Mazmorras.", description = "Dejemos esto claro, <nombre>. No confío en ti. Pero en estas circunstancias, tendré que pasarlo por alto.\n\nEsto es lo que vamos a hacer: baja ahí y habla con Thredd. Si realmente estás de nuestra parte, mátale y tráeme su cabeza como prueba. Si descubrimos que estás de su parte, cuando te encontremos... morirás junto con el resto de esos gusanos.", progress = "O me traes la cabeza de Thredd, o me quedo con la tuya, ¿lo entiendes, <nombre>?", completion = "Sin Thredd para liderarlos, esperemos poder controlar los motines. Ya veremos.\n\nDebo decirte que no esperaba que duraras más de media hora, pero parece que te juzgué mal.\n\n¿Supongo que no conseguiste sacarle mucha información útil, no? Pero sé un par de cosas que podrían interesarte sobre las actividades de Thredd.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[392] = {title = "Extraño visitante", objective = "Lleva la descripción sellada del visitante de Thredd al Baros Alexton en Ventormenta.", description = "Durante los primeros años del encarcelamiento de Thredd, nunca recibió visitas. Supuse que ya no resultaba útil para la Hermandad y que le habían abandonado.\n\nEn fin, hace unos meses, todo cambió. Comenzó a recibir visitas regularmente... una, o quizás dos veces por semana. Era un hombre extraño... un tipo tranquilo. Tenía sospechas, pero sus papeles estaban en regla.\n\nEn los documentos venía que su nombre era Maelik; esta es su descripción. No creo que venga a visitar más a Thredd.", progress = "¿Sí, <nombre>? ¿Qué has encontrado?", completion = "Mmm. ¿Maelik? No, no me suena el nombre, ni tampoco la descripción. ¡Pero podría ser cualquiera! Un antiguo Albañil, un miembro de la Hermandad Defias... Aunque si está tan bien conectado como sugiere Thelagua, quizás alguien le pueda reconocer. Deja que lo piense...", rewards = "Recibirás:\n\n"},
[393] = {title = "Sombras del pasado", objective = "Lleva la descripción sellada del visitante de Thredd al maestro Mathias Shaw en Ventormenta.", description = "No tengo ni idea, <nombre>. Hay alguien más que quizás pueda ayudarte y resulta que me debe un favor por... bueno, no hace falta entrar en detalles. En los cuarteles del Casco Antiguo, puedes encontrar al maestro Mathias Shaw del IS:7. Shaw está metido en la mayor parte de los negocios sucios o turbios de la ciudad.\n\nSi alguien puede saberlo, es él.", progress = "Saludos. ¿En qué puedo serte de ayuda, <clase>?", completion = "¿Qué? ¿Qué es esto? Sí, creo que sé quién es, pero no se llama Maelik. Los asesinos usan muchos nombres y muchas máscaras, cambiando de personalidad cada vez que lo necesitan. Ese hombre también utilizaba otro alias, Marzon; aunque tampoco creo que ese sea su verdadero nombre.\n\nNo importa, es un asesino y sé mucho sobre él.\n\nAhora mismo, Marzon trabaja para Lord Gregor Lescovar, un lord bastante sombrío, incluso para la norma de corrupción de la nobleza de Ventormenta.", rewards = "Recibirás:\n\n"},
[394] = {title = "La cabeza de la bestia", objective = "Habla con Mathias Shaw en Ventormenta.", description = "Encuentra a Shaw en el Casco Antiguo. Dile que nos hemos encargado de la amenaza de Lescovar y cuéntale lo de tu participación en los planes del señor contra la ciudad. Seguro que se sentirá orgulloso de tener a otro héroe entre los ciudadanos de Ventormenta.\n\nLo más probable es que te envíe a ver al príncipe o a otro noble. Pero que no se te suba a la cabeza, <nombre>. Los nobles pueden creer que gobiernan la ciudad, pero hay algunas cosas que se les escapan de las manos.", progress = nil, completion = "¿¡Está muerto!? Has administrado justicia rápidamente, &lt;nombre&gt;. Rezo a la Luz para no convertirme nunca en tu enemigo.\n\n\n\nNo es por criticar, &lt;nombre&gt;. ¿Cómo podría hacerlo siendo el maestro de todos los asesinos? Pero, a decir verdad, has hecho un gran servicio al pueblo de Ventormenta y, por eso, te doy las gracias.", rewards = "Recibirás:\n\n"},
[395] = {title = "El fin de la Hermandad", objective = "Habla con Baros Alexton en Ventormenta.", description = "¡Ah, casi lo olvidaba! Alexston vino cuando no estabas, quería hablar contigo. Sin duda quiere informar a los nobles de los tejemanejes de la Hermandad Defias.\n\nUn pequeño consejo, <nombre>: si alguien te pregunta, da todos los detalles, menos que fue tu mano la que mató a Lescovar. Confía en mí. Sé cómo van esas cosas...\n\nY, con respecto a Trias, bueno, si puedes evitarlo, nunca jamás hables de él con nadie.", progress = nil, completion = "Me alegro de verte, &lt;nombre&gt;. He oído que Lord Gregor Lescovar fue encontrado muerto no hace mucho tiempo.\n\nParece ser que has tenido éxito en tu misión. Tienes mi gratitud, nos has salvado a mí y a muchos más, de la venganza de VanCleef.", rewards = "Recibirás:\n\n"},
[396] = {title = "Una audiencia con el Rey", objective = "Lleva el informe de Baros Alexston sobre la Hermandad Defias al rey Varian Wrynn en el Castillo de Ventormenta.", description = "He preparado un informe para el rey. Creo que tú deberías entregárselo, junto con un resumen de tus descubrimientos. No te preocupes, sin duda te elogiará por hacer tu deber con el pueblo de Ventormenta.\n\nTu audiencia comenzará pronto, así que será mejor que te des prisa, todavía te queda un buen tramo hasta el Castillo de Ventormenta.", progress = "<nombre>, ¿sí? Baros Alexston me informó de tu llegada. Debo decir que me interesa mucho lo que tienes que decir.", completion = "Te estamos muy agradecidos por el servicio que has hecho a Ventormenta, &lt;nombre&gt;. Si VanCleef hubiese tenido éxito con sus maquinaciones, sin duda habría causado daño al reino... algo que, obviamente, queremos evitar a toda costa.\n\n&lt;clase&gt;, acepta esto como muestra del agradecimiento del rey. Siento que no te lo pueda dar él mismo, pero está fuera en una misión diplomática y no volverá en un tiempo.", rewards = "Recibirás:\n\nLacre de Wrynn"},
[397] = {title = "Nos has hecho un gran servicio", objective = "Habla con Zggi, el sirviente de Zardeth de los Zarpanegra, para pedir tu recompensa.", description = "He oído que a Lord Wishock le gustó mucho la bebida.\n\nNos has hecho un gran servicio, <nombre>. Los brujos de Ventormenta podremos seguir trabajando sin más interrupciones gracias a ti. Por tus esfuerzos, te he preparado un regalo especial. Habla con mi sirviente, Zggi, y pídele tu recompensa.", progress = nil, completion = "Maestro dice dar regalo a &lt;nombre&gt;. Zggi querer quedarse con bonito regalo, pero Zggi no ser tan tonto para traicionar al maestro.", rewards = "Recibirás:\n\nSombrero de mago terrorífico"},
[398] = {title = "Se busca: Ojo de gusano", objective = "Mata a Ojo de gusano y llévale su garra al ejecutor Zygand de Rémol para obtener una recompensa.", description = "Se busca: ¡Ojo de gusano!\n\nConsiderado una bestia incluso entre los propios gnolls, Ojo de gusano ha sido sentenciado a muerte por sus crímenes en contra de los Renegados. Este infame villano ha estado robando cuerpos y poniéndolos a disposición del ejército del rey Exánime. Ojo de gusano fue visto por última vez saqueando las tierras situadas en las proximidades de la Granja de Garren, al norte de Rémol.\n\nA partir de este momento, la ciudad de Rémol ofrece una recompensa al que sea capaz de llevar a cabo su ejecución. Muéstrale al ejecutor Zygand la garra del villano una vez ejecutada la sentencia.\n\nLa clemencia queda relegada a los débiles.", progress = "¿Sí?", completion = "Por fin los ruines actos de Ojo de gusano han encontrado su venganza. Esperemos que este acto de valentía por tu parte sea un mensaje claro para aquellos que desean traer el sufrimiento a nuestro pueblo. En nombre de la ciudad de Rémol te doy las gracias, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones de escamas de latónJubón de vástago\n\nTambién recibirás:\n\nJubón de vástagoPantalones de escamas de latón"},
[399] = {title = "Principios humildes", objective = "Ve a la casa de Baros Alexston en Páramos del Poniente y busca su brújula. A continuación, llévasela al Distrito de la Catedral, en Ventormenta.", description = "Parece que hace una eternidad desde que tan solo era un chico trabajando en la granja de Páramos de Poniente. Dicen que nunca puedes regresar, y es cierto. Doblemente cierto en mi caso, unos ladrones quemaron y tomaron mi casa.\n\nHe hablado con mi padre para descubrir el destino de alguna de mis posesiones, incluyendo mi primera brújula. No fue capaz de salvarlas. Sin embargo, también dice que deberían estar escondidas en la granja.\n\nEncontrarás la Hacienda de Alexston al oeste de la Colina del Centinela. ¿Quizás podrías ir y recuperarla?", progress = "¡<nombre>! ¿Has tenido suerte?", completion = "¡Oh, muchas gracias, &lt;nombre&gt;! No me sirve de nada, pero el valor sentimental... No hace falta que te dé las gracias por traerme esto... y veo que has tenido que sufrir algún que otro percance para conseguirlo, sin mencionar el tiempo que has perdido yendo a Páramos de Poniente. Tienes mi gratitud. Acepta esto como muestra de ello.", rewards = "Recibirás:\n\n"},
[400] = {title = "Herramientas para Brasacerada", objective = "Entrega las herramientas de Brasacerada a Beldin Brasacerada.", description = "Beldin Brasacerada es el dueño de la tienda de mecánica local y además es el mejor técnico de máquinas de asedio. Pero es implacable con sus herramientas. ¡Te aseguro que rompe más llaves de arclite de las que le podemos suministrar!\n\nAcabamos de completar su último pedido. Si se lo llevas, seguro que te compensa el esfuerzo.\n\nSu tienda, el Almacén de Brasacerada está al noreste de Kharanos. Y es un lugar en el que se reúnen los pilotos veteranos de máquinas de asedio, así que, <atento/atenta>, por si oyes algo interesante.", progress = "¿Mm? ¿No eres un poco joven para ser piloto de máquinas de asedio? Bueno, no importa. ¿Necesitas reparar algo?\n\nSi es así, coge número y ponte cómodo. Ahora mismo estoy con un par de motores y no tendré tiempo para nada más hasta dentro de unos días.\n\n¿O venías por otro motivo?", completion = "¿Tienes mis herramientas? ¡Bien hecho, muchacho! Acabo de romper mi último destornillador hace una hora y necesitaba otro para acabar de reparar la máquina de asedio del piloto Roscapiedra. Desde luego, me has hecho un favor enorme trayendo estas herramientas hasta aquí, &lt;nombre&gt;. \n\nTen unas monedas.", rewards = "Recibirás:\n\n"},
[401] = {title = "Espera a que Sirra acabe", objective = "Espera un momento y luego vuelve a hablar con Sirra Von'Indi.", description = "Casi he acabado de traducir esa nota. Vuelve en un rato, probablemente habré acabado.", progress = nil, completion = "Ya está. Está traducida...\n\n&lt;El aire es cada vez más pesado, como si una espesa y negra cortina se estuviera cerrando sobre el mundo...&gt;", rewards = "Recibirás:\n\n"},
[402] = {title = "Sirra está ocupada", objective = "Vuelve más tarde a ver si Sirra sigue aún ocupado.Lo siento pero, tal y como están las cosas, tengo otros asuntos que atender. Intentaré acabar la traducción más tarde... Si sobrevivimos.", description = "Lo siento pero, tal y como están las cosas, tengo otros asuntos que atender. Intentaré acabar la traducción más tarde... Si sobrevivimos.", progress = nil, completion = nil, rewards = nil},
[403] = {title = "El barril de Cebatruenos custodiado", objective = "Este barril está custodiado por Jarven Cebatruenos. Mientras esté en el sótano, nadie tocará los barriles.", description = nil, progress = nil, completion = nil, rewards = nil},
[404] = {title = "Una tarea pútrida", objective = "Llévale 7 garras pútridas al guardia de la muerte Dillinger de Rémol.", description = "La Plaga ha alcanzado los Claros de Tirisfal y ha infestado el área al oeste de Brill, cerca del puente viejo.\n\nDirígete allí y enfréntate a los muertos podridos. Trae sus garras pútridas y los Guardias de la Muerte te recompensarán.", progress = "¿Has acabado lo que te encomendé? ¿Ya tienes las garras pútridas?", completion = "Bien hecho. ¡Cómo me hubiera gustado presenciar el fin de esos no-muertos!", rewards = "Recibirás:\n\n"},
[405] = {title = "El exánime pródigo", objective = "Comunícale las órdenes de Sevren a Bethor Duroyelo, que está en Entrañas.", description = "Bethor Duroyelo, un poderoso mago de alto rango de Entrañas, que cae al sur, me ha pedido que le envíe a alguien capaz de hacer frente a la Plaga. Tú, <nombre>, eres <el elegido/la elegida>.\n\nHázselo saber a Bethor y él te explicará cuál es tu misión. Yo desconozco los detalles pero sé que tiene algo que ver con reclutar a un exánime, y parece peligroso.\n\nPrepárate, <clase>, pues no debes fallar.\n\nBusca a Bethor en el Barrio de la Magia de Entrañas.", progress = "Saludos. O no sabes quién soy o tienes algo urgente que tratar conmigo.\n\nEn caso de que ninguno de estos dos supuestos te atañan, desearás no haberme molestado.", completion = "Ah, si te envía el magistrado Sevren entonces tengo una tarea de suma importancia que confiarte.\n\nEspero que estés a la altura... aunque eso ya se verá.", rewards = "Recibirás:\n\n"},
[407] = {title = "Campos de pena", objective = "Llévale la calabaza envenenada al integrista Escarlata capturado que está encerrado en el sótano del Mesón La Horca.", description = "¿Simples e inofensivas calabazas, verdad? Eso es lo que deben parecer... Si queremos detener el avance de Arthas por el norte y la infestación humana por el sur, tenemos que comenzar a valorar nuestro propio potencial.\n\nCon un poco de ingenio, una simple calabaza se convierte en un arma de nuestra Dama Oscura. Solo hay que echarle un poco de mi última fórmula.\n\nHan capturado a otro integrista Escarlata y está encerrado en el sótano del Mesón La Horca. Llévale a ese desgraciado esta calabaza.", progress = "¡No te acerques a mí, inmunda e impura criatura! ¡Que la Luz me proteja! La Cruzada Escarlata te expulsará de Azeroth y... . \n\n... espera. ¿Me traes comida? Tengo tanta hambre…", completion = "¡Por la Luz! ¡Al fin algo de comida! Dulce, dulce calabaza…", rewards = "Recibirás:\n\n"},
[408] = {title = "La Cripta de la Familia", objective = "Mata a 8 ancestros de los Lamentos y a 8 ancianos putrefactos.\n\nMata al capitán Dargol y llévale su calavera al magistrado Sevren de Rémol.", description = "Los miembros de la Plaga han encontrado cobijo en los Molinos de Agamand. El punto más sólido es la Cripta de la Familia Agamand, lugar en el que su líder militar, un esqueleto llamado capitán Dargol, ha establecido su puesto de mando. Por medio de la necromancia, ha revivido a los ancestros de los Agamand y planea utilizarlos contra nosotros.\n\nEsto no puede ocurrir y tú has de evitarlo. Viaja a la Cripta de la Familia Agamand y derrota a los ancestros de los Agamand.\n\n¡Ah! Y tráeme la calavera del capitán Dargol.", progress = "El tiempo corre en nuestra contra, <nombre>. Cada hora que pasa, la Plaga afianza su posición en los Claros de Tirisfal.", completion = "Con tus acciones has asestado un duro golpe a la Plaga. Tu victoria no pasará inadvertida, ni para los Renegados ni para nuestros enemigos.\n\nSigue luchando como nosotros y quizás algún día consigamos expulsar al mismísimo rey Exánime de Azeroth.", rewards = "Podrás elegir una de estas recompensas:\n\nBastón LeñoscuroPartehuesos\n\nTambién recibirás:\n\nPartehuesosBastón Leñoscuro"},
[409] = {title = "Prueba de lealtad", objective = "Coge una vela de señalización.\n\nDespierta a Lillith Nefala y mátala.\n\nVuelve a ver a Gunther a su isla.", description = "Afirmas que no estás <sometido/sometida> a la voluntad del rey Exánime y el encantamiento que Bethor lanzó sobre mi libro lo corrobora. Sin embargo, para que pueda confiar en ti debes darme una prueba de que estás en contra de la Plaga.\n\nHay un viejo altar, a poca distancia de la costa sur de esta isla, que utiliza un poderoso agente de la Plaga llamado Lillith Nefara.\n\nCoge una vela de señalización de este cajón. Enciéndela en el altar y Lillith se despertará. Destruye a Lillith y creeré que en verdad eres <un enemigo/una enemiga> del rey Exánime.", progress = nil, completion = "Has derrotado a Lillith Nefara, lo que demuestra que eres enemigo de la Plaga. Es increíble.\n\nHubiera jurado que era el único no-muerto con voluntad propia pero me has demostrado que también hay otros libres de la dominación del rey Exánime.", rewards = "Recibirás:\n\n"},
[410] = {title = "Sombra durmiente", objective = "Esta vieja mesa está hecha polvo.", description = nil, progress = "Esta vieja mesa está hecha polvo.", completion = "Colocas la vela de señalización sobre la mesa y la enciendes...", rewards = nil},
[411] = {title = "El regreso del exánime pródigo", objective = "Llévale la gema infernal a Bethor Duroyelo, que está en Entrañas.", description = "Dale esta gema infernal a mi viejo camarada Bethor. A través de ella podremos comunicarnos... y en verdad tengo mucho que contarle.", progress = "¿Le has devuelto a Gunther su libro? ¿Ha reaccionado?", completion = "¡Has conseguido que se una a nuestra causa! Has hecho algo estupendo por los Renegados, &lt;nombre&gt;. La Dama Oscura conocerá tu hazaña.", rewards = "Recibirás:\n\nRodela de huesos"},
[412] = {title = "Operación Recombobulador", objective = "Lleva a Razzle Energiodentado a Kharanos 8 levas estabilizadoras y 8 engranajes giromecánicos.", description = "Tras analizar bien la situación de Gnomeregan, parecía que no solo no habíamos logrado erradicar a los troggs sino que además, habíamos transformado a la mayor parte de los gnomos en unos maléficos leprosos sin conciencia.\n\nOzzie y yo planeamos invertir ese horrible efecto con nuestro último invento: el Recombobulador. La máquina está casi acabada, pero necesitamos desesperadamente algunas levas estabilizadoras y engranajes giromecánicos. Ve a Gnomeregan, trata con los gnomos leprosos y consíguenos algunos.", progress = "El recombobulador estará listo en cuanto tengamos suficientes levas estabilizadoras y engranajes giromecánicos.", completion = "Estos engranajes giromecánicos y estas levas estabilizadoras cumplen todos los requisitos del esquema de Ozzie para el dispositivo de recombobulación. Cuando añada algo de hidrolubricante a los pistones de combustión, ajuste la manivela y aumente la viscosidad del gel electrogómico, la raza gnoma volverá a ser lo que era.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de buceoCapa manchada de aceite\n\nTambién recibirás:\n\nGuantes de buceoCapa manchada de aceite"},
[413] = {title = "Cerveza negra Brillante", objective = "Lleva el barril de cerveza negra Brillante al montaraz Cebadiz.", description = "Me gustaría que mi hermano Wellart probara mi nueva cerveza negra Brillante. Es un montaraz. Está destacado en la torre de vigilancia del sur, en la frontera del Loch Modan.\n\nNo se dedica a hacer cerveza, como los demás Barleybrew, pero le encanta beberla y sé que esta le gustará.\n\n¿Podrías llevarle este barril de cerveza negra Brillante de mi parte?", progress = "¡Hola, <clase>! ¿Qué te trae hasta aquí?\n\nAlgo emocionante, espero. Hace días que no consigo participar en una buena batalla y se me está acabando este barril de cerveza...", completion = "&lt;El montaraz Cebadiz prueba la cerveza negra Brillante...&gt; \n\nOh. ¡VAYA! Esta cosa le da como un brillo nuevo a la vista. ¡Creo que puedo ver en la oscuridad!", rewards = "Recibirás:\n\n"},
[414] = {title = "Cerveza negra para Kadrell", objective = "Lleva el barril de cerveza negra Brillante al montaraz Kadrell.", description = "He estado bebiendo la cerveza negra Brillante esa que hizo mi hermano. ¡Está buenísima! Quiero que la pruebe un compañero mío. Se llama Kadrell y normalmente patrulla por el camino de Thelsamar en Loch Modan.\n\nPara llegar a Thelsamar, ve al sureste a través del túnel y gira al noreste cuando el camino se divida. Sigue en esa dirección y llegarás <derecho/derecha> a Thelsamar.\n\nDale este barril de cerveza negra Brillante al montaraz Kadrell. ¡A ver qué le parece!", progress = "Saludos, <clase>. ¡Bienvenido a Thelsamar!", completion = "A ver, deja que pruebe... \n\n¡Por la barba de Magni! ¡Esta cosa te hace girar la cabeza! Y oye, te hace sentir como más ligero... \n\n¡Seguro que puedo volar!", rewards = "Recibirás:\n\n"},
[415] = {title = "La nueva bebida de Rejold", objective = "Habla con Rejold Cebadiz.", description = "Rejold Barleybrew de Las Birras ha estado preguntando por ti.\n\nQuiere hablar contigo sobre una nueva bebida que ha estado preparando... dijo algo de que quería que le ayudaras. Quizás quieras ir a Las Birras a ver qué necesita de ti.\n\nPodrías ganarte una cervecita...", progress = nil, completion = "¡&lt;nombre&gt;! ¿Recuerdas la cerveza de cardaluz que me trajiste? Pues la utilicé en algunas recetas en las que estoy trabajando y una de ellas me salió mejor que genial.\n\nMira, prueba y dime qué opinas.", rewards = "Recibirás:\n\nJarra de cerveza brillante"},
[416] = {title = "Mata ratas", objective = "Lleva 12 orejas de kobold al montaraz Kadrell a Thelsamar.", description = "Tenemos una infestación grave al oeste. Esos kobolds asquerosos se han instalado en las laderas. ¡Están cavando madrigueras y ensuciándolo todo!\n\nQueremos erradicarlos. Si los cazas y me traes sus orejas, puedes quedarte con su botín.\n\nLos encontrarás por toda la ladera al oeste de Thelsamar.", progress = "Ehl, <nombre>, ¿me traes las orejas?", completion = "¡Puaj, qué peste!\n\nAquí tienes la recompensa, &lt;nombre&gt;. Buen trabajo.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales de pellejo quemadosBotas de hidalgo\n\nTambién recibirás:\n\nBotas de hidalgoBrazales de pellejo quemados"},
[417] = {title = "Vengar al piloto", objective = "Mata a Zarpasarnosa. \n\nLleva su zarpa sarnosa y el diario de Hildelve al piloto Podomartillo.", description = "Hildelve escribió en su diario que fue atacado por Zarpasarnosa, un gran oso zarpahelada. Ahuyentó a al bestia, pero no antes de que esta lo atacara salvajemente.\n\nA juzgar por el estado del cuerpo de Hildelve, Zarpasarnosa debió de regresar para matarlo.\n\nPero Hildelve tuvo tiempo de escribir un último deseo en su diario:\n\nZarpasarnosa debe pagar por esto.", progress = "Hola, <nombre>. ¿Tienes noticias de mi amigo Hildelve?", completion = "Estas son malas noticias: Hildelve era un buen amigo. Me habría gustado estar allí cuando encontraste a la bestia: ¡Sarnagarra!\n\nGracias, &lt;nombre&gt;. Los pilotos de la Brigada de asedio de Forjaz recordarán tu hazaña.", rewards = "Podrás elegir una de estas recompensas:\n\nDaga de artesanoMartillo compacto\n\nTambién recibirás:\n\nMartillo compactoDaga de artesano"},
[418] = {title = "Morcillas de Thelsamar", objective = "Lleva 3 trozos de carne de oso, 3 tripas de jabalí y 3 porciones de icor de araña a Vidra Hogartufa, en Thelsamar.", description = "Aquí en Thelsamar siempre hay alguien que quiere comer, críos por todos lados, los obreros de la excavación y eso. Lo más conocido es la morcilla, ¿la has probado?\n\n¿No? Bueno, pues aquí hay que trabajar para comer, no creas que te libras porque seas <clase>.\n\nNecesito carne de oso, tripa de jabalí y un poco de icor de araña para darle sabor. ¡Tú trae eso y de cocinar me ocupo yo, Vidra!", progress = "Si me traes los ingredientes, me pongo ya mismo a cocinar.", completion = "Es más fácil de lo que parece.\n\nNo había conocido a nadie con tanto interés, pero ya que preguntas, te daré la receta. ¡Recuerda que no se conservan demasiado tiempo!", rewards = "Recibirás:\n\nMorcillaReceta: morcilla"},
[419] = {title = "El piloto perdido", objective = "Encuentra al piloto Hildelve.", description = "Mi compañero y amigo, Mori Hildelve, que es piloto de máquinas de asedio, está perdido en los montes. Buscábamos un raro mineral necesario para fabricar un potente tipo de pólvora y durante nuestra búsqueda, subió con su máquina una empinada montaña ¡y la máquina se averió!\n\nAún convencido de que el mineral estaba en estas montañas, Hildelve me encargó que custodiara las máquinas mientras él continuaba la búsqueda a pie.\n\nPero ya han pasado días y por las noches oigo unos estremecedores aullidos en las montañas. Mori es duro como una roca, pero estoy preocupado.\n\nTe lo ruego, <nombre>. Encuéntralo.", progress = nil, completion = "Encuentras el cadáver de un enano. Masacrado, congelado y saqueado por los carroñeros de la montaña. Aprieta con la mano un libro que contiene anotaciones garabateadas por Mori Hildelve. El cuerpo está envuelto en un jubón de brigadier que aún está en perfectas condiciones.\n\nEste escenario truculento es seguramente aquel en el que el piloto de máquina de asedio perdió la vida.", rewards = "Recibirás:\n\nJubón de brigada de asedio"},
[420] = {title = "Las observaciones de Senir", objective = "Lleva el informe de Grelin a Senir Barbablanca a Kharanos.", description = "Los troggs del túnel son extremadamente hostiles y no dudan en atacar a los viajeros que lo cruzan. Pero tú pareces fuerte. No deberías tener problemas para pasar.\n\nMmm... indicaciones... Pues para hablar con Senir tienes que ir a Kharanos.\n\nÁbrete paso a través del túnel y cuando lo hayas cruzado, sigue el camino. Te llevará directamente a Kharanos.", progress = "¿Cómo estás? ¿Te apetece tomarte un algo conmigo? No es que haya mucho más que hacer con este frío.", completion = "¿Qué es eso? Ah, el informe de mi hermano. Mmm... \n\nLe dije que moderara el uso del nombre del Rey, pero no me ha hecho caso. Supongo que no hará daño a nadie, pero sí que va a erizar alguna que otra pluma en el Senado. ¡Y no vendrá nada mal, no creas! ¡Ja, ja! \n\nEn fin, supongo que yo debería enviar mi informe a Forjaz también, pero la verdad es que voy un poco atrasado. Maldito frío. \n\nSi te interesa, más tarde podría tener trabajo para ti.", rewards = "Recibirás:\n\n"},
[421] = {title = "Demuestra tu valía", objective = "Dalar Tejalba en El Sepulcro quiere que mates 5 canosos Furia Lunar.", description = "Lady Sylvanas ha encomendado a Varimathras la conquista de las tierras humanas y enanas del sur.\n\nPero ese necio, Arugal, charlatán de Dalaran y ahora bestia maldita del Castillo de Colmillo Oscuro, dejó que su imprudente magia causara estragos en el bastión estratégico del Bosque de Argénteos.\n\nNecesito a alguien muy diestro en combate para ayudar a arreglar el desastre de Arugal. Demuéstrame tu valía matando a 5 canosos Furia Lunar. Es fácil encontrar a esas bestias salvajes al final del camino, hacia la colina de abajo.", progress = nil, completion = "Me has servido bien, &lt;clase&gt;. Es obvio que serás de gran ayuda mientras me preparo para liberar el Bosque de Argénteos de la maldición de Arugal.", rewards = "Te van a lanzar el siguiente hechizo:\n\nIntelecto Arcano\n\nRecibirás:\n\n"},
[422] = {title = "La locura de Arugal", objective = "Recupera el Remedio de Arugal para Dalar Tejalba, en El Sepulcro.", description = "No me puedo imaginar cómo Arugal consiguió que los Kirin Tor le aceptasen.\n\nLo que importa ahora es descubrir exactamente, qué magia utilizó Arugal. Así podré volverla contra él y asegurar el Bosque de Argénteos para la Dama Oscura.\n\nAl principio, Arugal se estableció en la granja de trigo que está justo al norte del puente. Uno de los mortacechadores, informó sobre algunos libros de hechizos que había visto, pero no pudo hacerse con ellos. Consigue el hechizo llamado Remedio de Arugal que está en esos libros.", progress = "Antes de saber lo que tenemos entre manos, <clase>, tengo que estudiar el hechizo conocido como el Remedio de Arugal. Tráemelo ahora, o tendré que buscar a un sirviente digno de hacerlo.", completion = "Precisamente, este es el hechizo que estaba buscando, &lt;clase&gt;. Si tu dedicación a la causa de la Dama Oscura permanece infalible, tendrás un futuro entre los Renegados.", rewards = "Te van a lanzar el siguiente hechizo:\n\nIntelecto Arcano\n\nRecibirás:\n\n"},
[423] = {title = "La locura de Arugal", objective = "Lleva 6 grilletes de glotón y 3 grilletes de almanegra a Dalar Tejalba en El Sepulcro.", description = "Tras examinar el trabajo de Arugal, se han confirmado mis peores sospechas. Ese viejo chiflado no estaba cualificado ni para limpiar orinales en Dalaran, y mucho menos para representar a los Kirin Tor en su momento más desesperado. ¡Imbéciles!\n\nArugal utilizó objetos encantados para reforzar su débil magia. Necesito examinar esos objetos. Continúa tu viaje y mata glotones Furia Lunar y almanegras Furia Lunar hasta que hayas reunido suficientes grilletes encantados para mi investigación. Puedes encontrar a estas repugnantes criaturas hacia el norte y el este.", progress = "Necesitaré 6 grilletes de glotón y 3 grilletes de almanegra antes de poder evaluar la situación y encontrar una solución para Arugal. Ahora haz lo que tienes que hacer y mata glotones Furia Lunar y almanegras Furia Lunar hasta que tenga todo lo que necesito.", completion = "Espero poder adquirir suficiente energía de una muestra tan limitada. Quizás tenía que haberte pedido más grilletes.\n\nDe todas formas, has demostrado una gran habilidad para reunir estos, &lt;clase&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nIntelecto Arcano\n\nRecibirás:\n\n"},
[424] = {title = "La locura de Arugal", objective = "Mata a Grimson el Pálido y lleva su cabeza a Dalar Tejalba en El Sepulcro.", description = "Voy a tardar mucho más de lo que había pensado en descubrir los oscuros secretos que yacen tras los encantamientos que Arugal estaba usando.\n\nPero, mientras tanto, necesito que te encargues de un pequeño problema que nuestros acechadores negros han descubierto. Parece ser que Arugal permitió que su magia se extendiera hasta la Mina de Elem, en las colinas del sureste.\n\nLa mina resultó ser un lugar estratégico para el avance de Varimathras.\n\nQuiero que decapites al supervisor contaminado de la mina, Grimson el Pálido. Tras su muerte, la mina será nuestra.", progress = "¿Has matado a Grimson el Pálido? Varimathras no estará contento si no hacemos lo que quiere.", completion = "¡Soberbio! La mina nos proporcionará excelentes recursos para nuestras tropas mientras avanzamos a través de Argénteos de camino a Azeroth.", rewards = "Te van a lanzar el siguiente hechizo:\n\nIntelecto Arcano\n\nRecibirás:\n\n"},
[425] = {title = "Ivar el Hediondo", objective = "Mata a Ivar el Hediondo y lleva la cabeza de Ivar a Rane Yorick en Los Dominios de Ivar.", description = "Mi hermano Quinn y yo teníamos que explorar el Bosque de Argénteos e informar al sumo ejecutor Hadrec. Pero no llegamos muy lejos... Cuando atravesábamos una granja, fuimos atacados por el necrófago Ivar el Hediondo.\n\nConseguimos escapar de Ivar, pero hirió gravemente a Quinn. Ahora se está recuperando gracias a la poción que le diste, pero Ivar podría volver antes de que Quinn esté totalmente bien.\n\nMata a Ivar para que Quinn tenga tiempo para curarse. Generalmente, Ivar está en el establo de allí, esperando a que baje la guardia.", progress = "¿Has matado a Ivar el Hediondo? Si no quieres hacerlo por mi hermano y por mí, al menos hazlo por los Renegados.", completion = "Ah, muchas gracias. Tienes mi gratitud, &lt;nombre&gt;.\n\nY te incluiré en el informe que presento a mis superiores.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales guateadosCinturón deteriorado\n\nTambién recibirás:\n\nBrazales guateadosCinturón deteriorado"},
[426] = {title = "La invasión de los molinos", objective = "Recoge 5 costillas dentadas y 3 calaveras ennegrecidas, y después regresa ente el guardia de la muerte Dillinger de Rémol.", description = "Los miembros de la Plaga están intentando establecer una base en los Molinos de Agamand. Si lo consiguen, podrán lanzar más ataques sobre Tirisfal.\n\nLas órdenes son claras: hay que detener a la Plaga en los Molinos.\n\nVe a los Molinos. Recoge costillas dentadas de los soldados armadóseos y los soldados testapartida, y calaveras ennegrecidas de los tirahuesos Ojosombra.\n\nPara llegar hasta los Molinos, sigue el camino en dirección oeste. Tras cruzar el puente, toma el siguiente desvío hacia el norte y continúa en esa dirección. Cuando veas los molinos de viento, dará comienzo la batalla.", progress = "Nuestras batallas contra la Plaga están en marcha, <nombre>. ¡Haz lo que te corresponde y envía a esos malditos no-muertos de vuelta a las Tierras de la Peste!", completion = "Nos estás ayudando mucho en la guerra, &lt;nombre&gt;. No te daré las gracias por hacer lo que es necesario, pero has de saber que los guardias de la muerte de Tirisfal no olvidaremos tu nombre.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de caminacriptasPantalones de paño robusto\n\nTambién recibirás:\n\nBotas de caminacriptasPantalones de paño robusto"},
[427] = {title = "En guerra con la Cruzada Escarlata", objective = "El ejecutor Zygand de Rémol quiere que mates a 10 guerreros Escarlata.", description = "Los documentos que el ejecutor Arren nos ha proporcionado son exactamente lo que necesitábamos para dar un giro a nuestra batalla contra la desdichada Cruzada Escarlata. Ahora sabemos sus ubicaciones exactas en Claros de Tirisfal.\n\nPero el guardia de la muerte tiene otras preocupaciones mayores. El ejército del rey Exánime es más numeroso cada día que pasa. Necesitamos a alguien con un poco de \"iniciativa\" como tú para que envíe a la Cruzada Escarlata a la tumba.\n\nDemuéstrame que eres capaz de servir a la Dama Oscura; para ello, viaja al oeste hasta la torre que encontrarás pasada la Hacienda Solliden y mata a 10 guerreros Escarlata.", progress = nil, completion = "Impresionante, &lt;clase&gt;. Tu habilidad en el arte de la lucha es innegable.", rewards = "Recibirás:\n\n"},
[428] = {title = "Los Mortacechadores desaparecidos", objective = "Encuentra a los Mortacechadores Quinn y Rane Yorick.", description = "Hace un par de semanas, dos de nuestros Mortacechadores, Rane y Quinn Yorick, fueron a una misión de reconocimiento por la parte norte de Argénteos. No hemos vuelto a tener noticias suyas.\n\nQuizás hayan caído en manos de la Plaga pero, si es así, necesito una confirmación. Encuentra a estos mortacechadores.\n\nSu primer objetivo era explorar las granjas al norte de Argénteos. Deberías comenzar por allí.", progress = nil, completion = "¡Nos has encontrado! Y justo a tiempo.", rewards = "Recibirás:\n\n"},
[429] = {title = "Corazones salvajes", objective = "Consigue 6 corazones de lupo decolorados y llévaselos al boticario Renferrel en El Sepulcro.", description = "El necrófago, Ivar el Hediondo, hirió de gravedad a mi hermano Quinn y no sé si podrá curarse sin ayuda mágica. Si bien yo no soy un boticario, sé que nuestros boticarios pueden preparar pociones curativas a partir de corazones de lupos decolorados.\n\nConsigue esos corazones y llévaselos al boticario Renferrel en El Sepulcro, al sur. Después, vuelve aquí con la poción.\n\nEncontrarás muchos lupos entre aquí y la Huerta de Malden, al este.", progress = "No me queda mucho tiempo, <clase>, la Dama Oscura me ha encargado una tarea de vital importancia.", completion = "&lt;Renferrel coge los corazones.&gt;\n\nResulta de lo más perturbador saber que nuestros Mortacechadores han fallado su misión.\n\nEsperemos que no tengamos muchos fracasos más.\n\nTe daré la poción en un momento, pero aquí tienes la receta. Si esos Mortacechadores necesitan ayuda en el futuro, quizás podías dárselo tú mismo.", rewards = "Recibirás:\n\nReceta: poción curativa descoloridaPoción curativa descolorida"},
[430] = {title = "Vuelve con Quinn", objective = "Lleva la poción de Quinn a Quinn Yorick en Los Dominios de Ivar, al norte de El Sepulcro.", description = "Aquí está la poción para curar a Quinn Yorick.\n\nAhora, si me perdonas, tengo algo muy importante que hacer...", progress = "Mi hermana me dijo que estabas aquí para ayudarnos. ¿Es eso cierto?", completion = "&lt;Quinn coge la poción y la bebe.&gt;\n\nSí, siento que funciona. Pronto podré viajar.\n\nMe has hecho un gran favor, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[431] = {title = "Velas de señalización", objective = "Coges una de las velas y la colocas en tu talega.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nVela de Señalización"},
[432] = {title = "¡Malditos troggs!", objective = "Mata a 6 aplastacráneos Comepiedras para Foreman Petrocejas de la Cantera de Gol'Bolar.", description = "¡Esos malditos troggs están destrozando mi excavación! ¡Mira! ¡Ahí va otro barril de polvo!\n\n¡Ay! ¡Cómo me gustaría bajar ahí y retorcerles a todos ese pescuezo huesudo que tienen! ¡Quiero matarlos a todos!\n\nEsto no puede ser bueno para la salud. Tanta ira, tanto estrés... ¿Qué puedo hacer sin perjudicarme la salud? ¡Nada!\n\nLos mataría yo mismo, pero mi puntería no es la que era. ¡Entra tú en la cantera y mata a un puñadito de esos bichos asquerosos! Te pagaré... ¡Tú hazles daño! ¡Mátalos!", progress = nil, completion = "Vaya, ahora me siento un poco mejor... \n\n¡Pero no conseguiré paz hasta que estén todos muertos! ¡Todos ellos!", rewards = "Recibirás:\n\n"},
[433] = {title = "El servidor público", objective = "Mata a 10 quebrantahuesos Comepiedras para el senador Mehr Piedrasanta de la Cantera de Gol'Bolar.", description = "Como miembro del Senado y de la Liga de Exploradores, he asumido la responsabilidad de ocuparme de la infestación trogg que ha invadido nuestras tierras.\n\nPorque, sin motivo alguno, han convertido en un caos la Cantera de Gol'Bolar. Van saliendo a medida que profundizamos en nuestra excavación, y destruyen nuestro equipo y ahuyentan a nuestros mineros. Así que tenemos que ir exterminando a cuantos podemos, luego reconstruimos todo y volvemos al trabajo.\n\nSi me ayudas con los troggs, te recompensaré.", progress = nil, completion = "Es un comienzo. Con suerte podremos aprovechar el momento y llevar a algunos montaraces para asegurar el área. Luego podremos comenzar a reconstruir nuestra infraestructura para que los mineros vuelvan al trabajo.\n\nGracias por tu ayuda, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[434] = {title = "El ataque", objective = "Quédate en el jardín hasta que Lord Gregor Lescovar y Marzon el Filo Silencioso hayan terminado de hablar. A continuación, mátalos antes de volver a reunirte con Elling Trias en Ventormenta.", description = "Cuando le ponga el disfraz al robot espía, irá a hablar con Lescovar. Los guardias le dejarán pasar sin problemas.\n\nMientras estabas reuniendo los materiales, me encargué de informar a Marzon de que tenía que reunirse de nuevo con Lescovar. Incluso le di información falsa.\n\nLescovar dirá a sus guardias que se retiren y esperará a Marzon aquí fuera, en el jardín. En cuanto empiecen a hablar de los Defias, te toca mover ficha. Tienes que matarlos antes de que vuelvan los guardias. Cuando hayas terminado, habla con Trias.", progress = nil, completion = "¿Entonces ya está? Bien. Muchísimas gracias por tu discreción, &lt;nombre&gt;. Has demostrado tu gran valía en más de una ocasión. Me aseguraré de seguir de cerca tu progreso en la Alianza.\n\nNo te metas en líos.", rewards = "Recibirás:\n\n"},
[435] = {title = "Escolta a Erland", objective = "Escolta a Erland a través de los lobos, hasta llegar a Rane Yorick.", description = "Los lobos que están fuera no se mueven y me miran de forma malvada cada vez que salgo por esa puerta. Deben de estar increíblemente hambrientos si piensan que puede merecer la pena comerme.\n\nTengo que informar a mi compañero de equipo, el Mortacechador Rane Yorick. Y, para conseguir llegar donde está Rane, necesitaré ayuda con esos lobos.\n\n¡Por favor, escóltame! Hay un montón de lobos ahí fuera, así que tendríamos más oportunidades si tienes algún amigo que pueda ayudarnos...", progress = nil, completion = "Gracias por tu ayuda, &lt;nombre&gt;. Temía que Erland hubiera perecido en Argénteos. Pero, gracias a ti, veo que sigue con vida.", rewards = "Recibirás:\n\nEspada corta de Mortacechador"},
[436] = {title = "La excavación de Vetaferro", objective = "Habla con el explorador Tallavil.", description = "El prospector Vetaferro dirige la excavación de unas antiguas ruinas al este del lago. Últimamente está haciendo pocos progresos, a pesar de todos los suministros que le hemos enviado.\n\nVetaferro es un enano honrado y tenaz que valora los resultados. Por eso sospecho que hay otros factores en su contra.\n\nVe a la Excavación de Vetaferro y habla con Magmar Tallavil. Él se encarga de los detalles de la excavación y sabrá a qué se debe el retraso en los trabajos.\n\nPara llegar a la excavación, rodea el extremo sur del lago y luego dirígete al este.", progress = nil, completion = "¿Que por qué hay un retraso? \n\nVetaferro está preparando el informe, pero ya te digo yo lo que causa el retraso: \n\n¡Los troggs!", rewards = "Recibirás:\n\n"},
[437] = {title = "El Campo Muerto", objective = "Mata gnolls Putrepellejo en El Campo Muerto.\n\nCuando aparezca Latipesadilla, mátala.\n\nLleva la esencia de Latipesadilla al sumo ejecutor Hadrec en El Sepulcro.", description = "Los informes iniciales de nuestros exploradores en Argénteos, muestran que los Putrepellejo tienen una base en una vieja granja del norte, El Campo Muerto. Hemos lanzado pequeños asaltos contra ellos, pero cada vez que lo intentamos, el alma en pena Latipesadilla les ayuda, utilizando su magia para diezmar nuestras tropas.\n\nQueremos destruir a Latipesadilla.\n\nVete a El Campo Muerto y ataca a los Putrepellejo. Si no ves a Latipesadilla, mata a los Putrepellejo hasta que aparezca y, después, mátala a ella.\n\nSu esencia se convertirá en polvo. Tráeme ese polvo como prueba.", progress = "¿Has derrotado a Latipesadilla, <nombre>? ¡Nuestro éxito contra los Putrepellejo depende de su destrucción!", completion = "Me he enterado de tu exitoso asalto contra los Putrepellejo, &lt;nombre&gt;, y esta esencia demuestra la muerte de Latipesadilla.\n\nHoy, la Plaga ha perdido terreno. Tu valía aumenta para los Renegados.", rewards = "Recibirás:\n\nBotas de reconocimiento"},
[438] = {title = "El Viejo Embarcadero", objective = "Ve a El Viejo Embarcadero.\n\nEncuentra la razón por la que la Plaga está ahí.", description = "<nombre>, hemos descubierto una nueva infestación de la Plaga: El Viejo Embarcadero. El embarcadero está al este de El Sepulcro y está muy protegido por los Putrepellejo y otros no-muertos.\n\nDebe de haber una razón para la ocupación de la Plaga de esa zona. Vete a El Viejo Embarcadero y descubre por qué está allí la Plaga.", progress = nil, completion = "En este barco hay una pila de cadáveres envueltos. Y, mirando hacia el este a través del Lago Lordamere, se puede ver un puerto en la Isla de Fenris.\n\nEl destino de esos cadáveres debe de ser la Isla de Fenris.", rewards = "Recibirás:\n\n"},
[439] = {title = "Pistas de Putrepellejos", objective = "Lleva el anillo de Deliah al sumo ejecutor Hadrec en El Sepulcro.", description = "Entre los cadáveres sobresale una mano, abierta. Es la mano de una mujer, con marcas de la Peste, pero relativamente fresca.\n\nEn la mano hay un anillo. Al sacar el anillo e inspeccionarlo revela una inscripción:\n\n\"Para Deliah\"", progress = "<nombre>. Has regresado, así que supongo que tus investigaciones han tenido éxito.", completion = "¿Dices que este anillo lo llevaba una mujer asesinada recientemente?\n\nEl único sitio de donde se puede sacar un cadáver así es de las fosas comunes de Tirisfal. Los gnolls Putrepellejo deben de estar llevando esos cadáveres a la Isla de Fenris.", rewards = "Recibirás:\n\n"},
[440] = {title = "El anillo grabado", objective = "Lleva el anillo de Deliah al magistrado Sevren en Rémol.", description = "Si quieres encontrar al marido o bienamado de Deliah, quien le dio el anillo... habla con el magistrado Sevren en Rémol. Debería tener registros de los fallecimientos recientes y, con suerte, podrás descubrir quién es Deliah.\n\nPero yo no contendría el aliento. Quienquiera que le dio el anillo, está probablemente muerto. Y, si no está muerto, probablemente ya no le importa Deliah.", progress = "Hola, <nombre>. ¿Qué te trae de nuevo por Rémol?", completion = "Había una Deliah que vivía en Rémol no hace mucho. La enterraron en la fosa común pero, si lo que dices es cierto, los gnolls Putrepellejo deben de haber robado su cadáver para llevárselo a Argénteos.\n\nTenemos que destruir a los Putrepellejo, al igual que a toda la Plaga. Confío en que te estés encargando de eso.", rewards = "Recibirás:\n\n"},
[441] = {title = "Raleigh y Entrañas", objective = "Lleva el anillo de Deliah a Raleigh.", description = "Deliah tenía un marido, Raleigh, que ahora vive en Entrañas. Si la Deliah que vivía en Rémol es la dueña de este anillo, Raleigh lo sabrá.\n\nRaleigh está en el Barrio del Comercio, en Entrañas.", progress = "¿Nos conocemos?", completion = "Sí, este anillo era de mi mujer. Compartió destino con todos los que cayeron en esta dura época. No siento nada por su muerte ni por la historia del secuestro de su cadáver.\n\nHace mucho tiempo que lo que sentía por ella se enfrió. Pero el deseo sigue quemando en mi corazón muerto, &lt;nombre&gt;.\n\nDeseo de venganza.", rewards = "Recibirás:\n\n"},
[442] = {title = "Asalto a la Isla de Fenris", objective = "Lleva la cabeza de Thule al sumo ejecutor Hadrec en El Sepulcro.", description = "Ha llegado el momento de asaltar el punto fuerte de la Plaga en Argénteos: la Isla de Fenris.\n\nDebes volver a la isla y destruir a Thule Corvozarpa. ¡Quiero su cabeza!\n\nEsta misión es tan peligrosa como vital, <nombre>. No puedes fallar.", progress = "No podemos permitir la presencia de la Plaga en Argénteos, <nombre>. Vuelve a verme cuando tengas alguna prueba de la muerte de Thule.", completion = "La muerte de Thule Corvozarpa fue un gran golpe, &lt;nombre&gt;. Renferrel me dice que las energías que se liberaron cuando destruiste a Thule, se sintieron incluso en El Sepulcro.\n\nTus acciones resuenan en las filas de los Renegados. Tu valía crece ente nosotros.", rewards = "Podrás elegir una de estas recompensas:\n\nGran toga del AdjudicadorGolpe de talón\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nGran toga del AdjudicadorGolpe de talón"},
[443] = {title = "Icor de Putrepellejo", objective = "Lleva 8 botellas de icor de Putrepellejo al boticario Renferrel en El Sepulcro.", description = "Antes de lanzar un asalto en la Isla de Fenris, debemos determinar quién está detrás de los gnolls Putrepellejo. Nuestros boticarios creen que su origen yace en el extraño líquido que fluye por sus cuerpos.\n\nVete a la Isla de Fenris, al este de El Viejo Embarcadero y recoge icor de los gnolls que encuentres allí. Llévaselo al boticario Renferrel, está fuera de El Sepulcro.", progress = "¿Tienes el icor de Putrepellejo, <nombre>?", completion = "Muy bien. Este icor nos ayudará a entender la naturaleza de los Putrepellejo y quizás nos descubra sus debilidades... \n\n... si es que tienen alguna.", rewards = "Recibirás:\n\n"},
[444] = {title = "El origen de los Putrepellejos", objective = "Lleva la muestra de icor a Bethor Duroyelo en Entrañas.", description = "He reducido el icor que me has traído y he separado sus componentes básicos. Mis descubrimientos no son concluyentes, pero he determinado que en la creación del icor se utilizó brujería y necromancia.\n\nMientras sigo estudiando el icor, quiero que le lleves una muestra a Bethor Duroyelo en Entrañas. Bethor no es un boticario, pero su conocimiento de la magia, puede ayudarnos a descubrir el misterio de los Putrepellejo.\n\nVete. Encontrarás a Bethor en el Barrio de la Magia de Entrañas.", progress = "Necesito una gran concentración para mis estudios. Espero que tu visita no sea frívola.", completion = "Esta sustancia está mezclada con poderosos hechizos, algunos de ellos no los había visto en mucho tiempo...\n\nUn momento, debo invocar un hechizo augurador...", rewards = "Recibirás:\n\n"},
[445] = {title = "Entrega a Bosque de Argénteos", objective = "Llévale las conclusiones del boticario Johaan al boticario Renferrel del Bosque de Argénteos.", description = "El tiempo es un lujo que no nos podemos permitir quienes trabajamos para Lady Sylvanas. Pero seguro que ya te habías dado cuenta.\n\nComo miembro de la Sociedad Real de Boticarios es mi deber compartir mis conocimientos con mis colegas para que con nuestro esfuerzo colectivo logremos algún día proporcionarle a la Dama Oscura su tan ansiada Nueva Peste.\n\nLlévale estas conclusiones al boticario Renferrel que está en El Sepulcro en el Bosque de Argénteos. Sigue los caminos que parten en dirección sur desde Rémol. Desde Entrañas, dirígete hacia el suroeste.", progress = "¿Qué te trae ante mí, <clase>?", completion = "¡Qué bien que el boticario Johaan haya enviado los resultados de su investigación. Con tantas innovaciones aquí en el Bosque de Argénteos, casi me había olvidado de los hallazgos que llegan de Lordaeron y Claros de Tirisfal. ¡Ah! Esto me recuerda que tengo que hacer llegar estas muestras a Necrópolis lo antes posible.\n\nVaya, ya me he ido por las ramas, perdona... Prolonga tu estancia en el Bosque de Argénteos, &lt;nombre&gt;. Sería muy útil tener a un &lt;clase&gt; en tan buena forma como tú por aquí.", rewards = "Recibirás:\n\n"},
[446] = {title = "Thule Corvozarpa", objective = "Lleva el pergamino de Bethor al boticario Renferrel en El Sepulcro de Argénteos.", description = "Este icor fue creado por un viejo amigo mío: Thule Corvozarpa. Antes de la Gran Guerra, era un joven mago muy prometedor, pero cuando el rey Exánime ocupó el trono, traicionó a sus hermanos y aliados con la Plaga.\n\nNo me había dado cuenta de que todavía sigue con vida, ni de que él es quien lidera a los Putrepellejo contra nosotros.\n\nConozco muchos de sus hechizos y puedo ayudar a preparar una poción para contrarrestarlos. Lleva este pergamino al boticario Renferrel. Tiene los conjuros necesarios para la poción.", progress = "Hola, <nombre>. ¿Qué te ha dicho Bethor?", completion = "Este pergamino está imbuido de poderosa magia. Nunca he usado fuerzas así en una poción... será una mezcla muy poderosa y debería servirte si atacas a Thule en su fortaleza de la Isla de Fenris.", rewards = "Recibirás:\n\nPoción de Bethor"},
[447] = {title = "Una fórmula mortal", objective = "Consigue 6 corazones de grisoso y 6 muestras de sangre de Celearácnida y llévaselas al maestro boticario Faranell en Entrañas.", description = "Arthas cuenta con innumerables tropas. Pero, con una Nueva Plaga podríamos erradicar tanto a al ejército de la Plaga como a la infestación humana.\n\nMis estudios han demostrado que la sangre de araña combinada con una toxina derivada del corazón de un grisoso, resulta en un elixir letal. Reúne muestras de las arañas en Penumbra de las Celerácnidas, al noroeste, y de los osos que vagan por el Bosque de Argénteos. Lleva los componentes al maestro boticario Faranell de la Sociedad Real de Boticarios, en Entrañas.", progress = "Alto, <clase>. ¿Qué tienes que hacer en la Sociedad Real de Boticarios?", completion = "Qué amable por parte del boticario Renferrel haberte enviado, &lt;nombre&gt;. Estas muestras serán sin duda útiles para nuestra causa. Comenzaré a estudiar sus propiedades tóxicas y contaminantes ahora mismo.", rewards = "Recibirás:\n\nRaíz de senggin"},
[448] = {title = "Preséntate ante Hadrec", objective = "Preséntate ante el sumo ejecutor Hadrec en El Sepulcro.", description = "Antes de iniciar un asalto a la Isla de Fenris, ve a ver al sumo ejecutor Hadrec. Es posible que tenga equipamiento o información adicional que pueda serte útil.", progress = nil, completion = "Si lo que Bethor dice es cierto, hay que encargarse rápidamente de Thule Corvozarpa.\n\nPara ayudarte, puedes coger algo de nuestro arsenal...", rewards = "Podrás elegir una de estas recompensas:\n\nPiedrafuerte burdaPiedra de afilar burda\n\nTambién recibirás:\n\nJuego de armadura medioPiedra de afilar burdaPiedrafuerte burda"},
[449] = {title = "El informe de los Mortacechadores", objective = "Lleva el informe  de los Mortacechadores al sumo ejecutor Hadrec en El Sepulcro.", description = "Si bien las heridas de Quinn se están curando, quiero entregar nuestro informe al sumo ejecutor Hadrec tan pronto como sea posible. Contiene información vital para la ocupación de los Renegados de Argénteos.", progress = "¿Has descubierto lo que ha pasado con los Mortacechadores?", completion = "No había contado con el nivel de presencia de la Plaga que vimos en el Bosque de Argénteos, ni con los problemas que nuestros Mortacechadores tuvieron en sus misiones de exploración.\n\nTu ayuda a nuestros Mortacechadores ha sido inapreciable, &lt;nombre&gt;. Y te recomendaré.", rewards = "Recibirás:\n\n"},
[450] = {title = "Una fórmula mortal", objective = "Recupera el Diario del boticario Berard de la Aldea Piroleña y llévaselo al boticario Renferrel en El Sepulcro.", description = "Puedes seguir ayudando a la Sociedad Real de Boticarios, <nombre>.\n\nCuando Arugal utilizó su magia maligna en el bosque, maldijo a uno de nuestros miembros más prometedores, el boticario Berard. Ahora es un sirviente de Arugal, sin dominio de sí mismo. En estas condiciones, Berard no nos sirve de nada. Pero sus descubrimientos podrían servirnos. Estaba estudiando una antigua plaga que acabó con la vida acuática del Lago Lordamere años atrás.\n\nVe a buscar el Diario de Berard. Está en su estudio, el la Posada de la Aldea Piroleña, y llévaselo al boticario Renferrel.", progress = "Veo que has regresado, <nombre>.", completion = "¡El Diario del boticario Berard! Eres muy valiente, &lt;nombre&gt;. Ahora veamos lo que estaba haciendo Berard antes de volverse loco...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[451] = {title = "Una fórmula mortal", objective = "Lleva 6 muestras de musgo de trepador del Lago, 6 muestras de musgo de vigilante del Lago y un tumor endurecido al maestro boticario Faranell en Entrañas.", description = "Según el Diario de Berard, el musgo de los vigilantes del Lago y los trepadores podría ser útil para nuestro objetivo de crear una Nueva Plaga. Esas bestias viven en las islas del Lago Lordamere, al este.\n\nTambién menciona un tumor endurecido muy extraño que descubrió en un múrloc Anca Vil. Por lo visto, el tumor es tan poco común, que Berard solo pudo obtener uno.\n\nReúne muestras del musgo y del tumor endurecido y llévaselas al maestro boticario Faranell en Entrañas.", progress = "¿Qué te trae de nuevo a Entrañas, <nombre>?", completion = "Perder a Berard ha sido un duro golpe para la Sociedad Real de Boticarios. Pero gracias a lo que has hecho y a la diligente investigación del boticario Renferrel, los estudios de Berard serán de mucha utilidad para la Dama Oscura.\n\nEstamos a punto de conseguir algo grande, &lt;nombre&gt;. Si no tuvieras tanto talento para el combate, intentaría reclutarte para la Sociedad.", rewards = "Podrás elegir una de estas recompensas:\n\nBrebaje Resplandor nocturnoCapa antiácido\n\nTambién recibirás:\n\nElixir de entereza menorPoción de velocidadBrebaje Resplandor nocturnoCapa antiácido"},
[452] = {title = "La emboscada de Aldea Piroleña", objective = "Ayuda al mortacechador Faerleia a destruir al Consejo Piroleño.", description = "¡Has llegado justo a tiempo, <nombre>! \n\nEl Consejo Piroleño está a punto de regresar. Arugal tiene que encargarse de otras cosas en el bastión. Utiliza el Consejo como si fueran marionetas, para mantener a los ineptos esbirros de la Aldea Piroleña bajo su control. Pero creo que sin la ayuda del Consejo Piroleño, los hijos de Arugal se volverían contra su maestro.\n\nNo puedo encargarme de todo sin ayuda. Te necesito. Juntos podemos acabar con el Consejo para Lady Sylvanas.", progress = nil, completion = "Luchas como un verdadero discípulo de la Dama Oscura, &lt;nombre&gt;. Con la destrucción del Consejo, quizás podamos conseguir el control de esta ciudad de una vez por todas y expulsar a Arugal del Castillo de Colmillo Oscuro. ¡Te aplaudo!", rewards = "Podrás elegir una de estas recompensas:\n\nEscudo de FaerleiaPantalones de cuero elásticosChal místico\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPantalones de cuero elásticosChal místicoEscudo de Faerleia"},
[453] = {title = "Encuentra a la figura", objective = "Encuentra a la figura misteriosa. Las pistas que tienes: \n\nNo es oriundo de Villa Oscura. \n\nEs una persona nerviosa, que se altera fácilmente. \n\nDejó Villa Oscura y se dirigió al oeste.", description = "Buena suerte, <nombre>. Espero que encuentres al tipo este... y que lo encuentres vivo.\n\nSi dejó la seguridad de Villa Oscura, podría haber sido víctima de las bestias salvajes.", progress = "¿Cómo estás? ¡Déjame!", completion = "&lt;Alterio suspira...&gt;\n\nSí, soy el autor de ese libro. Por fin me has encontrado, ¿eh?\n\nSi has leído ese diario... ¡solo sabes la mitad de la historia!", rewards = "Recibirás:\n\n"},
[454] = {title = "Tras la emboscada", objective = "Habla con Miran.", description = "Antes llevábamos juntos los barriles de pólvora. Pero ahora que ha pasado esto... Ahora tenemos que dividir el trabajo, Miran lleva los barriles y yo vigilo los otros.\n\nNo me gusta que Miran vaya por ahí solo con los barriles. ¿Te importa acompañarlo?", progress = nil, completion = "Hola, &lt;clase&gt;.\n\n¿Tú vas a ser mi escolta? Me alegro, todo este asunto de los Hierro Negro me tiene un poco nervioso.\n\nBueno, voy a acabar de prepararme; ven a hablar conmigo cuando quieras que salgamos.", rewards = "Recibirás:\n\n"},
[455] = {title = "El asedio de Algaz", objective = "Atraviesa Dun Algaz.\n\nMata 8 exploradores Faucedraco y 6 brutos Faucedraco.\n\nInforma a Valstag Quijahierro de Puerto de Menethil.", description = "Los orcos del clan Faucedraco tienen un refugio en Los Humedales ¡y ahora se han adentrado en Dun Algaz! No podemos permitir que continúen afianzándose aquí o Loch Modan perderá el contacto con el Puerto de Menethil, nuestro puerto y bastión en Los Humedales.\n\nAtraviesa Dun Algaz y dirígete a Los Humedales, ¡cuando estés allí mata a tantos orcos como puedas! Informa a Valstag Quijahierro en Puerto de Menethil.\n\nMenethil está pasado Dun Algaz, siguiendo el camino hacia el Norte y al Oeste en el cruce.\n\nContamos contigo, <nombre>. No nos falles.", progress = nil, completion = "¡Tu informe está lleno de malas noticias!\n\nVeo que los Faucedraco están avanzando con fuerza en Algaz. Normalmente los Faucedraco se quedan escondidos en las colinas. Es un misterio qué les hizo avanzar hacia Algaz...\n\nGracias, &lt;nombre&gt;. Seguramente necesitaremos tu ayuda para combatir a esos orcos.", rewards = "Recibirás:\n\n"},
[456] = {title = "El equilibrio de la naturaleza", objective = "Mata 7 sables de la noche y 4 jabalíes cardo jóvenes y ve a ver al conservador Ilthalaine.", description = "Saludos, <nombre>. Soy el conservador Ilthalaine. He venido a Cañada Umbría para asegurarme de que se mantiene el equilibrio de la naturaleza. \n\nEsta primavera ha llovido más de lo normal, lo que ha beneficiado mucho a algunas criaturas del bosque, pero ha perjudicado a otras. \n\nAhora hay demasiados sables de la noche y jabalíes cardo; la zona no puede alimentar a tantas bestias. Joven <clase>, reduce las poblaciones de jabalí cardo y sable de la noche para mantener la armonía de la naturaleza.", progress = nil, completion = "Buen trabajo, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de instrucción en tiro con arcoBrazales de tallo\n\nTambién recibirás:\n\nGuantes de instrucción en tiro con arcoBrazales de tallo"},
[457] = {title = "El equilibrio de la naturaleza", objective = "El conservador Ilthalaine quiere que mates 7 sables de la noche sarnosos y 7 jabalíes cardo.", description = "Acabar con los ejemplares más jóvenes de Cañada Umbría ha sido un buen comienzo, <nombre>, pero queda mucho por hacer. \n\nSi no tomamos medidas, se agotarán los recursos del bosque. Exterminar animales es un mal necesario por el bien de todos los que comparten la tierra. Para mantener el equilibrio, ve al bosque y acaba con los jabalís cardo y sables de la noche sarnosos.", progress = nil, completion = "Has demostrado tu dedicación a la naturaleza, &lt;nombre&gt;. Prometes mucho.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa envueltaCinturón de cuero ennegrecido\n\nTambién recibirás:\n\nCapa envueltaCinturón de cuero ennegrecido"},
[458] = {title = "La protectora de los bosques", objective = "Busca a la dríade Tarindrella.", description = "Me alegro de que hayas llegado, <clase>. El susurro de los espíritus del bosque me ha traído extrañas nuevas. \n\nLa misteriosa protectora de los bosques, Tarindrella, ha regresado a Cañada Umbría. Hacía años que la dríade no venía a los bosques de Kalimdor; su presencia aquí no presagia nada bueno. \n\nBusca a Tarindrella y averigua qué asuntos la han traído aquí. Uno de los centinelas la ha avistado al suroeste de Aldrassil.", progress = nil, completion = "Te doy la bienvenida, &lt;raza&gt;. Melithar ha hecho bien en enviarte aquí.", rewards = "Recibirás:\n\n"},
[459] = {title = "La protectora de los bosques", objective = "Lleva 8 muestras de musgovil a Tarindrella.", description = "En los bosques de Teldrassil está creciendo algo siniestro. Investiga en las colinas en las que vivían los pacíficos furbolgs. Han abandonado la zona y se están reuniendo bajo el nombre de la tribu Tuercepinos. \n\nSolo la corrupción del musgovil puede haber causado esta transformación. La zona está infestada de grells y grellkin, que amenazan a los residentes de Cañada Umbría. \n\nEnfréntate a ellos y averigua si se hallan bajo el encantamiento del musgovil.", progress = "Confirma mis sospechas, <nombre>. Tráeme 8 plantas de musgovil.", completion = "Tus servicios a Cañada Umbría merecen una recompensa, &lt;nombre&gt;.\n\nSin embargo, has confirmado mis peores temores; tiemblo al pensar en qué les habrá ocurrido a los furbolgs Tuercepinos.\n\nSi pasas por Dolanaar, ve a ver a Athridas Manturso, un sabio druida que también se preocupa por el bosque.", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos canopeBotas de rastreoGuantes de parra\n\nTambién recibirás:\n\nHierba curativaLeotardos canopeBotas de rastreoGuantes de parra"},
[460] = {title = "Descansar en piezas", objective = "Lleva la cabeza de Alaric a su tumba.", description = "\"No pareces un Putrepellejo. Bien. Ya había perdido la esperanza...\n\nMe llamo Alaric, y fui el jefe de los sirvientes y guardaespaldas de Thule. Utilizó su magia para \"protegerme\" de la Peste de no-muertos, pero su hechizo me dio una no-vida de la que no puedo escapar. Después de crear a los malvados gnolls Putrepellejo, Thule me decapitó... y dio mi cabeza a los gnolls como juguete.\n\nPor favor, llévame con mi cuerpo. Creo que los gnolls lo enterraron aquí, en el Castillo de Fenris cerca del establo, en una tumba sin nombre.”", progress = "Esta tumba se ha cavado a toda prisa.", completion = "Gracias, &lt;nombre&gt;. Temía que los gnolls hubieran destruido mi cuerpo. Me alegra saber que no es así.", rewards = "Recibirás:\n\n"},
[461] = {title = "El nicho oculto", objective = "Lleva a Alaric al nicho oculto.", description = "Conozco un nicho oculto en el castillo que contiene un objeto muy valioso para Thule y sus viejos colegas. También contiene algo que podrías encontrar útil. ¡Llévame allí!\n\nEl nicho está en la torre suroeste del castillo, detrás de una estantería polvorienta.\n\nCoge mi cuerpo, pero deja mis botas. De todas formas... nunca me quedaron bien...", progress = "¡Aquí es! Acércame más...", completion = "Aquí estamos, está abierto. Déjame aquí. Pasé los mejores días de mi vida aquí y el pensamiento de descansar entre sus muros me reconforta.\n\nAsegúrate de cerrar bien el nicho, no quiero que esos Putrepellejos me encuentren.\n\nOh, y acepta esto. Te lo has ganado.", rewards = "Recibirás:\n\n"},
[463] = {title = "El Guardaverde", objective = "Encuentra al Guardaverde.", description = "¿Has oído hablar del Guardaverde? ¿No lo estarás buscando, verdad? Si lo buscas es que estás <loco/loca>, ¿pero quién soy yo para apartar a un idiota de su muerte?\n\nSi estás buscando a esa bestia, he oído decir que está en los pantanos, al este del camino cuando se divide hacia Dun Modr. Está escondido entre los crocoliscos... ¡y cosas aún peores!\n\nDeja aquí tu dinero. No lo necesitarás allí a donde vas... no querrás que Verdín se rompa un diente con tus monedas cuando te parta en dos de un bocado, ¿verdad?", progress = nil, completion = "Las hiedras y helechos hablan muy bien de ti, pequeño. Aunque tengo muchos nombres, solo respondo a unos pocos. Tú puedes llamarme Rethiel.\n\nDebes aguzar el oído... lamentar el destino de estas tierras heridas y te encargaré la misión de sanarlas.", rewards = "Recibirás:\n\n"},
[464] = {title = "Estandartes de batalla", objective = "Lleva 8 estandartes de guerra Faucedraco al capitán Puñorrecio.", description = "El clan orco Faucedraco fue prácticamente destruido al final de la Segunda Guerra, cuando perdieron su control sobre los dragones rojos que eran sus mascotas. Los dragones no estaban muy contentos con sus antiguos dueños y dieron caza despiadadamente a todos los Faucedraco. Los pocos que quedaron huyeron de Grim Batol y se refugiaron en las montañas al este de la Excavación de Whelgar.\n\nAhora han ganado fuerza y tienen un campamento al noreste de Menethil.\n\nQueremos que ataques ese campamento. Recoge sus estandartes de guerra y tráemelos como prueba.", progress = "¿Has atacado ya el campamento Faucedraco?", completion = "Buen combate, &lt;nombre&gt;. Los Faucedraco ya no suponen la misma amenaza que cuando tenían dragones, pero siguen siendo unos duros oponentes.\n\nLos Faucedraco están liderados por Nek'rosh, hijo de Nekros, su antiguo cabecilla. Nekros utilizó poderosa magia para domar a los dragones rojos, pero cuando la magia le falló... los dragones no estaban muy contentos.\n\nNekros fue asesinado y su hijo ha jurado venganza contra los dragones que acabaron con su padre.", rewards = "Recibirás:\n\n"},
[465] = {title = "La estrategia de Nek'rosh", objective = "Destruye las catapultas Faucedraco.", description = "Mientras estabas combatiendo a los Faucedraco, descubrimos más cosas sobre sus planes.\n\nNek'rosh ha construido catapultas ¡y pretende asediar Menethil! ¡Además, debido a la lucha en Dun Algaz no podemos recibir refuerzos!\n\nTienes que acabar con esas catapultas. Están en el campamento Faucedraco. Toma, coge esta yesca.\n\nCuando hayas terminado, encuentra y mata a Nek'rosh. Necesitamos acabar con esa bestia Faucedraco... de raíz.", progress = "La primera catapulta está ante ti. ¿Vas a prenderle fuego?", completion = "Colocas algo de yesca en la catapulta y haces saltar una chispa.\n\n¡Aparece una llama que se extiende rápidamente!", rewards = "Recibirás:\n\n"},
[466] = {title = "Búsqueda de incendicita", objective = "Lleva 6 piezas de mineral de incendicita al piloto Roscapiedra de Dun Morogh.", description = "He oído decir que se encontró un extraño mineral, incendicita, en Los Humedales, en una cueva al noroeste de la Puerta de Algaz. La incendicita es un material volátil y es difícil de utilizar sin que explote, ¡pero estoy seguro de que si se funde de la forma adecuada podría hacer proyectiles de cañón que harían un daño tremendo!\n\nTráeme mineral de incendicita y encontraré a alguien que se atreva a experimentar con él.\n\nOh, una cosa más, para extraer el mineral de un filón tendrás que saber minería. ¡Si no eres <minero/minera> lleva uno contigo a la cueva!", progress = "¡¿Dónde está el mineral, <nombre>?!", completion = "¡Tienes el mineral! ¡Bien hecho, &lt;nombre&gt;! Tengo que encontrar a alguien que quiera trabajar con él. ¡Esperemos que no salga volando por los aires!", rewards = "Recibirás:\n\nGuantes manchados de cerveza"},
[467] = {title = "La búsqueda de los Roscapiedra", objective = "Habla con el piloto Roscapiedra.", description = "¡Al piloto Roscapiedra le encanta su máquina de asedio! ¡Y siempre anda buscando maneras de mejorar su cilindrada!\n\nHa oído hablar de un mineral muy raro y necesita a alquien que se lo consiga. Si te apetece una aventurilla, ve a hablar con él en el Almacén de Brasacerada en Dun Morogh.", progress = nil, completion = "Hola, &lt;nombre&gt;, ¿vienes por el trabajito? ¡Me alegro de ver que aún hay gente que se emociona con la idea de una aventura!", rewards = "Recibirás:\n\n"},
[468] = {title = "Preséntate ante el montaraz Rockgar", objective = "Habla con el montaraz Rockgar.", description = "El montaraz Rockgar está buscando voluntarios para una misión que les llevará a través de Dun Algaz hasta Los Humedales.\n\nSi quieres mojarte las botas en ese embarrado condado del Norte, habla con Rockgar en Dun Algaz, siguiendo el camino hacia el Norte, pasada la torre de vigilancia.", progress = nil, completion = "\"¡Hola, &lt;clase&gt;! ¿Estás aquí para la misión de exploración, verdad? ¡Muy bien!\n\nEspero que tu equipo esté en buenas condiciones porque esta misión \"de exploración\" se ha convertido en algo más... turbio...", rewards = "Recibirás:\n\n"},
[469] = {title = "Entrega cotidiana", objective = "Entrega el fardo de pieles de crocolisco a James Halloran, el peletero, en Puerto de Menethil.", description = "¡Saludos <viajero/viajera>!\n\nNo creo que te dirijas hacia Menethil, ¿verdad? Si no lo haces, te recomendaría hacer un alto, este es un buen lugar para hacer unas compras o para disfrutar de una buena pinta y de algo de descanso.\n\nAhora bien, si estás yendo hacia allí, no creo que te importe llevarle este fardo de pieles de crocolisco a James Halloran, el peletero.\n\nLo haría yo mismo, pero últimamente mi mujer no está de muy buen humor, me gustaría verla lo más tarde que pueda.", progress = "Hola, <clase>. ¿Estarías interesado en un par de botas? ¿Quizás en algunas pieles recién curtidas?", completion = "Ummm... esto es de parte de Einar, ¿verdad? No me ha venido a darme sus pieles en persona desde hace casi una semana. ¡Tendrá otra vez problemas con su mujer!\n\nMe parece que tendré que mandar a mi esposa a hablar con Shida...", rewards = "Recibirás:\n\n"},
[470] = {title = "Abriéndose paso entre los mocos", objective = "Uno de los mocos de la Tumba de Barbaférrea tiene la Bolsa de Shida, recupérala y devuélvesela en Puerto de Menethil.", description = "¡Durante mi visita semanal a la Tumba de Barbaférrea fui atacada por espantosos y pringosos mocos! Por supuesto, me entró pánico.\n\nLe lancé mi bolsa a uno de ellos, ¡pero no le hizo nada! El moco la absorbió... Por fortuna eso me dio algo de tiempo para escapar.\n\n¡La mala noticia es que necesito recuperar el contenido de mi bolsa!\n\nNo sé a qué moco le lancé la bolsa, ¡todos parecían iguales! La Tumba de Barbaférrea está al noreste de aquí, cerca de Dun Modr. ¡Muchísimas gracias!", progress = "¿Encontraste mi bolsa?", completion = "¡Oh, gracias, &lt;nombre&gt;! ¡Creía que la había perdido para siempre!\n\n\n\nUmm... esto ya no me vale de mucho, pero lávalo y estoy segura de que te será muy útil.", rewards = "Recibirás:\n\nBolsa cubierta de moco"},
[471] = {title = "Deberes de aprendiz", objective = "Reúne 6 pieles de crocolisco gigante y llévaselas a James Halloran en Puerto de Menethil.", description = "Envié a mi aprendiz a los pantanos para desollar algunos de nuestros famosos crocoliscos gigantes de Los Humedales. Cuando no regresó salí a buscarlo... Basta decir que no fue una visión muy agradable.\n\nLa matrona dice que el chico podría recuperarse, ¡pero hasta entonces sigo necesitando pieles!\n\n... Si quisieras matar unas cuantas de esas bestias no estaría en contra, después de todo lo que hicieron. Encontrarás a los crocoliscos al Norte, siguiendo la costa y pasadas las casuchas de los murlocs.", progress = "Mi aprendiz es como un hijo para mí. Le va a ser difícil vivir con una sola pierna.\n\nAh, <nombre>, ¿qué tal va? ¿Tienes mis pieles?", completion = "Umm... No creo que estés interesado en convertirte en mi aprendiz, ¿verdad?\n\n¡Ja! No, estoy bromeando. Eres demasiado mayor. Además por tu aspecto se diría que tienes otras ambiciones. Bueno, en cualquier caso, me has traído buenas pieles y me serán muy útiles.", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos de anillas maleablesPoncho resistente\n\nTambién recibirás:\n\nReceta: potaje de crocoliscoLeotardos de anillas maleablesPoncho resistente"},
[472] = {title = "La caída de Dun Modr", objective = "Busca a Largatrenza junto a la ciudad de Dun Modr.", description = "¡Dun Modr ha caído en manos de los enanos Hierro Negro!\n\nMis heridas son graves, <clase>. ¡Y casi todo mi regimiento fue asesinado! Los matones Hierro Negro nos atacaron antes de que pudiéramos reagruparnos tras la emboscada del Puente Thandol.\n\nNuestro líder, Largatrenza, hizo sonar el cuerno de retirada. Cuando dejamos la ciudad fui herido en la espalda con un hacha perdida. Todo se volvió negro.\n\nDesperté aquí, en Menethil, pero estoy preocupado por mis compañeros. Aún abrigo esperanzas. ¡Quizás Largatrenza siga con vida! ¡Ve a ver si puedes encontrarle cerca de Dun Modr, <raza>!", progress = nil, completion = "¿Qué valiente &lt;clase&gt; envía un mensaje desde Menethil? Ah &lt;nombre&gt;, te llaman.\n\nTraes algo de luz a una situación desesperada. 'Es una buena noticia que el joven Harlo sobreviviera. Al huir resultó gravemente herido. Apenas tuvimos tiempo para enviarle a lomos del único corcel que quedaba con vida de todo el regimiento.\n\nPero ahora nos enfrentamos a una situación muy peligrosa. Dun Modr ha caído y solo somos una sombra del gran regimiento que fuimos un día.", rewards = "Recibirás:\n\n"},
[473] = {title = "Preséntate ante el capitán Puñorrecio", objective = "Habla con el capitán Puñorrecio.", description = "<nombre>. El clan orco Faucedraco está mostrando su fuerza, y no solo en Algaz. El capitán Puñorrecio ha pedido hablar con quienes quieran ofrecer su ayuda contra esos orcos. Lo encontrarás en el castillo, en la Sala de Mandos.", progress = nil, completion = "&lt;nombre&gt;. Valstag me ha informado de tus éxitos sobre los orcos Faucedraco en Dun Algaz. Eres un &lt;raza&gt; de gran valor.\n\nY tengo una tarea para ti.", rewards = "Recibirás:\n\n"},
[474] = {title = "Derrota a Nek'rosh", objective = "Mata al jefe Nek'rosh.\n\nLleva la cabeza de Nek'rosh al capitán Puñorrecio.", description = "Ahora que las catapultas han ardido, encuentra y mata al jefe Nek'rosh.", progress = "¿Has matado a Nek'rosh?", completion = "Lo has hecho con gran resolución, &lt;nombre&gt;. Has dado un golpe contundente al clan orco de los Faucedraco. Quizás nunca se recuperen. Y la destrucción de las catapultas han hecho de Puerto de Menethil un lugar mucho más seguro.\n\nTus méritos son innegables, y el reino de Forjaz sabe recompensar a quienes le sirven.", rewards = "Podrás elegir una de estas recompensas:\n\nEspada de guerra antiguaSegadora para barriles\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nEspada de guerra antiguaSegadora para barriles"},
[475] = {title = "Una brisa problemática", objective = "Busca a Gaerolas Talvethren en la Aldea Brisa Estelar.", description = "Se avecinan problemas en el bosque.\n\nGaerolas Talvethren es el Gran Alcaide de los druidas de la Garra hibernantes en Túmulo de Ban'ethil. Su función como protector de los Adormecidos es preservar su seguridad de modo que no se rompa el pacto que tienen con Ysera. Viaja al este hasta la Aldea Brisa Estelar para hablar con Gaerolas y regresa para informarme de todo. Cuando sepa que mis hermanos duermen apaciblemente, me quedaré más tranquilo.", progress = nil, completion = "¡Gracias a los espíritus del bosque que estás aquí! Sabía que Athridas percibiría los problemas y enviaría ayuda.", rewards = "Recibirás:\n\n"},
[476] = {title = "Corrupción en Tuercepinos", objective = "Ve a ver a Athridas Manturso a Dolanaar.", description = "¡La tribu de los Tuercepinos está sumida en la corrupción!\n\nLos antes apacibles furbolgs se han vuelto en contra de los protectores del bosque. Me tendieron una emboscada cuando me marchaba para Túmulo de Ban'ethil y saquearon Aldea Brisa Estelar. Ursal el Violento, su jefe, está empleando los maléficos poderes del musgovil para volverlos locos.\n\nYo estoy malherido y no puedo llevarle estas trágicas noticias a Athridas por lo que tendrás que hacerlo tú, joven <clase>. Esperemos que los trastornados Tuercepinos no hayan llegado a Ban'ethil.", progress = nil, completion = "¡Por todas las estrellas! ¡Qué noticia tan alarmante!", rewards = "Recibirás:\n\n"},
[477] = {title = "Pasa la frontera", objective = "Recupera el contenido de una de las cajas de zahoríes de Dalaran. Encontrarás la Aldea Piroleña hacia el sur.", description = "Como sabrás, la ciudad de Molino Ámbar sigue siendo una fuente de oposición humana, principalmente gracias al apoyo que reciben de los magos de Dalaran.\n\nNo sé cuál es su plan, pero que se hayan interesado por un lugar tan remoto como Molino Ámbar, indica que tiene que ser más importante de lo que pensamos.\n\nHan estado enviando cajas por carro desde Trabalomas. Muchos de esos suministros acaban en un pequeño campamento al norte de la Aldea Piroleña. Consigue los contenidos de una caja y tráemelos.", progress = nil, completion = "&lt;Al destapar la caja, encuentras una selección de objetos cuidadosamente empaquetados. En la parte de arriba de la caja hay unos mapas amarillentos por el paso del tiempo, que parecen describir el Bosque de Argénteos y otras partes del oeste de Lordaeron.\n\nBajo los mapas hay un colgante de aspecto misterioso.&gt;", rewards = "Recibirás:\n\n"},
[478] = {title = "Mapas y runas", objective = "Lleva el extraño colgante al sacerdote de las Sombras Allister en El Sepulcro.", description = "<Rápidamente coges el colgante de la caja y vuelves a colocar la tapa.>", progress = "¿Has aprendido algo útil, <nombre>?", completion = "Mapas y este... colgante, ¿mmm? Interesante.\n\nEs obvio que los zahoríes de Dalaran están interesados en Molino Ámbar por alguna razón, pero aún así, no llego a entender el motivo.\n\nQuizás Dalar sepa algo.", rewards = "Recibirás:\n\n"},
[479] = {title = "Las investigaciones de Molino Ámbar", objective = "Consigue 8 colgantes de Dalaran al sacerdote de las Sombras Allister en El Sepulcro.", description = "Dalar está intentando localizar la fuente de los hechizos de los magos. Por ahora, tendremos que ralentizar su avance como podamos...\n\nLos conjuradores, magos y protectores tienen, sin lugar a dudas, los colgantes. Quítaselos.\n\nSigue hacia el sur por el camino principal y después, dirígete al este, a Molino Ámbar.", progress = "Debemos darnos prisa, no podemos permitir que lleven a cabo su plan, o podríamos perder nuestra posición en el Bosque de Argénteos.", completion = "Dalar ha descubierto que han avanzado bastante a pesar del revés.\n\nTienen a un zahorí de gran poder guiando la energía mágica. Tenemos que detenerle rápidamente.", rewards = "Recibirás:\n\n"},
[480] = {title = "El Tejedor", objective = "Mata al archimago Dalaran, recupera su bastón y llévaselo al sacerdote de las Sombras Allister en El Sepulcro.", description = "Un archimago de gran poder está supervisando la reactivación del nodo de energía Ley. Con el progreso que han conseguido, nuestra única esperanza de detenerles es matándole. Será difícil, estará protegido por muchos magos y guardianes.\n\nDebes ir y acabar con él rápidamente. Cuando esté muerto, tráeme su bastón. Ahora, vete.", progress = "¡Rápido, nos quedamos sin tiempo, <nombre>!", completion = "La magia de los zahoríes se disipa y las energías Ley entran en letargo. Tus esfuerzos han dado fruto, &lt;nombre&gt;, y hemos causado un duro golpe a nuestros enemigos y a los dominantes zahoríes de Dalaran.\n\nSin embargo, debemos ser vigilantes ya que sabemos que la fuente de la energía está tan cerca... Tenemos que hacer planes para el futuro.", rewards = "Podrás elegir una de estas recompensas:\n\nVara ceraniumTúnica de camuflaje\n\nTambién recibirás:\n\nVara ceraniumTúnica de camuflaje"},
[481] = {title = "El análisis de Dalar", objective = "Lleva el colgante con runas grabadas a Dalar Tejalba en El Sepulcro.", description = "Lleva el colgante a Dalar Tejalba, quizás pueda ayudarnos a entender los planes de los magos.", progress = "¿Sí, <clase>? ¿Qué quieres?", completion = "Interesante, parece que se trata de más maquinaciones del Kirin Tor. Se trata de un poderoso artefacto. Los poderosos zahoríes del Kirin Tor lo utilizaban frecuentemente para canalizar las energías mágicas.\n\nQue estén enviando tantos a Molino Ámbar indica que deben de estar preparando un proyecto de gran magnitud.\n\nA ver si consigo atravesar sus escudos y frustrar su intento...", rewards = "Recibirás:\n\n"},
[482] = {title = "Las intenciones de los Dalaran", objective = "Habla con el sacerdote de las Sombras en El Sepulcro.", description = "¡Por supuesto! ¿Cómo se me ha podido olvidar?\n\nMolino Ámbar fue uno de los lugares marcados por los Kirin Tor, debido a que tiene un nodo de energía Ley inactivo. Los magos deben de estar planeando reactivar el nodo y usar sus energías para algo.\n\nNo podemos permitir que esto ocurra...\n\nPara activar las energías Ley de la zona, necesitarán una gran cantidad de poder. Podemos paralizarlo todo si conseguimos los colgantes de los magos.\n\nCuéntale esto a Allister, él decidirá lo que tenemos que hacer.", progress = nil, completion = "Traes nefastas noticias, &lt;nombre&gt;. Si Dalar tiene razón, y no me cabe duda de que así es, debemos movernos rápidamente.\n\nTengo que decidir con él nuestro plan de ataque. Pero, mientras tanto, tendremos que retrasar su avance.", rewards = "Recibirás:\n\n"},
[483] = {title = "Las Reliquias del Despertar", objective = "Consigue las Reliquias del Despertar y llévaselas a Athridas Manturso a Dolanaar.", description = "Los invasores Tuercepinos han sido vistos saqueando Túmulo de Ban'ethil hacia el oeste.\n\nLos druidas durmientes quedarán encerrados en el Sueño Esmeralda por toda la eternidad, ajenos a sus destinos, a menos que les ayudemos. El delicado ritual de hibernación no puede detenerse sin las Reliquias del Despertar.\n\nViaja a El Cubil y consigue el talismán de la garra del cuervo, el plumón pluma negra, el zafiro de cielo y la runa de nidal que los druidas guardan en cofres sagrados. Tráemelos y podré preparar el ritual del despertar.", progress = "<nombre>, los druidas de la Garra secuestrados quedarán atrapados por toda la eternidad en el Sueño Esmeralda si no conseguimos las Reliquias del Despertar que se encuentran en el Túmulo de Ban'ethil, al oeste de aquí.\n\nCada minuto que pasa, están más cerca de su condena eterna.", completion = "¡Lo has conseguido, joven &lt;clase&gt;! Bien hecho. ¡Y justo a tiempo!", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos de malla de cortezaBastón de raíz de arena\n\nTambién recibirás:\n\nLeotardos de malla de cortezaBastón de raíz de arena"},
[484] = {title = "Piel de crocolisco joven", objective = "Consigue 4 pieles de cría de crocolisco para James Halloran de Puerto de Menethil.", description = "Soy James Halloran, el peletero. Mis pieles curtidas de crocolisco son las más apreciadas de todas y llegan cargamentos a todos los puertos del mundo.\n\n¿Viste a Einar? Es uno de los cazadores encargados de proporcionarme pieles de crocolisco.\n\nMe estoy quedando sin las suaves pieles de crías de crocolisco.\n\nPor eso te pagaré generosamente por una pila de esas pieles. Podrás encontrar crías de crocolisco en los pantanos que están a las mismas puertas de la ciudad.", progress = "<nombre>, ¿verdad? ¿Tienes mis pieles?", completion = "Has hecho un buen trabajo, &lt;nombre&gt;. Estas pieles son espléndidas. Tendré que curtirlas lo antes posible. Oh, pero antes de hacerlo, tengo otro trabajo del que podrías encargarte.", rewards = "Recibirás:\n\n"},
[485] = {title = "¡Encuentra a OOX-09/HL!", objective = "Lleva el localizador al robot mensajero de Oglethorpe, que está en el nacimiento del río de las Tierras del Interior, cerca de Roca Oculta.", description = "“Has descubierto una especie de instrumento extraño de metal y con forma de huevo. Si jugueteas con uno de los botones el huevo, este toma vida. ¡Se convierte en una especie de artilugio robótico gnomo! Y del interior del huevo sale una voz.\n\n\"Mi nombre es Oglethorpe Obnoticus, ¡y mi robot mensajero se ha averiado! Te recompensaré por su rescate. Por favor, lleva este localizador hasta el robot.\"\n\n\"He procesado las coordenadas del robot: falló en la cabecera del río, cerca de Roca Oculta.\"”", progress = "Parece que has descubierto los restos de... ¿un pollo robótico? La voz que proviene del huevo vuelve de nuevo a la vida:\n\n\"¡Sí, un trabajo excelente! Este es sin duda mi robot mensajero, aunque mis sensores indican que necesita un empujón para que pueda volar de regreso a Bahía del Botín para ser reparado. Coloca el localizador en el interior del robot. ¡El localizador hará todo lo demás!\"", completion = "Colocas el huevo... localizador de emergencia... dentro del pollo... el robot mensajero.\n\nDentro se oyen zumbidos y chirridos. Cuando el robot es revivido, se vuelve a oír la voz de Oglethorpe una vez más, pero ahora, viene de dentro del robot:\n\n\"OOX-09/TI funciona, pero necesita reparaciones. Es demasiado pesado para llevarlo, pero tengo una idea. ¿Puedes vigilarlo un rato más?\"", rewards = "Recibirás:\n\n"},
[486] = {title = "Ursal el Violento", objective = "Mata a Ursal el Violento y ve a ver de nuevo a Athridas Manturso a Dolanaar.", description = "Ha llegado el momento de salvar a los druidas de la Garra. Si fallamos ahora, <nombre>, nunca podrán despertar de su sueño.\n\nPrepararé las Reliquias del Despertar para llevar a cabo el ritual. Para que mis esfuerzos surtan efecto, hay que eliminar a las malditas bestias responsables de esta horrible situación. Solo en ese momento podrá completarse el ritual.\n\nUrsal el Violento fue quien se metió con nuestros hermanos y él será el que pague las consecuencias de su liberación. Ve a las Tierras de los Tuercepinos, en el sudeste, y mátalo.", progress = nil, completion = "&lt;nombre&gt;, has demostrado ser un &lt;clase&gt; de gran talento y valentía. Un &lt;raza&gt; que sigue el camino del honor del modo en que tú lo haces, sin duda alcanzará la gloria en este mundo.\n\nQue los espíritus del bosque te acompañen allá donde vayas.", rewards = "Podrás elegir una de estas recompensas:\n\nHacha de defensorGarrote de raíz de espinas\n\nTambién recibirás:\n\nHacha de defensorGarrote de raíz de espinas"},
[487] = {title = "El camino a Darnassus", objective = "Acaba con 6 emboscadores Tuercepinos y después ve a ver de nuevo a Amara Andanoche a las afueras de Dolanaar.", description = "Hay que reforzar la seguridad en el camino que conduce a Darnassus. Algunos que han viajado de Dolanaar a Darnassus nos han informado de que en ese camino los corruptos furbolgs de la tribu de los Tuercepinos llevan a cabo despiadados ataques.\n\nEse camino se utiliza a diario para transporte de mercancías y es la vía de comunicación más importante de Darnassus. No podemos permitir que esa pandilla de delincuentes paganos aterrorice a la gente.\n\nLevántante en armas en nombre del bosque sagrado, <clase>. Su guarida se encuentra en algún lugar cerca de aquí. Acaba con 6 furbolgs Tuercepinos y después vuelve aquí.", progress = nil, completion = "Has servido con diligencia a las gentes de Dolanaar y Darnassus, valiente &lt;clase&gt;. Como miembro del Escuadrón de centinelas de Teldrassil, aplaudo tus esfuerzos.", rewards = "Recibirás:\n\n"},
[488] = {title = "Las apuestas de Zenn", objective = "Llévale a Zenn Nauseapezuña a las afueras de Dolanaar 3 colmillos de sable de la noche, 3 plumas de lechuza estrígida y 3 muestras de seda de araña Tejemadera.", description = "Veo que tienes ganas de trabajar... Por suerte para ti, no hay un día que pase que no desee tener cerca a un inocente <clase> a quien proponerle mi oferta.\n\n¿Sabes, <nombre>? Puedo hacerte muy feliz y ofrecerte cosas que jamás hubieras soñado tener, pero para ello deberás traerme una serie de objetos.\n\nPara desempeñar mi trabajo en el bosque a veces necesito ciertos... componentes. Tráeme 3 colmillos de sable de la noche, 3 plumas de lechuza estrígida y 3 muestras de seda de araña Tejemadera.\n\nY que esto quede entre nosotros, <raza>.", progress = "¿Por qué has tardado tanto, <nombre>? He estado esperando a que llegaras con todo lo que necesito.", completion = "¡Ja, ja, ja! ¡Lo has hecho!\n\n¿Quién habría imaginado que yo, Zenn Nauseapezuña, conseguiría que un &lt;raza&gt; hiciera lo que se me antojara? ¡Yo no, la verdad! Pero así es... este maravilloso mundo está lleno de sorpresas.\n\n¡Tres hurras por nuestro inocente de hoy!", rewards = "Recibirás:\n\nGarra de vudú cortada"},
[489] = {title = "¡Búsqueda de redención!", objective = "Recoge 3 conos inferi y dáselos a Zenn Nauseapezuña, que está a las afueras de Dolanaar.", description = "El Consejo del Bosque se ha enterado de que ayudaste a Zenn Nauseapezuña, un sátiro enemigo del bosque. Como buen <raza> que eres, no deberías dedicarte a mancillar el bosque matando criaturas de la Naturaleza.\n\nHas de reparar tu error ante el Consejo si deseas seguir siendo <amigo/amiga> de Teldrassil.\n\nDale a Nauseapezuña una lección y recibirás tu perdón. Los conos inferi son semillas corruptas que caen de los árboles y que expulsan humo verde.\n\nDale algunas a Nauseapezuña, él creerá que se trata de un inofensivo aperitivo.", progress = "¿Qué tienes para mí, <nombre>? Parece un rico aperitivo...", completion = "¡Ah, qué maravilla de &lt;raza&gt;! Sabía que me serías de ayuda.", rewards = "Recibirás:\n\n"},
[491] = {title = "Una varita para Bethor", objective = "Lleva la varita tejida a Bethor Duroyelo en el Barrio de la Magia, en Entrañas.", description = "Una vez Thule me dijo que, mucho tiempo atrás, él y dos de sus colegas crearon una varita mágica, combinando las varitas de aprendiz que cada uno de los magos tenía. Esta varita fue un símbolo de su amistad y, aunque Thule llegó a despreciarla, no pudo deshacerse de ella, ni permitir que los otros la tuvieran. Así que robó la varita y la escondió en el Castillo de Fenris.\n\nAquí. Lleva la varita a Bethor Duroyelo, uno de los antiguos amigos de Thule. Seguro que la quiere.", progress = "<nombre>, ¿has derrotado a Thule Corvozarpa?", completion = "¿Qué es esto? ¿Thule tenía nuestra varita tejida? Creí que la había perdido cuando nuestra amistad se destruyó y se alió con el rey Exánime.\n\nNo ha invocado su poder porque, si lo hubiera hecho, yo lo habría sentido. Y me alegro de que no la haya usado...\n\nEs un descubrimiento maravilloso. Tienes mi gratitud, &lt;nombre&gt;, y recibirás una recompensa.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones estampadosGuantes de malla bastaCuchillo dentado\n\nTambién recibirás:\n\nPantalones estampadosGuantes de malla bastaCuchillo dentado"},
[492] = {title = "Una nueva Peste", objective = "Llévale la bebida especial de Johaan al montaraz capturado.", description = "De acuerdo con el guardia de la muerte, acaban de capturar a otro de esos insensatos enanos montaraces. El guardia de la muerte suele encerrar a los prisioneros en el sótano del Mesón La Horca hasta que pueda encargarse \"debidamente\" de ellos.\n\n¿Por qué no vas a comprobar si al montaraz capturado le gusta la bebida especial que he preparado para él? Contiene una pequeña muestra de lo que la Dama Oscura tiene reservado para todo Azeroth.", progress = "Si tuviera mi rifle acabaría contigo, <clase>. ¡Espera a que la Brigada de los tanques de vapor me saque de aquí!", completion = "¡Ah, por fin algo que beber! Estoy seguro de que no es malta rapsódica pero en este momento mojaría el garguero con cualquier cosa.", rewards = "Recibirás:\n\n"},
[493] = {title = "Viaja a Laderas de Trabalomas", objective = "Lleva los descubrimientos del boticario Renferrel al boticario Lydon en la ciudad de Molino Tarren, en las Laderas de Trabalomas.", description = "La Dama Oscura está presionando mucho a la Sociedad Real de Boticarios para que creen una Nueva Plaga. Estamos trabajando diligentemente y hemos avanzado mucho. Creemos que lo conseguiríamos antes si compartiésemos información entre la Sociedad.\n\nPor esa razón, quiero que entregues mis últimos descubrimientos al boticario Lydon en el Molino Tarren, una pequeña ciudad en las Laderas de Trabalomas. El viaje será largo.\n\nDirígete al sur y no te alejes de los caminos. ¡Sigue las señales de cerca!", progress = "¿Tienes algo para mí?", completion = "¡Excelente, excelente! El boticario Renferrel se merece todo mi respeto. Me muero por estudiar su obra.", rewards = "Recibirás:\n\n"},
[494] = {title = "El momento de atacar", objective = "Ve a Molino Tarren para llevar el mensaje del mortacechador Lesh a la suma ejecutora Darthalia.", description = "Cállate y presta atención, <clase>.\n\nMe han enviado aquí para vigilar Trabalomas. La mía es una misión de reconocimiento. Debes enviar un mensaje a la suma ejecutora Darthalia, a Molino Tarren, de inmediato. Dile que el mortacechador Lesh le envía este mensaje:\n\n\"Desde el oeste nos llega el llanto del cuervo.\"\n\nSigue el camino que va al este y fíjate bien en las indicaciones. ¡Apresúrate! ¡Ve a Molino Tarren a toda prisa!", progress = nil, completion = "\"Desde el oeste nos llega el llanto del cuervo.\"\n\n¡Qué noticia tan excelente! Todo está a punto para arrasar Trabalomas. Esto complacerá a Varimathras.", rewards = "Recibirás:\n\n"},
[495] = {title = "La Corona de la Voluntad", objective = "Informa a Melisara que está en Molino Tarren, en las Laderas de Trabalomas.", description = "Mug'Thol y sus ogros debían ir a Alterac en busca de la Corona de la Voluntad para Lady Sylvanas. Por desgracia, en lugar de regresar con ella, el muy idiota decidió probarla... Supongo que los ogros no se pueden resistir a nada luminoso y brillante.\n\nSin embargo, su constante incompetencia se está convirtiendo en un problema. La Corona de la Voluntad le permitió resistirse a la posesión que le obligaba a servir a Lady Sylvanas.\n\nLa Dama Oscura quiere darle un castigo ejemplar; viaja al Molino Tarren e informa a Melisara, mi teniente.", progress = nil, completion = "¿Te envía Shalindra? Muy bien, pues vayamos al grano.\n\nMug'Thol es un estorbo así que debemos ocuparnos de él sin perder ni un segundo.", rewards = "Recibirás:\n\n"},
[496] = {title = "Elixir del sufrimiento", objective = "El boticario Lydon de Molino Tarren quiere 10 lenguas de oso gris y un poco de icor de trepador.", description = "Otro maldito día en el Molino Tarren. Todo este aire puro me pone enfermo, <nombre>.\n\nTenemos que infestar a los humanos cuanto antes. He estado estudiando potenciales agentes letales para utilizar en mis brebajes pero no he tenido tiempo de recogerlos todos.\n\nSi quieres hacer algo útil, podrías procurarme los siguientes objetos: diez lenguas de oso gris e icor de trepador, que es muy raro y difícil de encontrar. Nada más salir del Molino Tarren encontrarás osos y trepadores.", progress = "Ah, ya estás aquí. Espero que traigas los ingredientes; me deprimo si paso más de un día sin matar nada.", completion = "Muy bien, perfecto. Gracias por arrojar un tenue rayo de alegría a otro día sombrío.", rewards = "Recibirás:\n\n"},
[498] = {title = "El rescate", objective = "Krusk de Molino Tarren quiere que liberes a Drull y a Tog'thar en el Castillo de Durnholde.", description = "El Jefe de Guerra me envió con algunos compañeros a Durnholde para recoger un objeto e investigar a esa tal Hermandad.\n\nFuimos a echar un vistazo, pero un numeroso grupo de humanos nos atacó por sorpresa. Luchamos como fieras, pero estábamos en franca minoría. Me capturaron, pero conseguí escapar.\n\nDurnholde está al otro lado del río, hacia el sureste. Drull y Tog'thar están retenidos allí; debes ir a rescatarlos.", progress = nil, completion = "Así que los humanos querían esclavizarnos, ¿eh? ¡Eso ya lo veremos!\n\nParece que han trasladado a Gol'dir; debemos encontrarlo y rescatarlo.\n\nTe avisaré cuando tengamos alguna pista de su paradero.", rewards = "Podrás elegir una de estas recompensas:\n\nJubón de brutoCadena de guerra orca\n\nTambién recibirás:\n\nReceta: chuleta de oso grandeCadena de guerra orcaJubón de bruto"},
[499] = {title = "Elixir del sufrimiento", objective = "Dale el elixir del sufrimiento a Umpi.", description = "Tardaré un momento en mezclar estos ingredientes para crear lo que yo llamo mi elixir del sufrimiento.\n\n¡Allá vamos! Ah, nada me alegra tanto la vida en este lugar miserable como traer padecimientos al mundo.\n\nVeamos si la poción tiene el efecto deseado.\n\nDale a probar a mi asistente Umpi lo que la Dama Oscura ha planeado tanto para los humanos como para la Plaga.", progress = "¡Croac!", completion = "¡Croac!", rewards = "Recibirás:\n\n"},
[500] = {title = "La recompensa Aplastacresta", objective = "Recoge 9 huesos sucios de orcos Aplastacresta en las Montañas de Alterac. LLévaselos al mariscal Rutagrana a Costasur.", description = "Los ogros Aplastacresta han construido un túmulo en las Montañas de Alterac, cerca de la ciudad arrasada de Alterac. Los rastreadores indican que también han invadido las ruinas.\n\nSi dejamos que se queden ahí, su siguiente paso será bajar las colinas y llegar hasta nosotros.\n\nVe a liquidar ogros en las Montañas de Alterac, hacia el norte. Si quieres ganar un buen botín, tráeme todos los huesos sucios que llevan.", progress = "¿Estás cazando ogros, <nombre>? ¿Me traes los huesos sucios que te pedí?", completion = "Qué buen montón de huesos sucios me traes, &lt;nombre&gt;.\n\nEspero que ahora los ogros nos dejen tranquilos; al menos, de momento.", rewards = "Recibirás:\n\n"},
[501] = {title = "Elixir del dolor", objective = "Lleva 10 viales de sangre de puma al boticario Lydon de Molino Tarren.", description = "Oh, <nombre>. En Trabalomas todo está florecido y el aire es puro y fresco. No puedo soportarlo. Miro por mi mugrienta ventana y espero el día en que nuestra Nueva Plaga traiga a este mundo la muerte que merece.\n\nAunque me gustaría perderme en mis negras reflexiones, el deber hacia la Dama Oscura me llama. ¿Quieres ayudarme? Según mis estudios, puedo obtener una estupenda poción letal con sangre de puma. Tráeme un poco, verás qué bien lo pasaremos.", progress = "Ah, sí, <nombre>; ibas a ayudarme con mi elixir. Necesito la sangre para extender el dolor y la muerte en Trabalomas.", completion = "Excelente trabajo, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de BrawnBotas de pisotónBotas de caminante de fuego\n\nTambién recibirás:\n\nReceta: chuletas de león picantesGuantes de BrawnBotas de pisotónBotas de caminante de fuego"},
[502] = {title = "Elixir del dolor", objective = "Dale el elixir del dolor a Stanley.", description = "¡No hay nada como el olor a sangre fresca!\n\nAhora añadimos un poquito de esto y una pizca de esto otro ¡y ya está! Esta poción se llama el elixir del dolor. Si mis cálculos son correctos, este brebaje complacerá enormemente a Lady Sylvanas. Pero antes hay que probarlo.\n\nLlévalo a la granja norteña de Campos de Trabalomas, que está al suroeste. A ver qué le parece esta golosina a Stanley, el perrito del granjero Ray.", progress = "Guau.", completion = "Te pones un poco de elixir en el hueco de la mano y se lo das a Stanley.", rewards = "Recibirás:\n\n"},
[503] = {title = "Gol'dir", objective = "Libera a Gol'dir e informa a Krusk en Molino Tarren.", description = "Según los documentos que has traído, tienen a Gol'dir en una importante base de la Hermandad situada al norte, probablemente en las Montañas de Alterac. Ya lleva demasiado tiempo cautivo; hay que liberarlo.", progress = "Dame la llave, ya me ocupo yo.", completion = "Gracias, &lt;nombre&gt;. Yo era el jefe del grupo, pero como ves las cosas no han salido como esperábamos. Me han tenido a trabajos forzados.\n\nPero mi sufrimiento no es importante; tengo información que debes llevar de inmediato a quien sea que nos lidera ahora.", rewards = "Recibirás:\n\n"},
[504] = {title = "Belicistas Aplastacresta", objective = "Mata a 15 belicistas Aplastacresta y vuelve a hablar con el mariscal Rutagrana en Costasur.", description = "Ahora que has conocido a los ogros Aplastacresta, quiero que les revientes las narices...\n\nVe a las Ruinas de Alterac y busca a esos Aplastacresta pendencieros. Quiero que liquides a un buen número de ellos. Es la única forma de que esos brutos aprendan a mantenerse alejados del territorio de la Alianza.", progress = nil, completion = "Muy bien; aquí tienes la recompensa.\n\nDime, ¿cómo has visto Alterac? Supongo que la cosa estará mal; los ogros no son famosos por ser limpios.\n\nPor suerte, gracias a tus esfuerzos se mantendrán alejados de Costasur.", rewards = "Podrás elegir una de estas recompensas:\n\nPlata ardienteRodela lunar\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nRodela lunarPlata ardiente"},
[505] = {title = "Hermandad de asesinos", objective = "Mata a 12 asaltantes de caminos y 8 ladrones de la Hermandad e informa al magistrado Henry Maleb en Costasur.", description = "Soy el nuevo magistrado de Costasur. Mi ilustre predecesor fue asesinado. No se ha encontrado a los asesinos, pero la investigación apunta a que eran mercenarios de la Hermandad, un grupo de ladrones liderados por unos nobles sin honor del desaparecido reino de Alterac.\n\nLas pistas señalan al campamento que tienen al norte de Molino Tarren, territorio ocupado por la Horda. Elimina a los miembros de la Hermandad que te encuentres.", progress = nil, completion = "Gracias, &lt;nombre&gt;. Espero que algunos de los asaltantes de caminos y ladrones que has matado fueran responsables de la muerte del magistrado. Y aunque no fuera así, la Hermandad es una lacra que debe ser erradicada. \n\nEn Costasur se celebran esas muertes.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón cruzadoGuantes aislantes de sabio\n\nTambién recibirás:\n\nCinturón cruzadoGuantes aislantes de sabio"},
[506] = {title = "El legado de Lodonegro", objective = "Lleva la información recopilada por Gol'dir a Krusk a Molino Tarren.", description = "Hace uno o dos días me visitó el líder de una organización que se llama la Hermandad. Su nombre era Aliden Perenolde y se considera a sí mismo el señor de esta tierra. Me confió sus planes de convertir en esclavos a los orcos que se enfrentan a él, tal y como su padre y su mentor, el despreciado Lodonegro, habían planeado.\n\nPero lo más importante es que traía consigo a su querida, una chica menudita llamada Elysa. Que llevaba puesto el colgante que desea el Jefe.\n\nAhora ve. Yo seguiré mi camino cuando haya acabado de arreglar mis asuntos.", progress = nil, completion = "¡Pero cómo se atreven esos humanos!\n\nAhora, además de recoger el abalorio para el Jefe de Guerra, nos ocuparemos de demostrarles que no nos someteremos nunca.", rewards = "Recibirás:\n\n"},
[507] = {title = "Lord Aliden Perenolde", objective = "Mata a Aliden Perenolde y pregunta a su querida, Elysa, por el colgante de Taretha.", description = "Es evidente que hay que eliminar a Perenolde, erradicar su vileza. Su mansión está hacia el norte, a orillas del Lago Lordamere. <nombre>, cumple con tu deber, recupera el colgante que anhela el Jefe de Guerra y vuelve aquí.", progress = nil, completion = "¡No me hagas daño! ¡Yo no he hecho nada, me tenía retenida! ¡Piedad, te lo ruego!", rewards = "Recibirás:\n\n"},
[508] = {title = "Un regalo de Taretha", objective = "Devuélvele el colgante de Taretha a Krusk Molino Tarren.", description = "¿El colgante? Claro que te lo puedes quedar... ¡Llévatelo! No quiero ningún recuerdo de él... tan solo, solo deja en paz...", progress = "Has vuelto, <nombre>. ¿Algún éxito que comunicar?", completion = "Es extraño que el Jefe de Guerra se gaste tanto en una cosa tan pequeña. No puedo decir que lo entienda pero acataré sus órdenes, sean las que sean. Lo has hecho muy bien, &lt;nombre&gt;, seguro que el Jefe de Guerra se alegrará de conocer tu éxito.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de mantisEspaldares de Brigand\n\nTambién recibirás:\n\nBotas de mantisEspaldares de Brigand"},
[509] = {title = "Elixir de agonía", objective = "Lleva 6 flores de morrobarro al boticario Lydon de Molino Tarren.", description = "Un día más, un elixir más. Si por mí fuera, dejaría que esos ridículos humanos y la Plaga se mataran mutuamente. De hecho, ese parece ser su único objetivo.\n\nNuestra Dama Oscura quiere acelerar el proceso, así que ¿por qué no pasar estos negros días sembrando muerte y enfermedad por el lugar?\n\nEste es el plan, <nombre>: ve a la Granja Nethander, al sur de Molino Tarren y al este del río y de Costasur. Tráeme seis flores de morrobarro para mi próximo elixir.", progress = "¿Qué tal en Granja Nethander?", completion = "Detesto la peste de las flores de morrobarro. Al menos esos asquerosos gnolls sirven para algo.", rewards = "Recibirás:\n\nPoción de velocidadPoción de curación"},
[510] = {title = "Documentos de planificación", objective = "Lleva los documentos de planificación al magistrado Maleb a Costasur.", description = "Entre los documentos hay un pedido de suministros. Incluye objetos de uso diario (alimentos, armas, ropas) y otros menos usuales (máscaras, combustible, veneno).\n\nPero lo más importante es la urgencia del pedido: los suministros eran necesarios para lo que se describe como \"una importante misión contra Costasur.\"", progress = "¿Qué te preocupa, <nombre>? ¿Tienes algo que decir?", completion = "&lt;Maleb lee los documentos.&gt;\n\nEsto es muy inquietante, &lt;nombre&gt;. Por lo que dice aquí y por las acciones de la Hermandad, esos rufianes han declarado la guerra a Costasur.", rewards = "Recibirás:\n\n"},
[511] = {title = "Una carta cifrada", objective = "Lleva la carta cifrada al maestro del Conocimiento Dibbs en Costasur.", description = "Una de las cartas de la mesa está cifrada y es inescrutable.", progress = "¿Querías algo?", completion = "¿Qué es esto? Déjame ver...\n\nNunca había visto nada parecido pese a mi considerable experiencia.", rewards = "Recibirás:\n\n"},
[512] = {title = "Nobles muertes", objective = "Lleva 7 anillos con sello de Alterac al magistrado Maleb a Costasur.", description = "Temo que la Hermandad aspire a provocar la caída de Costasur. Debemos eliminar a la cúpula para que no puedan llevar a cabo sus planes.\n\nVe a las ruinas de Strahnbrad; están al norte, en las Montañas de Alterac. Creemos que la Hermandad se concentra en esa zona.\n\nSabemos que los jefes de estos maleantes son antiguos nobles de Alterac. Puede que estén disfrazados de agentes ordinarios de la Hermandad, pero los distinguirás por los anillos de sello que llevan.\n\nTráeme los anillos.", progress = "¿Te estás ocupando de la Hermandad? ¿Tienes los anillos?", completion = "Este golpe reducirá la libertad de acción de la Hermandad y los reducirá a la banda de maleantes que son.\n\nSirves muy bien a la Alianza, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[513] = {title = "Elixir de agonía", objective = "Lleva el compuesto de morrobarro al maestro boticario Faranell a Entrañas.", description = "Justo cuando pensaba que este sería otro día triste y sin alegría, me traes estas maravillosas flores de morrobarro.\n\nEl primer paso es molerlas. Luego el boticario Faranell de Entrañas aplicará su coloide de decadencia y ya estará listo.\n\nLlévale este compuesto de morrobarro al boticario a toda prisa, <nombre>.", progress = "¿No ves que estoy ocupado, <clase>?  La Dama Oscura quiere que prepare una nueva plaga antes de que la Plaga nos invada desde Rasganorte. Los humanos amenazan desde el sur y a mí me falta el tiempo. ¡Espero que esto sea importante!", completion = "¿Que te envía el boticario Lydon? ¡Haberlo dicho!\n\nPese a su pesimismo innato, es uno de los mejores.\n\n¡Y me envía compuesto de Morrobarro! Me gusta su línea de pensamiento. La Dama Oscura estará muy complacida con los avances en la nueva peste.", rewards = "Recibirás:\n\n"},
[514] = {title = "Una carta para Pico Tormenta", objective = "Lleva la carta muy bien cifrada al prospector Pico Tormenta a Forjaz.", description = "Tengo un colega en Forjaz que quizás pueda descifrarla. Ve a la biblioteca de la Sala de los Exploradores, en Forjaz, y habla con el prospector Pico Tormenta. Dile que vas de mi parte y enséñale la carta. Si hay alguien que pueda descifrar esto al norte de Cumbre de Roca Negra, ese es él.", progress = "Así que te envía Dibbs de Costasur, ¿eh? ¿Cómo le va a esa ratilla de biblioteca?", completion = "Ah, sí, soy un especialista en códigos cifrados. A ver...", rewards = "Recibirás:\n\n"},
[515] = {title = "Elixir de agonía", objective = "Lleva la mezcla de morrobarro, una poción fuerte de sangre de trol, 5 escamas de dagaespinazo y 5 ojos de rompeanca al boticario Lydon a Molino Tarren.", description = "¿Un compuesto de morrobarro? ¡Brillante! ¿Cómo no se me ha ocurrido a mí?\n\nVoy a añadir mi coloide de decadencia a este maléfico brebaje.\n\nPara activar los agentes contaminantes de este preparado, Lydon necesitará una poción fuerte de sangre de trol, escamas de dagaespinazo y ojos de rompeanca de la costa sur de Trabalomas. Él ya sabrá qué hacer con esos reactivos. ¡Ardo en deseos de saber el resultado del experimento!", progress = "Me alegro de verte, <nombre>. Me estaba aburriendo mortalmente, sin nada que hacer salvo envenenar y reanimar a Umpi una y otra vez.\n\n¿Qué tal en Entrañas? ¿Pudiste eliminar a muchos humanos?", completion = "¡Por fin, algo con lo que entretenerme! Gracias por tu ayuda, &lt;nombre&gt;. ¡Vamos a extender la muerte y la enfermedad por el lugar!", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de gran boticarioFajín de meditación\n\nTambién recibirás:\n\nFajín de meditaciónCapa de gran boticario"},
[516] = {title = "El Desafío de Beren", objective = "Encuentra El Desafío de Beren y, a continuación, mata 6 esclavos Corvozarpa y 6 guardianes Corvozarpa. Por último, vuelve a ver al sacerdote de las Sombras Allister en El Sepulcro.", description = "Me han informado sobre un grupo de no-muertos que están reagrupándose para preparar un ataque contra nosotros. Con esta información, podemos hacer algo y atacar primero, poniendo fin de un manotazo a su pequeño plan.\n\nPor desgracia, la información de la que dispongo es bastante incompleta. Se supone que están escondidos en El Desafío de Beren. Se desconoce la ubicación exacta, pero parece ser una cueva en las colinas, cerca de unos magos de Dalaran.\n\nConfío en tus recursos. Encuéntrales y acaba con ellos.", progress = nil, completion = "Si nuestros exploradores y agentes continúan proporcionándonos información valiosa como esta, tendremos a la Plaga del rey Exánime fuera de nuestras tierras en un abrir y cerrar de ojos.", rewards = "Recibirás:\n\nVarita de Descomposición"},
[517] = {title = "Elixir de agonía", objective = "Lleva un barril de cerveza Machacacanillas al boticario Lydon de Molino Tarren.", description = "El nuevo elixir ya está listo para probar. Pero sería una pena malgastar una plaga potente y altamente contagiosa en un sapo o un perro. Vamos a divertirnos en nombre de Lady Sylvanas.\n\nVete a Dun Garok. ¿Sabes las barracas en las que se hacinan esos asquerosos enanos? Tráeme un barril de cerveza Machacacanillas. Y cuando lo traigas nos pegaremos una buena fiesta, <nombre>.", progress = "A veces me aburro tanto aquí en Molino Tarren que me da por probar cócteles de químicos.  Pero Umpi se pone nerviosa.\n\nBueno, ¿traes el barril? Si no enveneno pronto a alguien con mi elixir mortal me va a dar un ataque.", completion = "¡Por fin podemos pasar a la acción!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[518] = {title = "Corona de la Voluntad", objective = "Melisara de Molino Tarren quiere que mates a 14 destructores Aplastacresta.", description = "Mug'Thol y sus ogros Aplastacresta han invadido las ruinas de Alterac. Tenemos que reducir sus filas si queremos castigarlo.\n\nPrimero los debilitamos y después procedemos,\n\n¿entendido? Bien. Adelante.", progress = nil, completion = "La nieve está roja con la sangre de los ogros, pero no debemos cejar hasta que los Aplastacresta muerdan el polvo y sean aniquilados por completo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[519] = {title = "Corona de la Voluntad", objective = "Mata a Targ, Muckrake y Glommus y lleva sus cabezas a Melisara a Molino Tarren.", description = "Targ, Muckrake y Glommus son los generales de Mug'Thol. Le son leales, aunque quién sabe qué instiga la lealtad de un ogro.\n\nTráeme sus cabezas, <nombre>, y después solo quedará separar a Mug'Thol de la suya, corona incluida.", progress = "<nombre>, quiero cabezas, no excusas.", completion = "Y yo que creía que lo peor de los ogros era el olor. \n\nCuando me traigas la cabeza de Jarra'thol, clavaré las 4 cabezas en picas; será un aviso para los que contrarían a la Dama Oscura.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[520] = {title = "Corona de la Voluntad", objective = "Mata a Mug’Thol y lleva su cabeza con la Corona de la Voluntad a Melisara a Molino Tarren.", description = "Con los tenientes de Mug'Thol muertos, ya solo queda matar a Mug'Thol y hacerse con la Corona de la Voluntad. No tengo dudas de que no querrá dártela voluntariamente, y menos ahora que has acabado con sus servidores.\n\nEncuentra a Mug'Thol y tráeme su cabeza y su corona. La resistencia Aplastacresta tiene que terminar.", progress = "Tengo 3 cabezas, me falta una. Si no es la de Jarra'thol, será la tuya, <nombre>; tenlo por seguro.", completion = "Bien hecho, &lt;nombre&gt;. La cabeza de Jarra'thol adornará la cumbre más alta de las Ruinas de Alterac y la Guardia de la Muerte acabará de exterminar a los Aplastacresta.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[521] = {title = "Corona de la Voluntad", objective = "Ve a Entrañas para entregarle la Corona de la Voluntad a Sharlindra.", description = "Ahora solo falta ir a Entrañas para entregarle la corona a Sharlindra. Ella se ocupará de entregársela a Lady Sylvanas. Te advierto que no debes usarla para tus propios fines, <nombre>; recuerda lo que le ha pasado a Mug'Thol.", progress = "¿Sí? ¿Tienes alguna noticia?", completion = "Así que esto es lo que usó el ogro para enfrentarse a la Dama Oscura. \n\nCuesta creer que algo tan sencillo tenga tanto poder. Buen trabajo, &lt;nombre&gt;. Entregaré la corona a Lady Sylvanas y le hablaré de tu proeza.", rewards = "Recibirás:\n\nTalismán etéreo"},
[522] = {title = "Contrato de asesino", objective = "Lleva el contrato de asesinato al magistrado Maleb a Costasur.", description = "Esto es un contrato para el asesinato del magistrado Henry Maleb. Estaba sellado con cera roja y un anillo de sello muy distintivo.", progress = "¡Vaya, qué prisa traes! ¿Tienes noticias?", completion = "&lt;Maleb estudia el contrato.&gt;\n\nEsto explica el ataque a Costasur; la Hermandad quiere liquidarme como hicieron con mi predecesor.", rewards = "Recibirás:\n\n"},
[523] = {title = "Muerte al Barón", objective = "Lleva la cabeza del Barón Vardus al magistrado Maleb a Costasur.", description = "Está firmado por el Barón Vardus, un noble del antiguo reino de Alterac conocido por su crueldad y brutalidad. No me sorprende que el Barón sea responsable de esto.\n\nDebes buscarlo. Encuéntralo y entrégalo a las autoridades. Debe de estar en alguna guarida de la Hermandad, en las ruinas de Strahnbrad o en el valle que hay al norte de ellas.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[524] = {title = "Elixir de agonía", objective = "Coloca el barril contaminado en la alfombra para los granjeros.", description = "Los guardias de la muerte tienen retenidos a varios granjeros. La suma ejecutora Darthalia sospechaba que uno de los estrategas militares de Ventormenta estaba entre ellos. Al parecer fue encontrado muerto anoche en las Tierras de la Peste. Darthalia, siempre pensando en mí, decidió que yo me ocuparía de los prisioneros.\n\nCreo que ya es hora de montarles una fiesta a los granjeros. ¡Va a ser estupendo!\n\nColoca este barril contaminado en la alfombra de su cuarto. ¡Y que corra la plaga!", progress = "La alfombra huele a moho.", completion = "Dejas el barril contaminado en la alfombra polvorienta.", rewards = "Recibirás:\n\n"},
[525] = {title = "Más misterios", objective = "Lleva la carta descifrada al magistrado Maleb a Costasur.", description = "He descifrado la carta, pero no sé cómo interpretarla. ¿Y la tenían unos ladrones en las laderas de Alterac? Qué raro…\n\nLlévasela al magistrado de Costasur. Aunque la carta es críptica, su ciudad es la más cercana a Alterac; si se está cociendo algo, es mejor que lo sepa.", progress = "<nombre>, ¿has estado de viaje por las montañas otra vez? ¿Qué noticias traes?", completion = "&lt;Maleb lee la carta.&gt;\n\n¿Esto estaba en un campamento de la Hermandad? Esto no lo escribió un ladrón ni tampoco un noble de Alterac.\n\nParece que la Hermandad está aliada con otro poder; me estremezco al pensar cuál puede ser.", rewards = "Recibirás:\n\n"},
[526] = {title = "Lingotes Forjaluz", objective = "Reúne 5 lingotes Forjaluz y vuelve con Glorin Frenteacerada en Los Humedales.", description = "Has encontrado un lingote Forjaluz en el cofre, ¡pero no es suficiente para hacer nada decente!\n\nDeberías volver y buscar más. Quizás los murlocs que rodean los restos del naufragio tengan más lingotes Forjaluz.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[527] = {title = "La batalla de Trabalomas", objective = "Mata a 6 jornaleros y 6 granjeros de Trabalomas, al granjero Ray y al granjero Getz e informa a Darthalia en Molino Tarren.", description = "Ponte firme cuando te dirijas a mí, <clase>.\n\nEstoy a las órdenes directas de Varimathras. Debemos sofocar la infestación humana hasta que nuestros boticarios desarrollen la nueva plaga.\n\nNuestros datos indican que Trabalomas es vulnerable. Tu primera misión es interrumpir el abastecimiento de alimentos y su infraestructura.\n\nVe hacia el oeste. Ataca las granjas más septentrionales de Campos de Trabalomas. Asegúrate de que los propietarios, el granjero Ray y el granjero Getz, son ejecutados.", progress = nil, completion = "Descansa, &lt;clase&gt;. Mis fuentes me indican que luchaste bien en el frente de Trabalomas.", rewards = "Recibirás:\n\n"},
[528] = {title = "La batalla de Trabalomas", objective = "Mata a 15 campesinos de Trabalomas e informa a Darthalia en Molino Tarren.", description = "El ataque a las granjas del norte tiene aterrados a los lugareños. Excelente.\n\nNuestros mortacechadores informan que los labriegos huyen a los campos del sur de Trabalomas. Esos necios creen que pueden escapar a su destino escondiéndose en el huerto.\n\nPero se equivocan, <nombre>. Vuelve a Campos de Trabalomas y busca a los labriegos ocultos. Aniquílalos en el nombre de nuestra Dama Oscura.\n\nPuedes retirarte.", progress = nil, completion = "La muerte cierra el cerco en torno a Trabalomas. La batalla va bien.", rewards = "Recibirás:\n\n"},
[529] = {title = "La batalla de Trabalomas", objective = "Mata al herrero Verringtan y a 4 aprendices de herrero de Trabalomas.\n\nRecupera un cargamento de hierro e informa a Darthalia en Molino Tarren.", description = "Los humanos de Trabalomas han opuesto más resistencia de la esperada. Al parecer, el herrero del lugar está fabricando armas. Tenemos que interrumpir todo suministro.\n\nTu misión es ejecutar al herrero Verringtan. Elimina también a los aprendices. Y trae un cargamento de hierro de su almacén. Nos irá bien cuando terminemos con Trabalomas y pasemos a conquistar Costasur.", progress = "¿Cumpliste la misión, <clase>? ¿Eliminaste a Verringtan y sus aprendices? ¿Dónde está el cargamento de hierro?", completion = "Misión cumplida, &lt;clase&gt;. Y cumplida con gran talento, además.", rewards = "Recibirás:\n\n"},
[530] = {title = "La venganza de un esposo", objective = "Mata a Valdred Moray.\n\nLlévale sus manos a Raleigh Andrean, que está en Entrañas.", description = "Valdred, el que una vez fue mi mejor amigo, es el asesino de mi esposa. Aunque soy incapaz de llorar su muerte, sí que anhelo venganza. Si matas a Valdred y me traes sus manos malditas, te recompensaré.\n\nLa última información de que dispongo es que se encuentra en La Muralla de Cringris, al sur del Bosque de Argénteos, con intención de escapar al reino de Gilneas.", progress = "¿Tienes las manos, <nombre>?", completion = "Ah, bien. Estas son las manos que dieron muerte a mi esposa, Deliah. Lo único que quería era vengarme de su asesino, y tú lo has hecho posible.\n\nToma, quédate el anillo que me trajiste. Hace tanto tiempo de la muerte de Deliah, que ya no significa nada para mí. Las manos, sin embargo, las conservaré para recordar la traición del que fue mi amigo.", rewards = "Recibirás:\n\nAnillo de Desdén"},
[531] = {title = "La venganza de Vyrin", objective = "Lleva la cabeza de Ol' Sooty a Vyrin Vientoveloz a la Cabaña del Errante.", description = "¡Por la Luz! No puedes meter eso aquí, es antihigiénico, antiestético y... ¡y huele mal!\n\nLlévatelo, ¿quieres? ¡Ese trofeo sangriento no le interesa a nadie!", progress = "¡Muy bien, <nombre>, eso le enseñará! Dame la cabeza, ya me ocupo yo de ella.", completion = "Gracias por tu ayuda, &lt;nombre&gt;. Llevaba tiempo buscando la revancha, ¡y lo hemos conseguido! Ten, te has ganado esto.", rewards = "Podrás elegir una de estas recompensas:\n\nSaco me munición de cazaCarcaj de caza\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCarcaj de cazaSaco me munición de caza"},
[532] = {title = "La batalla de Trabalomas", objective = "Mata al magistrado Ladoquemao y a 5 concejales de Trabalomas. Destruye la Declaración de Trabalomas. Saquea el registro civil. Después informa a Darthalia en Molino Tarren.", description = "Trabalomas está en ruinas y creemos que a punto de caer. Pero debemos mantenernos firmes hasta erradicar a toda la chusma humana para toda la eternidad.\n\nLos humanos resisten y siguen luchando pese a sus abrumadoras pérdidas. Debemos eliminar a su líder, el magistrado Ladoquemao, además de a los ediles. También debes destruir su más preciado documento, la Declaración de Trabalomas. Y ya que estás, saquea el registro civil.", progress = "¿Qué nuevas me traes?", completion = "Nos ha llegado el relato de tus proezas en Trabalomas. Buen trabajo, &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[533] = {title = "Infiltración", objective = "Consigue información sobre el paradero de Gol'dir's e informa a Krusk en Molino Tarren.", description = "Por fin empezamos a tener noticias acerca del paradero de Gol'dir. Los humanos lo han llevado de un lado a otro, pero hemos centrado la búsqueda en el campamento de la Hermandad que está al norte de aquí.\n\n<nombre>, tienes que ir y conseguir toda la información que puedas.", progress = "¿Has averiguado algo? Cuanto más tiempo pasa, menos posibilidades tiene Gol'dir...", completion = "Al fin hemos sabido dónde está Gol'dir. El campamento no está lejos y esperamos que no estén tan bien preparados como la otra vez.", rewards = "Recibirás:\n\n"},
[535] = {title = "Valik", objective = "Llévale 1 jarra de cerveza Costasur a Valik en el campamento de la Hermandad.", description = "Urrg... Herido, agonizante y frente al enemigo.\n\nDe todos modos, seguro que me matarás, pero si me traes cerveza negra de Costasur, los de tu clase beben, ¿no?, te contaré lo que deseas saber.", progress = "¿Me has traído una copa o has regresado para rematar el trabajo?", completion = "Dada mi inminente muerte, creo que puedo decir, sin temor a equivocarme, que esta es la más dulce... Toma, llévate esto, no me hará ninguna falta cuando haya muerto.\n\nBueno, ¡acaba de una vez!", rewards = "Recibirás:\n\nMisiva de la Hermandad"},
[536] = {title = "Al lo largo de la costa", objective = "Mata a 10 cazamareas y 10 oráculos Rompeanca por orden del general Farren Orinelle de Costasur.", description = "Me han transferido de la guardia de Ventormenta a Costasur, un lugar que merecería ser abandonado, a juzgar por su aspecto. No me quito de encima el olor a puerto.\n\nPor no hablar de Rutagrana... Quiere que me encargue de los murlocs. ¡Ja!\n\nApenas me animo a acercarme a esos pescados gigantescos de la Playa del Oeste. Te agradecería mucho que te ocuparas de ello en mi lugar.", progress = nil, completion = "Así Rutagrana se convencerá de que he cumplido sus órdenes... Es un decir. En fin, el caso es que ya no hay murlocs gracias a ti.", rewards = "Recibirás:\n\n"},
[537] = {title = "Consejo oscuro", objective = "Mata a 4 magos de la Sombra Argus.\n\nLleva la cabeza de Nagaz al magistrado Maleb de Costasur.", description = "La carta fue escrita por un ser que responde al nombre de Nagaz, miembro del Despertar de Argus. No sé quiénes son, pero si están aliados con la Hermandad debemos considerarlos una amenaza.\n\nQuiero que localices a Nagaz y a los magos de la Sombra Argus mencionados en la carta. Localízalos y elimínalos.\n\nExplora las Tierras Altas, el bastión de la Hermandad. Está al norte de Strahnbrad.\n\nCon un poco de suerte, encontrarás a los enviados del Despertar de Argus allí.", progress = "¿Localizaste a los enviados de Despertar de Argus?", completion = "Buen trabajo, &lt;nombre&gt;.\n\nPero debes saber que Nagaz no era más que un siervo; el Despertar de Argus tiene profundas raíces en Azeroth.\n\nY puede que en todo el mundo.", rewards = "Recibirás:\n\n"},
[538] = {title = "Costasur", objective = "Habla con el maestro del Conocimiento Dibbs en Costasur.", description = "<nombre>. Si no te molestan los viajes largos, tengo una tarea para ti, para ayudar a llenar la biblioteca de Ventormenta.\n\nEl viejo reino de Alterac cayó tras la Gran Guerra y tenía una gran colección de libros. Es una tragedia que la ciudad esté en ruinas, pero mucho más trágico es haber perdido todo su conocimiento. Debemos recuperar todo lo que podamos.\n\nCostasur es la ciudad más próxima a las Ruinas de Alterac. Vete allí y habla con el maestro del Conocimiento Dibbs. Podrá guiarte hasta las ruinas y sabe qué libros puedes encontrar allí.", progress = nil, completion = "Ah, sí, recuerdo a Milton. Un ratón de biblioteca sin duda alguna. Bendito sea.\n\nY tiene razón. Todavía quedan textos en las Ruinas de Alterac que corren el peligro de desaparecer para siempre.\n\nAhora Alterac está lleno de ogros y no es que les importe mucho el aprendizaje y el conocimiento.", rewards = "Recibirás:\n\n"},
[539] = {title = "La batalla de Trabalomas", objective = "Mata al supervisor Bonds y 10 mineros de Trabalomas e informa a Darthalia en Molino Tarren.", description = "El Registro de Trabalomas indica que la Mina Azur se encuentra bajo el control de la Alianza. Esa mina es una de las principales fuentes de hierro de la armería de la Alianza.\n\nAunque hemos asestado un golpe mortal a Trabalomas, la Alianza sigue defendiendo la Mina Azur,\n\nque está al sur de Trabalomas. Ve y asesina al supervisor, un humano llamado Bonds. Elimina también a los mineros. Eso le enseñará a la Alianza.", progress = nil, completion = "A la Dama Oscura le gustará saber que dispone de otra mina con la que abastecer de armas a sus soldados.", rewards = "Recibirás:\n\n"},
[540] = {title = "Proteger el conocimiento", objective = "Lleva 5 tomos y el libro de piel gastado que contiene El Arma de Gri'lek al maestro del Conocimiento Dibbs a Costasur.", description = "Esos bestiales ogros viven en las ruinas de Alterac. Me estremezco al pensar en lo que estarán haciendo con los valiosos libros que quedaron allí. ¡Debes recuperar tantos como puedas!\n\nVe a las Ruinas de Alterac y busca los volúmenes que hayan robado. Tráeme todos los que puedas, pero hay un libro en especial que debes buscar: El Arma de Gri'lek. En ese libro hay ciencia trol antigua que necesito; en las Ruinas de Alterac había un ejemplar intacto, el último del que se tiene noticia.", progress = "Me gustaría ir contigo, pero ya estoy mayor para andar explorando por ahí. Dime, ¿cómo va tu búsqueda de conocimiento, <nombre>?", completion = "¡Excelente! ¡Empezaré a estudiar el Arma de Gri'lek de inmediato!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[541] = {title = "La batalla de Trabalomas", objective = "Ve a Dun Garok y mata a 10 montaraces, 8 fusileros, 4 sacerdotes y al capitán Ferrocolina e informa a Darthalia en Molino Tarren.", description = "¿Enanos ayudando a los humanos? Malditas sean esas pequeñas y sucias bestias.\n\nPondremos a prueba la solidez de esa supuesta Alianza. Veremos si esos medias piernas están tan ansiosos por ayudar a los humanos cuando hayan sufrido nuestra ira. Sabemos exactamente dónde se encuentra su fortaleza en este territorio.\n\nSe han instalado en un barracón llamado Dun Garok, al sureste de Trabalomas, junto a la Muralla de Thoradin. La misión final de esta batalla es asediar Dun Garok, <clase>. El capitán Ferrocolina, su líder, debe morir.", progress = nil, completion = "Tus hazañas en el campo de batalla merecen las mayores alabanzas.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[542] = {title = "Vuelve con Milton", objective = "Lleva los Tomos de Alterac a Milton Gavilla a Ventormenta.", description = "Voy a dedicar cierto tiempo a estudiar El Arma de Gri'lek, pero seguro que Milton espera con impaciencia los demás libros para incluirlos en la colección de Ventormenta.\n\nAquí tienes los libros que has traído de las Ruinas de Alterac, ya catalogados y empaquetados. Ve a Ventormenta y dáselos a Milton Gavilla; seguro que te lo agradecerá.", progress = "¡Has vuelto, <nombre>! ¿Qué tal en Alterac? ¿Traes algo?", completion = "No puedo creer que todo esto haya sobrevivido a la hecatombe de Alterac.\n\n¡Muchas gracias, &lt;nombre&gt;! Has ayudado a preservar una fuente de conocimiento y a enriquecer el Archivo Real de Ventormenta.", rewards = "Recibirás:\n\n"},
[543] = {title = "La Tiara Perenolde", objective = "Lleva la Tiara Perenolde a Remington Crestabuena en Ventormenta.", description = "La Tiara Perenolde es una antigua reliquia familiar de los \"nobles\" Perenolde, la familia soberana de Alterac antes de que comenzara el declive del reino. Se dice que ha sido forjada por los maestros joyeros de Forjaz y que lleva engarzadas unas esmeraldas de gran calidad.\n\nCuando Alterac fue destruido, se perdió la Tiara, pero corren rumores de que un mago ogro, Grel'borg el Avaro, encontró la Tiara y ahora vaga por las Ruinas de Alterac en busca de más tesoros.\n\nNos gustaría que recuperases la Tiara para nosotros.", progress = "Hola, <nombre>. Intenta evitar pisar el dobladillo de mis pantalones. Este traje es nuevo.", completion = "¡Ah, qué bien! Está claro que habrá que limpiar esto pero, realmente, te has superado a ti mismo, &lt;nombre&gt;. Haré que alguien prepare la Tierra y la coloque en nuestra colección con presteza.\n\nHas de saber que nuestra gratitud, es tan inmensa como nuestra colección.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[544] = {title = "Colarse en la cárcel", objective = "Localiza a los traidores y recupera los objetos robados. Luego habla otra vez con el mago Vacuamirada en Molino Tarren.", description = "Había venido a Molino Tarren para estudiar, pero ahora debo solucionar una crisis. Verás, hace unos meses, cuatro Renegados huyeron de Entrañas. Dieron la espalda a su gente, pero lo peor es que robaron a la Dama Oscura.\n\nEntraron en una cámara de seguridad y robaron cuatro objetos que usaron nuestros boticarios en algunos proyectos. Sylvanas quiere recuperar esos objetos.\n\nLos ladrones huyeron de Entrañas a Dalaran y esos magos los pusieron en cuarentena en el Campo de Reclusión de Lordamere.", progress = "¿Encontraste a los traidores? ¿Traes lo que robaron?", completion = "Has rendido un gran servicio a los Renegados, &lt;nombre&gt;; tenemos estos objetos en gran estima.\n\nMe alegro de que los culpables hayan recibido su merecido.", rewards = "Recibirás:\n\n"},
[545] = {title = "Las patrullas de Dalaran", objective = "Mata a 6 invocadores de Dalaran y 12 esclavos elementales. Luego vuelve a hablar con el mago Vacuamirada en Molino Tarren.", description = "Los magos de Dalaran construyeron una gran cúpula mágica alrededor del corazón de la ciudad. Hay quien dice que fue un medio de protección en estos tiempos violentos, y quien cree que los magos buscaban soledad para estudiar y conspirar.\n\nLa razón no me importa. Lo que me importa es que entre las ruinas, fuera de la cúpula, puede haber muchos tesoros mágicos.\n\nPero los magos patrullan la zona con sus esclavos elementales.\n\nTienes que eliminarlos. Vuelve cuando hayans reducido sus filas.", progress = nil, completion = "Excelente; ahora podré explorar las ruinas.\n\nGracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[546] = {title = "Recuerdes de muerte", objective = "El guardia de la muerte Samsa de Molino Tarren quiere 10 calaveras humanas de Trabalomas.", description = "Oí que has sido reclutado por Darthalia para luchar contra los humanos de Trabalomas. Estoy celoso.\n\nMientras tú te diviertes matando humanos, saqueando el pueblo y aterrorizando a gente inocente… yo estoy aquí clavado haciendo guardia en el Molino Tarren.\n\n¿No podrías apiadarte de un viejo guardia de la muerte como yo? Verás, colecciono calaveras humanas. Y tú pasarás algún tiempo combatiendo contra humanos. Reúne 30 calaveras mientras combates tu larga batalla y tráemelas. Haré que te merezca la pena.", progress = "¿Ya estás aquí, <nombre>? ¿A cuántos humanos liquidaste? ¡Espero que a cientos!\n\n¿Traes las calaveras?", completion = "¡Qué magníficas calaveras! Ten tu recompensa, &lt;nombre&gt;!", rewards = "Recibirás:\n\nAnillo calavera"},
[547] = {title = "La espada de Humbert", objective = "Recoge la espada del guardia de la muerte Humbert en Dun Garok y llévasela a Molino Tarren.", description = "La suma ejecutora Darthalia me envió a vigilar Dun Garok desde cierta distancia. Acampé donde no pudieran verme. A la mañana siguiente fui a buscar leña para el fuego.\n\nY al volver descubrí que me habían robado el equipamiento.\n\nParece que unos rastreadores enanos de Dun Garok pasaron por allí y se lo llevaron todo. ¡Necesito la espada! Te agradecería mucho que me la trajeras. Aquí hay tantos enanos que será difícil descubrir al ladrón.", progress = "¿Tienes mi espada, <nombre>? La suma ejecutora Darthalia me colgará si se entera de que la perdí.", completion = "¡La encontraste!\n\nAh, qué bien sienta sostener la espada otra vez.", rewards = "Podrás elegir una de estas recompensas:\n\nPeto en canaléLeotardos de mercenario\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPeto en canaléLeotardos de mercenario"},
[549] = {title = "SE BUSCA: Personal de la Hermandad", objective = "Mata a 10 pícaros y 10 vigilantes de la Hermandad. La suma ejecutora Darthalia te dará tu recompensa en Molino Tarren.", description = "Por orden de la Lady Sylvanas, se decreta la pena de muerte para todos los miembros de la organización conocida como la Hermandad. Aquellos que sean capturados serán ejecutados. Se sabe que se están reuniendo en las ruinas del Castillo de Durnholde, al sureste de Molino Tarren.\n\nSe ofrece una generosa recompensa a quien pueda dar cuenta de sus actividades a la suma ejecutora Darthalia.", progress = nil, completion = "Excelente, &lt;clase&gt;. Pronto conseguiremos librarnos definitivamente de la Hermandad.", rewards = "Recibirás:\n\n"},
[550] = {title = "La batalla de Trabalomas", objective = "Lleva la mención honorífica sellada de Darthalia a Varimathras, que está en Entrañas.", description = "He redactado una mención honorífica describiendo tu heroica actitud en combate para que el alto mando reconozca tu valor y entrega en la Batalla de Trabalomas.\n\nLleva esta mención sellada a Varimathras; está en Entrañas. Ve con la frente muy alta, <clase>.", progress = "¿Por qué estás en Entrañas y no en el campo de batalla, <clase>?", completion = "¿Así que eres tú quien asestó el golpe mortal a las fuerzas de la Alianza en las Laderas de Trabalomas? Tu fama ha llegado hasta Entrañas.\n\nSigue así y te espera un futuro brillante en las filas de la Dama Oscura.\n\nAunque la amenaza principal es la Peste del rey Exánime por el norte, no podemos olvidarnos de los humanos. Pero tú has demostrado que es posible derrotarlos.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones de entierro sacroEscudo calaveraHoja oscura rúnica\n\nTambién recibirás:\n\nSortija de EntrañasEscudo calaveraHoja oscura rúnicaPantalones de entierro sacro"},
[551] = {title = "El pergamino encantado", objective = "Lleva el pergamino encantado al maestro del Conocimiento Dibbs en Costasur.", description = "Este pergamino está encantado con un hechizo que lo hace indescifrable. Aunque se puede leer una palabra en la parte de arriba:\n\n\n Nagaz\n\n", progress = "Oh. ¿Tienes algo que te gustaría que viera? Algún resto de tradición recolectado en las montañas, espero...", completion = "¿Más notas cifradas?\n\nOjalá tuviera el talento para descifrar todos estos extraños signos que nos llegan de Alterac. Pero me temo que me falta el conocimiento necesario.", rewards = "Recibirás:\n\n"},
[552] = {title = "La venganza de Helcular", objective = "Róbale la Vara de Helcular a los yetis y llévasela al novicio Thaivand a Molino Tarren.", description = "El maestro Helcular casi había completado su transformación en exánime cuando una multitud airada descubrió su refugio ritual en las colinas y lo asesinó brutalmente. Para empeorar las cosas, lo enterraron en el cementerio de Costasur.\n\nTamaño desprecio por tan gran mago es inaceptable. Yo, su aprendiz, me aseguraré de que el fuego de la venganza arrase Costasur.\n\nNecesitamos la Vara de Helcular. Los yeti invadieron el refugio ritual. Seguro que una de esas bestias tiene la vara.", progress = "¿Ya tienes la Vara de Helcular?  Hay tantos yetis por aquí que será difícil dar con ella, pero la necesitamos.", completion = "¡Magnífico!", rewards = "Recibirás:\n\n"},
[553] = {title = "La venganza de Helcular", objective = "Carga la Vara de Helcular con el poder de la Llama de Azel, la LLama de Veraz y la Llama de Uzel.\n\nLuego llévala a la tumba de Helcular en Costasur.", description = "Toma la Vara de Helcular; la vas a necesitar.\n\nHelcular diseñó tres piras ceremoniales como parte de su hechizo más potente. La Llama de Azel y la Llama de Veraz reposan en las cuevas de la ladera. La Llama de Uzel está en una gruta situada sobre las cuevas, hacia el norte.\n\nDebes cargar la Vara de Helcular en estas tres llamas. Cuando hayas completado el ritual, lleva la vara a la tumba de Helcular en Costasur, la muy bien protegida ciudad humana del sur. ¡Nos vengaremos!", progress = "De la tumba de Helcular emana una energía mística.", completion = "Oyes un rugido procedente del subsuelo.", rewards = "Recibirás:\n\n"},
[554] = {title = "La descodificación de Pico Tormenta", objective = "Lleva el pergamino codificado al prospector Pico Tormenta a Forjaz.", description = "El prospector Pico Tormenta es un maestro de la descodificación. Llévale el pergamino que has encontrado; con un poco de suerte, podrá descifrarlo. Está estudiando en la oficina central de prospección, en Forjaz.\n\nDile que has hablado conmigo; últimamente le mando bastante trabajo.", progress = "¡¿Otra interrupción?!\n\nAh, este pergamino está protegido por un hechizo muy complicado. Puedo descifrarlo, pero me llevará tiempo y no tengo mucho...\n\n¿Y si me lo vendes?", completion = "Sabia decisión. Soy el único que puede descifrarlo. Y no te preocupes, te informaré de su contenido.", rewards = "Recibirás:\n\n"},
[555] = {title = "Sopa de tortuga calmante", objective = "Lleva 10 piezas de carne de tortuga y especias calmantes al chef Jessen a Costasur.", description = "¡Lo que más vas a apreciar en Costasur es que tenemos la mejor cocina al norte de Ventormenta!\n\nPor ejemplo, mi sopa de tortuga. Sé de gente que ha venido incluso de Villa Oscura solo para probarla. Por cierto, hace mucho que no la hago. Antes iba pasado Dalaran hasta el Lago Lordamere para cazar quijafortes, pero ahora es demasiado peligroso. ¡Si me traes carne de tortuga de la de los quijafortes del norte y especias calmantes, te preparo una sopa!", progress = "¿Encontraste los ingredientes? Esta tierra ya no es tan tranquila como antes.  Ay, qué tiempos... . .", completion = "Todo el mundo estará encantado de que vuelva a haber sopa de tortuga en el menú.", rewards = "Recibirás:\n\nReceta: sopa de tortuga reconfortanteSopa de tortuga reconfortante"},
[556] = {title = "Muestras de piedra", objective = "Lleva 10 muestras de piedra gastada al guardián Bel’varil de Molino Tarren.", description = "Los magos de Dalaran tienen una notable facilidad para relacionarse con elementales, que son poderosas criaturas de otras dimensiones. Los magos controlan a los elementales con talismanes mágicos. El mago Vacuamirada quiere analizar estos talismanes.\n\nVe a buscarlos al territorio de los magos de Dalaran, al oeste de Trabalomas.\n\nCualquier ciudadano puede llevar uno, pero te sugiero que busques en el campo de reclusión. Es más seguro que ir a Dalaran.", progress = "¿Traes las muestras de piedra gastada? El mago está deseando estudiarlas.", completion = "Estupendo; aunque su magia parece gastada, estas muestras son perfectas para estudiarlas.\n\nGracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[557] = {title = "Brazales de vínculo", objective = "Lleva 4 brazales de vínculo con la Tierra al guardián Bel’varil de Molino Tarren.", description = "El mago está ocupado estudiando las muestras de piedra que has traído, pero pronto querrá ver sus opuestos: los brazales de vínculo. Cuando un elemental los lleva, queda ligado al servicio del mago.\n\nCaza elementales en las ruinas de Dalaran y recoge sus brazales de vínculo con la Tierra para ganarte el favor del mago.", progress = "Hola, <nombre>. ¿Cómo fue con los elementales? ¿Traes los brazales de vínculo?", completion = "Excelente. ¿Tuviste problemas para conseguirlos?\n\nTienes razón, la pregunta es absurda.\n\nTe agradecemos tus servicios, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[558] = {title = "El autógrafo de Jaina", objective = "Viaja a Theramore para intentar conseguir un autógrafo de Jaina Valiente para tu pupilo.", description = "Dicen que Lady Jaina Valiente de Theramor es una de las más grandes heroínas de la Alianza de todos los tiempos. ¡Cuando sea mayor también seré un héroe de la Alianza!\n\n¿Conoces a Jaina, <nombre>? ¿Me puedes hacer un favor? ¿Me conseguirás su autógrafo? No me atrevo a pedírselo, seguramente estará muy ocupada para alguien como yo... ¡pero tú! ¡Tú eres <un aventurero/una aventurera> como ella! ¡Seguro que te dará su autógrafo!\n\n¡¡Por favor!!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[559] = {title = "La prueba de Farren", objective = "Recoge 10 cabezas de murloc en la Playa del Oeste por orden del general Farren Orinelle de Costasur.", description = "¿Qué te parece, <nombre>? A Rutagrana no le basta con todos los murlocs que hemos matado. Sí, hemos, tú también has tenido algo que ver. Ahora quiere que le traiga pruebas, que me dedique a rebanar cabezas de murloc para que las inspeccione.", progress = "¿Me traes las cabezas, <nombre>?", completion = "¡Lo estás poniendo todo perdido!\n\nYo no puedo tocar esas cosas; el pescado vaya y pase, pero estos bichos... Puaj. Llevo semanas sin comer pescado, y en Costasur eso es mucho decir", rewards = "Recibirás:\n\n"},
[560] = {title = "La prueba de Farren", objective = "Entrega las cabezas de murloc al mariscal Rutagrana de Costasur.", description = "Verás, <nombre>, a mí esto de las cabezas me pone mal cuerpo... Ya que le has cogido el gusto a la cosa, ¿te importaría ir a llevárselas a Rutagrana por mí?", progress = "Saludos. ¿Vienes a ayudar en la defensa de Costasur?", completion = "Así que te envía Farren, ¿eh?\n\nSi tuviste algo que ver con las cabezas, te lo agradezco; necesitamos gente dispuesta a trabajar.", rewards = "Recibirás:\n\n"},
[561] = {title = "La prueba de Farren", objective = "Habla con el general Farren Orinelle en Costasur.", description = "Informa al teniente Orinelle que debe concentrarse en los nagas que han infestado la Playa del Este. Dile que me informe personalmente del resultado de la operación.", progress = nil, completion = "Los naga, ¿eh? Pues va listo Rutagrana si cree que va a ganarse una medalla a mi costa. Ahora te explico.", rewards = "Recibirás:\n\n"},
[562] = {title = "Cambio de destino", objective = "Mata a 10 cazacostas y 10 sirenas Dagaespinazo por orden del general Farren Orinelle de Costasur.", description = "Mi antiguo mentor de la guardia de Ventormenta me dice que el alto mando de la Alianza está preocupado por la amenaza de los nagas. Según él, quien pueda ayudar a este respecto recibirá un ascenso, o al menos un cambio de destino.\n\nSolo hay un problema... No puedo enfrentarme a los nagas, <nombre>. Puedo intervenir en una pelea de borrachos o reducir a un indigente belicoso, pero los nagas me superan. ¡<nombre>, tienes que ayudarme!", progress = "Tengo una carta lista para el mayor Samuelson de Ventormenta; solo necesito el informe.", completion = "Ahora que no hay naga en Costasur, estoy más cerca de que me devuelvan a Ventormenta y salir de aquí de una vez.", rewards = "Recibirás:\n\nEntrañas de pescado"},
[563] = {title = "Nueva asignación", objective = "Entrega el informe de Farren al mayor Samuelson en el Castillo de Ventormenta.", description = "No debería ser un problema conseguir que vea mi informe. El nuevo capitán de la guardia, el mayor Samuelson, fue mi mentor cuando estuve destinado en Ventormenta. Eso debería ser excusa suficiente para que me traslade a la ciudad de nuevo. Pero necesitaré que le lleves mi informe... no quiero que Rutagrana se entere de nada.", progress = "Saludos. ¿Necesitas ayuda de la guardia de Ventormenta?", completion = "¿Qué es esto? Ah, veo que se han tomado medidas con los nagas de Costasur. Gracias, tendré en cuenta este informe.", rewards = "Recibirás:\n\n"},
[564] = {title = "Una amenaza costosa", objective = "Darren Malvew de Costasur quiere que mates 8 pumas y 10 pumas gigantes.", description = "Nada me duele más que perder estos hermosos caballos. Y hemos perdido unos cuantos entre los puñeteros no-muertos, la maldita Hermandad y toda la demás chusma que corre por aquí.\n\nPero te juro por mi abuelo que la amenaza más grave para los caballos son los pumas de Alterac. ¡Siempre bajan de la montaña y me complican la vida!\n\n¿Te apetece una aventura? ¡Pues ve y ocúpate de ellos!", progress = nil, completion = "¡Me alegro de oír eso! Ya sabía yo que tú me resolverías el problema... ¡Eres un orgullo para tu familia, &lt;nombre&gt;!", rewards = "Podrás elegir una de estas recompensas:\n\nFaja de pastorGuantes de pastor\n\nTambién recibirás:\n\nReceta: filete de león sabrosoFilete de león sabrosoFaja de pastorGuantes de pastor"},
[565] = {title = "Capa de piel de yeti de Bartolo", objective = "LLeva una bala de lana, una capa de montañero, hilo de alta calidad y 10 trozos de piel de yeti a Bartolo Ginsetti a Costasur.", description = "Yo, el gran Bartolo, te haré una de mis capas de piel de yeti, admiradas por toda la Alianza.\n\nTe desafío a recorrer el mundo en busca de una capa mejor. ¿Cómo dices, <raza>? \"¡Bartolo, no hay una capa mejor!\" Sí, sí. Ya lo había oído antes.\n\nBartolo necesita una bala de lana, una capa de montañero hecha por un buen peletero, hilo de alta calidad y, naturalmente, piel de yeti. ¡Tráeme todo eso y te haré una capa magnífica!", progress = "Has venido de nuevo al gran Bartolo. ¿Ya tienes lo necesario para la capa de piel de yeti? ¿O has venido por la deliciosa compañía del gran Bartolo?", completion = "¡Ahora el gran Bartolo pondrá en práctica su magia! No necesito bastón de mago ni vara de hechicero. ¡Mi magia solo necesita hilo y aguja!", rewards = "Recibirás:\n\nCapa de piel de yeti"},
[566] = {title = "SE BUSCA: Barón Vardus", objective = "Mata al Barón Vardus y entrega su cabeza a la suma ejecutora Darthalia en Molino Tarren.", description = "Por orden de la suma ejecutora Darthalia, se busca al humano conocido como el Barón Vardus por crímenes contra los Renegados. Vardus es un miembro dirigente de la organización conocida como la Hermandad.\n\nFue visto por última vez en las Tierras Altas de las Montañas de Alterac, al norte de Molino Tarren.\n\nSe halla bajo pena de muerte. Se ofrece recompensa a quien presente una prueba de su muerte a la suma ejecutora Darthalia.", progress = "Ah, <nombre>. ¿Has venido a por tu recompensa por la cabeza del Barón Vardus?", completion = "Buen trabajo, &lt;nombre&gt;. Con el Barón Vardus muerto, se debilita la cúpula de la Hermandad. Les queda poco tiempo.", rewards = "Recibirás:\n\nToga inferno"},
[567] = {title = "¡Atención!", objective = "La suma ejecutora Darthalia de Molino Tarren ofrece recompensa por el secretario Horrace Corzalbal, el ciudadano Wilkes, el minero Hackett y el granjero Kalaba.", description = "¡Atención!\n\nLos siguientes humanos de Trabalomas son peligrosos. La suma ejecutora Darthalia ha designado una recompensa por su captura:\n\nSecretario Horrace Corzalbal. Se busca por el asesinato del guardia de la muerte Toma.\n\nCiudadano Wilkes. Se busca por el asesinato del boticario Eli.\n\nMinero Hackett. Se busca por el asesinato del mortacechador Fry.\n\nGranjero Kalaba. Se busca por el bloqueo de suministros de Entrañas.\n\nTodos estos enemigos se encuentran fugados y su captura se hará difícil. Se recompensará a quien pueda certificar su muerte.", progress = nil, completion = "Excelente, &lt;clase&gt;. Buen trabajo.", rewards = "Podrás elegir una de estas recompensas:\n\nArco de saqueoRodela de avizorVarita carbonizada\n\nTambién recibirás:\n\nCapucha ampliaArco de saqueoRodela de avizorVarita carbonizada"},
[568] = {title = "La defensa de Grom'gol", objective = "El comandante Aggro'gosh del Campamento Grom'gol, quiere que mates 15 raptores colazote.", description = "¡Throm'ka, <clase>!\n\nLlegas a tiempo a Grom'gol. Como comandante del campamento base del Jefe de Guerra aquí, en la jungla, he jurado encargarme de la seguridad de todos los miembros de la Horda. Nuestra misión de proporcionar una cadena de suministros segura hasta Rocal se ha visto obstaculizada por algunos de los habitantes locales.\n\nTe pongo al cargo de disminuir la población de raptores en las afueras de Grom'gol. Cuando hayas conseguido un avance significativo, vuelve a informarme y te reasignaré.", progress = nil, completion = "Has realizado tu trabajo con honor, &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[569] = {title = "La defensa de Grom'gol", objective = "El comandante Aggro'gosh del Campamento Grom'gol, quiere que mates 10 toscos ogros y 5 médicos brujos ogros.", description = "Cuando llegamos por primera vez a esta jungla, no pretendíamos tener que luchar con las tribus locales. Nuestra primera prioridad es enviar suministros a Rocal. Pero han surgido complicaciones. Una banda de ogros ha ocupado las Ruinas de Mizjah al sureste de Grom'gol. A pesar de las advertencias, siguen preparando emboscadas a nuestras caravanas de suministros.\n\nTe pongo al cargo de tratar con esos ogros tan poco cooperativos.\n\nVete a las Ruinas e intenta expulsarlos de la zona. Al menos, con esto les mandaremos un firme mensaje.", progress = nil, completion = "Bien hecho. Has servido al Jefe de Guerra con honor.", rewards = "Recibirás:\n\nRodela Grom'gol"},
[570] = {title = "El encantamiento de Mok'thardin", objective = "El vidente Mok'thardin del Campamento Grom'gol, quiere 8 garras Faucesombrías y un colmillo de tigresa inmaculado.", description = "La Horda tiene que darse cuenta del poder de la magia de la jungla y, después, utilizarlo. Hasta ahora, mis rituales han tenido éxito. Puedo lanzar encantamientos muy poderosos. Para ti, valiente <clase>, crearé un objeto muy especial.\n\nTráeme algunas zarpas de panteras Faucesombrías junto con un colmillo de tigresa. No valdrá cualquier colmillo. Debe estar en perfecto estado.\n\nLas bestias moran en la jungla, pero encontrarás algunas por aquí cerca, justo al otro lado del río que va desde las Ruinas de Mizjah hasta el sureste.", progress = "¿Qué noticias me traes?", completion = "Conoces la selva a la perfección, &lt;nombre&gt;. Esto será perfecto. Agradeceré a los espíritus el sacrificio de unas bestias así por la causa de la Horda.", rewards = "Recibirás:\n\n"},
[571] = {title = "El encantamiento de Mok'thardin", objective = "Lleva un tendón de gorila maduro al vidente Mok'thardin en Grom'gol.", description = "Lo que necesito ahora, es bastante difícil de conseguir.\n\nPara guardar las plumas, las zarpas y el colmillo inmaculado, necesito una cadena mágica. Solo servirá un tendón de gorila maduro.\n\nEl único lugar donde podrás encontrar uno es en un gorila del Valle Velo de Bruma viejo. Suelen ir a por comida al este de la Bahía del Botín.\n\nEl tendón debe ser perfecto. Quizás necesites usar algunas de tus habilidades para encontrar exactamente lo que necesito, pero merecerá la pena.", progress = "¿Te ha acompañado la suerte, <nombre>? ¿Has conseguido encontrar un tendón de gorila maduro?", completion = "Un tendón perfecto, &lt;nombre&gt;. Tus esfuerzos han merecido la pena.", rewards = "Recibirás:\n\n"},
[572] = {title = "El encantamiento de Mok'thardin", objective = "Lleva 10 plumas de acechadores de la jungla al vidente Mok'thardin en el Campamento Grom'gol.", description = "Los objetos que has conseguido de las bestias felinas del Valle son solo el primer requisito para un poderoso objeto encantado. Estas joyas de la jungla combinadas con mi ritual chamanístico, te proporcionarán una herramienta muy útil para ayudar a la Horda y al Jefe de Guerra.\n\nPero ahora debes conseguirme Plumas de acechadores de la selva. Tradicionalmente, las bestias llevan las plumas en sus brazaletes. Encontrarás una tribu de raptores directamente al sur de Grom'gol, no muy lejos de La Gran Arena.", progress = "¿Han dado fruto tus esfuerzos?", completion = "¡Soberbio! Prepararé esto inmediatamente.", rewards = "Recibirás:\n\n"},
[573] = {title = "El encantamiento de Mok'thardin", objective = "El vidente Mok'thardin de Grom'gol necesita agua sagrada de manantial. También quiere que mates 10 exploradores naga.", description = "Necesito un componente más para completar el encantamiento.\n\nAl sur, hacia la costa oeste, por encima de los riscos, hay un manantial de agua sagrada. Este manantial es el sujeto de un gran conflicto entre eruditos religiosos y místicos. Todas las razas de Azeroth afirman que el agua del manantial recibió su don por su pueblo.\n\nMi última petición: tráeme agua sagrada de manantial. Ten cuidado. Una expedición de exploradores naga ha avanzado hacia el manantial. Tendrás que encargarte de ellos.", progress = "Casi estoy a punto de completar el encantamiento. ¿Has hecho lo que te he pedido?", completion = "Con este agua sagrada, la gargantilla ceremonial estará lista para el encantamiento.\n\nLlévala con orgullo, &lt;nombre&gt;, en el nombre del Jefe de Guerra y en defensa de la poderosa Horda.", rewards = "Recibirás:\n\nGargantilla del Alto chamán"},
[574] = {title = "Fuerzas especiales", objective = "Mata 10 comandos Kurzen y 6 reductores de cabezas Kurzen.\n\nPreséntate ante el teniente Doren en el Asentamiento Rebelde.", description = "Ahora que hemos conseguido reducir algunas de sus tropas regulares, debes enfrentarte a las tropas de élite. Cuando no están fuera matando a nuestros soldados rebeldes, se retiran a las profundidades de la parte trasera de la Base de Kurzen, a una cueva a la que llaman Reservas.\n\nConozco personalmente a muchas de las tropas de Kurzen y sé que son habilidosas, leales y crueles. La tarea que tienes ante ti no es fácil, <nombre>.\n\nCuando las derrotes, informa al teniente Doren, mi oficial en jefe. Quiere recibir tu informe de primera mano.", progress = nil, completion = "&lt;nombre&gt;. La sargento Yohwa confiaba en tus habilidades y yo confío en ella. La derrota de las tropas de Kurzen es casi demasiado buena para ser verdad. Temía que los rebeldes estábamos solos y que nadie se podría enfrentar a al gran coronel.\n\nQuizás todavía nos quede alguna esperanza de sobrevivir.", rewards = "Recibirás:\n\n"},
[575] = {title = "La oferta y la demanda", objective = "Lleva 2 pieles grandes de crocoliscos fluviales a Drizzlik en la Bahía del Botín.", description = "\"El mejor artesano peletero de los Mares del Sur, del mundo antiguo y del mundo nuevo.\" ¡Así es como me llaman! ¡A mí! ¡Drizzlik! El mejor... ejem. Bueno.\n\nMis objetos de cuero se conocen en el mundo entero como los más exquisitos, ¡la elección de los entendidos!\n\nAcabo de recibir un pedido del director Riddlevox del Gremio de Manitas, de una docena de borceguíes. El producto que más vendemos.\n\nPara el molde básico, necesitaré pieles de crocolisco de los crocoliscos que están en el río de la parte norte de Tuercespina.", progress = "Tengo todo el equipamiento listo, <nombre>, solo necesito esas pieles. No queremos que Riddlevox tenga que esperar, eso queda claro.\n\nNo es buena idea que nadie deje insatisfecho al inventor jefe del Gremio de Manitas.", completion = "Con estas pieles será suficiente. La calidad no es demasiado importante, visto que se utilizarán para la capa interior de las botas, pero es importante que sean cómodas.\n\nSi son demasiado rígidas, utilizaré sangre de tigre para ablandarlas. Trucos del oficio, ¿sabes?", rewards = "Recibirás:\n\n"},
[576] = {title = "¡Ojo!", objective = "Dizzy el Tuerto, en la Bahía del Botín, quiere que recuperes su ojo de los bucaneros Velasangre.", description = "¡Alto! ¿Quién va?\n\nAaah, no soy nada sin mi ojo encantado especial. ¡Ya es bastante malo haber perdido mi ojo en combate en primer lugar! Pero por un golpe de suerte atracamos en Menethil y un zahorí viajante encantó un ojo especial de cristal para que pudiera ver de nuevo.\n\nAyer por la noche unos bucaneros Velasangre me atacaron cuando salía. ¡Y cogieron mi ojo! Parece ser que esos tipos han conseguido llegar a la Bahía del Botín.\n\n¿Quieres ayudar a un viejo lobo de mar a recuperar su ojo?", progress = "¡Hola, colega! Quizás no puedo ver, pero te huelo desde el otro lao del puerto.\n\nMe vendría mu bien recuperar el ojo. El capitán va a pasarme por la quilla cuando descubra que mi aujero de ver lo tienen esos asquerosos Bucaneros Velasangre.\n\n¿De qué vale un vigía que no puede ver? La torre del vigía solía ser mi hogar... ahora, sin mi ojo, tendré suerte si puedo fregar la quilla.", completion = "¡Voto a bríos! ¡Has encontrao mi precioso ojo! Veamos si sigue funcionando.\n\n¡Jo, jo, jo, &lt;nombre&gt;! ¡¡Fantástico, puedo ver!!", rewards = "Recibirás:\n\nManteo marea oscura"},
[577] = {title = "Hay que ensamblar", objective = "Lleva 5 pieles de crocoliscos Quijaforte a Drizzlik en la Bahía del Botín.", description = "Bien, ya tengo el molde básico y todo va bien, pero con las pieles que has traído no tendré suficiente para las capas interiores.\n\n\"¿Qué vamos a hacer, Drizzlik?\" Sin duda esa es la pregunta que ibas a hacer y, en fin, qué suerte que estés hablando con Drizzlik, porque te va a decir lo que vamos a hacer.\n\nLas capas interiores no se ven, así que podemos coger unas pieles más gruesas y baratas de los crocoliscos que están por el Lago Nazferiti.", progress = "Las botas de cuero estilo borceguí de Drizzlik, la mejor artesanía, cien por cien botas de piel de crocolisco aguasalada de la mejor calidad. ¡Insuperables!\n\n¿A que no se puede superar una descripción como esa, eh?\n\nA no ser... ¿tienes esas pieles que te pedí?", completion = "Mis botas... son como salchichas, &lt;nombre&gt;. Tienen una pinta estupenda y están buenísimas. Te digo que están hechas solo con las mejores carnes e ingredientes y, si las pruebas, te encantarán.\n\nEs más seguro, y más agradable, que simplemente confíes en mí y no preguntes lo que tienen dentro.", rewards = "Recibirás:\n\n"},
[578] = {title = "La piedra de las mareas", objective = "Encuentra la isla encantada para el Barón Revilgaz en la Bahía del Botín.", description = "Si realmente hay un mago goblin y elementales de agua en esa isla, ¡la Piedra de las Mareas podría ser mía!\n\nQuiero que encuentres esa isla encantada... descubre lo que está pasando. Oh, y si quieres saber algo más sobre la Piedra de las Mareas, lleva este manuscrito a la Biblioteca de Ventormenta, deberían poder encontrarte una copia del libro donde me encontré la leyenda por primera vez.\n\n¿Podría ser que Gazban realmente descubriese la Piedra...?", progress = nil, completion = "¡Así que las historias eran ciertas! ¡Increíble!\n\nSin embargo, necesitaremos descubrir la forma de acabar con la maldición de la Piedra de las Mareas si queremos recuperarla. La maldición conlleva magia, así que necesitaremos a uno de esos zahoríes para que nos ayude.", rewards = "Recibirás:\n\n"},
[579] = {title = "La biblioteca de Ventormenta", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nLa historia de Morgan LadimorLeyendas de los Gurubashi, Volumen 3\n\nTambién recibirás:\n\nLa historia de Morgan LadimorLeyendas de los Gurubashi, Volumen 3"},
[580] = {title = "El grog de Whiskey Slim", objective = "Whiskey Delgado, en la Bahía del Botín, quiere que le lleves las botellas de oporto Pupellyverbos que perdió.", description = "Tábamos ayustando la braza mayor y soplaba fuerte en Los Acantilados Dominantes, veíamos las Tierras del Interior a distancia. Creí que el mar iba a tragarnos enteros.\n\nEl viejo Slim lo sabe todo sobre la valentía líquida. El capitán tenía una caja de oporto Pupellyverbos que le encargamos a un corsario que iba a Ventormenta.\n\nAsí que cuando cayó la tormenta de las Tierras del Interior, trinqué el grog. Una gran ola y todas las botellas cayeron por la borda. El capitán va a darme una buena paliza si no me ayudas a recuperarlas.", progress = "¡Que me aspen! ¡Casi me he quedao sin bebida! El capitán va a echar el ancla y el viejo Slim va a quedar tan seco como el Desierto de Tanaris. Y cuando descubra que su oporto ha desaparecido. ¡La que me espera!", completion = "¡Voto a bríos! ¡Has encontrao el grog del capitán!\n\nToy en deuda contigo, amigo.\n\nParece que hay una tormenta ahí fuera preparándose pa atacar. Quizás me quede con una de estas botellitas de aquí, solo pa esperar que pase la tormenta...", rewards = "Recibirás:\n\nRon Rumsey"},
[581] = {title = "Caza para Yenniku", objective = "Lleva 9 colmillos Sangrapellejo a Nimboya en el Campamento Grom'gol.", description = "Una maldición. Ha caído una maldición sobre el hijo menor de mi jefe, Yenniku. Como es costumbre entre los nuestros, fue entregado a los trols Gurubashi de Zul'Gurub, pero después de que mi tribu, los Lanza Negra, se fueran con el orco Thrall, perdimos al pobre Yenniku. Me enviaron desde el lejano Orgrimmar, de vuelta a Tuercespina, para buscar al hijo de mi jefe.\n\nY creo que los Sangrapellejo lo tienen.\n\nSi quieres ayudarme, ve a por los Sangrapellejo. Les encontrarás en el norte. Consigue sus colmillos y vuelve a verme.", progress = "<clase>. Demuéstrame que has tenido éxito con la tribu Sangrapellejo.\n\nEnséñame los trofeos.", completion = "Unos colmillos de gran calidad, &lt;nombre&gt;. Has demostrado que tienes la fuerza necesaria para cazar a los Sangrapellejo.\n\nY me alegro, porque todavía no has acabado con ellos.", rewards = "Recibirás:\n\n"},
[582] = {title = "Caza cabezas", objective = "Lleva 20 cabezas reducidas a Nimboya en el Campamento Grom'gol.", description = "Debemos descubrir si Yenniku está muerto.\n\nMata a los rebanacabezas Sangrapellejo y coge sus cabezas reducidas. Debes recoger muchas, muchas cabezas y las inspeccionaré para estar seguros de que la cabeza de Yenniku no está entre ellas.\n\nY si los Sangrapellejo asesinaron a Yenniku, ¡que caiga una maldición de 600 años sobre el espíritu de su jefe, Gan'zulah!\n\nVe, <nombre>. Encontrarás muchos rebanacabezas en las ruinas de Zuuldaia y Zul'Kunda, en la costa, hacia el norte.", progress = "<nombre>. Temo por el hijo de nuestro jefe. ¿Tienes las cabezas reducidas para que pueda inspeccionarlas?", completion = "Gracias, &lt;nombre&gt;. Inspeccionaré estas cabezas para asegurarme de que el pobre Yenniku se libró del hacha.", rewards = "Podrás elegir una de estas recompensas:\n\nPuños Lanza NegraBraquiales Lanza Negra\n\nTambién recibirás:\n\nBraquiales Lanza NegraPuños Lanza Negra"},
[583] = {title = "Bienvenido a la jungla", objective = "Habla con Hemet Decautela.", description = "¡Te doy la bienvenida a Tuercespina!\n\nQuizás no sepas esto, pero ese enano que está allí es el único y genuino Hemet Decautela, famoso héroe de guerra de la Alianza y gran maestro cazador. No le suele gustar que vengan forasteros al campamento, pero parece que tú has visto algo de acción en tu día, <clase>.\n\nSiendo un veterano de muchas batallas, el maestro Decautela tiene debilidad por los héroes.\n\nVete a hablar con él. Quizás pueda darte algunos trucos para cazar.", progress = nil, completion = "Un placer conocerte, &lt;nombre&gt;. Hacía mucho tiempo que no veía a un &lt;clase&gt; por aquí.\n\nMe metí en esta expedición por una razón: para cazar a una gran bestia esquiva. Viendo que eres un gran héroe, voy a permitirte participar en la cacería, pero primero, debes demostrar tu valía.\n\nTe enseñaré cómo matar raptores. Ajeck te instruirá en la cacería de tigres. Y Sir Erlgadin es un maestro del rastreo de panteras.\n\nDemuestra tu habilidad y te permitiré participar en la gran cacería.", rewards = "Recibirás:\n\n"},
[584] = {title = "Las cabezas de los Sangrapellejo", objective = "Lleva la cabeza de Gan'zulah y la cabeza de Nezzliok a la caldera en ebullición en el Campamento Grom'gol.", description = "La cabeza de Yenniku no estaba, demos las gracias a la Gran serpiente. Pero no estamos más cerca de encontrarle.\n\nDebemos conocer la verdad y me temo que solo el jefe de los Sangrapellejo y su médico brujo lo saben. Son los enemigos de la tribu Lanza Negra y no hablarán por voluntad propia, así que tendremos que sacar la verdad de sus labios sin vida.\n\nTráeme las cabezas de Gan'zulah y Nezzliok el Feroz y colócalas en esta caldera.\n\nEstán en las profundidades de Zul'Kunda. Trae sus cabezas... las obligaremos a hablar.", progress = "El caldo verde de la caldera está hirviendo.", completion = "Colocas las cabezas de Gan'zulah y Nezzliok en la caldera...", rewards = "Recibirás:\n\n"},
[585] = {title = "Hablando con Nezzliok", objective = "Lleva un trofeo de Balia'mah, un trofeo de Ziata'jai y un trofeo de Zul'Mamwe a la caldera en ebullición del Campamento Grom'gol.", description = "<La cabeza de Nezzliok habla.>\n\nEs cierto. Sé dónde llevaron al chico de los Lanza Negra. Me has vencido y has atrapado mi espíritu, pero no diré nada si no recibo algo a cambio.\n\nLos Sangrapellejo están en guerra con los Machacacráneos. Para conseguir mi favor, roba 3 cráneos trofeo a los Machacacráneos y colócalos aquí con nosotros, para que mi espíritu tenga esclavos en el Vacío Abismal. Un cráneo debe ser de su pila de trofeos en Balia'mah, otro de Ziata'jai y el último de Zul'Mamwe.", progress = "Espero los cráneos...", completion = "Sí. Son perfectos. Cuando mi espíritu haya sido liberado, entraré en el Vacío Abismal con tres sirvientes.\n\nHonras a nuestro clan, &lt;raza&gt;, y me has aplacado.", rewards = "Recibirás:\n\n"},
[586] = {title = "Hablando con Gan'zulah", objective = "Mata 8 cazadores Machacacráneos, 6 rebanacabezas Machacacráneos y 4 rabiosos Machacacráneos.\n\nLleva la armadura despedazada de Ana'thek a la caldera en ebullición del Campamento Grom'gol.", description = "<La cabeza de Gan'zulah habla.>\n\n¡Yo te maldigo! ¡Y maldigo a tus ancestros!\n\nSolo la sangre y la venganza podrán templar mi ira, así que si eso es lo que deseas... Haz esto:\n\nlánzate de pleno al corazón de los Machacacráneos, al este. Mata a los más fieros y, si sobrevives, enfréntate a su jefe, Ana'thek el Cruel. ¡Ja! Tráeme su armadura despedazada si puedes.\n\nQue te desgarre tus miembros y deje que tu carne se pudra y sea comida de buitres.", progress = "Aunque mi propia piel está siendo desgarrada y separada de mi cráneo, todavía puedo oler tu miedo.\n\n¡Ja! Nunca conseguirás hacer lo que te he pedido...", completion = "No quiero creerlo, pero no puedo negar lo que has hecho. Y mi ira disminuye ahora que has vencido a Ana'thek. \n\nTu poder te conducirá a grandes logros, &lt;nombre&gt;. Pero un día... te veré en el Vacío Abismal y volveremos a enfrentarnos.\n\nTe estaré esperando.", rewards = "Recibirás:\n\n"},
[587] = {title = "Tabaco rapé", objective = "Deeg, en la Bahía del Botín, quiere que le lleves 15 unidades de tabaco.", description = "Ah, la vida del mar. No diré que es una buena vida, pero es mi vida.\n\nCuando llego a puerto me gusta buscar una buena botella de grog y rapé de buena calidad. Unas costumbres muy malas, ¿pero qué es un pirata sin sus vicios, eh?\n\nAsí que te puedes imaginar cómo me sentí cuando me enteré de que el herborista de la ciudad se había ido y lo había vendido todo. Parece ser que esos malditos asaltantes Velasangre secuestraron su cargamento.\n\nApuesto a que un audaz <clase> como tú podría convencerlos para compartir las mercancías, ¿eh?", progress = "¿Has tenido suerte, colega?", completion = "¡Muy bien hecho, &lt;nombre&gt;! ¡Y justo cuando yo también tengo que embarcar! No te lo puedo agradecer bastante, viejo amigo.", rewards = "Recibirás:\n\n"},
[588] = {title = "El destino de Yenniku", objective = "Habla con Kin'weelay.", description = "<Nezzliok habla.>\n\nNos has aplacado a mi jefe y a mí, así que te diré lo que buscas...\n\nEl clan ha perdido al joven Yenniku.\n\nFue secuestrado por el médico brujo Zanzil el Desarraigado. Ahora Zanzil le controla, en cuerpo y alma, y solo la magia más poderosa podría liberarle.\n\nUna magia que tú no posees, <clase>.\n\nQuizás tus aliados Lanza Negra puedan ser la esperanza de la salvación de Yenniku. Pero lo dudo.", progress = nil, completion = "¿¿Zanzil el Desarraigado??\n\nSi Yenniku está en manos de ese trol, un malvado médico brujo, me temo que solo nos queda una pequeña esperanza. Muy pequeña. Tan pequeña como el insecto más ínfimo.\n\nPero, aún así, sigue siendo una esperanza.", rewards = "Recibirás:\n\n"},
[589] = {title = "Los cristales canto", objective = "Lleva 3 fragmentos azul intenso a Kin'weelay en el Campamento Grom'gol.", description = "Las leyendas hablan de una joya oculta en las profundidades del corazón de Tuercespina. Llamamos a esa joya el Ojo de la Mente, y tiene grandes poderes. Si consigues el Ojo, quizás podamos usarlo para traer de la oscuridad al joven Yenniku.\n\nPreguntaré a los espíritus de la jungla cómo encontrar el Ojo de la Mente. Mientras lo hago, ve a la Mina de Cristal, al sureste de aquí y recoge fragmentos azul intenso de los basiliscos Quijahierro. Los fragmentos son poco comunes, pero necesitamos su magia para conseguir dominar el Ojo de la Mente.", progress = "¿Tienes los fragmentos, <nombre>? Los necesito para mi ritual final, cuando tengamos el Ojo de la Mente.", completion = "Lo has hecho muy bien, &lt;nombre&gt;. Estos fragmentos son perfectos. Ya puedo oír su canto.\n\nY, mientras no estabas, hablé con los espíritus de la selva. Sé dónde está el Ojo de la Mente...\n\n... y quién lo tiene.", rewards = "Recibirás:\n\n"},
[590] = {title = "Un trato de pícaro", objective = "Derrota a Calvin Montague en Camposanto.", description = "¿Qué?\n\n¡¿Qué?!\n\n¿Quieres dinero? Yo no te debo dinero.\n\n¿Una carta? ¿Qué carta? Ah, estás buscando pelea. Muy bien, colega. ¡Vamos allá! Que te debo dinero...", progress = nil, completion = "¡Increíble! ¡Qué habilidad!\n\nPensaba que serías una presa fácil, &lt;nombre&gt;, pero me has dado una buena tunda.\n\nAquí tienes tu dinero... No es tanto como te dije, pero eso es porque te mentí desde el principio.", rewards = "Recibirás:\n\n"},
[591] = {title = "El Ojo de la Mente", objective = "Lleva el Ojo de la Mente a Kin'weelay en el Campamento Grom'gol.", description = "El Ojo de la Mente está en posesión de un mago ogro, al este de aquí, en las profundidades del Túmulo Ogro Mosh'Ogg. Se llama Mai'Zoth. Le conozco de cuando los Lanza Negra vivían en Tuercespina, es vicioso y su magia es poderosa.\n\nY, si tiene el Ojo de la Mente, entonces su magia será más fuerte todavía.\n\nDebes enfrentarte a Mai'Zoth y conseguir el Ojo de la Mente. Se trata de una misión peligrosa, pero es la única oportunidad que tenemos de salvar a Yenniku.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[592] = {title = "Salvar a Yenniku", objective = "Lleva la gema de alma llena a Nimboya.", description = "He convertido el Ojo de la Mente en una gema de alma. Tiene un doble propósito: debes usarla para aturdir a Yenniku y, cuando lo hayas hecho, colócala sobre su cabeza para atrapar su alma.\n\nA continuación, lleva la gema a Nimboya.\n\nYenniku vive con Zanzil el Desarraigado, en las Ruinas de Aboraz, junto a La Costa de Cristal, en El Cabo de Tuercespina.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nBastón místico de NimboyaMedalla de Coraje\n\nTambién recibirás:\n\nBastón místico de NimboyaMedalla de Coraje"},
[593] = {title = "Gema de alma", objective = "Yenniku está aturdido justo delante de ti, pero sus pensamientos están muy lejos...", description = nil, progress = "Yenniku está aturdido justo delante de ti, pero sus pensamientos están muy lejos...", completion = "Colocas la gema de alma sobre su frente. Se estremece cuando su alma le abandona, dejando su cuerpo, un caparazón sin vida, detrás.", rewards = "Recibirás:\n\nGema de almas llena"},
[594] = {title = "Mensaje en una botella", objective = "Encuentra al autor del mensaje en la botella.", description = "La nota dice, con buena caligrafía:\n\nA quien pueda ayudarme.\n\nPor favor, necesito ayuda urgentemente. Un gran Cieloleno me tiene de rehén en una isla en los Mares del Sur.\n\nLas estrellas de la noche son extrañas y los árboles de la jungla oscurecen el firmamento pero, en los días más claros, puedo ver la Orilla Salvaje al noroeste y lo que parecen grandes barcos cargueros al suroeste.\n\nTe lo suplico, ven a rescatarme.", progress = nil, completion = "¿Has venido a liberarme?", rewards = "Recibirás:\n\n"},
[595] = {title = "Bucaneros Velasangre", objective = "El primer oficial Crazz quiere que eches un vistazo a la cueva norte de la Bahía del Botín.", description = "Parece que tienes muchas ganas de ponerte a trabajar, colega. No tengo muy claro por qué un <raza> <clase> marinero de agua dulce, se ofrece a ayudar a los asaltantes Aguasnegras, pero no voy a rechazar tu ayuda.\n\nNuestro barco acaba de atracar aquí, en la Bahía del Botín, y corren rumores de que nuestros rivales, esos malditos y asquerosos bucaneros Velasangre, están por todo el sur de Tuercespina.\n\nTodos los hombres están muy ocupados dándole al grog y buscando damiselas. Lo que necesitamos es un explorador.\n\nExplora la cala que está justo al norte de la Bahía del Botín en La Costa Salvaje, a ver lo que descubres.", progress = nil, completion = "Encima del barril descubres un mapa que tiene escrito algo de forma apresurada y unas monedas.", rewards = "Recibirás:\n\n"},
[596] = {title = "Collares de huesos sangrientos", objective = "Lleva 25 collares de huesos sangrientos a Kin'weelay en el Campamento Grom'gol.", description = "Me enviaron a Grom'gol para ayudar a Nimboya en su misión, pero... yo también tengo razones para estar aquí.\n\nLa tribu Sangrapellejo es enemiga de mi tribu. Conseguir trofeos de nuestros enemigos nos da poder sobre ellos. Podrías ayudar a nuestra tribu dando caza a nuestros enemigos.\n\nViven al norte. Mata trols Sangrapellejo y coge sus collares de huesos sangrientos. Consigue muchos y tendrás una gran recompensa.", progress = "¡Algún día, los Lanza Negra aplastarán a los Sangrapellejo! ¿Tienes los collares de nuestros enemigos?", completion = "Ah, muy bien. Los herviré y les chuparé la sangre, y los espíritus de sus dueños se debilitarán cuando destruya estos collares.\n\nToma, &lt;nombre&gt;. Esto pagará la deuda que tengo contigo.", rewards = "Recibirás:\n\nSortija de hueso de sangre"},
[597] = {title = "Bucaneros Velasangre", objective = "Informa sobre tus descubrimientos al primer oficial Crazz en la Bahía del Botín.", description = "En el mapa, la Bahía del Botín aparece marcada con una \"X\" roja, dibujada exactamente en el lugar donde estás ahora.\n\nEl texto lleno de garabatos dice:\n\n\"Debéis asegurar un campamento en esta ubicación y mantener la calma. Esperad la señal del puesto de mando del sur antes de atacar. Si alguien se aproxima, matadle.\"", progress = nil, completion = "Claro que me acuerdo de ti, &lt;clase&gt;. Veo que has vuelto de tu exploración...", rewards = "Recibirás:\n\n"},
[598] = {title = "Collares de huesos partidos", objective = "Lleva 25 collares de huesos partidos a Kin'weelay en el Campamento Grom'gol.", description = "Ahora que has conseguido traerme trofeos de los Sangrapellejos, tengo otra misión para ti. La tribu de trols Machacacráneos vive al sureste y, durante años, hemos luchado con ellos. Si bien los Lanza Negra ya no estamos asentados en Tuercespina, estamos atados a nuestro conflicto con los Machacacráneos.\n\nVete a por ellos. Consigue sus collares de huesos partidos y vuelve a verme. Ayúdanos en nuestra guerra y conseguirás la amistad de los Lanza Negra.", progress = "¡Los Machacacráneos maldecirán el día que nos obligaron a entrar en guerra! ¿Has completado tu misión?", completion = "Tu fuerza es muy grandiosa si has podido conseguir estos collares, &lt;nombre&gt;. Los Machacacráneos caen ante ti, como los árboles jóvenes caen a la tierra tocados por un basilisco.\n\nTus logros son de gran ayuda para nosotros.", rewards = "Podrás elegir una de estas recompensas:\n\nZapatos Lanza NegraBotas Lanza Negra\n\nTambién recibirás:\n\nBotas Lanza NegraZapatos Lanza Negra"},
[599] = {title = "Bucaneros Velasangre", objective = "Informa del ataque planeado de los Velasangre al maestro de flota Cuernomarino.", description = "¡Voto a bríos! ¿Así que los bucaneros Velasangre están planeando atacar la Bahía del Botín? ¡No me puedo creer lo de esos bribones! La Bahía del Botín siempre estará dominada por los asaltantes Aguasnegras.\n\nEsto es más grande de lo que yo había pensado. Informa al maestro de flota Cuernomarino inmediatamente. Ese viejo lobo de mar está por la ciudad, en alguna parte...", progress = nil, completion = "¿Bucaneros Velasangre atacando Bahía del Botín? Para eso, podrían tirarse de Los Acantilados Dominantes abajo. De todas formas, trayendo esta información nos has hecho un gran servicio a mí y a mi flota de aventureros. Los asaltantes Aguasnegras te dan las gracias.", rewards = "Recibirás:\n\n"},
[600] = {title = "La minería de Ventura y Cía.", objective = "Lleva 10 cristales canto azules a Loquillo Pinchabujas en la Bahía del Botín.", description = "Ventura y Cía. está llevando varias operaciones en Tuercespina que priva a los duros trabajadores goblins, como yo, de conseguir un dinero honrado. ¡Por favor, tienes que ayudarme!\n\nVentura y Cía. está excavando cerca de la Mina de Cristal, al norte. No pueden entrar en la Mina debido a los basiliscos, pero consiguen extraer cristales canto de las colinas de alrededor.\n\nCoge los cristales y demuéstrales que no son los amos de la jungla.\n\nY... mmm... ¡tráeme esos cristales como prueba!", progress = "¿Tienes esos cristales? ¡¡Los necesito!!\n\nVamos a ver... necesito saber que la maldita Ventura y Cía. está perdiendo terreno en Tuercespina. Porque, ¿sabes?, los profesionales honestos tenemos que vivir de algo...", completion = "¡Genial! ¡Estupendo! ¡¡Puedo conseguir mucho por esto!!\n\nEsa Ventura y Cía. eligieron la selva equivocada donde excavar, ¿eh? A todos nos vendría estupendamente bien que se fueran de aquí.\n\nGracias, &lt;nombre&gt;. Hoy has hecho una buena acción.", rewards = "Podrás elegir una de estas recompensas:\n\nEncendedor goblinPala de plata\n\nTambién recibirás:\n\nPala de plataEncendedor goblin"},
[601] = {title = "Elementales de agua", objective = "Consigue 6 brazales de elementales de agua para el Barón Revilgaz en la Bahía del Botín.", description = "Sin duda, Gazban, el mago goblin que se rumorea que ha descubierto la Piedra de las Mareas, invocó a los elementales de agua. Como quizás sepas, la Piedra de las Mareas maldice a su portador, provocando que entre y salga del plano existencial...\n\nQuizás podamos encontrar la forma de contraatacar la maldición. Podemos conseguir que un mago de Dalaran analice el residuo mágico de uno de los brazales de un elemental de agua. Necesito que vuelvas a la isla y consigas algunos especímenes.", progress = "¿Has tenido suerte, <nombre>?", completion = "Mmm, no tengo capacidad para examinar los brazales y ver si alguno podría servirnos, pero seguro que, al menos uno, podrá ayudarnos. Llévalos a Dalaran y haz que un mago los vea.\n\nDespués de todo, yo no puedo irme tranquilamente de la Bahía del Botín para charlar con esos viejos magos cansados de la vida.\n\nNo me preocupa que alguien intente abrirse paso para convertirse en el nuevo barón. Nadie se interpone a Revilgaz y vive para contarlo.", rewards = "Recibirás:\n\n"},
[602] = {title = "Análisis mágico", objective = "Lleva los brazales de elementales de agua al archimago Ansirem Tejepista en Dalaran.", description = "Por lo que he oído, los magos de Dalaran están ocupados con la reconstrucción de la ciudad. Seguro que puedes encontrar a uno de ellos que tenga algo de tiempo para echar un vistazo a los brazales. Si algo sé sobre magos es que no pueden resistirse a un puzzle mágico.\n\nCoge la bolsa de brazales de elementales de agua y veamos si nos pueden ayudar con lo de la maldición de la Piedra. Busca al archimago Ansirem Tejepista. Ya he tratado antes con él y es muy sabio.", progress = "Si no te importa, tengo mucho que hacer ahora mismo.", completion = "¿La Piedra de las Mareas? Muy interesante. Podría sorprenderte saber que yo escribí un libro... hace muchos años ya... sobre las leyendas del Imperio Gurubashi. Sé algo sobre la Piedra.\n\nAl menos uno de estos brazales parece tener un residuo mágico que creo poder analizar.\n\nVeamos...", rewards = "Recibirás:\n\n"},
[603] = {title = "Llave de Ansirem", objective = "Habla con Catelyn en la Bahía del Botín.", description = "El mago que tiene la Piedra está ampliando su poder mediante algún punto focal... un altar de algún tipo. Deberías poder detenerlo destruyéndolo.\n\nResulta que sé cómo hacerlo: hay una daga antigua, otra reliquia trol. Y solo hay un pequeño... problema.\n\nVerás, encontré la daga mientras estaba investigando en Tuercespina, pero se la di a mi testaruda hija, Catelyn, que se escapó a la Bahía del Botín para convertirse en pirata.", progress = nil, completion = "¿Mi daga? ¿Qué eres, algún tipo de bufón?\n\n¿Qué? ¿Quieres que te repita la historia de cómo pasé de ser el mejor luchador de la Bahía del Botín para convertirme en el hazmerreír de todos los piratas?\n\nPerdí en una pelea con cuchillo contra un Bucanero Velasangre, ¡malditos bastardos! Y se llevaron mi espada como premio.\n\nNo es que pueda seguir siendo un luchador solo con mis insultos y sin un cuchillo...", rewards = "Recibirás:\n\n"},
[604] = {title = "Bucaneros Velasangre", objective = "El maestro de flota Cuernomarino en la Bahía del Botín quiere que mates 10 aventureros Velasangre y le lleves las cartas de navegación y las órdenes Velasangre.", description = "Crazz habla muy bien de tu entrega a los asaltantes Aguasnegras. Voy a confiarte una misión de vital importancia para la flota.\n\nInfíltrate en el puesto de mando del sur que los bucaneros Velasangre han establecido en la Orilla Salvaje. Asalta el campamento. Tráeme sus cartas de navegación y las órdenes escritas. Veremos exactamente qué pretenden hacer esas babosas.", progress = "Hola, <nombre>. ¿Qué noticias traes del campamento Velasangre del sur?", completion = "Perfecto, &lt;nombre&gt;. Ahora que conocemos su plan, solo es cuestión de tiempo.\n\nY pensar que esos imbéciles han soltado el ancla justo fuera del cabo...", rewards = "Recibirás:\n\n"},
[605] = {title = "Fragmentos de cristales canto azules", objective = "Lleva 10 fragmentos de cristales canto a Loquillo Pinchabujas.", description = "Solo hay cristales canto en Tuercespina y, para ciertos grupos, son muy valiosos. Yo puedo conseguir esos cristales, pero la maldita Ventura y Cía. ha complicado las cosas para los empresarios como yo.\n\nMe gustaría contratarte.\n\nLos basiliscos de Tuercespina comen esos cristales. Eso es lo que les da su piel endurecida y, algunas veces, se pueden conseguir cristales de calidad decente de la piel.\n\nPuedes conseguirlos de cualquier basilisco, pero los menos asquerosos están en la orilla sur de Zul'Kunda, hacia el norte.", progress = "¿Tienes esos fragmentos ya? ¡Te daré un buen porcentaje de los beneficios!", completion = "¡Ajá! Esto será perfecto, &lt;nombre&gt;. ¡Sí, excelente!\n\nAquí tienes tu parte y... ha sido un placer hacer negocios contigo.", rewards = "Recibirás:\n\n"},
[606] = {title = "Asusta a Trémulo", objective = "Lleva 5 menudillos de gorila del Valle Velo de Bruma al \"Trémulo\" Phillipe en la Bahía del Botín.", description = "Si hay algo que no soporto, ¡es la gente que no paga sus deudas de juego! Y hay muchos que me deben dinero. ¿Crees que podrías ayudarme?\n\nPrimero, está \"Trémulo\" Phillipe, ahí, en el muelle. Me debe mucho después del juego de la semana pasada, ¡y quiero lo que me corresponde! Pero para conseguirlo, tendremos que asustarle.\n\nSal de la ciudad y caza gorilas del Valle Velo de Bruma viejos. Encuentra una bonita selección de menudillos y llévaselos a Trémulo. No le digas que son de un animal. Deja que piense lo peor.", progress = "¡Eh! No me gusta cómo me estás mirando, chico.", completion = "¡Aaah! ¿De dónde has sacado esto? No, no me lo digas, no quiero saberlo.\n\n¿Quién te envía? ¿Lobo de Mar?", rewards = "Recibirás:\n\n"},
[607] = {title = "Vuelve con MacKinley", objective = "\"Lleva el pago de Trémulo a \"Lobo de Mar\" MacKinley\".", description = "Toma, coge esta bolsa y dásela a Lobo de Mar. ¡Y dile que le iba a pagar! ¡¡Lo juro!!\n\nSolo que me emborraché un poco y me perdí... ya sabes cómo son estas cosas. Lo entenderá, ¡no hace falta que te pongas así!", progress = "Hola, hola, <nombre>. ¿Has conseguido que el Trémulo te pagase?", completion = "¡Je, je, bien hecho! Y cuando le enseñaste a Trémulo esos menudillos, ¿se puso a temblar?\n\n¡Ja! Es muy predecible, cuando juego a las cartas con él, siempre gano.\n\nGracias por tu ayuda, &lt;nombre&gt;. Ahora me toca pagarte a ti...", rewards = "Recibirás:\n\n"},
[608] = {title = "Bucaneros Velasangre", objective = "El maestro de flota Cuernomarino en la Bahía del Botín quiere que mates al capitán Aguasmansas, al capitán Sinquilla y al maestro de flota Firallon.", description = "¡Maldito sea Firallon! No podría navegar un esquife por el Lago de Cristal y mucho menos liderar toda una flota contra nosotros.\n\nLos bucaneros Velasangre pagarán su descuido. Ahora que sabemos su plan de ataque, podemos atacar y tomarles por sorpresa.\n\n¿Pero por qué enviar a toda la flota para hacer el trabajo de un grupo de aventureros? <nombre>, coge a algunos de tus camaradas más capaces, id al lugar de atraque de Firallon y asesinad al líder Velasangre. Su flota se hundirá poco después...", progress = nil, completion = "¡Ah, qué día tan fantástico para los asaltantes Aguasnegras! ¡Tres hurras por ti, &lt;nombre&gt;!", rewards = "Recibirás:\n\nTúnica Aguasnegras"},
[609] = {title = "El vudú debido", objective = "Lleva el Pie Deforme de Maury, el catalejo dorado de Jon-Jon y el anillo enorme de Chucky a \"Lobo de Mar\" MacKinley en la Bahía del Botín.", description = "La lista empieza a menguar, pero todavía hay muchos que me deben dinero.\n\nA continuación tenemos a Maury \"Patón\" Wilkins, Jon-Jon el Cuervo, y Chucky \"Diezdedos.\"\n\nLas deudas de esos malditos perros son de varios meses y creí que se habían largado de la ciudad para evitar pagarme. Pero después me enteré de que están malditos y hechizados y se dedican a vagar por las ruinas de la jungla. Pero no me importa su destino, ¡quiero lo que me pertenece!\n\nEstán en las Ruinas de Aboraz y en las Ruinas de Jubuwal, al noreste de aquí. Encuéntralos y consigue mi dinero.", progress = "<nombre>, ¿has encontrado a esos tres perros? ¿Tienes sus posesiones?", completion = "¡Fantástico! ¡Podré conseguir un buen precio! Especialmente, por lo que hay en el pie de Maury. Muchas gracias, &lt;nombre&gt;.\n\nEspero que esos tres hayan aprendido la lección: no apuestes algo que no puedes permitirte perder.\n\nY quizás sea demasiado tarde para ellos... deberían tener cuidado con quién apuestan.", rewards = "Recibirás:\n\n"},
[610] = {title = "\"El Guapo\" Duncan\"", objective = "Consigue la daga de Catelyn de \"El Guapo\" Duncan.", description = "¿Mi PADRE te ha enviado? Pensaba que ese viejo chocho ya habría dejado de intentar encontrarme.\n\nRealmente no me escapé por su culpa, es solo que él no debería haber supuesto que aceptaría tener una vida aburrida, leyendo libros polvorientos y tratando a magos canosos, estirados y acartonados.\n\nSi necesitas mi daga, tendrás que recuperarla, la tiene \"el Guapo\" Duncan, uno de los bucaneros Velasangre. Pon algo entre sus hombros y seguro que te la da.", progress = "Nunca me han vencido. ¿Lo sabías, <nombre>? Sí, obviamente me han herido alguna vez, no te puedes meter en un combate sin llevarte algún que otro rasguño, pero solo consiguió golpearme en la cabeza y caí a plomo. Cuando me desperté, me faltaba la espada.\n\nA pesar de mi nombre, sigo necesitando el acero para luchar y, para mí, tenía que ser esa espada.", completion = "¿Has derrotado a Duncan? Entonces, supongo que no eres tan imbécil como parecías a primera vista.\n\nMe imagino que eso significa que no te desafiaré de aquí a un tiempo.", rewards = "Recibirás:\n\n"},
[611] = {title = "La maldición de las mareas", objective = "Destruye el Altar de las Mareas con la daga de Catelyn, mata a Gazban y lleva la Piedra de las Mareas y la daga de Catelyn al Barón Revilgaz.", description = "¿Así que estás buscando un altar? Bueno, no soy tan experta en ruinas trol como mi padre, pero he oído hablar de una ciudad trol hundida cerca de esa isla que estás buscando. Quizás podrías probar allí.\n\nHas recuperado mi cuchillo, así que supongo que puedes usarlo... ¡Pero lo quiero de vuelta cuando hayas terminado!", progress = "¿Has recuperado ya la Piedra de las Mareas, <nombre>? Ya he dejado un espacio libre y he comprado un pedestal para ella.", completion = "¡Sorprendente! ¡La Piedra de las Mareas! Tendrá un lugar de honor en mi colección de artefactos trols antiguos. ¿Has visto que parece casi como si tuviera una marea atrapada bajo la superficie? ¡Magnífico!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nToga de Aguas cristalinas"},
[613] = {title = "El pie roto de Maury", objective = "Lleva la llave de Maury a \"Lobo de Mar\" Mackinley en la Bahía del Botín.", description = "¡Maldición! Ese asqueroso de Maury ha puesto un candado en su pie, ¡y no consigo abrirlo! Guarda el botín en el pie, ¡y es justo lo que me debe!\n\nTenemos que encontrar la llave que abre el candado. Todos saben que Maury apostaba con los ogros Mosh'Ogg, en su túmulo, al noreste. Y me dijo, cuando todavía estaba vivo, que la última vez que fue al túmulo, acabó siendo perseguido porque había ganado mucho. Quizás los ogros de allí tengan su llave.\n\nSi es así, probablemente tengas que matar a muchos de ellos para encontrar al que la tiene...", progress = "¡Buenas, <nombre>! ¿Has encontrado la llave de Maury?", completion = "¡La has encontrado! Un millón de gracias, &lt;nombre&gt;. ¡Has sido de gran ayuda!\n\nAquí tienes, ¡te lo has ganado!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nColección de placas"},
[614] = {title = "El cofre del capitán", objective = "Lleva el cofre de Smotts a Hecklebury Smotts en la Bahía del Botín.", description = "Yo solía navegar en barco desde aquí hasta Trinquete, en Kalimdor. Fui un capitán de mucho éxito e inmejorable reputación.\n\nHasta que...\n\nEsos malditos gigantes marinos acabaron conmigo. Destruyeron mi barco, mataron a mi tripulación y me dejaron en un barco salvavidas con dirección a la Bahía del Botín. Y uno de esos gigantes, Gorlash, robó mi cofre del Capitán. Dijo que lo quería para usarlo como caja de rapé. ¡Increíble!\n\nHe oído que Gorlash está cerca de la costa al este de aquí. ¡Encuéntrale y recupera mi cofre! Hazlo y conseguirás la gratitud de un capitán.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nBrazales de Bandasangre"},
[615] = {title = "El aflanje del capitán", objective = "Habla con Sprogger.Cuando perdí mi primer barco por culpa de esos gigantes, compré otro. Se llamaba La Venganza de Smott, lo llené de suministros y una tripulación y partí en busca de los villanos. Les encontré... pero me vencieron. Destruyeron La venganza de Smott, mataron a mi segunda tripulación y de nuevo me dejaron en un barco salvavidas.\n\nEsta vez, otro de los gigantes, Negolash, robó mi alfanje.\n\nEnfréntate a Negolash y tráeme mi alfanje.\n\nHabla con El Pibe. Fue mi cocinero en La venganza de Smott y sobrevivió al ataque. Podrá ayudarte a encontrar al gigante.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", description = "Cuando perdí mi primer barco por culpa de esos gigantes, compré otro. Se llamaba La Venganza de Smott, lo llené de suministros y una tripulación y partí en busca de los villanos. Les encontré... pero me vencieron. Destruyeron La venganza de Smott, mataron a mi segunda tripulación y de nuevo me dejaron en un barco salvavidas.\n\nEsta vez, otro de los gigantes, Negolash, robó mi alfanje.\n\nEnfréntate a Negolash y tráeme mi alfanje.\n\nHabla con El Pibe. Fue mi cocinero en La venganza de Smott y sobrevivió al ataque. Podrá ayudarte a encontrar al gigante.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[616] = {title = "La isla fantasma", objective = "Habla con el Barón Revilgaz en la Bahía del Botín.", description = "El Barón Revilgaz me ha pedido permanecer alerta en busca de rumores y cosas extrañas que ocurren en la Bahía del Botín y en Tuercespina. Generalmente no creo mucho en historias de fantasmas, pero oí a unos marineros en la taberna hablando sobre una isla encantada a poca distancia de la costa. Dicen que hay elementales de agua y un fantasma goblin enloquecido.\n\nSi vas a ver a Revilgaz, ¿podrías contarle la historia?", progress = nil, completion = "Interesante. Hace algún tiempo, leí un libro que sugería que...\n\nAh, claro, se trataba de una leyenda trol. Si lo que Krazek dice es cierto, quizás pronto pueda añadir la Piedra de las Mareas a mi colección de artefactos y reliquias antiguos.", rewards = "Recibirás:\n\n"},
[617] = {title = "Tallos de juncos akiris", objective = "Consigue 10 fajos de junco akiris para el corsario Bloads en Bahía del Botín.", description = "¿Andas buscando trabajo, <nombre>? Quizás tenga algo pa ti si t'apetece un pequeño desafío.\n\nTengo un amigo llamado Groy que se está forrando vendiendo lo que los nagas llaman junco akiris: es un tipo de valioso junco del océano. No tengo muy claro pa qué sirve, pero paga bien por fajos de esa cosa.\n\nTráeme diez fajos de eso y haremos negocios. La última vez que vimos a los nagas fue en la playa que está al norte de aquí.", progress = "Si no tienes nada que me incumba, sigue tu camino, <nombre>.", completion = "Bien hecho, &lt;nombre&gt;. Después de todo, ¿no ha sido tan difícil conseguir 10 tallos de juncos akiris, eh? Sin ofender a los naga, ¡pero todo el mundo tiene derecho a ganarse la vida!", rewards = "Recibirás:\n\nFajín agostador"},
[618] = {title = "Enfréntate a Negolash", objective = "Lleva 10 Alas de águila ratonera a la parrilla y 5 botellas de vino de vid de jungla al barco salvavidas del capitán Smotts.\n\nMata a Negolash y lleva el alfanje de Smotts al capitán Smotts en Tuercespina.", description = "Negolash es un gigante hambriento.\n\nCuando los gigantes destruyeron nuestro barco, Negolash fue directo a la cocina. No quería comerse a la tripulación, como los otros gigantes. Negolash quería vino... y mis alas de águila ratonera a la parrilla. Son mi especialidad, y he tenido que ir hasta las Tierras Inhóspitas para encontrar la receta.\n\nSi quieres atraer al gigante, consigue vino y un montón de alas de águila ratonera. Ponlo todo en nuestro viejo barco salvavidas, en la costa, al sureste de aquí. Cuando Negolash huela toda esa comida, ¡irá seguro!Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nBrújula de Smotts"},
[619] = {title = "La tentación de Negolash", objective = "Es un barco salvavidas abandonado. En el casco, escritas con pintura descascarillada y apagada, se pueden leer las palabras:\n\n\"La venganza de Smotts\"", description = nil, progress = "Es un barco salvavidas abandonado. En el casco, escritas con pintura descascarillada y apagada, se pueden leer las palabras:\n\n\"La venganza de Smotts\"", completion = "Amontonas la comida y la bebida en la plataforma...", rewards = nil},
[620] = {title = "La banda con monograma", objective = "Devuelve la banda con monograma a su propietario.", description = "Esta preciosa banda tiene bordadas las letras \"HS\" en el dobladillo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[621] = {title = "El secreto de Zanzil", objective = "Lleva 12 muestras de la mezcla de Zanzil a Loquillo Pinchabujas en la Bahía del Botín.", description = "Zanzil el Desarraigado vive con sus seguidores en las Ruinas de Jubuwai y Aboraz, al noreste de la Bahía del Botín. Está exiliado de su tribu. ¿Por qué? Bueno...\n\nZanzil sabe cómo someter la mente de otros y sus líderes le temen. Utiliza una mezcla de plantas de la jungla con sus seguidores para eliminar su voluntad y fortalecer sus cuerpos.\n\nEsa mezcla sería muy valiosa para aquellos que deseen controlar a otros.\n\nTráeme muestras de esa mezcla y seguro que encuentro un buen uso.", progress = "¿¿Tienes las muestras de la mezcla de Zanzil??", completion = "Ah, muy bien. Aquí tienes lo tuyo.\n\nY un poco más... por tu discreción.", rewards = "Recibirás:\n\nCinturón de Corrupción"},
[622] = {title = "Vuelve con el cabo Kaleb", objective = "Lleva la olla mecánica de Krazek arreglada al cabo Kaleb en el Asentamiento Rebelde.", description = "Mientras no estabas arreglé la olla. Incluso añadí un pitorro de vapor extra, ¡y mira! Hay una nueva trituradora en el fondo que puede cortar incluso huesos de piernas.\n\nSí, sí, la olla mecánica de Krazek, es una herramienta que satisface al cocinero en un 100%. Seguro que su dueño estará satisfecho.", progress = "Hola, <nombre>. ¿Has logrado llegar a la Bahía del Botín? ¿El goblin Krazek me ha arreglado la olla?", completion = "¡Funciona! Esto es fantástico, ahora podré ponerme a preparar la cena. Muchísimas gracias, &lt;nombre&gt;.\n\nEspero que no tuvieras problemas en la Bahía del Botín. Ese lugar puede ser bastante peliagudo y... he oído rumores que dicen que, algunas veces, los goblins se llevan a marineros borrachos a sus barcos de esclavos... quién sabe dónde... para hacer quién sabe qué...", rewards = "Recibirás:\n\nAlmete de Armonía"},
[623] = {title = "Tallos de juncos akiris", objective = "Lleva un fajo de juncos akiris al corsario Groy, en Theramore.", description = "<nombre>, ya que tás aquí, he recibío un mensaje de Groy cuando no estabas. Dijo que estaba satisfecho con el último cargamento y que tiene un cliente que quiere más de esa cosa ahora mismo.\n\nYo no voy a Theramore hasta dentro de una semana, pero si tú quieres hacer el viaje, podrías llevar los juncos por mí.\n\n¿Te apetece una aventura? Sabes dónde está el Marjal Revolcafango, ¿no? Yo iría con cuidado, Theramore puede ser peligroso dependiendo de quién seas...", progress = "¿Sí?", completion = "¡¿Qué?! Tengo muchas cosas mejores que hacer que hablar con tipos como tú, &lt;clase&gt;. Haz que merezca la pena, o lárgate de aquí.\n\n¿De acuerdo? Bloads te ha enviado, ¿eh? A ver, ¿qué tienes pa mí?\n\n\n\nNo está mal, nada mal. Con otro de estos, mi cliente estaría mu contento.\n\nSupongo que tu próxima pregunta va a ser sobre el dinero, ¿eh, &lt;clase&gt;? ¡Pues claro que sí!\n\nToma, te voy a dar unas monedas y un consejo: largo de mi muelle.", rewards = "Recibirás:\n\n"},
[624] = {title = "El enigma de Cortello", objective = "¡Resuelve el enigma!", description = "Hay un puente ornamentado y con mucho tránsito\nAl este de Viento Muerto y al sur de Crestagrana\nBajo el puente te espera algo\nAlgunos lo llaman un consejo otros, una pista\nAllí te esperará eternamente\nViendo pasar el tiempo en el Pantano de las Penas", progress = nil, completion = "El pergamino húmedo contiene la siguiente pista...", rewards = "Recibirás:\n\n"},
[625] = {title = "El enigma de Cortello", objective = "¡Resuelve el enigma!", description = "Te espera una tierra muy lejana, en Kalimdor\nDonde el peligro ha destruido las esperanzas y los destinos\nAl este de Los Baldíos el tiempo es severo\nLa penumbra y el peligro imperan en el Marjal Revolcafango\nPero allí hay una pista, para aquellos dispuestos a aceptar el desafío\nEsperando ser encontrada... en una cueva... al suroeste", progress = nil, completion = "Debes encontrar un pergamino mohoso.", rewards = "Recibirás:\n\n"},
[626] = {title = "El enigma de Cortello", objective = "¡Resuelve el enigma!", description = "¡A Lordaeron! La gran tierra de confusión\nDonde valientes soldados juegan con la muerte\nEncuentra tu camino a través del gran disturbio\nAllí encontrarás el final de esta misión\nJunto a la costa de las Tierras del Interior, en la base de las cataratas\nDesde las profundidades del agua, te llama tu tesoro.", progress = nil, completion = "Abres el cofre y en su interior encuentras...", rewards = "Recibirás:\n\nMorral de expedicionario"},
[627] = {title = "Un favor a Krazek", objective = "Lleva 4 fajos de mineral de sangrita inferior a Krazek en la Bahía del Botín.", description = "Hay una cueva en la montaña, en el Cañón Mostacho Seco, al norte de las Tierras Altas de Arathi. En esa cueva hay un extraño mineral: sangrita. La sangrita de esta cueva no tiene una calidad superior, pero incluso la sangrita de baja calidad es valiosa... para aquellos que sepan usarla.\n\nMe gustaría conseguir sangrita.\n\nArreglaré tu olla mecánica, pero mientras lo hago... tráeme algunas muestras de sangrita. Si me traes las suficientes, te daré una bonificación.\n\nOh, y si no tienes la habilidad necesaria para extraerla... ¡encuentra a alguien que la tenga!", progress = "Hola, <nombre>. ¿Has conseguido la sangrita?", completion = "¡Menuda maravilla! Me has hecho un gran favor.\n\nAquí tienes tu pago...", rewards = "Recibirás:\n\n"},
[628] = {title = "Borceguí", objective = "Lleva una piel de crocolisco maduro a Drizzlik en la Bahía del Botín.", description = "Ahora que hemos terminado el interior, lo único que queda es añadir la capa exterior. Para esto, solo utilizo la mejor piel de crocolisco anciano. Duran más y son un lienzo perfecto para añadir los diseños y dibujos que completan el estilo borceguí de Drizzlik.\n\nPero no es fácil encontrar la piel perfecta, la única que podría servir es la piel de un crocolisco aguasalada anciano. Los crocoliscos ancianos solo salen fuera para defender a los otros crocoliscos aguasalada, así que quizás tengas que matar a unos cuantos para que salgan.", progress = "He oído por ahí que los crocoliscos aguasalada se están secando... malas noticias para el negocio, ¿eh?\n\nEn fin, espero que tengas esa piel de crocolisco aguasalada anciano para mí.", completion = "¡Genial! Gracias a tu ayuda, tendré este pedido terminado y se lo enviaré al director Riddlevox en el próximo barco que salga para Villabajo. ¡Incluso antes de la hora prevista!\n\nHe hecho algunas de más, así que, por tus esfuerzos, puedes quedarte con un par. Regalo de Drizzlik.\n\nNo hay nada que supere el estilo borceguí de Drizzlik !", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nBorceguíes"},
[629] = {title = "El Arrecife Mortal", objective = "Lleva fragmento de tablilla a Kin'weelay en el Campamento Grom'gol.", description = "Mucho tiempo atrás, un gran temblor de tierra sumergió una antigua ciudad trol bajo las aguas de La Costa Escarpada, al noroeste. Llamamos a ese lugar El Arrecife Mortal, ya que ahora los murlocs residen en las ruinas de la ciudad, atacando a todo el que se acerca.\n\nEn esas ruinas hay una antigua tablilla que narra la vieja fábula de Gri'lek, un héroe de leyendas trol. La fábula es sagrada para la tribu Lanza Negra y, aunque la tablilla es muy grande como para moverla, quiero un fragmento para consagrarlo en nuestro nuevo hogar de Orgrimmar.", progress = "Estoy esperando por el fragmento de la tablilla, <nombre>.", completion = "Y eso está bien. Cuando vuelva a Orgrimmar, colocaré este fragmento en un lugar sagrado. Nos recordará el poder y el honor de Gri'lek.\n\nGracias, &lt;nombre&gt;. Tus actos marcan una creciente amistad con la tribu Lanza Negra.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[630] = {title = "Mensaje en una botella", objective = "Mata al rey Mukla y vuelve a por la princesa Poobah con la llave para liberarla.", description = "<nombre>, pongo mi fe en ti y en cualquiera que te ayude a liberarme. Mi secuestrador es el gran Cieloleno rey Mukla. Domina a las bestias de esta isla que me tienen aquí contra mi voluntad con ayuda de una cadena. Si puedes matarle y encuentras la llave de estos grilletes, estaré en deuda contigo eternamente.\n\nA menudo puedo oírle en la parte sureste de la isla, pero ten cuidado. Hay que tomarle muy en serio.", progress = "Agradezco a mis ancestros que alguien haya venido a ayudarme. ¿Tienes la llave para liberarme?", completion = "&lt;nombre&gt;, me has salvado de un gran deshonor. Si estuviera en mis manos, te ofrecería un lugar en mi tribu. Pero, por ahora, solo puedo ofrecerte esto por el coraje y la nobleza que has mostrado.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nAro de Pooh-Bah"},
[631] = {title = "El puente Thandol", objective = "Rhag Garmason quiere que localices a Herrumbra.", description = "Se oyó el estruendo de una enorme explosión por todas las colinas. '¡Fue horrible, créeme!\n\nLos buenos soldados murieron en la defensa el Puente Thandol. Largatrenza dice que nos vengaremos de los Hierro Negro en un santiamén.\n\nPero estamos en un mundo lleno de dolor y no veo ningún refuerzo. Largatrenza envió a Herrumbra a explorar el puente del Este para ver si podemos ganarlo.\n\nEso fue hace días. Con Dun Modr bajo asedio no tenemos hombres suficientes para ir a buscarlo.\n\nQuizás tú puedas ayudarnos...", progress = nil, completion = "Registras el diminuto cadáver enano. Grabado en el yelmo puedes leer claramente: Ebenezer Herrumbra.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[632] = {title = "El puente Thandol", objective = "Vuelve a informar a Rhag Garmason y enséñale el pergamino que encontraste en el cadáver de Herrumbra.", description = "Parece que el pobre Herrumba cayó en una emboscada de los Hierro Negro. Descubres un pergamino junto a su puño cerrado.\n\nLleva la desalentadora noticia a Rhag Garmason y enséñale lo que has descubierto.", progress = "¿Alguna señal de Herrumbra, <nombre>?", completion = "Oh, no puede ser, son malas noticias...", rewards = "Recibirás:\n\n"},
[633] = {title = "El puente Thandol", objective = "Destruye el cajón de explosivos.", description = "¡¡¡Cielos!!! ¡¡¡No!!! Pobre Herrumbra. Es una tragedia.\n\nNo debemos permitir que su muerte haya sido en vano. El pergamino que has descubierto indica que Kam Furiahonda, el cerebro del ataque, ordenó atesorar un alijo de explosivos al otro lado del Puente Thandol, en las Tierras Altas de Arathi.\n\n¡Si esos explosivos llegan al puente, nuestra principal vía de suministros recibirá un duro golpe! ¡Hay que destruir el alijo, <nombre>! Vuelve a verme cuando hayas terminado tu misión.\n\nEres nuestra única esperanza.", progress = nil, completion = "¡Lo que queda del puente está a salvo! Has prestado un gran servicio al rey Magni y a las gentes de Khaz Modan. \n\nSi tan solo pudiéramos resistir hasta la llegada de los refuerzos...", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de guarda enanoPantalones Tierra pantanosa\n\nTambién recibirás:\n\nCapa de guarda enanoPantalones Tierra pantanosa"},
[634] = {title = "Petición a la Alianza", objective = "Busca al capitán Nials en el Refugio de la Zaga.", description = "Voy a pedirte otro pequeño favor, <clase>. Estamos esperando las reservas de Forjaz. Pero Roggo tiene noticias de un campamento de la Alianza al norte de las Tierras Altas de Arathi, en el Refugio de la Zaga.\n\nViaja hasta allí y busca al capitán Nials. Pregunta al buen capitán si la Alianza puede ofrecernos algunos soldados que nos ayuden a recuperar Dun Modr y el Puente Thandol.", progress = nil, completion = "Todas mis condolencias para Largatrenza y sus valientes soldados. Las noticias del Puente Thandol y de la caída de Dun Modr son un duro golpe. \n\nPero Stromgarde ha caído, de ahí nuestra presencia aquí, en el Refugio de la Zaga. Nos enfrentamos a la reconquista de nuestra propia ciudad. \n\nEl honor y el sentido del deber nos impiden abandonar las Tierras altas hasta que en Stromgarde vuelva a ondear el estandarte del Rey. Enviaré un mensaje a Largatrenza advirtiéndole de que está solo...", rewards = "Recibirás:\n\n"},
[635] = {title = "Cristal en las montañas", objective = "Encuentra el cristal de tu visión.\n\nObjeto provisto: \n", description = "El fragmento palpita cuando lo agarras...\n\nSientes una especie de súplica saliendo del fragmento, como si alguien estuviera intentando comunicarse contigo, alguien que necesita tu ayuda desesperadamente.\n\nUna visión llena tu mente... son las montañas septentrionales de Arathi, y ves también un cristal brillante sobresaliendo del suelo.\n\nTe sientes <atraído/atraída> hacia ese lugar.", progress = nil, completion = "Este cristal palpita, es como si tuviera una fuerza viva atrapada en su interior.", rewards = "Recibirás:\n\n"},
[637] = {title = "La carta de Sully Balloo", objective = "Entrégale la carta de Sully Balloo a Sara Balloo.", description = "El sobre empapado y desgastado se deshace entre tus manos, revelando varias páginas de una carta escrita con una cuidada caligrafía.\n\nLa carta está dirigida a Sara Balloo de Forjaz y comienza así: \"Querida Sara...\"", progress = "Parece que hayas recorrido una gran distancia y visto gran caos en tu vida, <clase>. Los tiempos que vivimos ponen a prueba el alma. ¿Qué noticias me traes?", completion = "¿Dices que viste a mi Sully tendido sin vida bajo las aguas heladas? ¡Dime que no es así!\n\nDéjame leer lo que escribió...", rewards = "Recibirás:\n\n"},
[638] = {title = "Aterratrols", objective = "Viaja a Sentencia en las Tierras Altas de Arathi y habla con Zengu.", description = "Con Thoras Aterratrols muerto y Stromgarde en ruinas, Vol'jin ha ordenado que hagamos todo lo posible por robar la legendaria espada Trol'kalar a los humanos, para que podamos usarla en nuestras próximas batallas contra los trols de Zul'Gurub.\n\nZengu, uno de nuestros cazadores más expertos, se ha dirigido a Sentencia, al noreste de las Tierras Altas de Arathi, para encargarse de todo.", progress = nil, completion = "¿Qué tal te va, &lt;clase&gt;? La tarea que nos ha propuesto Vol'jin será difícil y cualquier ayuda que puedas proporcionarnos, será más que apreciada.", rewards = "Recibirás:\n\n"},
[639] = {title = "El sigilo de Strom", objective = "Consigue el sigilo de Strom para Zengu en Sentencia.", description = "En tiempos ancestrales, los trols lucharon contra el Imperio Arathoriano y contra nuestro más temible adversario Lord Ignaeus de Strom, al que llamaban Aterratrols. Acudía a la batalla empuñando a Trol'kalar, verdugo de troles en su antigua lengua.\n\nPero ahora la estirpe de Aterratrols se ha debilitado, su bastión está en ruinas.\n\nLa espada está protegida por varios sigilos que se dividieron entre los supervivientes de Stromgarde, debemos recuperarlos.\n\nLa Hermandad se llevó el primero, lo custodia uno de sus hombres de Stromgarde.", progress = "¿Has encontrado ya el primer sigilo? Lo más probable es que lo lleve encima uno de los agentes de la Hermandad de Stromgarde.", completion = "Fantástico, colega. Es un buen comienzo para reunir los sigilos que necesitamos para extraer a Trol'kalar de la tumba de Aterratrols.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[640] = {title = "El sigilo roto", objective = "Recupera los 11 fragmentos de sigilo de los defensores de Stromgarde y llévaselos a Tor'gan en Sentencia.", description = "La tumba de Thoras Aterratrols está protegida por tres sigilos, el primero de los cuales recuperaste de la Hermandad. Será más difícil conseguir los otros dos ya que la milicia de Stromgarde los tiene firmemente atrincherados.\n\nEl hijo de Thoras, Galen, dividió el sigilo de Thoradin en once fragmentos que dispersaría entre los defensores de Stromgarde. Recupera todos los fragmentos y llévaselos a Tor'gan, el chamán trol, será capaz de recomponer con ellos el sigilo completo.", progress = "Throm-ka, <clase>. Zengu me ha informado de que requerías mi ayuda.", completion = "Ah, ¿me necesitas para reconstruir este sigilo? No debería ser muy difícil... todas las piezas parecen estar aquí y será fácil reconstituir la estructura mágica.\n\nPuede que te convenga cerrar los ojos.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[641] = {title = "El sigilo de Thoradin", objective = "Llévale el sigilo de Thoradin restaurado a Zengu en Sentencia.", description = "La reconstrucción ha funcionado perfectamente. Ahora se puede usar el sigilo para desbloquear el sello de la tumba de Aterratrols. Devuélveselo a Zengu, seguro que tiene más tareas para ti.", progress = "¡<nombre>! ¿Tienes el segundo sigilo?", completion = "Ya estamos a medio camino de conseguir extraer a Trol'kalar de la tumba de Aterratrols. Aún no estamos seguros de dónde se encuentra el tercer sigilo porque sus poseedores parecen haberse dirigido a algún lugar cerca de Arathi pero tengo fe en que lograremos encontrarlo.", rewards = "Recibirás:\n\n"},
[642] = {title = "La princesa secuestrada", objective = "Recolecta 12 motas de Myzrael y llévalas a los fragmentos iridiscentes en el Cañón Mostacho Seco.", description = "Me llamo Myzrael. Soy una princesa de la tierra, y mis captores, los gigantes, me han encerrado en el subsuelo de las Tierras Altas de Arathi. Estos fragmentos de cristal son mi único medio para hablar con el mundo de la superficie.\n\nPor favor, ayúdame. Los aliados de los gigantes, los kobolds Mostacho Seco, tienen un fragmento como este en el Cañón Mostacho Seco, hacia el este. Para que ese pedazo cobre poder, debes conseguir motas de Myzrael de los kobolds y aplicárselas.\n\nTe lo suplico, <nombre>, ¡ayúdame!", progress = "Este bloque de fragmentos se parece mucho a los fragmentos de Myzrael. Pero no late en ellos el mismo sentimiento de seguridad.", completion = "¡Ah! Has activado estos fragmentos. ¡Ya empiezo a sentirme como antes!\n\nAunque ¡qué lejos estoy aún de la liberación!", rewards = "Recibirás:\n\n"},
[643] = {title = "El sigilo de Arathor", objective = "Recupera el sigilo de Arathor y llévaselo a Zengu en Sentencia.", description = "Aunque apenas pueda afirmarse que controlan Stromgarde, y no digamos las Tierras Altas de Arathi, el príncipe humano lleva los restos de su caballería por el campo. No logro entender por qué lo hace, aunque ¿quién entiende a los humanos?\n\nSean cuales sean sus motivos, le ha dado uno de los sigilos al teniente Valorcall, el jefe de su caballería. Encuéntralo y tráeme el sigilo.", progress = "No será fácil seguirle el rastro a Valorcall, <nombre>. A caballo puede recorrer grandes distancias rápidamente. No obstante, debemos conseguir su sigilo.", completion = "La pérdida del jefe de la caballería será un golpe duro para los defensores de Stromgarde. Y lo que es aún mejor, significa que solo un sigilo nos separa de hacernos con Trol'kalar.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[644] = {title = "El sigilo de Aterratrols", objective = "Mata al Príncipe Galen Aterratrols y llévale el sigilo de Aterratrols a Zengu en Sentencia.", description = "El último sigilo necesario para desbloquear la tumba de Thoras Aterratrols está en manos de su propio hijo. Mora en lo más recóndito de Stromgarde, en una capilla en los distritos de la milicia. El príncipe Galen está bien protegido y es él mismo un guerrero respetable, así que esta tarea podría resultar de lo más difícil.", progress = "Has vuelto, <nombre>. ¿Se ha roto la Iínea de Ignaeus Aterratrols?", completion = "Vaya día, &lt;clase&gt;. La sangre de Aterratrols ya no obsesionará a mi pueblo, los sigilos necesarios para conseguir a Trol'kalar están en nuestras manos. Pronto, muy pronto, volveremos a atacar a los odiados trols de Zul'Gurub.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[645] = {title = "Trol'kalar", objective = "Recupera la espada Trol'kalar de la Tumba de Aterratrols en Stromgarde.", description = "¿Qué es esto? Con los cuatro sigilos reunidos, han logrado recomponerlos y crear un solo sigilo. No hay duda de que con este lograrán atravesar la protección de la tumba de Aterratrols.\n\nLos restos de Aterratrols están enterrados en el Sagrario detrás de la capilla de Stromgarde. Recupera Trol'kalar.", progress = "Los grabados de la piedra de la cara de la tumba de Aterratrols coinciden los rasgos brillantes del sigilo de Ignaeus...", completion = "Al hacer coincidir los rasgos del rostro de la tumba de Aterratrols con los rasgos brillantes del sigilo de Ignaeus, el sigilo se consume en medio de un destello y la legendaria espada Trol'kalar queda liberada de la pétrea mano que la empuña.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[646] = {title = "Trol'kalar", objective = "Entrega Trol'kalar a Zengu en Sentencia.", description = "La luz se desvanece despacio, la espada Trol'kalar, retenida en una funda pétrea se ha liberado.", progress = "¡<nombre>! ¿Has conseguido la espada?", completion = "Durante generaciones, las tropas de Strom y de Arathor han devastado a los trols. Ahora su arma más poderosa está en nuestras manos. Se acabó arañar cada centímetro de territorio en los húmedos bosques de Zul'Gurub.\n\nNos has sido de gran ayuda, &lt;nombre&gt;, y los trols Lanza Negra están en deuda contigo.\n\nMe ocuparé de que la espada se entrega a Nimboya en Grom'gol.", rewards = "Podrás elegir una de estas recompensas:\n\nArmadura tintada de sangreEscudo de luchador de fosa\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nEscudo de luchador de fosaArmadura tintada de sangre"},
[647] = {title = "Licor de Luna de Mackreel", objective = "Llévale el licor de luna de MacKreel al maestro cervecero Bilger de Costasur.", description = "¡Anoche tomé un poco de licor de luna! Largatrenza me mataría si supiera que perdí el sentido mientras estaba de guardia.\n\nEsa bebida sí que es fuerte... Sentía como si una brigada entera de máquinas de asedio me hubiera pasado por encima.\n\n¡Oh, mira qué hora es! Le prometí al maestro cervecero Bilger de Costasur que saldaría mi deuda con él enviándole licor de luna.\n\nEl problema es que solo dispongo de 15 minutos para ello. ¿Te importaría llevárselo tú? ¡Date prisa!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[648] = {title = "¡Rescatar a OOX-17/TN!", objective = "Lleva a OOX-17/TN hasta el Puerto Bonvapor y después informa a Oglethorpe Obnoticus, que está en Bahía del Botín.", description = "Vuelves a oír la voz del gnomo que sale del robot:\n\n\"Tengo que llevar a OOX-17/TN a un lugar abierto y seguro en el que pueda iniciar el largo proceso de despegue. Tiene un posicionador de serie, pero necesito tiempo para que vuelva a estar operativo. Lleva al robot a, digamos, el Puerto Bonvapor. Allí podré tomarme mi tiempo para intentar que todo vuelva a funcionar correctamente.\"\n\n\"Llévalo con cuidado al puerto y después ven a verme a Bahía del Botín. Oglethorpe Obnoticus. ¡Corto y cierro!\"", progress = nil, completion = "¡Ah, así que tú eres quien ha rescatado a OOX-17/TN! ¡No sé cómo darte las gracias por tu ayuda! Sin duda, tengo que perfeccionar mi robot mensajero pero gracias a ti no perderé innumerables horas de trabajo construyendo uno nuevo, lo que implicaría perder muchas piezas de oro.\n\nPor favor, elige uno de los objetos que tengo. Con suerte, alguno de ellos te resultará útil. Gracias de nuevo por tu inestimable ayuda. ¡Mis robots y yo estamos en deuda contigo!", rewards = "Podrás elegir una de estas recompensas:\n\nDeflector optomáticoTemporizador de huevos termostático\n\nTambién recibirás:\n\nDeflector optomáticoTemporizador de huevos termostático"},
[649] = {title = "Recupera el calimocho", objective = "Habla con Malton Droffers en Orgrimmar.", description = "Tengo un trabajito de rescate para ti, y si te gusta el buen calimocho, seguro que lo aceptas. ¿Que qué es?\n\nPues una bebida, ¿qué va a ser?\n\nHay un cargamento de calimocho de miel de las Tierras del Interior en una avanzada abandonada de la Horda. ¡Ya no se hace un calimocho como ese! Uno de esos atontados con los que trabaja Malton está interesado, así que traéme una caja de calimocho para él y un par para mí. Habla con Malton para que te dé los detalles.", progress = nil, completion = "¡Ah, sí, el calimocho! Mi padre quiere venderlo en una taberna de Orgrimmar, pero nos pagarán más si está en el envase original. Sé de alguien que puede encargarse; está en las Tierras del Interior, como el calimocho. Lo que pasa es que es, esto, un elfo noble.\n\nPero es bueno, no está con la Alianza. En realidad no es que sea bueno, es un borracho; no veas cómo le da.", rewards = "Recibirás:\n\n"},
[650] = {title = "Recupera el calimocho", objective = "Habla con Gilveradin Cazasol; su campamento está en las Tierras del Interior.", description = "El elfo con el que necesitas hablar se llama Gilveradin Cazasol. ¡Puede identificar cualquier vino, con cosecha y todo, con solo olerlo! Él puede embotellar el calimocho que necesitamos, así que tendrás que hablar con él. Necesitamos seis botellas en total: dos para papá y todas las que él quiera y, créeme, querrá alguna que otra. Cuando hayas embotellado el calimocho y tengas las dos botellas de papá, vuelve aquí.\n\nEl campamento de Gilveradin está por las Tierras del Interior, cerca de la casa de un alto elfo… y esos altos elfos no son buena gente. Créeme.", progress = nil, completion = "Ah, ¿conoces a Malton? Es un viejo compañero de juergas. He descubierto algo que trasciende las fronteras y los tratados: el alcohol. Su consumo nos hará libres y felices.\n\nPero sospecho que no has venido a hablar de las virtudes del alcohol, ¿verdad?", rewards = "Recibirás:\n\n"},
[651] = {title = "Piedras de vínculo", objective = "Reúne la llave ardiente, la llave del emblema y la llave tronadora de la Piedra de Vínculo Occidental, la Piedra de Vínculo Oriental y la Piedra de Vínculo Exterior.\n\nLlévalas a la Piedra de Vínculo Interior.", description = "Hay cuatro Piedras de vínculo diseminadas por todo Arathi. Esas piedras son las que me mantienen anclado bajo la tierra.\n\nPara desbloquearlas tienes que desbloquear primero la más fuerte: la Piedra de vínculo interior.  Y para hacerlo debes reunir las llaves de las otras tres.\n\nLas piedras están custodiadas por guardias pero, con un poco de astucia o suerte, encontrarás la forma de sortearlos.", progress = "Esta es la Piedra de Vínculo Interior. Es la más fuerte de las piedras de vínculo y hay que desbloquearla para abrir las otras.", completion = "&lt;Myzrael habla a través de la piedra.&gt;\n\n¡Lo has logrado! ¡Has abierto las Piedras de vínculo! Siento cómo los grilletes se sueltan y la libertad se acerca.\n\nComo &lt;clase&gt;, eres muy noble, &lt;nombre&gt;. ¡Mi salvador!", rewards = "Recibirás:\n\n"},
[652] = {title = "Desbloquear la piedra angular", objective = "Encuentra a Fozruk y mátalo. Lleva la Vara del Orden a la piedra angular en las Tierras Altas de Arathi.", description = "Desbloquear las Piedras de vínculo me liberó en gran parte de mi confinamiento. Pero aún queda una cerradura por abrir  Es la Piedra llave que ves en ese círculo de piedras. Y la llave la tiene uno de mis captores, Fozruk, el guardián de las piedras. \n\nDebes conseguir esa llave. Los guardianes de las piedras la llaman el Cetro del Orden. Arrebátasela a Fozruk y úsala para abrir la Piedra llave. \n\n<nombre>, me has ayudado mucho. Pero necesito un poco más de ayuda… ¡Mi gratitud hará temblar los mismísimos pilares de la tierra!", progress = "La piedra angular está cargada de energía geomántica.", completion = "Colocas la vara en la piedra angular. Y sientes cómo el suelo tiembla bajo tus pies...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[653] = {title = "Aliados de Myzrael", objective = "Habla con Gerrig Agarrahueso en La Caverna Abandonada de Forjaz.", description = "<Myzrael habla.>\n\n¡Libre! ¡SOY LIBRE! Libre para recuperar la fuerza, sin que me vean mis captores. Porque si me enfrentara a ellos ahora, seguro que me vencerían y me volverían a encerrar.\n\nPero con el tiempo haré frente a los gigantes ¡y se arrepentirán de haber sido mis guardias!\n\nEres <un/una> noble <aliado/aliada>, <nombre>. Dentro de algún tiempo volveré a necesitarte. Cuando esté listo, necesitaré que me conjures para devolverme a la superficie.\n\nHabla con Gerrig Agarrahueso. Se encuentra en Forjaz, en una tienda de La Caverna Abandonada. Él sabe cómo realizar el conjuro.", progress = nil, completion = "La Señora me avisó de tu visita, &lt;clase&gt;. Te doy la bienvenida en mi morada ya que tú, al igual que yo, estás al servicio de una más alta causa.", rewards = "Recibirás:\n\n"},
[654] = {title = "Recogida de muestras en Tanaris", objective = "Consigue muestras aceptables de 8 basiliscos, 8 hienas y 8 escorpiones. Devuélvele el equipo de estudio al ingeniero jefe Silvaina, que está en Gadgetzan, antes de que se agote la fuente de energía.", description = "Al activar la fuente de energía el equipo de estudio de campo empieza a brillar y a vibrar. Parece una señal de que todo funciona correctamente.\n\nDe acuerdo con el ingeniero jefe Silvaina, a partir de ahora tienes dos horas para recoger los datos necesarios en el desierto de Tanaris. Si no consigues hacerlo en un plazo máximo de dos horas, perderás toda la información que tenías hasta el momento y deberás comprar una nueva fuente de energía.\n\nVuelve a hablar con él cuando hayas concluido tu tarea.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[655] = {title = "Sentencia", objective = "Habla con Tor'gan en Sentencia.", description = "Lo recuerdo como si fuera ayer... Tantos años sin esperanza. Con la madera y el acero ligándome a este terrible lugar. ¡Y por fin, por fin! Ahí estaba el Jefe. Mazohecatombe, con sus agoreras placas y su martillo de guerra en alto.\n\nFue terrible... Que el golpe asestado por la mano de un cobarde acabara con nuestro héroe más admirado. Cargó por detrás con su caballo y lo atravesó con su lanza... Yo caí de rodillas. ¿Qué esperanza nos quedaba? ¿Qué esperanza nos queda...?", progress = nil, completion = "Ah, hablas de Gor'mul. Sí, por lo que cuenta, parece que nunca se repuso de la muerte de Martillo Maldito.\n\nGor'mul estuvo cautivo aquí cuando Thrall y Orgrim luchaban para liberar el campamento pero cuando la tierra tembló y nuestros hermanos escaparon, él se quedó atrás. Estuvo vagando en soledad durante años sin rumbo ni objetivo hasta que volvimos para asentarnos aquí en Sentencia.", rewards = "Recibirás:\n\n"},
[656] = {title = "Invocar a la princesa", objective = "Ve a los fragmentos de Myzrael, invócala y derrótala.\n\nRecolecta sus grilletes de Eldritch y pónselos a los fragmentos de Myzrael.", description = "Debes derrotar a Myzrael antes de se vuelva más fuerte. Si no lo haces, en cuanto tenga suficientes poderes ¡desafiará a sus captores! Hay quien piensa que puede controlarla, pero si así fuera, ¡¿por qué la encadenaron los gigantes?!\n\n¿A que no tiene ni pies ni cabeza?\n\nPara derrotarla, conjúrala en los fragmentos de Myzrael donde hablaste con ella la primera vez. Derrótala, coge sus grilletes de Eldritch y pónselos a los fragmentos de Myzrael.\n\nHazlo y volverá a estar presa.", progress = "Los fragmentos Myzrael están sospechosamente callados.", completion = "Al colocar los grilletes de Eldritch alrededor de los fragmentos, se oye un lejano gemido inhumano en el subsuelo. Tras este lamento se oye un rugido.\n\nLos fragmentos de Myzrael ya no invocan cálidos sentimientos de seguridad como otrora hicieran. Laten ahora con una amenaza abierta... como si lo que los sustenta encerrara una voluntad enferma para con la superficie.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nFragmento cristalino pulsante"},
[657] = {title = "Sospechas de una nueva Peste", objective = "Habla con Kinelory.", description = "<Quae empieza a examinar la carpeta.>\n\nEsto SÍ que es interesante. Al parecer, el boticario Jorell ha estado trabajando en unos experimentos letales aquí en Go'Shek. No estoy seguro de cuál es la relación con Trabalomas pero a Phin no le gustaría que dejásemos que siga adelante.\n\nTuvimos un roce con unos orcos cuando no estabas por lo que no estoy en condiciones de ayudar a Kinelory. Si te apetece, va a necesitar que la protejan cuando vaya a la Granja a robar lo que sea que Jorell aún no haya enviado a Molino Tarren.", progress = nil, completion = "Si le trae a Phin el descanso, destruiré personalmente uno a uno a esos Renegados. Ha hecho tanto por nosotros desde la Peste. Nunca pensé que volvería a ver a mis amigos o a la familia.\n\nDame un toque cuando estés preparado. Empezaremos enseguida.", rewards = nil},
[658] = {title = "Sospechas de una nueva Peste", objective = "Encuentra a la mensajera Renegada y lleva su cartera sellada a Quae cerca de la Granja Go'shek.", description = "¿Así que Phin te ha mandado ver qué hacemos? No me sorprende. Se preocupa demasiado.\n\nHemos vigilado la granja durante días y Kin dice que hace poco ha visto a un mensajero saliendo de esa casita que hay hacia el sur.\n\nHabla mucho, pero me fío de su instinto cuando estamos en sitios peligrosos.\n\n¿No sabrás seguir rastros, por un casual? Creemos que el mensajero podría llevar notas al Molino Tarren. Si pudiéramos echar el guante a esos documentos... Seguro que nos ayudan a entender qué está pasando en Trabalomas.", progress = "Te agradecemos tu ayuda en la búsqueda de ese mensajero, <nombre>.\n\nMientras estabas ausente, Kin vio a un boticario salir de la misma casa al sur de este lugar. Estoy seguro que no pueden tramar nada bueno, solo necesito una prueba antes de salir y hacer algo imprudente.\n\n¿Has encontrado algo?", completion = "¡Fantástico! \n\nVeamos de qué se trata...", rewards = "Recibirás:\n\n"},
[659] = {title = "Sospechas de una nueva Peste", objective = "Encuentra a Quae y a Kinelory cerca de la Granja Go'shek en las Tierras Altas de Arathi.", description = "Saludos. Te doy la bienvenida, <nombre>. Dirijo un grupo de aventureros que investigan sucesos extraños que han tenido lugar en esta zona. \n\nMi primo de Trabalomas ha tenido que sacrificar a su perro Stanley porque el animal creció místicamente hasta alcanzar un gran tamaño y empezó a atacar a todo el que se le acercara. \n\nCreo que los no-muertos están involucrados en eso y estamos buscando indicios que lo prueben. \n\nDos compañeros míos, Quae y Kinelory, fueron a la granja de Go'Shek en las Tierras Altas de Arathi y hace días que no tenemos noticias suyas. ¿Podrías encontrarlos por mí? Empiezo a preocuparme de verdad.", progress = nil, completion = "Procura estar tan quieto como puedas, viajero.\n\nA los orcos no les sienta muy bien que los espíen y en esta Granja hay más de lo que se ve a simple vista.", rewards = "Recibirás:\n\n"},
[660] = {title = "Sospechas de una nueva Peste", objective = "Protege a Kinelory hasta que haya regresado con Quae.", description = "Muy bien, <nombre>. Si tú estás listo, yo también.\n\n¿Vamos?", progress = nil, completion = "Nos has hecho un gran favor, &lt;nombre&gt;. Gracias.\n\nEspero que Kin no te lo pusiera demasiado difícil. Es muy simpática pero como enemiga es letal.", rewards = "Recibirás:\n\n"},
[661] = {title = "Sospechas de una nueva Peste", objective = "Encuentra a Phin Odelic en Costasur.", description = "He visto a Kinelory con mucho peor aspecto después de una noche en la taberna que ahora. No hay duda de que tu protección fue de gran ayuda.\n\nCon los objetos que trajo de la Granja, Phin debería hacerse una idea de lo que está ocurriendo en Arathi. Solo para asegurarnos de que no hay represalias después de vuestra incursión, nos quedaremos aquí un poco más. ¿Te gustaría volver a Costasur y hablar con Phin?", progress = nil, completion = "¡¿Conque esos dos pícaros están bien después de todo?! Ja, ja, me alegra oírlo. Te agradezco las noticias sobre su paradero y su estado; me complace enormemente que sigan vivos, los dos son amigos insustituibles y viejos compañeros de fatigas.\n\nPero... es más importante que te agradezca como es debido tu ayuda. Aquí tienes además de la gratitud de todos los míos.\n\nCon algo de suerte, averiguaremos qué traman los Renegados y le pondremos fin.", rewards = "Podrás elegir una de estas recompensas:\n\nTogas guardapolvoLeotardos de paso ligero\n\nTambién recibirás:\n\nTogas guardapolvoLeotardos de paso ligero"},
[662] = {title = "Rescate en alta mar", objective = "El primer oficial Nizlix quiere que recuperes los cuadernos de bitácora y las cartas de navegación de El capricho y del Espíritu de argénteos.", description = "O’Breen guió a las naves hasta la cala. Estábamos seguros de que ahí encontraríamos el tesoro. El Heraldo atracó sin novedad pero cuando El Capricho y el Espíritu de Argénteos se acercaban, la marea empezó a bajar. ¡Lo más extraño que haya visto nunca!\n\nbLas dos naves encallaron en algo… una especie de templo que había en el arrecife. Se hundieron a gran velocidad... Fue una tragedia.\n\nNecesitamos recuperar sus cuadernos de bitácora y sus cartas de navegación antes de partir. ¡No podemos permitir que nadie vea esa información!", progress = "¿Cómo va la misión de búsqueda y recuperación, <nombre>? Tenemos que recuperar los cuadernos de bitácora y las cartas de navegación de El capricho y del Espíritu de argénteos.\n\nSi la información de esas fuentes cayera en manos enemigas, el maestro de flota Cuernomarino nos haría pasear la tabla.\n\nEso si salimos algún día de este lío...", completion = "La certeza de que esta información está a buen recaudo tranquilizará al mando de Bahía del Botín. Muy bien, &lt;nombre&gt;.", rewards = "Recibirás:\n\nMartillo de agua negra"},
[663] = {title = "¡Tierra a vista!", objective = "Habla con el capitán O'Breen en la Cala de Faldir.", description = "Lolo ve a un minúsculo <raza>! ¡Siempre al loro, Lolo está!\n\n<Bienvenido/Bienvenida> a la Cala de Faldir. El capitán O’Breen dijo que solo estaríamos aquí durante unas horas. Lo justo para reunir el tesoro y volver a Bahía del Botín.\n\nPero Lolo piensa que vamos a estar allí más tiempo. Perdimos otros dos barcos de nuestra formación. Pobres Espíritu de argénteos y El capricho. ¡En el fondo del mar ahora descansan!\n\nLolo sugiere que hables con el capitán O'Breen si te vas a quedar por aquí.", progress = nil, completion = "¡Ah del barco, colega!\n\nSe ha producido un desastre. Cuernomarino se quedará con mi barco si se entera de lo que se ha hundido.\n\nUno de los inventos del profesor indicaba que el tesoro perdido estaba cerca de esta cala. Hice venir al Heraldo al embarcadero y apunté hacia el Espíritu de argénteos y El capricho para seguir su rumbo.\n\nEntonces el mar retumbó. La marea bajó y los dos barcos encallaron en el arrecife.\n\nNo es solo un arrecife. Allí abajo hay algo más...\n\nAyuda a mi tripulación y recibirás una generosa recompensa.", rewards = "Recibirás:\n\n"},
[664] = {title = "Ahogar las penas", objective = "El capitán Tripacero de Cala de Faldir quiere que mates a 10 asaltantes Espinadaga y a 3 hechiceras Espinadaga.", description = "Ah, esta vida es terrible. Debería estar en una tumba de agua ahora mismo, no aquí, junto al fuego y respirando aire fresco.\n\nLa tripulación se esforzó tanto para salvar El Capricho, mi hermosa nave... Benditos sean, benditos sean sus corazones.\n\nO'Breen llegó a la costa sano y salvo, pero nosotros no tuvimos tanta suerte. La mar nos hundió, lo juro.\n\nUn momento antes estábamos a flote y después… el agua empezó a entrar. ¡Y esas bestias! Mataron a todos los supervivientes. Esa escoria naga... Yo perdí el conocimiento y acabé aquí, a salvo.\n\nQuiero ver a esas bestias muertas.", progress = nil, completion = "Buen trabajo, sí señor, no-muerto.\n\nEsa escoria se ha llevado su merecido. Aunque nada me devolverá mi barco o mi tripulación, la muerte de esos nagas me brinda algo de consuelo.", rewards = "Recibirás:\n\nGuantes de lobo marino"},
[665] = {title = "El tesoro sumergido", objective = "Escolta al profesor Fiseltor hasta la cueva y a su regreso.", description = "Ahora que somos asaltantes Aguasnegras hechos y derechos, debemos ayudar a O’Breen a encontrar el tesoro elfo perdido.\n\nEs casi imposible encontrar las gemas en el mar sin ayuda. El doctor ha creado unas gafas que ayudan a ver en la oscuridad del mar. Pero hay que cargarlas con la energía que emana una piedra encantada de la cueva que hay en la colina.\n\n¡Y la cueva está maldita! Cuando alguien se acerca, cae en una emboscada. Defiéndeme para que pueda recoger la energía de la piedra con las gafas.", progress = nil, completion = "¡Las gafas protectoras funcionan ahora perfectamente! Muy bien, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[666] = {title = "El tesoro sumergido", objective = "El doctor Draxlegauge de la Cala de Faldir quiere que reúnas 10 gemas de elfo y le devuelvas las gafas de caza de gemas cuando hayas acabado.", description = "El tesoro lleva tanto tiempo en el fondo del mar que las gemas se han calcificado y convertido en piedras. Pero gracias al poder de estas gafas protectoras las localizarás sin esfuerzos.\n\n¡Un inventito gnomo de gran utilidad!\n\nAsí que toma prestadas estas gafas de caza de gemas, <nombre>, y mira a ver si puedes recolectar algo del tesoro perdido para el capitán O'Breen.\n\nYo mismo bucearía hasta allí pero... mm... estooo, tengo importantes asuntos científicos que atender en tierra firme y seca… digo, pues eso.", progress = "¿Qué tal funcionaban las gafas protectoras? ¿Todo funcionaba bien? Se pueden realizar ajustes si fuera necesario.\n\nAh, y necesitaré que me devuelvas las gafas protectoras cuando hayas acabado. Seguro que el capitán O'Breen encontrará muchas formas de usarlas más adelante.", completion = "¡Magnífico, &lt;nombre&gt;! ¡El Capitán estará muy complacido! ¡Y no veas cuando se entere el maestro de flota Cuernomarino!", rewards = "Podrás elegir una de estas recompensas:\n\nIrradiador gnómicoAlmádena servomecánica\n\nTambién recibirás:\n\nIrradiador gnómicoAlmádena servomecánica"},
[667] = {title = "La muerte viene de las profundidades", objective = "Protege a Temblores O'Breen durante el ataque.", description = "Vaya, malas noticias. Al parecer, los Dagaespinazo están amasando recursos para un ataque.\n\nLos nagas son famosos por su crueldad en el combate. Seguirán atacando hasta que me maten.\n\nNo valoro mi propia vida más que la de mis hombres, pero debes defenderme. La tripulación no será capaz de volver a Bahía del Botín sin mis conocimientos de navegación.\n\nLos nagas vendrán desde el mar. Necesito que permanezcas aquí conmigo para eludir el ataque. Controla el cañón y recházalos.\n\n¿Estás <listo/lista>, <nombre>?", progress = nil, completion = "¡Hemos sobrevivido al ataque!\n\nNo lo habríamos logrado sin ti, &lt;nombre&gt;.\n\nPartiremos en cuanto suba la marea. Si no fueras una promesa como héroe te ofrecería un puesto en mi tripulación. Pero se te nota que te esperan peces más gordos.\n\nAunque no seas más que un enclenque &lt;raza&gt;...", rewards = "Podrás elegir una de estas recompensas:\n\nAnillo aguafríaSortija de fuego marino\n\nTambién recibirás:\n\nSortija de fuego marinoAnillo aguafría"},
[668] = {title = "El tesoro sumergido", objective = "Llévale las gemas de elfo al capitán O'Breen.", description = "No hagamos esperar al capitán O'Breen. Querrá ver estas gemas el primero. A fin de cuentas, ¡a eso ha venido!\n\nY después de unas semanas tratando con los piratas, el profesor y yo no hemos dado cuenta de que lo peor para nuestra salud sería que nos pillaran con su tesoro en las manos.\n\nAhí va, <nombre>, llévaselas a O'Breen.", progress = "¡Ah del barco, <clase>! ¿Qué te trae por el puente?", completion = "¿Conque los pequeños gnomos demostraron su valor después de todo? ¡Sabía que esos mordedores de tobillo acabarían sirviendo para algo!", rewards = "Recibirás:\n\n"},
[669] = {title = "El tesoro sumergido", objective = "Llévale una muestra de gema de elfo al maestro de flota Cuernomarino en Bahía del Botín.", description = "El maestro de flota Cuernomarino querrá enterarse de nuestro hallazgo sin demora.\n\nPero como puedes ver, no estamos precisamente en posición de salir de aquí No solo la marea es demasiado baja, sino que esas malditas criaturas a las que hemos perturbado no nos quitan ojo de encima.\n\nPuedes ser de gran ayuda para los incursores Aguasnegras si consigues llegar a Bahía del Botín y decirle al maestro de flota Cuernomarino que hemos descubierto el tesoro y estamos trabajando para extraer más. Llévate esta muestra como prueba.", progress = "¿Qué te trae por Bahía del Botín, <clase>?", completion = "¡Bah, déjaselo al viejo Temblores O’Breen! ¡Sabía que era el capitán adecuado para la misión!", rewards = "Recibirás:\n\n"},
[670] = {title = "El tesoro sumergido", objective = "Llévale la carta de Cuernomarino a Temblores O'Breen en las Tierras Altas de Arathi.", description = "Tú, <nombre>, pareces tener un espíritu aventurero. Mi flota está anclada en Bahía del Botín hasta que podamos reabastecernos.\n\nEstán ocurriendo cosas graves aquí y en el extranjero y necesito hacer llegar una comunicación de alto secreto a Temblores O'Breen que está amarrado en la costa de Arathi.\n\n¿Puedo confiar en que entregues este mensaje a salvo y sin traicionar su secreto?", progress = "¿Qué? ¿Estaba Cuernomarino contento contigo? Apuesto que su sonrisa iba de Bahía del Botín hasta Trinquete.", completion = "Gracias por hacer un viaje tan largo y peligroso. Tienes el agradecimiento de los asaltantes Aguasnegras.", rewards = "Recibirás:\n\n"},
[671] = {title = "Magia demoníaca", objective = "Consigue 10 amuletos de sangrita para Tor'gan en Sentencia.", description = "He notado algo extraño en el aire... un sentimiento que me hace hervir la sangre. Algo sospechaba. Me puse en camino para encontrar el origen de mi desasosiego y descubrí al oeste, en la Mansión Redilnorte, restos residuales de un conjuro demoníaco. Conforme me acercaba, me empezó a hervir la sangre y crecía mi rabia.\n\nDescubrí en el suelo un amuleto de sangrita, que se usa para conjuros demoníacos. Aunque, el que encontré estaba agotado. Necesitaré otro para poder investigar pero no me atrevo a volver a la mansión. ¿Podrías traerme alguno?", progress = "La sangre que fuera maldita aún fluye por mis venas, <nombre>. Si alguien... algo está conjurando demonios, hay que detenerlo.", completion = "Llevará algún tiempo confirmar las verdaderas intenciones de la Hermandad pero me preocupa su abierta asociación con la magia oscura demoníaca. Te contaré más cuando lo sepa.", rewards = "Recibirás:\n\n"},
[672] = {title = "El despertar de los espíritus", objective = "Consigue 10 ojos de raptor de las Tierras Altas de los zancudos y de los vándalos de las Tierras Altas para Tor'gan en Sentencia.", description = "Tratamos de tranquilizarlo, alegrarlo, contarle historias de nuestra poderosa ciudad de Orgrimmar en Kalimdor, pero nada lo motivaba.\n\nRelatos de nuestro joven Jefe de Guerra, Thrall, que lleva la armadura y el martillo de Orgrim... pero, quizás... ¡Quizás! Una prueba de nuestra recién encontrada magia chamán le devolvería la esperanza. ¡Merece la pena intentarlo!\n\nUn pequeño objeto que podríamos hacer para él, sí... Tráeme unos ojos de raptor y veré lo que puedo hacer.", progress = "Crearé un amuleto que le dará a Gor'mul parte de la fuerza de los raptores. Quizás esto encienda su llama interior...", completion = "Fantástico. El espíritu de los raptores que dieron estos ojos aportará fuerza a quien lleve este amuleto.", rewards = "Recibirás:\n\n"},
[673] = {title = "Magia demoníaca", objective = "Recupera el orbe de sangrita de Marez Cowl y llévaselo a Tor'gan en Sentencia.", description = "No me cabe la menor duda de que un poderoso brujo reside entre las paredes de Stromgarde. De vez en cuando, usando una reliquia mágica arcana, este brujo ha conjurado a gran número de terribles demonios. Tenemos que eliminar la fuente de su energía.\n\nEncuentra al brujo y mátalo. Tráeme cualquier objeto mágico que encuentres sobre su persona y tomaré las medidas necesarias para destruirlo y asegurarme de que su poder no se usará para contaminar esta tierra.", progress = "La sangre arde en mí... cada día más. Hay que detener al brujo.", completion = "Mis agitadas noches se tornarán apacible sueño cuando logre destruir este orbe. Gracias, &lt;nombre&gt;. Es un recuerdo estremecedor del terrible poder de los demonios que se había apoderado de los orcos antes de que Grito Infernal los liberara de la maldición.", rewards = "Podrás elegir una de estas recompensas:\n\nAlmete de piel de draco blancaBrazales de plata radiantes\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAlmete de piel de draco blancaBrazales de plata radiantes"},
[674] = {title = "El despertar de los espíritus", objective = "Dale el amuleto de espolón de raptor a Gor'mul.", description = "Toma, lleva este amuleto a Gor'mul y veamos si la fuerza del raptor puede encender la llama del guerrero que otrora ardía en él.", progress = "¿Qué esperanza queda cuando la mano de un cobarde puede ejecutar al más poderoso de los poderosos? ¡Dime! ¡¿Qué esperanza?!", completion = "¿Qué es esto? Hueso pulido. ¿Y qué hago yo con hueso pulido? ¿Le devolverá un abalorio la gloria a la Horda?", rewards = "Recibirás:\n\n"},
[675] = {title = "El despertar de los espíritus", objective = "Habla con Tor'gan.", description = "<Gor'mul tira el amuleto al suelo.>\n\n¿Te ha enviado Tor'gan? ¡Bah! Por qué él compadecerse de mí ahora, no alcanzo entender. Compasión ser negada mí cuando no permitir mí morir con mi Jefe de Guerra en la batalla. Cruel golpe del destino tocar mí...", progress = nil, completion = "No ha funcionado, ya veo. Sin embargo, creo que demostrándole el poder de los chamanes a Gor'mul, le ayudaremos a recuperar algún día su orgullo. La potencia del amuleto era insuficiente para demostrarle nuestro nuevo poder.", rewards = "Recibirás:\n\n"},
[676] = {title = "La Sentencia", objective = "Mata a 8 ogros y 10 gorilas Puño de Roca; luego ve a ver a Bombo Vil a la avanzada de Sentencia, en las Tierras Altas de Arathi.", description = "Alfeñiques miserables... ¿Se puede saber qué miras, <clase>? Te crees muy especial, ¿eh? Demuéstralo.\n\nLa avanzada de Sentencia, en Arathi, está siendo atacada. La derrota es inevitable si no les enviamos ayuda de inmediato.\n\nPero antes de salir corriendo, será mejor que te pongas a prueba. Al noreste de Stromgarde, en Arathi, hay un túmulo de ogros en el que vive la tribu Puño de Roca. Cuando seas capaz de manejarte con esos ogros, preséntate ante Bombo Vil en Sentencia.", progress = "No tengo tiempo ni de afilar el hacha y mucho menos de salir a acabar con esos que nos amenazan.\n\nNo parece que tengas mucha experiencia, <clase>. ¿Qué quieres?", completion = "¿Crees que puedes ayudar a Sentencia? Muy bien.\n\nAlguna destreza tendrás, si no los ogros habrían acabado contigo. En todo caso, ando escaso de tropas.\n\nSi te quedas, descansa un poco y luego ven a verme otra vez. Seguro que te encuentro algunos cuellos que cortar.", rewards = "Recibirás:\n\n"},
[677] = {title = "Un llamamiento a las armas", objective = "Mata a 10 lanzahachas Secacortezas, 10 cazadores de cabezas y 8 médicos brujas y vuelve junto a Bombo Vil en la avanzada de Sentencia.", description = "Los trols Secacorteza y los ogros Puño de Roca de Arathi se han unido para arrebatarnos esta tierra, pero no dejaremos nuestro sino en sus manos ni en las de nadie más. Los ejecutaremos y les demostraremos que este lugar es nuestro hogar.\n\nLos trols nos han traicionado; los ogros nos usarían de comida y de cama si les dejásemos, eso jamás ocurrirá.\n\nEmpezamos el ataque sobre los trols Secacorteza al sur de este lugar. Ejecútalos por docenas y regresa solo cuando sus campamentos estén bañados en sangre.", progress = nil, completion = "Las huellas de la guerra se evidencian en tus prendas y el sudor cae de tu frente. Bien, pero que muy bien.", rewards = "Recibirás:\n\n"},
[678] = {title = "Un llamamiento a las armas", objective = "Mata a 10 toscos Puño de Roca y 4 magos Puño de Roca y vuelve junto a Bombo Vil en la avanzada de Sentencia.", description = "Tu fuerza es evidente, <nombre>. Veamos ahora cómo te va frente a un enemigo real.\n\nLos Puño de Roca tienen una guarida al suroeste de este lugar hacia el puente de los enanos y, si no me equivoco, los destructores y los magis lugareños supondrán para ti un desafío aún mayor, así ha sido para nosotros hasta ahora.\n\nEjecuta a tantos como puedas y regresa ante mí con ese cuerpo cuando lo hayas conseguido, o que lo haga tu espíritu si fracasas. Sea como fuere, alabaré tu tenacidad en la batalla.", progress = nil, completion = "Eres tan fuerte como un kodo y tu ansia de pelea es un orgullo para tu pueblo.", rewards = "Recibirás:\n\n"},
[679] = {title = "Un llamamiento a las armas", objective = "Mata a 10 chamanes Puño de Roca y 15 señores Puño de Roca y vuelve junto a Bombo Vil en la avanzada de Sentencia.", description = "La bestia está lista para la decapitación, y con toda la fuerza, <nombre>. Has demostrado tu valía a la hora de ayudarnos y creemos que eres la persona indicada para este golpe final. Los líderes de estos ataques han construido su guarida en las ruinas de Stromgarde, un claro símbolo de la fragilidad humana.\n\nEncuentra y mata a los Lores Puño de roca y su lacayo chamán, antes de que nos logren expulsar de estas tierras.", progress = nil, completion = "Eres motivo de orgullo para tu pueblo, &lt;nombre&gt;. Se ha disminuido en gran medida la amenaza y estoy seguro de que no tardaremos en progresar aquí en las Tierras Altas.", rewards = "Podrás elegir una de estas recompensas:\n\nCazador silenciosoMachacacráneos\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCazador silenciosoMachacacráneos"},
[680] = {title = "Una auténtica amenaza", objective = "Mata a Or'Kalar y lleva su cabeza a Korin Fel en la avanzada de Sentencia.", description = "Eres de gran ayuda para mi marido, <clase>, pero él no sabe por qué Sentencia está siendo atacada.\n\nOr'Kalar, el péon de Mug'thol' en Stromgarde, odia visceralmente a mi marido y desea por encima de cualquier cosa su muerte por derrotarlo hace años.\n\nSegún mis augurios, Or'Kalar le ha tendido una trampa a mi marido, así que le he ocultado signos de la presencia del ogro.\n\nMi marido se lanzaría sin más miramientos si supiera el paradero del ogro y me gustaría pedirte tu ayuda para ejecutar a la criatura.", progress = "Cerca de la Torre de Arathor, allí encontrarás a Or'Kalar. Con su muerte cesarán los ataques a Sentencia y la vida de mi marido estará protegida.\n\nVeo en tus ojos que me consideras débil, incluso deshonroso, por mi silencio. Algún día entenderás lo que es amar como yo. Quizás entiendas entonces que hay otra clase de fuerza y de honor.\n\nMientras tanto, haz lo que te pido y ejecuta a Or'Kalar.", completion = "Me has hecho un gran favor, &lt;nombre&gt;. Ten por seguro que has protegido la vida de mi marido y la de Sentencia un día más con tus acciones. Te ruego aceptes esta recompensa por tu valentía.", rewards = "Podrás elegir una de estas recompensas:\n\nFalda escocesa Rocío de nieblaEspada de Sentencia\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nFalda escocesa Rocío de nieblaEspada de Sentencia"},
[681] = {title = "Mansión Redilnorte", objective = "Mata a 10 bandoleros de la Hermandad y 6 mercenarios de la Hermandad.\n\nVuelve junto al capitán Nials en el Refugio de la Zaga.", description = "Estamos librando una guerra por Stromgarde, y la estamos perdiendo. Los ogros, la Hermandad y nosotros mismos hemos dividido nuestra otrora gran ciudad, y cada facción se encuentra atrapada en un sangriento enfrentamiento.\n\nNuestras fuerzas menguan mientras las del enemigo aumentan.\n\nNecesitamos ayuda de la ciudad de Costasur pero es difícil recibir suministros mientras la Hermandad controle el territorio que hay que cruzar.\n\nVe a la base de la Hermandad en Mansión Redilnorte y ejecútalos. Quizás entonces disminuya la presión sobre los trenes de suministros y nos llegarán más.", progress = nil, completion = "Muy bien, &lt;nombre&gt;. Con la muerte de los miembros de la Hermandad debería ser más fácil recibir los suministros de Costasur.\n\nQuizás aún quede esperanza para nuestra causa.", rewards = "Recibirás:\n\n"},
[682] = {title = "Insignias de Stromgarde", objective = "Llévale las insignias de Stromgarde al capitán Nials en el Refugio de la Zaga.", description = "La Hermandad de Stromgarde se resiste a nuestro contingente de la milicia allí desplazado. Para incentivar a la chusma a enfrentarse a nuestros más disciplinados soldados, los jefes de la Hermandad han puesto precio a los nuestros, prometiendo oro por cada insignia de Stromgarde arrebatada a soldados muertos.\n\nQuiero que recuperes esas insignias, ¡no podemos permitir que esos degolladores se lleven el trofeo de nuestros valientes caídos!\n\nPenetra en la zona de Stromgarde controlada por la Hermandad y busca nuestras insignias.", progress = "¿Has conseguido recuperar alguna de las insignias de Stromgarde, <nombre>? La Hermandad debe aprender que no se aprovecharán de nuestros muertos.", completion = "Gracias, &lt;nombre&gt;. Informaremos a las familias sobre los soldados caídos en la batalla.\n\nSerán noticias crudas pero es mejor que la incertidumbre de alguien querido que ha desaparecido.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nLeotardos de caballería de Stromgarde"},
[683] = {title = "Un favor a Sara Balloo", objective = "Lleva la nota de Sara Balloo al rey Magni Barbabronce de Forjaz.", description = "<nombre>, tengo un pequeño favor que pedirte.\n\nLlévale esta nota al gran monarca de Forjaz, el rey Magni Barbabronce. Es poca cosa para ti, que tanto has ayudado ya a la causa de la Alianza.\n\nPresentarme yo ante el Rey en mi estado de duelo resultaría contraproducente. Seguro que lo entiendes.", progress = "¿Eres <nombre>? El relato de tus servicios a la Alianza ha llegado hasta aquí.\n\nDime, ¿qué puedo hacer por ti?", completion = "Déjame unos minutos para leer la carta...", rewards = "Recibirás:\n\n"},
[684] = {title = "Se busca: Marez Cowl", objective = "Llévale la cabeza de Marez al capitán Nials en el Refugio de la Zaga.", description = "Le han puesto precio a la cabeza de Marez Cowl, una consejera de alto nivel y agente de la Hermandad. La vieron por última vez en el territorio controlado por la Hermandad en Stromgarde.\n\nLa recompensa se reclama al capitán Nials.\n\nOjo: Marez es una hábil y taimada bruja, famosa por sus trapicheos con los demonios. Se recomienda extremar la precaución en su caza.", progress = "Saludos, <clase>. ¿Tienes un informe para mí?", completion = "¿Has despachado a Marez Cowl? Muy bien, &lt;nombre&gt;. ¡Pero que muy bien!\n\nMarez era una amenaza para nuestros defensores de Stromgarde y se rumoreaba que era una emisaria entre la Hermandad y algún poder más oscuro. Nos alegramos de que ya no ejerza sus poderes contra nosotros.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nBrazaletes rúnicos Arcanos"},
[685] = {title = "Se busca: Otto y Lord Cresta de Halcón", objective = "Llévale la cabeza de Otto y la de Cresta de Halcón al capitán Nials en el Refugio de la Zaga.", description = "La Milicia de Stromgarde ha puesto precio a la cabeza de Lord Cresta de Halcón y a la de su guardaespaldas Otto. Cresta de Halcón dirige a la Hermandad en las Tierras Altas de Arathi y su muerte supondría un duro golpe para ellos.\n\nSu guardaespaldas Otto, aunque no sea un objetivo estratégico, es un feroz adversario y ha acabado con docenas de nuestros defensores.\n\nLas recompensas se reclaman al capitán Nials.", progress = "Hola. ¿Vienes a presentar un informe?", completion = "¿Cresta de Halcón y Otto? ¡Estupendo!\n\nAquí tienes tu recompensa, &lt;nombre&gt;. Te la pago con mucho gusto.", rewards = "Podrás elegir una de estas recompensas:\n\nVara de PenaBrazales de jinete de guerra\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBrazales de jinete de guerraVara de Pena"},
[686] = {title = "Un homenaje real", objective = "El rey Magni Barbabronce quiere que hables con el Gran maestro Mablesten.", description = "No espero que una buena enana como la señora Balloo entienda que las farsas de guerra son por el bien de nuestra gente. La muerte de su marido, como la de cada enano que lucha en nombre de la Alianza, me pesa en el alma.\n\nAunque no puedo traer a su marido de vuelta del Vacío Abisal, al menos puedo rendirle homenaje.\n\nVoy a encargar al Gran maestro Mablesten un monumento a la memoria de Sully Balloo.\n\n<clase>, a ti te corresponde ir a llevarle mensaje de mi encargo.", progress = nil, completion = "¿Que vienes de parte del rey Magni Barbabronce?\n\n¡Vaya, esto debe de ser importante!", rewards = "Recibirás:\n\n"},
[687] = {title = "Theldurin, el perdido", objective = "Encuentra a Theldurin el Perdido.", description = "Theldurin el Perdido fue un día miembro de nuestra orden, el Martillo Crepuscular. Pero perdió la cabeza y desdeñó nuestras enseñanzas. Antes de huir de nuestro lado, robó el Pergamino de Myzrael. No pudimos cogerle, pero hay rumores de que se oculta en las Tierras Inhóspitas, ha enloquecido y no dice más que incoherencias.\n\nPara traer de vuelta a la Dama, debemos encontrar el pergamino. Y para hacerlo, debes encontrar a Theldurin.", progress = nil, completion = "Hola. Hola, hola, hola, hola, hola, ¡¡HOLA!!\n\nLo siento, ¡espera, no lo siento! ¡No seas grosero!\n\nOh no... no estoy diciendo más que locuras.\n\nPero... a veces la locura es mi único consuelo.", rewards = "Recibirás:\n\n"},
[688] = {title = "Aliados de Myzrael", objective = "Habla con Zaruk en Sentencia.", description = "<Myzrael habla.>\n\n¡Libre! ¡SOY LIBRE! Libre para recuperar la fuerza, sin que me vean mis captores. Porque si me enfrentara a ellos ahora, seguro que me vencerían y me volverían a encerrar.\n\nPero con el tiempo haré frente a los gigantes ¡y se arrepentirán de haber sido mis guardias!\n\nEres <un/una> noble <aliado/aliada>, <nombre>. Dentro de algún tiempo volveré a necesitarte. Cuando esté lista, necesitaré que me conjures para devolverme a la superficie.\n\nHabla con Zaruk en Sentencia. Él sabe cómo realizar el conjuro.", progress = nil, completion = "Bienvenido, &lt;clase&gt;. La Señora me informó de tu visita y si hemos de devolverla a la superficie, entonces tenemos mucho de qué hablar...", rewards = "Recibirás:\n\n"},
[689] = {title = "Un homenaje real", objective = "El Gran maestro Mablesten de Forjaz quiere 5 piezas de granito de Alterac.", description = "¿Así que el Rey quiere un monumento en honor de un soldado llamado Sully Balloo? ¿Y dices que el pobre diablo murió durante la tragedia del Puente Thandol?\n\n¿Habrá alguna vez paz en Azeroth?\n\nSea como sea, todos los encargos del Rey son de la máxima importancia. Para crear una obra épica y duradera, necesito una de las mejores piedras disponibles. Tráeme granito de Alterac; lo encontrarás en las las Colinas de Darrow, en Trabalomas.", progress = "¡Ah, <nombre>! Dime que me traes granito de Alterac para empezar el monumento a Sully Balloo.", completion = "¡Perfecto! ¡Mejor de lo que habría podido soñar!\n\nY que sepas que tengo mucha imaginación.", rewards = "Recibirás:\n\n"},
[690] = {title = "Una petición de Malin", objective = "Encuentra a Skuerto en el Refugio de la Zaga, en las Tierras Altas de Arathi.", description = "Saludos, <nombre>. Tienes aspecto de aventurero y tengo una tarea que requiere atención.\n\nEl archimago Trelane, mi primer maestro, huyó de Stromgarde en las Tierras Altas de Arathi no hace mucho, y me ha pedido que le ayude a recuperar unas cosas que se dejó.\n\nSitiaron la ciudad e intentó defender la Torre de Arathor pero fue en vano. Ya tengo a agentes en las Tierras Altas pero me han pedido que envíe refuerzos. ¿Te interesa?", progress = nil, completion = "¿Así que t'envía Malin? M'importa un bledo que Muradin resucitara y te pusiera un dragón pa' traerte aquí. ¡Estoy ocupao! Habla con Kryten pa' lo que sea.", rewards = "Recibirás:\n\n"},
[691] = {title = "Valer su peso en oro", objective = "Encuentra 10 colmillos Secacorteza, 4 bolsas de medicina Secacorteza y una navaja de cazador de las Sombras para el aprendiz Kryten en el Refugio de la Zaga.", description = "El archimago Malin nos ha enviado a Skuerto y a mí a recolectar componentes para él. No sabía ni por dónde empezar pero ahora que estás aquí, creo que el problema está resuelto.\n\nNecesito lo siguiente: colmillos Secacorteza, bolsas medicinales Secacorteza y una navaja del tipo cazador oscuro.\n\n¿Podrías encontrar estas cosas para mí? Los Secacorteza viven al sudeste de Arathi.", progress = "¿Ha habido suerte hasta ahora, <nombre>?\n\n\n\nSi mi investigación es correcta, los colmillos deberían proceder de cualquier trol Secacorteza. Las bolsas de medicina proceden de los médicos brujos Secacorteza y el más fuerte de los cazadores oscuros Secacorteza debería llevar la navaja especial que se les entrega después de completar sus ritos de paso. No hay forma de saber qué cazador oscuro lleva la navaja hasta que te enfrentas a ellos.", completion = "¡Increíble! Ahora entiendo por qué Malin te pidió que te unieras a nosotros, ¡gran idea, digo! \n\nA este paso, Skuerto y yo estaremos de vuelta en Ventormenta antes de que nos demos cuenta. \n\nCreo que mi compañero enano ha vuelto de su exploración de la Sala de Puño de Roca; ve a hablar con él cuando estés listo. \n\nAl parecer, tuvo una escaramuza con los ogros. Aunque ni se lo mentes, es muy sensible ante las críticas a sus habilidades y a su trabajo como explorador.", rewards = "Recibirás:\n\n"},
[692] = {title = "Los fragmentos perdidos", objective = "Lleva el fragmento de pergamino rasgado, el fragmento de pergamino arrugado y el fragmento de pergamino chamuscado a Theldurin el Perdido.", description = "El pergamino de Myzrael... ¡¿el pergamino de Myzrael?! ¡Yo que tú, ni me acercaba! Su magia puede sacar a Myzrael a la superficie del mundo y si lo haces, ¡estaremos en un lío!\n\nO... quizás ya es demasiado tarde. A lo mejor ya está libre. Quizás solo esté recobrando fuerzas antes de desatar toda su ira sobre nosotros. De ser así, entonces ¡NECESITAS el pergamino!\n\n... ¡qué mala suerte que esté destruido! Furiosos elementales lo robaron, lo desgarraron y luchan ahora por los fragmentos.\n\nPero puedes reunir esos fragmentos, quizás podamos arreglarlo...", progress = "¡¿Has encontrado esos fragmentos?! Necesitarás el pergamino de Myzrael para cazarla de nuevo ¡antes de que sea demasiado tarde!", completion = "¡Ah, los has encontrado! ¡Buen trabajo, &lt;nombre&gt;! ¡Puede que esta sea la única oportunidad contra la perversa Myzrael!\n\nDame un segundo para encontrar mi trinquete y ver si puedo recomponer estos fragmentos...", rewards = "Recibirás:\n\n"},
[693] = {title = "Empuñar la varita", objective = "Encuentra la varita de evocación de Trelane y llévasela a Skuerto en el Refugio de la Zaga.", description = "Eh, <nombre>. Perdona, antes igual me quedara corto... y no, no es un chiste.\n\nM’he enterao del lugar donde acabó el primer objeto de Trelane: Kor'gresh lo tiene en la Sala Puño de Roca. Y, por cierto, le disparaba con hielo pa’ mantenerla firme, no debería de tardar en enterarse de pa' qué sirven sus poderes.\n\nLa Sala Puño de Roca está al sur de donde estamos, luego tira pa'l este de Puente Thandol. Encuentra al ogro y trae pa’cá esa varita.\n\nMientras no estás, veré si se m’ocurre qué hacer con Stromgarde.", progress = "Nuestro objetivo es la varita, <nombre>. Por ahora céntrate en eso.", completion = "¡Genial, &lt;nombre&gt;! 1 menos, solo faltan 3, según mi lista. Los otros 3 están en Stromgarde.\n\nDescansa un poco pa’ recuperar las fuerzas, que t’hará buena falta pa’ la siguiente fase de mi plan. Vuelve cuando hayas tomado algo caliente y hayas descansado.", rewards = "Recibirás:\n\n"},
[694] = {title = "Las defensas de Trelane", objective = "Encuentra un ágata azur y llévasela al aprendiz Kryten en el Refugio de la Zaga.", description = "¿Habéis descansado?\n\nEste será nuestro siguiente paso: hay ogros rodeando la Torre de Arathor en Stromgarde. Tendremos que activar las defensas de la torre pa’ tener la más mínima posibilidad de vencerles y llegar vivos hasta los objetos. Pa' eso, necesitaremos unas gemas a las que les ha tomao cariño el chamán Puño de Roca. Las arrebataron de la torre pero pa' mí que no les va a durar.\n\nConsigue una d’esas gemas y llévasela a Kryten, él conoce el hechizo pa’ que funcionen de llave.", progress = "Hola otra vez, <nombre>. ¿Has encontrado ya una ágata azur?", completion = "Impresionante, &lt;nombre&gt;. Una vez más, enhorabuena.\n\nDame un momento para lanzar el hechizo apropiado para encantar la gema ¡y listo!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[695] = {title = "Un encantamiento de aprendiz", objective = "Habla con Skuerto.\n\nObjeto provisto: \n", description = "Aquí tienes, <nombre>. Solo hay que apretar la gema contra uno de los pedestales cerca de la base de la torre para activar las defensas. Úsala cuando estés <listo/lista> para entrar en la torre a buscar los objetos de Trelane que faltan.\n\nQue tengas suerte. Estaré en el campamento esperando tu regreso. Ah, y habla con Skuerto antes de marcharte. Te dará el resto de detalles que necesitarás.", progress = nil, completion = "Bien, bien.\n\nSi no hay más impedimentos, ¿qué tal si empezamos?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[696] = {title = "Ataque a la torre", objective = "Encuentra la filacteria de Trelane, el orbe de Trelane y el ágata ámbar de Trelane, y llévaselo todo a Skuerto en el Refugio de la Zaga.", description = "Aquí tienes los objetos de Trelane qu’andabas buscando: la filacteria, el orbe y el ágata de brasa.\n\nLos tres deberían encontrarse en unos baúles en el interior de la torre y no deberías tener ningún problema pa’ abrirlos solo; aunque ten cuidado con los ogros.\n\nLas defensas de la torre no bastarán pa’ detenerlos; tendrás que tener las armas preparadas. Los ogros sabrán qu’estás allí robándoles, así que no s'andarán con mimos si te pillan.\n\nTen cuidado y suerte.", progress = "Haces esto y pa’ mí que Malin estará más que contento contigo.", completion = "Malin no podría haber encontrado una persona más valiosa pa’ venir a ayudarnos, &lt;nombre&gt;. ¡Has hecho un trabajo espléndido!\n\nSe alegrará de ver el éxito tan aplastante de nuestra misión.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[697] = {title = "Una petición de Malin", objective = "Lleva la carta sellada al archimago Malin en el Barrio de los Magos de Ventormenta.", description = "Lleva esta carta a Ventormentay dile a Malin que Kryten y yo pronto iremos pa’llá. Te recompensará debidamente por habernos ayudado aquí, <nombre>, de eso no me cabe la menor duda.\n\nQue viajes sin percances.", progress = "¿Sí? Me suenas algo, vagamente. Discúlpame, ¿nos conocemos? Estoy tan ocupado que se me escapan las cosas.", completion = "&lt;El archimago Malin abre la carta y pone ojos como platos.&gt;\n\nPor la Luz, ¡lo has conseguido! ¡Qué maravilla! Trelane se alegrará mucho. Estoy seguro de que Kryten y Skuerto están tan entusiasmados como yo porque voy a los voy a hacer regresar a Ventormenta.\n\nGracias, &lt;nombre&gt;. Te ruego aceptes esto, te ayudará en tus viajes.", rewards = "Podrás elegir una de estas recompensas:\n\nRodela de vigilanteBotas portalas\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nRodela de vigilanteBotas portalas"},
[698] = {title = "Falta de suministros", objective = "Lleva 8 costillas de denteserra sin preparar a Tok'Kar en el Pantano de las Penas.", description = "Estoy intentando reunir algunos suministros para enviarlos a nuestra avanzada cerca de la playa del este. Tok'kar hace lo que puede, pero entre sus guardias y cocinar para los soldados, no le queda mucho tiempo.\n\nLes encanta cocinar carne de denteserras cuando pueden, pero no he tenido tiempo de conseguirles otro cargamento. ¿Te importaría echarles una mano?\n\nLos denteserras jóvenes no son tan sabrosos como los viejos, así que no los cojas. Hay que esperar un año o dos para que alcancen el tamaño ideal.", progress = "Lok'tar, <clase>. Viajar por los pantanos no es algo que te puedas tomar a la ligera. Tienes que ser muy valiente si vas a demostrar lo que vales aquí.", completion = "¡Ah! Estupendo. Esta noche tenemos carne fresca. Gracias, &lt;nombre&gt;.\n\nHas ayudado a que las noches sean más tolerables. El fuego arde con brío y nuestros estómagos pronto estarán llenos.\n\nEs una agradable sorpresa que nos hayas traído un cargamento; Dar no es el orco más atractivo del mundo pero, al menos, su actitud apesta menos que su aliento.", rewards = "Recibirás:\n\n"},
[699] = {title = "Falta de suministros", objective = "Lleva 6 garras de pargos denteserra a Tok'Kar en el Pantano de las Penas.", description = "Con la amenaza de murlocs en la costa, nos hemos quedado aquí para proteger, o advertir si no podemos hacer más, de cualquier amenaza a Rocal. No nos deja mucho tiempo para relajarnos y nos vendría bien algo de ayuda...\n\nLas garras de pargos denteserra son unas armas excelentes si se preparan bien. Tráeme algunas garras y te recompensaremos con una de nuestras armas como agradecimiento por la carne fresca y la buena compañía.", progress = "Un buen licor de hidromiel, una buena hoguera, un ejército de orcos y un arsenal de armas... es todo lo que necesitamos aquí en la costa. No lamento para nada estar aquí fuera protegiendo Rocal, pero me gustaría poder conseguir una madriguera o, al menos, una torre.", completion = "Me alegra ver que sigues con vida, &lt;nombre&gt;.\n\nEstas garras serán perfectas. Cuantas más armas tengamos listas para los ataques, mejor parados saldremos.\n\nEmpezaré con tu arma en cuanto pueda. Solo me llevará un poco de tiempo. Me he vuelto muy hábil produciéndolas.", rewards = "Recibirás:\n\n"},
[700] = {title = "Un homenaje real", objective = "Informa al rey Magni Barbabronce.", description = "Ah, esta piedra tan buena es de Lordaeron. Es una lástima que la tierra esté arrasada.\n\n¡Pero el monumento ha quedado muy bien! Creo que el Rey estará satisfecho.\n\nPor favor, ve a notificar a Su Majestad que el monumento ya está terminado.", progress = nil, completion = "El granito parece un homenaje muy pobre para tan gran enano, pero ningún enano vive para siempre.\n\nEsperemos que el monumento sí dure y que las generaciones futuras recuerden al valiente Sully Balloo y otros bravos soldados como él.", rewards = "Recibirás:\n\nAnillo de recuerdo de Forjaz"},
[701] = {title = "La astucia del raptor", objective = "Consigue 12 corazones de raptor de los acechacarnes de las Tierras Altas para Tor'gan.", description = "Con algunos elementos más, poder crear un objeto que imbuir Gor'mul ¡con la astucia del raptor! Con el poder del raptor recorriéndolo, ¡Gor'mul conocer nuevo poder y descubrir otra vez al guerrero durmiente en é!\n\nNecesitar corazones de raptor para componer el objeto y el hechizo.", progress = "Tengo todos los preparativos listos, solo me faltan los corazones de raptor.", completion = "Sí, nos vendrán de perlas. Transferiré su poder y el espíritu de los raptores a este orbe. Entonces este orbe le conferirá a Gor'mul la feroz fortaleza e inteligencia del raptor.", rewards = "Recibirás:\n\n"},
[702] = {title = "La astucia del raptor", objective = "Dale el orbe de Tor'gan a Gor'mul.", description = "Toma este orbe y usa su energía para darle a Gor'mul la esencia del raptor. Seguro que su ánimo recobrará el ímpetu y volverá a encontrarle sentido a la vida. Cuando vuelva a la normalidad, podremos explicarle el estado actual de la Horda y contaremos con la ayuda de un gran guerrero en nuestras incesantes batallas.", progress = "¿Otra vez de vuelta? Desde luego, no hay nada que desee más que la compasión de Tor'gan.\n\n¿Qué me traes esta vez? ¿Quizás un trozo de cuerda de color vivo? O una cuerda suficientemente larga para acabar con mi sufrimiento...", completion = "Al menos esta vez me traes algo interesante. Extraño poder el que emana...", rewards = "Recibirás:\n\n"},
[703] = {title = "Alas de águila ratonera a la parrilla", objective = "Llévale 4 alas de águila ratonera a Aparejez.", description = "¡Sí señor, mis alas de águila ratonera a la parrilla son para chuparse los dedos! La salsa es cremosa y la carne, penetrante. ¡Y su aroma atraerá a los hambrientos de kilómetros a la redonda!\n\nTengo todo lo necesario para cocinar una tanda. Todo... ¡excepto las alas de águila ratonera!\n\nSi quieres algunas, tráemelas. Consígueme suficientes alas de águila ratonera ¡y hasta podría compartir la receta contigo!", progress = "Hola, <nombre>. ¿Me has traído mis alas?", completion = "Ah, empezaré con esas alas enseguida. ¡Ya casi puedo saborearlas!\n\n¡Aquí tienes la receta en caso de que las quieras preparar tú mismo!", rewards = "Recibirás:\n\nAla de águila ratonera a la parrillaReceta: ala de águila ratonera a la parrilla"},
[704] = {title = "El sino de Agmond", objective = "Llévale 4 urnas de piedra labrada al prospector Vetaferro en Loch Modan.", description = "El último informe de Agmond es preocupante, ¡sobre todo ahora que ha muerto! Mencionaba que en sus excavaciones había desenterrado unas \"urnas antiguas\" en la explotación situada en las afueras de Uldaman. Me temo que las urnas que encontró instigaron a los troggs, igual que lo hicieron los ídolos que encontré aquí.\n\nNecesito que recuperes esas urnas para nuestros estudios. Ve a la excavación de Uldaman, encuentra las urnas y tráemelas aquí.", progress = "¿Has encontrado esas urnas, <nombre>? ¡Debemos averiguar la relación entre ellas y los troggs!", completion = "¡Muy bien! Con suerte y diligencia, encontraremos la conexión entre los troggs y estos antiguos artefactos. \n\nNos has hecho un gran favor a los prospectores, &lt;nombre&gt;. No lo olvidaremos. Como tampoco lo hará Agmond.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nGuantes de prospector"},
[705] = {title = "La pesca de perlas", objective = "Llévale 9 perlas azules a Aparejez en las Tierras Inhóspitas.", description = "Las Tierras Inhóspitas son un lugar hostil, lleno de sanguinarios depredadores y de carroñeros sin escrúpulos. Aterrador, sobre todo para un diminuto goblin. Para sobrevivir, ¡tengo que ser astuto!\n\nConozco la receta de las bombas centelleantes. Las uso para espantar a los animales. Pero se me está acabando uno de los ingredientes: polvo de perla azul triturada. Consígueme un puñado y no te arrepentirás.\n\nEh, y espero que lleves puestas unas buenas botas. Las perlas azules que necesito se encuentran en unas almejas de El Arrecife Mortal.\n\nEse mismo, ¡El Arrecife Mortal de Tuercespina!", progress = "¿Has conseguido mis perlas? ¡Casi no me quedan bombas centelleantes!", completion = "¡Vendrán de maravilla! Solo necesito molerlas en polvo... luego mezclarlas con una pizca de esto y otra pizca de aquello. \n\n¡Prepárense, carroñeros! ¡Prepárense para ser fulminados!", rewards = "Podrás elegir una de estas recompensas:\n\nRifle cegadorVarita cegadora\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBomba cegadoraRifle cegadorVarita cegadora"},
[706] = {title = "Encantamientos de fuego abrasador", objective = "Consigue 1 corazón de dragón negro para Sigrun Extracero.", description = "Cuando era aprendiz de Feranor Ferrodedo, aprendí su técnica especial para imbuir armas con ardientes encantamientos. Lo último que supe de él es que estaba trabajando en una herrería para una partida de cazadores en Lordaeron.\n\nNo puedo contarte demasiados detalles del proceso pero sí puedo decirte que requiere sangre del corazón de un cachorro de dragón negro que aún esté latiendo. No estoy demasiado ocupado ahora, espero instrucciones de Forjaz. Si me traes un corazón de los cachorros del Barranco Lethlor, ¡te demostraré de lo que soy capaz!", progress = "¿Cómo va la caza, <clase>?", completion = "Ah, desde luego, ¡esto vendrá de perlas! ¡Esto hará que al atacar tus armas despidan un calor abrasador!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nHechizo Llamarada ígnea"},
[707] = {title = "¡Vetaferro te necesita!", objective = "Habla con el prospector Vetaferro en su excavación de Loch Modan.", description = "<nombre>, he recibido unos informes del prospector Vetaferro sobre la excavación de Loch Modan; dice que necesita a alguien que estudie otra excavación. \n<nombre>o dice nada más, pero si te apetece estirar las piernas, ve a hablar con Vetaferro.", progress = nil, completion = "¿Vienes de parte de Pico Tormenta? Muy bien.\n\nTengo trabajo para ti.", rewards = "Recibirás:\n\n"},
[708] = {title = "La caja negra", objective = "Lleva la caja negra corroída a la Sala de Armas de Forjaz.", description = "La superficie de esta caja negra metálica está muy hundida, como si la hubiesen sumergido en ácido, debe de haber pasado una buena temporada en el estómago del cóndor gigante.\n\nAl girar la caja, se ve un símbolo estampado en la parte inferior. Debajo del símbolo hay inscritas unas palabras:\n\n\"Motor n.º19 - Brigada de Asedio de Forjaz\".Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[709] = {title = "La solución a la maldición", objective = "Llévale la tablilla de Ryun'eh a Theldurin el Perdido.", description = "¡Ay, ay! ¡Ay, ay! Se acerca una catástrofe. ¡Recuerda mis palabras! ¡Una catástrofe!\n\nBNo, no puedo hablar de ello… no puedo pronunciar sus nombres, ¡podrían oírme! Pero si aprendemos de ellos... Sí, si aprendemos más... ¡quizás logremos frustrar sus planes!\n\nBQuizás.\n\nVe a Uldaman. ¡No! ¡Ya te lo dije! Tienes que encontrar... encontrar la tablilla de... Ryun’eh. ¡Sí, encuéntrala! ¡Y no me hagas pronunciar ese nombre otra vez!\n\nBSe encuentra en las profundidades de ese lugar, en un antiguo cofre. Ahí es donde se esconde... en un cofre creado antes del tiempo de los enanos.", progress = "¿Has encontrado la tablilla? ¡Debe de contener secretos! ¡Secretos para salvarnos!", completion = "¡Oh, la suerte está de nuestra parte! ¡Gracias! ¡Puede que aún nos salvemos!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nToga de cuentacondenas"},
[710] = {title = "Estudio de los elementos: roca", objective = "Encuentra 10 fragmentos de piedra pequeños y llévaselos a Lotwil Veriatus en las Tierras Inhóspitas.", description = "Aquí en las Tierras Inhóspitas, he dejado de trabajar en mi más reciente proyecto: mis grilletes de esclavitud elemental.\n\nCuando acabe, permitirán a los magos conjurar a elementales aún más poderosos para fines de más alcance. Para empezar, necesito pequeños fragmentos de piedra para probar mis más recientes hechizos ofensivos y unos aparatitos que he compuesto.\n\nEs fácil reunir tales fragmentos de los elementales de piedra inferiores hacia el oeste de este lugar. ¿Podrías traerme algunos?", progress = "Este sitio es perfecto para… ¿Podrías sujetarme eso un momento? Sí, justo ahí, a lo largo del vector normal. Ahora, ¿por dónde...? Ah, sí. El lugar perfecto para probar mi proyecto más reciente. La materia prima, el espacio abierto... ¿qué más podría pedir un ilustrado y brillante practicante de las artes?", completion = "Estos nos servirán, &lt;nombre&gt;. Gracias. Mm, algo más de arena de lo que preveía. Tengo que ajustar el coeficiente de fricción. No, espera; aún debería ser marginal con el aceite de escarcha.", rewards = "Recibirás:\n\n"},
[711] = {title = "Estudio de los elementos: roca", objective = "Llévale 3 losas de piedra grandes a Lotwil Veriatus en las Tierras Inhóspitas.", description = "<nombre>, has demostrado ya más valía que todos mis aprendices juntos.\n\nLas siguientes piezas que necesito requerirán algo más de fuerza. Si tienes cuidado, encontrarás grandes losas de piedra en los elementales de piedra. Sus cuerpos son más resistentes y voluminosos, así que tendrás más probabilidades de poder extraer piezas lo suficientemente grandes para mis pruebas. Los hallarás más hacia el oeste desde aquí o al sureste bordeando las faldas de las montañas.\n\nTómate tu tiempo; estaré ocupado probando estos fragmentos más pequeños que me has traído.", progress = "Estoy encantadísimo, <nombre>. Mis hechizos han afectado a las piezas más pequeñas, justo como predije. Si este experimento sale tan bien como anticipo, los elementales que podré controlar harán que cualquier cosa conjurada antes quede a la altura del betún.", completion = "¡&lt;nombre&gt;, sencillamente asombroso! Creía que haría falta media docena de tauren para traerme todos estos fragmentos.", rewards = "Recibirás:\n\n"},
[712] = {title = "Estudio de los elementos: roca", objective = "Llévale 5 brazales de vínculo de roca a Lotwil Veriatus en las Tierras Inhóspitas.", description = "<nombre>, necesito una pieza más de los elementales de las Tierras Infames: los brazales de roca de los grandes elementales de piedra. Según mis cálculos, esos brazales deberían bastar para medir la fuerza de mis grilletes.\n\nMis intentos de visualizar a los grandes elementales de piedra me mostraron un grupo de ellos al suroeste de aquí, cerca de unos ogros, si no me equivoco.\n\nTráeme 5 de sus brazales. Así tendré reservas. Servo es famoso por su facilidad para perder mis reactivos e investigaciones.", progress = "Las losas de piedra que encontraste eran ideales, <nombre>. Lucien llevará las que sobren a Kharanos cuando acabemos aquí. A fin de cuentas, podrían volver a ser útiles.\n\nPor ejemplo, serían una base estupenda para un pequeño y práctico cobertizo cuando empiece a investigar un enfoque multidisciplinario de los explosivos de alto rendimiento...", completion = "&lt;nombre&gt;, ¿alguna vez te has planteado convertirte en un ayudante de investigación ¿No? No sé lo que haría si no contara con tus facultades. Inteligencia, curiosidad y fuerza, todo en una sola persona.", rewards = "Recibirás:\n\n"},
[713] = {title = "Mantener la cabeza fría", objective = "Encuentra aceite de escarcha y llévaselo a Lotwil Veriatus en las Tierras Inhóspitas.", description = "Déjame decirte algo sobre el aceite de escarcha: nada impide que un motor o un engranaje se recalienten como ese aceite.\n\nSus propiedades de enfriamiento son extraordinarias y su longevidad como lubricante no tiene parangón. Y eso no es todo: también ayuda a retener los encantamientos de los objetos mágicos.\n\nNecesito un frasco de este aceite para aumentar la capacidad mágica de mis grilletes y hacerlos lo suficientemente poderosos para impedir que nuestros amigos conjurados nos maten a todos.\n\nBro'kin, un amigo goblin, te enseñará a confeccionarlo. Se encuentra en Alterac.", progress = "¿Has encontrado el aceite que necesitaba? ¡Me vendría tan bien que encontraras un poco!", completion = "¡Formidable! Será el refrigerante perfecto para mis nuevos brazales. Si los encantamientos no duran un siglo, le daré todas mis pertenencias a Servo.\n\nTodos los magos de la Asamblea sentirán un respeto reverencial cuando acabe con esto, &lt;nombre&gt;. Espera y verás.", rewards = "Recibirás:\n\n"},
[714] = {title = "Un giro... ¿Qué?", objective = "Llévale 1 girocronátomo a Lotwil Veriatus en las Tierras Inhóspitas.", description = "<nombre>, has hecho un trabajo más allá de toda expectativa.\n\n<Lotwil se ve claramente centrado en su trabajo.>\n\nSi por ventura nos volvemos a encontrar en Kharanos, pasa cuando quieras por trabajo.\n\n¡Vaya! Rápido, agarra el... sí, eso. Gracias.\n\nBien, ahora solo necesito una cosa más. Es una pieza pequeña pero me temo que bastante crucial: un girocronátomo.\n\nSi pudieras hacerte con uno y traérmelo, estaré listo para completar esto.", progress = "Sí, sí, ya voy. Solo un segundo.\n\n<Lotwil sigue trabajando en algún tipo de invento.>", completion = "¡TOMA!\n\n&lt;Lotwil te quita el girocronátomo.&gt;\n\nPerfecto… casi estamos. Bueno, casi. YO estoy casi listo. Este proyecto es mi prioridad absoluta... en estos momentos. Deja que coloque esto y verás con tus propios ojos a lo que has contribuido a crear. Solo harán falta unos minutos... a lo mejor... un poco más. Bueno, quizás mucho más. Ya veremos.", rewards = "Recibirás:\n\n"},
[715] = {title = "Piedra líquida", objective = "Llévale 1 poción de curación y 1 poción inferior de invisibilidad inferior a Lucien Tosselramienta en las Tierras Inhóspitas.", description = "Creo que esos objetos que conseguiste para Lotwil van a ser la menor de nuestras preocupaciones, <nombre>. Por otro lado, has demostrados ser más que hábil en las ciencias que Lotwil considera sagradas, así que ¿te apetece ayudarme?\n\nNecesito una poción de invisibilidad y una poción curativa; ambas podrían venir bien muy pronto si le sale esto.\n\nA cambio, te daré esta receta para una poción en la que he estado trabajando mientras aprendías cosas de Lotwil. Se basa en parte de la investigación que ha realizado aquí en las Tierras Inhóspitas.", progress = "Está a punto de acabar. ¿Cómo van las cosas de tu lado?", completion = "¡SÍ!\n\nDigo… gracias, &lt;nombre&gt;. Te vendrán bien cuando viajes por las Tierras Inhóspitas.", rewards = "Recibirás:\n\nReceta: poción de escudo de piedra inferiorPoción de escudo de piedra inferior"},
[716] = {title = "Es mejor la piedra que la tela", objective = "Llévale unos brazales de bronce grabados a Lucien Tosselramienta en las Tierras Inhóspitas.", description = "Sé lo agarrado que Lotwil puede llegar a ser, <nombre>, así que permite que te haga algo por las molestias. A lo mejor hasta te protege de la próxima atrocidad que será el más reciente invento de Lotwil.\n\nHe estado trabajando con todas las piedras sobrantes que habías recolectado para él y puedo hacerte unos buenos brazales que podrían serte de utilidad. Tienen un encantamiento, claro.\n\nSolo necesito brazales de bronce con dibujos confeccionados por un herrero para poder fabricar los brazales que te estoy haciendo.", progress = "Yo no esperaría demasiado para conseguir ese brazal, <nombre>.\n\n¿Quién sabe lo que puede pasar después?", completion = "Aquí tienes, &lt;nombre&gt;. Pruébatelos.", rewards = "Recibirás:\n\nBrazales de paño de piedra"},
[717] = {title = "Terremotos", objective = "Usa el Signo de la Tierra para activar el Pilar de Amatista, el de Ópalo y el de Diamante y así obtener las piedras rúnicas.\n\nColoca las piedras rúnicas en el Sello de la Tierra para liberar a Cinchoscura y a Hematus.\n\nMátalos y llévale a Garek las ataduras de Cinchoscura, las cadenas de Hematus y el Signo de la Tierra.", description = "Ha quedado claro que no podemos garantizar que Cinchoscura y Hematus permanezcan presos en Lethlor. Que esos dragones negros se han adueñaran del lugar y que los ogros consiguieran robar el Signo indica que se deben tomar medidas más drásticas.\n\nLa prisión de los dragones está controlada por tres piedras rúnicas que solo se pueden extraer con el Signo de la Tierra. Esas piedras rúnicas pueden entonces usarse para romper el sello que los mantiene presos.\n\n<nombre>,... asegúrate de que su sueño es eterno.", progress = "La cárcel de los dragones ha estado controlada por una poderosa magia y fuertes poderes pero no podemos permitir arriesgarnos a que liberen a Cinchoscura y a Hematus.", completion = "El Kirin Tor ya no tendrá que preocuparse de los prisioneros de Lethlor. Volveré para informar del éxito a mi maestro. Seguro que Krasus se alegrará.", rewards = "Podrás elegir una de estas recompensas:\n\nPeto Viento abrasadorCamisote de escama prismáticaPeto forjado por la guerraMedallón de ráfaga mental\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPeto Viento abrasadorCamisote de escama prismáticaPeto forjado por la guerraMedallón de ráfaga mental"},
[718] = {title = "Espejismos", objective = "Recupera el cajón de provisiones para Sigrun Extracero.", description = "Con las prisas por marcharnos de la excavación de Uldaman, nos obligaron a abandonar muchos carros y muchas cajas de suministros por el camino. Los ogros locales del Asentamiento Kosh se han lanzado sobre nuestro equipo abandonado como buitres sobre un cadáver en descomposición. No huele mucho mejor tampoco. Me refiero a los cadáveres.\n\nSea como fuere, se llevaron el carro en el que se transportaban todas nuestras armas y tal y tal…, bueno, esto es un poco violento, no hemos podido conseguir nada de comida.\n\nEstamos metidos en un lío, ¿nos podrías ayudar?", progress = "El desierto es mal lugar para tener hambre, amigo, te lo digo yo.\n\nEh... ¿qué es eso de ahí?...", completion = "Muchas gracias, &lt;nombre&gt;. Mientras no estabas, yo... estooo... tragué un puñado de arena. Me...\n\nBah, no importa, saldremos esta noche y conseguiremos algo de comida. ¡Comeremos como reyes!", rewards = "Recibirás:\n\n"},
[719] = {title = "Un enanos y sus herramientas", objective = "Encuentra el pico de la suerte de Ryedol y llévaselo al prospector Ryedol al sur de la excavación Piemartillo.", description = "Hola, <nombre>. Ten cuidado por estos lares. Hay enanos Hierro Negro por to’as partes, parecía clan Forjatiniebla.\n\nAcaban d’atacar la excavación donde trabajaba, s’han cargao a casi todos, hasta a mi jefe Piemartillo. M’escapé por los pelos.\n\nEl sitio está justo al norte d’este lugar y ‘stoy intentando planear la forma de recuperar parte de nuestros suministros, sobre tó’ mi pico de la suerte.\n\n¿T'apetecería echarme una mano? ¡Uno d’esos malditos Hierro Negro tendrá mi pico! Podría ser cualquiera.", progress = "Todavía no sé con seguridad qué buscaban los Forjatiniebla pero desde luego tenían un propósito.", completion = "Eres mi salvador, &lt;clase&gt;. Me alegro de que alguien le haya dado una lección a esos Forjatiniebla. Gracias por tu ayuda.", rewards = "Recibirás:\n\nMartillo de Ryedol"},
[720] = {title = "Un signo de esperanza", objective = "Localiza al prospector Ryedol y dile que Grez Dedomaza está vivo.", description = "<Hay texto garabateado de forma tosca con tiza en el mapa.>\n\n\"Ryedol, \nMe llevan a Uldaman. Envía ayuda.\n-Piemartillo\"", progress = nil, completion = "¡¿Piemartillo está vivo?! ¡Increíble, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[721] = {title = "Un signo de esperanza", objective = "Encuentra a Grez Dedomaza en Uldaman.", description = "<nombre>, ¿puedes encontrarlo? ¿Puedes encontrar a Piemartillo en Uldaman?\n\nSi esos asquerosos Forjatiniebla l’han raptao pa’ sonsacarle lo que sabe de las excavaciones de los titanes, entonces todo Forjaz podría ‘star en gran peligro.\n\nDurante mucho tiempo esos Hierro Negro han buscao magia pa’ hacer golems más poderosos. Piemartillo ‘staba haciendo lo mismo, claro, pero pa’ detenerlos.\n\nProbablemente lo mantengan vivo hasta que s’enteren de lo que sabe. ¡‘spabila si quieres salvarlo!", progress = nil, completion = "¿Sí? Lo siento... &lt;tose&gt; Estoy muy débil. ¿Puedes acercarte, &lt;clase&gt;?\n\n¿Así que t’envía Ryedol? Que los hados bendigan a Muradin, ese chico no es tan lento del tarro como pensaba.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[722] = {title = "El amuleto de los secretos", objective = "Encuentra el amuleto de Piemartillo y llévaselo a Uldaman.", description = "Eres valiente... <tose>... por venir a Uldaman pa’ mí, <clase>.\n\nSi realmente ‘stás aquí pa’ ayudar... al viejo Piemartillo, entonces... <tose>... tendrás qu’encontrar... mi amuleto.\n\nSombraprofunda lo tiene... Magregan Sombraprofunda. ‘stá por aquí... en las ruinas, seguro. Ese amuleto los conducirá... <tose>... derechito a los pergaminos si no lo paran. Depende de ti, <nombre>. Favor... recupera el amuleto.\n\nCon los pergaminos los Hierro Negro conseguirán crear golems aún más poderosos. Lo sé. Me lo dicen los huesos.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[723] = {title = "Tener fe en el porvenir", objective = "Llévale el amuleto de Piemartillo al prospector Ryedol en las Tierras Inhóspitas.", description = "<Piemartillo se sujeta las heridas y se toma un momento para recuperarse antes de seguir hablando.>\n\nEncuentra a Ryedol, <nombre>. Llévale mi amuleto. <Tose> Él… <tose> saber qué hacer. Sabe qu’estaba buscando los orígenes de nuestro pueblo, y él <tose> sabe que mi misión era detener al clan Forjatiniebla. Hay qu’impedir qu’encuentren esos pergaminos... <tose>", progress = "Diría que ha sido un mal día pero estamos en las Tierras Inhóspitas: cualquier día es malo.", completion = "¿El amuleto de Piemartillo? Creo que me encontraba más animado cuando creía que estaba muerto. Saberlo con seguridad solo aumenta el dolor. Este amuleto era el trabajo de su vida. No permitiré que haya muerto en vano.", rewards = "Recibirás:\n\n"},
[724] = {title = "Tener fe en el porvenir", objective = "Llévale el amuleto de Piemartillo a Karnik el Historiador en Forjaz.", description = "Vale, vale... ¿quién sabrá qué hacer?\n\n¡El historiador Karnik! ¡Eso es!\n\nSe encuentra en Forjaz. Podrá ayudarnos. Él y Piemartillo estaban trabajando juntos para encontrar los artefactos en Uldaman.\n\nSupongo que los Forjatiniebla se enteraron de que los dos sabían más de lo dejaban ver.\n\nDile a Karnik lo que le ha ocurrido a Piemartillo y dale el amuleto, él te dirá qué hacer después.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[725] = {title = "Avertir de la amenaza", objective = "Encuentra al consejero Belgrum y dale su nota.", description = "<nombre>, corre y encuentra al consejero Belgrum en cuanto puedas. Cuéntale lo que has presenciado y pregúntale qué quiere que hagamos a continuación. En esta nota encontrará más detalles.\n\nMientras tanto, dedicaré un tiempo a hablar con el espíritu de Piemartillo. A lo mejor sabe algo que nos ayudará a detener las actividades de esos cretinos Forjatiniebla en Tierras Inhóspitas. Solo nos queda rezar.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[726] = {title = "Avertir de la amenaza", objective = "Habla con Karnik el Historiador.", description = "<El consejero Belgrum acaba de leer la nota, los ojos se le abren aún más por un momento.>\n\nVaya unas malas noticias.\n\n<nombre>, regresa con el historiador Karnik y dile que por la autoridad del rey actúe pronto y de forma contundente en base a esta información. Tiene que detener las actividades de los Forjatiniebla en Tierras Inhóspitas.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[727] = {title = "Ir a Forjaz a buscar el \"Compendio\" de Yagyin", objective = "Dale el sigilo del martillo a Gerrig Agarrahueso en La Caverna Abandonada de Forjaz.", description = "La tablilla es muy reveladora. ¡Aunque no lo suficiente! ¡Ni de lejos!\n\nHay un cuaderno, una revista que Yagyin escribió en otros tiempos. La... gente de los que huí... guardan este cuaderno en muchos lugares. Y sus más eruditos lo estudian. El conocimiento en él contenido es muy vasto. ¡Conocimiento para protegernos!\n\nVe a ver a Gerrig Bonegrip. Se encuentra en una tienda de Forjaz, en La Caverna Abandonada. Muéstrale este sigilo y creerá que eres de los suyos. Pregúntale por la revista de Yagyin... ¡y tráemela!", progress = "Hola. Bienvenido a mi tienda pero ojo con lo que tocas.\n\nEl conocimiento puede ser mortal.", completion = "Oh. Saludos, hermano.\n\nY alabados sean los moradores del subsuelo.", rewards = "Recibirás:\n\n"},
[728] = {title = "Ir a Entrañas a buscar el \"Compendio\" de Yagyin", objective = "Llévale el sigilo del martillo al guardián Bel'dugur en Entrañas.", description = "La tablilla es muy reveladora. ¡Aunque no lo suficiente! ¡Ni de lejos!\n\nHay un cuaderno, una revista que Yagyin escribió en otros tiempos. La... gente de los que huí... guardan este cuaderno en muchos lugares. Y sus más eruditos lo estudian. El conocimiento en él contenido es muy vasto. ¡Conocimiento para protegernos!\n\nVe a ver al guardián Bel'dugur en la ciudad de Lordaeron en Oxis. Muéstrale este sigilo y creerá que eres de los suyos. Pregúntale por la revista de Yagyin... ¡y tráemela!", progress = "Vaya, estamos impacientes, ¿no, <clase>? ¿Has venido para aprender de mi colección? ¡Bien!\n\nPero ten cuidado: al igual que el conocimiento otorga poder a los fuertes... vuelve locos a los débiles.", completion = "Ten cuidado con lo que dejas ver, &lt;clase&gt;. Hay quien no cree en la legitimidad de nuestro compromiso.\n\nY hay quien intentará que fracasemos si saben de nuestra existencia.\n\nSiempre debemos tener cuidado. Y estar vigilantes. Y pacientes.", rewards = "Recibirás:\n\n"},
[729] = {title = "El prospector despistado", objective = "Viaja al sur y asegúrate de que el prospector Tripirrem está bien.", description = "Fue un honor que el maestro Mostachogris me destinara a trabajar bajo la guía del gran prospector Remtravel. En la academia de Forjaz todo el mundo sabía de los grandes descubrimientos de Remtravel.\n\nPero el prospector es un poco... mm... despistado... Vive en su mundo.\n\nPor ejemplo: habíamos descubierto pruebas de la existencia de una gran sociedad. Y un día, unos terribles golems surgieron de la tierra e invadieron la excavación. Y Remtravel ni se dio cuenta. Yo corrí a Auberdine a pedir ayuda.\n\n¡Te lo ruego, viaja hacia el sur y asegúrate de que el prospector está bien!", progress = nil, completion = "¿Hollee, eres tú? Necesito ayuda con esta piedra. Pásame mi cepillo, creo que veo algo.\n\n¡Oye, tú no eres Hollee! ¿No habrás visto mi cepillo de piedra?\n\n¡Eso no importa ahora! Prometí a la Liga que enviaría el misterioso fósil que encontré. ¿Adónde se fue Hollee?\n\n¡Con todo lo que queda por hacer! En fin... ¿y dónde estará ese fósil misterioso? ¿Y mi cepillo...? ¿Y Hollee?\n\n¿Y tú quien eres? En realidad no importa, siempre que estés aquí para ayudar.\n\nAvísame cuando estés listo para empezar a buscar.", rewards = "Recibirás:\n\n"},
[730] = {title = "¿Problemas en Costa Oscura?", objective = "Viaja a Auberdine y busca rastros del equipo de excavación enano.", description = "Me agrada ver a un <clase> mostrando interés por las grandes maravillas arqueológicas de nuestro mundo.\n\nA menudo nuestro trabajo es considerado poco más que una afición por nuestros amigos de la Alianza. Pero es porque no se dan cuenta de que son los recientes descubrimientos de Khaz Modan los que han probado que una poderosa fuerza amenaza todo Azeroth, de Lordaeron a Kalimdor.\n\nEmpiezo a preocuparme de verdad por el personal que envié a Costa Oscura. Hace semanas que no tengo noticias suyas.\n\nViaja a Auberdine y busca pistas de su paradero.", progress = nil, completion = "Cómo me alegro de que el arqueólogo jefe Mostachogris enviara a alguien a buscarnos...", rewards = "Recibirás:\n\n"},
[731] = {title = "El prospector despistado", objective = "Protege al prospector Tripirrem mientras busca el fósil misterioso. Luego vuelve a Auberdine y busca al arqueólogo Hollee.", description = "Dime, Hollee... ah, no, que no eres Hollee. ¿Dónde está Hollee?\n\nDeja, total, no es importante. Tenemos que encontrar ese cepillo de piedra... Quiero decir, el fósil misterioso. Tenemos que encontrar ese fósil. La Liga de Exploradores de Darnassus querrá algún informe de lo que he estado haciendo.\n\nAvísame cuando estés <listo/lista>. Iremos a la caza de ese cepillo. Quiero decir, ¡del fósil! Así encontraremos algo palpable para satisfacer a la Liga.\n\n¿Estás <listo/lista>?", progress = nil, completion = "¡Gracias a las estrellas el prospector Remtravel está bien! Te dije que era un poco despistado. No puedo creer que insistiera en quedarse allí con todas esas horribles criaturas al acecho.", rewards = "Recibirás:\n\n"},
[732] = {title = "Terremotos", objective = "Recupera el Signo de la Tierra para Garek.", description = "Sigrun me ha dicho que habías sido de gran ayuda para él. Quizás te reclute para ayudarme. Mi maestro, Krasus del Kirin Tor, me envió aquí para poner fin a los disturbios que se estaban produciendo en Barranco Lethlor.\n\nNo puedo revelarte la naturaleza exacta de mi misión aquí ni de las tareas que debo acometer pero ten por seguro que son de vital importancia.\n\nUn grupo de ogros errantes, avistados en ocasiones cerca del Asentamiento Kosh, robaron un objeto llamado el Signo de la Tierra; debo recuperarlo. Tráemelo y te contaré más detalles.", progress = "El grupo de ogros nómadas se desplaza entre sus campamentos por las Tierras Inhóspitas; el calor que despide la reseca tierra y los remolinos de viento dificultarán su rastreo. He oído que a veces pasan por el Asentamiento Boff. Si estás perdido, podrías empezar a buscar allí.", completion = "Es buena noticia que lleves el Signo, &lt;nombre&gt;. Los enanos Hierro Negro de la Fortaleza de Angor crearon el Signo de la Tierra para retener a dos de los más crueles tenientes Alamuerte, los gemelos Cinchoscura y Hematus.", rewards = "Recibirás:\n\n"},
[733] = {title = "¡A gorronear!", objective = "Consigue 7 piezas de restos de armadura para Sigrun.", description = "La cosa se va a poner fea aquí si no logro gorronear algunos materiales para construir unas defensas y otras necesidades. Me he dado cuenta de que los ogros de la zona parecen estar bien surtidos de chatarra. A lo mejor las historias de máquinas de asalto en las Tierras Inhóspitas no estaban muy alejadas de la realidad después de todo...\n\nEn cualquier caso, el prospector lleva un tiempo preocupado por el estado de nuestro campamento, así que debería ponerme manos a la obra pronto si es posible.\n\nEl principal campamento de ogros está en la Gruta Rotapolvo, una buena caminata hacia el oeste desde aquí.", progress = "Bueno, me he apañado con lo que había pero... no es mucho. ¿Has encontrado más suministros?", completion = "Es un comienzo, &lt;nombre&gt;, y tendrá que servir hasta que sepamos algo de Forjaz o de la Liga de Exploradores.\n\nBien, y si ahora pudiera conseguir sal en algún lado para mi barril de bebida...", rewards = "Podrás elegir una de estas recompensas:\n\nEscudo SalbacBotas con talón de acero\n\nTambién recibirás:\n\nEscudo SalbacBotas con talón de acero"},
[734] = {title = "Lo tienes crudo", objective = "Habla con Lucien Tosselramienta en las Tierras Inhóspitas.", description = "¡Pero qué maravilla!\n\n<Lotwil te dedica una mirada breve antes de centrarse de nuevo en sus grilletes.\n\nVe a hablar con Lucien, <nombre>. Necesito un piñón del n.º 5. ¿Me lo traerás?", progress = nil, completion = "Piñón del n.º 5, ¿eh? Sí, esto no va a valer.", rewards = nil},
[735] = {title = "La Estrella, la Mano y el Corazón", objective = "Devuelve los objetos de la lista a Gerrig Agarrahueso a Forjaz.", description = "El Compendio de Yagyin, ¿eh? ¿Se ha empezado a escribir otro capítulo sobre nosotros? Disculpa, no debería haber preguntado.\n\nTe ayudaré porque tengo dos ejemplares. Hay que ayudarse mutuamente, ¿eh? Así es como se prospera.\n\nQuiero tres objetos de poder. Tráemelos y te daré el Compendio.\n\nNo voy a decir los nombres en alto, nunca se sabe quién está escuchando. Te los voy a escribir.", progress = "¿Traes los objetos que te pedí?", completion = "Tienes muchos recursos, &lt;clase&gt;. Si eres tú quien debe encargarse de la continuidad de nuestra orden, no dudo en que lo harás magníficamente.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[736] = {title = "La Estrella, la Mano y el Corazón", objective = "Llévale los objetos que aparecen en la nota de Bel'dugur al vigilante Bel'dugur de Entrañas.", description = "¿Necesitas el Compendio de Yagyin? Así que, ¿tu plan es reunir a otro grupo? Claro que sí... y por supuesto te ayudaré, <hermano/hermana>.\n\nPero tendrás que darme algo a cambio.\n\nNecesito tres objetos de poder. Tráemelos y el compendio será tuyo.\n\nNo puedo decir en alto sus nombres así que te los escribiré.\n\nSi me consigues los objetos de esta lista, serás verdaderamente <merecedor/merecedora> de crear tu propia sección dentro de nuestra orden.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[737] = {title = "El saber prohibido", objective = "Llévale el compendio de Yagyin a Theldurin.", description = "Aquí tienes la revista, <nombre>. Huelga decir que debes extremar el cuidado con ella. Además de mantenerla alejada de manos enemigas.\n\nEste cuaderno contiene muchas verdades...\n\n... Y la verdad puede encender chispas.", progress = "¡Ja! Hace mucho que te habías marchado. ¿Has conseguido la revista?", completion = "¡La tienes! ¡Oh, rayo de esperanza!\n\nPuede que tus esfuerzos sean nuestra salvación, &lt;nombre&gt;. ¡Un monumento habrían de erigirte! ¡Un altar a ti dedicado! ¡Alabado sea tu nombre hasta que tus nietos tengan nietos!\n\nA menos que...ELLOS se liberen y vuelvan a la superficie. Si eso ocurriera, entonces puedes olvidarte de los hijos.\n\nSi ELLOS quedan libres, todos seremos alimento de gusanos.", rewards = "Recibirás:\n\nCráneo de Muerte inminente"},
[738] = {title = "Encontrar a Agmond", objective = "Encuentra a Agmond.", description = "El prospector Agmond estaba a cargo de nuestra excavación en las Tierras Inhóspitas, hacia el suroeste. Y digo \"estaba\" porque... bueno, hace semanas que no sé nada de él. Necesito que encuentres a Agmond para poder conseguir su informe.\n\nLa última vez que oímos algo de Agmond se encontraba en la más meridional de nuestras excavaciones en las Tierras Inhóspitas, una pequeña explotación más allá de la Fortaleza de Angor. Yo empezaría a buscarlo allí.", progress = nil, completion = "Este esqueleto debe de ser lo que queda del desafortunado Agmond. Su cuerpo está destrozado y han limpiado sus huesos.", rewards = "Recibirás:\n\nCinturón de prospector rasgadoJadeMineral de oro"},
[739] = {title = "Murdaloc", objective = "Mata al asesino de Agmond, Murdaloc.\n\nMata a 12 partehuesos Petracripta.\n\nPreséntate ante el prospector Vetaferro en Loch Modan.", description = "La pista de Agmond arranca en el oeste. Es fácil saber por las manchas de sangre en las rocas y en la arena que el enano se arrastró cierta distancia.\n\nY murió aquí.\n\nTras una inspección más detenida, se descubre la palabra \"Murdaloc\" toscamente garabateada en un pedazo de arenisca de las Tierras Inhóspitas.\n\nGarabateada junto a la inerte mano de Agmond.", progress = nil, completion = "Es una noticia aleccionadora, &lt;nombre&gt;. Pero Agmond tenía un brazo poderoso, con su martillo era brutal ¡y la cabeza tan dura como nadie! A pesar de que esos malditos troggs lo han matado, estoy seguro de que antes se llevó por delante a muchos.", rewards = "Recibirás:\n\nPulverizador de rocas"},
[741] = {title = "El prospector despistado", objective = "Ve a Darnassus y entrega el fósil misterioso al arqueólogo jefe Mostachogris.", description = "¿Así que el prospector quiere mandar el misterioso fósil a la Liga de Exploradores de Darnassus? No me gusta la idea de irme de Costa Oscura sin él.\n\nTen, <nombre>, ve a Darnassus y entrega el misterioso fósil al arqueólogo jefe Mostachogris.", progress = "¿Qué puedo hacer por ti, <raza>?", completion = "¡Ay, ay! ¡Deja que sea el prospector Remtravel quien descubra eso!", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de PuñomartilloGuantes de paño de vientoCinturón de cazador de reliquia\n\nTambién recibirás:\n\nGuantes de PuñomartilloGuantes de paño de vientoCinturón de cazador de reliquia"},
[742] = {title = "La Caza de Vallefresno", objective = "Habla con Senani Truenozón en Puesto del Hachazo, en Vallefresno.", description = "¡Atención, jóvenes aventureros! ¡Los páramos de Vallefresno os esperan!\n\nLa Horda ha aumentado su presencia en las tierras al norte de Los Baldíos. Nuestras dos avanzadas, Puesto del Hachazo y La Ensenada de Zoram, se esfuerzan por traer gloria a la Horda. Aquellos que deseen demostrar su valía deben buscar ayuda allí. Nota: Senani Truenozón del Puesto del Hachazo, al norte de Los Baldíos, busca a aventureros que deseen participar en la gran Caza de Vallefresno.", progress = nil, completion = "Te doy la bienvenida a la nueva frontera, &lt;nombre&gt;. Vallefresno es una tierra de oportunidades en la que un joven &lt;clase&gt; como tú cuenta con innumerables oportunidades de demostrar lo que vale. Date una vuelta por el Puesto del Hachazo y no olvides visitar La Ensenada de Zoram, la otra avanzada de la Horda.\n\nTu presencia aquí me indica que has venido a instruirte en las artes de la caza. Escucha con atención y con gusto te enseñaré todo lo que has de saber.", rewards = "Recibirás:\n\n"},
[743] = {title = "Las Viento Furioso son un peligro", objective = "Lleva 8 espolones de Viento Furioso a Ruul Garraguilar al Poblado Pezuña de Sangre.", description = "Te agradezco que te pares a hablar conmigo, <nombre>. Me llamo Ruul y soy guerrero y maestro.\n\nHas alcanzado la edad en la que debes prepararte para las pruebas que te esperan si quieres ir más allá de Mulgore.\n\nPara poner a prueba tu fuerza, ve al sureste a enfrentarte a las arpías Viento Furioso. Sus nidos están en los bordes de la montaña, alejados del camino.\n\nSon enemigas naturales de Mulgore y serán un buen indicativo de tu habilidad.", progress = "Las Viento Furioso son mortíferas; su potencia en combate solo es comparable a su sed de sangre.", completion = "Buen trabajo, &lt;nombre&gt;. Ha llegado el momento de seguir tu viaje; que el viento siempre esté a tu espalda.", rewards = "Recibirás:\n\n"},
[744] = {title = "Preparación para la ceremonia", objective = "Consigue 6 plumas azur y 6 de bronce, y llévaselas a Eyahn Garraguilar a Cima del Trueno.", description = "<nombre>, mi hermano pronto se personará ante el jefe Pezuña de Sangre y será un honor para mí prepararle su penacho.\n\nMe gustaría pedirte un favor mientras acabo de curtir estas cintas de piel. No he podido reunir todas las plumas que necesito y me preguntaba si tú podrías ayudarme a conseguir más.\n\nPuedes obtener las plumas del tamaño adecuado de las arpías que habitan al norte y noroeste de Cima del Trueno. Solo necesito 6 plumas azur y 6 de bronce para completar mi diseño.", progress = "Este penacho será el regalo perfecto para mi hermano.", completion = "Gracias por tu ayuda, &lt;nombre&gt;.\n\nYa casi he acabado el penacho. Tan solo me queda entregárselo a mi hermano y presenciar la ceremonia.", rewards = "Podrás elegir una de estas recompensas:\n\nArnés atadoEscudo de guerrero tribal\n\nTambién recibirás:\n\nEscudo de guerrero tribalArnés atado"},
[745] = {title = "Compartir la tierra", objective = "Mata a 10 peleteros, 8 desolladores y 5 furtivos Crines Pálidos y vuelve al Poblado Pezuña de Sangre para hablar con Baine Pezuña de Sangre.", description = "<nombre>, esta tierra está herida por muchos conflictos. Espero que no tengas que presenciar tantos como yo en mi vida. Sin embargo, te pido que vayas a uno. Verás lo peligrosa que puede ser cualquier amenaza, por pequeña que sea, si no se ataja de inmediato.\n\nUnos gnolls Crines Pálidos se han instalado al sur de Pezuña de Sangre y en una cueva del oeste. No respetan nuestros intentos de establecer comunicación y matan a placer por todo Mulgore.\n\nLas palabras ya no sirven de nada.", progress = nil, completion = "Buen trabajo, &lt;nombre&gt;. Es importante respetar la tierra y a sus criaturas. La vida y la muerte forman un círculo necesario, la una no puede existir sin la otra. Reflexiona sobre esto y no lo olvides nunca.", rewards = "Recibirás:\n\nPerdigón cegador"},
[746] = {title = "Una excavación de los enanos", objective = "Recoge 5 picos de prospector. \n\nBusca una forja para destrozar los picos. \n\nLleva 5 trozos de herramienta rota a Baine Pezuña de Sangre al Poblado Pezuña de Sangre.", description = "Al noroeste hay enanos preparando una excavación. Creen que la tierra está llena de secretos y eso es cierto, pero perforarla no es la mejor manera de descubrirlos.\n\nEncuentra las herramientas de los enanos, rómpelas y tráeme los pedazos.\n\nNecesitarás una forja para romperlas. Hay una en Cima del Trueno, pero te servirá cualquiera, incluso la del campamento enano.", progress = "¿Traes lo que te pedí? Esos enanos van a enfurecer a los kodos de Mulgore. ¡Tenemos que hacer algo!", completion = "Gracias, &lt;nombre&gt;, esto tranquilizará a las bestias de Mulgore. Y enseñará a los enanos que horadar la tierra no les llevará a alcanzar la sabiduría.", rewards = "Podrás elegir una de estas recompensas:\n\nAtaduras reforzadasLeotardos de kodo basto\n\nTambién recibirás:\n\nAtaduras reforzadasLeotardos de kodo basto"},
[747] = {title = "Empieza la caza", objective = "Grull Alalcón quiere que le lleves 7 plumas y 7 piezas de carne de zancudo de llanura al Campamento Narache.", description = "Tienes un aire prometedor, demostrarás tu valía ante la tribu. Puede que pronto te acojan en la gran ciudad de Cima del Trueno. Pero antes de eso, tendrás que mostrar tu valía ante mi padre, el Jefe Vientaguilar.\n\nAquí en Mesa de la Nube Roja estamos orgullosos de nuestra habilidad para la caza. Los tauren cazamos por necesidad y por deporte. Nos estamos quedando sin carne y necesitamsos plumas para la ropa. Caza zancudos de llanura y demuestra tu valor mientras reabasteces al poblado.", progress = "Conseguir carne y plumas para la tribu es el primer paso para probar tu valor ante el Jefe.", completion = "Los &lt;raza&gt; de Narache te dan las gracias, &lt;nombre&gt;. Prometes mucho.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón nómadaGuantes de anillas pintados\n\nTambién recibirás:\n\nGuantes de anillas pintadosCinturón nómada"},
[748] = {title = "Pozos envenenados", objective = "Lleva 6 zarpas de lobo de pradera y 4 espolones de zancudo de llanura a Mull Astatrueno al Poblado Pezuña de Sangre.", description = "¡Los goblins han mancillado nuestros pozos de agua sagrada! No podemos permitirlo.\n\nPara limpiarlos necesito un tótem purificador; tú lo llevarás al pozo y realizarás el ritual de limpieza.\n\nEl primer tótem es para el Pozo de agua Pezuña Invernal. Necesitamos la ayuda de la tierra. Tráeme zarpas de lobo de pradera y espolones de zancudo de llanura adulto. Los encontrarás al suroeste.\n\nSé prudente, <nombre>.", progress = "¿Traes lo que te pedí?", completion = "Buen trabajo, &lt;nombre&gt;. Voy a imbuir su poder en un tótem purificador.", rewards = "Recibirás:\n\n"},
[749] = {title = "La caravana devastada", objective = "Morin Acechanubes quiere que registres las cajas de la caravana quemada.", description = "Hace unos días nos encontramos una caravana escoltada de Ventura y Cía. en la costa norte del Lago Toro de Piedra. En un combate anterior habíamos perdido a dos de los nuestros, así que atacamos la caravana.\n\nDejamos las cajas porque lo incendiamos todo, pero hemos visto a rescatadores de Ventura y Cía. cerca de las carretas quemadas; quizás quedó algo de valor.\n\nPodrías acercarte a ver qué hay en las cajas.", progress = nil, completion = "La caja tiene el sello de Ventura y Cía. en todos los lados. En el lateral se indica que el contenido debe ser procesado en una de sus plantas centrales.", rewards = "Recibirás:\n\n"},
[750] = {title = "La caza sigue", objective = "Grull Alalcón quiere que le lleves 10 pieles de puma montés al Campamento Narache.", description = "Un buen cazador tauren sabe que la presa no es un mero trofeo. Los animales de la llanura son nuestro medio de subsistencia. Si quieres impresionar a los ancestros, trae algunas pieles de puma montés; son muy valiosas. Los pumas viven en esas colinas hacia el sur.\n\nLos niños necesitan ropa y tenemos que reparar las carpas.", progress = "Si consigues pieles para la tribu, hablaré de tu valor a mi padre, el Jefe Vientaguilar.", completion = "Los &lt;raza&gt; de Narache te agradecen estas provisiones, &lt;nombre&gt;. Con esa habilidad para la caza, algún día alcanzarás el nivel de reputación venerado en Cima del Trueno.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales nómadasCinturón de anillas pintado\n\nTambién recibirás:\n\nBrazales nómadasCinturón de anillas pintado"},
[751] = {title = "La caravana devastada", objective = "Lleva los documentos de Ventura y Cía. a Morin Acechanubes cerca del Poblado Pezuña de Sangre.", description = "Al abrir la caja ves que está llena de mineral, otra prueba de las operaciones mineras de Ventura y Cía. en Mulgore. Coges el paquete de documentos que hay encima del mineral.\n\nEn las laderas del este se oyen ruidos de actividad industrial y se ve una columna de humo.", progress = "Saludos, <nombre>. ¿Qué has descubierto sobre los planes de Ventura y Cía.?\n\nSi pretenden explotar nuestras tierras como han hecho con otras, los avanzados les harán cambiar de idea.", completion = "Parece que Ventura y Cía. intenta robarnos nuestras materias primas. Bien, ahora que lo sabemos con certeza, debemos hacer algo.", rewards = "Recibirás:\n\n"},
[752] = {title = "Una humilde contribución", objective = "El  Jefe Vientaguilar quiere que vayas a buscar a su madre cerca del pozo que hay al sureste del Campamento Narache.", description = "Todos los miembros de la tribu contribuyen a la armonía de la vida. Vivimos juntos y trabajamos juntos. \n\nCada uno de nosotros es responsable del bienestar de los demás. Quisiera pedirte un favor. \n\nMi madre se fue esta mañana a coger agua del pozo que hay al sureste de Narache. Empiezo a inquietarme; ¿podrías ir a buscarla mientras yo atiendo los asuntos de la tribu?", progress = nil, completion = "¿Has venido hasta aquí para ayudar a una anciana? ¡Qué admirable!", rewards = "Recibirás:\n\n"},
[753] = {title = "Una humilde contribución", objective = "Llena un cántaro de agua en el pozo. \n\nLlévaselo al Jefe Vientaguilar al Campamento Narache, que está al noroeste del pozo.", description = "He recorrido muchos caminos en la vida, mis piernas ya no son fuertes. Todavía puedo ser útil para la tribu, es solo que tardo un poco más en hacer las cosas. \n\nPero tú rebosas vitalidad, <clase>. Vamos a ponerla a prueba; llena un cántaro en el pozo y llévaselo a mi hijo, el jefe del Campamento Narache. \n\nRecuerda que incluso el más insignificante servicio puede valerte el respeto de los ancestros.", progress = "Ah, ya estás aquí. ¿Has encontrado a mi madre?", completion = "Ah, te dijo que trajeras agua.\n\nTu deseo de ayudar a los tauren del Campamento Narache me dice que algún día honrarás a la tribu en Cima del Trueno.", rewards = "Recibirás:\n\n"},
[754] = {title = "La purificación de Pezuña Invernal", objective = "Usa el tótem purificador en el Pozo de agua Pezuña Invernal y ve a ver a Mull Astatrueno.", description = "Este es el tótem para el Pozo de agua Pezuña Invernal. Llévalo al pozo y lleva a cabo el ritual de limpieza. El pozo está hacia el sureste y en poder de los repugnantes goblins.\n\nEs una misión difícil, pero no podemos perder el acceso a los pozos.\n\nSuerte, <nombre>.", progress = nil, completion = "Los animales ya están bebiendo del pozo.\n\nHas prestado un gran servicio a la tierra y a los &lt;raza&gt;, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[755] = {title = "Ritos de la Madre Tierra", objective = "Ve a ver al profeta Lenguagris, que vive en las colinas al sur del Campamento Narache.", description = "Tu buena disposición y entusiasmo por ayudar a los tauren de Narache te ennoblecen, <nombre>. Creo que un día se te aclamará en Cima del Trueno con grandes honores como <clase>. \n\nPero antes de eso, debes cumplir con los tres Ritos de la Madre Tierra. \n\nLa primera prueba es el Rito de la Fuerza. Ve a ver al profeta Lenguagris y dile que vas de parte del Jefe Vientaguilar. \n\nSu morada está al sur del campamento, escondida entre las colinas.", progress = nil, completion = "¿Te envía el Jefe Vientaguilar? Los Ritos de la Madre Tierra son un asunto de cierta envergadura...", rewards = "Te van a lanzar el siguiente hechizo:\n\nMarca de lo salvaje\n\nRecibirás:\n\n"},
[756] = {title = "El tótem de Tronacuerno", objective = "Lleva 6 garras de acechador y 6 garras de puma a Mull Astatrueno.", description = "Para crear el tótem para el Pozo Tronacuerno necesitamos las garras de los depredadores de Mulgore. Tráeme garras de acechador de pradera y puma de las estepas.\n\nLos encontrarás tanto hacia el este como hacia el oeste.", progress = "¿Traes lo que te pedí, <nombre>? El Pozo Tronacuerno se corrompe más cada minuto que pasa.", completion = "Gracias, &lt;nombre&gt;. La pureza con la que cazan estas bestias es vital para crear el tótem de limpieza. \n\nLos goblins nunca aprenderán la diferencia entre explotar la tierra y vivir en armonía con ella.", rewards = "Recibirás:\n\n"},
[757] = {title = "Rito de la fuerza", objective = "Mata Erizapúas en Barranco Cortazarza y lleva 12 de sus cinturones al Jefe Vientaguilar al Campamento Narache.", description = "Los Ritos de la Madre Tierra son los pasos con los que un joven tauren se gana el respeto de Cima del Trueno.\n\nEl primero que debes superar es el Rito de la Fuerza, con el que pruebas tu valor al matar enemigos de la tribu.\n\nLos Erizapúas de Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Atacan por emboscada a nuestros cazadores y roban en el poblado durante la noche.\n\nPara demostrar tu valor, acaba con esos villanos y entrega sus cinturones al jefe del Campamento Narache como prueba.", progress = "¿Has completado el Rito de la Fuerza, <nombre>? Quiero una prueba de tus acciones contra nuestros enemigos, los Erizapúas.", completion = "Has superado el primero de los Ritos de la Madre Tierra. La tribu estará orgullosa.", rewards = "Podrás elegir una de estas recompensas:\n\nMaza de rocaTomahawk de piedraCuchillo para tallarBastón de ancianoHacha de Valentía\n\nTambién recibirás:\n\nMaza de rocaTomahawk de piedraCuchillo para tallarBastón de ancianoHacha de Valentía"},
[758] = {title = "La purificación de Tronacuerno", objective = "Usa el tótem purificador en el Pozo Tronacuerno y ve a ver a Mull Astatrueno.", description = "<nombre>, el tótem ya está listo. Debes llevar a cabo el ritual una vez más.\n\nLlévate el tótem al Pozo Tronacuerno, que está al norte. Vuelve aquí cuando termines.\n\nQue los espíritus te acompañen.", progress = nil, completion = "Los antepasados del clan Tronacuerno me han hablado en un sueño para felicitarte por tus actos en el pozo.\n\nDesean que tengas esto como muestra de agradecimiento...", rewards = "Recibirás:\n\nCapa Tronacuerno"},
[759] = {title = "El tótem de Bravacrín", objective = "Lleva 8 dientes de alfa de pradera a Mull Astatrueno al Poblado Pezuña de Sangre.", description = "Ya solo queda por limpiar el Pozo Bravacrín. Para ello, la tierra debe ofrecernos los colmillos de un depredador feroz, el lobo alfa de pradera. Los encontrarás al norte. Vuelve cuando hayas conseguido los colmillos.\n\nLos alfa suelen deambular alrededor de la ciudad.", progress = "¿Traes lo que te pedí?", completion = "Gracias, &lt;nombre&gt;. Este sacrificio de la tierra me llena de tristeza y también de orgullo.", rewards = "Recibirás:\n\n"},
[760] = {title = "La purificación de Bravacrín", objective = "Usa el tótem purificador en el Pozo Bravacrín y ve a ver a Mull Astatrueno.", description = "Los espíritus están complacidos contigo por la limpieza de los pozos de Tronacuerno y Pezuña de Invierno, <nombre>. El tótem de Bravacrín ya está listo; te espera tu última tarea.\n\nEl pozo está al norte de Cima del Trueno. Lleva a cabo el ritual para limpiar la tierra de la inmundicia goblin. Que las aguas vuelvan a fluir puras y claras.", progress = nil, completion = "Has limpiado los pozos y sanado nuestra tierra; el Poblado Pezuña de Sangre te está agradecido, &lt;nombre&gt;.\n\nQue tus hazañas sean cantadas hasta la séptima generación de la séptima generación.", rewards = "Podrás elegir una de estas recompensas:\n\nTomahawk ceremonialBastón Vigilasueños\n\nTambién recibirás:\n\nTomahawk ceremonialBastón Vigilasueños"},
[761] = {title = "La caza de los aguilones", objective = "Lleva 8 plumas trofeo de aguilón a Harken Vientótem al Poblado Pezuña de Sangre.", description = "El aguilón es un ave muy astuta, difícil de encontrar y de cazar. Un trofeo de plumas de aguilón sería una prueba de tu talento cazador.\n\nVe a las llanuras y caza uno.\n\nTráeme las plumas y gana honor para tu clan.\n\nEstán por todo Mulgore, pero tendrás que estar ojo avizor para verlos y muy veloz para cazarlos.", progress = "¿Me traes las plumas de aguilón?", completion = "Sé que esto no ha sido fácil, &lt;nombre&gt;. Has probado tu valor; nos alegramos de tenerte en el Poblado Pezuña de Sangre.", rewards = "Recibirás:\n\n"},
[762] = {title = "Un embajador de mal", objective = "Mata al embajador Infernus en la Fortaleza de Angor llévale la prueba de su muerte al consejero Belgrum en Forjaz.", description = "El propio Ragnaros envió al embajador Infernus a la Fortaleza de Angor para controlar a los Forjatiniebla en su búsqueda de artefactos perdidos en Uldaman.\n\nHa supuesto ya gran número de bajas entre nuestra gente pero, a todas luces, ha empeorado. Dudo en adelantar una cifra respecto a cuántos artefactos habrán encontrado y usarán contra Forjaz.\n\nNuestro objetivo es sencillo, <clase>. Vuelve a Tierras Inhóspitas y destruye al embajador Infernus. Una vez eliminada la amenaza, vuelve con el consejero Belgrum con una prueba de tu victoria.\n\nAhora todo depende de ti.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nEspada de capitán enano"},
[763] = {title = "Ritos de la Madre Tierra", objective = "Lleva el tótem de Alalcón a Baine Pezuña de Sangre al Poblado Pezuña de Sangre. Sigue el camino que sale del Campamento Narache.", description = "Si quieres seguir con los Ritos de la Madre Tierra tendrás que pasar otras dos pruebas. Debes ampliar tu experiencia, <nombre>.\n\nVe al Poblado Pezuña de Sangre y habla con el Jefe Baine, hijo de Cairne Pezuña de Sangre. Allí podrás seguir viaje y ganarte la aceptación de los ancestros de Cima del Trueno.\n\nDale a Baine este tótem. Reconocerá mis grabados y te ayudará.\n\nSigue el camino que sale del Campamento Narache. Viaja deprisa y no te desvíes o te perderás.", progress = "¿Qué te trae por aquí, <clase>?", completion = "¡Ah, vienes de parte del Jefe Vientaguilar! Sus grabados indican que te considera muy especial.", rewards = "Recibirás:\n\n"},
[764] = {title = "Los Ventura y Cía.", objective = "Mata a 14 obreros y 6 supervisores de Ventura y Cía. por encargo de Morin Acechanubes, del Poblado Pezuña de Sangre.", description = "Eso explica que Ventura y Cía. estuviera adquiriendo mano de obra y material a gran escala en Mulgore. Esa empresa goblin se está expandiendo demasiado deprisa; acabarán mal. Solo piensan en el valor monetario de las cosas.\n\nPandilla de avariciosos... Por desgracia para ellos, los tauren no podemos permitir que trabajen en nuestras tierras. Ve a la mina, que está al noreste de la caravana quemada, y dales un mensaje.", progress = nil, completion = "Esto no deja mucho lugar a dudas, ¿eh? Los de Ventura y Cía. van a descubrir que con los tauren no se juega y que no pueden actuar con total impunidad.", rewards = "Recibirás:\n\n"},
[765] = {title = "Supervisor Fizsprocket", objective = "Mata al supervisor Fizsprocket y lleva su sujetapapeles a Morin Acechanubes al Poblado Pezuña de Sangre.", description = "Los documentos que recuperaste en la caravana muestran que Ventura y Cía. estaba haciendo planes para echarnos de nuestras tierras.\n\nMe enferma pensar en lo que son capaces de hacer para conseguir lo que quieren. Quiero que mates a quien ideó los métodos para echarnos. Es el supervisor Fizsprocket; está en la mina que hay al este de la caravana. Tráeme su sujetapapeles, a ver si averiguamos algo más.", progress = "Me enfurece pensar en lo que son capaces de hacer con tal de conseguir sus propósitos.", completion = "Saber que ese villano de Fizsprocket está muerto me hace estar un poco menos furioso. Voy a revisar sus pertenencias, a ver si encuentro algo más sobre los planes de Ventura y Cía.. Gracias por tu ayuda, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCuchillo de lucha compactoMachacagoblins\n\nTambién recibirás:\n\nMachacagoblinsCuchillo de lucha compacto"},
[766] = {title = "Mazzranache", objective = "Lleva 1 corazón de lobo de pradera, 1 fémur de puma de las estepas, escamas de zancudo de llanura y molleja de aguilón a Maur Clamalluvias al Poblado Pezuña de Sangre.", description = "Mi abuelo me contaba historias de sus batallas con un zancudo llamado Mazzranache. Me contó que tenía los ojos rojos, espolones como navajas y una mordedura venenosa.\n\nFui a dar con él mientras cruzaba las llanuras y se me llevó un pedazo de hombro. Era tan terrorífico como había dicho mi abuelo. Para desinfectar la herida necesito unas cuantas cosas: un corazón de lobo, un fémur de puma, escama de zancudo de llanura y molleja.\n\nApresúrate, el tiempo corre.", progress = "Cada vez tengo más fiebre, empiezo a delirar... Cómo se me ocurriría enfrentarme a un bicho con el que no pudo ni mi abuelo...\n\n¿Traes los ingredientes? Si no desinfecto la herida ya, no saldré de esta.", completion = "Gracias, &lt;nombre&gt;.Con esto podré formular una cura para la infección; te debo la vida.\n\nPero nunca podré olvidar el alarido de Mazzranache al atacarme...\n\nSé prudente siempre.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de AbiscorredorMuñequeras de cazador de las llanuras\n\nTambién recibirás:\n\nBotas de AbiscorredorMuñequeras de cazador de las llanuras"},
[767] = {title = "Rito de la Visión", objective = "Habla con Zarlman Dos Lunas en el Poblado Pezuña de Sangre.", description = "El Rito de la Visión, uno de los Ritos de la Madre Tierra, te enseñará cómo ganar el respeto de los ancestros de Cima del Trueno.\n\nNuestra gente ha aprendido que la tierra es la sagrada proveedora. Para tomar parte en la visión ritual, debes hablar con el guía espiritual de la aldea, Zarlman Dos Lunas.", progress = nil, completion = "Presentía tu llegada, &lt;clase&gt;. Es fácil reconocer a quien está intentando superar los Ritos de la Madre Tierra.", rewards = "Recibirás:\n\n"},
[768] = {title = "Recolecta cuero", objective = "Llévale 12 pieles de cuero ligero a Veren Zancaalta a Cima del Trueno.", description = "Saludos, joven. El brillo en tus ojos me indica que estás impaciente por continuar la caza. ¡Ojalá traigas honor a tu clan!\n\nYo trabajo con pieles y con ellas confecciono ropajes y armaduras para las gentes de Cima del Trueno.\n\nSi cazas bestias y me traes sus pieles, confeccionaré algo especialmente para ti.", progress = "Hola de nuevo. ¿Has estado cazando? ¿Tienes mis pieles?", completion = "Estas pieles son excelentes. Con ellas podré hacer objetos de cuero de primera calidad. \n\nToma, &lt;nombre&gt;. Coge esto a cambio...", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de piel de animalGuantes con doble capa\n\nTambién recibirás:\n\nGuantes con doble capaCinturón de piel de animal"},
[769] = {title = "Bolsa de pellejo de kodo", objective = "Llévale 4 pellejos de cuero ligero y 4 hilos gruesos a Veren Zancaalta a Cima del Trueno.", description = "Los kodos de Mulgore son fuertes y robustos, unas cualidades que apreciamos mucho. Si tienes conocimientos de peletería y deseas aprender a fabricar una bolsa de piel de kodo, tráeme suministros para mi oficio.\n\nHazlo y compartiré mis conocimientos contigo.", progress = "¿Tienes mi material, <nombre>?", completion = "Ah, bien. Gracias, &lt;nombre&gt;. Mi corazón se hinche de orgullo al ver que los jóvenes se interesan por el arte de la peletería.", rewards = "Recibirás:\n\nPatrón: bolsa de pellejo de kodo"},
[770] = {title = "La capa del demonio marcado", objective = "Busca a alguien que conozca a Aullido Fantasma.\n\nObjeto provisto: \n", description = "La piel destrozada de Aullido Fantasma muestra la terrible herida que recibió en combate contra la Legión Ardiente.\n\nHabría que avisar de su muerte en el Poblado Pezuña de Sangre...", progress = nil, completion = "¡No puedo creerlo! ¿Has derrotado a Aullido Fantasma? Acabas de ganarte mi respeto con tu habilidad para la caza, &lt;clase&gt;.\n\nYo ya no cazo, pero me honraría regalarte una de mis armas.\n\nQue te traiga reconocimiento y fortuna.\n\nEsperemos que Aullido Fantasma haya encontrado al fin la paz.", rewards = "Podrás elegir una de estas recompensas:\n\nMartillo de SkornRifle de Skorn\n\nTambién recibirás:\n\nRifle de SkornMartillo de Skorn"},
[771] = {title = "Rito de la Visión", objective = "Lleva 2 piedras de pozo y 2 bellotas ámbar a Zarlman Dos Lunas al Poblado Pezuña de Sangre.", description = "Para superar el Rito de la Visión debes conseguir los componentes necesarios para crear el Agua de los Visionarios.\n\nNecesito piedras de pozo, que encontrarás cerca de los pozos de Mulgore. Las mezclaré con bellota ámbar, que cae de los orgullosos árboles de nuestra tierra.\n\nEstos ingredientes son mágicos; tómate el elixir resultante junto a la hoguera tribal para tener una visión. Síguela y te llevará al siguiente paso de esta misión sagrada.", progress = "Cuando me traigas las piedras de pozo y las bellotas ámbar, te prepararé el Agua de los Visionarios.", completion = "Veo que has aprendido a recolectar en nuestra tierra sagrada, &lt;nombre&gt;. En un momento te daré el Agua de los Visionarios.", rewards = "Recibirás:\n\n"},
[772] = {title = "Rito de la Visión", objective = "Bebe el Agua de los Visionarios frente a la hoguera tribal del Poblado Pezuña de Sangre y sigue a la visión cuando aparezca.\n\nObjeto provisto: \n", description = "Aquí tienes el Agua de los Visionarios.\n\nTómatela cerca de la hoguera tribal. Al cabo de un momento la visión se materializará junto a la hoguera.\n\nA partir de ahí, de ti depende el seguirla...", progress = nil, completion = "No tengas miedo, &lt;nombre&gt;. Has superado el Rito de la Visión.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de caminante de lluviaCapa estropeada por el sol\n\nTe van a lanzar el siguiente hechizo:\n\nMarca de lo salvaje\n\nTambién recibirás:\n\nBotas de caminante de lluviaCapa estropeada por el sol"},
[773] = {title = "Rito de la Sabiduría", objective = "Ve a Roca Roja, al este de Cima del Trueno, y busca al espíritu ancestral.", description = "Para que los ancestros de Cima del Trueno te acepten, tienes que superar el Rito de la Sabiduría.\n\nAhora que has superado el Rito de la Visión, los espíritus ancestrales de Roca Roja te darán las bendiciones de los ancestros. Solo quien ha bebido el Agua de los Visionarios merece recibir la bendición.\n\nVe a Roca Roja, al este de Cima del Trueno, y busca al espíritu ancestral, <nombre>.", progress = nil, completion = "Has puesto gran voluntad en superar los Ritos de la Madre Tierra, &lt;nombre&gt;.\n\nLos espíritus ancestrales representamos a los &lt;raza&gt; que dieron la vida para fundar y proteger Cima del Trueno. Ahora ese deber de protección pasa a ti.\n\nHas superado el Rito de la Sabiduría, &lt;clase&gt;. Entra en Cima del Trueno con orgullo.", rewards = "Recibirás:\n\n"},
[775] = {title = "Viaja a Cima del Trueno", objective = "Habla con Cairne Pezuña de Sangre en Cima del Trueno.", description = "Se te venerará en nuestra gran ciudad. Te mereces tal honor, pues has completado los Ritos de la Madre Tierra.\n\nTu última misión consiste en buscar a nuestro noble jefe, Cairne Pezuña de Sangre, que vive en la cima de la mesa más alta de Cima del Trueno.", progress = nil, completion = "Saludos, &lt;clase&gt;. Veo que has hallado el sendero hasta mi puerta.", rewards = "Recibirás:\n\n"},
[776] = {title = "Ritos de la Madre Tierra", objective = "Cairne Pezuña de Sangre de Cima del Trueno quiere que le lleves el cuerno de Arra'chea.", description = "Has superado los Ritos de la Madre Tierra y te has ganado un lugar en Cima del Trueno.\n\nPero no puedes dormirte en los laureles; debes probar tu valor constantemente.\n\nSomos cazadores, <nombre>. En Mulgore vive un hermoso kodo que se llama Arra'chea. Tráeme el cuerno de Arra’chea para demostrarme tu habilidad para rastrear y cazar.", progress = "¿Ya has localizado a Arra’chea?", completion = "¡Buen trabajo!\n\nEnviaré a un grupo para retirar la piel y la carne.\n\nTu gente te debe mucho, &lt;nombre&gt;.", rewards = "Recibirás:\n\nLeotardos de cazador kodo"},
[777] = {title = "Lo tienes crudo", objective = "Llévale un piñón del n.º 5 a Lotwil Veriatus.", description = "Muy bien, aquí tienes.\n\nEstaré por aquí... a cubierto.\n\nSi quieres, puedes unirte a mí.", progress = "<Sin apartar la vista de los grilletes, Lotwil levanta una mano y agita los dedos con impaciencia.>\n\n¡El piñón, <nombre>, el piñón!", completion = "Ah, perfecto. ¡Retrocede mientras me preparo para hacer historia!\n\n&lt;Lotwil sigue trabajando febrilmente.&gt;", rewards = nil},
[778] = {title = "Lo tienes crudo", objective = "Derrota al guardián Fam'retor y devuélvele los grilletes de vínculo elemental a Lotwil Veriatus.", description = "¡Ea! ¡Ya está! ¡Los grilletes están listos!\n\nEs la hora, Lucien. ¡Es la hora, Servo!\n\n¿Estás <preparado/preparada> para ser testigo de la perfecta combinación de alquimia, ingeniería y lo Arcano, <nombre>?\n\nAhora voy a conjurar a un poderoso elemental. Este es un día marcado por la innovación para todos nosotros.", progress = "No es exactamente el final que esperaba. ¿Has logrado recuperar mis grilletes?", completion = "Gracias, &lt;nombre&gt;. Ha sido un suplicio, ¿verdad?\n\nNo estoy seguro de qué puede haber fallado. Menos mal que hemos contado con mi magia para enviar a la criatura de vuelta a su hogar.\n\n¿Quizás una muestra de aprecio por ser tan denodado...?", rewards = "Recibirás:\n\nCronómetro ingenioso"},
[779] = {title = "El Sello de la Tierra", objective = "El Sello de la Tierra se encuentra aquí, con tres hornacinas en las que se alojan la piedra rúnica de Amatista, la de Ópalo y la de Diamante.", description = nil, progress = "El Sello de la Tierra se encuentra aquí, con tres hornacinas en las que se alojan la piedra rúnica de Amatista, la de Ópalo y la de Diamante.", completion = "Al colocar las piedras rúnicas en las muescas correspondientes, un violento estruendo sacude la tierra y se rompe el Sello.", rewards = nil},
[780] = {title = "Los jabaguerreros", objective = "Grull Alalcón quiere que le lleves 8 morros y 8 costillas de jabaguerrero al Campamento Narache.", description = "Los jabaguerreros de Barranco Cortazarza se están instalando en nuestro terreno de caza tribal. Los jabaespines Erizapúas, con quienes estamos en guerra, los amaestran para aumentar su ferocidad.\n\nAcaba con esas bestias y trae algunos morros y costillas para hacer caldo.", progress = "Los jabaguerreros siguen atacando. ¿Me traes los morros y las costillas?", completion = "Excelente. Con esto prepararé un estofado y de paso hemos dado una lección a esos Erizapúas.", rewards = "Recibirás:\n\nJubón nómadaMendrugo de pan duro"},
[781] = {title = "Ataque al Campamento Narache", objective = "Lleva los planes de ataque de los Erizapúas al Jefe Vientaguilar al Campamento Narache.", description = "Al estudiar el mapa, ves que contiene los planes del Jefe de Guerra Erizapúas para el asedio prolongado del Campamento Narache. \n\n¡Hay que avisar al Jefe Vientaguilar!", progress = "Parece que algo te preocupa, <nombre>. ¿Qué noticias traes?", completion = "¡Qué inquietante! Pero podemos avisar a nuestros hermanos del Poblado Pezuña de Sangre para que nos ayuden. Has salvado la vida de muchos tauren, &lt;nombre&gt;.", rewards = "Recibirás:\n\nRodela de corteza gruesa"},
[782] = {title = "Alianzas rotas", objective = "Mata al jefe Tho'grun y llévale el Signo de la Tierra a Gorn en Kargath.", description = "Por la alianza de la Horda con el dragón Alamuerte durante la Segunda Guerra, sabemos que dos de sus tenientes, Cinchoscura y Hematus, estaban presos en el Barranco Lethlor, un lugar remoto hacia el este. ¡Debemos acallar a nuestros depravados aliados ancestrales para restaurar el honor de la Horda!\n\nContratamos a Tho'grun y a su grupo de ogros mercenarios para que nos ayuden a conseguir el Signo de la Tierra, una llave necesaria para desbloquear la cárcel de los dragones. Pero Tho'grun nos traicionó, nos aplastó y se lo quedó. Ve al Asentamiento Boff ¡y recupéralo!", progress = "La traición de Tho'grun... ¿puede extrañarse nadie de que los ogros ya no sean parte de la Horda? Me encantaría verlo muerto y que se recuperara el Signo de la Tierra.\n\nSi aún lo has despachado, no pierdas el tiempo yendo al Asentamiento Boff. Está al sureste de Kargath.", completion = "Con Tho'grun muerto y su cadáver limpiado por el viento del desierto y las águilas ratoneras, reinará la confusión entre los ogros. Aunque solo es una pequeña victoria, llevará a otra mucho más importante.\n\nEl Signo de la Tierra está ahora en nuestras manos y podemos abrir la cárcel de los dragones negros en Lethlor. Los liberaremos de su eterno encarcelamiento, ¡aunque para su condena!", rewards = "Recibirás:\n\n"},
[783] = {title = "Una amenaza del interior", objective = "Habla con el alguacil McBride.", description = "Espero que vengas con ganas, <clase>, porque aquí en Villanorte hay mucho que hacer.\n\nY no me refiero a trabajar la tierra.\n\nLos guardias de Ventormenta no dan abasto, ahora que muchos de nosotros están lejos y hay tantas amenazas. Por eso estamos reclutando a quien esté dispuesto a defender su tierra. Y a su alianza.\n\nSi vienes a luchar, habla con mi superior, el mariscal McBride. Está ahí atrás, en la abadía.", progress = nil, completion = "Más voluntarios, estupendo.\n\nEspero que seáis suficientes.\n\nLas tierras humanas se ven amenazadas desde el exterior y nuestras tropas están en las fronteras; por eso no tenemos protección en el interior.\n\nEsta baralla tiene muchos frentes, &lt;nombre&gt;. Prepárate para una campaña muy larga.", rewards = "Recibirás:\n\n"},
[784] = {title = "Vencer a los traidores", objective = "Mata a 10 navegantes Kul Tiras, 8 marineros Kul Tiras y al teniente Benedicto. Luego vuelve a buscar a Gar'Thok a Cerrotajo.", description = "Bajo el mando del almirante Valiente, los humanos de Kul Tiras invadieron Durotar, violando el pacto que el Jefe de Guerra selló con Jaina Valiente años atrás para derrotar a Archimonde.\n\nEl ataque humano fue rechazado y el Fuerte de Tiragarde cayó. Pero las reservas del Almirante, comandadas por el teniente Benedicto, han retomado el fuerte y vuelven a amenazar nuestro territorio. Los humanos no tienen respeto alguno por la diplomacia.\n\nPor tu honor, viaja al sur hasta el Fuerte de Tiragarde y acaba con los invasores humanos.", progress = nil, completion = "Los relatos de tu valor viajan raudos, &lt;clase&gt;. Los de tu victoria en el Fuerte de Tiragarde serán anunciados en Orgrimmar.", rewards = "Recibirás:\n\n"},
[785] = {title = "Una alianza estratégica", objective = "Busca a Lar Prowltusk en el flanco oeste del Poblado Sen'jin.Thrall es un líder sabio y noble. Su tenacidad en el campo de batalla solo es comparable a su diplomacia.\n\nLos trols Lanza Negra han demostrado ser unos valiosos aliados para los orcos. Hemos sellado un pacto de protección con ellos basado en la lealtad y el honor.\n\nEl Jefe de Guerra ha dado instrucciones de que nos aseguremos que nuestros amigos Lanza Negra del Poblado Sen'jin no están bajo amenaza directa. Uno de sus rastreadores de confianza, Lar Prowltusk está vigilando el flanco occidental.\n\nBusca a Lar y ayúdale en lo que necesite.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", description = "Thrall es un líder sabio y noble. Su tenacidad en el campo de batalla solo es comparable a su diplomacia.\n\nLos trols Lanza Negra han demostrado ser unos valiosos aliados para los orcos. Hemos sellado un pacto de protección con ellos basado en la lealtad y el honor.\n\nEl Jefe de Guerra ha dado instrucciones de que nos aseguremos que nuestros amigos Lanza Negra del Poblado Sen'jin no están bajo amenaza directa. Uno de sus rastreadores de confianza, Lar Prowltusk está vigilando el flanco occidental.\n\nBusca a Lar y ayúdale en lo que necesite.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[786] = {title = "Frustrar la los planes kolkar", objective = "Lar Prowltusk, de las afueras del Poblado Sen'jin, quiere que destruyas los 3 planes de ataque de Risco Kolkar.", description = "Baja la voz, <clase>. Los centauros Kolkar están justo al otro lado de este promontorio, al oeste, en el Risco Kolkar.\n\nLa otra noche, cuando estaban fuera, en una de sus batidas, me colé en su poblado y descubrí que esas bestias inmundas tienen planeado un ataque en tres oleadas contra los trols y orcos de Durotar.\n\nDebemos frustrar sus planes de invasión. Quizás tú puedas reunir la fuerza necesaria para infiltrarte en el Risco Kolkar y destruir sus planes.\n\nLo último que vi es que los habían repartido entre tres de sus líderes.", progress = nil, completion = "Si los centauros Kolkar atacaran, a buen seguro, la Horda prevalecería. Pero impidiendo la eventualidad de un ataque, hemos evitado un inútil derramamiento de la sangre de nuestros poderosos guerreros.\n\nPero sabemos, como sabemos que hay arena en el Desierto de Tanaris, que antes de que estos atroces tiempos queden atrás, se derramará sangre.\n\nHas servido bien a tu gente, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de luchador salpimentadoBrazales de cordón pesado\n\nTambién recibirás:\n\nCapa de luchador salpimentadoBrazales de cordón pesado"},
[787] = {title = "La nueva Horda", objective = "Informa a Gornek en El Cubil.Throm'ka, <clase>. Soy Eitrigg, Thrall me encargó supervisar la instrucción de los reclutas.\n\nLa Horda ya no es lo que era. Hubo un tiempo en el que abandoné sus filas desilusionado por la creciente influencia de los peones, sedientos de poder, de la Legión Ardiente. Durante mi exilio fui hecho prisionero por un grupo de humanos pero me rescató el Jefe de Guerra. Regresé tras oír su visión de una Horda libre de influencia demoníaca y guiada por los chamanes.\n\nGornek te dará más instrucciones.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", description = "Throm'ka, <clase>. Soy Eitrigg, Thrall me encargó supervisar la instrucción de los reclutas.\n\nLa Horda ya no es lo que era. Hubo un tiempo en el que abandoné sus filas desilusionado por la creciente influencia de los peones, sedientos de poder, de la Legión Ardiente. Durante mi exilio fui hecho prisionero por un grupo de humanos pero me rescató el Jefe de Guerra. Regresé tras oír su visión de una Horda libre de influencia demoníaca y guiada por los chamanes.\n\nGornek te dará más instrucciones.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[788] = {title = "Dientes afilados", objective = "Mata a 10 jabalíes jaspeados y vuelve con Gornek en El Cubil.", description = "El primer punto del día será fortalecer un poco tu espalda. Te podría enviar a Los Baldíos, a cazar kodos, pero, sinceramente, nos eres más útil <vivo/viva> que <muerto/muerta>.\n\nCreo que lo mejor será que empieces por los jabalíes jaspeados que encontrarás al norte de aquí.", progress = nil, completion = "Umm... no está mal, &lt;nombre&gt;. Pero que no se te suba a la cabeza... tendrás que luchar con cosas mucho más duras que un jabalí en tu carrera.\n\nSin embargo, has mostrado tu valía, y tu siguiente prueba será contra un adversario mucho más peligroso, necesitarás algo más de protección.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de lana suaveGuantes de cuero ajados por la batalla\n\nTambién recibirás:\n\nGuantes de cuero ajados por la batallaBotas de lana suave"},
[789] = {title = "Aguijón de escórpido", objective = "Consigue 10 colas de trabajador escórpido para Gornek en El Cubil.", description = "El poderoso guerrero y el torpe novicio... todos han caído bajo la venenosa picadura del escórpido. Encontrarás un gran número de escórpidos al noroeste de aquí. Tráeme 10 de sus colas como prueba de tu destreza y valor en la batalla.\n\nEl antídoto para sus picaduras se hace a base del veneno extraído de sus aguijones. Conservamos a mano grandes cantidades de antídoto para el veneno de escórpido con las que curar sangres jóvenes como la tuya...\n\nPero seguro que a ti no te hace ninguna falta, ¿no es cierto?", progress = "El caparazón de un escórpido no es tan resistente como para disuadir la fuerza y determinación de un guerrero. Golpea fuerte y sin dudas, y los escórpidos serán una presa fácil.", completion = "De tus luchas con los escórpidos debes sacar una lección importante. Tanto el menor como el mayor de tus oponentes pueden enviarte directo al infierno. En el combate cualquier cosa y en cualquier número o tamaño puede llevarte a la perdición.\n\nNo tengo nada más que enseñarte, &lt;nombre&gt;. Te has portado bien y seguiré con mucho interés tus progresos.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de lana suaveManteo ajado por la batalla\n\nTambién recibirás:\n\nCinturón de lana suaveManteo ajado por la batalla"},
[790] = {title = "Sarkoth", objective = "Mata a Sarkoth y llévale su zarpa a Hana'zua.", description = "¡<clase>! Creía que iba morir ahí fuera sin que nadie se enterara. Cuando estaba cazando los escórpidos del Valle me crucé con uno que tenía un aspecto especialmente atroz. Me lancé contra él y conseguí asestarle un enorme golpe antes de se enrollara en mi pierna.\n\nPero no estaba listo para su aguijón, y consiguió deslizarse en mi peto, desgarrando y penetrando en mi piel y derramando mi sangre. Por favor, ¡debes matar a ese escórpido! ¡Hay que defender mi honor! Combatí con él en la meseta que está al Sur.", progress = "Ahhh... mi padre siempre decía que nunca llegaría demasiado lejos y estando aquí, bajo un árbol viendo cómo mi vida se desvanece, parece que estaba en lo cierto.\n\nAl menos me gustaría morir sabiendo que mi último enemigo ha muerto.", completion = "Mis golpes no bastaron para matarle, pero cuando pienso en el daño que le hice siento algo de orgullo. Ese orgullo será todo lo que tenga si muero y, desde esa perspectiva, me enfurezco al pensar en las pocas cosas que he terminado en mi vida.", rewards = "Recibirás:\n\n"},
[791] = {title = "Asume tu responsabilidad", objective = "Furl Ceñosprecio de la atalaya de Cerrotajo quiere 8 retales.", description = "El tiempo me ha inutilizado para el combate. A mi edad, busco otras formas de ser útil.\n\nDesde este punto estratégico puedo vigilar la llegada de invasores. A medida que nuestra fuerza aquí se consolida, me encuentro dando la alarma cada vez menos.\n\nPara pasar el tiempo, creo objetos para ayudar a guerreros jóvenes y capacitados para defender nuestro hogar.\n\nA ti podría hacerte una bolsa para que lleves tus pertenencias. Si crees que te vendría bien, tráeme un retal. Sabrás lo que es... es un artículo muy normal tanto para centauros como para humanos.", progress = "Combatí con orgullo al lado del Jefe de Guerra cuando llegamos por vez primera a estas tierras. Mi piel está surcada por las cicatrices del combate.\n\nMi hacha y mi grito de guerra defendieron el honor de la Horda en la derrota de Archimonde, cuando humanos y elfos sellaron, unidos por la necesidad, su impía alianza.\n\nAhora, sigo sintiéndome útil en mi cargo de vigilante y oficial de suministros.", completion = "Excelente, &lt;nombre&gt;. Esta bolsa resultará muy útil en el campo de batalla.\n\n¡Aplaudo tu vigor y tu disposición para morir en nombre de la Horda!", rewards = "Recibirás:\n\nBolsa de cuero hecha a mano"},
[792] = {title = "Familiares viles", objective = "Mata a 12 familiares viles.\n\nVuelve con Zureetha Vistalonga a las puertas de El Cubil.", description = "Estoy segura de que aprenderás mucho en el Valle de los Retos, joven <clase>.\n\nFui enviada al Valle para guiarte, pero aquí he descubierto una creciente corrupción...\n\nUn grupo que se llama a sí mismo el Filo Ardiente tiene un aquelarre aquí mismo, en el Valle de los Retos. Se esconden en una cueva al Noreste, y sus familiares viles se han esparcido desde la boca de la cueva sembrando el caos y la confusión.\n\nTe ofrezco tu primera tarea contra el Filo Ardiente: derrotar a esos familiares. Aniquila a tantos como puedas y, si sobrevives, vuelve a verme.", progress = nil, completion = "Muy bien hecho, &lt;nombre&gt;.\n\nAunque los familiares viles no son más que mascotas de los poderes oscuros del Filo Ardiente, tu victoria presagia grandes hazañas.", rewards = "Podrás elegir una de estas recompensas:\n\nGarrote primitivoHoja de mano primitivaHachuela primitivaBastón de apoyo primitivo\n\nTambién recibirás:\n\nHachuela primitivaGarrote primitivoHoja de mano primitivaBastón de apoyo primitivo"},
[793] = {title = "Alianzas rotas", objective = "Usa el Signo de la Tierra para activar el Pilar de Diamante, el de Ópalo y el de Amatista y así obtener las piedras rúnicas.\n\nColoca las piedras rúnicas en el Sello de la Tierra para liberar a Cinchoscura y a Hematus.\n\nMátalos y llévale a Gorn las ataduras de Cinchoscura, las cadenas de Hematus y el Signo de la Tierra.", description = "El Sello de la Tierra está controlado por tres piedras rúnicas, que se encuentran en el Pilar de Diamante, el Pilar de Ópalo y el Pilar de Amatista en el Barranco Lethlor. Mediante el Signo de la Tierra, podrás deshacerte de las piedras rúnicas y desbloquear el Sello de la Tierra.\n\nAunque ándate con cuidado, <nombre>, los dragones negros Cinchoscura y Hematus son de armas tomar. Acaba con ellos y tráeme sus esclavas.\n\nPara llegar al barranco, dirígete hacia los confines orientales de las Tierras Inhóspitas. Y abre bien los ojos porque el barranco es tierra de multitud de dragones.", progress = "Hay pocos enemigos más letales que un dragonante, y pocos dragones luchaban más ferozmente que Cinchoscura y Hematus.", completion = "El Vuelo negro sigue buscando aliados entre los orcos renegados Roca Negra y hay que ponerlos a raya o eliminarlos. Con la muerte de Cinchoscura y Hematus, podemos centrar nuestra atención hacia el oeste, hacia la Cumbre de Roca Negra.", rewards = "Podrás elegir una de estas recompensas:\n\nPeto Viento abrasadorCamisote de escama prismáticaPeto forjado por la guerraMedallón de ráfaga mental\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPeto Viento abrasadorCamisote de escama prismáticaPeto forjado por la guerraMedallón de ráfaga mental"},
[794] = {title = "El medallón del Filo Ardiente", objective = "Lleva el Medallón del Filo Ardiente a Zureetha Vistalonga, a las puertas de El Cubil.", description = "Mis adivinaciones me traen visiones de un objeto lleno de poder, oculto en las profundidades del Aquelarre del Filo Ardiente y custodiado por bestias y magia negra.\n\nEs el llamado Medallón del Filo Ardiente y tu siguiente tarea es sacarlo del Aquelarre.\n\nPero ten mucha cautela, el Medallón puede estar en posesión de un agente del Filo Ardiente y su poder podría ser mayor que el del resto de los familiares con los que te hayas encontrado.\n\nVete, <nombre>. Encontrarás el Aquelarre en una cueva, al Norte y al Oeste.", progress = "¿Has terminado tu tarea, <nombre>? ¿Tienes el Medallón del Filo Ardiente?", completion = "¡Lo has recuperado! ¡Bien hecho!\n\nTus esfuerzos en el Aquelarre del Filo Ardiente son fundamentales para erradicar ese culto del Valle de los Retos. No obstante, temo que tienen más planes en esta tierra.\n\nAún no hemos visto su final.", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos cubiertos de polvoJubón de anillas dentadasPantalones rasgados\n\nTambién recibirás:\n\nPoción de curación menorLeotardos cubiertos de polvoJubón de anillas dentadasPantalones rasgados"},
[795] = {title = "El Sello de la Tierra", objective = "El Sello de la Tierra se encuentra aquí, con tres hornacinas en las que se alojan la piedra rúnica de Amatista, la de Ópalo y la de Diamante.", description = nil, progress = "El Sello de la Tierra se encuentra aquí, con tres hornacinas en las que se alojan la piedra rúnica de Amatista, la de Ópalo y la de Diamante.", completion = "Al colocar las piedras rúnicas en las muescas correspondientes, un violento estruendo sacude la tierra y se rompe el Sello.", rewards = nil},
[804] = {title = "Sarkoth", objective = "Informa de la situación de Hana'zua a Gornek en El Cubil.", description = "La hazaña que has hecho por mí llena de valor mi corazón. ¡No puedo caer tan fácilmente! ¡Debo resistir!\n\nPero lo cierto es que no puedo hacer el camino de vuelta a El Cubil sin ayuda. Por favor, <nombre>, vuelve a El Cubil e informa a Gornek de mi situación. Quizás él pueda ayudarme.", progress = nil, completion = "Según tu descripción de la bestia creo que estás hablando de Sarkoth. No me extraña que pudiera con Hana'zua. Le prestaremos ayuda inmediatamente, ya no necesitas seguir preocupándote por su situación.\n\nPero hay una cosa que debo decir, me ha impresionado saber que acabaste con la vida de Sarkoth. Es una hazaña de la que estar orgulloso, &lt;nombre&gt;. Además, el haber luchado por el honor de un desconocido, dejando a un lado tus propias tareas, es, sin duda alguna, todo un signo de honor por tu parte.", rewards = "Podrás elegir una de estas recompensas:\n\nJubón de lana suaveLeotardos de anillas ajados por la batalla\n\nTambién recibirás:\n\nJubón de lana suaveLeotardos de anillas ajados por la batalla"},
[805] = {title = "Preséntate en el poblado Sen'jin", objective = "Habla con el maestro Gadrin en Poblado Sen'jin.", description = "Tus retos contra el Filo Ardiente han terminado... aquí, en el Valle. Pero quiero que informes sobre todo lo que has descubierto.\n\nVe al Poblado trol Sen'jin y busca al maestro Gadrin. Poblado Sen'jin está al este del valle, y a la derecha cuando llegues al cruce.\n\nInforma a Gadrin sobre el Filo Ardiente y dile que han alcanzado el Valle de los Retos. Con él, averigua si también han llegado a Sen'jin.\n\nEn marcha, <nombre>, y sé <rápido/rápida>. Temo que el mal del Aquelarre del Filo Ardiente solo sea el heraldo de una mayor amenaza...", progress = nil, completion = "Ummm... tu informe llega en mal momento. El Filo Ardiente no ha sido visto por aquí, en Poblado Sen'jin, pero su mal se ha arraigado en la costa, en las Islas del Eco.\n\nLos orcos son amigos de los trols Lanza Negra. Amigos honorables. Queremos ayudar a los orcos, pero... nosotros también necesitamos ayuda.", rewards = "Recibirás:\n\n"},
[806] = {title = "Oscuras tormentas", objective = "Lleva la zarpa de Fizzle a Orgnil Escaralma a Cerrotajo.", description = "¡No podemos permitir que el Filo Ardiente se establezca en Durotar! ¡Debemos destruirlo antes de que extienda su maldad!\n\nYo conduje mi propia investigación y averigüé que un brujo del Filo Ardiente, un goblin llamado Fizzle Darkstorm, ha acampado en el Monte del Trueno, al noroeste. Ahí es donde están propagando el caos él y sus esbirros.\n\nEncuentra a Fizzle y acaba con él. ¡Y tráeme una zarpa suya!", progress = "¿Encontraste a Fizzle, <nombre>? ¡Él y el resto del Filo Ardiente deben ser expulsados de nuestras tierras!", completion = "¡Ajá! ¡Lo encontraste!\n\nTu clan se enorgullece de ti, &lt;nombre&gt;. Y gracias a ti, Durotar se ha librado de un gran mal.", rewards = "Recibirás:\n\nBotas de pellejo de tigre"},
[808] = {title = "La calavera de Minshina", objective = "Recupera la calavera de Minshina del círculo de poder de las Islas del Eco. \n\nLlévasela al maestro Gadrin al Poblado Sen'jin.", description = "En mis sueños oigo la voz de mi hermano Minshina llamándome.\n\nSe lo llevó Zalazane, el brujo de las Islas del Eco que hay al este. Y ahora está muerto.\n\nPero la muerte no lo ha liberado. Zalazane utilizó su magia para encerrar el espíritu de Minshina en su propia calavera. En mi sueño, lo veo con otras calaveras, en un círculo de poder que está en la más grande de las Islas del Eco. Mientras siga allí, el alma de mi hermano estará maldita.\n\nTe lo ruego, <nombre>. Encuentra el círculo y recupera la calavera de Minshina. Luego tráemela.\n\n¡Libera a mi hermano!", progress = "¿Tienes la calavera de mi hermano, <nombre>? ¿Está libre al fin?", completion = "Gracias, &lt;nombre&gt;. Has salvado a Minshina. ¡Has salvado al espíritu de mi hermano de la esclavitud!", rewards = "Recibirás:\n\nCalavera con resplandor tenue"},
[809] = {title = "Ak'Zeloth", objective = "Habla con Ak'Zeloth en Los Baldíos.", description = "Desde la última Gran Guerra, aquella en la que la Legión Ardiente fue derrotada, he buscado fuentes de corrupción demoníaca entre los orcos. El collar que me has traído confirma mis temores.\n\nPertenece al Filo Ardiente, un culto que adora un objeto de gran poder demoníaco. Se llama Semilla del Demonio y se encuentra en Los Baldíos, en la Cima Calígine. ¡Debe ser destruida!\n\nVe a la Avanzada del Puente, en la frontera oeste de Los Baldíos y habla con mi ayudante, Ak'Zeloth. Él te guiará.", progress = nil, completion = "Neeru quiere ver destruida la Semilla del Demonio, ¿no es así? Extraño...\n\nMuy bien. Si quiere que desaparezca, te diré qué hay que hacer.", rewards = "Recibirás:\n\n"},
[812] = {title = "Se necesita un remedio", objective = "Encuentra a Kor'ghan en Orgrimmar y consigue el antídoto para venocola. Luego lleva el antídoto a Rhinag. Está cerca del límite noroeste de Durotar.", description = "<nombre>... llegas en el momento perfecto. Solo espero poder alabar tu velocidad también.\n\nTuve un descuido combatiendo con unos venocolas aquí cerca y uno de ellos me picó. Siento que su veneno me debilita por momentos. A este paso, calculo que me queda una hora de vida. Voy a necesitar tu ayuda...\n\nKor'ghan, de Orgrimmar, sabe cómo preparar el antídoto. Encuéntralo... y deprisa, <nombre>. No viviré mucho. Debería estar en el Circo de las Sombras.", progress = "Casi me alegro de no poder volver a Sen'jin en este momento. Todos se reirían de mi debilidad y mi estupidez.", completion = "&lt;nombre&gt;, me has salvado la vida. Gracias.\n\nPor favor, acepta esto. Espero que te ayude en tus viajes y, si no es así, quizás puedas sacar algún dinero vendiéndolo. Por lo que dices, yo no voy a volver a usarlo de momento. Kor'ghan va a hacerme participar en muchos de sus rituales hasta que le demuestre mi valía. Tendré que matar jabalíes, recoger más colas de escórpido... lo de siempre, vaya...", rewards = "Podrás elegir una de estas recompensas:\n\nRodela de cargaArmadura de escórpido ligero\n\nTambién recibirás:\n\nArmadura de escórpido ligeroRodela de carga"},
[813] = {title = "En busca del antídoto", objective = "Lleva 4 glándulas de veneno de venocola a Kor'ghan a Orgrimmar.", description = "Los venocolas son los escórpidos más mortíferos de Durotar. Rhinag no debería haber intentado cazar uno sin llevar el antídoto... pero ahora, lo último que necesita son mis reproches.\n\nTráeme unas cuantas glándulas de veneno de cualquiera de los ejemplares de venocola que encontrarás en la entrada de Orgrimmar y yo prepararé el antídoto.\n\nY date prisa, <nombre>. Uno de los nuestros necesita nuestra ayuda.", progress = "Recuerda que, sea cual sea la criatura que desees cazar, debes estudiar y entender su comportamiento. Ese conocimiento podría salvarte la vida.", completion = "Aquí tienes el antídoto, &lt;nombre&gt;. Llévaselo a Rhinag tan aprisa como puedas. Y sé prudente.", rewards = "Recibirás:\n\nAntídoto Venocola"},
[815] = {title = "Romper huevos", objective = "Lleva 3 huevos de colazote al cocinero Torka de Cerrotajo.", description = "¡Ay! Casi me olvido de que tengo que preparar el desayuno.\n\n<nombre>, muévete y tráeme unos huevos de Colazote. Necesitaré por lo menos tres si quiero tener suficientes para la comida de mañana.\n\nLos Colazote Garrasangre son animales fieros y protegen sus huevos con la vida. A veces los entierran a gran profundidad y es difícil sacarlos sin correr peligro. Si te diriges a las Islas del Eco, al sureste del Poblado Sen'jin, encontrarás muchos.", progress = "¡Desayuno, almuerzo, cena! ¿A quién le importa qué comida es? ¡Sea cual sea, alguien tiene que prepararla! Y ese alguien, soy yo.", completion = "Quizás seas débil, pero desde luego, no eres torpe, &lt;nombre&gt;.\n\nTen, coge esto y quítate de mi vista. Tengo que cocinar.", rewards = "Recibirás:\n\nMendrugo de pan duroCecina dura"},
[816] = {title = "Perdido, pero no olvidado", objective = "Lleva un indicio del destino de Kron a Misha Tor'kren, a la granja que hay al noroeste de Cerrotajo.", description = "<nombre>, por favor, ¿podrías ayudarme? Mi hijo Kron fue a cazar hace días y aún no ha regresado. Fue hacia el oeste, hacia Los Baldíos, a cazar crocoliscos en el Río Furia del Sur. Temo lo peor.\n\nEs muy fuerte pero su orgullo le mete siempre en líos. Yo le advertí de que los crocoliscos son fuertes y aviesos. Pero solo conseguí que se fuera enfadado.\n\nSi no puedes encontrarlo, al menos tráeme algún indicio de su suerte... aunque tengas que abrir el estómago de todos los crocoliscos de las orillas del Furia del Sur.", progress = "Rezo para que Kron vuelva, aunque creo que ya sé cuál ha sido su suerte.", completion = "Oh, mi hijo... mi querido hijo.\n\nMe he atormentado constantemente desde que se fue, pues no tenía noticias de su suerte. Ahora que sé lo que le ocurrió, quizás pueda empezar a llorarlo.\n\nGracias, &lt;nombre&gt;. Y ten, acepta esto. Iba a dárselo a Kron como regalo, cuando volviera triunfante. Pero ahora que sé que está muerto, no puedo ni mirarlo.", rewards = "Recibirás:\n\nCapa cosida a mano"},
[817] = {title = "Presa práctica", objective = "Lleva 4 pieles de tigres de Durotar a Colmillo Vel'rin al Poblado Sen'jin.", description = "Muchas de las pieles que usamos provienen de tigres de Durotar, <nombre>. Mantas, armaduras, tiendas... tenemos muchos motivos para cazar a esas bestias y muchos también para dejar que proliferen.\n\nPero ha llegado el momento de hacer una selección en la manada, por decirlo de algún modo. Nuestros números crecen y nuestras necesidades empiezan a superar nuestras reservas. Necesito más pieles para preparar los bienes que nuestra gente necesita.\n\nTráeme 4 pieles de tigres de Durotar. Te recompensaré. Puedes encontrarlos en las islas que hay al sur de aquí.", progress = "Aún recuerdo la primera vez que cacé un tigre. Elegí cazar en la más grande de las Islas del Eco. Me senté entre el follaje y esperé y esperé... casi un día entero.\n\nTenía los músculos en tensión y preparados para atacar. Y por fin un tigre mordió el anzuelo... fue una de mis mayores victorias.", completion = "Estoy satisfecho, &lt;nombre&gt;. Con tu ayuda, nuestra gente estará protegida y no pasará frío cuando cambie la estación.\n\nGracias.", rewards = "Recibirás:\n\n"},
[818] = {title = "Un espíritu responsable", objective = "Lleva 4 ojos de makrura intactos y 8 viales de moco de reptador al maestro Vornal al Poblado Sen'jin.", description = "Mis ojos empiezan a fallarme, pero aún veo con claridad. Y a menudo tengo que utilizar mis habilidades alquímicas para ayudarme en hechizos que antes me salían con facilidad. Pero me niego a adoptar un aprendiz. No he encontrado ningún trol ni orco merecedor de serlo.\n\n¿Tú crees valer lo suficiente? Sí, claro... Es evidente que lo crees.\n\nNecesito unas cosillas. ¿Crees que podrás conseguírmelas?\n\nNecesito ojos de makrura intactos y frascos de moco de reptador. Puedes conseguirlos de cualquier makrura o reptador de Durotar. Volveremos a hablar pronto.", progress = "El tiempo es quien realmente pondrá a prueba tu fuerza y tu voluntad. Si pierdes la paciencia o cedes a la debilidad, se revelará tu auténtico yo.", completion = "Bien hecho, joven. Bien hecho.", rewards = "Recibirás:\n\nPegamento muy pegajoso"},
[819] = {title = "El barril vacío de Chen", objective = "Encuentra a alguien que sepa algo sobre el barril vacío de Chen.", description = "Un cartelito en uno de los extremos del barril reza,\n\n\"Brebaje del trueno de Chen-\n\nQue tu ánimo se levante y con tu copa siempre brindes.\"", progress = "¿Sí?", completion = "Bueno, me...\n\nEste barril era de brebaje del trueno de Chen. Viajaba con Rexxar hace mucho tiempo. Hace siglos que no lo veo ni sé nada de él. Te agradezco que me hayas traído esto, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[821] = {title = "El barril vacío de Chen", objective = "Llévale 5 colmillos de león de la sabana, 5 riñones de zancudo de llanura y 1 cuerno de truenagarto a Cebador Drohn en Trinquete.", description = "¿Te gustaría probar el tocayo de Chen? Ah, el brebaje del trueno es poderoso. Chen le enseñó la receta a mi mentor y mi mentor, a su vez, me la transmitió. Necesitaré que me consigas unas cosas pero te lo aseguro: el subidón lo merece.\n\nTráeme 5 colmillos de leones de la sabana de cualquier león de la sabana, 5 riñones de zancudo de llanura de cualquier zancudo de llanura y un asta de truenagarto de cualquier especie de lagartrueno. Eso debería irnos perfectamente.\n\nPuedes encontrar estos ingredientes por Los Baldíos.", progress = "¿Cómo va la búsqueda?", completion = "Ja, ja… Nunca pensé que volvería a ver esto.\n\nHas despertado en mí la nostalgia, &lt;nombre&gt;. En cierto modo, me recuerda la última vez que preparé un lote de brebaje trogg. Gracias. Los recuerdos aportan calor a mi estómago.", rewards = "Recibirás:\n\nBrebaje del trueno"},
[822] = {title = "El barril vacío de Chen", objective = "Llévale 5 glándulas relampagueantes, 1 glándula de saliva de halcón trueno y 1 hígado de kodo a Cebador Drohn en Trinquete.", description = "De categoría ese brebaje del trueno, ¿eh?\n\nTengo otra receta que también aprendí de Chen. ¿Te interesaría? No llevará mucho tiempo prepararla y podría serte de utilidad si vas a seguir con tus aventuras.\n\nTráeme 5 glándulas de relámpago de cualquier tormentero, 1 glándula de saliva de un halcón trueno de los mayores halcones trueno y 1 hígado de kodo de cualquier kodo de Los Baldíos.\n\nComo te he dicho, este brebaje es fuerte; es el brebaje de los troggs del que te he hablado antes.", progress = "¿Estás trabajando mucho para encontrar esos ingredientes, <nombre>?", completion = "¡Ea! Sabía que no te resistirías a este brebaje. Vuelve cuando quieras si quieres más, &lt;nombre&gt;. Solo tienes que traerme más ingredientes como los que te pedí me consiguieras.", rewards = "Recibirás:\n\nBrebaje trogg"},
[823] = {title = "Informa a Orgnil", objective = "Habla con Orgnil Escaralma de Cerrotajo.", description = "En el Poblado Sen'jin no nos faltan problemas, pero te agradecemos tu ayuda. Claro que las noticias que traes del Filo Ardiente podrían significar más problemas para todo el mundo.\n\nEn Cerrotajo hay un orco llamado Orgnil Soulscar que se encarga de librar Durotar de males como el que representa el Filo Ardiente. Busca a Orgnil y háblale de nuestra situación. Cuéntale también las noticias que traes del Valle de los Retos. Querrá saber esas cosas.\n\nCerrotajo se encuentra al norte.", progress = nil, completion = "¿Sí? ¿Tienes algún informe? ¡Pues adelante, informa!", rewards = "Recibirás:\n\n"},
[824] = {title = "Je'neu del Anillo de la Tierra", objective = "Llévale el globo de agua podrida a Je'neu Sancrea de la Avanzada de Zoram'gar, Vallefresno.", description = "El Anillo de la Tierra es un grupo de chamanes dedicados al estudio y la conservación de los elementos. Les sobra conocimiento sobre lunáticos dioses antiguos, ancestrales leyendas y tradiciones como nunca alcanzará la mayoría de los eruditos.\n\nEstás de suerte, conozco a alguien del Anillo de la Tierra aquí en Vallefresno. Dirígete a la Avanzada de Zoram'gar, en la costa de La Ensenada de Zoram, y enséñale a Je'neu Sancrea lo que has encontrado. Si alguien es capaz de interpretar el significado del globo, es él.", progress = "Eh, hola. Me paso la mayor parte del tiempo estudiando el agua, en realidad todos los elementos, así que a veces ni me entero de que hay gente alrededor. \n\n¿Puedo hacer algo por ti?", completion = "Este globo... es de lo más inquietante. Muchos de los más poderosos elementales reciben su poder y su energía a través de tales dispositivos. Encontrar uno como este casi intacto es una cosa… encontrar uno tan contaminado es otra historia.\n\nMe ocuparé de que el Anillo de la Tierra estudie debidamente este globo. Quizás logremos poner fin a esta corrupción de los elementos. Acepta esto a cambio de haber traído este globo a la atención del Anillo de la Tierra.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de equilibristaEscudo lodo acumuladoManteo de sauce suave\n\nTambién recibirás:\n\nCinturón de equilibristaEscudo lodo acumuladoManteo de sauce suave"},
[825] = {title = "Los restos del naufragio…", objective = "Gar'Thok de Cerrotajo quiere que recuperes 3 herramientas gnómicas de los restos de naufragio de la costa.", description = "Uno de mis mejores rastreadores me ha informado de que los restos de la flota naufragada de Valiente aún están en la costa de Durotar, al este del Fuerte de Tiragarde.\n\nNo es un secreto que los humanos, aliados con esas mínimas criaturas llamadas enanos, han desarrollado sus conocimientos de mecánica. Debemos estudiar a fondo a todos nuestros enemigos potenciales. Y nosotros también nos beneficiaremos de sus conocimientos.\n\nNada entre los restos del naufragio, <nombre> y recupera las herramientas de la Alianza.", progress = "Cuando sepamos cómo funciona la extraña maquinaria de los humanos, tendremos más posibilidades de derrotarlos en futuras batallas.\n\nUna vez adquirido ese conocimiento, a la Horda solo le queda aumentar su fuerza.", completion = "Tu misión ha sido un éxito, &lt;clase&gt;. Me aseguraré de que estas herramientas lleguen a Orgrimmar en la primera caravana.\n\nBuen trabajo.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas llenas de barroMuñequeras CorrearenasFaja ancha metálica\n\nTambién recibirás:\n\nMuñequeras CorrearenasFaja ancha metálicaBotas llenas de barro"},
[826] = {title = "Zalazane", objective = "Derrota a Zalazane. \n\nMata a 8 trols de vudú y 8 trols maléficos. \n\nLleva la cabeza de Zalazane a Gadrin.", description = "Zalazane, el curandero brujo, vive en las Islas del Eco, hacia el este. En otro tiempo, esas islas eran nuestro hogar.\n\nDesde ellas, Zalazane envía a sus trols a la península, a maldecir a nuestra gente y a esclavizarnos bajo su dominio.\n\nHay que detenerle.\n\nDerrota a Zalazane y a sus esbirros, aquellos que fueron trols Lanza Negra. Tráeme su cabeza. Así sabré que su demoníaco reinado ha llegado a su fin.", progress = "¿Has derrotado a Zalazane, <nombre>?", completion = "Con Zalazane derrotado, nuestra tribu puede volver a dormir en paz.\n\nGracias, &lt;nombre&gt;. La tribu Lanza Negra está en deuda contigo. Si sufres alguna dolencia, visita a mi ayudante Bom'bay. Está detrás de mí. Domina el vudú...", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de peso ligeroMandiletes velados\n\nTambién recibirás:\n\nMandiletes veladosBotas de peso ligero"},
[827] = {title = "Roca del Cráneo", objective = "Recoge los collares ardientes de los fieles de Roca del Cráneo. \n\nLlévaselos a Margoz a su campamento.", description = "<nombre>, el Filo Ardiente ha infestado la cueva que hay al este de Orgrimmar, Roca del Cráneo. En esa cueva celebran sus inmundos rituales y queman su propia carne con collares ardientes.\n\nCreo que usando esos collares, los adeptos entran en sintonía con los poderes demoníacos. Pero para confirmarlo, necesito estudiar esos collares.\n\nVe a Roca del Cráneo y recoge los collares ardientes que lleven los adeptos. Tráemelos y yo descubriré su secreto.", progress = "¿Ya has entrado en la Roca del Cráneo, <nombre>? ¿Tienes los collares ardientes?", completion = "Bien. Estos collares contienen el secreto que esconde el Filo Ardiente. Y yo sacaré a la luz ese secreto...", rewards = "Podrás elegir una de estas recompensas:\n\nDaga dentadaEspada bastarda firmeMaza hiriente\n\nTambién recibirás:\n\nEspada bastarda firmeDaga dentadaMaza hiriente"},
[828] = {title = "Margoz", objective = "Habla con Margoz.", description = "Uno de nuestros chamanes, Margoz, sabe más sobre la corrupción del Filo Ardiente. Habla de una cueva de las montañas llamada Roca del Cráneo... ¡justo a las afueras de Orgrimmar! Esa cueva proporciona refugio a una banda de adeptos del Filo Ardiente.\n\nAntes de ir a Roca del Cráneo, habla con Margoz. Es sabio y su consejo es valioso.\n\nEstá acampado hacia el noreste, entre la costa y el Barranco Árido.\n\nSigue su consejo pero, diga lo que diga Margoz, <nombre>, ¡aún quiero que aplastes a esos adeptos!", progress = nil, completion = "Bienvenido, &lt;nombre&gt;. Fui informado de tu llegada y de tus hazañas en Durotar.\n\nEstás desarrollando tus habilidades... Te estás haciendo un nombre en este mundo, &lt;clase&gt;.\n\nMantente en el camino de la pureza y tendrás un gran futuro.", rewards = "Recibirás:\n\n"},
[829] = {title = "Neeru Hojafuego", objective = "Lleva un collar de muestra a Neeru Hojafuego a Orgrimmar.", description = "Los collares ardientes que me trajiste son potentes instrumentos demoníacos. Pero me temo que discernir su origen está más allá de mis habilidades chamánicas. Necesitamos que los estudie un brujo.\n\nLleva un collar ardiente a Neeru Hojafuego. Es un brujo muy capacitado que dice que utiliza sus poderes contra los demonios. Asegura que sus investigaciones de lo oculto son beneficiosas. Sea esto cierto o no, creo que necesitaremos su ayuda para luchar contra el culto demoníaco de Durotar.\n\nEncontrarás a Neeru en Orgrimmar, en el Circo de las Sombras.", progress = "Mis más humildes saludos, <clase>. ¿En qué puedo ayudar a mi hermano en un día como hoy?", completion = "¿De dónde has sacado esto? El buen Margoz de Cerrotajo te envió a mí, ¿verdad?\n\nBien, bien, deja que eche un vistazo...", rewards = "Recibirás:\n\n"},
[830] = {title = "Las órdenes del almirante", objective = "Lleva las órdenes del almirante Valiente a Gar'Thok a Cerrotajo.", description = "Abres el viejo y desgastado sobre y descubres un documento que parece oficial. Reconoces el sello del almirante Valiente.\n\nParece importante. Gar'Thok, el comandante de Cerrotajo, podría estar interesado en esta información.", progress = "Pareces preocupado, <clase>. ¿Qué tienes ahí?", completion = "Esto no presagia nada bueno. Ha sido muy inteligente llamar mi atención sobre esta información.", rewards = "Recibirás:\n\n"},
[831] = {title = "Las órdenes del almirante", objective = "Entrega las órdenes del almirante Valiente a Nazgrel, que está en la Cámara de Thrall en Orgrimmar.", description = "Los humanos no son de fiar. Combatimos a su lado con el corazón encogido, sabiendo que un día nos traicionarían.\n\nLa muerte del almirante Valiente no acabó con su legado de engaños. Los despreciables humanos tenían sus planes bien definidos incluso antes de su muerte.\n\nParece que el reinado de Benedicto no morirá con él. Quién sabe cuánto tiempo pasará antes de que los hombres de Valiente vuelvan a nuestras costas.\n\n¡Necesitamos hacer llegar estas órdenes a Nazgrel a Orgrimmar de inmediato! Lo encontrarás en la cámara de Thrall.", progress = "¿No ves que estoy ocupado? Espero que sea importante...", completion = "Fueron innumerables las veces que exhorté al Jefe de Guerra que no confiara en los humanos... Pero aquí no está en juego el orgullo personal.\n\nHas servido con honor a la Horda, joven &lt;clase&gt;.\n\nAhora debes perdonarme, pero he de consultar estos asuntos con Thrall de inmediato...", rewards = "Recibirás:\n\n"},
[832] = {title = "Sombras Ardientes", objective = "Lleva este ojo a Neeru Hojafuego.", description = "<Del colgante emanan unas palabras que se introducen en tu mente.>\n\n¿Gazz'uz?\n\nGazz'uz... ¡informa!\n\nHay noticias sobre tu descubrimiento en la Roca del Cráneo. ¡Debes prepararte para un ataque!\n\n¡Gazz'uz! ¡Te ordeno que hables! Habla. O me aseguraré de que llegue a Neeru Hojafuego la noticia de tu presencia y dejaré que descargue sobre ti su brutalidad.\n\nY no creo que quieras medirte con Neeru...\n\n¿Gazz'uz? ¿Estás ahí...?", progress = "Tienes que informar de algo importante. Puedo verlo en tus ojos.", completion = "&lt;Examina el Ojo de la Sombra Ardiente.&gt;\n\nIntrigante. ¿La voz que oíste mencionó mi nombre? Todo el mundo sabe que doy caza a los enemigos de nuestro Jefe de Guerra, pero me resulta extraño que se mencione mi nombre en concreto. Y aún es más extraño que los fieles del Filo Ardiente de los que conseguiste este colgante tengan un nombre tan parecido al mío.\n\nDebo estudiar este colgante. Debo estudiarlo y ponderar el significado de este mensaje.\n\nGracias por venir a mí, &lt;nombre&gt;. Has hecho un gran servicio a tu gente.", rewards = "Recibirás:\n\n"},
[833] = {title = "Cementerio sagrado", objective = "El guardián del Conocimiento Totemlluvia quiere que mates a 8 intrusos Erizapúas en Roca Roja.", description = "Solo los tauren más valientes reposan en Roca Roja, nuestro cementerio sagrado. Es un honor que concedemos a los valientes guerreros que fundaron y defendieron Cima del Trueno y a aquellos que han entregado la vida por el bien de sus tribus y sus jefes.\n\nPero una vil amenaza se cierne sobre nuestra tierra sagrada. Una banda de Erizapúas está arrasando el cementerio; yo ya estoy mayor para enfrentarme a ellos.\n\nHay que emplear la fuerza, <nombre>.", progress = nil, completion = "Muy bien. Esos Erizapúas se lo pensarán dos veces antes de volver a acercarse a este lugar sagrado.", rewards = "Recibirás:\n\n"},
[834] = {title = "Las arpías Viento Seco", objective = "Recupera 5 sacos de suministros y llévaselos a Rezlak que está cerca de Orgrimmar.", description = "Soy Rezlak, uno de los chicos de Gazlowe. El jefe me manda a ayudar a los orcos de Durotar. Las cosas han ido bien... excepto para las caravanas. ¡No consigo mantenerlas al seguro! Eso hace mi trabajo un poco más difícil, ¿sabes?\n\nEl último envío –que, dicho sea de paso, prometieron que llegaría– fue robado por arpías Viento Seco de... ¿de dónde era que eran? ¿El Cañón del Ventajo?\n\n¡Y si no consigo esos suministros no podré hacer nada! Sigue el cañón hacia el sur. Encontrarás una brecha en las paredes este y oeste.", progress = "Hago muy bien mi trabajo, no te confundas, ¡pero esto es ridículo! ¡¿Qué se supone que tengo que hacer si no tengo con qué trabajar?!", completion = "¡Vaya, justo lo que necesitaba! Nos has traído nuestros suministros. Es genial. Así podré volver al trabajo, en cuanto encuentre a alguien que cargue con estos sacos.", rewards = "Recibirás:\n\n"},
[835] = {title = "Asegurar las rutas", objective = "Mata a 12 salvajes Viento Seco y 8 brujas de la tormenta Viento Seco para Rezlak, que está cerca del Barranco Árido.", description = "Gazlowe me dijo una vez, \"Rezlak. En la vida hay que saber una cosa: que si quieres algo bien hecho, tienes que hacerlo tú mismo.\"\n\nPues bien, como no me puedo permitir perder todas las caravanas, voy a tomar las riendas del asunto. El Barranco Árido se encuentra al otro lado de la parte oriental del Cañón del Ventajo. Si matamos a todas las arpías, las rutas serán más seguras.\n\nY claro, Gazlowe me enseñó algo más: \"Todo tiene un precio.\" Así que, ¿vas a ayudarme, <nombre>?", progress = "Sé que es difícil de creer de un tipo como Gazlowe, pero sabía todo de todo. \"No temas dar ese gran paso\" –decía.\n\nLas arpías no dejarán de molestarnos y las caravanas seguirán siendo atacadas mientras no hagamos algo al respecto, ¿no te parece?", completion = "Creo que ya puedo respirar tranquilo, sabiendo que las harpías no volverán a atacar a nuestras caravanas de suministros. Y justo a tiempo, además. ¡Porque en el siguiente envío me van a llegar algunos juguetitos!\n\nSe te da bien lo que haces, &lt;nombre&gt;, deberías buscar a mi jefe en Trinquete. Seguro que él podrá darte más trabajo.\n\nAh, y no te preocupes, no me he olvidado de pagarte por el favor que me has hecho.", rewards = "Podrás elegir una de estas recompensas:\n\nMáquina para cortar alas de arpíaArco corto de nogalBastón de madera manchado\n\nTambién recibirás:\n\nArco corto de nogalMáquina para cortar alas de arpíaBastón de madera manchado"},
[836] = {title = "¡Rescata a OOX-09/HL!", objective = "Acompaña a OOX-09/HL a la orilla que hay pasados los Acantilados Dominantes e informa a Oglethorpe Obnoticus en Bahía del Botín.", description = "Vuelves a oír la voz de gnomo que sale del robot:\n\n\"Tengo que llevar a OOX-09/HL a un lugar abierto y seguro en el que pueda iniciar el largo proceso de despegue. Tiene un posicionador de serie, pero necesito tiempo para que vuelva a estar operativo. Lleva al robot a, digamos, la costa que está más allá de los Acantilados Dominantes. Allí tendré tiempo para prepararlo todo.\"\n\n\"Llévalo a la orilla y ven a verme a la Bahía del Botín. Oglethorpe Obnoticus. ¡Corto y cierro!\"", progress = nil, completion = "¡Ah, tú rescataste a OOX-09/TI! ¡No tengo palabras para expresar mi gratitud! Aún tengo que perfeccionarlo, ¡pero me has ahorrado muchas monedas de oro e incontables horas de trabajo! \n\nTe ruego que aceptes uno de estos objetos en recompensa. Mis robots y yo te estamos muy agradecidos, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de inventor gnómicoInstrumento de hundimiento gnómico\n\nTambién recibirás:\n\nBotas de inventor gnómicoInstrumento de hundimiento gnómico"},
[837] = {title = "Invasión", objective = "Mata a 4 jabaespines Crines de Acero, 4 rastreadores Crines de Acero, 4 correpolvos Crines de Acero y 4 guardias de batalla Crines de Acero para Gar'Thok, de Cerrotajo.", description = "Cuando llegamos, los jabaespines Crines de Acero poseían gran parte de la tierra... era una espinita que teníamos clavada. Con mucho esfuerzo hemos expulsado a la mayoría, pero aún quedan algunos bien fortificados en algunas zonas.\n\nPero esto ha durado ya demasiado. Por nuestro propio bien, no podemos permitir que haya Crines de Acero en nuestras tierras. Sus campamentos están al oeste de aquí. Busca las zarzas y los encontrarás. Hoy los echaremos de Durotar y mañana... puede que hasta de Kalimdor.", progress = "Aunque sí están dotados de una cierta inteligencia y capacidad de organización, me sigue asombrando que los jabaespines consiguieran conquistar y conservar tanto territorio de Kalimdor. Quizás tengan más recursos de los que les atribuía...", completion = "Haré correr la voz de que los jabaespines ya no están en sus guaridas y de que se puede prender fuego a sus estructuras. Erradicar a los Crines de Acero de Durotar es un gran paso para mejorar la seguridad de las fronteras de nuestro nuevo hogar.", rewards = "Recibirás:\n\n"},
[838] = {title = "Scholomance", objective = "Habla con el boticario Dithers en el Baluarte, en las Tierras de la Peste del Oeste.", description = "En la isla de Castel Darrow se encuentra Scholomance, una escuela que sirve como bastión de la Plaga. Nuestro enemigo habita en ella, y puede allí, con toda seguridad, seguir con sus investigaciones para reforzar su poder. Una puerta bloquea el camino y por más que lo intentemos no podremos entrar si no tenemos la llave adecuada.\n\nEl boticario Dithers está muy versado en todo lo relativo a Scholomance, y sabe cómo obtener una llave. Te has mostrado capaz de hacerlo por lo que te confío esta tarea. Habla con él sobre este asunto.", progress = nil, completion = "Sí, &lt;clase&gt;, el Sumo Ejecutor dijo que podrías ser aquel en quien confiar respecto a la llave. He dedicado una parte importante de mis recientes estudios a Scholomance y creo que sé una forma de que adquieras una llave que te permita cruzar la puerta delantera siempre que lo desees. \n\nLa tarea no será fácil, pero este tipo de tareas rara vez lo son. Y tú pareces saberlo ya a ciencia cierta.", rewards = "Recibirás:\n\n"},
[840] = {title = "Nuevo recluta de la Horda", objective = "Sigue la carretera occidental de Cerrotajo a Los Baldíos. Pasa el puente. \n\nDetente en la avanzada orca que hay al otro lado del puente. \n\nEntrega tu carta de reclutamiento a Kargal Marcaguerra, en la Avanzada de Los Baldíos.", description = "Mmm, pareces fuerte.\n\nEscucha, mi buen amigo Kargal necesita nuevos reclutas para una misión en Los Baldíos. Sé que quieres servir bien a la Horda. Pues esta es tu oportunidad.\n\nLleva esta carta de reclutamiento a Kargal, a ver si la firma.", progress = "¿Qué quieres, cachorrito? Si no estás aquí para alistarte, no tengo tiempo para ti.", completion = "¿Así que te manda Takrin? Es un rastreador excelente, no sé qué haría sin él.\n\nFirmaré tu carta de reclutamiento, pero lo que necesitamos es tener a alguien en El Cruce.", rewards = "Recibirás:\n\n"},
[841] = {title = "¿Otra fuente de energía?", objective = "Llévale 10 bolsas de agua de los Deambuladores al ingeniero jefe Silvaina, que está en Gadgetzan, para conseguir otra fuente de energía.", description = "Déjame adivinar... no has conseguido a tiempo lo que fuera que debías conseguir. No, mejor aún... ¡se te cayó en la arena! Bueno, sea lo que sea que ha pasado, tengo más fuentes de energía que puedo venderte. Sin embargo, esta vez te costarán diez, ¡SÍ, DIEZ bolsas de agua de los Deambuladores cada una!\n\nCorren tiempos difíciles y las fuentes de energía no crecen en los árboles. Mmm, ¿y qué si lo hicieran? Quizás podríamos HACER un árbol de fuentes de energía...", progress = "¿Y bien, conseguiste las 10 faltriqueras de agua?", completion = "Muy bien, aquí tienes otra fuente de alimentación. Te recomiendo que la utilices correctamente en esta ocasión, a menos que desees gastarte otras diez faltriqueras de agua en comprar una nueva.", rewards = "Recibirás:\n\nFuente de poder modelo 4711-FTZ"},
[842] = {title = "Servicio militar en El Cruce", objective = "Sigue la carretera occidental desde la Avanzada de Kargath. \n\nGira a la izquierda en la intersección y sigue la carretera hacia el sur. \n\nEncuentra a Sergra Espinoscura en el cruce de carreteras que hay en El Cruce.", description = "Lleva esta carta de reclutamiento firmada a El Cruce.\n\nSergra Darkthorn, maldita sea su estampa, es quien está al mando allí. Es una tipa desagradable que te enredará de mala manera antes de dejarte hacer nada útil. Thrall está convencido de que es especial, pero yo no lo tengo tan claro.\n\nTendrás que formarte tu propia opinión...", progress = "Vaya, vaya, vaya, ¿qué tenemos aquí? ¿Kargal me considera merecedora de otro recluta?", completion = "Muy bien, &lt;nombre&gt;. ¿Quieres ganarte un sustento en la Horda? Pues aquí hay mucho que hacer, así que escucha con atención lo que voy a decirte.\n\nVeo en tu mirada una insolencia que no pienso tolerar. El propio Thrall ha decretado que la Horda debe tratar a las mujeres como iguales. Si me faltas al respeto de cualquier manera, prepárate para conocer el dolor, el auténtico dolor.", rewards = "Recibirás:\n\n"},
[843] = {title = "La reclamación de Gann", objective = "Gann Picopiedra quiere que mates a 15 excavadores de Bael'dun y a 5 supervisores de Bael'Dun.\n\nLlévale el diario de Khazgorm a Gann Picopiedra.", description = "Solitario, errante voy por estas carreteras.\n\nLos enanos de Bael Modan no muestran el menor respeto por mi tierra. Unas tremendas explosiones provocan enormes socavones en el suelo mientras sus máquinas destrozan las colinas. Esos destructivos enanos han cribado las tierras que otrora fueron nuestro hogar y de las que se alimentaba mi tribu.\n\nTodo intento de solución diplomática ha fracasado. Ha llegado la hora de una acción decisiva.\n\nExpulsa a la fuerza a los enanos de la excavación de Bael Modan y tráeme el diario de su jefe, el prospector Khazgorm.", progress = "Muchos tauren inocentes perdieron la vida o fueron expulsados de su hogar ancestral cuando llegaron los enanos de Bael Modan. ¡Debemos recuperar mi tierra!", completion = "Khazgorm se encontró con el sino que se merecía. No siento remordimiento alguno por ese idiota. Era un infiel en estas tierras.\n\nQue su muerte sirva de advertencia a todos los que amenazan con perturbar nuestro modo de vida.\n\nY ahora dediquemos un tiempo a estudiar el diario del enano....", rewards = "Recibirás:\n\n"},
[844] = {title = "La amenaza de los zancudos de llanura", objective = "Reúne 7 picos de zancudos de llanura y llévaselos a Sergra Espinoscura en El Cruce.", description = "Tu primera presa será fácil.\n\nLos zancudos de llanura del este han estado acechando nuestros suministros de comida y se han convertido en un estorbo.\n\nAcaba con los zancudos de llanura y tráeme sus picos.", progress = "¿Has conseguido los picos de los zancudos de llanura?", completion = "Muy bien, &lt;nombre&gt;. Ahora que ya no están los zancudos de llanura, será más fácil mantener la línea de suministros. Y espero que hayas aprendido unos cuantos trucos en Los Baldíos porque tu próxima presa es un hueso más duro de roer que los zancudos de llanura...", rewards = "Recibirás:\n\n"},
[845] = {title = "El astacebra", objective = "Mata a corredores astacebras para reunir 4 pezuñas de astacebra para Sergra Espinoscura en El Cruce.", description = "El astacebra, aunque no sea la más feroz de las fieras de Los Baldíos, te dará algo más de trabajo que los zancudos de llanura. No te preocupes, ya te enviaremos a por presas más grandes en su justo momento, pero por ahora tendrás que hincarle el diente a algo.\n\nCaza el astacebra al norte y al sur y recoge sus pezuñas. Tráemelas y quizás la próxima vez te enviemos a enfrentarte a algo más difícil de cazar.", progress = "¿Cuántas astacebras has matado?", completion = "No está mal, &lt;nombre&gt;. Esas astacebras tienen unas patas muy fuertes. ¡Una patada bien dada puede derribar hasta a un tauren en cuclillas!\n\nPor lo visto, tú también eres fuerte. ¿Estás listo para un reto de verdad?", rewards = "Recibirás:\n\n"},
[846] = {title = "La venganza de Gann", objective = "Gann Picopiedra quiere que le traigas 6 viales de nitroglicerina, 6 fajos de pulpa de madera y 6 muestras de nitrato de sodio.", description = "Es evidente que el prospector dependía de los explosivos y de esos ruidosos vehículos para destrozar el suelo. Cabe esperar que pronto llegará otro capataz para sustituirlo.\n\nQuiero darles a los enanos un poco de su propia medicina. Según estos planos, si combinamos nitroglicerina con pasta de madera y nitrato sódico, podemos crear una carga explosiva. Es bien sabido que el fusilero, los soldados y los oficiales tienen los suministros.\n\nReúne estos objetos y tráemelos. ¡Voy a preparar una carga que los enanos no olvidarán!", progress = "Veo que has vuelto, <nombre>.\n\nAunque la muerte de Khazgorm pueda retrasar algo la excavación de mis tierras ancestrales, no acabará completamente con la destrucción.\n\nEl castillo de Bael Modan es una amenaza militar creciente. Construido para defender la excavación, está ahora repleto de tropas ofensivas.\n\nSi tienes los ingredientes que pedí, puedo confeccionar una carga que hará que esos precipitados enanos se replanteen sus acciones en Los Baldíos.", completion = "Veo que eres muy trabajador, &lt;clase&gt;.\n\nSin duda tendremos pronto una sorpresa apropiada para esos enanos.", rewards = "Recibirás:\n\n"},
[847] = {title = "La astucia del raptor", objective = "Habla con Tor'gan.", description = "Este... este poder... ¿Es este el poder de los chamanes que Tor'gan intentó enseñarme? Debo admitirlo, es interesante. Dile a Tor'gan que me ha convencido... por ahora. Escucharé lo que tenga que decir.", progress = nil, completion = "Cuando el encantamiento se pase, hablaré con Gor'mul. Te agradezco tu ayuda para devolver a Gor'mul a la tierra de los vivos. No me cabe la menor duda de que él también te lo agradece infinitamente.", rewards = "Recibirás:\n\nLlamada del raptor"},
[848] = {title = "Esporas de hongos", objective = "Llévale las 4 esporas de hongos al boticario Helbrim en El Cruce.", description = "Los oasis de Los Baldíos ocultan un misterio. La energía de la vida fluye por sus aguas, revitalizando a las plantas y a los animales que las beben.\n\nRevitalizando… y alterando.\n\nCerca de esos oasis crece un tipo de champiñón. Sus esporas tienen unas propiedades que nosotros, los boticarios de Lordaeron, encontramos útiles.\n\nTráeme esas esporas y te ganarás nuestra gratitud.", progress = "¿Tienes las esporas, <nombre>? Debo enviarle un preparado a mi socio de Cima del Trueno, para el que necesito las esporas...", completion = "Ah, sí. Son buenos ejemplares. Potentes.\n\nSoy un Renegado y siempre cumplimos nuestros contratos. Aquí tienes tu recompensa, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[849] = {title = "La venganza de Gann", objective = "Destruye la máquina voladora de Bael Modan y regresa junto a Gann Picopiedra.", description = "Según los planos de Khazgorm, no debería ser un problema preparar una carga explosiva.\n\nAh, eso es.\n\nBien, <nombre>, voy a confiarte una tarea especial. Lleva esta barra de explosivo y úsala para hacer saltar la máquina voladora de Bael Modan.\n\nLa máquina voladora no es solo una parte clave de la infraestructura del sitio sino que se usa además como máquina de guerra. Es más, constituye un icono de poder y un insulto a la memoria de mi tribu.\n\nDame un motivo de orgullo completando esta tarea heroica y recibirás justa recompensa.", progress = nil, completion = "¡Lo has hecho muy bien, &lt;nombre&gt;! Que en todo Cima del Trueno te veneren como un defensor de las sagradas tierras tauren y como campeón de la tribu perdida.", rewards = "Recibirás:\n\nAnillo tótem del clan"},
[850] = {title = "Los jefes de Kolkar", objective = "Llévale la cabeza de Barak a Regthar Portamorta, al oeste de El Cruce.", description = "Los centauros han acosado a los tauren durante años. Y recientemente, los centauros Kolkar de Los Baldíos se han convertido en una auténtica amenaza. No solían estar organizados pero han surgido nuevos jefes entre ellos. Y se han unido.\n\nPara mantener nuestras propiedades, hay que destruir a estos jefes de los centauros.\n\nTráeme la cabeza de Barak Aterrakodos. Su campamento está cerca de Las Charcas del Olvido hacia el norte.\n\nDerrótalo y vuelve aquí.", progress = "Hola, <nombre>. ¿Me traes la cabeza de Barak?", completion = "Muy bien, &lt;nombre&gt;. Los centauros son criaturas bajas y primarias pero algunos son listos y tienen visión para erigirse en líderes. Y estos son los más peligrosos.\n\nBarak era uno de estos centauros. Está muy bien que haya muerto.", rewards = "Recibirás:\n\n"},
[851] = {title = "Verog el Derviche", objective = "Llévale la cabeza de Verog a Regthar Portamorta, al oeste de El Cruce.", description = "El centauro Verog el Derviche deambula por Los Baldíos y no será fácil encontrarlo. Pero sabemos que su base está en la tienda de mando de los centauros en El Oasis Estancado hacia el sureste. Es posible que consigas atraerlo hacia ti.\n\nVe hasta El Oasis Estancado al sureste y ataca a centauros cerca de la tienda de mando. Será peligroso pero si matas a suficientes centauros, darán la alarma y Verog acudirá.\n\nTráeme su cabeza y la colocaré junto a la de Barak Aterrakodos.", progress = "¿Has encontrado a Verog, <nombre>?", completion = "Muy bien. Debes de haber causado mucho revuelo entre los centauros, nuestros guardias detectaron actividad cerca de El Oasis Estancado, y probablemente fueras tú.\n\nPuedes sentirte orgulloso por este acto heroico, &lt;nombre&gt;. Eres muy valiente.", rewards = "Recibirás:\n\n"},
[852] = {title = "Hezrul Marcasangre", objective = "Llévale la cabeza de Hezrul a Regthar Portamorta, al oeste de El Cruce.", description = "Hezrul Sanguinario es el jefe de los centauros Kolkar en Los Baldíos. Es feroz, brutal y taimado. Si lo derrotas, sería un golpe contundente para los Kolkar y la amenaza que suponen se vería reducida en gran medida.\n\nAsí que mátalo. Y al igual que con Barak y Verog, tráeme la cabeza de Hezrul.\n\nDirige a los suyos desde el Oasis Aguaverde, hacia el sur.", progress = "¿Ha sido derrotado Hezrul, <nombre>?", completion = "Eres $gdigno:digna de elogio, &lt;nombre&gt;. Estos centauros son indisciplinados y no pueden concentrar su furia como los orcos pero no son menos feroces. Al derrotarlos, has demostrado tu valentía.", rewards = "Recibirás:\n\nAnillo de caza recompensas"},
[853] = {title = "Boticaria Zamah", objective = "Llévale las esporas convertidas a la boticaria Zamah en Cima del Trueno antes de que se agote el límite de tiempo.", description = "He convertido las esporas que me diste en una emulsión. Debo enviársela a mi socia, la boticaria Zamah. Si se la llevas, seguro que tendrá algo para ti de nuestro fondo de productos alquímicos.\n\nSe encuentra en las Pozas de las Visiones, una caverna en el subsuelo de los chamanes de Cima del Trueno. El camino hacia esta caverna está muy bien escondido pero la encontrarás en el risco del Alto de los Espíritus.\n\nApúrate. Esta emulsión solo conservará su efectividad por poco tiempo y se debe procesar y volver a sellar antes de que eso ocurra.", progress = "¿Traes algo del boticario Helbrim?", completion = "Ah, las esporas de Los Baldíos que Helbrim andaba recolectando. ¿Te pidió ayuda para entregarlas? Bien.\n\nY la emulsión aún tiene fuerza, así que no has debido de perder el tiempo en la entrega. Muy bien, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nElixir de entereza menorElixir de agilidad menorPoción de Rejuvenecimiento menorPoción de velocidad\n\nTambién recibirás:\n\nCucharón de calderoPoción de Rejuvenecimiento menorElixir de agilidad menorElixir de entereza menorPoción de velocidad"},
[854] = {title = "Viaja a El Cruce", objective = "Habla con Thork en El Cruce, en Los Baldíos.", description = "Había vaticinado tu llegada, joven <clase>. Aunque quizás ya no seas tan joven.\n\nHa llegado el momento de que te internes más en estas tierras. Tu destino es conseguir grandes cosas.\n\nTienes que ir a El Cruce; nuestra vida está ligada a la de los orcos y necesitan ayuda. Sigue este camino hacia el este y después hacia el norte hasta El Cruce. Habla con Thork, que tiene la bendición de la Madre Tierra.", progress = nil, completion = "Me alegro de ver que nos llegan aliados incuso desde Cima del Trueno.", rewards = "Recibirás:\n\n"},
[855] = {title = "Los brazales de centauro", objective = "Llévale 15 brazales de centauro a Regthar Portamorta, al oeste de El Cruce.", description = "Los Kolkar son una amenaza para las pretensiones de la Horda en Los Baldíos; debemos reducir sus efectivos. Por eso, tengo una misión con recompensa para ti.\n\nVe al territorio de los Kolkar, ejecuta a sus guerreros y reúne brazales de centauro. Vuelve cuando tengas una pila de brazales y la Horda te recompensará como te mereces.", progress = "¿Cómo va la caza, <nombre>?", completion = "Bravo. Seguro que has sembrado el terror entre los centauros supervivientes, &lt;nombre&gt;. Si tienen algo de sensatez, se tranquilizarán y detendrán su avance.\n\nAunque no creo que lo hagan y es una lástima.\n\nUna lástima... para ellos.", rewards = "Podrás elegir una de estas recompensas:\n\nArco de batalla de orcoHacha puntiagudaMartillo madera de piedra\n\nTambién recibirás:\n\nHacha puntiagudaMartillo madera de piedraArco de batalla de orco"},
[857] = {title = "La Lágrima de las Lunas", objective = "Feegly el Exiliado quiere que le consigas la Lágrima de las Lunas.", description = "¡Yo tenía el poder de la energía! ¡La tenía en mis propias manos! ¡Y quiero recuperarla! ¡Cuanto antes!\n\nEsos canallas excavadores la descubrieron... era tal su hermosura. Se consideraba la Lágrima de las Lunas, fue el primer gran descubrimiento que hicimos en Bael Modan.\n\n¡Tenía que ser mía! ¡Mía y solo mía!\n\nAl general Trenzado lo carcomían los celos del poder que me daba. Así que la encerró en el castillo. ¡Se atrevió a decir que estaba maldita! ¡Ignorante!\n\n¡Otros la codiciaban y había que detenerlos! Ese malnacido de Trenzado me apartó.\n\nAhora quiero recuperarla. ¡Cuanto antes!", progress = "¿Dónde está la Lágrima de las Lunas? ¡La necesito ahora! ¡Huelga decirlo!", completion = "Por fin, ¡el poder vuelve a ser mío! ¡Mío y solo mío!", rewards = "Recibirás:\n\n"},
[858] = {title = "La llave de contacto", objective = "Consigue la llave de contacto y llévasela a Rabiaire.", description = "¿No te mandará Petardol? Estoy en un apuro. Me metí sin darme cuenta de que necesitaba una llave para desbloquear la columna que mueve la trituradora. Uno de los operarios de la trituradora me preguntó si todo iba bien ¡y me entró el pánico! En lugar de decirle que me faltaba la llave, le dije que había algún tipo de problema mecánico.\n\nTenemos que salir de aquí ¡a paso ligero! Ve a la sala de control en la parte de arriba de la grúa, el supervisor debería tener una llave para esta trituradora.\n\n¡Ayuda!", progress = "¡Rápido, tenemos que salir de aquí antes de que alguien sospeche algo!", completion = "Sí, esta parece la llave correcta. Permíteme que me asegure de que sé cómo funciona...\n\nA ver... control de rotación… Esto regula la velocidad y la estabilidad... buen control de movimiento del brazo. ¡Fíjate en esto! No puedo creer que Ventura y Cía. lograran diseñar algo tan superior a nuestro modelo. ¡Tengo que llevar esto a Trinquete!", rewards = "Recibirás:\n\n"},
[860] = {title = "Sergra Espinoscura", objective = "Habla con Serga Espinoscura en El Cruce.", description = "Si deseas practicar la caza debes dirigirte a Los Baldíos. Las bestias que allí habitan son tozudas y fuertes. Si cazas allí aprenderás mucho y tu espíritu crecerá.\n\nHabla con Sergra Espinoscura pues ella será tu primera guía en Los Baldíos.\n\nSergra está en El Cruce. Para llegar hasta allí, viaja hacia el este desde el Poblado Pezuña de Sangre hasta Los Baldíos y después dirígete hacia el norte cuando el camino se bifurque. Y ten mucho cuidado por el camino pues Los Baldíos pueden ser muy peligrosos para los incautos y confiados.", progress = nil, completion = "¿Deseas cazar en Los Baldíos?\n\nEres joven e irradias entusiasmo, pero todavía no estás preparado para perseguir a una presa por las llanuras de este lugar.\n\nDe todos modos, no te preocupes. Trabajaremos para fortalecer tu cuerpo y tu mente y aprenderás que cazar no solo consiste en encontrar y matar a tu presa.\n\nPrepárate pues tu viaje comienza aquí y ahora.", rewards = "Recibirás:\n\n"},
[861] = {title = "Los hábitos del cazador", objective = "Lleva 4 garras de merodeador a Melor Pezuñapétrea a Cima del Trueno.", description = "Te veo con ganas de explorar. Yo también era así antes...\n\nMe gustaba explorar y cazar. Salir a cazar es un gran honor para los tauren.\n\nSi quieres iniciarte en el arte de la caza, ve a ver a Melor Pezuñapétrea. Está en el Alto de los Cazadores, en Cima del Trueno.\n\nPara demostrarle que vas en serio, llévale garras de los merodeadores de las llanuras de Mulgore. Son peligrosos y astutos, digna presa de ti, <clase>.", progress = "Saludos. Presiento que vienes a pedirme instrucción, ¿me equivoco?", completion = "Ah, vienes de parte de un gran cazador con mucha experiencia; su cuerpo será viejo, pero su espíritu aún es fiero y nos honra contar con él.\n\nVeo por estas garras que tienes habilidad innata para la caza.\n\nSí, puede que ese sea tu camino.", rewards = "Recibirás:\n\n"},
[862] = {title = "Estofado de ratas de escavación", objective = "Llévale 8 ratas de madriguera a Grub al este de El Cruce.", description = "Un <clase> debe de pasar hambre por Los Baldíos. Al menos yo, me muero de hambre.\n\n¿Sabes qué? Voy a preparar un rico estofado. Solo necesito unas ratas de excavación.\n\nAl parecer, a esos condenados enanos de Bael Modan les sobra lo mejor de lo mejor. Dirígete hacia el sur a Bael Modan y tráeme unas ratas de excavación.\n\nNo eres digno de llamarte aventurero curtido ¡hasta que has pasado un tiempo matando ratas! ¡Andando!", progress = "¿Ya les has dado una paliza a suficientes ratas excavadoras, <nombre>?", completion = "Normalmente no como estos roedores cuando están tan... tan... a punto. Me gusta dejarlos pudrirse unas semanas para sacarle el máximo de sabor. Pero parecías tener hambre.\n\nTe prepararé un guiso de rata de madriguera. Ah, aquí está.\n\n¡Aquí tienes!", rewards = "Recibirás:\n\nReceta: estofado de ratas de excavaciónEstofado de ratas de excavaciónGuantes de boticario"},
[863] = {title = "La fuga", objective = "Protege a Rabiaire y la trituradora goblin robada cuando los lleves a Petardol en Trinquete.", description = "Supongo que aprenderé sobre la marcha... No puede ser muy difícil. Unos botones por aquí, una palanca o dos... Bueno, ¿<listo/lista>?", progress = nil, completion = "Con suerte conseguiremos algo de valor por el prototipo de trituradora estropeado... ¡se me acaba el tiempo! Enviaré un equipo de rescate donde se encuentra Rabiaire a paso ligero.\n\nOh, y aquí tienes algo por las molestias, claro.", rewards = "Podrás elegir una de estas recompensas:\n\nBastón flamígeroPantalones de Manitas grasiento\n\nTambién recibirás:\n\nBastón flamígeroPantalones de Manitas grasiento"},
[864] = {title = "De vuelta a la boticaria Zinge", objective = "Llévale el equipo de estudio de campo sellado a la boticaria Zinge, que está en Entrañas.", description = "Aquí tienes, la fuente de poder se ha quitado con seguridad y la información se ha mantenido intacta. Bueno, lo de viajes seguros y todo eso. Espero que este nefasto experimento científico haya funcionado para ti y todo eso.\n\nEl goblin se aleja para atender sus labores con la Compañía de Aguas de Gadgetzan. Cierras bien el paquete, en preparación de tu viaje a Entrañas. La boticaria Zinge te espera allí para probar el objeto.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nGuantes aptos para la cargaManteo de maestro boticarioHombreras de piel de conocimiento\n\nTambién recibirás:\n\nGuantes aptos para la cargaManteo de maestro boticarioHombreras de piel de conocimiento"},
[865] = {title = "Los cuernos de raptor", objective = "Recolecta 5 cuernos de raptor intactos de los segadores solescama y llévaselos a Mebok Mizzyrix en Trinquete.", description = "Los raptores de Los Baldíos son más inteligentes que los de cualquier otro sitio, y creo que se debe precisamente a sus cuernos. Si es así, quizás podría moler algunos para fabricar \"bebidas de inteligencia\". ¡Podría amasar fortunas!\n\nAhí entras tú: busca cuernos intactos de raptores segadores solescama. Se encuentran por Los Baldíos del Sur y en la parte norte, cerca de la frontera con Vallefresno.", progress = "¿Has conseguido los cuernos, <nombre>? ¡Ganaré una fortuna con ellos!", completion = "¡Genial, los tienes! Ahora solo necesito molerlos, mezclarlos con algo de vino y...", rewards = "Recibirás:\n\nPonche de raptorCapa de posadero"},
[866] = {title = "Recoge muestras de raíces", objective = "Llévale 8 muestras de raíces a Mebok Mizzyrix en Trinquete", description = "Las hierbas de Los Baldíos no son como las de otras regiones. Sus propiedades son casi idénticas pero el agua de oasis las altera por lo que hay diferencias apenas perceptibles.\n\nDeseo estudiar esas diferencias... ¡para ver si se les puede sacar partido!\n\nSi eres un entendido en botánica, recolecta muestras de raíces de las hierbas que encuentres en Los Baldíos. Tráeme esas muestras para que las pueda comparar con las de otras regiones.", progress = "Hola, <nombre>. ¿Has encontrado esas muestras de raíces?", completion = "Sí, sí, ¡hermosos ejemplares! Cultivaré estas raíces y las estudiaré. Espero descubrir nuevas propiedades. ¡Ocultas y valiosas propiedades...!\n\nY no te preocupes, si lo consigo, serás el primero en saberlo. ¡Porque necesitaré más!", rewards = "Recibirás:\n\nTúnica cubierta de esporas"},
[867] = {title = "Arpías saqueadoras", objective = "Reúne 8 talones de Alabruja.\n\nVuelve junto a Darsok Movedaga en El Cruce.", description = "Las malditas arpías han estado saqueando los suministros de nuestras caravanas demasiado tiempo. Es hora de rebanarles la garganta y acabar con unas cuantas.\n\nHe estado husmeando por sus nidos y me he enterado de quién manda a quién. El ataque más devastador destruirá una buena parte de su cadena inferior de mando y entonces podemos seguir hacia arriba.\n\nEmpezarás poquito a poco, ve a por las arpías Alabruja y las Malapluma Alabruja hacia el noroeste y tráeme 8 garras Alabruja. No debería estar mal para empezar...", progress = "¿Ya has cortado suficientes? Sigue cortando y llevándote trofeos. Quiero 8 talones de Alabruja.", completion = "Oh, hermosos ejemplares. Realmente hermosos... Buen trabajo, &lt;nombre&gt;. Espero volver a trabajar contigo en el futuro.", rewards = "Recibirás:\n\n"},
[868] = {title = "¡A cazar huevos!", objective = "Llévale 12 huevos de silítido y la cuchara excavadora a Korran en El Cruce.", description = "Hola, <nombre>. Mis maestros de Orgrimmar me han enviado a El Cruce para patrullar por estas tierras e informar de los acontecimientos.\n\nUno de los objetos de mis pesquisas son esos insectoides que se encuentran al sur de la Tierra de Gigantes. No sabemos mucho de estas criaturas, así que me he propuesto averiguar más. Parecen tener inteligencia, una inteligencia superior a la de los animales corrientes.\n\nToma esta cuchara excavadora y recolecta unos huevos de sus montículos, pero ándate con ojo: si se dan cuenta, te atacarán.", progress = "Cuidado con los huevos que encuentres, <nombre>. Si se quiebran, no me servirán.", completion = "Me gustas, &lt;nombre&gt;. Los huevos y la herramienta. Muy bien.\n\nY de recompensa…", rewards = "Podrás elegir una de estas recompensas:\n\nTogas de arlequínArmadura de escamas violeta\n\nTambién recibirás:\n\nArmadura de escamas violetaTogas de arlequín"},
[869] = {title = "Los raptores ladrones", objective = "Llévale 12 cabezas de raptor a Gazrog en El Cruce.", description = "No hace mucho tiempo, robaron un cargamento de plata de nuestra torre de vigilancia. Serviría para pagar los sueldos de los guardias de El Cruce así que queremos recuperar esa plata.\n\nLo extraño es que... atrapamos a uno de los ladrones la noche del robo. Y... ¡era un raptor! ¡Increíble!\n\nQue me aspen si sé para qué querrían los raptores la plata. Pero no me importa, solo quiero ver a esos raptores muertos ¡para que no vuelvan a robarnos!\n\nCaza a esos raptores en Los Baldíos. ¡Y tráeme sus cabezas!", progress = "¿Me traes esas cabezas, <nombre>?", completion = "¡Te has librado de los raptores!\n\nGracias, &lt;nombre&gt;. \n\nEres un &lt;clase&gt; de gran valía.", rewards = "Recibirás:\n\n"},
[870] = {title = "Las Charcas del Olvido", objective = "Informa a Tonga Runatótem con tus hallazgos.", description = "Antaño, Los Baldíos era un lugar exuberante, rebosante de vida. Pero la guerra y los cataclismos asolaron esa tierra, abrasándola y dejándola como una cáscara seca. Así son las cosas y siento gran pesadumbre en el corazón.\n\nPero en los últimos años, se han formado nuevos oasis en Los Baldíos y la vida vuelve a despertar. En lo más hondo, nosotros, los druidas, sentimos que se está filtrando una energía hacia la superficie.\n\nDebemos encontrar la fuente. Dirígete a Las Charcas del Olvido, al noroeste de El Cruce. Busca en sus aguas una fuente de energía y entonces vuelve aquí.", progress = "¿Has estado en Las Charcas del Olvido, <nombre>? ¿Has encontrado algo?", completion = "¿Has encontrado una grieta en la tierra?... ¿y salían gases burbujeantes? ¡Qué extraño! Esto podría ser la respuesta a nuestras preguntas pero aún no podemos estar seguros.\n\nAunque es una pista. Y te agradezco que la encontraras.", rewards = "Recibirás:\n\n"},
[871] = {title = "Acabar con los ataques de los Crines de Acero", objective = "De la tribu Crines de Acero, mata a 8 buscaquas, 8 tejespinas y 3 cazadores y vuelve junto a Thork en El Cruce.", description = "Todos los jabaespines son nuestros enemigos, <nombre>. Es solo que unos son aún más molestos que otros.\n\nLa tribu de los Crines de Acero ha estado atacando nuestras líneas de suministros procedentes de Durotar, causando un problema tras otro. Tengo a rastreadores buscando al jefe de estos asaltos pero hasta que lo logren, cualquier baja que podamos infligirles a esos malditos hombres jabalí nos vendrá bien.\n\nEmpieza por el noreste hacia Durotar. Reconocerás sus guaridas por las enormes vides con espinas que brotan de la tierra. Búscalos y ejecútalos.", progress = nil, completion = "Lo has hecho muy bien, &lt;nombre&gt;. Esos insolentes jabaespines al fin aprenderán que el poder de la Horda no se debe ignorar.", rewards = "Recibirás:\n\n"},
[872] = {title = "El fin de las hostilidades", objective = "Mata a 8 geománticos Crines de Acero, 8 defensores Crines de Acero y a Kreenig Gruñucico.\n\nLlévale entonces el colmillo de Kreenig Gruñucico a Thork en El Cruce.", description = "Uno de mis rastreadores presenció un ataque a una caravana de Durotar, <nombre>. Y como sospechábamos, los culpables son la tribu de jabaespines Crines de Acero. Uno de los jabaespines en concreto dirige los asaltos: Kreenig Gruñucico. Lo avistaron al noreste desde aquí, justo al sur de la carretera de Durotar. Si queremos acabar con la amenaza, Kreenig debe morir.\n\nPara asegurarnos de que estos ataques cesan, sin embargo, tenemos que responder con un insulto ante su ofensa. Mata a Kreenig y a los que puedas de su tribu y vuelve a mí cuando tengas su colmillo.", progress = "Córtale la cabeza al jefe y el caos se instaurará, <nombre>. Aprende bien esta lección. Te será útil en el futuro.\n\nLos jabalíes estarán perdidos sin su líder y podremos recuperar Los Baldíos.", completion = "Muy bien, &lt;nombre&gt;. Con la muerte de Kreenig, seguro que los ataques a nuestras caravanas se reducirán.\n\nTu pueblo debería estar orgullos de alguien como tú.", rewards = "Podrás elegir una de estas recompensas:\n\nFaja de vínculoCinturón cinchado\n\nTambién recibirás:\n\nFaja de vínculoCinturón cinchado"},
[873] = {title = "Isha Awak", objective = "Llévale el corazón de Isha Awak a Mahren Visioceleste.", description = "El gran Isha Awak es dueño y señor de estas aguas. Grande es su fuerza y solemne su orgullo. Los humanos de la costa lo temen, porque les ha infligido muchas bajas.\n\nPero no lo temo. Incluso doy las gracias por su presencia. Es un desafío digno y una presa honorable.\n\nSi estás <listo/lista>, nada mar adentro en busca de Isha Awak, la Condena Abisal.\n\nSu ánimo mora en su corazón y oírlo latir es conocer tu sino.", progress = "No temas a las aguas, <raza>.", completion = "Ahora eres un todo con Isha Awak, &lt;nombre&gt;. Su espíritu está contigo. Ronda en tus ojos...\n\nOjos que, algún día, aprenderán a ver.", rewards = "Podrás elegir una de estas recompensas:\n\nVara para marcarAmuleto del Valle\n\nTe van a lanzar el siguiente hechizo:\n\nFuerza de Isha Awak\n\nTambién recibirás:\n\nFaja de maestro de bestiasVara para marcarAmuleto del Valle"},
[874] = {title = "Mahren Visioceleste", objective = "Habla con Mahren Visioceleste.", description = "Has cazado las fieras de Los Baldíos. Eso demuestra fortaleza de espíritu. Pero <un cazador/una cazadora> siempre debe estar <preparado/preparada. $gUn auténtico cazador:Una auténtica cazadora> puede seguirle la pista a una presa en cualquier terreno. No se detendrá ante montañas ni ciénagas.\n\nNi siquiera ante el mar.\n\nEncuentra a mi hermana, Mahren. Se dedica a cazar a los grandes monstruos marinos de la costa. Te enseñará todo lo que hay que saber sobre ellos.\n\nPero ándate con cuidado cuando la busques, ya que la costa de Los Baldíos está en manos de los humanos.", progress = nil, completion = "Te envía mi hermano, ¿no? Le habrás demostrado lo que vales. Si te has ganado el respeto de Jorn, también tienes el mío.\n\n¿Estás listo para profundizar en el camino del cazador?", rewards = "Recibirás:\n\n"},
[875] = {title = "Las tenientes arpías", objective = "Reúne 6 anillos de tenientes arpías de las destripadoras Alabruja y llévaselos a Darsok Movedaga en El Cruce.", description = "Ajá, <nombre>. ¿Vienes a por más? Bien, así me gusta.\n\nEsta vez quiero que asesines a algunas de las tenientes arpías en Las Colinas Áridas. Solo las cazadoras Alabruja pueden ser tenientes y se las reconoce por el anillo que llevan. Son peligrosas pero dirigen a unos cuantos subalternos de esa zona. Si acabas con 6, será un golpe decisivo contra su matriarcado.\n\nAsegúrate de que sufren una muerte dolorosa, <nombre>. Queremos que esas muchachas arpías aprendan cuán descabellado es buscar pelea con la Horda.", progress = "¿Tienes ya 6 anillos de tenientes arpías? Deben sentir el peso de la justicia por sus actos viciosos contra la Horda.", completion = "Excelente trabajo, amigo. Creo que llegarás lejos con la Horda.", rewards = "Recibirás:\n\n"},
[876] = {title = "Serena Sangrepluma", objective = "Mata a Serena Sangrepluma y llévale su cabeza a Darsok Movedaga en El Cruce.", description = "Te ha ido tan bien con las tenientes y los subalternos que me gustaría pedirte una última cosa.\n\nSerena Sangrepluma es la hermana de una arpía que Rexxar ejecutó hace algún tiempo. Al parecer, estos ataques a las caravanas de la Horda son una venganza por la muerte de su hermana.\n\nNecesito que le cortes la garganta y me traigas su cabeza. Quiero colocarla en la próxima caravana que enviemos... Les dará que pensar a esas arpías.", progress = "No conseguirás nada hasta que vea la cabeza de Serena.", completion = "¡TOMA! ¡Muy bien, &lt;nombre&gt;! Un trabajo de primera… No estaba seguro de que estuvieras a la altura de la tarea pero has demostrado que se te da bien degollar. Gracias por ayudarnos a erradicar la epidemia de arpías. Aquí tienes tu recompensa, úsala bien.", rewards = "Podrás elegir una de estas recompensas:\n\nEspada corta eleganteDesollador de arpíaHacha Zhovur\n\nTambién recibirás:\n\nDesollador de arpíaHacha ZhovurEspada corta elegante"},
[877] = {title = "El Oasis Estancado", objective = "Vuelve junto a Tonga en El Cruce después de investigar El Oasis Estancado.", description = "Como la que encontraste en Las Charcas del Olvido, puede que haya más grietas en los otros oasis de Los Baldíos. En tal caso, a lo mejor tales grietas son la fuente de vida de los oasis.\n\nDebemos hacer una prueba.\n\nAquí tienes unas semillas. Están muertas y no son fértiles pero llévalas a El Oasis Estancado hacia el sureste. Si allí también hay una grieta, coloca las semillas en su interior… y observa..", progress = nil, completion = "¡Tu descubrimiento es increíble! Las semillas que te di estaban secas y muertas. Sea lo que sea lo que hay debajo de esos oasis ¡puede crear vida de la nada!\n\nDebemos seguir investigando...", rewards = "Recibirás:\n\n"},
[878] = {title = "Tribus en guerra", objective = "Mata a 6 buscaquas Lomopelo, 12 tejespinas Lomopelo y 12 geománticos Lomopelo y vuelve junto a Quebrantadientes en el Campamento Taurajo en Los Baldíos.", description = "Debes acabar con mis enemigos. <Gruñe.> Debes ayudar a los Quebrantadientes y a los Crines de Acero a debilitar a los Erizapúas. Ambas tribus son muy espirituales, vinculadas a la tierra y a su poder. <Gruñe.> Nos da fuerza en todo… especialmente en la guerra.\n\nSi quieres saber quién le causa realmente este suplicio a Thork <gruñe> y a la Horda, entonces ¡ayuda a los Quebrantadientes! Ataca a los líderes espirituales de los Erizapúas hacia el norte; debilítalos lo suficiente para que los Crines de Acero puedan vencerlos y dominar Los Baldíos.", progress = nil, completion = "Sí, sí... me llena de alegría &lt;gruñe&gt; los Erizapúas conocen el dolor de mi tribu, no-muerto. Así es cómo debería sentirme pero &lt;gruñe&gt; no es el caso.\n\nAyudas a tu pueblo y te rinden honores. Quebrantadientes ayuda a su pueblo ¿y qué hacen? Le dan la espalda. ¡Lo olvidan y lo abandonan para que se pudra en una jaula!\n\nIncluso los ayudo entre rejas ¡y no mandan a nadie a rescatarme!", rewards = "Recibirás:\n\n"},
[879] = {title = "Traidores", objective = "Mata a Nak, Kuz y Lok Aterraorcos y lleva sus calaveras a Quebrantadientes en el Campamento Taurajo en Los Baldíos.", description = "Tres miembros de mi tribu salieron del Horado para encabezar unas incursiones contra la Horda, <raza>. <Gruñe.> Son crueles y taimados, y si los derrotas, entonces El Cruce y la mayor parte de Los Baldíos conocerán la paz... <gruñe> al menos por lo que respecta a la tribu Crines de Acero <gruñe.>\n\nNak, Kuz y Lok Aterraorcos son a los que tienes que buscar. Se encuentran en un lugar remoto del sur de Los Baldíos. El primero es un echador de hechizos, el segundo es un rastreador y el último, su líder, llamado Aterraorcos <gruñe> es un guerrero. Mátalos, <raza>, como ellos me han matado a mí.", progress = "¿Cómo va la caza, <raza>? \n\nThork piensa que sabe la verdad sobre quién encabeza las incursiones contra su pueblo  pero se dará cuenta de hasta qué punto está equivocado si logras completar la tarea.\n\nMi muerte es ineludible pero le has infligido gran sufrimiento a mis enemigos, así que Quebrantadientes no se arrepiente de nada.", completion = "Ya está. Es mejor que acabe así, dice Quebrantadientes. &lt;Gruñe&gt; Tendrían que &lt;gruñe&gt; haberse asegurado de que me había muerto cuando me dieron la espalda en lugar de &lt;gruñe&gt; dejarme en esta jaula para renegados.\n\nTe falta solo una tarea que completar, &lt;raza&gt;.", rewards = "Recibirás:\n\n"},
[880] = {title = "Fuentes de vida", objective = "Llévale 8 caparazones de quijafortes alterados a Tonga Runatótem en El Cruce.", description = "Tu descubrimiento es increíble, <nombre>. Estos oasis tienen unas propiedades que deben de proceder de una fuente exterior. O acaso de una interior.\n\nNecesito saber cómo afectan estas grietas a los animales que beben del agua de los oasis.\n\nCaza a unas tortugas quijafortes en el Oasis Aguaverde y en El Oasis Estancado. Tráeme sus caparazones para que pueda examinarlos.", progress = "¿Cómo te va la recolección? ¿Has conseguido los caparazones?", completion = "Gracias, &lt;nombre&gt;. El estudio de las criaturas de una región puede enseñarte mucho de la misma. Veremos qué nos cuentan estos caparazones.\n\nAgradezco infinitamente tu ayuda... y espero aceptes estas monedas que podrían venirte bien. No las necesito.", rewards = "Recibirás:\n\n"},
[881] = {title = "Echeyakee", objective = "Llévale la piel de Echeyakee a Sergra Espinoscura en El Cruce.", description = "Albineblina, Echeyakee en la lengua de los Tauren, es el rey de los felinos de la sabana. Sigiloso al cazar, dicen que es como una neblina blanca y etérea sobre la tierra. Y tan raudo mata a sus presas que no tienen tiempo de sentir miedo ni dolor.\n\nLos tauren dicen que es muerte y misericordia a la vez.\n\nAveriguarás si es cierto, porque te envío a la caza de Echeyakee. Su guarida está al noreste de El Cruce, entre los huesos de un kodo gigante.\n\n Y ahora vete. Haz sonar este cuerno cuando hayas alcanzado su guarida. Hazlo sonar y él acudirá.", progress = "El gran felino quiere vierte, <nombre>.", completion = "Has derrotado a Echeyakee y aunque se acabaron sus días de caza... su espíritu te acompaña. Te enseñará la fuerza de la sutileza y el honor de la misericordia.\n\nAún te queda mucho camino por recorrer, &lt;clase&gt;. Esperemos que lo recorras sin percances.", rewards = "Te van a lanzar el siguiente hechizo:\n\nGracia de Echeyakee\n\nRecibirás:\n\n"},
[882] = {title = "Ishamuhale", objective = "Llévale el colmillo de Ishamuhale a Jorn en el Campamento Taurajo.", description = "Ishamuhale, Dientarpón, es el más feroz de los raptores solescama de Los Baldíos. No caza por deporte, ni por comida. Caza porque le apasiona la caza. Mata porque está en su naturaleza.\n\nY tendrás la oportunidad de conocer su naturaleza, <nombre>, porque tu camino sigue ahora las huellas de las garras de Ishamuhale.\n\nEmpieza la caza. Mata a su presa favorita, una astacebra, y trae entonces sus despojos al árbol muerto al noroeste de Trinquete. Ishamuhale olerá los despojos y esto lo atraerá.\n\nPrepárate para cuando llegue.", progress = "<nombre>, ¿has derrotado a Ishamuhale!", completion = "Este colmillo solo es una prenda pero lo que representa es profundo.\n\nLa fuerza de Ishamuhale está en ti, &lt;nombre&gt;. Úsala con moderación. Esa es tu carga.\n\nEse es tu honor.", rewards = "Te van a lanzar el siguiente hechizo:\n\nIra de Ishamuhale\n\nRecibirás:\n\n"},
[883] = {title = "Lakota'mani", objective = "Llévale la pezuña de Lakota'mani a Jorn en el Campamento Taurajo.", description = "La pezuña de Lakota'mani es un símbolo de la fuerza del gran kodo.", progress = "<nombre>, cuando te acercabas noté un renovado poder en tus pasos. Dime, ¿se ha reunido tu espíritu con el gran Lakota'mani?", completion = "\"Lakota'mani se llama \"Sacuditerra\" en nuestro idioma y sus pasos retumban como el trueno. Ahora está contigo y su valor se funde con el tuyo. Juntos no hay conflicto que no podáis acometer.\n\nAnda con la cabeza alta, &lt;nombre&gt;. Anda con la cabeza alta, Sacuditerra.”", rewards = "Te van a lanzar el siguiente hechizo:\n\nTrueno de Lakota'mani\n\nRecibirás:\n\n"},
[884] = {title = "Owatanka", objective = "Llévale la espina caudal de Owatanka a Jorn en el Campamento Taurajo.", description = "Al tocarla, saltan chispas de la espina caudal de Owatanka.", progress = "<nombre>, ¡detecto un nuevo poder en tu mirada! ¿A que te has reunido con el poderoso Owatanka?", completion = "Los tauren llaman \"Rayo Azul\" al truenagarto Owatanka porque en él reside una velocidad que no se espera de su tamaño. Se dice que un rayo lo alcanzó hace mucho tiempo y, alimentada por la furia del cielo, la energía de la bestia no conoce límites.\n\nQue su energía fluya por ti, &lt;nombre&gt;. Puesto que ahora igualas el paso de Owatanka.", rewards = "Te van a lanzar el siguiente hechizo:\n\nVelocidad de Owatanka\n\nRecibirás:\n\n"},
[885] = {title = "Washte Pawne", objective = "Llévale la pluma de Washte Pawne a Jorn Videncielo en el Campamento Taurajo.", description = "La pluma de Washte Pawne duele al tocarla.", progress = "Hay en ti una nueva determinación, <nombre>. Una determinación nacida del dolor...\n\n¿Te pesa la muerte de Washte Pawne?", completion = "El halcón trueno es una bestia rara en Los Baldíos y su muerte puede conllevar gran fortuna… o una terrible condena.\n\nPero Washte Pawne, llamado \"Muerdespíritu\" en lengua de los tauren puede no traer nada.... como acarrear ambos.\n\nEl dolor que has sentido al cazar a Muerdespíritu es la punzada que todos sienten al lograr grandes beneficios a un coste elevado. La punzada que sentiste es la punzada del remordimiento.\n\nPero veo que te has impuesto a eso. Has aprendido que el remordimiento se convierte en sabiduría y que nos fortalecemos a través del dolor.\n\nHas aprendido mucho, &lt;nombre&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nDeterminación de Washte Pawne\n\nRecibirás:\n\n"},
[886] = {title = "El oasis de Los Baldíos", objective = "Habla con Tonga Runatótem en El Cruce.", description = "Los druidas de Cima del Trueno sentimos la presencia de un extraño poder en Los Baldíos, al este de Mulgore.\n\nEnviamos a Tonga Runatótem para que descubra la fuente de dicho poder pero tememos que necesite ayuda.\n\nVe a Los Baldíos y habla con Tonga. Lo encontrarás en El Cruce. Para llegar hasta allí, sigue el camino que sale del Poblado Pezuña de Sangre en dirección este. Una vez en Los Baldíos, coge hacia el norte cuando el camino se bifurque. Continúa en dirección norte y encontrarás El Cruce.\n\nTen cuidado de no abandonar el camino, <nombre>, pues Los Baldíos es un terreno muy abrupto.", progress = nil, completion = "Me alegro de que mis hermanos de Cima del Trueno te hayan enviado a verme, joven &lt;clase&gt;, pues yo solo no soy capaz de desentrañar el misterio que se esconde en Los Baldíos.\n\nEsperemos que con tu ayuda seamos capaces de encontrar respuestas.", rewards = "Recibirás:\n\n"},
[887] = {title = "Los filibusteros del Mar del Sur", objective = "Mata a 12 brigadieres del Mar del Sur y a 6 cañoneros del Mar del Sur para Gazlowe en Trinquete.", description = "Por supuesto que me gustaría que el vicealmirante Grezzlik hiciera mejor su trabajo de mantener los mares seguros para nuestros navíos. Con todo el oro que los grandes comerciantes transportan en la flota mercante, se me llevan los demonios de ver a tanto pirata escaparse alegremente después de abordar mis barcos y robarme la mercancía!\n\nHa llegado a mis oídos que los filibusteros del Mar del Sur han establecido un campamento justo al sur de aquí. Han sido una verdadera espina clavada y si Grezzlik no se ocupa del asunto, bueno, quizás tú puedas ayudarme a librarme de ellos.", progress = nil, completion = "Lo has conseguido, &amp;gchaval:chavala;. Un poco más de trabajo igual de bueno y habrá una cosa menos por lo que preocuparse. Con suerte, se convencerán de que deben operar en otro lugar.\n\nMientras tanto, ¡está el asuntillo de recuperar la mercancía que me robaron!", rewards = "Recibirás:\n\n"},
[888] = {title = "El botín robado", objective = "Recupera el envío de botas y los telescopios a Gazlowe en Gazlowe.", description = "Aunque me gustaría recuperar toda mi mercancía, estoy seguro de que no es posible. Sin embargo, ¡es imprescindible que recupere unas cuantas cosas!\n\nComo puedes ver, mi observatorio está listo pero necesita la más pequeña de las dos lentes. La primera llegó de Durotar por caravana pero la segunda se envió por barco… y nunca llegó a puerto. ¡Y tampoco me ha llegado el envío por mar de Drizzlik desde Bahía del Botín!\n\nDirígete al campamento pirata y a ver si los puedes encontrar.", progress = "¿Has encontrado mis cosas, <nombre>?", completion = "Es un alivio recuperar todo esto, gracias por tu ayuda, &lt;nombre&gt;. ¿Sabes?, no me vendría mal un &lt;raza&gt; como tú para ayudarme aquí. Si cualquier día buscas trabajo, ¡recuerda lo bien que paga Gazlowe!", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes caminanteBotas de láminas acolchadas\n\nTambién recibirás:\n\nBotas de láminas acolchadasGuantes caminante"},
[889] = {title = "El espíritu del viento", objective = "Tráele a Quebrantadientes 10 fragmentos ensangrentados y te bendeciré con gran velocidad. Correrás con el astacebra y hasta el viento tendrá celos de ti.\n\nNo durará mucho pero debería ser lo suficiente para ayudarte a viajar por Los Baldíos.", description = nil, progress = "Tráele a Quebrantadientes 10 fragmentos ensangrentados y te bendeciré con gran velocidad. Correrás con el astacebra y hasta el viento tendrá celos de ti.\n\nNo durará mucho pero debería ser lo suficiente para ayudarte a viajar por Los Baldíos.", completion = "Bien. Que el espíritu de Agamaggan te guíe, &lt;raza&gt;. Vuelve aquí si deseas dominar aún más magia de Agamaggan.", rewards = "Aprenderás:\n\nEspíritu del viento"},
[890] = {title = "El envío perdido", objective = "Llévale el libro de contabilidad de Gazlowe a Vertipeluca, el maestro de muelles.", description = "¡Llevo siglos esperando mi último cargamento de mercancías de Bahía del Botín! Pondría la mano en el fuego a que lo robaron los filibusteros pero para asegurarnos, podrías acercarte hasta el muelle y preguntarle a Vertipeluca si ya han guardado mi mercancía en el almacén sin notificármelo.\n\nToma mi libro de contabilidad y dile a Vertipeluca que coteje mis datos de inventario con sus registros.", progress = "¿Mercancías para enviar en el siguiente barco, <nombre>?", completion = "Ah, Gazlowe te envía, ¿no? Solo un momento, déjame echar un vistazo para comprobar mis registros y asegurarme de que ningún envío de Gazlowe ha sido despachado al lugar equivocado.\n\nApuesto que espera esas botas de Drizzlek...", rewards = "Recibirás:\n\n"},
[891] = {title = "Las armas del Fuerte del Norte", objective = "El capitán Thalo'thas Solbrillante de Trinquete quiere que le consigas 10 medallasTheramore y que ejecutes a la capitán Justamonta, al cañonero Whessan y al cañonero Smythe.", description = "¡Ahí va otro barco perdido por culpa de esos aplicadísimos humanos de Fuerte del Norte!\n\nHay que ocuparse de esos malnacidos. Su paranoia los tiene exaltados. Con el gatillo fácil, esos imprudentes patanes disparan a todo lo que se les cruza por delante.\n\nDirígete al sur por La Costa Mercante hacia el bastión y sitia a sus tropas. Tráeme las medallas de sus soldados como prueba. Y por el amor de mi flota, ¡ejecuta a esa capitán Justamonta y a los fanáticos de sus cañoneros!", progress = "Mi flota está en peligro con la capitán Justamonta y sus malditos cañoneros disparando sin parar desde Fuerte del Norte. También quiero que sus soldados paguen por ello. Lléname la mano con las medallas de sus muertos y sabré que se ha vengado a mis soldados caídos.", completion = "¡Ay, se han acallado las justas armas de la Alianza! Se te recompensará por tu ayuda, &lt;nombre&gt;.\n\nAhora para atender la funesta tarea de recuperar los cuerpos de los muertos....", rewards = "Podrás elegir una de estas recompensas:\n\nMosquete de soldadoCalzones de lobo marino\n\nTambién recibirás:\n\nMosquete de soldadoCalzones de lobo marino"},
[892] = {title = "El envío perdido", objective = "Llévale el libro de contabilidad de Gazlowe a Gazlowe en Trinquete.", description = "Todo parece cuadrar según mis registros, <nombre>. Vuelve y dile a Gazlowe que no ha habido suerte. No me sorprendería nada que los piratas le hubiesen birlado ese material.\n\nNo puedo hacer nada más por él. Bueno, sí, podrías decirle que tengo unas cosas de Villabajo para él y que pase a recogerlas cuando tenga un rato.", progress = "Bueno, ¿qué te dijo Vertipeluca?", completion = "¿Sin dados? Entonces supongo que fue cosa de los filibusteros... Vertipeluca es limpio, estoy seguro de que no me haría trampa. Después de todo, está en nómina.\n\nParece que tengo que devolver algo de mercancía, &lt;nombre&gt;. ¿Qué me dices?", rewards = "Recibirás:\n\n"},
[893] = {title = "Una selección de armas", objective = "Llévale 1 puñal Crines de Acero, 1 varita Crines de Acero carbonizada y 1 escudo Crines de Acero a Tatternack Forjacero en Campamento Taurajo en Los Baldíos.", description = "Por lo que me han contado, los jabaespines Crines de Acero hacia el sur, más allá de la Tierra de Gigantes, no cuentan con herreros cualificados pero, al parecer, han empezado a desarrollar armas resistentes. Me gustaría ponerle la mano encima a varios tipos diferentes para aprender su técnica si fuera posible\n\nSi deseas contribuir con honor a la Horda, tráeme algunas muestras de sus armas. Encuentra una daga de sus acechadores, una varita de sus videntes y un escudo de su arsenal. ¡Con eso debería bastar!", progress = "Yo también dudo mucho que los patéticos jabaespines puedan alcanzar cierto nivel de habilidad en herrería pero sigo las órdenes del Jefe de Guerra y aprenderé lo que pueda de la tribu Crines de Acero.\n\nMe han encargado otras tareas ínfimas y lo haré de buen grado si ayuda al pueblo <raza>.", completion = "Excelentes, excelentes ejemplares, sí señor.\n\nLos jabaespines han avanzado más de lo que esperaba.\n\nAquí tienes, &lt;clase&gt;, llévate esto y desaparece. Más vale que examine de cerca estas armas. Puede que hayamos subestimado a nuestro enemigo y no podemos permitirnos esta clase de errores.", rewards = "Podrás elegir una de estas recompensas:\n\nMartillo de demoliciónLinterna eterna\n\nTambién recibirás:\n\nMartillo de demoliciónLinterna eterna"},
[894] = {title = "El churumbele", objective = "Accede a la consola de control del laboratorio de investigación de Ventura y Cía..\n\nObjeto provisto: \n", description = "Ventura y Cía. ha montado un pequeño centro de investigación muy hacia el norte desde aquí, al suroeste de El Fangal. No tengo muy claro qué hacen allí pero logré descubrir que están haciendo pruebas con algo llamado \"churumbele\".\n\nY digo yo, ¿qué leches es un churumbele? Bueno, sea lo que sea, quiero examinarlo, así necesito que alguien me lo consiga.\n\nConseguí una copia de su manual de instrucciones del sistema de control, gracias al cual deberías poder encontrar la forma de desconectar el churumbele.", progress = nil, completion = "Varios botones, palancas y lucecitas parpadeantes se encuentran de forma algo desordenada en el frontal de la consola de control. Un pequeño indicador sirve para ver si el aparato funciona dentro de los niveles óptimos y que las tres primeras válvulas de control están abiertas. Hay una pequeña cerradura en la parte inferior derecha del panel de control.", rewards = "Recibirás:\n\n"},
[895] = {title = "SE BUSCA: Barón Longavera", objective = "Llévale la cabeza del barón Longavera a Gazlowe en Trinquete.", description = "SE BUSCA\n\nBarón Longavera, capitán de El Temerario\n\nEl capitán Longavera de Gilneas está al mando de los buques de los filibusteros del Mar del Sur y se le imputan cargos de piratería. ¡Los restos deben estar en condición de ser identificados!\n\n-- Gazlowe", progress = "¿Qué ocurre? ¿Puedo ayudarte en algo? Mercancías que despachar, ¿acaso un trabajo de ingeniería?", completion = "¡Ah! Esa clase de negocio. ¿Sabes?, una vez vi El Temerario en mar abierto. Un barco imponente, o era el barco... ¡esos marineros son tan especiales!\n\nEn cualquier caso, ver ese buque deslizarse por el agua era un espectáculo. Me alegro que su miserable capitán ya no vuelva a causarme problemas.", rewards = "Recibirás:\n\n"},
[896] = {title = "La fortuna de un minero", objective = "Recupera la esmeralda de ojo felino de un vigía de Ventura y Cía. o de uno de sus gorilas para Vertipeluca, el maestro de muelles, en Trinquete.", description = "Como maestro de los muelles de un puerto tan activo como Trinquete, estoy al tanto de cualquier información. Lo sé todo del intercambio de mercancía y dinero entre este lugar y Bahía del Botín.\n\nLo último que he oído es sobre Mina Pedrusco de Ventura y Cía. al noreste de El Fangal. Uno de los mineros descubrió una esmeralda del tamaño de tu puño. Sé de unos cuantos compradores que estarían interesados en ponerle las manos encima y no me importaría ir a medias en la venta.", progress = "Si al menos supiéramos qué minero encontró la esmeralda, sería un paseo...", completion = "¡Fíjate en el tamaño de esa cosa, &lt;nombre&gt;! ¡Nos vamos a hacer ricos! A ver, según los tipos que he visto en las gemas que van a Villabajo, debería poder calcular tu parte, cincuenta por ciento, ¡no te preocupes!\n\nAhora veamos... Creo que esto debería bastar. ¡Un placer hacer negocios contigo, &lt;nombre&gt;!", rewards = "Recibirás:\n\nUn costal de monedas"},
[897] = {title = "El Cosechador", objective = "Llévale la cabeza del Cosechador a Jorn Visioceleste.", description = "Esta extraña criatura insectoide parece de un orden natural por fuera, como si no perteneciera a Los Baldíos.", progress = "Noto en ti cierto desasosiego, <nombre>. ¿Cómo van tus lecciones en Los Baldíos? ¿Te has encontrado con algo inquietante?", completion = "No puedo decir nada sobre la naturaleza de esta bestia ya que su espíritu la ha abandonado. Pero como has adivinado, no pertenece a Los Baldíos. Me temo que puede ser el precursor de un futuro sombrío para nuestro pueblo...\n\nPero por ahora, prepárate y esperemos que con el tiempo se desvele el misterio de esta criatura.", rewards = "Recibirás:\n\n"},
[898] = {title = "Escapar del fuerte", objective = "Escolta a Gilthares Ramafuego de vuelta hasta Trinquete con el Capitán Solbrillante.", description = "¡Por fin! ¡Alguien que viene a rescatarme!\n\nNo puedo creer que hayas logrado superar a los guardias. Estos fanáticos de Theramore están completamente desquiciados. Hundieron nuestro barco y a mí, el único superviviente, me encarcelaron. Me calificaron de enemigo y de amenaza para la Alianza.\n\n¡Yo! ¿Una amenaza para su gran farsa de Alianza? Solo era un marinero en una tontería de remesa entre Trinquete y Bahía del Botín.\n\nPero basta de charla. Me ayudarás a volver a Trinquete, ¿verdad? Dime cuando estés <listo/lista> y saldremos para allá.", progress = nil, completion = "¡Y pensar que creíamos que Bola de fuego había muerto hacía mucho tiempo!\n\nGracias a tu heroísmo te has granjeado un lugar de honor entre los soldados de Thalo'dan, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas con hebillasGuanteletes ribeteados\n\nTambién recibirás:\n\nBotas con hebillasGuanteletes ribeteados"},
[899] = {title = "Consumido por el odio", objective = "Llévale 60 colmillos de jabaespín Lomopelo a Mankrik en El Cruce.", description = "Quizás conozcas la aflicción que la duda causa, o quizás no, <clase>. Pero ten esto por seguro: aquí estoy cada día, desde que el sol nace hasta que se pone, escudriñando el horizonte en busca de monstruos. He matado a cada hombre jabalí que se me ha cruzado pero mi sed de su sangre está lejos de estar saciada. Quizás debería haberme dirigido hacia Taurajo en lugar de hacia El Cruce.\n\nTú, <clase>, tú podrías ayudarme.\n\nMátalos. Mata a tantos como puedas. Tráeme sus colmillos y celebraremos juntos su muerte.", progress = "El jabaespín pagará por esto, <nombre>. Lo juro.", completion = "&lt;clase&gt;, te has tomado en serio mi tarea y te lo agradezco.\n\nAunque persiste mi deseo de venganza, al menos puedo sonreír al ver que los jabaespines también han sentido dolor.", rewards = "Podrás elegir una de estas recompensas:\n\nManteo de cazador de jabalíesFajín de pradera\n\nTambién recibirás:\n\nManteo de cazador de jabalíesFajín de pradera"},
[900] = {title = "El churumbele", objective = "Cierra la válvula de control de combustible, la válvula reguladora y la válvula de control principal y usa entonces de nuevo la consola de control.", description = "Ahora las tres válvulas de control están abiertas. Por los indicadores del panel de control cabe suponer que se deberían apagar antes de que se pueda apagar el dispositivo.", progress = "Los pilotos parpadeantes en la consola de control indican que aún no se han cerrado las principales válvulas de control.", completion = "Las luces de las tres válvulas de control se apagan al cortarlas. Las luces verdes se vuelven poco a poco amarillas y el interruptor que controla el aparato puede ahora extraerse con las válvulas cerradas; se puede apagar.", rewards = "Recibirás:\n\n"},
[901] = {title = "El churumbele", objective = "Consigue la llave de la consola de Manitas Sniggles para usarla en la consola de control.", description = "Una vez apagado el aparato, se puede abrir el panel de control y extraer el churumbele de la consola. Con todo lo demás listo, solo queda conseguir la llave que desbloquea la consola.", progress = "Los pilotos de la consola están atenuados y los diales están todos a cero, todo está parado.", completion = "Al girar la llave en la cerradura, la consola de control queda libre. Se apaga una luz roja en la parte superior de la consola y no queda corriente en el terminal.", rewards = "Recibirás:\n\n"},
[902] = {title = "El churumbele", objective = "Llévale el churumbele a Petardol en Trinquete.", description = "Al abrir el panel frontal de la consola de control se accede al denso cableado, conducciones y otros extraños mecanismos, la mayoría de los cuales no parece tener ninguna función práctica. Si se apartan y se mira hacia el interior de la consola, se verá el churumbele. Basta con tirar de él para sacarlo de su compartimento.", progress = "¡Eh, has vuelto, <nombre>! ¿Tienes el churumbele?", completion = "Esto es… interesante, vaya. Me ocuparé de que lo envíen al puesto de mando del Gremio de Manitas de Villabajo. Estoy seguro de que después de desarmar el aparato con cuidado y estudiarlo, conseguiré entender cómo funciona.\n\nPero primero le echaré un vistazo...", rewards = "Podrás elegir una de estas recompensas:\n\nMartillo de ingenieroEscudo de soldadura\n\nTambién recibirás:\n\nMartillo de ingenieroEscudo de soldadura"},
[903] = {title = "Merodeadores de los Baldíos", objective = "Reúne 7 garras de merodeador de la sabana y llévaselas a Sergra Espinoscura en El Cruce.", description = "Bien, ha llegado la hora de ir a por algo de mayor calibre. Los merodeadores de Los Baldíos son una especie dura de roer. Resistentes, con recursos, taimados… y mortales. Los hallarás en las praderas altas. Encontrarás a muchos al suroeste de El Cruce.\n\nExtrema la precaución al cazarlos, <nombre>, o pasarás de depredador a presa.", progress = "¿Cómo va la caza, <nombre>? ¿Has encontrado los merodeadores?", completion = "Ah, veo que has estado ocupado, acechando en las praderas y cazando merodeadores. ¿Crees que es hora de enfrentarte a uno de los mejores ejemplares?", rewards = "Recibirás:\n\n"},
[905] = {title = "La ira de los segadores", objective = "Mata a raptores solescama y recoge sus plumas. Usa estas plumas en los 3 nidos de segador. Vuelve junto a Sergra Espinoscura en El Cruce.", description = "Bueno, <nombre>, la caza se intensifica. Ahora debes derrotar a la presa y encontrar el camino hacia su guarida.\n\nCaza a los raptores solescama hacia el sur. Ejecútalos y desplúmalos. Coloca las plumas en los nidos de segadores al suroeste de El Oasis Estancado. ¡Demuestra a sus semejantes que no les temes!", progress = nil, completion = "¿Conque has ido a los nidos y has colocado las plumas de tu presa en el interior? Hace falta valor, sin lugar a dudas.\n\n¿Te sientes capaz de algo más difícil aún, &lt;nombre&gt;? Porque pareces más fuerte que cuando viniste aquí la primera vez. Supongo que te están sentando bien Los Baldíos.", rewards = "Recibirás:\n\n"},
[906] = {title = "Traidores", objective = "Llévale la calavera de Lok a Thork en El Cruce, en Los Baldíos.", description = "Llévale la cabeza de Lok a Thork en El Cruce, <raza>. <Gruñe.> Debería saber lo que le ha ocurrido a mi tribu sin más explicaciones, ¡vaya!, y no es que él se creyera que los Quebrantadientes tuvieran nada que ver con <gruñe> tales cosas. Pero estoy seguro de que te recompensará por llevarle tan gran trofeo <gruñe.>\n\nSeguiré bendiciéndote con el poder de Agamaggan por todo el tiempo que permanezca en esta jaula, <raza>. Hasta entonces, adiós.", progress = "¡Sí! ¿Qué ocurre?", completion = "¿Has encontrado el jefe de los Crines de Acero y lo has matado, &lt;nombre&gt;? Asombroso.\n\nNo tengo palabras pero eso no significa que no esté agradecido. La gente de El Cruce harían bien en respetar tu fuerza.\n\nDebo admitirlo, creía que el jefe de estos asaltos a nuestras líneas de suministros era otro jabaespín. Gracias otra vez, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nTúnica de malla de cortezaTúnica de moho seco\n\nTambién recibirás:\n\nTúnica de malla de cortezaTúnica de moho seco"},
[907] = {title = "La rabia de los truenagartos", objective = "Llévale 3 unidades de sangre de truenagarto a Jorn Visioceleste en el Campamento Taurajo.", description = "Bien, <nombre>, ahora debes enfrentarte a los colosales truenagartos. Son fieras tan monumentales que el suelo de las llanuras tiembla bajo sus pisadas. Ten cuidado cuando luches contra ellos, un paso en falso y te aplastarían como a una hormiga.\n\nEncontrarás a los truenagartos en los Baldíos del Sur. Se llaman Morrotormenta, Tormentero y Astatrueno. Vuelve cuando los hayas vencido.", progress = "¿Has derrotado a los truenagartos, <nombre>?", completion = "¡Qué bien volver a verte con la prueba de la victoria! Y mejor aún ver que lo has logrado sin partirte ningún hueso.", rewards = "Recibirás:\n\n"},
[908] = {title = "Entre ruinas", objective = "Llévale el núcleo de las profundidades a Je'neu Sancrea de la Avanzada de Zoram'gar, Vallefresno.", description = "El Martillo Crepuscular se ha trasladado a las ruinas del Santuario Lunar de las Cavernas de Brazanegra. Su presencia solo puede servir para forzar a los elementos a actuar contra nosotros. Si no nos ocupamos de ella, esta región caerá en sus manos definitivamente.\n\n<nombre>, dirígete a Brazanegra y encuentra la piedra de las profundidades en las ruinas; debería estar cerca, en el agua. En su interior hay un núcleo de las profundidades, un dispositivo que cuando se lee correctamente, revela la historia de toda actividad elemental. Si lo consigo, y con la ayuda de los miembros del Anillo de la Tierra, ¡quizás podamos detenerlos!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[909] = {title = "Barón Aquanis", objective = "Llévale el extraño globo de agua a Je'neu Sancrea de la Avanzada de Zoram'gar, Vallefresno.", description = "El elemental de agua derrotado ha dejado tras de sí un extraño globo de agua. Supones que el objeto de alguna forma alimentó la energía de la bestia. En el interior del globo chapotea masa líquida repulsiva. El propio globo parece impermeable a todo tipo de fuerza física.\n\nSi alguien es capaz de interpretar este artilugio, es Je'neu Sancrea del Anillo de la Tierra. Se encuentra en la Avanzada de Zoram'gar de la Horda en Vallefresno.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nSable de foragidoDedo de bruja\n\nTambién recibirás:\n\nSable de foragidoDedo de bruja"},
[913] = {title = "El chillido del halcón trueno", objective = "Encuentra 1 halcón trueno, mátalo y llévale sus alas a Jorn Visioceleste en el Campamento Taurajo.", description = "<nombre>, el halcón trueno es un animal de lo más feroz. Ha llegado la hora de que te enfrentes a uno de ellos. Debes averiguar por donde andan y traerme sus alas como prueba de haber cazado a uno.\n\nHazlo y ya casi habrás acabado tus tareas conmigo.", progress = "¿Ya está? ¿Has matado al halcón trueno?", completion = "Tú, al igual que el halcón trueno, deberías sentirte orgulloso. Has derrotado a todos los enemigos que te hemos marcado y lo has hecho con fuerza, valor y honor.\n\nPero ahí no acaba tu camino, &lt;nombre&gt;. De hecho, te darás cuenta de que los verdaderos cazadores siempre van a más, siempre siguen su camino con el mismo orgullo que me has demostrado.\n\nAhora debes seguir adelante.", rewards = "Podrás elegir una de estas recompensas:\n\nRodela de cobaltoBastón de jinete del viento\n\nTambién recibirás:\n\nGuantes de la LunaRodela de cobaltoBastón de jinete del viento"},
[914] = {title = "Líderes del Colmillo", objective = "Lleva las gemas de Cobrahn, Anacondra, Pythas y Serpentis a Nara Bravacrín en Cima del Trueno.", description = "Los druidas de las Cuevas de los Lamentos, los Druidas del colmillo, son una aberración. Fueron parte de una orden de nobles druidas que pretendían sanar Los Baldíos, pero ahora tan solo pretender cambiar esta tierra para amoldarla a sus propios y retorcidos sueños.\n\nLos Druidas del comillo tienen cuatro líderes, y cada uno de ellos posee una gema de sueño. ¡Sus rostros aún me siguen atormentando! Derrota a los líderes y tráeme sus gemas. Así Los Baldíos recuperarán la paz.\n\nVete, <nombre>. Los encontrarás escondidos en las profundidades de las Cuevas de los Lamentos.", progress = "Los recuerdos de mis pesadillas me persiguen, <nombre>. ¿Has derrotado a los líderes del Colmillo y conseguido sus gemas?", completion = "Lo has conseguido, &lt;nombre&gt;. Mataste a los líderes de los Druidas del Colmillo. Mis sueños están libres de sus malvados rostros y has ayudado a salvar a Los Baldíos de un futuro maldito.\n\nTe lo agradezco, &lt;nombre&gt;. Te lo agradezco en mi nombre, en nombre de los druidas de Cima del Trueno y en nombre de todo el país.", rewards = "Podrás elegir una de estas recompensas:\n\nBastón crecienteEspada de ala\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nEspada de alaBastón creciente"},
[916] = {title = "El veneno de la Tejemadera", objective = "Lleva 10 glándulas de veneno de Tejemadera a Gilshalan Caminaviento a Aldrassil.", description = "He venido a Cañada Umbría para observar a las arañas tejemadera de la Gruta Narácnida. Son parientes de unas arañas mucho más pequeñas; creo que el Árbol del Mundo ha tenido un profundo efecto en ellas y quiero estudiar unos especímenes para confirmarlo. \n\nPara empezar, necesito su veneno. Ve a la Gruta Narácnida, que está al norte de aquí, y tráeme glándulas de veneno; así podré compararlo con el veneno de sus parientes más pequeñas.", progress = "¿Tienes las glándulas de veneno, <nombre>?", completion = "Gracias, &lt;nombre&gt;. Compararé este veneno con el de otras arañas en Darnassus. Creo que tiene propiedades relacionadas con el crecimiento de nuestro Árbol del Mundo.", rewards = "Podrás elegir una de estas recompensas:\n\nGran maza CardoleñoDaga CardoleñoBastón CardoleñoEspada Cardoleño\n\nTambién recibirás:\n\nDaga CardoleñoBastón CardoleñoEspada CardoleñoGran maza Cardoleño"},
[917] = {title = "Huevo de Tejemadera", objective = "Lleva 1 huevo de Tejemadera a Gilshalan a Aldrassil.", description = "Ahora que ya tengo el veneno, me gustaría tener unos especímenes. Pero no puedo pedirte que captures una araña gigante viva, joven <clase>. ¡Ni siquiera yo podría conseguirlo! \n\nPero si me traes huevos, al final tendré mis especímenes en un entorno controlado. \n\nTiene que haber un nido en el fondo de la Gruta Narácnida; ve a buscar un huevo y tráemelo.", progress = "¿Fuiste a la Gruta Narácnida? ¿Tienes el huevo de araña?", completion = "Estupendo. Voy a enviarlo a Darnassus, yo me voy a quedar investigando. No sabes cuánto te agradezco tu ayuda, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nEscudo de TierraleñaTúnica de TierraleñaTogas de Tierraleña\n\nTambién recibirás:\n\nTúnica de TierraleñaEscudo de TierraleñaTogas de Tierraleña"},
[918] = {title = "Semillas de Brezomadera", objective = "Llévale 8 semillas de Brezomadera a Denalan, que está en el Lago Al'Ameth.", description = "Los Brezomadera de Teldrassil son elementales de la naturaleza. En cierto modo, representan el orden natural de las plantas y los animales de nuestro gran árbol.\n\nPor eso es desagradable ver lo malhumorados que están últimamente.\n\nYo creo que es algo relacionado con la tierra. He estado trabajando en diferentes métodos de nutrición de plantas y me gustaría probarlos con semillas de Brezomadera. Por favor, ¿podrías recoger semillas de Brezomadera alrededor del Lago Al'Ameth y traérmelas?", progress = "¿Tienes las semillas? Estoy deseando plantarlas.", completion = "Las has conseguido. ¡Estupendo!\n\nPlantaré estas semillas en una tierra muy especial que he preparado. Creo que las semillas germinarán dando lugar a Brezomadera mucho más dóciles. ¡Quizás más adelante puedas ver el resultado!", rewards = "Recibirás:\n\nChampiñón de bosque"},
[919] = {title = "Brotes de Brezomadera", objective = "Llévale 12 brotes de Brezomadera a Denalan, que está en el Lago Al'Ameth.", description = "Están brotando pequeños Brezomadera alrededor de las aguas del Lago Al'Ameth. Me temo que ya no es posible ayudarlos por lo que deberíamos eliminarlos antes de que crezcan más y empiecen a causar problemas.\n\nCuando pases por el lago, si ves algún brote de Brezomadera, arráncalo. ¡Ayúdanos a mantener la tierra limpia!", progress = "Hola, <nombre>. ¿Has encontrado algún brote cerca del agua?", completion = "¡Esto es demasiado! Me temo que su número está creciendo peligrosamente. Espero poder descubrir qué está provocando su corrupción.\n\nGracias por tu ayuda, &lt;nombre&gt;. Esta tierra es un lugar más limpio gracias a tus esfuerzos.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de jardineríaBrazales de piedra gris\n\nTambién recibirás:\n\nGuantes de jardineríaBrazales de piedra gris"},
[920] = {title = "Las invocaciones de Tenaron", objective = "Habla con Tenaron Tormentagarre en Aldrassil, en Cañada Umbría.", description = "¿Eres <nombre>? He oído que Tenaron te estaba buscando. Está en la sala más alta de Aldrassil. \n\nAunque es mejor tomarse la vida con calma, no lo hagas esperar demasiado.", progress = nil, completion = "Ah, &lt;nombre&gt;, me alegro de verte. Tengo una importante misión para ti.", rewards = "Recibirás:\n\n"},
[921] = {title = "La corona de la Tierra", objective = "Lleva la ampolla de cristal llena a Tenaron Tormentagarre a Aldrassil.", description = "Es hora de que vayas a buscar tu destino, <nombre>. Pero antes de que puedas aventurarte más allá de nuestros bosques, te queda mucho que aprender sobre nuestra historia reciente. \n\nHan cambiado muchas cosas desde la Batalla del Monte Hyjal. Nordrassil es una sombra de lo que fue cuando tenía poder para derrotar a Archimonde y expulsar a la Legión Ardiente... \n\nTienes una tarea que cumplir: ve a la poza de la luna que hay al norte de Aldrassil y tráeme una ampolla de su agua.", progress = "Las pozas de la luna contienen las aguas del Pozo de la Eternidad, la antigua fuente de magia que tantos males ha traído a nuestro mundo.\n\nLos druidas usan las pozas y los centinelas las veneran como santuarios de Elune, pero la hechicería está prohibida.", completion = "Has oído lo que pasó justo después de la Batalla de El Monte Hyjal. Hay mucho más, pero la misión que te encomiendo seguirá vigente durante tu recorrido por Teldrassil y Darnassus.", rewards = "Recibirás:\n\n"},
[922] = {title = "Rellian Verdeguja", objective = "Llévale 1 semilla de Brezomadera a Rellian Verdeguja, que está en Darnassus.", description = "<nombre>, ¿puedes llevarle una de las semillas que me trajiste a mi amigo Rellian Verdeguja? Él es un druida de Darnassus y la última vez que hablamos me confesó su interés por mi trabajo con los Brezomadera. Incluso tenía algunas ideas así que supongo que le encantaría tener una semilla para poder experimentar con ella.\n\nGracias, <nombre>. Me has sido de gran ayuda. Espero que algún día puedas ver el fruto de mi esfuerzo.\n\nEncontrarás a Rellian Verdeguja paseando por el Enclave Cenarion de Darnassus.", progress = "Hola...\n\n¿En qué puedo ayudarte?", completion = "¡Ah, una semilla de Brezomadera! Quería cultivar una de estas semillas para ayudar a Denalan en sus investigaciones...\n\nHasta que descubrí que muchos de los Brezomadera están corruptos y, por lo tanto, también sus semillas. Yo no puedo hacer nada para curarlos.\n\nDenalan tiene mucha mano para las plantas y puede que encuentre una curación para los futuros Brezomadera. Puede que él sea su única esperanza.", rewards = "Recibirás:\n\n"},
[923] = {title = "Los tumores", objective = "Lleva 5 tumores musgosos a Rellian Verdeguja a Darnassus.", description = "Hay algo malvado creciendo en Brezomadera. Estamos intentando encontrar la fuente de ese mal, pero hasta que lo hagamos, debemos talar todos los Brezomadera que ya no tengan esperanza. Por el bien de Teldrassil. Los que vagan por el Lago Primigenio, al norte de Teldrassil, son los más afectados. ¡Hay que eliminarlos!\n\nDestruye todos los Brezomadera que encuentres ahí y recoge los tumores musgosos que crecen en ellos. Y tráelos para que podamos quemarlos.", progress = "¿Has ido al Lago Primigenio, <nombre>? ¿Has estado cazando a los Brezomadera de allí?", completion = "¡Bien hecho! Estos tumores son el síntoma de la enfermedad de los Brezomadera. Están llenos de un veneno que tenemos que erradicar de nuestra nueva tierra.\n\nYo me desharé de estos tumores. Gracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\nCuchillo de poda"},
[924] = {title = "La Semilla del Demonio", objective = "Coge 1 piedra de energía imperfecta. Llévalo al Altar de Fuego antes de caduque la piedra y vuelve entonces junto a Ak'Zeloth.", description = "La Semilla del Demonio es una poderosa herramienta para la Legión Ardiente. Se encuentra en una caverna en la Cima Calígine, sobre un altar de fuego demoníaco.\n\nHe preparado piedras de energía que cuando se colocan cerca de la semilla interfieren en su conexión con la Legión.\n\nLlévate una piedra de la mesa y llévala al altar de fuego en lo más alto de la Cima Calígine. Dirígete hacia el oeste, luego hacia el norte en la bifurcación. Verás la montaña a tu izquierda La cara norte es la más segura de escalar.\n\nY apúrate, la energía de la piedra se va agotando sola con el tiempo.", progress = nil, completion = "Has completado la tarea. Has destruido La Semilla del Demonio como Neeru pedía. Pero esto no es el fin...\n\nLa destrucción de La Semilla del Demonio envió oleadas de energía a través del éter, oleadas de colosal fuerza. Las he sentido y estoy seguro de que otros seres también las sintieron. Otros que se preguntarán por qué se les rompió el juguete...\n\nPero independientemente de los problemas futuros, tengo algo para ti. Neeru lo envió como pago. Y me pidió que te transmitiera su agradecimiento.\n\nAsí que gracias.", rewards = "Recibirás:\n\nArmadura de alma en pena"},
[926] = {title = "La piedra de energía imperfecta", objective = "Estas piedras están imbuidas de energía mágica pero dañada a propósito. Son muy inestables y una vez las movamos de la mesa, se autoconsumirán rápidamente.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nPiedra de energía imperfecta"},
[927] = {title = "El corazón musgoso", objective = "Llévale el corazón musgoso a Denalan, que está en el Lago Al'Ameth.", description = "El corazón de Negromusgo el Fétido está recubierto de un musgo oscuro y grasiento. De hecho, se sabe que es un corazón porque late lenta y rítmicamente... Incluso ahora se sigue oyendo su latido.\n\nPuede que Denalan quiera verlo.", progress = "¡<nombre>! ¿Tienes algo para mí?", completion = "¿Qué es esto? ¿¿El corazón de un Brezomadera?? ¡Está recubierto de un musgo repugnante!\n\nGracias por traerme esto, &lt;nombre&gt;. Examinaré el corazón y, con un poco de suerte, descubriré la naturaleza del musgo que lo recubre.", rewards = "Recibirás:\n\n"},
[928] = {title = "La corona de la Tierra", objective = "Lleva la vasija casi llena a Corithras Furia Lunar a Dolanaar.", description = "Aunque podría contarte mucho más de las pozas de la luna y de Teldrassil, debo dejarte ir; Corithras Furia Lunar te está esperando. He puesto el agua que has traído en esta vasija; llévasela. \n\nEstará en la poza de la luna de Dolanaar. Coge el camino del sur de Aldrassil al salir de Cañada Umbría y sigue los adoquines cuando el camino gira al oeste. \n\nTen cuidado, <nombre>. Últimamente hay criaturas muy peligrosas en el bosque.", progress = "Saludos, <clase>. ¿En qué puedo ayudarte?", completion = "Ah, vienes de parte de Tenaron. Bien, tenemos mucho de qué hablar, mucho que hacer y muy poco tiempo.\n\nVamos a empezar.", rewards = "Recibirás:\n\n"},
[929] = {title = "La corona de la Tierra", objective = "Llena la ampolla de jade y devuélvesela a Corithras Furia Lunar, que está en Dolanaar.", description = "Para empezar, deja que te cuente más sobre el cometido que debes realizar. Los druidas de Darnassus usan el agua de las pozas de la luna de Teldrassil, que deben rellenarse de vez en cuando. Usando estas ampollas especiales puedes recoger agua de las pozas de la luna.\n\nCoge esta vasija y ve hasta la poza de la luna que está a las afueras de la Aldea Brisa Estelar, al este. Llénala de agua y tráemela de vuelta. Cuando hayas completado tu tarea, te seguiré contando la historia que comenzó Tenaron.", progress = "Hola otra vez, <nombre>. ¿Has terminado tu tarea?", completion = "Tras la Batalla de El Monte Hyjal, estábamos perdidos. Nordrassil respiró el humo del fuego de la batalla y como consecuencia perdimos nuestra inmortalidad, ¡la verdadera esencia de nuestra existencia!\n\nEn ese tiempo, el Traidor quedó libre y Shan'do Tempestira desapareció. Fue una época aciaga para todos.", rewards = "Recibirás:\n\n"},
[930] = {title = "El fruto resplandeciente", objective = "Llévale el fruto resplandeciente a Denalan, que está en el Lago Al'Ameth.", description = "Un gran fruto redondo pende entre las hojas de esta resplandeciente planta.\n\nTienes la certeza de que a Denalan le gustaría analizarlo.", progress = "<nombre>, parece que tienes algo que contarme. ¿Sabes algo nuevo de los Brezomadera?", completion = "¿Y esto lo encontraste en Teldrassil? Fascinante... Se trata de un fruto exótico. Quizás sus semillas fueran traídas hasta aquí desde un lugar muy lejano. ¡Incluso tan lejano como Azeroth! Hay algo en este fruto... parece que ha reaccionado de modo extraño tras entrar en contacto con la tierra de Teldrassil.\n\nGracias, &lt;nombre&gt;. Ahora, si me disculpas, voy a analizarlo en detalle...", rewards = "Recibirás:\n\n"},
[931] = {title = "Flora luminosa", objective = "Llévale una hoja brillante a Denalan, que está en el Lago Al'Ameth.", description = "Las hojas de esta planta brillan con la luz del bosque, creando un aura intermitente.\n\nCrees que a Denalan le gustaría tener una muestra.", progress = "¿Tienes algo para mí?", completion = "¿De dónde has sacado esto? No veía una planta como esta desde aquella vez que estuve en el Pantano de las Penas... ¡hace décadas! Es increíble que haya llegado hasta Teldrassil, ¡y que haya alcanzado este tamaño!\n\nGracias, &lt;nombre&gt;. Perdona la brevedad de mis palabras pero me gustaría someter a esta hoja a una prueba...", rewards = "Recibirás:\n\n"},
[932] = {title = "Odio enraizado", objective = "Mata a Lord Melenas y llévale su cabeza a Tallonkai Cruzarraíz, que está en Dolanaar.", description = "Debo advertirte, <nombre>, de que este asunto debe quedar entre nosotros. Los sátiros ya son bastante vergüenza para nosotros, y este en concreto está demasiado cerca.\n\nSe llama Lord Melenas y habita en la cercana cueva de Roca Mácula, donde ha reunido a un numeroso grupo de guerreros grells. Su corazón es negro como la noche, mas lo que trama es incluso más repugnante.\n\nDebes encontrarle en su cercana cueva, al norte de aquí, y traerme su cabeza.", progress = "¿Has matado ya a Lord Melenas? Es muy importante que alguien se ocupe de él rápido y con discreción, <nombre>. Su existencia es una vergüenza para todos nosotros.", completion = "Con Lord Melenas criando malvas al fin puedo encargarme de otros asuntos. Gracias, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales feralesEnvoltorio de parra\n\nTambién recibirás:\n\nEnvoltorio de parraBrazales ferales"},
[933] = {title = "La corona de la Tierra", objective = "Llena la ampolla de turmalina y devuélvesela a Corithras Furia Lunar, que está en Dolanaar.", description = "Hay otra poza de la luna al sudeste de la entrada a Darnassus, a orillas de los Estanques de Arlithrien. Los centinelas están teniendo problemas para patrullar la zona debido a los ataques y a la presencia cada vez mayor de los intempestivos furbolgs Tuercepinos.\n\nTen cuidado cuando busques la poza y ten siempre a mano tus armas.", progress = "Al principio, no le dimos demasiada importancia a los ataques de los enloquecidos furbolgs. ¿Quién iba a pensar que los pacíficos hombres-oso sucumbirían a la cólera sin sentido? Este es uno de tantos problemas que tenemos en nuestro nuevo hogar.", completion = "Shan'do Tempestira nunca regresó y entre los druidas reinaba la confusión. Tanto es así, que a día de hoy todavía no sabemos qué fue de él. En ausencia de Malfurion, el archidruida Fandral Corzocelada asumió el liderazgo de los druidas y convenció al Círculo de los Ancestros de Costa Oscura de que había llegado el momento de reconstruir nuestro pueblo y recuperar la inmortalidad.\n\nCon la aprobación del Círculo, Corzocelada y los druidas más poderosos cultivaron a Teldrassil, el nuevo Árbol del Mundo.", rewards = "Recibirás:\n\n"},
[934] = {title = "La corona de la Tierra", objective = "Llena la ampolla de amatista y devuélvesela a Corithras Furia Lunar en Dolanaar.", description = "Sin embargo, no todo iba bien en Teldrassil. Corzocelada lo había planeado todo meticulosamente ya que el Árbol del Mundo había brotado según lo previsto, pero ocurrió un pequeño desajuste, desajuste al que se pueden atribuir muchos de los conflictos de Teldrassil.\n\nPero no entraré en detalles por ahora. Debes ir a la última Poza de la Luna, al noroeste de El Claro del Oráculo. Debajo de las ramas del Árbol del Oráculo se encuentra la primera y más poderosa de nuestras pozas. Llena una ampolla con agua de esta poza y tráemela.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[935] = {title = "La corona de la Tierra", objective = "Llévale la vasija llena al archidruida Fandral Corzocelada de Darnassus.", description = "Sin la bendición de Alexstrasza la Protectora y Nozdormu el Atemporal, el crecimiento de Teldrassil ha encontrado muchas trabas. Se han visto a extrañas bestias surgiendo de las mismas raíces del árbol, y los dementes furbolgs atacan a los viajeros que pasan a su lado.\n\nSolo espero que el archidruida encuentre lo antes posible la solución que anda buscando. Verteré todas las ampollas que trajiste en esta vasija para que puedas llevarla a Darnassus.\n\nEntrégasela a Fandral Corzocelada, que se encuentra en la Arboleda de los Druidas.", progress = "Yo no te he llamado, de eso estoy seguro, así que no puedo evitar preguntarme por qué has venido a hablar conmigo.\n\nSea lo que sea, acaba pronto.", completion = "¡Ah, sí! El agua que había pedido. Sin duda, Tenaron y Corithras se tomaron su tiempo en enviarla... quizás se deba a que no han elegido al mensajero más apropiado.\n\nNo obstante, ya puedo retomar mi trabajo. Los problemas que sufre Teldrassil son una pesada carga para mí, un yugo del que me gustaría liberarme cuanto antes.\n\nCoge esto, seguro que le encuentras alguna utilidad.", rewards = "Podrás elegir una de estas recompensas:\n\nArco bosquecenizaMartillo de matorral\n\nTambién recibirás:\n\nMartillo de matorralArco bosqueceniza"},
[936] = {title = "Ayuda al archidruida Runatótem", objective = "Habla con el archidruida Hamuul Tótem Rúnico en Cima del Trueno.", description = "¿Tienes un minuto, <clase>?\n\nEl Círculo Cenarion de Darnassus busca gente aventurera como tú para que les ayude en un proyecto vital de investigación. No conozco los detalles, pero has de saber que quien dirige el proyecto es el mismísimo archidruida Hamuul Tótem Rúnico.\n\nSi te interesa, ve a hablar con él en Alto de los Ancestros, en Cima del Trueno.", progress = nil, completion = "Saludos, &lt;clase&gt;. Me alegro de ver que estás interesado en colaborar en nuestra investigación. Queda mucho trabajo por delante y te mentiría si te dijese que no entraña peligro. Esperemos que esto no te haga desistir... aunque ya se ve que no es la primera vez que le plantas cara al peligro.", rewards = "Recibirás:\n\n"},
[937] = {title = "El claro encantado", objective = "Consigue 6 cinturones Sangrepluma y llévaselos a la centinela Arynia Quiebranubes a El Claro del Oráculo.", description = "Me enviaron aquí con un pequeño grupo de centinelas para proteger el Árbol del Oráculo de las arpías que han anidado en el claro. Poco a poco estamos intentando expulsarlas.\n\nCuando el Árbol del Oráculo envió a un mensajero a Darnassus con un informe, este fue atacado y asesinado por un grupo de arpías.\n\nSi te sientes <capacitado/capacitada> para esta tarea, ve a sus nidos y acaba con ellas, y después tráeme sus cinturones como prueba de ello.", progress = "No te resultará sencillo enfrentarte a sus potentes garras y a sus afilados picos con las facultades que posees, <nombre>, pero confío en que no fallarás en esta misión.", completion = "Me impresiona realmente ver lo que has conseguido en un plazo tan corto de tiempo, &lt;nombre&gt;, tanto que me gustaría pedirte que te quedaras para ayudarme... pero en el fondo de mi corazón sé que te esperan tareas más importantes.\n\nMe he dado cuenta de que el Árbol del Oráculo se ha despojado de una parte de su corteza. Seguro que hay algo en lo que puedas ayudarle, así que ve a hablar con él.", rewards = "Podrás elegir una de estas recompensas:\n\nFaja apretadaManteo chispeado\n\nTambién recibirás:\n\nManteo chispeadoFaja apretada"},
[938] = {title = "Niebla", objective = "Lleva a Niebla ante la centinela Arynia Quiebranubes que se encuentra en la poza de la luna situada cerca del Árbol del Oráculo.", description = "La piel de la dientes de sable es de un color gris característico que armoniza con el del bosque en el que se encuentra. Mientras permanece allí tendida, observas que está malherida, con profundas laceraciones en la espalda y el estómago.\n\nLevanta la cabeza y te observa fijamente, dejando ver a través de sus azules ojos la gran inteligencia que posee. Parece que desea seguirte.", progress = nil, completion = "¡Oh, muchas gracias, &lt;nombre&gt;! Tenía miedo de no volver a ver a Niebla nunca más y tener que llorar su muerte sin poder contar con su fiel compañía. Te debo más de lo que imaginas y tienes mi eterna gratitud.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales de cordónRodela de riscoCapa de explorador\n\nTambién recibirás:\n\nBrazales de cordónRodela de riscoCapa de explorador"},
[939] = {title = "La Flauta de Xavaric", objective = "Lleva la Flauta de Xavaric y 5 muestras de lazo maldito de Fuego de Jade a Eridan Vientoazul al Frondavil meridional.", description = "Estudias la sencilla flauta de madera que recogiste tras derrotar a Xavaric.\n\nLa madera parece sólida, pero cuando giras la flauta en tus manos, te da la sensación de que los dibujos y espirales de su superficie empiezan a retorcerse. Entonces te invade un repentino pavor...\n\nSacudes la cabeza <seguro/segura> de que tu mente te está jugando una mala pasada.\n\nPuede que otros sátiros tengan más pistas sobre lo que has encontrado. Lleva lo que has descubierto al druida Eridan Vientoazul que vive al sur de Frondavil.", progress = "¿Qué ocurre? Pareces preocupado.", completion = "Siento el sufrimiento de los ancestros, &lt;nombre&gt;.\n\nTocar estos objetos es casi insoportable...", rewards = "Recibirás:\n\n"},
[940] = {title = "Teldrassil", objective = "Llévale el informe del Árbol del Oráculo al archidruida Fandral Corzocelada de Darnassus.", description = "El bosque susurra tus osadas proezas, <nombre>. Una vez expulsadas las arpías de sus nidos, el bosque vuelve a estar en calma y las criaturas que en él habitan se sienten de nuevo seguras. Temía enviar a otro mensajero al archidruida pero ahora sé que tú le harás llegar mi mensaje sin problema.\n\nEntrégaselo y el bosque te ofrecerá su protección en los caminos que conducen hasta el Bosque de Piedra.", progress = "Mmm... El espíritu del bosque habita en tu interior, <clase>. ¿Qué te trae ante el archidruida de los Kaldorei?", completion = "Me preguntaba por qué el Árbol del Oráculo llevaba tanto tiempo sin ponerse en contacto conmigo. Parece que mientras unos problemas se solventan otros surgen para sembrar la inquietud.\n\nTemo que mi trabajo en Teldrassil nunca llegue a buen puerto y que nunca recuperemos nuestra inmortalidad.\n\nNo obstante, tú has cumplido el cometido asignado por el Árbol del Oráculo y deberías obtener una recompensa por tu diligencia.", rewards = "Recibirás:\n\n"},
[941] = {title = "Plantar el corazón", objective = "Coloca el corazón máculo en la maceta de Denalan.", description = "He quitado el musgo que recubría el corazón pero aun así sigue estando sucio...\n\nPonlo en mi maceta y veamos qué pasa. Contiene tierra nutritiva que podría limpiar y purificar el corazón.", progress = "Esta maceta contiene tierra que el propio Denalan ha seleccionado.", completion = "Colocas el corazón en el interior de la maceta ¡y él mismo se entierra!\n\nUnos segundos después, aparece limpio. Late lentamente... incitándote a cogerlo.", rewards = "Recibirás:\n\nCorazón Cuaderna purgado"},
[942] = {title = "El prospector despistado", objective = "Lleva el fósil misterioso al arqueólogo Gargavino al Puerto de Menethil.", description = "Este fósil tiene un parecido asombroso con uno que vi en Forjaz una vez. Un arqueólogo llamado Gargavino lo trajo a nuestra conferencia anual de la Liga de Exploradores. Creía que el fósil encerraba un poder y que este podría liberarse de alguna forma.\n\nLo último que supe de Gargavino es que se había refugiado en el Mesón Aguahonda, en el Puerto de Menethil y que esperaba una escolta para llegar a la Excavación de Whelgar. Búscalo y muéstrale el fósil de Remtravel. Quizás él consiga revelar los secretos del pasado.\n\nEl barco para Menethil zarpa de Auberdine.", progress = "¿Qué tienes ahí?", completion = "¡Vaya! ¿Dices que esto viene de las lejanas tierras de Kalimdor?\n\n¡Increíble! ¡Sencillamente increíble!", rewards = "Recibirás:\n\n"},
[943] = {title = "El prospector despistado", objective = "El arqueólogo Gargavino, del Puerto de Menethil, quiere que le lleves la Piedra de Relu y el fósil de Gargavino.", description = "El fósil original lo tiene ahora el prospector Whelgar, en su excavación de Los Humedales. Cuando descubrí la Piedra de Relu, creía que era la clave para desvelar el misterio del fósil.\n\nCuando me dirigía a reunirme con Whelgar, fui atacado por raptors y perdí la reliquia. No sé cuál de esas bestias se tragó la reliquia pero, si la recuperas, podré liberar el poder de esos artefactos.\n\nTráeme la Piedra de Relu y el fósil original.", progress = "Con la Piedra de Relu podré desvelar los secretos de mi fósil así como del fósil de Costa Oscura de Tripirrem, <nombre>.", completion = "¡Excelente, &lt;nombre&gt;!\n\nAhora hay que ver si puedo utilizar la piedra para descubrir los poderes de los fósiles.", rewards = "Podrás elegir una de estas recompensas:\n\nHoja reliquiaAstillacalaveras\n\nTambién recibirás:\n\nAstillacalaverasHoja reliquia"},
[944] = {title = "El Glaive del Maestro", objective = "Reúne información y luego utiliza una ampolla de adivinación para crear un cuenco de visión. Utiliza el cuenco para hablar con Onu.", description = "Es extraño que haya naga aquí, en Costa Oscura. Pero aún más extraño es... lo del Azote del Ocaso. Ese culto adora a los antiguos Señores de la Tierra, que fueron derrotados hace mucho tiempo.\n\nUno de ellos cayó en El Glaive del Maestro, al sur de aquí.\n\nVe allí y busca a más adeptos del el Azote del Ocaso.\n\nLlévate esta ampolla de adivinación. Utilízala para crear un cuenco de visión cuando encuentres El Glaive del Maestro. Y luego utilízalo para hablar conmigo.", progress = nil, completion = "¿El Azote del Ocaso está en El Glaive del Maestro?\n\nQué desventura.\n\nEl lord que fue empalado en El Glaive lleva mucho tiempo muerto, pero eso no quiere decir que no queden aún trazas de su poder.\n\nEl Azote del Ocaso debe de estar buscando ese poder.", rewards = "Recibirás:\n\n"},
[945] = {title = "La fuga de Therylune", objective = "Ayuda a escapar a Therylune y luego dile a Therysil, que está en el Santuario de Aessina, que su hermana está a salvo.", description = "¡Socorro!\n\nEstaba paseando por El Glaive del Maestro cuando esos asquerosos del Culto me rodearon. ¡Menos mal que se me da bien esconderme!\n\n¿Puedes ayudarme a salir de aquí? También necesito avisar a mi hermana Therysil de que estoy bien. Está al sur, en Vallefresno, en el Santuario de Aessina.", progress = nil, completion = "¿¿Que mi hermana estaba dónde?? Sé que a Therylune no le importa ensuciarse, ¡pero ir a El Glaive del Maestro! Incluso para ella es un largo camino... y El Glaive es un lugar malsano...\n\nEn fin, gracias, &lt;nombre&gt;. Ha sido un detalle por tu parte informarme de que está bien.", rewards = "Recibirás:\n\n"},
[947] = {title = "La cueva de los champiñones", objective = "Lleva 5 Lecinums y 1 seta mortal a Barithras Sombraluna a Auberdine.", description = "Estoy ideando una poción para la que necesito unos champiñones muy raros, unos que solo crecen en una cueva determinada. Esa cueva se encuentra más allá del Salto de Fonroca, al este y un poco al norte por las montañas.\n\nIría hasta allí en persona, pero en el Páramo de los Ancestros me aconsejaron que me mantuviera lejos de ese lugar. Nuestros venerables aliados creen que la cueva es el refugio de un nuevo mal de Costa Oscura.\n\nTe lo ruego, <nombre>, tráeme algunos de esos champiñones. Y aprovecha para explorar la cueva y confirmar los temores de los Ancestros.", progress = "¿Tienes mis champiñones, <nombre>? ¿Has estado en la cueva?", completion = "Muchas gracias, &lt;nombre&gt;. ¡Estos champiñones son unos especímenes estupendos!\n\nOye, mientras estabas en el Salto de Fonroca, ¿encontraste algo que confirmara las preocupaciones de los Ancestros?\n\nLos Ancestros son sabios, pero esperaba que esta vez estuvieran equivocados.", rewards = "Recibirás:\n\nCapa de entrañasChampiñón con motas rojas"},
[948] = {title = "Onus", objective = "Ve al Páramo de los Ancestros y habla con Onu.", description = "Onus, un Ancestro de Conocimiento del Páramo de los Ancestros, ha sabido de tu viaje a Salto de Fonroca y desea hablar contigo. El Páramo se encuentra hacia el sur, cerca de las montañas.\n\nLos Ancestros son pacientes y sabios, <nombre>. Si Onus busca tu consejo sobre lo que viste en el Salto, temo que la cosa es urgente.", progress = nil, completion = "&lt;nombre&gt;. Estás aquí.\n\nBien.\n\nTenemos asuntos que discutir tú y yo.", rewards = "Recibirás:\n\n"},
[949] = {title = "El campamento del Azote del Ocaso", objective = "Encuentra una pista en el campamento del Azote del Ocaso en El Glaive del Maestro.", description = "No se sabe qué motivos han llevado al Azote del Ocaso a El Glaive del Maestro. Pero sus motivos son, sin duda, oscuros.\n\nDebes ir a su campamento de El Glaive. Busca algo que pueda arrojar un poco de luz sobre sus intenciones.\n\nPorque debemos saber cuáles son antes de preparar un plan.", progress = nil, completion = "Este libro fue manuscrito en un antiguo lenguaje e ilustrado por un artista.", rewards = "Recibirás:\n\n"},
[950] = {title = "Vuelve con Onus", objective = "Lleva a Onu los Insane Scribbles que has encontrado.", description = "Intentar leer el Libro del Crepúsculo es desesperante. El texto empieza a bailar y retorcerse al poco de comenzar la lectura y lo que al principio eran símbolos hermosos se convierten en formas grotescas.\n\nSolo da tiempo a arrancar una página llena de garabatos imposibles antes de perder el conocimiento.", progress = "<nombre>. Vuelve.", completion = "Este intrincado texto está envuelto en una primitiva y caótica magia. Percibo la mano de los mayores en él.\n\nEsperemos que nos revele el propósito del Azote del Ocaso en Costa Oscura...", rewards = "Recibirás:\n\n"},
[951] = {title = "Las reliquias de Mathystra", objective = "Lleva 6 reliquias de Mathystra a Onu al Páramo de los Ancestros.", description = "El pergamino que encontraste en El Glaive del Maestro es una página de un antiguo texto escrito por los elfos. Está terriblemente mancillado por la corrupción del Azote del Ocaso, pero parece que el Culto está sirviéndose de una antigua magia élfica para devolver a sus maestros a nuestro mundo\n\nViaja hacia el noreste, hasta las Ruinas de Mathystra y busca antiguas reliquias entre los hierbajos y las piedras. Estudiándolas, podríamos averiguar cómo pretende explotar el Azote del Ocaso la magia de los elfos.", progress = "<nombre>. ¿Ha tenido éxito tu exploración de las Ruinas de Mathystra?", completion = "Gracias. Estas reliquias son del tiempo en el que Mathystra refulgía. El que fue el gran bastión de los elfos es apenas su sombra ahora, pero aún conserva fragmentos de su magia. Esperemos que podamos desvelar los secretos de ese lugar antes que nuestros enemigos...\n\nMantente alerta, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCayada de madera duraEspada de Leñantro\n\nTambién recibirás:\n\nEspada de LeñantroCayada de madera dura"},
[952] = {title = "El Páramo de los Ancestros", objective = "Entrégale el mensaje de Fandral a Onu en el Páramo de los Ancestros en Costa Oscura, al sur de Auberdine.", description = "Si tuviste tiempo para hacer de <mensajero/mensajera> para el Árbol del Oráculo, seguro que puedo persuadirte para que lleves un mensaje al Páramo de los Ancestros en Costa Oscura, al sur de Auberdine.\n\nProbablemente, por motivos de seguridad, tengas que utilizar un hipogrifo como medio de transporte, pero confío plenamente en ti. Llévale esto a Onu, el Anciano del Conocimiento. Ha estado a la espera de noticias mías, igual que yo he aguardado noticias de El Claro del Oráculo.", progress = "¡Ah! <clase>, ¿en qué puede ayudarte Onus?", completion = "¡Ah! Gracias, &lt;nombre&gt;, aunque es extraño pues parece que el Archidruida siempre es víctima de la presura. El bosque sabe que cada cosa ha de pasar a su debido tiempo y eso Shan'do Tempestira lo respetaba.", rewards = "Recibirás:\n\n"},
[953] = {title = "La caída de Ameth'Aran", objective = "Estudia las tablillas que hablan de Ameth'Aran y de su caída y reúnete con la centinela Tysha Filoluna en Costa Oscura.", description = "Encontrarás las ruinas de Ameth'Aran hacia el este. Ahora las habitan los inquietos espíritus de los Ilustres que en vida moraban entre sus muros, unos muros entre los que los siervos de Azshara practicaban libremente su poderosa magia.\n\nCuando me enviaron a explorar las ruinas encontré dos grandes tablillas con grabados que relataban la historia de Ameth'Aran y de su caída. Mientras leía las runas me abordaron los espíritus y tuve que huir.\n\nPor favor, si puedes, aventúrate entre las ruinas e intenta descifrar las tablillas en mi lugar.", progress = nil, completion = "Tenemos poca información del tiempo de La guerra de los Ancestros y menos aún de la destrucción del Pozo de la Eternidad. Considerando la agitación y el caos que provocaron los acontecimientos que tenían lugar entonces, no es de sorprender.\n\nGracias, &lt;nombre&gt;. Con tu ayuda, pronto acabaré mi trabajo y podré entregar un informe completo al Círculo.", rewards = "Recibirás:\n\n"},
[954] = {title = "Bashal'Aran", objective = "Encuentra la fuente de la extraña aura azul en las Ruinas de Bashal'Aran.", description = "Las ruinas de Bashal'Aran, al este, están invadidas por esbirros demoníacos. Los espíritus y los sátiros que han establecido su morada en la zona se alimentan de la energía mágica del lugar. Sus poderes crecen continuamente.\n\nPero aún así, he notado que hay un santuario al que no se acercan jamás. En la parte occidental de las ruinas, sobre un pequeño acantilado, se ve una extraña aura azul... Debe haber una explicación a la reluctancia de los demonios.\n\nQuisiera que lo investigaras.", progress = nil, completion = "Aah... Me pregunto a qué debo este increíblemente especial honor de disfrutar de la compañía de alguien como tú. Pues he de reconocer que si se compara con mi compañía actual –sin ánimo de ofender a mis anfitriones, los nobles grells y sátiros– tu presencia aquí podría considerarse una increíble mejora.\n\nPor favor, no permitas que mi zafia lengua te aleje. Han pasado años, incluso décadas, desde la última vez que gocé de compañía civilizada.", rewards = "Recibirás:\n\n"},
[955] = {title = "Bashal'Aran", objective = "Consigue 8 pendientes de grell y llévaselos a Asterion a Bashal'Aran.", description = "Si tuviera que contar la historia de mi vida, sobrepasaría sin duda los límites de tu paciencia. Digamos que la mía ha sido una vida larga y atormentada y que esta forma espectral es el peor de los tormentos que he sufrido.\n\nLa magia me ata a este lugar. Mis palabras pueden sonar falsas, porque no habría palabras que expresaran mi agradecimiento si me ayudaras a averiguar cómo me retiene mi prisión. Me ata un sello y, examinando los pendientes de duendecillos y grells, quizás encuentre alguna pista que me lleve a él.", progress = "Cuando tenga los pendientes, lanzaré el hechizo para localizar el sello que me ata. Llevo siglos soñando con la libertad que alcanzaría destruyendo el sello... Quizás todos esos siglos han dejado mella en mi mente... algo de lo que podría no recuperarme jamás...", completion = "Vaya... los grells de Bashal'Aran no poseen lo que estoy buscando... pero han entrado en contacto con ello recientemente, sin duda. Recientemente... en tu escala del tiempo, no en la mía. Porque los acontecimientos que para mí son recientes, para ti se pierden en la niebla del pasado, en una Era de la que ni siquiera has oído hablar...", rewards = "Recibirás:\n\n"},
[956] = {title = "Bashal'Aran", objective = "Consigue el lacre de piedra lunar antiguo y llévaselo a Asterion a Bashal'Aran.", description = "Si los grells han entrado en contacto directo con el sello que me ata a mi prisión eterna, sospecho cuál podría ser la causa. Sin duda el sello está en poder del sátiro que los comanda.\n\nTengo el presentimiento de que es así, <nombre>. Debe tenerlo uno de los sátiros. Si logras hacerte con él, me acercarías tanto al otro lado de los barrotes de mi prisión que me harías llorar.", progress = "Los pilares de este santuario son para mí como los barrotes de una prisión, <nombre>. Las fuerzas que me queden no podrán quebrarlos, y tampoco me queda magia que podría lograrlo...\n\nMás de mil años he pasado observándolos, preguntándome si quedaría libre al final, si yo sobreviviera a la piedra... ¿O quedaría igualmente atrapado por unos barrotes invisibles?", completion = "Es duro saber que esto que ahora contemplo me ha retenido tanto tiempo... Pero no nos entretengamos, &lt;nombre&gt;. Cuando el sello haya sido destruido, podré volver a caminar por los bosques libremente.", rewards = "Recibirás:\n\n"},
[957] = {title = "Bashal'Aran", objective = "Destruye el lacre de piedra lunar antiguo en la antigua llama de Ameth'Aran y vuelve a reunirte con Asterion en Bashal'Aran.", description = "El sello que conforma mi prisión fue obra de uno de los Ilustres más poderosos. En Ameth'Aran, al sur, hay unas ruinas idénticas a estas. En ellas persiste, incluso hoy, una antigua llama, una llama azul. Esa llama podría destruir el sello.\n\nPero no bajes la guardia en las ruinas, <nombre>...", progress = nil, completion = "Soy libre, &lt;nombre&gt;. Ahora puedo ver con mis propios ojos los cambios que ha sufrido nuestro mundo... Solo había percibido una pequeña parte de ellos. Y pensar que la última vez que caminaba libre, la poza aún existía y los altonatos se reunían en la corte de Azshara, nuestra amada reina.\n\nSiento que mi carcelero, mi antiguo maestro, Athrikus, aún vive... Mi desesperanza empieza a tornarse en deseos de venganza...", rewards = "Podrás elegir una de estas recompensas:\n\nJubón de expedicionarioLeotardos de vagabundoVarita élfica\n\nTambién recibirás:\n\nVarita élficaLeotardos de vagabundoJubón de expedicionario"},
[958] = {title = "Las herramientas de los altonatos", objective = "Reúne 7 reliquias de los altonatos y llévaselas a Thundris Tejevientos a Auberdine.", description = "<clase>. Tengo que pedirte un favor.\n\n¿Has visto las ruinas de Ameth'Aran? Si no las has visto, las puedes encontrar en el lado este del camino principal, viajando hacia el sur. En otro tiempo fueron el hogar de los poderosos Ilustres. Hoy son testimonios de la destrucción que sus errores produjeron.\n\nLos centinelas me han dicho que los espíritus de los Ilustres persisten y que aún poseen sus antiguos instrumentos mágicos. Hemos de hacernos con esas reliquias para poder destruirlas.", progress = "Ay, si el cataclismo que destruyó nuestro mundo hubiera destruido también las herramientas de su destrucción... Pero no fue así. Y debemos asegurarnos de que los horrores de nuestro pasado no se repitan en el futuro.", completion = "Aunque seguramente es producto de mi imaginación, casi me parece sentir la corrupción de la magia de los altonatos en estas reliquias. Haré que sean destruidas, para que el mal que duerme en ellas nunca vuelva a aflorar.", rewards = "Recibirás:\n\nPuños de hiedra"},
[959] = {title = "¡A por la botella!", objective = "El operador de grúa Pelardo de Trinquete quiere que le consigas una botella de porto de 99 años de Magglish el Loco que se esconde en las Cuevas de los Lamentos.", description = "¡Vaya!\n\n¡He tenido un pequeño percance, como puedes ver! El envío del carguero de la mañana de Bahía del Botín era enorme. ¡Lo que se dice enorme de verdad! Nunca había visto tantas cajas. Bueno, estaba intentando acelerar las cosas, así que empecé a descargar un poco más de lo que podía manejar. Y ahí es cuando todo se vino abajo.\n\nAntes de que me pudiera dar cuenta, vi que esa amenaza de Loco Magglish salía corriendo con una botella de porto de 99 años. Lo perseguí hasta las Cuevas de los Lamentos. Pero ni hablar de meterme en ellas.\n\n¿Eres suficientemente valiente para encontrarlo?", progress = "El maestro de los muelles está investigando mi caso desde que Loco Magglish se escapó con ese porto de 99 años. Apuesto que esa botella tenía que haber sido un regalo para el Barón Revilgaz en persona.\n\nSi tienes la paciencia y el valor suficientes para buscar a Loco Magglish y recuperar ese porto, me ocuparé de que tengas una buena recompensa.", completion = "¡Soberbio! ¡Y justo cuando estaba a punto de perder mi empleo! Me ocuparé de hacer llegar este regalo a Gazlowe inmediatamente.\n\nGracias, &lt;nombre&gt;. Un buen trabajo, ¡vaya!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[960] = {title = "Onus lo está meditando", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nAmpolla de adivinación"},
[961] = {title = "Onus lo está meditando", objective = "El hallazgo de los fieles del Azote del Ocaso es preocupante. Debo meditar sobre sus intenciones...\n\nCuando tengas más información, habla conmigo a través de un cuenco de visión. Si necesitas otra ampolla de adivinación para crear un cuenco, aquí tienes una.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nAmpolla de adivinación"},
[962] = {title = "Repitilia", objective = "La boticaria Zamah de Cima del Trueno quiere que recojas 10 reptilias.", description = "La Sociedad Real de Boticarios, establecida en la gran Entrañas de Lordaeron, me ha enviado aquí con un propósito muy concreto, <nombre>. Quizás quieras ayudarme a mí y, de paso, también a la Dama Oscura en nuestro intento de mejorar la forma física de los Renegados.\n\nÚltimamente he estado estudiando una extraña planta llamada reptilia y creo que en grandes cantidades tiene un gran potencial.\n\nLamentablemente, la reptilia solo se encuentra en los recesos más oscuros de las Cuevas de los Lamentos, un sistema de cuevas muy peligroso situado en Los Baldíos.", progress = "Estoy deseando comprobar si eres capaz de recoger suficientes reptilias en las Cuevas de los Lamentos. Ya he enviado a muchos allí pero ninguno de ellos ha vuelto.", completion = "¡Ah, unos ejemplares estupendos! Lo has hecho muy bien, &lt;nombre&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nGuantes de boticario"},
[963] = {title = "Un amor eterno", objective = "Libera el espíritu de Anaia Correalba y lleva su colgante a Cerellean Zarpablanca a Auberdine.", description = "En el período que siguió a las batallas en el Pozo de la Eternidad, oí que Ameth'Aran había sido destruido y que su gente había muerto... y entre ellos, Anaia, mi amada.\n\nJamás habría pensado que, miles de años después, el recuerdo de Anaia seguiría atormentando mis sueños. Un día, deambulando por los bosques de Costa Oscura como en un letargo, me encontré en las ruinas de Ameth'Aran... y allí vi el espíritu atormentado de mi amada.\n\nDebo liberarla, pero me faltan las fuerzas. Sin embargo, su espíritu debe ser destruido.", progress = "Con una gran pena en el corazón, seguí a Shan'do Tempestira en su hibernación y arrastré mi pena en mis sueños durante miles de años.", completion = "Gracias, &lt;nombre&gt;. Quizás habría sido mejor... que lo hubiera hecho yo. Pero incluso después de tantos milenios, no podía soportar la idea de alzar mi mano contra mi amada.\n\nPor favor, déjame a solas con mi dolor...", rewards = "Recibirás:\n\nLágrima de dolor"},
[964] = {title = "Fragmentos esqueléticos", objective = "Lleva 15 fragmentos esqueléticos al boticario Dithers en El Baluarte, en las Tierras de la Peste del Oeste.", description = "La llave de Scholomance es la llamada Llave esqueleto. Debe forjarse a partir de los restos de un esqueleto (varios, en realidad) y endurecerse con los metales más fuertes en el molde adecuado. Un sello de poder obtenido de un ser que pueda abrir el portal de Scholomance por medios naturales será el último componente de la llave.\n\nPero empecemos por el principio, <nombre>. Necesitaremos fragmentos esqueléticos para forjar la llave. Los podrás obtener de los esqueletos que se encuentran dentro de Andorhal, aunque también deberían servirte los que están fuera de los muros de la ciudad.", progress = "Mata a muchos y mátalos rápido, <clase>. Necesitaremos un suministro abundante de trozos esqueléticos para la capa externa de la llave. Por algo se llama Llave esqueleto, ¿sabes?", completion = "¡Esto valdrá, &lt;nombre&gt;! Ahora me toca a mí, los imbuiré con un temple adecuado que impedirá que se destruyan en el proceso de fundición por el que van a pasar.\n\nEl siguiente paso consistirá en conseguir el molde adecuado para la llave. Conozco a un herrero goblin en Tanaris que en el pasado hizo moldes para objetos igual de macabros. Trabaja para quien trabajan todos los goblins: el mejor postor.", rewards = "Recibirás:\n\n"},
[965] = {title = "La torre de Althalaxx", objective = "Encuentra a Balthule Golpesombra cerca de La torre de Althalaxx en Costa Oscura.", description = "Saludos, joven <raza>. Soy Elissa Brisa Estelar y soy la encargada de proteger Auberdine de todo mal.\n\nCon tal propósito envié a Balthule Shadowstrike a observar los extraños sucesos de la Torre de Althalaxx, al noreste.\n\nY ya debería haber regresado. Me preocupa que haya encontrado algún peligro imprevisto en el bosque. Apreciaría sobremanera que fueras en su busca y te aseguraras de que se encuentra bien.", progress = nil, completion = "¿Te envía Elissa? Son buenas nuevas. Yo tengo noticias preocupantes que transmitirle y no he tenido forma de hacérselas llegar.", rewards = "Recibirás:\n\n"},
[966] = {title = "La torre de Althalaxx", objective = "Reúne 4 pliegos gastados para Balthule Golpesombra acerca de La torre de Althalaxx.", description = "Un grupo de brujos se ha instalado alrededor y dentro de la torre. Habría regresado a Auberdine a informar a Elissa, pero tenía miedo de perderme algo.\n\nDelgren sospechaba que una compañía como esta estaba reuniéndose en la torre, pero no sabía por qué.\n\nA mis manos han llegado algunos fragmentos de los pergaminos que llevan los brujos, pero necesito reunir más para resolver el enigma. Deja que te dé un consejo: no entres en la torre, los brujos que hay en ella son los más poderosos.", progress = "¿Has encontrado algún pergamino más? Si consigues encontrarlos, podría hacerme una idea más completa de lo que planean.", completion = "Muchas gracias, &lt;nombre&gt;. Esto debería arrojar algo de luz sobre esta reunión de brujos...\n\nMmm...el Culto de la Facción Oscura... Nunca había oído hablar de este culto. Es imposible saber qué planes tienen sin conocer su historia.\n\nNo hay tiempo que perder. Hay que enviar un aviso a Delgren de inmediato.", rewards = "Recibirás:\n\n"},
[967] = {title = "La torre de Althalaxx", objective = "Entrega la carta de Balthule a Delgren el Purificador que está en el Bosque de Vallefresno.", description = "Mi maestro, Delgren el Purificador es un paladín que ha ofrecido gentilmente su ayuda en la defensa de nuestros bosques de los demonios y los no-muertos. Me ha enseñado mucho sobre la Luz Sagrada y el arte de la guerra.\n\nDelgren debe saber de las operaciones del culto cuanto antes.\n\nLo encontrarás al sur de aquí, en la Atalaya de Maestra del Bosque de Vallefresno. Debes apresurarte: la amenaza de la Facción Oscura crece cada hora.", progress = "¿Quieres algo de mí, <clase>?", completion = "Perdona que te lo diga, pero estás muy bien equipado para ser mensajero, ¿no? Supongo que Balthule quería asegurarse de que su carta llegaba a mis manos. Veamos qué dice...\n\nSon noticias desalentadoras. Cuando aparecen fuerzas de no-muertos y de demonios, ayudo a las Centinelas a destruirlas.\n\nTardé en acostumbrarme a los modos de los elfos de la noche, pero he llegado a respetarlos como aliados.", rewards = "Recibirás:\n\n"},
[968] = {title = "Los Poderes Subyacentes", objective = "Lleva el libro Los Poderes Subyacentes a Gerrig Agarrahueso a La Caverna Abandonada, en Forjaz.", description = "Aunque la cubierta de este libro está gastada y arañada, aún se puede leer el título:\n\nLos Poderes Subyacentes\n\nEn el interior del libro hay una lista de nombres impronunciables con títulos terribles, rituales de adoración y sacrificios preferidos... muchos de los cuales incluyen criaturas vivas, humanoides.\n\nEn la cubierta interior del libro hay unas palabras escritas: \"Runas y Maldiciones de Bonegrip, La Caverna Abandonada, Forjaz. Propiedad de: Gerrig Bonegrip.\"\n\nSi Bonegrip vende libros de este tipo, quizás me recompre este.", progress = "Saludos, señora.\n\n¿Puedo tentarle con alguno de mis libros?", completion = "Ah, una copia de Los Poderes Subyacentes. Es un texto interesante. Y hay quien lo encuentra útil también...\n\nPero este ejemplar está en muy mal estado. ¡Mira esto! ¡Hay notas en los márgenes de casi todas las páginas!\n\nMm... Yo conozco este ejemplar. Se lo vendí a Bolgar el año pasado. ¿Eres... amiga suya? Eso pensaba, sí. ¡Tienes el mismo fuego en los ojos!\n\nPues, por definición, una amiga de Bolgar es amiga mía. Y a los amigos hay que cuidarlos, ¿no te parece?", rewards = "Recibirás:\n\n"},
[969] = {title = "Que la suerte sea contigo", objective = "Recoge 10 esquirlas de Machacahielo para la médico bruja Mau'ari de Vista Eterna.", description = "Ah, verte basta para saber que lo que quieres es un talismán de la suerte. ¡Mau'ari todo sabe!\n\nAl Sur, encontrarás un cañón y gigantes hechos de hielo duro. A veces los gigantes las llevan, pero puedes recoger tú <mismo/misma> las esquirlas en el cañón. Las esquirlas tienen una poderosa cualidad reflectante.\n\nTráeme 10 esquirlas de los Machacahielo y mi talismán de la suerte será tuyo.", progress = "¡Trae las esquirlas y te diré algo más!", completion = "Ahh, sí... te haré el talismán ahora mismo. ¡Espera un segundo!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[970] = {title = "La torre de Althalaxx", objective = "Reúne una gema de alma brillante y vuelve junto a Delgren el Purificador en la Atalaya de Maestra.", description = "La carta de Balthule es espantosa. Este culto de la Facción Oscura es una espina clavada que debo eliminar. Me las he visto con algunos de los miembros de la Facción Oscura al noreste de este lugar, en Ordil'Aran. Uno de ellos posee una gema de almas que, creo, guarda el secreto del poder del culto.\n\nTráemela y podré descifrar el secreto de su interior.", progress = "¿Has conseguido la gema de alma?", completion = "Mm, esta piedra de alma está encantada con magia oscura. Será mejor que veamos que encierra.", rewards = "Recibirás:\n\n"},
[971] = {title = "El conocimiento de las profundidades", objective = "Lleva el manuscrito de Lorgalis a Gerrig Agarrahueso, que está en la Caverna Abandonada en Forjaz.", description = "Como puede que ya sepas, recopilo conocimiento. Conocimiento antiguo. Conocimiento con poder. Conocimiento que abre puertas y puede despertar a los Durmientes.\n\nSe dice que un antiguo documento, el manuscrito de Lorgalis, se halla en las Cavernas Anegadas de Vallefresno. Allí vivieron unos elfos que murieron hace mucho. Acumularon mucha sabiduría antes de que su ciudad se arruinara.\n\nBusca el manuscrito en las Cavernas Anegadas. Nunca olvidaré este favor. Ni siquiera después del fin de los días.", progress = "El viaje a Vallefresno es largo y tu misión no es fácil. Pero <nombre>, si me traes el manuscrito, te llevarás una buena recompensa.", completion = "¡El manuscrito! ¡Qué alegría! ¡Por fin podremos divulgar los conocimientos antiguos de Lorgalis!\n\nNo puedo expresar mi agradecimiento, &lt;nombre&gt;. Espero que este regalo sea buena prueba de mi gratitud.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nAnillo de sustención"},
[972] = {title = "La sapta de agua", objective = "Lleva la sapta del agua y encuentra la poza de agua corrupta del Bosque de Argénteos, <nombre>. Tengo mucha fe en tus facultades.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nSapta de agua"},
[973] = {title = "La torre de Althalaxx", objective = "Llévale el Libro de Ilkrud Magthrull a Delgren el Purificador en la Atalaya de Maestra.", description = "Ilkrud Magthrull. Sí, sé algo acerca de él. Es un poderoso brujo orco que mora en el Santuario de la Escara, un lugar de extrema maldad al suroeste de Vallefresno.\n\nHasta ahora no había pasado de ser una molestia menor y no he tenido que tratarlo pero, por lo visto, ha llegado su hora.", progress = "Pareces inquieto, <nombre>. ¿Te encuentras bien?", completion = "Hoy has hecho algo grande, &lt;nombre&gt;. Puedes sentirte orgulloso de haberte enfrentado a tan poderoso brujo.\n\nAhora, veamos qué podemos averiguar en los escritos de Ilkrud.", rewards = "Podrás elegir una de estas recompensas:\n\nAnillo de cleroBastón del Purificador\n\nTambién recibirás:\n\nBastón del PurificadorAnillo de clero"},
[974] = {title = "Busca la fuente", objective = "Krakle, en el Cráter de Un'Goro, quiere que encuentres la zona más caliente de la Cresta del Penacho en Llamas.\n\nSiempre que des con una zona caliente, pulsa con el botón derecho sobre el termómetro para comprobar la temperatura. Sigue buscando hasta que encuentres el punto más caliente.", description = "Las fuentes termales de esta zona son un auténtico misterio. ¿De dónde viene el calor?\n\nTeniendo en cuenta su proximidad con las fuentes de la Cresta del Penacho en Llamas, me pregunto si el volcán puede tener algo que ver. He creado un termómetro para tomar lecturas de las temperaturas del volcán y me gustaría localizar la zona más caliente. ¡Mide las temperaturas en grados Kraklenheit!\n\nToma, cógelo, ¡y haz mediciones de las temperaturas en la cresta cada vez que localices una zona caliente!", progress = "Iría allí yo mismo, pero... ¡me dan algo de miedo los elementales de fuego!", completion = "Sí, veo claramente la relación entre la temperatura del volcán y las fuentes termales. Tengo que volver a comprobar mis ecuaciones pero parece que la distancia y la tasa de transferencia del calor... Ummm...", rewards = "Recibirás:\n\n"},
[975] = {title = "Alijo de Mau'ari", objective = "Vuelve a hablar en un momento con la médico bruja Mau'ari para recibir el talismán de la suerte.", description = "Antes del talismán darte, explico mejor cómo usarlo.\n\nSi encima lo llevas, caerán objetos de las criaturas que mates. Si encima el talismán no tienes no caerán. ¿Entiendes?\n\nLos objetos conocerás por el nombre... se llaman E'ko. Si encuentras esos objetos, dáselos a la vieja Mau'ari y ella te ayudará.\n\nEspera aquí, yo preparo tu talismán.", progress = nil, completion = "Aquí lo tienes, &lt;nombre&gt;. Recuerda, contigo lo llevas. ¿Por qué no volver a la naturaleza y ves qué encontrar?", rewards = "Recibirás:\n\nCarretilla de Mau'ari"},
[976] = {title = "Suministros a Auberdine", objective = "Habla con Delgren el Purificador en la Atalaya de Maestra después ayudar a Feero a cruzar el Bosque de Vallefresno y dejarlo a buen recaudo.", description = "Delgren me ha pedido que le entregue varios mensajes y unos suministros a la centinela Brisa Estelar en Auberdine pero odio marcharme solo. Peligrosos enemigos acechan en el bosque y no me gustaría correr el riesgo de que esta información y estos suministros cayeran en manos malintencionadas.\n\nSi tú y alguien más pudieseis ser mis guardas, podría marcharme ahora. Con tu protección, seguro que estas cosas llegarán a buen puerto en Auberdine.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nLinterna eternaPeto de Kor\n\nTambién recibirás:\n\nLinterna eternaPeto de Kor"},
[977] = {title = "Yeti... ¿ya hemos llegado?", objective = "Reúne 2 cuernos prístinos de yeti para Umi Risitas en Vista Eterna.", description = "Ahora que tengo lo que necesito para cubrir las partes mecánicas móviles, solo necesito una cosa más. Bueno, dos. ¡Dos cuernos!\n\nLos patriarcas y matriarcas Cardo Nevado tienen los cuernos más grandes así que, por supuesto, esos son los que quiero. Ese tipo de yeti habita una cueva al Sureste. Hay muchos de ellos, así no deberías tener ningún problema para encontrarlos.\n\nRecuerda que solo quiero los que tengan mejor aspecto... ¡no traigas cuernos rotos o arañados por favor!", progress = "Acabo de terminar de poner las pieles, <nombre>. Por favor, ¡date prisa con esos cuernos!", completion = "¡No puedo esperar a enseñárselo a mis amigos! Espera, es solo un momento... voy a poner estas...\n\n¡Perfecto!", rewards = "Recibirás:\n\n"},
[978] = {title = "Lechúcico Lunadón", objective = "Recoge 10 plumas Lunadón de Cuna del Invierno y regresa con Eralas Cieloámbar en Aldea Rut'theran.", description = "Las plumas de lechúcico de las Tierras del Interior parecen tener rastros de cualidades mágicas. Estos poderes... no parece que las criaturas se valgan de ellos, es más bien algo inherente a la especie... parte de su esencia.\n\nDurante mis estudios pude oír que hay un gran número de lechúcicos en las tierras nevadas de Cuna del Invierno. ¿Tendrán esas criaturas los mismos poderes? Por favor, ayúdame a encontrar una respuesta, <nombre>. Ve a Cuna del Invierno y recoge del suelo las plumas que se hayan caído. Cuando las tengas, vuelve a verme.", progress = "¿De dónde pueden haber surgido estos poderes? Creo que estamos siguiendo la pista correcta para encontrar la respuesta, <nombre>.", completion = "Ya puedo comparar las plumas... Veamos...", rewards = "Recibirás:\n\n"},
[979] = {title = "Encuentra a Ranshalla", objective = "Encuentra a Ranshalla en la zona oriental de Cuna del Invierno.", description = "Sí, sí... estas plumas parecen albergar el mismo encantamiento mágico que ya había observado... Aunque no estoy seguro de qué es lo que podemos encontrar, te pido encarecidamente que sigas buscando respuestas, <nombre>.\n\nUna colega mía viajó recientemente a Cuna del Inverno para observar a los lechúcicos en su propio hábitat. ¿Por qué no vas con ella y averiguas qué ha descubierto?", progress = nil, completion = "¡Hola! ¿Vienes de parte de Erelas? Genial, estaba preparándome para ir a las cuevas, no me importaría tener algo de compañía.", rewards = "Recibirás:\n\n"},
[980] = {title = "Nuevos manantiales", objective = "Viaja hasta Cuna del Invierno y habla con Donova Cubilnevado.", description = "Bien, he oído hablar de otras fuentes termales en un lugar llamado Cuna del Invierno, muy al norte de aquí. Lo extraño es que no hay volcanes cerca de ellas. Interesante, lo sé.\n\nBien, si quieres averiguar más cosas, deberías ir a Cuna del Invierno y hablar con una amiga mía llamada Donova Cubilnevado, se encuentra junto a las fuentes termales de esa zona.\n\n¡Gracias de nuevo por ayudarme con mi termómetro! ¡Hasta la vista!", progress = nil, completion = "Hola, &lt;nombre&gt;. Sí, lo que te dijo Krakle es cierto...", rewards = "Recibirás:\n\n"},
[981] = {title = "La torre de Althalaxx", objective = "Habla con Delgren el Purificador en la Atalaya de Maestra.", description = "Por todo lo que has hecho, bien mereces una recompensa. Busca a Delgren y dile que Athrikus Narassin ha sido derribado por tu mano y que el Culto de la Facción Oscura ha sido derrotado y dispersado.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos piadososGolpe de Seraph\n\nTambién recibirás:\n\nGolpe de SeraphLeotardos piadosos"},
[982] = {title = "Profundo océano, mar inmenso", objective = "Recupera la caja fuerte del Amanecer de plata y la caja fuerte del Halo de bruma para Gorbold Mano de Acero de Auberdine. Ambos objetos deberían estar en los cascos de los navíos naufragados al norte de la aldea.", description = "En Costa Oscura, al norte de aquí, naufragaron dos barcos, el Amanecer de plata y el Halo de bruma. Hace un tiempo, los dos barcos fueron abordados por unos murlocs cuando navegaban hacia Auberdine. Ahora yacen en el fondo del océano como trofeos de esos demonios.\n\nLos capitanes no sobrevivieron y sus cuadernos de bitácora y otros efectos aún siguen en sus cajas fuertes. Me gustaría que los recuperaras para nosotros. Es importante para los miembros de la tripulación que aún están por aquí.", progress = "Los capitanes de esas naves eran buenos elfos de la noche. Merecen un destino mejor del que tuvieron. Atender sus efectos personales sería la mejor forma de dar paz a sus espíritus.", completion = "Has hecho un gran servicio a Auberdine, muchacho. Cuidaremos bien de sus efectos.\n\nPor favor, acepta esto. Es lo mínimo que puedo hacer por alguien que ha tenido el valor suficiente para enmendar esta situación.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de gota de pozoGuanteletes de Noosegrip\n\nTambién recibirás:\n\nGuantes de gota de pozoGuanteletes de Noosegrip"},
[983] = {title = "La caja mecánica 827", objective = "Reúne 6 piernas de reptador y colócalas en la caja mecánica 827.", description = "¡Eh! Necesito ayuda con mi último invento, la caja mecánica. ¡Sirve para hablar con gente que está lejos!\n\nPuede que hayas visto alguna ya. Son unas cajas con muchas palancas. El problema que tienen es que necesitan un mantenimiento constante.\n\nCada una tiene un problema diferente, pero yo he hecho una cosa muy inteligente. He colocado cada caja cerca de las criaturas que tienen las piezas adecuadas para repararlas. Y ahora, hay que reparar la caja mecánica 827. Está al sur de Auberdine, muy cerca. Hacen falta 6 patas de reptador para arreglarla. Te pagaré...", progress = "La caja mecánica 827 guarda un inquietante silencio. Un destello luminoso indica que hay que colocar 6 patas de reptador en su soporte.", completion = "Cuando introduces las patas de reptador en la máquina, oyes chirriar sus engranajes. A juzgar por los sonidos que provienen de la caja mecánica, parece que las patas de reptador están colocándose en su sitio. La máquina empieza a zumbar y luego oyes una vocecita.\n\n\"¿Hola? Eeh... ¡Hola! ¡La has arreglado! ¡Por cierto, esta es Zumbang! ¡Muchas gracias! Oye, ¿te apetecería reparar otra...?\"", rewards = "Recibirás:\n\n"},
[984] = {title = "La extensión de la amenaza", objective = "Encuentra el campamento de furbolgs corruptos en Costa Oscura, vuelve a Auberdine y busca a Terenthis.", description = "Algunos de mis hermanos fueron salvados de un fúrbolg corrupto en Teldrassil y yo he jurado impedir otras atrocidades. No permitiré que los nuestros resulten heridos... o algo peor.\n\nYa he detectado un par de indicios de corrupción en Costa Oscura, pero aún he de encontrar signos de que se haya extendido. Creo que lo lógico sería continuar la investigación con los furbolgs. ¿Podrías buscar alguno de sus campamentos y decirme si has visto algún signo de corrupción?", progress = nil, completion = "Es una noticia terrible, &lt;nombre&gt;.\n\nCon los furbolgs tan cerca de Auberdine, tendremos que prepararnos para lo inevitable.\n\nGracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[985] = {title = "La extensión de la amenaza", objective = "Mata a 5 soplavientos y a 8 abrecaminos Bosque Negro y vuelve a Auberdine a buscar a Terenthis.", description = "Ya has demostrado que sabes rastrear a nuestros enemigos, <nombre>. ¿Crees que también podrás enfrentarte a ellos?\n\nLo pregunto porque algunos aventureros prefieren eludir el combate directo...\n\nSi crees que estás a la altura de la tarea, puedo decirte que la mayor amenaza para nuestra gente proviene del campamento que hay al sur de Auberdine. Allí encontrarás una parte de la tribu Bosque Negro. Mata a 8 abrecaminos y a 5 soplavientos y vuelve aquí.", progress = nil, completion = "Limpia la sangre de tu ropa, &lt;nombre&gt;, y no sufras por lo que tuviste que hacer. Más bien tendrías que dar gracias. Has aliviado en parte los temores de las gentes de Auberdine, aunque Costa Oscura siga peligrando por los efectos del musgovil.", rewards = "Recibirás:\n\n"},
[986] = {title = "El maestro perdido", objective = "Encuentra 5 pieles de Acechalunas y llévaselas a Terenthis a Auberdine.", description = "<nombre>, con tus habilidades me has ayudado mucho en mi empresa. ¿Podría abusar una vez más de tu buena disposición y pedirte que ayudes a Zarpadura y a su maestro Volcor? Puedo crear para ti una capa mágica que te permitirá caminar entre las criaturas de Costa Oscura sin ser <detectado/detectada>.\n\nPero para tener suficiente material para crear la capa necesitaré 5 buenas pieles de señor Acechalunas o de una matriarca Acechalunas. Puedes encontrar felinos de esos al sur, cerca del Río Culebra o cerca de Vallefresno, más al sur todavía.", progress = "Mi conciencia no me permite enviarte a buscar a Volcor hasta que te haya hecho la capa.", completion = "Estas pieles son estupendas, &lt;nombre&gt;. Empezaré a trabajar en la capa de inmediato.\n\nDame un momento y luego vuelve.\n\nAh, otra cosa: cuando hayas usado el hechizo en la capa, tu capacidad de interactuar con otros estará limitada. Si puedes, limítate a hablar, solo eso. Si haces algo más, podrías destruir la ilusión.", rewards = "Recibirás:\n\n"},
[990] = {title = "El camino de Vallefresno", objective = "Encuentra a Raene Correlobo en Vallefresno.", description = "<nombre>, mi señora, Raene Correlobo, te espera en la ciudad de Astranaar en Vallefresno. Con tu ayuda, quizás podamos reducir algo la corrupción que allí se ha extendido.\n\nDirígete al sur desde aquí y mantente cerca de la carretera, llegarás allí sin problemas si sigues estas instrucciones.", progress = nil, completion = "Ah, un &lt;clase&gt; de Costa Oscura. Selarin ha hecho bien en enviarte tan rápidamente, &lt;nombre&gt;. Ojalá tu viaje aquí no se debiera a tan funesta urgencia. Quizás con tu ayuda logremos mejorar la situación.\n\nEmpezaría mi visita hablando con otros ciudadanos de Astranaar. Seguro que alguno te puede ayudar.", rewards = "Recibirás:\n\n"},
[991] = {title = "La limpieza de Raene", objective = "Encuentra a Teronis en Vallefresno.", description = "<nombre>, un viejo amigo también está colaborando con las centinelas aquí en Vallefresno pero aún no ha regresado.\n\nTenía pistas sobre cómo encontrar un objeto que, según él, podría frenar los ataques de los furbolgs contra nuestro pueblo, una vara creada por un ahora difunto mago maligno.\n\nAntes de marcharse, mencionó algo de una gema para la vara.\n\nTambién comentó que la gema probablemente estuviera escondida en el altar del Lago Falathim al pie de la montaña hacia el oeste. La gema se encontraba allí antes de que lo invadieran.\n\nEncuentra a mi amigo, <nombre>, te lo ruego.", progress = nil, completion = "El cuerpo de Teronis yace destrozado en la isla. Por alguna razón desconocida, los murlocs lo han dejado en paz.\n\nLos profundos cortes en su cadáver proceden sin duda de las armas y garras de los murlocs.", rewards = "Recibirás:\n\n"},
[992] = {title = "Un estudio de agua de Gadgetzan", objective = "Utiliza la pipeta de muestras abierta en la poza de agua de Vigía Penas de Arena. Cuando hayas recogido la muestra, llévale la pipeta de muestras cerrada al perito jefe Picoflojo, que está en Gadgetzan.", description = "Por si te interesa, tenemos un puesto vacante para perito adjunto temporal en la compañía de aguas de Gadgetzan.\n\nDado que los nómadas se aprovechan de nuestros pozos, tenemos que explotar más fuentes gratuitas de agua. En el desierto hay agua pero solo para aquellos que sepan encontrarla.\n\nCoge esta pipeta y toma una muestra del agua de la poza situada al lado de Vigía Penas de Arena. Está a la vista de los trols que andan por allí. ¡Tráeme la pipeta cerrada cuando hayas acabado!", progress = "¡Bienvenida de nuevo, perita adjunta <nombre>! ¿Ya has terminado tu tarea?", completion = "¿Cómo? ¿Te han tendido una emboscada una especie de bichejos de mojo malo? Vaya, esto no augura nada bueno para la compañía de aguas de Gadgetzan... \n\nSupongo que tenía que haberte hablado de los peligrosos bichos que al parecer están consumiendo toda el agua del desierto. Al principio, le resté importancia pues ya tenemos bastantes problemas con los nómadas y creí que era una más de sus artimañas. Al menos ahora sabemos lo que está pasando.", rewards = "Recibirás:\n\n"},
[993] = {title = "El maestro perdido", objective = "Encuentra a Volcor en Costa Oscura y dale la capa de Acechalunas encantada.", description = "La capa está acabada, <nombre>. Ha llegado el momento de ir a buscar a Volcor. Solo espero que aún estemos a tiempo.\n\nLa magia de la capa, una vez invocada, no durará mucho... solo unos cinco minutos. Tú decides cuándo es la ocasión de utilizarla: pero yo sugeriría que esperaras hasta que encuentres a Zarpadura.\n\nSi lo que me ha dicho es verdad, encontrarás a su maestro en una cueva al sur de aquí. Zarpadura te estará esperando al lado del camino. Cuando lo veas, saluda con un ademán y él te señalará el camino hacia su maestro.", progress = nil, completion = "¡Estupendo! Terenthis ha encontrado a alguien que me ayude.\n\nY mira, una capa de Acechalunas...&lt;tose&gt; Ay... qué dolor. Gracias, &lt;nombre&gt;.\n\nLos furbolgs me hirieron antes de que consiguiera escapar. Dame un momento para recuperarme. Luego podremos hablar.", rewards = "Recibirás:\n\n"},
[994] = {title = "Escaparse por la fuerza", objective = "Protege a Volcor hasta que lleguéis al camino y luego ve a Auberdine a hablar con Terenthis.", description = "Muy bien, <nombre>, llegó el momento. Salgamos de aquí. Cuando tú digas. Nos abriremos paso hasta el camino combatiendo. Zarpadura aún debería estar allí. Una vez allí, nos dividiremos.\n\nTerenthis querrá oír tu versión de la historia inmediatamente, así que dirígete a Auberdine en cuanto salgamos de aquí.", progress = nil, completion = "¡&lt;nombre&gt;! Me alegra ver que has conseguido volver. Que ayudaras a Volcor me da esperanzas... creo que podremos superar los desafíos que nos esperan en Costa Oscura y más allá.", rewards = "Recibirás:\n\nCincho de determinación férrea"},
[995] = {title = "Escaparse furtivamente", objective = "Escapa de la cueva fúrbolg y reúnete con Terenthis en Auberdine.", description = "Muy bien... si no tienes dudas, salgamos de aquí. Partiremos cuando tú digas.\n\nY recuerda, cuando hayas escapado, busca a Terenthis en Auberdine. Yo iré hacia allí por mi cuenta. Probablemente me muevo más rápido que tú.", progress = nil, completion = "¡&lt;nombre&gt;! Me alegra ver que has conseguido volver. Que ayudaras a Volcor me da esperanzas... creo que podremos superar los desafíos que nos esperan en Costa Oscura y más allá.", rewards = "Recibirás:\n\nPantalones escarabajo"},
[996] = {title = "Flor del viento corrupta", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[997] = {title = "Las tierras de Denalan", objective = "Llévale el paquete con tierras especiales a Denalan, que está en el Lago Al'Ameth.", description = "¿Te diriges hacia el sur? ¿Al Lago Al'Ameth? De ser así hay algo que quiero pedirte...\n\nMi compañero Denalan tiene un campamento en el extremo oriental del lago, donde está estudiando y haciendo experimentos con la flora de Teldrassil. Encargó un paquete con tierras especiales de Darnassus que llegó con retraso pues no hace mucho que lo recibimos aquí en Dolanaar.\n\n¿Podrías llevarle tú el paquete?", progress = "¿Tienes algo para mí?", completion = "¡Ah, aquí está! Llevo tiempo esperando este paquete con tierras especiales. Espero que todavía esté en buen estado...\n\nGracias por traérmela, &lt;nombre&gt;. Eres un &lt;raza&gt; comprometido con su tiempo.", rewards = "Recibirás:\n\n"},
[998] = {title = "Flor del viento corrupta", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[999] = {title = "Cuando los sueños se vuelven pesadillas", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1000] = {title = "La nueva frontera", objective = "Habla con el archidruida Hamuul Runatótem en el Alto de los Ancestros de Cima del Trueno acerca de la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.", description = "¡Atención, héroes!\n\n¡El Círculo Cenarion busca voluntarios sanos para explorar las vastas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en representación suya.\n\nTodos aquellos que estén interesados pueden solicitar audiencia en el Círculo Cenarion. ¡Hablad con el archidruida Hamuul Runatótem en el Alto de los Ancestros en Cima del Trueno para más información!", progress = nil, completion = "Saludos, &lt;clase&gt;. \n\nMe alegra ver que alguien como tú se preocupa por el bienestar de la naturaleza. He oído hablar de tus hazañas por lo que eres bien recibida aquí. Al mirarte tengo la certeza de que el Círculo Cenarion recibirá la ayuda que necesita. \n\nNuestra relación con el Círculo Cenarion en el Claro de la Luna trasciende la barrera racial y política; no lo olvides nunca.", rewards = "Recibirás:\n\n"},
[1001] = {title = "La caja mecánica 411", objective = "Consigue 3 ojos de trillador de los trilladores de Costa Oscura en las profundidades del mar, cerca de la caja mecánica 411.", description = "Del interior de la máquina sale una vocecita.\n\n\"¡Soy Zumbang! La siguiente caja mecánica está al norte de Auberdine, en la playa.\n\nEs la número 411 y requiere 3 ojos de trillador para ser reparada. ¡Están cerca de la costa! Los trilladores de la Costa Oscura... *Hip*... Uy, perdón...\n\nComo la vez anterior, cuando le das los artículos a la caja mecánica, te escupe tu recompensa. *Hip*... ¿Qué? No, ¡estoy bien!\"", progress = "La cubierta de la máquina está abierta y parece estar esperando a que introduzcas 3 ojos de trillador.", completion = "La caja mecánica cobra vida en cuanto introduces los ojos de trillador. Después de un lapso de tiempo, oyes una vocecilla familiar...\n\n\"El vino de reptador es lo mejor...\" *hip*\n\n\"¿Eh? Oh, ¿ya la has arreglado? Vaya, sí que eres rápido. Dime, ¿quieres arreglar otra caja?\"\n\nOyes lo que parece el sonido de un líquido vertiéndose en una copa y luego ruidos ansiosos de alguien tragando.", rewards = "Recibirás:\n\n"},
[1002] = {title = "La caja mecánica 323", objective = "Reúne 6 colmillos de Acechalunas y colócalos en la caja mecánica 323.", description = "La caja mecánica sufre un ataque estático y Zumbang empieza a hablar.\n\n\"La siguiente caja mecánica es la 323. Está al norte de Auberdine pero..., ¿dónde está la cerveza? ¿Qué? Ah, está por el camino, cerca del puente. No deberías tener problemas.\"\n\nZumbang murmura algo incoherente y luego le oyes tragar escandalosamente.\n\n\"Esta necesita colmillos de Acechalunas... 6 en total.\"", progress = "Aunque aún no has colocado los 6 colmillos de Acechalunas, te parece oír unos sonidos estáticos y un farfulleo de gnomo saliendo de la máquina. ¿Alguien la habrá arreglado ya?", completion = "La caja mecánica se activa de repente y oyes sonidos de dientes que se pulverizan en su interior.\n\n\"¡Ashí que lo hash conseguido! Esh una notishia excelente... Entonshesh... ¿Por qué no shiguesh con lo tuyo? ¡Oh! ¡A no sher que quierash otro encarguito!\"", rewards = "Recibirás:\n\n"},
[1003] = {title = "La caja mecánica 525", objective = "Consigue 4 pellejos canosos de los grisosos cardo del sur de Auberdine y colócalos en la caja mecánica 525.", description = "\"Por encima de los ruidos que provienen de la máquina, oyes la voz de Buzbang que te llama.\n\n\"La shiguiente eshtá al shur, por el camino. No deberías tener problemas para encontrarla... jushto al lado d'un eshcondite... cerca de un pantano. Creo que la eshcondí en unosh robushtosh... Digo, arburshtosh. Je, je, je... Shí, la caja mecánica 525 neceshita pellejosh canosos de los Oshos Cardosh esos. ¡Tuercash! ¡Qué bocado! Canoshosh... Je, je, je...\"\n\nLa máquina traga y traga y se desconecta. Y, de repente, vuelve a la vida.\n\n\"¡4!\"", progress = "Parece que la caja mecánica 525 está cantando.\n\n\"El vino de reptador esh dulce, la cerveza de trillador esh mejor y el licor de luna tiene un gran shabor. Pero nada hay másh reconfortante que la cerveza de Oso Cardo, ¡y pronto tendré una jarra en la mano!\"\n\nLa cubierta está abierta y espera los 4 pellejos canosos mientras la caja mecánica repite su canción una y otra vez.", completion = "\"¿Ashí que metishte losh pellejosh? Esho esh eshtupendo... ¿Qué? Ah, eshte.. claro, te hash dado cuenta de puedo hablar contigo shin losh materialesh en la máquina, te mentí un poco. ¡Pero eshtaba diciendo la verdad cuando te dije que estaban rotash!\n\nEsh que, verásh, ademásh de sher herramientash de comunicación, lash cajash mecánicash deshtilan un licor eshtupendo. Shiento haberte engañado, pero esh que a losh elfosh de la noche no lesh gushta que mi licor pashe por shu ciudad. Pushe algo eshpecial en eshta caja para demoshtrarte mi gratitud por tush eshfuerzosh. ¡Que te gushte!\"", rewards = "Recibirás:\n\nZurrón de ZumbangBrebaje especial de Zumbang"},
[1004] = {title = "La nueva frontera", objective = "Habla con el archidruida Hamuul Runatótem en el Alto de los Ancestros de Cima del Trueno acerca de la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.", description = "¡Atención, héroes!\n\n¡El Círculo Cenarion busca voluntarios sanos para explorar las vastas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en su lugar.\n\nTodos aquellos que estén interesados pueden solicitar audiencia en el Círculo Cenarion. ¡Hablad con el archidruida Hamuul Runatótem en el Alto de los Ancestros en Cima del Trueno para más información!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1005] = {title = "Lo que acecha detrás", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1006] = {title = "Lo que hay detrás", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1007] = {title = "La antigua estatuilla", objective = "Llévale la antigua estatuilla a Talen, en su campamento cerca de La Ensenada de Zoram.", description = "Te encargaría una tarea, <nombre>.\n\nEstaba en mi pequeña embarcación, navegando sobre las sumergidas ruinas de Zoram, cuando los nagas me atacaron ¡surgiendo del agua y tratando de hacerme jirones con sus garras! Me escapé llevándome tantos suministros como pude para hacer este exiguo campamento.\n\nPero cuando llegué a la orilla y corrí... mi preciada posesión se había perdido.\n\nTe lo ruego, <nombre>, encuentra el lugar donde me tendieron la emboscada, por la costa hacia el norte, y busca una antigua estatuilla. Es la razón por la que me enfrenté a los peligros de La Ensenada de Zoram.", progress = "¿Has encontrado la estatuilla, <nombre>?", completion = "¡La encontraste! ¡Gracias, &lt;nombre&gt;!\n\nLa antigua ciudad de Zoram guarda muchos secretos y esta estatuilla podría ser la llave de gran parte de tales secretos.", rewards = "Recibirás:\n\n"},
[1008] = {title = "La Ensenada de Zoram", objective = "Llévale 20 cabezas de Colafuria a Shindrell Fuegoveloz en Astranaar.", description = "El mal nos acecha desde la costa noroeste conocida como La Ensenada de Zoram.\n\nEs el lugar de descanso de la ciudad maldita de Zoram, destruida en el Diluvio y desde entonces sumergida bajo los mares. Se perdió para los elfos de la noche durante siglos. Perdida y casi olvidada.\n\nAhora, los nagas han regresado y en cuanto a sus motivos… los ignoramos. Pero el porqué importa poco; debemos ejecutar a estos desalmados ¡y devolverlos a los abismos!\n\nVuelve a mí cuando hayas completado tu misión.", progress = "No podemos permitir que los nagasss invadan nuestras costas, <nombre>. Es vital que vayas a La Ensenada de Zoram y completes tu misión.", completion = "Un trabajo de primera, &lt;nombre&gt;. Tus acciones en La Ensenada de Zoram contra los nagas son encomiables.\n\nSé que no fue fácil realizar semejante proeza ya que la fuerza y la astucia de los nagas es sobradamente conocida entre los elfos de la noche. Lo sabemos… porque compartimos una historia con ellos.\n\nUna historia que me guardo de repetir.", rewards = "Recibirás:\n\n"},
[1009] = {title = "Ruuzel", objective = "Llévale el Anillo de Zoram a Talen, cerca de La Ensenada de Zoram.", description = "Temo que los secretos de la estatuilla antigua permanezcan ocultos para siempre a menos que encuentres la llave de su ancestral cerradura.\n\nEsa llave es un anillo, el Anillo de Zoram. Pertenecía a los gobernantes de esta ciudad antes de que las olas del Diluvio la inundaran.\n\nDurante años el anillo estuvo perdido pero los nagas lo encontraron recientemente.\n\nSu jefe, Ruuzel, mora en una isla en los confines norte de Zoram. Te lo ruego, <nombre>, ¡recupera ese anillo para mí!", progress = "Se dice que el Anillo de Zoram es la clave de cualquier cerradura de la ciudad. ¿Lo tienes, <nombre>?", completion = "¡Encontraste el anillo! Gracias, &lt;nombre&gt;. Acepta esto a guisa de pago y que sepas que tus acciones de hoy permanecerán por siempre en mi memoria.", rewards = "Recibirás:\n\nTogas de Antigüedad"},
[1010] = {title = "Cabello de Bathran", objective = "Llévale 5 cabellos de Bathran a Orendil Hojasangre en Vallefresno.", description = "Existe una planta que solía crecer entre las Ruinas de Bathran hacia el norte. La planta se llamaba cabello de Bathran y era famosa por curar enfermedades espirituales.\n\nHay una niña enferma en el pueblo y creo que se trata de algo más que una enfermedad física. ¿Irás a las Ruinas de Bathran y buscarás cabello de Bathran para nosotros? Podría necesitar esa planta para curar debidamente a esa niña.\n\nLas Ruinas de Bathran se encuentran al norte de la Atalaya de Maestra y justo al sur de la frontera con Costa Oscura.", progress = "¿Has conseguido el cabello de Bathran, <nombre>? La salud de la niña se debilita por minutos...", completion = "¡Ah, tienes el cabello! Ahora haré un preparado para la niña y rezaré para que funcione.\n\nY... ¿dices que los Renegados están en las Ruinas de Bathran? Inquietante noticia. De lo más inquietante...", rewards = "Recibirás:\n\n"},
[1011] = {title = "Los males de los Renegados", objective = "Llévale una botella de enfermedad a Kayneth Ventoleve en Canción del Bosque.", description = "Los Renegados, el brazo de no-muertos de la Horda, se han infiltrado en Vallefresno y merodean entre nuestras ruinas y túmulos. Creemos que su objetivo es propagar enfermedades por la tierra de los elfos de la noche. ¡Tenemos que saberlo con seguridad!\n\nLos Renegados tienen un campamento al sur de este lugar, cerca de El Túmulo de Dor'danil, donde crean venenos y enfermedades y los embotellan. Infíltrate en su campamento y roba una botella de enfermedades. Vuelve con esa botella. La probaré y averiguaré para qué sirve.", progress = "¿Has encontrado el campamento, <nombre>? ¿Tienes una botella de enfermedad?", completion = "Muy bien, &lt;nombre&gt;. Ahora... al probar el contenido de esta botella ¡se revelarán los objetivos de los Renegados!", rewards = "Recibirás:\n\n"},
[1012] = {title = "Druidas dementes", objective = "Mata a los druidas Taneel Leñoscuro, Uthil Llamaluna y Mavoris Quiebranubes y vuelve junto a Kayneth Ventoleve en Canción del Bosque.", description = "Cuando luchaba contra los venenos de los Renegados, tuve una visión. Tuve una visión de los druidas en El Túmulo de Dor'danil, envenenados... ¡asesinados por los no-muertos! Ahora son fantasmas errantes, separados de sus cuerpos y enloquecidos.\n\nPara que los espíritus de los druidas descansen en paz, debes penetrar en El Túmulo de Dor'danil y destruir a los que antaño fueron grandes líderes: Taneel Leñoscuro, Uthil Llamaluna y Mavoris Quiebranubes.\n\nHazlo y vuelve aquí.", progress = nil, completion = "Sentí que la angustia del Túmulo de Dor'danil disminuía y supe que habías completado tu tarea.\n\nHas hecho un macabro trabajo, &lt;nombre&gt;, ya que los espíritus que has tenido que destruir eran meras víctimas de la perversión de los Renegados. Pero con su destrucción, esperemos que otros espíritus enloquecidos encuentren el descanso.", rewards = "Recibirás:\n\nPrenda de Emil"},
[1013] = {title = "El Libro de Ur", objective = "Lleva el Libro de Ur al guardián Bel'dugur al Apothecarium de Entrañas.", description = "<nombre>, en el Castillo de Colmillo Oscuro hay un libro que deseo para mi colección: el Libro de Ur. Ur era un gran mago de Dalaran antes de que llegara la Plaga; sus estudios sobre otros mundos son muy valiosos para... ciertas facciones entre los Renegados. \n\nVe al castillo y tráeme el libro. Informaré a la Dama Oscura de tus servicios...", progress = "¿Encontraste el libro?", completion = "Perfecto; esto completa mi colección de obras de Ur. Era sabio, pero su conciencia lo mantenía alejado del poder verdadero. Así, cuando la Plaga lo puso a prueba, falló.\n\nLos Renegados no podemos permitirnos tal debilidad.\n\nNo si queremos vencer.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas pardasBrazales con cierre de acero\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBrazales con cierre de aceroBotas pardas"},
[1014] = {title = "Arugal debe morir", objective = "Mata a Arugal y lleva su cabeza a Dalar Tejalba en El Sepulcro.", description = "Arugal sigue viviendo en el Castillo de Colmillo Oscuro. No podemos reclamar Argénteos como bastión estratégico para la Dama Oscura hasta que no hayamos matado a Arugal.\n\nMe encargaré de erradicar su magia, <nombre>. Pero dejo en tus manos que Arugal se encuentre con la muerte que tanto merece.\n\nVe al Castillo de Colmillo Oscuro y pon fin de una vez por todas a los terribles hechizos de Arugal. ¡Tráeme la cabeza del malvado mago!", progress = "Con la muerte de Arugal, podemos aumentar el bastión de la Dama Oscura en Lordaeron.", completion = "Por fin el Bosque de Argénteos está libre del vicio de ese malvado Arugal. Has hecho un gran servicio a la Dama Oscura, &lt;nombre&gt;. Tu tenacidad será recompensada.", rewards = "Te van a lanzar el siguiente hechizo:\n\nIntelecto Arcano\n\nRecibirás:\n\nLacre de Sylvanas"},
[1015] = {title = "La nueva frontera", objective = "Habla con el archidruida Fandral Corzocelada en el Enclave Cenarion de Darnassus sobre la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.", description = "¡Atención, héroes!\n\n¡El Círculo Cenarion busca voluntarios sanos para explorar las vastas fronteras nuevas de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en representación suya.\n\nTodos aquellos que estén interesados pueden solicitar audiencia en el Círculo Cenarion. ¡Hablad con el archidruida Fandral Corzocelada de Darnassus para más información!", progress = nil, completion = "¿Qué te hace pensar que deseo que alguien como tú venga a molestarme, &lt;clase&gt;? Ahora mismo voy a abandonar tooodos esos insignificantísimos quehaceres para ayudarte a localizar el chisme que estás buscando. Sea como sea, el bienestar de todo Darnassus, no, de todo Teldrassil, debería estar en segundo lugar. Lo más importante es conseguir un adorno para tu colección.\n\nEsos imbéciles del Claro de la Luna me hacen perder el tiempo con sus llamaditas de ayuda, ¿y ahora tengo que aguantar esto?", rewards = "Recibirás:\n\n"},
[1016] = {title = "Brazales elementales", objective = "Reúne 5 brazales elementales intactos y aplícales el pergamino de adivinación. A continuación, llévale el pergamino adivinado a la centinela Velene Estrella en el Refugio Brisa de Plata.", description = "Toma este pergamino de adivinación, puede revelar al creador de estos elementales. Pero necesitarás algo suyo que usar de componente para concentrar la magia del pergamino, sus brazales deberían ir muy bien.\n\nUnos pocos brazales intactos de esas criaturas deberían bastar. Los otros brazales no servirían para lo que nos proponemos, aunque puede que les saques algo si se los vendes a un comerciante. Cuando consigas los brazales intactos, intenta usar el pergamino. Vuelve aquí con los resultados y ya veremos cómo proseguimos.", progress = "Puesto que debes destruir los brazales para matar los elementales, no será fácil encontrar los que contienen la información que el pergamino de adivinación requiere. Puedes intercambiar brazales con otros para conseguir los que necesitas o ¿acaso tienes ya el papel adivinado?", completion = "Muy bien, &lt;nombre&gt;, con esto deberíamos conseguir la información que necesitamos para descubrir al culpable. ¿Qué te parecería enfrentarte al mago que llena nuestras pozas de elementales corruptos? La corrupción de Vallefresno y más allá me revuelve el estómago, pero que un mago esté intentando llevar aún más lejos esta destrucción me pone enfermo como nunca nada antes lo ha hecho. Haremos que pague por ello.", rewards = "Recibirás:\n\n"},
[1017] = {title = "Mago invocador", objective = "Mata a Sarilus Fuenteviciosa y lleva su cabeza a la centinela Velene Estrella en el Refugio Brisa de Plata.", description = "Solo hay un mago no-muerto en Los Baldíos capaz de envilecer a tantos elementales de agua. También es el único lo suficientemente inconsciente para intentarlo. Sarilus Fuenteviciosa probablemente se encuentre en la cumbre de Cima Calígine, riéndose de mí.\n\n<nombre>, debes aventurarte en territorio enemigo y acabar con ese bellaco. Ya estoy cansada de su insensatez y tenemos que demostrarle lo que les ocurre a quienes irritan a las centinelas.\n\n¡Destrúyelo y tráeme su cabeza! Ve con cuidado, la Horda podría estar vigilándote.", progress = "¿Ya está? ¿Ha muerto Sarilus Fuenteviciosa?", completion = "Ahora veo su repugnante sonrisa borrada de su rostro para siempre. Lo has hecho muy bien, &lt;nombre&gt;. Acepta este presente, la Luz de Elune, bébela cuando todo parezca perdido y la muerte se acerque. Podría salvarte, la Diosa mediante.", rewards = "Recibirás:\n\nLuz de Elune"},
[1018] = {title = "La nueva frontera", objective = "Habla con el archidruida Hamuul Tótem Rúnico en Alto de los Ancestros, en Cima del Trueno, sobre la llamada del Círculo Cenarion a explorar las fronteras de Kalimdor.", description = "¡Atención!\n\n¡El Círculo Cenarion busca voluntarios para explorar las extensas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un yermo de horrores inimaginables; el Círculo necesita adeptos de honor que le ayuden.\n\nLos interesados deben solicitar audiencia con el Círculo. Para más información, contacten con el archidruida Hamuul Tótem Rúnico en Alto de los Ancestros, Cima del Trueno.", progress = nil, completion = "Saludos, &lt;clase&gt;. \n\nMe alegra ver que alguien como tú se preocupa por el bienestar de la naturaleza. He oído hablar de tus hazañas; te doy la bienvenida. Al mirarte tengo la certeza de que el Círculo Cenarion recibirá la ayuda que necesita. \n\nNuestra conexión con el Círculo en Claro de la Luna trasciende las barreras raciales y políticas; no lo olvides nunca.", rewards = "Recibirás:\n\n"},
[1019] = {title = "La nueva frontera", objective = "Habla con el archidruida Fandral Corzocelada en el Enclave Cenarion de Darnassus sobre la llamada del Círculo Cenarion a explorar las fronteras de Kalimdor.", description = "¡Atención!\n\n¡El Círculo Cenarion busca voluntarios para explorar las extensas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un yermo de horrores inimaginables; el Círculo necesita adeptos de honor que le ayuden.\n\nLos interesados deben solicitar audiencia con el Círculo. Para más información, dirigirse al archidruida de Darnassus, Fandral Corzocelada.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1020] = {title = "El remedio de Orendil", objective = "Llévale el remedio de Orendil a Pelturas en Astranaar.", description = "<nombre>, he creado un remedio que, creo, ayudará a la niña enferma. Llévala a Astranaar y dásela a su padre, Pelturas Lunablanca.\n\nPara llegar hasta Astranaar, sigue la carretera que va hacia el sur, y luego al este.", progress = "Mis disculpas, no tengo tiempo para hablar. Mi hija, Relara, ¡está gravemente enferma!", completion = "¿Esto es de Orendil?\n\nTiene un gran talento con hierbas y curaciones. Esta cura me da esperanza, antes de tomar un poco...", rewards = "Recibirás:\n\n"},
[1021] = {title = "¡Vil sátiro! ¡Dríade en peligro!", objective = "Encuentra a Anilia en Xavian.", description = "Oye, no sé quién eres pero necesito que me ayudes. Mis hermanas se marcharon para recuperar la Rama de Cenario del sátiro. Dijeron que volverían pronto pero ¡ya llevan dos días!\n\n<nombre>, ¡tienes que encontrarlas! Andaban buscando las ruinas de un templo de los elfos de la noche en Xavian hacia el noreste. No sé qué pensar. Busca a Anilia, las guiaba en su búsqueda.", progress = nil, completion = "A juzgar por la herida abierta en el costado, Anilia está al borde de la muerte.", rewards = "Recibirás:\n\n"},
[1022] = {title = "La Vega del Aullido", objective = "Ve a La Vega del Aullido y estudia el Libro de Mel'Thandris, vuelve entonces junto a la Centinela Melyria Sombrigélida en el Santuario de Aessina.", description = "Aunque hemos puesto muchos recursos y esfuerzo en expulsar hacia el norte a los demonios restantes en Frondavil, no hemos tenido mucho éxito. Hemos logrado mantener apartados de Vallefresno a casi todos los demonios.\n\nHacia el norte, cerca de los límites de Frondavil, el altar en ruinas de Mel'Thandris ha sido tomado por unos misteriosos hombres lobo. Sus llamadas te hielan la sangre y han hecho que se conozca esa zona como la Vega del Aullido. Es posible que el Libro de Mel'Thandris que se conserva en el altar arroje algo de luz sobre el motivo que ha traído a estos hombres lobo.", progress = nil, completion = "El Libro de Mel'Thandris funciona de extraña manera. Los acontecimientos que registra, los que muestra... Nadie entiende realmente por qué hace lo que hace.\n\nNo obstante, es evidente que lo que la sacerdotisa Cantoestelar recibió, esta Guadaña de Elune, merece algo más de investigación.", rewards = "Recibirás:\n\n"},
[1023] = {title = "La limpieza de Raene", objective = "Encuentra la gema resplandeciente y llévasela a Raene Correlobo en Astranaar junto con el diario de Teronis.", description = "Mientras rebuscas en las pertenencias de Teronis, encuentras un diario bien conservado.\n\nTomas el diario y dejas el cadáver de Teronis como está.\n\nSolo falta encontrar la gema que Teronis estaba buscando. Raene mencionó que estaba en esta zona. A lo mejor la tienen los murlocs...", progress = "Ah, <nombre>, bienvenido de nuevo.", completion = "Es terrible, &lt;nombre&gt;. ¡¿Teronis ha muerto?!\n\nYa llegará la hora del luto pero ahora tenemos que centrar nuestros esfuerzos. Enviaré a algunas centinelas para que recuperen el cuerpo de Teronis cuando antes.\n\nTienes el diario de Teronis y la gema que andaba buscando. ¿Te sientes capaz de completar su tarea? Descansaría mejor si supiera que no murió en vano.", rewards = "Recibirás:\n\n"},
[1024] = {title = "La limpieza de Raene", objective = "Encuentra a Shael'dryn en la poza de la luna al noreste.", description = "Tendrás que investigar por tu cuenta pero según las notas de Teronis, parece que tu siguiente paso será encontrar las demás piezas de la vara que Dartol creó. Empezaría por dirigirme a la Poza de la Luna al este del Lago Iris. Cuando estés $glisto;lista;, sigue la carretera y gira hacia el norte al alcanzar un pequeño lugar de descanso a tu izquierda.\n\nUna dríade llamada Shael'dryn es más indicada para guiarte. Búscala.\n\nLleva la gema contigo, Shael'dryn la necesitará si te va a ayudar a recrear la vara.", progress = "Hola. ¿Qué tal?", completion = "¿Te envía Raene? Entonces será grave. ¿En qué puedo ayudarte?", rewards = "Recibirás:\n\n"},
[1025] = {title = "Una defensa agresiva", objective = "Mata a 1 vigilante del cubil, 2 ursas, 10 totémicos y 12 guerreros de la tribu de los Corruptos y vuelve junto a Raene Correlobo en Astranaar.", description = "Antes de que vayas a la Poza de la Luna, tengo otra tarea que ayudará al pueblo de Vallefresno si te apetece poner a prueba tus habilidades.\n\nPor la carretera que va hacia el este antes de llegar al Río Falfarren, los furbolgs han estado asaltando a los viajeros. Sus torturadas mentes alimentándose del miedo y su apetito saciándose con sangre inocente.\n\nAyuda a las centinelas a proteger las tierras mostrando hacia esos monstruos la misma misericordia que ellos para con los nuestra clase.\n\nMe parece que sus campamentos están al sur de la carretera... ve con cuidado.", progress = nil, completion = "Lo has hecho muy bien, &lt;nombre&gt;. Gracias.", rewards = "Recibirás:\n\nPan de maíz húmedoZumo de melón"},
[1026] = {title = "La limpieza de Raene", objective = "Encuentra la llave de madera y una pieza de la vara de Dartol antes de volver junto a Shael'dryn en la poza de la luna.", description = "Ojalá tus nuevas fueran mejores.\n\nRaene tiene razón, he oído hablar de la creación de Dartol y de los antárboles de la región, al menos una vez. Ahora se han corrompido, su naturaleza se ha pervertido.\n\nSi tu objetivo es encontrar la siguiente pieza de la vara, tendrás que encontrar la llave que abre el baúl en el que se encuentra.\n\nLa llave de madera está en uno de los antárboles al noreste desde aquí, cerca de Frondavil. Cuando la tengas, busca un pequeño claro al oeste de la carretera cerca de los límites de Frondavil... el baúl debería estar escondido por ahí.", progress = "Me indigna que los antárboles se hayan corrompido. Ojalá pudiera hacer más por ayudarlos.", completion = "Me alegra que hayas vuelto, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[1027] = {title = "La limpieza de Raene", objective = "Encuentra la última pieza de la vara de Dartol y vuelve junto a Shael'dryn en la poza de la luna.", description = "Se entregó la última pieza de la vara a los druidas de Dor'danil para su custodia. No tengo la más remota idea de dónde la ocultaron. Lo siento, no puedo ayudarte más.\n\nSolo puede decirte que sigas la carretera que lleva a Los Baldíos. Los druidas se refugiaron al este de esa carretera pero no serán de mucha ayuda: los asesinaron recientemente… intento no pensar en ello demasiado.\n\nCuando encuentres la última pieza, tráemela y recompondré la vara.", progress = "¿Has encontrado la última pieza de la vara, <nombre>?", completion = "¿Has encontrado la última pieza? Fantástico para ti... y para Raene.\n\nBueno, habrá que ver si la arreglamos, ¿no?", rewards = "Recibirás:\n\n"},
[1028] = {title = "La limpieza de Raene", objective = "Encuentra el Santuario Oculto y encanta de nuevo la vara de Dartol.", description = "Aquí tienes la vara completada, <nombre>. Volver a construirla ha sido sencillo... encontrar el altar que tiene el poder para cargarla de nuevo será la parte complicada.\n\nCentrándote y con un poco de empeño, no debería ser demasiado difícil para alguien como tú, con tantos logros en tu haber.\n\nDirígete al sureste desde aquí, hay muy pocos caminos, así que ten cuidado, y mantén la vista hacia el norte. El altar se encuentra oculto en las montañas que rodean este claro.\n\nUna vez allí, coloca la vara en el interior del altar… el resto es magia. Je, je.", progress = "Aunque sutil, se siente una poderosa fuerza en el altar y los árboles circundantes.\n\nEmpieza por colocar la vara en el interior del altar y se oirá un repiqueteo suave alrededor del altar que empezará a irradiar luz...", completion = "Cuando se apague el brillo, verás que la vara vuelve a estar encantada en la pila del altar.", rewards = "Recibirás:\n\nVara de transformación de Dartol"},
[1029] = {title = "La limpieza de Raene", objective = "Vuelve junto a Raene Correlobo en Astranaar.\n\nObjeto provisto: \n", description = "Te sugiero que encuentres a Raene y les digas que la vara está recompuesta. Seguro que sabrá aconsejarte sobre el siguiente paso.\n\nSuerte, <nombre>.", progress = nil, completion = "¿Has recreado la vara que Teronis se había propuesto encontrar? ¡Qué alegría oírlo!\n\nTeronis estaría orgulloso de ver que su misión se completó incluso después de su trágico fin.", rewards = "Recibirás:\n\n"},
[1030] = {title = "La limpieza de Raene", objective = "Encuentra un fúrbolg sin corromper y, mediante el poder de la vara de la Transformación de Dartol, habla con él.\n\nObjeto provisto: \n", description = "Desafortunadamente, no estoy del todo segura de cuál era su intención.\n\nSi era cierto lo que contaba del poder de la vara, entonces hasta te permitirá adoptar otra forma. Qué forma, lo ignoro.\n\nQuizás pretendía usar sus poderes para acercarse a los furbolgs pero incluso la criaturas sin corromper lo asesinarían si no iba con cuidado. Transformado gracias a la vara, Dartol tendría que hablar con un fúrbolg que no estuviera bajo los efectos del musgovil.\n\nEmpezaría la búsqueda al sureste del Lago Mystral.", progress = nil, completion = "Sí, sí... eres como yo, pero aún completo. Aún limpio.\n\nNo estoy seguro de cómo has conseguido librarte de las atrocidades que los elfos de la noche han infligido a nuestro pueblo pero me alegro de saber que ya no estoy solo.\n\nLa corrupción corre ahora por las venas de los nuestros, joven, corrupción que los orgullosos elfos de la noche nos trajeron. Son los que le han hecho esto a nuestro pueblo... A ellos culpo por haberme apartado de mi tribu.\n\nDe noche aún puedo ver las fogatas en la oscuridad.", rewards = "Recibirás:\n\n"},
[1031] = {title = "La Rama de Cenario", objective = "Mata a Geltharis y llévale la Rama de Cenario a Illiyana en el Santuario de Aessina.", description = "Anilia te pone las manos en los hombros y te acerca. Entre jadeos, señala el arco al norte, antes de que sus ojos se cierren y el último soplo de vida salga de su boca. \"Se la ha llevado... Geltharis...\" En tu corazón sabes que el deseo póstumo de Anilia es que la Rama de Cenario vuelva a las manos de la dríade.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nManto feéricoFaja de escamas brillantes\n\nTambién recibirás:\n\nManto feéricoFaja de escamas brillantes"},
[1032] = {title = "¡Muerte a los sátiros!", objective = "Llévale 16 cuernos de sátiro a Illiyana en el Santuario de Aessina.", description = "No puedo creer que Anilia ya no esté... Solíamos pasarnos los días jugando juntos en el bosque. Casi siento... tristeza.\n\nSé de algo que podría hacer que me sienta mejor. ¡Por favor, ejecuta a los sátiros que me han arrebatado a Anilia! Tráeme sus cuernos como prueba de tus actos y tendrás mi gratitud eterna.", progress = "Te lo ruego, ayúdame a vengar la muerte de Anilia.", completion = "Gracias, amable &lt;raza&gt;. El espíritu de mi hermana se llena de gozo con tus esfuerzos.", rewards = "Recibirás:\n\n"},
[1033] = {title = "Lágrima de Elune", objective = "Llévale la lágrima de Elune a Pelturas en Astranaar.", description = "Esperaba que el remedio de Orendil salvara a Relara pero me temo que solo ha frenado la enfermedad de mi niña. ¡Tenemos que encontrar una medicina más eficaz! Por suerte, Orendil me habló de otros remedios que debía intentar si el suyo no funcionaba.\n\nLa lágrima de Elune es un tipo de piedra que solo se halla en un lugar: la isla del Lago Iris, al este de Astranaar. Orendil dijo que la piedra se llevaría la corrupción... por favor, <nombre>, ¡tráeme la lágrima de Elune y reza para que acabe con la enfermedad de mi niña!", progress = "<nombre>, ¿has encontrado el Lago Iris? ¿Tienes la Lágrima?", completion = "¡Vaya, la encontraste! Colocaré la lágrima en un guiso y rezaré para que la cure.\n\n¡Gracias! ¡Gracias, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[1034] = {title = "Las ruinas del Polvo Estelar", objective = "Llévale 5 puñados de polvo estelar a Pelturas en Astranaar.", description = "La lágrima de Elune logró curar en parte la enfermedad de mi hija. Tiene menos pesadillas y le estoy agradecido a la diosa por su bendición. Pero… sigue teniendo la fiebre muy alta.\n\nOrendil me habló de un polvo de escarcha que se encuentra en Las Ruinas del Polvo Estelar. Quizás mitigue la fiebre de Relara. Te ruego que consigas un poco de ese polvo y me lo traigas.\n\nLas Ruinas del Polvo Estelar se encuentran en el sur, <nombre>. Que Elune haga breve tu viaje.", progress = "¿Tienes ya el polvo estelar, <nombre>?", completion = "¡Has conseguido el polvo! Con él prepararé una cataplasma, eso debería bajar la fiebre de Relara.\n\nGracias otra vez, &lt;nombre&gt;. Sin tu ayuda, mi hija habría perecido.", rewards = "Recibirás:\n\n"},
[1035] = {title = "Lago Cielo Estrellado", objective = "Recupera una piedra lunar caída del Oráculo Sombramatorral y llévasela a Pelturas en Astranaar.", description = "La fiebre de Relara ya queda atrás, pero todavía sigue débil. No creo que con el tiempo solamente logre recuperar la energía perdida. Creo que va a necesitar la energía de una piedra lunar.\n\nLas solíamos recoger en el Lago Cielo Estrellado, al sureste... hasta que los Sombramatorral llegaron.\n\nSu líder, un oráculo Sombramatorral, recogió todas las que había y las esconde como su tesoro. Tu misión consiste en recuperar una.\n\nY para hacerlo, creo que deberás matar al líder.", progress = "¿Te has enfrentado ya al Oráculo de Sombramatorral, <nombre>? De ser así, te ruego me entregues la piedra lunar caída. ¡A Relara le quedan fuerzas para unos suspiros más!", completion = "Oh, &lt;nombre&gt;, ¡has salvado a mi hija! ¡Sé que el poder de la piedra lunar le devolverá las fuerzas!\n\nNunca podré pagarte por lo que has hecho pero... te ruego aceptes esto. Aparte de Relara, es mi más preciada posesión.", rewards = "Podrás elegir una de estas recompensas:\n\nArmadura riachueloEsposas bestialesHombrera luminiscente\n\nTambién recibirás:\n\nArmadura riachueloEsposas bestialesHombrera luminiscente"},
[1036] = {title = "¡Adelante, pillín!", objective = "Habla con el maestro de flota Firallon abordo del Valle carmesí cerca de la costa de la Vega de Tuercespina.", description = "<nombre>... sí, he oído hablar de ti. Me han dicho que no eres <amigo/amiga> de la Bahía del Botín, igual que nosotros, los bucaneros Velasangre. El maestro de flota Firallon está buscando reclutas robustos y fuertes como tú para una misión especial.\n\nSi te interesa, dirígete al Velo Carmesí y habla con él bajo cubierta. Está en La Costa Salvaje, al este de aquí, junto a las Mareas Vivas y la Damisela Afortunada.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1037] = {title = "Velinde Cantoestelar", objective = "Habla con Thyn'tel Tejespada en el Bancal del Guerrero en Darnassus.", description = "Velinde Cantoestelar era mi predecesora aquí en el Bosque de Vallefresno. Al principio parecía que tenía la situación bajo control pero poco a poco sus esfuerzos flaquearon. Un buen día desapareció sin más.\n\nMe enviaron aquí para continuar su trabajo. Aunque me temo que no sé nada de la sacerdotisa. Quizás Thyn'tel Tejespada, una comandante de las centinelas, sepa algo sobre su desaparición que no me haya comentado.\n\nSeguro que entenderá la trascendencia de tal información.", progress = nil, completion = "¿Velinde Cantoestelar? Que la sacerdotisa lleva algún tiempo desaparecida no es ningún secreto. Pero en cuanto a más detalles sobre los acontecimientos que rodearon su partida... haría falta una razón de peso para divulgar tal información.", rewards = "Recibirás:\n\n"},
[1038] = {title = "Las pertenencias de Velinde", objective = "Busca en diario de Velinde en su cofre y llévaselo, junto con la llave, a Thyn'tel Tejespada a Darnassus.", description = "¿El Libro de Mel'Thandris te enseñó esto? Supongo que no hacemos daño a nadie si te permito examinar sus pertenencias. Esta llave abre el cofre en el que ella guardaba sus cosas en el barracón de Centinelas. Llevaba un diario de servicio. Si hay algo que aprender por aquí, estará en su diario.\n\nDebería decirte que los Centinelas creen que tenía sus propias razones para irse y esperan que vuelva en cualquier momento. La sacerdotisa ha demostrado en el pasado que merece toda nuestra confianza.", progress = "¿Qué has descubierto?", completion = "Parece que la desaparición de Velinde es aún más preocupante de lo que parecía en un principio. Aún así, no quiero pensar que haya tenido un mal final. Quizás nos beneficiaría contactar con ella.\n\n&lt;nombre&gt;, como has demostrado tu interés en este asunto, quisiera pedirte tu ayuda.", rewards = "Recibirás:\n\n"},
[1039] = {title = "El puerto de Los Baldíos", objective = "Ve a Trinquete y habla con Vertipeluca, el maestro de muelles.", description = "Trinquete es el único puerto de Los Baldíos. Seguramente Velinde encontró un navío mercante en Trinquete que la llevó a la Cala Aguasnegras, en Azeroth. Hemos tratado poco con los goblins que llevan el puerto, pero el capataz del puerto debería tener información sobre los barcos de pasajeros.\n\nSigue el camino del sureste que atraviesa Vallefresno y te encontrarás en Los Baldíos. Pero ve con cuidado, <nombre>. Hay guerreros de la Horda patrullando esa tierra. En el puerto estarás al seguro.", progress = nil, completion = "&lt;raza&gt;, ¿eh? He de admitir que no solemos tener a muchos de los tuyos por aquí. Estás buscando a alguien, ¿eh? Dame un momento, seguro que lo tengo escrito en alguno de mis libros.\n\nPero la gente que pasa por aquí es mucha, así que necesito un momento.", rewards = "Recibirás:\n\n"},
[1040] = {title = "Pasaje a Bahía del Botín", objective = "Lleva un barco a Bahía del Botín y habla con el caravanero Ruzzgot.", description = "Ah sí, por fin lo encontré. Tenía que haberme dicho que había estado por aquí hace tanto tiempo. Veamos.\n\nVelinde. Reservó un billete para Bahía del Botín en el Pigargo negro. No figura nada más, así que se supone que llegaron a puerto sanos y salvos.\n\nMe temo que no puedo ayudarte mucho más… bueno, ella preguntó sobre una expedición por tierra en esa parte del mundo y le hablé de Ruzzgot, un conductor de caravanas establecido en Bahía del Botín. Puede que la tal Velinde viajara con él.\n\nVenga, vete ya. Tengo otras cosas que hacer.", progress = nil, completion = "¿Sí? Vaya, no tienes pinta de traer mercancía para enviar, así que o buscas trabajo en mi caravana, y ahora no hay ninguno, o te has equivocado de goblin.\n\n¿De qué se trata?", rewards = "Recibirás:\n\n"},
[1041] = {title = "La ruta de la caravana", objective = "Habla con el dependiente Daltry en Villa Oscura.", description = "Parece ser que me había equivocado contigo, y eso no me suele pasar todos los días.\n\nResulta que recuerdo a esa Velinde a quien estás buscando. No todos los días una sacerdotisa elfa de la noche quiere viajar en una vieja y sucia, aunque genial para transportes, caravana como la mía.\n\nNos separamos en el norte, ella se dirigía a Villa Oscura. El secretario de allí guarda todo tipo de registros. Quizás tenga alguna información útil.\n\nTen cuidado en la jungla, es un lugar lleno de peligros mortales en todo momento.", progress = nil, completion = "Con todos los extraños sucesos de los últimos años, guardo un registro de todos los forasteros. Nunca sabes cuándo va a ser necesario... Aunque no suele ser para un asunto agradable cuando se necesita. No pienses que estoy diciendo nada malo de esa Velinde a quien buscas.", rewards = "Recibirás:\n\n"},
[1042] = {title = "La familia Carevin", objective = "Habla con Jonathan Carevin en Villa Oscura.", description = "No, no tengo constancia de que ninguna Velinde Cantoestelar haya pasado por Villa Oscura. Aunque debo decir que no me imagino a una sacerdotisa elfa de la noche alojándose en la posada, ya me entiendes.\n\nPero de esos hombres-lobo de los que hablabas, sí que he oído algo. Precisamente el otro día vino Calor con una ristra de cabezas de hombres-lobo. Trabaja con la familia Carevin. Son cazadores de demonios, no-muertos y otras monstruosidades. Habla con Jonathan. Es el cabeza de familia.", progress = nil, completion = "Estos son tiempos oscuros. Cada rama que se mece al viento susurra noticias funestas y el mal puede encontrarse bajo cada piedra del camino.\n\nY en estos tiempos, un perfecto extraño entra en mi casa y me habla de ferocanis... No pediré perdón por mis sospechas, solo respuestas.\n\nY respuestas es lo que obtendré.", rewards = "Recibirás:\n\n"},
[1043] = {title = "La Guadaña de Elune", objective = "Busca signos de la Guadaña de Elune y regresa a Villa Oscura, con Jonathan Carevin.", description = "Tu historia suena convincente... No acabo de entender tus motivos pero, si tus negocios en el Bosque del Ocaso implican cazar ferocanis en cualquier número, no necesito entender nada.\n\nHay una mina al sur que ha sido invadida por ferocanis. Salieron de la nada pero, por lo que sabemos, es precisamente donde fueron encontrados por vez primera.\n\nTú ocúpate de tus negocios. Y si encuentras algo importante, compártelo conmigo, si no es mucho pedir. Aceptaremos cualquier ayuda en nuestra guerra contra el mal.", progress = "Los bosques son peligrosos para quienes no están bien preparados. Ten tus armas bien a mano y mantente alerta, o tu siguiente excursión al bosque encantado será la última.", completion = "Tu historia es interesante, &lt;nombre&gt;. Aunque la influencia de la familia Carevin no es tanta, no podremos encontrar las raíces de estos problemas y acabar con ellos para siempre. Pero haremos lo que podamos para contener el número de ferocanis en el Bosque del Ocaso.\n\nYa he dado instrucciones a Calor para que esta sea su prioridad.", rewards = "Recibirás:\n\n"},
[1044] = {title = "La respuesta a las preguntas", objective = "Regresa a Darnassus y habla con Thyn'tel Tejespada.", description = "Solucionaremos el problema de los ferocanis, no lo dudes. Ese mal que tu amiga trajo a nuestros bosques será contenido. Y has de saber que no le guardo rencor por sus actos. En estos tiempos se fraguan extraños acontecimientos, <nombre>, la oscuridad no descansa.\n\nNo te entretengo más. Sospecho que hay otros que también deberían saber de lo que encontrado en las minas del Bosque del Ocaso.", progress = nil, completion = "Así que esta fue la suerte que corrió Velinde... Esta noticia decepcionará a la suma sacerdotisa... Eran buenas amigas antes de que desapareciera. Aún así, la Guadaña de Elune le fue concedida por nuestra diosa y es inquietante pensar que ya no esté en manos de su sierva.\n\nPero de este asunto trataremos en otro momento. No sabes cuánto te agradezco lo que has hecho.", rewards = "Podrás elegir una de estas recompensas:\n\nArco LunarisVarita de destello lunar\n\nTambién recibirás:\n\nArco LunarisVarita de destello lunar"},
[1045] = {title = "La limpieza de Raene", objective = "Mata a Ran Sangradientes y 4 guardias Sangradientes. Después regresa con Krolg cerca del Lago Mystral.", description = "Los elfos de la noche existen desde la noche de los tiempos pero su arrogancia destruye hasta su propio mundo. Y ahora también destruye nuestras vidas. Pero eso es algo que ya no se puede controlar...\n\nCulpo a los elfos de muchas cosas pero seguimos siendo responsables de nuestros actos. Incluso más allá de la corrupción, somos capaces de causar los mayores males.\n\nRan Sangradientes es uno de estos males. Sediento de poder, codicioso... la corrupción lo ha fortalecido aún más. Siento el odio más encendido por los elfos pero no deseo el mal de su estirpe, así que debemos vigilar la nuestra.", progress = nil, completion = "Lo has hecho muy bien, mi amigo.\n\nEl bosque aún está contaminado pero al menos hemos hecho algo para devolverle parte de su gloria pasada. Si quieres volver aquí, siempre disfruto de la compañía de uno de los míos.\n\nCuídate. Quizás nuestras tribus vuelvan a encontrarse en tiempos menos violentos.", rewards = "Recibirás:\n\n"},
[1046] = {title = "La limpieza de Raene", objective = "Llévale la calavera de Ran Dentesangre y la vara de la Transformación de Dartol a Raene Correlobo en Astranaar.", description = "Vuelve a tu propia tribu y háblales de tu victoria aquí.\n\nEnséñales tu trofeo y date un baño de gloria...", progress = "Las centinelas hacen lo que pueden aquí en Vallefresno pero es gracias a elfo de la noches como tú que hemos recuperado el terreno.", completion = "¿Así que esta es la calavera de la amenaza fúrbolg más feroz de Vallefresno?\n\nTus facultades son todo un portento, &lt;nombre&gt;. Las centinelas no pueden agradecértelo lo suficiente. Espero que podamos volver a contar contigo en el futuro para ayudar a nuestro pueblo.\n\nPondré esta calavera y la vara de Dartol en un lugar de veneración para que los demás las vean.\n\nTeronis estaría orgulloso de ti. Gracias otra vez.", rewards = "Podrás elegir una de estas recompensas:\n\nPiedra glacialHoja de entrañas\n\nTambién recibirás:\n\nAnillo de plata puraPiedra glacialHoja de entrañas"},
[1047] = {title = "La nueva frontera", objective = "Ve a Darnassus, al Enclave Cenarion y habla con el archidruida Fandral Corzocelada sobre la llamada del Círculo Cenarion para explorar las fronteras de Kalimdor.", description = "¡Atención, héroes!\n\n¡El Círculo Cenarion busca voluntarios sanos para explorar las vastas nuevas fronteras de Kalimdor! Al oeste de Tanaris y Gadgetzan se extiende un páramo lleno de inimaginables peligros. El Círculo necesita voluntarios honorables para explorarlos en representación suya.\n\nTodos aquellos que estén interesados pueden solicitar audiencia en el Círculo Cenarion. ¡Hablen con el archidruida Fandral Corzocelada de Darnassus para más información!", progress = nil, completion = "¿Qué te hace pensar que deseo que alguien como tú venga a molestarme, &lt;clase&gt;? Ahora mismo voy a abandonar tooodos mis insignificantísimos quehaceres para ayudarte a localizar el chisme que estás buscando. Porque, POR SUPUESTO, el bienestar de Darnassus o, mejor dicho, el de tooodo Teldrassil, debe quedarse aparcado hasta que tú consigas ese adorno para tu colección.\n\nEsos tontos del Claro de la Luna me hacen perder el tiempo con su petición de ayuda ¿y ahora resulta que tengo que soportar también esto?", rewards = "Recibirás:\n\n"},
[1048] = {title = "En el Monasterio Escarlata", objective = "Mata a la alta inquisidora Melenablanca, al comandante Escarlata Mograine, a Herod el campeón Escarlata y al domador de jaurías Loksey, y después ve a ver de nuevo a Varimathras a Entrañas.", description = "Mientras la Dama Oscura se encarga de retener el avance del rey Exánime por el norte, yo me ocupo de los problemas que tienen lugar cerca de nuestro hogar.\n\nSin duda, los humanos que se hacen llamar la Cruzada Escarlata representan la mayor amenaza en nuestra zona. Los Claros de Tirisfal están atestados de estas pequeñas hormigas rojas.\n\nVe al Monasterio, <clase>, y acaba con la alta inquisidora Melenablanca y sus principales tenientes.", progress = nil, completion = "Has servido con diligencia a la Dama Oscura, &lt;clase&gt;. Lo que te pedí no era tarea fácil.\n\nSe tendrá en cuenta tu lealtad a los Renegados y siempre será recordada tu tenacidad en el combate.", rewards = "Podrás elegir una de estas recompensas:\n\nEspada de AugurioCaña proféticaCollar de sangre de dragón\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nEspada de AugurioCaña proféticaCollar de sangre de dragón"},
[1049] = {title = "El Compendio de los Caídos", objective = "Recupera el Compendio de los Caídos del Monasterio que se encuentra en los Claros de Tirisfal y regresa ante Sabio Buscaverdad, que está en Cima del Trueno.", description = "Las intenciones del Anillo de la Tierra para con nuestros hermanos eran puras. ¿Pero quién sabe las motivaciones de los que confabulaban con el Consejo de los Ancianos?\n\nLa historia de los Renegados con los que nos aliamos está plagada de grandes engaños, de ahí que nuestro pacto con ellos haya sido breve. Quizás Cairne fue lo suficientemente inteligente para atender a las advertencias de Orgrimmar...\n\nEn lo más profundo del Monasterio Escarlata se encuentra el Compendio de los Caídos, custodiado por agresivos y fanáticos humanos. Su método proviene de la locura pero su trabajo de investigación puede resultar de utilidad.", progress = "Cairne es un líder valiente y noble y confío en él ciegamente, cosa que no puedo decir de otros dirigentes.\n\nCuando cuente con todos los datos necesarios, podré aconsejar al Jefe.\n\n¿Has tenido oportunidad de viajar al Claro de Tirisfal? ¿Al Monasterio Escarlata? El Compendio de los Caídos es una pieza más de este complicado puzzle...", completion = "¡El Compendio de los Caídos! Me empezaba a preguntar si ese libro realmente existía.\n\nHas servido fielmente a los tauren de Cima del Trueno, &lt;nombre&gt;. Tu dedicación no se olvidará.", rewards = "Podrás elegir una de estas recompensas:\n\nProtector vilRodela Piedra de potenciaOrbe omega\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nProtector vilOrbe omegaRodela Piedra de potencia"},
[1050] = {title = "Mitología de los Titanes", objective = "Coge Mitología de los Titanes en el monasterio y llévaselo a la bibliotecaria Mae Palipolvo a Forjaz.", description = "¡El futuro es ineludible! Nos estrellamos contra él como las gotas de lluvia contra el Mare Magnum.\n\nPero quizás el estudio del pasado nos ayude a prepararnos mejor para lo desconocido.\n\nAntes de que la tragedia se abatiera sobre Lordaeron, existía un templo del saber oculto en los Claros de Tirisfal. Se dice que la biblioteca del antiguo Monasterio albergaba un libro llamado Mitología de los Titanes.\n\nSi me lo traes, puede que consigamos desvelar los secretos del pasado.", progress = "El monasterio era un seminario para paladines. Fue un bastión de la Luz, pero cayó en manos de los fanáticos de la Cruzada Escarlata.\n\nCreían perseguir un noble objetivo: limpiar la tierra de la plaga de los no-muertos; pero acabaron enloqueciendo y enemistándose con todos.\n\nTráeme Mitología de los Titanes para que lo guarde aquí en Forjaz.\n\nLas corruptas salas del monasterio no son lugar para un tesoro como ese.", completion = "¡Me traes el texto sagrado!\n\nHas viajado lejos y sin duda fue difícil conseguir el libro. Pero tu labor beneficia a los enanos de Forjaz.\n\nGracias en nombre de la Liga de Exploradores, &lt;nombre&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nMención de honor de la Liga de Expedicionarios"},
[1051] = {title = "La venganza de Vorrel", objective = "Lleva la alianza de Vorrel Sengutz a Monika Sengutz de Molino Tarren.", description = "Aunque tengo libre albedrío, no puedo seguir sirviendo a la Dama Oscura. Siento que se acaba mi tiempo en Azeroth. Mi fuerza vital está mermada.\n\n¡Es culpa del interrogador Vishas! ¡Me torturó durante semanas! Me quitó la alianza y se la dio a su mujer. ¡Maldito!\n\nMe vengaré... ¡Juro que me vengaré!\n\nEse cretino pomposo anda presumiento de su mujer, Nancy, que vive junto al Lago Lordamere. Liquida a esa bruja y llévale la alianza a mi mujer, Monika, a Molino Tarren.\n\nCumple mi último deseo...", progress = "¿Qué quieres?", completion = "Vorrel tenía muchos problemas, pero era un buen compañero...\n\n¡Malditos humanos!\n\nPero al menos te vengaste en su nombre y te lo agradezco.\n\nPor favor, déjame sola...", rewards = "Podrás elegir una de estas recompensas:\n\nManto de TragediaManteo de acero rictus\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBotas de VorrelManteo de acero rictusManto de Tragedia"},
[1052] = {title = "El sendero Escarlata", objective = "Lleva la carta de mención honorífica del hermano Antón a Raleigh el Devoto, en Costasur.", description = "Nosotros, los de la Cruzada Escarlata, hemos reclamado las fortalezas desde la Vega del Amparo hasta los Claros de Tirisfal. Nos sentimos bastante orgullosos de haber conseguido nuestros bastiones en Lordaeron.\n\nHas demostrado estar en contra de los no-muertos en Desolace. Pero el corazón de la Plaga está en Lordaeron.\n\nVe a la ciudad de Costasur, en los Reinos del Este. Busca a un cruzado llamado Raleigh el Devoto. Dale esta carta de recomendación que lleva mi sello y te escoltará a un lugar de honor en nuestro Monasterio Escarlata.", progress = "¿Quién va? ¿Qué quieres de mí?", completion = "¿El hermano Antón sigue divulgando la palabra de la Cruzada Escarlata? ¡Madre mía!", rewards = "Recibirás:\n\n"},
[1053] = {title = "En el nombre de la Luz", objective = "Mata a la alta inquisidora Melenablanca, al Comandante Escarlata Mograine, a Herod, el Campeón Escarlata y al domador de jaurías Loksey. A continuación, preséntate ante Raleigh el Devoto, en Costasur.", description = "Una vez serví a la Cruzada Escarlata con honor, lealtad y orgullo. Creí que su causa era noble: Librar Azeroth de los no-muertos.\n\nPero según pasé más y más tiempo en el Monasterio, en los Claros de Tirisfal, me di cuenta de que habían perdido la noción de la realidad. Ahora creen que todo aquel que no lleva el tabardo de la Cruzada, está infestado. Hombres y mujeres inocentes torturados porque, supuestamente, estaban infestados.\n\nHay que poner fin a la Cruzada Escarlata, en el nombre de la Luz. Tú, <nombre>, debes destruir al enloquecido régimen.", progress = nil, completion = "Mientras que los discípulos de la Luz nunca se deleitan con la pérdida de la vida, debemos aceptar que, en algunas ocasiones, esos sacrificios deben ocurrid por el bien del reino y del planeta.\n\nGracias a tus acciones en el Monasterio se han salvado muchas vidas inocentes, &lt;nombre&gt;. Aplaudo tu tenacidad.", rewards = "Podrás elegir una de estas recompensas:\n\nEspada de SerenidadMascahuesosAmenaza negraOrbe de Lorica\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nEspada de SerenidadMascahuesosAmenaza negraOrbe de Lorica"},
[1054] = {title = "Matanza selectiva", objective = "Llévale la calavera de Dal Zarpasangrante a Raene Correlobo en Astranaar.", description = "Directamente al norte de Astranaar están los furbolgs Piel de Cardo. Mis rastreadores me informan de que en los últimos meses han aumentado mucho sus efectivos. Si acaban siendo demasiados, podrían envalentonarse y atacar directamente Astranaar.\n\nMata a tantos de su clase como puedas pero tráeme la calavera de su jefe como prueba de que hemos reprimido el movimiento... por ahora.\n\nSeguiré aquí en Astranaar y esperaré tus noticias. Que Elune esté contigo.", progress = "La corrupción de los furbolgs ha asestado un duro golpe a la gloria de Vallefresno.", completion = "Muy bien, &lt;nombre&gt;.\n\nLa amenaza de los furbolgs es constante. Esperemos que esto los mantenga a raya un poco más.", rewards = "Recibirás:\n\nBálsamo restablecedor"},
[1055] = {title = "La limpieza de Raene", objective = "Vuelve junto a Shael'dryn en la poza de la luna.", description = "La vara, ahora completa, está en tus manos. Shael'dryn se alegrará al enterarse de tu éxito.", progress = nil, completion = "¡Lo has logrado, &lt;nombre&gt;! ¡Muy bien!", rewards = "Recibirás:\n\n"},
[1056] = {title = "Viaje a Cima del Espolón", objective = "Ve junto al vigilante Albagorm en la Cima del Espolón.", description = "Según los espíritus del bosque, eres valiente y no te importa viajar.\n\nHacia el sur, cerca del Lago Mystral, se encuentra un túnel conocido como El Paso del Espolón. Este túnel lleva a una zona llamada Risco Cortaviento en Sierra Espolón. Cuando llegues allí, prosigue tu camino hacia el suroeste hasta pasar el Lago del Peñasco, dirígete entonces hacia el norte por una cuesta empinada hasta alcanzar la Cima del Espolón.\n\nAllí aguarda el guardián Albagorm. Presta atención a su encargo, <raza>.\n\nEl viaje será peligroso.", progress = nil, completion = "Por fin has llegado. Veo que Faldreas ha escuchado a los espíritus del bosque...", rewards = "Recibirás:\n\n"},
[1057] = {title = "La reconquista de La Vega Carbonizada", objective = "El vigilante Albagorm en la Cima del Espolón quiere que mates 7 arpías Furia Sangrienta, 7 emboscadores Furia Sangrienta, 7 cazadores Furia Sangrienta y 7 Plumapícaros Furia Sangrienta.", description = "Como <protector/protectora> de los espíritus, sin duda sabrás por qué siento tanta inquietud.\n\nHas viajado a través de estas montañas para llegar a la cumbre. Seguro que has visto la parodia en la que se ha convertido esta tierra. La codicia ha llevado a la destrucción de estas colinas sagradas.\n\nEl ambicioso ejército de industriales mecanizados, incendió La Vega Carbonizada, al sur. Peor aún, los Furia Sangrienta que han invadido la tierra, nos niegan el paso para comenzar a reconstruir.\n\n¡Expulsa a las malvadas arpías, <nombre>!", progress = "Hay mucho trabajo por hacer antes de poder restaurar la gloria que, antiguamente, reinaba en La Vega Carbonizada.\n\n¡Elimina esas amenazas de la zona, <nombre>! Solo entonces podremos reclamar lo que le pertenece a la naturaleza.", completion = "Ahora que los Furia Sangrienta han sufrido una pérdida tan grande, quizás pueda comenzar el verdadero trabajo de restauración...", rewards = "Recibirás:\n\n"},
[1058] = {title = "Magia del bosque de Jin'Zil", objective = "El médico brujo Jin'Zil en Malaka'jin quiere 5 porciones de savia Espolón, 5 mostachias Crepusculares, 30 ojos de corcel y una escama de dragón Fey.", description = "¡Qué bien, qué bien!\n\n¡Tres cajitas para tres elfitos! ¿Pero qué haremos con nuestros simpáticos amiguitos \"abraza-árboles\"? Mmm... Démosles un poco de... ¿Cómo la llaman?... ¡Magia del bosque!\n\n¡Necesitaré un buen montón de savia Espolón! Tráeme algunas mostachias Crepusculares de esos horribles felinos. Y muchos ojos de corcel... Nunca se puede tener suficientes ojos. Oh, y que no nos olvidemos, una preciosa escama de dragón Fey.\n\n¡Vete rápidamente a la Cima del Espolón, <nombre>! Así podré crear algo de magia del bosque.", progress = "Mira a nuestros pobres amiguitos elfos. ¡Todos enjaulados sin un bosque donde retozar!\n\n¿Has podido conseguirme la savia? ¿Y las mostachias de los corredores Crepusculares? ¿Supongo que no tendrás la escama de dragón Fey?\n\n¡Y los ojos! ¡Necesito montones de ojos! ¡No puedes crear una poción de magia del bosque poderosa sin muchos ojos!", completion = "¡Oh, qué contentos estarán estas pequeñas ninfas de los bosques! Haremos que se sientan como en casa con este brebaje especial.", rewards = "Podrás elegir una de estas recompensas:\n\nManto vudúBrazales embrujados\n\nTambién recibirás:\n\nManto vudúBrazales embrujados"},
[1059] = {title = "La reconquista de La Vega Carbonizada", objective = "Ve a ver al Díscolo Falfindel en la frontera entre Las Mil Agujas y Feralas.", description = "Hemos sometido a los Furia Sangrienta. Pero volverán a reagruparse pronto si no hacemos nada por remediarlo.\n\nAl sur de Los Baldíos hay una tierra conocida como Las Mil Agujas. Si te diriges hacia el oeste en Las Mil Agujas, hasta que llegues al final del bosque de Feralas, encontrarás a un druida llamado el Díscolo Falfindel.\n\nEste sabio druida es capaz de invocar poderosos espíritus del bosque que podrán ayudarnos a reclamar La Vega Carbonizada.\n\n¡Háblale de nuestra gran necesidad! Vete a buscarle.", progress = nil, completion = "El vigilante Albagorm demostró inteligencia al enviarte con tanta urgencia.\n\nVeamos si conseguimos invocar ayuda del gran bosque para viajar a La Vega Carbonizada antes de que sea demasiado tarde.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales templadosRodela de agente\n\nTambién recibirás:\n\nBrazales templadosRodela de agente"},
[1060] = {title = "Una carta para Jin'Zil", objective = "Entrégale la carta de Darsok a Jin'Zil en su cueva de Malaka'jin, en Espolón.", description = "Has hecho un gran trabajo para mí, <nombre>. Eres como una pata de conejo de la suerte y eso es algo que siempre me alegro de ver.\n\nLas arpías no son de los alrededores de Los Baldíos. Proceden de un lugar llamado Espolón, muy lejos al noroeste desde aquí. Aunque es probablemente un lugar peligroso para ti, necesito que le entregues esta misiva a un viejo amigo que vive allí, un médico brujo que responde al nombre de Jin'Zil.\n\nSe alegrará de saber que Serena Sangrepluma ha muerto y creo que Espolón te parecerá un lugar interesante.", progress = "Pareces un personaje turbio. ¿Traes algo para mí?", completion = "Ah, una carta de Darsok. Hacía muchos años que no sabía nada de él.\n\n¿Conque el último de los Sangrepluma ha muerto? Qué buena noticia, recuerdo cuando mi amigo Rokhan se marchó con el loco mok'nathal para matar a su hermana mayor. Ah, quién fuera joven otra vez, como tú.", rewards = "Recibirás:\n\n"},
[1061] = {title = "Los espíritus del Espolón", objective = "Habla con Seereth Rompepedras en Sierra Espolón.", description = "Los espíritus de Sierra Espolón están enojados por la intrusión de los goblins y sus siervos. Por eso la tierra se ha lamentado y aquellos que aún escuchan han oído su súplica.\n\nSe envió a Seereth Rompepedras, una prometedora chamán de la Horda. Nos dijo que los goblins están deforestando la tierra a gran velocidad y pidió ayuda.\n\nVe a Sierra Espolón a hablar con ella; su campamento está cerca de la Vega del Gran Bosque. Lo mejor será que sigas el camino hacia el oeste desde El Cruce, en Los Baldíos.", progress = nil, completion = "Ah, &lt;clase&gt;. Perfecto, tu habilidad nos ayudará con esos invasores.", rewards = "Recibirás:\n\n"},
[1062] = {title = "Los invasores goblin", objective = "Mata 15 leñeros de Ventura y Cía. y vuelve a ver a Seereth Rompepedras, en la frontera entre Espolón y Los Baldíos.", description = "La empresa goblin Ventura y Cía. llegó a Sierra Espolón, cortando árboles y quemando enormes secciones de bosque. Los espíritus del lugar están prácticamente ciegos de dolor e ira. ¡Debemos detener a Ventura y Cía.!\n\nDirígete al noroeste, más allá de la Vega del Gran Bosque hasta el Risco Cortaviento. Encontrarás a los goblins y a sus siervos en pleno trabajo. Demuéstrales que la Horda no permitirá la explotación de Espolón. Y hazlo utilizando el único idioma que comprenden...\n\nLa violencia.", progress = nil, completion = "Muy valiente, &lt;nombre&gt;.\n\nQuizás al final necesitemos la sabiduría de los ancestros tauren para apaciguar a los espíritus de Espolón, pero...\n\nAcabar con el personal de Ventura y Cía. es un buen primer paso contra ellos.", rewards = "Recibirás:\n\n"},
[1063] = {title = "La vieja bruja", objective = "Habla con Magatha en Cima del Trueno.", description = "Para conectar realmente con los espíritus de Espolón, debemos consultar con los ancestros tauren.\n\nNo sé el nombre de muchos de los líderes, pero sí conozco uno: Magatha. He oído que es muy vieja y muy sabia. Si hay alguien que pueda conseguir ayuda espiritual de los tauren, es ella.\n\nMagatha vive en la capital tauren de Cima del Trueno, en el Alto de los Ancestros. En el risco que queda al noreste de la ciudad. Habla con Magatha y cuéntale el peligro que corre Sierra Espolón.", progress = nil, completion = "Te doy la bienvenida. ¿Estás aquí en busca de consejo?", rewards = "Recibirás:\n\n"},
[1064] = {title = "La ayuda de los Renegados", objective = "Habla con la boticaria Zamah en las Pozas de las Visiones de Cima del Trueno.", description = "Los taurens tienen un vínculo con la tierra y me apena conocer la difícil situación de Espolón pero me temo que para salvar a la tierra primero debemos eliminar el mal que la puebla.\n\n¿No es una suerte, pues, que los Renegados sean nuestros aliados? Ellos saben mucho del mal y creo que podrán ayudarnos. De ese modo, reforzarán la confianza entre nuestros pueblos.\n\nHabla con la boticaria Zamah, una erudita y emisaria de los Renegados. La podrás encontrar en las Pozas de las Visiones, al pie del Alto de los Espíritus.", progress = nil, completion = "Magatha dijo que vendrías, &lt;nombre&gt;. Mi corazón ya ha dejado de latir pero aun así siento lástima por los habitantes de Espolón.\n\nLos Renegados estamos deseosos de ayudar en todo lo que podamos.", rewards = "Recibirás:\n\n"},
[1065] = {title = "Viaja a Molino Tarren", objective = "Entrégale la nota de Zamah al boticario Lydon, que está en Molino Tarren.", description = "Para liberar a Espolón de Ventura y Cía., debemos tomar medidas extremas pero evitar el derramamiento de sangre.\n\nPuede que un colega mío tenga lo que necesitamos. Su nombre es Lydon y se encuentra en la ciudad de Molino Tarren en las Laderas de Trabalomas, en la lejana tierra de Lordaeron.\n\nPara llegar a Trabalomas viaja en zepelín hasta nuestra capital, Entrañas. Después dirígete en dirección norte a los Claros de Tirisfal, al sudeste hasta Argénteos y en dirección sur para llegar a Trabalomas.\n\nBusca a Lydon y entrégale esta nota en la que se detalla todo lo que necesitamos.", progress = "¿Has venido a traerme mis nuevos sujetos de experimento?", completion = "¿Una nota de parte de Zamah? Debe de estar desesperada para enviarte desde tan lejos. Veamos lo que dice...\n\n¡Ah, estupendo! ¡Sé exactamente lo que hay que hacer!", rewards = "Recibirás:\n\n"},
[1066] = {title = "Sangre de inocentes", objective = "Lleva 5 viales de sangre de los inocentes al boticario Lydon de Molino Tarren.", description = "Voy a crear una toxina que drene la violencia de Ventura y Cía.; así se volverán débiles y dóciles. Eso solucionará el problema de la Horda en Sierra Espolón.\n\nPero necesito sangre de los inocentes, que es difícil de conseguir.\n\nPor suerte, la Hermandad, que es un grupo de maleantes humanos de esta zona, emplea a magos de la sombra, que recolectan sangre de los inocentes para sus turbios propósitos.\n\nLos encontrarás en el Castillo de Durnholde, al sureste de aquí.", progress = "¿Traes la sangre que te pedí?", completion = "¡Muy bien!\n\nComo dije, no es fácil conseguir Sangre de los inocentes; suerte que la Hermandad nos hizo el trabajo.\n\nEspera mientras preparo la toxina.", rewards = "Recibirás:\n\n"},
[1067] = {title = "Vuelve a Cima del Trueno", objective = "Lleva la toxina de Lydon al boticario Zamah de Cima del Trueno.", description = "Aquí está la toxina. Tendrá un efecto favorable, quizás incluso contagioso, en Ventura y Cía.. ¡Me emociona ver una demostración práctica de mi trabajo!\n\nPero antes de usarla, llévasela a la boticaria Zamah. Tiene que diseñar un proceso que intensifique el efecto de la toxina.", progress = "Hola, <nombre>. ¿Me traes algo del boticario Lydon?", completion = "¿De parte de Lydon? Muy bien. Seguro que esta toxina nos ayuda en nuestra lucha contra Ventura y Cía..", rewards = "Recibirás:\n\n"},
[1068] = {title = "Las sierras XT:4 y XT:9", objective = "Destruye las sierras XT:4 y XT:9, a continuación vuelve a ver a Seereth Rompepedras, en la frontera entre Sierra Espolón y Los Baldíos.", description = "Hay una relación entre la magia espiritual de los chamanes y la magia natural de los druidas. Por eso los chamanes podemos oír la súplica de los espíritus de Espolón. No me llegan muchos de los susurros, pero tengo muy claro un mensaje, una visión...\n\nHe visto dos máquinas en forma de hombre en el Risco Cortaviento, al noroeste de la Vega del Gran Bosque, cortando árboles a velocidad imposible. Hay que destruir a esas bestias de metal.\n\nEn mi visión, las máquinas tenían grabados unos extraños símbolos:\n\nXT:4 y XT:9.", progress = nil, completion = "La destrucción de las máquinas XT:4 y XT:9 ha calmado a muchos de los espíritus de Espolón, &lt;nombre&gt;. Esperemos que, con su pérdida, Ventura y Cía. se vaya de estas montañas.\n\nPorque si no lo hace, me temo que las atrocidades que este lugar ha sufrido palidecerán en comparación con las que nos depara el futuro.", rewards = "Recibirás:\n\n"},
[1069] = {title = "Huevos de arañas musgondo", objective = "Llévale 15 huevos de Musgondo a Mebok Mizzyrix en Trinquete", description = "¡Las tortillas de huevos de araña están causando furor en Bahía del Botín! Solo hay un problema... no hay huevos en Bahía del Botín.\n\n¡Puedo oler la oportunidad!\n\nLa araña musgondo vive en el Risco Cortaviento y en el Cañón Sishir, en Sierra Espolón hacia el noroeste. Tráeme sus huevos ¡y te pagaré un dineral!\n\nEn el Risco Cortaviento, los huevos estarán cerca de lo que quede de arboleda.\n\nEn el Cañón Sishir... bueno si no tienes un estómago a prueba de bomba, quizás debas limitarte a recolectar los huevos en el Risco Cortaviento...", progress = "¿Has conseguido los huevos, <nombre>? ¡Ya he cerrado un trato de flete con el maestro de los muelles Vertipeluca!", completion = "¡Los tienes! ¡Gracias!\n\nLos mandaré a Vertipeluca para que los envíen a Bahía del Botín. ¡Ya puedo disfrutar de la ganancia!\n\nAquí tienes tu parte, &lt;nombre&gt;. No habría conseguido este trato sin ti.", rewards = "Recibirás:\n\n"},
[1070] = {title = "Estar de guardia en Espolón", objective = "Encuentra a Kaela Lanzasombra contemplando la vista del valle a la entrada del Risco Cortaviento, en Sierra Espolón.", description = "Hola, <nombre>.\n\nPareces tener ansias de conocer mundo. ¿Has estado en Sierra Espolón? Es una tierra plagada por Ventura y Cía. y otros enemigos de los elfos nocturnos.\n\nRecientemente, la centinela Correlobo envió a mi amiga Kaela Lanzasombra allí para investigar lo que está pasando en la tierra, pero todavía no sé nada de ella. Y me preocupa mucho, porque la Alianza envió a un mago gnomo con ella... y eso es una señal de problemas seguros.\n\n¿Puedes encontrarla y venir a informarme?", progress = nil, completion = "Ah, vienes de Vallefresno a instancias de Tara... ¡Cuánto echo de menos mi casa, mis amigos y a ella! Le deben estar yendo bien las cosas con las centinelas si está encargando tareas a aventureros. Me enorgullezco de ella.\n\nUn placer conocerte, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[1071] = {title = "Una tregua para el gnomo", objective = "Mata 10 leñeros de Ventura y Cía. y 10 deforestadores de Ventura y Cía. A continuación, vuelve a ver a Gaxim Silvóxido en Espolón.", description = "Verás, <nombre>, soy miembro de la Asamblea Iluminada de Arcanología, Alquimia e Ingeniería, y mi objetivo... no, ¡mi DEBER!... es demostrar a esos elfos de la noche, que la unión de magia, combinaciones y mecanismos puede ayudarles a salvar sus bosques.\n\nPero se trata de una guerra dura, ¡y, por lo visto, esos goblins se reproducen como conejos! Ni siquiera he tenido tiempo de construir mis últimos inventos.\n\nNecesito algo de tiempo para planear y organizarme. Pero, para ello, necesito que me ayudes a mantener a raya a los goblins en el Risco Cortaviento, al este...", progress = nil, completion = "Muchísimas gracias por tu ayuda, &lt;nombre&gt;. Vuelves justo a tiempo.\n\nCreo que tengo el plan perfecto... explosivos. No, ese no es el plan, pero es parte del plan.\n\nVerás, para asustar a los goblins, he estado utilizando explosivos muy potentes, no te preocupes, no son tan peligrosos como piensa todo el mundo. Puedo llevarles de un lado para otro como si fueran ganado con unas cuántas detonaciones en el los lados de la montaña, pero llevo unos días con falta de suministros...", rewards = "Recibirás:\n\n"},
[1072] = {title = "Un viejo colega", objective = "Habla con Lomac Destrabaengranajes en Forjaz.", description = "El dispositivo en el que estoy pensando, es la versión más avanzada hasta la fecha. Pero necesitaremos una poción especial para que funcione. Y creo que también podríamos intentar ir a por lo grande, porque esta podría ser tu última misión.\n\nY, para eso, vamos a necesitar explosivos potentes: nitromirgliceronio.\n\nEl único que puede hacer NG-5 es un viejo amigo mío: Lomac Destrabaengranajes. Búscale en Ciudad Manitas.\n\nConvéncele para que nos haga NG-5. Me pondré a trabajar en mis dispositivos.", progress = nil, completion = "Ahhh... ¿Te envía Gaxim, eh? ¿Cómo sé que no me estás mintiendo? ¿Mm? ¿Mmm...?", rewards = "Recibirás:\n\n"},
[1073] = {title = "Ineptitud + Productos químicos = Diversión", objective = "Lleva 4 pociones de maná menores y 2 elixires de entereza menores a Lomac Destrabaengranajes en Forjaz.", description = "¡Ja, ja, ja! ¿Necesitas nitromirgliceronio para evitar que los goblins destruyan un bosque?\n\nNo me importa si conoces a Gaxim o no... Te lo daré solo para verte a ti, o a los otros, explotar en un millón de trocitos.\n\nPero hay un problema: me he quedado sin NG-5.\n\nPero mira, si me traes las pociones correctas, te prepararé lo que necesitas ahora mismo.\n\nLo mínimo que podías hacer es demostrar tu habilidad como <boticario/boticaria> antes de que te dé una muestra de mi mejor creación.", progress = "No puedo hacerte el nitromirgliceronio si no tengo las pociones... son vitales para crearlo.", completion = "Esto servirá.\n\nSi no supiera que no es así, diría que tienes conocimientos de alquimia. Bien, bien... el mundo no gira solo con espadas y magia, ¿sabes?", rewards = "Recibirás:\n\n"},
[1074] = {title = "Ineptitud + Productos químicos = Diversión", objective = "Lleva el nitromirgliceronio de vuelta a Gaxim Silvóxido en Espolón.", description = "Aquí tienes, <forastero/forastera>... Pero ten muchísimo cuidado... es un material muy volátil.\n\nSi, realmente, Gaxim es tu amigo, dale recuerdos de mi parte. Si no es así, que te diviertas cuando explotes...", progress = "Ah, ¡qué bien! Ya has vuelto.", completion = "Has completado tu tarea sin problemas, &lt;nombre&gt;.\n\nEl NG-5 es el componente inequívoco de la receta para hacer explotar los carros de dinamita.\n\n¿Qué? Sí, dinamita... carros llenos de dinamita. Es la distracción perfecta, te lo digo yo... espera y verás.", rewards = "Recibirás:\n\n"},
[1075] = {title = "Un pergamino de Mauren", objective = "Encuentra a Collin Mauren en Ventormenta.", description = "Muy bien y, ahora, la parte de detonación remota de mi plan... Oh, eso no te lo he explicado todavía, ¿no?\n\nPues en resumen se trata de esto: con un poco de magia, puedo conseguir que los explosivos exploten cuando tú no estés cerca. Estoy creando un dispositivo que te permitirá, tan solo con pulsar un botón, hacer que el NG-5 explote.\n\nPero necesito un hechizo de un amigo de Ventormenta... Collin Mauren. Podrás encontrarle en el Barrio de los Magos. Es un humano, así que no deberías tener problemas para encontrarle.", progress = nil, completion = "Bien, este hechizo que necesitas... que te permita detonar explosivos a distancia... mmm.", rewards = "Recibirás:\n\n"},
[1076] = {title = "Demonios en Páramos de Poniente", objective = "Lleva restos de demonio de polvo a Collin Mauren en Ventormenta.", description = "Ah, creo que ya lo tengo. Un hechizo mensajero... Creo que ese es el efecto que está buscando.\n\nTendré que hacer un pergamino donde grabar el hechizo. Para ello, necesitaré restos de un demonio de polvo.\n\nMientras reúno el resto de los componentes, ¿qué te parece si vas a los Páramos de Poniente a buscar a una de esas malvadas criaturas? Los demonios de polvo son peligrosos, pero seguro que puedes encargarte de algo así si estás <dispuesto/dispuesta> a someterte a uno de los experimentos de Gaxim.\n\nEstaré aquí cuando vuelvas.", progress = "Hola, <nombre>. ¿Has encontrado el componente que necesito?", completion = "Dame unos minutos y tendré el pergamino listo.", rewards = "Recibirás:\n\n"},
[1077] = {title = "Entrega especial para Gaxim", objective = "Lleva el Pergamino de mensajes de vuelta a Gaxim Silvóxido en Espolón.", description = "Aquí estás, <nombre>. Con esto bastará.\n\nPor favor, dale recuerdos a Gaxim la próxima vez que le veas. Me muero de ganas por saber qué tal salió todo cuando terminéis.\n\nAdiós.", progress = "¡Ja, ja! ¡Has vuelto! ¿Lo tienes? ¿Tienes el hechizo?", completion = "¡Maravilloso, &lt;nombre&gt;! Bien hecho.\n\nEste conjuro, mezclado con el NG-5, debería lograr el explosivo remoto perfecto.\n\nPodremos crear una distracción fantástica para que puedas colarte y robar las cartas y los planos de ingeniería de Ventura y Cía.\n\nSeguro que eso paraliza de alguna forma sus acciones de deforestación.", rewards = "Recibirás:\n\n"},
[1078] = {title = "Escamas cristalizadas para Mauren", objective = "Lleva 8 escamas cristalizadas a Collin Mauren en Ventormenta.", description = "Todos los viajeros siguen preguntándome por Sierra Espolón. Parece ser un lugar muy popular para aventureros... No importa si buscas dracoleones, elementales, o tienes algo que hacer con Ventura y Cía.\n\nEn La Vega Carbonizada, en Espolón, solía haber una especie de basiliscos cuyas escamas, al molerlas, producían un increíble componente para alguno de los hechizos que he creado.\n\nSi esos basiliscos siguen con vida, me encantaría conseguir algunas escamas.\n\nTómate tu tiempo, no corre prisa, pero puedo pagarte generosamente.", progress = "Hola otra vez, <nombre>.\n\n¿Ya has regresado de La Vega Carbonizada de Espolón con las escamas?", completion = "Perfecto, &lt;nombre&gt;. Muchas gracias.\n\nQuizás, algún día yo pueda ayudarte como tú me has ayudado a mí.", rewards = "Recibirás:\n\nVarita de artesano de hechizos"},
[1079] = {title = "Operaciones secretas: plan A", objective = "Recupera los planos de ingeniería de Ventura y Cía. de la serrería en el Risco Cortaviento.", description = "Muy bien, <nombre>. Presta mucha atención. Los dispositivos están terminados y listos para usar.\n\nLlévate estas páginas. Contienen más información sobre los detonadores y explosivos y dónde deberías colocarlos.\n\nRecuerda, el éxito de tu misión no depende de la destrucción que provoques. El éxito del plan Alfa depende de que recuperes los planos de ingeniería de Ventura y Cía. que están dentro de la serrería al sur del Lago del Peñasco.", progress = "¿Has conseguido los planos, <nombre>?", completion = "¡Increíble! Un gran golpe para Ventura y Cía.\n\nPodremos encontrar los puntos débiles de sus máquinas e incorporar sus diseños a nuestras propias creaciones. ¡Este es un gran día!", rewards = "Recibirás:\n\n"},
[1080] = {title = "Operaciones secretas: plan B", objective = "Recupera las cartas de Ventura y Cía. de la Mina Cortaviento, en el extremo este del Risco Cortaviento.", description = "El plan Beta es como el plan Alfa, <nombre>.\n\nCausamos una distracción y robamos las valiosas cartas.\n\nLo más importante es conseguir sus cartas... No lo olvides.\n\nLas páginas que te he dado te dirán dónde colocar los explosivos. Solo tienes que volver aquí con vida... y con las cartas...", progress = "¿Qué tal fue todo, <nombre>?", completion = "¡Increíble, &lt;nombre&gt;! Kaela y las Centinelas estarán encantadas. Con tu habilidad y mis creaciones, somos imparables.", rewards = "Recibirás:\n\n"},
[1081] = {title = "El recibimiento de Tyrande", objective = "Encuentra a Tyrande Susurravientos en Darnassus.", description = "<nombre>, seguro que la mismísima Tyrande Susurravientos querrá agradecerte tu participación en esta misión.\n\nQuizás no te lo parezca a primera vista, pero lo que has ayudado a empezar, podría reforzar nuestra ciudadela en Sierra Espolón.\n\nSi te consideras <nuestro aliado/nuestra aliada> y estás en contra de estas atrocidades, vete a ver a Tyrande en el Templo de la Luna, en Darnassus.", progress = nil, completion = "Hola, &lt;nombre&gt;. He oído tu nombre y, aunque sigues siendo muy joven, me alegra ver el camino que has decidido seguir.\n\nMe harías un gran honor si quisieras aceptar esto como muestra de aprecio.", rewards = "Podrás elegir una de estas recompensas:\n\nToga de floraciónTúnica griseznaCadena de madera salvaje\n\nTambién recibirás:\n\nSortija de Gracia élficaToga de floraciónTúnica griseznaCadena de madera salvaje"},
[1082] = {title = "Noticias para Centinela Thenysil", objective = "Ve a ver a la centinela Thenysil en Vallefresno.", description = "<nombre>, nos has prestado un gran servicio.\n\nLas Centinelas de Vallefresno te recompensarán generosamente si les informas de que Gaxim y yo estamos vivos. Diles que vamos de camino a detener a Ventura y Cía. y curar la tierra.\n\nBusca a Tara Thenysil en Astranaar; estará encantada al verte volver con buenas noticias.", progress = nil, completion = "Gracias, &lt;nombre&gt;. Has hecho un gran servicio a las Centinelas.\n\nMe alegro mucho de ver que Kaela... e incluso Gaxim, están bien.\n\nTe has ganado nuestro aprecio.", rewards = "Recibirás:\n\n"},
[1083] = {title = "Los espíritus enfurecidos", objective = "Lleva 8 ascuas ardientes de cualquiera de los elementales de fuego de la Vega Carbonizada a Kaela Lanzasombra, en Espolón.", description = "No tengo muy claro lo que Ventura y Cía. hizo en La Vega, pero es obvio que consiguieron dominar a las criaturas que vivían allí.\n\nIncendiaron el bosque y utilizaron sus inventos de destrucción para diezmar La Vega tanto como pudieron.\n\nPero, en su ignorancia, hicieron algo más: liberaron espíritus ardientes, elementales de fuego, piedra y furia.\n\nLa tierra no tendrá la oportunidad de ver la sonrisa de Elune mientras esas criaturas vaguen a su libre albedrío.\n\nTráeme sus ascuas ardientes para demostrarme que has conseguido reducir sus filas.", progress = "La primera vez que vi La Vega Carbonizada, casi lloro, <nombre>.", completion = "Los restos de nuestro enemigo... tu fuerza nos hace un gran servicio, &lt;nombre&gt;. Gracias.", rewards = "Recibirás:\n\n"},
[1084] = {title = "Los antárboles corruptos", objective = "Lleva una pieza de metralla corroída a Kaela Lanzasombra en Espolón.", description = "Me parece que los protectores de La Vega, los antárboles, han tenido algo que ver con la marcha de Ventura y Cía. Por desgracia, incluso esos antiguos guardianes, están corruptos ahora.\n\nGaxim cree que su corrupción está, de alguna forma, vinculada con las armas que los goblins utilizaron en su retirada.\n\nLe dije que conseguiría una muestra de un antárbol muerto para que pudiera estudiarla y descubrir si es cierto.\n\nTráeme metralla para que el gnomo pueda contarme algo más...", progress = "Tengo el presentimiento de que Gaxim sabrá cómo utilizar la metralla sin tener en cuenta cómo la ha usado Ventura y Cía. en el pasado.", completion = "Gracias, &lt;nombre&gt;.\n\nEsto debería ser más que suficiente para que Gaxim descubra cómo fueron corrompidos los antárboles. Se lo daré ahora mismo.", rewards = "Recibirás:\n\n"},
[1085] = {title = "Estar de guardia en Espolón", objective = "Habla con Gaxim.", description = "La situación aquí, en Espolón, es obviamente grave para que una elfa nocturna y un gnomo estén trabajando juntos.\n\nVentura y Cía. ha devastado estas tierras y yo he pasado la mayor parte de mi tiempo intentando poner fin a la destrucción.\n\nNo juzgues a Gaxim prematuramente. Comprende la necesidad que tenemos de trabajar juntos.\n\nSi quieres ayudarnos, empieza por hablar con Gaxim.\n\nSiento no poder enviarte de vuelta a ver a Tara con buenas noticias todavía. Quizás pronto...", progress = nil, completion = "Traicionera... traicionera y peligrosa, así es esta tierra.\n\nPero estoy aquí para demostrar a los elfos de la noche que todo va un poco mejor con la ayuda de la vieja ingeniería de toda la vida y una pizca de magia.\n\nVeamos, jurar dejar la magia completamente no ayudará a los elfos de la noche... la necesitan. El mundo la necesita. Y, con la ingeniería y la alquimia, nunca te puedes equivocar. Las posibilidades son infinitas.", rewards = "Recibirás:\n\n"},
[1086] = {title = "El minipuerto", objective = "Lleva la niebla tóxica al minipuerto de Ventura y Cía. e informa al boticario Zamah.", description = "La toxina ya está lista. Solo queda entregarla. Y eso te toca a ti, <nombre>.\n\nVentura y Cía. tiene que disponer de un sistema para enviar mensajes y paquetes ligeros entre su administración central y las operaciones de Sierra Espolón. Probablemente sea un minipuerto, no lo bastante grande para un zepelín, pero sí para un transportador aéreo. Recorre las zonas de influencia de Ventura y Cía. en Sierra Espolón y encuentra el minipuerto. Si lo localizas, planta la toxina.\n\nY sal de ahí inmediatamente.", progress = nil, completion = "¡Buen trabajo, &lt;nombre&gt;! ¡Seguro que esos globlins se llevaron una sorpresa!\n\nNo te sientas culpable por lo que has hecho; esa gente merece esto y más.\n\nEn mis años de servicio he aprendido algo:\n\ntodo medio es válido si conduce a la victoria.", rewards = "Recibirás:\n\n"},
[1087] = {title = "El legado de Cenario", objective = "Mata 4 Hijos de Cenario, 4 Hijas de Cenario y 4 botánicos Cenarion para Braelyn Manofuego, cerca del Refugio Roca del Sol.", description = "No puedes ni imaginarte el odio que siento por esta tierra maldita. Los de Cenario y los druidas kaldorei de la cima, utilizan sus patéticos poderes para reprimir el flujo de la magia, incluso muy lejos, al sur de sus patéticas posesiones... sus amados bosques.\n\nYo podría regresar a Jaedenar y regocijarme con el flujo de la magia, pero mi maestro no me permite volver hasta que no haya finalizado mi trabajo.\n\nNo me cabe la menor duda de que sientes tanto aprecio por los elfos de la noche y sus pequeños aliados como yo. Ayúdame y ambos quedaremos satisfechos.", progress = nil, completion = "Ya puedo sentirlo... sí... un poder tan dulce... el flujo... Los kaldorei son unos necios por desdeñar los regalos de este mundo, por rechazar el curso de la magia cuando fluye cantando por tus venas.\n\n¡De qué sirve la vida sin esa ardiente comunión!", rewards = "Recibirás:\n\n"},
[1088] = {title = "Ordanus", objective = "Lleva la cabeza de Ordanus a Braelyn Manofuego cerca del Refugio Roca del Sol.", description = "Creyeron poder detener el flujo de la magia. Se equivocaron. Creyeron que podían ocultar a Ordanus, ¡pero conseguiré su cabeza!\n\nLa magia me otorga la visión verdadera. Sí... ahí está. Una encrucijada... En la copa de un gran árbol en el Bosque de Vallefresno. Un nombre... Algaba. Uno de los hijos mayores del dios del bosque. Le hicieron desaparecer de la cima antes de que pudiera encontrarle, pero se está quedando sin tiempo.\n\nTráemelo y compartiré contigo un poderoso regalo.", progress = "Cuando mi maestro me destinó a la Cima del Espolón, supuse que todo lo que tendría que hacer era acabar con los elfos de la noche que se arrastraban por las ruinas sagradas de nuestro antiguo Templo de Azshara despojándolo de su santidad al tocarlo... pero me había olvidado de Ordanus.", completion = "Una vez vi a Ordanus, hace mucho. Tenía la arrogancia típica de los suyos, creyéndose por encima incluso de los altonatos.\n\nPero ahora su cabeza no está tan estirada, ¿eh, &lt;nombre&gt;?\n\nTe prometí una recompensa, y la tendrás. Atiéndeme.", rewards = "Recibirás:\n\n"},
[1089] = {title = "El Cubil", objective = "Ve a El Cubil, en la Cima del Espolón. Utiliza la Llave del Guardián y consigue los objetos ocultos del druida. Utiliza esos objetos para abrir el tesoro de El Cubil del Espolón.", description = "Los druidas construyeron el Cubil del Espolón, en la Cima del Espolón, y allí guardan poderosos objetos.\n\nDiseñaron un complejo sistema para mantener a estos objetos alejados de manos equivocadas.\n\nPor fortuna, he logrado... extraer algunos detalles de uno al que he interrogado.\n\nEn una antorcha de la entrada al cubil encontrarás la Llave del Guardián. Con ésta podrás abrir los cofres del Cubil. Los objetos que obtengas te permitirán abrir su tesoro, ubicado en una de las zonas centrales.", progress = "Hay tres ojos de cerradura grabados en la piedra.", completion = "Según las tres llaves se deslizan por las aperturas, en medio de una brillante luz, aparecen varios objetos.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nSortija sacraArmadura de panteraLeotardos de Jugernauta"},
[1090] = {title = "Órdenes de Gerenzo", objective = "Consigue el misterioso mineral de Piznik cuando termine de extraerlo.", description = "¿Sabes? Una vez fui la segunda persona más importante de este lugar. Pero todo cambió con la llegada de Gerenzo. Despidió a mi jefe, Ziz Fizziks, y me envió a trabajar a las minas. Sin embargo, Ziz me dijo que permaneciese aquí y reuniese algo de información. Su nuevo jefe nos pagará bien si podemos descubrir los planes de Gerenzo. \n\nDe todas formas, lo que sí sé es que Gerenzo ha ordenado excavar grandes cantidades de su mineral dorado verdoso. Puedo conseguirte una muestra para que se la lleves a Ziz, pero tendrás que vigilar por si aparecen patrullas mientras lo hago.", progress = nil, completion = "Gracias, &lt;nombre&gt;. La situación parecía algo complicada y, probablemente, habíamos atraído demasiada atención.\n\nLo más importante es que tenemos la muestra del mineral y que debemos llevársela a Ziz lo antes posible.", rewards = "Recibirás:\n\n"},
[1091] = {title = "Noticias para Kaela", objective = "Lleva las cartas de Ventura y Cía. a Kaela Lanzasombra.", description = "<nombre>, lleva estas cartas a Kaela. Seguro que querrá saberlo. O, al menos, las Centinelas querrán.\n\nAsegúrate de decirle que hemos tenido éxito y que no podríamos haberlo logrado sin ayuda de la magia.", progress = "¿Sí, <nombre>? ¿En qué puedo ayudarte?", completion = "Esto es muy interesante.\n\nGaxim y tú lo habéis hecho muy bien, &lt;nombre&gt;. Gracias.", rewards = "Recibirás:\n\n"},
[1092] = {title = "Órdenes de Gerenzo", objective = "Lleva el mineral sin identificar a Ziz Fizziks en Sierra Espolón.", description = "Supongo que también debería pensar en cómo salir de aquí. Las noticias de lo ocurrido aquí no tardarán en llegar a los de arriba y seguro que no van a estar muy contentos al verme de nuevo.\n\nSaldré a hurtadillas para encontrarme con Ziz. Con suerte, ¡podrá encontrarme otro trabajo!\n\nVoy a intentar arreglar un poco este desastre, pero deberías llevar la muestra del mineral a Ziz ahora mismo. Me dijo que iba a establecer su campamento en alguna parte del lado oeste del Risco Cortaviento. Buena suerte.", progress = "¿Puedo ayudarte con algo?", completion = "Ahhh, sabía que había hecho lo correcto al decirle a Piznik que se quedara atrás. Seguro que los ingenieros de Villabajo estarán más que interesados por esto...\n\nEstá claro que me quedaré con parte para mí, pero ojos que no ven corazón que no siente, ¿no crees?", rewards = "Recibirás:\n\nBotas dragado"},
[1093] = {title = "Súper Leñadora 6000", objective = "Consigue los planos de la Súper Leñadora 6000 para Ziz Fizziks de Sierra Espolón.", description = "Es terrible lo que Ventura y Cía. ha hecho aquí, ¿verdad? ¿En qué estaban pensando? ¡Una compañía goblin contratando a un gnomo!\n\nPor suerte para mí, Petardol puede distinguir a un genio en cuanto lo ve. ¡Me contrató justo después de que me despidieran!\n\nMe dice que han construido una enorme máquina cortaleña y Petardol quiere que robe los planos para él. El problema es que Gerenzo, maldito gnomo, me quiere muerto... Quizás tú podrías robar los planos por mí... Seguro que los lleva encima alguno de sus operadores.", progress = "¿Has encontrado esos planos? Quiero impresionar al nuevo jefe. Con suerte, cuando esté convencido de mi lealtad, bueno... todo lo que pueda estar convencido... El honor de los goblins y todo eso, ¿sabes? Pues eso, que cuando esté convencido, querrá utilizar mi impresionante genialidad.", completion = "Eh, ¿es eso lo que creo que es? ¡Genial!\n\nVeamos. ¡Agh! ¿Cómo se supone que voy a entender esto...? No sé cómo piensan los gnomos. Te diré algo, me sorprende que consiguieran construir la rueda y mucho menos esas máquinas voladores que tanto les gustan.", rewards = "Recibirás:\n\n"},
[1094] = {title = "Más instrucciones", objective = "Lleva el sobre sellado a Petardol en Los Baldíos.", description = "Este sobre contiene toda la información que conseguí coger antes de que me echaran a patadas... Documentos internos de Ventura y Cía., algunos manuales, cosas técnicas... Oh y, por supuesto, los planos de la Súper Leñadora 6000.\n\nPetardol vive en Trinquete, en Los Baldíos. No creo que tengas problemas para encontrarle, es pequeñito y verde.", progress = "¡Hola! ¿Cómo puedo ayudarte?", completion = "Fizziks, ¿eh? No es que confíe mucho en él, pero su información parece bastante fiable. ¿Qué no daría porque me quitaran de encima a Ventura y Cía.?\n\nAunque inventan buenas máquinas... No puedo negar que nos hemos beneficiado de algunos de los artefactos que han desarrollado.", rewards = "Recibirás:\n\n"},
[1095] = {title = "Más instrucciones", objective = "Lleva las nuevas órdenes a Ziz Fizziks en Sierra Espolón.", description = "El asunto de este gnomo contratado por Ventura y Cía. ha provocado algo de revuelo en Villabajo. Los grandes comerciantes no aprueban la asociación de Razdunk con el tal Gerenzo.\n\nRiddlevox me ha pedido que me ocupe del problema y estoy seguro de que la orden viene de los grandes comerciantes. Por supuesto, el Director no me lo ha dicho. Digamos que… tengo un presentimiento.\n\nSea como fuere, hay que apartar a Gerenzo y eso parece una prueba a la medida de Fizziks.", progress = nil, completion = "¿Eliminar a Gerenzo? Vaya, esa es una idea a la que no me opondría.\n\nLos gnomos ya son bastante malos pero lo que sé sobre Gerenzo, no hace más que empeorar las cosas. Al parecer, era uno de los ingenieros jefe en Gnomeregan, donde trabajaba en un proyecto secreto; no logramos infiltrar a Dun Morogh para averiguar más detalles concretos. Fue un fracaso. Según nuestros informes, fuera lo que fuera, les explotó literalmente en la cara.", rewards = "Recibirás:\n\n"},
[1096] = {title = "Gerenzo Silbillave", objective = "Lleva el brazo mecánico de Gerenzo Silbillave a Ziz Fizziks en Sierra Espolón.", description = "Una vez vi a Gerenzo, pero te diré algo, es difícil de mirar... Su carne parece podrida y tiene injertos de metal y engranajes por todo su cuerpo. Ha reemplazado algunos de sus miembros por otros mecánicos.\n\nGeranzo vigila toda la zona, desde el noreste de aquí, más arriba del Lago del Peñasco, cerca de la noria. Yo no podría llegar hasta ahí arriba, por no mencionar enfrentarme a Gerenzo... Pero tú pareces muy capaz de hacerlo.\n\nSi le matas por mí, ¡te recompensaré!", progress = "Petardol querrá alguna prueba de que Gerenzo está muerto. Creo que este brazo mecánico servirá.", completion = "Mira eso... se necesita un gnomo para inventarse algo así. No puedo comprender por qué alguien querría cortarse un brazo y reemplazarlo con eso cuando puedes quedarte con ambos brazos y añadir un tercero.\n\n¡Gnomos!...\n\nAl menos Gerenzo ha pasado a la historia. Esperemos que eso sea suficiente para demostrarlo y que Petardol me asigne a uno de los talleres del Gremio de Manitas en Gadgetzan o quizás incluso en Villabajo.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa de ingenieroBotas de dibujante\n\nTambién recibirás:\n\nCapa de ingenieroBotas de dibujante"},
[1097] = {title = "La tarea de Elmore", objective = "Habla con Grimand Elmore.", description = "Hay un enano forjador de armas en Ventormenta, Grimand Elmore, que ha enviado un mensaje diciendo que necesita ayuda con una entrega. Creo que quiere enviar un paquete a su hogar, en el norte.\n\n¡Tienes unas piernas muy fuertes! Si te interesa ponerlas en marcha, vete a hablar con Grimand. Podrías sernos muy útil aquí, pero también debemos mantener nuestros tratos con los enanos.\n\nPuedes encontrar a Grimand Elmore en la tienda de armas del Distrito de los Enanos de Ventormenta, en la sección noreste de la ciudad.", progress = nil, completion = "¿Estás aquí para ayudar con la entrega? ¡Estupendo!", rewards = "Recibirás:\n\n"},
[1098] = {title = "Mortacechadores en Colmillo Oscuro", objective = "Encuentra a los mortacechadores Adamant y Vincent.", description = "Estamos preparando un ataque a Colmillo Oscuro y enviamos a dos mortacechadores a investigar. De eso hace ya muchos días ¡y necesitamos su informe para preparar el ataque! \n\n<nombre>, tienes que ir al Castillo de Colmillo Oscuro, hacia el sur, y localizar a los mortacechadores Adamant y Vincent. Ayúdalos si lo necesitan. \n\nEsta misión es tan peligrosa como crítica, <nombre>. No falles.", progress = nil, completion = "El cuerpo de Vincent yace inmóvil.\n\nAunque el mortacechador Adamant enviará un informe a Hadrec, Vincent se llevó a la tumba otros secretos.\n\nPero tiene en la mano un abalorio...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nManto fantasmal"},
[1099] = {title = "¡Los gnomos ganan!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCaja con premio goblin"},
[1100] = {title = "El diario de Henrig Soliceja", objective = "Lee el diario de Henrig Soliceja.", description = "Abres lentamente el desgastado diario de Henrig Soliceja...", progress = "De tu cara de preocupación se deduce que algo no marcha bien...", completion = "¿Y dices que este diario lo tenía un enano muerto? Dame un momento para leerlo...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1101] = {title = "La bruja del Horado", objective = "Llévale el medallón de Filonavaja a Díscolo Falfildel de Thalanaar.", description = "¡Pobre Heralath! Y el enano Soliceja, ¡qué valiente era!\n\nNo debemos permitir que su valentía caiga en el olvido. Hay que detener a esa malvada... Charlga Filonavaja.\n\nSin duda, infiltrarse en el Horado es arriesgado pero no hay tiempo para enviar noticias a Darnassus. <nombre>, reúne un equipo para matar a Charlga.\n\nTráeme el medallón de Filonavaja como prueba de su muerte.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nProtectores de berilioFaja Puño de piedraRodela de mármol\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTrabuco \"Ojo de mago\"Protectores de berilioRodela de mármolFaja Puño de piedra"},
[1102] = {title = "Un destino vengador", objective = "Llévale el corazón de Filonavaja a Auld Picopiedra, que está en Cima del Trueno.", description = "Cairne es un líder noble que ha unido a nuestro pueblo aquí en Cima del Trueno.\n\nPero no puedo perdonar a aquellos que nos han echado de nuestras tierras ancestrales. Mi pueblo habitó en los Baldíos del Sur durante décadas, una tierra que era sagrada para nosotros, hasta que numerosos enemigos nos expulsaron de ellas.\n\nEl destino le depara venganza a Charlga Filonavaja, que tiene a un ejército congregado en Horado Rajacieno.\n\nTráeme el corazón de Filonavaja y podré morir en paz, <nombre>.", progress = "Cuando las bestias de Horado Rajacieno hayan sido expulsadas de su guarida, podré por fin descansar en paz sabiendo que han recibido su merecido.\n\n¿Has echado a los jabaespines del Horado? Dime que sí, y que me traes el corazón de Filonavaja como prueba de ello...", completion = "La tribu de Horado Rajacieno ya conoce de primera mano el sufrimiento vivido por mi pueblo. ¡Esas bestias se lo tenían bien merecido!\n\nExpulsar a alguien de una tierra sagrada es un pecado que merece un duro castigo.\n\nGracias por tu ayuda, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nProtectores de berilioFaja Puño de piedraRodela de mármol\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nProtectores de berilioRodela de mármolFaja Puño de piedra"},
[1103] = {title = "La llamada del agua", objective = "Llévale una sapta de agua a Tiev Mordune en el Bosque de Argénteos si no consigues hablar con la manifestación de agua menor.", description = "¡Saluditos, <hermano/hermana>! ¿También has venío a purificar las aguas y a demostrarle a Islen que l’has captao to’?\n\nYo mesmamente m’he cargao al espíritu corrupto del agua y estaba a punto de colocar los brazales y las gotas restantes d’agua en el blandón.\n\n¿Qué pasha? Ah, ya. Tiev s’entera cuando algo no anda bien. Vale, Tiev podrá ayudarte mogollón si tienes que volver a ver al espíritu del agua pura.\n\nTráeme una sapta d’agua de Islen como prueba de qu’estás <listo/lista> y esperaré pa' cumplir mi parte.", progress = nil, completion = nil, rewards = nil},
[1104] = {title = "Veneno de escórpidos salados", objective = "Llévale 6 glándulas de veneno de escórpidos salados a Wizzle Pernolatón, que está en El Desierto de Sal.", description = "¡Tengo un montón de diseños para nuestro coche cohete! ¡Muchos! ¡Y tengo que probarlos todos!\n\nEstoy trabajando en un nuevo tipo de combustible. ¡Uno que arde a muy, muy, muy alta temperatura! Aún no he conseguido la fórmula perfecta pero creo que ya sé lo que necesito...\n\nEl veneno de los escórpidos que rondan El Desierto de Sal contiene gran cantidad de sal. ¡Es algo único y es justo lo que necesito!\n\nTráeme ese veneno y... créeme, ¡nuestro coche volará!", progress = "¡Hola, hola, hola! ¿¿Conseguiste el veneno??", completion = "¡Genial! ¡Este veneno será perfecto! ¡Gracias, &lt;nombre&gt;!\n\nAhora solo tengo que espesarlo algo, echarle un poco de correcina, añadir un negacatalizador y... ¡Maldición! ¿¡Dónde están mis notas!?", rewards = "Recibirás:\n\n"},
[1105] = {title = "Caparazones endurecidos", objective = "Llévale 9 caparazones de tortuga endurecidos a Wizzle Pernolatón, que está en El Desierto de Sal.", description = "Mi hermano está trabajando en un nuevo tipo de combustible para su coche cohete. Es un coco, en serio. Es tan inteligente que... ¡creo que el nuevo combustible que está diseñando es tan potente que podría corroer el chasis del coche!\n\nQuiero reforzar sus depósitos de combustible antes de que eso ocurra.\n\nLas tortugas que habitan El Desierto de Sal tienen unas conchas muy resistentes y quiero utilizarlas para hacer nuevos depósitos para nuestro coche cohete que sean capaces de contener el nuevo combustible de mi hermano.\n\nTú pareces alguien responsable. ¿Podrías conseguirme esos caparazones?", progress = "Mi hermano está haciendo progresos en su nuevo combustible y eso me intranquiliza. ¡Quiero ponerme manos a la obra con el depósito de caparazón de tortura lo antes posible!\n\n<nombre>, ¿tienes ya los caparazones?", completion = "¡Los has conseguido! ¡Gracias, &lt;nombre&gt;!\n\nVaya, estos caparazones son más duros de lo que yo pensaba. ¡Voy a romper todas mis herramientas!", rewards = "Recibirás:\n\n"},
[1106] = {title = "Martek el Exiliado", objective = "Llévale la carta de Fizzle Pernolatón a Martek el Exiliado, que está en las Tierras Inhóspitas.", description = "Estoy trabajando en un nuevo motor que hará que el coche corra como el viento. ¡Podrá incluso volar, lo sé! Lo único que tengo que hacer es crear unos pistones que puedan aguantar una presión realmente alta. ¡Todos mis ensayos fueron un fracaso porque no consigo que los pistones sean lo suficientemente resistentes!\n\nAunque hay alguien que puede que sepa cómo conseguirlo.\n\nSu nombre es Martek el Exiliado. Es un gran herrero y sabe más sobre metales que ningún otro. Llévale esta carta, ¡estoy seguro de que nos puede ayudar!\n\nAunque está muy, muy lejos, en las Tierras Inhóspitas de Azeroth, en un campamento con un goblin.\n\n¡Ve y encuéntralo!", progress = "Genial, me has encontrado. Bien por ti.\n\n¿Vienes por el hacha que le hice a Gath'Ilzogg? ¿O acaso vienes a reclamar el honor de la duquesa Pamay?\n\nEn realidad, no me interesan tus motivos. Así que si estás aquí para pelear, acabemos cuanto antes...", completion = "¿Te envía Fizzle? ¡Eso lo cambia todo! Pues claro que te voy a ayudar. ¡Fizzle y su hermano son de los pocos a los que no les gustaría verme muerto!\n\nAsí que Fizzle quiere unos pistones, ¿verdad? ¿Unos que aguanten bastante presión? Conociendo a Fizzle, ¡seguro que su nuevo coche será un volcán con ruedas!", rewards = "Recibirás:\n\n"},
[1107] = {title = "Aletas dorsales escamadas", objective = "Llévale 10 aletas dorsales escamadas a Wizzle Pernolatón, que está en El Desierto de Sal.", description = "¡Nuestro coche cohete es el más rápido de El Desierto de Sal! Es tan rápido que... ¡tengo miedo de que la fricción lo haga reventar en pedazos!\n\nPara evitarlo, tengo que crear un compuesto para recubrir con él las partes móviles del coche. Tiene que ser duro pero que no se rompa, y tiene que ser resistente a la corrosión de la sal o después de un tiempo en El Desierto de Sal acabará desgastándose.\n\n¿Has oído hablar de El Arrecife Mortal, <nombre>? Está en la costa, más allá de Vega de Tuercespina. Allí habitan murlocs de escamas saladas, y sus aletas dorsales son el ingrediente imprescindible para mi compuesto.", progress = "¿Tienes las aletas dorsales? Si no puedo proteger nuestro coche de la velocidad, no duraremos mucho en las carreras.", completion = "¡Gracias, &lt;nombre&gt;! Cuando las haya molido y mezclado con una serie de ingredientes, el compuesto estará listo. Y cuando así sea, ¡el coche no sufrirá ningún daño vaya a la velocidad que vaya!\n\nEso sí, siempre que no choque con nada...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1108] = {title = "Indurio", objective = "Llévale 10 láminas de indurio a Martek el Exiliado, que está en las Tierras Inhóspitas.", description = "No estoy aquí solo para estar lejos de la gente. Hay metal en abundancia en las Tierras Inhóspitas, <nombre>, siempre que tengas las agallas para ir a por él.\n\nEl metal se llama indurio y si es cierto lo que dicen, posee una alta resistencia al calor y a la presión. Puede que sirva para el coche de Fizzle.\n\nPero tenemos que asegurarnos. Consígueme unas muestras de indurio y así podré comprobar sus propiedades.\n\nLa verdadera mena de indurio se encuentra en Uldaman, pero los troggs Grutacanto de las Tierras Inhóspitas suelen llevar encima algunas láminas de indurio. Tráeme esas láminas.", progress = "¿Encontraste alguna lámina de indurio, <nombre>? Tengo la forja preparada para recibirlas.", completion = "Bien, estas son perfectas. Apliquémosles un poco de calor y veamos qué pasa...", rewards = "Recibirás:\n\n"},
[1109] = {title = "Guano del Horado", objective = "Llévale una pila de guano del Horado al maestro boticario Faranell de Entrañas.", description = "Como puedes comprobar, <nombre>, tenemos unos interesantes experimentos entre manos aquí en la Sociedad Real de Boticarios.\n\nDado que yo soy el maestro aquí, mi trabajo consiste en supervisar nuestros proyectos de alquimia más ambiciosos. ¡Hay tanto trabajo y tan poco tiempo!\n\nParece que tienes muchos kilómetros a tus espaldas, <clase>, así que quizás puedas ayudarnos.\n\nNecesito una sustancia no muy común y un tanto nauseabunda que proviene de una extraña especie de murciélagos que solo se encuentran en ese agujero apestoso conocido como Horado Rajacieno. Tráeme el guano de los murciélagos del Horado para que podamos ponernos manos a la obra.", progress = "Ah, veo que estás de vuelta. ¿Has conseguido guano del Horado?", completion = "¡Espléndido! Era justo lo que necesitábamos para comenzar, &lt;nombre&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1110] = {title = "Piezas de coches cohete", objective = "Llévale 30 piezas de coche cohete a Kravel Karbarbán, que está en El Desierto de Sal.", description = "Je, je, con tantas carreras que se celebran aquí no es de extrañar que haya tantos accidentes. Si echas un vistazo por El Desierto de Sal te darás cuenta de que está plagado de restos de accidentes. ¡Hay piezas de coches cohete por todas partes!\n\nY esas piezas tienen mucho valor para los gnomos y los goblins, que siempre están en busca de nuevos artilugios que instalar en sus coches.\n\nAsí que sal ahí fuera y consígueme algunas piezas. Tráeme un buen montón y te pagaré como mereces.", progress = "<nombre>, ¿has estado en El Desierto de Sal? ¿Me has traído las piezas que quería?", completion = "¡Vaya, mira todo eso! Hay algunas cosas interesantes en ese montón de basura. Seguro que saco un buen pellizco por ellas.\n\nEspera... ¿eso que está ahí espachurrado es una oreja? ¡Qué dolor! No me gustaría ser ese piloto...\n\nGracias, &lt;nombre&gt;. Aquí tienes tu dinero. Y te voy a dar un consejo gratis...\n\nSiempre se puede sacar beneficio de las desgracias ajenas.", rewards = "Recibirás:\n\n"},
[1111] = {title = "Maestro de los muelles Vertipeluca", objective = "Llévale el pedido de piezas de Kravel al maestro de los muelles Vertipeluca, que está en Trinquete.", description = "Mi trabajo consiste en proporcionar a los demás lo que necesitan, y en este caso los gnomos me han hecho un pedido de piezas. Las piezas llegaron a Trinquete hace una semana pero yo no he podido ir a recogerlas.\n\nAsí pues, ¿te apetece ir a dar un paseo? Ve a Trinquete y entrégale este pedido de piezas al maestro de los muelles Vertipeluca, que seguramente esté en el puerto.\n\nHaz esto por mí y te encargaré más trabajos.\n\nTrabajos, <amigo mío/amiga mía>, que te reportarán grandes beneficios...", progress = "¡Habla! Dime... ¿vienes a entregar o a recoger mercancía?", completion = "Ah, sí. Las piezas para Kravel... Me alegro de que hayas venido. Llevan días aquí y tenía miedo de que se oxidaran.", rewards = "Recibirás:\n\n"},
[1112] = {title = "Las piezas de Kravel", objective = "Llévale las piezas de Kravel a Kravel Karbarbán, que está en El Desierto de Sal.", description = "Aquí está el paquete para Kravel, <nombre>. Cuando se lo entregues, dile que si quiere más pedidos especiales ¡tendrá que correr con los gastos!", progress = "¿Conseguiste llegar a Trinquete y hacerte con las piezas?", completion = "¡Genial, las tienes! Ahora hay que llevárselas a los gnomos, pero antes...\n\nVoy a elegir una de ellas para quedármela.", rewards = "Recibirás:\n\n"},
[1113] = {title = "Corazones de fervor", objective = "El maestro boticario Faranell de Entrañas quiere 20 corazones de fervor.", description = "Por qué necesito un tipo de guano tan poco común, te preguntarás. Bueno, para este experimento quiero ver el efecto que dos sustancias químicas diametralmente opuestas tienen entre sí, sobre todo cuando se combinan con un agente incluso más tóxico para obtener... los resultados más horripilantes.\n\nEn las cumbres más nororientales de los Claros de Tirisfal se encuentra el Monasterio Escarlata, lugar en el que se reúnen los fervientes discípulos de la Cruzada Escarlata. Mátalos a todos y tráeme sus corazones.\n\n¡Nuestro brebaje será de lo más vil!", progress = "¿Has estado en el Monasterio Escarlata? ¡Voy a necesitar muchos corazones para mi maravilloso experimento!\n\nSin duda, a Varimathras y a la Dama Oscura también les gustará deshacerse de la porquería escarlata...", completion = "Me has traído sangre en abundancia, &lt;nombre&gt;. Tus esfuerzos han sido más que sobresalientes.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1114] = {title = "Un envío para los gnomos", objective = "Llévale las piezas de coche frágiles a Fizzle Pernolatón, que está en El Desierto de Sal.", description = "Ahora que tengo lo que necesito, entrégale estas piezas a Fizzle Pernolatón. Lleva mucho tiempo esperando por ellas. Estoy seguro de que no echará en falta la pieza con la que me he quedado...\n\n¡Y yo que tú no se lo mencionaría! Era pequeña e insignificante.\n\n¡Confía en mí!", progress = "¿Tienes algo para mí? ¡Genial! A ver, a ver...", completion = "¡Aquí están los hidrotrinquetes y las ballestas! Son perfectos. ¡Gracias!", rewards = "Recibirás:\n\n"},
[1115] = {title = "Rumores", objective = "Habla con Krazek en Bahía del Botín.", description = "Ahora que ya hemos prestado servicio a los gnomos, ha llegado el momento de hacerles un favor a los goblins. \n\nHe oído que el piloto de carreras goblin, Nazz Vaporio, no siempre ha sido conocido por ese nombre. Me pregunto por qué se lo habrá cambiado, si será un secreto y si realmente valora ese secreto.\n\nSi alguien sabe la verdad de todo esto, ese es Krazek de Bahía del Botín, en el extremo meridional de Tuercespina. Habla con Krazek y pregúntale acerca del piloto goblin.\n\nSi consigues información relevante, puede que le saquemos beneficios.", progress = nil, completion = "¿Preguntas por Nazz Vaporio? Es un piloto de carreras en El Desierto de Sal, ¿no?\n\nSí, puede que averigüe algo sobre él, pero no será fácil. Ni rápido...\n\n¡Y menos barato!", rewards = "Recibirás:\n\n"},
[1116] = {title = "Polvo de sueño en el pantano", objective = "Llévale 10 pizcas de polvo de sueño a Krazek a Bahía del Botín. El polvo de sueño se obtiene de las crías de dragón que habitan en el Pantano de las Penas.", description = "Si te diera información sobre Nazz te estaría haciendo un favor, <nombre>, una gran favor. Pero has venido desde El Desierto de Sal para conseguirla... ¿estás <dispuesto/dispuesta> a viajar un poco más?\n\nMientras contacto con mis fuentes, ¿por qué no vas a coger algo para mí, eh?\n\nVeamos, qué podría ser... ¿Qué te parece polvo de sueño de las crías de dragón que hay en el Pantano de las Penas? ¡Sí, me encantaría tener polvo de sueño para mezclarlo con mi tabaco!\n\n<nombre>, consígueme un poco y te daré la información que andas buscando.", progress = "¿Ya has vuelto? ¿Tienes mi polvo de sueño?", completion = "Eh, mira eso. ¡Encontraste el polvo de sueño!\n\n¿Y qué, te gustó el pantano, &lt;nombre&gt;? No fácil conseguir comida allí, ¿verdad?", rewards = "Recibirás:\n\n"},
[1117] = {title = "Rumores para Kravel", objective = "Llévale los rumores del goblin a Kravel Karbarbán, que está en El Desierto de Sal.", description = "¡Tuve que hacer algunos favores a cambio, regalar una botella muy especial de vino élfico y atar cabos! Pero finalmente conseguí la información que querías.\n\nAquí tienes, escribí una carta en la que explico lo más destacado de nuestro amigo, Nazz Vaporio. Haz con ella lo que creas oportuno pues es demasiado peligrosa para que yo la guarde.\n\n¡Pero no le digas a nadie de dónde la has sacado!", progress = "¿Has averiguado algo de Nazz Vaporio? ¿Algún secreto que no quiere que se sepa, quizás?", completion = "¡Vaya, vaya! Esto es interesante... muy interesante. Eh, quizás la próxima vez que vea al piloto goblin debería hacerle una reverencia.\n\nLo que has averiguado vale oro, &lt;nombre&gt;. Estoy seguro de que Nazz pagará una pequeña fortuna para mantener su secreto... en secreto.", rewards = "Recibirás:\n\n"},
[1118] = {title = "De vuelta a Bahía del Botín", objective = "Llévale el plan de Kravel a Loquillo Pinchabujas, que está en Bahía del Botín.", description = "<nombre>, sé que eres un <clase> en quien puedo confiar, tanto para realizar un trabajo como para mantener la boca cerrada mientras lo haces.\n\nHe ideado un nuevo plan. Es arriesgado pero si sale bien... ¡podríamos ganar un dineral! Y creo que tú eres la persona adecuada para conocer todos los detalles.\n\nPara empezar, debes volver a Bahía del Botín. Lo siento, <nombre>. Sé que para el último encargo también tuviste que ir allí pero, en esta ocasión, tendrás que hablar con Loquillo Pinchabujas.\n\nEntrégale esta carta y si necesita ayuda haz todo lo posible por echarle una mano. El esfuerzo merecerá la pena.", progress = "Hola, hola. ¿Qué trae a un noble <raza> como tú a Bahía del Botín?", completion = "Entiendo. Kravel ha ideado un nuevo plan. Este Kravel es un tanto siniestro, y eso me gusta.\n\nAsí que necesitas algo para confundir los sentidos, ¿no es cierto? Algo fuerte...\n\nMe pregunto si la mezcla que Zanzil les da a los zombis serviría...", rewards = "Recibirás:\n\n"},
[1119] = {title = "La mezcla y cerveza de tontos de Zanzil", objective = "Llévale la cerveza de tontos a Kravel, que está en El Desierto de Sal.", description = "Creo que ya sé cómo preparar con la mezcla de Zanzil y un poco de cerveza un brebaje bárbaro: la cerveza de tontos. Creo que es justo lo que Kravel necesita pero quiero que la pruebes y me digas qué tal son los resultados.\n\nY <nombre>, no estés mucho tiempo sin utilizarla pues la mezcla de Zanzil tiene un efecto muy extraño sobre la levadura y no tarda mucho en estropearse.", progress = "Hola, <nombre>. ¿Pinchabujas nos ha echado una mano o no?", completion = "¡Estupendo! ¡Pinchabujas me ha echado una mano! Lo recordaré la próxima vez que lo vea.\n\nGracias, &lt;nombre&gt;. Estás haciendo un buen trabajo y ambos vamos a ganar mucho con todo esto...", rewards = "Recibirás:\n\n"},
[1120] = {title = "Emborrachar a los gnomos", objective = "Llévale la cerveza de tontos al jefe técnico de los gnomos.", description = "Tenemos que probar la cerveza de tontos con uno de los equipos técnicos. Si quieres probarla con los gnomos, coge la cerveza y dásela al jefe técnico de los gnomos.\n\nY no tardes mucho pues la cerveza podría estropearse.", progress = "¡Hola! ¿Tienes algo para mí?", completion = "¿Cerveza? ¡Qué detalle por tu parte! ¡Gracias!\n\nNo deberíamos beber antes de la carrera pero un poquito de nada no les hará daño a los chicos. Además, no será tan fuerte, ¿verdad?", rewards = "Recibirás:\n\n"},
[1121] = {title = "Emborrachar a los goblin", objective = "Llévale la cerveza de tontos al jefe técnico de los goblins.", description = "Tenemos que probar la cerveza de tontos con uno de los equipos técnicos. Si quieres probarla con los goblins, coge la cerveza y dásela al jefe técnico de los goblins.\n\nY no tardes mucho pues la cerveza podría estropearse.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1122] = {title = "Volver a informar a Pinchabujas,", objective = "Llévale la cerveza de tontos a Loquillo Pinchabujas, que está en Bahía del Botín.", description = "¡Esa cerveza de tontos funciona a las mil maravillas! He apostado en la próxima carrera así que esperemos que los técnicos la hayan hecho buena con los coches.\n\nHay una cosa más que debes hacer: llévale esta nota a Loquillo Pinchabujas a Bahía del Botín. Le gustará saber que la prueba ha sido todo un éxito.\n\nCon un poco de suerte, podremos además conseguir más cerveza de tontos para otras carreras.", progress = "Hola, <nombre>. ¿Ha funcionado la cerveza o no? Es fuerte, ¿verdad?", completion = "Aquí dice que la cerveza de tontos ha sido todo un éxito. ¡Qué noticia tan buena!\n\nSi necesitas más, solo tienes que decírmelo y conseguir un poco de mezcla de Zanzil.", rewards = "Recibirás:\n\n"},
[1123] = {title = "Rabine Saturna", objective = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Rabine Saturna. Claro de la Luna se encuentra entre Frondavil y Cuna del Invierno; para llegar, tienes que seguir el camino que sale del Bastión de los Fauces de Madera.", description = "El mayordomo del guardián Rémulos, Rabine Saturna, está en Claro de la Luna buscando ayuda para explorar los vastos páramos al oeste de Tanaris y Un'Goro. Se encuentra en Amparo de la Noche, el poblado principal de Claro de la Luna.\n\nQuiero que seas consciente de lo importante que es que estés allí pues hay ciertas tensiones entre los elfos de la noche de Darnassus y el Círculo Cenarion de Claro de la Luna. Obvia los límites raciales y aplica la sabiduría heredada de tus ancestros a la hora de tratar con el Círculo, <nombre>. Buena suerte.", progress = nil, completion = "Soy Rabine Saturna. Te doy la bienvenida de parte del guardián Remulos y de los demás habitantes de este sagrado claro. \n\nAprecio tu interés y tu deseo de ayudar al Círculo Cenarion. Los tauren respetan los principios de Cenarius y, por ello, la Horda también es bien recibida en este claro. Ambos perseguimos un objetivo común, que es preservar Azeroth. \n\nAquí estás entre amigos, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[1124] = {title = "Páramo", objective = "Enséñale la carta del rabino a Layo Estrella cerca del cementerio de Sosiego del Valor en Silithus.", description = "Al oeste del Cráter de Un'Goro vía la cresta noroccidental, se encuentra una tierra inhóspita: Silithus. Pocos saben que allí se libró una guerra entre los elfos de la noche y un poder maligno y extraño. El mal fue sellado, pero sospechamos que hay fuerzas que intentan despertar a este horror inimaginable.\n\nBusca a Layo Estrella, uno de nuestros agentes en Silithus; lo encontrarás en Sosiego del Valor, donde reposan los caídos en los eriales. Dale esta carta y él te entregará su confianza.", progress = "Este no es lugar para pasearse, <clase>. ¿Qué te trae a Silithus?", completion = "Ah, vienes de parte del rabino. Te doy la bienvenida a estas tierras inhóspitas.\n\nYo no fui testigo de las grandes guerras, pero la desolación se huele en el aire. Gracias a tu ayuda, puede que el Círculo Cenarion ponga fin a una amenaza que se creía eliminada hace generaciones.", rewards = "Recibirás:\n\n"},
[1125] = {title = "Los espíritus de Viento del Sur", objective = "Libera los espíritus de 8 druidas torturados y 8 centinelas torturadas en la Aldea del Viento del Sur. A continuación, reúnete con Layo Estrella en el cementerio de Sosiego del Valor en Silithus.", description = "Al suroeste de aquí hay un lugar trágico, las ruinas de la Aldea del Viento del Sur. Ahí es donde deberíamos empezar a trabajar, <nombre>.\n\nPor lo que he descubierto, antiguamente, esa aldea era una base de operaciones de los elfos de la noche en Silithus. Mucho tiempo atrás fue tomada por las colmenas de silítidos que infestan los restos. Solo puedo imaginarme el horror de la destrucción de la aldea...\n\nLos espíritus torturados de los druidas y centinelas caídos, vagan por las ruinas sin rumbo fijo. Explora el lugar y libera las almas de esos pobres seres.", progress = nil, completion = "Creía que quizás la enorme colmena que está cerca de las ruinas de la aldea había tenido algo que ver. Tu trabajo ha demostrado que esto es correcto. La proximidad de la colmena supone una amenaza para cualquier trabajo futuro que podamos hacer aquí. \n\nTe agradezco lo que has hecho para liberar las almas de los caídos; ese tipo de trabajo no es fácil, lo sé. Sin embargo, cuando estés preparado, necesito volver a pedirte ayuda...", rewards = "Recibirás:\n\n"},
[1126] = {title = "Una colmena en la torre", objective = "Sube a la torre de la Aldea del Viento del Sur y encuentra la forma de agitar la colmena de silítidos para que entre en actividad. Recoge cualquier cosa extraña que encuentres y llévasela a Layo Estrella en el cementerio de Sosiego del Valor en Silithus.", description = "Hay que buscar las colmenas en la Aldea del Viento del Sur y creo que sé la forma de atacarlas. Según mi experiencia, las colmenas de silítidos son muy sensibles a su entorno. Esto explica que ataquen al oír los lamentos de los espíritus.\n\nLa presencia más fuerte de silítidos está en la torre, la que tiene una masa de avispas volando alrededor. Escala la torre y consigue que la colmena se agite. Vuelve a informarme sobre tus descubrimientos, ¡pero ten cuidado! Si descubres algo que pueda ayudar, tráemelo.", progress = "¿Y qué? ¿Ha funcionado? Sigues de una pieza, me alegro. ¿Has encontrado algo?", completion = "Pero... ¿pero qué...?\n\n¿Tiene forma de... caja, quizás? Está recubierto de algo muy sólido. Espera, aquí hay una parte a la vista... ¿Es eso un libro lo que hay debajo de toda esa porquería?\n\nCreo que has encontrado un gran descubrimiento, &lt;nombre&gt;... bueno, al menos eso espero. Quizás sea un diario o un registro de tiempos pasados guardado en este contenedor.\n\nCreo que sé la forma de descubrir lo que es.", rewards = "Recibirás:\n\n"},
[1127] = {title = "Cerveza de tontos", objective = "Llévale 12 mezclas de Zanzil a Loquillo Pinchabujas, que está en Bahía del Botín.", description = "¿Así que quieres un poco de cerveza de tontos, eh? Seguro que estás pensando en volver a las carreras...\n\nEstá bien, tráeme más mezclas de Zanzil y yo te prepararé más cerveza de esa.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nCerveza de tontos"},
[1130] = {title = "Melor Pezuñapétrea", objective = "Habla con Melor Pezuñapétrea en Cima del Trueno.", description = "<nombre>, Melor Pezuñapétrea ha oído hablar de tus hazañas y ha trazado un camino para ti.\n\nSi deseas seguirlo, ve a Cima del Trueno y habla con Melor. Lo encontrarás en el Alto de los Cazadores.", progress = nil, completion = "Has venido, &lt;nombre&gt;. ¿Estás preparado para tu viaje por tierras lejanas?\n\nSi es así, joven &lt;clase&gt;, escucha atentamente.", rewards = nil},
[1131] = {title = "Metaleador", objective = "Llévale la costilla de Metaleador a Melor Pezuñapétrea, que está en Cima del Trueno.", description = "A veces se caza para conseguir alimento, otras por honor e incluso se puede cazar para aprender las lecciones que enseña la Madre Tierra.\n\nPero la caza que yo te encargo no pertenece a ninguno de estos tipos. Se trata de matar a una criatura que no cuenta con la bendición de la Madre Tierra.\n\nLa hiena Metaleador vaga por Las Mil Agujas, al sur de Los Baldíos. Es un tirano que intenta atraer a otras hienas y que infunde miedo a otras criaturas más pacíficas, provocando un derramamiento de sangre.\n\nEncuentra a Metaleador, <nombre>. Encuéntralo y derrótalo.", progress = "¿Has derrotado a Metaleador, <nombre>?", completion = "Has derrotado a Metaleador y con ello has liberado a Las Mil Agujas de una auténtica pesadilla.\n\nEsa era la lección que quería enseñarte hoy.", rewards = "Recibirás:\n\n"},
[1132] = {title = "Fiora Orejona", objective = "Habla con Fiora Orejona en Theramore.", description = "¡Ay, estar de nuevo al lado del mar! Sentir la caricia del viento y dejar que las olas me mezan como hacía mi querida madre...\n\n¡Ay! Ojalá estuviera en tu lugar, <clase>, pues veo el mar en tu futuro.\n\nMi trabajo es hablarles a las almas ambiciosas de la tierra de Kalimdor, ¡la tierra de las oportunidades! Si deseas probar suerte en el mar, coge un barco y dirígete al maravilloso puerto de Theramore. Allí habla con mi socia, la elfa Fiora Orejona.\n\n¡Ella te ayudará a comenzar tu aventura por Kalimdor!", progress = nil, completion = "¡Buenos días! ¿Te envía el señor Llavesílex de Puerto de Menethil? El señor Llavesílex es un buen psicólogo así que si él te envía, eso quiere decir que en Kalimdor encontrarás tu sitio.", rewards = "Recibirás:\n\n"},
[1133] = {title = "Viaje a Astranaar", objective = "Habla con Shindrell Fuegoveloz en Astranaar.", description = "Si no conoces Menethil lo primero que debes hacer es ir a Astranaar. Estoy seguro de que un miembro de la Alianza entusiasta como tú encontrará algo que hacer allí. Habla con Shindrell Fuegoveloz y ofrécele tus servicios.\n\nPero no le digas quién te envía pues Shindrell no me conoce...", progress = nil, completion = "¿Estás aquí para ofrecer tus servicios a la Alianza, &lt;nombre&gt;? Toda ayuda es bien recibida porque, aunque el Bosque de Vallefresno conserva su belleza, ya no es un lugar tan pacífico como antes.", rewards = nil},
[1134] = {title = "Alaorgullo del Esploón", objective = "Lleva 12 sacos de veneno de alaorgullo a Shindrell Fuegoveloz en Astranaar.", description = "No tenemos la guerra declarada a la Horda, pero nuestra paz es frágil. A menudo encontramos a sus agentes actuando en nuestra contra, pero lo hacen encubiertamente.\n\nNos enteramos de uno de esos avances mediante unas fuentes ocultas en Theramore, decían que los orcos recogen veneno de las alaorgullo, cerca del Lago Mirkfallon en Sierra Espolón, hacia el sur. Los asesinos orcos utilizan el veneno en sus asaltos secretos contra la Alianza.\n\nSi esto es cierto, debemos detenerlos, <nombre>. Tenemos que impedir que accedan a su fuente de veneno.", progress = "¿Tienes los sacos de veneno de alaorgullo, <nombre>? No podemos permitir que los orcos utilicen el veneno de esas bestias contra nosotros.", completion = "Gracias, &lt;nombre&gt;. Me entristece ordenar matar a criaturas que no quieren ayudar voluntariamente a nuestro enemigo, pero es mi deber hacerlo.\n\nTu corazón puede llorar por los alaorgullo que has matado, pero también debería estar henchido de orgullo al saber que tus aliados están a salvo del veneno de alaorgullo.", rewards = "Recibirás:\n\n"},
[1135] = {title = "Glándulas de veneno de Nido Alto", objective = "Llévale 10 glándulas de veneno de Nido Alto a Fiora Orejona, que está en Theramore.", description = "Como ya sabrás, los orcos suelen domar a dracoleones para utilizarlos como montura. Lo que quizás no sepas es que el veneno de un dracoleón es, en algunos círculos, mucho más valioso que la propia bestia. Así que si no te da miedo ir a la caza del dracoleón, entonces... tengo un trabajo para ti.\n\nHay un lugar llamado Nido Alto en la tierra de Las Mil Agujas, al oeste de aquí. Los dracoleones de Nido Alto tienen un potente veneno. Potente y muy valioso...", progress = "¿Encontraste Nido Alto, <nombre>? ¿Qué tal te fue con los dracoleones que allí habitan?", completion = "Vaya, la mayor parte de ellas están cargadísimas de veneno, &lt;nombre&gt;. Las glándulas como estas se suelen obtener de dracoleones jóvenes o de hembras fértiles. ¡Tuvo que haber sido una auténtica matanza en Nido Alto!", rewards = "Recibirás:\n\nCinturón Portavientos"},
[1136] = {title = "Faucehielo", objective = "Lleva la melena de Faucehielo a Melor Pezuñapétrea en Cima del Trueno.", description = "<nombre>, ha llegado la hora de que viajes a lejanas tierras. Estás <preparado/preparada>.\n\nAhora vas a cazar a Faucehielo, un yeti de las Montañas de Alterac. Es escurridizo, su inmaculado pelaje se confunde con la nieve de la montaña en la que mora. Rara vez se deja ver.\n\nPero te enseñaré a engatusar a una criatura como él. Aquí tienes, toma este pergamino. En él explico cómo atraer a Faucehielo.\n\nAhora vete, <nombre>. Tu viaje es largo y te aguardan innumerables desafíos. Solo cuando vuelvas con la melena de Faucehielo habrás completado tu tarea.", progress = "¿Has estado en las Montañas de Alterac, <nombre>? ¿Te has enfrentado al escurridizo Faucehielo?", completion = "Hacía falta fuerza y astucia para encontrar a Faucehielo y derrotarlo, &lt;nombre&gt;. Y tienes ambas.\n\nTu camino te ha llevado al valle de Las Mil Agujas y a Altomonte en Alterac. Un día te conducirá por todas las tierras de la Madre Tierra, porque tal es el camino del cazador.", rewards = "Recibirás:\n\nPenacho de cazador de espíritus"},
[1137] = {title = "Noticias para Fizzle", objective = "Habla con Fizzle Pernolatón en El Desierto de Sal.", description = "¡El indurio tiene una extraordinaria resistencia al calor! Estoy seguro de que será perfecto para los pistones de Fizzle. Pero necesitará indurio de verdad, no tan sólo unas láminas.\n\nPara poder acceder a él, habrá que extraerlo de las mismísimas profundidades de Uldaman, en el extremo norte de las Tierras Inhóspitas. Esa es la auténtica fuente del indurio.\n\nMándale recuerdos a Fizzle Pernolatón de mi parte y dile lo que te he contado.\n\nBuen viaje, <nombre>. No me sorprendería que volviéramos a vernos pues cuando Fizzle compruebe las propiedades del indurio, es probable que te envíe a Uldaman a por él.", progress = nil, completion = "&lt;nombre&gt;, ¡has vuelto! ¿Encontraste a Martek? ¿Hablaste con él? ¿Y qué dijo? ¿¿Puede ayudarme??\n\n¡Cuéntamelo todo!", rewards = "Podrás elegir una de estas recompensas:\n\nMechero de gasolina de FizzleGuantes de mecánico gnómico\n\nTambién recibirás:\n\nMechero de gasolina de FizzleGuantes de mecánico gnómico"},
[1138] = {title = "Rico marisco", objective = "Reúne 6 trozos de cangrejo de gran calidad para Gubber Blump de Auberdine.", description = "Adoro los cangrejos. ¡Los cangrejos son el fruto del mar! Puedes asarlos, hervirlos, guisarlos, hacerlos a la plancha... Se hacen en sartén, en olla, en hogueras... Y también hay ensalada de cangrejo, sopa de cangrejo, guiso de cangrejo, cangrejo a la pimienta, cangrejo al limón y cangrejo-sorpresa de Forjaz. Creo que no me dejo nada.\n\nPero ahora estoy pescando. No voy a conseguir ningún cangrejo. Se pueden conseguir de correarrecifes y de corremareas taraceados. Y se pueden hacer mil cosas con ellos. Puedes asarlos, hervirlos, guisarlos, hacerlos a la plancha...", progress = "...cangrejo al limón y cangrejo-sorpresa de Forjaz. Creo que no me dejo nada.\n\nOh, hola, <nombre>. ¿Tienes esos trozos de cangrejo de gran calidad de los que te hablaba?", completion = "Vaya, estos trozos de cangrejo de gran calidad son justo lo que necesito. ¡Gracias, &lt;nombre&gt;! Encontré esto pescando hace un tiempo y no lo he usado. Quizás tú puedas darle uso. Desde luego, no podrás usarlo para preparar platos de cangrejo.\n\nYa te he hablado de los cangrejos, ¿verdad?", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de cribaBotas crustáceo\n\nTambién recibirás:\n\nGuantes de cribaBotas crustáceo"},
[1139] = {title = "Las tablillas perdidas de Voluntad", objective = "Encuentra la tablilla de Voluntad y llévasela al consejero Belgrum en Forjaz.", description = "Hemos conseguido frenar al clan Forjatiniebla al aislarlos de la Cumbre de Roca Negra pero aún no hemos completado la misión de Piemartillo. Su muerte no debe ser en vano. Soñaba con encontrar la tablilla de Voluntad: un fragmento de piedra grabado con magia de los mismísimos titanes para ayudar a imbuir de vida a sus creaciones dándoles voluntad propia, o al menos eso cuenta la leyenda.\n\nDebemos recuperar la tablilla antes de que lo haga el clan Forjatiniebla.\n\nRegresa a Uldaman, encuentra la tablilla y vuelve aquí inmediatamente.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nMedalla de Coraje"},
[1140] = {title = "La torre de Althalaxx", objective = "Libera las almas de los altonatos atrapadas en la Senda de la Noche y en Satyrnaar y vuelve junto a Delgren el Purificador en la Atalaya de Maestra.", description = "La letra de los orcos es un poco mejor que los garabatos de un niño. Muchos de los de mi orden se burlaron cuando decidí aprender su lengua pero me ha resultado de provecho tener un conocimiento más profundo de mis enemigos.\n\nPor los garabatos de Ilkrud, parece que Athrikus aún posee dos gemas de almas que se guardan en la Senda de la Noche y en Satyrnaar, áreas bajo el dominio de los sátiros al noreste de Vallefresno.\n\nVe con cuidado, <nombre>, los templos de los sátiros bañados en sangre pueden ser perturbadores hasta para un <clase> curtido como tú.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1141] = {title = "La familia y la caña de pescar", objective = "Pesca 6 meros de Costa Oscura para Gubber Blump de Auberdine.", description = "Me encanta pescar. Los Blump somos una famosa familia de pescadores. Yo soy Gubber Blump. Yo también pesco.\n\nEn esta zona hay un pez que me gusta pescar: el mero de Costa Oscura. Yo solía salir en un barco a pescar meros, pero desde que aparecieron los murlocs, ya no sale ningún barco. Además, seguro que los murlocs se han comido todos los meros.\n\nOye, ¿quieres ayudarme a pescar un mero de Costa Oscura? Si lo haces, ¡te daré una auténtica caña de pescar de la familia Blump! Es una caña estupenda para pescar.", progress = "Con los meros de Costa Oscura se pueden preparar muchos platos muy buenos. Están muy ricos. Pero a mí me gusta más pescarlos.\n\n¿Tú has pescado alguno ya?", completion = "¡Estos meros son estupendos, &lt;nombre&gt;! ¡Gracias por ayudarme!\n\nEres como mínimo, tan buen pescador como mi prima, Graun Blump. Ella también tiene barba y huele un poco raro, pero eso no le impide cazar como una diosa. Está claro que tienes lo que hace falta para utilizar una caña de pescar de la familia Blump.", rewards = "Recibirás:\n\nCaña de pescar de la familia Blump"},
[1142] = {title = "Decaída mortal", objective = "Encuentra el colgante de Treshala Arroyobarbecho y llévaselo a Darnassus.", description = "No voy a durar mucho... Estoy débil. \n\nEl valiente enano Cimahuraña ha ido a advertir a Falfindel...\n\nAntes de morir, quisiera...\n\nSe han llevado el colgante de vínculo de mi esposa, Treshala...\n\nEncuéntralo, por favor... Llévaselo a Treshala a Darnassus...\n\nY dile que he... muerto.", progress = "¿Qué te trae por aquí, <raza>?", completion = "¡No!\n\n¡Oh, mundo cruel!\n\nAhora debo llevar el peso de mi pena y no dejar que el trabajo de Heralath muera con él.\n\nAgradezco tu valentía, &lt;nombre&gt;, pero déjame ahora, por favor.\n\nQuiero llorar a solas.", rewards = "Podrás elegir una de estas recompensas:\n\nChal de lutoBotas de lancero\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nChal de lutoBotas de lancero"},
[1143] = {title = "La torre de Althalaxx", objective = "Mata a Athrikus Narassin y lleva su cabeza a Balthule Golpesombra que está cerca de La torre de Althalaxx.", description = "Entiendo. Bien, si lo que dices es cierto, es el momento de actuar. Creo que Athrikus reside en la parte superior de la torre. Pero ten cuidado, está protegido por muchos brujos poderosos, mucho más mortíferos que los que has afrontado hasta ahora.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1144] = {title = "Willix el Importador", objective = "Ayuda a Willix el Importador a salir de Horado Rajacieno.", description = "¡Por fin llega ayuda!\n\nNo sabes cuánto hace que me tienen aquí. Ya ni recuerdo la última vez que estuve fuera.\n\nVerás, es que soy empresario, siempre ando olfateando negocios. Oí decir que Mebok Mizzyrix pagaba bien el tubérculo hojarroja para su porqueriza, y me pareció un negocio redondo y fácil. Como puedes ver, ¡calculé mal!\n\nPero si me ayudas a salir de aquí, te recompensaré.", progress = nil, completion = "¡Gracias otra vez! Y ahora, la recompensa prometida.", rewards = "Podrás elegir una de estas recompensas:\n\nAnillo de monoAro de serpienteSortija de tigre\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAnillo de monoSortija de tigreAro de serpiente"},
[1145] = {title = "El enjambre crece", objective = "Entrégale la nota sellada de Korran a Belgrom Destrozarroca en Orgrimmar.", description = "Muchos viajeros que han pasado por El Cruce me han ayudado a recoger huevos de la Tierra de Gigantes y muchos de ellos podrán contarte historias de lo peligrosas que son esas bestias. Yo ya he oído las suficientes como para poder afirmar que esos insectos representan una amenaza mayor de lo que mis maestros pensaban.\n\nNecesito enviar a un mensajero a Orgrimmar, a ver a Belgrom Destrozarroca. No se trata de un asunto cualquiera por eso necesito que vaya alguien que se tome esta tarea en serio.\n\nBelgrom suele andar por las inmediaciones de la Casa de la Hermandad de Guerreros.", progress = "Te envidio, <clase>. Tu cara es la de alguien que ha visto mundo más allá de Orgrimmar.\n\nYo también viajaría en nombre del Jefe de Guerra si Thrall no precisara de mis habilidades en la ciudad. Pero es un buen líder y yo confío en su criterio, así que me quedaré aquí mientras me necesite.", completion = "Ah, una nota de parte de Korran. Es un fiel servidor, desde hace ya algún tiempo. Me pregunto qué tal irán las cosas por El Cruce…", rewards = "Recibirás:\n\n"},
[1146] = {title = "El enjambre crece", objective = "Entrégale la nota sellada de Belgrom a Moktar Krin en Las Mil Agujas.", description = "Por lo que veo, Kornan ha llegado a la misma conclusión que yo acerca de esos insectos... parece que representan una amenaza mayor de lo que yo pensaba.\n\nHace unos días, un cazador regresó agonizante de Las Mil Agujas. Formaba parte de un destacamento de asalto enviado a El Desierto de Sal para expulsar de allí a un equipo de excavación formado por enanos. Durante el ataque, empezaron a salir insectos de una cueva que formaba parte de la excavación y casi acaban con todo el destacamento.\n\nEstaban liderados por Moktar Krin y se han refugiado en la frontera de El Desierto de Sal. Llévale esta nota.", progress = "La guerra nos ha traído a mis hombres y a mí a El Desierto de Sal, <clase>. ¿Qué te trae a ti aquí?", completion = "No hagas caso de los estruendos que retumban en las montañas. Los goblins y los gnomos están en El Desierto de Sal haciendo carreras con unas atrocidades creadas en nombre de lo que ellos denominan ciencia. Pero hay amenazas más importantes aquí en Las Mil Agujas, y una de ellas es la que acabó con más de la mitad de mi destacamento.\n\nAun así, nos quedaremos aquí hasta recibir noticias de Belgrom, que está en Orgrimmar. Hasta entonces, mantendremos nuestra posición sin bajar la guardia.", rewards = "Recibirás:\n\n"},
[1147] = {title = "El enjambre crece", objective = "Mata a 5 buscadores silítidos, 5 zánganos de la Colmena silítida y 5 invasores silítidos, y después ve a ver de nuevo a Moktar Krin a Las Mil Agujas.", description = "¡Vaya! Parece que Belgrom piensa que esos monstruos representan una amenaza y quiere que sean devueltos al lugar al que pertenecen.\n\nSupongo que haber matado a los enanos enviados por la Liga de Expedicionarios no ha sido suficiente y ahora tengo que enviar a un destacamento de asalto exhausto a otra batalla. No lo conseguiremos... no en estas condiciones. La excavación se encuentra al sur de aquí, y ya no debería quedar ningún enano por allí. ¿Te sientes con ánimos de blandir una espada o un bastón y echarnos una mano?", progress = nil, completion = "¡Ja! ¡El icor te favorece mucho, &lt;nombre&gt;! Si bien es cierto que apesta... Te recomendaría que no te acercases demasiado a los kodos, no vaya a ser que provoques una estampida o, lo que es peor, que se pongan en celo.\n\nCreo que todos estamos de acuerdo en que tu olor apestoso demuestra que efectivamente has combatido contra esos insectos. Gracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[1148] = {title = "Pedazos del enjambre", objective = "Llévale 1 corazón de silítido, 5 espolones de silítido y 3 caparazones intactos de silítido a Korran, que está en El Cruce.", description = "Inspeccionas el caparazón detenidamente. A medida que lo giras, te das cuenta de que la coraza de quitina está entrelazada de tal forma que le confiere una resistencia increíble. No se parece a nada que hayas visto antes. Observas el resto del cuerpo y te das cuenta de que estas criaturas son muy poderosas y están muy evolucionadas.\n\nAl mirar de nuevo el caparazón, crees que a Korran le gustaría recibir más muestras de estas criaturas. Le resultarían útiles para descubrir qué son y de dónde proceden.", progress = "Ah, hacía tiempo que no venías a verme, <nombre>. ¿Qué puedo hacer por ti? Tienes cara de haber combatido hace poco. Eso es bueno... para la Horda y para ti. Cuantas más batallas libres, más útil y más fuerte serás.", completion = "¿Has visto a Moktar recientemente? Él siempre me anda buscando. Siento que tenga que perder el tiempo velando por su pequeño escuadrón de combate en lugar de centrarse en la batalla pero estoy seguro de que se lo pasa bien matando a los miembros de la Liga de Expedicionarios.\n\nHas hecho bien en traerme estos especímenes, &lt;nombre&gt;. Con esto y los huevos que encontremos en la Tierra de Gigantes, podré empezar a hacerme una idea de qué son estos insectos y qué andan buscando.", rewards = "Recibirás:\n\n"},
[1149] = {title = "Prueba de fe", objective = "Si tienes fe, salta desde las tablas que dominan Las Mil Agujas.", description = "¿Me has estado buscando o quizás ha sido el destino el que te ha traído a mí? No tiene importancia... Ahora estás aquí y tu viaje encontrará un nuevo punto de partida.\n\nSon muchas las facetas que conforman un ser, tanto su espíritu como su cuerpo y su mente. Algunas de ellas son mensurables por las tradiciones de una cultura. Otras, sin embargo, solo podemos esperar que encuentren un lugar dentro de nosotros. Si no te opones, pondré a prueba algunas de tus facetas.\n\nSi superas las pruebas, te harás más fuerte. De lo contrario, descubrirás la inmensidad del Vacío Abisal.\n\nLa primera es una prueba de fe.", progress = "La fe es mucho más importante de lo que algunos piensan.\n\nMuchas veces lo importante no es en qué creas, sino que creas en algo.", completion = "Hasta que no nos enfrentamos a la adversidad, no sabemos lo fuerte que es nuestro espíritu. Diste el salto de fe, incluso sin saber si sobrevivirías. Tu mente está abierta y tu espíritu preparado para lo que venga.\n\nSi lo deseas, puedes pasar a la siguiente prueba.", rewards = "Recibirás:\n\n"},
[1150] = {title = "Prueba de resistencia", objective = "Llévale la garra de Grenka a Dorn Acechaprados, que está en Las Mil Agujas.", description = "Me complace comprobar que tu valor permanece inquebrantable después de lo que acabas de hacer. Dado que tu fe está intacta, ha llegado el momento de poner a prueba tu cuerpo.\n\nEn primer lugar, harás una prueba de resistencia. Ve con algunos amigos a Guarida Malapluma al oeste del Poblado Viento Libre. Consigue llegar hasta las profundidades de su guarida y destruye sus alimentos. Con eso provocarás su ira. Si eres capaz de aguantar con vida el tiempo suficiente, la propia Grenka Aullasangre irá a por ti. Acaba con ella y tráeme su garra para superar tu prueba.", progress = "Debes estar preparado espiritual, física y mentalmente para los desafíos que tienes por delante.\n\nMuchas veces, queremos avanzar sin estar preparados. Mi obligación con los jóvenes, sean de la tribu que sean, es prepararlos para enfrentarse a los peligros con los que se puedan topar cuando abandonen el calor del hogar.", completion = "Para ser un &lt;raza&gt;, Grenka era muy poderoso. Debes sentirte orgulloso de lo que has logrado hoy.\n\nDurante tu prueba de fe, demostraste poseer gran fuerza de espíritu y, al acabar con las arpías Malapluma, has demostrado tener gran resistencia en combate. Ha llegado la hora de que afrontes una nueva prueba.", rewards = "Recibirás:\n\n"},
[1151] = {title = "Prueba de fuerza", objective = "Llévale fragmentos de Rok'Alim a Dorn Acechaprados, que está en Las Mil Agujas.", description = "En los tiempos de los dioses antiguos, los elementales ocupaban la mayor parte de este mundo. Ellos veneraban a los dioses antiguos y estos, haciendo uso de su poder, relegaron a los elementales a este mundo. Cuando los dioses antiguos desaparecieron, los elementales se desvanecieron de nuestro mundo y ahora solamente regresan cuando los poderosos magos requieren su presencia.\n\nAun así, hay algunas excepciones. Algunos elementales eran más poderosos y consiguieron permanecer aquí por su cuenta. Uno de ellos es Rok'Alim.\n\nPara poner a prueba tu fuerza, encuentra a Rok'Alim en Las Mil Agujas y acaba con él.", progress = "Las tribus de centauros de Las Mil Agujas denominan a Rok'Alim el Aporreador debido a la fuerza con la que sus puños golpean a las criaturas lo suficientemente valientes o demasiado insensatas como para cruzarse en su camino.", completion = "La prueba de fuerza ha concluido. Has demostrado ser fuerte y valiente, y ahora estás preparado para la prueba final.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1152] = {title = "Prueba de conocimiento", objective = "Encuentra a Braug Espiritoscuro cerca de la entrada de El Paso del Espolón en Sierra Espolón.", description = "Tu tiempo conmigo está llegando a su fin. Tengo una amplia cultura mas la prueba de conocimiento no seré yo quien te la haga. Hay lecciones que aprender y lugares que visitar que corresponden a otros.\n\nSi estás <preparado/preparada>, busca a Braug Espiritoscuro, un chamán muy sabio. Él continuará poniéndote a prueba, así que sigue sus indicaciones.\n\nCuando lo desees, busca el túnel que comunica Sierra Espolón con Vallefresno pues Braug anda por esa zona. Y ten cuidado, <nombre>, a los elfos de la noche les encantaría impedir que acabaras tus pruebas.", progress = nil, completion = "El desasosiego crece en este mundo a medida que los goblins expolian la tierra y los elfos de la noche ponen el punto de mira en las razas más jóvenes. Los Renegados y los orcos no son los únicos culpables de las penurias sufridas por Kalimdor y Lordaeron. Muchas de las razas inteligentes han contribuido al malestar que reina en el mundo. Dorn lo sabe y te ha enviado a verme porque has demostrado ser capaz de entender la situación.\n\nTe encuentras ante Braug.", rewards = "Recibirás:\n\n"},
[1153] = {title = "Una nueva muestra de mineral", objective = "Arrebátale una muestra de mineral no refinado a un cavador o a un perito Gravamorro y llévasela a Tatternack Forjacero al Campamento Taurajo de Los Baldíos.", description = "<clase>, me has ayudado con eficacia en el pasado. El Jefe de Guerra está satisfecho con mis logros y quiere que continúe con renovada determinación. Según me dicta mi honor, informaré al Jefe de Guerra de tu valía para que te otorgue el reconocimiento que te mereces. Pero antes he de pedirte que me ayudes otra vez.\n\nRecientemente han llegado unos viajeros del sur, de un lugar llamado Las Mil Agujas. Estos afirman que allí mataron a un kóbold que tenía una muestra de un extraño mineral, pero no están dispuestos a vendérmelo. Quiero una muestra de ese mineral para analizarlo.", progress = "He vuelto a ver a los viajeros, los que no me quieren vender la muestra de ese mineral. Les escupí por no colaborar con el Jefe de Guerra.\n\nDeben morir en la batalla como cobardes y no como guerreros como corresponde a aquellos que están de parte de la Horda.", completion = "Buena muestra, &lt;nombre&gt;. Muy buena muestra...\n\nEsos viajeros ya no podrán restregarme por la cara el mineral que encontraron.\n\nHe de ponerme manos a la obra de inmediato. Si resulta ser lo suficientemente resistente, enviaré un arma fabricada a partir de este material al Jefe de Guerra como prueba de nuestro éxito.\n\nNunca olvidaré lo que has hecho por mí, &lt;clase&gt;.", rewards = "Recibirás:\n\nEspada de guerra orca"},
[1154] = {title = "Prueba de conocimiento", objective = "Encuentra el Legado de los Aspectos y devuélveselo a Braug Espiritoscuro que se encuentra cerca de la entrada de El Paso del Espolón en Sierra Espolón.", description = "Has superado con éxito las pruebas de Dorn y sé que quieres hacer lo mismo con la siguiente. Muy bien, pero ten en cuenta que hasta el momento las pruebas han sido demasiado sencillas. Es fácil poner a prueba el cuerpo... pero la mente es un tema aparte.\n\nTe lo explicaré bien para que lo entiendas: encuentra el libro que yo te diga, estúdiatelo bien y responde la pregunta que te haga acerca de su contenido.\n\nQué libro, te estarás preguntando... El Legado de los Aspectos. Lo encontrarás en la parte baja de Dor'danil, en el sur de Vallefresno, bajo la protección de los druidas muertos y sus asesinos.", progress = "Tráeme el libro que has estudiado. No te haré la pregunta hasta que me lo hayas entregado.", completion = "Ese el libro que te pedí. Podrás responder mi pregunta correctamente... a no ser que te hayas olvidado de estudiar su contenido.\n\nCuando estés preparado, ven a hablar conmigo y te plantearé la pregunta.", rewards = "Recibirás:\n\n"},
[1159] = {title = "Prueba de conocimiento", objective = "Encuentra a Parqual Fintallas en Entrañas.", description = "Bien hecho, <nombre>. Has superado la primera parte de la prueba de conocimiento pero aún hay otra pregunta que debes responder. Sin embargo, no seré yo quien te la haga.\n\nBusca a Parqual Fintallas en la ciudad de Lordaeron. Él era un gran historiador al servicio de los humanos antes de que la Plaga lo sorprendiera. Ahora sigue persiguiendo el conocimiento, pero de un modo distinto.\n\nAntes de formularte ninguna pregunta, te asignará otro encargo. Lo has hecho tan bien hasta el momento que no tengo ninguna duda de que superarás este obstáculo sin problemas.", progress = nil, completion = "¿Te envía Braug? No es posible...\n\n&lt;Parqual te mira de arriba abajo.&gt;¿Estuviste en Dor'danil, diste el salto de fe y luchaste y derrotaste a un poderoso elemental? ¡Bah! Seguro que eres un impostor. Alguien que escuchó una conversación en alguna taberna y cree estar preparado para superar mi prueba. Ya lo veremos... &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[1160] = {title = "Prueba de conocimiento", objective = "Encuentra la Guía básica para no-muertos y devuélvesela a Parqual Fintallas, que está en Entrañas.", description = "Te diré lo que tienes que buscar, e incluso te formularé mi pregunta, pero no porque desees ponerte a prueba. El conocimiento va más allá de la fuerza física. Aunque otros puedan superarte en habilidad, el conocimiento del mundo te dará ventaja.\n\nDirígete al monasterio que está al norte de los Claros de Tirisfal. En la biblioteca que encontrarás allí, verás un libro titulado Guía básica para no-muertos. Lo utilizaban los ahora corruptos paladines de allí para instruir a los no-muertos para luchar contra los de mi especie.\n\nTráemelo.", progress = "Espero que hayas hecho sufrir un poco a la Cruzada Escarlata antes de robarle el libro.\n\nMi opinión sobre sus integrantes está más que justificada. En su día fueron seguidores de la Luz pero ahora persiguen destruir a toda criatura que no esté de acuerdo con su religión. Creen que los no-muertos, Renegados o no, deben ser aniquilados y que todo el que viaje por sus tierras o está loco o sujeto a la poderosa magia de los no-muertos. ¡Insensatos! Son todos unos insensatos y unos ignorantes.", completion = "Este el libro que te pedí que buscaras.\n\n&lt;Parqual acaricia con cuidado la tapa del libro mientras lo observa fijamente.&gt;\n\nAsí que ahora quieres que te haga una pregunta para demostrar que tu mente es tan poderosa como tu cuerpo y tu espíritu. Si has estudiado el libro lo suficiente, procederé a plantearte la pregunta y habrás acabado las pruebas.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1164] = {title = "Robar a un ladrón…", objective = "Llévale las cabezas de Kenata, Fardel y Marcel Dabyrie a Genavie Callow, que está en Entrañas.", description = "¡Desgraciada! ¡Canalla! Kenata sigue viva, y su familia está sana y sigue prosperando mientras yo sufro. ¡Renegados tenían que ser!\n\nMi familia ha muerto debido a la peste. Arruinaron nuestra finca durante la guerra, la saquearon y le plantaron fuego. Y después de todo esto, ella y sus despreciables hijos tuvieron el descaro de robar las únicas posesiones de valor que me quedaban.\n\nYa no me preocupa lo que me fue robado. ¡Lo que quiero ahora son sus cabezas!\n\n¡Mátalos por mí! Ve a la Granja de los Dabyrie en Arathi, al nordeste del Refugio de la Zaga.", progress = "¡Mátalos y tráeme sus cabezas!", completion = "Has cumplido mi venganza y me inclino ante ti en agradecimiento.\n\nAhora que están todos muertos y que se ha acabado su progenie en Azeroth, puedo concentrar mis energías en algo más lucrativo. Espero que hayas saqueado su granja tras haberles dado muerte.", rewards = "Podrás elegir una de estas recompensas:\n\nEspaldares macabrosPuños de galante\n\nTambién recibirás:\n\nPuños de galanteEspaldares macabros"},
[1166] = {title = "Las pertenencias del Señor Supremo Mok'Morokk", objective = "El Señor Supremo Mok'Morkk del Poblado Murohelecho quiere que recuperes su grog, su barril de tabaco y su caja fuerte del Pantano Revolcafango.", description = "¡Inclínate ante Señor Supremo Mok'Morokk!\n\n¡Rexxar hacerme líder poderoso del clan Quebrantarrocas!\n\nYo buen jefe. Yo cuidar del clan.\n\nGrandes dragones vienen y hacen fuego en otra casa. Pero yo soy un ogro listo. Encontré nueva casa para el clan.\n\nClaro que dejamos casa vieja tan rápido que yo olvidé cosas. Pequeño ser, ve a por mis cosas. Ir al sur y encontrar mi rico grog, mi barril de tabaco y mi caja fuerte. Si haces eso, me caerás bien. Busca en Cubil de la Llama y en Ruinas Quebrantarrocas... Estar ahí, ¡yo seguro!", progress = "Tengo sed. Quiero grog. Y tabaco. ¿Dónde está mi caja fuerte? Tú vas a buscarlos para Señor Supremo Mok'Morokk.\n\nVe al sur, a casa vieja. Yo estoy muy ocupado mandando a ogros.", completion = "Tú buen &lt;raza&gt;. Yo echo de menos mis cosas. Mucho. Ahora me gustas.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nBotas de ogro enigma"},
[1167] = {title = "La torre de Althalaxx", objective = "Habla con Balthule Golpesombra cerca de La torre de Althalaxx.", description = "Antes de enfrentarte a Athrikus, probablemente sería prudente que consultaras a Balthule. No me cabe la menor duda de que habrá estado muy atento en su observatorio de la torre y podría tener información sobre los últimos movimientos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1168] = {title = "El ejército de los dragones negros", objective = "Tharg en el Poblado Murhelecho quiere que mates a 5 exploradores Pirocabello, 10 colacenizas Pirocabello y 5 guardias Pirocabello.", description = "Mok'Morokk fue nombrado Señor Supremo por Rexxar. Le preocupa más el poder que los ogros.\n\nLos dragones negros matan a muchos ogros. Matan a la mujer de Tharg. Mok'Morokk nos dice que huir. Nos hace venir aquí.\n\nLos ogros deben luchar. Los ogros no huir. No buenos corredores. Mok'Morokk solo preocupa de Mok'Morokk.\n\nYo llamo casa a las Ruinas Quebrantarrocas. A mí gustar el sur. Este lugar no casa.\n\nAyuda ogros combatir ejército de los dragones negros. Quiero venganza. Quiero ir a casa.", progress = nil, completion = "Tú matar a muchos del ejército de dragones negros. Tú valiente &lt;raza&gt;.\n\nPoner a Tharg contento.", rewards = "Podrás elegir una de estas recompensas:\n\nCordones de ThargDisco de Tharg\n\nTambién recibirás:\n\nCordones de ThargDisco de Tharg"},
[1169] = {title = "Identifica a los cachorros", objective = "Draz'Zilb del Poblado Murohelecho quiere que le lleves 15 lenguas abrasadoras y 15 corazones abrasadores.", description = "Mientras ese loco cobarde de Mok'Morokk se revuelca en poder y Tharg lucha contra los demonios de su pena y su venganza y compite por el liderazgo del clan, solo yo me preocupo de identificar la fuente de la agresión a nuestro antiguo hogar.\n\n¿Por qué me miras así, <nombre>? ¿Esperabas que hablara como un burdo pueblerino solo porque soy un ogro?\n\nEn fin, volvamos a nuestros asuntos. Tráeme los corazones y las lenguas de los cachorros y las crías. Yo buscaré la raíz de este mal...", progress = "Con los corazones y las lenguas de las crías chamuscadas del ejército de los dragones negros, podré averiguar qué bestia introdujo a su prole en nuestra casa. Si reunimos información sobre nuestros agresores, quizás podamos destruirlos de una vez para siempre y rescatar de la ruina al poblado Quebrantarrocas.\n\nPor supuesto, conseguir que Mok'Morokk arrastre su letárgica mole de ese agujero de estiércol, va a ser toda una odisea...", completion = "Debió de ser peligroso eso de reunir tantos corazones y lenguas. Me sumergiré en las profundidades de lo Arcano para rastrear el origen de esa negra amenaza que se ha cernido sobre nuestro antiguo hogar.", rewards = "Podrás elegir una de estas recompensas:\n\nManteo abrasadoGuantes de ladronzuelo\n\nTambién recibirás:\n\nManteo abrasadoGuantes de ladronzuelo"},
[1170] = {title = "Los hijos de Onyxia", objective = "Habla con el Señor Supremo Mok'Morkk en el Poblado Murohelecho.", description = "El Poblado Quebrantarrocas fue invadido por los hijos de Onyxia. Pero, ¿por qué arrasaría nuestras tierras la hija de Alamuerte, el Señor de los dragones negros?\n\nEs preocupante... Porque es evidente que Onyxia no vino aquí solo para asediar a un pequeño grupo de ogros.\n\n¡Informa a Mok'Morokk de inmediato! Debemos actuar.", progress = nil, completion = "¿Por qué me hablas de dragones? No me gustan. A mí me gusta esto. Aquí no hay dragones.", rewards = "Recibirás:\n\n"},
[1171] = {title = "Los hijos de Onyxia", objective = "Ve al Poblado Murohelecho y habla con Draz'Zilb.", description = "No me molestes. Ve a decir a Draz'Zilb que nos quedamos aquí. Aquí no hay dragones negros.", progress = nil, completion = "¡Ese vago cobarde!\n\nMok'Morokk demostró ser fuerte cuando Rexxar era nuestro líder. Pero en cuanto Rexxar partió, Mok'Morokk cayó presa de una insaciable ansia de poder. En lugar de liderar, exige. Ya no sirve a su gente, ahora espera que nosotros le sirvamos a él.\n\nHay que hacer algo al respecto.", rewards = "Recibirás:\n\n"},
[1172] = {title = "Los hijos de Onyxia", objective = "Draz'Zilb, del Poblado Murohelecho, quiere que destruyas 5 huevos de Onyxia.", description = "Los hijos de Onyxia han sido dispersados por todo El Pantano del Dragón. Es vital que impidamos que incube más crías.\n\nCorre hacia la Ciénaga de Fuego, al sur del Marjal Revolcafango. Seguro que ha establecido allí su guarida. Busca los huevos de ese ser maligno y destrúyelos.\n\nJamás reclamaremos el poblado Quebrantarrocas si los alrededores son un semillero de crías de dragón.\n\nY respecto a Mok'Morokk... Tengo otros planes para ese demonio.", progress = nil, completion = "¡Espléndido! Pasará algún tiempo hasta que Onyxia pueda poner más huevos.\n\nTe doy las gracias de parte de mi gente, &lt;nombre&gt;.\n\nSoy consciente de que ya te he pedido mucho. Pero quizás quieras hacer un último servicio al clan Quebrantarrocas.\n\nMok'Morokk no puede ser nuestro líder. Tharg sería un guía excelente, pero está entorpecido por sus heridas y su edad. Expulsa a Mok'Morokk de este lugar por la fuerza. Solo entonces podremos proclamar un nuevo líder que nos lleve de regreso al sur, a nuestro verdadero hogar.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas encarmineBotas de Zua'tec\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBotas encarmineBotas de Zua'tec"},
[1173] = {title = "Desafiar al Señor Supremo Mok'Morokk", objective = "Derrota a Mok'Morokk y ve al Poblado Murohelecho a informar a Draz'Zilb.", description = "¿Osas desafiar al Señor Supremo Mok'Morokk?\n\n¡Ja!\n\n¡Yo aplastar insignificante <raza>!\n\nDime cuándo estar <listo/lista> para la paliza.", progress = nil, completion = "Esa mole cobarde de Mok'Morokk huyó como un niño asustado. Nos has hecho un gran servicio, &lt;nombre&gt;.\n\nAhora mi gente puede proclamar un nuevo líder, uno que nos ayudará a reclamar nuestro antiguo hogar.", rewards = "Podrás elegir una de estas recompensas:\n\nPica endemoniadaBastón Uñafría\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPica endemoniadaBastón Uñafría"},
[1174] = {title = "¡Los gnomos ganan!", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCaja con premio gnomo"},
[1175] = {title = "Un bache en la carretera", objective = "Mata a 10 basiliscos Gapo Salino, 10 pielcristales Gapo Salino y 6 observadores Gapo Salino en El Desierto de Sal.", description = "Tras el choque del Trueno Rojo de los goblins con un basilisco, Kronkpiloto me contrató para que me ocupe de la pista. Yo no sé lo que comerán esos animales en el desierto para crecer tanto pero tenemos que encargarnos de ellos si no queremos que deambulen por la pista durante la celebración de una carrera.\n\nTe diré lo que vamos a hacer. Si quieres ir ahí fuera y hacer algo de provecho, sal y acaba con los basiliscos que encuentres por la zona.", progress = nil, completion = "¡Espero que no hayas matado a ningún basilisco en la pista! Sus cuerpos tardan mucho en descomponerse al sol y prefiero no tener que coger una pala para despejar la pista.", rewards = "Recibirás:\n\n"},
[1176] = {title = "Menos peso", objective = "Consigue 10 huesos huecos de buitre para Pozzik en El Desierto de Sal.", description = "¡Cuando se trata de correr, todo cuenta! Ya sea un pequeño ajuste en la salida de potencia, que se afloja la bobina de tensión o que se aligera el peso del cuadro. ¡Hay que tener todo en cuenta!\n\nUtilizamos huesos de buitre para la mayor parte de los pequeños cambios que hacemos en la estructura de los coches, por su perfecto equilibrio entre resistencia y peso. Y lo mejor de todo es que son muy fáciles de encontrar pues en El Desierto de Sal hay muchos buitres.\n\nTráeme algunos y te pagaré bien por ellos.", progress = "¿Tienes los huesos? Estaba pensando en aligerar la dirección pero no quiero empezar hasta tener todas las piezas que necesito.", completion = "¡Genial! Ya puedo poner al equipo técnico a trabajar en esto. ¡Gracias, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[1177] = {title = "¡Qué hambre!", objective = "Fangolpe Piesucio del norte de Revolcafango quiere 12 cabezas de Ancalodo.", description = "Mud ogro grande.\n\nYo tiene hambre.\n\nTú buscar peces. Trae comida a Mud.\n\nTú das de comer a Mud o Mud te come a ti.", progress = "Da comida o yo me como a ti.", completion = "¡A mí gusta cabeza de pez!\n\nYo no me como a ti ya.", rewards = "Podrás elegir una de estas recompensas:\n\nTrituradores de barroVapuleadores Piesucios\n\nTambién recibirás:\n\nTrituradores de barroVapuleadores Piesucios"},
[1178] = {title = "Patrocinio goblin", objective = "Habla con Gazlowe en Trinquete.", description = "Nuestro equipo de carreras es el orgullo de todos los goblins. Te sorprendería saber quiénes contribuyen de un modo u otro en esto.\n\n¿Conoces a Gazlowe? Él nos busca piezas por todo el mundo. ¡Incluso en una ocasión vino aquí para arreglar uno de estos trastos!\n\nHace unas cuantas semanas, dijo que nos enviaría un prototipo de un nuevo regulador de combustible pero todavía no nos ha llegado. Y él suele ser muy rápido... ¿Te importaría ir a Trinquete y preguntarle por el regulador? ¡No me gusta meter prisa pero los gnomos nos están ganando!", progress = nil, completion = "¿Te envía Pozzik, eh? Sabes, Pozzik era uno de los manitas más respetados de Villabajo hasta que decidió dejarlo todo para dedicarse a las carreras.\n\nLo primero que hizo tras unirse al equipo fue desarrollar el coche de doble motor. Puede ser que el negocio experimente un bajón ahora que él ya no trabaja en el Gremio de Manitas pero... ¿a quién le importa? ¡No hay nada como las carreras!", rewards = "Recibirás:\n\n"},
[1179] = {title = "Los hermanos Pernolatón", objective = "Llévale los cascos de Máquinas de Asedio a Wizzle Pernolatón, que está en El Desierto de Sal.", description = "¿Has oído hablar de El Desierto de Sal? Es una franja de tierra salada y llana de Kalimdor, al sur del cañón de Las Mil Agujas. ¡Es un lugar ideal para echar una carrera ya que es totalmente llano!\n\nUn par de hermanos gnomos amigos míos están allí probando su nuevo coche cohete y, por lo que tengo entendido, ¡es una bala! Eh, es tan rápido que los pilotos se estrellan continuamente y sufren conmociones.\n\nPor eso me han pedido algunos cascos de Máquinas de Asedio pero tengo cosas que hacer aquí, ¡y no puedo ir!\n\n¿Podrías ir tú?", progress = "¡Mira por dónde pisas! ¡Ten mucho, mucho, mucho cuidado! Todos estos chismes que ves por aquí son muy delicados y si queremos ganar, ¡deben funcionar a la perfección!", completion = "¡Ah, Barbalarga nos ha enviado un cargamento de cascos! ¡Hurra!\n\nQuizás ahora podamos convencer a algún piloto para que conduzca nuestro coche...", rewards = "Recibirás:\n\n"},
[1180] = {title = "Apoyo al goblin", objective = "Habla con el jefe de embarcadero Lozgil en Bahía del Botín.", description = "Has vuelto por lo que habías venido a buscar. Sé que le prometí a Pozzik que le entregaría un nuevo regulador de combustible pero aún no lo he recibido. Revilgaz de Bahía del Botín dijo que lo había enviado pero se debe de haber perdido por algún lado.\n\nSi es cierto, no hay mucho que podamos hacer. Al menos podemos averiguar si salió realmente de Bahía del Botín.\n\nVe para allá y compruébalo con el maestro de los muelles Lozgil, lo sabrá por los registros de cargamentos.", progress = nil, completion = "¿Un envío a Trinquete? Permite que compruebe los registros…\n\nNo, el envío más reciente que me consta en las salidas hacia Trinquete son unas botas de Drizzlik. No ha habido mucho más, aparte de suministros y herramientas de construcción. Nada de piezas para carreras.", rewards = "Recibirás:\n\n"},
[1181] = {title = "Apoyo al goblin", objective = "Habla con el Barón Revilgaz en la Bahía del Botín.", description = "Me temo que no puedo ayudarte más, <nombre>. Si quieres seguir investigando, te sugiero que vayas a ver al Barón. Si alguien sabe lo que está haciendo Revilgaz, es Revilgaz.\n\nLe encontrarás en la taberna al otro lado del puerto, en el piso de arriba.", progress = nil, completion = "Oh, por supuesto que sabes lo que estás diciendo. Pero no, no te puedo ayudar. Algunos de mis hombres tenían que rastrear los planos del regulador de combustible, pero volvieron con las manos vacías.", rewards = "Recibirás:\n\n"},
[1182] = {title = "Apoyo al goblin", objective = "Lleva los planos del regulador de combustible al Barón Revilgaz en la Bahía del Botín.", description = "Verás, las trituradoras de Ventura y Cía. del Lago Nazferiti (en la parte norte de Tuercespina) están equipadas con un nuevo modelo de regulador de combustible. Creo que Pozzik podría sacar ventaja de eso si pudiera conseguir algunos detalles. Por desgracia, Cozzle, el supervisor que está ahí fuera, parece más inteligente de lo que yo había pensado.\n\nQuizás tú tengas más suerte para encontrar los planos. Sé que los guarda en su casa, pero seguro que los tiene bajo llave.", progress = "Por mucho que odie admitirlo, ese equipo gnomo que tienen ahí fuera es bastante bueno. Nuestros chicos necesitan toda la ayuda que puedan conseguir.", completion = "Deja que haga una copia de esos planos para tenerlos en mis notas y se los puedes llevar directamente a Pozzik. No quiero que crea que uno de sus mecenas le ha abandonado.", rewards = "Recibirás:\n\n"},
[1183] = {title = "Apoyo al goblin", objective = "Entrega los planos del regulador de combustible a Pozzik en El Desierto de Sal.", description = "Creo que ya estás <listo/lista> para irte. Da recuerdos a Pozzik de mi parte y dile que, si alguna vez necesita algo, no dude en pedírmelo. ¡Estaré esperando la noticia de que ha conseguido batir el récord!", progress = "¡<nombre>! ¿Has conseguido encontrar el regulador de combustible?", completion = "Esperaba que ya estuviera listo, pero esto presenta la oportunidad perfecta de mejorar el diseño original. Gracias, &lt;nombre&gt;. Creo que podré preparar unos esquemas mejorados , construir esta cosa e instalarla rápidamente.", rewards = "Recibirás:\n\n"},
[1184] = {title = "Pedazos del enjambre", objective = "Ve a ver de nuevo a Belgrom Destrozarroca a Orgrimmar.", description = "Ve a ver a Belgrom a Orgrimmar y dile que Moktar está cumpliendo con su cometido en Las Mil Agujas. Dile también que yo he progresado en mis investigaciones y que espero saber más cosas pronto. Seguro que se alegra de saberlo.", progress = nil, completion = "Agradezco tus esfuerzos, &lt;clase&gt;. El Jefe de Guerra se alegrará de ver la tenacidad con la que has cumplido tus obligaciones.\n\nPor supuesto, le hablaré bien de ti. Él habla conmigo a menudo y valora mucho mis opiniones.\n\nAhora vete, tengo mucho que hacer. Si hablas con Korran y tiene alguna novedad acerca de estas criaturas, entonces ven a verme de nuevo. De ser así, tendría que empezar a pensar que son una seria amenaza que hay que detener.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones de hojas secasCreciente sangrante\n\nTambién recibirás:\n\nPantalones de hojas secasCreciente sangrante"},
[1185] = {title = "Debajo de la quitina había…", objective = "¡Anda, si eres tú! ¡O sea, <nombre>!\n\nMe alegro de verte.\n\nEl libro que encontraste en Silithus es un diario escrito en un antiguo dialecto. Voy a estudiarlo a fondo. Pensé que te gustaría saber que es muy valioso.\n\n¡Tienes la gratitud del Círculo!", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1186] = {title = "El piloto nº18", objective = "Habla con Razzeric en El Desierto de Sal.", description = "Yo estaré muy ocupado intentando integrar el nuevo regulador de combustible con los diseños de nuestros coches pero si quieres seguir prestando tu ayuda al equipo, habla con Razzeric. Él es nuestro piloto, bueno... nuestro decimoctavo piloto para ser exactos, y disfruta realizando sus pequeñas modificaciones en los coches.\n\nEstá un poco chalado así que ten cuidado con lo que haces.", progress = nil, completion = "Tú eres quien ha estado ayudando a Pozzik, ¿verdad? Yo antes era como tú; andaba vagando por los boxes y ayudando a los mecánicos en lo que podía.\n\n¡Y mírame ahora! ¡Soy el piloto del equipo de carreras de los goblins!", rewards = "Recibirás:\n\n"},
[1187] = {title = "Los ajustes de Razzeric'", objective = "Consigue el propulsor de seforio para Razzeric, que está en El Desierto de Sal.", description = "Pozzik es un gran mecánico, lo sé, pero tiene miedo de aumentar la potencia y lanzarse a la carrera. Yo no, sin embargo.\n\nCuando Pozzik acaba con un coche, yo me tomo un tiempo para realizar mis propios ajustes... ya sabes, aflojo los mandos, quito algunas piezas del cuadro y le pongo más combustible.\n\nLe encargué un propulsor de seforio a Shreev para que me lo enviara desde la Cacharrería de Gadgetzan pero parece que el zepelín en el que venía se estrelló en Majal Revolcafango. ¡Lo necesito, <nombre>!", progress = "¿Cómo voy a participar en la carrera sin contar con un pequeño empujoncito? ¡Haría el ridículo!", completion = "¡Mira esta belleza! ¡Siete kilos de potencia de seforio macizo! Cuando se lo ponga a mi coche, iré tan rápido por la pista... ¡que adelantaré a los gnomos dos veces!", rewards = "Recibirás:\n\n"},
[1188] = {title = "La seguridad primero", objective = "Llévale el propulsor de seforio a Shreev, que está en Gadgetzan.", description = "Pero espera, ¿qué es esto? Shreev dejó el seguro puesto y eso limitará la potencia del propulsor. ¡Le dije que se lo quitara! Esto no me gusta.\n\nTienes que llevar esto a la Cacharrería de Gadgetzan en el Desierto de Tanaris y decirle a Shreev que se lo quite.", progress = "¡Seguro que ya sabes que en la Cacharrería podrás encontrar todo lo que necesites! ¿En qué puedo ayudarte?", completion = "Razzeric es el único que podría echar un vistazo a esto y decir que de algún modo le he puesto un seguro. ¡Este propulsor es seforio puro! Lo modifiqué para que suelte la mayor parte de la carga explosiva pero Razzeric siempre quiere más. ¡Quiere que vaya a tope!", rewards = "Recibirás:\n\n"},
[1189] = {title = "La seguridad primero", objective = "Devuélvele el propulsor de seforio modificado a Razzeric, que está en El Desierto de Sal.", description = "Aquí tienes, he hecho lo que Razzeric me pidió. Dile que tenga cuidado al activarlo. Si comete el más mínimo error, podría saltar por los aires.\n\nY sería una auténtica pena... ¿Quién sabe cómo sería el decimonoveno piloto?", progress = "¿Puedes creer que Pozzik puso un cubo de arena en el coche? ¿Cómo se supone que voy a llegar al máximo de velocidad con un cubo de arena bajo el asiento?", completion = "¡Esto está mejor! Me gustaría ver la cara que pondrán los gnomos cuando me vean pasar a la velocidad del rayo con esta belleza.\n\n¡Pero estaré demasiado ocupado batiendo un nuevo récord!", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón personalizado de RazzericMandiletes de competición de Razzeric\n\nTambién recibirás:\n\nCinturón personalizado de RazzericMandiletes de competición de Razzeric"},
[1190] = {title = "Mantener la velocidad", objective = "Consigue que Zamek haga una artimaña de distracción y roba los diseños de Rizzle para Pozzik, que está en El Desierto de Sal.", description = "Los hermanos Pernolatón han descubierto un nuevo material de construcción, es la única explicación posible. Sus coches cada vez son más rápidos pero sus diseños y todos los datos que reunimos durante sus carreras son los de siempre.\n\nLo que sí sabemos es que Rizzle Pernolatón tiene unos diseños de ese nuevo descubrimiento.\n\nMientras Zamek los entretiene, tú roba los diseños.", progress = nil, completion = "Un pergamino de notas y diseños que explican el proceso de refinado de un material, garabateados con mala letra.", rewards = "Recibirás:\n\n"},
[1191] = {title = "La distracción de Zamek", objective = "¡Vamos!", description = nil, progress = nil, completion = nil, rewards = nil},
[1192] = {title = "Mineral de indurio", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1193] = {title = "Una trampa rota", objective = "Tienes ante ti una gran trampa rota. Parece que los ogros han intentado reparar la trampa, pero sin éxito. Al lado de la trampa hay una nota escrita a toda prisa. Lo curioso es que describe exactamente lo que se necesita para reparar la trampa: 1 trasto wow de torio y un poco de aceite de escarcha. Si se reparara la trampa, cualquier ogro que pasara por aquí podría caer en ella... uno menos.\n\nEvidentemente, el hecho de que sean pocos los ogros que saben leer, podría parecer un hecho fortuito.\n\nPodría parecerlo...", description = nil, progress = "Tienes ante ti una gran trampa rota. Parece que los ogros han intentado reparar la trampa, pero sin éxito. Al lado de la trampa hay una nota escrita a toda prisa. Lo curioso es que describe exactamente lo que se necesita para reparar la trampa: 1 trasto wow de torio y un poco de aceite de escarcha. Si se reparara la trampa, cualquier ogro que pasara por aquí podría caer en ella... uno menos.\n\nEvidentemente, el hecho de que sean pocos los ogros que saben leer, podría parecer un hecho fortuito.\n\nPodría parecerlo...", completion = "El trasto wow de torio se desliza a su sitio sin esfuerzo, creando un conducto para verter el aceite de escarcha. Cuando se ha vertido la última gota de aceite, la trampa se coloca en posición.\n\n¡Pobre del ogro despistado que pase por aquí!", rewards = nil},
[1194] = {title = "Los esquemas de Rizzle", objective = "Llévale la muestra de indurio a Pozzik, que está en El Desierto de Sal.\n\nObjeto provisto: \n", description = "Los esquemas de Rizzle describen el proceso de refinado del indurio. En sus garabatos queda claro que el indurio se obtiene de la excavación dirigida por los enanos en Uldaman, en las Tierras Inhóspitas.\n\nEl resto de notas hablan de la resistencia al calor.\n\nEncima de los diseños, a modo de pisapapeles, hay lo que aparenta ser indurio no refinado.", progress = nil, completion = "¿Indurio? Interesante... Si tiene una resistencia al calor tan alta como dices, eso podría explicar la mejora en los resultados experimentada por los gnomos.\n\nAdemás, esta muestra que me has traído tiene un valor incalculable. Podré hacer algunas pruebas con ella pero necesitaré más para poder sacarle partido.", rewards = "Recibirás:\n\n"},
[1195] = {title = "La llama Sagrada", objective = "Llévale una ampolla con grabados llena a Zangen Pezuñapétrea, que está en Cima del Trueno.", description = "Los centauros Galak de Las Mil Agujas custodian un artefacto desde el comienzo de sus tiempos.\n\nNos gustaría arrebatárselo pero para ello necesitamos una ampolla de agua de una de las pozas de la luna de los elfos de la noche.\n\nPara conseguir el agua, primero tendrás que hacerte con una de las ampollas que portan los dríades cerca del Refugio de la Algaba en el corazón del Bosque de Vallefresno. Hay una poza de la luna cerca de la orilla occidental del Río Falfarren, al sur de la carretera principal, donde podrás llenar la ampolla de agua.", progress = "Cuando tengamos la ampolla llena, se la podremos enviar a mi socio Rau, que está en Las Mil Agujas. Él es quien pidió la ampolla de agua así que sabrá mejor qué hacer con ella.", completion = "Muy bien, es justo lo que Rau quería. ¿No podrías llevársela? Seguro que te apetece pasar un tiempo en Las Mil Agujas.", rewards = "Recibirás:\n\n"},
[1196] = {title = "La llama Sagrada", objective = "Entrégale la ampolla con grabados llena a Rau Abiscorredor del Poblado Viento Libre.", description = "Llévale la ampolla con agua a Rau Abiscorredor en el Poblado Viento Libre en la zona sudeste de Las Mil Agujas. Podrás llegar a Las Mil Agujas cogiendo El Gran Elevador que encontrarás en el extremo sur de Los Baldíos.\n\nNo me sorprendería que pudieras seguir ayudándonos.", progress = "Por casualidad no te enviará Zangen, ¿no?", completion = "Sí, esta ampolla de agua servirá a nuestros propósitos. Debido a la ascendencia común de los dríades y los centauros, con el agua de las pozas de la luna podremos romper la protección de la llama eterna que rodea la reliquia de los centauros.", rewards = "Recibirás:\n\n"},
[1197] = {title = "La llama Sagrada", objective = "Consigue la pezuña partida para Rau Abiscorredor del Poblado Viento Libre.", description = "Con el agua que has traído podrás apagar temporalmente la llama que protege la reliquia de los centauros. Sin embargo, su efecto no es permanente por lo que tendrás que darte prisa.\n\nEncontarás el Risco Pezuña Quebrada, el fuerte de los centauros, en dirección norte. La llama se encuentra en el Campamento Pezuña Quebrada, un lugar fuertemente custodiado del risco.\n\nTen cuidado pues los centauros Galak tienen a muchos hombres allí y no dejarán que les arrebates algo tan preciado para ellos con facilidad.", progress = "Estoy deseando tener la pezuña partida en mis manos. ¿Ya la has conseguido?", completion = "Siento una extraña fuerza que emana de la pezuña... Confío en que, tras haberla analizado, descubramos nuevos y sorprendentes datos acerca de los centauros.\n\nMuchas gracias, &lt;nombre&gt;. Y ahora, si me disculpas, tengo que empezar a descifrar los misterios que me has traído.", rewards = "Podrás elegir una de estas recompensas:\n\nObjetivo de AbiscorredorFajín azur\n\nTambién recibirás:\n\nObjetivo de AbiscorredorFajín azur"},
[1198] = {title = "Buscando a Thaelrid", objective = "Busca al guardia Argenta Thaelrid en las Cavernas de Brazanegra.", description = "Estos últimos días circulan muchos rumores sobre la reunión del Azote del Ocaso y los nagas  en las Cavernas Anegadas de las costas de Vallefresno.\n\nEstoy aquí para reunir toda la información que pueda para el Alba Argenta, para que sepamos dónde y cuándo necesitaremos ayuda.\n\nHace unas semanas envié a un explorador para observar la actividad de las Cavernas Anegadas... Pero Thaelrid, mi guardia Argenta, aún no ha regresado.\n\nMis otros exploradores están ocupados con asuntos más urgentes.\n\nBusca a Thaelrid por mí, <nombre>.", progress = nil, completion = "Shaedlass Guardalbas ha sido muy considerado al enviarte. Sin duda en este corrupto lugar se está tramando un oscuro complot.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1199] = {title = "Cascada del Crepúsculo", objective = "Lleva 10 colgantes del Azote al guardia Argenta Manados a Darnassus.", description = "Con el poder que les confieren los ritos de los caóticos dioses antiguos, los miembros del Azote del Ocaso trabajan para devolver a sus oscuros Señores a este mundo y así extender la muerte y el terror por él.\n\nCreemos que el Azote del Ocaso se ha aliado con los nagas . Se están concentrando en las Cavernas Anegadas de la costa de Vallefresno.\n\nDebemos frustrar los planes que se forjan en las profundidades del viejo santuario, sean cuales sean.\n\nEnfréntate al Azote y tráenos pruebas de tus hazañas. Tus esfuerzos serán ampliamente recompensados.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nBotas NimbusFaja de duramen"},
[1200] = {title = "La vileza de Brazanegra", objective = "Llévale la cabeza del Señor Crepuscular Kelris a Selgorm Guardalbas en Darnassus.", description = "Las fuerzas me han abandonado. ¡Necesito tu ayuda!\n\nOtrora este lugar era un gran templo de Elune. Pero la desgracia lo ha llevado a la ruina cuando la corrupción de un dios antiguo empezó a calar desde el subsuelo y contaminó la sagrada Poza de la Luna.\n\nAku'Mai, sirviente del dios antiguo, surgió de las aguas.\n\nLos adeptos del Azote del Ocaso se han unido a los nagas para ocupar esas tierras. Esos adeptos, cuyo jefe es Kelris, sacrifican a inocentes a Aku'Mai a cambio de poder.\n\nMata a Kelris y llévale su cabeza a Selgorm en Darnassus, <nombre>, te lo ruego.", progress = "El tiempo es un valioso bien, <clase>.\n\nMi papel aquí en Darnassus consiste en garantizar que los Alba Argenta prosperan y que se neutralizan las malvadas tropas que invaden Kalimdor.\n\nDime rápidamente a qué vienes o sigue tu camino.", completion = "Kelris lleva bastante tiempo evitándome.\n\nParece que cada vez que el mal se manifiesta por estos lares, Kelris hubiera tenido algo que ver. Durante algún tiempo lo dimos por muerto o desaparecido.\n\nPero esto encaja perfectamente. Al poner fin a su reinado has salvado las vidas de muchos inocentes.\n\n¡Por la Luz que nos guía! ¡Sacrificar a alguien a un servidor de un dios antiguo para el beneficio personal no entra en los límites de lo reprobable!\n\nNos has hecho un gran favor, &lt;nombre&gt;. Me inclino ante ti en nombre de los Alba Argenta.", rewards = "Podrás elegir una de estas recompensas:\n\nCetro de lápidaRodela ártica\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCetro de lápidaRodela ártica"},
[1201] = {title = "Los espías de Theramore", objective = "Mata a 9 infiltradores de Theramore y luego reúnete con Nazeer Picasangre en el Poblado Murohelecho.", description = "La tregua de Horda y la Alianza peligra. Ambas partes intentan dañar a la otra. Refriegas, acciones encubiertas... ¡Nuestro deber es asegurarnos de que gane la Horda!\n\nY esta es nuestra oportunidad de conseguirlo.\n\nHay infiltrados de la Alianza al este del Poblado Murohelecho. Espían el poblado e informan de nuestros movimientos a Theramore, cosa que no podemos permitir.\n\nDa caza a los infiltrados y mátalos. Envía un claro mensaje a sus superiores: ¡no toleraremos la presencia de sus espías!", progress = nil, completion = "Buen trabajo. Sus infiltradores son buenos, ¡pero no lo suficiente para escapar al ojo vigilante de la Horda!", rewards = "Recibirás:\n\n"},
[1202] = {title = "El puerto de Theramore", objective = "Lleva los documentos del Capitán a Nazeer Picasangre al Poblado Murohelecho.", description = "<nombre>, enviamos a dos de nuestros espías a robar documentos de un navío de los puertos de Theramore. Consiguieron los papeles, pero fueron descubiertos y se vieron obligados a tirarlos al mar.\n\nUno de ellos consiguió escapar más tarde, pero no logró recuperar los documentos. Vino a mí a informar de su fracaso y ahora yo te encargo a ti la misión.\n\nLos documentos están en un baúl, en las aguas del puerto de Theramore. Encuéntralos y tráemelos aquí, <nombre>.", progress = "¿Encontraste los documentos, <nombre>? Contienen información vital para la estabilidad de la Alianza y, por tanto, vital para la seguridad de la Horda.", completion = "Buen trabajo, &lt;nombre&gt;. Y no era una misión fácil. Pero nuestros superiores valoran muchísimo los documentos que has conseguido.", rewards = "Recibirás:\n\n"},
[1203] = {title = "Jarl necesita una espada", objective = "Lleva 1 espada de acero lunar a Jarl al Marjal Revolcafango.", description = "Tengo algo que cortar. No puedo decirte qué voy a cortar, pero... Necesito una espada. Una espada concreta...\n\nHay quien la llama espada de acero lunar. Yo prefiero llamarla espada tajante.\n\n¿Me ayudas? Puede que la encuentres en la ciudad. Habla con un herrero, quizás te la haga.\n\nPero no te duermas en los laureles. Lo que quiero cortar no esperará para siempre...Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nAnillo Nudo de JuncoPantalones de artesano"},
[1204] = {title = "Sopa de fangorroca con tropezones", objective = "Lleva 8 lenguas Fangorroca bifurcadas a Morgan Stern a Theramore.", description = "Estoy trabajando en un plato que llamaré \"sopa de fangorroca con tropezones.\" Pero necesito los ingredientes principales y... bueno, no puedo ir a buscarlos al Marjal Revolcafango, ¡no creo que durara mucho allí!\n\nQuizás podrías ir tú por mí, <nombre>. Para empezar, necesito lenguas de tortugas Fangorroca. Puedes encontrar tortugas Fangorroca en la costa, sobre todo en la bahía.\n\n¡Pero no me sirve cualquier lengua! Sé que algunas Fangorroca tienen lenguas bífidas...\n\nY se dice que las lenguas bífidas tienen un sabor especial... Quiero ese sabor en mi plato.", progress = "¿Conseguiste las lenguas bífidas?", completion = "¡Estas son estupendas! ¡Gracias, &lt;nombre&gt;!\n\nAhora probaré a cocinar estas lenguas con diferentes técnicas para encontrar la que más realce su sabor.\n\n¡Conseguiré un plato digno de servirse en El Ermitaño Azul de Ventormenta!", rewards = "Recibirás:\n\n"},
[1205] = {title = "Lodomuerto", objective = "Lleva el Diente de Lodomuerto a Melor a Cima del Trueno.", description = "Lodomuerto es un anciano crocolisco del Marjal Revolcafango. Y ha llegado su hora.\n\nSus viejos huesos crujen cuando desplaza su enorme cuerpo por el pantano y aunque aún se mueve con el vigor y la velocidad de un joven, su anciano cuerpo tortura al gran espíritu que lo habita, un espíritu cuya llama no se extinguirá.\n\nSigue viviendo con constantes y enloquecedores dolores. Debes poner fin a la vida de esta noble criatura, <nombre>. Debes dar paz a Lodomuerto.", progress = "¿Tienes el diente de Lodomuerto, <nombre>? ¿Has acabado con los sufrimientos de la criatura?", completion = "Muy bien, &lt;nombre&gt;.\n\nSeguro que la caza no fue fácil, pero me alegra saber que has puesto fin al sufrimiento de la pobre bestia.", rewards = "Recibirás:\n\n"},
[1206] = {title = "Jarl necesita ojos", objective = "Lleva 40 ojos Niebla Negra a Ojolodo Jarl a la Mansión Cienaluz.", description = "El zumo de ojo es una bebida estupenda. Es una pena que la mayoría de los bichos solo tengan dos ojos.\n\nPero las arañas tienen muchos. Muchos, muchos. Y el zumo de ojo de araña Niebla Negra está muy rico. ¡Suavecito y salado! Redonditos ojos de araña... Se me hace la boca agua...\n\nLas arañas Niebla Negra están al oeste de aquí, en la Cueva de la Niebla Negra. Está claro que las arañas llevan el nombre de la cueva en la que viven... ¿o será al revés?", progress = "¿Dónde están esos ojos?", completion = "¡Tienes los ojos! ¡Tienes los ojos! ¡Ricos, ricos ojos de araña!\n\nAlgunos ojos... parece que te miran cuando los miras. Esos son los últimos que hay que comerse. Así te hacen compañía... en el pantano la compañía escasea.\n\nHola, ojitos de araña. ¡Hola! Sabéis a pollo. ¡Rico pollito salado!\n\nMmm.", rewards = "Recibirás:\n\n"},
[1218] = {title = "Especias calmantes", objective = "Lleva 3 especias calmantes a Ojolodo Jarl al Marjal Revolcafango.", description = "Me gusta cocinar. Sobre todo, la buena cocina del pantano. Ancas de rana verdes fritas y esas cosas.\n\nY para cocinar, necesito especias. Especias calmantes. Cuando las tenga haré un guiso. Lo removeré y removeré y luego me lo comeré. Y también te daré un poco a ti.\n\n¿Te gustan los guisos?", progress = "Especias. A todos nos vendrían bien. Dan sabor a la vida. ¿No te parece?", completion = "¡Esto sí que son especias! Aquí tienes un poco de buen guiso del pantano.", rewards = "Recibirás:\n\nEstofado de anca de rana"},
[1219] = {title = "El informe orco", objective = "Lleva el informe del espía al Teniente de Theramore.", description = "Una criba de la tierra revela la mano cortada de un orco. En la mano hay un tubo de hueso. Y en el tubo, un pergamino manchado de tierra.\n\nEl pergamino está escrito en lenguaje orco... y parece importante.", progress = "¿Tienes algo que informar?", completion = "Mm... No entiendo muy bien el lenguaje orco pero, esto parece una especie de informe... ¡escrito por un espía orco!", rewards = "Recibirás:\n\n"},
[1220] = {title = "Capitán Vimes", objective = "Lleva el informe del espía al capitán Garran Vimes.", description = "El capitán Vimes debería ver el informe orco que encontraste, <nombre>. Conoce el lenguaje orco mejor que muchos. ¡Y debería saber que hay espías de la Horda cerca de Theramore!\n\nEl Capitán está en los barracones que hay en la parte noreste de la Isla Theramore.", progress = "¿Tienes algo para mí?", completion = "¡Un informe de un espía orco! ¿Cómo lo encontraste? No importa. Veamos lo que dice...", rewards = "Recibirás:\n\n"},
[1221] = {title = "Los tubérculos hojazul", objective = "Coge un cajón agujereado.\nCoge una vara de mando de Husmeador.\nLee el manual del propietario del Husmeador. \n\nCuando llegues a Horado Rajacieno, usa el cajón agujereado para invocar un husmeador taltuza; usa la vara para que busque tubérculos. \n\nLleva 6 tubérculos hojazul, la vara de mando de Husmeador y el cajón agujereado a Mebok Mizzyrix a Trinquete.", description = "Los tubérculos hojazul son muy apreciados en todo el mundo. Pero son muy raros. Solo se encuentran aquí, en Los Baldíos, enterrados profundamente en Horado Rajacieno.\n\n¡Y es imposible encontrarlos a no ser que sepas dónde mirar! Por eso he amaestrado husmeadores taltuza para que los busquen. Tienen un olfato extraordinario, huelen los tubérculos a una distancia de 50 pasos.\n\nNo será fácil, pero si me traes tubérculos te pagaré muy bien.", progress = "¿Traes los tubérculos?", completion = "¡Estupendo, me pagarán un buen precio por ellos!\n\nGracias, &lt;nombre&gt;, eres genial.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nUn pequeño contenedor de gemas"},
[1222] = {title = "La fuga de Apestoso", objective = "Escolta a Ignatz el Apestoso y luego ve a Theramore a hablar con Morgan Stern.", description = "¡Genial! ¡Por fin algo que no quiere comerme!\n\nNecesito ayuda con los bichos de por aquí. Y tú pareces ser justo lo que necesito, <chaval/señorita>.\n\nEl cocinero Morgan Stern está trabajando en una nueva receta y me ha mandado aquí a recoger hojas de cienagrana. Creo que opina que son sabrosas.\n\nEs lo mismo... el caso es que cuando buscaba una planta de cienagrana... ¡Me vi rodeado! ¿Podrías ayudarme a salir de aquí y vigilarme las espaldas mientras busco la cienagrana?", progress = nil, completion = "El señor Ignatz ha estado aquí con las hojas de cienagrana y me dijo que le habías ayudado a salir de un auténtico atolladero.\n\nTe lo agradezco, &lt;nombre&gt;. Pediré a mi tío que te invite a un festín la próxima vez que vayas a El Ermitaño Azul de Ventormenta.", rewards = "Recibirás:\n\nElixir de entereza"},
[1238] = {title = "El informe perdido", objective = "Lleva el informe del espía a Nazeer Picasangre al Poblado Murohelecho.", description = "Una criba de la tierra revela la mano cortada de un orco. En la mano hay un tubo de hueso. Y en el tubo, un pergamino manchado de tierra.\n\nEl pergamino es el informe de un orco que pasó semanas espiando la ciudad de Theramore.\n\nCon una rápida lectura del pergamino te das cuenta de que debes llevarlo al maestro de espías orco del Poblado Murohelecho.", progress = "¿Tienes algo para mí?", completion = "Buen trabajo, &lt;nombre&gt;. Nuestro agente, Marg Ojonocturno, se encontraba fuera de Theramore. Tenía el cometido de espiar la ciudad.\n\nEste es su último informe... y por lo que dices, será de verdad el último, pues temo que Marg encontrara su final en el pantano...", rewards = "Recibirás:\n\n"},
[1239] = {title = "La cabeza decapitada", objective = "Lleva la cabeza de orco desafiante a Nazeer Picasangre al Poblado Murohelecho.", description = "Una enérgica excavación revela la cabeza de un orco macho, salvajemente decapitada. Sus labios se petrificaron con un gruñido y sus ojos oscuros tienen aún una mirada desafiante.", progress = "¿Tienes algo más que comunicarme, <nombre>?", completion = "¡Esta es la cabeza de Marg Ojonocturno! Tal y como temía, está muerto.\n\n¡Mira la herida que tiene en el cuello! ¿Podría haberla causado un arma, o quizás nuestro agente murió a manos de una bestia?\n\nEs un misterio que debemos resolver, &lt;nombre&gt;. ¡Debemos descubrir si Marg encontró la muerte a manos de la Alianza!", rewards = "Recibirás:\n\n"},
[1240] = {title = "El trol médico brujo", objective = "Lleva la cabeza de orco desafiante a Kin'weelay al Campamento Grom'Gol de Vega de Tuercespina.", description = "¡Debemos conocer las circunstancias de la muerte de Marg! Y la única forma de conocerlas, <nombre>, es a través de la magia de los trols.\n\nLleva la cabeza de Marg a Kin'weelay. Está en el Campamento Grom'Gol, en la Vega de Tuercespina.\n\nKin'weelay es el médico brujo de los trols Lanza Negra. Es muy poderoso. Si alguien puede hablar al espíritu de Marg Ojonocturno, ese es él.", progress = "Percibo un espíritu iracundo al mirarte, hermano. ¿Qué te trae a Kin'weelay?", completion = "Ah, ya veo... El espíritu de este orco está ligado a nuestro mundo; hierve de rabia por un asunto que dejó sin concluir.\n\nBien, hablemos con este iracundo orco...", rewards = "Recibirás:\n\n"},
[1241] = {title = "El diplomático desaparecido", objective = "Encuentra a Jorgen en Ventormenta.", description = "Recientemente, un diplomático fue enviado a Theramore para encontrarse con Jaina Valiente. Ese diplomático nunca llegó a su destino.\n\nCreo que los Defias están implicados, aunque no sé exactamente cómo. La desaparición del diplomático sigue sin ser del dominio público, pero no seguirá así mucho tiempo.\n\nHay unos agentes del rey explorando la ciudad en busca de pistas, pero tengo a mis propios contactos que me gustaría que trabajasen en esto.\n\nEn el Valle de los Héroes hay un viejo amigo mío llamado Jorgen. Encuéntrale, dale esta nota y sigue sus instrucciones.", progress = "Mmm, será mejor que tengas una buena razón para interrumpir a un hombre que está pescando. No me gustan mucho los vendedores ambulantes y mucho menos los vagabundos.", completion = "Mmm... &lt;Jorgen te mira con los ojos entrecerrados.&gt;\n\nMuy bien. Te ayudaré, pero no porque quiero hacerlo, sino porque estoy obligado.\n\nY dejemos una cosa clara: sabes más de lo que deberías saber. Mantengamos esto en secreto y no vayas por las tabernas por ahí hablando de tus descubrimientos. ¿Comprendido?", rewards = "Recibirás:\n\n"},
[1242] = {title = "El diplomático desaparecido", objective = "Entrega la nota sellada de Jorgen a Elling Trias en Ventormenta.", description = "Lleva esta nota a Elling Trias. No le digas nada. Simplemente sé amable y espera a que te responda. Y no te preocupes, lo hará.\n\nVoy a decirte esto una vez más, <nombre>. Trata este asunto con seriedad, y mantén la boca cerrada.\n\nPodrás encontrar a Trias en su tienda de quesos, Queso de Trias, en el Distrito de Mercaderes. Buena suerte.", progress = "Hola, <nombre>. ¿Puedo hacer algo por ti? ¿Quizás te gustaría probar nuestro cheddar de Trias especial? ¿O un trocito de queso azul de Darnassus?", completion = "Perdona, ¿una carta para mí? Bueno, yo... cuando te pasas el día trabajando, como yo, no se suele recibir algo que no sea una caja o un barril. Veamos de qué va esto.\n\n&lt;Elling te mira mientras abre la carta.&gt;\n\nPareces algo tenso, &lt;nombre&gt;, ¿estás bien?", rewards = "Recibirás:\n\n"},
[1243] = {title = "El diplomático desaparecido", objective = "Entrega la nota sellada al vigilante Backus en el Bosque del Ocaso.", description = "Jorgen, viejo hijo de... Así que, ¿así es como va a ser, eh?\n\nPues muy bien, puedo con eso. Espero que encuentre El Vacío Abismal muy pronto.\n\nEsto es lo que va a pasar, <nombre>. Empezaré a usar mis contactos aquí, pero tú vas a encargarte de los paseítos, no quiero perder dinero.\n\nVete a Villa Oscura en el Bosque del Ocaso y encuentra al Vigilante Backus. Generalmente patrulla el camino del norte justo fuera de la ciudad. Dile que estás buscando actividad Defias, que yo te lo he pedido y te ayudará dándote toda la información que puedas.", progress = "No es demasiado seguro ir por el bosque, <nombre>. Ten cuidado y, si ves algo sospechoso o peligroso, avisa a los vigilantes inmediatamente. No intentes nada sin ayuda. No nos hacemos responsables de ninguna cosa terrible que te pueda suceder.\n\nPero ambos sabemos que vas a pasar de mí y a luchar contra cualquier cosa que aparezca en medio de la oscuridad, ¿verdad? ¡Ja, ja! Sí, vosotros, los aventureros... sois todos iguales.", completion = "¿Te envía Trias, eh? Eso cambia la cosa.\n\nEntonces, puedes luchar contra cualquier monstruosidad que salga de la oscuridad. Seguro que podrás vencer lo que sea fácilmente, ya que eres capaz de tratar con gente de su calaña.\n\nY bien, ¿para qué me necesita mi amigo en un día tan fúnebre como este?", rewards = "Recibirás:\n\n"},
[1244] = {title = "El diplomático desaparecido", objective = "Encuentra el certificado Defias y llévaselo al vigilante Backus en el Bosque del Ocaso.", description = "¿Actividad Defias? Bueno, siempre hay actividad; incluso si están tranquilitos, les seguimos considerando una amenaza.\n\nPero ahora que lo pienso, hace poco tiempo vimos algo extraño. Debe de haber sido hace un par de semanas, algunos de sus agentes se habían reunido en la Granja de Addle. Según el informe, supuse que algo grande estaba pasando.\n\n¿Por qué no vas a echar un vistazo y me traes lo que encuentres?\n\nLa granja está al sur del camino de Páramos de Poniente, justo al entrar en el Bosque del Ocaso.", progress = "Ten cuidado ahí fuera, <nombre>. Sé que Trias fue quien te envió, pero eso no significa que no haya nada acechando en los bosques que podría causarte un par de problemas... especialmente contigo investigando a los Defias.", completion = "Has vuelto de una pieza. Y no se puede pedir mucho más que eso, ¿eh?\n\nAhora, veamos lo que tenemos aquí. Esto parece bastante oficial, a juzgar por los documentos Defias que he visto. Pero podría ser una falsificación, solo para hacerte perder la pista de lo que estés buscando.", rewards = "Recibirás:\n\n"},
[1245] = {title = "El diplomático desaparecido", objective = "Lleva el certificado Defias a Elling Trias en Ventormenta.", description = "¡Guau! Me parece muy complicado para ser algo que pueda llevarte por mal camino. Ni siquiera debería estar viendo esto. Llévaselo a Trias, él podrá encontrarle el significado. Y NO permitas que nadie se interponga en tu camino. Esto me supera, quizás incluso le supere a él, pero ya veremos.\n\nY hazme un favor, <nombre>, no le digas a nadie que te he ayudado. Si mis superiores se enterasen de que sé algo de esto y no se lo cuento, probablemente me colgarían.", progress = "Veo que has vuelto, y que estás de una pieza. Ambos sabemos que no estás aquí por el queso, así que vayamos al grano. ¿Backus te ayudó, o todavía no has terminado en el Bosque del Ocaso?", completion = "Sí... una lectura interesante. Jorgen y DeLavey fueron inteligentes por avisarme.\n\nParece que los Defias han estado esforzándose mucho por completar este plan. Espera, ¿qué es esto? ¿\"Puño\"? Ese nombre me suena.", rewards = "Recibirás:\n\n"},
[1246] = {title = "El diplomático desaparecido", objective = "Habla con Dashel Puñopiedra en Ventormenta.", description = "Sí, ahora que lo pienso... Dashel... Dashel algo... ¿Cómo se llamaba? ¡PUÑO! Dashel Puñopiedra. Le llaman Puño en algunos círculos... probablemente debido a que le encanta meterse en peleas.\n\nSuele estar en el Casco Antiguo. Justo en el centro. Vete a hablar con él. Si tienes que hacerlo, utiliza una espada o algo pesado y contundente para persuadirle. Y no dudes. Si está metido en esto tanto como sugieren estos papeles, entonces no va a darte ninguna información por voluntad propia.", progress = nil, completion = "¡¿Qué?!\n\n¿¿¿Vienes a mi callejón a hacerme preguntas personales sobre mis asuntos??? Asuntos con los que, obviamente, no tienes nada que ver.", rewards = "Recibirás:\n\n"},
[1247] = {title = "El diplomático desaparecido", objective = "Habla con Elling Trias en Ventormenta.", description = "Eh, sí... Mmm... Quizás sepa algo sobre esa reunión en la Granja de Addle. Creo que fue hace unas semanas. Yo no participé. Acabé cuando el plan A falló... Malditos aventureros.\n\nEl tipo que teníamos dentro para el plan B, salió de Menethil; un tal Slim. Eso es todo lo que sé sobre su plan de reserva.", progress = nil, completion = "Me alegra ver que sigues con vida, &lt;nombre&gt;. ¿Todo fue bien?\n\nHe estado leyendo más sobre este plan suyo y, aunque no dice el nombre del diplomático al que perseguían, habla sobre un plan B que pusieron en marcha tras fallar su primer intento de captura.\n\nEse Slim al que mencionó Dashel no tiene ningún otro nombre aquí, pero si dices que está en Menethil, al menos es un comienzo.", rewards = "Recibirás:\n\n"},
[1248] = {title = "El diplomático desaparecido", objective = "Habla con Mikhail en Los Humedales.", description = "Uno de mis contactos se retiró y se fue a Los Humedales, en la ciudad portuaria de Menethil. Seguro que te ayuda si le dices que estás trabajando para mí. Se llama Mikhail. Dijo algo sobre convertirse en camarero antes de irse de Ventormenta hace unos años.\n\nHabla con él, encuentra a ese Slim, y descubre si hay algo más que saber sobre el diplomático secuestrado. Tenemos que descubrir dónde le han llevado, o al menos, quién es. En los papeles no se dice nada sobre sus planes después de la captura.", progress = nil, completion = "Te doy la bienvenida con los brazos abiertos, &lt;nombre&gt;. Me alegra que te unas a nosotros. ¿Te apetece una cervecita? ¿O quizás algo de carne de ovino?\n\nDi lo que quieras y me encargaré de que lo tengas. Me llamo Mikhail, y solo deseo servirte bien.\n\n¿Mmm? ¿Qué es eso? ¿Trias? Habla bajo, amigo. Esto no es algo de lo que se deba enterar toda la taberna.", rewards = "Recibirás:\n\n"},
[1249] = {title = "El diplomático desaparecido", objective = "Vence a Tapoke Jahn antes de que escape y vuelve a ver a Mikhail en Menethil.", description = "¿Dices que Elling te ha enviado? Bueno, pues entonces, la hospitalidad que te expresé, multiplícala por diez. Si necesitas cualquier cosa, solo tienes que pedirla. Haré lo que pueda para ayudarte.\n\nMmm, ¿Slim dices? No sé si le conozco, pero mantendré los ojos abiertos por si le veo.\n\nNo mires ahora, <nombre>, pero el caballero que está allí en la salida, está mirando hacia aquí. Está escuchando nuestra conversación con demasiado interés, ya sabes lo que quiero decir... Si yo fuera tú, haría algo para detenerle antes de que salga corriendo.", progress = nil, completion = "Le has cogido, ¿eh? Bien por ti. Me sorprende que ese fuera Slim.\n\nSeré sincero, nunca pensé que echaría de menos la excitación de algunas de las actividades más turbias. Supongo que las viejas costumbres nunca mueren. No se lo digas a Elling... probablemente intentaría conseguir que volviera a Ventormenta si se enterase de que echo de menos la acción.\n\nMe alegro de haber podido ayudarte en tu estancia en Menethil. Si necesitas algo más, solo tienes que pedirlo.", rewards = "Recibirás:\n\n"},
[1250] = {title = "El diplomático desaparecido", objective = "Habla con Mikhail en Los Humedales.", description = "Los Defias no me pagaron lo suficiente para morir por su causa. Cogieron al rey. Sí, el rey de Ventormenta, no cualquier diplomático. Estaba de camino a Theramore para encontrarse con Jaina Valiente. Por lo visto, se trataba de algo gordo, un gran secreto. Pero no solo los Defias están implicados. Trabajan con alguien. Creo que sabían que esto era algo grande.\n\nLo único que yo tenía que hacer era subir abordo del barco del rey a su hombre, mientras el barco estaba en el puerto. El tipo se llamaba Hendel. Eso es todo lo que sé. Lo juro.", progress = nil, completion = "¡¿EL REY?! Eh... ¿el rey? ¿El rey, rey? ¿Estás de broma? No dijiste que estabas buscando a los secuestradores del rey. Ni siquiera sabía que había desaparecido... aunque eso explicaría la coronación de su hijo.\n\nEscribiré a Elling ahora mismo. Querrá saber que estás encargándote de esto.\n\nNunca habría adivinado que Jahn vendiese Ventormenta por una simple moneda. Incluso un pícaro debe tener honor cuando se trata de ciertas cosas.\n\nAhora, lo importante es encontrar a ese Hendel. Y creo que puedo ayudarte con eso.", rewards = "Recibirás:\n\n"},
[1251] = {title = "El escudo negro", objective = "Lleva el escudo de hierro ennegrecido a Krog al Poblado Murohelecho.", description = "Un escudo de hierro, ennegrecido por el fuego que arrasó la posada, cuelga de la chimenea, una de las pocas partes de la estructura de la Posada Reposo Umbrío que han sobrevivido.\n\nEl escudo puede separarse del ladrillo de la chimenea.", progress = "¿Qué es eso que tienes ahí, <nombre>? ¿Algo de los restos de la Posada Reposo Umbrío?", completion = "Entiendo que los humanos tienen gustos de decoración un poco extraños pero... ¿usarían un simple escudo de hierro para adornar una chimenea? Quizás ese escudo fue colocado allí por quienes quemaron la posada.\n\n¿Será una advertencia? ¿Será una amenaza?", rewards = "Recibirás:\n\n"},
[1252] = {title = "Teniente Paval Reethe", objective = "Lleva la Tarjeta de Reethe al capitán Garran Vimes de Theramore.", description = "Debajo de los escombros, en el suelo, ves un destello de luz. Apartas los escombros y la ceniza y descubres una insignia de esmalte blanco con un ancla de oro: el sello de Theramore.\n\nDebajo del ancla hay un nombre grabado: \"Teniente Paval Reethe\".", progress = "¿Has encontrado algo de interés en la Posada Reposo Umbrío?", completion = "Cuando me traen la insignia de un guardia, nunca viene acompañada de buenas noticias.\n\nA veces, me la traen porque un hombre o una mujer ha muerto en combate. Y yo tengo que comunicar la noticia a su familia. Otras veces no se sabe ni qué fue de quien portaba la insignia. Y en ocasiones... es peor que todo lo anterior.", rewards = "Recibirás:\n\n"},
[1253] = {title = "El escudo negro", objective = "Lleva el escudo de hierro ennegrecido al capitán Garran Vimes a Theramore.", description = "Un escudo de hierro, ennegrecido por el fuego que arrasó la posada, cuelga de la chimenea, una de las pocas partes de la estructura de la Posada Reposo Umbrío que han sobrevivido.\n\nEl escudo puede separarse del ladrillo de la chimenea.", progress = "¿Has encontrado algo en la Posada Reposo Umbrío?", completion = "Mm, no soy un experto en la creación de armas y armaduras. En su uso, sí, pero no en su fabricación.\n\nEste escudo parece tan normal como cualquiera, exceptuando el daño que le causó el fuego de la posada.", rewards = "Recibirás:\n\n"},
[1258] = {title = "... con tropezones", objective = "Lleva 12 patas de reptador prístinas a Morgan Stern a Theramore.", description = "Otro ingrediente de mi nueva receta será pata de cangrejo gigante sin pelar. Y los cangrejos de Bahía Revolcafango, esos que llaman Valvafango, ¡no me sirven! Los únicos cangrejos que puedo usar en mi receta viven lejos de aquí, en el Pantano de las Penas.\n\nLa pata de cangrejo es un elemento clave de la presentación del plato, así que necesito patas impolutas, sin marcas. Un cangrejo adulto solo tiene una o dos patas de esas y yo necesitaré muchas para mis pruebas.\n\nDime, <nombre>... ¿estás <preparado/preparada> para viajar?", progress = "Mis experimentos con las lenguas bífidas van estupendamente. Espero que tu caza de cangrejos esté yendo bien también...", completion = "¡Estupendos ejemplares, &lt;nombre&gt;! Están en perfectas condiciones, y la carne está rosadita y tierna. ¡Tienes sangre de chef corriendo por tus venas!", rewards = "Recibirás:\n\nMandil barroco"},
[1259] = {title = "Teniente Paval Reethe", objective = "Averigua más sobre Reethe hablando con el ayudante Tesoran de Theramore.", description = "No reconozco el nombre... Teniente Paval Reethe... Será mejor que consultes al tesorero adjunto. Él lleva todos los registros de la Guardia de Theramore, desde el personal hasta el equipamiento.\n\nÉl debería poder decirte quién es Reethe, solo tendrá que echar un vistazo a los libros.", progress = nil, completion = "Hola, &lt;nombre&gt;. ¿Deseas información sobre algún miembro de la Guardia de Theramore en concreto? Reethe, ¿no?\n\nDame un momento para que encuentre el libro correcto.", rewards = "Recibirás:\n\n"},
[1260] = {title = "Morgan Stern", objective = "Habla con Morgan Stern en Theramore.", description = "Estoy preparando un nuevo menú para el restaurante. Estará lleno de platos rurales con un mismo tema: el pantano. ¡Seguro que a mis clientes de El Ermitaño Azul les encanta!\n\nEnvié a mi sobrino Morgan a Theramore, una ciudad que hay en la frontera del Marjal Revolcafango. Está recogiendo recetas de sus habitantes y necesito saber qué ha conseguido hasta ahora.\n\nEl viaje a Theramore es largo. Demasiado para mí. Pero seguro que <un jovencito/una jovencita> como tú, aprovecha cualquier oportunidad para viajar.", progress = nil, completion = "Ah, así que te envía mi tío, ¿eh?\n\nBueno, he estado investigando unas recetas, y tengo algunas pistas, pero necesito ayuda.\n\nAyuda de alguien a quien no le importe mojarse los pies.", rewards = "Recibirás:\n\n"},
[1261] = {title = "Las declaraciones de Marg", objective = "Lleva el colgante enjoyado a Nazeer al Poblado Murohelecho.", description = "¡Maldito Valvafango! ¡No puedes matar a Marg! Ah... pero estoy muerto. Así que debió de matarme...\n\n¡Mi misión! ¡Mi misión! <nombre>, debes completarla.\n\nCuando estaba cerca de Theramore, estuve espiando a un grupo de soldados de la Alianza que había acampado en la playa. Uno de esos soldados llevaba un colgante cargado de gemas. ¡Ningún soldado puede permitirse un colgante así!\n\nUna noche me colé en su campamento y robé el colgante, ¡pero cuando regresaba a la base, uno de esos hombres-cangrejo Valvafango me mató!\n\n¡<nombre>, debes encontrar a ese Valvafango y recuperar el colgante!", progress = "<nombre>, ¿averiguaste qué suerte corrió Marg?", completion = "¿Y este colgante lo llevaba un soldado de Ventormenta??\n\nGracias, &lt;nombre&gt;. Has resuelto el misterio de la muerte de Marg. Pero este colgante es un nuevo misterio que debemos resolver... pues podría tener funestas consecuencias para la Alianza y para la Horda.", rewards = "Recibirás:\n\n"},
[1262] = {title = "Informa a Zor", objective = "Lleva el colgante enjoyado a Zor Árbolhuraño a Orgrimmar.", description = "Es extraño que ese colgante que robó Marg estuviera en manos de simples soldados. Quizás su dueño estaba disfrazado... ¡o quizás los soldados lo habían robado a su vez!\n\nSea cual sea la verdad, este es el colgante de un noble. Debemos encontrar a su dueño si queremos conocer su misteriosa historia.\n\nLleva el colgante a Zor Árbolhuraño a Orgrimmar. Conoce muy bien el ámbito político de la Alianza. Él sabrá si la historia de este colgante se entrelaza en algún punto con la de los nobles de Ventormenta.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1263] = {title = "La posada quemada <CHANGE TO GOSSIP>", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1264] = {title = "El diplomático desaparecido", objective = "Encuentra al comandante Samaul en el Marjal Revolcafango.", description = "A principios de mes, todos los marineros de Kul Tiras reciben la paga y, generalmente, se van de Theramore en busca de diversión. Muchos vienen aquí a buscar mujeres, jarana y a pasárselo bien. Hay uno de esos grupos que viene regularmente a la taberna, y creo que ese Hendel está con ellos.\n\nEl comandante Samaul de Theramore podrá ayudarte a encontrarle, si Hendel está destinado allí. Es completamente leal a Valiente, pero si dejas caer el nombre de Elling, te ayudará... discretamente.", progress = nil, completion = "Un placer conocerte, &lt;nombre&gt;. Soy el comandante Samaul. Estoy al cargo de las tropas de Jaina Valiente aquí, en Theramore.\n\nEstoy bastante ocupado pero, si tienes alguna pregunta, no dudes en hacérmela. Quizás no tenga mucho tiempo, pero no hay razón para que me ponga desagradable si necesitas mi ayuda.\n\nSi estás planeando viajar fuera de la ciudad, te sugiero que tengas cuidado. Además de ogros, orcos y murlocs, la parte sur del pantano está atestada de dragones negros.", rewards = "Recibirás:\n\n"},
[1265] = {title = "El diplomático desaparecido", objective = "Explora el Alto del Centinela en busca de alguna señal del soldado Hendel.", description = "¿Hendel? Sí, le conozco. ¿Pero para qué necesitas a uno de mis soldados?\n\n¿Elling te ha enviado a buscarle? Mmm, no me gusta cómo suena eso. Elling no te habría pedido que vinieses primero a hablar conmigo si no se tratase de algo preocupante. Comprendo que no puedas decirme de qué va el tema, pero has de saber algo. Mi deber es proteger estas tierras. Si vas a causar problemas, te sugiero que me informes de todo lo que puedas.\n\nCreo que Hendel estaba destinado a la primera torre de las afueras de Theramore. Ten cuidado.", progress = nil, completion = "El hombre al que buscas, Hendel, no está aquí. Ha llegado información a través de otras fuentes a Lady Valiente, que creo que ya sabes. Me ha pedido que te busque y te ayude en tu misión. Si me lo permites, te diré dónde encontrar al soldado Hendel para que puedas llevarle ante la justicia.", rewards = "Recibirás:\n\n"},
[1266] = {title = "El diplomático desaparecido", objective = "Encuentra al soldado Hendel en el Marjal Revolcafango.", description = "El soldado Hendel está al oeste de aquí. Actualmente está de guardia, protegiendo a algunos de los infiltrados de Theramore mientras descansan. Han estado vigilando la base de la Horda, Murohelecho, y atentos a cualquier otra amenaza potencial en nuestras fronteras.\n\nNo deberías tener problemas para encontrarle. Busca las tiendas que están junto al camino.\n\nTengo que encargarme de otro asunto, pero hablaremos pronto. Si me necesitas, puedes encontrarme en la torre de Lady Valiente.", progress = nil, completion = "Hola, ciudadano. Este es un lugar peligroso para las visitas. Yo no me quedaría mucho por aquí si valoras tu vida.\n\nSi tus preguntas son cortas, intentaré ayudarte. Mantén los ojos abiertos y ten cuidado con esos sucios ogros. Según los rumores, hay una base de la Horda cerca del pantano. Los infiltrados hacen todo lo que pueden para encontrarla, pero el proceso va lento y no hemos conseguido avanzar mucho.", rewards = "Recibirás:\n\n"},
[1267] = {title = "El diplomático desaparecido", objective = "Habla con Jaina Valiente en el Pantano Revolcafango.", description = nil, progress = nil, completion = "Primero, quiero darte las gracias por todo lo que has hecho, &lt;nombre&gt;. Has sido una increíble bendición para todos los míos y para todo el pueblo de Ventormenta. Siento no haber podido conocerte en mejores circunstancias.\n\nEspero que comprendas que no puedo darte las gracias abiertamente, pero te aseguro, que lo aprecio mucho.\n\nCuando vengas a Theramore en el futuro, búscame e intentaré compensarte.\n\nGracias de nuevo, &lt;nombre&gt;.", rewards = "Recibirás:\n\nSello de Jaina"},
[1268] = {title = "Huellas pezuñales sospechosas", objective = "Informa a Krog, del Poblado Murohelecho, sobre las huellas sospechosas de la Posada Reposo Umbrío.", description = "Hay hileras de huellas que se alejan del esqueleto, aún humeante, de la Posada Reposo Umbrío. Hay unas pocas impresiones visibles y algunas hileras de huellas casi imperceptibles en el barro blando del pantano.", progress = nil, completion = "Huellas pezuñales... Podrían haber sido los jabaespines, supongo. O incluso los centauros. Su presencia en Los Baldíos no es insignificante pero para cometer este acto...\n\nNo te lo conté, pero no solo quemaron la posada... La mujer y el hijo del dueño fueron asesinados. Mal asunto, mal asunto.\n\nEn fin, voy a mandar igualmente a Kagoro a seguir el rastro de esas huellas.", rewards = "Recibirás:\n\n"},
[1269] = {title = "Teniente Paval Reethe", objective = "Lleva la Tarjeta de Reethe a Krog al Poblado Murohelecho.", description = "Debajo de los escombros, en el suelo, ves un destello de luz. Apartas los escombros y la ceniza y descubres una insignia de esmalte blanco con un ancla de oro: el sello de Theramore.\n\nDebajo del ancla hay un nombre grabado: \"Teniente Paval Reethe\".", progress = "¿Has encontrado alguna pista sobre lo que ocurrió en la Posada Reposo Umbrío?", completion = "Conozco a este humano, Paval Reethe. Es patético, incluso para ser humano. Abandonó a sus compañeros y ahora se esconde en el bosque.\n\nEnvié a Ogron a buscarle. Algunos hombres creen que ha estado robando nuestros suministros. Creo que vi a Ogron dirigiéndose hacia el sureste.", rewards = "Recibirás:\n\n"},
[1270] = {title = "La fuga de Apestoso", objective = "Escolta a Ignatz el Apestoso y luego ve a Trinquete a hablar con Mebok Mizzyrix.", description = "¡Genial! ¡Por fin algo que no quiere comerme!\n\nNecesito ayuda con los bichos de por aquí. Y tú pareces ser justo lo que necesito, <chaval/señorita>.\n\nUn goblin de Trinquete llamado Mebok Mizzyrix necesita unas hojas de cienagrana para una poción o algo así... Tampoco es que me importe mucho. Me dio un buen dinero y conozco el pantano, así que...\n\n¡Una pena que me rodearan antes de que encontrara la cienagrana! ¿Puedes ayudarme? ¿Podrías cubrirme las espaldas mientras busco la planta de Mebok?", progress = nil, completion = "Vino Apestoso con las hierbas que le pedí y me dijo que le habías ayudado a salir de un aprieto. ¡Bien hecho! ¡Un amigo de Apestoso es amigo mío!\n\nPoco antes de dirigirse hacia el sur, hacia el pantano, me pidió que te diera esto si te veía.", rewards = "Recibirás:\n\nElixir de entereza"},
[1271] = {title = "¡Una fiesta en tu honor!", objective = "Mi sobrino me dijo que le habías ayudado en su investigación en Revolcafango y que sacaste a su socio de un buen apuro. ¡Pues los Stern estamos muy unidos y atendemos a nuestros amigos!\n\nAsí que lávate las manos, <nombre>, ¡porque estamos a punto de montar una fiesta en tu honor!", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nToallita húmeda"},
[1272] = {title = "Encontrar a Reethe <CHANGE INTO GOSSIP>", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1273] = {title = "El interrogatorio de Reethe", objective = "Ve con Ogron a hablar con Reethe y luego vuelve al Poblado Murohelecho a hablar con Krog.", description = "Me llevó un buen rato, pero encontré a Reethe. Estaba bien escondido, para ser humano.\n\nOgron temía que pudiera haber enloquecido después de pasar tanto tiempo en el pantano. Ven conmigo. Tiene que darnos algunas respuestas.", progress = nil, completion = "Mm. Que Reethe no vaya a seguir deambulando por el pantano es una buena noticia, pero es una pena que los humanos sean tan débiles... que no pudiera recuperarse de una mínima herida de flecha, es una pena.\n\nParece que este camino no nos ha ayudado a resolver el misterio. Debemos probar por otro lado.", rewards = "Podrás elegir una de estas recompensas:\n\nSalta ojosArcabuz explosivo\n\nTambién recibirás:\n\nSalta ojosArcabuz explosivo"},
[1274] = {title = "El diplomático desaparecido", objective = "Encuentra al Obispo DeLavey en el Castillo de Ventormenta.", description = "Disculpa, noble <caballero/gentil dama>. El Obispo DeLavey me pidió que buscara la ayuda de aventureros que quizás puedan ayudarle.\n\nSi pudieras ir discretamente al Castillo de Ventormenta a hablar con él, seguro que te lo agradecería mucho. Y, de nuevo, por favor, con mucha discreción. Se trata de un asunto muy importante.", progress = nil, completion = "Hola, &lt;nombre&gt;. Soy el Obispo DeLavey.\n\nSin duda, si estás aquí preguntándome sobre mi asunto del Castillo, es porque el joven Thomas ha tenido éxito a la hora de pedirte ayuda para nuestro reino. Siempre ha sido experto en hacer lo que le pido.\n\nMe temo que no tengo mucho tiempo para explicarme, así que iré directo al problema... y, por favor, intenta que nadie se entere de esto.", rewards = "Recibirás:\n\n"},
[1275] = {title = "Investigaciones acerca de la corrupción", objective = "Gershala Noctusurro de Auberdine quiere 8 bulbos raquídeos corruptos.", description = "Esos insensatos elfos de sangre están estudiando la magia demoníaca. ¿Acaso no vieron lo que ocurrió con los nagas y los sátiros de Kalimdor?\n\nTemo que los elfos de sangre acabarán sufriendo una deformación similar. Azeroth no puede permitirse el surgimiento de otra raza de monstruos maléficos.\n\nQuisiera estudiar los cerebros de los sátiros y los nagas. Quizás consiga averiguar qué destino espera a los elfos. Puedes encontrar sátiros y nagas en las Cavernas Anegadas de la costa de Vallefresno.\n\nEs un largo viaje, <nombre>.", progress = "Cuando tenga suficientes muestras, podré empezar a trabajar de verdad. Con los bulbos raquídeos corruptos de los sátiros y los nagass de las Cavernas Anegadas podré encontrar la correlación entre las mutaciones.\n\nQuizás, una vez identificada, podré impedir que la deformidad y el mal arraiguen más en los elfos de sangre.", completion = "Has viajado lejos y sin duda, habrás afrontado grandes peligros.\n\nTus acciones son desinteresadas; &lt;nombre&gt;; me ayudas con mi trabajo por el bien de todo Azeroth. Y te doy las gracias por todo lo que has hecho.\n\nAhora, si me perdonas, tengo mucho que hacer...", rewards = "Podrás elegir una de estas recompensas:\n\nBroches de escarabajosManteo de prelación\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBroches de escarabajosManteo de prelación"},
[1276] = {title = "El escudo negro", objective = "Ve a Cima del Trueno y enseña el escudo de hierro ennegrecido a Mosarn.", description = "¡Ah, ya lo recuerdo! Mosarn es un respetado herrero de Cima del Trueno. Sabía que había oído ese nombre antes...\n\nComo agradecimiento, el Anillo de la Tierra me ha recompensado con una armadura hecha por Mosarn. Es un buen trabajo.\n\nQuizás él pueda decirte más sobre el escudo que forjó.\n\nNo es gran cosa, pero como no conseguimos nada de Reethe, es todo lo que tenemos.", progress = "Hola, <clase>, ¿puedo ayudarte en algo?", completion = "Sí, tienes mucha razón. Este escudo lo hice yo.\n\nPero me temo que no puedo decirte mucho más, &lt;nombre&gt;. Forjo tantos escudos y armaduras diferentes que no puedo seguirles la pista a todos.", rewards = "Recibirás:\n\n"},
[1281] = {title = "La canción de Jim <CHANGE TO GOSSIP>", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1282] = {title = "Lo llaman Jim Sonrisas", objective = "Ve a Theramore y habla con el capitán Garran Vimes.", description = "El pobre Jim me da mucha pena. Antes no era así. Llevaba una posada en el pantano. Un lugar estupendo.\n\nPero no quiero hablar de esto. Si tienes que hablar de ello, hazlo con el capitán Vimes.", progress = nil, completion = "James Hyal es su auténtico nombre. Abrió una posada en el límite de Los Baldíos. Quería crear un lugar agradable en el que los viajeros pudieran descansar...\n\nPuede que hiciera todo demasiado a lo grande, pero el caso es que la posada fue muy bien durante un tiempo. Y entonces perdimos el control de los caminos del pantano y llegaron los ogros.\n\nJames era demasiado cabezota para dejar su sueño atrás y buscar la seguridad de nuestros muros y... la Posada Reposo Umbrío fue arrasada por un incendio. Estamos investigando el suceso, pero hasta ahora no hemos tenido mucha suerte. Si quieres ayudarnos, ve a ver si encuentras algo entre los escombros.", rewards = "Recibirás:\n\n"},
[1283] = {title = "El incendio de Reposo Umbrío <CHANGE TO GOSSIP>", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1284] = {title = "Huellas pezuñales sospechosas", objective = "Informa al capitán Garran Vimes de Theramore sobre las huellas sospechosas de la Posada Reposo Umbrío.", description = "Hay hileras de huellas que se alejan del esqueleto, aún humeante, de la Posada Reposo Umbrío. Hay unas pocas impresiones visibles y algunas hileras de huellas casi imperceptibles en el barro blando del pantano.", progress = nil, completion = "Huellas pezuñales, ¿eh? Es verdad que los centauros están desplegando una actividad extraordinaria en las tierras cercanas al Marjal Revolcafango. En cortas excursiones a Los Baldíos, encontramos sus campamentos y sus propiedades, estaban por toda la zona.\n\nEnviaré a Falgran Hastil, uno de nuestros rastreadores, a ver si puede averiguar a dónde conducen las huellas.", rewards = "Recibirás:\n\n"},
[1285] = {title = "Los hombres de Daelin", objective = "Lleva la información sobre Reethe al capitán Garran Vimes a Theramore.", description = "Reddon... Redpath... Ah, aquí está. Teniente Paval Reethe.\n\nSe unió a la Guardia de Theramore junto con los reclutas que llegaron del mar con Lady Valiente. Se alistó con los marinos de Kul Tiras a la edad de dieciséis años. Servicio distinguido, ascensos...\n\nConsta como desaparecido.", progress = nil, completion = "¿Desaparecido? Eso, dicho por el tesorero, quiere decir que es un traidor.\n\nSin duda es uno de esos estúpidos que traicionó a los suyos, cegado por su lealtad al almirante Daelin. ¿No es una deliciosa ironía? Los desertores nos acusan de traicionar a la Alianza.", rewards = "Recibirás:\n\n"},
[1286] = {title = "Los desertores", objective = "Consigue información sobre Reethe hablando con Balos Jacken y los Desertores del Marjal Revolcafango.", description = "Mantener nuestra presencia en el pantano ha sido una lucha constante. Pero con la fuerza de nuestra determinación, hemos conseguido mantener varias atalayas defensivas en la zona.\n\nLos últimos informes que he recibido de los rastreadores indican que hay un grupo de desertores en una de nuestras torres abandonadas en Punta Perdida, al sureste de la posada.\n\nEl líder de los desertores es un hombre llamado Jacken. Ve a ver si consigues algo de información de él.\n\nPero ten cuidado, <nombre>. Será duro de pelar.", progress = nil, completion = "¿Te envía Vimes? Claro, no podía hacer este trabajo sucio él mismo, ¿verdad?", rewards = "Recibirás:\n\n"},
[1287] = {title = "Los desertores", objective = "Ve a Theramore y habla con el capitán Garran Vimes sobre Reethe.", description = "¿Reethe? ¿Ese cobarde?\n\nLo expulsamos del campamento porque insistía en robar los suministros de los ogros del Poblado Murohelecho. Y no somos tantos como para afrontar a esos brutos inconscientes.\n\nCruzó la línea demasiadas veces, así que dejamos que se las apañara solito.\n\nBien, yo te he ayudado... ¿Qué tal si ahora nos ayudas tú olvidando que nos has visto?", progress = nil, completion = "Enviaré al teniente Caldwell a tratar con los desertores en Punta Perdida. Además, le ordenaré que encuentre a Reethe. El pantano no es tan grande, podrá encontrar su rastro.", rewards = "Recibirás:\n\n"},
[1288] = {title = "El informe de Vimes", objective = "Lleva el informe de Vimes a Lady Jaina Valiente a Theramore.", description = "Has hecho un buen trabajo, <nombre>, lo mejor que he visto en <un/una> amateur.\n\nLady Valiente me pidió que la mantuviera al corriente de esta delicada situación pero hace tiempo que no informo.\n\nIba a enviarle mi informe más tarde, pero creo que sería mejor que fueras tú quien hablara con ella. Probablemente podrás contestar mejor a sus preguntas que yo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1301] = {title = "James Hyal", objective = "Ve al Puerto de Menethil y habla con Vincent Hyal.", description = "Antes de trabajar aquí, en El Ermitaño Azul, me formó uno de los mejores chefs y posaderos que he conocido en mi vida. Su nombre era James Hyal. Todo lo que tengo se lo debo a él. Gracias a él he llegado hasta aquí.\n\nMe pregunto cómo le va... Su hermano Vincent y él fueron a trabajar a una posada del Puerto de Menethil, pero no sé nada de él desde hace un tiempo.\n\nSi alguna vez te acercas por allí, ¿podrías saludarlo de mi parte?", progress = nil, completion = "¿James? Oh, se fue hace algún tiempo. \n\nSupongo que sintió una nueva oleada de patriotismo. No es difícil entender por qué lo hizo. Con tanta locura en Lordaeron, muchos fueron los que se unieron a Jaina Valiente cuando cruzó el mar.", rewards = "Recibirás:\n\n"},
[1302] = {title = "James Hyal", objective = "Ve a Theramore y habla con el dependiente Lendry.", description = "Mi hermano James cruzó el mar con Lady Valiente. Lo último que oí es que la lucha había cesado en la zona y que James había abierto una posada en algún lugar del continente. Esté donde esté, seguro que el negocio le va bien. Era un cocinero estupendo.\n\nHace tiempo que no sé nada de él... estará ocupado con su posada. Seguro que el secretario de Theramore puede decirte algo más.", progress = nil, completion = "James Hyal, James Hyal... ¿Por qué me suena tan familiar?\n\n¡Claro! Por el incidente de la Posada Reposo Umbrío. Si tienes información relacionada con lo que ocurrió allí, deberías comunicársela al capitán Vimes cuanto antes. Es él quien lleva la investigación.", rewards = "Recibirás:\n\n"},
[1318] = {title = "Asunto Gordok sin finiquitar", objective = "Encuentra el guante del Poder de Gordok y llévaselo al capitán Kromcrush en La Masacre.\n\nSegún Kromcrush, la \"antigua historia\" cuenta que Tortheldrin, un elfo \"espeluznante\" que decía ser un príncipe, se lo había robado a uno de los reyes Gordok.", description = "Así que tú ser Rey… ¡pues a lo mejor recuperar artefacto Gordok de asqueroso elfo, él ir de ““Príncipe””! Rey anterior fracasar, como resto reyes antes Tortheldrin, asqueroso elfo robar rey Gordok mucho tiempo!\n\nArtefacto llamarse Guante del Poder de Gordok. Historia muy, muy vieja decir solo rey verdadero de Gordok poder recuperar. Historia muy, muy vieja también decir asqueroso elfo aún estar cerca, tú encontrar, ¡tú llevar poderoso guante siempre jamás! ¡Ea, yo enseñar tú!Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nManoplas de GordokGuanteletes de GordokGuantes de GordokMitones de Gordok\n\nTambién recibirás:\n\nManoplas de GordokGuanteletes de GordokGuantes de GordokMitones de Gordok"},
[1319] = {title = "El escudo negro", objective = "Ve a Theramore y enseña el escudo de hierro ennegrecido a Caz Dosdientes.", description = "Siento no ser de más ayuda, <nombre>, pero puedo remitirte a alguien que podrá serlo. Caz Dosdientes, uno de nuestros mejores herreros. Trabaja en la forja de la herrería.\n\nLlévale el escudo. Espero que pueda decirte más.", progress = "¿Quieres enseñarme algo?", completion = "Vaya, esa pieza de metal que tienes ahí es interesante. Es evidente que no es obra de ninguno de los herreros que conozco a este lado del mar. Y desde luego, tampoco es obra mía.", rewards = "Recibirás:\n\n"},
[1320] = {title = "El escudo negro", objective = "Ve a Theramore y habla con el capitán Garran Vimes.", description = "Ah, casi se me escapa esto... La tira de cuero del reverso ha debido de quemarse... pero parece que se hizo para una persona de gran tamaño. Creo que para un tauren, de hecho.\n\nNo puedo decirte nada más. Di al capitán Vimes que siento no poder ser de más ayuda.", progress = nil, completion = "Esa información no nos ayuda gran cosa, pero la tendremos en cuenta. Con suerte, alguna de las otras piezas de este misterio acabará encajando y nos proporcionará una idea más clara de lo que ocurrió.", rewards = "Recibirás:\n\n"},
[1321] = {title = "El escudo negro", objective = "Muestra el escudo de hierro ennegrecido a Do'gol del Poblado Murohelecho.", description = "¿Mmm? No veo nada fuera de lo normal en este escudo. Deberíamos enseñárselo a Do'gol, el herrero, a ver si sabe decirnos algo más, pero no me haría muchas ilusiones...", progress = "¿Tienes algo para mí, <clase>?", completion = "Es un buen escudo. Muy fuerte, pesado, bueno. Pero Do'gol no necesita. Yo hago escudos, no los compro, &lt;nombre&gt;.\n\nDeberías saberlo.", rewards = "Recibirás:\n\n"},
[1322] = {title = "El escudo negro", objective = "Consigue 6 glándulas de veneno ácido para Do'gol del Poblado Murohelecho.", description = "Mmmm, hay algo escrito en el borde del escudo... pero es difícil leerlo... está muy quemado.\n\nNecesito ácido para limpiar la superficie. Podría utilizar el veneno de las arañas Colmillonegro del pantano. Consígueme algunas de sus glándulas de veneno para que pueda limpiar esto.", progress = "¿Conseguiste las glándulas de las arañas?", completion = "No me gustan esas arañas que son tan grandes que no puedo sentarme en ellas para aplastarlas.\n\nGracias por conseguírmelas, &lt;nombre&gt;. Ahora las abriré y verteré su contenido en el escudo...", rewards = "Recibirás:\n\n"},
[1323] = {title = "El escudo negro", objective = "Ve al Poblado Murohelecho y habla con Krog.", description = "Ay, creo que he vertido demasiado ácido sobre el escudo. No importa. Froto un poco y...\n\nMm, es difícil de leer, pero parece ser un nombre... Mosarn. No he oído hablar de él. Quizás Krog lo conozca. Krog conoce a mucha gente.", progress = nil, completion = "¿Mosarn? Me suena de algo... Pero no sé de qué. Quizás me venga más tarde.\n\nProbablemente no es importante. Un escudo puede venir de cualquier parte y no significar nada...", rewards = "Recibirás:\n\n"},
[1324] = {title = "El diplomático desaparecido", objective = "Derrota al soldado Hendel en el Marjal Revolcafango.", description = "¡¿Qué?! ¡¿Vienes aquí acusándome de ser aliado de los Defias y crees que me voy a quedar callado mientras me insultas?! Algo me dice que llevas mucho tiempo planeando esto.", progress = nil, completion = "Te lo aseguro, todos estamos bastante disgustados por los sucesos recientes, pero permite que le llevemos a Theramore para interrogarle a ver si conseguimos información sobre el rey.\n\nSeguro que Lady Valiente querría agradecerte tu ayuda. Habla con ella cuando tengas un momento.", rewards = "Recibirás:\n\n"},
[1338] = {title = "Un pedido de Pico Tormenta", objective = "Lleva el pedido de Pico Tormenta a Furen Barbalarga a Ventormenta.", description = "Hay un forjador de armaduras enano cuyo trabajo me gusta mucho. Se llama Furen Barbalarga y es el mejor de su gremio. Necesito un escudo nuevo, y quiero que me lo haga él.\n\nSolo hay un problema... Furen vive muy al sur, en el Distrito de los Enanos de Ventormenta. ¡En tierra humana! No entiendo por qué vive ahí, tan lejos de Forjaz. ¡Será que los humanos le pagan una fortuna!\n\nSi te apetece viajar y ganar algo de dinero, ve a ver a Furen para hacer mi pedido de un escudo.", progress = "¿Una carta de tierras enanas? No sé quién me escribirá de tan al norte...\n\nA ver, déjame ese pedido.", completion = "Ah, un pedido de Pico Tormenta... Una de las mejores familias de enanos y también una de las más ricas.\n\nGracias, &lt;nombre&gt;. Esta tarde me pondré con el escudo.", rewards = "Recibirás:\n\n"},
[1339] = {title = "El montaraz de Picos Tormenta", objective = "Habla con el montaraz Pico Tormenta.", description = "Dicen que el montaraz Pico Tormenta busca alguien que le haga de mensajero. Alguien con ganas de viajar un poco. ¿Qué te parece? ¿Te ves capaz, <raza>?\n\nEntonces, ve a hablar con Pico Tormenta en la cima de la torre norte.", progress = nil, completion = "Pues sí, tengo un trabajito que te llevará lejos de Loch Modan.\n\n¡Ya tienes otra aventura servida!", rewards = "Recibirás:\n\n"},
[1358] = {title = "Muestra para Helbrim", objective = "Lleva la muestra de corazón de lobo al boticario Helbrim en Los Baldíos.", description = "Un colega de Kalimdor, el boticario Helbrim, está investigando los efectos de las toxinas de Lordaeron en las bestias de Kalimdor. Estaría muy interesado en una muestra de los corazones de lobo que Renferrel me envió.\n\nHelbrim está en Los Baldíos, en una aldea conocida como El Cruce. Para llegar a él, deberás viajar en zepelín hasta la capital orca de Orgrimmar y, a continuación, dirigirte a pie hacia Durotar. Sigue en dirección oeste por Los Baldíos y pronto llegarás a la aldea El Cruce.", progress = "¿Tienes un paquete para mí?", completion = "Ah, muy bien. Si los informes iniciales son ciertos, la toxicidad de estos corazones podría llevarnos a progresar en nuestro conocimiento sobre venenos.\n\nTe agradezco mucho la entrega, &lt;nombre&gt;. Te mereces tu recompensa.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de cerveceroManteo de caída larga\n\nTambién recibirás:\n\nGuantes de cerveceroManteo de caída larga"},
[1359] = {title = "Una entrega de Zinge", objective = "Lleva las muestras de corazón de lobo al boticario Zinge en Entrañas.", description = "Los corazones de lobo que conseguiste, poseen una calidad nunca vista antes en especímenes similares. Tienen tal grado de toxicidad que deberían ser mortales incluso para los lobos en los que laten esos corazones.\n\nEs bastante impresionante, y debemos estudiarlo en profundidad. He guardado algunas muestras de los corazones. Llévaselas a mi colega, la boticaria Zinge. La encontrarás en nuestro puesto de mando, en Entrañas.", progress = "¿Tienes algo para mí?", completion = "Ah, sí, las muestras de corazón de las que habló Renferrel. Me muero por experimentar con esto... encontrar nuevas formas de toxinas siempre resulta de gran interés para la Sociedad Real de Boticarios.", rewards = "Recibirás:\n\n"},
[1360] = {title = "Los tesoros reclamados", objective = "Recoge la posesión más preciada de Krom Brazorrecio de su cofre, que está en la Sala Comunal Norte de Uldaman, y llévasela a Forjaz.", description = "Tenía un trabajo estupendo en la excavación de Uldaman en las Tierras Inhóspitas; todo iba bien hasta que aparecieron los malditos trogg. Escapé por los pelos y con lo puesto. Me dejé mi posesión más preciada. Está en mi cofre, a salvo de los trogg, pero claro, está allí. \n\n¡Te agradecería mucho que me lo trajeras! Está al fondo de la Sala Comunal Norte; tiene mi nombre. Anda, ve.", progress = "Bueno, ¿conseguiste recuperar mi posesión de mi cofre?", completion = "¡Por fin! Te lo agradezco mucho.\n\nAlgunos enanos aman el brillo del metal, otros el resplandor de las gemas... ¡Pero un Brazorrecio tiene sus reliquias personales!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1361] = {title = "Regthar Portamorta", objective = "Habla con Regthar Portamorta.", description = "Un guerrero de la Horda, Regthar Portamorta, está reclutando agentes para una misión en la tierra de Desolace. Habla con él si quieres unirte a los voluntarios. Se encuentra en Los Baldíos, en una serie de búnkers que hay al oeste de El Cruce.\n\nY afila bien tus armas. Desolace está en guerra.", progress = nil, completion = "Sí, me encargo del reclutamiento. La misión nos llevará a Desolace, a luchar contra los centauros. Seré breve para que puedas empezar cuanto antes.", rewards = "Recibirás:\n\n"},
[1362] = {title = "Los Kolkar de Desolace", objective = "Habla con Felgur Doscortes. Está en el Campamento del Espíritu Errante, en Desolace.", description = "Los centauros Kolkar viven en Desolace y sus fuerzas se han extendido hasta Los Baldíos. Es un hecho. Por eso tenemos agentes de la Horda lidiando con ellos allí.\n\nPero mientras los centauros conserven su fuerza en Desolace, seguirán siendo una amenaza. Es hora de que nos deshagamos de esa amenaza.\n\nHabla con Felgur Twocuts. Está en el Campamento del Espíritu Errante, en Desolace. Fue enviado a reunir información sobre los centauros. Él sabrá cuál es la mejor manera de tratar con los Kolkar de Desolace.", progress = nil, completion = "¿Te envía Regthar? Entonces has venido a cazar centauros. Bien.\n\nEspero que hayas venido descansado. No sé cómo acabará nuestra campaña contra los centauros, pero sé que la lucha será larga.\n\nToma asiento y escucha.", rewards = "Recibirás:\n\n"},
[1363] = {title = "La orden de Mazen", objective = "Habla con el acólito Dellis en Ventormenta.", description = "Cuido de los libros que tenemos aquí, en la biblioteca de la Academia de Artes y Ciencias Arcanas y es mi deber asegurar que todos los tomos y pergaminos mágicos están registrados en todo momento. ¿Quién sabe qué podría ocurrir si alguno de estos textos encantados cayese en las manos equivocadas?\n\nLo que me recuerda... Mi ayudante, Dellis, cometió un terrible error la semana pasada. Es bastante competente en la biblioteca, pero un gran incompetente en cuanto sale por las puertas de Ventormenta. Sé <un héroe/una heroína> y ayuda a Dellis, ¿lo harás?", progress = nil, completion = "No fue culpa mía. En serio, no fue culpa mía.", rewards = "Recibirás:\n\n"},
[1364] = {title = "La orden de Mazen", objective = "Recupera los ensayos de Khadgar sobre convergencia dimensional y entrégaselos al vigilante Mahar Ba en el Castillo de Nethergarde.", description = "Las órdenes del maestro Mazen fueron bastante sencillas: llevar un ensayo muy importante al vigilante Mahar Ba en el Castillo de Nethergarde.\n\n¡Pero nunca antes había ido a Las Tierras Devastadas! ¡El viaje fue aterrador!\n\nCuando llegué al Pantano de las Penas, me equivoqué de camino y acabé en el medio de horribles criaturas en forma de planta. Empezaron a acercarse como si fueran a atacar, así que salí corriendo. Pero, al huir, se me cayó el ensayo. Seguro que lo cogió una de las bestias.\n\n¿Podrías recuperarlo y entregárselo a Mahar Ba?", progress = "¿Qué te trae por el Castillo de Nethergarde, <raza>?", completion = "¡Ah, estupendo!\n\nDurante un tiempo temí que el maestro Mazen de la Academia de Artes y Ciencias Arcanas hubiese olvidado mi pedido de los ensayos de Khadgar sobre convergencia dimensional.\n\nDebe de tener muchísimas peticiones teniendo en cuenta los extraños sucesos que están teniendo lugar.\n\nAhora, si me disculpas, tengo que comenzar con mi investigación. Llegar a comprender por fin a alguien tan grandioso como Khadgar me excita en gran medida.", rewards = "Podrás elegir una de estas recompensas:\n\nFajín de maestroBotas del placer errante\n\nTambién recibirás:\n\nFajín de maestroBotas del placer errante"},
[1365] = {title = "Khan Dez'hepah", objective = "Lleva la cabeza del khan Dez'hepah a Felgur Doscortes a Desolace.", description = "Los Kolkar no son los únicos centauros de Desolace. En sus áridas llanuras hay muchos otros clanes, combatiendo y matándose entre ellos. Y eso es bueno. Son fuertes y brutales, nos conviene que luchen entre sí. Si los centauros se unieran contra nosotros, serían un fiero enemigo.\n\nDebemos encargarnos de ellos antes de que unan sus fuerzas.\n\nTu primera tarea será causar un problema al clan Kolkar. Matarás a su líder, el khan Dez'hepah.\n\nEl Poblado Kolkar está al noreste de aquí. Ve. Y que la suerte te acompañe.", progress = "<nombre>, ¿has derrotado al khan Dez'hepah?", completion = "Bien hecho. Esa rápida acción contra los Kolkar es una demostración efectiva de nuestra fuerza, y eso es precisamente lo que mejor entienden los centauros. \n\nLa muerte del khan Kolkar no solo llevará confusión a su clan, también desconcertará a los demás.", rewards = "Recibirás:\n\n"},
[1366] = {title = "Colección de orejas", objective = "Lleva 15 orejas de centauro a Felgur Doscortes a Desolace.", description = "Ahora que has asustado a los clanes de centauros, es hora de sangrarlos. Cuando viajes por Desolace, caza centauros. De cualquier clan, no me importa. Pero recolecta orejas. Una horda de orejas.\n\nReduciendo su número reducirás su determinación y borrarás de su cabeza cualquier plan de expansión.\n\nBuena caza, <nombre>.", progress = "¿Cómo va tu colección de orejas, <nombre>? ¿Está grandota?", completion = "A estas alturas, los centauros deben de conocer tu nombre. Sé que lo conocen y lo respetan, y que incluso lo temen. Y de la misma forma, conocen y temen el estandarte que portas. \n\nEl estandarte de la Horda.", rewards = "Podrás elegir una de estas recompensas:\n\nFajín lilaManoplas con abrazaderas\n\nTambién recibirás:\n\nFajín lilaManoplas con abrazaderas"},
[1367] = {title = "La alianza con los Magram", objective = "Gana un nivel de reputación de amistoso con los Magram y luego habla con Warug.", description = "Para derrotar a los centauros debemos usar contra ellos su odio natural. Nos aliaremos con uno de los clanes y, a través de ese clan, aprenderemos a derrotar a los demás.\n\nSi eliges aliarte con los Magram, mata a sus enemigos, los Gelkis. Así te ganarás el respeto de los Magram. Cuando creas que tienes su respeto, habla con el portavoz de los Magram, Warug. Está en las afueras del Poblado Magram, al sureste de Desolace.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1368] = {title = "La alianza con los Gelkis", objective = "Gana un nivel de reputación de amistoso con los Gelkis y luego habla con Uthek la Sabia.", description = "Para derrotar a los centauros debemos usar contra ellos su odio natural. Nos aliaremos con uno de los clanes y a través de ese clan, aprenderemos a derrotar a los demás.\n\nSi eliges aliarte con los Gelkis, mata a sus enemigos, los Magram. Cuando te hayas ganado el respeto de los Gelkis, habla con su chamán, Uthek la sabia. Está en las afueras del Poblado Gelkis, al suroeste de Desolace.", progress = nil, completion = "La tierra me habla. Y me dice que has matado muchos Magram. Y eso está bien.\n\nSi eres amigo de los Gelkis, escucha. Tengo dos misiones más para ti.", rewards = "Recibirás:\n\n"},
[1369] = {title = "Lágrimas rotas", objective = "Mantén tu reputación con los Magram y lleva 3 lágrimas rotas a Warug al Poblado Magram.", description = "Los Gelkis creen que nuestra madre, Theradras, nos protege. Son estúpidos. Nadie protege a un centauro. ¡Los Magram no necesitamos protección! ¡Somos los más fuertes!\n\nDemuestra a los Gelkis lo estúpidos que son. Ve a la cueva que hay en su poblado. Allí esconden unos cristales, las lágrimas de Theradras. Destruye las lágrimas y tráeme los fragmentos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1370] = {title = "¡A robar provisiones!", objective = "Lleva 6 bolsas de Crudely Dried Meat a Uthek la Sabia al Poblado Gelkis.", description = "Cuando no estamos combatiendo, cazamos para conseguir carne. La carne nos hace fuertes. Y un estómago satisfecho combate mejor que uno vacío.\n\nLo mismo ocurre con los Magram. Almacenan carne para poder combatir más tiempo seguido sin tener que ir a cazar. Si asaltas su poblado del este y les robas su reserva de carne, les fallarán las fuerzas.", progress = "¡Vuelve cuando hayas demostrado a los Gelkis tu valía!", completion = "Bien. ¡Ya estoy oyendo cómo les rugen las tripas a esos Magram!\n\nDaré esta carne a los Gelkis. Podremos combatir sin descanso muchos días.\n\nLo que has hecho es bueno para los Gelkis, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nEstrella de plataFlecha de plumasBala explosiva\n\nTambién recibirás:\n\nEstrella de plataFlecha de plumasBala explosiva"},
[1371] = {title = "Un cacharro para Warug", objective = "Mantén tu reputación con los Magram y lleva 1 muñeco-diana avanzada a Warug al Poblado Magram de Desolace.", description = "Hace mucho tiempo, llegó un pequeño gnomo a Desolace. Tenía caballos y una carreta y en la carreta llevaba cosas muy extrañas. Cosas divertidas. Cosas que se quemaban con humo de diferentes colores. Cosas que se movían aunque no estaban vivas. Yo maté al pequeño gnomo, pero antes de eso... me hizo reír.\n\nY me quedé lo que más me gustó de su carreta. Era un humano hecho de madera que me saludaba hasta que un día le pegué y se rompió.\n\nQuiero otro. ¡Consígueme uno nuevo!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1372] = {title = "El suero de la verdad", objective = "Ve al Refugio de los Mendigos y habla con el boticario Faustin.", description = "Nuestra fuerza expedicionaria fue enviada a bajo Azeroth a instancias de Varimathras.\n\nMientras rastreábamos el terreno, vi a un infiltrador humano espiando el Refugio de los Mendigos. Antes de que pudiera aprehenderlo, algunos de nuestros... hermanos orcos... lo capturaron y se lo llevaron a Rocal, su fortaleza en el Pantano de las Penas.\n\nQuiero... ayudar... a nuestros aliados orcos. No vieron nuestro campamento, pero aún así, quiero echarles una mano.\n\nColabora con Faustin para preparar... un suero de la verdad para nuestro amigo de Rocal.", progress = nil, completion = "Zraedus me estaba hablando de ese entrometido infiltrador justo antes de que llegaras.\n\nEs un problema bastante serio. Lo último que necesitamos es un metomentodo husmeando por el Refugio de los Mendigos.\n\nEstoy seguro de que los orcos de Rocal estarán impacientes por escuchar lo que les tenga que contar nuestro amiguito. Y como son nuestros aliados, ejem, tenemos que ayudarles a conseguir cualquier información que se le pueda sacar al humano.\n\nColabora conmigo, &lt;nombre&gt; y conseguiremos llevar un suero de la verdad a Rocal.\n\n¡Por la Horda!\n\n¿Se dice así?", rewards = "Recibirás:\n\n"},
[1373] = {title = "Ongeku", objective = "Mantén tu reputación con los Gelkis y lleva 1 fragmento de draenetista a Uthek la Sabia al Poblado Gelkis de Desolace.", description = "Tienes más que demostrar. Tu siguiente cometido te llevará lejos así que, de momento, los Gelkis combatirán a los Magram sin tu ayuda.\n\nLa tierra susurra muchos secretos a quienes saben escuchar. Uno de los secretos que he oído tiene que ver con un cristal rojo. Se llama draenetista y es nuevo en nuestra tierra. Proviene de un lugar en el que las montañas vuelan y gobiernan los demonios.\n\nTráeme un fragmento de draenetista para que fortalezca mi magia. Encontrarás un fragmento en un lugar llamado Pantano de las Penas. Lo controla Ongeku, uno de los Perdidos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1374] = {title = "Khan Jehn", objective = "Lleva la cabeza del khan Jehn a Uthek la Sabia al Poblado Gelkis de Desolace.", description = "La tierra me habla y me dice que los Magram se están debilitando. ¡Es el momento de matar a su Khan!\n\n¡Tráeme la cabeza de Jehn, el khan Magram!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1375] = {title = "Khan Shaka", objective = "Mantén tu reputación con los Magram y lleva la cabeza del khan Shaka a Warug al Poblado Magram de Desolace.", description = "Tú ayudas a los Magram. Me gustas. Ahora, ¡mata al Khan de los Gelkis! ¡Quiero la cabeza del khan Shaka! ¡Quiero mirarle a la cara y reírme de él!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1380] = {title = "Khan Hratha", objective = "Mantén tu reputación con los Gelkis y lleva el fragmento de llave Maraudine a Uthek la Sabia al Poblado Gelkis.", description = "<nombre>, nos has ayudado a combatir a los Magram. ¡Ayúdanos ahora contra los Maraudine! Mata al jefe del clan, el khan Hratha.\n\nPara enfrentarte a él, toca el cuerno de guerra Maraudine. Está en la cima de un alto risco, en el Valle de las Lanzas.\n\nPero para tocar el cuerno, debes conseguir una boquilla. Es un objeto que solo poseen los centauros Maraudine de mayor confianza.\n\nCuando hayas tocado el cuerno, los centauros irán acudiendo a su llamada. Ve matándolos hasta que aparezca Hratha.\n\nEl khan Hratha lleva algo encima... un fragmento de una llave. Tráemelo...Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nCadena de maleante GelkisDedo de Uthek\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCadena de maleante GelkisDedo de Uthek"},
[1381] = {title = "Khan Hratha", objective = "Mantén tu reputación con los Magram y lleva el fragmento de llave Maraudine a Warug al Poblado Magram de Desolace.", description = "El khan Gelkis está muerto. ¡Y ahora caerán los Maraudine! ¡Mata a Hratha, el khan Maraudine!\n\nPara enfrentarte a él, toca el cuerno que hay en la cima de un risco del Valle de las Lanzas. Para utilizar el cuerno necesitas una boquilla. Y para conseguir la boquilla, tendrás que matar algunos centauros Maraudine. Ve matándolos hasta que encuentres uno que tenga una boquilla. Y entonces, úsala para soplar el cuerno.\n\nCuando lo hagas, los centauros Maraudine se dirigirán a la torre. Ve matándolos hasta que aparezca Hratha.\n\nEl khan Hratha tiene un fragmento de una llave. Tráemelo...Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de cazador MagramManta ceremonial centaura\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCinturón de cazador MagramManta ceremonial centaura"},
[1382] = {title = "Extraña alianza", objective = "Gana un nivel de reputación de amistoso con los Gelkis y luego habla con Uthek la Sabia.", description = "Tenemos suerte de que los clanes de centauros combatan entre sí porque, si se unieran, serían imparables. Sí, tenemos suerte. Y deberíamos aprovechar esta ventaja.\n\nEl clan Gelkis está combatiendo una sangrienta guerra contra el clan Magram. Si atacamos a los Magram, quizás podamos forjar una alianza con los Gelkis.\n\nHabla con Uthek la Sabia. La encontrarás a las afueras del campamento Gelkis, en el Desolace suroccidental. Con su ayuda, quizás consigamos sellar esa alianza.", progress = "¡Largo de aquí! Los Gelkis no te quieren aquí. ¡¡Márchate!!\n\nVuelve cuando hayas matado a muchos Magram. Entonces hablaré contigo.", completion = "Tu nombre se susurra entre los Gelkis. Tienes mucha sangre Magram en las manos. Y eso está bien.\n\nTambién estará bien que hablemos. Hablemos de qué puedes hacer por los Gelkis y de qué podemos hacer nosotros por ti.", rewards = "Recibirás:\n\n"},
[1383] = {title = "El suero de la verdad", objective = "El boticario Faustin del Refugio de los Mendigos necesita 5 corazones de pantera sombría, hongos de Señor del Lodo y un tumor de zancudo de las profundidades.", description = "Conozco el suero perfecto. Trata la verdad justo como la verdad debe ser tratada.\n\nPara preparar la pócima necesitaré varios corazones de pantera de las Sombras del pantano. También necesito unos hongos encantados del Lord fangoso que vive allí. Estoy seguro de que alguien con tu habilidad, <nombre>, podrá convencerle de que le dé algunos hongos.\n\nPero lo más duro será localizar un tumor de zancudo de las profundidades de Desolace. En raras ocasiones, los gigantes enferman y desarrollan tumores.\n\nHala, ¡en marcha!Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nCapa de ApestadoGran maza de riscoleño\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCapa de ApestadoGran maza de riscoleño"},
[1384] = {title = "Asalta a los Kolkar", objective = "Mantén tu reputación con los Gelkis y lleva 10 talismanes primitivos a Uthek la Sabia al Poblado Gelkis de Desolace.", description = "Los centauros Kolkar creen que su magia es poderosa. ¡Pero no saben nada de magia! ¡No reconocerían la magia ni aunque llevara un cartel!\n\nTienes que demostrarles que su magia es débil. Ve al noreste y mata a los Kolkar. Coge sus talismanes. Tráemelos. ¡Yo los tiraré al fuego y me echaré unas risas!", progress = "¿Tienes los talismanes? ¿Demostraste a los Kolkar que son débiles?", completion = "Has hecho una buena cosa para los Gelkis. Los Kolkar son débiles y morirán. Su magia es patética; no les salvará. ¡Les enseñaste esto y su miedo los hará aún más débiles!\n\nY en nuestras tierras... los débiles mueren pronto.", rewards = "Recibirás:\n\n"},
[1385] = {title = "Políticas brutales", objective = "Gana un nivel de reputación de amistoso con los Magram y luego habla con Warug.", description = "Desolace es una tierra en guerra. Los centauros están en guerra entre ellos y creemos que quien salga victorioso podría convertirse en una amenaza para nuestras fortalezas. Y eso no podemos permitirlo.\n\nTenemos que averiguar cómo vencer a los centauros. Y para eso, hemos de aliarnos con uno de los clanes. Los centauros Magram están en guerra con los Gelkis. Si combates contra los Gelkis, quizás los Magram tomen en consideración la creación de una alianza.\n\nGánate el favor de los Magram y luego habla con Warug. Lo encontrarás en el Poblado Magram, al sureste de Desolace.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1386] = {title = "Ataca a los Kolkar", objective = "Mantén tu reputación con los Magram. Mata a 12 centauros Kolkar, 12 rastreadores Kolkar y 6 destructores Kolkar y luego busca a Warug en el Poblado Magram en Desolace.", description = "Los Kolkar creen que son fuertes, ¡pero nosotros nos reímos de ellos! ¡No reímos y los matamos! Tú también tienes que matarlos. Demuéstrame que puedes matar a un Kolkar como nosotros. Ve al norte ¡y mátalos a todos!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1387] = {title = "Colección de orejas", objective = "Lleva 15 orejas de centauro al cabo Melkins que está en Punta de Nijel, en Desolace.", description = "La raza de los centauros está dividida en muchos clanes. Cada uno tiene su propio jefe y sus objetivos. Pero todos tienen algo en común: su crueldad.\n\nSi queremos conocer la paz, debemos eliminar a los centauros. Y precisamente, eso es lo que quiero que hagas.\n\nMientras viajes por Desolace, da caza a los centauros y córtales las orejas. Tráeme muchas, muchas orejas.\n\nHazlo por la Alianza, <nombre>, y estaremos un paso más cerca del final de la guerra.", progress = "¿Tienes algún informe, <nombre>?", completion = "Ah, las orejas. Bien hecho.\n\nTu lealtad a la Alianza es incuestionable, &lt;nombre&gt;. La sangre que derramaste en Desolace salvará muchas vidas, te lo aseguro.", rewards = "Recibirás:\n\nAnillo de Calma"},
[1388] = {title = "El suero de la verdad", objective = "Dale el suero de la verdad de Faustin al mortacechador Zraedus en el Refugio de los Mendigos.", description = "Todo procede según el plan, <nombre>. Quiero que le entregues este suero de la verdad a Zraedus el mortacechador. Él te indicará cómo administrarlo correctamente.\n\n¡Ja, ja!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1389] = {title = "Cristales draenetistas", objective = "Llévale 6 cristales draenetistas a Magtoor que está en El Malecón del Pantano de las Penas.", description = "Los Perdidos que habitan en Retiro Fangoso son como nosotros en apariencia pero, en realidad, son monstruos que enloquecieron debido a los rigores sufridos durante nuestro viaje a Azeroth. En su día hermanos nuestros, viven ahora en Retiro Fangoso hacia el este, donde atrapan o matan a todo aquel que vague por la zona.\n\nAdemás, nos han robado algo sagrado para nosotros: los cristales draenetistas.\n\nCarecemos de la fuerza necesaria para aventurarnos en Retiro Fangoso y recuperar los cristales que nos han sido robados de entre sus cabañas y hogueras, pero sin ellos... quizás perdamos nosotros también nuestro camino.", progress = "¿Tienes los cristales?", completion = "Gracias, &lt;nombre&gt;. Estamos en deuda contigo.", rewards = "Recibirás:\n\n"},
[1391] = {title = "El suero de la verdad", objective = "Lleva el brebaje de Zraedus al infiltrador Marksen a Rocal.", description = "Tenemos que actuar con una cierta discreción. Después de todo, no puedes entrar ahí tal cual y esperar que ese loco se tome el suero de Faustin sin más, ¿no te parece?\n\nPero si hay una cosa a la que los humanos no se resisten es... la bebida. Voy a mezclar el suero de Faustin con esta cerveza. Te garantizo que ese infeliz se beberá la cerveza sin pensárselo dos veces.\n\nCorre hasta Rocal. Recuerda que está en el Pantano de las Penas. Y mantén nuestra misión en secreto...Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1392] = {title = "Noboru el Garrote", objective = "Llévale el garrote de Noboru al líder de El Malecón del Pantano de las Penas.", description = "Noboru ha asediado a los draenei de El Malecón durante meses y su garrote es un símbolo de la brutalidad sufrida a manos de Los Perdidos.", progress = "Saludos. Bienvenido a El Malecón.", completion = "¿Has conseguido el arma del terrorífico Noboru? ¿Eso quiere decir que está muerto?\n\nEste es un día de júbilo para El Malecón. Noboru y los suyos representaban una seria amenaza para nosotros pues vagaban por los pantanos matando a todos los draenei que se cruzaban en su camino. Sin duda, ponía en peligro nuestra existencia pero nosotros no teníamos la fuerza suficiente para hacerle frente.\n\nSomos afortunados por tenerte como amigo, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[1393] = {title = "La huida de Galen", objective = "Pon a salvo a Galen, y después encuentra su campamento en el Pantano de las Penas y abre su caja fuerte.", description = "Por favor, necesito ayuda. ¡Estas criaturas van a devorarme!\n\n¡Sácame de aquí y te recompensaré con un gran tesoro! Está en mi campamento, en el pantano situado al norte de Rocal, pero está guardado en una caja fuerte que solo yo puedo abrir.\n\nSi me ayudas a escapar, te enseñaré cómo se hace.", progress = nil, completion = "El ojo de la cerradura de esta caja fuerte está llena de resina negra y resulta imposible abrirla.\n\nPero Galen te dijo que había un pasador escondido en la base de la caja fuerte... \n\nTras una búsqueda rápida, localizas el pasador. Lo levantas y la caja fuerte se abre...", rewards = "Recibirás:\n\nRodela visionaria"},
[1394] = {title = "La recta final", objective = "Habla con Dorn Acechaprados en Las Mil Agujas.", description = "Lo has hecho muy bien, <nombre>. Has superado mi prueba y todas las anteriores. Vuelve a ver a Dorn a Las Mil Agujas. Podrá ver los cambios que has experimentado y comprobará que tu mente, tu cuerpo y tu espíritu son lo suficientemente fuertes, por lo que te recompensará por tus esfuerzos.\n\nPermanece fiel al equilibrio que has demostrado a lo largo de estas pruebas, <nombre>. Dorn te pondrá a prueba más adelante si así lo haces.", progress = nil, completion = "Ah, &lt;nombre&gt;. Hacía tiempo que no venías por aquí. Parece que hayan pasado muchas lunas desde que diste el primer salto que te inició en tu camino.\n\nY ahora regresas a mí más fuerte de lo que una vez fuiste... y más sabio. Eso me complace y creo que deberías recibir una recompensa por haber superado mis pruebas.", rewards = "Podrás elegir una de estas recompensas:\n\nMartillo tormenta de vientoLlama de danza\n\nTambién recibirás:\n\nMartillo tormenta de vientoLlama de danza"},
[1395] = {title = "Suministros para Nethergarde", objective = "Llévale caja de piedras de potencia al intendente Lungertz antes de que caduquen.", description = "Hay que llevar esta caja de piedras de potencia al Castillo de Nethergarde, en Las Tierras Devastadas. No conservarán la potencia mucho tiempo, así que no podemos esperar a la siguiente caravana.\n\nTienes que llevarlas, y debes hacerlo si perder ni un minuto.\n\nLlévaselas al intendente Lungertz en el Castillo de Nethergarde. Para llegar al castillo, debes dirigirte al Paso de la Muerte, luego ir hacia el este a través del Pantano de las Penas, y por último cruzar Las Tierras Devastadas.\n\nTen mucha precaución, <nombre>. Pasarás por tierras muy peligrosas y no puedes fracasar.", progress = "¡Hola! No hagas caso del revuelo, es lo normal en el Castillo de Nethergarde.\n\n¿Tienes algo para mí?", completion = "¡Ah, las piedras de potencia! ¡Los magos se alegrarán de verlas!\n\nMuchas gracias, &lt;nombre&gt;. Sé que tu viaje desde Villa Oscura no fue fácil.", rewards = "Recibirás:\n\n"},
[1396] = {title = "Contra la fauna feroz", objective = "Mata a 8 crocoliscos Denteserra jóvenes, 10 girapenas y 10 jaguares del pantano, y después ve a ver al vigilante Biggs al Pantano de las Penas.", description = "Mi caravana iba destinada a la costa oriental pero no alcanzamos nuestro objetivo. Los Perdidos nos atacaron: mataron a los guardias y destrozaron los suministros.\n\nTan solo yo sobreviví a su ataque. He jurado proteger los suministros restantes pero hay criaturas salvajes vagando por aquí. ¡Por favor, ayúdame a defender mi campamento de las bestias!", progress = nil, completion = "Los sonidos de los animales ahora son débiles y distantes. Gracias, &lt;nombre&gt;. ¡Esperemos que no vuelvan!", rewards = "Recibirás:\n\n"},
[1398] = {title = "Madera a la deriva", objective = "Llévale 8 piezas de madera a la deriva desecada al vigilante Biggs en el Pantano de las Penas.", description = "Te debo mucho, <nombre>. En tu interior late un corazón noble. Y ahora tengo otra tarea para la que humildemente pido tu ayuda.\n\nSi he de llegar hasta Nethergarde, tendré que arreglar este carro. Y para ello... necesitaré madera. Madera fuerte y seca, no como la madera esponjosa de los árboles del pantano.\n\n<nombre>, ve hacia el este hasta llegar a la costa y busca madera que haya sido arrastrada por el mar y secada por el sol. Cuando la tengas, tráemela.", progress = "¿Encontraste la madera, <nombre>?", completion = "Esta madera es ligera pero muy resistente. Gracias, &lt;nombre&gt;. Es justo lo que necesito.", rewards = "Recibirás:\n\n"},
[1418] = {title = "Neeka Marca de Sangre", objective = "Habla con Neeka Marca de Sangre.", description = "Las Tierras Inhóspitas son escabrosas y están situadas cerca de las tierras de Loch Modan dominadas por la Alianza, por lo que la comunicación entre Rocal y la avanzada de Kargath de las Tierras Inhóspitas en ocasiones se corta.\n\nDemuestra tu valía, <nombre>. Viaja a las Tierras Inhóspitas y habla con Neeka Marca de Sangre, la jefa de la avanzada de Kargath. Habla con ella y regresa para contarme lo que averigües.", progress = nil, completion = "¿Así que Helgrum quiere un informe? Envié un avanzado a Rocal no hace ni tres días. ¿No cumplió su misión? Si mi informe no ha llegado a Rocal significa que así es.\n\nEsperemos que el siguiente corredor que envíe sea mejor o tenga más suerte.", rewards = "Recibirás:\n\n"},
[1419] = {title = "Ladrones coyote", objective = "Llévale 30 mandíbulas de coyote a Neeka Marca de Sangre, que está en Kargath.", description = "¿Has estado muchos días en las Tierras Inhóspitas, <clase>? ¿Has sentido el calor que hace allí? ¿Te has topado con las bestias que vagan por sus áridas llanuras? Pues es hora de que lo hagas.\n\nLos individuos de coyote montés son muy fuertes. Fuertes y lo suficientemente audaces para acercarse con sigilo a Kargath y robar las provisiones de comida. ¡Ayuda a la avanzada a deshacerse de estos ladrones!\n\nCaza a los coyotes monteses, <nombre>, y tráeme sus mandíbulas. Si me traes bastantes quizás te crea cuando afirmas que conoces bien las Tierras Inhóspitas.", progress = "¿Tienes las mandíbulas?", completion = "¡Qué maravillosa colección de mandíbulas, &lt;nombre&gt;! Esos coyotes ya no destrozarán más nuestras provisiones en busca de comida.\n\nBuen trabajo. Los coyotes de las Tierras Inhóspitas no son una presa fácil. Tus actos demuestran que quizás seas capaz de sobrevivir aquí.", rewards = "Recibirás:\n\n"},
[1420] = {title = "Un informe para Helgrum", objective = "Llévale el informe de Neeka a Helgrum el Veloz, que está en Rocal.", description = "He redactado un nuevo informe para Helgrum, que está en Rocal, y quiero que se lo lleves.\n\nSi te consideras <un buen avanzado/una buena avanzada>, entonces no te pierdas por el camino ni dejes que te atrape el enemigo.", progress = "Has vuelto. ¿Tienes algún informe?", completion = "Gracias, &lt;nombre&gt;. En el informe de Neeka puedo ver que la Alianza también tiene problemas. Es posible, además, que tengamos enemigos comunes.", rewards = "Recibirás:\n\n"},
[1421] = {title = "La caravana perdida", objective = "Encuentra el cofre de la caravana en Retiro Fangoso, consigue los componentes de zahorí y llévaselos al vigilante Biggs.", description = "Yo perdí el sentido durante el ataque de Los Perdidos pero, antes de que mi vista se nublara, vi cómo uno de nuestros carros era conducido hasta Retiro Fangoso, la ciudad perdida situada al norte.\n\nUno de los cofres de la caravana que contenía componentes muy importantes iba en ese carro, unos componentes esenciales para que los zahoríes del Castillo de Nethergarde puedan continuar con sus estudios. Encuentra ese cofre y recupera los componentes, <nombre>. Consíguelos y tráemelos aquí.", progress = "¿Tienes los componentes de zahorí, <nombre>?", completion = "¡Ah, los has encontrado! Con ellos, los zahoríes de Nethergarde podrán continuar con su trabajo. No sé con exactitud lo que hacen pero tiene que ver con El Portal Oscuro de Las Tierras Devastadas y es crucial para la seguridad de la Alianza.", rewards = "Recibirás:\n\n"},
[1422] = {title = "La amenaza marítima", objective = "Habla con Katar en el Pantano de las Penas.", description = "Mientras me pongo a trabajar en tu arma, ¿por qué no hablas con Katar? Dijo que necesitaba ayuda con las patrullas por la playa y, sinceramente, preferiría verte alerta en lugar de que te emborracharas con algunos de los otros canallas con los que comparto este campamento.", progress = nil, completion = "Silencio un momento, &lt;clase&gt;. Incluso por encima del rugir del mar se pueden oír esos horribles gritos de los pescadores.", rewards = nil},
[1423] = {title = "Los suministros perdidos", objective = "Llévale los suministros perdidos al intendente Lungertz.", description = "Este barril es pequeño pero resistente. Sus juntas están selladas con una cera acre de color amarillo. En el fondo del barril están estampadas las palabras:\n\nINTENDENTE LUNGERTZ\n\nCASTILLO DE NETHERGARDE", progress = "¿Tienes algo para mí?", completion = "¡Ah! ¡Gracias, &lt;nombre&gt;! Se han perdido muchos suministros desde Villa Oscura hasta aquí. Siempre es bueno recuperar parte de ellos.", rewards = "Recibirás:\n\nCaja de suministros"},
[1424] = {title = "Charca de Lágrimas", objective = "Fel'zerul, en Rocal, quiere que consigas 10 artefactos Atal'ai.", description = "El Jefe de Guerra nos envió a investigar los extraños sucesos que están ocurriendo en El Templo de Atal'Hakkar.\n\nLos trols Atal'ai son una tribu de magos oscuros, famosos por practicar la magia prohibida. Te puedes imaginar lo sorprendidos que nos quedamos cuando llegamos y descubrimos que el templo estaba sumergido.\n\nAhora, los dragones verdes protegen el lugar, erigiéndose como protectores de Azeroth.\n\nNecesitamos descubrir qué ha pasado. Explora la Charca de Lágrimas y consigue artefactos Atal'ai. Con eso, podré empezar a unir las piezas del puzzle.", progress = "Nadie sabe qué tipo de oscuras prácticas sucedieron en El Templo de Atal'Hakkar. Pero sí sabemos que se emplearon poderosas fuerzas mágicas.\n\nDebemos descubrir todo lo que podamos sobre esa magia. Quizás los hechizos de los Atal'ai tengan algún uso para la Horda.\n\nCuando hayas conseguido suficientes artefactos Atal'ai de la Charca de Lágrimas, podré comenzar a entender lo que pretendían lograr los trols y qué puede haber salido tan horriblemente mal.", completion = "Tu diligencia para conseguir los artefactos Atal'ai es digna de elogio, &lt;clase&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1425] = {title = "Envío de suministros", objective = "Entrégale los suministros al intendente Lungertz del Castillo de Nethergarde.", description = "Mientras yo arreglo el carro, <nombre>, tengo una última tarea que encomendarte.\n\nHe empaquetado los suministros más importantes que se salvaron del ataque de los ogros. Llévalos hasta el Castillo de Nethergarde. El castillo está enclavado en las colinas del norte de Las Tierras Devastadas y su misión es vigilar El Portal Oscuro.\n\nPara llegar a Nethergarde, sigue el camino del este. Cuando se bifurque, ve en dirección sur hacia Las Tierras Devastadas y a continuación gira a la izquierda en el siguiente cruce. Sigue el camino, <nombre>, y ve con cuidado.", progress = "¿Tienes algo que tratar conmigo?", completion = "¿Qué? ¿Un paquete de la caravana de Biggs? Ya lo dábamos todo por perdidos...\n\nGracias, &lt;nombre&gt;. Con un poco de suerte contendrá el componente que tanto necesitamos, los cristales de Azora. Nos estamos quedando sin ellos...", rewards = "Recibirás:\n\n"},
[1426] = {title = "La amenaza marítima", objective = "Mata a 10 murlocs del pantano, 10 vomitatintas del pantano y 10 comecarnes del pantano, y después ve a ver a Katar al Pantano de las Penas.", description = "No siento aprecio por el mar ni por ninguna de las criaturas que he visto emerger de sus profundidades. Es una entidad poderosa pero ciega, a la vez cruel y brutal. Sería un placer para mí dar muerte a los hombres-pez de cien en cien si estuviera en mi mano pero he de conformarme con ver cómo caen ante aquellos que cumplen la voluntad de la Horda.\n\nLo cual nos lleva a ti, <clase>. ¿Cuántos serías capaz de matar? ¿Te atreverías a ponerte a prueba ante los más poderosos de su especie?", progress = nil, completion = "El mar semeja inmenso e implacable pero tú lo has teñido con su propia sangre. Los hombres-pez están sufriendo. Tras la devastación que has provocado, su insensatez hará que refuercen sus filas, y eso es bueno.\n\nHaz que sus gritos ahogados resuenen sin cesar. Disfrutaré cada vez que uno de ellos muera.\n\nRegresa a mí si deseas matar a más hombres-pez pues te recompensaré de nuevo con mucho gusto.", rewards = "Recibirás:\n\n"},
[1427] = {title = "La amenaza marítima", objective = "Habla con Tok'Kar en el Pantano de las Penas.", description = "Antes de tomarte un descanso, habla de nuevo con Tok'Kar. Durante tu ausencia creo que ha rematado algunas armas y te ofrecerá una de ellas. Harías bien en probar su fuerza contra los hombres-pez cuando sientas que estás <preparado/preparada>... pero solamente si deseas complacerme aún más.", progress = nil, completion = "¡Ah, &lt;nombre&gt;, has vuelto! ¡Qué bien! Ya he acabado de fabricar más armas para luchar contra los murlocs. Te ofrezco una para que puedas traernos más carne fresca de denteserras y con ello hacer feliz a Katar.", rewards = "Podrás elegir una de estas recompensas:\n\nFaca múrloc de Tok'karAzotador múrloc de Tok'karTrituradora múrloc de Tok'kar\n\nTambién recibirás:\n\nAzotador múrloc de Tok'karTrituradora múrloc de Tok'karFaca múrloc de Tok'kar"},
[1428] = {title = "La amenaza continua", objective = "Mata a 10 vomitatintas del pantano, 10 comecarnes del pantano y 10 oráculos del pantano, y después ve a ver a Katar al Pantano de las Penas.", description = "Me complace comprobar que tu odio por los hombres-pez crece con el mío.\n\nBúscalos a lo largo de la playa y cuando te hayas hartado de combatir, vuelve a verme. Entonces hablaremos de una recompensa por tu diligencia.", progress = nil, completion = "Bien hecho, &lt;clase&gt;. Quiero más de su sangre. ¡Acaba con todos los hombres-pez!", rewards = "Recibirás:\n\n"},
[1429] = {title = "El exiliado Atal'ai", objective = "Lleva los artefactos Atal'ai al exiliado Atal'ai en las Tierras del Interior.", description = "Mientras te enfrentabas a los peligros de la Charca de Lágrimas, uno de mis exploradores encontró un pergamino hecho de carne seca y escrito con sangre. Parece ser una sentencia judicial contra un sacerdote de la tribu Atal'ai.\n\nEste sacerdote fue condenado a un destino considerado como mucho peor que la muerte entre los Atal'ai.\n\nFue exiliado al norte de las Tierras del Interior, con los trols Secacorteza.\n\nGuardaré estos artefactos. Tienes que encontrar al exiliado y descubrir para qué se usan. Regresa cuando hayas completado tu viaje.", progress = "Eres muy valiente al haber venido aquí. Valiente o imbécil...", completion = "¿Me traes los artefactos de invocación de los Atal'ai? \n\nVeo que Jammal'an el Profeta lidera a los discípulos de la Cámara Sangrienta hacia su destino elegido. Me pregunto si Hakkar ha regresado al templo. ¿Han encontrado su destino?", rewards = "Recibirás:\n\n"},
[1430] = {title = "Carne fresca", objective = "Lleva 10 patas de reptadores monstruosos a Dar en el Pantano de las Penas.", description = "Entre mis clientes, hay una gran demanda de la suculenta carne de reptador, especialmente la carne de los reptadores monstruosos.\n\nSi planeas dirigirte a la costa, podría hacer que sacaras algún provecho de tu viaje, si me traes algunas patas para transportarlas a Orgrimmar.\n\nLa costa queda al este de aquí y no creo que tengas problemas para encontrar a las criaturas que reptan por las playas y bajo el mar.", progress = "Tengo un barril de vino esperando a que tengas éxito, <nombre>. Es normal que pruebe las mercancías antes de enviárselas a mis clientes.", completion = "Ah, esto será perfecto. Me muero por probar esto después de haberse cocinado lentamente en la hoguera. Deberías llevarte una muestra para tu viaje. ¡Qué delicia!", rewards = "Recibirás:\n\nPatas de rey reptador a la parrillaCinturón de cuero de jefe"},
[1431] = {title = "Las relaciones de la Alianza", objective = "Habla con Keldran en Orgrimmar.", description = "Guarda silencio, <clase>, y deja que te hable de algo que tiene mucha importancia para mi gente.\n\nSomos orgullosos y la guerra con los humanos ha impacientado incluso a Thrall. Verás, nuestro Jefe de Guerra es sabio y prudente y siempre piensa primero en su pueblo. También es diplomático y aprendió mucho de los humanos cuando estuvo con ellos.\n\nSi la historia te interesa, ve a ver a Keldran, que está cerca de la salida oeste de la ciudad.", progress = nil, completion = "Saludos, noble &lt;clase&gt;. Me llamo Keldran, estudioso de la magia y otras artes místicas.\n\nEl asunto por el que vienes a verme requiere sutileza, pero creo que podrás ayudarnos.\n\nSiéntate y escucha por qué necesitamos tu ayuda.\n\nTodo está relacionado con las apariencias, &lt;nombre&gt;, con cómo la Horda se ve a sí misma. Y cómo la ven los enemigos.", rewards = "Recibirás:\n\n"},
[1432] = {title = "Las relaciones de la Alianza", objective = "Habla con Takata Hojacero en Desolace.", description = "A medida que la Alianza avanza en Kalimdor, podría extenderse la errónea creencia de que la Horda ha pactado con la Legión Ardiente. El Jefe de Guerra no puede permitirlo y me ha autorizado a atajar estas creencias erróneas.\n\nTengo agentes en la región de Desolace, en el Campamento del Espíritu Errante que está al norte del Cementerio de Kodos. Para encontrarlo, ve hacia el sur desde La Vega Carbonizada, en Sierra Espolón. Cuando llegues, busca a Takata Hojacero.", progress = nil, completion = "Saludos, &lt;clase&gt;. Veo que traes noticias de Orgrimmar.\n\nLa tierra está ahogada por la infestación demoníaca. Los centauros se consideran dueños de la tierra, pero hay fuerzas que escapan a su control y pueden acabar aplastándolos.\n\nMe disgusta ayudar a los centauros aunque sea indirectamente, pero tengo que pensar en el bien de la Horda, por lo que hago lo que tengo que hacer como Jefe de Guerra, incluso si esto significa ayudar a los brujos dentro de la Horda.", rewards = "Recibirás:\n\n"},
[1433] = {title = "Las relaciones de la Alianza", objective = "Habla con Maurin Partehuesos en Desolace.", description = "Los miembros de Filo Ardiente son malvados y corruptos hasta la médula; la única forma de impedir que lleven a cabo sus planes es acabar con todos ellos.\n\nKeldran envió también a Maurin porque conoce bien a esos brujos.\n\nÉl se ocupa de los planes contra el Filo y yo me encargo de las otras amenazas demoníacas de Desolace.\n\nHabla con él y te dirá sus planes.", progress = nil, completion = "Siempre se agradece la ayuda de los leales al Jefe de Guerra.\n\nMe llamo Maurin y estoy aqúí a petición de Keldran y de Thrall. Quiero combatir contra los males que acechan a mi jefe.\n\nAcércate, &lt;clase&gt;, te voy a explicar mi plan para erradicar a Filo Ardiente.", rewards = "Recibirás:\n\n"},
[1434] = {title = "La contaminación del sátiro", objective = "Mata a 7 pícaros, 7 jurapenas, 7 traidores y 7 clamainfernos Odio Feroz y ve a ver a Takata Hojacero a Desolace.", description = "Las infestaciones demoníacas no ocurren por sí solas; alguna otra fuerza les permite establecerse.\n\nPuede ser un espíritu debilitado, el ansia injustificada de poder, un deseo de venganza o de justicia. Cuando nos atenaza el dolor, el mal aprovecha su oportunidad con seres como el sátiro.\n\nLos sátiros han pactado con el Filo Ardiente y Thrall quiere eliminarlos. Los encontrarás hacia el noreste. Vuelve cuando los hayas expulsado.", progress = nil, completion = "Admiro el honor y dedicación que has mostrado, &lt;clase&gt;.\n\nCuando termine la campaña en Desolace, informaré al Jefe de Guerra sobre tus actos y tu ayuda.\n\nGracias.", rewards = "Recibirás:\n\n"},
[1435] = {title = "Los espíritus ardientes", objective = "LLeva la gema ardiente y 15 gemas ardientes imbuidas a Maurin Partehuesos a Desolace.", description = "Esas despreciables criaturas prefieren la muerte antes que dar la espalda a su señor. Pero la muerte no basta cuando tratas con aquellos que se han entregado a las tinieblas.\n\nTen esta gema; actívala cuando algún miembro de Filo Ardiente esté a punto de morir. El hechizo hará que el espíritu corrupto abandone a su víctima y deje una gema imbuida.\n\nTráeme las gemas para que me encargue de ellas.\n\nVe, y que la muerte te eluda.", progress = "He intentado imbuir hechizos simples. Extraer el espíritu de la víctima de su forma corrupta te será fácil. No confíes en las gemas para aniquilar al enemigo; solo debes activarlas cuando el sujeto esté a punto de morir. De lo contrario, no servirán de nada.", completion = "Me alegro de verte. Dame las gemas, las pondré en lugar seguro.\n\nNo tengo más tareas para ti, &lt;clase&gt;. Ve a ver a Takata cuando hayas descansado; puede que tenga trabajo para ti o que tengas que ir a Orgrimmar para informar a Keldran.", rewards = "Recibirás:\n\n"},
[1436] = {title = "Las relaciones de la Alianza", objective = "Habla con Keldran en Orgrimmar.", description = "Esto va bien, <nombre>. Si la lucha contra los demonios y sus huestes sigue yendo así, puede que el Jefe de Guerra nos deje volver a casa antes de que cambie la estación.\n\nVe a ver a Keldran y dile lo que hemos hecho. Seguro que te recompensará por tu ayuda. Su causa es importante para él y seguro que se enorgullece de saber que el Jefe de Guerra lo tiene en alta estima.", progress = nil, completion = "Lo has hecho muy bien en Desolace, &lt;nombre&gt;.\n\nGracias por tu esfuerzo, tu tiempo y tu comprensión. Thrall desea que haya paz entre la Horda y la Alianza; ahora que nuestro aspecto es aceptable, evitaremos que haya más violencia.\n\nEstoy en deuda contigo.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de KapelanManteo Correveloz\n\nTambién recibirás:\n\nGuantes de KapelanManteo Correveloz"},
[1437] = {title = "Buscando a Vahlarriel", objective = "Busca y registra el carro de Tyranis y Dalinda Malem en Desolace.", description = "Yo soy Vahlarriel, <nombre>. He venido aquí a descubrir el paradero de dos nobles de una familia de mercaderes de Darnassus, los Malem. El padre se teme lo peor y yo debo hacer todo lo que pueda para encontrar a sus hijos. Tengo que empezar mi búsqueda por Tyranis o Dalinda, pero espero no estar aquí demasiado.\n\nLo último que se sabe es que cruzaron la Sierra Espolón hace pocas semanas.\n\nSi encuentras su carro, por favor, ven a buscarme de inmediato.", progress = nil, completion = "Cuando examinas los restos del carro, te das cuenta de que fue atacado tanto por hechizos como por armas. Parece que el ataque llegó por un flanco y que fue rápido; no hay restos de sangre y tampoco cuerpos.\n\nQuienquiera que atacara el carro era eficiente y rápido.", rewards = "Recibirás:\n\n"},
[1438] = {title = "Buscando a Vahlarriel", objective = "Encuentra a Dalinda Malem en Desolace.", description = "Busca a Dalinda y a Tyranis y vuelve a contarme tus progresos. Empieza la búsqueda en la fortaleza. Yo me pondré inmediatamente en contacto con mis superiores en Darnassus y les informaré de la situación.\n\n<nombre>, hay algo más que quiero transmitirte. Mis superiores han manifestado su preocupación por los motivos que pueda tener Tyranis para venir aquí. Consideran más importante encontrar a Dalinda y ponerla a salvo. Tyranis debe ser... evaluado antes de ser llevado a casa.", progress = nil, completion = "Gracias a Elune, por fin alguien ha venido en mi ayuda. Te lo ruego, &lt;clase&gt;, tienes que ayudarme. Mi hermano Tyranis y yo fuimos capturados y traídos hasta aquí por el Filo Ardiente. ¡Creo que quieren ofrecerme como sacrificio a sus demonios!\n\nSabía que venir a Desolace era un error, pero Tyranis... últimamente ha desarrollado un ferviente interés por todo lo arcano, incluso contra la voluntad de nuestra gente. Decidió emprender este camino, a pesar de haber oído historias de los demonios que infestan estas tierras.\n\nNo sé cómo está pero... temo lo peor.", rewards = "Recibirás:\n\n"},
[1439] = {title = "Buscando a Tyranis", objective = "Encuentra a Tyranis Malem en Desolace.", description = "Cuando los orcos asesinaron a nuestros guardias y siervos, fuimos traídos aquí. ¡Qué cosas tan horribles les hicieron! Pensaba que nos iban a matar ahí mismo, pero no tardamos en darnos cuenta de que sus planes eran otros.\n\nTyranis intentó dialogar con ellos. Creo que quería impresionarles con sus conocimientos de lo Arcano... No sé muy bien por qué.\n\nNo puedo irme de aquí hasta que sepa qué le ocurrió a mi hermano. Te lo ruego, encuéntralo y vuelve a mí cuando sepas cuál fue su suerte.", progress = "Temo que Tyranis intente desvelar los secretos del Filo Ardiente y unirse a ellos si lo permiten... Siempre ha ansiado el poder mágico que proporciona a quienes se acercan a ellos.\n\nNo sé si tengo el valor para volver a casa con la noticia de que el espíritu de Tyranis está flirteando con tales peligros. Entre mi gente, ya es bastante terrible perseguir tales cosas... y convertirse en aquello de lo que intentamos protegernos... es... mucho peor.", completion = "¡Esto es terrible! Pero supongo que era necesario. Si no lo hubieras hecho tú, seguramente mi gente habría intervenido. Supongo que debería darte las gracias... perdóname si no parezco muy sincera. No es mi intención ofenderte.\n\nAhora que sé qué suerte corrió mi hermano, parece que ha llegado el momento de que huya. Si aún sigue en pie tu ofrecimiento, aceptaré tu ayuda para escapar de este horrible lugar.\n\nCuando estés listo, podemos emprender nuestro camino hacia la salvación.", rewards = "Recibirás:\n\n"},
[1440] = {title = "Vuelve con Vahlarriel", objective = "Escolta a Dalinda Malem fuera de la Fortaleza del Hacha de Trueno y vuelve a Desolace a hablar con Vahlarriel Destripademonios.", description = "¿Así que ha llegado el momento? Aprecio tus esfuerzos y, por si más tarde no tengo ocasión de hacerlo, permíteme que te agradezca ahora todo lo que has hecho por mí y por mi familia. Si ya estás <listo/lista>... Aquí no queda nada para mí. Podemos partir en el momento que consideres adecuado.", progress = nil, completion = "Gracias, &lt;nombre&gt;. Dalinda llegó sana y salva a Punta de Nijel y en mejores condiciones de lo que cabría haber esperado. El hecho de que llegara hasta aquí en pleno control de su poder y sus facultades es una buena señal. Espero que tú estés bien.\n\nMe duele pensar que Tyranis y Dalinda no han regresado juntos, pero sobre todo me pesa que ella tenga que soportar la vergüenza de las decisiones que tomó Tyranis. Es algo con lo que tendrá que enfrentarse cuando regrese a Darnassus.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de forcejeadorGuantes de PerspicaciaCapa de cuartelHombrera luz lunar\n\nTambién recibirás:\n\nCinturón de forcejeadorGuantes de PerspicaciaCapa de cuartelHombrera luz lunar"},
[1442] = {title = "La búsqueda de la gema Kor", objective = "Lleva una gema Kor a Thundris Tejevientos a Costa Oscura.", description = "Saludos, <nombre>. ¿Cómo puedo ayudar a quien sigue una senda sagrada?\n\nAh, sí, Fontana, el herrero... Cuánto tiempo. Lo recuerdo bien, aunque hacía tiempo que no oía hablar de él.\n\nNo lo tengo por avaricioso, luego su deseo de tener una gema Kor solo puede tener un motivo noble.\n\nPara encontrar una de esas gemas tendrás que ir hacia el noroeste de Vallefresno. Allí encontrarás unas ruinas antiguas y las Cavernas Anegadas. Allí viven los nagas que siguen usando esas gemas. No vayas por tu cuenta, <clase>.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nGema kor purificada"},
[1444] = {title = "Vuelve con Fel'Zerul", objective = "Vuelve a ver a Fel'Zerul en Rocal.", description = "Jammal'an el Profeta me condenó al exilio del Templo de Atal'Hakkar. Antiguamente creía que el sumo sacerdote era un buen líder.\n\nSu implacable fe en su profecía llevará a la destrucción de los Atal'ai. Si es que todavía no lo ha hecho.\n\nCuando hablé sobre mis preocupaciones, fui considerado un infiel y me expulsaron del Templo.\n\nEsos artefactos que me has traído son inofensivos. Pero si Jammal'an ha descubierto el encantamiento fetiche correcto para el ritual de invocación, el mundo corre un grave peligro...", progress = nil, completion = "Hace mucho tiempo que no hablamos, &lt;clase&gt;. Espero que hayas tenido éxito en tu viaje. \n\n¿Qué conocimiento has obtenido de los exiliados Atal'ai en las Tierras del Interior?", rewards = "Recibirás:\n\n"},
[1445] = {title = "El Templo de Atal'Hakkar", objective = "Reúne 20 fetiches de Hakkar y llévaselos a Fel'Zerul en Rocal.", description = "¿Así que habló de la profecía de Jammal'an?\n\nEl líder espiritual de los Atal'ai cree que cuando Hakkar vuelva a Azeroth desde el Vacío Abismal, el dios concederá la inmortalidad a la tribu Atal'ai.\n\nArtimañas estúpidas para conseguir una muerte temprana, si me lo preguntas.\n\n¿Pero dices que el exiliado habló sobre unos fetiches encantados? Eso me preocupa. Si esos fetiches son, realmente, la llave del ritual Atal'ai que ha causado todo este caos, debemos comprender los poderes intrínsecos.\n\nVete al Templo y consigue los fetiches.", progress = "Si los fetiches Atal'ai tienen el poder de invocar a Hakkar y cumplir la profecía de Jammal'an, tenemos que conseguirlos. ¡La Horda debe descubrir esos poderes!", completion = "Valiente &lt;clase&gt;, has demostrado ser un gran campeón de la Horda.\n\nAhora, hay que encargarse de esta colección de fetiches de Hakkar.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nTalismán guardián"},
[1446] = {title = "El profeta Jammal'an", objective = "El exiliado de Atal'ai, en las Tierras del Interior, quiere la cabeza de Jammal'an.", description = "A mis compañeros Secacorteza no les gustan los extraños errantes.\n\n¿Yo? Yo ya no tengo hogar. Vivo en el exilio, lejos de mi gente, los Atal'ai del Pantano de las Penas.\n\nNuestro líder espiritual, Jammal'an, tuvo lo que él llama una visión. Cree que invocar al dios Hakkar traerá la inmortalidad a los Atal'ai.\n\nPero yo pedí prudencia. ¿Y si la visión no es más que manipulación?\n\nQuiero vengarme por estar en el exilio. Tráeme la cabeza de Jammal'an. A lo mejor así mi gente podrá ser libre.", progress = "Cuando acabes con Jammal'an podré volver con mi gente.\n\nSi sigue siendo su líder espiritual, los Atal'ai van directos a la perdición.", completion = "La imprudente fe de Jammal'an en visiones falsas ha llevado a mi gente a la perdición.\n\nGracias por vengar mi exilio, &lt;clase&gt;. Estoy de luto por mi gente y mi hogar perdido.", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos de zancalluviaYelmo de Exilio\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nLeotardos de zancalluviaYelmo de Exilio"},
[1447] = {title = "El diplomático desaparecido", objective = "Derrota a Dashel Puñopiedra en Ventormenta.", description = "Sí, tá claro que no eres el <clase> más listo que ha venido a Ventormenta, pero tás a punto de ser uno de los más tontos que han salido de aquí.\n\n¿Conoces a mis amigos?", progress = nil, completion = "¿Has venido por alguna razón en particular? ¿Qué necesitas?", rewards = "Recibirás:\n\n"},
[1448] = {title = "En búsqueda del templo", objective = "Brohann Barriliga, en Ventormenta, quiere que busques el Templo de Atal'Hakkar.", description = "Hace tiempo, yo era joven y rebosaba vigor como tú, <clase>.\n\nPero ahora soy mayor e incapaz de explorar el mundo como hice antaño. ¡Ah, las cosas que vi!\n\nPero un misterio me eludió siempre. En mi última misión, la Liga de Exploradores me envió a buscar El Templo de Atal'Hakkar. Se decía que estaba situado en el Pantano de las Penas.\n\nDebido a mi delicado estado, viajé surcando los cielos sobre un grifo buscándolo, pero no conseguí encontrarlo.\n\n¿Ayudarías a un viejo enano? Quizás tengas más suerte que yo si vas a pie.", progress = nil, completion = "¿El Templo de Atal'Hakkar está sumergido bajo la Charca de Lágrimas? ¿Y rodeado del Vuelo Verde?\n\nUn descubrimiento increíble, &lt;clase&gt;. Pero, obviamente, algo ha salido terriblemente mal.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1449] = {title = "A las Tierras del Interior", objective = "Habla con el Gran señor feudal Falstad Martillo Salvaje en el Pico Nidal, en las Tierras del Interior.", description = "Como mencioné antes, intenté buscar el templo desde el aire. Aunque no estaba exactamente solo.\n\nFui a la famosa pajarera de grifos en Pico Nidal, en las Tierras del Interior. Allí recluté a uno de sus mejores pilotos. El piloto se cayó de la montura, al pantano...\n\nA mis años, no soy capaz de recordar el nombre del pobre piloto. Pero, sin duda, el maestro de la pajarera se acordará.\n\nVe allí y busca a Falstad Martillo Salvaje.\n\nTenemos que saber lo que le ocurrió a ese piloto.", progress = nil, completion = "¿Qué quieres, muchacho? ¿No ves que tengo cosas que hacer aquí? ¿A quién le sobra el tiempo para perderlo con un &lt;raza&gt; charlatán?\n\n¡Hay trols saliendo de la carpintería! Habla rápido.", rewards = "Recibirás:\n\n"},
[1450] = {title = "Maestro de grifos Garracha", objective = "Habla con el maestro de grifos Garracha dentro de la pajarera del Pico Nidal.", description = "¿Así que eres $gel chico:la chica de los recados de algún vejete de la Liga de Exploradores, eh?\n\nBueno, pues escúchame bien. No sé con quién volaba por Azeroth tu viejo amigo. ¿Acaso te parezco el maestro de grifos Garracha? ¿Eh?\n\nSi tanto te interesan las asignaciones de vuelo, ¿por qué no le preguntas al maestro de grifos? Si te pillo haciendo el vago por aquí mucho más tiempo, voy a ponerte a trabajar.\n\nY ahora, lárgate.", progress = nil, completion = "Ah, sí. Pues claro que recuerdo haber enviado a uno de mis jinetes de grifos para ayudar a Brohann Barriliga.\n\nSiempre he sentido debilidad por la Liga de Expedicionarios. De hecho, mi padre ayudó al equipo inicial de excavación que descubrió Uldaman.\n\nEnvié al que creía que era mi mejor jinete de grifo con Brohann. Un tipo llamado Rapsodio Machacacanillas.\n\n¡Y menudo jinete era! Bueno, hasta que...", rewards = "Recibirás:\n\n"},
[1451] = {title = "Rapsodio Machacacanillas", objective = "Busca a Rapsodio Machacacanillas en las Tierras del Interior.", description = "Rapsodio empezó a darle bastante al alcohol. No me malinterpretes, a nosotros, los enanos que estamos en la pajarera, nos encanta una buena cerveza de vez en cuando.\n\nPero al viejo Machacacanillas empezó a gustarle tanto la bebida como a un grifo le gusta el cielo. Y fue una pena. Nadie podía volar como Rapsodio.\n\nEl primer signo de problemas, fue cuando volvió dando tumbos de esa misión con tu amigo, Brohann. Y, después de eso, todo fue cuesta abajo. Tuve que dejarle marchar.\n\nLa última vez que supe de él, había establecido una fábrica de cerveza al este de aquí...", progress = nil, completion = "¡Hip!\n\nTe estoy mirando y no paras de moverte. Tás mareándome. ¡Jo, jo!\n\n¡Hip!\n\n¡Guau! ¿Lo has sentido? La tierra se mueve, amigo. Necesito una copa.\n\n¡Hip!", rewards = "Recibirás:\n\n"},
[1452] = {title = "El cubata de Kalimdor de Rapsodio", objective = "Rapsodio Machacacanillas, en las Tierras del Interior, quiere que le lleves 3 mollejas de Roc, 3 hígados de Groddoc y 3 hígados de piel pétrea.", description = "¡Hip!\n\n¿Así que te ha mandao ese loco de Brohann? Pedazo de aventura que tuvimos... ¡Hip!\n\nSi quieres que te cuente lo que pasó allí en el pantano, voy a necesitar beber algo fuerte. No fue nada bonito, ¿sabes? ¡Hip!\n\nMira... Tráeme lo que necesito pa hacer un cubata de Kalimdor y te lo contaré.\n\nVoy a necesitar mollejas de Roc de Tanaris y... veamos... algunos Groddocs e hígados de piel pétrea de Feralas...\n\nUn buen cubata de Kalimdor... ¡Con eso hablaré!", progress = "¿Alguna vez te has tomao una malta rapsódica? Una de mis mejores bebidas, si me se permite decirlo. ¡Hip!\n\nPero si voy a revivir los horrores de aquel espantoso vuelo sobre... hip... el Pantano, bueno, voy a necesitar beber algo un pelín más fuerte. ¡Hip!\n\n¿Supongo que no habrás encontrao esas mollejas y los hígados que necesito pa darle tono a mi cubata de Kalimdor, no?\n\n¡Hip!", completion = "¡Ya veo que no has tenio problemas pa traerme lo que necesitaba! Tiene que haber sido complicao lo de allí, en el lejano Kalimdor.\n\nNo me llevará ni un momento extraer lo que necesito de estos hígados y mollejas. ¡Hip!\n\nDespués podré destilar una bebida bastante fuerte y entonces podré contarte lo que quieres saber.\n\n¡Hip!\n\nMe da escalofríos pensar en ese viejo pantano.\n\n¡Hip!\n\nDeja que me tome primero una copita rápida.", rewards = "Recibirás:\n\n"},
[1453] = {title = "Los negocios de los Reivindicadores en Desolace", objective = "Habla con Kreldig Ungor en Desolace.", description = "Hay mucho que hacer en Desolace, <nombre>. Es un lugar atroz, mancillado por centauros y otros seres desagradables. Pero si buscas un trabajito honrado, ve, busca a Kreldig Ungor y dile que vas de mi parte.\n\nEstará en Punta de Nijel, un campamento pequeño oculto en las montañas del norte de Desolace. Si entras por la Sierra Espolón, ve hacia el este. Es difícil de ver, así que estate pendiente.", progress = nil, completion = "Me alegra ver que aún queda gente aventurera.\n\nMe llamo Kreldig. Has hecho bien en unirte a los Reivindicadores, &lt;nombre&gt;. Todo el mundo busca algo: un tesoro, un amor perdido... Y tú puedes encontrarlo... por un precio.", rewards = "Recibirás:\n\n"},
[1454] = {title = "El naufragio de Karnitol", objective = "Encuentra el cofre de Karnitol en Desolace.", description = "Mi primera orden de negocios es para el mago Karnitol, una especie de noble de Ventormenta. Por lo visto, su barco fue atacado por gigantes marinos cuando navegaba al noroeste de Desolace. Él y sus sirvientes intentaron llevar a la costa uno de sus cofres más valiosos, pero cuando los gigantes marinos atacaron en manada, decidió abandonarlo y teletransportarse con sus sirvientes.\n\nQuiere que encontremos su cofre y su contenido y que se lo llevemos a Ventormenta.", progress = nil, completion = "Examinas el cofre y ves que no parece haber sido dañado por el agua.\n\nPero es evidente que la tapa ha sido forzada y, por las marcas de quemaduras de los bordes, ves que cualquier trampa mágica que se pusiera en su momento para proteger su contenido, fue anulada hace tiempo.", rewards = "Recibirás:\n\n"},
[1455] = {title = "El naufragio de Karnitol", objective = "Vuelve a Desolace a hablar con Kreldig Ungor.", description = "Inviertes unos segundos en intentar averiguar qué abrió el cofre y notas que en los laterales y la tapa del cofre hay unos profundos arañazos que indican que algo que no era un hombre ni un gigante se interesó por él. Las marcas no parecen haber sido causadas por una daga o una espada.\n\nRevisas el cofre y lo encuentras totalmente vacío.\n\nNo hay nada que hacer. Cierras el cofre y te preparas para irte. Kreldig estará decepcionado.", progress = nil, completion = "Bienvenido de nuevo a Punta de Nijel, &lt;nombre&gt;. Dime, ¿qué has encontrado?\n\n¡¿Qué?! ¿Nada? No van a darnos nada por un cofre vacío...\n\nHáblame de esas marcas que viste.", rewards = "Recibirás:\n\n"},
[1456] = {title = "El naufragio de Karnitol", objective = "Encuentra la cartera de Karnitol y devuélvesela a Kreldig Ungor, que está en Desolace.", description = "Intrigante.\n\nBueno, puede que aún no debamos perder la esperanza.\n\nA lo largo de esa misma costa pero hacia el norte, cerca de la Isla Ranazjar, en el fondo del mar, hay unas ruinas de los elfos de la noche. Una tribu de naga, los Sigifilo, han hecho de ellas su hogar. Creo que es nuestra mejor baza.\n\nSi una partida de exploradores llegó tan al sur, debía de ser bastante poderosa. Si quieres encontrar las pertenencias de Karnitol, yo empezaría con los nagas más poderosos: los cazamareas o las brujas de mar.", progress = "No quisiera ofender a Nijel, aunque no sé ni quién es, pero este campamento no es precisamente el sitio más cómodo para pasar la noche, ya me entiendes, <nombre>.", completion = "¡Es genial, &lt;nombre&gt;! ¡Has encontrado las pertenencias de Karnitol!\n\nEsos malditos naga... siempre andan dando problemas. Creo que preferiría pasar el resto de mi vida luchando con la Horda antes que combatir con esa calaña. Has hecho un gran servicio a los Reivindicadores.", rewards = "Recibirás:\n\n"},
[1457] = {title = "El naufragio de Karnitol", objective = "Lleva la cartera de Karnitol a Roetten Rocamartillo a Forjaz.", description = "Lleva las cosas de Karnitol a Forjaz. Dáselas a Roetten y él te pagará por un trabajo bien hecho.\n\nJa, ja, si no tienes cuidado, va a estar tan contento contigo que te pedirá que te unas a nosotros a jornada completa.\n\nUna vez más, buen trabajo, <nombre>.\n\nCuidado en el camino de regreso. Y no te entretengas mucho, Karnitol no es el más paciente de los hombres, aunque sí es muy generoso.", progress = "El negocio va de maravilla, <nombre> y no tengo mucho tiempo para preguntas. Si tienes asuntos con los Reivindicadores, te escucharé. Si no es así, tendré que pedirte que hables con uno de mis subalternos. Ellos se ocupan de las cosas por aquí.", completion = "Ah, sí, el asunto Karnitol de Desolace. ¡Fantástico!\n\nAsí que el cofre estaba destruido pero los objetos están todos aquí, ¿eh? Bueno, dejaré que sea él quien lo juzgue. Lo bueno que tiene este negocio es que, si no los clientes no quedan satisfechos, ¡les puedo mandar a paseo! Confían en que nosotros encontraremos sus cosas... no han pedido que estén estupendas e intactas cuando las recuperen.\n\nBuen trabajo, &lt;nombre&gt;. Ahora vamos a ocuparnos de que recibas tu pago.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de demonioEspaldares sanguinos\n\nTambién recibirás:\n\nBotas de demonioEspaldares sanguinos"},
[1458] = {title = "Componentes para Reivindicadores y Cía.", objective = "Lleva 10 zarpas de Odio Feroz y 10 cuernos de Odio Feroz a Kreldig Ungor a Desolace.", description = "Uno de mis cometidos es recoger componentes para Zurdibrujo, un mago gnomo de Kharanos. Roetten parece pensar que es fácil porque ya estamos aquí. Y no le preocupa que nos falte gente.\n\nLos primeros artículos de la lista deberían ser fáciles de encontrar. Y habría una buena recompensa para quien me ayudara.\n\nNecesito algunos cuernos y patas de los sátiros Odio Feroz que están al este de aquí. Tráemelos y seguiremos con el resto de la lista.", progress = "Los sátiros son unas criaturas despreciables. La leyenda dice que están ligados de alguna forma a los elfos de l anoche, pero no sé si creérmelo.\n\nCreo que esa leyenda surgió por el ese elfo que los arrastró a la guerra hace unos años. El mundo no ha vuelto a ser el mismo desde entonces. Es mucho más peligroso y más terrorífico.", completion = "Bien, bien, &lt;nombre&gt;. No sé si esto es de buena calidad, pero si Zurdibrujo quería algo más específico, tenía que haberlo dejado claro antes de enviar a los Reivindicadores aquí fuera.\n\n¿Qué te pareces si te pones con el siguiente objeto de su lista?", rewards = "Recibirás:\n\n"},
[1459] = {title = "Componentes para Reivindicadores y Cía.", objective = "Lleva 7 viales de veneno de escórpido y 3 pieles de kodo maduro a Kreldig Ungor a Desolace.", description = "Lo siguiente de la lista de Zurdibrujo es... veamos... mmm. Ah, aquí está.\n\nEs simple. Suele ser muy explícito cuando da instrucciones. Necesita veneno de Escorfasi y el pellejo de algún kodo. La letra pequeña dice que ambas cosas deben ser de criaturas específicas. El veneno tiene que venir de cualquiera de los Escorfasi y el pellejo tiene que ser de un kodo maduro, no de uno anciano o moribundo.\n\n¡Magos! De verdad, a veces piden unas cosas...", progress = "Siento no poder decirte dónde podrías encontrar kodos y escórpidos. No conozco muy bien la zona y no tengo mucho tiempo para explorarla.", completion = "Eres una gran ayuda, &lt;nombre&gt;. Estás reduciendo al menos a la mitad mi estancia en Desolace. Gracias a ti, no tardaré en volver a Forjaz a calentarme los pies.\n\nCuando nos hayamos ocupado de esta lista y yo haya vuelto a casa, búscame. Te invitaré a una cerveza.\n\nPero hasta entonces, acabemos de reunir el resto de los componentes.", rewards = "Recibirás:\n\n"},
[1462] = {title = "Sapta de tierra", objective = "Pide otra sapta de tierra a la profetisa Plumacuervo.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nSapta de tierra"},
[1463] = {title = "Sapta de tierra", objective = "Pide a Clamor de Tierra Canaga otra Sapta de tierra.", description = nil, progress = nil, completion = "Te daré una de muy buena fe. Ya has probado tu valía en una ocasión, pero deberías tener más cuidado en el futuro.", rewards = "Recibirás:\n\nSapta de tierra"},
[1464] = {title = "La sapta de fuego", objective = "Habla con Telf Joolam para conseguir otra sapta de fuego.", description = nil, progress = nil, completion = "Que el fuego arda en tu corazón, &lt;nombre&gt;.\n\nLos elementos te esperan.", rewards = "Recibirás:\n\nSapta de fuego"},
[1465] = {title = "Buscando a Vahlarriel", objective = "Lleva el colgante Malem a Vahlarriel Destripademonios a Desolace.", description = "Cuando estudias el carro encuentras un colgante y huellas que se dirigen al sur. En la distancia puedes ver humo saliendo de un gran fuerte. El humo proviene de un fuego controlado, no de algún edificio incendiado y tu instinto te dice que, si los Malem aún están vivos, fueron llevados en esa dirección.\n\nCrees que Vahlarriel, que está en la Punta de Nijel, debería ver el colgante y también saber que has visto signos de vida al sur.", progress = nil, completion = "¡Esto es exasperante! ¡Todo este asunto!\n\nLos dos fueron advertidos de lo peligroso que sería venir aquí. Y ahora han desaparecido... o algo peor.\n\n¿Dices que viste huellas que se dirigían a un fuerte cercano? Esa es nuestra mejor apuesta.\n\nPor lo que dicen los habitantes de la zona, esa fortaleza está infestada de miembros del Filo Ardiente. Orcos. Orcos que se han consagrado a la causa demoníaca. No representan la mayoría de las creencias de la Horda, pero son malvados y, si te pillan husmeando por allí, te matarán en cuanto te vean.", rewards = "Recibirás:\n\n"},
[1466] = {title = "Componentes para Reivindicadores y Cía.", objective = "Lleva 10 cerebros de can manáfago, 10 alas infernales y 10 viales de sangre de guardiavil a Kreldig Ungor a Desolace.", description = "Estos son los últimos objetos y, no te voy a mentir, no es un grupito agradable. Además, creo que te llevará más tiempo conseguir estos objetos que los demás.\n\nBueno, basta de charla introductoria, vamos al grano. Zurdibrujo dice que necesita: diez cerebros de devoradores de maná, de cazadores de magos o de cazadores Ley. Diez alas infernales de una doncella, hermana o hechicera infernal. Y... diez frascos de sangre de un guardiavil, que tiene que ser capitán o señor.\n\nSencillito, ¿eh? Oyee... si prefieres pasar, lo entiendo.", progress = "Ni siquiera sabía que hubiera demonios de estos en Desolace. Me quedé en que la tierra la ocuparon los centauros hace años, cuando los elfos de la noche recogieron sus bártulos y se fueron.\n\nMe pregunto por qué se instalaron aquí, y como.", completion = "¿La verdad? Jamás lo habría pensado. Eres increíble, &lt;nombre&gt;. Te enfrentas a demonios por el experimento de un mago... ¡y además vuelves de una pieza! ¡Buen trabajo!\n\nVoy a hablar personalmente a Roetten sobre ti cuando vuelva a Forjaz.", rewards = "Recibirás:\n\n"},
[1467] = {title = "Componentes para Reivindicadores y Cía.", objective = "Lleva el paquete de Zurdibrujo a Roetten Rocamartillo a Forjaz.", description = "Si quieres que te paguen, tendrás que volver a Forjaz y hablar con Roetten en persona. Estará encantado de quitar de en medio el paquete de Zurdibrujo. Tú dale esto. Debería bastar para conseguir tu recompensa.\n\nY no te preocupes, hablaré bien de ti cuando vuelva. No debería quedarme mucho más en Desolace.", progress = "Bienvenido, <nombre>. El negocio va de maravilla. Los golpeteos de los yunques resuenan por todo Forjaz, los fuelles alimentan el fuego y mi mujer está haciéndome un postre especial para cenar. ¿No es estupenda la vida?", completion = "Ah, el paquete de Zurdibrujo de Desolace. Excelente. Aprecio mucho tu trabajo, &lt;clase&gt; y tu eficiencia. Me alegro de tenerte a bordo, al menos para este trabajito.\n\nTengo tu pago aquí mismo. Que se sepa que Reivindicadores y Cía. siempre paga bien por una dura semana de trabajo.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales áuricosGuanteletes Tempesfuego\n\nTambién recibirás:\n\nBrazales áuricosGuanteletes Tempesfuego"},
[1468] = {title = "Semana de los Niños", objective = "Utiliza el silbato de huérfano humano para hablar con el niño a quien tienes que cuidar durante la Semana de los Niños.\n\nObjeto provisto: \n", description = "¿Quieres ayudarnos aquí en el orfanato? <Bendito seas, amigo/bendita seas> amiga;. Tu ayuda durante la Semana de los Niños no tiene precio.\n\nCoge este silbato. Solo podrás utilizarlo durante un breve período de tiempo, básicamente, hasta que se acabe la Semana de los Niños. Cuando lo uses, podrás hablar con el niño que esté a tu cuidado. Utilízalo de nuevo para que el niño se vaya.\n\nSea como sea, <nombre>, utiliza el silbato y conoce a tu pupilo. Seguro que el niño estará encantado de conocerte.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[1469] = {title = "El relato de Rapsodio", objective = "Lleva el fragmento de la tablilla Atal'ai a Brohann Barriliga, en Ventormenta.", description = "Antes del vuelo, me bebí un barril entero de cerveza negra. ¡Hip!\n\n¡Y menudo pedazo de vuelo! De un lao pa otro con Brohann siguiéndome. Aunque no vimos ni un templo. ¡Hip!\n\nBueno, yo no vi ni un templo hasta que me puse un poco achispao y me caí de la montura. Caí justo en el agua y me arrastró pa’bajo. Me encontré rodeao de un montón de trols muy feos. ¡Salí de allí pitando! Incluso conseguí escapar con este souvenir trol. Toma, llévaselo a Brohann y dile que su templo tá ahí abajo. ¡Hip!", progress = "Por fin has regresado, <nombre>. Hace mucho que no nos veíamos. Creí que quizás te habías olvidado de mi misión.\n\n¿Encontraste a ese jinete de grifos artero? ¿Te dijo algo?", completion = "¡Rapsodio Machacacanillas! ¡Exacto!... ¿Cómo podría olvidarme de un nombre así?\n\n¿Así que sabía lo del hundimiento del Templo todo este tiempo? Bueno, pues eso podría haberte ahorrado mucha caminata.\n\n¡Pero este artefacto Atal'ai...! ¡Menudo descubrimiento!", rewards = "Recibirás:\n\n"},
[1470] = {title = "Descubrir el misterio", objective = "Lleva 3 calaveras de armadóseos a Venya Martmani.", description = "Buenas, criatura. Veo que aún no has desarrollado del todo los poderes de los brujos. Pero siento que tienes un gran deseo de aprender.\n\nY aprenderás.\n\nNo hay fuerza en la ignorancia. El conocimiento es nuestro mayor poder. Aplicando el conocimiento podemos controlar las magias y los seres del vacío y atarlos a nuestra voluntad.\n\nTe enseñaré a atar a un siervo a tu voluntad. Pero primero tienes que traerme 3 calaveras de los esqueletos armadóseos que hay cerca de la herrería abandonada.", progress = "Tu poder ahora es escaso, pero sospecho que ya sientes las posibilidades que ofrece.\n\nIncluso los muertos podemos sentirnos vivos con ese poder corriendo por nuestras venas.", completion = "Aún no has sido puesto a prueba y estás al principio de tu aprendizaje, pero has demostrado que posees la habilidad suficiente para invocar y poseer a un diablillo. No te dejes engañar por su tamaño. No posee fuerza física, pero sí poderes y una inteligencia que te vendrán bien.\n\nAtiéndeme bien, &lt;nombre&gt;, porque no repetiré esta lección; dejaré que la domines por tu cuenta.", rewards = "Aprenderás:\n\nInvocar a diablillo\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a diablillo\n\nTambién recibirás:\n\n"},
[1471] = {title = "El vínculo", objective = "Utilizando las Runas de invocación, invoca y domina a un abisario, y después devuélvele las Runas de invocación a Carendin Halgar, que está en el Barrio de la Magia de Entrañas.", description = "¿Ves el Círculo de Invocación? Ahí es donde debes invocar a un abisario.\n\nCon la ayuda de estas runas, podrás liberarlo y luego tendrás que dominarlo por medio de la fuerza de las armas y la magia.\n\nCuando hayas conseguido subyugarlo será completamente tuyo. Vuelve entonces a verme y te explicaré el hechizo de invocación para que te ayude en el fin que persigas.", progress = "Espero que lo consigas, <nombre>. De no ser así, no cumplir mis expectativas será el menor de tus males.", completion = "Lo has hecho muy bien, &lt;nombre&gt;. Ahora te enseñaré el hechizo que has de utilizar para invocar a tu abisario.\n\nSu poder está ahora en tus manos. Utilízalo sabiamente.", rewards = "Aprenderás:\n\nInvocar a abisario\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a abisario\n\nTambién recibirás:\n\n"},
[1472] = {title = "Devoradora de almas", objective = "Habla con Godrick Farsan en el Templo de los Condenados.", description = "Un sayaad es un devorador de almas, un dominador de voluntades y un tentador de corazones. Una criatura de inmensa maldad con una mente muy característica.\n\nLas leyendas que cuentan tanto personas honorables como malvadas emplean un nombre común: súcubo para las hembras e íncubo para los machos.\n\nHace falta un estímulo para atraer al demonio a nuestro mundo: los corazones de mortales buenos y sensibles. El sayaad no se podrá resistir. Entonces podrás dominarlo y hacerlo tuyo.\n\nHabla con mi asistente, Godrick Farsan, que está en el templo. Él te ayudará a encontrar el anzuelo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1473] = {title = "Criatura abisal", objective = "Recupera el grimorio de Egalin y dáselo a Carendin Halgar, que está en el Templo de los Condenados.", description = "Parece que todo apunta a que estás <preparado/preparada> para aprender a invocar a un abisario.\n\nSe trata de un ser creado a partir del caos y, si lo invocas pero eres incapaz de dominarlo, será tu fin.\n\nEgalin fue el primero en invocar a un abisario, y explica el proceso en su grimorio.\n\nDesgraciadamente, nos lo robó un agente de la Cruzada Escarlata y ahora está escondido en la base que tienen en una torre hacia el oeste. Tráemelo y compartiré ese conocimiento contigo.", progress = "Egalin fue mi maestro. Con una simple mirada, se hizo con el control del abisario que había invocado.\n\nCuando llegó mi turno, me confié demasiado. No juegues con los poderes que posees, <nombre>, pues pueden acabar hasta con el más avezado.", completion = "¡Estupendo! Perrine no tenía ni idea de la importancia de este libro.\n\nAhora no permitiremos que nos lo vuelvan a arrebatar. Observa la estructura del hechizo y el modo en el que debes canalizar tu poder.", rewards = "Recibirás:\n\n"},
[1474] = {title = "El vínculo", objective = "Utilizando los corazones puros, invoca y domina a un súcubo, y después devuélvele los corazones puros a Carendin Halgar, que está en el Barrio de la Magia de Entrañas.", description = "Usando estos corazones, lanza un hechizo al Círculo de Invocación para abrir una puerta por la que el súcubo pueda entrar. Tus poderes son enormes ahora, pero los necesitarás todos para dominar al súcubo. \n\nTen cuidado con sus encantos, <nombre>, muchas mentes regias han sucumbido a su voluntad. Cuando lo hayas dominado, el súcubo volverá al vacío, y entonces te enseñaré el hechizo para invocarlo a tu antojo en el futuro.Aprenderás:\n\n", progress = nil, completion = nil, rewards = "Aprenderás:\n\nInvocar a súcubo\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a súcubo\n\nTambién recibirás:\n\nFaltriquera de almas pequeña"},
[1475] = {title = "En El Templo de Atal'Hakkar", objective = "Consigue 10 tablillas de Atal'ai para Brohann Barriliga, en Ventormenta.", description = "Según la leyenda, El Templo de Atal'Hakkar es un santuario sagrado dedicado al dios ancestral, Hakkar el Cazador de Almas. Está protegido por una tribu viciosa de trols, los Atal'ai, y se rumorea que el templo es impenetrable.\n\nAhora está hundido en la Charca de Lágrimas, con lo que sacar artefactos del templo es mucho más difícil. Pero eso es lo que necesito que hagas, <nombre>.\n\nAyúdame, tráeme las tablillas de Atal'ai intactas. Tráemelas y podré volver con los Exploradores convertido en un héroe. Juntos compartiremos la gloria.", progress = "El anochecer desciende sobre mi carrera, <nombre>, y te pido que me ayudes en mi última misión de la Liga de Expedicionarios. Si mis viejos huesos tuvieran la fuerza de enfrentarme al Pantano de las Penas y al Templo Sumergido, te aseguro que me verías luchando contigo hombro con hombro.\n\nEl orgullo me dificulta la tarea de pedirte ayuda para conseguir las tablillas de Atal'ai. Pero los años que llevo encima, me obligan a hacerlo.\n\nPor favor, ayúdame, <nombre>. Quiero retirarme de la Liga de Expedicionarios habiendo completado mi último encargo.", completion = "¡Las tablillas de Atal'ai! ¡Gracias, &lt;nombre&gt;!\n\nCuando las lleve a la Liga de Expedicionarios, nuestros nombres pasarán juntos a formar parte de la historia. Has ayudado a un viejo enano a cumplir con su último deber. Te estaré eternamente agradecido.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nTalismán guardián"},
[1476] = {title = "Corazones puros", objective = "Llévale los corazones de Dalin Forjador y Corma Villardo a Carendin Halgar, que está en el Templo de los Condenados.", description = "Para persuadir a un súcubo se necesita un corazón puro, pero ¿hay algún mortal que no tenga vicios?\n\nRecogiendo datos de historias y rumores que se oyen por ahí, he encontrado a dos humanos que cumplen esta condición.\n\nEl primero es Dalin Forjador, que se consagró al cuidado de los refugiados de Lordaeron, que fueron repudiados por los suyos en la Muralla de Cringris.\n\nLa otra es Corma Villardo, una mujer que sigue siendo fiel a su amor perdido y que vaga por Los Humedales en busca de una señal de ella.\n\nEntrégale sus corazones a Carendin. Pero primero, deja que te muestre una cosa.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1477] = {title = "Suministros de vital importancia", objective = "Habla con el maestro de vigilantes Sorigal.", description = "Nuestros magos están realizando una investigación en el Castillo de Nethergarde en Las Tierras Devastadas, una investigación vital para entender El Portal Oscuro. Necesitan suministros prácticamente ininterrumpidos pero nuestra línea de suministros a Nethergarde es débil.\n\nTienes que ayudarnos para que los suministros lleguen a Nethergarde. Preséntate al maestro de vigilantes Sorigal en Villa Oscura y sigue sus instrucciones. Esta tarea te llevará a tierras inhóspitas, <nombre>. Prepárate.", progress = nil, completion = "¿Te envía Andromath? Muy bien. Tengo una importante tarea para ti...", rewards = "Recibirás:\n\n"},
[1478] = {title = "Las invocaciones de Halgar", objective = "Habla con Carendin Halgar en Entrañas.", description = "¡Ah, <nombre>! Parece que Carendim Halgar desea verte para continuar con tu instrucción.\n\nDeberías sentirte muy <orgulloso/orgullosa>, <nombre>. No todos los días el maestro Halgar se digna a instruir a <un joven brujo/una joven bruja>.\n\nLo encontrarás en el Barrio de la Magia de Entrañas, cerca del Templo de los Condenados.", progress = nil, completion = "Bien, veo que Ageron logró encontrarte, &lt;nombre&gt;. Dejémonos de charlas y cortesías y comencemos con tu instrucción. Aún te queda mucho que aprender.", rewards = "Recibirás:\n\n"},
[1479] = {title = "La Rama de los Eternos", objective = "Lleva al huérfano al banco de Darnassus. El banco está dentro de un árbol conocido como la Rama de los Eternos.", description = "He oído decir que los elfos de la noche tienen su banco en un árbol... ¡y que parece un oso! ¡Es una pasada! Está muy bien que los elfos de la noche cuiden de la naturaleza y de esas cosas, ¡pero los osos son demasiado!\n\n¿Me puedes llevar a ver el árbol-oso de Darnassus, por favor? Hay un barco que nos llevará cerca de allí en poco tiempo. ¡Por favor, por favor, por favor!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1480] = {title = "El corruptor", objective = "Lleva la piel de demonio a Maurin Partehuesos a Desolace.", description = "La piel desgarrada huele a podrido y es muy flexible pese a lo seca y frágil que parece.\n\nAl desenrollarla ves que tiene símbolos gráficos en rojo en un lado. En la parte inferior se ve muy claro un símbolo; supones que es una firma.\n\nQuizás Maurin te pueda explicar lo que es.", progress = "Filo Ardiente empieza a percatarse de cuán vulnerables son. Su magia es ridícula, carecen de fuerza.\n\nSu poder proviene de aquellos a quienes sirven, pero no lo comprenden en absoluto.", completion = "Estas runas son muy, muy antiguas, &lt;nombre&gt;. Se parecen un poco a las del Eredun, pero con grandes diferencias. Esto parece un contrato... Sí, este símbolo es la firma del demonio: Lord Azrethoc.", rewards = "Recibirás:\n\n"},
[1481] = {title = "El corruptor", objective = "Lleva 1 cabellera de acechasombras a Maurin Partehuesos a Desolace.", description = "Creo que puedo encontrar a este señor demoníaco con los ingredientes apropiados.\n\nSi es tan poderoso como sospecho, necesitaré objetos cogidos de criaturas mortales. Pero actúas en nombre del Jefe de Guerra, así que seguro que aceptarás mi encargo.\n\nPrimero, tendré que romper la guardia que pueda tener contra la adivinación. Para ello, necesitaré la cabellera de un sátiro, pero no de cualquier sátiro. La cabellera debe proceder de un acechasombras Odio Feroz.\n\nBúscalos entre las antiguas ruinas al noreste de Desolace.", progress = "Tráeme la cabellera adecuada, <clase>. Podríamos morir si los ingredientes no son correctos. Es posible que el señor demoníaco tenga hechizos ocultos en la manga.\n\nYa he sufrido bastante en esta vida.", completion = "Los acechasombras no pudieron contigo, &lt;clase&gt;: impresionante.\n\nCon esta cabellera podré realizar el hechizo; mientras estabas fuera conseguí los demás componentes.\n\nAhora debo concentrarme, necesito algo que proyecte una imagen del señor demoníaco.", rewards = "Recibirás:\n\n"},
[1482] = {title = "El corruptor", objective = "Lleva 1 cristal del oráculo a Maurin Partehuesos a Desolace.", description = "Un cristal del oráculo me iría muy bien. Los fabrican los nagas para sus oráculos.\n\nA lo largo de la costa, hacia el noroeste, hay un arrecife de coral en el que se han instalado los nagas. Allí encontrarás los oráculos. Los Sigifilo son mortales y no van a tomarse bien tu instrusión, así que ten cuidado y date prisa.\n\nCuando vuelvas nos ocuparemos de buscar a ese Azrethoc señor demoníaco.", progress = "Dame el cristal del oráculo, <clase>. Si acabamos con Lord Azrethoc nos ganaremos el favor del Jefe de Guerra; nos interesa colaborar.", completion = "Un cristal del oráculo precioso, sin mácula... Qué belleza.\n\nEmpecemos: debemos encontrar y eliminar al señor demoníaco.\n\nApártate; voy a concentrarme para descubrir su paradero.", rewards = "Recibirás:\n\n"},
[1483] = {title = "Ziz Fizziks", objective = "Habla con Ziz Fizziks en Risco Cortaviento.", description = "Ventura y Cía. se quedó con muchos de los cerebros goblins más brillantes pero de vez en cuando, conseguimos robarle alguno.\n\nHace algún tiempo, la delegación de Ventura y Cía. en Sierra Espolón contrató a Gerenzo Silbillave, ¡un gnomo! Esto afectó tanto a su otro ingeniero, Ziz Fizziks, que me rogó que le encontrara un nuevo empleo con el sindicato de manitas. Le asigné unas tareas pero puede que necesite ayuda.\n\n¿Podrías ir a ver cómo le va por mí? Se encuentra en el extremo occidental del Risco Cortaviento.", progress = nil, completion = "Creía que los ejecutivos de Ventura y Cía. eran unos negreros pero entonces conocí a Petardol, ¡el más cruel de todos!\n\n¡Ja! Aunque supongo que es mejor que estar sin trabajo... O algo peor, trabajar con el tal Gerenzo. Si estás aquí para ayudar, tengo algo de lo que podrías encargarte.", rewards = "Recibirás:\n\n"},
[1484] = {title = "El corruptor", objective = "Habla con Takata Hojacero en Desolace.", description = "Esta criatura es mucho más peligrosa de lo que esperaba. Seguro que Takata se pregunta si seré capaz de vencerla y sería un insulto a su honor el no informarle de lo que acaba de pasar.\n\nHabla con el espadachín y dile que, con su permiso, abatirás al demonio. Pero no insistas demasiado; es mejor que crea que es idea suya.", progress = nil, completion = "He visto lo ocurrido entre Maurin y el señor demoníaco. Parece tener más poder que mi brujo. Iría yo mismo, pero deshonraría a mi clan si abandonara mis deberes para ir a enfrentarme con un enemigo por mi propio honor.", rewards = "Recibirás:\n\n"},
[1485] = {title = "Familiares viles", objective = "Lleva 6 cabezas de familiar vil a Ruzan.", description = "Los brujos que llegaron antes que nosotros se ganaron la desconfianza de la Horda, y aunque se nos tolera, no somos realmente queridos.\n\nPero esto no ha servido para unir a los brujos, muchos de ellos viven recluidos, trabajando a solas en su magia. No pretendo conducirte a uno u otro camino, sino ayudarte a madurar.\n\nAntes de enseñarte las artes de la invocación, tráeme seis cabezas de los familiares viles que encontrarás en la cueva del Noreste.", progress = "Es importante que puedas controlar a los seres que invocas a través de tu magia. Son tan pocos los interesados en aprender las artes del <clase> que no puedo correr el riesgo de hacerte perder la vida enseñándote algo si no estás realmente preparado.", completion = "A medida que avance tu instrucción como &lt;clase&gt; aprenderás a invocar a seres de muchos tipos. Algunos de ellos quizás no lo parezcan, pero son instrumentos muy útiles.\n\nQue no te engañe el pequeño tamaño del diablillo. Como ya habrás observado es un adversario temible.", rewards = "Aprenderás:\n\nInvocar a diablillo\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a diablillo\n\nTambién recibirás:\n\n"},
[1486] = {title = "Pellejo de los descarriados", objective = "Nalpak de las Cuevas de los Lamentos quiere 20 cueros descarriados.", description = "Cuando Naralex se hundió en su pesadilla, una extraña raza de bestias salió de debajo de Los Baldíos y se adentró en las Cuevas de los Lamentos.\n\nEstas criaturas descarriadas tienen propiedades extrañas y como de otro mundo. Aunque su naturaleza es malvada, creo que puede venir algo bueno de su presencia aquí en Kalimdor. Creo que sus pieles serán especialmente útiles para la peletería.\n\nSi crees que estás <preparado/preparada> para emprender esta tarea, aventúrate en las cuevas y hazte con algunos cueros descarriados.", progress = "Tengo mucho interés en examinar los cueros de las criaturas descarriadas que infestaban las cuevas. ¿Has podido recoger alguno, <clase>?", completion = "Tus esfuerzos reuniendo los cueros no serán olvidado, &lt;nombre&gt;.\n\nGracias por tu entrega y dedicación.", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos desviados escurridizosTalega de pellejo desviado\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTalega de pellejo desviadoLeotardos desviados escurridizos"},
[1487] = {title = "Erradicación de descarriados", objective = "Ebru de las Cuevas de los Lamentos quiere que mates 7 devastadores descarriados, 7 víboras descarriadas, 7 arrastrados descarriados y 7 colminfernos descarriados.", description = "Naralex tiene un fin muy noble.\n\nNuestro gran líder aspiraba a entrar en el Sueño Esmeralda y a ayudar a que estas duras tierras volvieran a convertirse en el exuberante bosque que fueron un día. Pero algo fue terriblemente mal.\n\nEl sueño de Naralex se convirtió en pesadilla y criaturas corruptas comenzaron a habitar las cavernas.\n\nMientras algunos discípulos de Naralex se esfuerzan por despertar a nuestro maestro, yo me ocupo de librar a las cuevas de esas malvadas bestias.\n\nAventúrate a las cavernas, <nombre>, y erradica esa prole descarriada.", progress = nil, completion = "Como discípulo de Naralex no puedo sino elogiar tu valor, &lt;nombre&gt;.\n\nTu ayuda en el asalto de las cavernas es el primer paso en nuestra larga lucha por restaurar Los Baldíos.\n\nGracias y mucha prosperidad.", rewards = "Podrás elegir una de estas recompensas:\n\nPatrón: cinturón de escamas desviadasPalo de frituraGuanteletes de Damire\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nPatrón: cinturón de escamas desviadasGuanteletes de DamirePalo de fritura"},
[1488] = {title = "El corruptor", objective = "Mata a Lord Azrethoc y a Jugkar Grim'rod y ve a ver a Takata Hojacero a Desolace.", description = "Aunque no sé nada de este Azrethoc, coincido con Maurin en que debemos detenerlo, sobre todo si está intentando ganar influencia en Filo Ardiente.\n\nYa has demostrado tu voluntad de ayudar al Jefe de Guerra; te confío la tarea de buscar un equipo para eliminar al señor demoníaco y su siervo brujo.\n\nEncontrarás al demonio hacia el sur; vuelve cuando hayas terminado con él.", progress = nil, completion = "Disfruta de tu gran victoria, &lt;nombre&gt;. Gracias a ti, el futuro no parece tan amenazador como creía. \n\nEliminaremos a los demonios, Filo Ardiente caerá y nosotros volveremos a tener un hogar.", rewards = "Podrás elegir una de estas recompensas:\n\nRodela de basaltoEspaldares Déspota\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nRodela de basaltoEspaldares Déspota"},
[1489] = {title = "Hamuul Runatótem", objective = "Habla con Hamuul Runatótem.", description = "Los caparazones que me has traído están contaminados, nunca había visto algo así. Estoy seguro de que el agua de los oasis es la causa, y puedo sentir la confusión que causan en esta tierra. Es como si la tierra soñara y no pudiera despertar.\n\nLas Cuevas de los Lamentos deben de ser la fuente de la contaminación. Pero antes de ir allí habla con Hamuul Runatótem, el druida anciano. Ya le hice saber lo que está ocurriendo en Los Baldíos. Es sabio y podrá ofrecerte consejo.\n\nLo encontrarás en Cima del Trueno, en el Alto de los Ancestros.", progress = nil, completion = "Mis más cordiales saludos, &lt;nombre&gt;. Eres bienvenido entre nosotros. Te convendría hacer un acopio de todas tus fuerzas... la tarea que te espera... es extremadamente ardua.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1490] = {title = "Nara Bravacrín", objective = "Habla con Nara Bravacrín.", description = "Entré en el país de los sueños, y soñé con un culto de druidas que habitaba en las Cuevas de los Lamentos. Vi que sus fines fueron nobles, pero se echaron a perder. Ahora sirven a la corrupción que se está filtrando por Los Baldíos.\n\nSoy viejo y mi fuerza se desvanece. No puedo morar mucho tiempo en el país de los sueños y sé muy poco sobre los druidas corruptos. Pero Nara es joven y su espíritu implacable. Ella permaneció sumergida en la ensoñación cuando yo tuve que huir.\n\nHabla con Nara. La encontrarás en la tienda, ahí mismo.", progress = nil, completion = "Sí, es cierto. Ya conocía a los druidas de las Cuevas de los Lamentos y he visto sus rostros retorcidos y demacrados. Conozco sus crímenes y sus planes.\n\nDebes detenerlos, &lt;nombre&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1491] = {title = "Las bebidas astutas", objective = "Llévale 6 unidades de esencia de lamentos a Mebok Mizzyrix en Trinquete", description = "Bueno, sí, los cuernos de raptor no funcionaron muy bien. Pero no importa, ¡nadie se hace rico a la primera! Y además, creo que he encontrado algo ¡que funcionará aún mejor que mis bebidas astutas!\n\nHay una sustancia llamada esencia de lamentos y creo que es la causa de la extraña fauna y flora de Los Baldíos. ¡Consígueme algo de esa esencia y la probaré en mi nueva bebida!\n\nHe oído que se concentra en las criaturas ectoplásmicas de las Cuevas de los Lamentos hacia el oeste. Caza a esas criaturas y recolecta la esencia.", progress = "¿Dónde está mi esencia de lamentos?", completion = "¡Genial, la tienes! Me muero por probarla. Conseguiré una fortuna con esto, ¡estoy seguro!\n\nGracias, &lt;nombre&gt;. Esto es para ti, y cuando me haga rico, te enviaré más, ¡prometido!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1492] = {title = "Vertipeluca, el maestro de muelles", objective = "Llévale el cajón de seguridad a Vertipeluca, el maestro de muelles, en Trinquete.", description = "Necesito enviar una caja de pociones, componentes y otras cosas a mis socios del Viejo Mundo. Apenas tengo tiempo de llevarlos al embarcadero de Trinquete y la última caravana ya ha salido.\n\nSi puedes, lleva esto al maestro de los muelles Vertipeluca. Se asegurará de que sale en el primer barco hacia Cala Aguasnegras.", progress = "¿Buscas pasaje en el barco siguiente o quieres enviar algo a bordo?", completion = "Una caja lo suficientemente pequeña. Sí, debería encontrar un hueco a bordo para esto. Zarpa con la próxima marea, suficientemente pronto, ¿verdad? Fantástico. Permite que lo registre en mi libro de contabilidad.\n\n¡Ya está todo! Que tengas un buen día, &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[1498] = {title = "Camino de defensa", objective = "Lleva 5 escamas chamuscadas a Uzzek en Avanzada del Puente en Los Baldíos.", description = "Una de las tareas más importantes de un guerrero es... la protección. Somos el escudo de la Horda y mantenemos a salvo a nuestros hermanos más débiles. Si vas a unirte a nuestras filas debes demostrarme tu entereza.\n\nVe al Monte del Trueno en Durotar, donde viven las lomorayo y los truenagartos. ¡Caza a esas bestias! ¡Demuéstrame que eres más fuerte que ellos! Resiste sus ataques y tráeme un fardo de sus escamas chamuscadas como prueba.\n\nHazlo y te instruiré.", progress = "¿Tienes las escamas, <nombre>? Si no puedes resistir los ataques de los truenagartos, mi instrucción no te servirá de nada.", completion = "Ah. Veo que has derrotado a los truenagartos, una buena prueba de tu dureza. Ahora te enseñaré como pueden combinarse una piel resistente y unos movimientos diestros para dar forma a un muro de defensa impenetrable.\n\nAprende bien la lección, &lt;nombre&gt;, y prepárate para tu futura instrucción.", rewards = "Te van a lanzar el siguiente hechizo:\n\nCamino de la defensa\n\nRecibirás:\n\n"},
[1499] = {title = "Familiares viles", objective = "Habla con Zureetha Vistalonga.", description = "Zureetha ha estado enviando novicios como tú para ayudar a acabar con las criaturas que habitan la cueva cercana al lugar en el que combatiste a los familiares viles.\n\nCreo que te convendría ayudarle y ganar experiencia. No olvides decirle que me ayudaste con el problema de los diablillos, seguro que te dará alguna recompensa.", progress = nil, completion = "Muy bien hecho, &lt;nombre&gt;.\n\nAunque los familiares viles no son más que mascotas de los poderes oscuros del Filo Ardiente, tu victoria presagia grandes hazañas.", rewards = "Podrás elegir una de estas recompensas:\n\nHoja de mano primitivaBastón de apoyo primitivo\n\nTambién recibirás:\n\nHoja de mano primitivaBastón de apoyo primitivo"},
[1500] = {title = "Naralex ambulante", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1501] = {title = "Criatura abisal", objective = "Lleva la tablilla de Vergua a Gan'rul Ojo sangriento a Orgrimmar.", description = "Quedan tan pocos brujos... Tienes que perfeccionar tus habilidades rápidamente si quieres ser útil a la Horda y al Jefe de Guerra.\n\nTe voy a enseñar otro hechizo de invocación.\n\nPero antes tienes que recuperar la tablilla de Vergua; la robaron unos brujos de Filo Ardiente y ahora está en su guarida de Roca del Cráneo, al este de la ciudad. La tablilla tiene las runas necesarias para invocar a un abisario.", progress = "¿Traes la tablilla de Vergua, <nombre>? No te hablaré hasta que la traigas.", completion = "Espero que causaras bajas en Filo Ardiente cuando fuiste a buscar esto.\n\nEstán molestando mucho al Jefe de Guerra y a los brujos en general.", rewards = "Recibirás:\n\n"},
[1502] = {title = "Thun'grim Vistafuego", objective = "Habla con Thun'grim Vistafuego en Los Baldíos.", description = "Es fundamental que el arma de un guerrero sea de calidad. Y si hay un orco que pueda hacer armas así es Thun'grim Vistafuego. Es un ermitaño que vive en la cumbre de las colinas al este de El Cruce.\n\nSi buscas un arma, <guerrero/guerrera>, viaja hacia el Oeste, hasta El Cruce, y cuando llegues sigue hacia el Este, a las colinas. Su campamento está en la cumbre.", progress = nil, completion = "Mis ojos se secaron hace mucho tiempo por mirar fijamente la llama de la forja, pero hay cosas que aún puedo ver. Puedo ver que quieres algo.\n\nY si viniste hasta aquí creo saber qué deseas.", rewards = "Recibirás:\n\n"},
[1503] = {title = "Acero fundido", objective = "Lleva los lingotes de acero fundido a Thun'grim Vistafuego en Los Baldíos.", description = "Te daré un arma, <nombre>, pero antes debes ayudarme. Los Crines de Acero del Noroeste, vinieron a mi campamento hace unas noches. No se enfrentarían a mí en combate, pero saben que mis ojos no son buenos... unos cuantos me distrajeron con bramidos, ¡y mientras el resto se dedicaba a robarme! ¡Cobardes!\n\nCogieron un cofre de hierro en el que había algunos lingotes de acero fundido. Si puedes recuperarlos te daré el arma que deseas.\n\nBusca la caja de hierro en los campamentos de jabaespines y vuelve con los lingotes de acero fundido.", progress = "¿Tienes mis lingotes de acero?", completion = "¡Los encontraste! Están todos los lingotes, no me sorprende. No creo que los Crines de Acero supieran qué hacer con un acero como este.\n\nGracias, &lt;nombre&gt;. Aquí tienes tu recompensa. Espero que te sea útil.", rewards = "Podrás elegir una de estas recompensas:\n\nHacha de Thun'grimMaza de Thun'grimDaga de Thun'grimEspada de Thun'grim\n\nTambién recibirás:\n\nHacha de Thun'grimDaga de Thun'grimMaza de Thun'grimEspada de Thun'grim"},
[1504] = {title = "El vínculo", objective = "Usa los glifos de invocación para invocar y dominar a un abisario y lleva los glifos a Gan'rul Ojo sangriento a Orgrimmar.", description = "Verás que la tablilla contiene los glifos de invocación. Llévatela al círculo de la carpa de Neeru Hojafuego, que está al otro lado de la caverna; allí podrás invocar al abisario.\n\nDerrótalo con cualquier medio físico o mágico de que dispongas. Luego devuélveme los glifos de invocación y te enseñaré cómo ligar a la criatura a tu voluntad.", progress = "Los abisarios tienen mucho poder. Recuerdo cuando empecé como tú ahora, <nombre>. Con el tiempo mi abisario va luchando menos conmigo, pero creo que espera que baje la guardia.\n\nNo lo olvides.", completion = "Buen trabajo, &lt;nombre&gt;. Voy a enseñarte a invocar un abisario parecido al que acabas de derrotar.\n\nHas de saber que tenemos grandes esperanzas puestas en ti y hasta ahora no nos has decepcionado.\n\nPero no te confíes; las tareas que te esperan harán que esto te parezca un paseo por el bosque.", rewards = "Aprenderás:\n\nInvocar a abisario\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a abisario\n\nTambién recibirás:\n\n"},
[1505] = {title = "Uzzek, el veterano", objective = "Habla con Uzzek en Avanzada del Puente.", description = "La Horda honra a sus guerreros porque su fuerza es incuestionable. Pero los guerreros realmente grandes son también grandes alumnos.\n\nBusca a Uzzek. Es veterano de muchas batallas y ahora instruye a jóvenes guerreros en las artes del combate. Lo encontrarás al sur de la Avanzada del Puente, la plaza fuerte orca en la frontera de Los Baldíos y Durotar al Este.", progress = nil, completion = "Hola, joven. Has venido en busca de instrucción, ¿verdad?", rewards = "Recibirás:\n\n"},
[1506] = {title = "Las invocaciones de Gan'rul", objective = "Habla con Gan'rul Ojo sangriento en Orgrimmar.", description = "¡Eh, tú! <nombre>, ¿verdad?\n\nEspero que no tengas la agenda muy llena porque Gan'rul quiere verte ahora mismo. Lo encontrarás con los brujos mayores en el Circo de las Sombras.\n\nSegún mi experiencia con Gan'rul, esto de que te convoque solo puede ser muy bueno o muy malo.", progress = nil, completion = "Bien. Temía que Ophek no llegara a ti.\n\nEs hora de que continúe con tu instrucción de &lt;clase&gt;. Cualquier cosa que tengas entre manos ahora, tendrá que esperar.\n\nEspero que eso no sea un problema.", rewards = "Recibirás:\n\n"},
[1507] = {title = "Devoradora de almas", objective = "Habla con Cazul en el Circo de las Sombras.", description = "Ya a estás a punto para aprender más invocaciones. Te voy a enseñar a invocar a un sayaad, una criatura con unos poderes mentales increíbles. Puede esclavizar a los débiles, pero tú no tienes de qué preocuparte; seguro que resistirás.\n\nPara invocarlo, tienes que atraerlo con un objeto que simbolice amor y entrega. Ve a buscar un objeto adecuado.\n\nSi me permites un consejo, habla con Cazul; no hay nada que ese brujo no sepa.", progress = nil, completion = "&lt;nombre&gt;... Acabas de emprender este sendero, pero percibo un gran potencial en ti.\n\nNaciste con muchos dones, &lt;nombre&gt;; no los malgastes.", rewards = "Recibirás:\n\n"},
[1508] = {title = "Cazul, el ciego", objective = "Habla con Zankaja en Orgrimmar.", description = "Vienes de parte de Gan'rul, lo que quiere decir que vas a invocar un súcubo, una hembra de la raza sayaad, y necesitas un objeto que lo atraiga.\n\nMmm... Si confías en que un ciego te guíe, <nombre>, presta atención. En los riscos de Orgrimmar, al oeste de la torre de los jinetes del viento, vive Zankaja, que espera el regreso de su compañero.\n\nVe a verla.", progress = nil, completion = "¿Traes noticias de Dogran? Hace días que tendría que haber vuelto.", rewards = "Recibirás:\n\n"},
[1509] = {title = "Noticias de Dogran", objective = "Habla con Gazrog en El Cruce.", description = "A Dogran lo ascendieron y lo destinaron a El Cruce. Estaba muy contento.\n\nMe daba pena que se fuera, pero no se lo dije. Y también estaba preocupada. Los Baldíos son más peligrosos que Orgrimmar y me temía lo peor.\n\nYa hace una semana que tendría que haber vuelto y no sé nada de él...\n\n¿Puedes ir a El Cruce a preguntar? Habla con Gazrog, estaban destinados juntos.", progress = nil, completion = "¿Zankaja, la compañera de Dogran?\n\nSí que sé dónde está, pero no creo que a ella le guste saberlo...\n\nNo está muerto, pero sí herido y prisionero de los jabaespines.", rewards = "Recibirás:\n\n"},
[1510] = {title = "Noticias de Dogran", objective = "Habla con Ken'zigla en Malaka'jin.", description = "Estaba destinado al Campamento Taurajo, intentando erradicar a los jabaespines. Un día los siguió hasta el campamento, donde fue atacado por esas bestias. Sus compañeros lo rescataron, pero estaba muy grave. Sus heridas tenían muy mala pinta y no se atrevían a moverlo.\n\nNos pidieron una poción sanadora de los trols de Malaka'jin, al pie de Sierra Espolón. Enviamos a un mensajero, pero todavía no ha vuelto.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1511] = {title = "Poción de Ken'zigla", objective = "Lleva la poción de Ken'zigla al bruto Logmar al Campamento Taurajo.", description = "Imaginaba que vendría alguien más a por la poción, así que la tengo preparada.\n\nAquí tienes, <nombre>. Recuerda, ¡dale solo la mitad! Con eso podrán desplazarlo sin que se desangre. Al día siguiente, dale la otra mitad.\n\nLlévasela a Logmar, que está en el Campamento Taurajo. Por lo que me dijo el primer mensajero, él sabe dónde está el tal Dogran.", progress = "Sí, ¿qué quieres?", completion = "¡Ya era hora! Dogran se estaba muriendo.\n\nTenemos que enviársela, pero los brutos se han ido de misión.", rewards = "Recibirás:\n\n"},
[1512] = {title = "Un regalo de amor", objective = "Lleva el colgante sucio a Gan'rul Ojo sangriento a Orgrimmar.", description = "Zankaja es joven y guapa, no puede encadenarse a un lisiado; no permitiré que malgaste su vida.\n\nDéjame solo, <nombre>. Y gracias. Así puedo caer honrosamente en combate, en lugar de extinguirme como un vegetal.\n\nY por favor, no le digas nada a ella, no quiero que se sienta culpable.", progress = "Has vuelto. ¿Te atreves a enfrentarte a un súcubo?", completion = "Si dices que con esto basta, te creo, &lt;nombre&gt;. Pero voy a limpiarlo un poco, así llamará más la atención al súcubo.", rewards = "Recibirás:\n\n"},
[1513] = {title = "El vínculo", objective = "Usa el colgante de Dogran para invocar y dominar a un súcubo y lleva el colgante a Gan'rul Ojo sangriento a Orgrimmar.", description = "Lleva eso al círculo de invocación de la carpa de Neeru. Invoca a un súcubo y demuéstrale lo que puedes hacer. Eres lo suficientemente fuerte. Pero sé prudente; la arrogancia y el exceso de confianza han sido la perdición de más de un buen brujo.\n\nCuando lo hayas derrotado, devuélveme el colgante para que pueda decirle a Zankaja lo que ha pasado.", progress = "Los súcubos me inspiran una mezcla de euforia y temor.\n\nTen mucho cuidado.", completion = "No esperaba menos de ti.\n\nHas probado tu valor; yo te enseñaré a invocar a tu súcubo dominado. Ve y sigue aprendiendo; la próxima vez que nos veamos, habrás dominado a tu súcubo.\n\nRecuerda la tragedia de Dogran y Zankaja como protección contra el poder ocuro que has aprendido a invocar hoy.", rewards = "Aprenderás:\n\nInvocar a súcubo\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a súcubo\n\nTambién recibirás:\n\nFaltriquera de almas pequeña"},
[1514] = {title = "Flor del viento corrupta", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[1515] = {title = "El cautiverio de Dogran", objective = "Lleva la poción de Ken'zigla a Dogran a Los Baldíos.", description = "Ya que has llegado hasta aquí, no seré yo quien te detenga. Pero no tengo personal que te acompañe, tendrás que ir por tu cuenta.\n\nLo tienen retenido en un campamento de jabaespines al norte de aquí; busca dos chozas juntas. Estará muy vigilado, si es que no lo han matado ya sus heridas o esos animales.\n\nSuerte.", progress = "Urrrg...", completion = "Qué asco...\n\nSiento que recupero las fuerzas, pero he perdido la pierna. Y el brazo también.", rewards = "Recibirás:\n\n"},
[1516] = {title = "Clamor de Tierra", objective = "Lleva 2 pezuñas de acechador vil a Clamor de Tierra Canaga en el Valle de los Retos.", description = "Ha llegado el momento, joven <clase>. Te estás haciendo fuerte y tu espíritu aguanta firme como la tierra. Al seguir la ruta del chamán has demostrado sabiduría antes incluso de haberte puesto a prueba.\n\nEl elemento tierra guiará tu destino, y pasará a formar parte de ti si estás <preparado/preparada>. Pero siempre debes mostrarle tu reconocimiento. Si estás <listo/lista> verás cosas que solo conoce un chamán.\n\nVe a buscar a los acechadores viles, al Norte, y coge dos de sus pezuñas. Cuando las tengas vuelve aquí y seguiremos hablando de tu futuro.", progress = "La paciencia es la mayor virtud de la tierra. La tierra es testigo del ciclo de todas las cosas: las montañas que se convierten en desiertos, los ríos convirtiéndose en cañones. La tierra es el más testarudo y sabio de todos los elementos y no tolera la impaciencia.\n\nLa tierra te reclama porque se acerca un tiempo de conflictos. Cuando vuelvas a mí triunfante, estarás mucho más cerca de la realización.\n\nOtros envidiarán tu sabiduría y te odiarán por su ignorancia.", completion = "Excelente. Tu éxito demuestra que estás preparado. \n\nDe entre todos los elementos, la tierra representa el cimiento de todas las cosas. Es tu poder, tu fuerza y tu paciencia. Como el resto de elementos, la tierra puede ser inconstante. La tierra es el fundamento de la vida y si sigues sus leyes crecerá tu sabiduría. Nunca olvides los secretos que la tierra comparte contigo. \n\nTe haré una sapta y, al aceptarla, completarás tu senda.", rewards = "Recibirás:\n\n"},
[1517] = {title = "Clamor de Tierra", objective = "Encuentra la Roca de los Espíritus y bebe la Sapta de tierra.\n\nObjeto provisto: \n", description = "Una sapta es una bebida creada para unir nuestros espíritus con los elementos. Cargada de magia, la poción permite a quien tiene fuerza de voluntad ver los elementos que nadie más puede ver. Tu espíritu se conectará con los elementos para los que fue creada la sapta, y son pocos los chamanes que conocen la receta.\n\nLa sapta siempre se bebe en un lugar sagrado, y en ningún otro sitio... recuérdalo.\n\nBusca la Roca de los Espíritus al suroeste de aquí, la encontrarás al final del sendero oculto. Cuando llegues, bebe la sapta, y... deberías poder ver.", progress = nil, completion = "Ah... &lt;nombre&gt;... la tierra se complace de tenerte aquí.\n\nComprometido en cuerpo y espíritu, avanzarás sabiendo que las montañas son tu fuerza, las llanuras tu paciencia y el mundo tu esencia.\n\nLa humildad ante la tierra es todo lo que necesitas para pasar este rito. Pero los otros no son tan pasivos. Pueden ser caóticos y violentos... tal es la naturaleza de los elementos, naturalezas duales en eterna oposición. Debes aprender estas cosas en toda su complejidad.", rewards = "Recibirás:\n\n"},
[1518] = {title = "Clamor de Tierra", objective = "Lleva el Cuarzo en bruto a Clamor de Tierra Canaga en el Valle de los Retos.", description = "Coge este cuarzo en bruto y llévaselo a Clamor de Tierra Canaga como prueba de nuestro encuentro. Te hará un tótem y este guijarro será su corazón. Puede parecer muy pequeño, pero descubrirás que el tamaño no importa, y que incluso las cosas más pequeñas pueden superar a otras.\n\nTen paciencia, <nombre>. Sigue siendo fuerte. Y busca la sabiduría. Es todo lo que te pide la tierra. Al final te harás <uno/una> con ella... respétala del mismo modo que ella te respeta a ti.", progress = "Quisiera ser el primero en darte la bienvenida a la primera de las cuatro sendas que guiarán tu destino.\n\nSi tienes la piedra que te dio la tierra te daré el tótem prometido.\n\nSi no la tienes, márchate hasta que encuentres tu camino.", completion = "¡Excelente!\n\nEl tótem que haré para ti será mucho más que un símbolo de tu estatus entre los chamanes... Tiene habilidades que van mucho más allá. Uno de los usos del tótem es el de actuar como foco de hechizos. Los hechizos están vinculados a la tierra y a medida que aumente tu sabiduría podrás acceder a más y más habilidades de la tierra.\n\nLos instructores sabrán cuándo estás preparado para aumentar tu conocimiento.\n\nPor ahora, toma este Tótem de Tierra...", rewards = "Aprenderás:\n\nTótem piel de piedra\n\nTe van a lanzar el siguiente hechizo:\n\nTótem piel de piedra\n\nTambién recibirás:\n\nTótem de tierra"},
[1519] = {title = "La llamada de la tierra", objective = "LLeva 2 dosis de bálsamo ritual a la profetisa Plumacuervo al Campamento Narache.", description = "<clase>, ha llegado el momento; tu cuerpo crece en edad y en fuerza y tu espíritu gana profundidad, como la tierra. Veo en tus ojos la sabiduría de tu alma. \n\nLa tierra guía a mi gente y nos habla, como te hablará a ti en el futuro. Pero debes ponerte a prueba. Si tu momento ha llegado, verás cosas que solo los nuestros pueden ver. \n\nBusca a los chamanes Erizapúas en el barranco que hay al este y coge un bálsamo que usan en sus rituales. Regresa cuando hayas conseguido el suficiente para crear tu propia poción.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1520] = {title = "La llamada de la tierra", objective = "Encuentra la Roca de los Kodos y bebe la sapta de tierra.\n\nObjeto provisto: \n", description = "La sapta es una bebida que nos ayuda a vincular el espíritu a los elementos. Con la magia adecuada, la poción ayuda a ver los elementos como nadie los ha visto antes. Tu espíritu quedará ligado para siempre al elemento para el que se creó la sapta. Solo unos pocos elegidos conocen la receta. \n\nSiempre debes beberte la sapta en un lugar sagrado; nunca jamás en otro sitio. \n\nVe al lugar santo de los chamanes, hacia el este, y bebe allí la sapta.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1521] = {title = "La llamada de la tierra", objective = "Lleva el cuarzo en bruto a la profetisa Plumacuervo al Campamento Narache.", description = "Toma este cuarzo bruto y llévaselo a la profetisa Ravenfeather como prueba de nuestro encuentro. Esta piedra será el centro del tótem que te hará la profetisa. Parece pequeña, pero aprenderás que el tamaño no importa: la cosa más insignificante puede derribar montañas. \n\nSé fuerte y paciente, <nombre>. Busca la sabiduría. Al final te vincularás a la tierra y la respetarás como ella te respeta a ti.Aprenderás:\n\n", progress = nil, completion = nil, rewards = "Aprenderás:\n\nTótem piel de piedra\n\nTe van a lanzar el siguiente hechizo:\n\nTótem piel de piedra\n\nTambién recibirás:\n\nTótem de tierra"},
[1522] = {title = "La llamada del Fuego", objective = "Busca a Kranal Fiss en Los Baldíos.", description = "Que el fuego te acompañe, colega. Veo que te manejas bien con la tierra. Creo que ya estás <listo/lista> para hablar con Kranal.\n\nKranal vive al norte de El Cruce, en Los Baldíos. Él sabe mucho de los elementos y puede enseñarte cosas del fuego... si crees que estás <listo/lista>.\n\nTe será fácil encontrarlo, pero no vayas muy lejos, o acabarás en las tierras de los elfos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1523] = {title = "La llamada del Fuego", objective = "Busca a Kranal Fiss en Los Baldíos.", description = "Ha pasado tiempo desde tu última prueba, <nombre>. Hasta ahora has demostrado paciencia en tus estudios, pero ha llegado el momento de que aprendas algo más. Debes aprender de la destrucción... y el caos. De un elemento: el fuego. Podrás comprenderlo, si estás <dispuesto/dispuesta> a pagar el precio.\n\nBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña casa al norte de El Cruce. Pero cuidado: si llegas hasta las tierras de los elfos de la noche, habrás ido demasiado lejos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1524] = {title = "La llamada del Fuego", objective = "Lleva la antorcha de la Llama Latente a Telf Joolam a Durotar.", description = "Ya tienes poder suficiente para llevar el tótem del fuego, pero eso no quiere decir que estés <listo/lista>.\n\nA lo largo del Río Furia del Sur de Durotar, antes del Mare Magnum, hay una senda oculta que lleva a uno de los picos más altos de la tierra. Ábrete camino hasta la cima. Allí, custodiado por Telf Joolam, encontrarás uno de nuestros santuarios: el Santuario de la Llama Latente.\n\nLleva esta antorcha a Joolam. Así sabrá que intentas domar el fuego. Enciéndela en el blandón del santuario y tráemela. Yo me encargaré de que te sea concedido el tótem de fuego.", progress = "Saludos, <nombre>. Te hallas en el Santuario de la Llama Latente. Solo aquellos que poseen un conocimiento suficiente de las artes chamánicas logran descubrir los secretos de este lugar y el poder que alberga. Siento que hayas hecho tanto camino para ser rechazado –sobre todo, un camino tan lleno de peligros– pero yo soy el guardián de este santuario y no puedo permitir que cualquiera acceda a él.", completion = "Ay, perdona, &lt;nombre&gt;. Te pido disculpas. No me he dado cuenta de que llevabas la antorcha. Me alegro de conocerte.\n\nMuchos son los aventureros que llegan hasta aquí buscando tesoros perdidos o míticas criaturas a las que matar y que vuelven sobre sus pasos, decepcionados, tras ver un templo aparentemente vacío. Pero nosotros sabemos algo que ellos ignoran, ¿no es cierto, &lt;nombre&gt;?\n\nEste templo guarda de los miembros mundanos de la Horda un gran secreto. Para nosotros, es sagrado.", rewards = "Recibirás:\n\n"},
[1525] = {title = "La llamada del Fuego", objective = "Lleva 1 alquitrán y 1 bolsa de componentes a Telf Joolam a Durotar.", description = "Pero tú no has venido aquí a recibir lecciones de historia, ¿verdad? Has venido aquí por la Llama Eterna y el tótem de fuego.\n\nEl primer paso es crear una sapta y unirte a la llama.\n\nPuedo crearte la sapta de fuego si me traes dos objetos. Primero necesito alquitrán. Un objeto sencillo que suelen llevar los encantadors Crines de Acero de la región este de Los Baldíos.\n\nEl segundo objeto será más difícil: una bolsa de componentes de uno de los adeptos del Filo Ardiente. Los encontrarás en una cueva que está sobre los barrancos del noreste de Cerrotajo.", progress = "Si me consigues la bolsa de componentes y algo de alquitrán, tendré ingredientes para crear la sapta de fuego. Los fanáticos del Filo Ardiente llevan en esas bolsas componentes para la mayoría de sus hechizos y suelen tener suficiente cantidad de los objetos que necesito, así que no habrá que buscar muy lejos.\n\nLas saptas siempre se hacen con ingredientes que están ligados a sus respectivos elementos. Eso nos facilita el trabajo por los lazos que unen a los brujos al fuego.", completion = "Esto servirá, &lt;nombre&gt;.\n\nEl fuego es algo más que caos y destrucción, recuérdalo. También es vida y creación. Todas las cosas están unidas entre sí. Las fuerzas se oponen unas a otras, pero a la vez, no pueden existir las unas sin las otras. Ambas pueden destruir a su opuesta, pero también le dan significado. Recuérdalo siempre.\n\nUtilizar diferentes tipos de magia en conjunción, no solo demuestra una gran habilidad, también una gran sabiduría.", rewards = "Recibirás:\n\nSapta de fuego"},
[1526] = {title = "La llamada del Fuego", objective = "Derrota a la Manifestación de fuego menor y coloca las ascuas ardientes sobre el altar de la llama eterna.", description = "La sapta de fuego está acabada. Ha llegado el momento de que afrontes la llama. Utiliza la sapta del santuario y sube a la cima de la montaña para enfrentarte a la aparición que encontrarás allí. Él no te atacará hasta que no estés <listo/lista>, pero se dará cuenta de que puedes verlo.\n\nAtaca con rapidez, <nombre>, pues cuanto más tardes en matarlo, más crecerá su poder. Una vez le hayas derrotado, coloca sus ascuas ardientes en el blandón y enciende la antorcha.", progress = "El blandón tiene aproximadamente un tercio de tu altura. Y parece normal, excepto por las inscripciones que hay en las bandas de metal. El metal no está desgastado por el tiempo y la pila que tiene en la parte superior está fría al tacto.", completion = "Una ardiente llama blanca surge en el cuenco cuando colocas el ascua en él. Puedes sentir los efectos mágicos de la sapta recorriendo tu cuerpo mientras ves cómo el fuego baila ante ti.\n\nColocas la parte superior de la antorcha en la pila y esta prende fuego inmediatamente.", rewards = "Recibirás:\n\nAntorcha de la Llama eterna"},
[1527] = {title = "La llamada del Fuego", objective = "Lleva la antorcha de la llama eterna a Kranal Fiss a Los Baldíos.", description = "Con la antorcha firmemente sujeta, te alejas del blandón y diriges la vista sobre Durotar. Sientes una excitación creciente que tira de tu espíritu a la vez que el viento agita la llama y la hace bailar con violencia.\n\nEmprendes el camino de regreso a Los Baldíos en busca de Kranal Fiss.", progress = "¿Sí, <nombre>? ¿Qué tienes ahí?", completion = "Tu espíritu arde con más brillo que ninguna antorcha, &lt;nombre&gt;, deberías estar orgulloso de ello.\n\nSi sigues por este camino, podrás dominar los elementos y nuestros hermanos acudirán a ti para que los guíes. Deja que sea el primero en felicitarte y en presentarte a tu nuevo tótem.", rewards = "Aprenderás:\n\nTótem abrasador\n\nTe van a lanzar el siguiente hechizo:\n\nTótem abrasador\n\nTambién recibirás:\n\nTótem de Fuego"},
[1528] = {title = "La llamada del agua", objective = "Encuentra a Islen Vident'aqua en Los Baldíos.", description = "Haber agua'n tu futuro, colega. Veo tú ser ya familiar con fuego, así qu’estarás <listo/lista> pa’ hablar con Islen.\n\nIslen vivir pa’llá por la costa de Los Baldíos. Si vas pa’l sur por el Río Furia del Sur, no se t’escapa, ¡buscar gran tiburón! Ella ser muy buena pa' temas d'agua... si creer que tú estar <listo/lista>.\n\n‘star tirao encontrarla, ir to’ pa’l sur, ella estar pasao Trinquete.", progress = nil, completion = "El viento viene presuroso hacia ti desde el horizonte, sientes la tierra firme bajo tus pies y tu espíritu ya siente el calor del fuego; diría que ha llegado la hora de que aprendas cosas sobre la pureza del agua.\n\nTu camino te ha conducido hasta aquí y tu espíritu te dará la fuerza para llegar aún más lejos. Pero tú has de dar el primer paso.", rewards = "Recibirás:\n\n"},
[1529] = {title = "La llamada del agua", objective = "Encuentra a Islen Vident'aqua en Los Baldíos.", description = "Mucho tiempo ha pasado desde que a prueba te pusieron, <nombre>. Paciencia en tus estudios hasta ahora has mostrado pero de aprender aún más la hora ha llegado. Ha llegado la hora de que aprendas cosas sobre la pureza del agua.\n\nBusca a Islen Vident'aqua en Los Baldíos. Se encuentra por la costa oriental, al sur del Río Furia del Sur y de Trinquete. La encontrarás en su cabaña de pesca. Que la Fortuna te sea favorable, <nombre>.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1530] = {title = "La llamada del agua", objective = "Encuentra a Salmuera en los Baldíos del Sur.", description = "Si hay una lección que has de aprender acerca del agua, es esta: el agua significa renacimiento.\n\nSu energía fluye, erosiona y limpia todo tipo de cosas. Su capacidad de curación y de reconstitución te parecerá sin parangón pero solo se da cuando es pura.\n\nCuando entiendas que si el agua está contaminada, puede causar estragos en todo aquello que toca, te darás cuenta de cuán vital es este recurso.\n\nSi deseas la sapta del agua, encuentra a Brine en lo más recóndito de los Baldíos del Sur. Los jabaespines la importunan constantemente en su hogar.", progress = nil, completion = "¿Sí?\n\nMm, me parece que sé lo que vas a preguntarme: ¿qué posee a alguien para vivir en el más remoto rincón de Los Baldíos, rodeado de la amenaza de los jabaespines y sin protección? Bueno, todos tenemos nuestros motivos para justificar la forma en que actuamos. La verdadera cuestión es qué trae a un humilde &lt;raza&gt; a lo más recóndito de Los Baldíos para hablar con una mujer que a todas luces no se preocupa por su propio bien.\n\nApostaría que ambos conocemos la respuesta.", rewards = "Recibirás:\n\n"},
[1531] = {title = "La llamada del aire", objective = "Encuentra a Prate Nubeprofeta en Las Mil Agujas.", description = "Tú estar haciendo progresos, colega. Estar <preparado/preparada> para demostrar si merecer recibir Tótem de Aire, pero antes encontrar a Prate Nubeprofeta en Las Mil Agujas debes. Si tratas a ella con respeto, ella dirá lo que tener que hacer para conseguir tu Tótem de Aire.\n\nVe al sur por Los Baldíos y al llegar a Las Mil Agujas, hacia el este. Encontrarás a ella por allí escondida con su novio... ¡Ja, ja, ja! No digas que yo dije... ¡a ella no gustar!", progress = nil, completion = "De momento, te daré lo que necesitas para realizar tus hechizos e invocar a los espíritus del aire. Coge este tótem y, cuando estés preparado, ven a verme para continuar con tu instrucción.", rewards = "Te van a lanzar el siguiente hechizo:\n\nViento veloz\n\nRecibirás:\n\nTótem de aire"},
[1532] = {title = "La llamada del aire", objective = "Encuentra a Prate Nubeprofeta en Las Mil Agujas.", description = "Los vientos de Kalimdor se apresuran por conocerte. Tú serás uno de nuestros más poderosos <hermanos/hermanas>. Me llena de orgullo comprobar que has llegado hasta aquí y que cuentas con un prometedor futuro.\n\nEl aire es un elemento difícil de controlar pero confío en que tus habilidades te sirvan para superar todos los obstáculos que te encuentres.\n\nDirígete al sur de Los Baldíos y ve a Las Mil Agujas. Allí, en la zona este, encontrarás a Prate Nubeprofeta. Ella te podrá enseñar mejor.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nViento veloz\n\nRecibirás:\n\nTótem de aire"},
[1534] = {title = "La llamada del agua", objective = "Llena la bota de agua azul vacía en las Ruinas del Polvo Estelar en Vallefresno y llévasela a Salmuera en Los Baldíos.", description = "Toma esta bota y adéntrate en el frondoso bosque que los elfos de la noche llaman Vallefresno. Está en un lugar remoto del norte de Los Baldíos.\n\nCuando hayas penetrado en el bosque, dirígete hacia el oeste. Por las montañas que separan Espolón y Vallefresno, y más allá del Lago Mystral, encontrarás un lugar sagrado: las Ruinas del Polvo Estelar. En el centro del lago encontrarás una fuentecilla.\n\nTráeme una muestra del agua de esa fuente. Será el último componente que necesitarás antes de que te mande de nuevo con Islen.", progress = "¿Tienes ya el agua, <nombre>? Según la leyenda, las ruinas estaban protegidas por colosales criaturas de la naturaleza que no acogerían muy bien a los visitantes pero podrían ser mitos para espantar a los enemigos de los elfos de la noche.\n\nSea como fuere, ten cuidado.", completion = "Maravilloso, &lt;nombre&gt;. Con las 3 muestras reunidas, puedo crear un vial con el agua más pura que ninguna criatura viviente haya conocido jamás. Se usará en los próximos pasos de tu rito de paso.\n\nMe alegra que estés de nuestro lado, &lt;nombre&gt;. Cualquier &lt;clase&gt; estaría orgulloso de ti y espero oír relatos sobre tus hazañas en el futuro.", rewards = "Recibirás:\n\n"},
[1535] = {title = "La llamada del agua", objective = "Llena la bota de agua marrón vacía en el surtidor que está debajo de la cabaña de Salmuera y llévasela a Los Baldíos.", description = "Para adquirir el conocimiento y ganarte la sapta, debes buscar solo las formas más puras de agua. Conforme recoges muestras para mí, observa a tu alrededor. Ten cuidado con las criaturas que dependen del agua, que la protegen. Fíjate en cómo afecta a los bosques y a los desiertos. Presencia cómo la mismísima sustancia en la que se sustenta la vida, obliga a las criaturas a arrebatar vidas.\n\nEmpezaremos con una tarea simple: debajo de mi cabaña hay un estanque del preciado líquido. Llena este odre y vuelve aquí.", progress = "Casi todo está listo para enviarte a tus tareas siguientes. ¿Has llenado ya el odre?\n\nSé cuán protector puede ser el jabaespín. Hasta una fuente tan exigua de agua es un vasto tesoro oculto para ellos. Tienen lugar grandes celebraciones cuando la más mínima lluvia cae sobre Los Baldíos. El agua es sagrada para su especie por su escasez. ¿Lo entiendes?", completion = "Creía que se produciría un baño de sangre si intentabas llevarte agua de la poza. Es una pena que el jabaespín no se dé cuenta de que todos los seres vivientes son interdependientes y que su avaricia solo perpetúa el dolor y el sufrimiento de su pueblo. ¡Cuánto podrían logras si dejaran a un lado sus diferencias y sus clanes se unieran!\n\nInsisto, es una lástima, pero es más que probable… que sea un favor para las otras razas de Los Baldíos.", rewards = "Recibirás:\n\n"},
[1536] = {title = "La llamada del agua", objective = "Llena la bota de agua roja vacía en el pozo de Molino Tarren y llévasela a Salmuera en Los Baldíos.", description = "Hace mucho tiempo seguí a unos compañeros hasta las Laderas de Trabalomas. Nos defendimos de tenaces humanos que allí moran y acabamos en un pueblecito que los Renegados habían tomado: Molino Tarren.\n\nAllí fue donde encontré algo muy especial: en el centro del pueblo había un pequeño pozo con agua dulce de manantial. No sé muy bien si los lugareños infestados se daban cuenta de su valor, es un pequeño vestigio sagrado en un mar sin consagrar.\n\nTráeme una muestra de esa agua en esta bota.", progress = "El pozo que permanece puro en el pueblo me recuerda historias que oí cuando era más joven, <nombre>. Incluso en tiempos de gran sufrimiento y pesadumbre, se encuentra un rayo de esperanza, un pequeño símbolo junto al que cobijarse. Así ven a Thrall los orcos. Sobrevivió a los mayores peligros y se erigió en el líder de nuestro pueblo gracias a su fuerza y a su ejemplo.\n\nNo creo que ningún <raza> haya sufrido tanto como él pero tampoco creo que ningún <raza> sean tan fuerte como él.", completion = "Esto es bueno, &lt;nombre&gt;. Te entregas a la tarea que te ocupa y tienes la facultad de procurarte lo que hace falta.\n\nSolo necesito una muestra más para crear el componente necesario para tu sapta. Islen también se alegrará.", rewards = "Recibirás:\n\n"},
[1558] = {title = "La Presa de Fraguapiedra", objective = "Lleva al huérfano a la Presa de Fraguapiedra en Loch Modan. Deberías llevarle al centro de la presa para que pueda mirar por encima de la gigantesca cascada.", description = "En Forjaz viven todos los enanos y gnomos. Cerca hay un sitio llamado Loch Modan y los enanos están construyendo allí una presa ENORME. Dicen que es una de las cosas más grandes que se han hecho nunca. ¡Guau!\n\n¿Podrías llevarme a Loch Modan para ver la presa, por favor? Creo que es la Presa de Traga... ¿Tragapiedra? ¿Fraguapiedra? Bueno, es muy grande, qué más dará el nombre. Me gustaría ver la cascada desde arriba. Te prometo que no me asomaré mucho.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1559] = {title = "Receta de bomba cegadora", objective = "¿Conque eres ingeniero, eh? Bueno, por tu ayuda para conseguir esas perlas, aquí tienes la receta de las bombas centelleantes.\n\n¡Suerte y ojito con dejarte ciego!", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEsquema: bomba cegadora"},
[1560] = {title = "La misión de Tooga", objective = "Muéstrale a Tooga el camino de vuelta con su esposa, Torta.", description = "¡Estoy totalmente perdido!\n\nMi esposa, Torta, me envío a buscar la cena y en algún momento debí de coger el camino equivocado.\n\n¿Serías tan amable de llevarme de vuelta con mi esposa Torta? Ella me está esperando al sur del Puerto Bonvapor.", progress = nil, completion = "¿Así que no fue capaz de encontrar el camino de vuelta él solo? Figúrate tú...\n\nBueno, supongo que mereces algo por tus esfuerzos...", rewards = "Podrás elegir una de estas recompensas:\n\nPuños chelonianosSortija de la Gran Tortuga\n\nTambién recibirás:\n\nPuños chelonianosSortija de la Gran Tortuga"},
[1578] = {title = "Abastecer al frente", objective = "Lleva 6 hachas y 6 cinturones de malla de cobre a Thorvald a la torre de vigilancia sur de Loch Modan", description = "Los montaraces de Loch Modan están luchando denodadamente contra los trogg. Necesitan un suministro constante de armaduras y armas.\n\nPor eso está allí mi colega Thorvald, encargándose del equipamiento de los montaraces. Seguro que te agradecerá que le fabriques unas hachas y unos cinturones de cobre. Puede incluso que comparta su sabiduría contigo.\n\nThorvald está en la torre sur de Loch Modan.", progress = "No tengo tiempo de charlar. ¿Querías algo?", completion = "¡Ah, perdona! No había visto que traes suministros. ¡Nos serán de gran ayuda!\n\nTen, es el patrón de una armadura; espero que te sea útil.", rewards = "Recibirás:\n\nDiseño: jubón de anillas de cobre"},
[1579] = {title = "Mecanismos eléctricos", objective = "Lleva 8 mecanismos eléctricos a Maniveo Zumbang a Auberdine.", description = "Vine a Auberdine por su tranquilidad. Quería inventar mis inventos en un sitio tranquilo. El ajetreo de Forjaz era demasiado para mí cuando había pasado la noche bebiendo.\n\nAsí que me embarqué hacia Auberdine. Pero bebí demasiado durante la travesía. ¡Y se me cayó la caja de mecanismos eléctricos por la borda! ¡Necesito mis mecanismos!\n\nNo sé exactamente dónde se me cayeron, pero si puedes ir de pesca al océano, quizás tengas suerte...", progress = "¿Has encontrado mis mecanismos eléctricos, <nombre>? ¡Sin mis mecanismos eléctricos no puedo ajustarme las clavijas!", completion = "¡No puedo creer que los hayas encontrado! ¡Eres sin duda el ser más afortunado de Kalimdor!", rewards = "Recibirás:\n\n"},
[1580] = {title = "Los electromuelles", objective = "Lleva 12 electromuelles a Maniveo Zumbang a Auberdine.", description = "Sabemos que tienes mucha suerte, pero... ¿hasta dónde llega, <nombre>?\n\nUna noche, estaba yo deambulando por los caminos de Costa Oscura... Iba buscando un buen río o lago para probar mi nuevo señuelo-pato autopropulsado explosivo y... creo que estaba un poco alegre porque perdí el conocimiento y me levanté horas después en Auberdine. ¡Tenía el pelo quemado y había perdido mi bolsa de electromuelles! ¡Seguro que se me cayeron al agua!\n\n¡Necesito mis electromuelles! Quizás puedas pescarlos de algún lago o río de Costa Oscura...", progress = "¿Encontraste algún electromuelle? Los necesito para continuar mis pruebas con el señuelo-pato...", completion = "¡No puedo creerlo! ¡Los has encontrado! ¡Eres el mejor pescador que he conocido en mi vida!\n\nGracias, &lt;nombre&gt;. Y ten, acepta esto. Aunque eres tan bueno que probablemente no te hacen falta, te ayudarán.", rewards = "Recibirás:\n\nLentes de peces acuadinámicas"},
[1581] = {title = "Elixires para Filohoja", objective = "Llévale 6 elixires de fuerza de león y 2 elixires de defensa menor a Syral Filohoja, que está en Dolanaar.", description = "Nuestra tienda de alquimia y hierbas marcha bien, pero mi marido pasa tanto tiempo preparando pociones que casi nunca tenemos tiempo para nosotros.\n\n¿Podrías ayudarnos?\n\nSi me pudieras traer un pedido de elixires prefabricados, estaría encantada de hacer negocios contigo. Tengo algunas hierbas raras que puede que encuentres útiles.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nRaíz terrenalMarregal"},
[1582] = {title = "El jubón de brillo lunar", objective = "Lleva 1 guante de cuero estampado, 5 botas de cuero estampado y 5 capas de cuero estampado a Lotherias a Darnassus.", description = "En Darnassus estamos muy ocupados, sobre todo los artesanos. Los jóvenes aventureros están impacientes por probar sus habilidades en las tierras salvajes y encargan suministros constantemente.\n\nSi pudieras ayudarme a atender mis pedidos de artículos de cuero, yo podría enseñarte una técnica de peletería avanzada.", progress = "Saludos, <nombre>.", completion = "Son unas buenas piezas. Tienes manos hábiles y un buen ojo para los detalles. Tu futuro como peletera es prometedor.\n\nTen, &lt;nombre&gt;. Deja que te enseñe el patrón para hacer el jubón de brillo lunar.", rewards = "Recibirás:\n\nPatrón: jubón Resplandor lunar"},
[1598] = {title = "El libro robado", objective = "LLeva los Poderes del vacío a Drusilla La Salle.", description = "¡Eh, tú! Eres <nombre>, ¿verdad?\n\nEspero que estés muy seguro de que quieres convertirte en brujo, porque es bien difícil.\n\nYo te instruiré, pero antes debo asegurarme de que tienes lo que hay que tener.\n\nTeníamos un novicio en la abadía que huyó para unirse a la Hermandad Defias. ¿Que qué más da? Pues da que robó unos libros importantes.\n\nYo quería estudiar uno de ellos, Poderes del vacío. Tráemelo; lo tienen escondido en su carpa de los Viñedos.Aprenderás:\n\n", progress = nil, completion = nil, rewards = "Aprenderás:\n\nInvocar a diablillo\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a diablillo\n\nTambién recibirás:\n\n"},
[1599] = {title = "Novato", objective = "Lleva 3 talismanes de plumas a Alamar Grimm a Yunquemar.", description = "Debes de ser <el nuevo brujo del/la nueva bruja de la> que habla todo el mundo. Has debido de impresionar a alguien, si no, no me pedirían que robara tiempo a mis investigaciones para instruirte.\n\nNo te preocupes, no te lo echaré en cara.\n\nLa forma más sencilla de conjurar hechizos es la del diablillo. Pero antes de que te imparta tal conocimiento, debes probarme que tienes las habilidades mágicas y físicas necesarias.\n\nTráeme tres talismanes de plumas de los novicios de Peloescarcha. Los encontrarás en una cueva que hay al suroeste.", progress = "Those feather charms the novices carry have some sort of magical power to them. It will be interesting to research them. I wouldn't have expected it from the trolls.", completion = "Very good, very good! You've done well, &lt;nombre&gt;. Perhaps the interest that is being shown in you is deserved, after all.\n\nThat'll be for the others to decide--assuming your imp doesn't overpower you and nip your career in the bud. For now, I'm satisfied that you'll probably survive your first few months as a &lt;clase&gt;.\n\nA word about the imp, &lt;nombre&gt;. As a &lt;raza&gt; knows, don't let its size fool you, its magic can be very dangerous.", rewards = "Aprenderás:\n\nInvocar a diablillo\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a diablillo\n\nTambién recibirás:\n\n"},
[1618] = {title = "Equipar a los Crestagrana", objective = "Tormus Forjahonda quiere que le lleves 4 cinturones de cobre con runas y 4 almádenas pesadas de cobre a Verner Osgood a Crestagrana.", description = "Los enanos no somos los únicos que luchamos. ¡Nuestros aliados humanos también están muy ocupados!\n\nCrestagrana sufre ataques constantes de los orcos Roca Negra y siempre anda mal de equipamiento. Si decides ayudarles, seguro que su herrero, Verner Osgood, te enseñará lo que sepa.", progress = "¡Espero que traigas buenas noticias de Forjaz, porque ya no puedo más!", completion = "Gracias, &lt;nombre&gt;, esto me va a venir muy bien. Ahora que los orcos Roca Negra atacan desde el Fuerte de Petravista y los gnolls de Crestagrana merodean por las laderas del norte, solo trabajo en armaduras y armamento. \n\nLo que me has traído me permitirá descansar un poco; así tendré tiempo de enseñarte algo.", rewards = "Recibirás:\n\nDiseño: peto de Forjaz"},
[1638] = {title = "Instrucción de guerrero", objective = "Habla con Harry Burlguard.", description = "Has demostrado ser <un guerrero/una guerrera> excelente, <nombre>, pero todavía eres joven y tienes mucho que aprender.\n\nVe a hablar con Harry Burlguard, suele estar en la taberna El Cerdo Borracho, en el Casco Antiguo de Ventormenta. Harry es un viejo veterano y un buen instructor. Puede enseñarte algunos movimientos que te ayudarán a seguir con vida algo más tiempo.", progress = nil, completion = "¡Oh, jo! Tómate un trago conmigo, muchacho. A primera vista pareces muy duro, pero veamos si puedes arreglártelas con algunas jarras de las especialidades más potentes de El Cerdo Borracho\n\nO bien... ¿has venido para recibir instrucción?", rewards = "Recibirás:\n\n"},
[1639] = {title = "Bartleby, el borracho", objective = "Habla con Bartleby.", description = "Sí, puedo instruirte. Pero antes quiero ver qué movimientos conoces ya.\n\n¿Ves a ese borracho al final de la barra? ¿Bartleby? Quiero que le quites la jarra.\n\nAh, si conoces a Bartleby ya sabrás que no te la dará fácilmente.", progress = nil, completion = "¡Hola muchachito! ¿Qué hace un repipi como tú en El Cerdo Borracho? Ten cuidado, se te podría romper una uña...", rewards = "Recibirás:\n\n"},
[1640] = {title = "Vence a Bartleby", objective = "Golpea a Bartleby y habla con él.", description = "¿¿Que quieres qué?? ¡Estás <loco/loca>!\n\nSolo me quitarás la jarra de mis manos frías y muertas...", progress = "Sólo me quitarás la jarra de mis manos frías y muertas...", completion = "¡Eres mucho más duro de lo que pareces!", rewards = "Recibirás:\n\n"},
[1641] = {title = "El Libro de la Divinidad", objective = "Aquí, en Ventormenta, yo me encargo de poner en el camino de la virtud a los paladines que se consagran a la Luz y defienden Azeroth de la Plaga y otras amenazas. \n\nY ha llegado el momento de que tú des tus primeros pasos hacia la virtud verdadera. Si aceptas, te daré un escrito sobre divinidad. Estudiándolo, aprenderás algo sobre la Luz y sobre lo que se espera de ti. Si lo entiendes y tienes capacidad para ello, conseguirás mayores habilidades.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEscrito sobre Divinidad"},
[1642] = {title = "El Libro de la Divinidad", objective = "Ve a Ventormenta y habla con Duthorian Rall.", description = "Un paladín debe reflejar en todas las cosas la Luz que complementa su fuerza. El esfuerzo por alcanzar la divinidad, para uno de nosotros, no significa aspirar al bien, pues aspiramos al bien en todas nuestras acciones.\n\nAunque en estos duros tiempos somos convocados para atacar al mal, debes recordar que será la ayuda que prestes a los demás lo que te distinguirá de los ciudadanos de Azeroth. La compasión, la paciencia y el valor tienen tanta importancia en un paladín como su fuerza en la batalla.\n\nAprende esto ahora y nunca lo olvides.", progress = nil, completion = "Hola de nuevo, &lt;nombre&gt;. ¿Cómo van tus estudios del Libro de la Divinidad?\n\nSi lo has estudiado, deberías estar preparado para pasar una prueba. Siempre hay cosas que hacer en la ciudad y en nuestras tierras. Actos de compasión y comprensión, gente a la que ayudar, criaturas a las que matar...\n\nSi demuestras que tienes la paciencia necesaria para ayudar a otros, especialmente a los menos afortunados, demostrarás que estás en pleno uso de tus facultades y que eres un siervo de la Luz.", rewards = "Recibirás:\n\n"},
[1643] = {title = "El Libro de la Divinidad", objective = "Busca a Stephanie Turner en el Distrito de Mercaderes de Ventormenta.", description = "Ahora, tu tarea, <nombre>, es encontrar en Ventormenta a alguien que necesite ayuda. Alguien de intenciones y corazón puros. Quizás alguien que ayude a los demás. Alguien que necesite ser guiado. En cualquier caso, esa persona debe tener intenciones nobles y depender de tu bondad.\n\nVuelve a mí cuando hayas acabado. Entonces seguiremos hablando de tu camino y de lo que significa ser un paladín de la Luz.", progress = nil, completion = "Oh, hola, cielo. ¿Cómo van tus aventuras? Me alegro mucho de que te vaya tan bien.\n\n¿Que qué tal yo? Pues gracias por interesarte por mí. En general, estoy bien.\n\nEl aumento de mercaderes ha reducido un poco las cantidades de cuero y tela que dona la gente, así que últimamente está resultando difícil hacer ropa para los niños del orfanato... Pero nos las arreglaremos. Y con \"nos\", quiero decir mi marido y yo. Él está en Forjaz ahora, intentando conseguir ayuda de sus habitantes.", rewards = "Recibirás:\n\n"},
[1644] = {title = "El Libro de la Divinidad", objective = "Lleva 10 retales de paño de lino a Stephanie Turner a Ventormenta.", description = "Benditos sean, la mayoría de ellos han perdido a su familia en la última década... a manos de la Plaga.\n\nMi marido John recoge material para que yo haga camisas y pantalones para los niños, pero no he tenido tiempo de hacerlo. Mi primera preocupación ha sido ponerles comida que en el plato y procurarles un techo.\n\n¿Cómo dices? ¿Que quieres ayudar? ¡Oh, gracias a la Luz! ¡Y gracias a ti, <nombre>!\n\nSi pudieras traerme paño de lino para que cosa ropa para los niños, me ayudarías mucho.", progress = "Espero que mi John esté teniendo más suerte en Forjaz. No le gusta ir allí porque tiene que dejarnos a mí y a los niños solos, pero sabe que tiene que hacerse. Es tan bueno...\n\nAlgunos años han sido más difíciles que otros, pero este desde luego, ha sido el más duro de todos. Parece que haya algo en el aire que anuncia un gran cambio. Pero... ¿quién sabe?", completion = "¡Muchas gracias, &lt;nombre&gt;! ¡Muchas, muchas gracias!\n\nLos niños estarán felices, de verdad. ¡Y a mí me has ahorrado tanto tiempo! Ahora podré concentrarme en las otras necesidades de los niños. Y, con suerte, mi John podrá volver a casa antes de lo que pensábamos.\n\nGracias de nuevo, &lt;nombre&gt;. Sabía que podía contar con la caridad de los demás. Que la Luz sea contigo.", rewards = "Recibirás:\n\n"},
[1645] = {title = "El Libro de la Divinidad", objective = "Es un placer conocerte, <nombre>. Yo me encargo de poner en el camino de la virtud a los paladines que se consagran a la Luz y defienden Azeroth de la Plaga y otras amenazas.\n\nY ha llegado el momento de que tú des tus primeros pasos hacia la virtud verdadera. Si aceptas, te daré un Libro de la Divinidad. Estudiándolo, aprenderás algo sobre la Luz y sobre lo que se espera de ti. Si lo entiendes y tienes capacidad para ello, conseguirás mayores habilidades.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEscrito sobre Divinidad"},
[1646] = {title = "El Libro de la Divinidad", objective = "Ve a Forjaz y habla con Tiza Forjabatalla.", description = "Un paladín debe reflejar en todas las cosas la Luz que complementa su fuerza. E esfuerzo por alcanzar la divinidad, para uno de nosotros, no significa aspirar al bien, pues aspiramos al bien en todas nuestras acciones.\n\nAunque en estos duros tiempos somos convocados para atacar al mal, debes recordar que será la ayuda que prestes a los demás lo que te distinguirá de los ciudadanos de Azeroth. La compasión, la paciencia y el valor tienen tanta importancia en un paladín como su fuerza en la batalla.\n\nAprende esto ahora y nunca lo olvides.", progress = nil, completion = "Bienvenido de nuevo, &lt;nombre&gt;. Espero que hayas dedicado un rato a leer el libro.\n\nSi lo has hecho, deberías estar preparado para pasar una prueba. Siempre hay cosas que hacer en la ciudad y en nuestras tierras, actos de compasión y comprensión, gente a la que ayudar, criaturas a las que matar.\n\nSi demuestras que tienes la paciencia necesaria para ayudar a otros, especialmente a los menos afortunados, demostrarás que estás en pleno uso de tus facultades y que eres un siervo de la Luz.", rewards = "Recibirás:\n\n"},
[1647] = {title = "El Libro de la Divinidad", objective = "Busca a John Turner en el anillo exterior de Forjaz.", description = "Ahora, tu tarea, <nombre>, es encontrar en Forjaz a alguien que necesite ayuda. Alguien de intenciones y corazón puros. Quizás alguien que ayude a los demás. Quizás alguien que necesite ser guiado. En cualquier caso, esa persona debe tener intenciones nobles y depender de tu bondad.\n\nVuelve a mí cuando hayas acabado. Entonces seguiremos hablando de tu camino y de lo que significa ser un paladín de la Luz. Esperaré tu regreso en el templo. Habrá otros paladines que necesiten mi guía.", progress = nil, completion = "EL ORFANATO DE STOR... ay, perdona, no quería gritarte así.\n\nMe llamo John Turner. ¿Cómo estás?\n\nBien, bien, me alegro de oír que las cosas te van bien. He tenido un día muy largo y echo mucho de menos a mi mujer... Pero es el precio que hay que pagar cuando tienes niños que alimentar y que vestir.", rewards = "Recibirás:\n\n"},
[1648] = {title = "El Libro de la Divinidad", objective = "Lleva 10 retales de paño de lino a John Turner a Forjaz.", description = "Mi esposa y yo llevamos un orfanato en Ventormenta. En los últimos diez años hemos acogido a muchos niños que han perdido a sus padres en ataques del Azote. Yo vengo a Forjaz más o menos cada mes. Recojo suministros que me dan los habitantes y hablo con todos los grupos para ver si pueden ayudar en algo.\n\nEste viaje ha sido de una lentitud agonizante y no muy productivo.\n\nSi pudieras conseguirme más tela para que mi mujer haga algo de ropa, podría por fin hacer un envío a casa.", progress = "Gracias a la afluencia de comerciantes por todo Azeroth, tenemos un pequeño suministro de objetos que utilizamos para hacer ropa para los niños de nuestro orfanato. Ayudarme a reunir telas para que mi mujer pueda hacer ropa para ellos es muy noble por tu parte, <nombre>. Te lo agradezco.\n\nEspero poder volver a casa con mi mujer pronto.", completion = "Esto es todo un detalle, &lt;nombre&gt;. Gracias otra vez.\n\nEres un buen hombre. No mucha gente haría esto por mí. No me extraña que hayas elegido el camino de la Luz. Me gustaría encontrar la forma de pagarte por todo lo que has hecho... ¿Qué? ¿Que era lo mínimo que podías hacer? ¿Ves? Eso es precisamente lo que decía, &lt;nombre&gt;, eres la personificación de la nobleza.\n\nQue la Luz te guíe y te proteja en tus viajes.", rewards = "Recibirás:\n\n"},
[1649] = {title = "Libro del valor", objective = "Habla con Duthorian Rall en Ventormenta.", description = "El valor y todas las virtudes que lo acompañan, son como minerales preciosos y poco comunes: debes apreciarlos cuando los encuentras porque son valiosos, pero también debes invertir tiempo en cuidarlos... refinarlos. Cuando se mezclan correctamente, se forma una fuerte aleación que, algunas veces, resulta imposible de romper, incluso con los golpes más poderosos.\n\nTodo esto debe ser puesto a prueba: la fuerza, tu fe, tu valentía. Debes aprender al no perder la fe y darte cuenta de que con el poder de la Luz, puedes conseguir cosas que otros no pueden.", progress = nil, completion = "Ah, quieres pedirme una prueba de coraje. Espléndido.\n\nHay muchas tareas en la ciudad y en las tierras de alrededor que presentan muchos desafíos, perfectos para alguien con tus habilidades.\n\nEsta prueba no se puede tomar a la ligera, &lt;nombre&gt;. Esto, al igual que muchas cosas con las que te encontrarás en tu camino, podría costarte la vida. La iglesia siempre quiere reforzar sus filas, pero también comprende los sacrificios necesarios para asegurar que los paladines son dignos de ella.\n\n¿Estás preparado?", rewards = "Recibirás:\n\n"},
[1650] = {title = "Libro del valor", objective = "Encuentra a Daphne Fontana en Páramos del Poniente.", description = "¡Bien! Que no vaciles es una señal inequívoca de que estás <preparado/preparada> y <dispuesto/dispuesta> a ponerte a prueba. Que así sea.\n\nJusto al final de Longavera, en Páramos de Poniente, hay una pequeña casa con vistas al océano. Allí vive una pareja, los Fontana. Jordan es un herrero fabuloso y ha ido a Forjaz a pasar una temporada con los enanos. Pero esto ha dejado a Daphne, su mujer, sola y desprotegida.\n\nLos informes sugieren que los Defias han tomado casi toda la zona, es posible que Daphne necesite protección. Ve allí.", progress = nil, completion = "Hola, &lt;nombre&gt;. Por la armadura y todas esas armas que llevas, supongo que estás aquí para intentar conquistar mi tierra, o porque la iglesia por fin ha enviado a alguien para protegerme mientras mi Jordan no está.\n\nueno, me presentaré: soy Daphne Fontana. Es un placer.\n\nNo llegas demasiado tarde... Solo he matado media docena de esas bestias desde el último amanecer. Han tardado bastante en subir por esa curva de ahí, pero probablemente te hayan visto venir, así que supongo que estarán aquí en cualquier momento.", rewards = "Recibirás:\n\n"},
[1651] = {title = "Libro del valor", objective = "Defiende a Daphne Fontana del ataque de los Defias.\n\nSi quieres tener éxito, vuestros espíritus no pueden salir de sus espirales mortales.\n\nCuando lo hayas conseguido, vuelve a hablar con Daphne Fontana.", description = "Apuesto a que volverán pronto, así que debes prepararte.\n\nTengo una pistola en la casa. La cogeré y me prepararé para ayudarte pero, en general, estarás <solo/sola>.\n\nNo tengo ni idea de cuántos mandarán, pero seguro que serán más de los que mandaron antes.\n\nRuega a la Luz que nos bendiga porque... ¿Has oído eso? Creo que vienen ya.\n\n¡Nos colocaremos cerca de la casa!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1652] = {title = "Libro del valor", objective = "Habla con Duthorian Rall en Ventormenta.", description = "Por favor, vuelve a ver a Duthorian y dile que, por ahora, estoy a salvo. Y todo gracias a ti y a la iglesia.\n\nÉl enviará el mensaje a Forjaz para que mi querido Jordan sepa que no tiene nada que temer.\n\n¡Oh, cómo echo de menos a mi marido! Qué ganas tengo de que vuelva.Aprenderás:\n\n", progress = nil, completion = nil, rewards = "Aprenderás:\n\nCaptar no-muertes\n\nTe van a lanzar el siguiente hechizo:\n\nCaptar no-muertes\n\nTambién recibirás:\n\nBastión de Ventormenta"},
[1653] = {title = "La prueba de rectitud", objective = "Habla con Jordan Fontana en Forjaz.", description = "No dudo que Jordan desea darte las gracias personalmente, y puesto que debo enviarle mensaje de que su esposa está bien, creo que eres la mejor elección.\n\nJordan iba a ser paladín de la Luz hasta que su dedicación al arte de su padre lo llevó por otros derroteros; conoce bien nuestro modo de vida y lo consideramos uno de nuestros más devotos.\n\nEstará en Forjaz; le gusta trabajar en el exterior por algo relacionado con el aire fresco.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1654] = {title = "La prueba de rectitud", objective = "Consulta la lista y llévale a Jordan Fontana de Forjaz lo siquiente: madera de roble de Piedrablanca, envío de oro refinado de Bailor, el martillo de herrero de Jordan y una gema Kor.", description = "Si aceptas, te recompensaré. Entiéndeme bien, mi generosidad depende de hasta qué punto me ayudes, pero creo que una alianza nos sería beneficiosa.\n\nTe haré un arma digna de tus logros, un símbolo de los paladines y de la Luz, pero tienes que traerme los objetos que necesito para crearla. Tengo aquí la lista; solo necesito a alguien valiente que vaya a buscar los objetos. ¿Qué me dices, <clase>?Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1655] = {title = "Envío de minerales de Bailor", objective = "Lleva el envío de Jordan a Bailor Manopétrea a Loch Modan.", description = "¡Me has asustado, <tío/tía>! ¿No ves que estaba reflexionando?\n\n¿Qué es eso? El cargamento de Jordan para Forjaz. Vale, ya lo pillo. Los ogros de Mo'grosh son los que afanaron los minerales de mi amigo. Atacaron la caravana cuando venía de Los Humedales. El único guardia que sobrevivió palmó hace poco; los sanadores no sabían qué hacer.\n\nSi quieres el cargamento, tendrás que cruzar Loch Modan hacia el noreste. Tiene que estar por ahí.", progress = "¿Qué hay? Esos ogros no pueden contigo, ¿eh?", completion = "Vale, aquí está todo. Si llego a saber que eres tan capaz, te habría dado una lista en vez de pedirte solo una cosa. Pero no estás trabajando para mí. \n\nUn trato es un trato y lo acordado con Jordan era el cargamento. \n\nSalúdalos de mi parte. Buen viaje, &lt;clase&gt;.", rewards = "Recibirás:\n\nEnvío de mineral refinado de Jordan"},
[1656] = {title = "Una tarea por terminar", objective = "Lleva las pieles al tabernero Kauth al Poblado Pezuña de Sangre.", description = "Por favor, <nombre>, ¿puedes ayudarme? Estoy cumpliendo la prueba del Rito de la Fuerza; mi padre me pidió que entregara estas pieles en la posada de Pezuña de Sangre. \n\nNo puedo completar el rito y el recado de mi padre; no tengo fuerzas. ¿Puedes hacer la entrega por mí? \n\nLa posada es un sitio muy agradable para descansar de tanto viaje; seguro que me lo agradecerás.", progress = "Hola, ¿querías algo?\n\nEl viaje desde la Mesa puede ser terrible para algunos y delicioso para otros. ¿En qué grupo estás tú?", completion = "Ah, las pieles. Gracias.\n\nHan llegado antes de lo que esperaba; podremos hacer buenas mantas.\n\nQuédate todo el tiempo que quieras; la posada siempre está abierta. Y dormir bien es importante, sobre todo cuando viajas.", rewards = "Podrás elegir una de estas recompensas:\n\nMendrugo de pan duroAgua de manantial refrescante\n\nTambién recibirás:\n\nAgua de manantial refrescanteMendrugo de pan duro"},
[1657] = {title = "Apesta Costasur", objective = "Lanza 3 bombas fétidas de los Renegados en el corazón de Costasur en Trabalomas, y después ve a ver a Llamasombra Yanka, que está en el Festival del Hombre de Mimbre en los Claros de Tirisfal.", description = "Para celebrar la fiesta de Halloween con estilo... ¡infríngeles dolor a nuestros enemigos de Costasur!\n\nCoge una de estas bombas fétidas especialmente diseñadas. Contiene una sustancia apestosa que ningún humano u otra bestia cualquiera de poca voluntad pueden soportar. Tendrás que lanzarla en el mismísimo centro de Costasur para que surta efecto, así que prepárate para una batalla contra la Alianza.\n\nCuando hayas realizado esta tarea, este tributo a nuestra liberación... ¡ven a verme!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nGolosinas de calabaza de Halloween"},
[1658] = {title = "Controlar el Festival del Hombre de Mimbre", objective = "Localiza el Festival del Hombre de Mimbre de los Renegados en Claros de Tirisfal. Cuando acabes, ve a ver al sargento Hartman a Costasur.", description = "Necesito gente valiente para ir a controlar el Festival del Hombre de Mimbre en los Claros de Tirisfal. No te voy a mentir: la misión es peligrosa. Los guardas del festival serán excepcionalmente brutales. Pero tenemos que saber cómo es el festival y cuántos problemas nos van a dar los Renegados en Halloween.\n\nVe a Tirisfal y echa un vistazo por el festival... si te atreves. Te daré una recompensa cuando vuelvas con la información.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nGolosinas de calabaza de Halloween"},
[1661] = {title = "Libro de la nobleza", objective = "Habla con Duthorian Rall en Ventormenta.", description = "Te saludo de nuevo, <nombre>. Siento más orgullo que nunca por poder llamarte <hermano/hermana>. Tu dedicación es un honor para todos nosotros.\n\nHa llegado el momento de que te haga un gran regalo. Pero has de saber algo: por ahora, te doy esto libremente pero, más tarde, cuando sea el momento preciso, esto será un desafío mucho mayor para todos los paladines que decidan aceptar mi misión.\n\nVuelve a hablar conmigo y te concederé un gran favor.Aprenderás:\n\n", progress = nil, completion = nil, rewards = "Aprenderás:\n\nInvocar a caballo de combate\n\nTe van a lanzar el siguiente hechizo:\n\nInvocar a caballo de combate"},
[1665] = {title = "La jarra de Bartleby", objective = "Lleva la jarra de Bartleby a Burlguard.", description = "Vale, vale, toma mi jarra. Pero deja que me la acabe... allá vamos.", progress = "Vi tu combate con Bartleby. ¡Bien hecho! ¿Tienes su jarra?", completion = "¡La tienes! Seguro que no quería separarse de ella pero has sido muy persuasivo. Bien hecho.\n\nPasemos ahora a tu lección...", rewards = "Te van a lanzar el siguiente hechizo:\n\nCamino de la defensa\n\nRecibirás:\n\n"},
[1666] = {title = "El alguacil Haggard", objective = "Habla con el alguacil Haggard.", description = "Has aprendido algunos movimientos, pero te vendría bien un arma decente. Seguro que si hablaras con el alguacil Haggard podría ayudarte.\n\nEl alguacil Haggard vive en una mansión en el Bosque de Elwynn, en el Aserradero de la Vega del Este. Está retirado, pero ha reunido mucho equipo durante toda su carrera.\n\nDile que vas de mi parte y, con algo de suerte, tendrá un arma de sobra que ofrecerte.", progress = nil, completion = "¿Quién es? ¿Burlguard, eres tú? Tendrás que acercarte un poco más. Mis ojos ya no son lo que eran. ¡Andando así creía que eras un gnoll!", rewards = "Recibirás:\n\n"},
[1667] = {title = "Jack Dentomuerto", objective = "Recupera la insignia de Haggar del campamento de Jack Dentomuerto y regresa con el alguacil Haggard.", description = "¿Te ha enviado Burlguard? Sí, tengo algunas armas de sobra, pero antes tengo una tarea para ti...\n\nHace algún tiempo, un villano Defias, Jack Dentomuerto, me engañó. Estaba oscuro y vestía un tabardo de Ventormenta, así que creí que era el alguacil Dughan. Dijo que necesitaba mi identificación de alguacil de Ventormenta, se la di y en cuanto la tuvo en sus manos salió corriendo antes de poder atraparle.\n\n¡Tráeme de vuelta mi identificación! Seguramente Jack Dentomuerto la tendrá guardado en su campamento del Sur.", progress = "¿Tienes mi insignia? No quiero ni pensar en todo el daño que ha podido hacer Jack Dentomuerto con ella.", completion = "¡Genial! ¡La encontraste! Temía que estuviera perdida para siempre y que la utilizaran los Defias para fingir ser algún oficial de Ventormenta. Bien, ahora ya no pueden hacerlo, gracias a ti.\n\nEstas son algunas armas que utilicé cuando era alguacil. Las conservé en buen estado, ¡elige lo que quieras!", rewards = "Podrás elegir una de estas recompensas:\n\nHacha de HaggardMartillo de HaggardDaga de HaggardEspada de Haggard\n\nTambién recibirás:\n\nHacha de HaggardDaga de HaggardMartillo de HaggardEspada de Haggard"},
[1678] = {title = "Vejrek", objective = "Lleva la Cabeza de Vejrek's a Muren Pico Tormenta en Forjaz.", description = "Si quieres subir en las filas de los guerreros, tendrás que ser muy fuerte. Puedo enseñarte nuevos movimientos pero, antes de nada, debes demostrarme que puedes vértelas con auténticos adversarios.\n\nVejrek es el trol más duro que encontrarás por estos lares, y es a él a quien tienes que derrotar. Tiene una cabaña en las laderas al sur de Poblado Peloescarcha.\n\nTráeme su cabeza y sabré que realmente tienes entereza.", progress = "¿Has estado en la cabaña de Vejrek, <nombre>? ¿Eres tú el que apesta a trol?", completion = "Vejrek tenía que ser bastante feo, ¿eh? ¡Bien hecho, &lt;nombre&gt;! Tienes todo lo necesario, comencemos nuestra lección...\n\nTal vez no lo sepas, pero una defensa fuerte es fundamental para un &lt;clase&gt;. Le permite mantenerse en pie frente a enemigos que derribarían al resto, y eso es lo que necesitan de ti tus aliados.", rewards = "Te van a lanzar el siguiente hechizo:\n\nCamino de la defensa\n\nRecibirás:\n\n"},
[1679] = {title = "Muren Pico Tormenta", objective = "Habla con Muren Pico Tormenta.", description = "¡Hola <guerrero/guerrera>! Pareces <cargado/cargada> de promesas, pero todavía tienes que aprender algunos trucos y Muren Picotormenta es exactamente el enano que necesitas.\n\nPodrás encontrarlo en la Sala de Armas, en La Sala Militar de Forjaz. Ve a hablar con él cuando estés <listo/lista> para aprender lo que es ser <un auténtico guerrero/una auténtica guerrera>.", progress = nil, completion = "Ah, &lt;nombre&gt;. He oído a los guerreros de Dun Morogh vitorear tu nombre. ¡Espero que no te hayas pasado todo el día en las cervecerías!", rewards = "Recibirás:\n\n"},
[1680] = {title = "Tormus Forjahonda", objective = "Habla con Tormus Forjahonda.", description = "Ahora que ya has aprendido nuevos movimientos, es el momento de darte un arma decente. Ve a hablar con Tormus Forjahonda. Te hará un arma que seguro que te gustará.\n\nLo encontrarás aquí, en Forjaz, en La Gran Fundición.", progress = nil, completion = "Vienes de parte de Muren, ¿verdad? Sí, puedo hacerte una bonita arma... pero antes de hacerlo te tengo que pedir un favor.\n\nNo te preocupes. Es muy peligroso y podría llevarte a la muerte... exactamente lo que le gusta a un &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[1681] = {title = "Complejo Vetaferro", objective = "Lleva una pieza de mineral umbrío a Tormus Forjahonda en Forjaz.", description = "Mi mentor, Sturgy Vetaferro, tenía un complejo al sur del Lago de Helm, en la parte oriental de Dun Morogh. Allí enseñaba herrería y era el mejor.\n\nPero un día, uno de sus estudiantes le llevó un extraño mineral. El maestro Vetaferro lo llamó mineral umbrío y estaba ansioso por trabajar con él, pero tan pronto como llegó el cargamento un grupo de enanos Hierro Negro atacaron su complejo. ¡Mataron a todo el mundo!\n\nEntra en el Complejo Vetaferro, <nombre>, y venga la muerte de mi maestro. Tráeme también el cargamento de mineral umbrío que le causó la muerte.", progress = "¿Has entrado en el Complejo Vetaferro? ¿Encontraste el mineral umbrío?", completion = "¡Lo encontraste! ¡Bien hecho!\n\nEspero que mataras a un montón de enanos Hierro Negro mientras buscabas. Me temo que el Complejo Vetaferro no será el último lugar donde los veamos.\n\nPero gracias por el mineral, &lt;nombre&gt;. Estoy ansioso por ponerme a trabajar con él y descubrir sus propiedades.", rewards = "Recibirás:\n\n"},
[1682] = {title = "Armas de hierro gris", objective = "Estas son algunas armas combadas sobre el yunque y hechas con el mineral umbrío que me trajiste.\n\n¡Por favor, <nombre>, elige lo que quieras!", description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nHacha umbríaMaza umbríaDaga umbríaEspada umbría\n\nTambién recibirás:\n\nHacha umbríaDaga umbríaMaza umbríaEspada umbría"},
[1683] = {title = "Vorlus Pezuñavil", objective = "Lleva el cuerno de Vorlus a Elanaria en Darnassus.", description = "Antes de poder instruirte, debes mostrarme tu determinación.\n\nEl sátiro Vorlus Pezuñavil ha encontrado una de nuestras pozas de la luna más remotas, al sureste del Túmulo de Ban'ethil. Ahora se pasa el día chapoteando en sus aguas y corrompiéndolas con su mugre.\n\nEncuentra a Vorlus en la poza de la luna y termina con él. Tráeme su cuerno como prueba y comenzaré tu instrucción avanzada como <guerrero/guerrera>.\n\nEl camino a la poza de la luna está bien escondido, <nombre>, pero mantente <concentrado/concentrada> y alerta y lo encontrarás.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nCamino de la defensa\n\nRecibirás:\n\n"},
[1684] = {title = "Elanaria", objective = "Habla con Elanaria.", description = "Saludos, <guerrero/guerrera>. Tus habilidades siguen creciendo, pero hay mucho más de lo que crees en tu profesión. Para seguir progresando debes encontrar a un instructor.\n\nLa guerrera Elanaria vive en Darnassus, en el Bancal del Guerrero. Ella podrá instruirte.", progress = nil, completion = "Recibe mis más cordiales saludos, &lt;nombre&gt;. Tu nombre es muy conocido en Darnassus, eres muy prometedor. Pero pronto veremos si tienes la fuerza de voluntad necesaria para seguir recorriendo la senda del &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[1685] = {title = "La invocación de Gakin", objective = "Preséntate ante Gakin Presotenebra en el Barrio de los Magos de Ventormenta.", description = "Si es que... siempre igual. Me he pasado el día buscándote sin encontrarte y ahora, cuando ya ni te esperaba, te plantas aquí, sin más.\n\nYa he perdido demasiado tiempo buscándote. No perdamos más. El maestro Gakin te ha convocado para instruirte. Creo que deberías ir en su busca lo antes posible.\n\nLo encontrarás en el sótano de la posada El Cordero Degollado. Por cierto, será mejor que no comentes por ahí ese detalle de la posada... No queremos que se corra la voz de dónde se encuentra...Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1686] = {title = "La sombra de Elura", objective = "Lleva 8 piezas de mineral de elunita y el Medallón de Elura a Elanaria en Darnassus.", description = "Has aprendido mucho, <nombre>. Ahora podremos fabricarte un arma. Un arma de <auténtico guerrero/auténtica guerrera>.\n\nPara hacerlo debes viajar a la Costa Oscura.\n\nHace mucho tiempo, un barco naufragó junto a las rocas, cerca del faro de Auberdine, esparciendo muchos cajones de mineral de elunita por todo el fondo marino.\n\nPara empezar, debes derrotar a la guardiana de la elunita, la Sombra de Elura. La que fue capitana del barco se condenó a sí misma a vagar por las profundidades y a proteger el cargamento por el resto de los tiempos. Acaba con ella, hazte con su medallón y recoge los cajones de elunita perdidos.", progress = "¿Has vencido a la Sombra de Elura, <nombre>? ¿Tienes el mineral de elunita perdido?", completion = "Has sido muy valiente, &lt;nombre&gt;.\n\nHas dado a la Sombra de Elura la paz que merecía y utilizaremos la elunita para hacerte una magnífica arma.", rewards = "Recibirás:\n\n"},
[1687] = {title = "El faro fantasma", objective = "Lleva al huérfano a ver el faro de la cosa de los Páramos de Poniente.", description = "A veces, por la noche, las supervisoras del orfanato nos cuentan historias de miedo. ¡Son una PASADA! Hay una sobre un fantasma que se aparece en un faro de los Páramos de Poniente. Dicen que era un capitán pirata y que otros piratas abordaron su barco. ¡Guau!\n\n¿Podrías llevarme a ver el faro de los Páramos de Poniente, por favor? ¡Quiero ver si el fantasma del capitán está allí! Te prometo que no me separaré de ti, ¡no saldré corriendo ni nada!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1688] = {title = "Surena Caledon", objective = "Consigue la gargantilla de Surena para Gakin el Presotenebra de Ventormenta.", description = "Tuve a una estudiante llamada Surena Caledon. Estaba justo donde estás tú ahora, dispuesta a aprender brujería y sin una pizca de talento. A pesar de eso, era joven y muy bonita. Tenía que haberlo visto venir, ¡esa mujerzuela traidora!\n\nLa ladronzuela escapó con uno de los Defias, Erlan Morosiervo. Si bien su pérdida no es muy importante, le regalé una gargantilla de sangrita que debo recuperar.\n\nAhora, su vida no significa nada para mí. La encontrarás en la Plantación de Calabazas de Brackwell. Recupera lo que me pertenece.", progress = "Incluso los más viejos y sabios pueden caer rendidos ante la gloria de la belleza y la juventud, <nombre>. Un consejo que te vendrá bien recordar toda tu vida.", completion = "Surena tenía mucho talento, pero no tanto como para conseguir dominar la magia de los brujos sin ayuda. Es una pena ver el desperdicio de talento pero, algunas veces, es necesario.\n\nEspero tener resultados diferentes contigo.", rewards = "Recibirás:\n\n"},
[1689] = {title = "El vínculo", objective = "Con la gargantilla de sangrita, invoca y somete a un abisario. Después devuelve la gargantilla de sangrita a Gakin el Presotenebra, en Ventormenta.", description = "Al recuperar mi gargantilla y vencer a Surena has demostrado ser, al menos, tan capaz como ella.\n\nCon eso dicho, también debo decirte que necesitas instrucción desesperadamente. Puedes controlar los flujos de la magia, sí, pero ser un brujo es mucho más que eso.\n\nTe demostraré como controlar a un abisario, pero antes de poder hacerlo, debes invocar a uno en un Círculo de Invocación y dominarlo con todas las armas, tanto físicas como mágicas, que tienes a tu disposición. Utiliza la magia de esta gargantilla en el Círculo de Invocación que está ahí abajo.", progress = "No puedo enseñarte a controlar a un abisario hasta que no hayas vencido a uno.", completion = "Lo has hecho mejor de lo que esperaba, &lt;nombre&gt;, pero has estado a la altura de mis esperanzas. Ahora puedes aprender a dominar a tu abisario sometido, un aliado que será muy valioso en los desafíos que, sin lugar a duda, se te presentarán.\n\nNo tengo nada más que enseñarte en este momento, pero todavía no he acabado contigo, &lt;nombre&gt;.", rewards = "Aprenderás:\n\nInvocar a abisario\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a abisario\n\nTambién recibirás:\n\n"},
[1690] = {title = "Justicia para bandidos", objective = "Acaba con 10 bandidos y ladrones Deambuladores al este de Gadgetzan y después vuelve a ver al ingeniero jefe Silvaina.", description = "¡Eh, tú! Es hora de que ayudes a la compañía de aguas de Gadgetzan.\n\n¡Los nómadas Deambuladores de Tanaris se han hecho con el poder de todos los pozos de agua! Ahora que ya no tenemos acceso a ellos, el suministro de agua se agotará en poco tiempo. Si deseas recibir nuestra gratitud, ayúdanos a impartir justicia a esos nómadas.\n\nDirígete hacia el este, captura a 10 bandidos y ladrones Deambuladores y regresa aquí inmediatamente. ¡La justicia no se hace esperar por ningún hombre... goblin... lo que sea! ¡Ponte ya en marcha!", progress = nil, completion = "¡A eso le llamo yo ser eficiente! Has acabado con tantos nómadas que ya se dibuja la posibilidad de recuperar nuestros pozos de agua.\n\nLa compañía de aguas de Gadgetzan te agradece tu inestimable ayuda, aunque todavía queda mucho trabajo que hacer con estos nómadas. Escucha atentamente, esto lo próximo que necesitamos que hagas...", rewards = "Recibirás:\n\n"},
[1691] = {title = "Más justicia para bandidos", objective = "Acaba con 10 magos oscuros Deambuladores, 8 pícaros Deambuladores y 6 asesinos Deambuladores para el ingeniero jefe Silvaina de Gadgetzan.", description = "Necesitamos que te encargues de las filas más fuertes de los nómadas Deambuladores. Has hecho un buen trabajo hasta el momento pero si queremos recuperar los pozos de agua, lo más inteligente es que nos enfrentemos al menor número de nómadas posible.\n\nEn esta ocasión te pedimos que acabes con sus pícaros, asesinos y magos oscuros. Los encontrarás en dirección este y sudeste.\n\nNo voy a mentirte: son unos seres repugnantes. Si finalmente haces esto por nosotros, ¡estaremos un paso más cerca de recuperar lo que nos pertenece!", progress = nil, completion = "Un trabajo de primera, &lt;nombre&gt;... ¡de primera! ¡Has reducido la población de los nómadas hasta tal punto que ya podemos hacerles frente!\n\nSi todavía no lo has hecho, ve a hablar con el grifoperador Luglunket que está aquí. Él te dará una recompensa por cada una de las bolsas de agua de los Deambuladores que hayas encontrado mientras impartías justicia a esos nómadas.", rewards = "Recibirás:\n\n"},
[1692] = {title = "Mathiel, el herrero", objective = "Lleva el estuche de elunita al herrero Mathiel.", description = "He colocado la elunita que recogiste en un estuche. Llévaselo al herrero Mathiel, te hará un arma de elunita.\n\nMathiel está hacia el Sur, al otro lado de este edificio.", progress = "Saludos joven <clase>. ¿De qué pueden servirte mis habilidades?", completion = "Ah, mineral de elunita. Es un metal estupendo con el que trabajar, bendecido como está por la propia diosa Elune. Será un honor fabricar armas con él.\n\nPor favor, espera mientras trabajo...", rewards = "Recibirás:\n\n"},
[1693] = {title = "Armas de Elunita", objective = "<nombre>, he hecho estas armas con la elunita que me trajiste. Por favor, coge la que te sea más útil.\n\nDebo darte las gracias. Trabajar con elunita es un honor y todo un placer.", description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nHacha elunitaMartillo elunitaDaga elunitaEspada elunita\n\nTambién recibirás:\n\nHacha elunitaEspada elunitaMartillo elunitaDaga elunita"},
[1698] = {title = "Yorus Cebadiz", objective = "Habla con Yorus Cebadiz.", description = "Yorus Cebadiz ha lanzado un desafío para todos los guerreros de la Alianza. Espera en la Posada de Villa del Lago, en Crestagrana, y promete una enorme recompensa para el que pueda superar su prueba.", progress = nil, completion = "¡Ja! Así que oíste hablar de mi desafío, ¿verdad?\n\nBien, siéntate y escucha. Toma un trago y saboréalo... puede ser lo último que bebas.", rewards = "Recibirás:\n\n"},
[1699] = {title = "El guantelete de Rethban", objective = "Entra en las Cavernas de Rethban, llega hasta la primera bifurcación y regresa junto a Yorus Cebadiz en el tiempo estipulado.\n\nNo debes morir y liberar tu espíritu.", description = "He llamado a mi desafío El guantelete de Rethban. Para superar la pruebas todo lo que tienes que hacer es entrar en las Cavernas de Rethban, hasta el primer cruce, y volver aquí. ¡Es fácil! Aunque hay algunos peros...\n\nTienes que volver en una hora.\n\nEncontrarás montones de gnolls de Crestagrana en el camino\n\nAntes de empezar tienes que tomar un trago de la bebida de mi familia: ¡brebaje de Cebadiz!\n\nBueno, ¿qué dices? Las cavernas de Rethban están en las laderas al norte de Villa del Lago. ¿Estás <listo/lista> para afrontar El guantelete de Rethban?", progress = nil, completion = "¡Lo conseguiste! ¡Bien hecho! ¡Tres hurras por &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[1700] = {title = "Grimand Elmore", objective = "Lleva las notas de Furen a Grimand Elmore.", description = "Hice una copia de mis notas sobre las técnicas que aprendí al hacer tu armadura, y necesito que se las lleves a Grimand Elmore. Está aquí, en el Distrito de los Enanos, en la tienda de armas.\n\nAgáchate cuando se las des. Está muy orgulloso de sus habilidades, no le gustará la idea de aprender algo de mí...Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1701] = {title = "Armadura de mallas endurecida con fuego", objective = "Reúne los materiales que necesita Furen Barbalarga y llévaselos a Ventormenta.", description = "Te he dado un buen escudo, pero la cosa no termina aquí. Conozco los secretos para hacer una armadura de malla extremadamente dura. No es fácil y se necesitan algunos materiales bastante extraños, pero si quieres hacer tú los preparativos te haré una pieza.\n\n¿Qué me dices, <nombre>? Si estás <listo/lista> para el desafío, te daré una lista de lo que necesitas y dónde conseguirlo.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1702] = {title = "El forjador de escudos", objective = "Lleva el barril de brebaje a Furen Barbalarga.", description = "Superando El guantelete de Rethban has demostrado de qué estás <hecho/hecha>, <nombre>. Y yo soy fiel a mi palabra. Te daré la recompensa prometida...\n\nEl único problema es que está en Ventormenta.\n\nVe a hablar con mi amigo, Furen Barbalarga, en el Distrito de los Enanos de Ventormenta. Es el mejor forjador de escudos que podrás encontrar, y me debe un gran favor.\n\nToma, llévale este barril de brebaje de Cebadiz a Furen y pídele un escudo. Seguro que te dará uno estupendo.", progress = "¡Hola! Qué sequía, ¿no te parece?", completion = "Ah, brebaje de Cebadiz. ¡Es estupendo! ¿Así que has venido a ver a Yorus, eh? \n\n¡Puedo conseguirte un escudo, sí señor! No encontrarás ninguno mejor, ¡y no estoy mintiendo!", rewards = "Recibirás:\n\nFavor de Furen"},
[1703] = {title = "Mathiel", objective = "Lleva las notas de Furen a Mathiel.", description = "Hice una copia de mis notas sobre las técnicas que aprendí al hacer tu armadura, necesito que se las entregues al herrero Mathiel. Vive en Darnassus. Está bastante lejos pero seguro que estará muy agradecido de aprender lo que yo he aprendido.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1704] = {title = "Klockmort Palmalicate", objective = "Lleva las notas de Furen a Klockmort Palmalicate.", description = "Hice una copia de mis notas sobre las técnicas que aprendí al hacer tu armadura y necesito que alguien se las lleve a Klockmort Palmalicate. Es un herrero gnomo de Forjaz y cuando aprenda lo que yo he aprendido... apuesto a que se tirará de las barbas de la emoción.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1705] = {title = "Sangre ardiente", objective = "Lleva 20 viales de sangre ardiente y 1 roca ardiente a Grimand Elmore en Ventormenta.", description = "Odio reconocer que Furen ha dado con algo realmente bueno. Pero para asegurarme tendré que probar sus técnicas en una nueva armadura. Si me ayudas con los materiales, <nombre>, podrás quedarte con ella.\n\nPara hacer la armadura necesito muestras de sangre ardiente y de roca ardiente.\n\nSolo las podrás conseguir en el Bosque del Ocaso. Los ferocanis nocturnos que se asientan en la mina de la Condena de Roland tienen sangre ardiente y su líder, Gutspill, tendrá la roca.\n\nQue la fortuna te permita ser <rápido/rápida>, <nombre>.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1706] = {title = "La armadura de Grimand", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nAlmófar endurecido con fuego"},
[1707] = {title = "Cazador de bolsas de agua", objective = "Llévale 5 bolsas de agua de los Deambuladores al grifoperador Luglunket, que está en Gadgetzan.", description = "Saludos, <nombre>. Mi nombre es Luglunket y, si quieres, te puedo ofrecer una misión. ¡Huzzah!\n\nNada me agradaría más como grifoperador que me trayeses las faltriqueras de agua de los Deambuladores que encuentres por el desierto. Al fin y al cabo, es nuestra agua. Los nómadas la han robado. La compañía de aguas de Gadgetzan te ofrecerá un paquete de curación oficial por cada cinco faltriqueras que entregues. Y lo más importante, ¡estarás ayudando a Gadgetzan!", progress = nil, completion = "Excelente, ¡cuantas más bolsas de agua consigamos, más nos alejaremos del desastre! Consigue tantas como puedas y te daré una recompensa por cada 5 bolsas de agua que me traigas.", rewards = "Recibirás:\n\nPaquete de curación de la compañía de aguas de Gadgetzan"},
[1708] = {title = "Coral férreo", objective = "Lleva 20 piezas de coral férreo a Klockmort Palmalicate en Forjaz.", description = "Quiero probar las nuevas técnicas de Furen Barbalarga en mi propia armadura. Y como tú ayudaste a Furen y él me ayudó a mí, seguro que te gustará ver la armadura terminada.\n\nPara terminar la pieza necesito un coral muy especial, coral de fuego. Sólo crece en un lugar, a lo largo de la costa sur del Puerto de Menethil, en Los Humedales.\n\nTráeme suficiente coral de fuego y estaré listo para trabajar en la armadura. Date prisa, <nombre>. ¡Tengo tantas ganas de empezar que me estoy mordiendo las uñas!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1709] = {title = "La creación de Klockmort", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nGuanteletes endurecidos con fuego"},
[1710] = {title = "Cáscaras asoladas", objective = "Lleva 20 cáscaras asoladas a Mathiel en Darnassus.", description = "Mi compañero, Furen Barbalarga, ha hecho un descubrimiento que estoy ansioso por probar. Me gustaría probarlo en una nueva armadura que estoy desarrollando, porque creo que las técnicas de Furen darán resultados aún mejores.\n\n¿Me ayudarás? Si reúnes lo que necesito para hacer la armadura haré una pieza extra para ti.\n\nNecesito cáscaras asoladas de huevo del dracoleón Nido Alto que vive en Nido Alto, en Las Mil Agujas. Busca las cáscaras bajo los nidos y vuelve a verme cuando tengas suficientes.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1711] = {title = "La armadura de Mathiel", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nLeotardos endurecidos con fuego"},
[1712] = {title = "Cicloniano", objective = "Lleva los objetos sobre el pergamino de Bath'rah a Bath'rah el Cazavientos en Alterac.", description = "He pasado años observando este sitio. Vigilo para poder ver un día el descenso del gran Cicloniano. Pero todavía no he encontrado a ningún campeón que pueda derrotar al Señor del Viento.\n\nQuizás tú seas ese campeón.\n\nDemúestramelo. Reúne los objetos necesarios para invocarle y sabré que eres <un gran guerrero/una gran guerrera>. Quizás entonces puedas derrotarle y hacerte con su corazón de espirálico.\n\nSi lo haces imbuiré el poder del corazón en un arma y te la daré a ti.\n\nEste es el pergamino con los objetos que necesito.", progress = "¿Tienes los objetos, <nombre>? Reúnelos para demostrarme que tienes la fuerza necesaria para enfrentarte a Ciclonio.", completion = "Has reunido los objetos para mi hechizo y has demostrado fuerza y astucia. Pero el siguiente paso de nuestra búsqueda es aún más peligroso. \n\nEsperemos que tu fuerza no falle.", rewards = "Recibirás:\n\n"},
[1713] = {title = "La invocación", objective = "Lleva el corazón de espirálico a Bath'rah el Cazavientos.", description = "Estoy listo para el ritual de invocación de Cicloniano. Cuando haya sido invocado debes vencerle y hacerte con su corazón de espirálico. Lo utilizaré para imbuir su poder en un arma.\n\n<nombre>, ¿estás <listo/lista> para enfrentarte al Señor del Viento?", progress = "<nombre>, ¡necesito el corazón de espirálico!", completion = "¡Lo lograste! ¡Venciste a Ciclonio y te hiciste con el corazón de espirálico!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1714] = {title = "Esencia del exilio", objective = "La caldera burbujea. Los vapores se elevan como una señal...", description = nil, progress = "La caldera burbujea. Los vapores se elevan como una señal...", completion = "Los talismanes se hunden lentamente en la caldera... \n\nUna gran burbuja se eleva hasta la superficie del líquido y estalla... \n\n...Y aparece un grumo de roca de magma.", rewards = "Recibirás:\n\nEsencia del exilio"},
[1715] = {title = "El Cordero Degollado", objective = "Preséntate ante Gakin Presotenebra en el Barrio de los Magos de Ventormenta.", description = "Bah. Antes, todos los brujos de Khaz Modan estudiaban con los maestros de Gnomeregan, pero ahora que hemos tenido que venir a este pueblucho que es Forjaz y que Magni ha decidido vigilarnos, tenemos que enviar a los novicios a estudiar a Ventormenta.\n\nPor suerte, con Gakin aprenderás todo lo que necesites saber, pero aun así, ¡este asunto me hace hervir la sangre!\n\nLo encontrarás en el sótano de El Cordero Degollado en Ventormenta.", progress = nil, completion = "¿Te envía Llavenegra? Esos malditos brujos de Forjaz tienen que organizarse; no hay tantos instructores en Ventormenta. Tanto movimiento acabará por llamar la atención.", rewards = "Recibirás:\n\n"},
[1716] = {title = "Devoradora de almas", objective = "Habla con Takar el Profeta en Los Baldíos.", description = "Un sayaad es un devorador de almas, un dominador de voluntades y un tentador de corazones. Lo único que sabe es hacer el mal.\n\nLos relatos más escabrosos se refieren a este demonio por su nombre común: súcubo para las hembras e íncubo para los machos.\n\nHace falta un estímulo para atraer a la criatura. Con un símbolo de amor como cebo, seguramente atenderá a tu llamada. Derrótalo y conviértelo en tu herramienta.\n\nDe todas formas, no será fácil encontrar algo parecido. Deberías hablar con Takar el Vidente. Le encontrarás en Los Baldíos de Kalimdor, cerca del Campamento Taurajo.", progress = nil, completion = "¿Ha sido un viaje difícil, &lt;nombre&gt;? Has venido desde muy lejos para hablar conmigo. Pero no temas, sé por qué te ha enviado Gakin y ha hecho bien, porque yo puedo ayudarte.", rewards = "Recibirás:\n\n"},
[1717] = {title = "La invocación de Gakin", objective = "Preséntate ante Gakin Presotenebra en el Barrio de los Magos de Ventormenta.", description = "¡<nombre>! No esperaba verte por aquí, pero se me ha ordenado estar pendiente de tu llegada.\n\nYa es hora de proseguir tu formación; ve a ver a Gakin cuando pases por Ventormenta. Te estará esperando.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1718] = {title = "El isleño", objective = "Habla con Klannoc Macleod.", description = "Tu fama como <guerrero/guerrera> no deja de crecer, <nombre>. Ha llegado el momento de enfrentarte a tus pares y ver cuál es tu posición entre ellos.\n\nHay una isla en la costa de Los Baldíos, al sur de Trinquete. Es conocida como la Isla de Batalla y en ella se encuentran los guerreros.\n\nSe encuentran y combaten.\n\nHabla con el jefe guerrero de Isla de Batalla, Klannoc Macleod. Si te encuentra <digno/digna> te guiará por la senda del guerrero.\n\nPara llegar a Isla de Batalla, busca el banco de arena que cubre La Costa Mercante.", progress = nil, completion = "Hola &lt;clase&gt;. Me alegra ver que conseguiste llegar a Isla de Batalla. Espero que sea de tu gusto...", rewards = "Recibirás:\n\n"},
[1719] = {title = "La Reyerta", objective = "Mata a Will el Grande y habla con Klannoc Macleod en Isla de Batalla.", description = "Si quieres instruirte conmigo tendrás que superar La Reyerta. Es un desafío que ofrecemos aquí, en Isla de Batalla, a los guerreros que se creen fuertes, si superas la prueba sabremos que lo eres.\n\nPara empezar dirígete al centro de esa multitud y ponte en la rejilla. Así indicarás a Twiggy Cabezaplana que estás <preparado/preparada> y enviará a tus contrincantes.\n\nDerrótalos y llamará a Will el Grande. Mata a Will el Grande y vuelve a verme dentro del tiempo permitido.", progress = nil, completion = "Bien hecho, &lt;nombre&gt;. ¡Has superado La Reyerta! \n\nEres un digno &lt;clase&gt; y es un honor instruirte...", rewards = "Te van a lanzar el siguiente hechizo:\n\nCamino del rabioso\n\nRecibirás:\n\n"},
[1738] = {title = "Duramen", objective = "Recupera el duramen de Vallefresno y llévaselo a Gakin el Presotenebra en el Barrio de los Magos de Ventormenta.", description = "Para preparar tu llegada, utilicé mi visión para ver por encima del mundo y a través de la niebla del tiempo. He encontrado lo que buscas.\n\nEn la guerra de los Ancestros, dos amantes cayeron juntos en combate. En el lugar donde exhalaron su último aliento, nació un árbol conocido como el duramen, un símbolo de su amor imperecedero.\n\nEso debería ser suficiente para tentar a un súcubo. Lo encontrarás en las Ruinas de Ordil'Aran, en la parte noroeste del Bosque de Vallefresno.", progress = "Was your search a success, <nombre>?", completion = "Yes, I can tell this will do nicely, let me just work with it for a moment...\n\nTakar is a resourceful fellow to have around, isn't he? If only I could persuade him to move back to civilization.", rewards = "Recibirás:\n\n"},
[1739] = {title = "El vínculo", objective = "Utilizando el corazón del duramen, invoca y somete a un súcubo. Después devuelve el corazón del duramen a Gakin el Presotenebra en El Cordero Degollado.", description = "Utilizando el corazón del duramen, podrás lanzar un hechizo en el Círculo de Invocación de la cripta subterránea para abrir un portal y que pueda pasar el súcubo.\n\nTen cuidado, <nombre>, algunos súcubos han dominado incluso a las mentes más fuertes.\n\nCuando hayas vencido y lo devuelvas al vacío, vuelve a verme y te enseñaré a invocarlo y controlarlo.", progress = "Sometimes I wonder if Surena was not a succubus in disguise, sent to warp and twist my mind... Ha! I give her too much credit, I think.", completion = "Well done, well done, &lt;nombre&gt;. I see we've done right in deciding to train you. Your performance certainly does not disappoint.\n\nI've no qualms in showing you the way to control your newly bound succubus. Observe carefully.", rewards = "Aprenderás:\n\nInvocar a súcubo\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a súcubo\n\nTambién recibirás:\n\nFaltriquera de almas pequeña"},
[1740] = {title = "El orbe de Soran'ruk", objective = "Encuentra 3 fragmentos de Soran'ruk y 1 fragmento grande de Soran'ruk y llévaselos a Doan Karhan en Los Baldíos.", description = "Cuando los demonios de la Legión caen en desgracia, sus espíritus quedan presos en unos orbes que se distribuyen entre sus magos y brujos.\n\nMuchos fueron llevados por sus sirvientes en Azeroth. Incluso rotos en fragmentos, poseen poderes de los demonios en ellos presos.\n\nEl orbe de Soran'ruk se partió en dos piezas, una de las cuales se rompió en pedazos aún más pequeños para los acólitos del Azote del Ocaso de las Cavernas Anegadas. La otra, la de mayor tamaño, acabó en manos de un mago del Castillo de Colmillo Oscuro. Encuéntralas y podré reconstituirlo.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nOrbe de Soran'rukBastón de Soran'ruk\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nOrbe de Soran'rukBastón de Soran'ruk"},
[1758] = {title = "El Libro del Conciliábulo", objective = "Habla con Krom Brazorrecio en Forjaz.", description = "Hará falta algo más que un simple círculo de conjuro para invocar a un manáfago al que quieres desafiar.\n\nMis tres acólitos y yo te ayudaremos a crear los círculos mágicos. Necesitas un ejemplar del Libro del Conciliábulo, que lleva un tiempo desaparecido. Lo escribieron los primeros brujos que conjuraron a un manáfago por sus propios medios.\n\nA lo mejor Krom Brazorrecio puede ayudarnos. Hoy en día es un brujo itinerante, se pasa el tiempo acumulando textos antiguos y merodeando por la biblioteca de Forjaz.", progress = nil, completion = "¿El Libro del Conciliábulo? Mm… me suena. Aunque no lo he visto nunca aquí y, te lo aseguro, me acuerdo de cada libro que leo.\n\nA ver...\n\n¡Ah, sí! He visto referencias al mismo en los libros de catalogación de la bilblioteca.", rewards = "Recibirás:\n\n"},
[1778] = {title = "El Libro de la Divinidad", objective = "Ve a Forjaz y habla con Tiza Forjabatalla.", description = "Voy a empezar a hacer planes para volver pronto a casa. Espero que a Stephanie le vaya bien en Ventormenta. Estoy deseando volver a verla. Hasta la próxima, <clase>.", progress = nil, completion = "Vaya, sí que has vuelto rápido. ¿Y qué se supone que has conseguido en este tiempo?\n\n¿Conseguiste ayudar en una noble causa? Quizás hayas aprendido el valor de la caridad o algo más sobre tus obligaciones para con los habitantes de Azeroth. Dime a quién has ayudado y cómo... Impresionante, &lt;nombre&gt;, desde luego, son acciones de alguien que sigue el camino de la Luz.\n\nTu sacrificio se verá recompensado con el tiempo.", rewards = "Recibirás:\n\n"},
[1779] = {title = "El Libro de la Divinidad", objective = "Coge el Símbolo de la Vida y habla con Muiredon Forjabatalla. Está en Forjaz.\n\nObjeto provisto: \n", description = "Quiero que cojas esto, <nombre>. Se llama Símbolo de la Vida.\n\nHas demostrado tener paciencia y caridad y ahora quiero que pruebes tus aptitudes y tu poder en combate.\n\nMi marido Muiredon tuvo hace poco un encuentro con unos enanos Hierro Negro en el que casi pierde la vida. Y ahí está... Uno de nuestros sacerdotes está curando sus heridas.\n\nCoge el Símbolo y habla con él. Si puedes ayudarle, volveremos a hablar. Pronto. Que la Luz sea contigo.", progress = nil, completion = "*tose*\n\nTe deseo un buen día, &lt;clase&gt;. No prestes atención a mis heridas. Este buen sacerdote se ocupará del viejo Muiredon. Todavía no necesito el poder de ese Símbolo de la Vida que tienes ahí.\n\nDime, te envía mi mujer, ¿verdad? Lo suponía. Hablaré contigo. Te contaré qué les ocurrió a Muiredon y a su mejor amigo, Narm, un &lt;clase&gt; valiente, como tú.", rewards = nil},
[1780] = {title = "El Libro de la Divinidad", objective = "Ve a Ventormenta y habla con Duthorian Rall.", description = "Llevaré esto a los niños hoy mismo. Cuídate, por favor. No encuentra una a menudo personas como tú en la ciudad. Has demostrado que los paladines de Ventormenta nunca vuelven la espalda a quienes los necesitan.\n\nAdiós.", progress = nil, completion = "Qué pronto has vuelto, &lt;nombre&gt;. ¿Y qué has conseguido en este tiempo que no has estado en la Catedral?\n\n¿Has conseguido ayudar en alguna noble causa? Quizás hayas aprendido el valor de la caridad o algo más sobre tus obligaciones para con los habitantes de Azeroth. Dime a quién has ayudado y cómo... Vaya, estoy impresionado, &lt;nombre&gt;, las tuyas son sin duda acciones de alguien que sigue el camino de la Luz y antepone el bien de los demás al propio.\n\nTu sacrificio se verá recompensado con el tiempo.", rewards = "Recibirás:\n\n"},
[1781] = {title = "El Libro de la Divinidad", objective = "Lleva el Símbolo de la Vida a Gazin Tenorm a Ventormenta.\n\nObjeto provisto: \n", description = "Coge esto, <nombre>. Se llama Símbolo de la Vida.\n\nHas demostrado ser paciente y <caritativo/caritativa> y ahora ha llegado el momento de ver si tienes poder suficiente para centrarte en uno de los mayores dones de la Luz.\n\nMi amigo Gazin tuvo hace poco un encuentro con unos Defias en Elwynn, cerca de la Vigilia de los Héroes... casi le cuesta la vida. Está cerca del altar. Uno de nuestros sacerdotes está curando sus heridas.\n\nLlévale el Símbolo y habla con él. Si puedes ayudarle, volveremos a hablar. Pronto. Que la gloria de la Luz sea contigo.", progress = nil, completion = "*tose*\n\nEs un placer conocerte, &lt;clase&gt;. No prestes atención a mis heridas. Este buen sacerdote se ocupará del viejo Gazin. No necesitaré el poder de ese Símbolo de la Vida que has traído... al menos, no todavía.\n\nAsí que te envía Duthorian, ¿eh? Muy bien. Te diré exactamente lo que nos ocurrió a mí y a mi amigo Henze. Y, con un poco de suerte y algo de habilidad por tu parte, quizás puedas ayudarnos a todos.", rewards = nil},
[1782] = {title = "La armadura de Furen", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCamisote endurecido con fuego"},
[1783] = {title = "El Libro de la Divinidad", objective = "Coge el Símbolo de la Vida y resucita a Narm Faulk en Dun Morogh.", description = "Estábamos espiando a unos enanos Hierro Negro que se habían establecido en un búnker al sur del Lago de Helm. Sabíamos que no tramaban nada bueno. Por eso Narm y yo quisimos echar un vistazo de cerca. Vimos a su líder dando órdenes a sus hombres.\n\nNarm y yo aprovechamos la ocasión para matar a algunos de ellos, pero enseguida llegaron más. Y eran más fuertes de lo que creíamos. Narm me dijo que corriera a buscar refuerzos, así que aquí estoy.\n\nNarm necesita ayuda y no descansaré hasta que la consiga.\n\n¿Puedes ayudarnos, <nombre>?", progress = nil, completion = "No sé cómo darte las gracias, hermano.\n\nVeo que llevas el Símbolo de la Vida, así que solo puedo deducir que Tiza y Muiredon te enviaron a ayudarme.\n\nCaí combatiendo contra los Hierro Negro cuando intentaba hacerme con uno de los textos. Conseguí retenerlos lo suficiente para que Muiredon escapara, pero cuando llegaste, la Luz casi me había abrazado por completo. El hecho de que tuvieras la fuerza para utilizar el Símbolo tiene mucha importancia para ti, pero ya hablaremos de eso más adelante.", rewards = "Recibirás:\n\n"},
[1784] = {title = "El Libro de la Divinidad", objective = "Consigue un guión Hierro Negro de los enanos Hierro Negro que están cerca de Helm y luego vuelve a Forjaz y busca a Muiredon Forjabatalla.", description = "Antes de que yo cayera en combate, Muiredon y yo casi habíamos derrotado a uno de los espías Hierro Negro cerca de esa casa pequeña. Vimos cómo su capitán repartía órdenes e intentamos matar a uno de ellos y hacernos con esas órdenes.\n\nEn el Consejo de Forjaz hay quien todavía no cree que en Dun Morogh hay enanos Hierro Negro. Si conseguimos esas órdenes, no solo tendremos una prueba de su presencia, sino que además, podremos hacernos una idea de lo que planean.\n\nConsigue una de esas órdenes y llévasela a Muiredon, <nombre>.", progress = "¡Has vuelto! ¡Cómo me alegro, <nombre>! Mis heridas han sanado y ya estoy listo para volver al Lago de Helm... en cuanto consiga convencer a mi mujer de que no me pasará nada.", completion = "¡Ja, ja! ¡Lo has conseguido! Has salvado a Narm y has encontrado las pruebas que necesita mi mujer para ir al Consejo. Eres una bendición de la Luz... como mi mujer.\n\nGracias otra vez, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[1785] = {title = "El Libro de la Divinidad", objective = "Ve a Forjaz y habla con Tiza Forjabatalla.", description = "Ya'stoy viendo a mi mujer hinchándose d'orgullo. Más te vale ir yendo p'allá ya mismo. Creo que querrá echarse unas palabras contigo.\n\nQue la Luz t'acompañe, potente <clase>. Te buscaré en el campo de batalla cuando llegue el momento de luchar contra la Plaga y los Hierro Negro esos.", progress = nil, completion = "Hoy has hecho un gran favor a todo Forjaz, &lt;nombre&gt;. Me enorgullece contarte entre los nuestros y, si te sientes preparado, quisiera bendecirte con este pergamino. El Símbolo de la Vida solo habría funcionado en manos de un &lt;clase&gt; que estuviera listo para una de las mayores bendiciones de la Luz: el poder de devolver la vida a los muertos.\n\nTe enseñaré cómo resucitar a tus compañeros. Utiliza ese don con sabiduría, &lt;nombre&gt;. Te doy las gracias, también de parte de mi marido y de Forjaz.", rewards = "Aprenderás:\n\nRedención\n\nTe van a lanzar el siguiente hechizo:\n\nRedención\n\nTambién recibirás:\n\n"},
[1786] = {title = "Libro de la Divinidad", objective = "Coge el Símbolo de la Vida y resucita a Henze Faulk en Elwynn.", description = "Henze y yo conseguimos llegar a la Vigilia de los Héroes, una isla que hay en el centro del Lago del Hito, al noreste de Elwynn. Habíamos oído que los Defias habían cruzado nuestras fronteras y queríamos conseguir pruebas para los consejeros del Rey.\n\nVimos a un par de esos tipos leyendo unos documentos y atacamos. Por desgracia, llegaron más magos y pronto nos vimos superados.\n\nHenze los contuvo mientras yo escapaba... ese tonto, haciéndose el héroe...\n\nAhora está ahí, solo... ¿Puedes ayudarnos, <nombre>?", progress = nil, completion = "Gracias, hermano.\n\nVeo que portas el Símbolo de la Vida, ¿puedo deducir de eso que te envían Duthorian o Gazin?\n\nCaí ante los hechizos de los Defias cuando Gazin y yo intentábamos hacernos con sus planes. Conseguí aguantar el tiempo suficiente para que Gazin escapara, pero al final, su magia me superó.\n\nY entonces llegaste tú y me trajiste de nuevo a la vida. La habilidad de utilizar el Símbolo no la tiene cualquiera, &lt;nombre&gt;. Debes apreciar lo que significa que pudieras utilizarlo con ayuda de tu fe.", rewards = "Recibirás:\n\n"},
[1787] = {title = "Libro de la Divinidad", objective = "Recupera 1 pergamino Defias de los zahoríes bribones Defias de Elwynn y vuelve con Gazin Tenorm a Ventormenta.", description = "Ya tendremos ocasión de hablar sobre el Símbolo más adelante. Lo importante ahora es conseguir esos papeles que tenían los Defias. Si logramos hacernos con ellos, podremos probar que la banda Defias se ha infiltrado en Elwynn y, con suerte, averiguaremos cuáles son sus planes.\n\nYo ahora no estoy en disposición de combatir, <nombre>. ¿Crees que tienes la fuerza necesaria para arrebatar esos papeles a los Defias de la isla?\n\nSi consigues hacerte con ellos, llévaselos a Gazin. Y dile también que estoy bien.", progress = "¿Henze está a salvo, <nombre>? ¿Tuviste éxito?\n\nEntre tu habilidad con las armas y el poder de la Luz, creo que la Vigilia de los Héroes estará a salvo de cualquier amenaza de los Defias, pero eso deben decirlo el Rey y sus consejeros.", completion = "¡Estupendo! Lo has conseguido, has salvado a Henze. ¡Es evidente que la Luz está en ti, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[1788] = {title = "Libro de la Divinidad", objective = "Ve a Ventormenta y habla con Duthorian Rall.", description = "Cuando Duthorian haya echado un vistazo a este texto, se lo llevaré a los consejeros del Rey. Pero tú deberías ir a hablar con Duthorian ya. ¡Querrá darte una palmadita en la espalda!\n\nGracias otra vez, <nombre>. Espero que volvamos a encontrarnos. Eres <un aliado al/una aliada a la> que me enorgullece considerar <un amigo/una amiga> también.", progress = nil, completion = "Has actuado bien, &lt;nombre&gt;. Deberías enorgullecerte de lo que has conseguido.\n\nDeberías estar orgulloso de tus habilidades. No todo el mundo puede utilizar el poder del Símbolo de la Vida. Si puedes invocar a la Luz para devolver a los muertos a la vida, es que estás preparado para uno de los mayores honores de los paladines: el poder de resucitar.\n\nPodrás devolver a la vida a compañeros caídos, tal y como hiciste con Henze.\n\nCuídate, &lt;nombre&gt;. La Luz te ilumina. Recíbela en ti.", rewards = "Aprenderás:\n\nRedención\n\nTe van a lanzar el siguiente hechizo:\n\nRedención\n\nTambién recibirás:\n\n"},
[1789] = {title = "El Símbolo de la Vida", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nSímbolo de Vida"},
[1790] = {title = "El Símbolo de la Vida", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nSímbolo de Vida"},
[1791] = {title = "El Cazavientos", objective = "Habla con Bath'rah el Cazavientos.", description = "Ahora que has aprendido la actitud colérica, querrás tener un buen arma.\n\nVe a hablar con Bath'rah el Cazavientos, un ermitaño trol que vive entre las Montañas de Alterac y las Tierras de la Peste del Oeste. Es un forjador de armas muy diestro y un poderoso chamán.\n\nPara encontrar a Bath'rah ve hasta el río que fluye en el lado oriental del Molino Tarren. Síguelo hacia el Norte. Bath'rah está acampado junto a las ruinas que encontrarás en la orilla oriental del río.", progress = nil, completion = "Has hecho un largo viaje, joven &lt;clase&gt;. Veo fuego en tu mirada. Buscas un arma de gran poder, ¿verdad? \n\nPuedo ayudarte en tu búsqueda. Y tú puedes ayudarme en la mía.", rewards = "Recibirás:\n\n"},
[1792] = {title = "Arma torbellino", objective = "Tengo el corazón de torbellino y ya puedo colocarlo en el recipiente que elijas. \n\nElige tu arma, <nombre>, y no olvides que su poder será el poder del Ciclonio.", description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nHacha espirálicaEspada espirálicaMartillo de guerra espirálico\n\nTambién recibirás:\n\nHacha espirálicaMartillo de guerra espirálicoEspada espirálica"},
[1793] = {title = "Libro del valor", objective = "Se ha demostrado que la Luz camina a tu lado, <nombre>, pero el peligro nos acecha cada día y es importante saber qué fuerza se esconde en el interior de cada uno. Tu temple se pondrá a prueba cuando demonios, dragones, o cualquier otro ser malvado entre en tus tierras. ¿Serás lo suficientemente fuerte?\n\nEl coraje, sin tener en cuenta en qué situación, es tan necesario para los paladines como la Luz, tu armadura y tu arma. Proteger a los débiles y no perder la fe, son dos lecciones más que tendrás que aprender cuando estudies en la Catedral de la Luz.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEscrito sobre Valor"},
[1794] = {title = "Libro del valor", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEscrito sobre Valor"},
[1795] = {title = "El vínculo", objective = "Mediante el Libro del Conciliábulo invoca y subyuga a un manáfago y luego devuélvele el Libro del Conciliábulo a Strahad Farsan en Trinquete.", description = "Si estás suficientemente <preparado/preparada>, deberíamos empezar. Permite que te explique el proceso.\n\nEmpezaré por crear el círculo de conjuro básico. Cuando este esté acabado, mis acólitos usarán las varas de mediación para crear el círculo más grande.\n\nUna vez hecho esto, leerás los conjuros del Libro del Conciliábulo y el manáfago será invocado. Ten cuidado, <nombre>. Un manáfago es un poderoso enemigo y me gustaría que conservaras la cabeza sobre los hombros.", progress = "Todo <clase> tiene un manáfago bajo su mando. Aunque cuesta mucho amaestrarlo, el poder que le otorga a su amo compensa en mucho el esfuerzo.", completion = "Debo decir que estoy impresionado, &lt;nombre&gt;. Cuando me enfrenté a mi primer manáfago, salí con vida de milagro. Presiento que tienes el potencial para convertirte en una de las más poderosas brujas de esta era.", rewards = "Aprenderás:\n\nInvocar a manáfago\n\nTe van a lanzar el siguiente hechizo:\n\nEnseñar a invocar a manáfago\n\nTambién recibirás:\n\nCaja de almas"},
[1796] = {title = "Los componentes para la toga de sangre dorada encantada", objective = "Llévale las túnicas arcanas a Menara Nadiz en Los Baldíos.", description = "El primer objeto que te ayudaré a crear es una túnica. Tú recolectarás los elementos para mí y yo los uniré. Después, y siempre que lo consigas, obtendrás el primero de muchos objetos que te harán más <poderoso/poderosa> y que te ayudarán en tus esfuerzos.\n\nPara empezar, tendrás que haber confeccionado túnicas arcanas. Un sastre puede hacer esto por ti, o quizás puedas comprarle una a alguien, pero estaría de lo más impresionada si la hicieras tú <mismo/misma> pero sea como fuere, tráeme las túnicas y ya veremos.", progress = "¿Cómo va tu búsqueda de las túnicas, <nombre>? ¿Las has hecho tú mismo? Encuentro que tales toques personales les confieren una magia aún más dulce. Te da un sentido de logro aún mayor. Pero entiendo que si has elegido la vía más rápida y has pedido que te las confeccionen, se puede decir algo de semejante... ambición.", completion = "Esto me servirá, &lt;nombre&gt;.\n\nTodos los objetos mágicos deben empezar con un objeto de gran calidad. Cuanto más esfuerzo y habilidad se ponga en confeccionarlo, mayor será la magia que pueda contener. Los componentes raros y caros mantienen mejor la magia. No te arrepentirás de que tus togas se conviertan en las que te haremos más tarde.", rewards = "Recibirás:\n\n"},
[1798] = {title = "Buscando a Strahad", objective = "Habla con Strahad Farsan en Los Baldíos.", description = "Es hora de que aprendas a invocar a un manáfago, <nombre>. No tengo los componentes ni los objetos necesarios para enseñarte aquí, así que tendrás que buscar a otro, Strahad Farsan, otro de nuestro círculo interno.\n\nVive en el puerto goblin de Trinquete. Enviaré un mensaje para informarle de tu llegada.", progress = nil, completion = "¿&lt;nombre&gt;? Ah sí, por supuesto. Nuestro último prodigio. Me temo que la realidad no está a la altura de la leyenda.\n\nNo importa, has venido a aprender, no a escuchar mis chistes malos.", rewards = "Recibirás:\n\n"},
[1799] = {title = "Los fragmentos del orbe de Orahil", objective = "Habla con los acólitos de Menara que están en el interior de la torre que domina Trinquete y elige uno de sus caminos para seguirlo.\n\nDespués, llévale un orbe infernal a Tabetha en Marjal Revolcafango.", description = "Bien, entonces empezaremos.\n\nPrimero debes hablar con mis acólitos de ahí adentro. Elige uno de los caminos que te ofrezcan, ellos te darán más detalles.\n\nEn segundo lugar, debes viajar hasta Desolace y encontrar un orbe infernal en manos de uno de los brujos del Aquelarre Mannoroc. Este es el orbe que limpiarás con la ayuda de una maga llamada Tabetha que se encuentra en lo más recóndito al sur de Marjal Revolcafango. Te ayudará por nuestro pasado común.\n\nInsisto, no vuelvas hasta que hayas completado las dos cosas que te he pedido.", progress = "Y claro, si no eres un mago, me pregunto si puedo fiarme de ti. Puedo oler el poder arcano en ti pero pareces haber eludido el hedor de la corrupción que con tanta facilidad alcanza a los de tu clase.\n\nSí, tú, <clase>... un <clase> ha venido a pedirme ayuda.\n\nBien, ¿qué puedo hacer por ti, <nombre>?", completion = "Oh, así que te envía Menara. Vale... Supongo que puedes quedarte como invitado por ahora. Pero debes estar detrás de algún asunto urgente, ¿en busca de poder perdido quizás? A lo mejor me equivoco. A lo mejor la corrupción ni se te ha acercado después de todo.", rewards = "Recibirás:\n\n"},
[1801] = {title = "El Libro del Conciliábulo", objective = "Habla con Jorah Annison en Los Baldíos.", description = "Hará falta algo más que un simple círculo de conjuro para invocar a un manáfago al que quieres desafiar. \n\nMis tres acólitos y yo te ayudaremos a crear los círculos mágicos. Necesitas un ejemplar del Libro del Conciliábulo, que lleva un tiempo desaparecido. Lo escribieron los primeros brujos que conjuraron a un manáfago por sus propios medios. \n\nA lo mejor Jorah Annison puede ayudarte. Ahora trabaja de cartógrafo en Entrañas pero sigue siendo el que más sabe de textos antiguos de todos mis conocidos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1802] = {title = "Libro del Conciliábulo", objective = "Lleva el tomo mohoso y el manuscrito maltrecho a Krom Brazorrecio a Forjaz.", description = "Se cree perdido, pero sospecho dónde podría estar.\n\nSolo hay un ejemplar y en algún momento alguien le arrancó una gran parte. Se dice que pasó a manos de los centauros de Las Mil Agujas.\n\nEl resto se perdió en la costa al oeste de Costasur, desde donde iba a ser transportado por barco al Puerto de Menethil. Nunca llegó a su destino.\n\nLo único que te pido a cambio de esta información es que me lo traigas si lo encuentras.", progress = "¿Son ciertos los rumores, <nombre>?", completion = "¡Excelente! Qué privilegio es tenerlo entre mis manos.\n\nArdo en deseos de estudiarlo. ¡Qué momento, &lt;nombre&gt;! Déjame echarle un vistazo; seguro que Strahad me enviará una copia.", rewards = "Recibirás:\n\n"},
[1803] = {title = "Libro del Conciliábulo", objective = "Recupera el tomo mohoso y el manuscrito ajado para Jorah Annison de Entrañas.", description = "¡Ah, veo que eres <un brujo/una bruja> con gustos similares a los míos! De hecho, la adquisición del Libro del Conciliábulo es una noble aspiración. ¿Sabías que una vez estuvo guardado entre los muros de Lordaeron? Pero lo destrozaron y arrancaron casi la mitad de sus páginas. ¿Quién podría estar detrás de un acto tan atroz?\n\nEsa mitad se perdió, aunque algunos rumores apuntan a que cayó en manos del centauro de Las Mil Agujas.\n\nEl resto del texto debía ser conducido a Forjaz pero se perdió por el camino en la costa occidental de Costasur.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1804] = {title = "Libro del Conciliábulo", objective = "Lleva el tomo restaurado y 3 varas de mediación a Strahad Farsan a Trinquete.", description = "Verás, me cuesta separarme de él. Pero eres tú quien lo ha encontrado y lo necesitas para cosas más importantes. Solo te pido que le digas a Strahad que quería una copia.\n\nHa enviado un mensaje mientras estabas fuera; dice que, si quieres que sus acólitos participen en la invocación, tienes que conseguir tres varas de mediación.\n\nPuede que él no sepa dónde encontrarlas, ¡pero yo sí! Fueron usadas por última vez por los orcos Faucedraco en Los Humedales. Comprobaré sus libros de hechizos.", progress = "Hacía mucho que no te veía. ¿Encontraste el tomo y las varas?", completion = "Magnífico, aquí están: el Libro del Conciliábulo y las varas.\n\nEsto va a ser interesante: hace tiempo que ningún joven &lt;clase&gt; se enfrenta a un manáfago.", rewards = "Recibirás:\n\n"},
[1805] = {title = "Libro del Conciliábulo", objective = "Lleva el tomo restaurado y las 3 varas de mediación a Strahad Farsan a Trinquete.", description = "Es uno de los textos más interesantes que he estado buscando en estos últimos años. La selección que tengo aquí es bastante limitada, la mayoría son libros de geografía, y ya los he leído todos. Espero que Strahad me envíe una copia de ese libro cuando tenga ocasión de hacerlo.\n\nHablando de él, me envió noticias cuando tú estabas ausente. Para completar tu misión, debes conseguir tres varas de mediación.\n\nDado que me has dado la oportunidad de mirar este texto, voy a compartir un secreto contigo. Podrás encontrar las varas en los orcos del clan Faucedraco de Los Humedales.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1806] = {title = "La prueba de rectitud", objective = "Espera mientras Jordan Fontana te forja un arma.", description = "¿Vamos a la forja y empezamos? Llevo tiempo esperando tu llegada, así que no tardaremos mucho.\n\nY entonces podrás defender la Luz mucho mejor de lo que nunca has podido.\n\nTe diré la verdad, <nombre>: ver el fruto de tus esfuerzos me da cierta envidia. Pero poder ofrecerte mi humilde ayuda me llena de alegría.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPuño de Verigan"},
[1818] = {title = "Habla con Dillinger", objective = "Habla con el guardia de la muerte Dillinger en Rémol.", description = "Me han dicho que el guardia de la muerte Dillinger tiene un trabajo para alguien como tú. Si quieres probar tu valor para las fuerzas de la Dama Oscura, informa inmediatamente a Dillinger.\n\nEl puesto del guardia de la muerte Dillinger está en el extremo occidental de Rémol, junto al cementerio.", progress = nil, completion = "¿Has venido por una misión? Bien. Tengo una importante y que pondrá a prueba tu destreza en combate... y tu firmeza en la defensa de los Renegados.", rewards = "Recibirás:\n\n"},
[1819] = {title = "Ulag, el cuchillero", objective = "Mata a Ulag el Cuchillero y ve a hablar con el guardia de la muerte Dillinger.", description = "Cuando la Plaga se extendió por los Claros de Tirisfal, capturamos a uno de ellos para someterlo a estudio. Tenía una mente libre, se llamaba a sí mismo Ulag el Cuchillero. Pero, aunque era libre, seguía cautivado por el rey Exánime.\n\nEsperábamos encontrar un modo de romper el control del rey sobre Ulag, pero fracasamos. Ahora, por fin, ha llegado el momento de liberarle.\n\nLibérale y mátale.\n\nActiva el disparador de piedra calavera junto al mausoleo del Norte. Así levantarás el sello que le mantiene atado.\n\nCuando lo hagas... estate <preparado/preparada>, <nombre>.", progress = nil, completion = "Bien hecho, &lt;nombre&gt;. Ulag no era un agente menor de la Plaga. Has mostrado a los guardias de la muerte y, por tanto, a la Dama Oscura, que eres un &lt;clase&gt;  y firme y que mereces seguir avanzando en tu instrucción.", rewards = "Te van a lanzar el siguiente hechizo:\n\nCamino de la defensa\n\nRecibirás:\n\n"},
[1820] = {title = "Habla con Coleman", objective = "Habla con Coleman Lontananza.", description = "Has demostrado tu valor a la Dama Oscura, quizás ahora puedas probarte ante uno de sus súbditos.\n\nColeman Lontananza aguarda a un guerrero que quiera convertirse en su instrumento de venganza contra los enemigos de los Renegados.\n\nLo encontrarás en el Mesón La Horca, en la parte oriental de Rémol.", progress = nil, completion = "Sí, es cierto, necesito de tu pericia en combate. Mis medios son escasos, pero mi tarea sirve también a la causa de los Renegados. Hazlo y tu recompensa será doble.", rewards = "Recibirás:\n\n"},
[1821] = {title = "Reliquias Agamand", objective = "Lleva a Coleman Lontananza el hacha de la familia Agamand, la espada de la familia Agamand, la maza de la familia Agamand y la daga de la familia Agamand.", description = "Los miembros de la familia Agamand fueron mis patrones en vida, ¡y también fueron la causa de mi muerte! ¡Qué apropiado que los Agamand sean ahora unos esbirros insignificantes del rey Exánime!\n\nMe gustaría tener los últimos vestigios de su honor. Ve a la cripta de la familia y róbalos de sus reliquias familiares: una espada, un hacha, una daga y una maza. ¡Tráemelos!\n\nLa Cripta de la Familia Agamand está en el extremo norte de los Molinos de Agamand, protegida por la Plaga y por los Agamand muertos.", progress = "¿Tienes las reliquias, <nombre>?", completion = "¡Ah! ¡Los has recuperado! El último vestigio de honor y de recuerdo ya ha sido arrancado de las tierras malditas de Agamand. ¡La familia ha desaparecido por completo!", rewards = "Recibirás:\n\n"},
[1822] = {title = "Arma reliquia", objective = "Conservaré tres de las armas que me has devuelto, como recuerdos de mi sufrimiento y de mi pérdida. Pero por favor, <nombre>, elige tu favorita entre las reliquias y quédatela.\n\nQue te sea de más ayuda que a sus antiguos y malditos propietarios.", description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nHacha reliquiaMartillo reliquiaDaga reliquiaEspada reliquia\n\nTambién recibirás:\n\nHacha reliquiaDaga reliquiaMartillo reliquiaEspada reliquia"},
[1823] = {title = "Habla con Ruga", objective = "Habla con Ruga Furiatótem.", description = "Eres <un guerrero/una guerrera> de fama y talento. ¿Estás <preparado/preparada> para la prueba de Ruga Furiatótem? Si lo estás, encontrarás a Ruga en Campamento Taurajo, en Los Baldíos.\n\nEstá esperando a todo guerrero que responda a su llamada.", progress = nil, completion = "¿Has venido por mi prueba, &lt;nombre&gt;?", rewards = "Recibirás:\n\n"},
[1824] = {title = "Prueba en Tierra de Gigantes", objective = "Lleva 5 antenas agitadas a Ruga Furiatótem de Campamento Taurajo.", description = "Para superar la prueba debes viajar hacia el Sur, hasta Tierra de Gigantes. Allí encontrarás gran cantidad de criaturas reptantes, insectos. Son nuevas en Los Baldíos y no me gusta lo que presagian.\n\nTráeme sus antenas cuando aún sigan agitándose y no pierdas tiempo, <nombre>, o las antenas dejarán de agitarse. Tráeme las antenas en el tiempo previsto y superarás la prueba.", progress = "¿Tienes las antenas? Si las tienes dámelas enseguida, las antenas agitadas no siguen agitándose mucho tiempo...", completion = "¡Muy bien hecho, &lt;nombre&gt;! Al superar la prueba en Tierra de Gigantes has dado un gran paso adelante en la senda del &lt;clase&gt;.", rewards = "Recibirás:\n\nBastión de Ruga"},
[1825] = {title = "Habla con Thun'grim", objective = "Habla con Thun'grim Vistafuego en Los Baldíos.", description = "Thun'grim Vistafuego, un afamado herrero orco, ha oído hablar de tus hazañas y del valor mostrado a la Horda. Le gustaría hacerte una armadura, te conviene ir a hablar con él inmediatamente.\n\nThun'grim está acampado al este de El Cruce, en Los Baldíos.", progress = nil, completion = "Sí, has venido. Es estupendo, tenemos mucho de qué hablar tú y yo.", rewards = "Recibirás:\n\n"},
[1838] = {title = "Armadura brutal", objective = "Lleva 15 lingotes de hierro humeantes, 10 azuritas en polvo, 10 lingotes de hierro y un vial de flogisto a Thun'grim Vistafuego.", description = "He oído hablar de tu fuerza y valor y me gustaría hacerte una armadura, una compañera que te ayudará en tus nuevas hazañas.\n\nPero la elaboración de la armadura será una auténtica misión, <nombre>. Necesitaré tu ayuda y la de otros herreros de la Horda.\n\nToma, coge este pergamino. He pasado muchas noches luchando con mis ojos casi ciegos, escribiendo lo que necesito para la armadura. Tráeme los objetos que aparecen en el pergamino y crearé la primera pieza de la armadura.", progress = "¿Tienes los objetos que indicaba mi pergamino, <nombre>?", completion = "Trajiste todo lo que te pedí. Ahora, haré mi parte...", rewards = "Recibirás:\n\n"},
[1839] = {title = "Ula'elek y los guanteletes brutales", objective = "Habla con Ula'elek en Durotar.", description = "El herrero trol Ula'elek podrá elaborar los guanteletes Brutal. Habita en Durotar, en Poblado Sen'jin, con sus hermanos Lanza Negra, está esperando tu llegada.", progress = nil, completion = "Saludos, &lt;nombre&gt;. El viejo Thun'grim me avisó de tu llegada pero si hubiera guardado silencio sobre tu presencia aquí no me habría sorprendido.\n\nLos espíritus siguen tus progresos, joven &lt;clase&gt;, y me dicen cuántos enemigos han caído a tus pies.", rewards = "Recibirás:\n\n"},
[1840] = {title = "Orm Pezuñapétrea y el yelmo brutal", objective = "Habla con Orm Pezuñapétrea en Cima del Trueno.", description = "Orm Pezuñapétrea es muy respetado por sus habilidades. Te hará el yelmo Brutal.\n\nLo encontrarás en Cima del Trueno, cerca del estanque, junto al gran tótem central.", progress = nil, completion = "Saludos, &lt;clase&gt;. Soy Orm, Thun'grim me dijo que querías que te hiciera un yelmo, ¿cierto?", rewards = "Recibirás:\n\n"},
[1841] = {title = "Velora Noctis y las musleras brutal", objective = "Habla con Velora Noctis en Entrañas.", description = "Se dice que, antes de su muerte, Velora Noctis era una de las mejores forjadoras de armaduras de Lordaeron. La muerte, al parecer, no ha menguado su talento. Habla con Velora, te hará tus musleras Brutal.\n\nVelora comercia en Entrañas de Lordaeron, en el Barrio del Comercio.", progress = nil, completion = "Has venido en busca de unas musleras, ¿eh? ¡Genial!\n\nUmm... ¿te las tengo que hacer yo o tú me las haces a mí?\n\nEra broma, ¿no ha sido divertida? No lo sé... perdí mi sentido del humor cuando perdí mi olfato. Más o menos cuando dejé también de respirar.", rewards = "Recibirás:\n\n"},
[1842] = {title = "Pezuñas de sátiro", objective = "Lleva 7 pezuñas unguladas de sátiro a Ula'elek de Poblado Sen'jin en Durotar.", description = "Puedo hacerte un par de guanteletes Brutal que tendrán una enorme fuerza mágica. Pero antes debes reunir esa magia y traérmela.\n\nLos sátiros habitan en la parte noreste de Vallefresno por el poder del bosque. Se alimentan de su pureza, corrompiéndola y exprimiéndola. Puedo decirte cuáles son los sátiros que extraen la mayor parte de la magia de Vallefresno con sus pezuñas hinchadas y unguladas.\n\nEsas pezuñas albergan un poder enorme. Las necesitaremos para elaborar tus guanteletes.", progress = "¿Tienes las pezuñas, <nombre>? Su magia es muy poderosa.", completion = "¡Ah, ya las tienes! Y ahora, de la misma forma en que los sátiros exprimieron toda la fuerza de Vallefresno, exprimiré yo el poder de estas pezuñas y lo utilizaré en mi creación...", rewards = "Recibirás:\n\n"},
[1843] = {title = "Guanteletes brutales", objective = "Gracias, <nombre>. Estos guanteletes son mi mejor creación. Cógelos. Llevándolos me honrarás a mí y a mis antepasados.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nGuanteletes brutales"},
[1844] = {title = "Cuerno de quimera", objective = "Lleva un cuerno galvanizado a Orm Pezuñapétrea de Cima del Trueno.", description = "Te haré el yelmo, pero debes demostrar que lo mereces.\n\nEscondida en el límite noroeste de la Sierra del Espolón encontrarás La Vega Carbonizada. Fue un día un auténtico vergel, pero hoy está desolada y reducida a cenizas y las criaturas que quedaron allí están locas de terror y desesperación.\n\nAllí es a donde te envío, <nombre>.\n\nDebes ir a La Vega Carbonizada y encontrar una matriarca quimera. Mátala, libérala de su miseria y tráeme su cuerno galvanizado como prueba. Si lo haces te haré el yelmo.", progress = "¿Tienes el cuerno? ¿Has librado a la matriarca de su sufrimiento?", completion = "Has terminado tu tarea, &lt;nombre&gt;, y me has demostrado tu valía y resolución. El yelmo será tuyo...", rewards = "Recibirás:\n\n"},
[1845] = {title = "Yelmo brutal", objective = "Te concedo este yelmo.\n\nMe diste grandes gestas, <nombre>, y si la Madre Tierra quiere... aún te esperan gestas mayores.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nYelmo brutal"},
[1846] = {title = "Tibias de Faucedraco", objective = "Lleva 8 tibias de Faucedraco robustas a Velora Noctis de Entrañas.", description = "Para elaborar las musleras Brutal necesito una llave de tibias robustas. Los orcos Faucedraco de Los Humedales tienen unas tibias perfectas, muy resistentes. Las necesito.\n\nDeben ser robustas. Asegúrate de probar las tibias que recojas. Si puedes romperlas con las manos no serán lo bastante robustas para trabajar con ellas.", progress = "Estoy esperando esas tibias, <nombre>. Serán un armazón perfecto para tus musleras.", completion = "¡Bien hecho! Estas tibias parecen muy robustas, creo que serán perfectas. ¿Cómo se tomaron los orcos que te llevaras sus huesos, &lt;nombre&gt;? ¿Se quejaron mucho?\n\nEra otra broma. No te estás riendo.\n\nNo estoy muy graciosa últimamente ¿verdad?...", rewards = "Recibirás:\n\n"},
[1847] = {title = "Musleras brutales", objective = "Aquí tienes tus musleras, <nombre>. Déjame ver si te están bien o si hay que hacer algún ajuste. ¡Estoy aquí para servir! Al menos es lo que solía hacer.\n\nHoy en día no me preocupo mucho por mi trabajo, ¡pero es mejor que ser un esbirro sin cerebro de la Plaga!\n\nSupongo.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nMusleras brutales"},
[1848] = {title = "Camisote brutal", objective = "Habrás tenido que soportar duras privaciones y habrás derramado mucha sangre cumpliendo mi tarea, <nombre>. Este es el producto de todo tu esfuerzo.\n\nUtilízalo bien y quizás un día vuelvas a contarme cuántas veces te ha sido de ayuda.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCamisote brutal"},
[1858] = {title = "Mano Destrozada", objective = "Roba la llave de Tazan, abre su cartera y llévale el contenido a Therzok al Circo de las Sombras, en Orgrimmar.", description = "Tú te encargas de abrirla, luego tráeme el contenido. La cerradura es complicada, así que lo más fácil será que consigas la llave.\n\nComo Tazan no la llevaba encima, debemos suponer que se la dio a alguien... Ah, claro. Tenía un secretario, un borracho que se llama Gamon. Lo más seguro es que esté en la taberna; intenta quitarle la llave.", progress = "Es humillante tener el objeto que deseas a tu alcance y no ser capaz de tomarlo.", completion = "Mm, interesante. Has probado sobradamente tu valía, &lt;nombre&gt;. Considérate el miembro más reciente de Mano Destrozada. Y toma esto; te ayudará mucho en el futuro.", rewards = "Recibirás:\n\nHoja de Astucia"},
[1859] = {title = "Therzok", objective = "Ve a Orgrimmar, al Circo de las Sombras, y habla con Therzok.", description = "Baja la voz, <nombre>. Sé quién eres. Tenía tu descripción y te esperaba.\n\nPor lo que he oído de ti, te manejas bien en combate y no te opones a alguna incursioncilla que otra de vez en cuando.\n\n¿Me equivoco?\n\nEs lo mismo. Tienes que ir a Orgrimmar a ver a Therzok. Tiene una oferta que hacerte.", progress = nil, completion = "Muy bien, me alegro de que Kaplak consiguiera encontrarte.\n\nTenemos que hablar de varias cosas y la más importante es tu futuro en la Mano.", rewards = "Recibirás:\n\n"},
[1860] = {title = "Habla con Jennea", objective = "Habla con Jennea Cannon en Ventormenta.", description = "Los magos tenemos una tarea por hacer, y es vital para la seguridad de Ventormenta y del Bosque de Elwynn.\n\nJennea Cannon sabe los detalles. Estudia en el Sagrario del Mago, en el Barrio de los Magos de Ventormenta. Deberías ir a verla lo antes posible.", progress = nil, completion = "¿Te han enviado para ayudarme con mi tarea, &lt;nombre&gt;? Bien. Es alentador ver cómo los jóvenes magos se esfuerzan por ayudar a nuestra causa...\n\n¿Qué causa, dices? A su debido tiempo, joven. Lo sabrás... a su debido tiempo.", rewards = "Recibirás:\n\n"},
[1861] = {title = "Lago Espejo", objective = "Lleva una muestra del Lago Espejo a Jennea Cannon en Ventormenta.", description = "Este mundo ha padecido enormes luchas místicas, que desbordaron corrientes de magia de canales antes estables. Nos esforzamos en determinar los efectos que han causado esos cambios y hay quien teme lo peor.\n\nPero ya te hablaré de todo ello más adelante. Por ahora, necesitamos que reúnas información.\n\nToma, coge esta botella. Ve a la base de la cascada del Lago Espejo, al suroeste de las puertas de Ventormenta y recoge una muestra de agua. Vuelve a verme para que pueda comprobar si tiene contaminación mágica.", progress = "¿Tienes la muestra, <nombre>? El flujo mágico de Ventormenta y Elwynn ha sido alterado y debo averiguar si se está filtrando en el agua.", completion = "Gracias, &lt;nombre&gt;. Comprobaré las propiedades mágicas de este agua. Esperemos que no contenga ninguna o los que han estado bebiendo demasiado tiempo agua del Lago Espejo podrían estar afectados.\n\nToma, &lt;nombre&gt;. Coge este orbe o este bastón como prueba de tus servicios. Que te sean de ayuda.", rewards = "Podrás elegir una de estas recompensas:\n\nOrbe LeyBastón Ley\n\nTambién recibirás:\n\nOrbe LeyBastón Ley"},
[1878] = {title = "Cazador de bolsas de agua", objective = "¿Ya has vuelto, <nombre>? Si tienes cinco bolsas más de agua te puedo dar ya mismo tu recompensa.", description = nil, progress = "¿Ya has vuelto, <nombre>? Si tienes cinco bolsas más de agua te puedo dar ya mismo tu recompensa.", completion = "Excelente, ¡cuantas más bolsas de agua consigamos, más nos alejaremos del desastre! Consigue tantas como puedas y te daré una recompensa por cada 5 bolsas de agua que me traigas.", rewards = "Recibirás:\n\nPaquete de curación de la compañía de aguas de Gadgetzan"},
[1879] = {title = "Habla con Bink", objective = "Habla con Bink en Forjaz.", description = "¡<nombre>! Bink, mi colega gnomo de Forjaz, ha enviado un mensaje. ¡Tiene una tarea que hacer y solo confía en un mago para hacerlo! ¿Serás tú ese mago, <nombre>?\n\nSi es así, ve a hablar con Bink. La encontrarás en la Sala de los Misterios en La Sala Mística de Forjaz.", progress = nil, completion = "Oh, veo que eres prometedor, ¡sí, lo eres! Pero hace falta algo más que una promesa para ser un buen &lt;clase&gt;. Y si no eres un buen &lt;clase&gt;, eres un mal &lt;clase&gt;. Y los malos magos son parte del problema, ¡sí, lo son!\n\nUps, me estoy adelantando... ¡empecemos por el principio!", rewards = "Recibirás:\n\n"},
[1880] = {title = "Trastónitor maguitronic", objective = "Llévale a Bink su trastónitor maguitrónic.", description = "¡Los magos hacemos mucho más que menear los dedos y sacar chispas! ¡Controlamos la magia, la estructura básica de nuestro mundo! Exige mucho cuidado, resolución y conocimiento... sí, ¡mucho conocimiento!\n\nUno de mis instrumentos, el trastónitor maguitrónic recoge y almacena ese conocimiento en grandes cantidades, ¡pero lo he perdido! Cuando dejamos nuestro hogar, Gnomeregan, lo dejé en un cofre en una de las cabañas a las puertas de la ciudad. ¡Y ahora el lugar está plagado de gnomos leprosos!\n\n¡Por favor, <nombre>, recupera mi trastónitor!", progress = "¿Encontraste el trastónitor? ¡Debo recuperarlo antes de que se quede sin suministro de energía!", completion = "¡Oh, un día espléndido! Cuando hice mi trastónitor no me di cuenta de lo que iba a depender de él. ¡Ya puedo volver a empezar mis estudios! \n\n¡Gracias, &lt;nombre&gt;! Y toma, estas fueron mis primeras herramientas de &lt;clase&gt;. Están un poco estropeadas pero funcionan bien. Escoge lo que quieras, seguro que cualquiera de las dos cosas te vendrá bien.", rewards = "Podrás elegir una de estas recompensas:\n\nOrbe ArcanoBastón Arcano\n\nTambién recibirás:\n\nOrbe ArcanoBastón Arcano"},
[1881] = {title = "Habla con Anastasia", objective = "Habla con Anastasia Pozovenado.", description = "<nombre>, Anastasia Pozovenado tiene una misión para alguien con tus aptitudes. Habla con ella en el Barrio de la Magia en Entrañas y prepárate para tu primera tarea como mago de los Renegados.", progress = nil, completion = "Saludos, &lt;nombre&gt;. Fuiste sabio al responder a mi llamada, y aún lo serás más si escuchas lo que voy a decirte...", rewards = "Recibirás:\n\n"},
[1882] = {title = "La Hacienda Balnir", objective = "Lleva las Bocas de dragón de Balnir a Anastasia Pozovenado en el Barrio de los Magos de Entrañas.", description = "Las Tierras de la Peste del Este son una aberración. Ideadas por el rey Exánime, propagan su magia a la tierra, corrompiendo y matando toda forma de vida. Aunque las fuerzas destructivas de la Peste tienen un cierto encanto, nosotros, los magos de los Renegados, creemos que es indomable y que debe ser reducida.\n\nPero antes de nada, debemos medir hasta qué punto se ha propagado en Tirisfal. Ve a la Hacienda Balnir en la parte oriental de Tirisfal. En el jardín encontrarás flores: Bocas de dragón. Recógelas y vuelve a verme.", progress = "¿Tienes las Bocas de dragón? Esa clase de flor es muy receptiva a las energías mágicas y estudiar las Bocas de dragón que se crían tan cerca de las Tierras de la Peste podrá indicarnos si la Peste se ha introducido en los Claros de Tirisfal.", completion = "Ah, es un espécimen fantástico. Lo enviaré enseguida para que lo estudien nuestros boticarios y para que puedan determinar la extensión de la corrupción de las Tierras de la Peste.\n\nBien hecho, &lt;nombre&gt;. A cambio de todos tus servicios te doy a elegir entre un orbe o un bastón. Elijas lo que elijas te será muy útil.", rewards = "Podrás elegir una de estas recompensas:\n\nOrbe ArcanoBastón Arcano\n\nTambién recibirás:\n\nOrbe ArcanoBastón Arcano"},
[1883] = {title = "Habla con Un'thuwa", objective = "Habla con Un'thuwa.", description = "Un'thuwa, mago tribal de Poblado Sen'jin, detecta una alteración en las corrientes mágicas de Durotar y está buscando ayuda. Habla con Un'thuwa y haz lo que puedas para ayudarle.\n\nHazlo y con el tiempo se te revelarán nuevos secretos.", progress = nil, completion = "Ave, &lt;nombre&gt;. Loado seas, has respondido a mi petición de auxilio. Esperemos que estés preparado para esta tarea...", rewards = "Recibirás:\n\n"},
[1884] = {title = "Montones de Ju-Ju", objective = "Destruye 4 montones de Juju y vuelve con Un'thuwa en Poblado Sen'jin.", description = "El brujo Zalazane de las Islas del Eco ha interrumpido el flujo normal de la magia en Durotar. Lo hace a través de fetiches corruptos, montones de Juju, que ha colocado entre sus cabañas en la Isla del Eco principal.\n\nEstos montones corrompen nuestra naturaleza y dan poder a los brujos y a las criaturas infernales que merodean las zonas más oscuras del país.\n\nDebes destruir los montones de Juju, <nombre>, antes de que el flujo de magia quede alterado para siempre.", progress = nil, completion = "Puedo sentir que los montones de Juju de Zalazane han sido destruidos y con tiempo y algo de suerte, el flujo de magia de Durotar volverá a la normalidad.\n\nHas hecho una gran gesta por nuestro pueblo, &lt;nombre&gt;. Y has demostrado tu valor para nosotros, los magos de la Horda.", rewards = "Podrás elegir una de estas recompensas:\n\nOrbe LeyBastón Ley\n\nTambién recibirás:\n\nOrbe LeyBastón Ley"},
[1885] = {title = "Mennet Carkad", objective = "Habla con Mennet Carkad en el Barrio de los Pícaros de Entrañas.", description = "¿<nombre>? No eres exactamente como  esperaba, teniendo en cuenta que el mismísimo Carkad se ha interesado por ti. Si bien es cierto que la primera impresión no siempre es la que cuenta, ¿no crees?\n\nEn cualquier caso, deberías ir a verlo tan pronto como puedas. No te gustaría saber lo que les ocurrió a los que hicieron caso omiso a su llamada.", progress = nil, completion = "¿Mm? No hay duda de que has venido tan pronto te has enterado de que requería tu presencia. Claro que no esperaría otra cosa de un futuro mortachechador.", rewards = "Recibirás:\n\n"},
[1886] = {title = "Los Mortacechadores", objective = "Consigue la carta de presentación de Astor y devuélvesela a Mennet Carkad, que está en el Barrio de los Pícaros.", description = "Si quieres formar parte de los mortacechadores, la guardia de élite de Lord Varimathras, tendrás que demostrar tu valía.\n\nNo quiero mentirte, <nombre>, esta misión puede suponer tu muerte.\n\nYo tengo otras cosas que requieren de mi atención. Necesito los efectos personales de Astor Hadren, un mensajero que viaja entre el Bosque de Agénteos y Entrañas. Te dejo a tu elección el método que debes utilizar.\n\nCuanto menos sepas sobre la naturaleza de su negocio, mejor para ti. Si realizas con éxito está misión, te contaré más.", progress = "¿Lo has logrado?", completion = "Buen trabajo, &lt;nombre&gt;. Has superado la primera parte de esta prueba. Con un poco de suerte, Andron no se dará cuenta de nada y creerá que eres realmente el mensajero que estaba esperando.", rewards = "Recibirás:\n\n"},
[1898] = {title = "Los Mortacechadores", objective = "Habla con Andron Gant en Entrañas.", description = "La carta de presentación debería ser suficiente para convencer a Andron de que tú eres <el mensajero/la mensajera> que estaba esperando. Conozco bien a Andron y sé que no es demasiado desconfiado.\n\nLord Varimathras sospecha que Andron ha estado ayudando a otro grupo. Desea saber de qué organización se trata y quiere pruebas.\n\nQuiero que vayas a hablar con Andron y que obtengas esta información. Lo encontrarás en su tienda, cerca de El Apothecarium.", progress = "Hola. ¿Hay algo que pueda hacer por ti?", completion = "¡Ah! Te estaba esperando. Seguro que quienes te envían están satisfechos con la información que les he dado hasta el momento, ¿no? \n\nSupongo que no tiene mucho sentido que te haga esta pregunta pues seguro que desconoces la respuesta. No creo que confiaran tanto en un simple mensajero...", rewards = "Recibirás:\n\n"},
[1899] = {title = "Los Mortacechadores", objective = "Llévale el libro de contabilidad de Astor a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas.", description = "Encontrarás el libro de contabilidad con la información que procuran los que te envían en la estantería que está a mis espaldas. Llévaselo y diles que preferiría que te enviaran a ti en caso de que deseen seguir adelante con nuestro plan.", progress = "¿Lo has logrado, <nombre>?", completion = "Sin duda, has obtenido más información de Andron de la que esperaba. Déjame que le eche un vistazo antes de asignarte tu siguiente tarea.", rewards = "Recibirás:\n\n"},
[1918] = {title = "El elemento podrido", objective = "Llévale el globo de agua podrida a Mastok Sibilante en el Puesto del Hachazo, Vallefresno.", description = "El elemental caído ha producido un globo de agua de brillo intenso. El contenido del globo es, cuando menos, repulsivo. No me parece descabellado suponer que lo que contaminara el Lago Mystral se encuentra casi con toda seguridad en el agua del globo.\n\nNo es precisamente conocido quién podría estar interesado en semejante hallazgo; quizás la persona que te envió en primer lugar al lago, Mastok Sibilante del Puesto del Hachazo, podría tener una idea.", progress = "¿Sí? ¿Necesitas algo?", completion = "Te encuentras una asquerosa bola de agua ¿y decides traérmela? ¡¿Quién crees que soy?! ¿El Anillo de la Tierra o algo así?\n\nDe hecho, esto me da una idea... quizás necesites al Anillo de la Tierra para esto. Vamos, que si contribuye a aclarar lo del lago de una vez por todas, estoy contigo.\n\n¿Sabes quiénes integran el Anillo de la Tierra, &lt;nombre&gt;?", rewards = "Recibirás:\n\n"},
[1919] = {title = "Preséntate ante Jennea", objective = "Habla con Jennea Cannon en Ventormenta.", description = "Tu conocimiento sobre esta disciplina no deja de crecer, joven <nombre>. ¿Estás <listo/lista> para comenzar las auténticas lecciones? ¿Estás <listo/lista> para ocupar tu lugar en la guerra secreta en la que estamos embarcados?\n\nSi es así, ve a hablar con Jennea Cannon, en el Sagrario del Mago, en el Barrio de los Magos de Ventormenta. Tiene una tarea para ti y esa tarea te abrirá la primera ventana al mundo de lo invisible.", progress = nil, completion = "&lt;nombre&gt;, tengo una misión para ti, aquí en el distrito de los magos. Escucha atentamente, no hay tiempo que perder.", rewards = "Recibirás:\n\n"},
[1920] = {title = "Investiga lo que pasa en El Ermitaño Azul", objective = "Detrás de Jennea Cannon encontrarás un Canto de manifestación y un cofre de arcas de contención. Cógelos y lleva las arcas de contención llenas a Jennea, en el Sagrario del Mago.", description = "Han llegado informes de una alteración en la taberna El Ermitaño Azul del Barrio de los Magos. Criaturas invisibles y extrañas vagan por la taberna y temo que se haya formado una falla de maná.\n\nQuiero que investigues la taberna y que captures a esas criaturas. Utiliza el Canto de manifestación en la taberna para hacer visibles a las criaturas, cuando las tengas dominadas utiliza un arca de contención para capturarlas. Cuando tengas bastantes vuelve a verme trayendo contigo las arcas llenas y vacías y el Canto.", progress = "¿Has capturado a las criaturas, <nombre>? Hay que estudiarlas para determinar el motivo de su llegada.", completion = "¡Muy bien hecho! Estudiaré esas criaturas y, con diligencia y algo de suerte, determinaré cómo se infiltraron en nuestro mundo.\n\nTu valor para la sociedad de magos no deja de crecer, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[1921] = {title = "Recolección de materiales", objective = "Lleva 10 piezas de lino y 6 gemas cargadas de la falla a Wynne Larson en Ventormenta.", description = "Has servido bien a tus compañeros magos y ha llegado el momento de vestir tu túnica de mago. Wynne Larson te hará la túnica, pero debes llevarle los materiales necesarios.\n\nLa túnica lleva lino, que es muy fácil de encontrar, pero también debes traer gemas cargadas de la falla que solo encontrarás en la Mina de Fuenteplata de Loch Modan, en los cajones de la Liga de Mineros enanos.\n\nReúne el lino y las gemas y llévaselos a Wynne Larson en Diseños Larson del Barrio de los Magos.", progress = "Hola, <nombre>. Jennea me dijo que vendrías. ¿Tienes algo para mí?", completion = "Sí, son unas piezas de lino excelentes. Podré hacer una bonita túnica y cuando entreteja las gemas de la falla, ¡tendremos una prenda realmente digna de un &lt;clase&gt;!", rewards = "Recibirás:\n\n"},
[1938] = {title = "Tratado de Ur sobre la Magia de las Sombras", objective = "Lleva el Tratado de Ur sobre la Magia de las Sombras al sumo hechicero Andromath en Ventormenta.", description = "Hace mucho tiempo, tres magos estudiaban en esta torre hasta que uno de ellos sucumbió a las artes oscuras. Se le instó a apartarse de esa magia maligna, pero no pudo resistirse. Así que fue condenado al exilio.\n\nLos nuevos dominios del mago caído están en la Torre de Ilgalar en Crestagrana, a donde llevó un poderoso libro: el Tratado de Ur sobre la Magia de las Sombras.\n\nNosotros, los magos de Ventormenta, queremos recuperar el libro. Entra en la Torre de Ilgalar, encuentra el tomo en su biblioteca y tráemelo.", progress = "¿Tienes el libro, <nombre>? Sus secretos no son para los no instruidos y mirar sus entrañas puede llevar a la perdición y a la ruina.", completion = "Temía que Morganth hubiera escondido el libro en algún otro sitio o que hubiera encontrado un modo de abrirlo y leer su contenido.\n\nMe alegra saber que no fue así y que los sellos del Tratado de Ur sobre la Magia de las Sombras siguen en su sitio.", rewards = "Recibirás:\n\n"},
[1939] = {title = "Alto Hechicero Andromath", objective = "Habla con el sumo hechicero Andromath.", description = "El sumo hechicero Andromath quiere hablar contigo, <nombre>. Está en la parte superior del Sagrario del Mago, en el Barrio de los Magos de Ventormenta.\n\nDeberías ir a hablar con él, Andromath es uno de los magos más poderosos fuera de Dalaran. Su consejo es, sin duda, muy valioso.", progress = nil, completion = "Saludos, &lt;nombre&gt;. Es un placer verte, espero que tengas algo de tiempo para escuchar las palabras de un anciano...", rewards = "Recibirás:\n\n"},
[1940] = {title = "Seda prístina de araña", objective = "Lleva 8 sedas prístinas de araña a Wynne Larson en Ventormenta.", description = "Tus servicios han sido inquebrantables, <nombre>. Creo que quizás ha llegado el momento de que tengas unas prendas más delicadas, más adecuadas a tu posición como mago adepto. Si reúnes los materiales necesarios, te haremos la túnica.\n\nTendrás que adentrarte en el Bosque del Ocaso y cazar arañas ermitañas carroñeras. Extrae su seda, pero solo la más delicada, la seda prístina es la más indicada. Cuando tengas suficiente seda, llévasela a Wynne Larson, en el Barrio de los Magos, en Diseños Larson. Ella hará tu túnica.", progress = "Ah, sí. Los magos del Sagrario del <clase> dijeron que vendrías a hablar conmigo. ¿Tienes la seda necesaria?", completion = "Unos bonitos especimenes. ¡Me pondré manos a la obra!", rewards = "Recibirás:\n\n"},
[1941] = {title = "Toga Tejemaná", objective = "Aquí está tu toga. ¡Es una de las mejores que he hecho nunca! Llévala con orgullo y cuando te pregunten... ¡di que te la hicieron en Diseños Larson!", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nToga Tejemaná"},
[1942] = {title = "Prendas de nudo astral", objective = "¡Ya está lista! La seda que trajiste era tan delicada... que te hice la prenda un poco pequeña para quedarme yo con algo de sobra, ¿no te importa, verdad?\n\nEs una broma. Te quedará perfecta y será ideal tanto para tus aventuras como para una cena formal. ¡Que la disfrutes!", description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nToga de nudos astralBlusa de nudos astral\n\nTambién recibirás:\n\nToga de nudos astralBlusa de nudos astral"},
[1943] = {title = "Habla con Deino", objective = "Habla con Deino.", description = "Deino, la maga trol, necesita tu ayuda, <nombre>. Vive en Orgrimmar, en el Refugio Zarzaoscura.", progress = nil, completion = "Bien, has venido. Mi tarea no es fácil, pero es vital para la sociedad de magos. Si me ayudas estoy segura de que te merecerá la pena.", rewards = "Recibirás:\n\n"},
[1944] = {title = "Aguas de Xavian", objective = "Lleva la muestra de agua de Xavian a Deino en Orgrimmar.", description = "La legendaria ciudad de Xavian se encuentra escondida en las profundidades de la parte oriental de Vallefresno. Los eruditos conocen historias de su antigua sabiduría, se dice, por ejemplo, que las aguas de Xavian albergan grandes poderes mágicos.\n\nMe gustaría tener una muestra de esas aguas, tomada de las propias fuentes.\n\nToma esta botella y ve a Xavian. Recoge agua del fondo de su cascada más alta y vuelve a verme. Ten mucho cuidado cuando atravieses la antigua ciudad, los sátiros protegen las ruinas y no les gusta ver a magos entrometiéndose en sus asuntos.", progress = "Me alegra verte de vuelta intacto, <nombre>. ¿Tienes la muestra de agua? Me muero por estudiar sus propiedades.", completion = "Ah, muy bien. Seguro que los sátiros de Xavian te dieron algún problemilla, ¿verdad?\n\nGracias, &lt;nombre&gt;. Estudiaré esta muestra de agua de Xavian inmediatamente.", rewards = "Recibirás:\n\n"},
[1945] = {title = "Hermanas sonrientes", objective = "Lleva 12 cabellos de hermana sonriente a Kil'hala en El Cruce.", description = "A cambio de tus servicios he dispuesto la creación de tus nuevas túnicas, siempre que puedas reunir los materiales necesarios...\n\nPara hacerlo debes volver a Vallefresno y conseguir mechones de cabello de las hermanas sonrientes, las dríades que se reúnen junto al Refugio de la Algaba.\n\nCuando tengas su cabello llévaselo a Kil'hala, un sastre trol de El Cruce en Los Baldíos. Te estará esperando y elaborará tu túnica.", progress = "Saludos, <nombre>. Deino me dijo que vendrías y que traerías el cabello de hermana sonriente. ¿Lo has traído?", completion = "Ah, este cabello será perfecto para tejer una buena túnica, &lt;nombre&gt;. Es una pena que las hermanas sonrientes tuvieran que morir para quitárselo, pero dudo que lo hubieran dado voluntariamente.\n\nUna pena... pero pequeña.", rewards = "Recibirás:\n\n"},
[1946] = {title = "Prendas de encaje abisal", objective = "Aquí tienes tu encaje abisal, <nombre>. Es cómodo y duradero y alberga la magia del cabello de hermana sonriente.\n\nDisfrútalo y si lo encuentras adecuado menciónaselo a Deino, la maga. Me gustaría estar de su lado...", description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nToga abisalTúnica abisal\n\nTambién recibirás:\n\nToga abisalTúnica abisal"},
[1947] = {title = "Viaje al pantano", objective = "Habla con Tabetha.", description = "Ha llegado el momento de que obtengas tu varita de mago. Para comenzar esta búsqueda habla con Tabetha, una ermitaña humana que habita en el Marjal Revolcafango. Encontrarás su choza en las profundidades del marjal, al oeste de Theramore.\n\nHabla con ella, su saber es enorme. Encontrarás su casucha al oeste de Theramore y al norte de las Ruinas Quebrantarrocas.", progress = nil, completion = "¡Silencio! ¡Es un momento muy delicado, no me molestes!\n\nUn momento...", rewards = "Recibirás:\n\n"},
[1948] = {title = "Objetos de poder", objective = "Lleva 1 jade y la zarza electrificada a Tabetha en el Marjal Revolcafango.", description = "Crear la varita de un mago no es fácil. Se necesitan sustancias extrañas y debe hacerse un recipiente que pueda almacenar grandes cantidades de energía mágica. Debes reunir estas cosas y traérmelas.\n\nHe escrito en este pergamino todo lo que necesitas e instrucciones para conseguirlo. Tráeme exactamente lo que está en la lista y sigue las indicaciones al pie de la letra, si no la varita podría hacer cosas... no muy buenas.", progress = "Hola, <nombre>. ¿Tienes los objetos que necesito?", completion = "¡Aja! ¡Tienes el jade y la zarza! ¡Bien hecho! \n\n¿No es humo lo que veo salirte de las orejas? Debías estar demasiado cerca cuando el rayo golpeó el palo de tótem, pero no te preocupes, ¡ser golpeado por un rayo trae buena suerte!", rewards = "Recibirás:\n\n"},
[1949] = {title = "Secretos escondidos", objective = "Habla con Magus Tirth en El Desierto de Sal.", description = "Para hacer tu varita, necesito leer unos pasajes clave de un libro determinado. El libro se llama Rituales de poder, y fue escrito por Magus Tirth.\n\nNo tengo ninguna copia del libro y ninguna de las grandes bibliotecas del mundo lo tiene. Debes hablar con Tirth y conseguir que te lo dé.\n\nBuena suerte. Tirth era un gran mago y erudito, pero ahora se pasa el día en las carreras de los goblins y de los gnomos en El Desierto de Sal, bebiendo y haciendo apuestas.", progress = nil, completion = "¿Qué? ¡Habla más alto! Estos viejos oídos ya no son lo que eran.\n\n¡Bien, espero que hayas venido con una jarra fresca de cerveza o con alguna información para la siguiente carrera! Si no es así, más vale que te vayas... ¡porque tengo una varita polimorfizadora que lleva escrito tu nombre!", rewards = "Recibirás:\n\n"},
[1950] = {title = "Santo y seña", objective = "Averigua la frase para la caja fuerte de Tirth y regresa con Tirth.", description = "¡Puedo ayudarte con el libro, pero antes tienes que ayudarme!\n\nEl tomo que buscas está en mi caja fuerte cerrada mágicamente. Para abrirla hay que pronunciar una determinada frase, ¡y no puedo recordarla! Normalmente mi asistente me ayuda a recordar esas cosas, pero no hay forma de encontrarlo.\n\nSi quieres mi libro debes encontrar a mi aprendiz. Pregunta por la pista de carreras, si tienes suerte lo habrá visto alguien. ¡Tráelo de vuelta aquí o consigue que te diga mi frase mágica para poder abrir la caja!", progress = "¿A qué estás esperando? ¡Necesito la frase mágica!", completion = "Ah, ya recuerdo...\n\nMe había olvidado de la pequeña maldición que lancé sobre Johnson. No puedo recordar porqué lo hice pero seguro que le ha venido bien, ¡pequeño ingrato!", rewards = "Recibirás:\n\n"},
[1951] = {title = "Rituales de poder", objective = "Lleva el libro Rituales de poder a Tabetha en el Marjal Revolcafango.", description = "Umm... ahora que lo pienso... ¡no tengo el libro! Verás, tenía algunas deudas de juego y para pagarlas vendí la última copia de Rituales de poder a un miembro del clero... \n\nUn miembro de la Hermandad Escarlata. ¡Debe haberlo llevado a su biblioteca en el Monasterio Escarlata de los Claros de Tirisfal!\n\nSi quieres el libro, tendrás que ir al monasterio a buscarlo. Y perdona por todo este lío... últimamente no soy yo mismo.", progress = "Saludos, <nombre>. ¿Encontraste el libro, Rituales de poder?", completion = "¡Eso es! ¡Es todo lo que necesito para preparar el ritual de creación de tu varita! Espero que no tuvieras muchos problemas para encontrarlo...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1952] = {title = "Varitas de mago", objective = "Habla con Tabetha cuando termine su ritual.", description = "Ahora voy a hacer las varitas.", progress = nil, completion = "Estas son las varitas que hice. ¡Elige la que quieras!", rewards = "Podrás elegir una de estas recompensas:\n\nVarita Furia de HieloVarita de potencia abisalVarita Ira Ardiente\n\nTambién recibirás:\n\nVarita Ira ArdienteVarita Furia de HieloVarita de potencia abisal"},
[1953] = {title = "Regreso al pantano", objective = "Habla con Tabetha.", description = "<nombre>, es hora de que vayas a hablar con Tabetha... la ermitaña del Marjal Revolcafango. Te instruirá sobre la creación de un instrumento muy poderoso para un mago.\n\nLa choza de Tabetha está en las profundidades del Marjal Revolcafango, al oeste de Theramore.", progress = nil, completion = "Ah si. ¿Has venido buscando un orbe o un bastón, &lt;nombre&gt;? Muy bien. Te ayudaré a construirlo.\n\nPero te lo advierto: el proceso es peligroso y puede significar tu muerte.\n\nO la mía. En cualquier caso, la muerte de alguien...\n\nPero vamos a esperar que sea la muerte de otro, ¿eh?", rewards = "Recibirás:\n\n"},
[1954] = {title = "El orbe infernal", objective = "Lleva un orbe infernal a Tabetha en el Marjal Revolcafango.", description = "El primer paso es adquirir un orbe con poder. Los brujos del Filo Ardiente tienen orbes fantásticos, de gran calidad... ¡lo malo es que tienen atados demonios en su interior! Pero podemos arreglarlo, sí, podemos.\n\nVe a Desolace y hazte con un orbe infernal de un invocador del Filo Ardiente. Encontrarás invocadores en el Aquelarre Mannoroc.\n\nCuando tengas el orbe, vuelve a verme.", progress = "¿Tienes un orbe infernal, <nombre>?", completion = "¡Oh, es un orbe estupendo! Y no, no es estupendo como lo sería un zumo fresco en un día de verano.\n\nLo cierto es que ahora que lo pienso NO es nada estupendo. No digo el zumo. ¡El orbe!\n\n¡Concéntrate, &lt;nombre&gt;! ¡Deberás estar concentrado si queremos terminar tu tarea!", rewards = "Recibirás:\n\n"},
[1955] = {title = "El exorcismo", objective = "Mata al demonio del orbe y habla con Tabetha.", description = "Ahora que tenemos un orbe de invocador, debemos exorcizar al demonio que hay en su interior. Podemos hacerlo. Es la parte más fácil.\n\nPero tú tienes que hacer el trabajo duro. Tendrás que matar al demonio cuando esté fuera.\n\nY tendrás que hacerlo rápido. Si te lleva demasiado tiempo volverá a meterse en el orbe y tendremos que empezar de nuevo.\n\nTendrás que estar <preparado/preparada>. Ten listos tus hechizos más poderosos y ten tus pociones a mano. Trae también a un amigo, si lo tienes, esto no va a ser nada fácil.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1956] = {title = "Poder en Uldaman", objective = "Hazte con una fuente de poder obsidiano y llévasela a Tabetha en el Marjal Revolcafango.", description = "Ahora que hemos eliminado al demonio, debemos hacernos con una fuente de poder. Por desgracia, las mejores fuentes de poder suelen ser las más antiguas y las más difíciles de obtener. Los juegos y tretas del destino son fascinantes, ¿verdad?\n\nAsí que vas a tener que ir a Uldaman y vencer al centinela obsidiano. Es grande, duro ¡y su fuente de poder da buenos golpes!\n\nTe deseo todo lo mejor, <nombre>. Y si no tienes suerte... ha sido un placer conocerte.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[1957] = {title = "Oleadas de maná", objective = "Mata 12 oleadas de maná dentro del tiempo permitido.", description = "Ya casi hemos terminado, pero el último paso es el más peligroso, <nombre>.\n\nTengo el orbe del invocador vacío y estoy lista para otorgarle poderes. Lo haremos aprovechando la energía de una falla de maná. Lanzaré un hechizo que abrirá temporalmente una falla de la que saldrán criaturas, oleadas de maná. Deberás vencerlas y se liberará su poder, yo lo recogeré.\n\nMata bastantes oleadas de maná antes de que se cierre la falla y pondré la energía reunida en tu orbe.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[1958] = {title = "Poder celestial", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nOrbe celestialBastón celestial\n\nTambién recibirás:\n\nOrbe celestialBastón celestial"},
[1959] = {title = "Preséntate ante Anastasia", objective = "Habla con Anastasia Pozovenado.", description = "Los magos de Entrañas han notado una perturbación mágica dentro de la ciudad y han dado la orden a toda la Horda de investigar el suceso.\n\nAnastasia Pozovenado te puede contar más. Habla con ella en el Barrio de la Magia de Entrañas.", progress = nil, completion = "Es cierto. Se ha detectado una alteración mágica en nuestra ciudad. Todavía no estamos seguros de su origen, si es maligna o benigna. Antes debemos reunir más información...\n\nY esto, &lt;nombre&gt;, nos lleva a hablar de tu tarea.", rewards = "Recibirás:\n\n"},
[1960] = {title = "Investiga la tienda de alquimia", objective = "Detrás de Anastasia Pozovenado encontrarás un Canto de manifestación y un cofre de arcas de contención. Cógelos y lleva 3 arcas de contención llenas, el cofre de arcas de contención y el Canto de manifestación a Anastasia en Entrañas.", description = "La tienda de alquimia de Algernon, en El Apothecarium, es el centro de una extraña alteración. Criaturas invisibles, hechas tan solo de magia, vagan por la tienda... ¡debe haber una razón y debemos descubrirla!\n\nInvestiga la alteración y captura a esas criaturas. Coge el cofre de arcas y el pergamino que hay detrás de mí. Utiliza el pergamino en la tienda para hacer visibles a las criaturas, cuando las hayas dominado con tus hechizos, arroja un arca para capturarlas. Vuelve con las arcas llenas, las arcas vacías y el canto.", progress = "¿Has investigado la alteración, <nombre>? ¿Has capturado a las criaturas?", completion = "Muy bien, &lt;nombre&gt;. Nuestras mentes más preclaras estudiarán las criaturas que hay en el interior de estas arcas de contención y determinarán los motivos de su presencia en Entrañas.\n\nSi su aparición es el resultado de una falla en el flujo natural de la magia debemos determinar si esa falla fue aleatoria. Si no lo fue, y fue producto del ataque de alguna fuerza mágica, nos prepararemos para alteraciones futuras y más serias.", rewards = "Recibirás:\n\n"},
[1961] = {title = "Recolección de materiales", objective = "Lleva 10 piezas de lino y 6 gemas de maná de Dalaran a Josef Gregorian.", description = "Ha llegado la hora de que tengas tus primeras togas mágicas, <nombre>. Son un símbolo de tu condición y un testimonio de tu valor.\n\nPara obtenerlas, deberás recoger gemas de maná de los magos de Dalaran, en los Argénteos. Sus creencias son similares a las nuestras, por lo que matarlos no es nada placentero, pero bueno, si no son capaces de hacernos frente, la verdad que merecen morir.\n\nTrae las gemas y un poco de lino para Josef Gregorian en la tienda de sastrería del Barrio de la Magia.", progress = "Anastasia dijo que vendrías a verme, estoy listo para elaborar tu túnica. ¿Tienes las gemas y el lino?", completion = "Es un material excelente y aunque no tengo el talento necesario para valorar la calidad de estas gemas de maná, pronto sabremos si poseen el poder necesario para crear togas del fuegohechizo...", rewards = "Recibirás:\n\n"},
[1962] = {title = "Toga Fuegohechizo", objective = "Aquí tienes tu toga, <nombre>. Utilízala bien.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nTogas inferiores del fuegohechizo"},
[1963] = {title = "Mano Destrozada", objective = "Mata a Tazan y llévale su cartera a Therzok al Circo de las Sombras, en Orgrimmar.", description = "El Jefe de Guerra es demasiado cauteloso a la hora de usar el poder de la Horda. En el pasado fuimos conquistadores y debemos volver a serlo. Mano Destrozada se encargará de eso.\n\nAtacamos al enemigo expuesto desde la sombra, para reducir sus filas en secreto. Hay algo que debes hacer para probar tu valía.\n\nEl trol Tazan, que comercia con los piratas cerca de Trinquete, tiene información que me interesa. Mátalo y tráeme su cartera.", progress = "Solo un necio cree que hay más peligro en un ataque frontal con un hacha que en una daga en la oscuridad.", completion = "Me alegro de que Tazan haya muerto, pero una cartera cerrada no me sirve de nada.\n\nParece un buen desafío para quien aspira a ingresar en Mano Destrozada.", rewards = "Recibirás:\n\n"},
[1978] = {title = "Los Mortacechadores", objective = "Entrégale el libro de contabilidad de Andron a Varimathras, que está en la Cámara de la Dama Oscura en Entrañas.", description = "¿La Hermandad? Nunca lo habría sospechado. Parece que la traición de los señores de Alterac comenzó hace años.\n\nLord Varimathras se complacerá de escuchar esto, y como tú has sido una pieza clave en este descubrimiento, sería perfecto si se lo hicieras llegar tú mismo.\n\nY por cierto, no me he olvidado:\n\n<bienvenido/bienvenida> a los mortacechadores.", progress = "¿Sí?", completion = "Mmm, qué interesante... Los mortacechadores han hecho un buen trabajo al descubrir esto. \n\nPor lo que sé, a ti también te corresponde una mención honorífica. Espero que lo sigas haciendo igual de bien, &lt;nombre&gt;, pues serás de gran ayuda para los mortacechadores.", rewards = "Recibirás:\n\nHoja de Astucia"},
[1998] = {title = "Fenwick Thatros", objective = "Mata a Fenwick Thatros y llévale su cabeza a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas.", description = "Lord Varimathras me ha enviado una misiva sobre una amenaza para los no-muertos que debe ser eliminada de inmediato. Su nombre es Fenwick Thatros, el que Lord Varimathras cree que es el líder de los no-muertos que se han asentado en el Bosque de Argénteos.\n\nTu nombre me vino a la mente rápidamente, <nombre>. Confío en tus facultades y sé que podrás realizar esta misión a la perfección.\n\nSegún los mortacechadores del Bosque de Argénteos, puedes encontrar a Thatros cerca del embarcadero destartalado de Lordamere.", progress = "He depositado toda mi confianza en ti para que realices este servicio para Lord Varimathras.", completion = "Has hecho un buen trabajo, &lt;nombre&gt;. Dado que ya has realizado muchos servicios para los mortacechadores, y con ello has probado con creces tu valía, considero que ha llegado el momento de proseguir con tu instrucción en las artes de la sombra.", rewards = "Recibirás:\n\n"},
[1999] = {title = "Herramientas de comercio", objective = "Encuentra el informe de estado de Dalaran y devuélveselo a Mennet Carkad, que está en el Barrio de los Pícaros de Entrañas.", description = "Uno de nuestros prisioneros ha confesado que los zahorís de Kirin Tor en el Molino Ámbar esconden importantes documentos relativos al estado actual de Dalaran. Lord Varimathras ha encomendado a los mortacechadores la tarea de recuperarlos.\n\nAhora esta tarea te corresponde a ti, <nombre>. Sin embargo, necesitarás algún equipamiento y saber forzar cerraduras. Habla con Estelle Gendry, nuestra modista, y pídele un juego de herramientas antes de partir.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2018] = {title = "La prueba de Rokar", objective = "Mata al vigía Glibby y llévale su silbato a Rokar Filosombra a Durotar.", description = "¿Así que ahora Therzok me envía pupilos? Quizás pueda enseñarte un par de cosas. No es que me importe lo que estás haciendo, pero no quiero tener tu muerte sobre mi conciencia.\n\nPero antes, tienes que probar tu valía.\n\nMira, <nombre>, a mí Ventura y Cía. me da mala espina. Esta tierra me recuerda a Draenor y no me gusta que esos goblins lo pongan todo patas arriba. Si matas al vigía de El Fangal, la operación se interrumpirá.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2019] = {title = "Herramientas de comercio", objective = "Habla con Therzok en el Circo de las Sombras de Orgrimmar.\n\nObjeto provisto: \n", description = "Ten, <nombre>, esto te servirá.\n\nPero no te molestes en contarme tu historia, los planes de Therzok no me interesan. Solo quiero vivir en paz.\n\nSi alguna vez necesitas herramientas nuevas, ven a verme y te las daré. ¡Aun así, cuídalas bien!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2038] = {title = "Suministros de Bingles", objective = "Localiza las herramientas de Bingles y devuélveselas:\n\nllave inglesa, destornillador, martillo y cartucho de explosivos.", description = "Por si no fuera bastante haberme estrellado en Loch Modan, me atacaron unos trogg de una isla cercana y me robaron las herramientas y mis cartuchos de explosivos Pimpampum. ¡Sin girocóptero y sin explosivos no hay nada que hacer!\n\nTráeme mis herramientas y mis explosivos, <nombre>. ¡Por Gnomeregan!", progress = "¡<nombre>! ¡¿Dónde están mis cosas?!", completion = "Nos has prestado un gran servicio, &lt;nombre&gt;. ¡Esos troggs se van a enterar de lo que valen los gnomos de Gnomeregan!", rewards = "Recibirás:\n\nGuantes voladores de Bingles"},
[2039] = {title = "Encuentra a Bingles", objective = "Encuentra a Bingles Pimpampum en Loch Modan.", description = "¡Hemos perdido contacto con Bingles! Fue a una misión de reconocimiento a Gnomeregan hace una semana y no ha vuelto a dar señales de vida. Estoy seguro de que ha saltado por los aires.\n\nLa última vez que se le vio, volaba sobre Loch Modan. Quizás tú puedas enterarte de qué ha pasado, <nombre>.", progress = nil, completion = "¿Así que Gnoarn cree que no sé arreglármelas solo? Pues no veo por qué...\n\nDa igual, ¡te necesito! Bueno, ¡el movimiento te necesita!", rewards = "Recibirás:\n\n"},
[2040] = {title = "Asalto subterráneo", objective = "Recupera la Trituradora Piñonera de Las Minas de la Muerte y devuélveselo a Shoni el Shilenshioso, en Ventormenta.", description = "¡Esos malditos troggs han tomado Gnomeregan! La situación es grave, pero quizás puedas ayudar, <nombre>.\n\nEn las profundidades de Las Minas de la Muerte hay una trituradora goblin operativa. Encuentra la trituradora y trae su batería intacta. Con la batería de la trituradora, podemos dar a nuestras excavadoras gyroagujereamáticas la energía necesaria para atravesar los rocosos límites subterráneos de Gnomeregan, ¡y nos abrirán el camino para el asalto gnomo!", progress = "¿Has encontrado la dentrituradora goblin, <nombre>?", completion = "Bien hecho, &lt;nombre&gt;. Gracias a ti, Gnomeregan está a un paso más cerca de su día de liberación.", rewards = "Podrás elegir una de estas recompensas:\n\nGuanteletes polaresVarita de dientes de sable\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nGuanteletes polaresVarita de dientes de sable"},
[2041] = {title = "Habla con Shoni", objective = "Habla con Shoni la Silenciosa en Ventormenta.", description = "A lo mejor... A lo mejor puedes ayudarnos en la batalla por Gnomeregan.\n\nVe a Ventormenta a hablar con Shoni la Silenciosa, la jefa de trabajos subterráneos. Necesita un cable con las excavadoras gyroagujereamáticas.\n\nLo más seguro es que esté con los enanos en Ventormenta.\n\nSuerte, <nombre>.", progress = nil, completion = "¿Me traes un mensaje?", rewards = "Recibirás:\n\n"},
[2078] = {title = "La venganza de Gyromast", objective = "Encuentra a Trillomajareitor 4100, la Primera unidad de compañía de Gelkak y condúcela hasta él.", description = "Tienes que encontrar a mi segundo de abordo, <marinero/marinera> de agua dulce.\n\nCoge esta llave, pónsela en la cabeza y tráelo aquí, con su capitán.\n\nEsos murlocs no son sus dueños... ¡ya se darán cuenta cuando mi segundo se encargue de ellos!", progress = nil, completion = "¡Eeeeeh! Has demostrado ser casi un perro de mar, marinero de agua dulce.\n\nPuede que el capitán te ascienda a limpiador de la cubierta de popa si sigues así...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nElixir de respiración acuática"},
[2098] = {title = "La búsqueda de Gyromast", objective = "Encuentra los tres fragmentos de la llave de Gelkak Gyromast y llévaselas.", description = "La vida del pirata no es fácil, te lo digo yo. ¡No es todo abordajes y ron! También hay desgracia... Mi barco se hundió cerca de la costa por culpa de esos malditos trillanodontes.\n\n¡Necesito tener conmigo a mi segundo de a bordo! ¡Cuanto antes!\n\nCuando alcancé la costa se me cayó la llave de mi segundo de abordo en las rocas y me la robaron esas bestias de Costa Oscura. Por el rabillo del ojo pude ver reptadores furiosos, feroces zancudos de bosque y murlocs rebuscando entre los restos del naufragio.", progress = "¿Has encontrado mi llave, marinero de agua dulce?", completion = "¡Eh, marinero de agua dulce! ¡Lo conseguiste! ¡Dentro de nada mi segundo de abordo volverá a estar en pie y esas bestias no sabrán ni de dónde les llegó el golpe!\n\nEste... ¿has visto a mi segundo?", rewards = "Recibirás:\n\n"},
[2118] = {title = "Tierras infestadas", objective = "Captura un Oso Cardo rabioso vivo y llévaselo a Tharnariun. \n\nSi no consigues capturar un Oso Cardo rabioso y pierdes la trampa, pídele otra a Tharnariun Espinarbolado.", description = "Por Costa Oscura se extiende una enfermedad que se hace con la mente y el cuerpo de todo lo que toca.\n\nLos Osos Piel de Cardo han sido los más afectados por esta plaga. Eran nobles animales que se han convertido en rabiosos objetos de destrucción. Creo que yo podría tener una cura para este mal.\n\nLleva esta trampa al bosque y colócala en el suelo. Cualquier Oso Cardo rabioso que cruce la luz será manso por un breve período de tiempo. Y cuando esto ocurra, te seguirá. Guía uno hasta aquí, <nombre>.", progress = "¿Me has traído al animal enfermo, <nombre>?\n\nSi tienes problemas para accionar la trampa, no te apures, la Esperanza de Tharnariun es eterna. Si necesitas otra trampa, abandona tu misión y vuelve a mí.", completion = "Nuestro trabajo apenas ha empezado, &lt;nombre&gt;. ¿Estás preparado para tu siguiente tarea?", rewards = "Recibirás:\n\n"},
[2138] = {title = "La limpieza de la infección", objective = "Libra al bosque de 20 Osos Cardo rabiosos y ve a buscar a Tharnariun Espinarbolado a Costa Oscura.", description = "Tal y como temía... la cura no funciona.\n\nMe entristece ordenar la muerte de una criatura de la naturaleza, pero estos osos enfermos deben ser sacrificados.\n\nVuelve al bosque y mata a 20 Osos Cardo rabiosos, <nombre>. Eso no detendrá la plaga pero, al menos durante un tiempo, reducirá el daño que esos animales están haciendo al bosque y a sus habitantes.", progress = nil, completion = "Has hecho un gran servicio a Auberdine, &lt;nombre&gt;, pero aún no has acabado tu cometido.\n\nDescansa un poco y vuelve.\n\nY..., &lt;nombre&gt;, prepara tu mente y tu alma para más derramamientos de sangre.", rewards = "Recibirás:\n\n"},
[2139] = {title = "La esperanza de Tharnariun", objective = "Encuentra a la madre del cubil y mátala.", description = "El precio por la supervivencia de nuestra gente y nuestras tierras es muy alto, <nombre>.\n\nHas derramado la sangre de muchos siervos de la naturaleza. Has derribado a muchos animales enfermos. Los habitantes de Auberdine están en deuda contigo, pues, aunque sea solo por un tiempo, has mitigado el dolor del bosque...\n\nTengo una última tarea para ti. Pero te advierto, tendrás que volver a matar. Tengo la esperanza de que esta sea la última vez que recurro a ti, <nombre>.\n\nAl noreste hay una cueva, cerca de Bashal'Aran. En ella reside la Madre del Cubil. Mátala.", progress = nil, completion = "Has combatido por Auberdine con valentía y honor, &lt;nombre&gt;. En estas tierras desgarradas por la guerra, la muerte y la desesperación son habituales. Y son muchos los que no se atreven a aceptar el desafío de vivir en nuestro nuevo hogar y eligen esconderse en sus casas o huir a las tierras mejor defendidas de la Alianza.\n\nGracias, &lt;nombre&gt;. Uno de estos días combatiremos como camaradas contra otro enemigo.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes siempre verdeManteo de madera\n\nTambién recibirás:\n\nGuantes siempre verdeManteo de madera"},
[2158] = {title = "Descanso y relajación", objective = "Habla con el tabernero Farley de la Posada Orgullo de León.", description = "Todos los aventureros deben descansar cuando les invade el agotamiento, ¡y no hay mejor lugar para descansar que la Posada Orgullo de León!\n\nMi mejor amigo, el posadero Farley, lleva la Posada Orgullo de León. Si le dices que te envío yo, puede que te haga un descuento en la comida y la bebida.\n\nPara encontrar la Posada Orgullo de León, viaja al sur por el camino que sale de aquí, ¡no tiene pérdida!", progress = nil, completion = "¡Descanso y relax para quien no puede más! ¡Ese es nuestro lema! Por favor, toma asiento cerca del fuego y da un respiro a tus cansados huesos.\n\n¿Te gustaría probar una selección de nuestra excelente comida y bebida?", rewards = "Podrás elegir una de estas recompensas:\n\nCalabaza pequeñaAgua de manantial refrescante\n\nTambién recibirás:\n\nAgua de manantial refrescanteCalabaza pequeña"},
[2159] = {title = "Envío desde Dolanaar", objective = "Llévale el paquete de Dolanaar al posadero Keldamyr.", description = "Saludos, joven <clase>. ¿Me podrías prestar tu ayuda? Tengo un paquete de hierbas que debo entregar en la ciudad de Dolanaar pero todavía tengo asuntos pendientes con los druidas de Cañada Umbría y no puedo irme.\n\n¿Puedes entregar este paquete por mí? Su destinatario es el tabernero Keldamyr de la posada de Dolanaar, que se encuentra un poco más adelante en dirección sur.", progress = "¿Cómo puedo ayudarte, joven? ¿Vienes para alojarte en la posada? ¿Necesitas una piedra de hogar?", completion = "Ah sí, en paquete de hierbas de Cañada Umbría. Es una lástima que no lo haya podido traer el propio Porthannius pues él y yo tenemos mucho de qué hablar. No obstante, me alegro de hayas venido a traerme las hierbas.\n\nYa que estás aquí, aprovecha para descansar. Los héroes han de velar por su fuerza y ánimo, y buscar descanso y solaz cuando sea necesario. Si no se proporciona paz al cuerpo y a la mente, el fracaso es inevitable.\n\nAsí pues... descansa.", rewards = "Podrás elegir una de estas recompensas:\n\nQueso azul de DarnassusAgua de manantial refrescante\n\nTambién recibirás:\n\nAgua de manantial refrescanteQueso azul de Darnassus"},
[2160] = {title = "Suministros para Tannok", objective = "Lleva a Kharanos la caja de suministros para la posada y entrégasela a Tannok Martilloescarcha.", description = "¡Eh! Tienes pinta de ser de corazón aventurero. Si vas a arriesgarte a atravesar el paso, ¿crees que podrías llevar un paquete a la posada de Kharanos?\n\nPorque pensabas hacer un alto en la posada, ¿no? Sí, seguro, si consigues cruzar el paso, necesitarás descansar un poco.\n\nY como vas a pasar por allí, no te costará nada entregar esto a Tannok Martilloescarcha, el ayudante del posadero. Yo no puedo cruzar el paso ¡y pasarán días hasta que llegue una escolta de montaraces!", progress = "¡Eh, sé bienvenido! Venga, siéntate junto al fuego y entra en calor con una jarra de cerveza.", completion = "¡Ah, por fin! ¡Los suministros! Empezaba a preocuparme. Hemos tenido pocas noticias de Yunquemar desde que los troggs se hicieron con el paso. \n\nGracias por traerme esto, &lt;nombre&gt;. Te lo ruego, ponte cómodo. Debes de estar cansado del viaje.", rewards = "Podrás elegir una de estas recompensas:\n\nCecina duraAgua de manantial refrescante\n\nTambién recibirás:\n\nCecina duraAgua de manantial refrescante"},
[2161] = {title = "Un cargamento de peón", objective = "Lleva el cargamento de Ukor al tabernero Grosk a Cerrotajo.", description = "Perdón... ¿Puedes ayudarme?\n\nTengo un montón de comida aquí. ¡Y por el camino he tenido que afrontar escórpidos y arañas y cosas peores! Traje la comida al Valle de los Retos porque pensaba que la necesitarían y quería ayudar. ¡Pero no necesitan comida! Y ahora tengo que llevármela de vuelta a Cerrotajo y tengo miedo de las bestias que pueda encontrarme por el camino...\n\n¿Podrías llevarla tú por mí? Yo soy una criatura insignificante y tú, sin embargo, eres un ser aguerrido. ¡No temes a nada!\n\nLlévaselo al tabernero Grosk de la posada de Cerrotajo. ¡Gracias!", progress = "¡Bienvenido a Cerrotajo!", completion = "Ah, esta es la comida que Ukor llevó al Valle de los Retos. ¿No les hacía falta? Pues será que les gusta matar a los héroes de hambre mientras los entrenan. ¡Fortalece el espíritu, según dicen!\n\nGracias por traerme la comida. La pondré de nuevo en las estanterías... ¡Pero primero deja que te ofrezca un tentempié!\n\nY no olvides ir a descansar a la posada. Seguro que estás deseando comerte el mundo, pero si te falta energía, no podrás hacer gran cosa, ni por ti ni por la Horda.", rewards = "Podrás elegir una de estas recompensas:\n\nMendrugo de pan duroAgua de manantial refrescante\n\nTambién recibirás:\n\nAgua de manantial refrescanteMendrugo de pan duro"},
[2178] = {title = "Guiso de zancudo", objective = "Llévale 5 piezas de carne de zancudo a Alanndarin Arrullanoche a Auberdine.", description = "¿Hueles ese delicioso aroma? Es mi receta secreta, <nombre>: guiso de zancudo recién hecho. ¡Es una delicia!\n\nQuizás te interese aprender la receta...\n\nPues para que te la enseñe, tendrás que probarme que eres capaz de dominar a las bestias.\n\nNo espero que salgas, domestiques a un zancudo y vuelvas cabalgando en él, pero si consigues matar a unos pocos y traerme algo de su suculenta carne, me dejaría convencer para compartir mi receta.", progress = "¡Esta olla está vacía y mi estómago también! ¿Por qué estás tardando tanto?", completion = "¡Fantástico! ¡Dime lo que piensas, &lt;nombre&gt;!", rewards = "Recibirás:\n\nReceta: estofado de zancudoEstofado de zancudo"},
[2198] = {title = "El collar hecho añicos", objective = "Busca al creador del collar hecho añicos y descubre para qué sirve.", description = "Has encontrado un collar deslustroso, que todavía deja ver el minucioso y mágico trabajo de artesanía del que ha sido fruto. El collar está dañado y parece que faltan tres gemas. En la parte de atrás del collar hay una inscripción apenas visible con el sello de la ciudad de Forjaz. Debajo hay tres pequeñas iniciales grabadas. \"TdK\". \n\nSi este collar significa algo, tendrás que descubrir de qué se trata. El sello y las iniciales serán un buen punto de partida...", progress = "¡Te has equivocado de gnomo, amigo! ¡Yo nunca he estado allí! ¡No puedes probar nada!\n\nEspera un momento... ¿quién eres? ¿Pero qué...? ¿Dónde encontraste este collar?", completion = "Sí... Yo soy el joyero que ha hecho el collar pero no soy de los que dan información a cambio de nada. La información se paga, amigo. Si quieres que te ayuda, primero has de ayudarme tú a MÍ.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2199] = {title = "Esa información tiene un precio", objective = "Lleva 5 lingotes de plata a Talvash del Kissel a Forjaz.", description = "¡Soy un joyero de gran renombre en Forjaz! O al menos, lo era... ¡Me veo arruinado por culpa de las argucias de un competidor! Y la mala reputación nunca ayuda en los negocios. Y así me veo en la más completa miseria.\n\nNecesito cinco lingotes de plata para acabar un encargo, el primero que recibo en ni sé cuánto tiempo.\n\nTráeme los lingotes y yo te diré lo que sepa del collar.", progress = "¿Traes los 5 lingotes de playa? Si no, no te contaré la historia del collar. ¡Ayuda a este gnomo!", completion = "No eres mala gente, pese a lo que dicen por ahí... ¡Es broma! En serio, gracias.\n\nEl collar es una de mis mejores obras, pero le faltan las 3 gemas con las que se imbuye de poder mágico. Lo diseñé para un paladín hace mucho; libraba una cruzada para limpiar la \"corrupción\" de Uldaman, en las Tierras Inhóspitas.\n\nMurió allí y entonces empezaron mis problemas.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2200] = {title = "El regreso a Uldaman", objective = "Busca pistas sobre el paradero del collar de Talvash en Uldaman. Dijo que un paladín muerto fue su último dueño.\n\nObjeto provisto: \n", description = "Un competidor hizo correr el rumor de que mi collar tenía una maldición que cayó sobre el paladín. Evidentemente, nadie quiere comprar joyas malditas.\n\nHagamos un trato, <nombre>: tráeme las gemas y yo arreglaré el collar, ¡siempre que hagas publicidad de que mis joyas no están malditas!\n\nBusca pistas en Uldaman; puede que encuentres el cadáver del paladín, su último dueño conocido. Usa esto cuando encuentres las gemas; así podrás comunicarte conmigo aquí en Forjaz.", progress = nil, completion = "El diario revela que el paladín consiguió localizar las 3 gemas del collar:\n\nlos enanos Forjatiniebla tenían el rubí en zona segura durante su lucha contra los troggs.\n\nEstos, a su vez, guardaban el topacio en una urna situada cerca de enanos de la Alianza.\n\nY el ancestro trogg se quedó el zafiro para sí.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2201] = {title = "Encuentra las gemas", objective = "Encuentra el rubí, el zafiro y el topacio que están desperdigados por Uldaman. Cuando los tengas, contacta con Talvash del Kissel mediante la ampolla de adivinación que él te dio.\n\nA través del diario, sabes que...\n*El rubí está escondido en una zona de Forjatiniebla con barricadas.\n*El topacio permanece oculto en una urna en una de las zonas de los troggs, cerca de algunos enanos de la Alianza.\n\n*El zafiro obra en poder de Grimlok, el líder de los enanos, según él mismo afirma.", description = "El diario del paladín ha aportado quizás las únicas pistas que Uldaman revelará acerca del paradero de las gemas del collar. Si los garabatos del paladín son acertados, habrá que enfrentarse a los enanos Forjatiniebla y los troggs que se disputan el control de la excavación.\n\nSabiendo esto, la búsqueda de las gemas empieza de verdad. Cuando tengas todas las gemas, tendrás que notificárselo a Talvash de Forjaz utilizando la ampolla de adivinación, tal y como él te dijo.", progress = "La imagen de Talvash se dibuja en las aguas del cuenco de visión.\n\n\"¡Vaya, todavía sigues con vida! ¿Encontraste mis gemas? ¿Eres quien va a salvar mi reputación, esa a quien he estado esperando? Por favor, dime que no has desperdiciado una recarga de la ampolla solo para hablar de tonterías... Son muy caras y lo cierto es que no gozo precisamente de una buena situación económica.\"", completion = "Efectivamente, esas son las tres gemas. ¡Buen trabajo! Por desgracia, tu trabajo no acaba aquí.\n\nNo, no estoy intentando jugarte una mala pasada. El objetivo es hacer joyas mágicas de calidad, ¡de la calidad que solo puede proporcionar Talvash del Kissel! Necesitarás una potente fuente de energía para darle vidilla a ese collar.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2202] = {title = "Componentes de Uldaman", objective = "Llévale 12 setas moradas a Musgofusión en Kargath.", description = "He oído hablar de unas plantas recién descubiertas llamadas setas moradas. Crecen en macizos en lo más remoto de la excavación de Uldaman; puede que también se encuentren en otra parte pero ese es el único sitio donde sé que hay. Deseo estudiar su uso potencial en alquimia y ahí es donde entras tú.\n\nEncuentra los macizos de setas moradas y tráeme una docena de setas. Ándate con ojo, los macizos pueden arrojar veneno si se les sacude.\n\nHaz esto por mí ¡y te prepararé un lote de mis famosos elixires vigorizantes!", progress = "¿Has conseguido las setas moradas que necesito para mis proyectos de alquimia? Si no hay setas, ¡no hay premio!", completion = "Muy bien, &lt;nombre&gt;, ¡has vuelto a demostrar tu valía! Será interesante estudiar las setas, apuesto lo que sea.\n\nY ahora, lo prometido es deuda, disfruta de un trago de uno de mis brebajes de recuperación más eficaces de mi colección privada...\n\nSi eres un hábil alquimista, quizás podamos compartir algún negocio más. Sea como fuere, ¡agredecido quedo por tu ayuda!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nPoción reconstituyente"},
[2203] = {title = "Componentes de Tierras Inhóspitas II", objective = "Usa las vasijas de taumaturgia vacías con los dragones guardianes abrasados encontrados en las Tierras Inhóspitas. Cuando las hayas llenado, llévaselas a Jarkal Musgofusión en Kargath.", description = "Has sido de gran ayuda, <clase>. Te recompensaré con la mismísima receta del brebaje que te di antes... pero me gustaría hacerte un último encargo un tanto peligroso. Esta es mi petición.\n\nEsta caja fuerte contiene tres vasijas de taumaturgia vacías; están imbuidas de un aura de Armonización que absorberá la sangre de un dragón guardián abrasado. Si la usas con la fiera, se enfurecerá, así que ve con mucho cuidado. Cuando tengas las tres llenas tráemelas.\n\nHazlo y la receta será tuya.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nPoción restauradora\n\nRecibirás:\n\n"},
[2204] = {title = "Restaurar el collar", objective = "Obtén una fuente de energía del constructo más poderoso que encuentres en Uldaman, y entrégasela a Talvash del Kissel en Forjaz.", description = "Vas a tener que darle \"vidilla\" a ese collar y Uldaman probablemente es el mejor lugar para conseguir lo que necesitas. Supongo que hay muchos constructos por allí, ¿no? Bien, pues elige el más grande de todos los constructos que encuentres y extrae su fuente de energía. Estoy seguro de que podré insertar esa fuente en el interior del collar.\n\nSin la fuente de energía no es posible restaurar el collar. Regresa a Forjaz cuando la tengas y podremos arreglarlo para ti.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2205] = {title = "Busca el SI: 7", objective = "Entrega el paquete de Keryn Sylvius al maestro Mathias Shaw que está en el Cuartel de Ventormenta.", description = "¿La insignia? Oh, no es nada, pescadito. Al menos nada que tú puedas arreglar. Por la pinta que tienes, diría que Mathias, más que pensar que eres <un pícaro/una pícara>, te tomaría por <el chico/la chica> de los recados del quesero. Además, el trabajo me fue asignado a mí y fui yo quien se comprometió a hacerlo.\n\nHablando de lo cual... lleva este paquete a Mathias. Quizás te dé un par de pequeñas tareas.\n\nEn marcha.", progress = "¿Puedo ayudarte, <clase>?", completion = "Me has encontrado. Y, por tu cara, diría que la carrera y la búsqueda han sido un tremendo esfuerzo para tu mente y tu cuerpo.\n\n¿Quieres que Cuchillo te recomiende una posada, o quieres ponerte a trabajar ya mismo?", rewards = "Recibirás:\n\n"},
[2206] = {title = "El robo", objective = "Encuentra al maestro de embarcadero Defias y recupera el horario de envíos para el maestro Mathias Shaw.", description = "Nuestros operativos en el campo informan sobre una gran actividad Defias al sur de Villadorada. Según los informes, los Defias han tomado el Embarcadero de Jerod y lo utilizan para llevar clandestinamente grandes cantidades de contrabando desde Crestagrana hasta Páramos de Poniente.\n\nNecesito que te infiltres en la casa del embarcadero y me traigas el horario de envíos, <nombre>. Probablemente esté en el bolsillo del maestro de embarcadero.\n\n... Y <nombre>, si te descubrieran, el SI:7 negaría todo conocimiento de tu existencia.", progress = "Tu mortalidad disminuye con cada minuto que pierdes vagueando en el cuartel, <clase>.", completion = "Me sorprende... me sorprende que Keryn, finalmente, haya encontrado un recluta que pueda llegar a ser un &lt;clase&gt; de verdad. Coge esta espada, &lt;nombre&gt;. Llegará el día cuando te volverán a llamar para servir al IS:7. Perfecciona tus habilidades, mantén los ojos abiertos y la espada afilada.\n\nNo nos verás llegar, pero lo podrás sentir...", rewards = "Recibirás:\n\nHoja de Astucia"},
[2218] = {title = "El camino hacia la salvación", objective = "Ve a Forjaz y habla con Hulfdan Barbanegra.", description = "Otra alma perdida buscando guía, ¿eh? Supongo que quieres encontrar un propósito en esta vida... tu propósito. Lo he visto mil veces, todos los héroes acaban buscando trabajo.\n\nPues bien, hoy es tu día de suerte. Hogral tiene la cura para tu mal.\n\nTe voy a dar algo que hacer: Sigue este sendero hasta Forjaz y ábrete camino hasta La Caverna Abandonada. Una vez en ella, localiza a la hermandad de ladrones y habla con Hulfdan Barbanegra. Él te indicará el camino hacia la salvación.", progress = nil, completion = "¿Así que te consideras un bribón? ¿Destinado a convertirte en uno de los elegidos de Barbanegra... un oficial del Círculo Oculto? \n\nSolo un pillo redomado se lo tendría así de creído. \n\n¡Y eso es exactamente lo que buscamos! ¡Bienvenido a bordo, &lt;nombre&gt;!|n", rewards = "Recibirás:\n\n"},
[2238] = {title = "Un simple subterfugio", objective = "Encuentra a Onin MacHammar cerca de Gnomeregan.", description = "El Rey quiere sacar todo el mineral y las gemas de estas minas. Más tesoros de los que un enano vería en diez vidas.\n\nEl círculo oculto tiene valores, <nombre>. Valoramos lo valioso, ¿entiendes?\n\nY pensamos que el Rey debería compartir su buena fortuna, pero sin que se entere, ¿me pillas?\n\nTienes que ir a hablar con Onin MacHammar en Gnomeregan y volver con lo que te diga. El muy ladino se camufla entre los leprosos. Ándate con ojo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2239] = {title = "El informe de Onin", objective = "Lleva el informe de Onin a Hulfdan a Forjaz.", description = "¿La cantera? Sí, veo esos monstruosos tanques de vapor blindados pasar por aquí casi cada día.\n\nMis contactos en el este me dicen que el tanque sale de Forjaz, hace una recogida en la Cantera de Gol'Bolar, para en Kharanos a repostar y luego, por no sé qué maldito motivo, da un rodeo para esquivar Gnomeregan antes de volver a Forjaz para descargar.\n\nEstá todo en el informe, <raza>. Llévaselo a Hulfdan para que pueda planear el siguiente paso.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nHoja de Astucia"},
[2240] = {title = "La cámara oculta", objective = "Lee el diario de Baelog, inspecciona la cámara oculta y ve a informar al prospector Pico Tormenta.", description = "Pasemos a la siguiente tarea.\n\nExiste una cámara secreta, la cámara de Khaz'mul, que hay que examinar. ¡El problema es que esta cámara se encuentra tras una enorme puerta cerrada con llave! Es una suerte que sepa cómo abrirla.\n\nLo explico todo en mi diario, que está sobre la mesa. Mi equipo y yo no podemos salir de aquí... así que tú, <nombre>, debes encontrar la cámara de Khaz'mul por nosotros.\n\nLee mi diario, examina la cámara y ve a informar al prospector Pico Tormenta de la Liga de Expedicionarios en Forjaz.", progress = nil, completion = "¿Has visto la cámara de Khaz'mul? ¡No puedo creerlo! Baelog estaba seguro de su existencia pero yo he de reconocer que tenía mis dudas.\n\nGracias, &lt;nombre&gt;. Con un poco de suerte, tu hallazgo incitará a nuestros líderes a seguir luchando por recuperar Uldaman. Y, si algún día vuelve a estar bajo el control de Forjaz, ¡los secretos de la cámara de Khaz'mul serán nuestros!", rewards = "Podrás elegir una de estas recompensas:\n\nCarga de enanoNorte y guía de la Liga de Expedicionarios\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCarga de enanoNorte y guía de la Liga de Expedicionarios"},
[2241] = {title = "Cuando cae la manzana", objective = "Llévale la rosa de Jannok a Syurna, que está en Darnassus.", description = "Dicen que soy un \"amante furibundo\", aunque no sé muy bien a qué se refieren.\n\nEs cierto que pienso en todo momento en Syurna, que mi casa está repleta de retratos de ella, y que paso días enteros sin comer, beber o dormir lamentándome por el amor que podríamos haber tenido. ¿Acaso es algo malo?\n\n¡Y ella no quiere verme! ¡A MÍ! He intentado enviarle mensajes pero ya no habla con nadie que no sea un pícaro.\n\n¿Podrías llevarle tú esta flor? No olvides decirle que es de parte de Jannok.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2242] = {title = "La llamada del destino", objective = "Encuentra a Sethir el Ancestro y ve a contarle todo lo que descubras a Syurna.", description = "Creemos que hay un ser que podría ayudarnos a entender qué está ocurriendo en los bosques de Teldrassil. Por desgracia, los intentos de convencer a la criatura para que coopere han fracasado.\n\nSe esconde en una rama a las afueras de El Claro del Oráculo. Todos los que se han acercado al sátiro, ya sea por las buenas o por las malas, han encontrado la muerte. Quizás el sigilo sea la respuesta, <nombre>.\n\nConsigue lo que porta Sethir el Ancestro en sus talegas y vuelve a verme.", progress = "¿Te ha dicho algo Sethir el Ancestro, <nombre>?", completion = "Son malas noticias, &lt;nombre&gt;. Parece que la enfermedad que se ha apoderado de los bosques y los ríos de Costa Oscura ha empezado a extenderse por nuestro hogar.\n\nNos has servido con diligencia pero ya no hay nada más que puedas hacer aquí. Coge esta espada como recompensa por tu ayuda a Teldrassil.", rewards = "Recibirás:\n\nHoja de Astucia"},
[2258] = {title = "Componentes de Tierras Inhóspitas", objective = "Llévale 5 mollejas de águila ratonera, 10 colmillos de coyote del risco y 5 fragmentos de elemental de piedra a Jarkal Musgofusión en Kargath, en las Tierras Inhóspitas.", description = "<nombre>, no me vendría mal tu ayuda si te apetece echar una mano. Soy un alquimista de cierto renombre y necesitaría tu ayuda para conseguir unos componentes que se hallan en las Tierras Inhóspitas.\n\nMe hacen falta los siguientes objetos para reponer mis existencias: 5 mollejas de águila ratonera, 10 colmillos de coyote montés y 5 fragmentos de elemental de piedra. Como es lógico, puedes conseguirlos de los propios animalitos; si no eres del tipo luchador, llévate a unos amigos que lo sean.\n\nConsígueme esos objetos y acabarás con unas monedas más en el bolsillo. ¿Qué me dices? ¿Te hace?", progress = "¿Tienes las cosas que necesitaba? No te pagaré a menos que hayas traído esos componentes.", completion = "¡Ah, muy bien! Me deberían durar una temporada, al menos. Aquí tienes el pago que te prometí.\n\nSi te interesa, no me vendrían mal otros componentes. Aunque estos solo se encuentran en lo más profundo de la excavación de Uldaman. No serán fáciles de conseguir pero haré que merezca la pena si te apetece volver a ayudarme.\n\nQuizás algo más que unas relucientes monedas, ¿qué te parece?", rewards = "Recibirás:\n\n"},
[2259] = {title = "Erion Sombrusurro", objective = "Localiza a Erion Sombrusurro en Darnassus.", description = "No te preocupes, <nombre>. No te voy a enviar a ver a Syurna. Erion requiere tu presencia en el Enclave Cenarion. ¡Date prisa!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2260] = {title = "La orden de Erion", objective = "Viaja a la Ciudad de Ventormenta y habla con Renzik Cuchillo.", description = "\"Las noticias de tus hazañas han llegado lejos, <nombre>. Tanto, que el Servicio de Inteligencia de Ventormenta ha preguntado por ti.\n\nEl viaje es agotador y no está exento de peligros, pero la oportunidad de ser entrenado por el IS:7 no debería tomarse a la ligera.\n\nUn nuevo mundo te espera, <nombre>. Dirígete al Casco Antiguo de Ventormenta y habla con Renzik Cuchillo en el puesto de mando del IS:7.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2278] = {title = "Los discos de platino", objective = "Habla con el guardapiedras y descubre qué conocimiento antiguo alberga. Cuando hayas adquirido el conocimiento que te ofrece, activa los Discos de Norgannon.", description = "En el centro de esta cámara del tesoro se encuentran cuatro discos de platino gigantes, cada uno de ellos adornado con sigilos de origen desconocido. Al tocarlos sientes un cosquilleo en la mano. Tras haber tocado los discos, se muestra ante ti un enorme guardapiedras.\n\n\"Saludos. Soy un guardián del conocimiento. La síntesis de los Creadores de los terráneos se describe y almacena para este mundo en los Discos de Norgannon.\"\n\n¡Acabas de encontrar una fuente de conocimiento nunca antes descubierta por nadie!", progress = "La voz del vigía de piedra parece surgir de tu propio interior:\n\n\"En primer lugar tendrás que asimilar la información contenida en los Discos de Norgannon. Una vez cumplido este requisito, podrás optar a una copia de los datos aquí guardados. Asegúrate de revisar toda la información que encontrarás aquí antes de proseguir.\"", completion = "Siguiendo las indicaciones del guardapiedras, accedes de nuevo a los discos de Norgannon ahora que ya conoces la historia de los terráneos, los troggs y los enanos. Se oye un ligero que proviene del interior de los discos. Entonces, el guardapiedras habla de nuevo:\n\n\"Los discos han sido sintetizados. Accede de nuevo a los discos de Norgannon para recibir una copia personal.\"", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2279] = {title = "Los discos de platino", objective = "Lleva la reproducción en miniatura de los Discos de Norgannon a la Liga de Expedicionarios de Forjaz.", description = "Al tocar los Discos de Norgannon de nuevo, sientes un cosquilleo en la mano. En esta ocasión, sin embargo, se oye un extraño ruido proveniente de su interior.\n\nSientes cómo tu mochila de repente es mucho más pesada que antes. ¡En su interior descubres una reproducción en miniatura de los discos!\n\nSin duda, un objeto de este tipo tendría un valor incalculable para alguien interesado en la historia antigua de Azeroth. Piensas en los miembros de la Liga de Expedicionarios de Forjaz, muy conocidos en la Alianza, y supones que ellos les sacarían provecho.", progress = "¿Puedo ayudarte en algo? Te aseguro que tengo mucho que hacer. Sin embargo, si se trata de algo importante para la Liga de Expedicionarios... ¡entonces soy todo oídos!", completion = "¿Qué tienes ahí? ¡Vaya! ¿Dónde encontraste esto?\n\n¿De verdad?\n\n¿Qué hablaste de qué con quién?\n\n¡NO PUEDE SER VERDAD!\n\nMi querido &lt;clase&gt;, ¡este puede ser el descubrimiento arqueológico del año! ¿¡Qué digo del año!? ¡Del siglo! Si estos discos son lo que creo que son, ¡tenemos delante la última pieza del rompecabezas! Esto podría demostrar de una vez por todas la legitimidad de la creación encantada de los enanos.", rewards = "Recibirás:\n\n"},
[2280] = {title = "Los discos de platino", objective = "Lleva la reproducción en miniatura de los Discos de Norgannon a uno de los sabios de Cima del Trueno.", description = "Al tocar los Discos de Norgannon de nuevo, sientes un cosquilleo en la mano. En esta ocasión, sin embargo, se oye un extraño ruido proveniente de su interior.\n\nSientes cómo tu mochila de repente es mucho más pesada que antes. ¡En su interior descubres una reproducción en miniatura de los discos!\n\nSin duda, un objeto de este tipo tendría un valor incalculable para alguien interesado en la historia antigua de Azeroth. Los sabios de Cima del Trueno son historiadores, muy conocidos en la Horda, y supones que ellos les sacarían provecho.", progress = "Sí joven, veo que entras en mi tienda de campaña con paso firme... y con marcada determinación. ¿Vienes a pedir mi consejo acerca de algún tema de gran importancia para la Horda? ¿O quizás deseas que te revele un misterio del universo?", completion = "¿Qué es esto?\n\n¡Impresionante, joven! Estos discos encierran grandes secretos de la tierra que han permanecido ocultos durante años. Ha sido muy inteligente por tu parte habérmelos traído.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2281] = {title = "Cita en Crestagrana", objective = "Adéntrate en Villa del Lago, en las Montañas Crestagrana, y habla con Lucius.", description = "Enviarte ahora a la batalla sería suicida. Renzik no necesita otro pícaro muerto en sus manos. Pero no te apresures, <nombre>, ya te meterá prisa Renzik.\n\nDirígete hacia las Montañas Crestagrana y habla con Lucius, es un miembro del IS:7. Lo encontrarás en el embarcadero de Villa del Lago, con los demás vagos y maleantes.", progress = nil, completion = "Ah, así que tienes los dedos un poco torpes, ¿eh? No te preocupes, &lt;nombre&gt;, he convertido a ladrones más torpes que tú en pícaros muy profesionales.", rewards = "Recibirás:\n\n"},
[2282] = {title = "Molino de Alther", objective = "Abre la caja fuerte de Lucius y llévale la muestra de robo a Villa del Lago.", description = "¡Llevamos años entrenando ladrones aquí! No todos los bribones nacen con los dedos ligeros del amigo Lucius. \n\nSigue el camino y encontrarás el Molino de Alther. Dentro hay un cofre con algo importante. Tráeme lo que contiene y te ganarás tu Certificado de Ratería.", progress = "¿Te había hablado de los orcos?\n\nHe oído que andan por el molino.", completion = "¿Ya la has abierto? Buen trabajo, &lt;nombre&gt;.\n\nSi te mueres, sabremos que fue por estupidez y no por falta de experiencia.", rewards = "Recibirás:\n\nCertificado de Ratería"},
[2283] = {title = "La recuperación del collar", objective = "Busca el collar en la excavación de Uldaman y llévaselo a Dran Droffers a Orgrimmar. Puede que el collar esté estropeado.", description = "Tenemos un trabajillo para ti. ¿Te atreves?\n\nAl parecer, hace poco que un idiota de paladín murió en la excavación Uldaman, en medio de las Tierras Inhóspitas. Lo que nos interesa de esto es que llevaba un collar muy valioso con tres gemas: un rubí, un zafiro y un topacio.\n\nEn resumen: queremos el collar y cubriremos de plata a quien lo traiga. Aunque esté deteriorado, no importa; ya lo arreglaremos.", progress = "¿Tienes el collar? ¡Si no hay collar, no hay recompensa!", completion = "Lo huelo desde aquí... ¡Déjame ver!\n\nPero... ¿Me quieres tomar el pelo o qué? ¡Faltan las gemas! ¿Pensabas que no nos íbamos a dar cuenta? Hasta Malton se dio cuenta.\n\nBueno, a lo mejor no las has robado, pero el collar no vale nada sin las gemas. ¡Encuéntralas y termina el trabajo!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2284] = {title = "La recuperación del collar, 2ª parte", objective = "Busca pistas sobre el paradero de las gemas en las profundidades de Uldaman.", description = "Me juego el cuello a que las gemas están en Uldaman.\n\nComo dije antes, si lo que hemos oído es correcto, el collar lleva un rubí, un zafiro y un topacio. Encuentra las gemas y colócalas en el collar.\n\nEn cuanto a dónde están esas gemas... no lo sabemos. Con un poco de suerte, estarán en el cadáver del paladín, si es que no lo ha saqueado alguien ya.", progress = nil, completion = "Has encontrado los restos del paladín del que habló Dran. Encuentras un diario, muy estropeado y cubierto de sangre. Lo que consigues leer está escrito en humano, te parece.\n\nTendrás que encontrar a alguien que lo traduzca.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2298] = {title = "Una regia sacudida", objective = "Viaja a Ventormenta y busca a Renzik Cuchillo.", description = "Y ahí estábamos, a punto de empezar a planear la fase III, cuando llegaron un par de esos brutos de Magni hablando de un atraco o algo así y arrestaron a gente del círculo oculto.\n\n¡Y al viejo Barbanegra no le arresta nadie! Los ánimos se caldearon y no sé cómo, salió a relucir tu nombre.\n\n¿Mi consejo? Coge el primer grifo que salga de Forjaz y ve a Ventormenta. Ve al Casco Antiguo y habla con un viejo amigo mío que se llama Renzik Cuchillo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2299] = {title = "¡Ve a hablar con Hulfdan!", objective = "Viaja a Forjaz y habla con Hulfdan Barbanegra.", description = "¿Dónde has estado, <nombre>? Hulfdan te ha estado buscando por todas partes. Pensábamos que el Rey te había hecho arrestar.\n\n¡Ve a Forjaz en cuanto puedas y habla con Hulfdan!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2300] = {title = "SI:7", objective = "Viaja a Ventormenta y busca a Renzik Cuchillo.", description = "Renzik te estaba buscando, pescadito.\n\nYo no haría esperar al segundo de Mathias si fuera tú.", progress = nil, completion = "¿Estás lista para trabajar o necesitas echarte una siestecita antes?", rewards = "Recibirás:\n\n"},
[2318] = {title = "La traducción del diario", objective = "Encuentra a alguien que pueda traducir el diario del paladín. El lugar más cercano en el que podrás encontrar a alguien es Kargath, en las Tierras Inhóspitas.\n\nObjeto provisto: \n", description = "Llegados a este punto, sabes que necesitarás la traducción del libro en una lengua que puedas comprender. Son muchos los que podrían hacerlo y el lugar más cercano en el que podrás encontrar a alguien que te ayude es, sin embargo, la Avanzada de Kargath en las Tierras Inhóspitas. Quizás sea un buen lugar para comenzar.", progress = nil, completion = "Poderoso &lt;clase&gt;, sé a qué has venido a las Tierras Inhóspitas. Me necesitas por mi vasto conocimiento de otras lenguas y estás buscando las piezas del collar que encontraste en Uldaman.\n\n¿Sorprendido? No lo estés tanto, mi trabajo consiste en averiguar estas cosas. Así pues, centrémonos en el diario y dime qué recibiré a cambio de su traducción. Créeme, no encontrarás a nadie más que pueda hacerlo.\n\nEs más, te haré una oferta mejor que la de Dran Droffers de modo que todavía puedas cumplir su trato. ¿Qué dices?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2338] = {title = "La traducción del diario", objective = "Préstale el collar a Jarkal. A cambio, te traducirá el diario.", description = "Droffers y Son Salvag no son lo suficientemente listos para restaurar la magia del collar, pero yo sí. Solo quiero estudiar el collar para aprender a hacerlos yo mismo. Para ello, necesitaré tu collar. Préstamelo y, a cambio por el tiempo que me dejes estudiarlo, te traduciré el diario. Mientras buscas las gemas, debería tener tiempo suficiente para aprender lo que necesito.\n\nEs un trato sencillo y nadie se enterará. ¿Qué? ¿Te hace?", progress = "Has pensado en ello, ¿verdad? Eres inteligente, <clase>, si aceptas este trato, ambos conseguiremos lo que queremos, y Rescates Droffers e Hijos pensará que tienen lo que buscan.", completion = "Se reconoce un buen negocio cuando lo ves, sabía que podía contar contigo.\n\nSolo será un momento mientras lanzo un hechizo para traducir rápidamente la lengua humana en una forma que entenderás. Soy un trol de palabra, pregúntale a cualquiera de la Horda por Jarkal Musgofusión. Bien, empecemos...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2339] = {title = "Encuentra las gemas y la fuente de poder", objective = "Recupera las 3 gemas y una fuente de energía para el collar de Uldaman y llévalo todo a Jarkal Musgofusión en Kargath. Jarkal cree que es posible que la fuente de energía más poderosa se halle en un constructo de Uldaman.\n\nPor el diario sabes…\n* Que han ocultado el rubí en una zona cerrada con barricadas en Forjatiniebla.\n* Que el topacio se ha ocultado en una urna en un área trogg, cerca de los enanos de la Alianza.\n* Que Grimlok, el jefe trogg, se ha quedado con el zafiro.", description = "Aquí tienes el diario traducido para que puedas leerlo. El quid de la cuestión dice que los enanos escondieron el rubí, los troggs ocultaron el topacio y un trogg llamado Grimlok tiene el zafiro.\n\nOye, vas a necesitar una fuente de energía para que el collar vuelva a funcionar. Dran Droffers no lo sabe. Para encontrarla, esto es lo que yo haría: me adentraría lo más posible en Uldaman y acabaría con el constructor más fuerte que allí hubiera. Sea lo que sea, seguro que la tendrá.\n\nCuando lo tengas todo, ¡vuelve aquí!", progress = "Te espera otro viaje a las profundidades de Uldaman si no tienes las gemas y una fuente de energía capaz de canalizar el poder mágico de nuevo hacia el collar.\n\nRecuperarás las gemas para entregárselas a los matones de Orgrimmar; me quedaré con la fuente de energía, puesto que será lo que haga funcionar el collar para empezar...", completion = "Un trabajo de primera, &lt;clase&gt;. Ahora veremos los bonitos frutos de nuestro desvío. He acabado con el collar y confío en poder reproducir el proceso de construcción sin problemas.\n\nPero primero usaré un conjuro especialmente diseñado para analizar rápidamente esas gemas antes de que te las lleves con el collar de vuelta a Orgrimmar. Solo será un momento.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2340] = {title = "Entregar las gemas", objective = "Entrégale el collar y la gema salvaje a Dran Droffers en Orgrimmar.", description = "Cualquier engaste de una gema en el collar funcionará, siempre que sean del mismo material. Esta fuente de energía, sin embargo, es lo que le devolverá la magia al collar... Espero.\n\nCon mucho gusto aportaré las gemas necesarias para tu versión del collar, siempre que la fuente de energía funcione realmente.\n\nDirígete a Orgrimmar y cumple tu parte del trato con Droffers y Son Salvag. Ni una palabra de nuestro trato y cuando vuelvas podría tener una maravillosa sorpresa para ti.", progress = "¡Vaya, mira quién tenemos ahí! Te ha llevado un tiempo llegar hasta aquí, ¿eh? ¡Esperemos que no me hayas hecho perder el tiempo! No te pagaré a menos que hayas traído todo lo que te encargué. Y créeme, ¡lo comprobaremos todo dos y hasta tres veces!", completion = "Sí, aquí está todo. Oye, ¿te importa si nos quedamos también con la bolsa? ¿No? ¡Gracias, de verdad! ¡Ja!\n\n¡Este collar será nuestra mayor botín! Pero no te pongas así, seguro que el collar no te sentaría bien. ¡Toma que toma!\n\nUn trato es un trato, aquí tienes tu parte por el rescate. Ya hemos rematado el negocio por ahora; vuelve más tarde por si tuviéramos más trabajo para ti. Hasta entonces, ¡andando!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2341] = {title = "La recuperación del collar, 3ª parte", objective = "Habla con Jarkal Musgofusión en Kargath.", description = "Mientras Dran y Malton Droffers se pelean por el botín, tú ya tienes la mitad del asunto resuelto. Ahora tienes que volver a las Tierras Inhóspitas para rematar las cosas; puede que Jarkal Musgofusión tenga una recompensa para ti en Kargath.", progress = nil, completion = "Lo que me trajiste superó todas mis expectativas; tengo un collar para mí, uno para ti y me ha sobrado para hacer 3 más. Voy a hacer un buen negocio, al margen de Rescates Droffers e Hijos.\n\nTen; te doy las gracias por tu ayuda y algo muy especial como muestra de gratitud.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCollar de mejora de Jarkal"},
[2342] = {title = "Los tesoros reclamados", objective = "Coge el tesoro de la familia de Patrick Garrett del cofre de su familia que se encuentra en la Sala Comunal Sur de Uldaman y llévasela a él a Entrañas.", description = "¡Espantar murciélagos con una escoba no es apropiado para un Garrett! He ahorrado algo con este sórdido trabajo, y quiero entregártelo a ti para que me ayudes.\n\nEl arma elegida por la familia Garrett está guardada en nuestro cofre de la Sala Comunal Sur de Uldaman desde que nosotros, los Garrett, trabajábamos allí como excavadores. Sin ella, estaré atrapado aquí para siempre, literalmente, limpiando este lugar de estos murciélagos infernales.\n\n¡Reclama el tesoro de mi familia y tráemelo! ¡Ayúdame a recuperar mis derechos!", progress = "Nunca acabaré con estos murciélagos que se mofan de mí y de mi escoba. ¡Dime que has reclamado el tesoro de mi familia! Te he ofrecido mis piezas de plata... ¿es que no es suficiente?", completion = "¡Esto ya es otra cosa! No hay duda de que te mereces cada pieza de plata que te he dado.\n\n¿Me estáis escuchando, repugnantes murciélagos? ¡Ahora tengo el arma elegida por la familia Garrett! ¡¡Temblad, murciélagos, temblad!!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2359] = {title = "La torre de Klaven", objective = "Roba la Llave de la Torre Defias, entra en la Torre Defias y descubre qué contiene el Cofre del Bosque del Ocaso. Lleva toda la información que encuentres a Mathias Shaw de Ventormenta. Lee el diario de Kearnen para saber cómo acceder a la torre.", description = "Hace semanas que vigilo esta torre, <nombre>. Hace poco pasó un gran cargamento procedente del Bosque del Ocaso, y con él vinieron esos guardias.\n\n¿Qué son? Tu suposición es tan acertada como la mía: no son humanos.\n\nEn cualquier caso, en el cargamento había un cofre, supongo que es lo que guardaban esas abominaciones. El zombi con los pinchos tiene una llave que necesitaremos.\n\nConsigue esa llave, ve a la torre y averigua qué hay en ese cofre. Lleva a Mathias lo que descubras.", progress = "¿Alguna noticia del agente Kearnen?", completion = "Esto explicaría la presencia de subordinados Defias que visteis Kearnen y tu a las puertas de la torre, &lt;nombre&gt;. También explicaría la relación de Venture y Cía. con los Defias y los extraños envíos que parten del Bosque del Ocaso.\n\nDe acuerdo a este libro, los trols de Zanzil de Tuercespina han descubierto un modo de levantar a los puertos con pociones... o para ser más concretos: con veneno.\n\nNo servirás de nada al IS:7 sin un amplio conocimiento de venenos, &lt;nombre&gt;... especialmente si no te sabes al dedillo la receta para crear no-muerte.", rewards = "Te van a lanzar el siguiente hechizo:\n\nVenenos\n\nRecibirás:\n\nReceta: té de cardo"},
[2360] = {title = "Mathias y los Defias", objective = "Ve a Páramos de Poniente y encuentra a la agente Kearnen para dar parte.", description = "Justo a tiempo, <nombre>. La división de reconocimiento del IS:7 ha informado sobre un aumento de actividad Defias en los Páramos de Poniente.\n\nParece ser que los Defias han tomado una atalaya abandonada de la Alianza al sureste de la Colina del Centinela y la están utilizando como base de operaciones para robar mercancías de la ruta de suministros de Crestagrana.\n\nVe a Páramos de Poniente y contacta con la agente Kearnen para dar parte. La encontrarás escondida, cerca de la torre.\n\n... Y <nombre>, si te descubrieran, el SI:7 negaría todo conocimiento de tu existencia.", progress = nil, completion = "¿Así que Mathias ha enviado a otro agente a por mí, eh? Bueno, espero que esté listo para oír malas noticias.", rewards = "Recibirás:\n\n"},
[2361] = {title = "Restaurar el collar", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCollar de mejoría de Talvash"},
[2378] = {title = "Encuentra a los Mano Destrozada", objective = "Viaja a Orgrimmar y habla con Shenthul, que estará en el Circo de las Sombras.", description = "Has servido con diligencia a los mortacechadores, <nombre>, pero quizás sea hora de que vayas más allá. Aunque tengas cualidades para el sigilo y el engaño, debes continuar con tu instrucción.\n\nLos mortacechadores cuentan con aliados entre los Mano Destrozada. Busca a los que nos puedan echar una mano; encuentra a Shenthul en Orgrimmar.", progress = nil, completion = "¿Así que tú eres a quien han elegido nuestros poderosos aliados para venir a ver a Shenthul? Qué aburrido...", rewards = "Recibirás:\n\n"},
[2379] = {title = "Zando'zan", objective = "Habla con Zando'zan en el Circo de las Sombras.", description = "No conseguirás superar los obstáculos que nos esperan solo con fuerza de voluntad.\n\nHabla con Zando'zan para empezar tu formación como miembro de Mano Destrozada.\n\nSolo cuando domines el sigilo y el robo podrás seguir tu camino hacia tu destino.", progress = nil, completion = "Puede que Zando'zan se equivocara; te pide disculpas.", rewards = nil},
[2380] = {title = "Ve a Orgrimmar", objective = "Ve a Orgrimmar, al Circo de las Sombras, y habla con Shenthul.", description = "No eres <el rey/la reina> del sigilo pero eso puede cambiar. Y cambiará. Evidentemente, has impresionado a los de arriba con tus pobres habilidades, porque el gran Shenthul desea verte.\n\nSigue la carretera que hay al norte de aquí. Te llevará a Orgrimmar. Habla con Shenthul, estará en el Circo de las Sombras.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2381] = {title = "Saquear a los saqueadores", objective = "Llévale el tesoro de los Mares del Sur a Desdio el Desdichado en Trinquete. No olvides conseguir un G.E.A.Q. y herramientas de ladrones del aparato Gizmotrónico de Wrenix. Necesitarás todo eso para completar tu misión.\n\nSi te atacaran unos loros anormalmente agresivos, ¡usa el G.E.A.Q.!", description = "No quiero asustarte, <nombre>, pero los cinco últimos ladrones a los que había enviado a robar el tesoro de los Mares del Sur acabaron en pedacitos.\n\nHacia el sur, bordeando la costa, encontrarás el Surcamareas, el barco de los filibusteros de los Mares del Sur. A buen recaudo en la bodega se encuentra el premio: el tesoro de los Mares del Sur. En el segundo nivel de ese barco está la guarida de los piratas, un lugar donde practican todos sus ladrones.\n\nSube a bordo de ese barco, roba el tesoro y tráemelo. Usa la guarida de los piratas si necesitas practicar cómo robar ¡pero ten cuidado!", progress = "Bueno, por lo menos conservas todos tus miembros. ¿Has encontrado el tesoro?", completion = "¡Increíble! ¡Improbable! ¡Sencillamente asombroso! Tienes mucho talento, &lt;nombre&gt;. ¡O eso o eres el ser vivo más afortunado!\n\nAquí tienes tu parte del pago. Estoy seguro de que le sacarás más partido que yo.", rewards = "Recibirás:\n\nTé de cardo"},
[2382] = {title = "Desdio de Trinquete", objective = "Ve a Trinquete y habla con Desdio el Desdichado.", description = "Zando'zan ha visto que se acerca, sí, la muerte se acerca a Thrall. Será un puñal en la espalda, o un veneno que recorra su cuerpo... Zando'zan ha dicho demasiado.\n\n¡<nombre>! Zando'zan tiene una misión para <nombre>.Consiste en ir a Trinquete, que está al este de El Cruce, y hablar con Desdio el Desdichado. Ve.", progress = nil, completion = "¿Para qué? Los últimos 5 que me envió el trol acabaron de cena de los tiburones. Espero que a ti te vaya mejor.", rewards = "Recibirás:\n\n"},
[2383] = {title = "Pergamino sencillo", objective = "Lee el Pergamino sencillo y habla con Frang en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó un pergamino para ti, <nombre>.\n\nLéelo cuando tengas tiempo, si no me equivoco viene de parte del instructor de guerreros, Frang. Querrá tener unas palabras contigo cuando estés <listo/lista>. Lo encontrarás a las puertas de El Cubil, al abrigo de la sombra.", progress = "¿Qué es lo que tienes ahí? ¿Un pergamino? No eres un erudito... espero...", completion = "Ah, mi pergamino. Oí que habías venido al Valle, &lt;nombre&gt;. Me alegra saber que lo has conseguido... esperemos que sobrevivas todos los retos que te aguardan. Como dije, ven a verme cuando crezca tu poder, haré todo lo que pueda por instruirte en las artes del &lt;clase&gt;.\n\nBuena suerte, &lt;nombre&gt;, y vuelve a verme cuando creas que estás preparado.", rewards = "Recibirás:\n\n"},
[2398] = {title = "Los enanos desaparecidos", objective = "Encuentra a Baelog en Uldaman.", description = "La excavación de Uldaman no siempre estuvo ocupada por extraños. Nosotros los enanos solíamos controlar la zona... pero eso era antes de que los troggs hicieran su aparición. ¡Llegaron tan rápido y eran tan numerosos que ni los enanos fuimos capaces de hacerles frente!\n\nDe hecho, su ataque fue tan repentino que yo mismo temí que algunos de los nuestros hubieran podido quedar atrapados tras las líneas enemigas.\n\nMi amigo Baelog estaba al frente de un pequeño grupo de enanos en las profundidades de la excavación en el momento en el que aparecieron los troggs. Si él y quienes lo acompañaban siguen con vida, quiero que los encuentres.", progress = nil, completion = "Ya era hora de que aparecieras. ¡Temía perder la apuesta que hice con Olaf!\n\nÉl apostó que su barba le crecería hasta las rodillas antes de que alguien viniera a buscarnos. Aquí estás y, como puedes observar, ¡a la barba de Olaf aún le queda un buen trecho para llegar hasta sus rodillas!\n\nGracias, &lt;nombre&gt;. Repartiré las ganancias contigo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2399] = {title = "Flora en brote", objective = "Las hojas que Denalan plantó en su jardín han retoñado y crecido. Esperan con impaciencia a que alguien las coja...", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nFronda crecida"},
[2418] = {title = "Las piedras de energía", objective = "Llévale 8 piedras de energía de dentrio y 8 piedras de energía de An'Alleum a Aparejez en las Tierras Inhóspitas.", description = "Hace algún tiempo, me ocurrió un incidente con los enanos de Forjatiniebla. Me trajeron a la Fortaleza de Angor para estudiar dos tipos de extrañas piedras de Uldaman. Descubrí que las piedras eran fuentes de energía estable y continua si se manipulaban correctamente. ¡Es asombroso las cosas que te puedes encontrar en esa vieja excavación!\n\nEn fin, después de compartir con ellos el secreto de las piedras, me echaron de la Fortaleza de Angor y siguieron excavando para conseguir más en Uldaman.\n\n¡Quiero esas piedras para mí! Tráeme de los dos tipos ¡y te pagaré con creces!", progress = "¿Tienes las piedras de energía, <nombre>? Uno de mis roedores robóticos necesita una fuente de alimentación...", completion = "¡Fantástico, encontraste algunas! Mil gracias, &lt;nombre&gt;. ¡Eres el &lt;raza&gt; más formal que he conocido! \n\nMe muero por probarlas en unos dispositivos que he fabricado. Apuesto que, en las circunstancias apropiadas, los dispositivos impulsados con estas piedras funcionarán, y funcionarán y funcionarán...", rewards = "Podrás elegir una de estas recompensas:\n\nCírculo de Piedras cargadoBrazales de DuracinBotas perpetuas\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCírculo de Piedras cargadoBrazales de DuracinBotas perpetuas"},
[2438] = {title = "EL cazasueños esmeralda", objective = "Llévale el cazasueños esmeralda a Tallonkai Cruzarraíz, que está en Dolanaar.", description = "Gaerolas Talvethen, el alcaide de Túmulo de Ban'ethil, me dio una vez un cazasueños esmeralda. Este poderoso amuleto es capaz de extraer la energía del Sueño Esmeralda y dar buena suerte a quien lo porta.\n\nPor desgracia, me lo he dejado en Aldea Brisa Estelar... A pesar de que en un tiempo Brisa Estelar fue un lugar tranquilo, ahora ha sucumbido a la corrupción de los furbolgs que allí habitan.\n\n¿Estás dispuesto a recuperar mi cazasueños, <clase>?", progress = "Date prisa, por favor. Solo espero que los furbolgs no hayan estropeado mi cazasueños esmeralda.\n\n¿Lo has recuperado ya, <nombre>?", completion = "Mi cazasueños esmeralda es muy importante para mí. Es un regalo reservado a unos pocos. Gracias por devolvérmelo, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2439] = {title = "Los discos de platino", objective = "Lleva tu vale de recompensa a Dinita Quebrantarrocas, que está en las Arcas de Forjaz.", description = "Este objeto es un hallazgo indescriptible. ¡Si lo que aquí dice es cierto, los antiguos Titanes, o Creadores según estos discos, son el enlace con nuestra creación encantada! Debemos dedicar a esto tanto tiempo como sea posible.\n\nY en agradecimiento por tu valor, <clase>, ten este vale y llévaselo a Dinita Quebrantarrocas, que está en las Arcas. ¡Con él conseguirás algo que considero pago adecuado a tu descubrimiento!\n\nVuelve después; ¡queda mucho trabajo por hacer en la Liga de Exploradores!", progress = "Bienvenido a Las Arcas de Forjaz, <nombre>. ¿En qué puedo ayudarte?", completion = "Ah, un vale de la Liga de Exploradores, y muy bueno, además.\n\nEscoge tu recompensa en sus arcas.", rewards = "Podrás elegir una de estas recompensas:\n\nPoción de curación excelentePoción de maná superior\n\nTambién recibirás:\n\nSaco de pelambre descongeladaPoción de curación excelentePoción de maná superior"},
[2440] = {title = "Los discos de platino", objective = "Llévale este vale de recompensa a Bena Pezuña Invernal, que está en Cima del Trueno.", description = "Concentraré mis energías en estos discos pues tarde o temprano me acabarán revelando su auténtico propósito.\n\nEn cuanto a ti, <nombre>, te recompensaré por haberme informado con tanta presteza. Entrégale este vale a Bena Pezuña Invernal, la instructora de alquimia de Cima del Trueno. Ella te dará un objeto muy especial que confío te resultará tentador y te entregará una selección de pociones de mi reserva personal.\n\nVuelve pronto a mi lado pues presiento que nos quedan muchas cosas por hacer en relación a este hallazgo.", progress = "¡Saludos, <clase>! ¿Estás aquí para recibir instrucción en alquimia o quizás para otra cosa?", completion = "Vaya, &lt;nombre&gt;. En vista de lo que te ha dado, debes haberle caído muy bien a Sabio Buscaverdad. Tienes derecho a elegir como recompensa 5 pociones excelentes de curación o 5 pociones superiores de maná de su alijo de pociones.", rewards = "Podrás elegir una de estas recompensas:\n\nPoción de curación excelentePoción de maná superior\n\nTambién recibirás:\n\nSaco de pelambre descongeladaPoción de curación excelentePoción de maná superior"},
[2458] = {title = "Agente encubierto", objective = "Ve a la torre de Ventura y Cía., al norte de El Fangal, y localiza al capataz Fizzule, el agente de Mano Destrozada. \n\nCuando veas la torre, usa la pistola de bengalas para avisar de tu llegada. Lanza 2 bengalas y acércate al capataz Fizzule para darle el /saludo de los Mano Destrozada. \n\nTienes que hacerlo en este orden, porque Fizzule te matará si no te reconoce.\n\nObjeto provisto: \n", description = "¿Has oído hablar de Ventura y Cía.?\n\nNos ha costado un año de duros esfuerzos, pero hemos conseguido infiltrar un agente.\n\nHemos descubierto que planean algo a gran escala. Ve hacia el norte, a El Fangal; en la torre de Ventura y Cía. Encontrarás a nuestro agente, el capataz Fizzule.\n\nCuando encuentres la torre, dispara la pistola de bengalas, busca a Fizzule y dale el saludo de Mano Destrozada.", progress = nil, completion = "Me alegro de ver agentes de Mano Destrozada, pero lo de las bengalas ha sido excesivo.\n\nSi quieres verme otra vez, sopla 2 veces este silbato.", rewards = "Recibirás:\n\nSilbato de Fizzule"},
[2459] = {title = "Ferocitas el Comesueños", objective = "Tallonkai Cruzarraíz de Dolanaar quiere que mates a 7 místicos Tuercepinos y encuentres la joya perdida.", description = "La esmeralda... ¡ha desaparecido! ¡Mi cazasueños está estropeado!\n\nHay un grupo de místicos Tuercepinos al norte de Brisa Estelar. He oído que su líder, Ferocitas el Comesueños, tiene un collar que brilla de noche con una luz de color verde. En vista de mi cazasueños, estoy seguro de que él me ha robado la esmeralda... No se daría cuenta ni en mil años de que el poder de la esmeralda es inútil en sus manos.\n\nEncuentra la joya perdida, <nombre>. Y ya de paso, mata también a algún místico corrupto.", progress = "Ferocitas y los místicos Tuercepinos me tienen que devolver lo que es mío. Por favor, recupera la esmeralda para que pueda reparar el cazasueños esmeralda.", completion = "Ahora ya puedo completar mi cazasueños. Gracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2460] = {title = "El saludo de los Mano Destrozada", objective = "Da el saludo de los Mano Destrozada a Shenthul.", description = "Si quieres pertenecer a Mano Destrozada, tendrás que aprender el saludo. Es la única forma de identificar a los miembros.\n\nMírame, apréndelo y repite.", progress = nil, completion = "Muy bien, &lt;nombre&gt;. Sigue, sigue.", rewards = "Recibirás:\n\n"},
[2478] = {title = "Misión: posible, pero no probable", objective = "Roba la llave de la torre del supervisor Silixiz, mata a 2 zánganos mutados de Ventura y Cía., 2 patrullas de Ventura y Cía. y 2 vigías de Ventura y Cía.\n\nAsesina al Gran Supervisor Puzik Gallywix y quédate con su cabeza, fuerza la cerradura del arcón de Gallywix y roba la mezcla de Zanzil modificada que allí está oculta. Lleva el alijo y la cabeza de Gallywix a Shenthul en Orgrimmar, él te dará más instrucciones.", description = "Tendrás que robarle la llave de la torre al capataz Silixiz, <nombre>. Cuando la llave esté en tus manos, quédatela, podría necesitarla más tarde.\n\nRecuerda, el veneno lo ha debilitado todo en esta torre. Usa todos tus recursos para sembrar el caos y la destrucción entre sus habitantes. Masácralos a todos ¡sin piedad!\n\nCuando todo esté bañado en sangre, toma la caja fuerte de Gallywix y roba el preparado de Zanzil ahí guardado. Lleva la cabeza de Gallywix y el preparado a Shenthul en Orgrimmar.", progress = "¿Qué noticias traes, <nombre>?", completion = "Pobre Burbujita, era un buen agente, uno de los mejores. Por desgracia, es el precio que pagamos, &lt;nombre&gt;. Ser un agente de campo de la Mano Destrozada es un trabajo arriesgado.\n\nHas cumplido con tu deber, &lt;clase&gt;. Sería una lástima que no vivieras para volver a colaborar con nosotros.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nReceta: té de cardo"},
[2479] = {title = "La ayuda de Hinott", objective = "Ve a Molino Tarren, en Laderas de Trabalomas, para entregar la muestra de Zanzil a Serge Hinott. \n\nPara ir, coge el zepelín a Entrañas y sigue el camino hacia el sur por el Bosque de Argénteos. ¡Sigue las indicaciones!", description = "Llévale una muestra de la mixtura a Serge Hinott, que está en Molino Tarren.\n\nSi hay alguien que pueda curarte, ese es él. Si vives lo suficiente, te enseñará a crear y usar venenos.", progress = "¿Qué es esto?", completion = "Qué palidez tan deliciosa la tuya, &lt;clase&gt;. ¿Y qué es ese terrible olor?", rewards = "Recibirás:\n\n"},
[2480] = {title = "La ayuda de Hinott", objective = "Espera a que Serge Hinott te cure.", description = "¡Jamás vi un veneno con tan extraordinario efecto! ¿Zanzil, dices? Nunca he oído hablar de él, pero admiro su genio por crear este brebaje.\n\n¿Quieres que te cure y te enseñe a usar el veneno?\n\nDame un momento para examinar la toxina, <nombre>.", progress = nil, completion = "¡La cura está lista! Bebe este antídoto para detener la demencia. Sin embargo, no puedo hacer nada con el olor.", rewards = "Te van a lanzar el siguiente hechizo:\n\nVenenos\n\nRecibirás:\n\nAceite de Hinott"},
[2498] = {title = "El regreso a Denalan", objective = "Rellian Verdeguja quiere que hables con Denalan, que está en el Lago Al'Ameth.", description = "Me acabo de enterar de que Denalan, que está en el Lago Al'Ameth, ha descubierto la causa de los tumores que les nacen a los Brezomadera. Por favor, habla con él y dile que vas de mi parte.\n\nDate prisa, <nombre>, pues necesita tu ayuda.", progress = nil, completion = "¡Ah, Rellian te ha enviado para que me ayudes! Me alegro de que hayas llegado tan pronto.\n\n¡He descubierto algo bastante alarmante!", rewards = "Recibirás:\n\n"},
[2499] = {title = "Roblegüello", objective = "Denalan, que está en el Lago Al'Ameth, quiere que cojas el tumor gigantesco que tiene Roblegüello.", description = "En una cueva situada en la orilla sur del lago, un Brezomadera llamado Roblegüello está corrompiendo a las demás criaturas que habitan el Lago Al'Ameth.\n\nNo me atreví a acercarme mucho pero, incluso desde la distancia, lo vi perfectamente: Roblegüello tiene un tumor enorme... diría incluso gigantesco, que lo está envenenando.\n\n<nombre>, ya me has ayudado mucho pero quiero pedirte una última cosa. Atrapa a Roblegüello y extráele el tumor para eliminar así la corrupción de este lugar que es mi hogar.", progress = "¿Has localizado ya a Roblegüello, <nombre>?", completion = "Sabía que serías capaz de acabar con esa bestia que aterrorizaba el lago.\n\nEstamos ante una penosa situación; Roblegüello fue en su día un gran líder entre los suyos... pero la corrupción no hace distinción entre el pueblo llano y los nobles.\n\nEl tamaño de este tumor es preocupante pero debo estudiarlo en profundidad para averiguar más datos sobre la enfermedad que está asolando a los Brezomadera.\n\nGracias, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón tierraleñoGuanteletes cubiertos de musgo\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCinturón tierraleñoGuanteletes cubiertos de musgo"},
[2500] = {title = "Componentes de Tierras Inhóspitas", objective = "Ve a las Tierras Inhóspitas a buscar los ingredientes que te ha pedido Ghak Sanadón y llévaselos a Thelsamar.", description = "<clase>, necesito asistencia. Soy un alquimista de cierto renombre y necesito que me ayudes a conseguir ingredientes que se encuentran en las Tierras Inhóspitas.\n\nEsto es lo que necesito: 5 mollejas de águila ratonera, 10 colmillos de coyote montés y 5 fragmentos de elemental de piedra. Evidentemente, estos ingredientes están en las propias bestias; si la lucha no es lo tuyo, lleva a un par de amigos duros.\n\nSi me traes todo esto, te ganarás unas monedas.\n\nDime, ¿aceptas?", progress = "¿Me traes lo que te pedí? No te pagaré hasta que me des los ingredientes.", completion = "Buen trabajo, &lt;nombre&gt;; con esto ya tengo para una temporada. Ten, tu recompensa.\n\nY tengo otro trabajo para ti, si te interesa. Lo que busco está en la excavación de Uldaman y no es fácil de conseguir, pero te recompensaré bien.\n\nY con algo más tentador que las monedas, ¿qué me dices?", rewards = "Recibirás:\n\n"},
[2501] = {title = "Componentes de Tierras Inhóspitas II", objective = "Usa las vasijas de taumaturgia en los dragones guardianes abrasados de las Tierras Inhóspitas. Llévaselas a Ghak Sanadón a Thelsamar.", description = "Agradezco mucho tu ayuda, <nombre>. Quiero regalarte la receta del elixir que te di antes, pero tengo que pedirte un favor bastante peligroso.\n\nEsta caja fuerte contiene tres vasijas de taumaturgia vacías imbuidas de un aura de Armonización que absorberá la sangre de un dragón guardián abrasado. Los dragones se pondrán furiosos, así que ten cuidado. Tráeme las vasijas llenas.\n\nA cambio, te daré la receta.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nPoción restauradora\n\nRecibirás:\n\n"},
[2518] = {title = "Las lagrimas de la Luna", objective = "La sacerdotisa A'moora del Templo de la Luna de Darnassus quiere que le lleves las hilanderas plateadas de Lady Sathrah.", description = "Lady Sathrah estuvo una vez en gracia de Elune. Grácil y pura, la araña tejió sus telas de plata a la luz de la luna, atrapando la niebla de la noche. El rocío plateado tenía grandes poderes de curación por lo que se guardó en el templo.\n\nPero en los últimos tiempos, Sathrah cayó en la locura y ahora sus generaciones futuras también están en peligro.\n\n<nombre>, encuentra a Lady Sathrah y pon fin a su sufrimiento. Suele estar al norte de Teldrassil, cerca del Lago Primigenio. Atrápala y tráeme sus hilanderas plateadas.", progress = "Siento haberte encomendado esta tarea pero entiende que queda poca esperanza para Lady Sathrah.\n\nLo único que nos queda es ofrecer el sacrificio de sus hilanderas a Elune. Con este sacrificio, Elune bendecirá a Sathrah de modo que podrá nacer de nuevo en paz.", completion = "El bosque llora por Lady Sathrah pero era algo que había que hacer.\n\nGracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2519] = {title = "El templo de la Luna", objective = "La hermana Aquinne quiere que hables con la sacerdotisa A'moora en el Templo de la Luna.", description = "Una criatura del bosque, en su día grandiosa, necesita ayuda, <raza>.\n\nHay una difícil tarea que cumplir.\n\nPor favor, busca a la sacerdotisa A'moora; ella te lo explicará todo. La encontrarás al sudeste de aquí, dentro del Templo de la Luna.", progress = nil, completion = "Así que te envía la hermana Aquinne, ¿verdad?", rewards = "Recibirás:\n\n"},
[2520] = {title = "El Sacrificio de Sathrah", objective = "La sacerdotisa A'moora quiere que coloques las hilanderas plateadas de Lady Sathrah en la fuente que hay en el interior del templo, y que después vayas a verla de nuevo.", description = "Ahora has de cumplir el cometido que te ha sido asignado. Coge las hilanderas y mata a Lady Sathrah como ofrenda a Elune.\n\nEn el interior de este templo se encuentra la fuente central, lugar en el que debes realizar el sacrificio. Las aguas sagradas purificarán y eliminarán la corrupción que hizo enloquecer a Sathrah.\n\nCuando hayas cumplido tu cometido, vuelve a verme.", progress = "¿Has colocado el sacrificio en la fuente, <nombre>?", completion = "La pérdida de Lady Sathrah es angustiante pero puede que sea el único modo de que vuelva a nacer con un espíritu renovado.\n\nEsperemos que Elune acepte de buen grado el sacrificio que le has ofrecido.", rewards = "Podrás elegir una de estas recompensas:\n\nPantalones con encajeBotas amortiguadas\n\nTambién recibirás:\n\nPantalones con encajeBotas amortiguadas"},
[2521] = {title = "Al servicio de Kum'isha", objective = "En tus viajes por Las Tierras Devastadas, si te encontraras con 1 esfera draenetista perfecta, llévasela a Kum'isha el Coleccionista.\n\nTen cuidado, esta gema es uno de los cristales más raros de todo Azeroth. Cualquier criatura de estas tierras podría tener una.\n\nSe te recompensará por cada esfera draenetista perfecta que consigas.", description = "<raza>, ¿será capaz tu cerebro de mosquito de entender cuán rara es una esfera draenetista perfecta? Generaciones de draenei han buscado uno solo de estos cristales y solo lograron un destino fatal, su objetivo por cumplir.\n\nAsí que te presentas ante Kum'isha, sabiendo lo que Kum'isha ha pedido ¿y aún deseas ayudar? Tú verás, <raza>... En el momento que sea a lo largo de tus viajes por estas tierras, en cuanto encuentres una esfera draenetista perfecta, tráemela y te recompensaré con riquezas con las que ni siquiera puedes soñar.", progress = "He dedicado mi vida a la investigación de las esferas draenetistas perfectas. ¡Una vida entera, <raza>!", completion = "¿Acaso me engañan los ojos? ¡Imposible! ¿Dónde...? ¿Cómo...?\n\nA lo mejor ahora se puede abrir una grieta para volver a nuestra patria.", rewards = "Recibirás:\n\nCofre taraceado Esmeralda"},
[2522] = {title = "Los esfuerzos de Kum'isha", objective = "Encuentra 1 esfera draenetista perfecta y llévasela a Kum'isha el Coleccionista.", description = "Aunque el conjuro del portal fracasara, conservo la esperanza de que mi próximo intento funcione.\n\nLa esperanza de los draenei es tan ferviente como las baratijas que codicias, <raza>. Vuelve a esas tierras malditas y consigue otra esfera draenetista perfecta.\n\nPor supuesto, tú decides si aceptas esta tarea. <sonríe.>Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nCofre taraceado Esmeralda"},
[2523] = {title = "Melodía corrupta", objective = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una melodía. De la planta emana un olor acre y malsano. Necesita desesperadamente ser atendida para recuperar su estado normal.", description = nil, progress = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una melodía. De la planta emana un olor acre y malsano. Necesita desesperadamente ser atendida para recuperar su estado normal.", completion = "Aplicas un poco de bálsamo de planta Cenarion a la melodía. Inmediatamente empieza a mudar su forma corrupta y se transforma en una planta rebosante de salud.", rewards = nil},
[2541] = {title = "El druida durmiente", objective = "Llévale un talismán de vudú de chamán a Oben Garrafuria al Túmulo de Ban'ethil.", description = "Los chamanes Tuercepinos que habitan este lugar han descubierto un modo de separar el espíritu de un druida durmiente de su cuerpo físico. Los furbolgs han reanimado mi representación física y la están utilizando para atacar a todo el que intente entrar en Túmulo de Ban'ethil. Yo estoy atrapado en el Sueño Esmeralda y no puedo hacer nada para detenerlos.\n\nTienes que ayudarme. Los chamanes Tuercepinos portan un extraño talismán para llevar a cabo el ritual y me gustaría examinarlo. Por favor <nombre>, tráeme uno.", progress = "Si pudiera examinar el talismán, podría descubrir cómo romper el encantamiento. ¿Has encontrado alguno?", completion = "Gracias, &lt;nombre&gt;.\n\nQué objeto más extraño... Puedo sentir el aura de maldad que emana de él. Se trata de un potente encantamiento.", rewards = "Recibirás:\n\n"},
[2561] = {title = "El druida de la garra", objective = "Oben Garrafuria quiere que mates su cuerpo desalmado y que después utilices el talismán de vudú.", description = "Tras haber estudiado el talismán, <nombre>, veo con claridad lo que hay que hacer. Cógelo y haz lo que te digo.\n\nTendrás que explorar las áreas más profundas de Túmulo de Ban'ethil. Allí encontrarás mi cuerpo desalmado. Aunque me cueste aceptar lo que voy a decir, no veo otro modo de liberarme del control de los Tuercepinos.\n\nPara que pueda escapar, debes matar mi representación física. Hecho esto, utiliza el talismán de vudú sobre mi cuerpo inerte. Vuelve aquí cuando hayas cumplido tu cometido.", progress = "<nombre>, ten cuidado al acercarte a mi representación física; el encantamiento al que está sometida es muy poderoso.", completion = "Al fin estoy libre del control de los Tuercepinos. Gracias, &lt;nombre&gt;.\n\nAhora mi espíritu podrá descansar en paz para siempre en el Sueño Esmeralda.\n\nQuizás nos volvamos a encontrar algún día, joven &lt;clase&gt;. De momento, acepta esta recompensa como muestra de mi gratitud.", rewards = "Podrás elegir una de estas recompensas:\n\nTogas para dormirHoja Leñomaleza\n\nTambién recibirás:\n\nTogas para dormirHoja Leñomaleza"},
[2581] = {title = "Papadas de hiena risita", objective = "Llévale 3 papadas de hiena risita, 2 pulmones de jabalí maldito y 1 pinza de scorpok al mago sangriento Drazial.", description = "Es bien sabido que las hienas poseen una gran fuerza en sus papadas pero ¿tanta como para morder a través de 7,5 centímetros de acero de torio? Imposible, improbable, y lo cierto es que lo he visto con mis propios ojos.\n\nAdéntrate en Las Tierras Devastadas y tráeme 3 papadas de hiena risitas Cuando busques la hiena risitas, estate atento a los jabalíes y a los scorpoks. Para la preparación inicial de polvo también hacen falta 2 pulmones de jabalí maldito y 1 pinza de scorpok.", progress = "Apestas, <nombre>. ¿Has estado por terrenos sulfurosos? ¡¿Y dónde están mis componentes?!", completion = "¿Cómo te encuentras, &lt;nombre&gt;? Irradias el aura de la hiena risitas, tu fuerza ¡no tiene parangón entre los mortales! \n\nDesgraciadamente, con los componentes que me trajiste, solo pude hacer suficiente polvo para una pastilla. Necesitaré más componentes si te hiciera falta otra pastilla.", rewards = "Te van a lanzar el siguiente hechizo:\n\nCólera de los tiempos\n\nRecibirás:\n\nR.O.I.D.S."},
[2582] = {title = "La cólera de las edades", objective = "Llévale 3 papadas de hiena risita, 2 pulmones de jabalí maldito y 1 pinza de scorpok al mago sangriento Drazial.", description = "Ah, <nombre>, <bienvenido/bienvenida> de nuevo. Parece que has disfrutado del efecto de impregnación operativa y robusta que la hiena risitas produce. Ten cuidado, aunque el efecto inmediato es un impulso titánico de fuerza, los efectos a largo plazo sobre tu ánimo y tu inteligencia podrían ser peligrosos.\n\nHecha la advertencia, ¿necesitas más? Si es así, tráeme otras 3 papadas de hiena risitas, 2 pulmones de jabalí maldito y 1 pinza de scorpok.", progress = "¿Tienes los órganos de la bestia?", completion = "¡Aquí tienes! Recuerda, úsala con moderación. Si necesitas más, ven a verme cuando hayas acabado con la que tienes ahora.", rewards = "Recibirás:\n\nR.O.I.D.S."},
[2583] = {title = "La vitalidad del jabalí", objective = "Llévale 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco al mago sangriento Drazial.", description = "En Las Tierras Devastadas hay dos especies de jabalí: el cenicrín y el jabinferno.\n\nAl parecer, la fortaleza de estas bestias los vuelve prácticamente inmunes a cualquier daño, ya que los demás animales los encuentran demasiado difíciles de matar y, por lo tanto, que no merece la pena tal esfuerzo.\n\nTenemos que examinarlos más a fondo, <nombre>. Si me traes 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco, con ellos crearé un elixir divino a partir de los residuos del polvo. ¡Serás mi <primer/primera> cobaya!", progress = "¡¿Dónde están los órganos, <nombre>?!", completion = "¡El espíritu del jabalí ilumina tu ser, &lt;nombre&gt;! Me pregunto... ¿crees que eres tan invulnerable como estas bestias?|n", rewards = "Te van a lanzar el siguiente hechizo:\n\nEspíritu de jabalí\n\nRecibirás:\n\nCóctel de zumo de pulmón"},
[2584] = {title = "El espíritud del jabalí", objective = "Llévale 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco al mago sangriento Drazial.", description = "La primera vez que te administré el elixir, <nombre>, sabía que una o dos aplicaciones no bastarían para saciar tu apetito. Incluso ahora veo la ansiedad en tus ojos. Ese brebaje tiene propiedades adictivas a las que, al parecer, no se resiste ni la voluntad más férrea.\n\n¡Que así sea! Si necesitas más cóctel de zumo de pulmón, vuelve al páramo y encuéntrame 3 pulmones de jabalí maldito, 2 pinzas de scorpok y 1 cerebro de basilisco.", progress = "¡¿Dónde están los órganos, <nombre>?!", completion = "Debo advertirte, &lt;nombre&gt;, no intentes mezclar las fórmulas que he creado. Las propiedades químicas de la fórmula ingerida en último lugar siempre prevalece sobre encantamientos anteriores, por lo que se anularían los efectos de las fórmulas precedentes.", rewards = "Recibirás:\n\nCóctel de zumo de pulmón"},
[2585] = {title = "El golpe decisivo", objective = "Llévale 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito al mago sangriento Drazial.", description = "¿Los has visto combatir alguna vez, <nombre>? Me refiero a los scorpoks. ¿Has visto alguna vez la gracia y la agilidad de sus movimientos? ¿La firmeza de sus golpes?\n\nSi pudiéramos aislar de alguna manera esa agilidad y envasarla, nuestra riqueza sería más... digo… sería un tremendo impulso para la comunidad científica.\n\nAyúdame, <nombre>, y te regalaré una muestra del polvo.\n\nPara crear una muestra estable, voy a necesitar 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito.", progress = "¡¿Dónde están los órganos, <nombre>?!|n", completion = "Fascinante. Este ha sido el polvo más difícil de analizar. \n\nToma esta pizca de sal residual y colócala debajo de tu lengua. Deberías sentir una leve sensación de euforia seguida de un estado de agilidad y velocidad nunca visto.", rewards = "Te van a lanzar el siguiente hechizo:\n\nGolpe de Scorpok\n\nRecibirás:\n\nPolvo de scorpok"},
[2586] = {title = "La sal del Scorpok", objective = "Llévale 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito al mago sangriento Drazial.", description = "¡Estás <vivo/viva>! Esto sí que es una buena noticia. Supongo que has vuelto a por más polvo de scorpok, ¿no?\n\nMuy bien, <nombre>, vuelve al páramo y no regreses hasta que hayas recolectado 3 pinzas de scorpok, 2 mollejas de buitre y 1 pulmón de jabalí maldito.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPolvo de scorpok"},
[2601] = {title = "El bocado de basilisco", objective = "Llévale 10 cerebros de basilisco y 2 mollejas de buitre al mago sangriento Lynnore.", description = "Estos páramos están plagados de basiliscos Rocarroja, <nombre>. Estos basiliscos se han adaptado y se han multiplicado más que cualquier otra especie de la región.\n\nLos he observado a cierta distancia cuando cazan y en sus actividades cotidianas; esas bestias muestran un comportamiento reflexivo más complejo incluso que el de ciertos humanoides.\n\n¡Debo estudiar sus cerebros!\n\nConsígueme 10 cerebros, <nombre>. Aunque el preparado no estará completo si no tengo además 2 mollejas de buitre para crear la base de la solución.", progress = "¿Vuelves con los órganos que te pedí?", completion = "¡Maravilloso, &lt;nombre&gt;! Te ofrezco probar las entrañas del basilisco Rocarroja. \n\nSi el compuesto fuera de tu agrado, siempre se puede hacer más... previo pago.", rewards = "Te van a lanzar el siguiente hechizo:\n\nMente infalible\n\nRecibirás:\n\nCompuesto de córtex cerebral"},
[2602] = {title = "La mente infalible", objective = "Llévale 10 cerebros de basilisco y 2 mollejas de buitre al mago sangriento Lynnore.", description = "¿Ya has perdido la concentración? ¡Debo seguir investigando!\n\nAh, sí, el precio sigue siendo el mismo, <nombre>. Adéntrate en Las Tierras Devastadas y tráeme 10 cerebros de basilisco Rocarroja y 2 mollejas de buitre.", progress = "¿Vuelves con los órganos que te pedí?", completion = "Sí, ya va, dame un momento y tendré más preparado para que puedas consumirlo, &lt;nombre&gt;.\n\nToma... y, &lt;nombre&gt;, no intentes consumir este brebaje experimental de forma muy seguida. El resultado podría ser desastroso.", rewards = "Recibirás:\n\nCompuesto de córtex cerebral"},
[2603] = {title = "El vigor del buitre", objective = "Llévale 10 mollejas de buitre y 2 papadas de hiena risita al mago sangriento Lynnore.", description = "La muerte tiene alas en estas tierras malditas, <raza>. Yo he visto a esos negros asesinos y carroñeros dándose festines con los cadáveres de muchos exploradores... alimentándose incluso de los cadáveres más viles y enfermos. Es un milagro que sus organismos hayan sobrevivido a la pestilencia que reclama estas tierras.\n\n¡Ayúdame con mis estudios, <nombre>! ¡Necesito mollejas! También necesitaré dos papadas de hiena para el brebaje.\n\nSerás doblemente recompensado por tus esfuerzos.", progress = "No me hagas perder el tiempo, <nombre>. No tenemos nada de qué hablar, a menos que hayas recuperado los objetos que te pedí.", completion = "¡Soberbio! Me pondré a trabajar de inmediato. ¡Quizás encuentre una cura para muchos de los males que afligen al mundo! \n\nAh, sí, tu recompensa, claro; si te hiciera falta más chicle, vuelve aquí y te asignaré otra tarea.", rewards = "Te van a lanzar el siguiente hechizo:\n\nDominio espiritual\n\nRecibirás:\n\nChicle de mollejas"},
[2604] = {title = "Dominación espiritual", objective = "Llévale 10 mollejas de buitre y 2 papadas de hiena risita al mago sangriento Lynnore.", description = "Veo que el fuego interno ha menguado. ¿Dirías que la euforia espiritual ha durado una hora?\n\nSi más resina de mollejas quieres, más mollejas necesito. De vuelta al páramo, <nombre>. No regreses hasta que hayas recuperado 10 mollejas y 2 papadas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nChicle de mollejas"},
[2605] = {title = "El goblin sediento", objective = "Consigue una glándula cargada de rocío y llévasela a Marin Tragonublo a Gadgetzan.", description = "He tenido una idea. He oído hablar de unas extrañas criaturas llamadas colectores de rocío que habitan hacia el suroeste... capaces de almacenar agua en unas glándulas especiales.\n\nCreo que si pudiera hacerme con una de esas glándulas, sería capaz de idear una bebida para saciar mi espantosa sed. ¿Crees que podrías ayudarme, <nombre>?\n\nBusca el jardín de cactos, <nombre>, pues allí encontrarás a los colectores de rocío.", progress = "Por favor, dime que has encontrado a los colectores de rocío.", completion = "¡Oh, maravilloso! ¡Trae, me lo voy a beber!", rewards = "Recibirás:\n\n"},
[2606] = {title = "Un buen sabor", objective = "Marin Tragonublo quiere que hables con Roxio en Gadgetzan.", description = "¡Puaj! ¡Esto sabe fatal! Menuda idea...\n\nEspera, ¿qué? Ah, ya veo. Crees que debería tirarlo. Está bien.\n\n¡Eh, tengo una idea! ¡Es perfecto! Llévale este rocío a mi amiga Roxio, que es una gran cocinera. Dile lo que pasa y a ver si consigue que baje algo mejor.\n\nEs decir, si va a seguir sabiendo igual de mal, al menos que dé un buen pelotazo... Yo de momento no siento nada...", progress = "Así que Marin está otra vez en ello, ¿eh? ¿De qué está hecho este brebaje?", completion = "Marin es un buen tipo pero un poco excéntrico, diría yo.\n\nEn cualquier caso, está claro que necesita mi ayuda una vez más.", rewards = "Recibirás:\n\n"},
[2607] = {title = "Toque de Zanzil", objective = "Habla con Doc Mixilpixil en el sótano de los Cuarteles.", description = "Parece ser que has obtenido más información de la que me esperaba. Ciertamente, esto es un extraño giro de los acontecimientos, <nombre>.\n\n¿Una cura? Mmm... Vete al sótano y habla con el doctor Mixilpixil. Quizás pueda ayudarte.", progress = nil, completion = "¡Ya era hora! Deja el queso en la mesa y pon pies en polvorosa. Osborne te pagará al salir.", rewards = nil},
[2608] = {title = "Toque de Zanzil", objective = "Túmbate para que te examine Doc Mixilpixil. ¿Cómo va a encontrar la cura si no encuentra la causa?", description = "¡Oh! Pareces como <muerto/muerta>. Corrección, he visto muchos muertos, tú tienes peor aspecto. <Caballero/señorita>, tú serías lo que la muerte obtuvo como resultado en su último examen médico.\n\n¡Ja! ¿Has oído eso, Noarm? ¡Escríbelo!\n\n*Tose* Mmm, sí. Para poder ayudarte, necesito sacarte sangre. No te preocupes, solo duele las primeras... cincuenta veces, o así... Túmbate cuando estés $gpreparado:preparada para el tratamiento, <nombre>.", progress = nil, completion = "Dame un momento para hablarlo con mis colegas, &lt;nombre&gt;. Dr. Montgomery, Mr. Noarm, ¡hablémoslo!", rewards = "Recibirás:\n\n"},
[2609] = {title = "Toque de Zanzil", objective = "Lleva a Doc Mixilpixil un ramo de flores silvestres simples, un vial emplomado, un tubo de bronce y una bobina de hilo de seda chartreuse. La 'itis' no se cura sola, <jovencito/jovencita>.", description = "Es unánime, <nombre>. Tienes la 'itis'.\n\nPor suerte para ti, tengo la cura para la 'itis'.\n\nEsto es lo que necesito: un ramo de flores silvestres simples, un vial emplomado, un tubo de bronce y por último, pero no menos importante, una bobina de hilo de seda chartreuse. La vieja Emma dejó una bobina para mí en 'El Paño Fino'. Podrás encontrar la tienda en los canales.\n\nY ahora date prisa, <nombre>, e intenta no tocar a nadie hasta que estés <curado/curada>. Oh, y el doctor Montgomery dice que necesitas una ducha... Lo dice él, no yo.", progress = "Si quieres que te cure la 'itis', vas a tener que poner más de tu parte, <nombre>. ¿Dónde están los componentes para el remedio?", completion = "¿Qué estoy haciendo? ¿Qué parece que estoy haciendo? ¡Estoy preparando un remedio para tu 'itis'! ¡No te enfades ahora conmigo, &lt;nombre&gt;!\n\nUnos segundos más. ¡Ya está! Rocíate con esta, mmm, medicina de olor tan fantástico, y volverás a sentirte bien.", rewards = "Recibirás:\n\nAgua de Mixilpixil"},
[2621] = {title = "El deshonrado", objective = "Habla con el comandante de despachos Ruag en Rocal.", description = "Arrepentimiento. Eso es lo que me queda. Una mal decisión que desembocó en varias tragedias. ¡¿Por qué diablos no esperaría las órdenes?! ¡A lo mejor habrían descrito el horror que nos aguardaba en esas malditas tierras! ¿O quizás nos habrían enviado a otro destino? A fin de cuentas, fue culpa mía. Todo fue culpa mía.\n\nAsí que aquí estoy, esperando órdenes. Debo conocerlas. Debo tener esas órdenes.\n\nEl comandante de despachos Ruag vive en Rocal. Encuéntralo y averigua qué fue de esas órdenes.", progress = nil, completion = "La garra de Thrall... El recuerdo aún está vívido en mi memoria. \n\n&lt;El comandante Ruag se sacude la cabeza.&gt; \n\nFue terrible lo que le ocurrió a ese regimiento. La Horda perdió a algunos de sus mejores soldados cuando aniquilaron a ese batallón.", rewards = "Recibirás:\n\n"},
[2622] = {title = "Las órdenes perdidas", objective = "Habla con Bengor.", description = "¿Las órdenes? ¿Y qué harías tú con las órdenes?\n\n¿Insinúas algo, <nombre>? ¡Por supuesto que transmití esas órdenes! Tendieron una emboscada a los soldados que envié con ellas, los murlocs les dieron una brutal paliza. Solo uno sobrevivió, si se le puede llamar sobrevivir. Apenas puede hablar, es incapaz de andar y hay que alimentarlo a través de un tubo. Si quieres saber qué ocurrió con esas órdenes, habla con Bengor. Estoy seguro de que está por aquí cerca... y, <nombre>, ten mucho cuidado con dónde metes la nariz, te la podrían arrancar de un bocado.", progress = nil, completion = "Hablador del pan... pan.. pantano...|n", rewards = "Recibirás:\n\n"},
[2623] = {title = "El Hablador del pantano", objective = "Recupera las órdenes del Jefe de Guerra y vuelve junto al Héroe Caído de la Horda.", description = "El comandante de despachos dio las órdenes. Tomamos... tomamos las órdenes. Camino de Las Tierras Devastadas, emboscada.\n\nMurlocs... Muchos murlocs... Uno llamado Hablador del pantano. Me arrancó las órdenes de las manos cuando una lanza me atravesó la cabeza. Este... caverna...\n\n<Bengor fallece.>", progress = "A la espera de órdenes.", completion = "Es aún peor de lo que me imaginaba. Diecinueve de mis hombres podrían haberse ahorrado la agonía que sufrieron. ¡Si yo no hubiera actuado de forma tan irracional! \n\n¡Tienes que ayudarlos, &lt;nombre&gt;! A mí me es imposible.", rewards = "Recibirás:\n\n"},
[2641] = {title = "El ingrediente secreto de Roxio", objective = "Roxio de Gadgetzan quiere que consigas un tragano violeta y se lo lleves.", description = "Cuando era niña, siempre cocinaba mi hermana. ¡Era espantoso! Muchos de sus brebajes no se tragaban... hasta que descubrí lo que yo llamo mi ingrediente \"secreto\". Échaselo a lo que sea y... ¡HALA! ¡Sabe a gloria!\n\nY qué es, te preguntarás. Es un champiñón que se conoce como tragano violeta. Si quieres que te ayude a hacer de este nauseabundo brebaje algo pasable, dirígete a las Tierras del Interior y localiza un pequeño lago que hay allí.\n\nAh, y recuerda que el tragano violeta solamente crece bajo el agua.", progress = "El único lugar en el que encontrarás tragano violeta es bajo las aguas de ese lago. ¡Espero que tengas buenos pulmones!", completion = "¡Eso es lo que quería, &lt;nombre&gt;!\n\nTiene buena pinta, a ver... ¡Ajá! ¡Perfecto!", rewards = "Recibirás:\n\n"},
[2661] = {title = "Un envío para Marin", objective = "Roxio quiere que le lleves el polvo violeta a Marin Tragonublo a Gadgetzan.", description = "Aquí tienes el polvo violeta, <nombre>. ¡Llévaselo a Marin y dile que me debe un favor!", progress = "Ah, <nombre>, al fin has vuelto. ¿Qué traes?", completion = "¿Polvo violeta, eh? Déjame que lo pruebe... De todos modos, es imposible que empeore las cosas.", rewards = "Recibirás:\n\n"},
[2662] = {title = "El elixir de Tragonublo", objective = "Marin Tragonublo quiere que hables con él cuando acabe de preparar el elixir.", description = "Ahora tengo que mezclar estos polvos con el rocío.\n\nMárchate y regresa dentro de un minuto... ¡y ya tendré preparado el fabuloso Elixir de Tragonublo!", progress = nil, completion = "¡Dale un trago! ¡¡¡Es estupendo!!!\n\nSabía que lo conseguiría... Gracias otra vez por tu ayuda, &lt;nombre&gt;. Eso sí, hazte a un lado. ¡Estás interrumpiendo mi trabajo! ¡Voy a hacerme rico con este fabuloso Elixir de Tragonublo! ¡Rico!\n\n¿Cómo? ¿Roxio dice que le debo una? ¡Hay que ver qué sentido del humor tiene Roxio!", rewards = "Recibirás:\n\nElixir de Tragonublo"},
[2681] = {title = "Las piedras que nos vinculan", objective = "Libera a 9 sirvientes de Razelikh, 3 sirvientes de Sevine, 3 sirvientes de Allistarj y 3 sirvientes de Grol. Vuelve junto al Héroe Caído cuando hayas acabado la tarea. Debes permanecer cerca de las piedras o el proceso no funcionará.", description = "No podrás vencer a las tropas de Razelikh y a sus seguidores tú <solo/sola>.\n\nReúne a un grupo de aguerridos aventureros ¡y libera a mis hombres de su eterna tortura! 18 piedras has de hacer añicos.\n\nCombate con brío y muere con honor, <nombre>.", progress = nil, completion = "¡Lo has logrado, &lt;nombre&gt;! Las pobres almas torturadas al fin descansan en paz. \n\nNuestro trabajo aún no se ha acabado, &lt;nombre&gt;. Tenemos mucho de que hablar y mucho que hacer.", rewards = "Recibirás:\n\n"},
[2701] = {title = "Los héroes de los antiguos", objective = "Abre el cofre y reclama tu recompensa.", description = "Acepta esta ancestral reliquia. Y no nos sirve.\n\nQue te sea de provecho en tu lucha contra la Legión.", progress = nil, completion = "Estás cegado temporalmente por las centellas y el brillo.", rewards = "Recibirás:\n\nFragmento de los Pezuña Quebrada"},
[2702] = {title = "Los héroes de los antiguos", objective = "Habla con el cabo Thund Pezuña Quebrada.", description = "Me has honrado con tu nobleza y heroísmo, <nombre>.\n\nY también los has honrado a ellos...", progress = nil, completion = "Desde luego, has cumplido con todos nosotros. Mereces algo más que nuestro agradecimiento.", rewards = "Recibirás:\n\n"},
[2721] = {title = "Kirith", objective = "Averigua qué le ha ocurrido al teniente Kirith.", description = "Concluye un capítulo de tu aventura y comienza uno nuevo, <nombre>.\n\nUno de mis hombres ha desaparecido, perdido en algún lugar de Las Tierras Devastadas. Me refiero al teniente Kirith, claro.\n\nComo he mencionado antes, Allistarj nos capturó y torturó a ambos, pero al pobre Kirith le falló la fuerza de voluntad. Desde mi jaula podía oír los experimentos; los alaridos de su voz torturada recorrían cada recoveco de la caverna. Desde entonces no he vuelto a ver ni a oír a Kirith.", progress = nil, completion = "¿Trebor? ¿Te envía Trebor?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2741] = {title = "El Superhuevomático", objective = "El Superhuevomático es uno de los últimos inventos de Curgle Saltamanivela. Se trata de una máquina impresionante.\n\nEl Superhuevomático chisporrotea cuando te acercas al panel de control.", description = nil, progress = "El Superhuevomático es uno de los últimos inventos de Curgle Saltamanivela. Se trata de una máquina impresionante.\n\nEl Superhuevomático chisporrotea cuando te acercas al panel de control.", completion = "Colocas el huevo de hipogrifo en la máquina. Se oyen una serie de pitidos y de ruidos mecánicos extraños.\n\nParece que el Superhuevomático ha pesado, medido y empaquetado el huevo. ¡Eso sí que es un servicio rápido!", rewards = "Recibirás:\n\nCajón de huevos"},
[2742] = {title = "¡Capturaron a Rin'ji!", objective = "Ayuda a Rin'ji a salir de la Avanzada Quel'Danil y encuentra su secreto oculto en los Acantilados Dominantes.", description = "¡<raza>!\n\n¡Rin'ji perdió el hacha y fue capturado! Ayúdame, por favor. ¡Rin'ji sabe que no te gusta su gente, pero por favor! ¡Quiero salir!\n\nTengo algo para ti si me ayudas a escapar...\n\nAlgo secreto escondido en los Acantilados Dominantes.", progress = nil, completion = "Esta tablilla debe de ser el secreto del que hablaba Rin'ji. \n\nTiene unos símbolos extraños que no puedes descifrar.", rewards = "Recibirás:\n\n"},
[2743] = {title = "La cubierta de la Oscuridad", objective = "Vuelve junto al Héroe Caído de la Horda con tus hallazgos.", description = "Dejémonos de rodeos, <raza>; ando escaso de tiempo.\n\nDebes destruir la tríade de poderes que protege al señor demoníaco, Razelikh. No será una tarea tan sencilla como liberar a un alma maldita.\n\n<Kirith esboza una sonrisa.>\n\nVerás, cada subordinado de Razelikh lleva colgado del cuello un amuleto, una creación de Razelikh. Y no es otro que el amuleto necesario para conjurar al archidemonio.\n\nVuelve con Trebor y dile lo que hace falta para conjurar a Razelikh. Él te dará entonces el resto de las instrucciones.", progress = nil, completion = "El señor demoníaco hace gala de una astucia tan calculadora como su brutalidad, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2744] = {title = "El cazademonios", objective = "Habla con Loramus Thalipedes en Azshara.", description = "La tríade está protegida por Razelikh.\n\nA cambio de su servidumbre, Razelikh les concedió a cada uno poderes casi ilimitados sobre Las Tierras Devastadas. Cada pieza de amuleto está adaptada a la fuerza vital de su propietario y le confiere la inmortalidad. Mientras permanezcan en Las Tierras Devastadas no se les puede matar, su vida se extiende hasta el final de los días.\n\nSé de alguien que podrá ofrecerte más ayuda, <nombre>. Ve a Azshara y encuentra al cazademonios, Loramus Thalipedes. Busca en las islas.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2745] = {title = "Infíltrate en el castillo", objective = "Habla con Tyrion en el Castillo de Ventormenta.", description = "Mmm, una historia interesante, y bastante grave. Sí, puedo ayudarte, pero si quieres que esto salga bien, lo que necesitas es impartir una forma de justicia que no es... legal.\n\nHay que encargarse de Lescovar y Marzon y lo más rápido posible. ¿Crees que eres capaz de algo así?\n\nTú decides. Si es así, apresúrate al castillo y busca a un viejo amigo mío, un gnomo llamado Tyrion. Lleva un par de semanas vigilando a Lescovar. Debería poder ayudarte con esto.", progress = nil, completion = "¿Qué demo...? ¡No vuelvas a asustarme así! ¡Y baja la voz! En el nombre de las Sombras, ¿por qué demonios has tenido que acercarte tan sigilosamente? ¿No ves que tengo mucho trabajo, imbécil?\n\n¿Qué quieres decir? ¿Que qué estoy haciendo? Estoy esperando a que se abra el próximo Portal Oscuro, ¿qué te parece a ti que estoy haciendo? Di lo que tengas que decir y lárgate de aquí... antes de que los guardias nos vean a los dos aquí escondidos juntos en los matorrales. Tengo una reputación que mantener.", rewards = "Recibirás:\n\n"},
[2746] = {title = "Objetos importantes", objective = "Lleva 3 paños de seda y 2 Manzanas Frescas de Clara a Tyrion en Ventormenta.", description = "Trias quiere que adelantemos el plan, ¿eh? Muy bien, podemos hacerlo, pero todavía tengo que terminar de prepararme. Y, has de saber una cosa, yo me encargo de la parte difícil, pero tú eres quien se va a ensuciar las manos. Bien.\n\nDe acuerdo, necesito que me consigas un par de cosas antes de poder poner mi plan en marcha. Tengo el disfraz perfecto para que el robot espía pase por delante de esos guardias.\n\nTráeme seda y un par de manzanas. La seda la tienes que buscar, pero la pequeña granja de Clara, justo en las afueras de la ciudad, tiene las manzanas del tamaño perfecto.", progress = "Lleva un par de semanas vigilando a Lescovar. Trias nunca ha confiado en él, pero nosotros no sospechábamos que estaba involucrado con la Hermandad Defias. De hecho, pensábamos que tenía algo que ver con el Martillo Crepuscular. De cualquier forma, se ha vuelto tan peligroso que esta ciudad ya no puede contenerle, y ahí es donde entramos nosotros.\n\nConsígueme esos objetos y podremos poner en marcha mi plan.", completion = "Esto será perfecto, &lt;nombre&gt;. Buen trabajo.\n\nTengo lo demás que necesito en mi talega... gracias a la Luz que conozco a un sastre, para que me haga una bolsa de seda. Me estaba quedando sin espacio por tener que cargar con todas mis cosas de un lado para otro.\n\nPero antes de que aparezca el robot espía, hablemos de los detalles para que sepas lo que tienes que hacer.", rewards = "Recibirás:\n\n"},
[2747] = {title = "Un huevo muy especial", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCaja de regalitos"},
[2748] = {title = "Un huevo de primera", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCaja de hechizos"},
[2749] = {title = "Un huevo normal", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCaja de raciones"},
[2750] = {title = "Un huevo pasado", objective = "Estos huevos no tienen salvación; son huevos que nunca eclosionarán.", description = nil, progress = "Estos huevos no tienen salvación; son huevos que nunca eclosionarán.", completion = "Me temo que tengo malas noticias para ti, &lt;nombre&gt;. Este huevo no sirve. Te lo cojo, pero únicamente para que sea desechado como es debido.\n\nPor favor, no permitas que esto te desanime; no podemos salvarlos a todos. Si vuelves alguna vez a Feralas, inténtalo de nuevo... ¡y trae otro huevo para el Superhuevomático!", rewards = "Recibirás:\n\n"},
[2751] = {title = "Almenas barbáricas", objective = "Lleva 2 brazales, 2 espadas magnas y 2 zarpas afiladas de bronce grabado a Orokk Omosh a Orgrimmar.", description = "Este martillo ha pasado por 20 generaciones de Omosh. Hemos moldeado cien mil toneladas de metal. Por desgracia, todo se acabará con Orokk, la vergüenza de los Omosh: no puedo tener descendencia.\n\n¡Las recetas secretas de Omosh no pueden morir conmigo! Alguien tiene que aceptar este legado, aunque no sea Omosh.\n\nDemuestra tu valía, <nombre>. Tráeme dos brazales de bronce grabado, dos espadas magnas de bronce y dos garras afiladas. Entonces seguiremos.", progress = "Bronce, <nombre>. ¡Más bronce!", completion = "¡La leyenda de los Omosh perdurará!\n\nHas demostrado tu habilidad y tu premio será ver tus armas y armaduras bañadas con la sangre de tus enemigos.\n\n¡Que tus creaciones protejan a tus aliados y destruyan a tus enemigos!", rewards = "Recibirás:\n\nDiseño: peto de hierro barbárico"},
[2752] = {title = "Espaldares de hierro", objective = "Lleva 4 hachas de combate y 4 martillos de guerra de bronce a Orokk Omosh a Orgrimmar.", description = "Veo que tienes muchas ganas de aprender, <nombre>. Quieres absorber toda la sabiduría Omosh.\n\nTe daré tres lecciones.\n\nVuelve al yunque, empápate del calor de la forja y crea cuatro hachas de combate y cuatro martillos de guerra de bronce. Entonces te enseñaré a fabricar un espaldar de hierro.", progress = "Demuestra tu entrega, <raza>.", completion = "Me enorgulleces, &lt;nombre&gt;. ¡Lee y aprende!", rewards = "Recibirás:\n\nDiseño: hombreras de hierro barbárico"},
[2753] = {title = "¡Aplástalos!", objective = "Lleva 4 cascos, 4 brazales y 2 leotardos de hierro verde a Orokk Omosh a Orgrimmar.", description = "Solo te daré dos lecciones más, <nombre>. Sé que te duelen los músculos, pero hazlo por los Omosh.\n\nTráeme cuatro cascos, cuatro brazales y dos mallas, todo de hierro verde. ¡Así aprendes y perjudicas al enemigo!", progress = "¡Aplástales el cráneo con las botas!", completion = "Me recuerdas al jefe Gorokk, abuelo de Orokk. Lee y aprende.", rewards = "Recibirás:\n\nDiseño: botas de hierro barbárico"},
[2754] = {title = "Cuernos de frenesí", objective = "Lleva 2 almádenas de hierro macizo y 2 botas y 2 guanteletes de bronce plateado a Orokk Omosh a Orgrimmar.", description = "Esta es mi última lección, <nombre>. Regresa a la forja sagrada. Tráeme dos almádenas de hierro y dos botas y dos guanteletes de bronce plateado.\n\n¡Se morirán de miedo con solo verte!", progress = "¡Demuestra tu poderío con el hierro, <nombre>!", completion = "¡Increíble! ¡Te has integrado con los Omosh, &lt;nombre&gt;!", rewards = "Recibirás:\n\nDiseño: yelmo de hierro barbárico"},
[2755] = {title = "Las alegrías de los Omosh", objective = "Mira la danza de la alegría de los Omosh.", description = "No lloro, es que tengo algo en el ojo, <nombre>. \n\n<Se seca una lágrima.>\n\nTengo otro secreto de la familia Omosh que enseñarte. Moriré feliz gracias a ti.\n\nAprende la danza de la alegría de los Omosh y te enseñaré lo que sé de los puños de hierro.", progress = nil, completion = "&lt;Orokk jadea.&gt;\n\nLlevaba mucho tiempo sin bailar. Aprende la danza, &lt;nombre&gt;.", rewards = "Recibirás:\n\nDiseño: guantes de hierro barbárico"},
[2756] = {title = "Los viejos hábitos", objective = "Lleva 4 petos y 4 cascos de acero a Aturk el Yunque a Orgrimmar.", description = "En las leyendas, Aturk trituraba las calaveras de sus enemigos hasta convertirlas en un polvillo fino. Esos días ya se fueron, <raza>. El Jefe de Guerra pide la concordia entre los clanes. ¡Ahora solo se vigila a los enemigos de reojo!\n\nEs increíble, pero esta es la voluntad de Thrall.\n\n¡Pero basta de batallitas! Has venido a aprender y eso es lo que harás. Tráeme cuatro petos y cuatro cascos de acero y probarás el sabor de la guerra.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nEnseñar leotardos de combate orcos\n\nRecibirás:\n\n"},
[2757] = {title = "¡Bahía del Botín o nada!", objective = "Habla con MacGavan en Bahía del Botín.", description = "Mira, no vas a aprender nada si andas perdiendo el tiempo por aquí.\n\nConozco a un tipo que suele estar por la herrería de Bahía del Botín. Enséñale esta insignia y te enseñará todo lo que hay que saber del mithril.\n\n¿Qué me dices, eh? Ya es hora de que aprendas cosas nuevas, <nombre>.\n\nSi te animas, ve a ver a ese tipo, McGavan. Enséñale esto y así verá que vas en serio.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2758] = {title = "El origen de la herrería", objective = "Lleva seis brazales de escamas doradas a Hank el Martillo en Ventormenta.", description = "Cuando tenía más o menos tu edad, un enano llamado Galvan me acogió y me enseñó a forjar armaduras. Antes de partir a Bahía del Botín, me hizo prometer que instruiría a todos aquellos que sintieran una férrea determinación por aprender el oficio de la herrería. Así que, aquí estoy, muchos años después.\n\nAhora, ha llegado la hora de que aprendas algo. Hazme seis brazales dorados de escamas y aprenderás el significado de la herrería, <nombre>.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nDiseño: guanteletes de escamas de oro"},
[2759] = {title = "Buscando a Galvan", objective = "Habla con McGavan en la Bahía del Botín.", description = "Si bien Galvan se marchó de la Bahía del Botín muchos años atrás, todavía quedan en la ciudad otros miembros de la orden esperando encontrar, algún día, a alguien más digno de la tutela de Galvan.\n\nLleva esta insignia y preséntasela a McGavan en la Bahía del Botín.\n\nSi consigues llegar hasta allí, sabrá que eres <digno/digna>. ¡Tengo fe en ti, <nombre>!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2760] = {title = "El pedido de mitril", objective = "Habla con Galvan el Ancestro en la Vega de Tuercespina.", description = "La mayoría de nuestros reclutas nunca llegan a la Bahía del Botín, <nombre>. El solo resplandor de la insignia es suficiente para activar el ansia de botín de incluso los herreros más devotos. Son unos necios, la insignia es tan solo una gotita comparada con la riqueza de los objetos que verás.\n\nEncuentra a Galvan el Ancestro y enséñale este alfiler. Vive al norte, entre Zul Gurub y el Túmulo Ogro Mosh'Ogg.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2761] = {title = "Funde o no funde", objective = "Lleva cuarenta lingotes de mitril y cuarenta lingotes de veraplata a Galvan el Ancestro en Tuercespina.", description = "Muchos herreros creen que la minería y la herrería van por separado, son trabajos independientes. ¿Pero podría existir la herrería sin la minería? ¡Ajá, es imposible! El buen herrero debe ser también un buen minero.\n\nDemuestra a Galvan de lo que eres capaz, <nombre>. Tráeme cuarenta barras de hierro y cuarenta de mitril. A cambio, Galvan te enseñará el secreto de los pantalones de mitril ornamentado.", progress = "Con tanta excavación, te han salido músculos y te duele el cuerpo.", completion = "¡Tu ética de trabajo es impecable, &lt;nombre&gt;! Estás comenzando a comprender la mineralogía de Galvan. ¡Toma esto y aprende todo lo que puedas!", rewards = "Recibirás:\n\nDiseño: pantalones de mitril ornamentado"},
[2762] = {title = "La veraplata no es verídica", objective = "Lleva cuarenta lingotes de mitril y cinco lingotes de veraplata a Galvan el Ancestro en Tuercespina.", description = "Dicen que la veraplata es más fuerte y duradera que el mitril. Galvan está de acuerdo con esta valoración de los minerales pero, al contrario de su nombre, ¡la veraplata no es verídica! ¡El mitril es un mineral mucho más noble! ¡JA! A pesar de todo, para esta lección necesitarás encontrar al mentiroso mineral, <nombre>.\n\nTráeme cuarenta barras de mitril y cinco de veraplata. A cambio, aprenderás el secreto de los guantes de mitril ornamentado y, lo más importante, ¡aprenderás que la veraplata no tiene nada de verdad!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nDiseño: guantes de mitril ornamentado"},
[2763] = {title = "El arte de imbuir", objective = "Lleva cuarenta lingotes de mitril y cuatro citrinos a Galvan el Ancestro en Tuercespina.", description = "Recoger gemas preciosas es de gran importancia para nuestro oficio, <nombre>. Y pocas gemas son más preciosas que el citrino. Las propiedades mágicas de las que están imbuidos los objetos se amplifican enormemente con la presencia de citrino.\n\nPara esta lección, fortificaremos cuarenta barras de mitril con cuatro citrinos. El resultado será una mejor comprensión de la gemología mágica y el conocimiento para crear hombreras de mitril ornamentado.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nDiseño: hombro de mitril ornamentado"},
[2764] = {title = "El mejor alumno de Galvan", objective = "Habla con Trenton Mazaligera en Gadgetzan.", description = "Si estás <preparado/preparada> para finalizar tu instrucción con Galvan, deberías ir a Gadgetzan.\n\nAllí encontrarás a mi mejor alumno: Trenton Mazaligera. Puede enseñarte mucho sobre el mitril ornamentado. ¡Quédate y aprende!\n\nOh, ¡Gadgetzan está en Tanaris, por supuesto!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2765] = {title = "¡Experto herrero!", objective = "Reclama tu recompensa a Galvan el Ancestro.", description = "Cuando viniste por primera vez a verme, sabía que acabarías siendo uno de mis más brillantes alumnos. Le diste un abalorio de bastante poder a McGavan sin dudarlo. Y lo hiciste solo para poder conocerme. Esa fe ciega y devoción debe ser recompensada.\n\nHas conseguido ser <un experto/una experta> en herrería, <nombre>. ¡Ahora te esperan las recompensas!", progress = nil, completion = "Solo es justo que recibas un abalorio de mucho más poder que el que has sacrificado aquí. Utilízalo con buena salud, &lt;nombre&gt;.", rewards = "Recibirás:\n\nSello de Experiencia"},
[2766] = {title = "¡Encuentra a OOX-22/FE!", objective = "Lleva el localizador de emergencia al robot mensajero de Oglethorpe a una cueva que hay cerca de Vega Cicatriz Feral.", description = "Has descubierto un extraño instrumento metálico con forma de huevo. Jugueteas con uno de los botones del huevo y este cobra vida y se convierte en una especie de artilugio robótico gnomo. Del interior del huevo robótico sale una voz.\n\n\"Mi nombre es Oglethorpe Obnoticus, ¡y mi robot mensajero se ha averiado! Te recompensaré por su rescate. Por favor, lleva este localizador hasta el robot.\"\n\n\"He procesado las coordenadas del robot: se encuentra en una cueva, cerca de la Vega Cicatriz Feral.\"", progress = "Parece que has descubierto la carcasa de... ¿un pollo robótico? La voz que proviene del huevo vuelve de nuevo a la vida:\n\n\"¡Sí, un trabajo excelente! Este es sin duda mi robot mensajero, aunque mis sensores indican que necesita un empujón para que pueda volar de regreso a Bahía del Botín para ser reparado. Coloca el localizador en el interior del robot. ¡El localizador hará todo lo demás!\"", completion = "Colocas el huevo... el localizador de emergencia... dentro del pollo... del robot mensajero.\n\nOyes murmullos y zumbidos dentro del robot mientras se esfuerza por levantarse. Cuando el robot vuelve a la vida, oyes la voz de Oglethorpe una vez más, pero ahora proviene del interior del robot:\n\n\"¡OOX-22/FE está funcionando, pero necesita reparaciones! Pesa demasiado para que cargues con él, pero creo que tengo una idea. ¿Te apetece vigilarlo un poco más?\"", rewards = "Recibirás:\n\n"},
[2767] = {title = "¡Escolta a OOX-22/FE!", objective = "Escolta a OOX-22/FE al embarcadero de La Costa Olvidada y preséntate ante Oglethorpe Obnoticus en Bahía del Botín.", description = "Vuelves a oír la voz de gnomo que sale del robot:\n\n\"Tengo que llevar a OOX-22/FE a un lugar abierto y seguro en el que pueda iniciar el largo proceso de despegue. Tiene un posicionador de serie, pero necesito tiempo para que vuelva a estar operativo. Escolta al robot de su posición actual a, digamos, el embarcadero que hay en La Costa Olvidada. Es el lugar perfecto. Allí tendré tiempo para repararlo.\"\n\n\"Llévalo al embarcadero y luego ve a buscarme a Bahía del Botín. Oglethorpe Obnoticus.\"", progress = nil, completion = "¡Oh, eres el gran salvador de OOX-22/FE! ¡No sé ni cómo empezar a expresarte mi gratitud por tu ayuda! Aún necesito trabajar más en el desarrollo de mi serie de robots mensajeros, pero de momento, tu ayuda me ha ahorrado innumerables horas de reconstrucción, ¡y mucho dinero en costes de fabricación!\n\nPor favor, elige uno de estos objetos. Gracias de nuevo por tu valiosa ayuda, &lt;nombre&gt;, ¡mis robots y yo estamos en deuda contigo!", rewards = "Podrás elegir una de estas recompensas:\n\nExperimento volador fallidoFelpa eslabón\n\nTambién recibirás:\n\nExperimento volador fallidoFelpa eslabón"},
[2768] = {title = "Cetro Divinomático", objective = "Lleva el Cetro Divinomático al ingeniero jefe Silvaina en Gadgetzan.", description = "¡El sargento me ha robado! Dijo que solo lo cogía prestado, ¡pero me robó mi querido Cetro Divinomático! Sin el Cetro, ¿cómo puedo saber dónde hacer nuevos pozos?\n\n¡Encuentra a Bly y tráeme mi cetro! He oído que condujo a su panda de aventureros a buscar tesoros en Zul'Farrak, al Oeste. Seguro que lo encontrarás allí.\n\nEsperemos que los trols se hayan ocupado de él porque si tuvieras que combatir con él por el cetro será un combate realmente duro.", progress = "¿Encontraste al sargento Bly? ¿Tienes mi Cetro Divinomático?", completion = "¡Lo encontraste! ¡Bien hecho, &lt;nombre&gt;! ¿Tuviste que luchar con Bly para conseguirlo? Espero que le dieras bien fuerte a él y a toda su banda.", rewards = "Podrás elegir una de estas recompensas:\n\nAnillo de fraternidad masonaCelada de hermandad de ingeniero\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAnillo de fraternidad masonaCelada de hermandad de ingeniero"},
[2769] = {title = "Los hermanos Pernolatón", objective = "Habla con Wizzle Pernolatón en El Desierto de Sal.", description = "¿Conoces a los hermanos Pernolatón? Están haciendo carreras contra los goblins en su coche cohete en El Desierto de Sal, en Kalimdor. Suelen ganar, lo que es normal: ¡el coche de los goblins es tan malo que apenas llega a cruzar la línea de meta!\n\nAun así, los Pernolatón siempre quieren mejorar su velocidad media y Wizzle me dijo que necesita una pieza muy rara para su próximo experimento.\n\nNecesitará a alguien que vaya a buscarla. Habla con él.", progress = nil, completion = "¿Vienes a ayudar? ¡Genial!\n\nNecesito una pieza para un experimento y me hace falta alguien valiente como tú para conseguirla.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2770] = {title = "Gahz'rilla", objective = "Llévale la escama electrificada de Gahz'rilla a Wizzle Pernolatón, que está en El Desierto de Sal.", description = "En las profundidades de Zul'Farrak, la ciudad de arena de los trols de Tanaris, se encuentra una poza sagrada en la que los trols invocan a una bestia gigante... ¡Gahz'rilla! Es tan violento que hasta sus escamas crepitan con energía. ¡Esa es la energía que busco para mi coche!\n\n¡Tráeme la escama electrificada de Gahz'rilla!\n\nPara ello tendrás que invocar a Gahz'rilla y ese ritual es un secreto bien guardado por los trols. Para enfrentarte a él, primero tienes que arrebatarles el secreto.", progress = "¿Tienes la escama? ¡Estoy deseando averiguar cómo aprovechar su energía!", completion = "¡Vaya, has conseguido la escama! Gracias, &lt;nombre&gt;. ¡Estoy deseando ponerme manos a la obra con ella!\n\nAsí que has visto a Gahz'rilla... ¿¿Es tan grande como dicen??", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nZanahoria pinchada en un palo"},
[2771] = {title = "La cabeza sobre los hombros", objective = "Llévale un par de tocas de mitril y una hombrera de mitril ornamentado a Trenton Mazaligera.", description = "Parece como si el maestro lo tuviera todo planeado. A mí no me enseñó a crear los objetos que te enseñó a crear a ti, y a ti no te enseñó a crear los objetos que me enseñó a crear a mí.\n\nEl maestro es sabio, <nombre>. ¡Podremos instruirnos el uno al otro!\n\nEnséñame a hacer una toca de mitril y hombreras de mitril ornamentado y yo te enseñaré a crear un casco de mitril ornamentado.", progress = "¿Has acabado de confeccionar los objetos, <nombre>?", completion = "¡Excelente! Me parece un trato justo, modestia aparte. ¡Mira y aprende, &lt;nombre&gt;!", rewards = "Te van a lanzar el siguiente hechizo:\n\nEnseñar a decorar yelmo de mitril\n\nRecibirás:\n\n"},
[2772] = {title = "El mundo a tus pies", objective = "Llévale un par de botas de mitril pesado y unos pantalones de mitril ornamentado a Trenton Mazaligera.", description = "Durante el Campeonato de herrería del valle del año pasado, todos los que estaban atentos vieron cómo un cobarde de la Fundición de Ventura y Cía. vino directo a mi pierna. Sin embargo, lo que no saben es la razón por la cual pude continuar y finalmente ganar el torneo. ¡Eso es, botas de mitril ornamentado!\n\nSin embargo, tengo que aprender a hacer pantalones de mitril ornamentado para el campeonato de este año pues participa un sabueso de Ventura y Cía. ¡Enséñame a hacer los pantalones y yo te enseñaré a hacer las botas!Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nEnseñar a decorar botas de mitril\n\nRecibirás:\n\n"},
[2773] = {title = "El chico de mitril", objective = "Llévale un par de petos de mitril pesado y unos guantes de mitril ornamentado a Trenton Mazaligera.", description = "Por aquí me conocen como el chico de mitril. Yo abastezco de mitril ornamentado a todos los aventureros del mundo.\n\n<Trenton susurra.>\n\nPocos saben que desconozco la técnica de creación de guantes de mitril ornamentado. ¡Sh! Este será nuestro pequeño secreto.\n\n<nombre>, enséñame la técnica para crear guantes de mitril ornamentado y, a cambio, yo te mostraré el resultado de la culminación de mi instrucción a las órdenes de Galvan: el peto de mitril ornamentado.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nEnseñar a decorar peto de mitril\n\nRecibirás:\n\n"},
[2781] = {title = "SE BUSCA: Caliph Escorpijón", objective = "Llévale la cabeza de Caliph Escorpijón al ingeniero jefe Silvaina, que está en Gadgetzan.", description = "SE BUSCA: Caliph Escorpijón.\n\nLa compañía de aguas de Gadgetzan en Tanaris ofrece una generosa recompensa por la cabeza de Caliph Escorpijón, el líder de los Deambuladores. Entre los crímenes que se le atribuyen se incluyen:\n\n1. ¡El asesinato de empleados de la compañía de aguas de Gadgetzan!\n2. ¡El robo de recursos de la compañía de aguas de Gadgetzan!\n3. ¡El allanamiento de las instalaciones de la compañía de aguas de Gadgetzan!\n\nEntrega una prueba del fallecimiento de Caliph Escorpijón al ingeniero jefe Silvaina y recibirás una recompensa al momento.", progress = "Sí <clase>, yo soy un oficial de la compañía de aguas de Gadgetzan. ¿En qué puedo ayudarte?", completion = "¡Al fin se ha hecho justicia! ¡Has asestado un duro golpe a esos infames nómadas! La muerte de Escorpijón será clave para expulsar finalmente a los que se están aprovechando de los pozos de agua de Gadgetzan. \n\nPor la autoridad que me confiere la compañía de aguas de Gadgetzan, te hago entrega de tu recompensa. Nos has prestado un gran servicio a todos nosotros.", rewards = "Recibirás:\n\n"},
[2782] = {title = "El secreto de Rin'ji", objective = "Oran Contorpiente es una conocida erudita en los trols Secacorteza; quizá pueda descifrar la tablilla. Vive en el Barrio de la Magia de Entrañas.", description = "En la tablilla se ven grabados de los trols Cortezaseca. El secreto de Rin'ji es todo un misterio...\n\nPuede que esto sea muy valioso. Por otra parte, también puede que no lo sea. Un Cortezaseca en peligro probablemente haría cualquier cosa con tal de escapar.", progress = "Vaya, ¿qué es esto que me traes?", completion = "Qué tablilla tan peculiar... ¿Dónde la encontraste, &lt;nombre&gt;?", rewards = "Recibirás:\n\n"},
[2783] = {title = "Peleas mezquinas", objective = "Habla con el Héroe Caído de la Horda donde Las Tierras Devastadas lindan con El pantano.", description = "Luchamos contra el enemigo equivocado, <nombre>.\n\nEl verdadero poder de estas tierras continúa creciendo en fuerza y número mientras nos peleamos con los orcos por estos terrenos moribundos.\n\n¿No te parece que estamos librando una batalla contra la Horda para un buen día terminar exterminados por los ejércitos de la Legión Ardiente? Es futil.\n\nDebemos dejar las diferencias de lado, <nombre>, así que habla con el Héroe Caído de la Horda. Él sabe la verdad que se oculta detrás de las Tierras Devastadas.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2784] = {title = "Pérdida de honor", objective = "Escucha la historia del Héroe Caído de la Horda.", description = "Esta es mi historia, <raza>.", progress = nil, completion = "Como espíritu, soy impotente, &lt;nombre&gt;. Ayúdame. Permíteme que recupere mi honor.", rewards = "Recibirás:\n\n"},
[2801] = {title = "Un historia triste", objective = "Escucha la historia del Héroe Caído de la Horda.", description = "Antes de que podamos proseguir, debes oír mi historia.", progress = nil, completion = "Ha llegado la hora de actuar, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2821] = {title = "La marca de la calidad", objective = "Lleva 10 pellejos gruesos de yeti a Pratt McGrubben al Bastión Plumaluna.", description = "Tengo mi filosofía: en peletería, la calidad del trabajo debe ser la mejor posible, sin excusas. Cualquiera que te diga que la calidad no es importante, está intentando venderte un chalé en primera fila de playa en Dun Morogh.\n\nSiempre estoy buscando materiales de primera calidad y las pieles de yeti son mi gran hallazgo. Los yetis son criaturas de Feralas. Se encuentran muchos en la Vega Cicatriz Feral. Quiero algunas pieles. Te daré uno de mis artículos de cuero si me traes 10. Si te parece justo, tenemos un trato.", progress = "Si me traes 10 pieles gruesas de yeti, te daré uno de mis artículos a elegir. Y como valoro la calidad, ¡puedo asegurarte de que será un buen artículo!\n\nSi no sabes dónde encontrar yetis, puedo indicarte un lugar en el que podrían estar. Busca tierra adentro desde La Costa Olvidada, por la Vega Cicatriz Feral.", completion = "¿Te paraste a examinar la textura de estos pellejos? Con el tratamiento adecuado, estos pellejos podrían ser más fuertes que el hierro. No mucha gente sabe de la existencia de estas pieles. Eso, combinado con los tratamientos que solo yo conozco, me permitirá crear objetos únicos, con un sello de calidad exclusivo y auténtico... ¡gracias a ti, claro!\n\nY ahora, por favor elige algo de lo que puedo ofrecerte por tus servicios.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas hechas a mano de PrattGuantes hechos a mano de Pratt\n\nTambién recibirás:\n\nBotas hechas a mano de PrattGuantes hechos a mano de Pratt"},
[2822] = {title = "La marca de la calidad", objective = "Lleva 10 pellejos gruesos de yeti a Jangdor Zancoveloz al Campamento Mojache.", description = "Me enorgullezco de mi trabajo de peletería y creo que jamás se debería renunciar a la calidad. Cualquiera que te diga que la calidad no es importante, ¡intentará también convencerte de que la tierra no tiene secretos!\n\nSiempre estoy buscando materiales de primera calidad y en mi búsqueda he descubierto las pieles de yeti. Los yetis son nativos de Feralas y a menudo se encuentran al oeste de aquí, en la Vega Cicatriz Feral. Te dejaré elegir uno de mis artículos de cuero si me traes 10 pieles. Si te parece una buena oferta, ¡tenemos un trato!", progress = "¿Has conseguido reunir 10 pieles gruesas de yeti? Mis artículos de cuero son insuperables y, si tienes las pieles, compartiré contigo una muestra de mi trabajo.\n\nSi no sabes dónde encontrar yetis, puedo indicarte un lugar en el que podrían estar. Busca al oeste de aquí, tierra adentro desde La Costa Olvidada, por la Vega Cicatriz Feral.", completion = "¡Excelente, excelente! Cuando les dé mi tratamiento especial, estos pellejos serán tan resistentes como el hierro, ¡te lo digo yo! Tendré que hacer muchas pruebas para conseguir la mezcla correcta para curtirlas, pero sé que cuando lo consiga, mis productos tendrán una calidad superior y exclusiva.\n\nY, volviendo a ti, &lt;nombre&gt;... tu ayuda ha sido valiosísima. Por favor, elige uno de estos objetos que he creado.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas hechas a mano de JangdorGuantes hechos a mano de Jangdor\n\nTambién recibirás:\n\nGuantes hechos a mano de JangdorBotas hechas a mano de Jangdor"},
[2841] = {title = "Las guerras de la plataforma", objective = "Consigue la combinación de la caja fuerte de Termochufe en Gnomeregan y lleva los planos de la plataforma a Nogg a Orgrimmar.", description = "Hasta hace dos años, la tecnología gnómica solo servía para echarse unas risas. Las cosas han cambiado mucho; han construido una plataforma mejor que todas las nuestras juntas. ¡Quiero los planos!\n\nEstán en una caja fuerte cerca del mekigeniero Termochufe. Para empeorar las cosas, él es el único que sabe la combinación.\n\nHaz lo que sea por conseguirla, <nombre>. ¡Tienes que abrir la caja y traerme los planos!", progress = "¿Traes los planos o has venido solo a irritarme?", completion = "¡Sabía que lo conseguirías! Sovik tenía sus dudas...\n\n&lt;Nogg carraspea.&gt;\n\nAhora que tenemos los planos, podremos estudiar las innovaciones gnómicas de la plataforma y adaptarlas al estilo goblin.", rewards = "Podrás elegir una de estas recompensas:\n\nTogas civinadPeto de corredorLeotardos duales reforzados\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTogas civinadPeto de corredorLeotardos duales reforzados"},
[2842] = {title = "Scooty, ingeniero jefe", objective = "Habla con Scooty en Bahía del Botín.", description = "En cuanto Kernobee nos avisó de lo de la plataforma gnómica, Scooty y yo empezamos a trabajar en el Transpoliportador 6000. Es el medio más seguro y rápido para ir a Gnomeregan.\n\nYa está a punto para despegar. Si quieres probarlo, ve a Bahía del Botín y habla con Scooty en la alcándara de jinete de Viento. Para ir a Bahía del Botín tienes que coger el barco en Trinquete.\n\nPor supuesto, puedes ir a Gnomeregan cruzando las montañas de Dun Morogh, que están infestadas de enanos.", progress = nil, completion = "Magnífico, ¿verdad? Es lo mejor de la tecnología goblin, solo ha explotado una docena de veces.\n\nNo te preocupes, &lt;nombre&gt;, ya está areglado y no explotará. O si lo hace, al menos no te enterarás de nada.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2843] = {title = "¡Gnomer-yaaaaa!", objective = "Espera a que Scooty calibre el transpondedor goblin.", description = "Mientras tengas el transpondedor, todo lo que necesitas hacer es colocarte sobre la plataforma y serás <teletransportado/teletransportada> a Gnomeregan.\n\nQuizás experimentes algunos extraños efectos secundarios, pero ninguno será permanente... al menos ahora.\n\nCasi me olvido de lo más importante, la vuelta. Cuando quieras volver, colócate en la plataforma del Transpoliportador en Gnomeregan.\n\nTienes que calibrar el transpondedor según tu talla y peso.", progress = nil, completion = "¡Listo! ¡Cuando estés preparado, súbete a la plataforma!", rewards = "Recibirás:\n\nTranspondedor goblin"},
[2844] = {title = "El guardián gigante", objective = "Localiza a la elfa de la noche perdida.", description = "No valgo para nada, <nombre>. Se supone que tenía que vigilar a mi amiguita Shay y, no sé cómo, se me perdió. No sé muy bien qué ocurrió, pero la última vez que la vi estábamos andando por las ruinas que hay al norte de aquí.\n\nIba todo bien. Y de repente, me giro... ¡y ella ya no estaba!\n\nNo sé qué hacer... ¿Alguna idea, <nombre>? Estoy terriblemente preocupado.\n\n¡Espera! ¡Tú puedes ir a buscar a Shay! Con tus piernas, irás más rápido que yo.", progress = nil, completion = "¡Ay, cómo me alegro de que me hayas encontrado! Porque estoy un poco perdida... Pero, al menos, este lugar es bonito...", rewards = "Recibirás:\n\n"},
[2845] = {title = "Las andanzas de Shay", objective = "Coge la campana de Shay del cofre.\n\nEscolta a Shay Correhoja al campamento de Muerdepiedras.", description = "¿Puedes ayudarme a encontrar a mi amigo Muerdepiedras? Él me estaba cuidando... Pero tengo la mala costumbre de perderme de vez en cuando. Hay tantas cosas que ver que me distraigo y me pierdo por ahí.\n\nPero hay algo que siempre consigue llamar mi atención.\n\nDentro de este cofre encontrarás una campana que para mí es muy valiosa. Me encanta cómo suena. Te prometo que si me alejo y tú tocas la campana, volveré corriendo.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nMandiletes de granitoCincho seto\n\nTambién recibirás:\n\nMandiletes de granitoCincho seto"},
[2846] = {title = "Tiara de la Profundidad", objective = "Lleva la Tiara de la Profundidad a Tabetha en el Marjal Revolcafango.", description = "Hace mucho tiempo tenía una bonita joya, la Tiara de la Profundidad. No solo era hermosa... tenía un gran poder para quien sabía usarlo.\n\nPor eso, cuando la hidromántica Velratha oyó hablar de ella quiso tenerla. Envío a sus agentes a mi hogar y me la robaron cuando estaba fuera. ¡Ladrones!\n\n¡Quiero recuperar mi tiara! Ve a Zul'Farrak, la ciudad trol de Tanaris, encuentra a Velratha y quítale la tiara. Cuando la tengas vuelve a verme y te ganarás mi favor.", progress = "¿Tienes la tiara, <nombre>? ¿Ha aprendido ya Velratha cuál es el precio de contrariarme?", completion = "¡Maravilloso! ¡La encontraste! Y lo que es más importante... ¡Velratha ya no la tiene! Gracias, &lt;nombre&gt;. ¡Te estaré siempre en deuda!\n\nY si antes te pareció que tenía algo de mal humor... no hagas caso. Verás que soy mucho más agradable con aquellos que no me han robado nada.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nVara de tramoyista de hechizosEspaldares de pizarra"},
[2847] = {title = "Armadura de cuero salvaje", objective = "Lleva 10 cueros gruesos a Pratt McGrubben al Bastión Plumaluna.", description = "¡<nombre>, te estás convirtiendo en <un peletero/una peletera> bastante <bueno/buena>! Yo conozco algunos patrones que podrían interesarte... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para alguien de tu habilidad, te lo garantizo.\n\nTe enseñaré el patrón, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2848] = {title = "Hombreras de cuero salvaje", objective = "Lleva 6 complementos gruesos para armadura y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.", description = "Las hombreras de cuero salvaje ofrecen una protección de primera a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.\n\nPara este patrón necesitaré 6 conjuntos de armadura gruesa y 1 atriplex salvaje. No deberías tener problemas para conseguirlos.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPatrón: hombreras de cuero salvaje"},
[2849] = {title = "Jubones de cuero salvaje", objective = "Lleva 2 petos de caparazón de tortuga, 2 guantes de caparazón de tortuga y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.", description = "Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.\n\nPara este patrón, necesitaré 2 petos de caparazón de tortuga, 2 pares de guantes de caparazón de tortuga y 1 atriplex salvaje. Son bastantes cosas, pero no debería resultarte difícil proporcionármelas.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPatrón: jubón de cuero salvaje"},
[2850] = {title = "Casco de cuero salvaje", objective = "Lleva 2 túnicas Nochefugaz, 2 cintas de pelo Nochefugaz y 1 atriplex salvaje a Pratt McGrubben al Bastión Plumaluna.", description = "Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, cuando se integra la atriplex salvaje al casco, esta le imbuye una potente mejora aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!\n\nPara este patrón necesitaré 2 casacas Nochefugaz, 2 cintas de pelo Nochefugaz y 1 atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPatrón: casco de cuero salvaje"},
[2851] = {title = "Botas de cuero salvaje", objective = "Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.", description = "Has aprendido bastante hasta ahora, <nombre>; pero aún tienes que aprender más. Lo siguiente serán las botas de cuero salvaje, la piedra angular de cualquier conjunto de armadura.\n\nEstas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.\n\nPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas todo esto, házmelo saber.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPatrón: botas de cuero salvaje"},
[2852] = {title = "Leotardos de cuero salvaje", objective = "Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Pratt McGrubben al Bastión Plumaluna.", description = "Una vez asentadas las bases, ya puedes aprender algo más complejo, <nombre>. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.\n\nPara este patrón necesito 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPatrón: leotardos de cuero salvaje"},
[2853] = {title = "Maestro de cuero salvaje", objective = "Entrega la carta de Pratt a Telonis, el maestro curtidor de Darnassus.", description = "Vaya <nombre>, eres la diligencia personificada. Dominas casi todos los patrones de la armadura de cuero salvaje. Solo te falta uno. Y no te haré trabajar para conseguirlo. Porque ya te lo has ganado, pero... no puedo enseñártelo yo. Mi profesor, el que me inició en la peletería, es quien debe recompensarte.\n\nLleva esta carta a Telonis; enseña peletería en Darnassus. Muéstrale la carta y te tratará como a <un/una> igual, como hago yo.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPatrón: capa de cuero salvaje"},
[2854] = {title = "Armadura de cuero salvaje", objective = "Lleva 10 cueros gruesos a Jangdor Zancoveloz al Campamento Mojache.", description = "<nombre>, tu habilidad como <peletero/peletera> te precede. Yo poseo un conocimiento de la peletería que harías bien en aprender... Por ejemplo, ¡el de la armadura de cuero salvaje! El cuero salvaje aprovecha las potentes y caóticas propiedades de la atriplex salvaje que se encuentra en áreas de exuberante vegetación y en algunas criaturas de la región. Las armaduras que se forjan con ella son las mejores para <un artesano/una artesana> de tu habilidad, te lo garantizo.\n\nTe enseñaré a crearla, pero tendrás que trabajar para mí durante un tiempo. Para empezar, tendrás que traerme 10 piezas de cuero pesado. Ya detallaremos algo más después.", progress = "Dejemos una cosa clara: tú serás mi aprendiz mientras aprendes el arte de la armadura de cuero salvaje. La atriplex salvaje es poderosa pero también caótica, por eso imbuye una gran mejora aleatoria a la armadura que harás, que de por sí, será fuerte. Has de saber que a mí no me fue fácil adquirir este conocimiento.\n\nEl coste inicial para empezar este proceso es de 10 piezas de cuero pesado.\n\nUna vez reunidas, empezaremos con el trabajo que tendrás que hacer para obtener los patrones.", completion = "Bien, &lt;nombre&gt;, me alegro de tenerte como aprendiz. Has tomado una buena decisión como peletero, quienes llevan armaduras de cuero te aclamarán cuando domines el arte del cuero salvaje.\n\nPongámonos a trabajar, ¿por dónde quieres empezar?", rewards = "Recibirás:\n\n"},
[2855] = {title = "Hombreras de cuero salvaje", objective = "Lleva 6 complementos gruesos para armadura y 1 atriplex salvaje a Jangdor Zancoveloz al Campamento Mojache.", description = "Las hombreras de cuero salvaje ofrecen una gran protección a la parte superior de los brazos y a los hombros de quien las lleva. Como ocurre con la armadura de cuero salvaje, cuando se cose la atriplex salvaje a las hombreras, esta les imbuye una mejora potente aleatoria.\n\nPara este patrón necesitaré 6 conjuntos de armadura gruesa y una atriplex salvaje. No deberías tener problemas para conseguirlos.", progress = "¿Tienes los 6 conjuntos de armadura gruesa y la atriplex salvaje que necesito?\n\nPiensa que haciendo conjuntos de armaduras aprendes a trabajar de forma fiable y regular. El método que desarrollas mientras manejas estos conjuntos, te ayuda a prepararte para el intrincado trabajo de elaboración de una armadura de cuero salvaje.\n\nCada lección tiene un propósito, <nombre>. Es importante sacar provecho de todas.", completion = "Estos complementos para armadura son de gran calidad, &lt;nombre&gt;. Si hay algo que exijo en mi trabajo y en el de quienes tutelo, es calidad.\n\nHas hecho bien en aprender este patrón, espero que te sea tan provechoso como a mí.", rewards = "Recibirás:\n\nPatrón: hombreras de cuero salvaje"},
[2856] = {title = "Jubones de cuero salvaje", objective = "Lleva 2 petos de caparazón de tortuga, 2 guantes de caparazón de tortuga y 1 atriplex salvaje a Jangdor Zancoveloz al Campamento Mojache.", description = "Los jubones de cuero salvaje son la pieza principal de cualquier conjunto de armadura de cuero salvaje. La tela del jubón se amalgama con la atriplex salvaje para crear un producto que ofrece la máxima protección para el torso a la vez que permite que su potente magia se abra paso a través de ella.\n\nPara este patrón, necesitaré 2 petos de caparazón de tortuga, 2 pares de guantes de caparazón de tortuga y 1 atriplex salvaje. No es una lección fácil, pero tú tienes sin duda la habilidad necesaria para aprenderla.", progress = "¿Tienes ya los 2 petos de caparazón de tortuga y los guantes? ¡Y no olvides la atriplex salvaje!\n\nCon los primeros patrones ya aprendemos que la esencia de la forma se traslada perfectamente a prendas más difíciles. La forma del peto de caparazón de tortuga te prepara, a través de diferentes objetos, para el detalle con el que hay que trabajar para crear un jubón de cuero salvaje.\n\nA medida que progreses en tus lecciones, las habilidades que te voy enseñando se convertirán en arte.", completion = "Estas son unas piezas estupendas, &lt;nombre&gt;. El trabajo y el cuidado con el que han sido creadas demuestran que has elegido dedicarte por entero a estas tareas.\n\nMe has traído lo que te pedí, así que, aquí tienes tu recompensa. ¡Ya posees el conocimiento para crear jubones de cuero salvaje!", rewards = "Recibirás:\n\nPatrón: jubón de cuero salvaje"},
[2857] = {title = "Casco de cuero salvaje", objective = "Lleva 2 túnicas Nochefugaz, 2 cintas de pelo Nochefugaz y 1 atriplex salvaje a Jangdor Zancoveloz al Campamento Mojache.", description = "Un casco de cuero salvaje ofrece a quien lo lleva una gran protección de las áreas vitales de la cabeza sin sacrificar el confort. Como en todas las partes de las armaduras de cuero salvaje, la atriplex salvaje integrada en el casco, le imbuye una potente mejora mágica aleatoria. ¡Quienes aprecian el cuero, sin duda desearán tener esta pieza!\n\nPara este patrón necesitaré 2 casacas Nochefugaz, 2 cintas de pelo Nochefugaz y 1 atriplex salvaje. ¡Si me traes eso, el patrón será tuyo!", progress = "Necesito esas casacas y esas cintas de pelo y también necesito la atriplex salvaje antes de compartir contigo el patrón del casco de cuero salvaje.\n\nLos complementos para la cabeza son muy codiciados por los aventureros, desde la cinta de pelo más simple hasta el casco más integral. Las bases de la creación de complementos para la cabeza son siempre las mismas, independientemente del aspecto estético del complemento.", completion = "El equipamiento Nochefugaz que me has traído es perfecto, &lt;nombre&gt;. No esperaba menos de una peletera de tu calibre.\n\nTe has ganado el honor de crear cascos de cuero salvaje. Te doy este patrón de buen grado y espero que te beneficies de él como mereces.", rewards = "Recibirás:\n\nPatrón: casco de cuero salvaje"},
[2858] = {title = "Botas de cuero salvaje", objective = "Lleva 2 pantalones Nochefugaz, 2 botas Nochefugaz y 2 atriplex salvajes a Jangdor Zancoveloz al Campamento Mojache.", description = "Tu conocimiento de la armadura de cuero salvaje crece, <nombre>; pero aún te quedan cosas que aprender.\n\nLas botas de cuero salvaje son la piedra angular de cualquier conjunto de armadura. Estas botas se crean para envolver al pie en confort a la vez que lo refuerzan para protegerlo de los peligros de una vida aventurera. Sus suelas blandas son una bendición para quienes necesitan moverse con sigilo.\n\nPara este patrón necesito 2 pantalones Nochefugaz y 2 botas Nochefugaz. Y también necesito 2 atriplex salvajes, no me basta con una. Cuando tengas estos objetos, procederemos.", progress = "Tienes que entregarme los pantalones, las botas y las plantas de atriplex salvaje para que te enseñe el patrón, <nombre>. ¿Lo tienes todo?\n\nYa cuando creamos nuestro primer par de botas entendemos que unas botas de calidad tienen que lograr un equilibrio perfecto entre confort y funcionalidad. Si lo conseguimos, creamos objetos que permiten que quien los lleva pueda ignorar la fatiga que atenaza los pies de quienes llevan botas de peor calidad.", completion = "Una vez más, has creado unos objetos dignos de un profesional experimentado. Te has ganado este patrón y mi enhorabuena.", rewards = "Recibirás:\n\nPatrón: botas de cuero salvaje"},
[2859] = {title = "Leotardos de cuero salvaje", objective = "Lleva 2 yelmos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 atriplex salvajes a Jangdor Zancoveloz al Campamento Mojache.", description = "Ya estás <listo/lista> para lecciones más complejas, <nombre>. Los leotardos de cuero salvaje son uno de los artículos de cuero de mayor solidez y potencia. Ofrecen una increíble protección a la cintura, las pantorrillas y a otros órganos vitales. Si los combinas con la mejora que proporciona la atriplex salvaje, los aventureros que los vistan te lo agradecerán cada vez que te vean.\n\nPara que te enseñe este patrón, tienes que traerme 2 cascos de caparazón de tortuga, 2 brazales de caparazón de tortuga y 2 plantas de atriplex salvaje.", progress = "¿Tienes los brazales y los cascos de caparazón de tortuga listos? Y no olvides las 2 plantas de atriplex salvaje que necesito.\n\nYa empiezas a darte cuenta de que la armadura de cuero salvaje alberga un poder potencial. Una armadura que no tiene fronteras de aplicación no tiene precio para un peletero. Los límites están solo en la cantidad de recursos que se utilizan para hacer los objetos, ¡no en la calidad de los productos acabados!", completion = "Una vez más, has cumplido a la perfección la tarea que te asigné. Quizás un día sea yo quien acuda a ti en busca de conocimiento, ¿eh, &lt;nombre&gt;?\n\nAquí tienes el patrón para los leotardos, con mi agradecimiento. ¡Suma este conocimiento a tus impresionantes habilidades e imprímelo en todas tus creaciones!", rewards = "Recibirás:\n\nPatrón: leotardos de cuero salvaje"},
[2860] = {title = "Maestro de cuero salvaje", objective = "Entrega la carta de Jangdor a Una, el maestro curtidor de Cima del Trueno.", description = "<nombre>, ya has aprendido todo lo que yo puedo enseñarte. Pero te queda por aprender un último patrón de la armadura de cuero salvaje. No te pediré que trabajes más, pues ya te has ganado el derecho de aprender este patrón. Pero no es un patrón que sea libre de compartir contigo.\n\nMi profesor, el que me inició en la peletería, es quien debe enseñártelo. Lleva esta nota a Una. Enseña peletería en Cima del Trueno. Muéstrale la carta y te tratará como a <un/Una> igual, como hago yo.", progress = "Saludos, <raza>... veo que eres peletera, como yo. ¿Has venido a aprender de mí o quieres alguna otra cosa?", completion = "Ah, sí, te envía Jangdor. Me congratula saber que uno de mis mejores estudiantes tiene ahora su propia aprendiz.\n\nSu dedicación y su talento con el cuero son impresionantes. Si él te recomienda en su carta, no necesito saber más, te doy la bienvenida con todo mi respeto.\n\nSé que te ha enseñado el arte del cuero salvaje. Permite que acabe tu instrucción con este patrón. Haz un buen uso de él. Somos pocos los que conocemos los secretos de la capa de cuero salvaje.", rewards = "Recibirás:\n\nPatrón: capa de cuero salvaje"},
[2861] = {title = "La tarea de Tabetha", objective = "Habla con Tabetha en el Marjal Revolcafango.", description = "Se rumorea que Tabetha, una compañera maga que vive en las profundidades del Marjal Revolcafango, tiene que arreglar cuentas con un trol de arena de Zul'Farrak. Si hablas con Tabetha te dirá más.\n\nY lleva contigo algunos amigos, <nombre>. Si Tabetha quiere que vayas a Zul'Farrak no querrás ir <solo/sola>.\n\nEncontrarás la cabaña de Tabetha al oeste de Theramore, al norte de las Ruinas Quebrantarrocas.", progress = nil, completion = "Es cierto. Tengo unas cuentas que arreglar con un trol. Algunas cuentas. ¡Todas!\n\n¿Quieres ayudarme?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2862] = {title = "En guerra con los Zarpaleña", objective = "Lleva 10 cabelleras de los gnolls Zarpaleña a Hadoken Zancoveloz al Campamento Mojache.", description = "Los gnolls... son algo más que una molestia. ¡Son una amenaza constante para este campamento! Que sean o no de Feralas es irrelevante. Si no redoblamos nuestros esfuerzos por controlarlos, acabarán por expulsarnos de la región entera.\n\nLos ataques de los gnolls al campamento se han intensificado últimamente. Necesitamos que nos ayudes a reducir su número. Tráeme 10 de sus cabelleras. Recompensaré generosamente tu esfuerzo.\n\n¡Ve!", progress = "¡Los hacemos retroceder y aún así, siguen atacando! Alguien o algo debe de estar arrastrándoles a este frenesí... Ah, <nombre>, ¿ya estás de vuelta? ¿Tienes las cabelleras necesarias para la recompensa?", completion = "Esto es un buen comienzo, &lt;nombre&gt;. Tus habilidades de combate son encomiables.\n\nY no eres el primero al que he asignado esta misión. Muchos antes que tú han combatido contra ellos. Nuestros esfuerzos los han mantenido a raya hasta ahora. Pero últimamente, sus intentos de avanzar hacia el norte por nuestra tierra, se han redoblado. Y sus ataques son cada vez más salvajes... casi como si estuvieran desesperados.\n\nPero yo tengo un plan que podría solucionar esto de una vez por todas.", rewards = "Recibirás:\n\n"},
[2863] = {title = "Ataca a los Alfas", objective = "Mata a 5 Alfas Zarpaleña y vuelve con Hadoken Zancoveloz en 1 hora.", description = "El plan es para varios líderes de los gnolls, esos que se hacen llamar Alfas. Para acabar con ellos en rápida sucesión. Si logramos ejecutarlo con la rapidez y precisión necesarias, haríamos que cundiera el caos entre sus filas. Y quizás conseguiríamos que en esas estúpidas cabezas piojosas entrara el mensaje de que podemos destruirlos ¡y lo haremos!\n\nQuiero que seas tú <el mensajero/la mensajera> de mi voluntad, <nombre>. Elimina a 5 de sus Alfas en una hora. ¡Si tardas más, el plan no funcionará y empeorarás nuestra situación!", progress = nil, completion = "Tú has hecho lo que te pedí y te aplaudo por ello. Acepta esta pequeña recompensa como premio por tus esfuerzos, &lt;nombre&gt;.\n\nAhora que sus líderes han muerto y hemos diezmado su número, se lo pensarán dos veces antes de irrumpir en el campamento. Pero eso no soluciona el problema.\n\nDebe de haber un motivo para que los gnolls hayan enloquecido. Y antes de que tengan tiempo de reagruparse, tenemos que averiguar qué es lo que causa su locura.", rewards = "Recibirás:\n\n"},
[2864] = {title = "Tran'rek", objective = "Habla con Tran'rek en Gadgetzan.", description = "Mi primo Tran'rek está en un aprieto. Me prometió un cargamento de caparazones de escarabajo hace semanas y todavía no lo ha traído. Es mi primo, así que odio tener que enviar a alguien a Gatgetzan para que le eche la bronca.\n\n¿Puedes hablar con Tran'rek por mí? Hazle saber que tiene que conseguir ese cargamento antes de que haga algo malo.\n\nGadgetzan está en el norte de Tanaris, en Kalimdor.", progress = nil, completion = "¡Hola!\n\nOh, ¿hablaste con mi primo Krazek? ¿Cómo está? Espero que le vaya bien en Bahía del Botín...\n\n¿Caparazones de escarabajo dices? Bien... cuéntame tu historia...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2865] = {title = "Caparazones de escarabajo", objective = "Lleva 5 caparazones de escarabajos sin grietas a Tran'rek en Gadgetzan.", description = "¡Los escarabajos de Tanaris tienen unos caparazones muy duros! Tan duros que se pueden utilizar para construir muchas cosas. ¡Muchísimas!\n\nDe hecho, esos caparazones son tan útiles y cazamos tantos escarabajos... ¡que casi se han extinguido!\n\nSé dónde hay más escarabajos, y si me prometes traerme sus caparazones te diré dónde están. ¿Prometido?\n\nMuy bien, hay una colonia de escarabajos en Zul'Farrak. Supongo que los trols no los cazan en busca de caparazones.\n\n¡Pero tú sí! ¡Ve a Zul'Farrak y consígueme caparazones sin grietas!", progress = "¿Tienes los caparazones? Mi primo de Bahía del Botín sigue esperando el cargamento, ¡y se está impacientando!", completion = "¡Oh, genial! ¡Los conseguiste! \n\nGracias, &lt;nombre&gt;. ¡Eres un auténtico salvavidas!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2866] = {title = "Las Ruinas de Solarsal", objective = "Explora las Ruinas de Solarsal e investiga la presencia de los naga allí.", description = "Al sur de aquí están las Ruinas de Solarsal, una zona que ya era antigua antes de nuestro tiempo. Exploramos las ruinas en su momento y no encontramos nada relevante, así que no volvimos a enturbiar la paz del lugar.\n\nHace poco, los nagas Crestafuria han ocupado las ruinas en número alarmante. Al principio, lo interpretamos como el anuncio de un ataque. Aún consideramos esa posibilidad, pero además, creemos que podría estar ocurriendo alguna otra cosa.\n\nVe a explorar las ruinas y busca cualquier cosa que te llame la atención. Buena suerte, <nombre>.", progress = nil, completion = "Un examen minucioso de la glorieta revela tres cosas interesantes y poco habituales que contrastan con las ruinas que la rodean.\n\nPrimero, la glorieta es evidentemente más reciente que las demás estructuras de las ruinas. Segundo, tiene una serie de glifos pequeños grabados en los lados. Los glifos parecen repetirse, pero su significado y su origen son desconocidos. Tercero, la piedra de la glorieta está caliente al tacto.\n\n¡Esto es sin duda algo que \"llama la atención\"!", rewards = "Recibirás:\n\n"},
[2867] = {title = "Vuelve al Bastión Plumaluna", objective = "Vuelve al Bastión Plumaluna e informa a Shandris Plumaluna del resultado de tu exploración.", description = "Tras descubrir la extraña glorieta en el corazón de las Ruinas de Solarsal, sabes que ha llegado el momento de regresar al Bastión Plumaluna e informar a la general Shandris Plumaluna del resultado de tu exploración.", progress = nil, completion = "Bienvenido, &lt;nombre&gt;. Tu informe sobre la glorieta es... preocupante. Parece que, aunque los naga no estuvieran planeando un ataque, su presencia es una mala señal para nuestra ofensiva en Feralas. En cualquier caso, aquí tienes tu compensación por tu servicio.\n\nParece que el Bastión Plumaluna necesita desesperadamente ayuda con el problema de los naga Crestafuria. Quisiera ofrecerte a ti la misión, si te parece. Hay mucho que hacer.", rewards = "Recibirás:\n\n"},
[2869] = {title = "En contra de los Crestafuria", objective = "Consigue 10 escamas de naga Crestafuria para Latronicus Lanzaluna en el Bastión Plumaluna.", description = "En este momento, lo mejor que podemos hacer es asumir que, independientemente de lo que estén haciendo los nagas, antes o después se moverán para asegurar la isla para los suyos. Necesitamos tomar medidas agresivas para combatir esa amenaza antes de que sea demasiado tarde.\n\nTráeme 10 escamas de Crestafuria que estén en buenas condiciones. No hará falta decirte cómo conseguirlas, supongo. Tú adquirirás experiencia con los nagas y nosotros conseguiremos reducir su presencia en Isla Sardor. Ambas cosas serán beneficiosas para nosotros.", progress = "<nombre>, si estás aquí para que te confirme la tarea que te ha sido asignada, excelente. Te confirmo que quiero 10 escamas de Crestafuria. Si has venido a charlar, te informo de que en estos momentos no estoy disponible para trivialidades.\n\nDebes saber algo: los elfos de la noche no somos belicistas. Nuestra raza dedica su tiempo a preservar la paz y la armonía. Pero eso no quiere decir que no sepamos proteger nuestros intereses. Ese es mi deber para con la general Plumaluna y el Bastión.", completion = "Sí, sí, estas escamas son más que satisfactorias. De hecho, creo que se las enviaré a Pratt, para que les eche un ojo. Quizás pueda hacer algo útil con ellas.\n\nEn cuanto a ti, tengo otra misión que encomendarte. Pero antes, aquí tienes tu recompensa por las escamas. Tu servicio a la general Plumaluna y al Bastión en su conjunto es una bendición en estos tiempos adversos.", rewards = "Recibirás:\n\n"},
[2870] = {title = "En contra del Señor Shalzaru", objective = "Elimina a Señor Shalzaru y luego lleva la reliquia misteriosa que desenterró a Latronicus Lanzaluna al Bastión Plumaluna.", description = "<nombre>, nos han llegado informes de los rastreadores que nos han dado una buena idea de cómo es la cadena de mando de los nagas. Entre sus filas hay Regios, que son los que mandan.\n\nEn concreto, hemos identificado a uno de los regios más crueles: Señor Shalzaru. Según mis rastreadores, él en persona ha desenterrado una extraña reliquia en una excavación naga de una cueva del sur de las ruinas principales, en la Isla del Terror.\n\nAcaba con él y tráeme esa reliquia para que podamos estudiarla.", progress = "Eliminando a Señor Shalzaru retrasaríamos sus planes semanas, incluso meses. Además, la reliquia que descubrió nos interesa.\n\n¿Has cumplido la misión que te fue asignada, <nombre>?", completion = "¡Bien, &lt;nombre&gt;! La muerte de Señor Shalzaru desconcertará a los naga durante un tiempo, si no para siempre. La amenaza que suponen ahora está más descontrolada que nunca, y eso te lo debemos a ti.\n\nAquí tienes más dinero del tesoro del Bastión. Te has ganado cada pieza. Tú y los tuyos sois siempre bienvenidos en el Bastión Plumaluna, amigo.\n\nUna cosita más, antes de que te vayas, &lt;nombre&gt;... si pudieras...", rewards = "Recibirás:\n\n"},
[2871] = {title = "Entrega la reliquia", objective = "Entrega la reliquia misteriosa a Vestia Lanzaluna que está en el Bastión Plumaluna.", description = "Si fueras tan amable de entregar la reliquia a mi esposa Vestia, te lo agradecería. No es solo una de las mejores sacerdotisas de nuestra comunidad, además es una de las estudiosas más eruditas que conocerás en tu vida. Yo no podría ni dibujar este instrumento, ¡ni aunque mi vida dependiera de ello! Pero mi mujer... Si alguien puede averiguar qué es esta cosa, esa es ella. ¡Y necesitamos saber qué es!\n\nEstá al otro lado de la curva. Estará estudiando, seguro. Gracias, <nombre>.", progress = "¿Sí? ¿Puedo ayudarte?\n\nEspera, tú eres el <clase> que ha estado ayudando a la general Plumaluna y a Latro con la amenaza naga, ¿no es así? Yo soy Vestia, la mujer de Latro... bueno, de Latronicus. ¡Es un placer conocerte!\n\nPerdona que hable tanto. Tú estás aquí por negocios, supongo. ¿Traes algo para mí... de él?", completion = "Interesante... ¿Y dices que viene de las Ruinas de Solarsal? Pues Latro tiene razón, tengo que mirar esto. A primera vista, no parece pertenecer a las ruinas. Y tampoco es de fabricación naga... no harían algo tan extraño como esto.\n\nMe va a llevar algún tiempo estudiar este objeto. Cuando haya acabado, quizás pueda hacer uso de tus talentos, si andas por aquí.\n\nTen, acepta este objeto. No lo necesitamos, pero espero que tú le des un buen uso.", rewards = "Podrás elegir una de estas recompensas:\n\nCoraza de Jinete del AlbaIntegumento de centinela\n\nTambién recibirás:\n\nCoraza de Jinete del AlbaIntegumento de centinela"},
[2872] = {title = "La deuda de Stoley", objective = "Habla con Stoley en el Puerto Bonvapor.", description = "Mi viejo amigo Stoley me debe una botella muy especial de ron. Él atiende el bar del Puerto Bonvapor en Tanaris, Kalimdor, y me aseguró que el ron allí es lo más fuerte que se puede encontrar en Bahía del Botín. Está tan seguro de lo que dice que prometió darme el doble de dinero del que me debe si después de probarlo no opino lo mismo.\n\nBueno, yo creo que ahora ya no está tan seguro como en su día y no quiere que pruebe el ron. Sin embargo, yo nunca me echo atrás ante una apuesta, así que por qué no vas a hablar con él...", progress = nil, completion = "Ah, has estado hablando con MacKinley, ¿no es cierto?\n\n¡No es lo que él piensa! No tengo miedo de dejarle probar el ron de la sentina Bonvapor...\n\n¡Simplemente no me queda ni gota!", rewards = "Recibirás:\n\n"},
[2873] = {title = "el Cargamento de Stoley", objective = "Llévale el cargamento de Stoley a Stoley, que está en el Puerto Bonvapor.", description = "Le debo algo de ron a \"Lobo de Mar\" MacKinley de Bahía del Botín por una apuesta, ¡pero no me queda ni gota! El capitán Cuergo robó mi último cargamento y lo escondió en esa ciudad pirata amurallada situada al sur.\n\nSi encuentras mi cargamento de ron y me lo devuelves, quizás pueda demostrarle a MacKinley que el ron de estas tierras es capaz de tumbar a cualquiera.", progress = "¿Has encontrado mi cargamento de ron, <nombre>?", completion = "¡Genial, gracias por el cargamento! Ahora MacKinley y yo podremos acabar con nuestra apuesta.", rewards = "Recibirás:\n\n"},
[2874] = {title = "Un envío para MacKinley", objective = "Llévale la botella de Stoley a \"Lobo de Mar\" MacKinley, que está en Bahía del Botín.", description = "Gracias de nuevo por tu ayuda, <nombre>. Aquí tienes una botella de ron de la sentina Bonvapor. Llévasela a MacKinley para que pueda comprobar por fin lo que se dice del ron de Bahía del Botín.", progress = "¿Aflojó Stoley la botella que me prometió?", completion = "Mm, a ver, déjame probar...\n\n¡No puedo creerlo! ¡Esto es... la bomba! ¡Genial!\n\nSupongo que Stoley puede quedarse el dinero que me debe. Tenía razón en cuanto al ron de la sentina Bonvapor... ¡Cómo rasca!", rewards = "Podrás elegir una de estas recompensas:\n\nFajín de aventureroBotas lustrosas\n\nTambién recibirás:\n\nFajín de aventureroBotas lustrosas"},
[2875] = {title = "SE BUSCA: Andre Barbafuego", objective = "Llévale la cabeza de Andre al jefe de seguridad Silvaina, que está en el Puerto Bonvapor.", description = "Se busca al malvado pirata Andre Barbafuego por crímenes contra Bonvapor y Gadgetzan.\n\nPor decreto del consejo de gobierno, todo el que lea esto tiene autorización expresa para utilizar fuerza letal durante la ejecución del arresto de Andre y sus cómplices.\n\nPor nuevo decreto, de producirse el fallecimiento de Andre o de sufrir este algún tipo de perjuicio durante la sesión de interrogatorio posterior a su detención, su cabeza actuará como prueba irrefutable de este hecho y su entrega ante el jefe de seguridad Silvaina constituye contrato vinculante.", progress = "Pareces un tanto aturdido. ¿Has estado leyendo nuestros documentos legales?", completion = "¡Ah, la cabeza de Andre! ¡Supongo que al final recibió su merecido!\n\nMuchas gracias, &lt;nombre&gt;. Y ahora si me firmas aquí, te pagaré por los servicios prestados.\n\nNo te preocupes por la letra pequeña, no pone nada interesante y... de todos modos, no creo que exista una lupa lo suficientemente buena como para poder leerla.", rewards = "Recibirás:\n\n"},
[2876] = {title = "El registro náutico", objective = "Informa del registro de envíos a alguna autoridad del Puerto Bonvapor.", description = "Este pergamino contiene el registro de envíos realizados entre el Puerto Bonvapor y Bahía del Botín. Están incluidos tanto los envíos ya hechos como los pendientes, así como los nombres de los barcos, algunos con una X al lado y otros con anotaciones del tipo \"GRAN BOTÍN\" y \"DURA LUCHA\".\n\nSeguro que hay alguien en el Puerto Bonvapor interesado en echarle un vistazo a este documento.", progress = "Pareces sofocado. ¿Tienes algo que contarme?", completion = "No puedo creerlo, ¡es una lista de todas las salidas y llegadas de barcos, con sus respectivas fechas, horas y todo! Si cayera en manos del capitán de un barco pirata, ¡este haría su agosto!\n\n¿Y dices que la conseguiste de un pirata? Vaya...\n\nGracias, &lt;nombre&gt;. Me alegro de que se la hayas quitado y también de que le hicieras morder el polvo.", rewards = "Recibirás:\n\n"},
[2877] = {title = "La limpieza de Roca Oculta", objective = "Mata 10 barrosos verdes y 10 mocos verde jade e informa a Fraggar Mantrueno en Pico Nidal.", description = "Los Martillo Salvaje recordamos el pasado mejor que nuestros hermanos de Forjaz. ¡Si quieres hacer amigos aquí, tendrás que ensuciarte las manos!\n\nLa Horda ha abandonado sus puestos de Roca Oculta en las Tierras del Interior; ahora todo está infestado de babosas y mocos. Tenemos planes para la región, pero antes hay que enviar una brigada de limpieza.\n\n¡Ese es tu trabajo, hermosura! Ve allí y tráeme 10 babosas verdes y 10 mocos verde jade. ¡Andando!", progress = nil, completion = "Lo que los Martillo Salvaje valoramos es la acción, &lt;clase&gt;.\n\nTu misión es mucho más importante de lo que crees. Si reducimos el número de mocos y barrosos, podremos expandir nuestra zona de influencia. Ten; además de nuevos amigos, te has ganado una recompensa.", rewards = "Recibirás:\n\n"},
[2878] = {title = "Melodía corrupta", objective = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una melodía. De la planta emana un olor acre y malsano. Necesita desesperadamente ser atendida para recuperar su estado normal.", description = nil, progress = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una melodía. De la planta emana un olor acre y malsano. Necesita desesperadamente ser atendida para recuperar su estado normal.", completion = "Aplicas un poco de bálsamo de planta de Cenarion a la melodía. Inmediatamente empieza a mudar su forma corrupta y se transforma en una planta rebosante de salud.", rewards = nil},
[2879] = {title = "El Bastón de Equinex", objective = "Energiza el bastón de Troyas y encuentra el Monolito de Equinex.", description = "¡Esta madera curva podría ser el Bastón de Equinex! El Bastón de Equinex es, en realidad, una llave que abre el Monolito de Equinex de las Ruinas de Viento Azabache, las que están en la península al oeste de Rama Oniria.\n\nEncuentra las cuatro llamas que aún flamean en esas ruinas: Samha, Imbel, Byltan y Lahassa. Recoge sus esencias y luego, cuando estés en el Monolito de Equinex, utilízalas para imbuir su energía a la madera curva. Si este es realmente el Bastón de Equinex, podrás abrir el Monolito y sacar un artefacto sagrado de él.", progress = "El Monolito de Equinex se yergue silencioso entre las ruinas. ¿Qué contendrá?", completion = "Sostener el bastón energizado es casi imposible. Su poder retumba por tu cuerpo.\n\nExtiendes la mano hacia la fría superficie del Monolito de Equinex...\n\nSe abre una pequeña puerta que te permite sacar el artefacto del interior.\n\nUnos rayos de luz inundan la zona que rodea al Monolito de Equinex.", rewards = "Recibirás:\n\nUna piedra centelleante"},
[2880] = {title = "Los collares de trols", objective = "LLeva 5 collares tribales trol a Fraggar Mantrueno a Pico Nidal.", description = "Que quede claro, <raza>: ¡NO formamos parte de la Alianza! Los Martillo Salvaje somos independientes, creemos que el valor se tiene que probar con actos y no con bonitos discursos.\n\n¡Si quieres que te aceptemos, tendrás que probar tu valía! Esos inmundos trols que ensucian las Tierras del Interior suelen llevar collares tribales. Tráeme cinco y lo consideraremos un acto de buena voluntad hacia nosotros.", progress = "Quiero 5 collares tribales trol. Y la forma más efectiva y segura de quitárselos es matarlos.\n\n¡Demuestra lo que vales a los Martillo Salvaje!", completion = "Con estos collares nos demuestras tu valor; los Martillo Salvaje apreciamos el coraje.\n\nSigue así y llegarás lejos.", rewards = "Recibirás:\n\n"},
[2881] = {title = "Los collares de trols", objective = "Ya has cumplido tu misión, pero aceptaremos todos los collares tribales que nos traigas; así seguirás ganando méritos ante los Martillo Salvaje.\n\nTrae 5 collares tribales, <nombre>.", description = nil, progress = "Ya has cumplido tu misión, pero aceptaremos todos los collares tribales que nos traigas; así seguirás ganando méritos ante los Martillo Salvaje.\n\nTrae 5 collares tribales, <nombre>.", completion = "¡Bien, ya hay menos trols de los que preocuparnos! Buen trabajo, &lt;nombre&gt;. ¡Sigue así!", rewards = nil},
[2882] = {title = "El oro de Cuergo", objective = "¡Encuentra el oro de Cuergo!", description = "Este mapa muestra un trecho de playa orientado hacia el sur. Debajo del mapa hay unas palabras escritas de manera elegante... \n\nMás al sur de Bonvapor\nAl sur del lar de tu corazón\nEncontrarás huesos, mástil y bandera\nEscava allí si tienes alma aventurera\nEscava y el oro de Cuergo reclama\nUna llave necesitarás y una llave encontrarás\nLos hombres a los que te enfrentarás\nen sangre pagarás.", progress = "¡Este cofre está cerrado!", completion = "¡El tesoro escondido del capitán Cuergo aguarda!", rewards = "Recibirás:\n\nTesoro escondido de Cuergo"},
[2902] = {title = "Investiga a los Zarpaleña", objective = "Encuentra los planes de batalla de los gnolls por los campamentos que hay al sur del Campamento Mojache.", description = "Los gnolls no son los tipos más rápidos del lugar, pero seguramente tendrán algún tipo de documentación sobre sus actividades. Si encontráramos sus planes, podríamos tener una idea más clara de lo que les está agitando tanto.\n\nQuiero que vuelvas a Zarpaleña y busques cualquier cosa que pueda parecer un plan de batalla. Tienen que tener algo: sus ataques han sido demasiado coordinados, han tenido que seguir un plan. Encuentra la documentación de sus planes y vuelve cuando la tengas.", progress = nil, completion = "Un examen del mapa revela que es el plan de ataque que quiere Hadoken Zancoveloz. Hay muchas flechas que probablemente representan movimientos de tropas. La mayoría parecen dirigirse hacia el sur, no al norte, que es donde se encuentra el Campamento Mojache.\n\nEn otra sección del mapa hay un dibujo de lo que parece ser una especie de insecto enorme, con varias marcas debajo. Por el aspecto de los gnolls, puede que represente las bajas que esos insectos desconocidos han causado en sus filas...", rewards = "Recibirás:\n\n"},
[2903] = {title = "Los planes de batalla de los Zarpaleña", objective = "Lleva los planes de batalla de los gnolls Zarpaleña a Hadoken Zancoveloz al Campamento Mojache.", description = "Ahora que has descubierto los planes de batalla de los gnolls, solo te queda llevárselos a Hadoken Zancoveloz al Campamento Mojache. Quizás él o alguien de allí pueda averiguar qué están tramando los gnolls.", progress = "Bienvenido de nuevo, <nombre>. ¿Has conseguido encontrar pruebas físicas de los planes de los gnolls?", completion = "Ah, esto es exactamente lo que necesitamos. No se me da muy bien descifrar los garabatos de este mapa, pero he visto suficientes planes de ataque en mi vida para entender lo que está pasando con los gnolls. Parece que están combatiendo en dos frentes. En uno de ellos, combaten contra nosotros... pero parece que han concentrado la mayoría de sus energías en combatir a su enemigo del sur.\n\nEsto podría ser importante. Quizás sea el momento de averiguar quién es ese otro enemigo.", rewards = "Podrás elegir una de estas recompensas:\n\nBarrera naturalBotas Crestarrápida\n\nTambién recibirás:\n\nBarrera naturalBotas Crestarrápida"},
[2904] = {title = "Un buen lío", objective = "Acompaña a Kernobee a La rampa del engranaje y luego ve a ver a Scooty a Bahía del Botín.", description = "<Gruñe.> ¿Qué..? ¿Qué ha pasado?\n\nSolo recuerdo que me capturó un grupo de enanos Hierro Negro, me dieron una paliza de muerte y me echaron aquí, con estos asquerosos trogg.\n\n¡Sácame de aquí, <nombre>!\n\nEste es el plan: llévame a la salida; si salimos con vida, tú te vas a la Bahía del Botín y le dices a Scooty que he ido a Trinquete para avisar a Manitas de Oro que los Hierro Negro están metidos en lo de la superplataforma.\n\n¡Andando!", progress = nil, completion = "Mereces una recompensa por habernos traído a Kernobee, &lt;nombre&gt;. Esto te ayudará a derrotar a ese traidor miserable que es el mekigeniero Termochufe si vuelves a Gnomeregan.", rewards = "Podrás elegir una de estas recompensas:\n\nBrazales soldados con fuegoManto alas de hada\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBrazales soldados con fuegoManto alas de hada"},
[2922] = {title = "¡Salva el cerebro de Techrobot!", objective = "Lleva el procesador central de memoria del techrobot al maestro manitas Sobrechispa a Forjaz.", description = "Supongo que mi pobre techrobot recibió algún chispazo perdido cuando cayó nuestra amada Gnomeregan. Antes nos era muy útil gracias a sus incontables funciones y su capacidad ilimitada de almacenaje de información, pero su cerebro positrónico se ha polarizado. Ahora es negatrónico y la conducta del techrobot se ha invertido. ¡Anda descontrolado por Gnomeregan!\n\nPero quizás podamos recuperarlo. Si me trajeras el procesador central de memoria del techrobot, podría descubrir la causa de la polarización y repararlo.\n\n", progress = "¿Encontraste al techrobot?  ¿Tienes el procesador central de memoria?", completion = "¡Excelente! ¡Gracias, &lt;nombre&gt;, voy a ponerme a trabajar de inmediato!\n\nPobre tecnobot, vaya susto te has llevado.\n\nEnseguida te arreglo; a ver, dónde estará mi llave inglesa giratoria con gato hidráulico...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2923] = {title = "Maestro manitas Sobrechispa", objective = "Habla con el maestro manitas Sobrechispa en Forjaz.", description = "El maestro manitas Sobrechispa está muy disgustado con lo ocurrido a su obra mecánica, el techrobot. Por lo que sé, el techrobot era una herramienta muy útil para los gnomos, pero dice que se ha averiado.\n\nLástima. ¡Aunque con suerte se podrá arreglar!\n\nVe a hablar con Sobrechispa a Ciudad Manitas, en Forjaz. Se alegrará de que vayas a ayudarlo.", progress = nil, completion = "Sí, hemos perdido el control del tecnobot. ¿Puedes ayudarme?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2924] = {title = "Esencias artificiales", objective = "Lleva 12 esencias artificiales a Klockmort Palmalicate a Forjaz.", description = "Los herreros y manitas de Gnomeregan usaban un componente mecánico casi universal, la esencia artificial, en su trabajo. Pero durante la huída de la ciudad, ¡nadie pensó en coger esencia! Necesitaré mucha para un trabajo que estoy a punto de empezar.\n\nTe ruego que vayas a Gnomeregan a conseguirme esencia artificial. Está en las zonas más profundas de la ciudad, en unos contenedores que se llaman extrapoladores artificiales.\n\n¡Date prisa y muchas gracias, <nombre>!", progress = "¿Tienes las esencias artificiales?", completion = "¡Ahora que tengo las esencias, puedo empezar!\n\n¡Gracias a tu valentía, la investigación gnómica va a dar un salto cuántico!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2925] = {title = "Klockmort Palmalicate", objective = "Habla con Klockmort Palmalicate en Forjaz.", description = "Klockmort Palmalicate, un amigo gnomo, me ha enviado un mensaje desde Forjaz. Dice que necesita ayuda en una misión peligrosa pero crucial en Gnomeregan, la gran ciudad hoy contaminada e invadida por gnomos dementes y sus aparatos.\n\nYo debo quedarme aquí, pero quiero ayudar a mi amigo. Si vas en mi lugar tendrás mi agradecimiento eterno.\n\nEncontrarás a Klockmort en Ciudad Manitas, en Forjaz.", progress = nil, completion = "Pues sí, necesito a alguien para una misión en Gnomeregan. ¿Te interesa, &lt;clase&gt;?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2926] = {title = "Gnogaine", objective = "Usa la ampolla de plomo con invasores o saqueadores radiactivos para recoger restos radiactivos. Cuando esté llena, llévasela a Ozzie Voltiflop a Kharanos.", description = "Desde el accidente, Gnoarn no piensa en otra cosa que en expulsar a los trogg de Gnomeregan y encontrar una cura para la plaga. <nombre>.\n\nPor suerte, Razzle y yo estamos a punto de conseguirlo; o eso, o hemos destilado un ron muy potente. ¡No lo sabremos hasta que lo probemos! Claro que para poner a prueba nuestra teoría, necesitamos algunas cosas, como restos de los trogg radiactivos en Gnomeregan. ¡Llena este frasco de material verdoso radiactivo y tráelo!", progress = "¡¿No ves que tenemos muchos gnomos que curar?! ¡Necesitamos más restos radiactivos!", completion = "¡Excelente! Vamos a probar la nueva fórmula. ¡Si funciona, no solo curaremos a los gnomos leprosos, sino que me volverá a crecer el pelo! ¡Aparta!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2927] = {title = "Al día siguiente", objective = "Ve a hablar con Ozzie Voltiflop en Kharanos.", description = "¿Crees que siempre he sido calvo? ¡Pues no <señor/señorita>! Perdí todo el pelo por culpa de la fuga radiactiva de Gnomeregan. Hasta entonces, tenía una mata de pelognomo única en todo Azeroth.\n\n<Sacude los puños.>\n\n¡Así se pudran esos malditos trogg en las simas del averno!\n\nA lo mejor no es tarde, <nombre>. Aún podemos salvarla. ¡A la ciudad, me refiero! Ve a hablar con Ozzie Voltiflop en Kharanos. Está buscando una solución con su colega Razzle.", progress = nil, completion = "Creí que no soportaría la pérdida del pelo, pero ya ves.\n\nEstábamos juntos cuando le cayó la nube radiactiva y se le empezó a caer el pelo a puñados. Fue horroroso. ¡Empezó a tocarse la cabeza, se le pusieron los ojos rojos y juró venganza! Desde entonces la tomó con los troggs.\n\n&lt;Ozzie se estremece.&gt;", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2928] = {title = "Gyrodrillmatic Excavationators", objective = "Lleva veinticuatro entrañas robomecánicas a Shoni en Ventormenta.", description = "Es terrible, <nombre>. No vamos a tener suficientes repuestos para llevar nuestra flota de excavadoras gyroagujereamáticas a Dun Morogh.\n\nLo que necesitamos son repuestos, <nombre>. ¿Y qué mejor lugar que Gnomeregan para conseguir repuestos para nuestros vehículos? He oído que robots y mecazancudos vagan libremente por ese lugar. \n\nNecesito que vayas a Gnomeregan y destruyas a esos robots. Tráeme sus entrañas robot. Con dos docenas tendré suficiente.", progress = "Si esto fuera una carrera, ya habrías perdido.", completion = "Esto será perfecto, &lt;nombre&gt;. Una excavadoras gyroagujereamática arreglada, solo quedan 398.", rewards = "Podrás elegir una de estas recompensas:\n\nHerramienta de desarme de ShoniMitones llamativos\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nHerramienta de desarme de ShoniMitones llamativos"},
[2929] = {title = "La gran traición", objective = "Ve a Gnomeregan y mata al mekigeniero Termochufe. Ve a ver al Manitas Mayor Mekkatorque cuando hayas terminado.", description = "Yo confiaba en Termachufe, <raza>. Nunca pensé que me traicionaría a mí y a todos los gnomos. ¿Y por qué? ¿Por el poder? ¿Por el dinero? Habría conseguido todo eso por sí mismo. Ahora nos han echado de nuestro hogar y ese chiflado es el que manda. Rey de pacotilla...\n\nVamos a recuperar Gnomeregan, <nombre>. No cejaremos hasta que la ciudad vuelva a nuestras manos. Si quieres ayudarnos, tengo un trabajito para ti. Mata al traidor. Acaba con el mekigeniero Termochufe.", progress = nil, completion = "Cuéntame otra vez cómo le aplastaste el cráneo con la bota, &lt;raza&gt;. Ahora que ha muerto, estamos más cerca de recuperar Gnomeregan.", rewards = "Podrás elegir una de estas recompensas:\n\nTogas civinadPeto de corredorLeotardos duales reforzados\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTogas civinadPeto de corredorLeotardos duales reforzados"},
[2930] = {title = "Rescatar los datos", objective = "Lleva una tarjeta perforada prismática al maestro mecánico Funditubo a Forjaz.", description = "¡Cuando tuvimos que huir de Gnomeregan, dejamos atrás información muy valiosa! ¡Qué digo, valiosa! ¡Vital!\n\nNecesito los datos de la tarjeta perforada prismática de un Perforágrafo Matriz 3005-D de alta seguridad oculto en Gnomeregan. Tienes que acceder a la terminal, pero para eso necesitas un permiso... Y para eso tienes que acceder a una terminal de baja seguridad de los modelos -A, -B y -C. Y para eso, necesitas tarjetas perforadas de seguridad en blanco... y todas se quedaron en la ciudad.\n\n¡Qué desastre! ¿Puedes ayudarme?", progress = "¿Tienes la tarjeta perforada prismática?", completion = "¡Has conseguido recuperar los datos! Son importatísimos, vitales.\n\nEste servicio que nos has prestado será recordado mucho tiempo, &lt;nombre&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nManteo de mecánicoMartillo de mecánico"},
[2931] = {title = "Trabajar para Funditubo", objective = "Habla con el maestro mecánico Funditubo en Forjaz.", description = "¡El maestro mecánico Castpipe de Forjaz busca gente aventurera! Quiere volver a Gnomeregan para recuperar datos; es una misión muy importante.\n\nAunque no sea miembro de la Asamblea Iluminada de Arcanología, Alchimia e Ingeniería como yo, es un gnomo inteligente, así que si piensa que algo es importante, según mis cálculos hay un 48% de posibilidades de que, en efecto, sea importante.\n\n¡Es una probabilidad muy alta!", progress = nil, completion = "Estoy organizando misiones a Gnomeregan. Si estás aquí por eso, escucha con atención.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2932] = {title = "Un macabro mensaje", objective = "Recoge calaveras de Secacorteza y clava la pica de Nimboya en unos de los poblados Secacorteza, en las Tierras del Interior. Luego ve a ver a Nimboya a Tuercespina.", description = "Los trols Cortezaseca fueron aliados de la Horda, ¡pero traicionaron a Thrall y deben ser castigados!\n\nToma mi pica y vete a las Tierras del Interior. Están al noreste de Molino Tarren, que está bajo control de nuestros aliados, los Renegados.\n\nCuando llegues, busca trols Cortezaseca. Recoge sus calaveras y clava mi pica en el suelo en uno de sus poblados más pequeños, Hiri'watha o Zun'watha.\n\n¡Que aprendan que sus ruines acciones tienen consecuencias!", progress = nil, completion = "Sé que has cumplido tu misión. Esperemos que los Secacorteza capten el mensaje.", rewards = "Recibirás:\n\n"},
[2933] = {title = "Botellas de veneno", objective = "Lleva una botella de veneno a algún boticario de Molino Tarren.", description = "En estas botellas hay un veneno verde e infame.\n\nVista la cantidad de botellas que hay en el poblado, los Cortezaseca deben de considerarlo muy importante. A lo mejor algún boticario de Molino Tarren estaría interesado en esto.", progress = "Saludos, <clase>. Hoy ha sido un día largo en el que todo ha ido mal. Espero que me traigas algo bueno.", completion = "¿Qué es esto? Parece veneno de arácnido... ¿De dónde sale?", rewards = "Recibirás:\n\n"},
[2934] = {title = "Glándula de veneno intacta", objective = "Lleva una glándula de veneno intacta al boticario Lydon de Molino Tarren.", description = "Necesito más veneno como el que trajiste. Creo que las arañas de las que se extrae viven en las Tierras del Interior; encuentra una con la glándula de veneno intacta y tráemela. Habrá muchas cerca de la ciudad trol de Shadra'Alor.\n\nNo te entretengas, <nombre>. La glándula tiene que ser fresca.", progress = "¿Traes la glándula de veneno? Estoy deseando hacer experimentos ¡y tengo unos cuantos animalitos muy monos para probar el veneno!", completion = "¡Magnífico! El veneno es fresco y letal... ¡Gracias!", rewards = "Recibirás:\n\n"},
[2935] = {title = "Consulta al maestro Gadrin", objective = "Habla con el maestro Gadrin en el Poblado Sen'jin.", description = "El veneno de las guardias del nido no era tan potente como el que me trajiste primero. Debía de ser de otra araña.\n\nQuiero que me traigas más de ese veneno. Para averiguar de dónde viene, ve al Poblado Sen'jin y habla con el maestro Gadrin. Es un trol que conoce bien a los Cortezaseca. Ve a Durotar y habla con Gadrin. Con un poco de suerte, sabrá de dónde sacan el veneno los Cortezaseca.", progress = nil, completion = "¿Que quieres veneno de las arñas de los Secacorteza? Bueno, pues siéntate; lo que voy a decir no te va a gustar.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2936] = {title = "Diosa araña", objective = "Lee el nombre de la diosa araña de los Secacorteza en la tablilla de Theka y vuelve a ver al maestro Gadrin.", description = "Los Cortezaseca adoran a una diosa araña, de la cual extraen su veneno más potente. Así que, si quieres el veneno, ¡tienes que invocar a la diosa!\n\nPara ello debes conocer su verdadero nombre, inscrito en una tablilla enterrada en Tanaris, en la ciudad trol de Zul'Farrak, en una tumba custodiada por el Mártir Theka.\n\nDerrótalo y lee el nombre en la tablilla. Después, vuelve.", progress = nil, completion = "Ah, Elortha no Shadra... Este nombre solo debe ser susurrado, pues tiene un gran poder en el reino del espíritu.\n\nVoy a preparar el hechizo de invocación.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2937] = {title = "Invocar a Shadra", objective = "Derrota a Shadra en el altar de Shadra'Alor y lleva su veneno al boticario Lydon de Molino Tarren.", description = "Lee este pergamino en el altar de Shadra'Alor, que está escondido en el reino de los Cortezaseca, en las Tierras del Interior. Así invocarás a Shadra, la diosa araña.", progress = "¿Traes el veneno?", completion = "¡Excelente!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2938] = {title = "Veneno para Entrañas", objective = "Lleva el paquete al maestro boticario Faranell a Entrañas.", description = "¡El veneno de la diosa araña de los Cortezaseca es extraordinario! Ha bastado una gota para matar a dos docenas de conejos. Es cierto que muchos eran muy pequeños y por tanto poco resistentes a las toxinas, ¡pero no puede negarse que este veneno es de lo más potente!\n\nMe gustaría enviar una muestra a mi superior, el boticario Faranell, del Apothecarium de Entrañas. Estoy seguro de que querrá hacer pruebas.", progress = "Te estaba esperando, <nombre>. ¿Me traes algo?", completion = "Muy bien; Lydon dijo que es el veneno más potente que conoce y que lo está estudiando.\n\nEspero que le encuentre muchos usos.\n\nGracias por todo, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nVestimentas de alto rango realCoraza protectora de honorÉgida de batalla\n\nTambién recibirás:\n\nVestimentas de alto rango realCoraza protectora de honorÉgida de batalla"},
[2939] = {title = "La búsqueda del conocimiento", objective = "Habla con Daryn Brisela de la Aldea Rut'theran.", description = "Las ruinas de Feralas pueden ser muy peligrosas, pero tienen mucho que decir acerca de lo que aquí pasó.\n\nEntre los escombros, hace  unos días, descubrí lo que parecía ser un bastón común y corriente. Sin embargo, creo que hay algo en él. Angelas y yo hemos estado leyendo buscando algo al respecto, pero no aparece nada.\n\nTengo una amiga en Darnassus que nos podría decir lo que es, <nombre>. ¿Por qué no vas a verla y le preguntas si nos ayuda?", progress = nil, completion = "Ah, ¿así que te envía Troyas Brisaluna?\n\n&lt;murmurando&gt;Siempre está molestándome con esas exageraciones de que ha encontrado algo importante...\n\nMira, &lt;nombre&gt;, te voy a hablar con franqueza. Troyas siempre cree que ha encontrado algo importante en esas ruinas. Y la verdad es que, cualquier cosa de valor que hubiera en ellas, seguramente ha desaparecido ya. Se la habrán llevado los orcos o las harpías que habitan en ellas ahora.\n\nPuedes echar un vistazo por aquí, pero no sé si encontrarás ningún libro que te ayude.", rewards = "Recibirás:\n\n"},
[2940] = {title = "Feralas: una historia", objective = "Pide a Daryn Brisela que te preste su libro.", description = "Parece que este libro no ha sido abierto en mucho tiempo. Las tapas están bastante desgastadas y sus páginas, amarillentas, pero después de examinarlo, te das cuenta de que puede ser lo que Troyas está buscando.\n\nLo recoges, pero entonces piensas que deberías preguntar antes de tomarlo prestado.", progress = nil, completion = "¿Así que quieres llevarte Feralas: Una historia?\n\nDeja que me lo piense.\n\nTe prestaré el libro, &lt;nombre&gt;, pero solo si haces algo por mí antes.", rewards = "Recibirás:\n\n"},
[2941] = {title = "El préstamo", objective = "Lleva esta carta a Curgle Saltamanivela a Tanaris.", description = "He estudiado muchos temas en mi tiempo y mi última obsesión son los Quijaforte que ocupan la playa de las Tierras del Interior. Y hay una en concreto a la que me gustaría ver, una Quijaforte gigante llamada Gammerita. Iría yo misma, pero mis investigaciones no me permiten moverme de aquí. Así que me conformaría con tener un dibujo de ella.\n\nLleva esta carta a Curgle Saltamanivela a Tanaris. Ha inventado un dispositivo para mí que puede capturar imágenes. Ella lo llamaba \"instantánea\" creo...", progress = "¿Una carta... para mí?", completion = "¿Eres otro de los internos de Daryn? Es una gran historiadora, &lt;nombre&gt;. Tienes suerte de estudiar bajo su guía.\n\nAhora deja que coja la Superistánea FX. ¡Te va a encantar!", rewards = "Recibirás:\n\n"},
[2942] = {title = "La Piedra del mañana", objective = "Devuelve la piedra refulgente y el Bastón de Equinex a Troyas Brisaluna que está en el Bastión Plumaluna.", description = "En el momento en el que extraes el artefacto del Monolito, sientes que la energía del Bastón de Equinex empieza a decaer.", progress = "Me alegro de que hayas vuelto, <nombre>. Estoy deseando saber qué has averiguado.", completion = "He estado leyendo desde que te fuiste, &lt;nombre&gt;. Esta piedra que encontraste se llama Piedra del mañana. Aún no tengo muy claro para qué sirve, pero sí sé que debe de ser muy importante, si no, no estaría escondida en el Monolito de Equinex.\n\nYo seguiré con mis estudios. Espero poder contar contigo en un futuro, &lt;nombre&gt;. Gracias por tu ayuda.", rewards = "Podrás elegir una de estas recompensas:\n\nFragmento de piedra de hitoAro de semillas\n\nTambién recibirás:\n\nFragmento de piedra de hitoAro de semillas"},
[2943] = {title = "Vuelva con Troyas", objective = "Entrega el libro a Troyas Brisaluna en el Bastión Plumaluna.", description = "Aquí lo tienes, <nombre>. Por favor, cuida de mi libro.\n\nY, ahora, ve. Seguro que Troyas está deseando que regreses.", progress = "Hola, <nombre>. ¿Has estado en Darnassus?", completion = "Has traído un libro. ¡Fantástico!", rewards = "Recibirás:\n\n"},
[2944] = {title = "Superistánea FX", objective = "Utiliza la Superistánea FX para sacar una instantánea de Gammerita y luego vuelve a la Aldea Rut'theran con Daryn Brisela.", description = "Echa un vistazo a mi nuevo invento, <nombre>. Lo único que hay que hacer es apuntar a lo que quieras retratar y pulsar el botón.\n\n¿Cuál era esa criatura de las Tierras del Interior que mencionaste? ¿Gammerita? Pues espero que la encuentres. Buena suerte. Estoy segura de que Daryn quedará satisfecha con la instantánea que le llevas.\n\nEn cualquier caso, ¡aquí tienes la Superistánea! ¡Diviértete!", progress = "Así que por fin has vuelto, <nombre>. Y me has traído el artilugio que Curgle hizo para mí, perfecto.", completion = "¡Bien! El invento de Curgle supera todas mis expectativas.\n\nNo te emociones demasiado pero debes saber que tu trabajo me satisface mucho, &lt;nombre&gt;.\n\nVeamos, ¿dónde dejé el libro?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2945] = {title = "Un anillo sucio", objective = "Encuentra la manera de limpiar el anillo sucio.", description = "Has encontrado algo que parece un anillo en el cadáver de un enano Hierro Negro. Está cubierto de barro y restos secos y endurecidos, por lo que no se puede llevar ni se puede ver qué tipo de anillo es. Entre el barro, te parece entrever el brillo de metal precioso y quizás una gema.\n\nSi quieres ponértelo, tendrás que encontrar la manera de limpiarlo.", progress = "El artilugio gnómico lleva una plaquita que dice: \"Destellamatic 5200\". Al parecer, esta máquina limpia los objetos sucios. Tiene una ranura para monedas y dice que hay que insertar el objeto y 3 monedas de plata.\n\n¡Esta máquina es perfecta para limpiar el anillo sucio que encontraste!", completion = "Pones el anillo en el Destellamatic 5200 y una moneda de plata en la ranura.\n\nLa máquina hace unos ruidos horribles y se sacude de uin lado a otro; a saber qué está pasando ahí dentro...\n\nSe detiene de pronto con un sonoro \"¡ping!\" Aparece una cajita con un lazo rojo. ¡Parece que esta máquina envuelve para regalo!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia"},
[2946] = {title = "A ver qué pasa", objective = "Lleva los minidiscos de platino a las puertas de Uldum, en Tanaris. Busca pistas sobre la conexión entre discos y puertas.", description = "Queremos que lleves los minidiscos a las puertas selladas de Uldum. No estamos seguros de qué pasará, la verdad.\n\nSabemos que la operación entraña un riesgo. Aunque puede que no ocurra nada. Puede que tengas que usar los discos de algún modo. Es posible que las puertas se desbloqueen porque lleves los discos. Mantén una actitud abierta y prepárate para cualquier cosa.\n\nCuando averigües lo que pasa con los discos, vuelve a Forjaz. ¡Tendremos mucho que analizar!", progress = "Este pedestal destaca sobre la ardiente arena. No puede ser casualidad que esté aquí...", completion = "Al tocar el pedestal, los minidiscos empiezan a zumbar y resplandecer. Te cosquillean las manos mientras surge otro Guardapiedras frente a las puertas de Uldum.\n\n\"Saludos; soy el guardián de entrada. Faltan las Placas de Uldum; incorpóralas a la estructura de los minidiscos y repite la secuencia de acceso. Gracias.\"", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2947] = {title = "La devolución del anillo", objective = "Puedes quedarte el anillo o buscar a quien realizó los grabados de la parte interior.", description = "Al abrir la caja del Destellamatic 5200 verás lo que estabas esperando: ¡un anillo reluciente! Cuando lo lleves sentirás que te recorre una fuerza mágica.\n\nEn la parte inferior está grabado, pequeño pero legible, el sello de Forjaz. Junto a él hay tres iniciales. \"TdK\". Pueden ser las iniciales del dueño del anillo o las del artesano que lo forjó. Quédate el anillo... o devuélveselo a esta persona. Tú verás...", progress = "No me hagas nada, ¡soy un profesional respetable! Es que el banco ya había cerrado y... Ah, no has venido a cobrar el alquiler.\n\n¡Qué fallo tan tonto!  Estás aquí porque necesitas a un buen joyero, ¡uno que no fabrique joyas malditas!B\nY dime, ¿qué querías?", completion = "¿Un anillo, dices? A ver... Sí, lo hice yo.\n\nTenía una tienda en Gnomeregan antes de todo el lío; a lo mejor me lo dejé al huir. El diseño es muy simple, hice varios de estos.\n\n¿Y lo tenían los Hierro Negro? Bueno, eso no sería nada nuevo. Pero eso es cosa de las autoridades, yo no puedo hacer nada.\n\nY respecto al anillo...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2948] = {title = "Mejora gnómica", objective = "Lleva tu anillo de oro, 1 lingote de plata, 1 ágata musgosa y 30 monedas de plata a Talvash del Kissel a Forjaz.", description = "Oye, <nombre>, ¡puedo hacerte un anillo mejor que el que llevas! Como decía, ese diseño es muy sencillo. Ahora soy mucho mejor, así que por 30 monedas de plata de nada mejoraré tu anillo. Bueno, en realidad, es algo más de 30...\n\nAhora mismo estoy sin material. ¡Si me traes un lingote de plata y ágata musgosa, los usaré para añadir detalle a tu anillo y darle otro aire!\n\nConfía en mí.", progress = "¿Traes lo que te pedí? A ver si pronto consigo mis propios materiales...", completion = "¡Es justo lo que necesitaba! Algún día, cuando salga de este maldito lugar, haré los mejores abalorios de Azeroth. Hasta entonces, gracias por traerme todo esto. Te he cobrado solo el precio de coste del anillo.\n\nPor cierto, ¡aquí lo tienes!", rewards = "Recibirás:\n\nAnillo de oro de Talvash"},
[2949] = {title = "La devolución del anillo", objective = "Puedes quedarte el anillo o buscar a quien realizó los grabados de la parte interior.", description = "¡Al abrir la caja del Destellamatic 5200 verás un anillo reluciente! Cuando te lo pongas sentirás que te recorre una fuerza mágica.\n\nEn la parte interior está grabado, pequeño pero legible, el sello de Orgrimmar. Junto al sello hay algo más, pero incluso después de limpiar el anillo, solo se lee: \"NOG...\"\n\nPueden ser las iniciales del dueño o las del artesano que forjó el anillo. Quédate con el anillo o busca a \"NOG\". Tú verás...", progress = "Ya podríamos haber encontrado la manera de superar a esos asquerosos gnomos. ¡Seguro que nos robaron los planos de la plataforma! Ladrones hijos de mala... ¿Sí? ¿Querías algo?", completion = "¿Un anillo de Gnomeregan? A ver... No es mío, pero es muy interesante.\n\nLo suyo es muy curioso; durante años y años han sido unos torpes de cuidado y de repente están construyendo plataformas mejores que las nuestras.\n\n¿Que lo tenían los Hierro Oscuro? ¡Eso es aún más interesante! A lo mejor los gnomos les robaron los planos y los modificaron. Seguro que los enanos querrán vengarse; eso habría que investigarlo.\n\nOye, ese anillo...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2950] = {title = "Nogg mejora el anillo", objective = "Lleva tu anillo de oro, 1 lingote de plata, 1 ágata musgosa y 30 monedas de plata a Nogg a Orgrimmar.", description = "No, no es que de pronto haya decidido que el anillo es mío. Normalmente no hago estas cosas, pero tu información sobre los Hierro Negro me ha sido muy útil. Mejoraré tu anillo de una forma que nunca se le habría ocurrido a su creador. Seguro que era un gnomo...\n\n¡Si me traes un lingote de plata y ágata musgosa, además de 30 monedas de oro, añadiré detalle a tu anillo y reforzaré su magia. ¿Te interesa la cosa?", progress = "¿Traes lo que te pedí? No puedo mejorar el diseño si no tengo los materiales.", completion = "Aquí tienes tu anillo mejorado, y gracias por la información sobre los Hierro Negro. Parece explicar muy bien cómo los gnomos se inspiraron de pronto con ideas de goblin.", rewards = "Recibirás:\n\nAnillo de oro de Nogg"},
[2951] = {title = "¡El Destellamatic 5200!", objective = "Inserta un objeto sucio en el Destellamatic 5200 y asegúrate de tener 3 monedas de plata a mano.", description = "Este aparato destaca entre los gnomos que se mantienen firmes frente a la locura que acecha fuera de la sala. Tiene una plaquita que dice: \"Destellamatic 5200\" y:\n\n\"¿La suciedad arruina sus destellos? ¡Destellamatic 5200 devuelve el brillo de forma brillante! Inserte el objeto que desea limpiar e introduzca tres monedas de plata en la ranura. Espere unos momentos, ¡y ya está! ¡Sus objetos de valor aparecerán limpios y resplandecientes!\"", progress = "Para activar el Destellamatic 5200 tienes que insertar un objeto sucio y 3 monedas de plata.", completion = "Insertas el objeto sucio y 3 monedas de plata en el Destellamatic 5200. Se pone en marcha como si le fuera la vida en limpiar el objeto; hace unos ruidos tremendos y parece que hasta la carcasa de la máquina se queja...", rewards = nil},
[2952] = {title = "¡El Destellamatic 5200!", objective = "La máquina se para tan bruscamente como había arrancado. Se oye un \"ping\" y se abre la boca de la máquina. Dentro del Destellamatic 5200 hay una cajita con un lazo; parece que envuelve para regalo además de limpiar.\n\nSolo hay una forma de saber si el objeto está bien limpio...", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCaja envuelta de la Destellamatic"},
[2953] = {title = "El Destellamatic en acción", objective = "El Destellamatic 5200 parece estar esperando un objeto sucio que limpiar y 3 monedas de plata para activarse.", description = nil, progress = "El Destellamatic 5200 parece estar esperando un objeto sucio que limpiar y 3 monedas de plata para activarse.", completion = "El Destellamatic 5200 se pone en marcha con sacudidas. El limpiador gnómico parece estar pulverizando el objeto del interior de la máquina.\n\nLa máquina se para con un \"¡ping!\" y la boca se abre para revelar una cajita muy bien envuelta.", rewards = "Recibirás:\n\nCaja envuelta de la Destellamatic"},
[2954] = {title = "El guardapiedras", objective = "Averigua qué quiere el guardapiedras de Norgannon y después interactúa con el pedestal de Uldum en el Desierto de Tanaris.", description = "\"Saludos, soy un guardián de la entrada. A menos que ya tengas las Placas de Uldum integradas con los discos, no permitiré tu entrada en Uldum.\"\n\nLa aparición del guardapiedras Norgannon debe ser el objetivo de haberte enviado al desierto de Tanaris con los discos de platino. Para poder continuar, deberás escuchar lo que el guardapiedras tenga que contarte.", progress = nil, completion = "La voz del guardapiedras resuena con fuerza y autoridad:\n\n\"Los discos te serán devueltos una vez que accedas al pedestal. Cuando las Placas de Uldum obren en tu poder y estén integradas con el conjunto de discos que tienes, podrás acceder al complejo.\"", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2962] = {title = "Necesitamos más material verdoso", objective = "Viaja hasta Gnomeregan y recupera los restos radiactivos de gran potencia. Pero ten cuidado, ya que es inestable y podría explotar en cualquier momento.\n\nOzzie también quiere que traigas la ampolla pesada de plomo una vez que hayas terminado el trabajo.", description = "Tu muestra no solo era insuficiente en cuanto a cantidad, además era impura.\n\nNecesitamos más material verdoso radiactivo. Es evidente que los troggs no han sido suficientemente irradiados. Tendrás que ir a Gnomeregan y traer restos radiactivos, directamente de la fuente. Estoy hablando de babosas, rondadores y horrores irradiados. Y ten cuidado: contener este tipo de restos es difícil. Es posible que tengas que hacer varios intentos hasta que hayas reunido una muestra adecuada.", progress = "Mmm, no brillas. Es una buena señal.", completion = "&lt;Ozzie te examina.&gt; ¡Impresionante! ¡Todavía tienes todos los dedos! Venga, vamos a ver qué puede hacer esta cosa.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2963] = {title = "Presagios de Uldum", objective = "Habla con el historiador Karnik en la Sala de los Exploradores de Forjaz.", description = "¡Saludos de nuevo, <nombre>! Lo que descubriste en Uldaman ha interesado muchísimo a la Liga de Exploradores. Están intentando descifrar los minidiscos de platino que encontraste. ¡Deja que te diga que probablemente sea algo importante!\n\nEl historiador Karnik ha descubierto pistas importantes sobre los verdaderos orígenes del Valle de los Vigías en el Desierto de Tanaris. Quiere hablar contigo lo antes posible.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2964] = {title = "Un tarea futura", objective = "Habla con el alto expedicionario Magellas de Forjaz.", description = "Bueno <nombre>... parece que hemos llegado a un punto muerto. Sabemos que necesitamos las Placas de Uldum, pero no tenemos ni idea de dónde pueden estar. Tendremos que estudiar un poco la situación para poder elaborar un buen plan de ataque. Yo tengo una ligera idea de por dónde empezar a buscar. Albergo la esperanza de que pronto te pidamos que vayas a recuperarlas.\n\nHay que informar al Alto Expedicionario de esto, <nombre>. Sin duda, él desea una vez más agradecerte personalmente que hayas prestado tu ayuda a la Liga.", progress = nil, completion = "Entonces, todos estamos de acuerdo en que el mejor plan de ataque consiste en esperar hasta que podamos delimitar las áreas en las que es posible que estén las placas. Hemos llegado hasta aquí gracias a ti, &lt;nombre&gt;, y espero que seas tú quien abra Uldum para todos nosotros.\n\nHasta entonces, permíteme que te entregue este honorario en compensación por los gastos que hayas tenido y un pellizquito más para ti. Vuelve pronto por aquí... ¡Uldum nos espera!", rewards = "Recibirás:\n\n"},
[2965] = {title = "Presagios de Uldum", objective = "Habla con Nara Bravacrín en el Alto de los Ancestros de Cima del Trueno.", description = "Ah, <nombre>, qué casualidad que hayas vuelto a mí. Tus descubrimiento en la excavación de Uldaman nos han dado mucho de qué hablar a los sabios. De hecho, estaba discutiendo con una druida llamada Nara Bravacrín cuando caí en la cuenta de que sus antiguos viajes podrían resultar importantes para el próximo paso que hemos de dar.\n\nMe gustaría que fueras a hablar con ella acerca de sus viajes al Valle de los Vigías en el Desierto de Tanaris. Ella te está esperando en el Alto de los Ancestros.", progress = nil, completion = "Hay un lugar, en lo más profundo del Desierto de Tanaris llamado Valle de los Vigías. Los discos que has recuperado... contienen unos glifos que hablan de un lugar llamado Uldum. Muchos creen que este lugar no es más que un mito.\n\nYo sé que no es cierto; Uldum existe. Yo he estado allí. Bueno... por allí cerca.\n\nHe aquí el quid de la cuestión, &lt;nombre&gt;: creemos que hay una conexión entre los discos y Uldum, por eso queremos que vayas allí.", rewards = "Recibirás:\n\n"},
[2966] = {title = "A ver qué pasa", objective = "Lleva los minidiscos de platino a las puertas de Uldum en Tanaris y busca pistas de la relación que existe entre ambos.", description = "A Sabio Buscaverdad y a mí nos gustaría que llevaras los minidiscos que encontraste a las puertas selladas de Uldum. No estamos seguros de qué pasará, la verdad.\n\nSabemos que la operación entraña un riesgo. Aunque puede que no ocurra nada. Puede que tengas que usar los discos de algún modo. Es posible que las puertas se abran si llevas los discos. Mantén una actitud abierta y prepárate para cualquier cosa.\n\nCuando averigües lo que pasa vuelve a verme. Tendremos mucho de qué hablar.", progress = "Este pedestal que sobresale de la ardiente arena te resulta familiar. Quizás su presencia signifique algo…", completion = "Cuando tocas el pedestal, los discos en miniatura de Uldaman producen un zumbido y resplandecen en tu mochila. La sensación familiar de cosquilleo se extiende por tus manos mientras otro guardapiedras cobra vida frente a las puertas de Uldum.\n\n\"Saludos. Soy un guardián de la entrada. Entre los discos utilizados para entrar faltan los discos correctos de entrada: las Placas de Uldum. Por favor, incorpora estas placas a la estructura de los discos y repite el proceso para poder entrar. Gracias.\"", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2967] = {title = "El regreso a Cima del Trueno", objective = "Habla con Nara Bravacrín en Cima del Trueno.", description = "Accedes de nuevo al pedestal y los discos de platino aparecen de repente en tu mochila.\n\nCon lo que te ha enseñado el guardapiedras y los discos de platino de nuevo en tu poder, ha llegado el momento de regresar a Cima del Trueno e informar a Nara Bravacrín de tu hallazgo.", progress = "Bienvenida de nuevo, <nombre>. ¿Les has sacado provecho a los discos o no has conseguido nada?", completion = "¿Te faltaban algunos discos? Vaya, es curioso pero parece que los Discos de Norgannon son solo una pieza más del conjunto; como las llaves de un llavero, si prefieres.\n\nSi encontramos las Placas de Uldum, podremos por fin acceder al mismísimo complejo de Uldum. Sin embargo, no sé si alegrarme o preocuparme.\n\nQuizás tengamos que arriesgar todo para asegurar que los secretos que alberga la tierra y que nos destruirían a todos sigan siendo eso, secretos.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2968] = {title = "Una tarea futura", objective = "Habla con Sabio Buscaverdad en Cima del Trueno.", description = "Bueno <nombre>... parece que hemos llegado a un punto muerto. Sabemos que necesitamos las Placas de Uldum, pero no tenemos ni idea de dónde pueden estar. Tendremos que estudiar un poco la situación para poder elaborar un buen plan de ataque. Yo tengo una ligera idea de por dónde empezar a buscar. Albergo la esperanza de que pronto te pidamos que vayas a recuperarlas.\n\nHay que informar a Sabio Buscaverdad de esto, <nombre>. Sin duda, él desea una vez más agradecerte personalmente que le hayas prestado tu ayuda.", progress = nil, completion = "Entonces, todos estamos de acuerdo en que lo mejor es esperar hasta que podamos delimitar las áreas en las que es posible que estén las placas. Hemos llegado hasta aquí gracias a ti, &lt;nombre&gt;, y espero que seas tú quien haga frente a los secretos de Uldum.\n\nHasta entonces, permíteme que te entregue este honorario que cubre los gastos que has realizado y un pellizquito más para ti. Vuelve pronto por aquí... Uldum nos espera a todos, joven.", rewards = "Recibirás:\n\n"},
[2969] = {title = "Libertad para todas las criaturas", objective = "Utiliza la llave de jaula de bambú para liberar a los duendes dardo del campamento Tótem Siniestro. Deben sobrevivir al menos 6 duendes dardo y Kindal Tejeluna.\n\nTienes 6 minutos antes de que Kindal decida que es hora de retirarse, entonces tu misión fracasará.", description = "Esos asquerosos del clan Tótem Siniestro están capturando dragones feéricos, <nombre> Jer'kai y yo no sabemos qué están planeando hacer con ellos, pero está claro que no es nada bueno.\n\nY no podemos entrar en el campamento a liberar a los dragones a brazo partido, pero quizás, con tu ayuda...\n\nSi quieres ayudar y tienes amigos fuertes, libera a las criaturas de sus jaulas con esta llave y asegúrate de que logran escapar. Yo estaré a tu lado todo el tiempo. Jer'kai se quedará aquí.", progress = nil, completion = "¡Gracias a Elune, &lt;nombre&gt;! Eres una bendición para todos nosotros y también para los dragones que salvaste. Jer'kai y yo jamás habríamos podido ayudarlos. Tu intervención en los asuntos de los Tótem Siniestro será recordada.\n\nAhora habla con Jer'kai... Tengo que preparar nuestra retribución. Que los dragones feéricos estén libres ahora, no quiere decir que vayan a seguir así. Jer'kai y yo seguiremos adelante con la ofensiva.", rewards = "Recibirás:\n\n"},
[2970] = {title = "La justicia del castigo", objective = "Mata a 12 naturalistas Tótem Siniestro, 10 asaltantes Tótem Siniestro y 6 chamanes Tótem Siniestro antes de volver con Jer'kai Tejeluna a Feralas.", description = "Como ya te ha dicho Kindal, ella y yo llevaremos el conflicto a los Tótem Siniestro. Aún deberíamos ser capaces de eliminar a algunos tauren sin sufrir mucho, pero si te apetece ayudar y eres lo suficientemente capaz, nos encantaría que lo hicieras.\n\nYa habrás visto el campamento que tenemos debajo. Pues hay más hacia el este. Usa tus propias tácticas y causa todo el daño que puedas a los tauren. Podremos reunirnos aquí cuando hayan sentido el dolor de las flechas de Kindal y el de tus armas.", progress = "Kindal necesita tiempo para prepararse, pero ya podemos contar por docenas las bajas que hemos causado. Y claro, a estas alturas, el clan Tótem Siniestro ha detectado nuestra presencia en la zona. Nos están buscando como quien busca víboras.\n\nHarán todo lo que puedan para separarnos la cabeza del cuerpo. No bajes la guardia: también irán a por ti ahora que saben que has intervenido.", completion = "Kindal tenía razón sobre ti, &lt;nombre&gt;. Tienes un don para esto. Tienes más experiencia que cualquiera de nosotros y te estamos agradecidos.\n\nEl clan Tótem Siniestro es mucho más hostil que muchos tauren... Me pregunto si el resto de su raza frunce el ceño antes sus actos. No me sorprendería. Como ya habrás notado, muchas razas de Azeroth están vinculadas a homólogos menos amables.\n\nPero dejaremos ese tema para otro día.", rewards = "Recibirás:\n\n"},
[2972] = {title = "La justicia del castigo", objective = "Lleva el anillo con sello de Jer'kai a la suma sacerdotisa Tyrande Susurravientos a Darnassus.", description = "Kindal y yo no tenemos aquí los medios para recompensar tu ayuda, pero no queremos que sientas que no la apreciamos. Llévate mi anillo con sello. Si alguna vez te encuentras en Teldrassil, ve al Templo de la Luna que está en el corazón de Darnassus. Allí encontrarás a la alta sacerdotisa Tyrande Whisperwind. El anillo le hará saber que nosotras estamos bien y dará crédito a tu historia.\n\nAmbas te damos las gracias, <nombre>.", progress = "Sé bienvenido, hijo. Siempre es un placer recibir a poderosos aventureros en el Templo. Si tienes tiempo, ¿podrás compartir alguna de tus historias con los aventureros más jóvenes? Sé que muchos de ellos sacarían provecho de tu sabiduría.\n\nAy, perdona mi mala educación. Has venido al Templo en busca de ayuda y yo abuso de tu amabilidad. Te ruego que me perdones. Dime qué podemos hacer por ti los siervos de la Luz.", completion = "Es una historia increíble, &lt;nombre&gt;. Nos reconforta saber que no solo has ayudado a los dragonantes, sino también a una de nuestras sacerdotisas. Jer'kai acertó al enviarte a mí. Yo me aseguraré de que le sea devuelto su anillo. Tengo exploradores de camino a Feralas. Haré que investiguen esto durante su misión.\n\nGracias otra vez. Y, por favor, estás en tu casa.", rewards = "Podrás elegir una de estas recompensas:\n\nMuñequeras de abeto y sauceFaja de escama nocturna\n\nTambién recibirás:\n\nMuñequeras de abeto y sauceFaja de escama nocturna"},
[2973] = {title = "Una capa nueva", objective = "Lleva 10 alas iridiscentes de duende dardo a Krueg Machacacráneos al Campamento Mojache.", description = "Saludos, <clase>. He viajado con mis compañeros hasta Feralas en una misión de nuestros maestros Orgrimmar.\n\nMi compañera Korrin adora la ropa nueva, así que voy a hacerle una nueva capa adornada con las ligeras alas de los dragones feéricos. ¿Podrías ayudarme? Los duendes dardo con las mejores alas están al oeste del Campamento Mojache.", progress = "Los duendes dardo pueden ser criaturas muy peligrosas si se les provoca. Ten cuidado cuando trates con ellos, <clase>. Y ten mucho más cuidado con los elfos de la noche que pueda haber por la zona. Esos molestos seres tienen por costumbre acechar en los bosques también.\n\nCuando tengas alas suficientes, dímelo. ¡Te recompensaré bien!", completion = "¡Estupendo, &lt;clase&gt;! Estas nos vienen de maravilla. Hay suficientes para hacerle la capa a Korrin, ¡incluso puede que sobren! Gracias.\n\nEspero que tu viaje no fuera demasiado peligroso. Feralas no es conocido por su compasión precisamente, pero está claro que no te ha ido tan mal.\n\nGracias otra vez, &lt;clase&gt;.", rewards = "Recibirás:\n\n"},
[2974] = {title = "Un descubrimiento siniestro", objective = "Lleva 20 cuernos Tótem Siniestro a Krueg Machacacráneos al Campamento Mojache.", description = "¡¿Qué?! ¿Que te has cruzado con tauren en esta zona? ¡Será broma! ¡¿Qué aspecto tenían?!\n\n¡El clan Tótem Siniestro! ¿Te has encontrado con ellos? Belgrom, uno de nuestros maestros, tiene un gran interés por ellos... ¡Hasta ha ofrecido una recompensa!\n\nTienes que volver, <clase>. Vuelve y tráeme sus cuernos... Tráeme montones de ellos, todos los que puedas. Pero no subestimes a los Tótem Siniestro. Ve con cuidado. Vuelve y mátalos, deja que los ríos de Feralas bajen enrojecidos con su sangre oscura.", progress = "Escucha bien lo que te digo, <clase>, el clan Tótem Siniestro es malvado. Si sabes dónde están, tienes que volver y atacarles. Tienes que asestar tú el primer golpe... antes de que vengan a nuestras tierras y arrasen nuestros poblados y saqueen nuestras tierras.", completion = "Son unos buenos cuernos, &lt;clase&gt;. Buen trabajo. Dámelos. Y no te preocupes, me aseguraré de hablar con Belgrom en cuanto regresemos a Orgrimmar. Ya me has ayudado dos veces y por eso te doy mi marca. ¡Quizás pueda ayudarte en un futuro! Buena suerte.", rewards = "Recibirás:\n\n"},
[2975] = {title = "Los ogros de Feralas", objective = "Rok Orhan del Campamento Mojache quiere que mates a 10 ogros Gordunni, 10 ogros-magi Gordunni y 5 brutos Gordunni.", description = "Los efectos de la invasión de la tribu Gordunni no pasan desapercibidos en la zona. Mira a tu alrededor, <nombre>; su mera presencia marca esta tierra. Debemos hacerles retroceder antes de que empiecen a extenderse por todo Feralas.\n\nSigue el río hacia el norte y encontrarás su puesto de avanzada. Reduce su número, <nombre>. Debemos hacerles ver que no son bienvenidos aquí.", progress = nil, completion = "Excelente, &lt;nombre&gt;. La tierra se curará con el tiempo. No podemos rendirnos.", rewards = "Recibirás:\n\n"},
[2976] = {title = "Un descubrimiento siniestro", objective = "Lleva el cajón de cuernos Tótem Siniestro a Belgrom Destrozarroca a Orgrimmar.", description = "¡¿Qué?!\n\nAh, así que te hablé de la recompensa... Eso me pasa por abrir la bocaza, no aprenderé nunca.\n\nBueno, supongo que, como tú hiciste el trabajo duro, es justo que recibas la recompensa. Llévate los cuernos. Yo haré que te los metan en una caja. Busca a Belgrom Destrozaroca en el Valle del Honor. Querrá saber qué viste y te recompensará por tu duro trabajo.\n\nCuídate, <clase>, y gracias de nuevo por todo.", progress = "Rapidito, <clase>, tengo mucho que hacer hoy.", completion = "¡¿Los Tótem Siniestro?! Esto es interesante. ¿Dices que te los encontraste mientras cazabas en Feralas? ¿De verdad?\n\nLos míos querrán oír tus noticias, &lt;clase&gt;. Cuéntame más detalles. Yo iré contando tus monedas mientras lo haces.\n\nY antes de irte, debes saber que te agradezco la información que me has proporcionado.", rewards = "Podrás elegir una de estas recompensas:\n\nManteo de batalla duraOrbe Luna de jade\n\nTambién recibirás:\n\nManteo de batalla duraOrbe Luna de jade"},
[2977] = {title = "El regreso a Forjaz", objective = "Habla con Karnik el Historiador de Forjaz.", description = "Accedes de nuevo al pedestal y los discos de platino aparecen de repente en tu mochila.\n\nCon lo que te ha enseñado el guardapiedras y los discos de platino de nuevo en tu poder, ha llegado el momento de regresar a Forjaz e informar a la Liga de Expedicionarios de tu hallazgo.", progress = "Bienvenido de nuevo, <nombre>. ¿Les has sacado provecho a los discos o acabaste arando en el mar?", completion = "¿Te faltaban algunos discos? Vaya, es curioso pero parece que los Discos de Norgannon son solo una pieza más del conjunto; como las llaves de un llavero, si prefieres.\n\nSi encontramos las Placas de Uldum, ¡podremos por fin acceder al mismísimo complejo de Uldum! Imagínatelo, explorar el lugar en el que trabajaron los Creadores sin necesidad de utilizar una gran pala!\n\nY con esto no quiero decir que tenga algo de malo utilizar palas, cavar y todo eso.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2978] = {title = "El pergamino Gordunni", objective = "Lleva el pergamino Gordunni a Rok Orhan al Campamento Mojache.", description = "Cuando recoges el sucio pergamino, notas que parece describir algún tipo de reunión o ceremonia. A Rok Orhan le interesará este pergamino por lo que pueda significar para los Gordunni.", progress = "¿Qué es eso?", completion = "Este pergamino... ¿Dices que lo encontraste en la Avanzada Gordunni?", rewards = "Recibirás:\n\n"},
[2979] = {title = "Una ceremonia oscura", objective = "Rok Orhan del Campamento Mojache quiere que encuentres 1 orbe Gordunni.", description = "Parecen ser planes para algún tipo de ceremonia oscura, <nombre>. Por lo que puedo entender, este es un ritual celebrado por los Señores de los magos Gordunni.\n\nEncuentra a uno de estos Señores de los magos y quítale su orbe.\n\nEl orbe de un mago encierra un gran poder. Nos ayudará a descubrir más sobre esta ceremonia y sobre la magia de los Gordunni.", progress = "Tenemos que descubrir por qué están aquí los Gordunni, <nombre>.", completion = "Este orbe nos proporcionará más información sobre los Gordunni... información que utilizaremos para planear su caída, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2980] = {title = "Los ogros de Feralas", objective = "Rok Orhan del Campamento Mojache quiere que mates a 10 chamanes Gordunni, 10 brujos Gordunni y 5 destructores Gordunni.", description = "Ahora que has demostrado que eres <un auténtico guerrero/una auténtica guerrera>, confío en que tengas el valor para continuar tu misión.\n\nViaja a las Altas Tierras Salvajes, al suroeste de aquí. Ahí encontrarás unas estructuras abandonadas cuyos restos yacen en una tierra que antaño perteneciera a los elfos de la noche. Busca esas ruinas y, cuando veas árboles marchitos y tierra profanada, encontrarás a los Gordunni. Hazlos retroceder.\n\nEsos ogros son mucho más fuertes que los que has afrontado hasta ahora, <nombre>, ten cuidado.", progress = nil, completion = "Has demostrado que se puede confiar en ti, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2981] = {title = "Una amenaza en Feralas", objective = "Belgrom Destrozarroca quiere que hables con Rok Orhan, del Campamento Mojache.", description = "Feralas es el lugar ideal para que cualquier <clase> ponga a prueba sus cualidades. Allí los ogros Gordunni son los amos y señores y cada día crecen en número. Hay que detenerlos antes de que pongan en peligro nuestra presencia en Feralas.\n\nConozco a una guerrera que vive en el Campamento Mojache. Se llama Rok Orhan. Ve a buscarla. Ella te dará más detalles del estado de la situación.", progress = nil, completion = "Así que te envía Belgrom. Pues llegas justo a tiempo, &lt;raza&gt;.\n\nDeja que te explique la situación y luego decides si estás a la altura del desafío... Claro que si te envía Belgrom, es evidente que valor no te falta.", rewards = "Recibirás:\n\n"},
[2982] = {title = "Las Altas Tierras Salvajes", objective = "Ángelas Brisaluna quiere que mates a 8 brujos Gordunni, 8 chamanes Gordunni y 8 brutos Gordunni.", description = "Aunque es peligroso, Troyas y yo hemos estado investigando las ruinas que cubren los bosques de Feralas.\n\nPero últimamente, Troyas ha empezado a ir solo a las Altas Tierras Salvajes y eso me preocupa mucho. Los ogros Gordunni han invadido la zona y no son muy simpáticos con los que entran en su territorio.\n\nEsperaba que pudieras viajar a las ruinas, que están al sureste, y que eliminaras a algunos de los orcos. Así el área será más segura para Troyas. Y yo te lo agradecería mucho.", progress = nil, completion = "Te agradezco mucho lo que has hecho por mí.\n\nPero, por favor, ni una palabra a Troyas sobre esto.", rewards = "Recibirás:\n\n"},
[2983] = {title = "La llamada del Fuego", objective = "Busca a Kranal Fiss en Los Baldíos.", description = "Ha pasado algún tiempo desde tu última prueba, <nombre>. Hasta el momento has sido paciente en tus estudios, pero llegó el momento de que adquieras mayores poderes... es hora de que aprendas el auténtico poder. El elemento del fuego será tuyo si estás <dispuesto/dispuesta> a pagar el precio.\n\nBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña casa al norte de El Cruce. Pero cuidado: si llegas hasta las tierras malditas de los elfos de la noche, habrás ido demasiado lejos.", progress = nil, completion = "Así que... te manda Swart, ¿eh?\n\nBien. Siempre he confiado en Swart. Si te envía a mí, es que estás listo para participar en la siguiente sapta... la sapta de fuego.\n\nYa puedo ver esa chispa, esa llama de impaciencia en tus ojos. Has probado el poder y ahora estás listo para adquirir más. Paciencia, &lt;nombre&gt;. Recuerda lo que la tierra te enseñó. El poder y la destrucción llegan fácilmente, pero siempre has de saber cuándo detener tu mano.", rewards = "Recibirás:\n\n"},
[2984] = {title = "La llamada del Fuego", objective = "Busca a Kranal Fiss en Los Baldíos.", description = "Ha pasado tiempo desde tu última prueba, <nombre>. Hasta ahora has demostrado paciencia en tus estudios, pero ha llegado el momento de que aprendas algo más. Debes aprender de la destrucción... y el caos. De un elemento: el fuego. Podrás comprenderlo, si estás <dispuesto/dispuesta> a pagar el precio.\n\nBusca a Kranal Fiss en Los Baldíos. Vive en una pequeña casa al norte de El Cruce. Pero cuidado: si llegas hasta las tierras de los elfos de la noche, habrás ido demasiado lejos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2985] = {title = "La llamada del agua", objective = "Encuentra a Islen Vident'aqua en Los Baldíos.", description = "<Dispuesto/Dispuesta> pareces para la prueba siguiente, <nombre>. Paciencia en tus estudios has mostrado pero de aprender aún más la hora ha llegado. Ha llegado la hora de que aprendas cosas sobre la pureza del agua.\n\nBusca a Islen Vident'aqua en Los Baldíos. Se encuentra por la costa oriental, al sur del Río Furia del Sur y de Trinquete. La encontrarás en su cabaña de pesca. Que la Fortuna te sea favorable, <nombre>.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2986] = {title = "La llamada del agua", objective = "Encuentra a Islen Vident'aqua en Los Baldíos.", description = "Mucho tiempo ha pasado desde que a prueba te pusieron, <nombre>. Paciencia en tus estudios hasta ahora has mostrado pero de aprender aún más la hora ha llegado. Ha llegado la hora de que todo lo sepas sobre la pureza del agua.\n\nBusca a Islen Vident'aqua en Los Baldíos. Se encuentra por la costa oriental, al sur del Río Furia del Sur y de Trinquete. La encontrarás en su cabaña de pesca. Que la Fortuna te sea favorable, <nombre>.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2987] = {title = "El cobalto Gordunni", objective = "Orwin Gizzmick, del Campamento Mojache, quiere 12 muestras de cobalto Gordunni.", description = "Acabo de llegar a Feralas y he oído que los ogros Gordunni del norte tienen minerales interesantes. No sé qué estarán tramando, ¡pero protegen sus minerales casi de forma sospechosa! No los dejan por ahí, no, los entierran para que gente como yo no consiga echarles el guante.\n\nSi quieres ayudarme, te daré mi pala. Ve a la Avanzada Gordunni que hay al norte de aquí y desentiérrame un poco de cobalto Gordunni. Cuando veas un brillo azul, ¡empieza a cavar!", progress = "¡Ve allí y empieza a cavar!\n\nYo tengo que ir a sitios y ver a gente, ¡el tiempo es oro!", completion = "¡Son perfectas! Me las llevaré conmigo en mi viaje, &lt;nombre&gt;. Seguro que consigo encontrar a alguien que me pague bien por este cobalto.\n\nTen. Por las molestias.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas del MaharishiCapa de admirador de estrellas\n\nTambién recibirás:\n\nBotas del MaharishiCapa de admirador de estrellas"},
[2988] = {title = "Las jaulas de Secacorteza", objective = "Mira en las jaulas de los poblados Secacorteza y vuelve a ver al maestro de grifos Garracha.", description = "¡El hijo de nuestro mejor grifo ha desaparecido! El hijo de Alarrápida, Picoafilado, se perdió hace unos días cuando cazaba. Creemos que lo tienen los trols.\n\n¡Ayúdanos! Busca entre las jaulas de los dos poblados Secacorteza, a ver si lo encuentras.", progress = nil, completion = "¿Que no has encontrado a Picoafilado? Eso es una mala noticia; es el jefe de la futura generación de grifos. Sin él se perderá gran parte de su fuerza y empuje.", rewards = "Recibirás:\n\n"},
[2989] = {title = "El Altar de Zul", objective = "Mira en el Altar de Zul y vuelve a ver al maestro de grifos Garracha.", description = "Los Secacorteza no tienen a Picoafilado. A lo mejor fueron los trols Vilrama. Busca al grifito en el Altar de Zul, en el extremo occidental del territorio Vilrama.", progress = nil, completion = "¿No has encontrado a Picoafilado? Eso es malo y bueno.\n\nAl menos no estaba en el altar de los sacrificios.\n\nGracias por tu ayuda, aunque me queda la tarea más difícil por delante.", rewards = "Recibirás:\n\n"},
[2990] = {title = "Thadius Sombramacabra", objective = "Lleva la pluma de Picoafilado a Thadius Sombramacabra a Las Tierras Devastadas.", description = "Llévale esta pluma al adivino Thadius Sombramacabra. Se fue de Pico Nidal hace años para estudiar ciencias oscuras, pero sigue siendo un Martillo Salvaje.\n\nA lo mejor puede ayudarnos a encontrar a Picoafilado.\n\nEsta pluma estaba en su nido. Con ella y un poco de suerte, Thadius usará sus poderes de adivinación para localizar al grifo.\n\nSuerte, <nombre>. Thadius vive en las Tierras Devastadas, en la torre de los magos del Castillo de Nethergarde.", progress = "¿Traes un regalo, <nombre>?", completion = "Ah, sí. Esta pluma pertenece a un noble grifo destinado a grandes cosas... si llega a la edad adulta.\n\nEscúchame si quieres encontrarlo, &lt;clase&gt;...", rewards = "Recibirás:\n\n"},
[2991] = {title = "Medallón de Nekrum", objective = "Lleva el medallón de Nekrum a Thadius Sombramacabra a Las Tierras Devastadas.", description = "Voy a averiguar en dónde está tu grifo, pero primero haz algo por mí.  Hay un trol en Zul'Farrak, Nekrum Cometripas, que tiene un medallón que me gustaría estudiar. Tráeme ese medallón y usaré mis poderes para encontrar a Picoafilado.\n\nZul'Farrak está al otro lado del océano, al oeste, en el desierto de Tanaris.", progress = "¿Traes el medallón de Nekrum?  Estoy deseando estudiar sus poderes.", completion = "Se dice que concede larga vida a los trols, pero a un precio muy alto. Espero poder extraer sus poderes sin sufrir sus desagradables efectos secundarios.\n\nAgradezco tu ayuda, &lt;nombre&gt;. Ahora cumpliré mi promesa.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2992] = {title = "El ritual de adivinación", objective = "Espera a que Sombramacabra realice el ritual de adivinación.", description = "Hazme sitio mientras realizo el ritual de adivinación. Encontraré la conexión entre la pluma y su dueño y la seguiré hasta llegar a Picoafilado.\n\nBueno... si todo sale bien. Si no, pues... Cierra los ojos.", progress = nil, completion = "El ritual ha salido bien; he visto a Picoafilado y puedo decirte dónde encontrarlo.", rewards = "Recibirás:\n\n"},
[2993] = {title = "Vuelve a las Tierras del Interior", objective = "Habla con el maestro de grifos Garracha.", description = "Picoafilado está enjaulado en un precipicio en la cumbre de Jintha'Alor, en las Tierras del Interior. La llave de la jaula está en poder de la Sacerdotisa Vil Hexx, suma sacerdotisa de los trols Vilrama.\n\nYa está. He terminado. Te deseo suerte en tu misión, <nombre>.", progress = nil, completion = "¿Qué ha dicho Sombramacabra?\n\n¿Cómo que los Vilrama tienen a Picoafilado? ¡Eso es intolerable! ¡Tenemos que rescatarlo!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[2994] = {title = "Salvar a Picoafilado", objective = "Llega hasta la cima de Jintha'Alor, mata a la Sacerdotisa Vil Hexx, consigue la llave de la jaula y rescata a Picoafilado.", description = "¡Tenemos que rescatar a Picoafilado!\n\n¡Debes llegar hasta Jintha'Alor, en el corazón del territorio Vilrama, conseguir la llave que tiene la Sacerdotisa Vil Hexx, encontrar la jaula y liberar al grifo!", progress = nil, completion = "Los padres de Picoafilado acaban de regresar con su hijo... ¿Eh? No lloro, es que se me ha metido algo en el ojo...\n\nTe agradecemos infinitamente lo que has hecho, &lt;nombre&gt;. Los grifos de Pico Nidal y sus jinetes cantarán tus alabanzas durante mucho, mucho tiempo.", rewards = "Podrás elegir una de estas recompensas:\n\nMartillo Tormenta de Jinete de grifosLeotardos de Jinete de grifos\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nMartillo Tormenta de Jinete de grifosLeotardos de Jinete de grifos"},
[2995] = {title = "Líneas de comunicación", objective = "Oran Cortopiente de Entrañas quiere que viajes a las Tierras del Interior y que quemes las actas de Vallealto, el informe de Vallealto y las notas de Vallealto.", description = "La Avanzada Quel'Danil es el centro de comunicación de los elfos nobles de las Tierras del Interior. Sus embajadores están trabajando para establecer relaciones con otras razas con las que compartir sus conocimientos sobre los trols Secacorteza que viven en la zona sur de las Tierras del Interior. Todos los documentos relativos a sus comunicaciones se guardan en la avanzada.\n\nTenemos que evitar las alianzas entre Vallealto y otros pueblos, <nombre>.\n\nDestruye los documentos de Vallealto. Tenemos que quemar sus líneas de comunicación... literalmente.", progress = nil, completion = "Muy bien, &lt;nombre&gt;. Mientras en Vallealto intentan recuperar la información perdida, nosotros podremos idear más modos de cortar sus comunicaciones.", rewards = "Recibirás:\n\n"},
[2996] = {title = "Buscando a Strahad", objective = "Habla con Strahad Farsan en Trinquete.", description = "Te he enseñado mucho, pero ha llegado el momento de que cambies de instructor. Se llama Strahad y vive en Trinquete. Es humano, pero no vayas a creer por eso que es débil. Su aspecto no refleja su poder; te enseñará invocaciones más potentes. Lo encontrarás en el puerto de Trinquete, cerca de su torre.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2997] = {title = "El Libro de la Divinidad", objective = "Ve a Forjaz y habla con Tiza Forjabatalla.", description = "Hola, <nombre>. Sé que estás <ocupado/ocupada> ayudando a las gentes de Dun Morogh, pero cuando tengas tiempo, deberías ir a Forjaz y hablar con una persona muy respetada por aquí: Tiza Forjabatalla. Los paladines más jóvenes deben acudir a ella cuando llega el momento de que aprendan más sobre la Luz y sobre lo que se espera de ellos.\n\nLa encontrarás escaleras arriba, en la Sala de los Misterios. Por si nunca has estado allí, está en La Sala Mística. Buena suerte, <nombre>.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[2998] = {title = "Libro de la Divinidad", objective = "Ve a Ventormenta y habla con Duthorian Rall.", description = "Saludos, <hermano/hermana>. Duthorian Rall, uno de nuestros paladines más influyentes, estuvo hablando de ti hace poco. Le oí decir que era hora de ponerte a la prueba... Y bueno, también dijo algo sobre lo bien que estás yendo hasta ahora y eso.\n\nSi yo fuera tú, iría a Ventormenta y hablaría con él. Seguramente estará en la Catedral de la Luz. Ahí es donde pasa la mayor parte de su tiempo.", progress = nil, completion = "En Ventormenta se está de maravilla, ¿no te parece?\n\nNo podías llegar en mejor momento, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[2999] = {title = "El Libro de la Divinidad", objective = "Ve a Forjaz y habla con Tiza Forjabatalla.", description = "Hola, <nombre>. Sé que estás <ocupado/ocupada> ayudando a las gentes de Dun Morogh, pero cuando tengas tiempo, deberías subir las escaleras y hablar con una persona que es muy respetada por aquí: Tiza Forjabatalla. Los paladines más jóvenes deben acudir a ella cuando llega el momento de que aprendan más sobre la Luz y sobre lo que se espera de ellos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3000] = {title = "El Libro de la Divinidad", objective = "Ve a Forjaz y habla con Tiza Forjabatalla.", description = "Hola, <nombre>. Sé que estás <ocupado/ocupada> ayudando a las gentes de Azeroth, pero cuando tengas tiempo, deberías volver a Forjaz y hablar con una de las personas más respetadas de tu casa: Tiza Forjabatalla. Los paladines enanos más jóvenes deben acudir a ella cuando llega el momento de que aprendan más sobre la Luz y sobre lo que se espera de todos nosotros.\n\nLa encontrarás escaleras arriba, en la Sala de los Misterios, que a su vez se encuentra en La Sala Mística.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3001] = {title = "Buscando a Strahad", objective = "Habla con Strahad Farsan en Trinquete.", description = "Yo te he instruido hasta ahora pero ha llegado el momento de que cambies de instructor. Se llama Strahad y vive en Trinquete. Es un humano, pero no vayas a creer por eso que es débil ni que ha de ser tu enemigo... al menos de momento... Es muy poderoso y merecedor de respeto, y puede enseñarte hechizos de invocación superior, hechizos que creo que estás <preparado/preparada> para conocer. Ve a buscarlo.\n\nLo encontrarás en el puerto de Trinquete, cerca de su torre.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3002] = {title = "El orbe Gordunni", objective = "Lleva el orbe Gordunni a Uthel'nay, que está en el Refugio Zarzaoscura de Orgrimmar.", description = "Aunque conozco bien a mi enemigo, soy solo un guerrero, <nombre>. No puedo ni empezar a decirte de qué es capaz este orbe.\n\nPero conozco a un mago de Orgrimmar que podrá decirnos más cosas sobre el orbe. Llévaselo a él.", progress = "¿Qué me has traído, <raza>?", completion = "Fascinante, &lt;nombre&gt;.\n\nEste orbe es muy potente, pero también muy peligroso.", rewards = "Recibirás:\n\n"},
[3022] = {title = "Un envío frágil", objective = "Llévale la caja cuidadosamente empaquetada a Erelas Cieloámbar, que está en Aldea Rut'theran.", description = "<nombre>, veo que ansías dedicarte a mi valerosa causa. ¡Pues claro que puedes hacer algo por mí!\n\nLlévale esta caja de huevos a Erelas Cieloámbar. Él es un druida que los sabe cuidar muy bien pues ayuda a salir del cascarón a los pequeños hipogrifos y después los instruye. Lo encontrarás en Teldrassil, en una pequeña aldea situada al sur que se llama Rut'theran.", progress = "Otro envío de parte de Curgle, ¿verdad?", completion = "Gracias por tu ayuda, &lt;nombre&gt;. La población de hiprogrifos no crecería de no ser por nosotros. Pero seguro que eso ya lo sabes pues has estado en Feralas y has visto con tus propios ojos la devastación causada por los Gordunni.\n\nBien, como puedes observar, tengo mucho trabajo así que será mejor que te vayas.\n\nGracias de nuevo, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[3042] = {title = "Temple trol", objective = "Lleva 20 viales de temple trol a Trenton Mazaligera en Gadgetzan.", description = "Los trols Furiarena de Zul'Farrak utilizan arenisca para crear sus materiales de temple y yo también puedo utilizarlo para templar mis creaciones, ¡sí! Es muy valorado entre los expertos, así que si me traes un buen suministro de temple, te recompensaré muy bien.\n\nZul'Farrak está al noroeste de Gadgetzan. Buena suerte.", progress = "¿Has traído el temple, <nombre>?", completion = "¡Ah, es un temple excelente! ¡Y hay mucho! ¡Podré trabajar muchos días antes de que se acabe!\n\nGracias, &lt;nombre&gt;. Por favor, acepta esto como pago.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3062] = {title = "Negro corazón", objective = "Talo Pezuñahendida, del Campamento Mojache quiere el corazón oscuro de Edana Garraodio.", description = "Edana Garraodio es la reina de las arpías de Nortearroyo que viven en las ruinas del oeste. Es el mal encarnado. He oído que su corazón es de cristal. Carece de sentimientos... aparte del odio que siente por todo lo que no sea ella. Debes enfrentarte a Edana y traerme su negro corazón.\n\nHe oído hablar de un cuerno que llevan esas arpías... Si lo tocan en las Piedras Garraodio, en la parte norte de las ruinas, su reina acude.\n\nImagina su sorpresa cuando vea que no son sus semejantes quienes la llaman, sino la muerte.", progress = "Debemos poner fin al odio que Edana propaga por la tierra.", completion = "Debes saber que Edana está verdaderamente muerta. Guardaré este corazón de recuerdo, por lo que representa. Gracias, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nGuantes CrestalaFaja de Stronghorn\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nGuantes CrestalaFaja de Stronghorn"},
[3063] = {title = "Vengarse de las Nortearroyo", objective = "Talo Pezuñahendida quiere que mates a 4 harpías Nortearroyo, 4 plumapícaros Nortearroyo, 4 clamavientos Nortearroyo y 4 destripadores Nortearroyo.", description = "En mis tiempos fui un avezado cazador. No había criatura a la que yo temiera. Hasta que...\n\nUn día estaba en el bosque, afilando mi espada después de una dura batalla y caí en una emboscada. Unas arpías me rodearon y... casi no lo cuento. Tuve que arrastrarme para ponerme a salvo.\n\nTodavía puedo oír el eco de sus gritos...\n\nDespués de aquello, no he vuelto a ser el mismo. Han pasado muchos años, pero el odio sigue hirviéndome en las venas. Debo desquitarme.\n\nPuedes encontrar a las arpías de Nortearroyo al oeste de aquí. Véngame, <nombre>.", progress = nil, completion = "Gracias, &lt;nombre&gt;. Has traído mucha paz a la conciencia de un viejo tauren.", rewards = "Recibirás:\n\n"},
[3065] = {title = "Tablilla sencilla", objective = "Lee la Tablilla sencilla y habla con Frang en el Valle de los Retos.", description = "Ah, mientras estaba fuera llegó un pergamino para ti, <nombre>.\n\nLéelo cuando tengas tiempo, si no me equivoco viene de parte del instructor de guerreros, Frang. Querrá tener unas palabras contigo cuando estés <listo/lista>. Lo encontrarás a las puertas de El Cubil, al abrigo de la sombra.", progress = "Otro <raza> para engrosar las filas. Parece lo bastante bueno.\n\nSeguramente no te será fácil blandir un buen arma como un hacha, pero una lanza es tan buena como cualquier otra cosa.\n\nBienvenido al Valle de los Retos.", completion = "Cuando sigas instruyéndote en las artes del &lt;clase&gt; encontrarás este lugar muy adecuado. Si tienes cualquier duda no temas en preguntar a cualquiera y cuando te sientas preparado vuelve a verme para recibir instrucción. Tengo el mismo dominio que cualquier otro &lt;clase&gt; del Valle y puedo transmitirte ese conocimiento... por un precio, claro.", rewards = "Recibirás:\n\n"},
[3082] = {title = "Tablilla con grabados", objective = "Habla con Jen'shan en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó una tablilla para ti, <nombre>.\n\nLéela cuando tengas tiempo, si no me equivoco viene de parte de la instructora de cazadores, Jeh'shan. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Jen'shan ve ante ella un poderoso <clase> y los espíritus dan su aprobación.\n\nLa senda del <clase> es uno de los caminos más antiguos. La Horda se dirigió a nosotros cuando su lucha era solo por la supervivencia en Azeroth y nos pidieron que les enseñáramos y que les mostráramos muchos secretos. Ya eran fuertes: fuertes en las artes chamánicas y fuertes en las artes del guerrero. Pero la senda del <clase> no era su senda... entonces.", completion = "Ahora les enseñamos todo eso y ellos nos enseñan otras cosas. Nos hacemos una única raza... o casi. Somos aliados desde hace mucho, así que recuerda ayudarles. Y Jen'shan recordará ayudarte.\n\nJen'shan te instruye en las artes del &lt;clase&gt;, te instruye bien. No olvides nunca, el &lt;clase&gt; es muy respetado en la tribu. Tú ir a tener un importante papel en el futuro... no olvides nunca.\n\nCuando lo necesites, ven a Jen'shan. Ella te enseña todo lo que pueda cuando crea que estás preparado.", rewards = "Recibirás:\n\n"},
[3083] = {title = "Tablilla encriptada", objective = "Lee la Tablilla encriptada y habla con Rwag en el Valle de los Retos.", description = "Ah, mientras estaba fuera llegó una tablilla para ti, <nombre>.\n\nLéela cuando tengas tiempo, si no me equivoco viene de parte del instructor de pícaros, Rwag. Querrá tener unas palabras contigo cuando estés <listo/lista>.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3084] = {title = "Tablilla inscrita con runas", objective = "Habla con Shikrik en el Valle de los Retos.", description = "Ah, mientras estaba fuera llegó una tablilla para ti, <nombre>.\n\nLéela cuando tengas tiempo, si no me equivoco viene de parte del instructor de chamanes, Shikrik. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Ah, bienvenido, <nombre>. Me alegra que hayas venido. Con tantos alumnos a los que instruir mis días son largos y ajetreados, pero la llegada de alguien como tú me hace sentir realizada... Todavía recuerdo los días en los que me instruía uno de sus chamanes.", completion = "Como seguramente ya sabrás, los elementos nos confieren nuestro poder. Fuego, tierra, agua y aire... esas son nuestras herramientas. \n\nNuestra fuerza nace de los espíritus de nuestros antepasados. Somos los líderes de nuestro pueblo. Lo que Thrall es para la Horda somos nosotros para los poblados y ciudades que visitamos. Buscan en nosotros sabiduría y protección. Para estar más preparado debes entrenar e instruirte a menudo. Cuando sientas que has ganado más poder ven a verme y te enseñaré todo lo que pueda.", rewards = "Recibirás:\n\n"},
[3085] = {title = "Tablilla Sagrada", objective = "Lee la Tablilla sagrada y habla con Ken'jai a las puertas de El Cubil en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó una tablilla para ti, <nombre>.\n\nLéela cuando tengas tiempo, si no me equivoco viene de parte del instructor de sacerdotes, Ken'jai. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Lo lograste... ¡bien, bien! Los espíritus haberme dicho que tú venir para ver a Ken'jai pronto. Déjame ser de los primeros en la bienvenida darte al Valle de los Retos. Este lugar no ser tu hogar mucho tiempo, pero mientras tanto, ¡aprovechar tanto como puedas!", completion = "Cuando vayas poniéndote a prueba verás que tener mejor y mejor control de los espíritus... recurrir a ellos para cosas más y más grandes... y allí entrar en escena el viejo Ken'jai. Ven a hablar con el viejo Ken'jai cuando creer que estar preparado. Si espíritus de acuerdo te enseñaremos habilidades más poderosas... pero solo si los espíritus de acuerdo.\n\nAhora vete, hablar con el resto de gente del Valle. Vuelve a hablar luego conmigo.", rewards = "Recibirás:\n\n"},
[3086] = {title = "Tablilla con glifos", objective = "Lee la Tablilla con glifos y habla con Mai'ah junto a la entrada a El Cubil, en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó una tablilla para ti, <nombre>.\n\nLéela cuando tengas tiempo, si no me equivoco viene de parte de la instructora de magos, Mai'ah. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Parece que tú ya complacer a Mai'ah, <nombre>. Ser bueno. Das respeto a Mai'ah y Mai'ah te devuelve respeto. Pero ya vale, tú aquí estás para magia aprender, no para a Mai'ah oír.\n\nSolo una cosa haber que Mai'ah puede enseñarte de magia, <nombre>: cómo parar la corrupción.", completion = "Tú estar entre los seres más poderosos del planeta, &lt;nombre&gt;... no hay duda. Y todos lo saben... ¡lo saben bien!\n\nPero debes estar cansado! No haber orco, ni elfo, ni enano al que tú temas... pero haber cosas que no puedes ver. Magia ser poderosa. Te obliga a hacer cosas que no quieres hacer. Te hace creer que eres más poderoso de lo que ser. Ten paciencia. Sé listo.\n\nCuando creas que estás preparado para instruirte vuelve con Mai'ah. Te enseña lo que estás listo para aprender, sin problema.", rewards = "Recibirás:\n\n"},
[3087] = {title = "Pergamino con grabados", objective = "Lee el Pergamino con grabados y habla con Jen'shan en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó un pergamino para ti, <nombre>.\n\nLéelo cuando tengas tiempo, si no me equivoco viene de parte de la instructora de cazadores, Jeh'shan. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Ah, por fin has venido. Bien. Jen'shan estar esperándote.\n\nMuchos creer que es un camino fácil, pero Jen'shan dice no es verdad. Para dominar las bestias de Durotar hay que tener habilidades y dominar las artes arcanas y hablar con nuestros antepasados.\n\nDisparar un rifle o un arco puede no ser tan peligroso como lanzar un hechizo, pero es igual de peligroso para tu enemigo.", completion = "Cuando hayamos terminado, deberías ir a hablar con el resto de los ciudadanos del Valle. Ellos te piden hacer cosas... cosas que probarán tu habilidad y te enseñarán algunos de los peligros de esta tierra.\n\nCuando crecer tu experiencia y saber, necesitarás que Jen'shan te enseñe mejores técnicas y habilidades. Vuelve mucho a Jen'shan. Te haré saber lo que puedo enseñarte.", rewards = "Recibirás:\n\n"},
[3088] = {title = "Pergamino encriptado", objective = "Lee el Pergamino encriptado y habla con Rwag en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó un pergamino para ti, <nombre>.\n\nLéelo cuando tengas tiempo, si no me equivoco viene de parte del instructor de pícaros, Rwag. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Shhh... no tan alto. No confío mucho en Nartok. No se puede andar jugando con alguien que tiene que esconderse más profundo que yo en una cueva.\n\nEspero que no hayas tenido ningún problema para encontrar tu camino, ¿verdad?", completion = "Ahora que sabes dónde me encuentro te diré algo: la Mano Destrozada me pidió que diera contigo. Quieren que te instruya y te mantenga informado de todo lo que necesiten. Aunque todavía no estés interesado en ayudarles (serías un idiota si no lo estuvieras) haré todo lo que pueda por instruirte... a cambio de un pequeño pago, por supuesto. Vuelve a verme cuando creas que estás listo y veremos si estás preparado para aprender.", rewards = "Recibirás:\n\n"},
[3089] = {title = "Pergamino inscrito con runas", objective = "Lee el Pergamino inscrito con runas y habla con Shilkrik en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó un pergamino para ti, <nombre>.\n\nLéelo cuando tengas tiempo, si no me equivoco viene de parte del instructor de chamanes, Shikrik. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Bien, ya has llegado. Ya estaba empezando a preguntarme cuándo aparecerías. Envié el pergamino hace tiempo... no sabía si había llegado.\n\nPero basta de chácharas... estás aquí porque has elegido, has elegido conducir las vidas espirituales de nuestro pueblo.", completion = "Serás el medio que utilizarán nuestros antepasados para comunicarse. Tendrás el poder de los elementos a tu entera disposición. Despedazar a tus enemigos será como un juego de niños y sanar a tus aliados tan fácil como respirar. Recuerda todo esto cuando afrontes nuevos retos y vuelve a verme siempre que quieras. Me quedaré aquí para enseñarte nuevos hechizos y para mostrarte el camino correcto cuando estés listo.", rewards = "Recibirás:\n\n"},
[3090] = {title = "Pergamino corrupto", objective = "Lee el Pergamino corrupto y habla con Nartok en El Cubil en el Valle de los Retos.", description = "Ah, mientras estabas fuera llegó un pergamino para ti, <nombre>.\n\nSiempre que puedo procuro evitar el trato con brujos, pero creo que deberías leerlo cuando tengas tiempo. Si no me equivoco viene de parte del instructor que te acaba de ser asignado, Nartok. Querrá tener unas palabras contigo cuando estés <listo/lista>.", progress = "Por fin, has llegado... y con discreción. Muy bien.\n\nEl mundo está lleno de idiotas, <nombre>. Hasta el poderoso Thrall tiene grandes fallos que la mayoría advierten. Da la espalda al poder necesario para lograr que esta tierra nos pertenezca por completo. No somos los mismos que fueron manipulados y utilizados por las criaturas del Vacío Abisal. Somos nuestro propio pueblo. Estamos orgullosos... y somos fuertes.", completion = "Así que te has unido a nuestras filas. No me entiendas mal, &lt;nombre&gt;. Solo por haber accedido a un poder que muchos envidiarían no significa que vayas a ser aceptado tan fácilmente. Thrall admite a los de nuestra clase en Orgrimmar porque todavía somos de los suyos... no puede darnos la espalda o nos haríamos tan penosos como los humanos que lo esclavizaron. Lo que quiero decirte es que tengas cuidado. Puedes ser poderoso pero si eres imprudente puedes darte por muerto.\n\nSi necesitas instrucción sobre hechizos vuelve a verme.", rewards = "Recibirás:\n\n"},
[3091] = {title = "La nota simple", objective = "Lee la nota simple y habla con Harutt Astatrueno en el Campamento Narache.", description = "<nombre>, hace un momento te vino a buscar una mensajera. Creo que venía de parte del instructor de guerreros Harutt. Si la nota es suya, será mejor que la leas pronto.", progress = "AL fin llegas, <clase>. Como decía en mi nota, somos una raza especial, como los chamanes y druidas de la tribu. Tú también eres importante; tenemos que defender las fronteras, acabar con los centauros e ir a la guerra.", completion = "Todos tenemos un papel en la tribu y todos pasamos los mismos ritos; no dejes que tu orgullo te diga lo contrario.\n\nYo me quedo aquí para entrenar a los guerreros que buscan la gloria del combate. Ven a verme cuando quieras, te daré la instrucción que pueda. Cuentas con la bendición de la tribu, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[3092] = {title = "La nota con grabados", objective = "Lee la nota con grabados y habla con Lanka Tirolargo en Mesa de la Nube Roja.", description = "<nombre>, hace un momento te vino a buscar una mensajera. Creo que venía de parte del instructor de cazadores Lanka. Si la nota es suya, será mejor que la leas pronto.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3093] = {title = "La nota con runas", objective = "Lee la nota con runas y habla con Meela Caminocaso en el Campamento Narache.", description = "<nombre>, hace un momento te vino a buscar una mensajera. Creo que venía de parte del instructor de chamanes Meela. Si la nota es suya, será mejor que la leas pronto.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3094] = {title = "La nota verde", objective = "Lee la nota verde y habla con Gart Correbruma en el Campamento Narache.", description = "<nombre>, hace un momento te vino a buscar una mensajera. Creo que venía de parte del instructor de druidas Gart. Si la nota es suya, será mejor que la leas pronto.", progress = "Es bueno que hayas llegado; los espíritus de los antepasados están inquietos en todo Mulgore, pero no sé por qué. Siempre debes escuchar a la naturaleza y ser su Avatar. No lo olvides.", completion = "Aunque estamos relacionados, no esperes una buena acogida de los elfos de la noche; su orgullo no les deja ver, aunque eso no es problema nuestro. Preocúpate solo de las enseñanzas de la Madre Tierra y de las lecciones que te daré. \n\nVen a verme a menudo, &lt;nombre&gt;. Descubrirás que la Madre Tierra recompensa a los más aplicados.", rewards = "Recibirás:\n\n"},
[3095] = {title = "Un pergamino simple", objective = "Lee el pergamino simple, luego ve a Camposanto y habla con Dannal Stern.", description = "Oye, mientras estabas por ahí ocupándote de la Plaga esa, llegó este pergamino para ti. Debe de ser importante porque parece que lleva el sello del instructor de guerreros Dannal. Yo me tomaría un momento para leerlo antes de ponerme en marcha otra vez.", progress = "Ah, así que has venido. Bien. Oí por ahí que hubo otros que escaparon a la \"muerte\" y se dirigían hacia aquí. Espero que esa resistencia tenga su compensación en un futuro.\n\nDebes a Sylvanas tu libertad. La de tu cuerpo y la de tu mente. Aunque aún te torturan los efectos de la plaga, ahora eres libre de ir a donde te parezca.", completion = "Estaré aquí para instruirte a medida que vayas adquiriendo experiencia. Ven a buscarme siempre que quieras. Cobro una tasa mínima, pero verás que todo lo que puedo enseñarte merece la pena.\n\nAhora ve, y prueba tu habilidad. A medida que ganes experiencia, irás aumentando también tus conocimientos y algunos de ellos podrás discutirlos conmigo. De momento, debes saber que Sylvanas necesitará guerreros con tu fuerza en el futuro. Serás la espina dorsal de nuestra salvación. No lo olvides.", rewards = "Recibirás:\n\n"},
[3096] = {title = "Un pergamino encriptado", objective = "Lee el pergamino encriptado, luego ve a Camposanto y habla con David Trias.", description = "Oye, mientras estabas por ahí ocupándote de la Plaga esa, llegó este pergamino para ti. Debe de ser importante porque parece que lleva el sello de David Trias, el instructor de pícaros. Yo me tomaría un momento para leerlo antes de ponerme en marcha otra vez.", progress = "¿Así que lo has conseguido? Bien, porque a medida que pasa el tiempo, necesitamos más pícaros. Los acechasombras te buscarán cuando crean que ha llegado el momento, así que no vayas a meter la pata o hacer algo de lo que puedas arrepentirte. Necesitan agentes profesionales... no carteristas torpes.", completion = "Paso la mayor parte del tiempo en la posada. Me fijo en las caras nuevas, por si aparece alguien prometedor. Si no te metes en líos, haré lo posible por enseñarte algunas cosas. Vuelve cuando quieras y veremos si estás listo para una o dos habilidades nuevas.", rewards = "Recibirás:\n\n"},
[3097] = {title = "Un pergamino sagrado", objective = "Lee el pergamino sagrado, luego ve a la iglesia de Camposanto y habla con el clérigo oscuro Duesten.", description = "Oye, mientras estabas por ahí ocupándote de la Plaga esa, llegó este pergamino para ti. Debe de ser importante porque parece que lleva el sello del instructor de sacerdotes Duesten. Yo me tomaría un momento para leerlo antes de ponerme en marcha otra vez.", progress = "¡Ah! Así que entre el fuego, la plaga, la lucha y males peores... llama a mi puerta otro \"creyente\". Pues bien, deja que te dé la bienvenida, <clase>. Y concédeme un minuto para explicarte un par de cosas antes de decidir que es hora de buscar lo que sea que estés buscando: la Luz Sagrada ya no te concierne; los espíritus de tus antepasados no son más que leyendas y las alimañas del vacío Abisal no te quieren.\n\n¿Me sigues?", completion = "Solo hay una cosa que debes saber: hemos sobrevivido solo con voluntad. Es la fe en nosotros mismos lo que nos separa de los demás y, con nuestros poderes, provocaremos un gran cambio en todo Azeroth. Los débiles acabarán dependiendo de ti. Los leprosos te llamarán señor. Y los ignorantes se volverán a ti en busca de guía. Mi deber es asegurarme de que tienes las herramientas necesarias para que, cuando llegue el momento, estés preparado. Cuando adquieras más experiencia, búscame. Si estás preparado, te enseñaré poderes mayores.", rewards = "Recibirás:\n\n"},
[3098] = {title = "Un pergamino con glifos", objective = "Lee el pergamino con glifos, luego ve a la iglesia de Camposanto y habla con Isabella.", description = "Oye, mientras estabas por ahí ocupándote de la Plaga esa, llegó este pergamino para ti. Debe de ser importante porque parece que lleva el sello de la instructora de magos Isabella. Yo me tomaría un momento para leerlo antes de ponerme en marcha otra vez.", progress = "Sabría que acudirías, <nombre>. Y no porque no tengas voluntad para seguir tu propio camino, sino porque somos espíritus afines. Los dos hemos conocido el dolor. Los dos hemos sufrido. Y ahora queremos adquirir el poder necesario para recuperar lo que es nuestro: esta tierra, nuestra vida, nuestro destino. Pero a ti te queda mucho que aprender aún.", completion = "Debes volver a mí a menudo si quieres adquirir el poder necesario para destruir a quienes se nos oponen. Aún estás sometido a las reglas de la magia, &lt;nombre&gt;. La corrupción te seguirá como un lobo hambriento en la noche. Te darás cuenta de que su caricia es como una adicción, como una antigua amante que aún quiere estar a tu lado. Pero esas cosas pueden evitarse. Esas cosas solo te controlan si se lo permites.\n\nAhora, vete. Y vuelve a mí cuando seas más fuerte.", rewards = "Recibirás:\n\n"},
[3099] = {title = "Un pergamino manchado", objective = "Lee el pergamino manchado, luego ve a Camposanto y habla con Maximillion.", description = "Oye, mientras estabas por ahí ocupándote de la Plaga esa, llegó este pergamino para ti. Debe de ser importante porque parece que lleva el sello de Maximillion, el instructor de brujos. Yo me tomaría un momento para leerlo antes de ponerme en marcha otra vez.", progress = "Sé bienvenido, hermano. Sabía que vendrías. Era solo cuestión de tiempo.\n\nLo que dije despertó tu interés, ¿cierto? Algo dentro de ti te dijo que lo que yo declaraba era verdad. Bien.\n\nPero debes saber una cosa: No estoy traicionando a Sylvanas. Es más bien ella quien debería darme la razón, si tenemos en cuenta que fueron sus propias creencias las que pusieron a los Renegados en la posición en la que están ahora.", completion = "Hablé de los esclavos que siguen siendo esclavos, sea cual sea su amo. ¿Lo recuerdas? Ambos sabemos que es ese tipo de control, ese tipo de poder, lo que nos guía ahora. Buscamos criaturas que nos sirvan. Ahora somos más poderosos y merecemos más respeto del que nos dan los otros... Por eso, intentamos conseguirlo.\n\nSeré tu aliado en esta lucha... la lucha por conseguir nuestra libertad. La libertad de forjar nuestras propias alianzas. Cuando te sientas preparado, vuelve a mí. Yo te enseñaré todo lo que sé.", rewards = "Recibirás:\n\n"},
[3100] = {title = "Una carta simple", objective = "Lee la carta simple y habla con Llane Beshere en la Abadía de Villanorte.", description = "<nombre>, me han pedido que te diera esto en cuanto volvieras de los campamentos kóbold. Parece una carta sellada con la insignia de Llane, nuestro instructor de guerreros. Es mejor que la leas antes de dedicarte a cualquier otra cosa.", progress = "Veo que traes mi carta, <nombre>.\n\nEstán llegando muchos soldados a Elwynn, lo que es bueno para Ventormenta, pero malo para los kobolds y los Defias.", completion = "Prepárate, inspecciona el terreno y ven a verme cuando necesites instrucción, a cualquier hora.\n\nLos caballeros de la Mano de Plata defienden bien el lugar, pero los habitantes tienen otros problemas; ayúdalos en lo que puedas. Suerte.", rewards = "Recibirás:\n\n"},
[3101] = {title = "Una carta consagrada", objective = "Lee la carta consacrada y habla con el hermano Sammuel en la Abadía de Villanorte.", description = "<nombre>, me han pedido que te diera esto en cuanto volvieras de los campamentos kóbold. Parece una carta sellada con la insignia del hermano Sammuel, nuestro instructor de paladines. Es mejor que la leas antes de dedicarte a cualquier otra cosa.", progress = "Por fin nos conocemos. Te doy la bienvenida a Villanorte. Aquí aprenderás mucho, pero antes o después tu camino te llevará a Ventormenta. Hasta entonces, ten paciencia y aprende.", completion = "Debes saber que eres un símbolo para muchos; no lo olvides nunca. La Luz Sagrada brilla en ti, visible tanto para amigos como para enemigos.\n\nA medida que adquieres sabiduría y poder, tendrás que aprender otras facultades. Cuando quieras continuar tu instrucción, ven a verme de nuevo. ¡Suerte, &lt;clase&gt;!", rewards = "Recibirás:\n\n"},
[3102] = {title = "Una carta cifrada", objective = "Lee la carta cifrada y habla con Jorik Kerridan en el establo que hay tras la Abadía de Villanorte.", description = "<nombre>, me han pedido que te diera esto en cuanto volvieras de los campamentos kóbold. Parece una carta sellada con la insignia de Jorik, uno de nuestros instructores. Es mejor que la leas antes de dedicarte a cualquier otra cosa.", progress = "Me alegra ver que llegas en relativo secreto. No quiero llamar la atención... Seguro que lo entiendes.\n\n¿Algún problema? Me alegro. No tardarán en llegar.", completion = "Muchos quisieran descubrir nuestros secretos, &lt;nombre&gt;: aventureros, el IS:7, incluso la Hermandad Defias. Pero tú estás con nosotros; ¡que nadie te obligue a hacer nada que no quieras! Además, tenemos todas las cartas, ¿me entiendes?\n\nEn fin, solo quería presentarme y decirte que puedes contar conmigo cuando quieras.", rewards = "Recibirás:\n\n"},
[3103] = {title = "Una carta sagrada", objective = "Lee la carta sagrada y habla con la sacerdotisa Anetta en la Abadía de Villanorte.", description = "<nombre>, me han pedido que te diera esto en cuanto volvieras de los campamentos kóbold. Parece una carta sellada con la insignia de Anetta, nuestra instructora de sacerdotes. Es mejor que la leas antes de dedicarte a cualquier otra cosa.", progress = "Al fin llegas; sabía que vendrías. Que la Luz Sagrada te ilumine. Estos son tiempos difíciles, la Legión Ardiente sigue presente en Azeroth y Kalimdor intenta defenderse. Ayuda en todo lo que puedas.", completion = "A medida que ganas experiencia, ven a verme y te enseñaré lo posible. Hasta entonces, que la sabiduría guíe tus pasos. Recuerda que de ti depende mejorar el mundo.", rewards = "Recibirás:\n\n"},
[3104] = {title = "Una carta con glifos", objective = "Lee la carta con glifos y habla con Khelden Bremen en la Abadía de Villanorte.", description = "<nombre>, me han pedido que te diera esto en cuanto volvieras de los campamentos kóbold. Parece una carta sellada con la insignia de Khelden, nuestro instructor de magos. Es mejor que la leas antes de dedicarte a cualquier otra cosa.", progress = "Hola, <nombre>, me llamo Khelden. ¿Querías algo?", completion = "Sabía que mi carta no te apartaría de tu camino. Bien, ¿vienes a aceptar tu destino y enfrentarte a quienes se oponen a tu búsqueda de sabiduría y poder?\n\nTe temerán tanto como te respetarán, &lt;nombre&gt;. Y yo siempre estaré aquí para instruirte; solo tienes que venir a buscarme.", rewards = "Recibirás:\n\n"},
[3105] = {title = "Una carta impía", objective = "Lee la carta impía y habla con Drusilla La Salle junto a la Abadía de Villanorte.", description = "<nombre>, me han pedido que te diera esto en cuanto volvieras de los campamentos kóbold. Parece una carta sellada con la insignia de Drusilla, una de nuestras instructoras. Es mejor que la leas antes de dedicarte a cualquier otra cosa.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3106] = {title = "Runa simple", objective = "Lee la runa simple, luego ve al Valle de Crestanevada y habla con Thran Khorman.", description = "Mientras me estabas ayudando, me fue confiada esta runa para que te la entregara. Cuando tengas la oportunidad, léela. Creo que es de Thran, el instructor de guerreros. Échale un vistazo y, cuando puedas, ve a buscar a Thran a Yunquemar.", progress = "Vaya que hace frío hoy, ¿eh? Parece que la nieve no piensa irse nunca, <nombre>.\n\nYa debes haberlo notado, pero ahora hay gnomos viviendo en nuestra ciudad. Es porque provocaron una explosión que los expulsó de su hogar. No lo comentes por ahí, pero no puedo culparlos: creo que yo también usaría explosivos si Forjaz estuviera infestada con Hierro Negro u otros seres despreciables.", completion = "Es bueno que sepas que también verás otras cosas, como enanos usando magia en una pelea en lugar de un rifle, a algunos de los nuestros que veneran la Luz sagrada como los humanos, y por último, pero no menos importante, troggs. Ya aprenderás más sobre ellos, pero lo que debes saber es que estoy aquí para ayudarte.\n\nEstoy para enseñarte a ser un verdadero &lt;clase&gt;. Si crees que necesitas más entrenamiento, ven a verme y veremos qué se puede hacer.", rewards = "Recibirás:\n\n"},
[3107] = {title = "Runa consagrada", objective = "Lee la runa consacrada, luego ve al Valle de Crestanevada y habla con Bromos Grummner.", description = "Mientras me estabas ayudando, me fue confiada esta runa para que te la entregara. Cuando tengas la oportunidad, léela. Creo que es de Bromos, el instructor de paladines. Échale un vistazo y, cuando puedas, ve a buscar a Bromos a Yunquemar.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3108] = {title = "Runa con grabados", objective = "Lee la runa con grabados, luego ve al Valle de Crestanevada y habla con Thorgas Grimson.", description = "Mientras me estabas ayudando, me fue confiada esta runa para que te la entregara. Cuando tengas la oportunidad, léela. Creo que es de Thorgas, el instructor de cazadores. Échale un vistazo y, cuando puedas, ve a buscar a Thorgas a Yunquemar.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3109] = {title = "Runa encriptada", objective = "Lee la runa encriptada, luego ve al Valle de Crestanevada y habla con Solm Hargrin.", description = "Mientras me estabas ayudando, me fue confiada esta runa para que te la entregara. Cuando tengas la oportunidad, léela. Creo que es de Solm, el instructor de pícaros. Échale un vistazo y, cuando puedas, ve a buscar a Solm a Yunquemar.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3110] = {title = "Runa sagrada", objective = "Lee la runa sagrada, luego ve a Yunquemar y habla con  Branstock Khalder.", description = "Mientras me estabas ayudando, me fue confiada esta runa para que te la entregara. Cuando tengas la oportunidad, léela. Creo que es de Branstock, el instructor de sacerdotes. Échale un vistazo y, cuando puedas, ve a buscar a Branstock a Yunquemar.", progress = "¡Eh! ¡Lo has conseguido, <nombre>! ¡Estupendo!\n\nEmpezaba a creer que no llegarías y estaba a punto de perder la fe... ¿Lo pillas? ¿Lo de perder la fe? Era un chiste, muchacho... déjalo, no importa. Además, no has venido aquí por mis pésimos chistes.", completion = "Soy el único que puede instruirte ahora que comienzas a encontrar tu camino por el Valle de Crestanevada. Cuando te sientas preparado, vuelve a mí. Te enseñaré uno o dos hechizos para expulsar a esos malditos troggs del Valle.\n\nLa Luz está de tu lado, junto con Forjaz, &lt;nombre&gt;... Pero no te sientas presionado para convertirte en algo especial.", rewards = "Recibirás:\n\n"},
[3112] = {title = "Memorándum simple", objective = "Lee el memorándum simple, luego ve al Valle de Crestanevada y habla con Thran Khorman.", description = "Mientras me estabas ayudando, me fue confiado este memorándum para que te lo entregara. Cuando tengas la oportunidad, léelo. Creo que es de Thran, el instructor de guerreros. Échale un vistazo y, cuando puedas, ve a buscar a Thran a Yunquemar.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3113] = {title = "Memorándum encriptado", objective = "Lee el memorándum encriptado, luego ve al Valle de Crestanevada y habla con Solm Hargrin.", description = "Mientras me estabas ayudando, me fue confiado este memorándum para que te lo entregara. Cuando tengas la oportunidad, léelo. Creo que es de Solm, el instructor de pícaros. Échale un vistazo y, cuando puedas, ve a buscar a Solm a Yunquemar.", progress = "Me alegra ver que recibiste mi nota. Perdona por haber hecho que pareciera un memorándum de tus superiores... no era con mala intención. Lo hice porque creo que nosotros, junto con los demás de nuestra clase, tenemos que permanecer unidos.\n\nHay quien cree que tener a alguien como tú y a alguien como yo en la misma ciudad no es lo ideal, pero yo creo que es una buena cosa. ¿No estás de acuerdo?", completion = "Suelo mantenerme un poco al margen de todo, para evitar sermones si desaparecen cosas y eso, pero si necesitas algo, entrenamiento o así, vuelve.\n\nTú y yo nos entendemos bien. Ya sabes, a la hora de la verdad, solo puedes fiarte de la familia... Y familia es lo que somos, ¿no? Aparte de eso de que Gnomeregan esté siendo invadido y eso y todo lo demás que se te cruce en el camino, nos tenemos el uno al otro para cubrirnos las espaldas. ¿No estás de acuerdo?", rewards = "Recibirás:\n\n"},
[3114] = {title = "Memorándum con inscripciones", objective = "Lee el memorándum con inscripciones, luego ve a hablar con Marryk Nurribit, dentro de Yunquemar, sobre el Valle de Crestanevada.", description = "Mientras me estabas ayudando, me fue confiado este memorándum para que te lo entregara. Cuando tengas la oportunidad, léelo. Creo que es de Marryk, el instructor de magos. Échale un vistazo y, cuando puedas, ve a buscar a Marryk a Yunquemar.", progress = "¿Sí? ¿Qué quieres, <raza>? ¿Podrías hablar más alto, por favor? No oigo muy allá después de unas cervezas.", completion = "¡Oh, sí! Artes Arcanas... claro.\n\nEsto es lo que necesitas saber, &lt;nombre&gt;: no te metas en muchos líos, reserva algún dinerillo para invitarme a cerveza... o comprarme algún hechizo... y mantente bien alejadito de Gnomeregan. Eso es todo. ¡Que tengas un buen día! Estaré por aquí por si me necesitas, o por si quieres entrenar o... lo que sea.", rewards = "Recibirás:\n\n"},
[3115] = {title = "Memorándum corrupto", objective = "Lee el memorándum corrupto, luego ve a hablar con Alamar Grimm que está en Yunquemar, sobre el Valle de Crestanevada.", description = "Mientras me estabas ayudando, me fue confiado este memorándum para que te lo entregara. Cuando tengas la oportunidad, léelo. Creo que es de Alamar, el instructor de brujos. Échale un vistazo y, cuando puedas, ve a buscar a Alamar a Yunquemar.\n\nY ten cuidado, <nombre>: los de tu clase no son bien recibidos por estos lares.", progress = "Wonderful! You got my memorandum obviously.\n\nIgnore the fools around you, <nombre>. The Holy Light?! The sword and shield?! These aren't paths for ones as open minded as us. Look at what \"normal\" magic did to our home. Coupled with  those damned tinkerers, our race almost became extinct. And now we have to rely on the dwarves-- they'd rather spend time allying with humans than help us rebuild our own home. We're like second-class citizens. Have you seen the way they look at us?", completion = "But none of that matters. What matters is that you've seen your own Holy Light! You know where power truly comes from. You realize that having our own allies is much more... prudent. Special allies. Allies that, no matter the task, will obey till their last breath.\n\nAnd that's where I come in, &lt;nombre&gt;. I can train you in the beginnings of those special powers. Seek me out often and I will do what I can to teach you more spells.", rewards = "Recibirás:\n\n"},
[3116] = {title = "El sigilo simple", objective = "Lee el sigilo simple y habla con Alyssia en Cañada Umbría.", description = "Un mensajero de nuestra instructora de guerreros, Alyssia, ha traído este sigilo. Parece que quiere hablar contigo. Léelo y luego llévaselo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3117] = {title = "El sigilo con grabados", objective = "Lee el sigilo con grabados y habla con Ayanna Semprerguida en Aldrassil, en Cañada Umbría.", description = "Este siglo me lo entregó un mensajero de nuestra instructora de cazadores, Ayanna. Parece que quiere hablar contigo. Léelo y luego llévaselo.", progress = "Te doy la bienvenida a Cañada Umbría, <nombre>.\n\n¿Todo va bien?\n\nVoy a enseñarte técnicas de caza, a domar animales para que luchen junto a ti, a disparar con el arco y a respetar toda la tierra.", completion = "Ven a verme a menudo mientras estés en Cañada Umbría; te ayudaré a entender el rol del &lt;clase&gt; y tus capacidades.\n\nNo confíes demasiado en la ayuda de tus mascotas. En estos tiempos hay cosas más peligrosas de lo que puedas imaginar.", rewards = "Recibirás:\n\n"},
[3118] = {title = "El sigilo cifrado", objective = "Lee el sigilo cifrado y habla con Frahun Sombrusurro en Cañada Umbría.", description = "Un mensajero de nuestro instructor de pícaros, Frahun, ha traído este sigilo. Parece que quiere hablar contigo. Léelo y luego llévaselo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3119] = {title = "El sigilo sagrado", objective = "Lee el sigilo sagrado y habla con Shanda en Aldrassil.", description = "Un mensajero de nuestra instructora de sacerdotes, Shanda, ha traído este sigilo. Parece que quiere hablar contigo. Léelo y luego llévaselo.", progress = "Me alegro de que hayas llegado tan pronto, <nombre>.\n\nPuede que sepas que hay viajeros por todo Teldrassil; incluso se ha permitido el acceso a miembros de la Alianza para que se reúnan con otros Kaldorei aventureros.", completion = "Ahora no te preocupes por el mundo que está bajo Teldrassil. Ve a hablar con las gentes de Cañada Umbría, te pondrán al día en lo ocurrido. Ayuda en lo que puedas y ven a verme cuando quieras seguir con tu instrucción.", rewards = "Recibirás:\n\n"},
[3120] = {title = "El sigilo verde", objective = "Lee el sigilo verde y habla con Mardant Cebaforte en Aldrassil, en Cañada Umbría.", description = "Este sigilo me lo entregó un mensajero de nuestro instructor de druidas, Mardant. Parece que quiere hablar contigo. Léelo y luego llévaselo.", progress = "Admiro tu entusiasmo por continuar tu formación, <clase>.\n\n¿Has estado ya en el Sueño Esmeralda? Puede que aún no sea el momento... Pero todo llegará.\n\nAntes de eso, tenemos otros temas que comentar.", completion = "Te voy a instruir en la interpretación de los designios de los espíritus y en los secretos del Sueño Esmeralda. \n\nA medida que ganas poder, aprenderás más de nuestros trances del sueño. Hasta entonces, ven a verme cuando quieras para seguir tu instrucción.", rewards = "Recibirás:\n\n"},
[3121] = {title = "Una extraña petición", objective = "Lleva la cabeza reducida a Neeru Hojafuego a Orgrimmar.", description = "¡Neeru Hojafuego de Orgrimmar tiene lo que necesito para empezar aquí! Ha preparado un mejunje que necesito para mi... bueno, ya lo verás.\n\nPonte en marcha. ¡A Orgrimmar! Y llévate esto contigo. Es una cabeza reducida que prometí a Neeru a cambio de las hierbas. Es la cabeza de un pobre enano que se tropezó conmigo en el bosque. Seguro que a Neeru le encantará.\n\nEncontrarás a Neeru en el Circo de las Sombras.", progress = "¿Sí?", completion = "He estado esperando a que Uzer'i me entregara esto...\n\nDeja que te dé la mezcla de hierbas que preparé para este... experimento...", rewards = "Recibirás:\n\n"},
[3122] = {title = "Vuelve con el médico brujo Uzer'i", objective = "Entrega la bolsa de hierbas al médico brujo Uzer'i a Feralas.", description = "Aquí tienes las hierbas, <nombre>. Llévaselas al médico brujo Uzer'i.\n\nY dile por favor que estoy muy satisfecho con la cabeza reducida.", progress = "¿Has hablado con Neeru?", completion = "Es justo lo que necesito para preparar la vasija de muisek, &lt;nombre&gt;. Pronto veremos si mis últimos hechizos resultan ser tan potentes como espero.\n\nDame un momento, por favor. Debo celebrar el ritual y aplicar las hierbas.", rewards = "Recibirás:\n\n"},
[3123] = {title = "Probar la vasija", objective = "Viaja a las Tierras del Interior y localiza a los lechúcicos. Mata a 10 de ellos y utiliza la vasija de muisek para reducir y capturar a los lechúcicos caídos.\n\nLleva 10 muiseks de lechúcico y la vasija de muisek al médico brujo Uzer'i a Feralas.", description = "Esto va a ser una prueba. Del ritual que celebré y de tus habilidades. Coge esta vasija, <nombre>. Con ella tendrás el poder de reducir y capturar a una criatura.\n\nViaja a las Tierras del Interior y busca a las criaturas llamadas lechúcicos, las que una vez fueron mascotas de Elune, la diosa de los elfos de la noche. Tu objetivo serán los lechúcicos sanguinarios, primitivos y salvajes, <nombre>. Mata a 10 de ellos y utiliza la vasija de muisek para reducirlos y capturarlos antes de que sus espíritus escapen.", progress = "¿Pudiste capturar a los lechúcicos?\n\nSi tienes éxito, podremos encoger y capturar el muisek de las criaturas de Feralas.", completion = "Si una criatura sufre una muerte violenta, su muisek puede escapar y buscar venganza. Encogiendo las criaturas como hiciste tú, &lt;nombre&gt;, se impide que el muisek escape. ¡Ahora podemos usarlo para lo que queramos!", rewards = "Recibirás:\n\n"},
[3124] = {title = "Hipogrifos muisek", objective = "ACaba con 10 hipogrifos Plumavieja de cualquier tipo y utiliza la vasija de muisek para encogerlos y capturarlos.", description = "Has demostrado merecer mi confianza y tener facultad suficiente para utilizar a las vasijas de muisek. Ahora quisiera que capturases a otra criatura.\n\nLos hipogrifos han sido compañeros leales de los elfos de la noche durante muchos años; sus espíritus son leales e inquebrantables.\n\nTu tarea consiste en acabar con 10 hipogrifos Plumavieja y, rápidamente, encogerlos y capturarlos usando la vasija, de forma que podamos conservar su muisek. Podrás encontrarlos en las Altas Tierras Salvajes, al sur de aquí.", progress = "¿Lograste capturar a los hipogrifos?", completion = "Si, después de matar a un animal, permitiéramos que su muisek escapara, este podría tomar la forma de algo más grande. Y nunca estaríamos a salvo, pues buscaría venganza.\n\nEl que te he encomendado es un trabajo peligroso, &lt;nombre&gt;, pero lo has hecho bien.", rewards = "Recibirás:\n\n"},
[3125] = {title = "Dragones feéricos muisek", objective = "Mata a 8 duendes dardo o duendes dragón. Utiliza la vasija de muisek para reducir y capturar a los dragones feéricos caídos.", description = "<nombre>, ahora quiero que captures a una criatura que es de apariencia frágil pero que posee un espíritu poderoso, que es lo que me interesa de ella. Hablo del dragón feérico, una criatura que ha servido como aliada a los elfos de la noche. Debemos capturar uno para que la Horda pueda beneficiarse de la fuerza de su espíritu.\n\nAquí tienes la vasija, <nombre>. Mata a 8 duendes dardo o duendes dragón y captura su muisek. Puedes encontrarlos al oeste de aquí.", progress = "¿Conseguiste hacerte con el muisek de los dragones feéricos?", completion = "Gracias, &lt;nombre&gt;. Ahora tenemos los poderes espirituales del dragón feérico a nuestra disposición.\n\nEspero que aceptes una tarea más.", rewards = "Recibirás:\n\n"},
[3126] = {title = "Antárboles muisek", objective = "Mata a 3 Caminabosques deambulantes. Utiliza la vasija de muisek para reducir y capturar a los Antárboles caídos.", description = "Los Caminabosques deambulantes que vagan por el bosque son nuestro siguiente objetivo, <nombre>. Los elfos de la noche se han aliado con los antárboles para combatir a la Horda. Se mueven con lentitud, pero su papel como protectores es innegable.\n\nPronto podrán usar ese poder en nuestro beneficio.\n\nEncuentra a 3 Caminabosques deambulantes, mátalos y utiliza la vasija de muisek para reducirlos y capturarlos.", progress = "¿Lograste capturar a los Caminabosques deambulantes?", completion = "Gracias, &lt;nombre&gt;. Nuestra colección de muiseks está casi acabada.", rewards = "Recibirás:\n\n"},
[3127] = {title = "Gigantes de montaña muisek", objective = "Mata a 7 caminantes de la tierra o gigantes del risco. Utiliza la vasija de muisek para reducir y capturar a los gigantes de montaña caídos.", description = "La última tarea que te encargaré es que viajes al noroeste y encuentres al enemigo definitivo.\n\nLos gigantes de montaña dejaron sus montañas para ir a ayudar a los elfos de la noche cuando la Legión Ardiente apareció en el mundo. Los reconocerás por su descomunal talla y los encontrarás por la forma en la que el suelo temblará cuando viajes por su rocosa tierra.\n\nEstoy deseando controlar ese descomunal poder. Mata a 7 gigantes de montaña y luego utiliza la vasija de muisek para capturar su potente muisek.", progress = "¿Lograste capturar a los gigantes monteses?", completion = "Imagina las posibilidades que nos da lo que tenemos, &lt;nombre&gt;. Con los materiales que has reunido, podré atrapar para siempre el muisek de estas criaturas y hacer con él lo que me plazca.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3128] = {title = "Materiales naturales", objective = "Lleva 2 troncos astillados, 6 minerales taraceados, 20 tendones elásticos y 40 fragmentos metálicos al médico brujo Uzer'i al Campamento Mojache.", description = "Necesitaré algunos materiales para completar mi tarea... Podrías conseguírmelos a la vez que cumples otras tareas que te asignaré.\n\nNecesitaré un tronco astillado de un antárbol de Feralas. Y minerales taraceados. Los llevan los gigantes de montaña y los dragones feéricos del oeste. También necesito algún tendón elástico de los Lechúcicos de las Tierras del Interior o de los hipogrifos de Feralas. Y por último, necesito una gran cantidad de fragmentos metálicos de cualquiera de las criaturas que he mencionado.", progress = "¿Has podido reunir los materiales que necesito?", completion = "¡Fantástico! Estos materiales, combinados con el muisek, deberían procurarnos los resultados que queremos.", rewards = "Recibirás:\n\n"},
[3129] = {title = "Armas del espíritu", objective = "El médico brujo Uzer'i quiere que esperes mientras prepara las armas.", description = "Imbuir el muisek en las armas es un proceso difícil. Ahora que tengo todo el material que necesito y el muisek de las criaturas que has capturado, puedo empezar a trabajar. Por favor, espera. Volveré en un momento.", progress = nil, completion = "Tal y como prometí, &lt;nombre&gt;, el muisek de las criaturas ha quedado vinculado para siempre al poder de estas armas. \n\nAhora puedes hacer tuya su energía. Utilízala bien.", rewards = "Podrás elegir una de estas recompensas:\n\nPotencia del hipogrifoEspíritu del dragón feéricoFuerza del antárbolVoluntad del Gigante de montaña\n\nTambién recibirás:\n\nFuerza del antárbolPotencia del hipogrifoEspíritu del dragón feéricoVoluntad del Gigante de montaña"},
[3130] = {title = "En contra de los Crestafuria", objective = "Preséntate ante Latronicus Lanzaluna, en el Bastión Plumaluna.", description = "Voy a remitirte a mi segundo, Latronicus. Ha estado vigilando de cerca a las tropas naga hasta hoy. Evaluaré la situación general de esta zona, en lo que a los nagas se refiere; aunque me encantaría enviar a todo un ejército contra ellos, dejar la fortaleza expuesta a un ataque directo de la Horda –o peor– no es una solución deseable.\n\nEn fin, Latronicus espera. Has hecho un buen trabajo hasta ahora, <nombre>. Sigue así.\n\nRetírate.", progress = nil, completion = "Excelente, &lt;nombre&gt;. Me gusta que quienes trabajan para mí tengan esa actitud de \"puedo hacerlo\".\n\nLa general Plumaluna me ha encargado que me dirija a los naga de forma directa. Mis planes tienen dos objetivos: uno, debilitar tanto su número como su cadena de mando y, dos, descubrir qué están haciendo en las ruinas. Con tu ayuda, reduciremos la presencia naga sin sacrificar la seguridad del Bastión.", rewards = "Recibirás:\n\n"},
[3141] = {title = "Loramus", objective = "Escucha la historia de Loramus Thalipedes.", description = "¿Así que quieres enfrentarte a demonios? Harías bien en escuchar mi relato.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3161] = {title = "Gahz'rilo", objective = "Marvon Buscarroblones de Tanaris quiere que reúnas 30 adornos de gahz'rilo.", description = "¡<nombre>, yo soy un científico pero tengo alma de cazador de tesoros!\n\nMucho tiempo atrás, cuando los trols ocupaban estas tierras, crearon gran cantidad de adornos inspirados en Gahz'rilla, un hidra al que rindieron culto como a una deidad. Estos adornos están tallados en un material que yo denomino \"gahz'rilo\", derivado del propio nombre del dios hidra.\n\nYo mismo encontré algunos pero me gustaría tener más para poder estudiarlos a fondo. Quizás tú puedas buscarlos...\n\n¡Incluso he ideado un detector que hace de buscar gahz'rilo un juego de niños! ¡Simplemente cógelo y que empiece la búsqueda!", progress = "¿Tienes algún problema?\n\nHay gahz'rilo por todas partes en Tanaris, <nombre>. ¡Sigue buscando!", completion = "Estos adornos son perfectos, simplemente perfectos. Muchas gracias por tu ayuda, &lt;nombre&gt;.\n\n¡Ah! ¿Me devuelves mi casco?", rewards = "Podrás elegir una de estas recompensas:\n\nTúnica de vigilanteBastón de Conocimiento\n\nTambién recibirás:\n\nBastón de ConocimientoTúnica de vigilante"},
[3181] = {title = "El cuerno de la bestia", objective = "Lleva el cuerno enorme de Margol al montaraz Guijarro.", description = "El enorme cuerno está lleno de arañazos y grietas. Hay un objeto metálico peculiar incrustado. \n\nAl observarlo de cerca, ves que es una cabeza de hacha rota. En el metal están incritas las palabras: \n\nPropiedad del montaraz Guijarro. \n\nCuando saques la cabeza de hacha, se romperá en mil pedazos. \n\n¡Qué cosas!", progress = "No voy a abrir.", completion = "¡Eso es una falsificación!\n\nHe visto a Margol, sé que su cuerno es al menos 8 veces más grande que ese, &lt;nombre&gt;. Será de un morrotormenta de Los Baldíos. ¡A Guijarro no lo engaña nadie!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3182] = {title = "Certificado de autenticidad", objective = "Lleva el cuerno enorme de Margol al comisario Thorius a Forjaz.", description = "¿Cómo quieres que me crea eso? El cuerno de verdad tendría todavía mi hacha clavada. \n\nLlévale esa falsificación al comisario Thorius a Forjaz. Si confirma tu historia, te dará un certificado de autenticidad. Si me lo traes, te daré la llave de La Garganta de Fuego. \n\n¡Andando!", progress = "¡Saludos! ¿Deseas visitar el museo?", completion = "¿Qué es esto?\n\n¡No me lo puedo creer! ¿Te das cuenta de lo que es, &lt;raza&gt;? ¡Es el cuerno de Margol la Furiosa, el terror de La Garganta de Fuego!\n\n¿Puedo quedármelo?", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3201] = {title = "¡Al fin!", objective = "Lleva el certificado de autenticidad al montaraz Guijarro.", description = "No puedo creer que Guijarro dude de la autenticidad del cuerno. Entre tú y yo, creo que está celosa; quería matar a la bestia ella misma, por aquello del incidente y tal. \n\nAsí que necesitas un certificado de autenticidad; por supuesto, te lo has ganado. \n\n<Empieza a rellenar un documento.> \n\n¡Aquí lo tienes! LLévaselo a Guijarro.", progress = "No me lo creo.", completion = "No entiendo cómo pudiste derrotar a Margol, pero un trato es un trato; toma la llave.\n\nSi te pasa algo, te lo habrás buscado tú.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nLlave de la Garganta de Fuego"},
[3221] = {title = "Habla con Renferrel", objective = "Habla con el boticario Renferrel en El Sepulcro.", description = "Mientras estabas ahí fuera ayudando a nuestros mortacechadores, el boticario Renferrel te envió un llamamiento. No me dio ningún detalle, pero quería hablar contigo sobre los corazones de lobo que le entregaste.", progress = nil, completion = "Ah, muy bien. Hay un problema con los corazones de lobo que requiere nuestra atención.", rewards = "Recibirás:\n\n"},
[3261] = {title = "Jorn Visioceleste", objective = "Habla con Jorn Visioceleste en el Campamento Taurajo.", description = "Tu formación conmigo ha llegado a su fin, <nombre>. Para seguir aprendiendo, debes ir al Campamento Taurajo, hacia el sur, y habla con Jorn Visioceleste. Él seguirá con tu aprendizaje.", progress = nil, completion = "Saludos, joven. ¿Has venido para seguir el camino del cazador?\n\nMuy bien. Empecemos.", rewards = "Recibirás:\n\n"},
[3281] = {title = "La plata robada", objective = "Llévale la plata robada a Gazrog en El Cruce.", description = "Puede que hayas matado a los raptores ¡pero aún hay que recuperar la plata que robaron! Me han informado que los raptores tienen un gran número de nidos al sur de Trinquete, llaman a ese lugar Tierras de los Raptores. Es probable que llevaran allí nuestra plata robada.\n\nVe a ese refugio de raptores y busca la plata robada. Cuando la encuentres, devuélvemela.", progress = "¿Tienes la plata?", completion = "¡Eso es! Así que los raptores tenían la plata escondida en su guarida como pensábamos. Cuesta creer que estas bestias codicien la plata. ¡Quizás lo que cuentan de su inteligencia no es tan descabellado como creía!\n\nGracias, &lt;nombre&gt;. Toma esto como recompensa por tus servicios y que sepas que la guardia de El Cruce te debe el próximo mes de paga.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de senderismoLeotardos de colono\n\nTambién recibirás:\n\nLeotardos de colonoBotas de senderismo"},
[3301] = {title = "Mura Runatótem", objective = "Habla con Mura Runatótem en El Sepulcro.", description = "Mi compañera de clan, Mura Runatótem, se dirigía al Bosque de Argénteos así que podría ayudar a nuestros aliados no-muertos. Este bosque está enfermo, se está muriendo, y Mura tenía la esperanza de devolverle la vida. Quizás la extraña vida que hemos encontrado aquí en Los Baldíos pueda ayudarla en su trabajo en el Bosque de Argénteos.\n\nToma uno de los caparazones que recolectaste y llévaselo a Mura en el Bosque de Argénteos. La encontrarás en El Sepulcro. Con un poco de suerte, el estudio del caparazón le dará una idea de lo que necesita para curar ese lugar agonizante.", progress = "Huelo el polvo de Los Baldíos en ti, <clase>. Has viajado muy lejos.", completion = "¿Qué es esto? Este caparazón tiene un brillo interior, como si aún hubiera vida en su interior. ¡Asombroso! Debo examinarlo más a fondo.\n\nGracias, &lt;nombre&gt;. Con suerte, este caparazón contendrá un secreto que podré usar para ayudar a la pobre tierra del Bosque de Argénteos.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de siembraCapa de sembrador\n\nTambién recibirás:\n\nCinturón de siembraCapa de sembrador"},
[3321] = {title = "¿Has perdido esto?", objective = "Espera a que Trenton acabe su trabajo.", description = "Has hecho mucho por la Orden, <nombre>. La dedicación necesaria para llegar hasta aquí hubiera desalentado a cualquier otro. No me cabe duda, ni a Galvan tampoco, de que eres <uno/una> de los nuestros.\n\nPor eso considero que mereces una recompensa. No es nada de otro mundo, tan solo un pequeño detalle. Puede que conozcas sus propiedades, que creo que es lo que en realidad te ha traído hasta nosotros.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia de mitril de luz trémula"},
[3341] = {title = "Acaba con la amenaza", objective = "Andrew Brownell quiere que mates a Amnennar el Gélido y que le lleves su calavera.", description = "En Los Baldíos se encuentra una horrible maraña de vides llamadas Zahúrda Rajacieno. Aunque los primeros estudios de este lugar indicaban que no representaba una seria amenaza, nuestros últimos descubrimientos son un poco más desconcertantes...\n\nLos jabaespines de Zahúrda Rajacieno se han aliado con la Plaga. Un exánime llamado Amnennar el Gélido los gobierna, usando el poder de su vasto conocimiento para controlar cada uno de sus movimientos.\n\n<nombre>, Amnennar tiene un vínculo telepático directo con Ner'zhul que debemos eliminar y, para ello, hay que acabar con el Gélido cuanto antes.", progress = "Este tema es de suma importancia, <nombre>. Tenemos que evitar cualquier intento de la Plaga de reforzar sus filas.", completion = "La lealtad de un exánime es inquebrantable, &lt;nombre&gt;. Debes mostrarles que tal lealtad les conducirá a su propia destrucción.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nEspada del vencedorTalismán Resplandor de Ámbar"},
[3361] = {title = "Las penurias de un refugiado", objective = "Lleva la caja de Félix, el cofre de Félix, y el cubo de tornillos de Félix Rayaullido a Yunquemar.", description = "Expulsamos a los troggs de Gnomeregan, ¡pero con eso no hemos solucionado nuestros problemas! Nuestra tierra está completamente irradiada y los gnomos estamos dispersos por todo Dun Morogh.\n\nCuando huía de la radiación, con las prisas, perdí todas mis pertenencias y herramientas. Y las cogieron los trols. ¡Robaron mi cofre, mi caja y mi cubo de tornillos! Se los llevaron a su campamento, al suroeste de Yunquemar.\n\nYo no soy un aventurero... ¿Podrías ir tú a buscar mis cosas, por favor?", progress = "Esto es terrible, <nombre>, esta ciudad no es adecuada para individuos como yo. ¡Hay tantas criaturas horribles aquí como en Gnomeregan antes del accidente!\n\n¿Tienes mis pertenencias? Si no es así, quién sabe lo que los trols habrán hecho con ellas a esta altura...", completion = "¡Hurra, los has encontrado! Eres todo un salvador. Toma, no es mucho, pero acéptalo por los problemas que te he causado. ¡Gracias!", rewards = "Recibirás:\n\n"},
[3362] = {title = "El Valle Cardizal", objective = "Tran'rek de Gadgetzan quiere que mates a 8 cardizales nudosos y a 8 enraizadores cardizales.", description = "¿Has estado alguna vez en el Valle Cardizal? Es un lugar muy peligroso, <nombre>.\n\nEsas extrañas bestias que hay allí... ¡no me fío ni un pelo de ellas! Creo que se llaman cardizales nudosos y enraizadores cardizales, o algo parecido...\n\nDe cualquier forma, quiero ir al valle pero con todos esos cardizales deambulando por ahí, no creo que consiga volver con vida. Y aquí es donde entras tú. Si pudieras acabar con algunos de ellos, quizás consiga regresar de una pieza. ¿Qué te parece, <nombre>? ¿Me ayudas?", progress = nil, completion = "No sabía que eran tan grandes. Creo que tengo que perfeccionar mi plan... \n\nPero tú has cumplido con tu parte, así que aquí tienes lo que te prometí.", rewards = "Recibirás:\n\n"},
[3363] = {title = "Melodía corrupta", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[3364] = {title = "Entrega una infusión de albaza hirviendo", objective = "Lleva la infusión de albaza hirviendo a Durnan Cortapieles a Yunquemar antes de que se enfríe, ¡tienes 5 minutos!", description = "¡Ay! Se supone que tenía que llevar esta deliciosa infusión de albaza a Durnan Cortapieles, de Yunquemar, pero antes tengo que entregar una a Grelin. ¡Se enfriará antes de que llegue a Yunquemar!\n\nTú pareces <rápido/rápida>. Seguro que tú lo consigues. Esta taza estará caliente solo unos cinco minutos más y Durnan no pidió infusión fría de albaza, así que, ¡en marcha! Yunquemar está al noreste. Es un emplazamiento excavado en la montaña.\n\nGracias, <nombre>. ¡Y no olvides devolverme la taza!", progress = "Sí, soy Durnan Cortapieles. ¿Tienes algo para mí?", completion = "¡Oh, qué bien me va a sentar esto! Deja que me tome un descansito mientras disfruto esta infusión de albaza hirviendo.", rewards = "Recibirás:\n\n"},
[3365] = {title = "Devuelve la taza", objective = "Devuelve la taza a Nori Rumborgullo.", description = "¡Qué a gusto me he quedado, oye! Nada calienta el alma en un frío día de invierno como una infusión calentita –no, ARDIENTE– de albaza.\n\nTen, <nombre>. Llévale a Nori esta taza vacía de mi parte, ¿quieres?", progress = "¡Espero que la infusión de albaza le llegara caliente a Durnan! ¿Te has acordado de traerme mi taza?", completion = "¡Excelente, &lt;nombre&gt;! Te sorprendería saber la cantidad de mensajeros que conozco que olvidan hacer algo tan simple como devolver una taza. ¡Y las tazas no crecen en los árboles, o eso dicen! \n\nAquí tienes algo para premiar tu esfuerzo. Y gracias de nuevo por tu ayuda.", rewards = "Recibirás:\n\n"},
[3366] = {title = "El fragmento brillante", objective = "Viaja hasta Trinquete para averiguar el significado oculto tras el Fragmento de Pesadilla.", description = "El casco es de un verde radiante y, aunque sólido, lo que parece humo se arremolina justo debajo de la superficie. Tu vista queda irremediablemente atrapada en las profundidades del cristal; y mientras esto ocurre, sabes que hay algo perturbador en él.\n\nA lo mejor alguien de Trinquete puede contarte algo más sobre este extraño casco de cristal.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3367] = {title = "Piedras de Suntara", objective = "Ayuda a Dorius a volver a Forjaz.", description = "<Gruñe.> \n\nEstoy malherido, <raza>. Todo me da vueltas... Seguro que tengo hemorragias internas. \n\nTengo que volver a Forjaz. Ayúdame, <nombre>.", progress = nil, completion = "Dorius dejó caer esta carta cuando le dispararon.", rewards = "Recibirás:\n\n"},
[3368] = {title = "Piedras de Suntara", objective = "Lleva la carta chamuscada a Thorius a Forjaz. Está en la Sala de los Exploradores.", description = "Hermano:\n\nSi estás leyendo esta carta, yo ya no estoy en este mundo. Da las gracias en mi nombre a quien te la haya entregado y recompénsale. \n\nEn cuanto a las piedras, te confirmo que existen y las tienen los Hierro Negro. Su jefe, Lathoric el Negro, las usa para tener esclavizado a mi equipo de excavación; se los llevaron a La Caldera. Están obsesionados con algo que llaman Obsidion. \n\nAyúdalos, hermano. Esta es mi última voluntad. \n\nDorius", progress = "<Thorius solloza.>", completion = "&lt;Thorius se seca la cara.&gt;\n\nPor la Luz que se cumplirá su deseo.", rewards = "Recibirás:\n\n"},
[3369] = {title = "En las pesadillas", objective = "Lleva el Fragmento de Pesadilla a Hamuul Runatótem en el Alto de los Ancestros.", description = "Este fragmento alberga grandes secretos, es la esencia pura del Sueño Esmeralda. Sin embargo, lo que veo en él no es un sueño; hay quien llamaría a esta especie de visión una auténtica pesadilla.\n\nNo quiero alarmarte, <nombre>, pero creo que lo mejor será que lleves este fragmento a manos de un sabio druida tan pronto como puedas...\n\nNecesitas encontrar a Hamuul Runatótem del Alto de los Ancestros. Dirígete a Cima del Trueno. No puedo decirte nada más.", progress = "¿Te puedo ayudar en algo?", completion = "¿Vienes de parte de Falla? Bueno, veamos el fragmento del que dices que era tan reacia a hablar... \n\n&lt;Hamuul intenta mirar dentro del fragmento&gt;\n\nOh... yo no.. bien.\n\nGracias por traérmelo. Pero no es muy inteligente hablar de cosas como esta, te lo advierto. No deberías meterte en este tipo de asuntos.\n\nDigamos que las cosas del Sueño Esmeralda no son como creíamos...\n\nQuizás nuestros caminos se vuelvan a encontrar en el futuro.", rewards = "Podrás elegir una de estas recompensas:\n\nManto TalbarGalochas del Lodazal\n\nTambién recibirás:\n\nManto TalbarGalochas del Lodazal"},
[3370] = {title = "En las pesadillas", objective = "Lleva el Fragmento de Pesadilla a Mathrengyl Garroso de Darnassus.", description = "Este fragmento alberga grandes secretos, es la esencia pura del Sueño Esmeralda. Sin embargo, lo que veo en él no es un sueño; hay quien llamaría a esta especie de visión una auténtica pesadilla.\n\nNo quiero alarmarte, <nombre>, pero creo que lo mejor será que lleves este fragmento a manos de un sabio druida tan pronto como puedas...\n\nNecesitas encontrar a Mathrengryl Garroso del Círculo Cenarion. Dirígete a Darnassus. No puedo decirte nada más.", progress = "¿Te puedo ayudar en algo?", completion = "¿Vienes de parte de Falla? Bueno, veamos el fragmento del que dices que era tan reacia a hablar... \n\n&lt;Mathrengyl intenta mirar dentro del fragmento&gt;\n\nOh... yo no.. bien.\n\nGracias por traérmelo. Pero no es muy inteligente hablar de cosas como esta, te lo advierto. Son asuntos que deben quedar en manos de los druidas Cenarion.\n\nDigamos que las cosas del Sueño Esmeralda no son como creíamos...\n\nQuizás nuestros caminos se vuelvan a encontrar en el futuro.", rewards = "Podrás elegir una de estas recompensas:\n\nManto TalbarGalochas del Lodazal\n\nTambién recibirás:\n\nManto TalbarGalochas del Lodazal"},
[3371] = {title = "La justicia de los enanos", objective = "Vuelve a La Garganta de Fuego y libera al equipo de Dorius.", description = "Siento que tengo el corazón como una piedra, <nombre>. Tardaré en asumir la muerte de Dorius. Voy a cumplir su última voluntad: hay que llevar ante la justicia a su asesino. \n\nVuelve a La Garganta de Fuego y libera a sus hombres.", progress = nil, completion = "Déjame, ayuda a los demás.", rewards = "Recibirás:\n\n"},
[3372] = {title = "Libéralos", objective = "Encuentra el artefacto misterioso que protegen los idólatras de Azote del Ocaso en el cuadrante noroeste de La Garganta de Fuego.", description = "Libéralos, <raza>. Aunque fallen sus cuerpos, el poder de Suntara les empuja a seguir trabajando. \n\nLa clave está en el Altar de Suntara. Ve al noroeste; Azote del Ocaso guarda una reliquia en las colinas que hay sobre la cueva. Recógela y colócala en el Altar de Suntara. \n\n<Pierde el conocimiento.>", progress = "El altar está vacío.", completion = "Colocas el artefacto misterioso en el Altar de Suntara.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3373] = {title = "Esencia de Eranikus", objective = "Coloca la esencia de Eranikus en la fuente de esencia situada en su guarida del Templo Sumergido.", description = "Cuando tocas la gema, una voz profunda resuena en tu cabeza.\n\n\"Mortal, escucha esta súplica. Soy Eranikus del Vuelo Verde, guardián de este templo maldito. Mi deber de asegurarme de que los trols nunca jamás vuelvan a traer su abominación de dios, ha fracasado. Mientras esta gema queda imbuida de parte de mi esencia, puedo sentir que mi vínculo con el Sueño se dirige hacia el caos. Por favor, coloca esta gema en la fuente de esencia de mi guarida, y deja que nuestra magia purgue la corrupción de mi psique, para que pueda descansar.\"", progress = "Según te aproximas a la fuente de esencia que Eranikus te dijo, vuelves a oír su voz en tu cabeza.\n\n\"Sí mortal, esta fuente de esencia redimirá cualquier elemento no contaminado de mi psique que permanezca imbuido en esta gema. Coloca la gema en la fuente y permite que la magia del Vuelo Verde limpie la corrupción y la contaminación de mi ser. Solo entonces estaré libre y solo entonces el Vuelo podrá reafirmar su dominio de los Atal'ai.\"", completion = "Colocas la gema en la fuente de esencia.\n\n\"Gracias, mortal, no tienes... esto, ¡esto no puede ser! ¡No! La corrupción de este dios maldito mancha la santidad de la fuente de esencia del Vuelo Verde! No, por favor, mi esencia... me abandona... EL DOLOR... Siento cómo me inunda una pesadilla eterna...\"\n\n\"Mortal, ¡te lo ruego! Encuentra un miembro del Vuelo Verde para detener esto... ayúdame... Estoy encadenado a la oscuridad... a la agonía eterna... por favor, no me tortures...\"", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nEsencia encadenada de Eranikus"},
[3374] = {title = "La esencia de Eranikus", objective = "Llévale la piedra de juramento del Vuelo de Ysera y la esencia encadenada de Eranikus a Itharius, que está en el Pantano de las Penas. Una vez allí, deberás decidir si quieres ayudar al Vuelo de Ysera o no.", description = "Coges la piedra de juramento y oyes en tu cabeza la voz de Itharius que te habla a través de ella.\n\n\"Al aceptar la orden que contiene esta piedra de juramento, te comprometes a ayudarme a mí y al Vuelo de Ysera a redimir la corrupta alma de Eranikus. El primero de los muchos sacrificios que con toda seguridad tú y yo haremos consiste en que me entregues la gema que está mermando lo poco de la psique de Eranikus que todavía estaba libre de malvadas influencias.\"\n\n\"<nombre>, hazlo y entonces podremos ofrecerle redención a un alma que bien la merece.\"", progress = "Aquí estamos <nombre>, ante una nueva encrucijada. Ha llegado la hora de tomar otra decisión. ¿Cuál es tu elección, mortal?", completion = "Has tomado una decisión. Hasta la fecha, puedo decir que has tomado las decisiones correctas. De ahora en adelante, tus acciones determinarán si esto continúa siendo así.", rewards = "Recibirás:\n\n"},
[3375] = {title = "Otra ampolla", objective = "Lleva 1 poción de maná, 1 trozo de carbón y néctar dulce a Talvash del Kissel a Forjaz. Te dará una ampolla de adivinación.", description = "¿Que necesitas otra ampolla de adivinación? Estás de broma, ¿verdad? ¡¿Qué has hecho con la otra?! Tenemos mucho trabajo, <raza>. ¡Las ampollas no crecen en los árboles! Y ojalá lo hicieran, porque no tengo ninguna.\n\nTe haré una, pero necesito lo siguiente: una poción de maná, un trozo de carbón y algo de néctar dulce. No preguntes por qué, tú tráeme estas cosas y ya está.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nAmpolla de visión de Talvash"},
[3376] = {title = "La cabeza de Cornapunta", objective = "Lleva la cabeza del Jefe Cornapunta Mantospina a Valiente Ventopluma a Mesa de la Nube Roja.", description = "He oído hablar de ti, <nombre>. Quizás tú puedas ayudarnos donde otros han fallado. \n\nLos tauren hemos hecho un hogar de esta tierra, pero a un precio alto. Los jabaespines Erizapúas de Barranco Cortazarza, liderados por el Jefe Cornapunta Mantospina, nos acosan continuamente. ¡Quiero la cabeza del jefe! ¡Tráemela! \n\nEstará en el barranco que hay al este de su aldea.", progress = "Cornapunta no nos dará más problemas; quiero su cabeza.\n\nLos <raza> vivimos para cazar y la mejor presa es aquella que podría contraatacar. Si consigues cumplir tu tarea, empezarás a entender esto que digo.", completion = "Hoy se ha hecho justicia gracias a ti, &lt;nombre&gt;. Que esto sirva de advertencia a todos cuantos amenazan nuestro hogar.\n\nAcepta esta recompensa por tu hazaña, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos de anillas pintadosGuantes nómadas\n\nTambién recibirás:\n\nLeotardos de anillas pintadosGuantes nómadas"},
[3377] = {title = "Oración a Elune", objective = "Escucha a Zamael.", description = "¿Quieres ayudarme, mortal? Escucha lo que tengo que decir.", progress = nil, completion = "He aceptado mi destino, pero debes encontrar la oración.", rewards = "Recibirás:\n\n"},
[3378] = {title = "Oración a Elune", objective = "Lleva la oración a Elune a Astarii Buscaestelar a Darnassus.", description = "¿Me ayudarás, mortal? Busca la oración a Elune. \n\nEstá grabada en una tablilla dorada. Seguro que alguno de esos rufianes idólatras la lleva consigo. \n\nSi consigues recuperarla, llévasela a Astarii Buscaestelar, que está en Darnassus. Poco importa que haya recuperado la fe después de todos los crímenes que he cometido, pero quiero que se oiga mi oración. No espero la absolución.", progress = "¿Qué te trae al Templo de la Luna, <nombre>?", completion = "Elune te sonríe, &lt;nombre&gt;; le has devuelto un alma perdida. Acepta esto en prueba de nuestra gratitud.", rewards = "Podrás elegir una de estas recompensas:\n\nHombreras KaylariProtegebrazos de acero rúnico\n\nTambién recibirás:\n\nHombreras KaylariProtegebrazos de acero rúnico"},
[3379] = {title = "Las tejedoras de Sombra", objective = "Mata a los furtivos Sedaumbría de La Garganta de Fuego. Nilith cree que son 5. Ve a verle cuando hayas terminado.", description = "Las arañas de La Garganta de Fuego son una fuente increíble de seda de las Sombras. Como ya sabrás, este elemento tiene mucha demanda.\n\nRecientemente, un grupo de furtivos ha invadido estas tierras, acabando con las arañas de forma masiva y sin pensar en los efectos a largo plazo de sus actos. Lo primero que te voy a pedir es muy sencillo: encuéntralos y acaba con ellos.\n\nComienza tu búsqueda por el norte.\n\nSi necesitas ayuda, habla con Raze.", progress = nil, completion = "Buen trabajo, &lt;nombre&gt;.\n\nSeguro que nunca has visto un tejido de sombra como este.", rewards = "Recibirás:\n\nBrazales enigmáticos"},
[3380] = {title = "El Templo Sumergido", objective = "Encuentra a Marvon Buscarroblones en Tanaris.", description = "En este mundo, hay espíritus inquietos, <nombre>. Espíritus marcados por su aislamiento, atados a un mal ancestral...\n\nHe oído hablar de un lugar en el fondo de las profundidades de la tierra. Ha sido desterrado allí por poderes muy superiores a los que jamás he visto.\n\nSi quieres saber más cosas sobre este lugar, el Templo Sumergido, busca a un goblin llamado Marvon Buscarroblones. Ha estudiado muchas zonas antiguas del mundo. Se sabe que dejó su hogar en Trinquete para estudiar las ruinas trols en Tanaris.D", progress = nil, completion = "Sí, el Templo Sumergido, puedo decir que lo he visto con mis propios ojos. ¿Quizás te gustaría oír más?\n\nParece que te gustan las aventuras... sé que estas cosas podrían interesarte...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3381] = {title = "Un encuentro con el maestro", objective = "Llévale 2 plumas de hipogrifo intactas a Sanath Lim-yo en Azshara si deseas hablar con su maestro.", description = "Ah, un joven <raza> que viene a hablar con Sanath. ¡Qué día tan maravilloso! Es un gran honor el que me concedes visitándome. ¡Gracias!\n\nTe lo ruego... no seas tan arrogante como para pensar que mi sarcasmo era un cumplido. Dejemos esto claro: no me gustas. Pero esta charlita nada tiene que ver con nosotros, tiene que ver con mi maestro.\n\nTráeme un par de plumas de hipogrifo para mis flechas y te daré permiso para verlo. Me ha pedido que le envíe aventureros si demostraban tener arrestos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3382] = {title = "Un equipo bajo el fuego enemigo", objective = "Defiende a la capitán Vanessa Beltis, su tripulación y los pasajeros del Explorador del horizonte frente al ataque de los nagas. La capitán Vanessa Beltis tiene que sobrevivir.", description = "Estábamos a escasa distancia de la costa buscando un puerto seguro donde echar anclas. El sol se había puesto más de una hora antes cuando atacaron... ¡malditos nagas!\n\nNo estoy seguro de lo que buscaban pero está claro que no lo encontraron, ya han vuelto una vez. Tengo a un par de rastreadores vigilando la entrada del valle para que nos puedan avisar con tiempo si ven otro ataque venir.\n\nSiento que hayas venido ahora, <nombre>, pero no depende de mí.\n\n¿Estás <listo/lista> para el combate de tu vida?Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[3385] = {title = "El Mercado Negro", objective = "Mata al jefe de comercio Kovic y a su esbirro, Clunk. Lleva su cartera a Nilith.", description = "Se autodenominan El Mercado Negro: son mercaderes degolladores que venden, traslada, intercambian o hacen de todo por el precio adecuado.\n\nY es aún peor, <nombre>. Han establecido una base de operaciones aquí en La Garganta de Fuego. El jefe de comercio Kovic encabeza las operaciones y mantiene estrechas relaciones con los enanos Hierro Negro de la región.\n\nTenemos que darles un duro golpe y enviar un mensaje a sus líderes. Mata al Jefe de comercio Kovic y a su esbirro Golpín. Trae su cartera de mercader cuando hayas acabado.\n\n¡Al caldero!Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCinturón enigmáticoCartera comercial de Kovic"},
[3402] = {title = "El Mercado Negro", objective = "Habla con Vizzklick en Gadgetzan.", description = "Quiero que tú des la noticia de la derrota de Kovic a Vizzklick, en Gadgetzan. Se alegrará de saber que Kovic ha muerto; seguro que su recompensa será generosa.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nPatrón: máscara de tejido de sombras"},
[3421] = {title = "El viaje de vuelta", objective = "Te haré volver al campamento de Sanath ahora. \n\nNo hagas caso de su mala educación, es así con todos a excepción del maestro. Quizás algún día le den una lección.", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nTeletransporte desde la torre de Azshara"},
[3441] = {title = "Castigo divino", objective = "Escucha a Velarok Espada del Viento.", description = "Escúchame bien.", progress = nil, completion = "¿Has escuchado lo suficiente como para decidir?", rewards = "Recibirás:\n\n"},
[3442] = {title = "La llama pura", objective = "Lleva 4 corazones de llama y 4 globos de aceite gólem a Velarok Espada del Viento.", description = "Has tomado la decisión correcta, <nombre>. \n\nDebemos atacar donde más daño hagamos: en las 4 torres del centinela. \n\nSi las incendiamos, podremos atacar sin darles tiempo a reaccionar. \n\nPara incendiar las torres necesitarás una herramienta. Necesito algunas cosas para fabricarla. \n\nTráeme 4 corazones de llama y 4 aceites de gólem; con eso te haré la antorcha de llama pura. \n\nBusca primero a los golems y elementales de estas tierras.", progress = "¿Me traes lo que te pedí?", completion = "Muy bien, &lt;nombre&gt;. Sigue, sigue.", rewards = "Recibirás:\n\n"},
[3443] = {title = "Forjar el mango", objective = "Lleva 8 dagas chapadas en torio a Velarok Espada del Viento.", description = "Necesitamos un soporte y un mango para transportar la llama pura. Busca primero el mango. \n\nLos capataces y esclavistas Hierro Negro de La Caldera usan armas de torio encantado, una aleación muy resistente. Si me traes 8 dagas chapadas en torio, fundiré la aleación para fabricar el mango.", progress = "<nombre>, ¡necesito 8 dagas, 8!", completion = "Eres excepcional, &lt;nombre&gt;. ¿Has pensado en cambiar de oficio? Podrías ser detective o cazatesoros... Ya sé, puedes hacer como esos gnomos locos que van por Azeroth con detectores de metal. Sí, ese trabajo te iría que ni pintado.", rewards = "Recibirás:\n\n"},
[3444] = {title = "El Círculo de Piedras", objective = "Recupera el Círculo de Piedras del taller de Marvon Buscarroblones, en Trinquete.", description = "Una vez entré en las cámaras prohibidas del Templo Sumergido. El mal que yace en ese lugar penetra en todo lo que entra. Casi no consigo salir de allí, vadeando entre el lodo que cubre el suelo. Todo lo que puedo recordar es que pasé a trompicones por una gran sala circular llena de estatuas de la Serpiente...\n\nHace poco, descubrí una piedra redonda que parece tener el mismo símbolo de la Serpiente grabado. Hice que me la enviaran a mi taller en Trinquete. Recupérala y podré decirte más cosas.", progress = "Trinquete no queda lejos de aquí, <nombre>. No deberías tardar mucho.", completion = "Sí... Esta es la piedra.\n\nAhora empiezo a recordarlo todo, &lt;nombre&gt;...", rewards = "Recibirás:\n\n"},
[3445] = {title = "El Templo Sumergido", objective = "Encuentra a Marvon Buscarroblones en Tanaris.", description = "He oído hablar de un lugar que yace en la profundidad de la tierra... Ha sido desterrado allí por poderes muy superiores a los que jamás he visto.\n\nSi quieres saber más cosas sobre este lugar, el Templo Sumergido, busca a un goblin llamado Marvon Buscarroblones. Al igual que Troyas y yo, ha estudiado muchas zonas antiguas del mundo. Dejó su hogar en Trinquete para estudiar las ruinas trols en Tanaris.\n\nTe deseo buena suerte para encontrarle, hace mucho tiempo que no se oye nada de él.", progress = nil, completion = "Sí, el Templo Sumergido, puedo decir que lo he visto con mis propios ojos. ¿Quizás te gustaría oír más?\n\nParece que te gustan las aventuras... sé que estas cosas podrían interesarte...", rewards = "Recibirás:\n\n"},
[3446] = {title = "En las profundidades", objective = "Encuentra el Altar de Hakkar en el Templo Sumergido, en el Pantano de las Penas.", description = "Dentro del templo, encontré una sala circular con seis balcones. Cada uno de ellos tenía una estatua de la Serpiente en el borde. Curioso, empujé una de ellas para ver lo que pasaba, ¡era una trampa! Casi encuentro la muerte en la explosión que surgió. Allí, en el borde, miré hacia abajo y vi otra estatua en el fondo de la sala, una especie de altar... Creo que se llamaba Hakkar.\n\nMe da la impresión de que ese altar es el secreto. Debes llevar este objeto al altar del Templo Sumergido.", progress = "Al examinar la estatua, ves un hueco en uno de los lados, del tamaño de la piedra que Marvon te dio...", completion = "Empujas el Círculo de Piedras y puedes oír cómo encaja en su sitio.\n\nAhora que la piedra está en su lugar, puedes continuar... ¿Pero qué debes hacer ahora?\n\nQuizás deberías examinar el altar con más detenimiento...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3447] = {title = "El secreto del círculo", objective = "Ve al Templo Sumergido y descubre el secreto oculto en el círculo de estatuas.", description = "Mis recuerdos son borrosos, pero te diré lo que pueda. Aunque el círculo de estatuas del Templo Sumergido es peligroso, creo que contiene el secreto para encontrar un tesoro mucho mayor.\n\nDesde el altar principal, conseguí activar una serie de luces. Si puedes descubrir el significado de esas luces, quizás puedas revelar el secreto que se esconde en esa sala.\n\nPero te lo advierto, <nombre>. Un gran mal yace en el templo. Lo más probable es, que si hay algo de valor, estará muy protegido...", progress = nil, completion = "El ojo del ídolo emite unos destellos brillantes, incluso en la semioscuridad del templo. Su transparencia revela algo detrás de él. Empujándolo a un lado, metes la mano y consigues coger lo que hay en el interior de la estatua.\n\nEl tesoro de los Atal'ai ya es tuyo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nUrna Hakkari"},
[3448] = {title = "Encargarse de la investigación", objective = "Habla con Tymor en Forjaz.", description = "Me han encargado que investigue Azshara, una tierra que fue hermosa y que los elfos llamaban hogar. Eso fue en la época en la que practicaban la magia. Ahora es un lugar más peligroso que hermoso, pero debemos buscar respuestas sobre nuestro pasado.\n\nUn compañero llamado Tymor se interesa por estas cosas y se ofreció a ayudarme.\n\nTiene una pequeña tienda a la derecha de la Sala de los Misterios, aquí en la ciudad.", progress = nil, completion = "¿Que vienes de parte de Karnik? Me lo imaginaba; he descubierto cosas asombrosas sobre Azshara. Los elfos de la noche viven muchos años, pero la gente tiende a olvidar los acontecimientos más desagradables de su historia. No lo digo como crítica, solo constato un hecho.\n\nHace mucho tiempo, construyeron ciudades imbuidas de magia y otras artes hoy perdidas. Esos temas me interesan, por eso empecé a investigar y descubrí cosas maravillosas.", rewards = "Recibirás:\n\n"},
[3449] = {title = "Runas arcanas", objective = "Haz calcos de la Runa de Beth'Amara, la Runa de Jin'yael, la Runa de Markri y la Runa de Sael'hai. Luego ve a la isla frente a la península meridional de Azshara y avisa al piloto Xiggs Luzfusil para que recoja los calcos.", description = "Hubo un tiempo en que algunos elfos veneraban la magia tanto como a Elune. Sus magos más poderosos crearon runas y tablillas con leyes de lo arcano y reflexiones sobre sus descubrimientos. Las dejaban en lugares públicos para quien quisiera estudiarlas.\n\nQuisiera que calcaras las runas de las Ruinas de Eldarath. Te daré el material necesario para calcar, pero las runas tendrás que encontrarlas tú. Pero ten cuidado; la Liga de Exploradores no es el único grupo interesado en Eldarath.", progress = "Me alegro de verte, <nombre>. ¿Tienes los calcos para Tymor?\n\nPara lo peligroso que es esto, está muy tranquilo. Igual tiene algo que ver con los nagas que hay por ahí... Pero no es mi problema; yo voy por aire.", completion = "Me alegro de verte de una pieza, &lt;nombre&gt;.\n\nVoy a enviarle los calcos a Tymor; se va a emocionar con solo mirarlos.\n\nTira la pistola de bengalas; no creo que te sirva para nada y yo ya tengo una.", rewards = "Recibirás:\n\n"},
[3450] = {title = "Un simple envío", objective = "Habla con Xiggs Luzfusil en Forjaz.", description = "Quiero que los calcos se entreguen en mi torre, pero no hace falta que vayas hasta allí. Haré que sean transportados por aire desde Azshara.\n\nXiggs Luzfusil me debe un par de favores y estará en esta zona en breve; lo han destinado a patrullar la costa. Habla con él antes de irte. Te dará una pistola de bengalas para comunicaros. Cuando tengas los calcos, ve a donde te diga y dáselos para que los envíe a mi torre. Estará en el hangar de la Sala Militar.", progress = nil, completion = "¿Qué querías, &lt;nombre&gt;? Tengo mucho que hacer, solo he venido a buscar unas crines de wendigo.\n\nAcabo de mandar a alguien al Almacén de Brasacerada, así que tienes un minuto para contarme lo que sea.", rewards = "Recibirás:\n\n"},
[3451] = {title = "Una señal para el envío", objective = "Habla con Xiggs Luzfusil para que te dé una pistola de bengalas en Forjaz.", description = "Así que te envía Tymor, ¿eh? Sí, le debo un par de favores. ¿Qué es esto? Vaya con el ladino de él. Bueno, puedo ocuparme de esto. Necesitas una pistola de bengalas y un punto de encuentro; eso es fácil.\n\nHay una isla pequeña con una pista de aterrizaje junto a la península sur de Azshara. Lanza una bengala cuando llegues. Yo iré a recoger esto, lo que sea que es.", progress = nil, completion = "Toma mi pistola de bengalas. Sé paciente después de disparar; solo llevan 2 cartuchos y yo puedo tardar hasta 1 minuto, depende de dónde esté. Tú mira al mar después de lanzar las bengalas.", rewards = "Recibirás:\n\nPistola de bengalas de venta estándar"},
[3452] = {title = "El soporte para la llama", objective = "Lleva un Símbolo de Ragnaros a Velarok Espada del Viento.", description = "Nos falta un elemento para acabar la antorcha. \n\nEn las montañas del noroeste encontrarás un campamento de Azote del Ocaso. Se dice que llevan símbolos e ídolos en honor a su dios; seguro que tienen suficiente magia para crear un soporte encantado. Tráeme 1 símbolo de Ragnaros; intentaré extraer su magia.", progress = "No podemos completar la antorcha de reprensión sin una fuente de energía arcana.", completion = "El símbolo irradia Magia demoníaca; justo lo que queríamos.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3453] = {title = "La Antorcha de Reprensión", objective = "Espera a que Velarok Espada del Viento termine la Antorcha de Reprensión.", description = "Déjame montar el conjunto y pronunciar los encantamientos.", progress = nil, completion = "Debes completar una última tarea.|n", rewards = "Recibirás:\n\n"},
[3454] = {title = "La Antorcha de Reprensión", objective = "Coge la Antorcha de Reprensión.", description = "Ahora hay que vincular la antorcha a quien la porta. Cógela, <nombre>.", progress = nil, completion = "Coges la antorcha y sientes una corriente de odio e ira correr por tus venas.", rewards = "Recibirás:\n\nAntorcha de Retribución"},
[3461] = {title = "Vuelve con Tymor", objective = "Devuelve el juego de calco a Tymor, que está en Forjaz.", description = "Tymor no me ha dado nada para pagarte, así que háblalo con él cuando vuelvas a Forjaz. Seguro que estará en la Sala Mística. Ve a saludar y no te vayas hasta que pague. Esto que te ha encargado no era fácil.\n\nPero gracias otra vez por ayudarle y buena suerte.\n\nAh, y llévate las cosas de calcar. Dáselas a Tymor cuando lo veas.", progress = "Me alegro de verte. ¿Traes mi juego de calco?", completion = "Estupendo, me alegro.\n\nEstoy impaciente por estudiar los calcos y celoso de que hayas estado en Azshara; es el sueño de cualquier estudioso de lo arcano.\n\nEn su momento no hablamos de recompensa porque trabajabas para la Liga de Exploradores, pero te has ganado una.", rewards = "Podrás elegir una de estas recompensas:\n\nGrebas de forjador de aceroOrbe de hechizo de calavera\n\nTambién recibirás:\n\nGrebas de forjador de aceroOrbe de hechizo de calavera"},
[3462] = {title = "Escudero Maltrake", objective = "Habla con mi enviado, el escudero Maltrake.", description = "Habla con mi enviado, el escudero Maltrake; te dirá cómo usar la Antorcha de Reprensión.", progress = nil, completion = "¿Preparado?|n", rewards = "Recibirás:\n\n"},
[3463] = {title = "¡Préndelas fuego!", objective = "Entra en las 4 torres del centinela e incéndialas con la Antorcha de Reprensión.", description = "Escúchame bien, <nombre>. Aunque no es posible destruir las torres, con la antorcha provocarás unos incendios muy difíciles de extinguir, un fuego que no puede apagar ninguna fuerza ni elemento. \n\nMientras ardan no podrá haber centinelas ni guardias; su primera línea de defensa quedará anulada. \n\nTienes que entrar en cada torre y usar la antorcha dentro de las fortificaciones. Ten cuidado con las unidades de élite de los Hierro Negro, <nombre>.", progress = nil, completion = "¡Qué vista tan gloriosa!", rewards = "Podrás elegir una de estas recompensas:\n\nLeotardos de VueloProtegecabezas PirodracoHacha del draco ébano\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nLeotardos de VueloProtegecabezas PirodracoHacha del draco ébano"},
[3481] = {title = "Abalarios...", objective = "Abre el cofre.", description = "El cofre está cerrado con llave.", progress = nil, completion = "El cofre se abre cuando lo tocas. ¿Cuánto queso puede haber aquí? Solo hay un modo de averiguarlo.", rewards = "Recibirás:\n\nTesoro oculto del Vuelo negro"},
[3483] = {title = "Una señal para el envío", objective = "¿Otra pistola de bengalas? Por supuesto.\n\nY recuerda que debes ir a la punta sur de Azshara. Es una islita reconvertida en pista de aterrizaje; seguro que la ves.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nPistola de bengalas de venta estándar"},
[3501] = {title = "Todo cuenta en grandes cantidades", objective = "Llévale a Kum'isha fragmentos de draenetista imperfecta y te recompensará por cada fragmento entregado.", description = "Aunque probablemente no encuentres nunca una esfera draenetista perfecta, seguro que encontrarás fragmentos imperfectos de draenetista al explorar estas tierras.\n\nAunque es improbable que Kum'isha pueda usar los fragmentos con imperfecciones para volver a su hogar, las piezas podrían ser de utilidad para otros fines de los draenei.\n\nTráeme todo fragmento de draenetista con imperfecciones que encuentres y te recompensaré.", progress = "¿Ha habido suerte con la exploración?", completion = "Me produces asco, &lt;raza&gt;. ¿Un hombre hecho y derecho babeando sobre una pila de baratijas? ¡Qué pena das! Toma tu recompensa y desaparece de mi vista.", rewards = "Recibirás:\n\nBasura de Kum'isha"},
[3502] = {title = "Un trasto de Draenei…", objective = "Llévale a Kum'isha fragmentos de draenetista imperfecta. Cualquier forma de monstruo de Las Tierras Devastadas podría llevar uno de estos fragmentos.", description = "Vaya, después de semejante despliegue de voraz codicia, siento náuseas.\n\nEn fin, no me detendré en tu materialismo.\n\nPor cada fragmento de draenetista con imperfecciones que traigas, te recompensaré con más baratijas. Mi pila de chatarra se reduce mientras la tuya crece. Como se dice en Draenor, no hay nada que perder.", progress = "¿Y bien? ¿Draenetista? ¿Te acuerdas?\n\n<Kum'isha cierra el puño y te da en la cabeza.>\n\n¿Alguna neurona en casa?", completion = "Me produces asco, no-muerto. ¿Una mujer hecha y derecha babeando sobre una pila de baratijas? ¡Qué pena das! Toma tu recompensa y desaparece de mi vista.\n\n¿Te suena? Debería, porque lo digo mil veces al día.\n\n¡Oh, cómo aborrezco este mundo!", rewards = "Recibirás:\n\nBasura de Kum'isha"},
[3503] = {title = "Encuentro con el maestro", objective = "Hubo un tiempo en que podía pedirles lo que fuera a los que buscaban a mi maestro. Pero ahora, solo tienes que pedirlo y te haré llegar hasta él. \n\nMe molesta pero no cuestiono sus órdenes. Solo puedo disfrutar de hacer bromas sobre tu ridículo atuendo o esa estúpida mirada que luces. Hasta eso me aburre... \n\n¡Hasta esta divagación me aburre! Bueno, ¿quieres ver a mi maestro o no?", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nTeletransporte a la Torre de Azshara"},
[3504] = {title = "Traición", objective = "Entrega la carta sellada a Ag'tor Puñosangre en Azshara.", description = "¡Elfa ladrona y traidora! Es el único nombre por el que merece ser llamada.\n\nHe cometido un gravísimo error, <nombre>. Confié en una elfa de sangre.\n\nAfirmaba que Azshara estaba llena de tesoros, de objetos de poder. Le concedí algunos guerreros como escolta esperando obtener beneficio, y mira lo que obtuve: los mató a todos.\n\nQuiero vengarme, <nombre>, pero no puedo prescindir de nadie más. Ve a ver a Ag'tor a Valormok, al norte del Campamento Haldarr. Te dará más información.", progress = "Vaya, así que Belgrom se ha decidido a ayudarnos por fin, ¿eh?\n\nEn este campamento había varios guerreros, pero han muerto todos; esa Rimtori ha acabado con ellos. Ha jugado con Belgrom, apuesto a que lo sedujo. No es cosa mía, esa elfa es tentadora; ayudo a Belgrom porque yo habría hecho lo mismo en su lugar.", completion = "Su ejército es fuerte; no ha regresado nadie de las tropas que enviamos. Supongo que muchos han muerto, pero otros habrán huído.\n\nPero ahora que tenemos refuerzos, quizás podamos darle el castigo que se merece por traicionar a Belgrom.\n\n¿Estás con nosotros?", rewards = "Recibirás:\n\n"},
[3505] = {title = "Traición", objective = "Mata a 10 reivindicadores y 10 agrimensores elfos de sangre antes de buscar el campamento de Magus Rimtori. Una vez allí, busca algo que sea importante para ella, algo con lo que provocar un combate.", description = "Esperaba algo así.\n\nEmpezaremos la campaña contra su gente en los riscos meridionales de la península norteña de Azshara. Masácralos a todos; que sepan que traicionarnos se paga muy caro.\n\nNo te preocupes por ella de momento; pasa casi todo el tiempo explorando Azshara con sus guardaespaldas. Cuando acabes con los suyos, busca su campamento privado. Explora formas de ataque. Puede que encuentres algo con lo que podamos provocarla.", progress = nil, completion = "Lo hojeas un poco y encuentras una página marcada por una cinta de seda negra. \n\nEn otra página ves un diagrama. Miras alrededor y ves que los cristales, el círculo e incluso algunos otros componentes del suelo están dispuestos de modo parecido al diagrama.", rewards = "Recibirás:\n\n"},
[3506] = {title = "Traición", objective = "Destruye el cristal de enfoque arcano de Magus Rimtori y entonces mátala. Lleva su cabeza a Ag'tor Puñosangre a Azshara.", description = "Piensas un poco y se te ocurre algo que te hace sonreír: puede que destruir los cristales irrite a la responsable de la muerte de tantos orcos y la traición de Belgrom.\n\nSigues descifrando las imágenes y el texto y ves que uno de los cristales está unido al creador del círculo; los demás lo están a algo mucho más siniestro.\n\nCierras el libro y te pones a pensar en qué cristal vas a destruir.", progress = "¿Cómo va la matanza de traidores elfos de sangre, <nombre>?\n\n¡Tienes que hacer pagar a esa traidora! ¡Merece una muerte lenta y dolorosa por lo que ha hecho!", completion = "¡Increíble! \n\n¡Lo has hecho! ¡Belgrom estará muy complacido! Al fin encontrarán la paz los espíritus de nuestros guerreros. Gracias, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[3507] = {title = "Traición", objective = "Habla con Belgrom Destrozaroca en Orgrimmar.", description = "Ve a ver a Belgrom a Orgrimmar. ¡Seguro que querrá ver la cabeza con sus propios ojos! Nosotros iremos pronto.\n\nAh, qué bien ha ido. Gracias por tu ayuda, <nombre>.", progress = "Te recuerdo, <clase>; te envié a Azshara para acabar con la traidora. Parece que han pasado muchas lunas... ¡No me digas que Ag'tor y los demás también han muerto!", completion = "¡Chúpate esa, asquerosa! \n\nGracias de todo corazón, &lt;nombre&gt;. ¡Qué alegría me has dado! \n\nTe daría todo un reino si pudiera, pero tendrás que conformarte con esto. \n\n \n\nQué manera de acabar, con la cabeza en una bolsa... ¡Eso te pasa por traicionarme!", rewards = "Podrás elegir una de estas recompensas:\n\nOrbe de piropiedraMartillo de Belgrom\n\nTambién recibirás:\n\nOrbe de piropiedraMartillo de Belgrom"},
[3508] = {title = "Romper el amuleto", objective = "Espera que Loramus acabe el hechizo de ilustración.", description = "Eres imprudente, <nombre>. Una cualidad admirable la de acometer una batalla a sabiendas de que apenas tienes posibilidades de salir <airoso/airosa>. Te ayudaré, aunque solo sea para ver cómo cae uno de los esbirros de Razelikh.\n\nLlevará algún tiempo romper la protección de los subordinados de Razelikh y requerirá además algunos artefactos raros.\n\nNo te muevas, primero realizaré el ritual de ilustración. La información que reúna servirá para forjar armas de gran potencia.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3509] = {title = "El nombre de la bestia", objective = "Habla con Lord Arkkoroc en Azshara. Empieza tu búsqueda en la orilla para encontrar el Templo de Arkkoran.", description = "Solo hay una forma de causarles daño al señor demoníaco y a sus esbirros. Un arma así de potente debe forjarse con su propio nombre inscrito en ella.\n\nCuando se forje semejante arma, el demonio y todo aquel bajo su mando serán vulnerables.\n\nEn Azshara reside una deidad menor: Lord Arkkoroc. Tienes que encontrarlo pero ándate con ojo, sus esbirros no se detendrán ante nada para destruirte si te descubren.\n\nPor supuesto, tendrás que pagar un precio por tal información.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3510] = {title = "El nombre de la bestia", objective = "Mata a Hetaera y lleva la cabeza ensangrentada, la cabeza golpeada y la cabeza magullada a Lord Arkkoroc.", description = "Ella mata a mis hijos, mortal. La misma que se atribuye el dominio sobre estos océanos. Una deidad de pacotilla, eso es lo que es. Aplástala y tráeme todas sus cabezas.\n\nA cambio, te daré el nombre que buscas y algo más de igual importancia.\n\nRonda la nidada... La llaman Hetaera.\n\n<Lord Arkkoroc señala hacia el suroeste.>\n\n¡Ve!Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3511] = {title = "El nombre de la bestia", objective = "Llévale el temple a Loramus Thalipedes.", description = "Lo prometido es deuda, te revelaré el nombre del señor demoníaco, Razelikh el Rapiñador. No obstante, primero debo preparar algo de vital importancia para tu viaje.\n\nEl arma que vas a crear requiere un temple especial. Dame un segundo para exprimir la sangre de estas cabezas.\n\n<Lord Arkkoroc aplasta las cabezas entre sus colosales puños y exprime la sangre de su interior.>\n\nLlévale el temple a Loramus además del nombre de la bestia: Rakh'likh.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3512] = {title = "Como diría  Eranikus…", objective = "Viaja a la parte este de Cuna del Invierno y habla con Umbranse el Contacto Espiritual.", description = "<nombre>, lo primero que debemos hacer es encontrar un modo de hablar con Eranikus a través de la gema. Él ahora se encuentra en un estado intermedio entre el sueño y la muerte, sufriendo el acoso de horripilantes pesadillas. A pesar de que la esencia que me has entregado está bastante contaminada, todavía existe una posibilidad de que podamos contactar con él.\n\nViaja a las heladas tierras del este de Cuna del Invierno y encuentra a Umbranse el Contacto Espiritual, uno de nuestros aliados. Es un médium humano que puede comunicarse con los muertos. Aunque Eranikus no esté muerto, puede que igualmente nos sirva de ayuda.", progress = nil, completion = "Ha sido una gran idea de Itharius el enviarte a verme. Dado que Eranikus no está muerto, al menos del modo en el que tú entiendes la muerte, pronto nos hablará él mismo y nos dará algunos consejos. De momento, no puedo ayudarte... al menos, por ahora.\n\nTe cederé mi habilidad para hablar con los muertos pero primero necesito la ayuda de alguien que, al contrario que yo, no sufra los achaques de la edad. Mi más potente dispositivo de augurios me fue arrebatado en contra de mi voluntad y, a menos que lo recupere, no podremos ayudar a Eranikus.", rewards = "Recibirás:\n\n"},
[3513] = {title = "El pergamino rúnico", objective = "Llévale el pergamino rúnico de la torre de vigilancia norte en Los Baldíos.", description = "El pergamino con aspecto oficial está firmemente atado con un lazo plateado. Seguro que alguien estará interesado en verlo, ¿pero quién?\n\nHay una avanzada cerca de la frontera con Vallefresno hacia el norte y si la información de este pergamino tiene que ver con los elfos de la noche, este es el lugar al que se debe llevar.", progress = "¿Traes algo pa' mí?", completion = "¡Qué rayos...! ¿Dónde has encontrado esto?", rewards = "Recibirás:\n\n"},
[3514] = {title = "La presencia de la Horda", objective = "Mata al jefe de El Cubil del Espolón, Rynthariel el Llavero.", description = "Este pergamino es de esa intrigante dríade de Rynthariel, ama de las llaves de El Cubil del Espolón en la Cima del Espolón.\n\nYa no se pondrá en contacto con el mensajero al que le robaste esto, ja, pero debemos mantener la posición aquí. La Horda no tolerará a sus mensajeros aquí.\n\n<nombre>, Dirígete a El Cubil del Espolón. Mata a Rynthariel y deja un símbolo para que todos lo vean. Llévate esta bandera de la Horda, <nombre>. Colócala en El Cubil del Espolón para que se lo piensen dos veces antes de enviar a otro mensajero por nuestro territorio.", progress = nil, completion = "¡Eres muy fuerte! La limpieza de El Cubil de Rynthariel es un gran avance, &lt;nombre&gt;. Tardarán en reagruparse y por lo tanto, cabe esperar que habrá menos mensajeros de la Alianza en Los Baldíos. Se lo pensarán dos veces antes de volver a enviar alguno hacia aquí.\n\nGracias.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas TraillamaradarGuantes de trenza de yute\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBotas TraillamaradarGuantes de trenza de yute"},
[3517] = {title = "El robo de conocimientos", objective = "Encuentra la tablilla de Beth'Amara, la tablilla de Jin'yael, la tablilla de Markri y la tablilla de Sael'hai antes de volver junto a Jediga en Azshara.", description = "Lo reconozco, <nombre>, casi todo lo que me ocurre en Azshara es culpa mía. Del castigo de Belgrom han resultado algunas cosas buenas.\n\nAntes de marcharme, hice algunas pesquisas y te sorprendería saber cuántos objetos valiosos hay por los alrededores.\n\nTe contaré más detalles de mis asuntos y hasta te daré una parte de los beneficios si me ayudas a recolectarlos.\n\nHay unas tablillas esparcidas por las Ruinas de Eldarath hacia el este que necesito. Son cuatro y cada una recibe un nombre por cuatro magos Ilustres. Consíguelas y seguiremos hablando.", progress = "Las tablillas que te he pedido que reúnas deberían aportar algo de cobre a las partes apropiadas. Te diré a quién hay que entregárselas cuando las encuentres. Pero no soy tonto... tendrás tu parte cuando tenga la prueba de que las has entregado.\n\nAsí que manos a la obra, no tenemos todo el día. Podría tener que encontrar a otro para que me ayude.", completion = "Perfecto, &lt;nombre&gt;, y además en buen estado. Deberíamos conseguir un buen precio de todas las partes interesadas.\n\nNo nos entretengamos aquí más de lo necesario. Hay un buen dinero que ganar.", rewards = "Recibirás:\n\n"},
[3518] = {title = "Entrega a Magatha", objective = "Llévale la tablilla de Beth'Amara a Magatha en Cima del Trueno.", description = "La primera tablilla es para una tauren... una tauren llamada Magatha. Forma parte de los Ancianos Cima del Trueno y está entre los que han propuesto una alianza entre los tauren y los Renegados. Ella, más que ningún otro Anciano, piensa que los tauren y sus métodos pueden ayudar a que los Renegados encuentren una vía para volver a ser humanos.\n\nLlévale la tablilla de Beth'Amara. Es posible que la tablilla sea lo que le permita encontrar la forma de ayudar a los Renegados… o al menos eso dice.", progress = "¿Qué hay, <nombre>? ¿Necesitas algo? Tengo muchos asuntos de que ocuparme, así que te ruego premura.", completion = "Ah, por fin, la tablilla de Beth'Amara. Me estaba preguntando si mi asociación con Jediga había fracasado o si la pobre orco había encontrado la muerte en Azshara. Me alegra ver que tuvo éxito.\n\nBeth'Amara era uno de los alquimistas con más influencia entre los altonatos. Se decía que el zahorí en solitario había conseguido dominar la trasmutación de metales entre otras cosas.", rewards = "Recibirás:\n\n"},
[3519] = {title = "Un amigo en apuros", objective = "Habla con Dirania Brillargente en Cañada Umbría.", description = "Uy... Me ha picado una araña llamada Gythyiss la Vil mientras exploraba la cueva de las arañas cerca de aquí. Estoy seguro de que me ha envenenado así que tienes que ayudarme.\n\nPor favor, habla con Dirania Brillargente. Ella podrá ayudarme.\n\nDate prisa... me estoy mareando...", progress = nil, completion = "¡Oh, no! Me preguntaba por qué no había visto hoy a Iverron. Y eso que siempre le he dicho que tuviera cuidado con esas arañas...", rewards = "Recibirás:\n\n"},
[3520] = {title = "Los espíritus de los estriadores", objective = "Captura el espíritu de 3 estridadores en Feralas y después ve a ver a Yeh'kinya al Puerto Bonvapor.", description = "Busco espíritus de los estridadores del valle de Feralas. Son primos lejanos de un viejo dios y sus espíritus están unidos al suyo.\n\nCoge esta rama de zarza y ve a Feralas. Busca estridadores del valle en la zona sur y central de Feralas y derrótalos. Después agita mi rama sobre sus cuerpos para que sus espíritus se muestren. Toca el espíritu y recoge su esencia.\n\nRegresa con mi rama y con la esencia que hayas recogido y te contaré más acerca del viejo dios del que te hablé.", progress = "¿Has capturado la esencia de los espíritus de los estridadores, <nombre>?", completion = "Lo has hecho muy bien, &lt;nombre&gt;. Quizás ha llegado el momento de que cuente más cosas...\n\nUna antigua profecía de los trols cuenta que el dios muerto Hakkar, el Cazador de Almas, un día regresará a esta tierra. Algunos dicen que el regreso de Hakkar traerá dicha y gloria a los trols, mientras que otros creen que su resurrección significará el principio del fin.\n\nYo no creo en esta leyenda pero sé que la esencia de Hakkar ostenta gran poder, por lo que no debe caer en manos de aquellos que no harán buen uso de ella...", rewards = "Recibirás:\n\n"},
[3521] = {title = "En antídoto de Iverron", objective = "Reúne 7 champiñones jacinto, 4 lirios alunados y 1 icor de Tejemadera para Dirania Brillargente, que está en Cañada Umbría.", description = "Conozco un antídoto que podría salvar a Iverron del veneno. Para prepararlo me harán falta algunos ingredientes.\n\nNecesitaré champiñones jacinto, que nacen al lado de los árboles. También puedes quitárselos a los grells que viven al sur de aquí, que parece que se han aficionado a ellos. También necesitaré lirios alunados, que solamente nacen cerca de charcas y grandes extensiones de agua.\n\nEl último ingrediente quizás sea el más difícil de conseguir. Se trata de coger icor de Tejemadera de las propias arañas que han envenenado a Iverron.", progress = "Por favor, reúne los ingredientes. Iverron necesita nuestra ayuda.", completion = "¡Gracias a Elune que has conseguido los ingredientes con tanta rapidez!\n\nEn un momento, tendré el antídoto preparado.", rewards = "Recibirás:\n\n"},
[3522] = {title = "En antídoto de Iverron", objective = "Llévale el antídoto de Iverron a Iverron antes de que se acabe el tiempo. Lo encontrarás junto a la cueva situada al norte.", description = "El antídoto está listo, <nombre>. Por favor, encárgate de que Iverron se lo tome.\n\nHay algo más que debes saber; el antídoto solamente será efectivo durante los próximos 5 minutos. Debes llevárselo a tiempo.\n\nEspero que lo logres, <nombre>.", progress = "Oh... <nombre>, me alegro de que hayas vuelto.", completion = "¿Qué es esto, &lt;nombre&gt;?\n\n¡Ah, sabía que Dirania podría ayudarme!\n\n\n\nSiempre me estoy metiendo en problemas y Dirania... bueno, ella siempre se las arregla para echarme una mano.\n\nYa me siento mucho mejor pero creo que me quedaré aquí sentado un rato hasta que esté totalmente recuperado. Mientras tanto, esperaba que pudieras despejar un camino de esas nocturácnidas sanguinarias...", rewards = "Podrás elegir una de estas recompensas:\n\nCalzones de currucaJubón de malla de corteza\n\nTambién recibirás:\n\nCalzones de currucaJubón de malla de corteza"},
[3523] = {title = "La plaga de la Zahúrda", objective = "Si decides ayudar a Belnistrasz, habla con él otra vez y devuélvele la piedra de juramento.", description = "¡Fantástico! Me llamo Belnistrasz, gracias por rescatarme. Esperaba la muerte o algo peor, como servir de cena a estas alimañas infectadas de Plaga. Sí, la Plaga. Han sellado algún pacto con los jabaespines y el resultado es esta pesadilla.\n\nNo quiero limitarme a huir; quiero cambiar las cosas, por el bien de todos. ¿Quieres ayudarme? Seguro que es peligroso, pero no parece que el peligro te preocupe mucho.", progress = "No me interesa la política, me da igual que estés con la Horda o con la Alianza. Lo que quiero es acabar con las amenazas a todas las razas, y aquí tenemos una importante.\n\nSi aceptas ayudarnos, dame la piedra de juramento como prueba de lealtad. Yo no soy de por aquí; las piedras de juramento son importantes para mi gente.", completion = "¡Estupendo, lo sabía! Ya lo tenemos todo hecho. Si nos concentramos en lo que tenemos que hacer, todo irá bien.\n\nVoy a ponerte al día de los acontecimientos...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3524] = {title = "Varado", objective = "Recoge huesos de la criatura marina varada que está al sur de Auberdine y llévaselos a Gwennyth Bly'Leggonde a Auberdine.", description = "Se dice que hay unas majestuosas criaturas que van a las playas de Costa Oscura a morir encalladas. Últimamente, estas criaturas han empezado a llegar cada vez en mayor número a la costa. Yo he venido aquí a petición del Templo de la Luna para investigar el suceso, pero la gran cantidad de murlocs que he encontrado en el agua dificulta mucho mis estudios.\n\nUna de esas gigantes criaturas llegó a la costa al sur de Auberdine para acabar rodeada de rabiosos murlocs Bruma Gris. ¿Podrías ir a traerme algunos huesos de la criatura para que los estudie?", progress = "Hace poco que esas criaturas han empezado a llegar a Costa Oscura en números tan alarmantes. No puedo evitar pensar que esto es algún tipo de augurio. Si consiguieras para Darnassus una muestra de los huesos de alguna de esas criaturas, nos ayudarías a entender la situación.", completion = "¡Espero que el múrloc no te pusiera difícil conseguirnos esto! Me aseguraré de mandar esto a Darnassus en el siguiente hipogrifo. El Templo de la Luna me ha concedido unos fondos para entregar a quien nos ayude. Te entrego una parte con nuestro agradecimiento.\n\nTu éxito me ha animado a ofrecerte la posibilidad de ayudar más al Templo de la Luna, si te interesa...", rewards = "Recibirás:\n\n"},
[3525] = {title = "Extinguir el ídolo", objective = "Acompaña a Belnistrasz al ídolo jabaespín en Zahúrda Rajacieno. \n\nProtégelo mientras realiza el ritual para inutilizar el ídolo.", description = "Cerca de la entrada hay un ídolo que sirve de horno macabro para los jabaespines. Esas bestias consumen literalmente a quienes secuestran en Los Baldíos. Puedo cerrar el ídolo e impedir que vuelvan a usarlo, pero necesito tu ayuda. Llévame hasta el ídolo y ejecutaré mi ritual. Apenas si podré comunicarme contigo, por no hablar de defenderme. Protégeme; si me matan, será el fin para todos nosotros.\n\n¿Estás conmigo?", progress = nil, completion = "Belnistrasz ha dejado atrás un blandón ardiente. Miras la llama y tu mente se llena con la imagen de un gran dragón rojo. Oyes la voz de Belnistrasz.\n\n\"Los mortales sois los héroes que estaba esperando. Gracias por detener la matanza. Acepta esto como recompensa por tu heroicidad.\"", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nAnillo garra de dragón"},
[3526] = {title = "Ingeniería goblin", objective = "Si quieres saber más sobre ingeniería goblin, llévale el Manual de disciplinas de ingeniería a Nixx Muellepiñón de Gadgetzan.", description = "La ingeniería avanzada se divide en dos disciplinas: la de los gnomos y la de los goblins. Claramente, la ingeniería goblin es la elección de todos los ingenieros brillantes. ¡Aprende a controlar el mundo a través del juicioso uso de potentes explosivos e increíbles aparatos!\n\n<nombre>, lee este manual. Si quieres aprender ingeniería goblin, llévaselo a Nixx Muellepiñón que está en Gadgetzan. Recuerda que si te inscribes, no podrás pasarte luego a la otra escuela, ¡así que piénsatelo bien!", progress = "Puedo oler a los reclutas novatos a un kilómetro de distancia, como a la buena pólvora. ¿Has venido para aprender a provocar explosiones?", completion = "Bueno, &lt;nombre&gt;, ¡has sido muy inteligente buscando mi enseñanza! Cualquier pringado puede convertirse en un experto de las bases de ingeniería, no hay más que ver a los gnomos y su irradiado hogar en Gnomeregan. Pero hace falta una mente fuerte y una mano FIRME para dominar la disciplina de la ingeniería goblin. \n\nEscucha con atención, &lt;nombre&gt;, y puede que aprendas algo.", rewards = "Recibirás:\n\n"},
[3527] = {title = "La profecía de Mosh'aru", objective = "Llévale la primera y segunda tablillas de Mosh'aru a Yeh'kinya, que está en Tanaris.", description = "La antigua profecía de Mosh'aru cuenta el modo de encerrar la esencia del dios Hakkar. Está escrita en dos tablillas que fueron depositadas en la ciudad trol de Zul'Farrak, al oeste de Gadgetzan.\n\n¡Tráeme las Tablillas de Mosh'aru!\n\nLa primera tablilla obra en poder de un trol difunto llamado Theka el Mártir. Se dice que sus perseguidores fueron transformados en escarabajos que ahora vagan por los alrededores de su sepulcro. \n\nLa segunda tablilla está en manos de la hidromántica Velratha, cerca de la poza sagrada de Gahz'rilla.\n\nCuando tengas las tablillas, tráemelas.", progress = "¿Tienes las tablillas de la profecía de Mosh'aru, <nombre>?\n\nCuando tenga las tablillas... ¡podré estudiarlas y descubrir cómo encerrar la esencia de Hakkar!", completion = "¡Ah, qué bien! Puedo fusionar estas dos tablillas y después estudiar los secretos que encierran. \n\nMi deuda contigo va en aumento, &lt;nombre&gt;, y continuará haciéndolo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3528] = {title = "El dios Hakkar", objective = "Llévale el Huevo relleno de Hakkar a Yeh'kinya, que está en Tanaris.", description = "Ha llegado la hora de atrapar la esencia del avatar de Hakkar.\n\nLleva el Huevo de Hakkar al Sagrario del Dios Caído que se encuentra en el Templo Sumergido de Azeroth. Invoca el poder del huevo para agitar al dios muerto.\n\nÉl enviará a sus esbirros a atacarte. Derrota a sus chupasangres y extráeles su sangre Hakkari. A continuación, utiliza la sangre para apagar las llamas eternas que protegen el espíritu de Hakkar. Cuando el fuego haya sido sofocado, el avatar de Hakkar entrará en nuestro mundo.\n\nDerrótalo y coloca su esencia en el interior del Huevo de Hakkar.", progress = "¿Has derrotado al avatar de Hakkar, <nombre>? ¿Has encerrado su esencia en el huevo que te di?", completion = "¡La esencia de Hakkar está encerrada! ¡Aun así, el huevo rezuma vida! ¿Será cierto el mito sobre Hakkar?\n\nEspero que no pues quién sabe qué haría Hakkar si volviera a nacer. Guardaré el huevo en un lugar seguro para que no caiga en malas manos.\n\nTu trabajo conmigo ha concluido, &lt;nombre&gt;. Ahora eres una auténtica leyenda y tus acciones algún día cambiarán nuestro mundo.", rewards = "Podrás elegir una de estas recompensas:\n\nYelmo de guardia vengadorDaga dirk Potencia de vidaAro Ráfaga de gemas\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nYelmo de guardia vengadorDaga dirk Potencia de vidaAro Ráfaga de gemas"},
[3541] = {title = "Entrega a Jes'rimon", objective = "Envía la tablilla de Jin'yael a Jes'rimon en Orgrimmar.", description = "Tengo un perista en Orgrimmar. Reside en La Calle Mayor. No es fácil dar con él si no sabes dónde buscar pero si miras en uno de los edificios más altos, no deberías tardar en encontrarlo.\n\nAunque hazme un favor, no vayas pregonando por todo Kalimdor que está allí y a qué se dedica, sería perjudicial para ambos.\n\nTe dará un precio más que correcto por la tablilla de Tablet de Jin'yael. No sé muy bien por qué pero deberías haber visto lo nervioso que se puso cuando supo su paradero.", progress = "¿Qué va a ser, colega? Tengo muchas cosas qu'hacer hoy y en principio no tengo hueco en la agenda pa' ti. Dame la lata y eso cambiará.", completion = "Ah, Jediga ha mandao su paquete desde Azshara. Bien. Qué ganas tenía de pillarlo. \n\nJediga, toda una mujer... mortal también. ¿Qué más se puede pedir? \n\nFíjate... la tablilla está en perfecto estado. Eres un mensajero daguten, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[3542] = {title = "Entrega a Andron Gant", objective = "Llévale la tablilla de Markri a Andron Gant en Entrañas.", description = "Andron Gant es mi contacto siguiente. Suele andar por Oxis... al ladito de El Apothecarium.\n\nTenía la lengua algo más suelta cuando se trataba de hablar de para qué quería la tablilla. Al parecer, algunos de los magos de Entrañas están estudiando algunos hechizos arcanos que los Ilustres usaban. Piensan que esta tablilla los ayudará. Markri era un adepto a los conjuros de criaturas, sobre todo los elementales.\n\nLleva la tablilla de Markri y vuelve aquí para tu parte de la recompensa.", progress = "Vaya... <hic>... ¡Qué sorpresa! ¡Un aventututurero... <hic>!\n\n¿Qué puedo hacer pa' ti... <hic>?", completion = "Toma, ahí van unas monedas... pa' que... &lt;hic&gt;... te compres una nueva armadura y tal... \n\nEsto supondrá una pieza o dos de oro pa' la gente... &lt;hic&gt; apropiada.", rewards = "Recibirás:\n\n"},
[3561] = {title = "Entrega al archimago Xylem", objective = "Llévale la tablilla de Sael'hai al archimago Xylem en Azshara.", description = "Esta última tablilla es para alguien muy peligroso, <nombre>. Créeme cuando te digo que confío menos en esta persona que en cualquiera de los otros a los que te he pedido que vayas a ver.\n\nEl archimago Xylem tiene una torre en las colinas que se elevan junto a Azshara. Es humano pero no se comporta como los humanos que he conocido… o matado. Exuda poder y no oso preguntarle cuáles son sus intenciones con la tablilla.\n\nVino a mi encuentro pero me dijo que su guardaespaldas Sanath me ayudaría a llegar a la torre cuando estuviera listo para verle. Busca a Sanath hacia el norte.", progress = "¿Qué puedo hacer por ti, <nombre>?", completion = "Ah, la tablilla de Sael'hai... ¡cuánto tiempo llevo esperando para estudiarla! \n\nGracias, &lt;nombre&gt;. Puedes decirle a Jediga que ha hecho un buen trabajo. Esto sin duda vale el pago que pedía.", rewards = "Recibirás:\n\n"},
[3562] = {title = "El pago de Magatha a Jediga", objective = "Entrega la nota de Magatha a Jediga en Azshara.", description = "Llévale esta nota a Jediga. Dile que el pago estará aquí para ella cuando regrese. Y dile que estoy satisfecha con su trabajo.\n\nCorre, <nombre>. Dile a tu maestra que estaré esperándola aquí.", progress = "Bueno, ¿qué dijo Magatha? La vieja bruja... Apuesto que no fue precisamente educada.", completion = "Ah, bien. Fue mucho más razonable de lo que esperaba... al menos contigo. Casi esperaba que te castigara por mi insolencia al enviar a alguien en mi lugar. Debe de tener muchas ganas de hacerse con esa tablilla. Interesante. \n\nAlgo en lo que pensar en otro momento.", rewards = "Recibirás:\n\n"},
[3563] = {title = "El pago de Jes'rimon a Jediga", objective = "Entrega la nota de Jes'rimon a Jediga en Azshara.", description = "Llevar esta carta a Jediga. Decir ella nuestro trato haber sido provechoso y que yo estar satisfecho con su trabajo.\n\nDecir a ella volver rápidamente a Orgrimmar y Jes'rimon cuidar ella bien.\n\nDecir eso ella, y quizás en futuro yo necesitar tus servicios otra vez, <nombre>.", progress = "¿Has hablado ya con Jes'rimon?\n\nEs un jabalí entre trols pero su información suele ser precisa y paga bien por sus trabajos.\n\nSi puedes soportar su actitud, entonces podrás ganarte un buen dinero con sus negocios.", completion = "Veo que estaba más que contento por el aspecto de su nota... un pelín demasiado contento. \n\nQuizás algún día me tome el tiempo para asegurarme de que sepa cuánto aprecio también sus insinuaciones. Bueno, al menos hay una razón para desear pasar más tiempo en Azshara. \n\nAquí tienes tu parte del pago.", rewards = "Recibirás:\n\n"},
[3564] = {title = "El pago de Andron a Jediga", objective = "Entrega la nota de Andron a Jediga en Azshara.", description = "Ahí va... lleva... (hic) esto a Jediga. ¡Que te vaya daguten!", progress = "¿Has visto ya a Andron? Casi nunca sale de Entrañas, así que no debería costar mucho encontrarlo.", completion = "Perfecto... espera un segundo. \n\n¡Ajá! ¿Qué le dijiste cuando le entregaste la tablilla, &lt;nombre&gt;? ¡Se ha pasado tres pueblos con el pago! No es que me queje. ¿Le dijiste que el trato había cambiado? ¿O acaso estaba borracho para variar? \n\nDa igual... aquí tienes, parte extra para ti también.", rewards = "Recibirás:\n\n"},
[3565] = {title = "El pago de Xylem a Jediga", objective = "Entrega la nota Xylem a Jediga en Azshara.", description = "¡¿Qué ocurre?! Ah sí, tu pago. Desde luego.\n\nToma esto y hasta luego. Seguro que Jediga aguarda con santa paciencia tu regreso.", progress = "Bueno, ¿qué te dijo Xylem? No te hizo nada, ¿verdad? ¿No te habrá lanzado un hechizo? ¿O engatusado para que le hagas una faenita?", completion = "¿Ninguna complicación? ¿Para nada? Bueno, eso casi merece quedar en los anales... Esperaba que mostrara algo de reticencia o que al menos traicionara nuestro pacto. Mmm, a lo mejor hasta es de fiar después de todo. \n\nBueno, tampoco hay que pensar tanto en eso. Aquí tienes tu parte Gracias por tu ayuda.", rewards = "Recibirás:\n\n"},
[3566] = {title = "¡Arriba, Obsidion!", objective = "Lleva la cabeza de Lathoric el Negro y el corazón de Obsidion a Thorius a Forjaz.", description = "Era una trampa... \n\nPerdóname. \n\nMata a ese malnacido. Hazlo por nosotros..., por Thorius.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nQuijotes centuriónCasco de LordrecAnillo de Entereza\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAnillo de EnterezaQuijotes centuriónCasco de Lordrec"},
[3567] = {title = "A la cima", objective = "¿Quieres ver la vista desde uno de los colosos gemelos? Porque puedo mandarte allí arriba, <raza>.\n\nPero tendrás que pensar tú en cómo harás para bajar. Yo te ofrezco solo el viaje de ida.", description = nil, progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nTeletransporte de colosos gemelos"},
[3568] = {title = "La filtración de la Corrupción", objective = "Llena los 4 viales vacíos en las pozas de las mareas que se encuentran a lo largo de la costa de las Ruinas de Eldarath en Azshara y después ve a ver al químico Cuely.", description = "¿Ves a la tauren que se encuentra detrás de mí, la que parece que va a estirar la pata de un momento a otro? Hace poco estuvo en Azshara. Afirma que unos nagas la hirieron cuando atravesaba las Ruinas de Eldarath.\n\nTras su enfrentamiento con ellos, se tomó un descanso en la playa y bebió de una de las pozas de las mareas. Ahora tiene náuseas... entre otros síntomas.\n\nMe gustaría descubrir qué ha bebido. Coge esta caja que contiene algunos viales vacíos que he etiquetado. Recoge muestras en las distintas pozas y regresa aquí.", progress = "Sabes cómo acabará esto, ¿no es así, <nombre>? No tiene ninguna posibilidad de sobrevivir... mírala. Lo mejor que puede hacer es ayudarnos a encontrar una cura para la peste. El agua que ella bebió podrá darnos alguna pista de cómo conseguirlo. De todos modos morirá así que es mejor que antes haga algo noble por nuestro pueblo. ¿No crees?", completion = "Estupendo. Has vuelto y ella aún sigue con vida. Muy bien.\n\nPongámonos manos a la obra.\n\nLlevo un tiempo observando sus síntomas y esto no tiene buena pinta. De hecho, me sorprende que siga en pie. Ha vomitado unas cuantas veces... ¡no veas qué olor!\n\nDame un segundo para prepararlo todo y podremos hacer una prueba con nuestra amiga.", rewards = "Recibirás:\n\n"},
[3569] = {title = "La filtración de la Corrupción", objective = "Dale el elixir de Cuely a Thersa Canzalvaje, que está en Entrañas.", description = "Dale esto a Thersa. Quizás la cure... o quizás no. ¡No lo sabremos hasta que se lo tome!", progress = "He venido a Entrañas porque se dice que sus alquimistas llevan tiempo estudiando el modo de curar la enfermedad de la que son víctimas. Suponía que podrían ayudarme.\n\nAunque este lugar no me resulte acogedor, no tengo elección pues la enfermedad que padezco me está matando y necesito ayuda.", completion = "El químico Cuely está siendo muy amable conmigo... quizás en exceso. Si él dice que me ayudará, no tengo elección. No tengo tiempo para buscar una solución alternativa. Ya he hablado con sacerdotes y chamanes, y ninguno pudo ayudarme.\n\nEsperemos que la Madre Tierra vele por mi espíritu.", rewards = "Recibirás:\n\n"},
[3570] = {title = "La filtración de la Corrupción", objective = "Nunca hubiera imaginado que el elixir hiciera efecto tan rápido. Gracias, <nombre>. Ahora me pondré manos a la obra con las demás muestras. Seguro que muy pronto les resultan de utilidad a Lady Sylvanas y a los Renegados.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3601] = {title = "¡Kim'jael, claro!", objective = "Busca en el campamento base thalassiano la brújula de Kim'jael, el telescopio, el pollo relleno y el y llévaselo todo a Azshara.", description = "¡¡Kim'jael, claro!! ¿Sabes lo que significa Kim'jael para los elfos de sangre, <nombre>? Ratita... o algo parecido. ¡Abusones!\n\nMe contrataron para que los ayudara a rastrear el área en busca de algún tipo de tesoro perdido. Por supuesto, no me lo pensé un momento cuando mencionaron el \"tesoro\" y el dinero que me iban a pagar pero claro, se volvieron contra mí e intentaron matarme... ¡A MÍ!\n\nToma prueba, un elfo de sangre no es de fiar.\n\nEl problema es que aún tienen mi equipo y no puedo recuperarlo. ¿Me ayudas?", progress = "¡Malditos elfos de sangre! Te lo digo, si fuera tan fuerte como tú, m'iba pa' allá ¡y les iba a dar pa’l pelo yo mismo! Pero me tengo que conformar con pagarte pa' que t'ocupes d'ellos.", completion = "¡Ea! ¡¿A que les has dado su merecido?!\n\n¡Estupendo! Eso enseñará a los elfos de sangre a no traicionar a sus socios... ¡y menos a alguien tan leal como un mercader goblin! ¡Bah, ¡elfos de sangre!\n\nBueno, lo mínimo que puedo hacer es pagarte por tu tiempo... como dije que haría.\n\nAquí tienes... deberían serte útiles.", rewards = "Recibirás:\n\nM73 granada de fragmentación"},
[3602] = {title = "Azsharita", objective = "Recupera 20 azsharitas cristalizadas del sur de Azshara. Puede que encuentres canes manáfagos dispuestos a ayudarte a rastrear la ubicación de los depósitos hábilmente escondidos.\n\nVuelve con Loramus cuando hayas completado la tarea.", description = "Los ancianos gigantes de los acantilados al sur de la región de Azshara se alimentan de las rocas y de las edificaciones. El excremento resultante es un material altamente maleable, un cristal de increíble resistencia conocido como azsharita.\n\nEstas formaciones abundan en el arco sur pero rara vez se recuperan debido a la naturaleza de los gigantes es extremadamente violenta.\n\nHas de dirigirte al sur y recuperar suficiente azsharita para crear el cuerpo de la herramienta. Mis canes manáfagos te ayudarán a conseguir el cristal.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3621] = {title = "La formación de Felbane", objective = "Llévale el cargamento a Galvan el Ancestro en la Vega de Tuercespina.", description = "Las armas están listas para ser forjadas. Debes lograr que la azsharita adquiera un temple con potencia para dañar la energía inferi.\n\nEn la Vega de Tuercespina encontrarás al herrero a la altura de esta tarea. Galvan el Ancestro, jefe de la Orden del Mitril, fabricará las armas como tú se lo encargues. Encontrarás su campamento entre Zul'Gurub y el Túmulo Ogro Mosh'Ogg.\n\nQue tu determinación y fe te acompañen en tu odisea. Si fracasas, ten la certeza de que cuentas con el orgullo de tu dios, mortal.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3625] = {title = "Armas Fel en Azsharita encantada", objective = "Espera que Galvan acabe de forjar las armas.", description = "<nombre>, llevará un tiempo crear cada arma.\n\nAdemás, solo puedes usar el poder del Vilhechizo un minuto de cada tres. Sus propiedades debilitadoras contra Razelikh el Rapiñador y sus esbirros pueden tener una duración variable. ¡Por eso debes utilizar el Vilhechizo en el momento justo!\n\nAhora dame un momento para fabricarte el arma, <nombre>.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nDaga Felbane de azsharita encantadaBastón Felbane de azsharita encantadoEspada Felbane de azsharita encantada\n\nTambién recibirás:\n\nEspada Felbane de azsharita encantadaDaga Felbane de azsharita encantadaBastón Felbane de azsharita encantado"},
[3626] = {title = "El regreso a Las Tierras Devastadas", objective = "Vuelve junto al Héroe Caído de la Horda.", description = "Se acerca la hora de la redención, <nombre>. Debes regresar a Las Tierras Devastadas con tus nuevas armas y hablar con el Héroe Caído de la Horda, él te indicará tu objetivo final.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3627] = {title = "Juntar pedazos del amuleto", objective = "Mata a Grol el Destructor, a Lady Sevine y al archimago Allistarj. De sus cadáveres coge el amuleto de Grol, el amuleto de Sevine y el amuleto de Allistarj.\n\nAcuérdate de USAR tus armas Fel en Azsharita encantada para debilitar a la tríade. Es la única forma de matarlos, pero úsalas con moderación...", description = "Para llegar hasta el Rapiñador, antes debes destruir la tríade de poderes y recuperar las piezas del amuleto. Cuando hayas recuperado las tres piezas, vuelve aquí y te daré el resto de las instrucciones.\n\n¡Vete, <nombre>! ¡A Las Tierras Devastadas! ¡Sevine, Allistarj y Grol deben caer!Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3628] = {title = "Eres Rakh'likh, demonio", objective = "Destruye a Razelikh el Rapiñador y llévale el cuerno cercenado del Rapiñador al Héroe Caído de la Horda. También tendrás que devolver el protector del Rapiñador para que el Héroe Caído pueda destruirlo y así impedir que caiga en las manos equivocadas.", description = "La guarida de Razelikh está en la cima de la montaña más alta de Las Tierras Devastadas. La única forma de acceder es mediante las runas teletransportadoras próximas a la montaña. Siempre que tengas el amuleto, podrás teletransportarte sin problemas hasta la guarida. Cuando llegues allí, debes conjurar a la bestia. La clave está en el amuleto, <nombre>. Utilízalo en el Altar de los Rapiñadores.\n\nSi logras derrotar al Rapiñador, tráeme su cuerno. Se convertirá en un símbolo de esperanza para quien lo vea, una prueba de que existen héroes en este mundo desgarrado por la guerra.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCollar de SantuarioSangre de demonioSaco de pellejo de demonio"},
[3629] = {title = "Ingeniería goblin", objective = "Si quieres aprender más sobre ingeniería gnoma, lleva el Manual de disciplinas de ingeniería a Nixx Muellepiñón en Gadgetzan.", description = "La ingeniería avanzada se divide en dos disciplinas: la de los gnomos y la de los goblins. Menciono a los goblins puramente porque existen. Les gusta construir cosas que exploran y pocas veces se preocupan por lo que hay en medio... incluso si son ellos mismos.\n\n<nombre>, léete este manual. Si quieres aprender a salir por los aires, llévaselo a Nixx Muellepiñón, en Gadgetzan. Recuerda que si te inscribes, no podrás pasarte luego a la otra escuela, ¡así que piénsatelo bien!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3630] = {title = "Ingeniería gnómica", objective = "Si quieres aprender más sobre ingeniería gnoma, lleva el Manual de disciplinas de ingeniería al maestro manitas Sobrechispa en Forjaz.", description = "En Ingeniería avanzada te encuentras con dos escuelas: la de los gnomos y la de los goblins. ¡La ingeniería de los gnomos es la mejor con diferencia! Se concentra en la creación de aparatos e instrumentos que hacen la vida más fácil.\n\nLéete este manual, <nombre>. Si quieres saber más sobre el tema, llévaselo aI maestro manitas Sobrechispa, aquí en Forjaz. Recuerda que si te inscribes, no podrás pasarte luego a la otra escuela, ¡así que piénsatelo bien! La ingeniería de los gnomos se concentra en la teoría y la aplica según nuestras necesidades.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3631] = {title = "Invoca un malignoecus", objective = "Habla con Strahad Farsan en Los Baldíos.", description = "Bah. Creo que no estás a punto, pero no soy yo quien decide. Strahad piensa lo contrario, así que haremos lo que quiere... por esta vez.\n\nQuiere hablar contigo lo antes posible. Estará en su torre con sus acólitos, más allá de Trinquete, en Los Baldíos.\n\nNo te entretengas por el camino.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3632] = {title = "Ingeniería gnómica", objective = "Si quieres saber más sobre el tema, llévale el Manual de disciplinas de ingeniería aI maestro manitas Sobrechispa a Forjaz.", description = "En ingeniería avanzada te encuentras con dos escuelas: la de los gnomos y la de los goblins. ¡La ingeniería de los gnomos es mejor con diferencia! Se concentra en la creación de aparatos e instrumentos que hacen la vida más fácil.\n\nLéete este manual, <nombre>. Si quieres saber más sobre el tema, llévaselo aI maestro manitas Sobrechispa, aquí en Forjaz. Recuerda que si te inscribes, no podrás pasarte luego a la otra escuela, ¡así que piénsatelo bien!", progress = "¿Puedo ayudarte en algo? No tengo nada mejor que hacer que ver cómo mi ciudad sucumbe a la muerte.", completion = "Me alegra ver que aún queda quien se interesa por la única escuela de ingeniería que vale la pena. Disculpa; la situación es difícil, pero me consuela ver que quedan aprendices para seguir nuestros pasos.", rewards = "Recibirás:\n\n"},
[3633] = {title = "Ingeniería goblin", objective = "Si quieres aprender más cosas sobre la ingeniería de los goblins, lleva el Manual de disciplinas de ingeniería a Nixx Muellepiñón en Gadgetzan.", description = "La ingeniería especializada en se plasma en dos disciplinas: la de los gnomos y la de los goblins. Por supuesto, la de los goblins es la que eligen todos los ingenieros inteligentes. ¡Aprende a controlar el mundo mediante el sensato uso de explosivos de gran potencia y mecanismos asombrosos!\n\n<nombre>, lee este manual. Si quieres aprender la ingeniería goblin, llévaselo a Nixx Muellepiñón en Gadgetzan. Recuerda, la pertenencia a una disciplina es permanente y excluyente de la otra, así que ¡piénsatelo bien antes de completar su tarea!", progress = "Puedo oler a los nuevos reclutas como un buen explosivo... en un kilómetro a la redonda. ¿Has venido para dominar el arte de los explosivos?", completion = "Bueno, &lt;nombre&gt;, ¡has sido muy prudente buscando mi enseñanza! Cualquier pringado puede convertirse en un experto de las bases de ingeniería, no hay más que ver a los gnomos y su irradiado hogar en Gnomeregan. Pero hace falta una mente fuerte y una mano FIRME para dominar la disciplina de la ingeniería goblin.\n\nAbre bien los oídos, &lt;nombre&gt;, y puede que aprendas algo.", rewards = "Recibirás:\n\n"},
[3634] = {title = "Ingeniería gnómica", objective = "Si quieres aprender más cosas sobre ingeniería gnómica, lleva el Manual de disciplinas de ingeniería al maestro manitas Sobrechispa en Forjaz.", description = "El nivel de especialización en ingeniería se plasma en dos disciplinas: la de los gnomos y la de los goblins. Aunque me parezca una pérdida de tiempo, tienes que saber que existe la especialización de los gnomos. Fabrican objetos y artilugios que pretenden cambiar el mundo pero que rara vez funcionan.\n\n<nombre>, lee este manual. Si quieres aprender cómo hacer pisapapeles metálicos, lleva este manual al maestro manitas Sobrechispa en Forjaz. Recuerda, la pertenencia a una disciplina es permanente y excluyente de la otra, así que ¡piénsatelo bien antes de completar su tarea!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3635] = {title = "Ingeniería gnómica", objective = "Si quieres saber más sobre ingeniería gnómica, llévale el Manual de disciplinas de ingeniería a Oglethorpe Obnoticus de Bahía del Botín.", description = "En Ingeniería avanzada te encuentras con dos escuelas: la de los gnomos y la de los goblins. Aunque yo piense que no sirve para nada, deberías de estar al corriente de la especialización de los gnomos. Crean objetos y aparatos que piensan van a cambiar el mundo, pero que raras veces funcionan.\n\n<nombre>, lee este manual. Si deseas aprender a hacer pisapapeles metálicos, lleva el manual a Oglethorpe Obnoticus que está en Bahía del Botín. Recuerda que si te inscribes, no podrás pasarte luego a la otra escuela, ¡así que piénsatelo bien!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3636] = {title = "Trae la Luz", objective = "El Arzobispo Bendictus quiere que mates a Amnennar el Gélido en Zahúrda Rajacieno.", description = "La Plaga es una amenaza constante para la Alianza, lo cual no es nada nuevo. Hace poco, nos topamos con unos hallazgos muy peculiares, <nombre>.\n\nEn los Baldíos existe un laberinto de púas llamado la Zahúrda Rajacieno. Solía ser el hogar de jabaespines, pero ahora parece ser que la Plaga ha hecho algún tipo de pacto, creando criaturas mucho peores... Las gobierna el exánime Amnennar el Gélido.\n\nÚnete a la Luz, <nombre>, y acaba con tal pacto.", progress = nil, completion = "Gracias, &lt;nombre&gt;. La Plaga ahora se lo pensará dos veces antes de intentar incrementar sus ejércitos.\n\nQue la Luz te bendiga.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nEspada del vencedorTalismán Resplandor de Ámbar"},
[3637] = {title = "Ingeniería gnómica", objective = "Si quieres aprender más cosas sobre ingeniería gnómica, lleva el Manual de disciplinas de ingeniería a Oglethorpe Obnoticus en Bahía del Botín.", description = "En Ingeniería avanzada te encuentras con dos escuelas: la de los gnomos y la de los goblins. Aunque yo piense que no sirve para nada, deberías de estar al corriente de la especialización de los gnomos. Crean objetos y aparatos que piensan van a cambiar el mundo, pero que raras veces funcionan.\n\n<nombre>, lee este manual. Si deseas aprender a hacer pisapapeles metálicos, lleva el manual a Oglethorpe Obnoticus que está en Bahía del Botín. Recuerda que si te inscribes, no podrás pasarte luego a la otra escuela, ¡así que piénsatelo bien!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3638] = {title = "Compromiso de secreto perpetuo", objective = "Si quieres convertirte en <un ingeniero/una ingeniera> goblin, haz clic con el botón derecho del ratón en el compromiso de secreto perpetuo y habla de nuevo con Nixx Muellepiñón de Gadgetzan.", description = "La ingeniería goblin persigue que sus creaciones tengan una aplicación práctica real y un poder explosivo aún mayor. Nuestros esquemas nos permiten fabricar potentes bombas y un poderoso armamento. En el manual que has leído se explica todo.\n\nEn él también se cita el juramento que hace mucho tiempo hicimos de mantener nuestros esquemas en secreto. Si te adhieres a la escuela de ingeniería goblin, ya está. No tendrás acceso de ningún tipo a la ingeniería de los gnomos.\n\nSi aun así sigues queriendo convertirte en un ingeniero goblin, firma este compromiso de secreto perpetuo y vuelve a hablar conmigo. Piénsatelo muy bien, <nombre>.", progress = "Si has firmado el documento y deseas entregármelo, no hay más que hablar. Ya sabes que no hay marcha atrás. ¡Si estás preparado, sube a bordo!", completion = "¡Bienvenido a bordo, ingeniero goblin &lt;nombre&gt;! Pronto tendrás acceso a los esquemas de ingeniería más poderosos en cualquier parte de Azeroth.\n\nAhora que te has comprometido a adquirir este conocimiento, veamos cómo se te da la ingeniería goblin. Después diseñaremos un currículo para desarrollar tus habilidades en ingeniería goblin.", rewards = "Recibirás:\n\n"},
[3639] = {title = "Muestra tus creaciones", objective = "Llévale 20 bombas grandes de hierro, 20 cartuchos de dinamita compacta y 5 ovejas explosivas a Nixx Muellepiñón de Gadgetzan.", description = "Como mentor tuyo que soy, quiero ver algunas muestras de tu trabajo. Nadie duda que se te da bien la ingeniería, <nombre>. Sin embargo, quiero analizar tus habilidades para orientarlas hacia técnicas propias de la ingeniería de los goblin. Necesito lo siguiente: 20 bombas de hierro grandes, 20 cartuchos de dinamita compacta y 5 ovejas explosivas. ¡Y mantente alerta!\n\nEntrégame todo esto y te daré tu tarjeta de miembro del Club de Ingeniería de los goblin; ¡la necesitarás para conseguir instructor!", progress = "Una vez que me entregues los objetos de ingeniería que has creado y pueda inspeccionarlos, te daré una tarjeta de miembro del Club de Ingeniería Goblin. Mientras la tarjeta sea válida, tendrás acceso ilimitado a todos los instructores de ingeniería goblin del mundo.", completion = "¡Aquí tienes tu primera tarjeta de miembro del Club de Ingeniería Goblin!\n\nEsta tarjeta tiene pagos los primeros catorce días. Todas las tarjetas expiran después de dos semanas de actividad. Cuando suceda, visita a cualquier instructor goblin de ingeniería para comprar otra. Sin la tarjeta, tu membresía caducará temporalmente y no podrás acceder a los nuevos esquemas.\n\nEsto no afecta tus conocimientos. Un ingeniero goblin nunca olvida lo que ya sabe.", rewards = "Te van a lanzar el siguiente hechizo:\n\nIngeniería goblin\n\nRecibirás:\n\nTarjeta de miembro del Club de Ingeniería Goblin"},
[3640] = {title = "La promesa del secreto", objective = "Si decides dedicarte a la ingeniería gnómica, haz clic con el botón derecho en el compromiso de secreto perpetuo y vuelve a hablar con el maestro manitas Sobrechispa en Forjaz.", description = "La ingeniería gnómica se concentra en la teoría y la aplica según nuestras necesidades. El manual ya lo explica: fabricamos instrumentos que controlan el mundo que nos rodea.\n\nTambién explica que juramos mantener nuestros planos en secreto. Si te adhieres a la escuela de ingeniería gnómica, ya está. No podrás leer nada sobre la ingeniería goblin.\n\nSi ya te has decidido, firma este compromiso de secreto perpetuo y vuelve a hablar conmigo. Piénsatelo muy bien, <nombre>.", progress = "Si me das el documento firmado, ya no hay marcha atrás.\n\nHaber firmado el compromiso de secreto perpetuo es un asunto muy serio; todos lo firmamos. ¡Bienvenido al mundo de la ingeniería gnómica, <nombre>!", completion = "¡Bien, &lt;nombre&gt;! Pronto tendrás acceso al mejor plan de estudios avanzado de ingeniería de Azeroth.\n\nVeamos cómo se te da la ingeniería gnómica y después diseñaremos un currículo adaptado a tus habilidades.", rewards = "Recibirás:\n\n"},
[3642] = {title = "La promesa del secreto", objective = "Si quieres convertirte en <un ingeniero/una ingeniera> gnomo, haz clic con el botón derecho del ratón en el compromiso de secreto perpetuo y vuelve a hablar con Oglethrope Obnoticus en la Bahía del Botín.", description = "La ingeniería de los gnomos pone la teoría de la ingeniería al servicio de nuestras necesidades. Nuestros sistemas nos permiten fabricar instrumentos que controlan el mundo que nos rodea. El manual que has leído habla de eso.\n\nTambién explica que juramos mantener nuestros planos en secreto. Cuando te conviertas en <un ingeniero/una ingeniera> gnomo, claro está. No podrás leer nada sobre la ingeniería goblin.\n\nSi ya te has decidido, firma este compromiso de secreto perpetuo y vuelve a hablar conmigo. Piénsatelo muy bien, <nombre>.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3643] = {title = "Muestra tu trabajo", objective = "Lleva 6 tubos de mitril, un escopo preciso y 2 muñecos-diana avanzados a Oglethorpe Obnoticus en la Bahía del Botín.", description = "Como tu mentor, quiero ver muestras de tu trabajo. Nadie duda que se te da bien la ingeniería, <nombre>. Pero quiero ver si te puedo orientar hacia técnicas más gnómicas para el futuro. Necesito lo siguiente: seis tubos de mitril, un escopo bien calibrado y dos muñecos-diana de prueba.\n\nEnvíame todo esto y yo te daré tu tarjeta de miembro del Club de Ingeniería Gnómica; ¡la necesitarás para conseguir instructor!Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nIngeniería gnoma\n\nRecibirás:\n\nTarjeta de miembro del Club de Ingeniería Gnoma"},
[3644] = {title = "Renovación de la tarjeta de miembro", objective = "Para renovar tu tarjeta de miembro tendrás que pagar las cuotas de renovación, que son dos piezas de oro.", description = nil, progress = "Para renovar tu tarjeta de miembro tendrás que pagar las cuotas de renovación, que son dos piezas de oro.", completion = "En vista de que eres una profesional de la ingeniería goblin con buena reputación, te voy a renovar tu tarjeta caducada por una cuota de dos piezas de oro. Cuando hayas pagado esta cuota, recibirás una nueva tarjeta de miembro con la que podrás recibir nuevos esquemas de ingeniería goblin de los instructores apropiados.\n\nEsta tarjeta tiene una validez de dos semanas de actividad, período tras el cual tendrás que pagar la cuota de nuevo. Si quieres tener acceso a los esquemas, tendrás que saldar tus deudas antes.", rewards = "Recibirás:\n\nTarjeta de miembro del Club de Ingeniería Goblin"},
[3645] = {title = "Renovación de la tarjeta de miembro", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nTarjeta de miembro del Club de Ingeniería Gnoma"},
[3646] = {title = "Renovación de la tarjeta de miembro", objective = nil, description = "[\n]Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nTarjeta de miembro del Club de Ingeniería Goblin"},
[3647] = {title = "Renovación de la tarjeta de miembro", objective = "In order to renew your membership card, you will need to pay the renewal fee of two gold pieces.", description = nil, progress = "In order to renew your membership card, you will need to pay the renewal fee of two gold pieces.", completion = "Since you're a practitioner of gnome engineering and are in good standing, I am able to renew a lapsed membership card for the fee of two gold pieces.  Once this fee is paid, you will be reissued a new membership card.  This will allow access to gnome engineering schemata from appropriate trainers.\n\nThis card will last for two weeks of activity, at the end of which your membership fees will become due again!  If you want to access schemata, you'll need to bring your dues out of arrears.", rewards = "Recibirás:\n\nTarjeta de miembro del Club de Ingeniería Gnoma"},
[3661] = {title = "¿Creados por Elune?", objective = "Recoge 15 plumas de lechúcico de las Tierras del Interior y llévaselas a Erelas Cieloámbar a la Aldea Rut'theran.", description = "Los ferálicos son muy impredecibles, <nombre>. Pueden ser muy mansos, pero si se sienten amenazados se vuelven extremadamente feroces.\n\nSe dice que fueron creados por Elune; me gustaría saber si es verdad.\n\nHe oído que hay ferálicos en las Tierras del Interior: los violentos y salvajes lechúcicos. Los encontrarás con el resto de la fauna local. ¿Me traerás unas cuantas plumas?", progress = "Intenta no acercarte a ellos; pueden sentirse amenazados y atacarte.", completion = "Gracias, &lt;nombre&gt;.\n\n¿Aprendiste algo de esas criaturas?\n\nCon estas plumas sabremos más de su naturaleza.\n\nVoy a estudiarlas; tú dedícate a tus cosas y vuelve a hablar conmigo en otro momento.", rewards = "Recibirás:\n\n"},
[3681] = {title = "Libro de la Divinidad", objective = "Ve a Ventormenta y habla con Duthorian Rall.", description = "Perdona por interrumpir tu viaje, <raza>, pero creí que deberías saber que ha llegado de Ventormenta un aviso que requería que los paladines regresaran para ser instruidos.\n\nSi no supiera que es imposible, diría que estás cerca del momento en el que te tocará estudiar los Libros.\n\nBusca a Duthorian Rall en Ventormenta. Normalmente entrena a los paladines en la Catedral de la Luz.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3701] = {title = "Las ruinas humeantes de Thaurissan", objective = "Ve a las Ruinas de Thaurissan, en las Estepas Ardientes, y recopila información sobre los tesoros de Thaurissan. Informa al historiador regio Archesonus.", description = "Intérnate en las ruinas de Thaurissan, en las Estepas Ardientes, y consigue toda la información que puedas.\n\nDicen que las ruinas están plagadas de tesoros misteriosos. Empieza tu búsqueda por ahí, <nombre>. Ten cuidado con los guardias de Thaurissan: son asesinos que protegen las ruinas y los secretos que hay en ellas.", progress = nil, completion = "Esto explica mucho sobre Thaurissan.\n\n¡Qué interesante!", rewards = "Recibirás:\n\nAnillo del Aristócrata"},
[3702] = {title = "Las ruinas humeantes de Thaurissan", objective = "Escucha al historiador regio Archesonus relatar la historia de Thaurissan.", description = "¿Te cuento la historia de los enanos Hierro Negro?", progress = nil, completion = "¿Proseguimos?", rewards = "Recibirás:\n\n"},
[3721] = {title = "Tu propio OOX", objective = "¡Has recuperado tres de mis robots mensajeros OOX! Has hecho mucho más de lo que tenías que hacer, así que te daré algo especial por toda tu ayuda. Es una versión miniatura del robot que tan bien conoces, ¡el OOX!\n\nNormalmente, no dejaría que nadie tuviese un dispositivo tan potencialmente letal, pero tú has sido de gran ayuda. Además, el mecanismo de combate y las funciones de búsqueda de este robot están desactivados.\n\nPor todo lo demás, ¡es un OOX auténtico!", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nPollo mecánico"},
[3741] = {title = "El collar de Hilary", objective = "Encuentra el collar de Hilary y llévaselo a Villa del Lago.", description = "Mi amiga Hilary ha perdido su collar. Hemos estado pescando todo el día justo aquí, pero ya hemos mirado. Tiene que haberse caído en el lago. Nuestros padres no nos dejan nadar porque dicen que hay cosas malas en el lago. Pero seguro que tú no tienes miedo de las cosas malas. \n\n¿Puedes buscar su collar, por favor?", progress = "Hola, ¿has encontrado mi collar? Papá dice que hay monstruos en el lago; ¿has visto alguno?", completion = "Gracias por encontrar mi collar, señor &lt;clase&gt;... Mi gatita también dice gracias, ¿a que sí, Effsee?", rewards = "Recibirás:\n\n"},
[3761] = {title = "Tierra de Un'Goro", objective = "Llévale 20 muestras de la tierra de Un'Goro a Ghede, que está en el Alto de los Ancestros de Cima del Trueno.", description = "Se dice que la tierra de Un'Goro ha sido enriquecida con potentes cualidades mágicas por lo que el Círculo Cenarion quiere estudiarla en grandes cantidades; ve a los páramos de Un'Goro y consigue suficientes muestras para que podamos continuar con nuestras investigaciones.\n\nEl suelo enriquecido formará pilas de cuando en cuando y es posible que las bestias que allí habitan posean muestras que te pudieran servir. Simplemente busca las pilas y estate <atento/atenta> a los taurens, y después llévale todo a Ghede. ¡Él tiene buenas intenciones pero más te vale que no lo cabrees!", progress = "¡Descanse, soldado! Si vienes a traer tierra de Un'Goro, estupendo. De lo contrario, márchate inmediatamente.", completion = "Muy bien, soldado &lt;nombre&gt;. ¡Quédate ahí mirando y no me ayudes a descargar la tierra! Si los Grandes Espíritus hubieran querido descargarla por arte de magia, ya lo habrían hecho, ¿no crees? \n\nEl archidruida Tótem de Runa quiere hablar contigo de nuevo pero para su siguiente tarea debes saber que le resultaría útil contar con un oficial herborista. Si no te ajustas al perfil, no te preocupes. Seguro que habrá algo en lo que le puedas ser de utilidad.", rewards = "Recibirás:\n\n"},
[3762] = {title = "Ayuda al archidruida Runatótem", objective = "Habla con el archidruida Hamuul Runatótem en Cima del Trueno.", description = "¿Tienes un minuto, <clase>?\n\nEl Círculo Cenarion de Cima del Trueno está buscando aventureros curtidos para un importante proyecto de investigación. No conozco los detalles, pero has de saber que quien dirige el proyecto es el mismísimo archidruida Hamuul Runatótem.\n\nSi te interesa, habla con él directamente. Lo encontrarás en el Alto de los Ancestros, en Cima del Trueno.", progress = nil, completion = "Saludos, &lt;clase&gt;. Me alegro de ver que te interesa colaborar en nuestra investigación. Queda mucho trabajo por delante y te mentiría si te dijese que no entraña peligro. Esperemos que esto no te haga desistir... aunque ya se ve que no es la primera vez que le plantas cara al peligro.", rewards = "Recibirás:\n\n"},
[3763] = {title = "Ayuda al archidruida Corzocelada", objective = "Ve a Darnassus y habla con el archidruida Fandral Corzocelada.", description = "Perdona, <clase>, ¿podrías concederme un momento?\n\nEl Círculo Cenarion de Darnassus está buscando la ayuda de aventureros curtidos para un importante proyecto de investigación. No tengo muchos detalles pero puedo decirte que es el mismísimo archidruida Fandral Corzocelada quien encabeza esta investigación.\n\nSi la cosa te interesa, habla con él directamente. Lo encontrarás en el Enclave Cenarion, en Darnassus.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3764] = {title = "La tierra de Un'Goro", objective = "Lleva 20 muestras de tierra de Un'Goro Soil a Jenal, que está en el Enclave Cenarion de Darnassus.", description = "Se dice que el suelo de Un'Goro ha sido enriquecido con potentes cualidades mágicas. Quiero saber qué hay de cierto en esto. Ve a los páramos de Un'Goro y consigue muestras fiables del suelo para que podamos estudiarlas.\n\nY si crees que estás muy por encima de tareas de recogida, no conseguirás ni mi respeto ni el del Círculo Cenarion. Lleva las muestras a Jenal que sin duda estará perdiendo el tiempo detrás de este árbol. Busca los montones de tierra. Él se encarga de la logística. No yo.", progress = "Ah, sí, la tierra. El archidruida quiere 20 cargas por aventurero. Y 20 tendrá. ¡Que aquellos que conspiran contra nosotros se pierdan en el camino!", completion = "Aligera aquí tu preciosa carga, noble &lt;clase&gt;. Ha debido de ser un gran peso... como el que llevo yo por ser un visionario. Aquí tienes un honorario por tu trabajo, tal y como dictó el propio archidruida.\n\nEl archidruida Corzocelada quiere hablar contigo de nuevo, &lt;nombre&gt;. Pero para la siguiente tarea que te encomiende necesitarás tener los conocimientos de botánica de un oficial. Aunque, si no los tienes, también podrás ayudar al Círculo.", rewards = "Recibirás:\n\n"},
[3765] = {title = "Lejana corrupción", objective = "Ve a ver a Gershala Noctusurro en Auberdine.", description = "Ventormenta... un centro de mucho movimiento.\n\nVengo desde muy lejos buscando ayuda. A menudo se dice que hay valientes aventureros caminando por las calles de esta ciudad.\n\nMi hermano, Gershala, necesita ayuda en Costa Oscura. Vive en la ciudad costera de Auberdine.\n\nEl viaje es largo y épico. Por esta razón, necesito la ayuda de un <clase> experimentado como tú.", progress = nil, completion = "Así que te ha enviado aquí mi hermano Argos, ¿eh? Supongo que debes de estar agotado de tanto viaje. Pero tenemos mucho que hablar.", rewards = "Recibirás:\n\n"},
[3781] = {title = "Investigar la mañagrana", objective = "Lleva el vale para semillas del archidruida a Mathrengyl Garroso al Enclave Cenarion de Darnassus.", description = "Con lo que llamamos una bolsa de siempreverde hemos cultivado semillas de Tharlendris en tierra del Cráter de Un'Goro. Estas semillas producen una gran variedad de hierbas potentes de forma aleatoria. Una de esas hierbas es la mañagrana, una hierba misteriosa de la que sabemos poco. Pero yo tengo intención de estudiarla a fondo.\n\nLleva esto a Mathrengyl. Está escaleras abajo. Él te dará algunas semillas para que empieces. Si te quedas sin reservas, puedes comprarle más.", progress = "Que la paz y la serenidad sean contigo. Vienes aquí por comisión del archidruida en persona, ¿verdad?", completion = "Sí, este vale es para veinte paquetes de semillas. Si se te acaban, podremos proporcionarte más. Aunque, por desgracia, producir grandes números de semillas de Tharlendris resulta bastante costoso. Por eso el Círculo Cenarion pide a quienes nos están ayudando que participen asumiendo parte de su coste.\n\nQuienes traigan mañagrana, encontrarán que la recompensa que ofrece el archidruida compensa con creces ese coste.", rewards = "Recibirás:\n\nPaquete de semillas Tharlendris"},
[3782] = {title = "Investigar la mañagrana", objective = "Llévale este vale para semillas a Bashana Runatótem, que está en Cima del Trueno.", description = "Con lo que llamamos una bolsa de siempreverde hemos cultivado semillas de Tharlendris en tierra del Cráter de Un'Goro. Estas semillas producen una gran variedad de hierbas potentes de forma aleatoria. Una de esas hierbas es la mañagrana, una hierba misteriosa de la que sabemos poco. Pero yo espero poder estudiarla a fondo para el Círculo.\n\nEntrégale este vale a mi hija Bashana y ella te dará algunas semillas para que puedas ayudarnos. Si te quedas sin ellas, tendrás que comprarle más.", progress = "La paz sea contigo, amigo. Vienes aquí por comisión del archidruida en persona, ¿verdad?", completion = "Por este vale puedo darte veinte paquetes de semillas. Si te quedas sin ellas, siempre puedes comprarme más. \n\nPor desgracia, &lt;nombre&gt;, resulta muy costoso cultivar semillas de Tharlendris en grandes cantidades. Por eso el Círculo Cenarion pide a quienes nos están ayudando que participen asumiendo parte de su coste. Aquellos que traigan mañagrana recibirán una recompensa del archidruida que compensará con creces los gastos.", rewards = "Recibirás:\n\nPaquete de semillas Tharlendris"},
[3783] = {title = "Yeti... ¿ya hemos llegado?", objective = "Reúne 10 pieles gruesas de yeti para Umi Risitas en Vista Eterna.", description = "Mis amigos siempre aparecen con grandes ideas. Siempre me ha parecido que no estaba a su altura... ¡hasta hoy! ¡Acabo de trazar el diseño de algo tremendo! Si me prometes no decir nada, te dejaré conocer el secreto...\n\n¡Voy a hacer un yeti mecánico! No solo servirá para asustar a mis amigos... ¡cuando esté listo será realmente impresionante!\n\nPara empezar necesito piel gruesa de yeti. ¿Me ayudarás? Podrás encontrarla al sureste de aquí.", progress = "Tengo muchas cosas que hacer. Por favor, ¡tráeme las pieles lo antes que puedas!", completion = "Estas pieles son perfectas, &lt;nombre&gt;. ¡Vamos a empezar a hacer algo grande!", rewards = "Recibirás:\n\n"},
[3784] = {title = "Ayuda al archidruida Runatótem", objective = "Habla con el archidruida Hamuul Runatótem en Cima del Trueno.", description = "¿Tienes un minuto, <clase>?\n\nEl Círculo Cenarion de Cima del Trueno está buscando aventureros curtidos para un importante proyecto de investigación. No conozco los detalles, pero has de saber que quien dirige el proyecto es el mismísimo archidruida Hamuul Runatótem.\n\nSi te interesa, habla con él directamente. Lo encontrarás en el Alto de los Ancestros, en Cima del Trueno.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3785] = {title = "Investigar la mañagrana", objective = "Utiliza una bolsa de siempreverde con 1 paquete de semillas de Tharlendris y 2 muestras de tierra de Un'Goro para intentar cultivar mañagrana. \n\nLleva 10 plantas de mañagrana a Mathrengyl Garroso a Darnassus.", description = "Aquí tienes la bolsa de siempreverde que necesitarás para las semillas de Tharlendris. También necesitarás algo de tierra de Un'Goro. Servirá de fertilizante para las semillas. Usa la bolsa cuando tengas todo. La bolsa se encargará de lo demás.\n\nCuando tengas suficiente mañagrana, tráemela aquí, al Enclave. Y por favor, no molestes al archidruida. Es un hombre muy ocupado y no es necesario interrumpirle cada vez que llega alguien con un poco de mañagrana.", progress = "¿Sí, <nombre>? ¿Has cultivado algo de mañagrana para la importante investigación del archidruida? El trabajo que está haciendo para el Círculo Cenarion es muy importante. ¡Tu ayuda no pasará desapercibida!", completion = "Bien hecho, &lt;nombre&gt;. Me aseguraré de dárselas al archidruida cuando las necesite. Mientras tanto, acepta esto como muestra del aprecio del Círculo Cenarion.\n\nNecesitamos mañagrana continuamente. Si quieres seguir ayudándonos, vuelve a mí cuando hayas cultivado más mañagrana.", rewards = "Recibirás:\n\nCarretilla del Círculo Cenarion"},
[3786] = {title = "Investigar la mañagrana", objective = "Mete en la bolsa de siempreverde un paquete de Tharlendris y dos muestras de tierra de Un'Goro para cultivar muestras de mañagrana.\n\nLlévale 10 mañagranas a Bashana Runatótem, que está en Cima del Trueno.", description = "Aquí tienes la bolsa de siempreverde que necesitarás para las semillas de Tharlendris. También necesitarás algo de tierra de Un'Goro que servirá de fertilizante para las semillas. Usa la bolsa cuando tengas todo y esta se encargará de lo demás.\n\nCuando tengas suficiente mañagrana, tráemela aquí, al Alto de los Ancestros. Y por favor, no molestes al archidruida. Estará muy ocupado con sus investigaciones y además tiene muchos asuntos más de los que ocuparse.", progress = "¿Sí, <nombre>? ¿Has cultivado algo de mañagrana para la importante investigación del archidruida? Cada día que pasa, se descubre algo nuevo sobre las misteriosas propiedades del Cráter de Un’Goro gracias a tu ayuda.", completion = "Bien hecho, &lt;nombre&gt;. Me aseguraré personalmente de entregárselas al archuidruida cuando las necesite. Mientras tanto, acepta esto como muestra de agradecimiento del Círculo Cenarion. \n\nDe momento, seguimos necesitando mañagrana así que si deseas seguir ayudándonos, vuelve cuando hayas cultivado más.", rewards = "Recibirás:\n\nCarretilla del Círculo Cenarion"},
[3787] = {title = "La petición de Jonguja", objective = "Habla con Quintis Jonguja en el Bastión Plumaluna.", description = "Oye, perdona...\n\nTengo un colega que querría hablar contigo sobre la investigación que estás llevando a cabo en colaboración con el Círculo Cenarion. Es un druida y se llama Quintis Jonguja. Vive aquí, en el Bastión Plumaluna, en Feralas. Quiere hablar contigo sobre la mañagrana. Y me ha pedido mucha discreción. No quiere que nada de esto llegue a oídos del archidruida.\n\nSi te interesa, habla con él. Sin duda, aclararemos muchas cosas cuando hayáis hablado.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3788] = {title = "La petición de Jonguja", objective = "Habla con Quintis Jonguja del Bastión Plumaluna.", description = "Oye, perdona...\n\nTengo un colega que querría hablar contigo sobre la investigación que estás llevando a cabo en colaboración con el Círculo Cenarion. Es un druida y se llama Quintis Jonguja. Vive aquí, en el Bastión Plumaluna. Quiere hablar contigo sobre la mañagrana. Y me ha pedido mucha discreción. No quiere que nada de esto llegue a oídos del archidruida.\n\nSi te interesa, habla con él. Sin duda, aclararemos muchas cosas cuando hayáis hablado.", progress = nil, completion = "¡Gracias por venir, viajero! Permite que me presente: soy Quintis Jonguja, druida y humilde servidor de Malfurión y del Círculo Cenarion. Mis colegas (el general Shandris Plumaluna y el Protectorado, igual has oído hablar de nosotros) y yo, queríamos hablar contigo sobre el trabajo que estás haciendo para Fandral Corzocelada en Darnassus.\n\nAntes de proseguir, quisiera pedirte que esta conversación quede entre nosotros. Si esto no te supone un problema, podemos continuar.", rewards = "Recibirás:\n\n"},
[3789] = {title = "Ayuda al archidruida Corzocelada", objective = "Habla con el archidruida Fandral Corzocelada en Darnassus.", description = "Perdona, <clase>, ¿podrías concederme un momento? \n\nEl Círculo Cenarion de Darnassus está buscando la ayuda de aventureros curtidos para un importante proyecto de investigación. No tengo muchos detalles pero puedo decirte que es el mismísimo archidruida Fandral Corzocelada quien encabeza esta investigación.\n\nSi la cosa te interesa, habla con él directamente. Lo encontrarás en el Enclave Cenarion, en Darnassus.", progress = nil, completion = "Me alegra ver que te has apresurado a venir al saber que yo dirijo el proyecto, &lt;clase&gt;.\n\nEscúchame con atención; no tengo ganas de repetir las cosas.", rewards = "Recibirás:\n\n"},
[3790] = {title = "Ayuda al archidruida Corzocelada", objective = "Habla con el archidruida de Darnassus, Fandral Corzocelada.", description = "¿Tienes un minuto, <clase>?\n\nEl Círculo Cenarion de Darnassus busca gente aventurera como tú para que les ayude en un proyecto vital de investigación. No conozco los detalles, pero has de saber que quien dirige el proyecto es el mismísimo archidruida Fandral Corzocelada.\n\nSi te interesa, ve a hablar con él en el Enclave Cenarion, en la misma Darnassus.", progress = nil, completion = "Bien, &lt;clase&gt;, veo que fuiste informada de que yo encabezaba la investigación en persona, y es evidente que viniste tan rápido como pudiste. \n\nAhora que estás aquí, presta atención, pues te daré los detalles una sola vez, no me gusta repetirme.", rewards = "Recibirás:\n\n"},
[3791] = {title = "El misterio de mañagrana", objective = "Lleva 10 plantas de mañagrana a Quintis Jonguja al Bastión Plumaluna.", description = "Permite que sea sincero contigo, <nombre>. Estoy interesado, no, más bien preocupado por el hecho de que el archidruida de Darnassus esté utilizando el nombre del Círculo Cenarion además de sus recursos para procurarse grandes cantidades de mañagrana. Soy un investigador. Mi especialidad son las maldiciones. He oído que la mañagrana, en determinadas condiciones, exuda las mismas cualidades que las hierbas que se utilizaban en maldiciones primitivas.\n\nQuiero saber más y para eso necesito tu ayuda. Tráeme 10 plantas de mañagrana y yo cuidaré de ti.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nGuantes de investigación de QuintisEspaldares de cortezaférrea\n\nTambién recibirás:\n\nGuantes de investigación de QuintisEspaldares de cortezaférrea"},
[3792] = {title = "Mañagrana a Bastión Plumaluna", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nPaquete de semillas Tharlendris"},
[3801] = {title = "El legado de los Hierro Negro", objective = "Habla con Franclorn Forjafina si te interesa obtener una llave de la ciudad.Saludos, <nombre>. Me llamo Franclorn Forjafina. Sí, el Franclorn Forjafina responsable del revolucionario método de arquitectura. Seguro que has cruzado o has estado en alguna de mis obras a lo largo de tus aventuras en este mundo desgarrado por la guerra.\n\nDebes de estar preguntándote qué hago aquí y qué quiero de ti, dos preguntas muy válidas.\n\nNecesito tu ayuda, claro. A cambio de esa ayuda, te concederé la llave de las profundidades de mi execrable ciudad.", description = "Saludos, <nombre>. Me llamo Franclorn Forjafina. Sí, el Franclorn Forjafina responsable del revolucionario método de arquitectura. Seguro que has cruzado o has estado en alguna de mis obras a lo largo de tus aventuras en este mundo desgarrado por la guerra.\n\nDebes de estar preguntándote qué hago aquí y qué quiero de ti, dos preguntas muy válidas.\n\nNecesito tu ayuda, claro. A cambio de esa ayuda, te concederé la llave de las profundidades de mi execrable ciudad.", progress = nil, completion = "Ya sea la codicia lo que te trajo aquí o un acto de entrega altruista, lo que estás a punto de acometer es bueno para todos.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3802] = {title = "El legado de los Hierro Negro", objective = "Mata a Finoso Virunegro y recupera el gran martillo, Ferrovil. Lleva a Ferrovil al Santuario de Thaurissan y coloca el martillo en la estatua de Franclorn Forjafina.", description = "Encontrarás a Fineous Virunegro pasado el Círculo de la Ley, en la Sala de los Oficios. Mata al miserable bellaco y recupera a Ferrovil.\n\nLleva a Ferrovil al Santuario de Thaurissan y coloca el martillo en su legítimo lugar: entre las manos de la estatua erigida en mi honor.\n\nCuando lo hayas hecho, el compartimento en el que guardé la llave maestra estará abierto. Y lo que es más importante, Ferrovil permanecerá para siempre jamás en mis manos. Si intentaran extraer el martillo, tanto la estatua como el martillo se harán añicos y se perderán para siempre.", progress = "La estatua se cierne inquietante sobre ti.", completion = "El martillo se desliza firmemente en su lugar, en el puño del colosal monumento Hierro Negro. Un minúsculo compartimento se abre en la base de la estatua. En el interior encuentras...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nLlave Sombratiniebla"},
[3803] = {title = "Mañagrana para Darnassus", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nCarretilla del Círculo Cenarion"},
[3804] = {title = "Mañagrana para Cima del Trueno", objective = "El archidruida siempre está buscando mañagrana para el proyecto de investigación sobre el Cráter de Un’Goro que lleva a cabo el Círculo Cenarion. La recogemos en lotes de 10 y recompensamos a quien la trae con un conjunto de objetos muy útiles. Si reúnes 10 plantas de mañagrana, tráemelas y te daré tu recompensa.", description = nil, progress = "El archidruida siempre está buscando mañagrana para el proyecto de investigación sobre el Cráter de Un’Goro que lleva a cabo el Círculo Cenarion. La recogemos en lotes de 10 y recompensamos a quien la trae con un conjunto de objetos muy útiles. Si reúnes 10 plantas de mañagrana, tráemelas y te daré tu recompensa.", completion = "Excelente, llegado el momento esta mañagrana sin duda será de utilidad. Sabemos que los elfos de la noche también están estudiando estas plantas, aunque desconocemos con qué propósito las usarán si llegan a descubrir algo. El archidruida cree que seguiremos necesitando mañagrana hasta que descubramos los misterios que encierra esta planta.\n\nEsto es para ti, &lt;nombre&gt;. El Círculo Cenarion te agradece tu constante apoyo.", rewards = "Recibirás:\n\nCarretilla del Círculo Cenarion"},
[3821] = {title = "Roca Machacamiedo", objective = "Adéntrate en Las Estepas Ardientes y averigua qué le ha ocurrido a Sha'ni Colminilludo.", description = "Enviaron a Sha'ni para investigar los informes sobre una extraña actividad ritualista procedente de los ogros Ardentrañas de Las Estepas Ardientes hace unas tres semanas.\n\nDientegore dice que no ha tenido noticias de nadie de esa sección y que es mejor que siga adelante y me centre en mi tarea actual. ¿Te lo puedes creer? ¡Se trata de mi esposa!\n\nNo puedo abandonar mi puesto, <nombre>, pero necesito saber qué le ha ocurrido a Sha'ni. Ve a Las Estepas Ardientes y búscala.", progress = nil, completion = "Pobre Thal'trak. \n\nHabíamos planeado mudarnos a vivir juntos con mi madre y mi padre en las Tierras del Interior al acabar nuestra gira. ¡Ahora todo se ha venido abajo!", rewards = "Recibirás:\n\n"},
[3822] = {title = "Krom'Grul", objective = "Recupera la alianza de la nariz de Sha'ni de Krom'Grul y llévasela a Thal'trak en Kargath. Busca por las diferentes cuevas y minas de la Roca Machacamiedo.", description = "Han capturado a toda mi sección, <nombre>. Todos ejecutados, excepto yo.\n\nSu jefe, Krom'Grul, quería reservarme para algún tipo de ritual.\n\nLa bestia me encadenó a este altar y me dio una paliza. Mi ser físico dejó de responder después de una hora de tortura. La bestia me arrancó entonces mi alianza de la nariz y se la puso en el dedo. ¡Nunca había visto semejante salvajada!\n\n¡Encuentra a Krom'Grul y mátalo! Después devuélvele mi alianza a Thal'trak.\n\nY, <nombre>, dile que nos volveremos a encontrar... en otra vida.", progress = "¿Has encontrado a Sha’ni? ¿Está bien?", completion = "&lt;Thal'trak se derrumba.&gt; \n\nÍbamos a mudarnos a las Tierras del Interior. ¿Te lo dijo? Esta iba a ser su última misión... \n\n&lt;Thal'trak solloza.&gt; \n\nNo la quiero, &lt;nombre&gt;. Quédatela, deshazte de ella, ¡no me importa! La sortija ahora ya no significa nada para mí. No sin ella...", rewards = "Recibirás:\n\nSortija de Sha'ni"},
[3823] = {title = "El exterminio de los Ardentrañas", objective = "Mata a 15 ogros magos Pirontraña, 7 ogros Pirontraña y 7 ogros brutos Pirontraña y vuelve junto a Oralius cuando hayas acabado.", description = "¡Debemos atacar al enemigo antes de que ellos lo hagan!\n\nSeré breve, soldado.\n\nLos ogros Ardentrañas de Las Estepas Ardientes son aliados de los ogros Roca Negra. Colaboran estrechamente con esos malvados orcos, manteniendo la vigilancia sobre la Roca Machacamiedo y sus abundantes minerales, así como garantizando que el suministro sea constante.\n\nQue noten tu presencia, <nombre>. Diezma sus tropas y vuelve aquí cuando hayas hecho tu trabajo.\n\n<Oralius hace un gesto como estrangulando el aire.>\n\n¡TE MATARÉ, WINKY!", progress = nil, completion = "¡Fantástico, soldado! Los orcos y los ogros se tambalearán unos minutos tras este ataque, ¡a lo mejor hasta horas!", rewards = "Recibirás:\n\n"},
[3824] = {title = "Gor'tesh, el Señor bruto", objective = "Encuentra a Gor'tesh, en el algún lugar al oeste de la Roca Machacamiedo. Mata a Gor'tesh y llévale entonces la cabeza cortada de Gor'tesh a Oralius en Las Estepas Ardientes.", description = "Se tambalean, ¡te lo digo yo! Nuestro próximo ataque debe ser decisivo.\n\nHay que matar a ese animal de lord Gor'tesh. Es más, quiero que me traigan su cabeza para poder preparar la tercera fase de nuestro ataque a los ogros Ardentrañas.\n\nPor cierto, soldado, Gor'tesh no gobierna desde la Roca Machacamiedo sino desde un enclave orco Roca Negra al oeste de la Roca Machacamiedo.\n\nPor lo tanto, tus órdenes son sencillas. Encuentra a Gor'tesh y córtale ese cabezón que tiene. Vuelve aquí cuando hayas acabado tu trabajo.\n\n¡CIERRA EL PICO, WINKY!", progress = "¡Oh, día de gloria! ¿Vuelves con la cabeza que te pedí?", completion = "¡Fíjate en esto, soldado! ¡Asqueroso! Cuando los ogros Ardentrañas vean esta gran jarra clavada en una estaca en la cumbre de su montaña, ¡se desatará la histeria colectiva!\n\n&lt;Oralius rompe en una carcajada desenfrenada.&gt;", rewards = "Recibirás:\n\n"},
[3825] = {title = "Una cabeza ogro empalada = FIESTA", objective = "Coge la cabeza cortada de Gor'tesh y colócala en lo alto de la Roca Machacamiedo. Busca un montículo de tierra blanda para clavar la pica.", description = "Solo queda una cosa más pendiente, soldado.\n\nMe he tomado la libertad de poner la cabeza de Gor'tesh en una pica. Solo tienes que llevar esta cabeza a la cima de la Roca Machacamiedo y clavarla en el suelo. Cuando lo hagas, ¡observa cómo esos ogros cobardes ponen pies en polvorosa! Cuando te hayas divertido lo suficiente, vuelve aquí y te recompensaré por esforzarte tanto en tu trabajo.\n\n<Oralius parece perder el contacto con la realidad.>", progress = nil, completion = "¡Fantástico trabajo, soldado! ¡Sencillamente asombroso! Oh, me darán la medalla al valor por esto, digo… que ¡te recompensarán generosamente por esto!", rewards = "Podrás elegir una de estas recompensas:\n\nGuanteletes exasperantesSortija de apnea\n\nTambién recibirás:\n\nGuanteletes exasperantesSortija de apnea"},
[3841] = {title = "Un huérfano que busca un hogar", objective = "Ve a Las Mil Agujas y habla con Quentin.", description = "Oh, hola, <nombre>. Me alegro de volver a verte. ¿Qué tal fue tu viaje a Darnassus?\n\nTe alegrará saber que por aquí hemos hecho muchos progresos en tu ausencia. Incluso logramos salvar un nido lleno de huevos. Por desgracia, la madre murió antes de que llegáramos a ella.\n\nDime, ¿te apetece tener una mascota? Los huevos eclosionarán pronto y las crías necesitarán <un buen padre/una buena madre>. ¿Y bien? ¿Qué dices?\n\nSi te interesa, lleva uno de los huevos a Quentin, está en el circuito de El Desierto de Sal. Él te dirá más.", progress = "Vaya, hola, <clase>.\n\nNo te dejes engañar, no estoy aquí por las carreras, por muy interesantes que puedan resultar.\n\nEstoy en Las Mil Agujas estudiando la tierra para ver si se puede salvar. He convertido en mi cruzada personal revitalizar esta zona con nueva tierra, tierra fértil. Quizás descubra que hay una fuente de agua cerca.\n\nPorque no me digas que no has notado lo extraña que es la transición de Feralas a Las Mil Agujas. Hay una magia extraña detrás de esto...", completion = "Ah, un huevo de duende dardo. Y uno que está casi a punto de eclosionar, si no me equivoco. Es asombroso que hayas encontrado un espécimen, sobre todo uno en tan buenas condiciones. Se ven pocos en Feralas en estos tiempos. Entiendo que había varios cazadores furtivos por la zona, intentando capturarlos y matarlos por varios motivos. Es una pena... son unas criaturas muy hermosas cuando llegan a la madurez.\n\nY dime, ¿cómo ha llegado a tus manos semejante joya?", rewards = "Recibirás:\n\n"},
[3842] = {title = "Una corta incubación", objective = "Lleva 2 elixires de la entereza a Quentin a Las Mil Agujas.", description = "Los tejelunas, ¿eh? Intrigante. Muy intrigante. Ojalá los duendes dardo supieran apreciar lo que significa que ellos dos les cuiden. Son muy capaces.\n\nAsí que te regalaron el huevo. Entonces cuídalo durante el tiempo que quieras. Parece que está a punto de eclosionar, pero deberíamos asegurarnos de que está bien y no ha sufrido daños.\n\nSolo necesito dos elixires de fortaleza para hacer un pequeño hechizo que proteja al huevo. Tengo el resto de los componentes necesarios aquí.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3843] = {title = "Un nuevo miembro en la familia", objective = "Lleva tu huevo de duende dardo frágil a las Tierras del Interior y habla con Agnar Domabestias, pero date prisa: si tardas más de 1 hora en encontrarlo, la cría que hay en el huevo morirá.", description = "Un magnífico espécimen, <nombre>. Si esta criatura sobrevive a la gestación, ¡será una mascota muy sana!\n\nHay una cosa más que deberías hacer si quieres seguir con esto. Dirígete al Pico Nidal de las Tierras del Interior. Habla con un enano que encontrarás allí. Se llama Agnar Domabestias y es un gran estudioso de los animales. Él debería poder guiarte en la fase final de incubación.\n\nPero date prisa, <nombre>, este huevo es frágil y no resistirá mucho sin los cuidados adecuados.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[3844] = {title = "Un secreto bien guardado", objective = "Busca alrededor del estanque alguna pista sobre a quién perteneció la balsa destrozada.", description = "Descubres una balsa destrozada junto a la orilla. Esto es solo un pequeño estanque así que no puedes hacerte a la idea de cómo terminó aquí. No obstante, cuando miras a los árboles descubres que el nivel del agua puede no ser constante.\n\nQuizás si exploras un poco la zona descubras más pistas...", progress = nil, completion = "Encuentras una talega empapada en el fondo del estanque.", rewards = "Recibirás:\n\n"},
[3845] = {title = "Un secreto bien guardado", objective = "Examina los objetos de la talega y devuélveselos a su propietario.", description = "Coges la talega y la llevas hasta la orilla. Tal vez examinando los objetos que contiene puedas averiguar a quién perteneció.\n\nHay algunos viajeros en el Refugio de Marshal. Quizás allí averigües algo más.", progress = "Hola. Ya te había visto antes... ¿no es cierto?", completion = "Oh... sí, esto me resulta familiar... en cierto modo...", rewards = "Recibirás:\n\n"},
[3861] = {title = "¡CLOQUEA!", objective = "Consigue comida para pollos y ve con el pollo que has conocido. Cuando lo encuentres, /dórale la píldora antes de darle la comida.", description = "\"Coo-co-co-ri-cooo... ¡COOC!\n\nCoo-co-cooo.\"\n\nEl gallo te mira y empieza a rascar el suelo. Te parece que está escribiendo algo, pero no lo lees bien. ¿Podría ser... S-A-L-D-E-A-N? El gallo se marcha antes de que puedas preguntar.", progress = "El pollo te mira con ojos fríos y cara de hambre.\n\n\"¡CO_COOOC!\n\nCoo-co-cooo.\"", completion = "\"¡COO-COOC!\"\n\nEl pollo se pone a engullir la comida.\n\nDe pronto se para, como sorprendido, y se agita incómodo. Vaya, este pollo es una... ¡está embarazado! Será mejor que mires debajo, a ver.", rewards = "Te van a lanzar el siguiente hechizo:\n\nInvocar huevo de gallina"},
[3881] = {title = "Expedición Salvación", objective = "Lleva un cajón de comestibles y el equipo de investigación a Williden Marshal en el Cráter de Un'Goro.", description = "No sé cómo conseguiste llegar hasta aquí, ¡pero eres una auténtica alegría para la vista! Soy Williden Marshal, y dirijo un grupo expedicionario independiente. Nuestro último y mayor esfuerzo se enfrentó a una serie innumerable de desastres, y el mayor de todos ellos fue la subestimada barbarie del Cráter de Un'Goro.\n\nCuando abandonamos nuestros campamentos base en las partes suroeste y noreste del cráter, dejamos atrás suministros de vital importancia. Pareces suficientemente <capacitado/capacitada>, ¿puedes ayudarnos a recuperarlos?", progress = "Si existe la más mínima posibilidad de salir de aquí con vida, necesitaremos nuestro cajón de comestibles. Además, recuperar algo de nuestro equipo de investigación sería un rayo de luz en un océano de desgracias.", completion = "¡Conseguiste recuperar nuestros comestibles Y nuestro equipo de investigación! No solo has salvado la expedición, ¡has salvado nuestras vidas! Yo y el resto de mi equipo no podemos agradecértelo lo suficiente. Has hecho posible que podamos quedarnos aquí por un tiempo.\n\nAunque no tenemos mucho, no vamos a permitir que te vayas con las manos vacías después de toda la ayuda que nos has prestado. Gracias, &lt;nombre&gt;, ¡ahora tienes un amigo en las Expediciones de Marshal!", rewards = "Recibirás:\n\n"},
[3882] = {title = "Choca esos huesos", objective = "Lleva 8 huesos de dinosaurio a Chispik Nilminer en el Cráter de Un'Goro.", description = "Tal vez viendo que soy el jefe de excavaciones de las Expediciones de Marshal, pienses que me gusta excavar. La verdad es que puede haber cosas peores en el mundo... pero no salto de alegría.\n\nSe supone que estábamos excavando en el Camino del Terror (un nombre FANTÁSTICO, por cierto) buscando diemetradones y stegodones muertos. ¡Y todavía quedan muchos vivos! Wil y Hol siguen estudiando este lugar, así que necesitamos huesos de esos dinosaurios. Pero entre tú y yo, también valdrá si traes huesos de ejemplares vivos.", progress = "Piensa lo que quieras de mí, pero no se darán cuenta de que los huesos que investigan son de stegodones y diemetradones antiguos o de los que casi nos devoraron cuando intentábamos establecer nuestros campamentos. Yo solo estaré contento si salgo de aquí con vida...", completion = "Excelente, estos deberían servirnos. Y ahora, si me lo permites...\n\nGRACIAS POR DESENTERRAR TODOS ESTOS HUESOS PARA NOSOTROS Y NO HACER NI UN SOLO RASGUÑO A LAS BESTIAS NATIVAS.\n\nSi lo hiciste o no, no lo sé ni me importa. Dejémoslo tal y como está, ¿de acuerdo? Toma, quizás con esto mantengas la boca cerrada... ¡Estoy dispuesto a cualquier cosa con tal de salir de aquí con vida, ya te lo dije!", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de cantera de arqueólogoCinturón de herramientas de excavador\n\nTambién recibirás:\n\nBotas de cantera de arqueólogoCinturón de herramientas de excavador"},
[3883] = {title = "Ecología alienígena", objective = "Utiliza el vial raspador para recoger una Muestra de pared de la colmena de uno de los criaderos Gorishi del Cráter de Un'Goro. Busca las cámaras con los huevos de larva colgantes.\n\nLleva la Muestra de pared de la colmena a Hol'anyee Marshal en el Cráter de Un'Goro.", description = "¡Solo porque hayamos sufrido un contratiempo no debemos detener nuestra investigación! Antes de acabar aquí estábamos estudiando los insectos Gorishi en la parte meridional del Cráter. Su presencia es invasora y extraña en el sistema ecológico de Un'Goro y queremos descubrir por qué están aquí.\n\nEn su red de colmenas debe haber criaderos de larvas. Podrás identificar esos lugares por los huevos de larva que cuelgan por todas partes. Utiliza este vial para recoger muestras de pared de la colmena cerca de cualquier criadero y trae esas muestras para que las estudiemos. ¡Pero ten mucho cuidado!", progress = "¿Tienes la muestra de la colmena Gorishi para que la estudie, <nombre>?", completion = "Sí, esto servirá. Has sido de gran ayuda... Ojalá pudiera darte algo más, pero estando aquí atrapado en medio de la nada no tengo muchas cosas que ofrecerte. Pero descansa tranquilo, ¡esta investigación no hubiera sido posible sin tu ayuda!", rewards = "Recibirás:\n\n"},
[3884] = {title = "Diario de Williden", objective = "Devuelve el diario a Williden Marshal en algún lugar del Cráter de Un'Goro.", description = "Has descubierto un diario destrozado. Algunas partes siguen siendo legibles y, al parecer, el contenido es obra de un tal Williden Marshal. Si el diario no miente, Williden estará en algún lugar al norte del Cráter. De acuerdo al diario, Williden y sus compañeros huyeron hacia las zonas más elevadas para refugiarse de las bestias salvajes del Cráter cuando sus campamentos base fueron invadidos.", progress = "¡Hola! ¡Mis compañeros y yo nos alegramos de ver formas de vida que no sean bestias salvajes hiperdesarrolladas! ¿Qué te trae al Cráter de Un'Goro?", completion = "¡Vaya, mira eso! ¡Mi diario! Pensaba que estaba perdido, como casi todo lo que trajimos a Un'Goro. ¡Muchísimas gracias por devolvérmelo!", rewards = "Recibirás:\n\n"},
[3901] = {title = "Sacudir esqueletos", objective = "Mata a 12 esqueletos armadóseos y cuando lo hayas hecho, vuelve a buscar al sacerdote de las Sombras Sarvis a Camposanto.", description = "<nombre>, has demostrado a los Renegados que tienes potencial... en circunstancias normales. Veamos qué tal funcionas bajo presión.\n\nLos esqueletos armadóseos, los esbirros más feroces del rey Exánime, son enemigos más duros que los zombis a los que te has enfrentado hasta ahora. Reduce su número y demuestra a los Renegados lo que vales. No te entretengas: cuando hayas acabado, vuelve a hablar conmigo.", progress = nil, completion = "He acabado contigo, &lt;nombre&gt;. Has demostrado ser merecedor de la libertad que te ha sido concedida. Muchos serán los que se alzarán contra ti por lo que eres ahora, pero debes saber que, intenten lo que intenten contra nosotros, somos libres y no volverán a encadenarnos.\n\nCoge esto y ponte en camino. Tienes mucho que lograr, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nBotas de lienzoTúnica de carroñeroLeotardos de nómada\n\nTambién recibirás:\n\nBotas de lienzoTúnica de carroñeroLeotardos de nómada"},
[3902] = {title = "Hurgar en Camposanto", objective = "Busca 6 piezas de mercancías extraviadas por Camposanto y los alrededores y llévaselas al guardia de la muerte Saltain.", description = "¡Eh, tú! Si buscas algo útil que hacer, ¡abre las orejas!\n\nNecesitamos reclutas fresquitos que vayan a Camposanto y busquen cualquier tipo de equipo que pueda sernos útil. Probablemente, estará en pilas de cajas. Dentro de poco se alzarán nuevos reclutas y, si no queremos que vayan por ahí desnudos, ¡más nos vale conseguirles algo que ponerse!\n\n¿Y bien? ¡Muévete, miserable saco de huesos! No habrá recompensa para los vagos.", progress = "¿Nos has conseguido algún objeto útil? No hay que avergonzarse de utilizar lo que otros han desechado. Nosotros no aceptamos limosnas. ¡Los Renegados nos las arreglamos solos!", completion = "Buen trabajo, &lt;nombre&gt;, sabía que no eras un inútil. Ten, acepta uno de los mejores objetos que he encontrado entre los lotes que hemos reunido hasta ahora.", rewards = "Podrás elegir una de estas recompensas:\n\nCinturón de lienzoBrazales de metal oxidadosManteo corto de murciumbrío\n\nTambién recibirás:\n\nCinturón de lienzoBrazales de metal oxidadosManteo corto de murciumbrío"},
[3903] = {title = "Milly Osworth", objective = "Habla con Milly Osworth.", description = "<nombre>, has demostrado que se puede confiar en ti y que no temes al trabajo duro.\n\nMi amiga Milly Osworth está en apuros. Tiene la carreta al otro lado de la abadía, cerca del establo. Seguro que le va bien que le eches una mano.", progress = nil, completion = "¿Que te envía el ayudante del alguacil Willem? Es valiente y voluntarioso, pero está en la Abadía de Villanorte...\n\n¿Tú podrías ayudarme?", rewards = "Recibirás:\n\n"},
[3904] = {title = "La cosecha de Milly", objective = "Lleva 8 cajas de uva recogida a Milly Osworth a la Abadía de Villanorte.", description = "¡Los Defias se instalaron en Viñedos de Villanorte mientras estaba vendimiando! Los denuncié a los guardias, que me dijeron que ya se encargarían, pero... ¡Temo por mis viñas! Si los ladrones no se comen la uva, los guardias la aplastarán cuando vengan a echarlos.\n\n¡Tienes que ayudarme! Ya tengo casi toda la uva recogida, pero dejé las cajas en los viñedos que hay al sureste.\n\n¡Tráemelas y salva mis uvas!", progress = "¿Traes la uva, <nombre>?", completion = "¡Gracias por traerme la uva! Espero que hayas dado una lección a unos cuantos Defias.\n\n¡Aunque tengamos pocas tropas, tenemos la suerte de contar con valientes como tú!", rewards = "Recibirás:\n\n"},
[3905] = {title = "Informe de la vendimia", objective = "Lleva el Informe de la vendimia al hermano Neals a la Abadía de Villanorte.", description = "Ahora que has salvado mis uvas, llévale el Informe de la vendimia al hermano Neals. Se encarga del almacén de abastos de Villanorte; se alegrará de saber que la vendimia ha sido buena.\n\nEstá en el campanario de la abadía; le gusta sentarse allí a probar el vino.", progress = "¡Te veo muy bien! ¡Siéntate y tómate algo!", completion = "Veamos...\n\nOh, has salvado la uva; me preocupé cuando me habló de esos villanos en los viñedos. Por suerte, nunca perdí la fe en la Luz.\n\nY ahora, gracias a tu valor, podremos hacer vino. Que la Luz te acompañe y te proteja, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCapa manchada de vinoCinturón con pasador\n\nTambién recibirás:\n\nCinturón con pasadorCapa manchada de vino"},
[3906] = {title = "La discordia de las llamas", objective = "Ve a la cantera de la Montaña Roca Negra y ejecuta al gran maestro Pyron. Vuelve junto a Truenozón cuando hayas completado este encargo.", description = "Los miembros del consejo han sentido una perturbación en los elementos. Los enanos Hierro Negro han conjurado a un espíritu maligno en nuestro mundo que amenaza con una catástrofe en nuestras tierras en forma de inundación permanente. Hay que investigar la discordancia de las llamas.\n\nVe hasta Montaña Roca Negra, atraviesa el Abismo de Llama y hasta la cantera. Allí encontrarás al Señor del Fuego: gran maestro Pyron.\n\nPon fin a la perturbación y vuelve aquí.", progress = nil, completion = "No puede... no debe ser. La destrucción de Pyron solo ha levantado un velo. En las profundidades de Roca Negra hay una fuente de corrupción mucho más insidiosa.|n", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3907] = {title = "La discordia de las llamas", objective = "Adéntrate en las Profundidades de Roca Negra y localiza a Lord Incendius. Mátalo y llévale toda la información que encuentres a Truenozón.", description = "Puedo oler que el aire que te rodea está viciado, <nombre>. Hay otro ente, oculto en las profundidades de Roca Negra, que lo controla.\n\nEl viento y la tierra gritan su nombre: Lord Incendius... pero alguien... algo... manda en este ente ser. Es un mero emisario.\n\n¡Encuéntralo y descubre dónde se oculta su maestro! Vuelve a mí cuando hayas obtenido esta información.", progress = "Las llamas pronto habrán consumido estas tierras. ¡Apúrate, <nombre>!|n", completion = "&lt;Corazón Atronador se aferra a la tablilla de Kurniya.&gt;\n\nRagnaros... aquí...\n\nLos ancianos tenían razón en temer la corrupción que emanaba de la Montaña Roca Negra. ¡Un general de los antiguos dioses! ¡EN NUESTRO MUNDO! Debemos revisar nuestras posiciones aquí en Kargath. Tenemos que decidir si quedarnos a luchar o escapar ante la posibilidad de un nuevo diluvio.\n\nTen mucho cuidado en cualquier nueva exploración en la Montaña Roca Negra, &lt;nombre&gt;. Un mal mucho mayor que cualquiera concebible en este mundo se encuentra en esas salvajes profundidades.|n", rewards = "Podrás elegir una de estas recompensas:\n\nManteo nacido del solGuantes OcasoBrazales de demonio de criptaGarra Stalwart\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBrazales de demonio de criptaManteo nacido del solGuantes OcasoGarra Stalwart"},
[3908] = {title = "Un secreto bien guardado", objective = "Lleva la Espada de Linken a Donova Cubilnevado en Cuna del Invierno.", description = "Verás, no tengo ni idea de cómo terminé aquí. Nadie lo sabe... ¡es un secreto para todos! Es como si no pudiera acordarme de nada...\n\nPero estos objetos que me has traído... Estoy recordando algo.\n\nMi espada. Creo tengo que reforzarla por alguna razón, pero no sé muy bien el motivo. Lo siento, no puedo decirte más, ¡pero es que no puedo recordarlo!\n\nEn cualquier caso, sí que sé adónde puedes llevarla: con Donova Cubilnevado de Cuna del Invierno. La encontrarás junto a las fuentes termales. Ella podrá templar la espada junto a las pozas.", progress = "Vaya, vaya... Así que Linken por fin ha encontrado algo de tiempo para mandarme su espada.", completion = "Sí, claro que puedo templar la espada. Mientras trabajo puedes ir explicándome por qué le ha costado tanto tiempo a Linken traérmela. No suele olvidarse de nada...", rewards = "Recibirás:\n\n"},
[3909] = {title = "El Elixir de Videre", objective = "Busca a Gregan Vomitabrebaje en el norte de Feralas. Con él, aprende cómo puedes obtener el Elixir de Videre. Cuando lo sepas, vuelve con Donova Cubilnevado en Cuna del Invierno.", description = "¿Y ahora qué pasa? Linken... ¿SE OLVIDÓ?\n\nOh, entiendo... Ha perdido la memoria...\n\nBueno, aquí está su espada. Pero no está terminada. Sé quién podría hacerlo, pero... hablar con él puede ser difícil.\n\nEscucha, sé de alguien que puede ayudarte. Es una larga historia y no puedo explicártela ahora, pero debes hacerte con un Elixir de Videre. Solo sé de una persona que pueda ayudarte a conseguirlo, Gregan Vomitabrebaje. No es fácil dar con él, pero lo último que supe es que estaba acampado en las remotas tierras de Feralas.", progress = "¿Ya estás de vuelta?", completion = "Necesitaré un vial para asegurarme de que este es de verdad el Elixir... Sí... Esto es.", rewards = "Recibirás:\n\n"},
[3911] = {title = "El último elemento", objective = "Viaja a las Profundidades de Roca Negra y recupera 10 esencias de los elementos. Tu primer impulso es buscar los gólems y a los creadores de gólems. Recuerdas que Vivian Lagrave también murmuró entre dientes algo sobre los elementales.|n", description = "Hay trabajo a mansalva para quien se aventure en las profundidades, <nombre>.\n\nLos Hierro Negro han perfeccionado la creación de gólems extremadamente poderosos.\n\nLos primeros informes de nuestros espías indican que los enanos usan una fuente de poder única para conferir a sus creaciones una potencia destructora sin igual.\n\n¡Tan solo imagínate lo que podríamos hacer con nuestras abominaciones si nos hiciéramos con esta esencia de los elementos! No dejes piedra por remover en esa ciudad si es necesario, ¡pero no vuelvas hasta haber encontrado la esencia! Recibirás un justo pago por el riesgo.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nLacre de Lagrave"},
[3912] = {title = "Encuentro en la tumba", objective = "Ve al cementerio a las afueras de Gadgetzan y utiliza el Elixir de Videre. Sigue las demás instrucciones de Donova para encontrar a Gaeriyan.\n\nObjeto provisto: \n", description = "Con la ayuda de este elixir podrás hablar con el que nunca se dejaría ver...\n\nLleva el Elixir de Videre y la Espada de Linken al cementerio que encontrarás a las puertas de Gadgetzan en Tanaris. Cuando estés allí, bebe la poción. Quizás te sorprendan los resultados, pero no te alarmes.\n\nEn tu \"otra\" forma dirígete hacia el Norte, hasta llegar a las montañas. Encontrarás a aquel con quien debes hablar en una de las grietas rocosas. Su nombre es Gaeriyan.\n\nPero recuerda, solo hablará contigo si adoptas esa forma...", progress = nil, completion = "Ha pasado mucho tiempo desde que vinieron a hablar conmigo por última vez...", rewards = "Recibirás:\n\n"},
[3913] = {title = "Una situación mortal", objective = "Resucita y busca la lápida de la que te habló Gaeriyan.", description = "Si vuelves al cementerio, <nombre>, encontrarás una lápida... No será una lápida cualquiera, destacará sobre todas las demás. Los secretos yacen en ella.\n\nEmpuja la lápida y aprenderás de esos secretos...", progress = "Esta debe ser la lápida que mencionó Gaeriyan...", completion = "Empujas la lápida con fuerza, y se desliza a un lado mostrando un pequeño hueco debajo de ella.\n\nCuando colocas la Espada de Linken en el hueco te das cuenta de que encaja a la perfección...", rewards = "Recibirás:\n\n"},
[3914] = {title = "La Espada de Linken", objective = "Entrega la Espada superior a Linken en el Cráter de Un'Goro.", description = "Tras un fuerte destello de luz puedes volver a coger la espada. ¡Se ha transformado!", progress = "¡Oh! ¡Te recuerdo!... Eres... ¡No, no me lo digas!", completion = "Mi espada... ¡está terminada!\n\nSi tan solo pudiera recordar para qué la necesitaba...\n\nToma, deja que al menos te pague algo por tu trabajo.", rewards = "Recibirás:\n\n"},
[3921] = {title = "Wenikee Tuercasmil", objective = "Llévale el churumbele roto a Wenikee Tuercasmil.", description = "Me parece que he averiado ese churumbele. Eso no es bueno porque ya les había hablado de él a unos compañeros de Villabajo y estaban muy intrigados. ¡Tenemos que encontrar la forma de arreglarlo!\n\nLlévale el churumbele averiado a Wenikee Tuercasmil, al sur de La Muralla de Mor'shan, la avanzada de la Horda entre Los Baldíos y Vallefresno. Es muy lista e inquisitiva. Si le enseñas el churumbele, apuesto a que no dejará escapar la oportunidad de repararlo.", progress = "Tengo muchísimo trabajo, <clase>. A menos que tengas algo para mí, me conviene seguir...", completion = "¿Qué es esto? ¿Un churumbele? ¿Qué es un churumbele?\n\nAh... pero fíjate cómo se mueve cuando lo manejas. Y se oyen engranajes en el interior. ¡Oh, por todos los...! Quiero arreglarlo y ver qué hace, ¿tú no?", rewards = "Recibirás:\n\n"},
[3922] = {title = "Engranajes", objective = "Llévale 15 engranajes a Wenikee Tuercasmil en Los Baldíos.", description = "Quiero arreglar este churumbele pero para eso voy a necesitar algunas cosas. Se me han acabado los engranajes del calibre diecisiete y medio y voy a necesitar un puñado para ese trabajo.\n\nEl lugar más cercano donde puedes conseguirlos es El Fangal, hacia el este. Ventura y Cía. suele tenerlos en sus cubos de herramientas. Ve hasta El Fangal y mira en los cubos de herramientas que encuentres, vuelve cuando hayas recolectado suficientes engranajes.", progress = "¿Tienes los engranajes, <nombre>? Le he echado un ojo a este churumbele y me muero por hacerle ajustes.", completion = "¡Genial, los tienes! Ahora puedo ponerme a trabajar…", rewards = "Recibirás:\n\n"},
[3923] = {title = "Rilli Gapegajoso", objective = "Llévale el churumbele roto y maltrecho a Rilli Gapegajoso en Orgrimmar.", description = "Bueno, eso no funcionó. Puse unos calibres adicionales en el churumbele para medir su integridad estructural y las lecturas dan mínimos históricos. Podría añadir algún dial más y quizá una palanca o dos para intentar repararlo pero… creo que solo empeoraría las cosas.\n\nDeberías llevarle el churumbele a Rilli Pegotegrasiento. Antes trabajaba para Ventura y Cía., si alguien sabe cómo arreglarlo, ese es Rilli.\n\nLo encontrarás en el Rincón de la Maquinaria de Nogg en Orgrimmar, en el Valle del Honor.", progress = "¡Cuidado con la cabeza! No quiero que se interponga en mi trabajo!", completion = "¿Qué es esto? ¡¿Qué?! ¡Es un churumbele! ¿Dónde lo has conseguido? ¿Has estado curioseando por las instalaciones de Ventura y Cía. en Los Baldíos?\n\nDebes de ser muy valiente, o muy estúpido.", rewards = "Recibirás:\n\n"},
[3924] = {title = "El manual de churumbele", objective = "Llévale el manual del churumbele a Rilli Gapegajoso en Orgrimmar.", description = "Creo que puedo arreglar el churumbele, pero primero voy a necesitar leer el manual. Cuando estaba en la Compañía Ventura y Cía. tenía un rival, el jefe Encobre, que atesoraba los secretos del churumbele, y comentaba algunos detalles solo a quienes tenía plena confianza. Yo no era de los de su confianza...\n\nEl jefe Encobre solía andar por la mina Pedrusco, en la parte noreste de Los Baldíos. Consigue el manual. Si ves que faltan páginas, seguro que se las ha dado a otros, así que deberás que conseguirlas como puedas.", progress = "¿Tienes el manual?", completion = "¡Tienes el manual! ¡Me muero por leerlo y averiguar cómo reparar el churumbele! Hablé con Petardol en Trinquete, quería ayudarte por tu ayuda y recompensarte por tu trabajo.\n\nTambién yo quiero darte las gracias, y si algún día consigo reparar este artilugio, te lo haré saber...", rewards = "Podrás elegir una de estas recompensas:\n\nLlave inglesa de TorkDestornillador de Churumbele\n\nTambién recibirás:\n\nDestornillador de ChurumbeleLlave inglesa de Tork"},
[3941] = {title = "La ayuda de una gnoma", objective = "Encuentra a J.D. Collie en la cueva del Refugio de Marshal.", description = "Piensa... Piensa... Sé que he venido aquí por algo importante...\n\nTengo una idea, <nombre>. En esa cueva de ahí, encontrarás a una gnomo llamada J.D. que fue muy amable conmigo cuando llegué aquí. Quizás en mi desorientación le dijera algo.\n\n¿Por qué no vas a preguntarle qué es lo que sabe? Ah, pero ten cuidado... siempre anda liada con esos cristales brillantes que he visto por el cráter. No estoy muy seguro de qué es lo que se trae entre manos.", progress = nil, completion = "Espera solo un momento... Estoy probando una nueva combinación de cristales...", rewards = "Recibirás:\n\n"},
[3942] = {title = "Recuerdos de Linken", objective = "Encuentra a Eridan Vientoazul en el sur de Frondavil.", description = "Oh... ¿Linken? Sí, es muy divertido. Cuando llegó aquí no paraba de mascullar sobre las cosas más extrañas. Seguramente se dio un golpe en la cabeza.\n\nPero dijo algo que se me quedó grabado... que estaba haciendo una especie de tótem y que tenía que hablar con alguien llamado Eridan Vientoazul... en un lugar conocido como Frondavil, me parece.\n\nPero no creo que pueda decirte mucho más. Si quieres más información deberías ir a buscar a Eridan tú <mismo/misma>.", progress = nil, completion = "Hola, &lt;clase&gt;. ¿Puedo ayudarte en algo?", rewards = "Recibirás:\n\n"},
[3961] = {title = "La aventura de Linken", objective = "Lleva el Tótem plateado de Aquementas a Linken, en el Refugio de Marshal.", description = "Y pensar que el motivo que trajo a Linken hasta aquí fue el librarnos de Correfuego... Parece un muchacho completamente normal, pero ya observé que no estaba muy interesado en mis experimentos e inventos, como lo estaría cualquier otro gnomo. Debe ser un auténtico guerrero.\n\nLlévale el tótem, quizás le ayude a recordar la razón por la que vino aquí.", progress = "¡Hola de nuevo, <nombre>!", completion = "¿Te ayudó J.D. en algo?", rewards = "Recibirás:\n\n"},
[3962] = {title = "Ir solo es peligroso", objective = "Viaja a Cresta del Penacho en Llamas, al sur del Refugio de Marshal.\n\nBusca a Correfuego y utiliza el Tótem para eliminar su aura protectora. Cuando le hayas derrotado, coge la Llama Dorada de la cueva.", description = "<nombre>, por fin recuerdo... Vine aquí para combatir a Correfuego. Estaba de viaje y este tenía que ser el último paso.\n\nPero he dejado de ser el héroe... ahora lo eres tú. Y por eso eres tú quien debe concluir todo este trabajo, no yo.\n\nCorrefuego cuida de un artefacto llamado la Llama Dorada. Otorga el mayor poder que haya visto nunca y es peligroso en manos de alguien con mal corazón.\n\nTendrás que llevar a otros contigo, es peligroso ir solo. Considéralo como mi consejo.", progress = "La protección del aura que creó sirviéndose de la Llama Dorada hace a Correfuego prácticamente invencible. Solo tú tienes el poder para eliminarla gracias al Tótem plateado de Aquamentas.", completion = "Gracias, &lt;nombre&gt;.\n\nLa Llama Dorada volverá por fin a mi hogar, allí la protegerán y nunca volverá a caer en manos del mal.\n\n¡Sin tu ayuda todavía seguiría sin saber quién soy! Como has hecho tanto por mí, me gustaría darte algo muy especial...\n\n¡Elige lo que quieras!", rewards = "Podrás elegir una de estas recompensas:\n\nEspada de maestría de LinkenEspíritu de Aquementas\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nBumerán de LinkenEspada de maestría de LinkenEspíritu de Aquementas"},
[3981] = {title = "Comandante Gor'shak", objective = "Encuentra al comandante Gor'shak en las Profundidades de Roca Negra.\n\nRecuerdas que en el dibujo burdo había rejas sobre el rostro del orco. Quizás deberías buscar una cárcel o algo similar.", description = "Pobre Gargal... no le dio tiempo ni a olerla. ¡BAM! La flecha se le clavó en el mismísimo centro de la cabeza. No es que fuera una lumbrera antes pero ahora...\n\n<Galamav esboza una mueca.>\n\nCuando le extrajimos la flecha, descubrimos una nota enrollada en el asta. En la nota había un dibujo burdo de un orco entre rejas con la firma del comandante Gor'shak adjunta.\n\nSi aún está vivo en las Profundidades, tendrás que encontrarlo.\n\n¿Una trampa? No estoy de broma, ¡pezuñas entumecidas! ¡Por eso vas!", progress = nil, completion = "No tenemos mucho tiempo. Escucha atentamente, &lt;raza&gt;. Lo que tengo que decirte es confidencial y solo para tus oídos.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[3982] = {title = "¿Qué pasa?", objective = "Defiende a Gor'shak.", description = "Me enviaron aquí bajo los auspicios de Thrall. ¿En serio crees que estos patanes podrían capturarme? Dejé que me apresaran para poder espiarlos.\n\n<Gor'shak habla ahora susurrando.>\n\nEl Emperador Thaurissan ha secuestrado a la Princesa de Forjaz. ¡La Princesa Barbabronce se encuentra aquí mismo en esta ciudad! Apuesto a que a Magni casi le da un infarto cuando lo supo.\n\n<Gor'shak suelta una carcajada.>\n\n¡Chitón! ¿Has oído eso? ¡Se acerca la guardia! ¡DEFIÉNDETE!", progress = nil, completion = "Eso ha estado muy cerca. Tu presencia los ha alertado... no tardaremos en ver los efectos.|n", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4001] = {title = "¿Qué pasa?", objective = "Habla con Kharan Martillazo y recaba información sobre el secuestro de la princesa Moira Barbabronce. Llévale esa información a Thrall en Orgrimmar.\n\nRecuerdas que Gor'shak mencionó que Kharan estaba preso en una celda cercana.", description = "Hasta ahora mi comunicación con Kharan, el enano que está en la celda al otro lado del vestíbulo, ha sido escasa. Sin embargo, he logrado convencerle de que yo, bueno, nosotros, no queremos hacerle ningún daño.\n\nVe a hablar con él y sácale toda la información que puedas sobre la Princesa y sus secuestradores. Después informa de todo ello al Jefe de Guerra.\n\nThrall no quiere que se hiera a la Princesa, por lo que ha elaborado un minucioso plan según el cual podrá volver sana y salva a Forjaz. Kharan ya está al tanto... ¡Vete!", progress = nil, completion = "Esta es una oportunidad para la Horda para cambiar para siempre el clima político de nuestro mundo desgarrado por la guerra. Escucha atentamente, &lt;nombre&gt;.\n\nEl fracaso en esta misión no es una opción.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4002] = {title = "Los Reinos del Este", objective = "Si aceptas la misión, habla con Thrall.", description = "<nombre>, la información que traes es excelente.\n\nMi deber como Jefe de Guerra es hacer lo que sea mejor para la Horda. Debo asegurar la libertad de las generaciones venideras.\n\nTenemos la oportunidad de formar nuestras futuras alianzas. Si ayudamos a los enanos, se estrecharían las relaciones con los Reinos del Este. ¡Por eso no puedes fallar!\n\n¿Estarás a la altura?", progress = nil, completion = "Este es el punto de no retorno, &lt;nombre&gt;.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4003] = {title = "El rescate real", objective = "Mata al emperador Dagran Thaurissan para liberar a la princesa Barbabronce del hechizo.", description = "Si sale bien, este golpe a los Hierro Negro sacudirá los cimientos de su reino.\n\nLa princesa Barbabronce está bajo el control del emperador Thaurissan; para liberarla, tendrás que matarlo. Ten en cuenta que tu misión es doblemente peligrosa, porque la princesa intentará atacarte mientras esté controlada por el Emperador.\n\n¡¡Asegúrate de que no sufra daños!!", progress = nil, completion = "¡¿Qué has hecho, criminal?!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4004] = {title = "¿Princesa salvada?", objective = "¡Vuelve junto a Thrall!", description = "¡Has matado a mi marido, <raza>! Mi hijo vendrá a este mundo sin un padre.\n\n<La Princesa Moira te señala directamente.>\n\nPuedes tener la certeza de que este niño será el próximo gobernante del Reino de Forjaz. Tú y todos los de tu especie sufriréis implacable persecución hasta el final de los tiempos por esta injusticia.\n\n<Crees que la Princesa Barbabronce aún puede estar sufriendo algún efecto del hechizo de Thaurissan.>\n\nY ahora déjame, <raza>. Estoy segura de que tu Jefe de Guerra estará impaciente por conocer los frutos de su intromisión.", progress = nil, completion = "&lt;Thrall se muestra muy afectado por la información que acabas de darle.&gt;\n\nUna nueva amenaza se cierne sobre el horizonte. Se acaban los años de Magni y con ello tendrá vía libre la estirpe no nata de Hierro Negro para gobernar Forjaz.\n\nHas cumplido lo que te había encargado y por ello serás recompensado.\n\n¡Márchate, &lt;nombre&gt;! Debo discutir algo con Sylvanas y Cairne.", rewards = "Podrás elegir una de estas recompensas:\n\nResolución de ThrallOjo de Orgrimmar\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nResolución de ThrallOjo de Orgrimmar"},
[4005] = {title = "Aquementas", objective = "Lleva el Libro de Aquor, las zarpas plateadas y el corazón de Troncoferro a Tanaris.\n\nUtiliza el Libro de Aquor para invocar Aquementas.\n\nCuando tengas el tótem completo llévaselo a J.D. Collie en el Refugio de Marshal.", description = "Lleva esta bolsa de suministros a Tanaris. Dirígete a la Cala del Aparejo Perdido y busca un círculo de piedras.\n\nEl secreto reside en la península más oriental...\n\nSolo cuando te encuentres en el círculo, lee el Libro de Aquor e invoca la única criatura que puede transformar los objetos que has reunido en un tótem que te ayudará a combatir a Correfuego.\n\nA los Aquementa no les gustará ser despertados, pero recuerda, debes permanecer en el círculo para poder transformar los objetos.", progress = "Así que has vuelto, ¿eh? Ummm... Déjame echar un vistazo a eso, <nombre>.", completion = "Correfuego, dices... \n\nSí, ese poderoso elemental vive en el tórrido centro del cráter. Procuramos mantenernos alejados, nadie ha podido atravesar la potente aura que le rodea. Muchos han salido volando montaña abajo al intentar acercarse a él.", rewards = "Recibirás:\n\n"},
[4021] = {title = "¡Contraataca!", objective = "Llévale una pieza del estandarte de Krom'zar a Regthar Portamorta, al oeste de El Cruce.", description = "La Horda elogia tu éxito frente a los Kolkar pero estos están enfurecidos. Según varios informes, un contingente Kolkar ha viajado hasta Los Baldíos desde sus dominios en Desolace en busca de venganza. Las últimas noticias eran que, ahora mismo, ¡se dirigen a estos bunkers desde el oeste!\n\nHaz frente a los invasores Kolkar y pon fin a esa amenaza en Los Baldíos. Mátalos hasta que aparezca su jefe, el Señor de la Guerra Krom'zar. Derrótalo y tráeme un pedazo de su estandarte como prueba.\n\nContamos contigo, <nombre>.", progress = "¿Tienes el estandarte?", completion = "¡Lo has logrado! ¡Has derrotado a Kolkar!\n\nMe aseguraré de que Thrall se entere de tus proezas en este lugar, &lt;nombre&gt;.\n\nVe con la cabeza bien alta. Eres motivo de orgullo no solo para ti sino también para la Horda", rewards = "Recibirás:\n\n"},
[4022] = {title = "Un sabor a llamarada", objective = "Enséñale a Cyrus Therepentio la escama de Vuelo Negro que recibiste de Kalaran Espada del Viento.", description = "El Vuelo tiene conocimiento de tu trabajo en La Garganta de Fuego.\n\nEnséñamela, <raza>. La escama, imbécil.", progress = "<Los ojos de Cyrus forman dos esferas encendidas.>", completion = "&lt;Cyrus examina la escama.&gt;\n\nHas demostrado que no eres completamente inútil, mortal.", rewards = "Recibirás:\n\n"},
[4023] = {title = "Un sabor a llamarada", objective = "Demuéstrale a Cyrus Therepentio lo que vales.\n\nTe da la impresión de que Cyrus ya sabe que no eres digno.", description = "¿Buscas al Vuelo? Apórtame una prueba de lo que vales, mortal.Te van a lanzar el siguiente hechizo:\n\n", progress = nil, completion = nil, rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4024] = {title = "Un sabor a llamarada", objective = "Viaja hasta las Profundidades de Roca Negra y mata a Bael'Gar.\n\nSolo sabes que el gigante vive en las Profundidades de Roca Negra. Acuérdate de usar la escama alterada de Vuelo Negro sobre los restos de Bael'Gar para capturar la esencia fiera.\n\nLlévale la esencia fiera recubierta a Cyrus Therepentio.", description = "Deseo probar las ardientes llamas de la montaña de fuego. Dejar que el calor del magma de Roca Negra recorra todo mi ser. Esa esencia, ese calor abrasador, es lo que ansío.\n\nBael'Gar mora en las Profundidades de Roca Negra: un ser que es llama pura, nacido de los fuegos de Ragnaros. Ejecuta a Slay Bael'Gar y tráeme un poco de su fuego interior de entre sus ardientes restos.\n\nUsa la escama de sus restos para extraer sin peligro la llama interior.\n\nConsíguelo y recibirás tu merecida recompensa.", progress = "¿Ha sucumbido el gigante?", completion = "&lt;Cyrus te arrebata la esencia fiera de las manos.&gt;\n\n¡SÍ!\n\nToma tu recompensa y vete. ¡Desaparece inmediatamente de mi vista!", rewards = "Podrás elegir una de estas recompensas:\n\nManteo de esquistoBufas de pellejo de vermisFajín valconiano\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nManteo de esquistoBufas de pellejo de vermisFajín valconiano"},
[4041] = {title = "El Elixir de Videre", objective = "Tráeme una raíz del mal y te haré ese Elixir Videre.\n\nSé mucho sobre hierbas y alquimia... ¡Podría contarte una de historias! Por ejemplo, un día transformé el queso que iba a desayunar en oro... ¡Eh! ¿Adónde vas?", description = nil, progress = "Tráeme una raíz del mal y te haré ese Elixir Videre.\n\nSé mucho sobre hierbas y alquimia... ¡Podría contarte una de historias! Por ejemplo, un día transformé el queso que iba a desayunar en oro... ¡Eh! ¿Adónde vas?", completion = "Así que se la has jugado al viejo Miblon, ¿eh?", rewards = "Recibirás:\n\nElixir de Videre"},
[4061] = {title = "La revuelta de las máquinas", objective = "Adéntrate en Las Estepas Ardientes y recupera 10 fragmentos de elemental agrietado para Hierofante Theodora Mulvadania.\n\nRecuerdas que Theodora mencionó que los golems y elementales de esa región eran la fuente de estos fragmentos.", description = "Las máquinas se hicieron notar solas en nuestras primeras incursiones en La Garganta de Fuego. Después de investigar el asunto, descubrimos que las estaban importando de Las Estepas Ardientes, posiblemente desde las Profundidades de Roca Negra. Por eso te necesitamos, <nombre>.\n\nTu tarea consistirá en aventurarte en Las Estepas Ardientes y recuperar los fragmentos de elementales de los autómatas de piedra y de otras creaciones de piedra menores de la región. Cuando hayas reunido la cantidad suficiente, vuelve aquí y determinaré la fuente de procedencia.", progress = "¡No te tomes esto a la ligera, <nombre>!", completion = "&lt;Theodora dispone los fragmentos frente a ella.&gt;\n\n&lt;Theodora examina detenidamente los fragmentos con un enorme monóculo.&gt;\n\nInteresante. En la fundación estructural de cada fragmento hay grabadas... iniciales.|n", rewards = "Recibirás:\n\n"},
[4062] = {title = "La revuelta de las máquinas", objective = "Lleva el ejemplar de fragmento de Elemental a Lotwil Veriatus.\n\nRecuerdas que Theodora dijo que Lotwil se encontraba en un campamento hacia el este.", description = "“G.L.A.\n\nEsas son las iniciales impresas en los fragmentos de elementales. Debajo de las iniciales hay algún tipo de rango. Las máquinas que destruiste en Las Estepas Ardientes eran del “”Rango IV””. Supongo que es un rango más bien bajo.\n\nLleva esta muestra de fragmento a Lotwil Veriatus en el campamento del este. Es un erudito de cierto renombre en ciencias elementales. Es posible que sea capaz de ayudarnos en esta investigación.\"", progress = "¿Qué tienes ahí, <raza>?", completion = "&lt;Lotwil palidece.&gt;\n\nHe... he visto antes estas marcas.", rewards = "Recibirás:\n\n"},
[4063] = {title = "La revuelta de las máquinas", objective = "Encuentra al Señor Gólem Argelmach y mátalo. Llévale su cabeza a Lotwil. Asimismo tendrás que reunir 10 núcleos de elemental intactos de los golems Furiatracadores y de los constructos belisario que protegen a Argelmach. Lo sabes porque eres un adivino.", description = "¡No puede ser Argelmach! Mataron a Argelmach hace diez años. ¿Que cómo puedo estar seguro? Yo mismo lo maté.\n\nEra un enano despreciable, empeñado en pervertir el trabajo de mi vida para satisfacer sus diabólicas necesidades. Parece que los malvados siempre consiguen escurrirse de la justicia.\n\nSi este es el trabajo de Argelmach, debe ser destruido. Necesitaré muestras de sus creaciones más recientes. Con las muestras adecuadas, puede que consiga detener esta revuelta. Las creaciones protegen a Argelmach en las Profundidades de Roca Negra.", progress = "¿Era él? ¿De verdad estaba vivo?", completion = "¡¿Qué es esto?! Esta cabeza no es de carne. Esto es algún tipo de creación de hierro negro: una sombra de Argelmach, ¡OTRA máquina! Sospecho que tu destrucción de Argelmach no durará ya que otro surgirá para ocupar su lugar pronto.", rewards = "Podrás elegir una de estas recompensas:\n\nHombre de luna azurMantón de lanzalluviasArmadura de escamas de basaltoGuanteletes de placas de lava\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nArmadura de escamas de basaltoHombre de luna azurMantón de lanzalluviasGuanteletes de placas de lava"},
[4081] = {title = "MATAR INMEDIATAMENTE: enanos Hierro Negro", objective = "Adéntrate en las Profundidades de Roca Negra ¡y destruye a esos viles agresores!\n\nEl señor de la guerra Dientegore quiere que mates a 15 custodios de Yunque Colérico, 10 alcaides de Yunque Colérico y 5 lacayos de Yunque Colérico. Vuelve junto a él cuando hayas acabado la tarea.", description = "Por orden del señor de la guerra Dientegore, comandante de la Fuerza Expedicionaria de Kargath:\n\nTodos y cada uno de los efectivos del general Forjafuria en las Profundidades de Roca Negra debe ser aniquilado.\n\nLos hombres de Forjafuria son crueles asesinos, responsables del asesinato masivo de la 109 división de la Fuerza Expedicionaria de Kargath. Se recomienda a los héroes extremar la precaución al enfrentarse a estos animales.\n\nCualquier persona que destruya la primera línea del general Forjafuria en las Profundidades de Roca Negra recibirá un tributo.", progress = nil, completion = "Este primer ataque ha debido de provocarle alguna cana a los Forjainquina. Ahora, andando, soldado. Tengo trabajo que hacer, batallas que planificar, ¡enemigos a los que aplastar! \n\nSigue pendiente de los carteles de \"Se busca\" si quieres más trabajo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4082] = {title = "MATAR INMEDIATAMENTE: oficiales Hierro Negro de alto rango", objective = "Adéntrate en las Profundidades de Roca Negra ¡y destruye a esos viles agresores!\n\nEl señor de la guerra Dientegore quiere que mates a 10 médicos de Yunque Colérico, 10 soldados de Yunque Colérico y 10 oficiales de Yunque Colérico. Vuelve junto a él cuando hayas acabado la tarea.", description = "Por orden del señor de la guerra Dientegore, comandante de la Fuerza Expedicionaria de Kargath:\n\nEl jefe de la división de reconocimiento de la F.E.K., maestro mayor Lexlort, ha vuelto con noticias sobre el paradero de oficiales Hierro Negro de alto rango. A puerta cerrada, cerca del corazón de la ciudad, entrenan a sus tropas para acabar con nuestros hombres. Ve a las Profundidades de Roca Negra ¡y destrúyelos!\n\nEl éxito en esta misión se traducirá en un tributo.", progress = nil, completion = "Forjainquina está sin lugar a dudas... furioso. Vuelve aquí cuando recibas un tercer tributo y procederemos con Operación: muerte a Forjainquina. No puedo entrar en detalles pero ten por seguro que alguien muere.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4083] = {title = "El cáliz espectral", objective = "El cáliz espectral flota en el aire, ascendiendo y descendiendo lentamente… como el latido de un corazón moribundo.", description = nil, progress = "El cáliz espectral flota en el aire, ascendiendo y descendiendo lentamente… como el latido de un corazón moribundo.", completion = "Las gemas caen silenciosas en lo más hondo del cáliz...", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[4084] = {title = "Corazón de plata", objective = "Recoge 11 zarpas plateadas y 1 corazón de Troncoferro para Eridan Vientoazul de Frondavil.", description = "No sé de quién hablas, <nombre>.\n\nPero no te preocupes, si tu amigo quería encontrarme, sé qué estaba buscando.\n\nEn el corazón del Cráter de Un'Goro habita el mal en su forma más pura. Adopta la forma de un elemental llamado Correfuego... protegido por un aura que nadie puede disipar...\n\nNadie...excepto los que tienen suficiente poder para hacerlo. Puedo enseñarte ese poder, <nombre>.\n\nPara empezar, haz lo que te pido. Reúne 11 zarpas plateadas y 1 corazón de Troncoferro, los obtendrás de las criaturas de Frondavil. Cuando tengas todo, vuelve a verme.", progress = "Puedo decirte más cosas para que logres derrotar a Correfuego, pero primero tienes que recolectar los objetos que te pedí.", completion = "Muy bien, &lt;nombre&gt;. Parece que te tomas muy en serio el ayudar a tu amigo.", rewards = "Recibirás:\n\n"},
[4101] = {title = "Limpiar Frondavil", objective = "Lleva 15 unidades de sangre ámbar a Arathandris Cielargente a Frondavil.", description = "Las plantas dan vida a Frondavil pero incluso ellas sufren la mácula de la corrupción. Conozco formas de limpiarlas, pero no puedo confiar tal conocimiento a alguien de quien no me fíe. Demuéstrame que realmente deseas apoyar la causa del Círculo Cenarion.\n\nAl norte están las Cavernas de Troncoferro. Los elementales alabeos que viven en ellas ya no pueden ser salvados. Te pido que me traigas su savia. La llaman sangre ámbar. Quiero estudiarla para ver si puedo salvar a otras formas de vida de aquí.", progress = "Sé que Frondavil sufre, <nombre>. ¡Y yo sufro con él! He de ser firme: si el conocimiento que poseo cayera en manos equivocadas, la desgracia caería sobre Frondavil.\n\nTráeme la sangre ámbar de los elementales alabeos muertos y te ganarás mi confianza; te dejaré que me ayudes a combatir la corrupción.", completion = "Esta sangre ámbar se utilizará para encontrar un medio de acabar con la corrupción de Frondavil. Me has demostrado que estás deseando hacer cualquier cosa por ayudar al Círculo Cenarion. Ahora confío en ti.\n\nPuede parecer extraño que te ganaras mi confianza matando a los elementales, pero cualquier medio está justificado si nos ayuda a lograr un fin mayor: salvar a innumerables seres sensibles. Ahora podemos centrarnos en preservar la vida con los bálsamos que preparo.\n\n&lt;nombre&gt;, ha llegado el momento de limpiar Frondavil.", rewards = "Recibirás:\n\n"},
[4102] = {title = "Limpiar Frondavil", objective = "Lleva 15 unidades de sangre ámbar a Maybess Brisafluvial a Frondavil.", description = "Las plantas dan vida a Frondavil pero incluso ellas sufren la mácula de la corrupción. Conozco formas de limpiarlas, pero no puedo confiar tal conocimiento a alguien de quien no me fíe. Demuéstrame que realmente deseas apoyar la causa del Círculo Cenarion.\n\nAl norte están las Cavernas de Troncoferro. Los elementales alabeos que viven en ellas ya no pueden ser salvados. Te pido que me traigas su savia. La llaman sangre ámbar. Quiero estudiarla para ver si puedo salvar a otras formas de vida de aquí.", progress = "Sé que Frondavil sufre, <nombre>. ¡Y yo sufro con él! He de ser firme, pues si el conocimiento que poseo cayera en manos equivocadas, la desgracia caería sobre Frondavil.\n\nTráeme el ámbar de sangre de los elementales alabeos muertos y te ganarás mi confianza; te dejaré que me ayudes a combatir la corrupción.", completion = "Este ámbar de sangre se utilizará para encontrar un medio de acabar con la corrupción de Frondavil. Me has demostrado que estás deseando hacer cualquier cosa por ayudar al Círculo Cenarion. Ahora confío en ti. \n\nPuede parecer extraño que te ganaras mi confianza matando a los elementales, pero cualquier medio está justificado si nos ayuda a lograr un fin mayor: salvar a innumerables seres sensibles. Ahora podemos centrarnos en preservar la vida con los bálsamos que preparo. \n\n&lt;nombre&gt;, ha llegado el momento de limpiar Frondavil.", rewards = "Recibirás:\n\n"},
[4103] = {title = "Bálsamo por caza", objective = "Una antorcha Cenarion te permite ver un fragmento de alma corrupto en esas bestias corruptas que deben ser sacrificadas por un bien mayor. Yo pulverizo los fragmentos y los convierto en un componente con el que se crea el bálsamo de planta de Cenarion. Y es lo que utilizaremos para sanar a las plantas corruptas.\n\nA cambio de esos fragmentos, te daré algunos bálsamos de planta de Cenarion que ya tengo preparados.", description = nil, progress = "Una antorcha Cenarion te permite ver un fragmento de alma corrupto en esas bestias corruptas que deben ser sacrificadas por un bien mayor. Yo pulverizo los fragmentos y los convierto en un componente con el que se crea el bálsamo de planta de Cenarion. Y es lo que utilizaremos para sanar a las plantas corruptas.\n\nA cambio de esos fragmentos, te daré algunos bálsamos de planta de Cenarion que ya tengo preparados.", completion = "Muy bien. Aquí están tus bálsamos de planta de Cenarion. ¡Explora Frondavil y busca plantas corruptas, &lt;nombre&gt;! Estén donde estén, utiliza el bálsamo que te he dado para purificarlas.", rewards = "Recibirás:\n\nUngüento de planta Cenarion"},
[4104] = {title = "Bálsamo por minería", objective = "Si llevas una antorcha Cenarion mientras trabajas en los yacimientos de Frondavil, podrás ver el vitriolo contaminado que corrompe una veta del metal. Yo sé cómo purificar ese vitriolo y cómo convertirlo en un componente del bálsamo de planta de Cenarion. Un bálsamo que podrás utilizar para salvar las plantas que encuentres en el bosque.\n\nSi me traes algo de vitriolo contaminado, te daré un bálsamo de planta de Cenarion que tengo preparado.", description = nil, progress = "Si llevas una antorcha Cenarion mientras trabajas en los yacimientos de Frondavil, podrás ver el vitriolo contaminado que corrompe una veta del metal. Yo sé cómo purificar ese vitriolo y cómo convertirlo en un componente del bálsamo de planta de Cenarion. Un bálsamo que podrás utilizar para salvar las plantas que encuentres en el bosque.\n\nSi me traes algo de vitriolo contaminado, te daré un bálsamo de planta de Cenarion que tengo preparado.", completion = "Muy bien. Aquí tienes algunos bálsamos de planta de Cenarion. Si encuentras más vitriolo cuando extraigas minerales en Frondavil, ¡acuérdate de reservármelo! Cuanto más encuentres, más bálsamo podré preparar.", rewards = "Recibirás:\n\nUngüento de planta Cenarion"},
[4105] = {title = "Bálsamo por recolección", objective = "Una antorcha Cenarion permite a un herborista experimentado detectar cualquier agente máculo insidioso, un criminal producto de la corrupción de Frondavil. Trabajando aquí he aprendido a purificar el agente máculo y convertirlo en un agente purificador... uno que se usa en lo que ahora llamamos bálsamo de planta de Cenarion.\n\nSi llevas la antorcha contigo mientras recoges hierbas en Frondavil, deberías poder ver el agente máculo. A cambio de lo que me traigas, te daré un poco de bálsamo de planta de Cenarion que tengo ya preparado.", description = nil, progress = "Una antorcha Cenarion permite a un herborista experimentado detectar cualquier agente máculo insidioso, un criminal producto de la corrupción de Frondavil. Trabajando aquí he aprendido a purificar el agente máculo y convertirlo en un agente purificador... uno que se usa en lo que ahora llamamos bálsamo de planta de Cenarion.\n\nSi llevas la antorcha contigo mientras recoges hierbas en Frondavil, deberías poder ver el agente máculo. A cambio de lo que me traigas, te daré un poco de bálsamo de planta de Cenarion que tengo ya preparado.", completion = "Un trabajo excelente, &lt;nombre&gt;. Cada gotita, una vez purificada, nos ayudará a recuperar Frondavil. Ten, llévate algunas dosis de bálsamo de planta de Cenarion y adéntrate en el bosque. Y date prisa: ¡debemos sanar la tierra cuanto antes, &lt;raza&gt;!", rewards = "Recibirás:\n\nUngüento de planta Cenarion"},
[4106] = {title = "Bálsamo por desuello", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nUngüento de planta Cenarion"},
[4107] = {title = "Bálsamo por desencantamiento", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nUngüento de planta Cenarion"},
