[4466] = {title = "Flor del viento corrupta", objective = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una flor del viento. Los frutos que cuelgan de la planta están podridos y envenenados. Necesita desesperadamente ser atendida para recuperar su estado normal.", description = nil, progress = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una flor del viento. Los frutos que cuelgan de la planta están podridos y envenenados. Necesita desesperadamente ser atendida para recuperar su estado normal.", completion = "Aplicas dos bálsamos de planta de Cenarion a la flor del viento. Inmediatamente empieza a mudar su forma corrupta y se transforma en una planta rebosante de salud.", rewards = nil},
[4467] = {title = "Flor del viento corrupta", objective = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una flor del viento. Los frutos que cuelgan de la planta están podridos y envenenados. Necesita desesperadamente ser atendida para recuperar su estado normal.", description = nil, progress = "Has encontrado una versión enfermiza y corrupta de lo que parece ser una flor del viento. Los frutos que cuelgan de la planta están podridos y envenenados. Necesita desesperadamente ser atendida para recuperar su estado normal.", completion = "Aplicas dos bálsamos de planta de Cenarion a la flor del viento. Inmediatamente empieza a mudar su forma corrupta y se transforma en una planta rebosante de salud.", rewards = nil},
