[6711] = {title = "TXT> UBRS, STRATH, SCHOL - Repeatable", objective = "Cuando completes esta misión ganarás:\n\n", description = nil, progress = nil, completion = nil, rewards = nil},
[6721] = {title = "El camino del cazador", objective = "Habla con Dazalar en Dolanaar.", description = "Hola, joven <cazador/cazadora>. Tienes cara de ser capaz de domar a una bestia.\n\nVe a ver a Dazalar a Dolanaar. Él puede enseñarte a conseguir tu propia mascota.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[6722] = {title = "El camino del cazador", objective = "Habla con Dazalar en Dolanaar.", description = "Hola, joven <cazador/cazadora>. Tienes cara de ser capaz de domar a una bestia.\n\nVe a ver a Dazalar a Dolanaar. Él puede enseñarte a conseguir tu propia mascota.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[6741] = {title = "¡Más botines!", objective = "¡Necesito más restos de armadura, <nombre>! ¿Tienes alguno?", description = nil, progress = "¡Necesito más restos de armadura, <nombre>! ¿Tienes alguno?", completion = "Gracias otra vez, &lt;nombre&gt;. Eres motivo de orgullo para la Horda.", rewards = "Te van a lanzar el siguiente hechizo:\n\nArmadura lustrosa"},
[6761] = {title = "La nueva frontera", objective = "Ve a Darnassus, al Enclave Cenarion y habla con Mathrengyl Garroso.", description = "He delegado en mi segundo, Mathrengyl Garroso, todo lo relacionado con la llamada de socorro de Claro de la Luna. Habla con él de este asunto. NO conmigo.\n\nY deja que te diga una cosa: fui YO quien nos sacó de la oscuridad cuando no quedaban esperanzas para nuestra raza. NO el Círculo Cenarion de Claro de la Luna. ¡Teldrassil nos da a todos una nueva vida! Intentan hacernos creer lo que quieren, ¡pero solo los tontos se creerían esa propaganda!\n\nTú decides si estás en el grupo de los tontos o no.", progress = nil, completion = "Sí, &lt;clase&gt;, gracias por venir. Siento el malentendido... el aire está saturado de la desconfianza entre el archidruida y el guardián Rémulos de Claro de la Luna. Cada uno acusa al otro de maquinar algo y el hecho de que las extrañas corrupciones de Teldrassil no remitan no contribuye precisamente a relajar este ambiente de desconfianza. \n\nEn cualquier caso, seguimos teniendo un deber para con el Círculo Cenarion, aunque aquellos que lo lideran no estén de acuerdo entre sí.", rewards = "Recibirás:\n\n"},
[6762] = {title = "Rabine Saturna", objective = "Ve a Claro de la Luna, al poblado Amparo de la Noche y habla con Rabine Saturna. Claro de la Luna se encuentra entre Frondavil y Cuna del Invierno; para llegar, tienes que seguir el camino que sale del Bastión de los Fauces de Madera.", description = "El mayordomo del guardián Rémulos, Rabine Saturna, está en Claro de la Luna buscando ayuda para explorar los vastos páramos al oeste de Tanaris e incluso más al oeste de Un'Goro. Podrás encontrarlo en Amparo de la Noche, la aldea más grande de Claro de la Luna.\n\nAunque tu trabajo será sin duda un beneficio tremendo para todos, te aconsejo que vayas con cuidado. Entre Darnassus y Claro de la Luna hay ciertas tensiones que... podrían crecer a lo largo del tiempo.", progress = nil, completion = "Soy Rabine Saturna. Te doy la bienvenida de parte del guardián Rémulos y de los demás habitantes de este sagrado Claro.\n\nAprecio tu interés y tu deseo de asistir al Círculo Cenarion. Sé también que el archidruida de Darnassus te expresó su desprecio por prestarnos ayuda. Puedes estar seguro de que esto es por un bien mayor y de que estarás a salvo de cualquier represalia malvada de la que puedan hacerte objeto.\n\nAquí estás entre amigos, &lt;nombre&gt;.", rewards = "Recibirás:\n\n"},
[6781] = {title = "Más restos de armadura", objective = "<nombre>, ¡necesitamos más restos de armadura para los suministros de nuestros soldados!", description = nil, progress = "<nombre>, ¡necesitamos más restos de armadura para los suministros de nuestros soldados!", completion = "Estas piezas son muy buenas, &lt;nombre&gt;. Puedo usarlas para confeccionar nuevos equipos para nuestros soldados.\n\nSi mi nivel de suministro llega a ser suficientemente alto, ¡a lo mejor nuestras avezadas unidades de combate irán al campo de batalla!", rewards = "Te van a lanzar el siguiente hechizo:\n\nArmadura lustrosa"},
[6801] = {title = "Lokholar, el Señor del Hielo", objective = "Vengo de Cima del Trueno, <raza>. El propio Cairne ha decidido que ayude al clan Lobo Gélido en estos amargos tiempos.\n\nPero no perdamos más el tiempo. Debes atacar a nuestros enemigos y traerme su sangre. Cuando tenga suficiente sangre, podrá empezar el ritual del conjuro.\n\nLa victoria estará garantizada cuando el Señor Elemental quede liberado sobre el ejército de los Pico Tormenta.", description = nil, progress = "Vengo de Cima del Trueno, <raza>. El propio Cairne ha decidido que ayude al clan Lobo Gélido en estos amargos tiempos.\n\nPero no perdamos más el tiempo. Debes atacar a nuestros enemigos y traerme su sangre. Cuando tenga suficiente sangre, podrá empezar el ritual del conjuro.\n\nLa victoria estará garantizada cuando el Señor Elemental quede liberado sobre el ejército de los Pico Tormenta.", completion = "Se ha derramado sangre enemiga. Mezclaré esta sangre con el resto de elementos que hemos recolectado. Cuando la ofrenda esté completa, el Señor del Hielo enseñará a esos perros Pico Tormenta ¡toda la ira del clan Lobo Gélido!", rewards = nil},
[6804] = {title = "Agua envenenada", objective = "Aplica el aspecto de Neptulon a los elementales infectados de las Tierras de la Peste del Este. Lleva 12 de sus brazales de vínculo y el aspecto de Neptulon al duque Hydraxis a Azshara.", description = "Una fuerza de rastreadores elementales enviada a las Tierras de la Peste del Este de Lordaeron fue infectada por la corrupción del lugar. ¡Los Señores del Agua no podemos tolerar este insulto a nuestra pureza!\n\nToma este frasco. Contiene un aspecto de Neptulon. Eliminará el veneno de los elementales infectados. Pero el conflicto interno les conducirá a una enajenación transitoria.\n\nDomina a los descontrolados y tráeme sus brazales de vínculo.", progress = "¿Tienes los brazales?", completion = "Buen trabajo, &lt;clase&gt;. Esos elementales eran una ofensa a todos los seres del agua. Nuestra fuerza reside en la pureza y la necesitamos para ganar esta guerra.", rewards = "Recibirás:\n\n"},
[6805] = {title = "Sirocosos y reptarenas", objective = "Mata a 15 sirocosos y 15 reptarenas y ve a hablar con el duque Hydraxis en Azshara.", description = "Nosotros, los elementales del agua, estamos en guerra con los seres de los elementos menores. ¡Demuestra que estás de nuestra parte!\n\nMuy lejos hacia el sur, en el desierto de Silithus, viven los siervos del aire y la tierra, en un pedazo de roca llamado La Vega de Cristal. Protegen objetos de poder frente a seres inferiores, mortales, y aunque ignoro por qué lo hacen, deseo contrariarlos.\n\nVe a La Vega de Cristal, en Silithus, y derrota a nuestros enemigos. Después vuelve a verme.", progress = nil, completion = "Has demostrado tu valía. Puede que tenga más tareas para ti, dignas de un aliado de los Señores del Agua.", rewards = "Recibirás:\n\n"},
[6821] = {title = "Ojo de brasadivino", objective = "Lleva el ojo del Piroguardián brasadivino al duque Hydraxis a Azshara.", description = "Has demostrado que puedes derrotar a nuestros enemigos y encargarte de misiones para la causa de los Señores del Agua. Pero hasta ahora solo te has enfrentado a enemigos menores... ¿Podrás vencer a un enemigo más poderoso?\n\nEsa es tu nueva misión.\n\nLos orcos han apresado a un siervo del fuego, el Piroguardián brasadivino, en Cumbre de Roca Negra. Encuentra dónde lo tienen cautivo, mata a sus captores y luego al piroguardián. Tráeme su ojo; me mostrará tu valor.", progress = "¿Traes el ojo? ¿Osaste entrar en Cumbre de Roca Negra?", completion = "Has demostrado que tienes fuerza y astucia.\n\nCreo que ganarás muchos honores en nuestras filas...", rewards = "Recibirás:\n\n"},
[6822] = {title = "El Núcleo de Magma", objective = "Mata a 1 Señor del Fuego, 1 gigante fundido, 1 canino del Núcleo y 1 marea de lava y ve a ver al duque Hydraxis a Azshara.", description = "<nombre>, ha llegado el momento de ponerte a prueba de verdad. ¿Te atreves?\n\nHay un pasaje al Núcleo de Magma en lo más hondo de las Profundidades de Roca Negra. Es un bastión del enemigo, defendido por los siervos del fuego más fuertes de este mundo.\n\n<nombre>, ve al Núcleo de Magma y demuéstrame que tienes fuerza y determinación para vencer a lo que te encuentres allí.\n\nSi tienes algún aliado, llévalo contigo. Para salir con vida del Núcleo de Magma necesitarás todo un ejército.", progress = nil, completion = "Has hecho lo imposible: ¡enfrentarte a los seres del Núcleo de Magma y vivir para contarlo!\n\nTienes muchos recursos pese a tu pequeño tamaño.\n\nEspero que algún día se forme una alianza entre tu raza y los Señores del Agua.", rewards = "Recibirás:\n\n"},
[6823] = {title = "Agente de Hydraxis", objective = "Consigue una mención de honor de los Señores del Agua y habla con el duque Hydraxis en Azshara.", description = "Si deseas aliarte con nosotros, lucha contra nuestros enemigos, los siervos del aire, la tierra y el fuego. Cada derrota que les inflijas les debilitará y probará que eres fiel a nuestra causa.\n\nLa campaña será larga, pero si perseveras recibirás grandes honores y gloria.", progress = nil, completion = "Se habla de tus hazañas en todos los salones de los Señores del Agua, &lt;nombre&gt;. Había quien pensaba que tu raza es demasiado pequeña y frágil, pero has conseguido lo imposible: te has ganado nuestro respeto.", rewards = "Recibirás:\n\n"},
[6824] = {title = "Las manos de los enemigos", objective = "Lleva las manos de Lucifron, Sulfuron, Gehennas y Shazzrah al duque Hydraxis a Azshara.", description = "<nombre>, te tenemos en alta estima. Por eso nos resistimos a enviarte a esta última misión; no queremos perder a alguien tan útil como tú.\n\nPero si tienes éxito, la ayuda a nuestro esfuerzo de guerra sería tan grande que merece la pena arriesgar tu vida.\n\nDebes ir al Núcleo de Magma y asesinar a estos capitanes: Lucifron, Sulfuron, Gehennas y Shazzrah. ¡Mátalos y tráeme sus manos!\n\nEsta misión exigirá toda tu capacidad, <nombre>, pero si lo consigues asestarás un terrible golpe a los Señores del Fuego.", progress = "¿Has derrotado a Lucifron, <nombre>? ¿Me traes las manos del enemigo?", completion = "¡Has eliminado a los capitanes! Si no fuera porque sé de lo que eres capaz, no me lo creería.\n\n&lt;nombre&gt;, has demostrado antes tu valor, pero esta hazaña será cantada en los salones de los Señores del Agua. Te vas a convertir en una leyenda.\n\nVuelve a hablar conmigo; puede que tenga otra misión.", rewards = "Recibirás:\n\n"},
[6825] = {title = "La llamada del aire: flota de Guse", objective = "Tenemos que preparar la nueva flota de alas de guerra, <raza>! Mis jinetes están listos para un ataque sobre el campo de batalla central pero primero hay que despertar su apetito… prepararlos para el asalto.\n\nNecesito carne de soldados Pico Tormenta, ¡como para alimentar a una flota! ¡Cientos de kilos! Seguro que puedes encargarte de eso, ¿verdad? ¡Andando!", description = nil, progress = "Tenemos que preparar la nueva flota de alas de guerra, <raza>! Mis jinetes están listos para un ataque sobre el campo de batalla central pero primero hay que despertar su apetito… prepararlos para el asalto.\n\nNecesito carne de soldados Pico Tormenta, ¡como para alimentar a una flota! ¡Cientos de kilos! Seguro que puedes encargarte de eso, ¿verdad? ¡Andando!", completion = "La flota se alimenta de su carne. Consume al enemigo.\n\n¡El vuelo es inminente!", rewards = nil},
[6826] = {title = "La llamada del aire: flota de Jeztor", objective = "Has trabajado duro, <nombre>, ¡pero es solo el principio!\n\nMis alas de guerra deben probar la misma carne de sus objetivos. ¡Esto garantizará un ataque preciso sobre nuestros enemigos!\n\nMi flota es la segunda más potente de nuestro mando aéreo. Por consiguiente, atacarán a los más poderosos de nuestros adversarios. Para ello, necesitan la carne de los tenientes Pico Tormenta.\n\n¡Apúrate, soldado!", description = nil, progress = "Has trabajado duro, <nombre>, ¡pero es solo el principio!\n\nMis alas de guerra deben probar la misma carne de sus objetivos. ¡Esto garantizará un ataque preciso sobre nuestros enemigos!\n\nMi flota es la segunda más potente de nuestro mando aéreo. Por consiguiente, atacarán a los más poderosos de nuestros adversarios. Para ello, necesitan la carne de los tenientes Pico Tormenta.\n\n¡Apúrate, soldado!", completion = "¡Más! ¡Más! ¡Tráeme más, que pronto estarán listos!", rewards = nil},
[6827] = {title = "La llamada del aire: flota de Mulverick", objective = "He pasado días encerrado en un horrible antro de enanos. ¡Vaya si quiero venganza!\n\nDebemos planificarlo minuciosamente.\n\nPrimero mis alas de guerra necesitan objetivos a los que apuntar, objetivos de alta prioridad. Tendré que alimentarlos con carne de los Comandantes Pico Tormenta. Por desgracia, ¡esos malnacidos están atrincherados muy lejos detrás de las líneas enemigas! Ahí tienes un trabajo a tu medida.", description = nil, progress = "He pasado días encerrado en un horrible antro de enanos. ¡Vaya si quiero venganza!\n\nDebemos planificarlo minuciosamente.\n\nPrimero mis alas de guerra necesitan objetivos a los que apuntar, objetivos de alta prioridad. Tendré que alimentarlos con carne de los Comandantes Pico Tormenta. Por desgracia, ¡esos malnacidos están atrincherados muy lejos detrás de las líneas enemigas! Ahí tienes un trabajo a tu medida.", completion = "¡No hay suficiente! Esperaría este nivel de trabajo de un Iniciado ¡¿pero de ti, &lt;nombre&gt;?! ¡Vamos, soldado! ¡DATE PRISA! ¡Hace falta más carne!", rewards = nil},
[6843] = {title = "Da Foo", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6844] = {title = "Umber, el archivista", objective = "Lleva el objeto recubierto de silítido a Umber, el archivista del Círculo Cenarion, en la aldea de Amparo de la Noche, en el Claro de la Luna.", description = "Todavía queda mucho por hacer aquí, pero creo que es vital llevar esto al Círculo para que lo analicen. Confía en mí... todavía quedará mucho trabajo por aquí cuando termines. Si dentro de este pegajoso esqueleto hay un diario, el Círculo podría tener por fin una ventana al pasado.\n\nLleva esto al archivista del Círculo en Amparo de la Noche. Se llama Umber y es brillante. Está algo tocado, eso seguro, ¡pero brillante de todas formas! Umber ha recuperado libros en mucho peor estado que este. Si se puede salvar, él puede hacerlo.", progress = "¡Eh, hola! Mmm...\n\nVoy a hablarte con sinceridad, amigo... apestas un poco. Tienes olor a pies, o algo así. No es que te esté atacando ni nada de eso. Solo digo que... en el futuro, quizás quieras refrescarte un poco después de salir del campo de batalla. De esa forma, las primeras impresiones que causes no serán tan impresionantes.\n\nEn fin... ¿Qué puedo hacer por ti?", completion = "¡Por las barbas de los sapos! ¿Qué llevas ahí? Tiene forma de libro... Intentaré romper la estructura que lo cubre.\n\n¿Layo te ha enviado a buscarme desde Silithus? Tan solo he oído susurros silenciosos, bueno, no tan silenciosos si los he oído, claro, sobre los horrores que están ocurriendo allí. Une eso a mi aversión por los bichos y tendrás la razón perfecta para que me quede aquí con los libros.", rewards = "Recibirás:\n\n"},
[6845] = {title = "El descubrimiento de los secretos del pasado", objective = "Habla con el rabino Saturna en el poblado Amparo de la Noche, en Claro de la Luna.", description = "Tendrás que venir a verme pronto, si es que quieres saber lo que había aquí. En cuanto lo averigüe, y desde luego si es un libro, te lo haré saber con sumo gusto.\n\nMientras tanto, iré a consultar a la Madre del Cubil, Rabine. Seguro que le interesa saber a qué te has estado dedicando. Nunca la hagas esperar, <nombre>, a no ser que quieras que te embadurne de miel.\n\nCréeme, no sería la primera vez.", progress = nil, completion = "Me alegro de verte, &lt;nombre&gt;.\n\nTe estaba esperando para comunicarte la gratitud del Círculo Cenarion por tu ayuda. Acepta esto como muestra de agradecimiento.\n\nMuchas gracias, &lt;clase&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nAnillo de Piedra vivaAnillo de cristal resplandeciente\n\nTambién recibirás:\n\nAnillo de Piedra vivaAnillo de cristal resplandeciente"},
[6846] = {title = "¡Al ataque!", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6847] = {title = "Maestro Ryson el Ojo q todo lo ve", objective = "Recupera el Ojo que todo lo ve del maestro Ryson en las cuevas de los Hacha Invernal y devuélveselo.\n\nPero ándate con ojo, al parecer el Ojo que todo lo ve del maestro Ryson ¡está maldito!", description = "Tu guerra en el corazón de Alterac no significa nada para la Hermandad, <clase>. Haremos lo de siempre: esperar hasta que un vencedor surja de las cenizas, acabe con toda esta locura y entonces dé el golpe definitivo.\n\nAunque no hemos venido para eso. El Ojo que Todo lo Ve se ha perdido, lo han arrebatado los trols Hacha Invernal de la región.\n\nRecupera el Ojo y nos pondremos a tu servicio por el tiempo que quede por tu favor.\n\nRegresa cuando consigas el Ojo para que pueda examinarte.\n\n<el Maestro Ryson esboza una sonrisa>.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[6848] = {title = "Maestro Ryson el Ojo que todo lo ve", objective = "Recupera el Ojo que todo lo ve del maestro Ryson en las cuevas de los Hacha Invernal y devuélveselo.\n\nPero ándate con cuidado, al parecer el Ojo que todo lo ve del maestro Ryson ¡está maldito!", description = "Tu guerra en el corazón de Alterac no significa nada para la Hermandad, <clase>. Haremos lo que siempre hemos hecho: esperar hasta que un vencedor surja de las cenizas, acabe con toda esta locura y entonces dé el golpe definitivo.\n\nAunque no hemos venido para eso. El Ojo que todo lo ve se ha perdido, lo han arrebatado los trols Hacha Invernal de la región.\n\nRecupera el Ojo y nos pondremos a tu servicio por el tiempo que quede por tu favor.\n\nRegresa cuando consigas el Ojo para que pueda examinarte.\n\n<el maestro Ryson esboza una sonrisa>.Cuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = nil},
[6861] = {title = "La unidad trituradora de Zinfizzlex", objective = "El maestro ingeniero Zinfizzlex quiere que le lleves lo siguiente:\n\n*30 lingotes de torio.\n\n*50 lingotes de mitril.\n\n*75 lingotes de hierro.\n\n*1 sierra de vapor.", description = "Estoy tan ansioso como tú por probar mi nueva trituradora, <raza>, pero antes de que pueda fabricarla, voy a necesitar materiales, ¡muchos materiales!\n\nPara ser exactos, voy a necesitar una montaña de torio, mitril y hierro. También necesitaré una sierra de vapor por unidad. Tendrás que conseguirlas en la serrería.\n\nCuando estaba curioseando por aquí (antes de mi captura) me fijé en una pila de sierras de vapor en la serrería de Pico Tormenta.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nUnidad trituradora de Zinfizzlex"},
[6862] = {title = "La unidad trituradora de Zinfizzlex", objective = "El maestro ingeniero Zinfizzlex quiere que le lleves lo siguiente:\n\n*30 lingotes de torio.\n\n*50 lingotes de mitril.\n\n*75 lingotes de hierro.\n\n*1 sierra de vapor.", description = "Estoy tan ansioso como tú por probar mi nueva trituradora, <raza>, pero antes de que pueda fabricarla, voy a necesitar materiales, ¡muchos materiales!\n\nPara ser exactos, voy a necesitar una montaña de torio, mitril y hierro. También necesitaré una sierra de vapor por unidad. Tendrás que conseguirlas en la serrería.\n\nCuando estaba curioseando por aquí (antes de mi captura) me fijé en una pila de sierras de vapor en la serrería de Lobo Gélido.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nUnidad trituradora de Zinfizzlex"},
[6881] = {title = "Ivus, el Señor del Bosque", objective = "El clan Lobo Gélido está protegido por una contaminación de energía elemental. Su chamán anda metido con poderes que seguramente nos aniquilarían si no nos ocupamos de ellos.\n\n¡El daño se ha extendido demasiado para que el Círculo lo controle! Tenemos que invocar a Ivus para que nos ayude.\n\nLos soldados Lobo Gélido llevan talismanes de elemental llamados cristales de tormenta. Podemos usar estos talismanes para conjurar a Ivus. ¡Adéntrate hasta ese lugar y recupera esos cristales, <nombre>!", description = nil, progress = "El clan Lobo Gélido está protegido por una contaminación de energía elemental. Su chamán anda metido con poderes que seguramente nos aniquilarían si no nos ocupamos de ellos.\n\n¡El daño se ha extendido demasiado para que el Círculo lo controle! Tenemos que invocar a Ivus para que nos ayude.\n\nLos soldados Lobo Gélido llevan talismanes de elemental llamados cristales de tormenta. Podemos usar estos talismanes para conjurar a Ivus. ¡Adéntrate hasta ese lugar y recupera esos cristales, <nombre>!", completion = "Muy bien, &lt;nombre&gt;. Usaré el poder de este talismán para aumentar la magia que ya se ha reunido. Cuando hayas acumulado suficiente poder, concentraré las energías reunidas e invocaré al Señor del Bosque para que nos ayude en nuestra causa.", rewards = nil},
[6901] = {title = "Lanzar el ataque", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[6921] = {title = "Entre ruinas", objective = "Llévale el núcleo de las profundidades a Je'neu Sancrea de la Avanzada de Zoram'gar, Vallefresno.", description = "El Azote del Ocaso se ha trasladado a las ruinas del Santuario Lunar de las Cavernas Anegadas. Su presencia solo puede servir para forzar los elementos a actuar contra nosotros. Si no nos ocupamos de ella, esta región caerá en sus manos definitivamente.\n\n<nombre>, dirígete a Brazanegra y encuentra la piedra de las profundidades en las ruinas; debería estar cerca del agua. Es un núcleo de sonda, un dispositivo que cuando se lee correctamente, revela la historia de toda actividad elemental. Si lo consigo, y con la ayuda de los miembros del Anillo de la Tierra, ¡quizás podamos detenerlos!", progress = "¿Has conseguido encontrar el núcleo de las profundidades? Sin él no tendremos ni idea de qué anda tramando el Azote del Ocaso por aquí.", completion = "Esto es exactamente lo que necesito. Un núcleo de las profundidades es un pozo de información que nos reportará muchas cosas buenas. Sea lo que sea lo que el Azote del Ocaso está tramando ahí dentro, y seguro que no puede ser nada bueno, mis camaradas lo descubrirán.\n\nLo has hecho muy bien hoy; el Anillo de la Tierra te mira con cariño por ayudarnos. Además has ayudado al conjunto de la Horda y eso es algo que debería enorgullecerte.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[6922] = {title = "Barón Aquanis", objective = "Llévale el extraño globo de agua a Je'neu Sancrea de la Avanzada de Zoram'gar, Vallefresno.", description = "El elemental de agua derrotado ha dejado tras de sí un extraño globo de agua. Supones que el objeto de alguna forma alimentó la energía de la bestia. En el interior del globo, una líquida forma repulsiva chapotea. El propio globo parece impermeable a todo tipo de fuerza física.\n\nSi alguien es capaz de interpretar este artilugio, es Je'neu Sancrea del Anillo de la Tierra. Se encuentra en la Avanzada de Zoram'gar de la Horda en Vallefresno.", progress = "Eh, hola otra vez, <nombre>. Yo por aquí, leyendo las olas, el mar, lo de siempre, vamos. ¿Qué te trae por la avanzada?", completion = "Esto... esto es un hallazgo, &lt;nombre&gt;.\n\n¿Y dices que lo conseguiste del barón Aquanis en Brazanegra? Hace tiempo que se cree que el barón Aquanis es un poder elemental corrupto; este globo demostrará qué fue exactamente lo que lo corrompió. Aunque no nos sorprendería que resultara ser el Azote del Ocaso, nuestros estudios revelarán también cómo lo hicieron.\n\nHas hecho bien en traerme esto. Te lo ruego, llévale esto junto con los mejores deseos del Anillo de la Tierra.", rewards = "Podrás elegir una de estas recompensas:\n\nSable de foragidoDedo de bruja\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nSable de foragidoDedo de bruja"},
[6941] = {title = "La llamada del aire: la flota de Vipore", objective = "¡Hay que ocuparse de las unidades de élite de los Lobo Gélido que vigilan las líneas, soldado! Te encargo que diezmes a esa manada de salvajes. Vuelve aquí con las medallas de sus tenientes y legionarios. Cuando me parezca que hemos despachado a suficiente chusma, desplegaré la fuerza aérea.", description = nil, progress = "¡Hay que ocuparse de las unidades de élite de los Lobo Gélido que vigilan las líneas, soldado! Te encargo que diezmes a esa manada de salvajes. Vuelve aquí con las medallas de sus tenientes y legionarios. Cuando me parezca que hemos despachado a suficiente chusma, desplegaré la fuerza aérea.", completion = "El vuelo no tardará en estar preparado. ¡Sigue trabajando duro!", rewards = nil},
[6942] = {title = "La llamada del aire: la flota de Slidore", objective = "Mis grifos están listos para atacar a las primeras líneas del frente pero no pueden hacerlo hasta que las líneas se reduzcan algo.\n\nLos guerreros Lobo Gélido, encargados de resistir en las primeras líneas del frente, llevan medallas de servicio que lucen orgullosos en la pechera. Arráncales esas medallas de sus podridos cadáveres y tráemelas.\n\nCuando la primera línea del frente esté suficientemente diezmada, ¡daré la orden de atacar desde el aire! ¡Muerte desde el cielo!", description = nil, progress = "Mis grifos están listos para atacar a las primeras líneas del frente pero no pueden hacerlo hasta que las líneas se reduzcan algo.\n\nLos guerreros Lobo Gélido, encargados de resistir en las primeras líneas del frente, llevan medallas de servicio que lucen orgullosos en la pechera. Arráncales esas medallas de sus podridos cadáveres y tráemelas.\n\nCuando la primera línea del frente esté suficientemente diezmada, ¡daré la orden de atacar desde el aire! ¡Muerte desde el cielo!", completion = "¡Sigue así y trae más, soldado! ¡Los grifos Nidal estarán listos para atacar muy pronto!", rewards = nil},
[6943] = {title = "La llamada del aire: la flota de Ichman", objective = "Los ánimos están decaídos, soldado. Tras el fracaso de nuestro último ataque aéreo contra la Horda, ¡se niegan a volar! De ti depende subirles la moral.\n\nRegresa al campo de batalla y ataca directo al corazón del mando de los Lobo Gélido. Acaba con sus comandantes y guardianes. Vuelve aquí y tráeme sus medallas, ¡todas las que te quepan en la talega! Te lo prometo, cuando mis grifos vean el botín y huelan la sangre de nuestro enemigo, ¡volverán a volar! ¡Ahora vete!", description = nil, progress = "Los ánimos están decaídos, soldado. Tras el fracaso de nuestro último ataque aéreo contra la Horda, ¡se niegan a volar! De ti depende subirles la moral.\n\nRegresa al campo de batalla y ataca directo al corazón del mando de los Lobo Gélido. Acaba con sus comandantes y guardianes. Vuelve aquí y tráeme sus medallas, ¡todas las que te quepan en la talega! Te lo prometo, cuando mis grifos vean el botín y huelan la sangre de nuestro enemigo, ¡volverán a volar! ¡Ahora vete!", completion = "¿Lo ves? ¡Fíjate en ellos, soldado! Se acercan... ¡Pronto el Vuelo de Ichman volverá a atacar el corazón del valle! ¡Los Pico Tormenta serán los amos del cielo!", rewards = nil},
[6961] = {title = "¡Papá Invierno está aquí!", objective = "Habla con Papá Invierno; está cerca de la zona de comercio de los Pastos de Bosquehumeante, en Orgrimmar.", description = "¡Feliz Festival de Invierno! ¡No dejes de saludar a Papá Invierno! Si te has portado bien, recibirás regalos magníficos. ¡No te olvides de decirle lo que quieres!\n\nY acuérdate también de tener regalitos para quien se haya portado bien. Disponemos de una estupenda selección. De la granja a la mesa, ¡todo lo bueno de los Pastos de Bosquehumeante!", progress = nil, completion = "Hola, hola. Soy Papá Invierno. Te deseo un muy feliz Festival de Invierno en mi nombre y en el de Pastos de Bosquehumeante: “”De la granja a la mesa: ¡deliciosos manjares de Pastos de Bosquehumeante!””.\n\nSi te has portado bien, quizás recibas un regalito de comida de Pastos de Bosquehumeante.", rewards = "Recibirás:\n\n"},
[6962] = {title = "Papá Invierno", objective = "Lleva 5 galletas de gengibre y leche fría Papá Invierno a Orgrimmar.", description = "¿Puedes hacerle un favor Papá Invierno, <chico/chica>?\n\nNo es que me guste hablar de mí mismo en tercera persona, pero Papá Invierno ha trabajado todo el día y se merece un regalito. Unas galletitas de gengibre y un vaso de leche fresquita me irían de perlas. Pórtate bien y tráeme eso, anda.", progress = "Tanto hablar de regalitos me ha dado hambre. Mmm, me encantarían unas galletas de jengibre y un vaso de leche...", completion = "Muchas gracias, &lt;nombre&gt;. Te deseo un feliz Festival de Invierno.\n\nTen, una cosita para agradecerte el favor.", rewards = "Recibirás:\n\nCaja con obsequio de Pastos de Bosquehumeante"},
[6963] = {title = "El robo de las golosinas del Festival de Invierno", objective = "Localiza al agente de Pastos de Bosquehumeante desaparecido en las zonas nevadas de las Montañas de Alterac.", description = "Oye, no es por fastidiarte la fiesta, pero Pastos de Bosquehumeante necesita ayuda de alguien con temperamento aventurero.\n\nHemos perdido una importante carga de regalitos, ¡y ahora hemos perdido al agente que había ido a investigar qué pasa! La última vez que se vio la carga o a nuestro hombre fue en las Montañas de Alterac.\n\n¡Ve a ver si los encuentras!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[6964] = {title = "El espíritu de esta fiesta", objective = "Habla con Sagorne Montacresta en el Valle de la Sabiduría sobre el Festival de Invierno.", description = "Regalos, golosinas, felicitaciones... ¡Bah!\n\nEsos goblins ensucian el verdadero sentido del Festival de Invierno. Es una época de renovación, en la que la tierra descansa bajo el manto de nieve tendido por Papá Invierno, ¡que NO es ese idiota del trajecito rojo!\n\nSi quieres saber el verdadero significado de la fiesta, ve a hablar con el historiador Karnik en la Liga de Exploradores. Seguro que le sorprende que alguien se interese por la historia en vez de por los regalos.", progress = nil, completion = "¿El Festival de Invierno? Por desgracia, con el tiempo ha ido perdiendo significado para los tauren.\n\nDebo decir que comparto el disgusto de Furmund; para él y su grupo, el pasado es como un pendón de guerra. A mí me parece bien que haya festejos, pero sin olvidar el verdadero espíritu del Festival.", rewards = "Recibirás:\n\n"},
[6981] = {title = "El fragmento brillante", objective = "Viaja a Trinquete y busca a alguien que pueda decirte algo más sobre el fragmento brillante.\n\nEntrega el fragmento como se te indique.", description = "El fragmento es verde y brillante y, aunque sólido, parece que hubiera humo arremolinándose bajo su superficie. No se puede evitar perder la mirada en las profundidades del cristal y, al mismo tiempo, uno sabe que hay algo muy desconcertante.\n\nQuizás deberías ir a ver si alguien de Trinquete puede decirte algo más sobre este extraño fragmento.", progress = "Hola, <raza>. ¿Qué puedo hacer por ti?", completion = "Oh... Nunca había visto algo así...", rewards = "Recibirás:\n\n"},
[6982] = {title = "Suministros de Dentefrío", objective = "Lleva 10 cajas de suministros de Dentefrío al intendente de la Alianza en Dun Baldar.", description = "Mina Dentefrío es un almacén de la Horda. No puedo tragar la comida que les dan a sus soldados ¡y el estado de sus armas y armaduras es lamentable! Pero... en tiempos como estos cuando los suministros son tan importantes, podemos arreglarnos con lo que tienen.\n\nEntra en Mina Dentefrío y tráeme sus suministros. Eso mantendrá a la Alianza en condiciones para el combate y privará a los guerreros de la Horda de un equipamiento de lo más necesario.\n\nSuerte, <nombre>. La mina está lejos al sur, en lo más profundo del territorio de la Horda.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[6983] = {title = "¡Qué ruin!", objective = "Encuentra los regalitos robados y devuélveselos a Kaymard Cobrellizco en Orgrimmar. Se cree que están en posesión de Grinch el Abominable, que se encuentra en algún lugar de las regiones nevadas de las Montañas de Alterac.", description = "Una c-c-criatura llamada \"el Abominable Grinch\" es el q-q-que ha robado nuestro envío de c-c-caprichos, <nombre>. ¡Y ten c-c-cuidado! ¡Él es q-q-quien me ha c-c-convertido en este lío de nieve!\n\nVas a n-n-necesitar ayuda p-p-para enfrentarte a él. Lo he visto d-d-deambular por d-d-donde están los yetis; ¡s-s-seguro que anda p-p-por ahí! Encuentra los caprichos, <nombre>, llévalos de vuelta a los Pastos de Bosquehumeante en Orgrimmar y d-d-diles que n-n-necesito que me d-d-descongelen!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[6984] = {title = "Un agradecimiento de Pastos de Bosquehumeante", objective = "Habla con Papá Invierno en Orgrimmar.", description = "Pastos de Bosquehumeante te agradece que hayas recuperado los regalitos robados, <nombre>. Queremos ofrecerte un regalo muy especial, ¡de manos del propio Papá Invierno!\n\nHabla con él; te dará nuestro regalo. De la granja a la mesa: ¡deliciosos manjares de Pastos de Bosquehumeante... y todo gracias a ti!", progress = nil, completion = "¡Saludos!\n\nLos goblins me han dicho que te has ganado un regalito especial. Aquí tienes. ¡Feliz Festival de Invierno!", rewards = "Recibirás:\n\nObsequio especial de Pastos de Bosquehumeante"},
[6985] = {title = "Suministros de Ferrohondo", objective = "Lleva 10 cajas de suministros de Ferrohondo al intendente de la Horda en el Bastión Lobo Gélido.", description = "Los enanos Pico Tormenta están usando Mina Ferrohondo como almacén. Quiero que realices una incursión en esa mina y me traigas sus suministros. Esto recortará los recursos de los Pico Tormenta en el Valle de Alterac ¡y nos aportará un equipo de lo más necesario!\n\nPero ándate con ojo, <nombre>. Mina Ferrohondo está en lo más recóndito del territorio de la Alianza y el control de la mina cambia como las nieves de Alterac.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7001] = {title = "Establos vacíos", objective = "Encuentra a un Lobo Gélido en el Valle de Alterac. Usa el collar Lobo Gélido cuando estés junto a él para domarlo. Cuando lo consigas, te seguirá hasta el maestro de establos de los Lobo Gélido. Habla con el maestro de establos para ganarte el crédito por la captura.", description = "La guerra en el valle se ha cobrado un precio sobre nuestros soldados. ¡Debemos dar vitalidad a nuestras tropas!\n\nSi conseguimos llenar el Establo, los Caballeros Lobo volverán a galopar por el campo de batalla.\n\nToma este bozal y encuentra a los Lobo Gélidos que pueblan el bosque vecino. Cuando encuentres un lobo, usa el bozal para domarlo. Tráeme a la bestia domada para que pueda acomodarla en el Establo.\n\nCuando tengamos suficientes lobos, los Caballero Lobo se reunirán con las patrullas de la primera línea del frente.", progress = nil, completion = "Solo espero que todos los que deben reunir pieles de carnero sean tan aplicados como tú, &lt;nombre&gt;.\n\nA este paso, tendremos el Establo lleno ¡en cuestión de horas!", rewards = nil},
[7002] = {title = "Arneses para carneros de montar", objective = "Mientras algunos de nuestros soldados están ocupados en la captura de lobos para el maestro de establos, otros deben encargarse de las necesidades básicas de la monta. Me refiero a los arneses para montar, claro.\n\nDebes atacar a los carneros locales de la región. ¡Los mismísimos carneros que la caballería Pico Tormenta usa de montura!\n\nAcaba con ellos y tráeme sus pieles. Cuando tengamos suficientes pieles, confeccionaremos los arneses para los jinetes. ¡Los Caballeros Lobo de los Lobo Gélido volverán por sus fueros!", description = nil, progress = "Mientras algunos de nuestros soldados están ocupados en la captura de lobos para el maestro de establos, otros deben encargarse de las necesidades básicas de la monta. Me refiero a los arneses para montar, claro.\n\nDebes atacar a los carneros locales de la región. ¡Los mismísimos carneros que la caballería Pico Tormenta usa de montura!\n\nAcaba con ellos y tráeme sus pieles. Cuando tengamos suficientes pieles, confeccionaremos los arneses para los jinetes. ¡Los Caballeros Lobo de los Lobo Gélido volverán por sus fueros!", completion = "Muy bien, soldado. Te lo haré saber en cuanto tenga suficientes pieles acumuladas.\n\n¡Sigue así!", rewards = nil},
[7003] = {title = "Gigantes reducidos", objective = "Utiliza el Ultra-reductor de Zorbin para encoger a cualquier gigante que encuentres en Feralas a un tamaño más manejable. Lleva 15 residuos de miniaturización encontrados en las versiones encogidas de los gigantes a Zorbin Ventipnotizador, al embarcadero de La Costa Olvidada de Feralas.", description = "¡Voy a tener que solucionar esto de los gigantes si quiero quedarme aquí! Porque no son buenos para el negocio, ¿no te parece?\n\nHe estado trabajando en una variante del rayo reductor gnomo, pero estoy utilizando los últimos avances tecnológicos goblin para construirla. Quisiera probarla pero me gustaría evitar esos pequeños inconvenientes que tiene la fase de experimentación, como la muerte, así que me gustaría que la probaras con los gigantes de Feralas. Después de encogerlos deberías encontrar un residuo de miniaturización, ¡tráeme un poco!", progress = "El residuo de miniaturización es una materia valiosísima en ingeniería, te lo aseguro. Y lo es porque sirve para asegurarse de que el controlador no hace algo tan decepcionante como explotar en la cara del usuario.\n\nOh, no te preocupes, me refería a otros controladores, ¡no al que te di a ti, claro está!", completion = "¡Espléndidamente espléndido! Me alegra ver que has vuelto con el residuo, ¡y con todos tus apéndices intactos, cabeza y todo! Eso está muy bien... no es que el Ultra-reductor fuera peligroso, pero, oye...\n\nAh, y aprecio mucho tu ayuda, &lt;nombre&gt;. De verdad, eres un buen amigo de los goblins de todo el mundo. Acepta esto como muestra de mi agradecimiento.", rewards = "Recibirás:\n\nMegarrebanador de Zorbin"},
[7021] = {title = "¡Papá Invierno está aquí!", objective = "Habla con Papá Invierno. Lo encontrarás cerca de la zona de comercio de los Pastos de Bosquehumeante, en Orgrimmar.", description = "¡Feliz Festival de Invierno <amigo/amiga>! ¡No dejes de saludar a Papá Invierno, que ahora mismo está en Orgrimmar! Si te has portado bien, recibirás regalos magníficos el día del Festival de Invierno. ¡No te olvides de decirle lo que quieres!\n\nY asegúrate de tener regalitos para los que se han portado bien. Disponemos de una estupenda selección. De la granja a la mesa, ¡todo lo bueno de los Pastos de Bosquehumeante!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7022] = {title = "¡Papá Invierno está aquí!", objective = "Habla con el Papá Invierno; está cerca de la zona comercial de Pastos de Bosquehumeante, en Forjaz.", description = "¡Feliz Festival de Invierno! ¡No dejes de saludar al Gran Papá Invierno! Si te has portado bien, recibirás regalos magníficos. ¡No te olvides de decirle lo que quieres!\n\nY asegúrate de tener regalitos para los que se han portado bien. Disponemos de una estupenda selección. De la granja a la mesa: ¡deliciosos manjares de Pastos de Bosquehumeante!", progress = nil, completion = "Hola, hola. Soy el Papá Invierno. Te deseo un muy feliz Festival de Invierno en mi nombre y en el de Pastos de Bosquehumeante: “”De la granja a la mesa: ¡deliciosos manjares de Pastos de Bosquehumeante!””.\n\nSi te has portado bien, quizás recibas un regalito de comida de Pastos de Bosquehumeante.", rewards = "Recibirás:\n\n"},
[7023] = {title = "¡Papá Invierno está aquí!", objective = "Habla con Papá Invierno. Está cerca de la zona de comercio de los Pastos de Bosquehumeante, en Forjaz.", description = "¡Feliz Festival de Invierno! ¡No dejes de saludar a Papá Invierno, que ahora mismo está en Forjaz! Si te has portado bien, recibirás regalos magníficos el día del Festival de Invierno. ¡No te olvides de decirle lo que quieres!\n\nY asegúrate de tener regalitos para los que se han portado bien. Disponemos de una estupenda selección. De la granja a la mesa, ¡todo lo bueno de los Pastos de Bosquehumeante!", progress = nil, completion = "Hola, hola. Soy Papá Invierno y quiero desearte un maravilloso Festival de Invierno por cortesía de Pastos de Bosquehumeante... \"De la granja a la mesa, todo lo bueno de los Pastos de Bosquehumeante\".\n\nSi te has portado bien este año, quizás recibas regalos de los Pastos de Bosquehumeante durante el Festival de Invierno. ¿A que es genial?\"", rewards = "Recibirás:\n\n"},
[7024] = {title = "¡Papá Invierno está aquí!", objective = "Habla con Papá Invierno. Lo encontrarás cerca de la zona de comercio de los Pastos de Bosquehumeante, en Orgrimmar.", description = "¡Feliz Festival de Invierno <amigo/amiga>! ¡No dejes de saludar a Papá Invierno, que ahora mismo está en Orgrimmar! Si te has portado bien, recibirás regalos magníficos el día del Festival de Invierno. ¡No te olvides de decirle lo que quieres!\n\nY asegúrate de tener regalitos para los que se han portado bien. Disponemos de una estupenda selección. De la granja a la mesa, ¡todo lo bueno de los Pastos de Bosquehumeante!", progress = nil, completion = "Hola, hola. Soy Papá Invierno. Te deseo un muy feliz Festival de Invierno en mi nombre y en el de Pastos de Bosquehumeante... \"De la granja a la mesa: ¡deliciosos manjares Pastos de Bosquehumeante!\".\n\nSi te has portado bien este año, quizás recibas regalitos de comida de Pastos de Bosquehumeante. ¿A que es genial?\"", rewards = "Recibirás:\n\n"},
[7025] = {title = "Golosinas para Papá Invierno", objective = "Lleva 5 galletas de gengibre y leche fría a Papá Invierno a Forjaz.", description = "¿Puedes hacerle un favor a Papá Invierno, <chico/chica>?\n\nNo es que me guste hablar de mí mismo en tercera persona, pero Papá Invierno ha trabajado todo el día y se merece un regalito. Unas galletitas de gengibre y un vaso de leche fresquita me irían de perlas. Pórtate bien y tráeme eso, anda.", progress = "Tanto hablar de regalitos me ha dado hambre. Mmm, me encantarían unas galletas de jengibre y un vaso de leche...", completion = "Muchas gracias, &lt;nombre&gt;. Te deseo un feliz Festival de Invierno.\n\nTen, una cosita para agradecerte el favor.", rewards = "Recibirás:\n\nCaja con obsequio de Pastos de Bosquehumeante"},
[7026] = {title = "Arneses para carneros de montar", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[7027] = {title = "Establos vacíos", objective = "Encuentra un carnero de Alterac en el Valle de Alterac. Usa el collar de doma Pico Tormenta cuando estés junto al carnero de Alterac para domarlo. Cuando lo consigas, te seguirá hasta el maestro de establos. Habla con el maestro de establos para ganarte el crédito por la captura.", description = "¡Solo tienes que mirar en el Establo, <nombre>! ¡Vacío! La brigada de los Pico Tormenta no tiene carneros que montar, la caballería ha desaparecido.\n\nTienes que ayudarnos, soldado. Llévate este collar de doma y adéntrate en la espesura de este valle. Encuentra a los carneros de Alterac que deambulan por esa zona y usa el collar para atrapar a alguno. Cuando hayas capturado un carnero, regresa aquí y lo acomodaré en nuestro Establo.\n\nCuando tengamos suficientes carneros, se notificará al comandante de los jinetes de carneros. Con tu ayuda, ¡la caballería renacerá!", progress = nil, completion = "El Establo estará lleno muy pronto. ¡Sigue con tu buen trabajo!", rewards = nil},
[7028] = {title = "Los males de Maraudón", objective = "Recoge 25 tallas de cristal terádrico y llévaselas a Willow a Desolace.", description = "Las cavernas de cristal de Terramok están al oeste, en pleno corazón de Maraudon... Es un lugar antiguo y cargado de historia. Quiero una parte de esa historia, <clase>. Tras las puertas gigantes hay reliquias de los dioses antiguos.\n\nSi te atreves a enfrentarte a los males de Maraudon, te compensaré generosamente. Tráeme todas las tallas de cristal terádrico que encuentres.\n\nAsí podré... estudiarlas. Los poderes que contienen llevan demasiado tiempo dormidos.", progress = "¡No preguntes!\n\nYa sabes lo que quiero.", completion = "¡Ja, ja! Sabía que volverías.\n\n&lt;Willow habla entre dientes.&gt;\n\nQué fácil de convencer.\n\nLo que sea por una recompensa. Je, je, je...", rewards = "Podrás elegir una de estas recompensas:\n\nTogas de sagacidadYelmo SprightringCadena incansableEspaldares de mole de piedra\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nTogas de sagacidadYelmo SprightringCadena incansableEspaldares de mole de piedra"},
[7029] = {title = "La corrupción de Lenguavil", objective = "Llena el vial revestido en el estanque naranja de Maraudon. \n\nAplica el líquido a la hiedra tallovil para que emerja el vástago tóxico. \n\nCura 8 plantas eliminando su vástago tóxico e informa a Vark Marcaguerra en la Aldea Cazasombras.", description = "Un sátiro llamado Lenguavil ha corrompido las cavernas de Maraudon y ha detenido el desarrollo de muchas de las plantas.\n\nToma este frasco, <nombre>. Llénalo en el estanque de cristal naranja de las cavernas; el relleno del frasco contrarrestará las toxinas y las transformará en agentes sanadores.\n\nEsparce el líquido purificado en la hiedra Tallovil de las cavernas naranja. Aunque se eliminará la corrupción de la planta, debes saber que seguirá bajo el control de Lenguavil. Puede que intente atacarte...", progress = "SI consigues curar a la planta, volverá a crecer. Tenemos que hacer todo lo que podamos, <nombre>...", completion = "Gracias, &lt;nombre&gt;; seguiré investigando con los restos de líquido.\n\nAunque queda mucho por hacer, has contribuido a limpiar parte de la corrupción de Lenguavil en Maraudon.", rewards = "Podrás elegir una de estas recompensas:\n\nAro de SemillaleñaFaja arbustoGuanteletes ramazarza\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAro de SemillaleñaGuanteletes ramazarzaFaja arbusto"},
[7041] = {title = "La corrupción de Lenguavil", objective = "Llena el vial revestido en el estanque naranja de Maraudon. \n\nAplica el líquido a la hiedra tallovil para que emerja el vástago tóxico. \n\nCura 8 plantas eliminando su vástago tóxico e informa a Talendria en Punta de Nijel.", description = "Un sátiro llamado Lenguavil ha corrompido las cavernas de Maraudon y ha detenido el desarrollo de muchas de las plantas.\n\nToma este vial, <nombre>. Llénalo en el estanque naranja corrupto de las cavernas; el relleno del vial contrarrestará las toxinas y las transformará en agentes sanadores.\n\nEsparce el líquido purificado en la hiedra Tallovil de las cavernas naranja. Aunque se eliminará la corrupción de la planta, debes saber que seguirá bajo el control de Lenguavil. Puede que intente atacarte...", progress = "SI consigues curar a la planta, volverá a crecer. Tenemos que hacer todo lo que podamos, <nombre>.", completion = "Gracias, &lt;nombre&gt;; seguiré investigando con los restos de líquido.\n\nAunque queda mucho por hacer, has contribuido a limpiar parte de la corrupción de Lenguavil en Maraudon.", rewards = "Podrás elegir una de estas recompensas:\n\nAro de SemillaleñaFaja arbustoGuanteletes ramazarza\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nAro de SemillaleñaGuanteletes ramazarzaFaja arbusto"},
[7042] = {title = "El robo de las golosinas del Festival de Invierno", objective = "Localiza al agente de Pastos de Bosquehumeante desaparecido en las zonas nevadas de las Montañas de Alterac.", description = "Oye, no es por fastidiarte la fiesta, pero Pastos de Bosquehumeante necesita ayuda de alguien con temperamento aventurero.\n\nHemos perdido una importante carga de regalitos, ¡y ahora hemos perdido al agente que había ido a investigar qué pasa! La última vez que se vio la carga o a nuestro hombre fue en las Montañas de Alterac.\n\n¡Ve a ver si los encuentras!", progress = nil, completion = "¡Eh! ¿Hay alg-g-guien ahí?\n\nN-no s-s-soy un m-m-muñeco de n-nieve. M-me llamo S-sacks y rep-p-presento a P-pastos de Bosquehum-mmeante en Ironf-f-forge... ¡M-me han atacado unas c--criaturas ext-t-trañas mientras buscaba una c-c-carga p-perdida!\n\nT-t-tengo frrrrrrío...", rewards = "Recibirás:\n\n"},
[7043] = {title = "¡Qué ruin!", objective = "Encuentra los regalitos robados y devuélveselos a Wulmort Calderilla en Forjaz. Se cree que están en posesión de Grinch el Abominable, que se encuentra en algún lugar de las regiones nevadas de las Montañas de Alterac.", description = "Una c-c-criatura llamada \"el Abominable Grinch\" es el q-q-que ha robado nuestro envío de c-c-caprichos, <nombre>. ¡Y ten c-c-cuidado! ¡Él es q-q-quien me ha c-c-convertido en este lío de nieve!\n\nVas a n-n-necesitar ayuda p-p-para enfrentarte a él. Lo he visto d-d-deambular por d-d-donde están los yetis; ¡s-s-seguro que anda p-p-por ahí! Encuentra los caprichos, <nombre>, llévalos de vuelta a los Pastos de Bosquehumeante en Forjaz y d-d-diles que n-n-necesito que me d-d-descongelen!", progress = "¡<nombre>, nuestro Festival de Invierno será un desastre sin esos regalitos que hay que reponer! El propio Graccu no podrá compartir sus deliciosas empanadas de carne con todos los niños y niñas esta temporada...", completion = "¡Lo encontraste! ¡Has salvado el Festival de Invierno de los Pastos de Bosquehumeante!\n\nPobre Sacks... nos aseguraremos de enviar un destacamento a por él y encontrar la forma de descongelarlo. Con suerte, volverá a tiempo para el día del Festival aunque tendrá que compensar el tiempo que no haya podido trabajar. Sin embargo, ¡esto no sería posible si no fuera por ti!", rewards = "Recibirás:\n\n"},
[7044] = {title = "Leyendas de Maraudón", objective = "Recupera las 2 partes del Cetro de Celebras: la vara y el diamante Celebrian. \n\nEncuentra el modo de hablar con Celebras.", description = "Un siniestro sátiro llamado Lord Lenguavil extiende su maldad por estas cuevas, envenenando las mentes. Sigue viviendo detrás de los cristales púrpura.\n\nTambién creó un símbolo viviente de su corrupción llamado Noxxion, que vive tras los cristales naranja. Han robado las dos piezas del cetro de mi hermano. Celebras, mi hermano, ha caído víctima de la corrupción y deambula ahí dentro.\n\n¡Tienes que ayudarlo! Busca las piezas y encuentra el modo de hablar con mi hermano.", progress = "He despertado de una pesadilla interminable...", completion = "Ahora que me has traído las piezas del cetro, podremos limpiar Maraudon de la corrupcíón de Lenguavil.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[7046] = {title = "El Cetro de Celebras", objective = "Ayuda a Celebras el Redimido mientras reconstruye el Cetro de Celebras. \n\nHabla con él después del ritual.", description = "Antes, mi cetro era una fuente de esperanza para mí. Su poder me permítía desplazarme rápidamente por estas cavernas. Quería dar paz al espíritu de mi tío... Te paso a ti esa tarea.\n\nNecesito que me ayudes en el ritual para unir las dos piezas. Sigue mis instrucciones mientras canalizo la energía necesaria para unir la vara y el diamante.\n\n¡Reconstruyamos el cetro de Celebras!", progress = nil, completion = "Ten; debes usar el poder que te ha sido concedido siempre para hacer el bien. Sigue explorando las cavernas, a ver si encuentras los restos de mi tío. Te esperan grandes peligros, &lt;nombre&gt;.\n\nVuelve a hablar conmigo si lo necesitas.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nCetro de Celebras"},
[7061] = {title = "El Festival de Invierno", objective = "Lee el libro \"El Festival de Invierno\" si quieres saber más sobre esta fiesta. Cuando termines, entrégaselo a Cairne Pezuña de Sangre en Cima del Trueno.", description = "Ten; si quieres saber más sobre esta fiesta, lee este libro. Opino que nuestro punto de vista es el apropiado, pero comprendo que es mejor que toda la información esté recopilada en una única fuente.\n\nCuando termines, llévale el libro a Cairne Pezuña de Sangre a Cima del Trueno. Le gustará saber que hay otros interesados en las antiguas leyendas.", progress = "Te doy la bienvenida a Mulgore y a Cima del Trueno. Me agrada la gente que se interesa por el reino y sus gentes. ¿Es esa la razón por la que estás aquí?", completion = "La gente ha ido olvidando las leyendas y la tradición del Festival de Invierno. Los goblins explotan la festividad para sus propios fines, pero eso también ayuda a que no caiga en el olvido.\n\nAdmiro tu interés en nuestra historia, &lt;clase&gt;. Te deseo lo mejor.", rewards = "Recibirás:\n\n"},
[7062] = {title = "El espíritu de esta fiesta", objective = "Habla con el historiador Karnik del Festival de Invierno en la Liga de Exploradores de Forjaz.", description = "Regalos, golosinas, felicitaciones... ¡Bah!\n\nEsos goblins ensucian el verdadero sentido del Festival de Invierno. Es una época de renovación, en la que la tierra descansa bajo el manto de nieve tendido por Papá Invierno, ¡que NO es ese idiota del trajecito rojo!\n\nSi quieres saber el verdadero significado de la fiesta, ve a hablar con el historiador Karnik en la Liga de Exploradores. Seguro que le sorprende que alguien se interese por la historia en vez de por los regalos.", progress = nil, completion = "¿El Festival de Invierno? Por desgracia, ha ido perdiendo significado para los enanos. Ahora parece que Papá Invierno no es más que un gancho comercial para vender golosinas y regalitos.\n\nDebo decir que comparto el disgusto de Goli.", rewards = "Recibirás:\n\n"},
[7063] = {title = "El Festival de Invierno", objective = "Lee el libro \"El Festival de Invierno\" si quieres saber más sobre esta fiesta. Cuando termines, entrégaselo al rey Magni Barbabronce de Forjaz.", description = "Ten; si quieres saber más sobre esta fiesta, lee este libro. Opino que nuestro punto de vista es el apropiado, pero comprendo que es mejor que toda la información esté recopilada en una única fuente.\n\nCuando termines, llévale el libro al rey Magni Barbabronce. Le gustará saber que hay otros interesados en las antiguas leyendas.", progress = "Te doy la bienvenida al Reino de Forjaz. Me agrada la gente que se interesa por el reino y sus gentes. ¿Es esa la razón por la que estás aquí?", completion = "La gente ha ido olvidando las leyendas y la tradición del Festival de Invierno. Los goblins explotan la festividad para sus propios fines, pero eso también ayuda a que no caiga en el olvido.\n\nAdmiro tu interés en nuestra historia, &lt;clase&gt;. Te deseo lo mejor.", rewards = "Recibirás:\n\n"},
[7064] = {title = "Corrupción de la tierra y de la semilla", objective = "Mata a la princesa Theradras y ve a ver a Selendra cerca de la Aldea Cazasombras, en Desolace.", description = "En las profundidades de Maraudon vive una criatura de gran maldad. La princesa Theradras es una fuerza elemental de la tierra relacionada con los dioses antiguos. Hace eones se unió a Zaetar, el primogénito de Cenarius. El fruto de su relación fueron las criaturas llamadas centauros en Kalimdor. Esas criaturas mataron a Zaetar y se quedaron sus restos. Mi objetivo es encontrar a quien tenga fuerza suficiente para matar a Theradras y recuperar los restos de Zaetar antes de volver a Cima del Espolón.", progress = nil, completion = "¡Estupendo, &lt;nombre&gt;! ¡Gracias!\n\nLe preguntaré a Marandis por los restos de Zaetar, pero al menos has hecho lo más difícil.\n\nElige uno de estos objetos como prueba de nuestra gratitud.", rewards = "Podrás elegir una de estas recompensas:\n\nZumbaespadaVara de resurgimientoObjetivo del vigilante de Verdantis\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nZumbaespadaVara de resurgimientoObjetivo del vigilante de Verdantis"},
[7065] = {title = "Corrupción de la tierra y de la semilla", objective = "Mata a la princesa Theradras y ve a ver al guardián Marandis a Punta de Nijel, en Desolace.", description = "En las profundidades de Maraudon vive una criatura de gran maldad, <nombre>. La princesa Theradras es una fuerza elemental de la tierra relacionada con los dioses antiguos. Hace eones se unió a Zaetar, el primogénito de Cenarius. El fruto de su relación fueron las criaturas llamadas centauros. Esas criaturas mataron a Zaetar y Theradras guardó sus restos. Mi objetivo es encontrar a quien tenga fuerza suficiente para matar a Theradras y recuperar los restos de Zaetar antes de volver a Cima del Espolón.", progress = nil, completion = "¡Estupendo, &lt;nombre&gt;! ¡Gracias!\n\nAunque me inquieta no tener los restos de Zaetar, debo respetar su voluntad. Esperemos que los centauros sean menos sanguinarios a partir de ahora.\n\nPuedes quedarte con uno de estos objetos como recompensa por tu ayuda.", rewards = "Podrás elegir una de estas recompensas:\n\nZumbaespadaVara de resurgimientoObjetivo del vigilante de Verdantis\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nZumbaespadaVara de resurgimientoObjetivo del vigilante de Verdantis"},
[7066] = {title = "La semilla de la vida", objective = "Busca a Remulos en Claro de la Luna y dale la semilla de la vida.", description = "Mis hijos e hijas, los desdichados centauros, necesitan a su padre.\n\nMira: este lugar está lleno de esperanza y todas las cosas que venera mi padre, Cenarius. ¡Todo eso salió de mí! Y ahora no voy a quitárselo, ni siquiera por el crimen que cometieron contra mí.\n\nPero voy a pedirte un favor para tranquilizar a mi hermano. Toma; es la primera semilla que nació de la vida alimentada por mis restos. Dile que mi espíritu está aquí y que seguiré viviendo. Lo entenderá.", progress = "Dejas un rastro de corrupción, <nombre>, como muchos aventureros. Pero tú tienes algo distinto. ¿Has venido a hablar conmigo? Me resultas familiar, pero no consigo ubicarte.\n\nDime, ¿por qué has venido a verme?", completion = "¿El espíritu de mi hermano ha encontrado al fin la paz? Me alegro de saberlo. Lo creí perdido para siempre, pero esto... Este símbolo de su sacrificio me demuestra que no está perdido sino que ha renacido. Ha hecho lo que debía hacer.\n\nGracias, &lt;nombre&gt;. Me has devuelto la esperanza; presiento que esta semilla será muy importante para las gentes de esta tierra.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[7067] = {title = "Las instrucciones del Paria", objective = "Lee las instrucciones del Paria. Busca el Amuleto de Unidad en Maraudon y llévaselo al sur de Desolace.", description = "Aunque me exiliaron por hereje, sé que digo la verdad y eso me anima a seguir adelante. Pero para completar mi destino, necesito la ayuda de alguien que no sea centauro.\n\nHay que entrar en las salas sagradas de Maraudon; mi gente protege la tumba y solo los más devotos pueden entrar.\n\nSi aceptas ayudarme, afirma con la cabeza. Te daré un pergamino con instrucciones. Léelo y considera mi propuesta.", progress = "¿Qué me dices, <nombre>? ¿Te atreves a pisar suelo sagrado para ayudarme a unificar a los centauros?", completion = "¡Al fin puedo iniciar la unificación de las tribus, &lt;raza&gt;! Gracias.\n\nAcepta esto en prueba de mi gratitud. Espero que cuando volvamos a vernos sea en tiempo de paz.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nMarca del Elegido"},
[7068] = {title = "Fragmentos Oscuros", objective = "Recoge 10 fragmentos oscuros en Maraudon y llévaselos a Uthel'nay a Orgrimmar.", description = "En Maraudon, el lugar sagrado de los centauros, se concentra una magia muy fuerte. Los centauros de Desolace protegen el lugar con su vida, pero no podrán impedir que cojamos lo que necesitemos.\n\nVe a Maraudon y encuentra a las criaturas de piedra hechas de fragmentos oscuros. Trae los cristales y Uthel'nay te compensará.\n\nEs un sitio peligroso, mejor llévate compañía.", progress = "¿Traes los cristales? Seguro que serán muy útiles en todo tipo de magia arcana, rituales de poder y grandes hechizos.", completion = "Perfecto... Bueno, toma uno de estos abalorios y lárgate, tengo mucho que hacer.", rewards = "Podrás elegir una de estas recompensas:\n\nColgante de fragmento oscuro de EntusiastaColgante de fragmento de sombras prodigioso\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nColgante de fragmento oscuro de EntusiastaColgante de fragmento de sombras prodigioso"},
[7070] = {title = "Fragmentos Oscuros", objective = "Recoge 10 fragmentos oscuros en Maraudon y llévaselos al archimago Tervosh a Theramore, en la costa de Marjal Revolcafango.", description = "He empezado a hacer pruebas con las propiedades arcanas de varios cristales. Lady Valiente me ha autorizado a pedir a los viajeros que se dirigen a Desolace que busquen un lugar llamado Maraudon. En el templo en ruinas hay grandes elementales compuestos por unas piedras moradas que llamo fragmentos oscuros.\n\nTe agradecería que te acercaras e intentaras encontrarme algún cristal. Incluso podría fabricarte un adorno con ellos, si quieres.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nColgante de fragmento oscuro de EntusiastaColgante de fragmento de sombras prodigioso\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nColgante de fragmento oscuro de EntusiastaColgante de fragmento de sombras prodigioso"},
[7081] = {title = "Los cementerios del Valle de Alterac", objective = "Asalta un cementerio y vuelve con el sargento Durgen Pico Tormenta en las Montañas de Alterac.", description = "Los cementerios del Valle de Alterac están muy codiciados. Tanto la Horda como la Alianza luchan encarnizadamente por este suelo sagrado. ¿<nombre>, estás <dispuesto/dispuesta> a unirte a la lucha?\n\nQuiero que asaltes un cementerio enemigo. Encuentra un cementerio con un estandarte de la Horda ¡y arráncalo del suelo! Hazlo y habrás completado tu tarea...\n\nPero si además puedes resistir en el cementerio durante unos minutos, nuestros soldados vendrán pronto a apoyarte.", progress = nil, completion = "¡Muy bien! Los cementerios son clave para el control del Valle de Alterac. Cuando capturas uno, contribuyes a asegurar la soberanía de la Alianza en ese lugar.\n\nVete ahora, &lt;nombre&gt;. Vuelve a la batalla y si ves un cementerio bajo control de la Horda... ¡ya sabes qué hacer!", rewards = "Recibirás:\n\n"},
[7082] = {title = "Los cementarios de Alterac", objective = "Asalta un cementerio y vuelve con el cabo Teeka Gruñidosangriento en las Montañas de Alterac.", description = "Las líneas del frente en el Valle de Alterac cambian constantemente. Los límites del territorio avanzan y retroceden conforme nos enfrentamos a nuestros adversarios en combate mortal. Y es en los cementerios, <nombre>, donde se libran las batallas más encarnizadas.\n\n¡Quiero que ataques un cementerio en manos de la Alianza! Encuentra un cementerio con un estandarte de la Alianza y arráncalo del suelo.\n\nHazlo y habrás completado tu tarea... pero si además puedes resistir en el cementerio durante unos minutos, será nuestro y nuestros soldados vendrán pronto a relevarte.", progress = nil, completion = "¡Muy bien, &lt;nombre&gt;! Con cada cementerio que controlemos, el espíritu de nuestros enemigos vagará perdido y sin rumbo.", rewards = "Recibirás:\n\n"},
[7101] = {title = "Torres y bunkers", objective = "Captura una torre enemiga y vuelve con el cabo Teeka Gruñidosangriento en las Montañas de Alterac.", description = "Las torres y los búnker son posiciones importantes ¡y debemos capturarlas y hacernos con el control de todas! Por eso quiero que ataques un búnker controlado por el enemigo.\n\nPara ello, fíjate donde haya un estandarte de la Alianza. En el interior verás otro estandarte. Saca ese estandarte y en su lugar aparecerá uno de la Horda. ¡Muy bien!\n\nEn ese momento habrás completado tu misión, pero si además puedes resistir en la posición lo suficiente sin que la Alianza lo recupere ¡será destruida!", progress = nil, completion = "¡He oído hablar de tu éxito en la misión! Me complace tal noticia...\n\nNuestros soldados podrán ahora cargar con impunidad ¡y demostrar a la Alianza que el Valle de Alterac no es lugar para ellos!", rewards = "Recibirás:\n\n"},
[7102] = {title = "Las torres y los bunkers", objective = "Destruye el estandarte de una torre enemiga o de un búnker y vuelve junto al sargento Durgen Pico Tormenta en las Montañas de Alterac.", description = "Las torres y los búnker son posiciones defensivas clave ¡y debemos controlar tantas como sea posible! Quiero que ataques una torre o un búnker en el Valle de Alterac.\n\nPara ello, entra en una torre donde veas un estandarte de la Horda en la parte superior. En el interior verás otro estandarte enemigo. Saca ese estandarte y en su lugar aparecerá uno de la Alianza. ¡Muy bien!\n\nEn ese momento habrás completado tu misión, si además puedes resistir en la posición lo suficiente sin que la Horda lo recupere ¡será destruida!", progress = nil, completion = "He recibido un informe de campo según el cual has contribuido a recuperar un estandarte enemigo. ¡Muy bien, &lt;nombre&gt;! Con sus líneas descolocadas ¡podemos adelantar más el frente!", rewards = "Recibirás:\n\n"},
[7121] = {title = "El intendente", objective = "Habla con el intendente de Pico Tormenta.", description = "<nombre>, nuestro intendente me ha encargado que le mande a los nuevos reclutas. Necesita suministros de los alijos ocultos en las minas de Alterac y probablemente te envíe para recuperarlos.\n\nSigue la carretera. Encontrarás a nuestro intendente al oeste.", progress = nil, completion = "Es bueno ver a nuevos soldados aquí, &lt;nombre&gt;, pero con un poco de suerte entrarás pronto en combate ¡y no serás un novato por mucho tiempo! \n\nVuelve a hablar conmigo. Tengo tareas que tratar contigo...", rewards = "Recibirás:\n\n"},
[7122] = {title = "Conquistar una mina", objective = "Captura una mina que no esté bajo control de los Pico Tormenta y vuelve junto al sargento Durgen Pico Tormenta en las Montañas de Alterac.", description = "Las minas del Valle de Alterac contienen algo más que minerales. Tanto los Lobo Gélido como los Pico Tormenta tienen alijos con suministros extras en las minas de Ferro Hondo y Mina Dentefrío... una buena idea, ¡ojalá logremos hacernos con su control!\n\nToma posesión de una mina y tráemelos. Para capturar una mina, penetra en una que no controlemos y mata a su líder. Cuando lo hagas la mina será nuestra ¡y mandaremos soldados para proteger la posición!", progress = nil, completion = "¡Lo has conseguido! ¡Muy bien, &lt;nombre&gt;!\n\nCuando nos hacemos con el control de una mina y tenemos a soldados estacionados allí, es mucho más fácil recolectar el mineral y los suministros.", rewards = "Recibirás:\n\n"},
[7123] = {title = "Habla con nuestro intendente", objective = "Habla con el intendente de Lobo Gélido.", description = "<nombre>, el intendente de los Lobo Gélido está buscando reclutas para tareas que llevan a las minas del Valle de Alterac. Habla con él, te contará más detalles.\n\nSigue la carretera hacia el oeste hasta el búnker, toma entonces la salida del oeste y encontrarás a nuestro intendente.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7124] = {title = "Conquistar una mina", objective = "Captura una mina y vuelve con el cabo Teeka Gruñidosangriento en las Montañas de Alterac.", description = "Mientras se libra una encarnizada batalla en el Valle de Alterac, también hay enfrentamientos en Mina Dentefrío y en Mina Ferrohondo. Ambas contienen minerales en abundancia y se utilizan además para almacenar suministros; ¡es normal que se codicien tanto!\n\n¡Únete a nuestro movimiento bélico, <nombre>! ¡Captura una de las minas para la Horda! Para capturar una mina, penetra en una que no controlemos y mata a su líder. Cuando lo hagas la mina será nuestra ¡y mandaremos soldados para proteger la posición!", progress = nil, completion = "¡Has capturado una mina! Con nuestros propios guerreros protegiéndola, será mucho más sencillo extraer minerales de sus profundidades y recolectar los suministros que allí hubieran escondido.\n\nMuy bien, &lt;nombre&gt;. Esta acción ha sido una gran contribución a nuestros esfuerzos en la batalla.", rewards = "Recibirás:\n\n"},
[7141] = {title = "La batalla de Alterac", objective = "Adéntrate en el Valle de Alterac, derrota al general Drek'thar de la Horda y vuelve junto a la prospectora Tallapiedra en las Montañas de Alterac.", description = "La Liga de Exploradores cree que hay artefactos en el Valle de Alterac pero los orcos Lobo Gélido no nos dejan explorarlo. ¡No podemos permitir que la Horda se interponga en nuestros objetivos de erudición! Derrota al general Lobo Gélido, Drek'Thar. Con él muerto, los orcos perderán su ventaja.\n\nDrek'Thar se encuentra en el Bastión Lobo Gélido, al sur del Valle de Alterac. Véncelo y tráemelo.", progress = nil, completion = "¡Muy bien, &lt;nombre&gt;! Con la sangre de la Horda ensangrentada tras la muerte de su general, ¡tendremos margen para explorar!", rewards = "Recibirás:\n\n"},
[7142] = {title = "La batalla por Alterac", objective = "Adéntrate en el Valle de Alterac y derrota al general enano Vanndar Pico Tormenta. Vuelve entonces junto a Voggah Presaletal en las Montañas de Alterac.", description = "Los enanos Pico Tormenta y sus aliados han invadido el Valle de Alterac ¡y tenemos que expulsarlos! Para ganar la batalla de Alterac, tenemos que derrotar a su general, Vanndar Pico Tormenta!\n\nEncontrarás a Vanndar en Dun Baldar, al norte del Valle de Alterac.\n\nDestrúyelo, <nombre>, y vuelve aquí.", progress = nil, completion = "¡Muy bien, &lt;nombre&gt;! Los Pico Tormenta no tienen ninguna posibilidad en esta insensata invasión.\n\n¡Ja! Hasta ahora, ¡solo han conseguido que maten a su general!", rewards = "Recibirás:\n\n"},
[7161] = {title = "Terreno de pruebas", objective = "Viaja hasta la cueva Zarpa Salvaje ubicada al sureste del campamento base en el Valle de Alterac y encuentra el estandarte de los Lobo Gélido. Devuélvele el estandarte al maestro de guerra Laggrond.", description = "Salve, <clase>. Mucho tiempo ha pasado desde que hemos tenido la oportunidad de responder a la llamada de la batalla. Los antiguos sentimientos tardan en emerger pero una vez despiertos, la sangre fluye cual torrente. La furia de la batalla vuelve...\n\nLos recién llegados al valle deben completar primero el rito de la batalla.\n\nEn lo más profundo de la cueva gnoll de Zarpa Salvaje se encuentra un estandarte de los Lobo Gélido. Adéntrate hasta ese lugar y recupera el estandarte. Devuélvemelo y te concederé una insignia de Iniciado de los Lobo Gélido.", progress = "¡Despierta! ¡Despierta, <nombre>! Esto no es un sueño. ¡Estás realmente atrincherado en medio de una cruda batalla!", completion = "Ese ardor que sientes es tu alma que se despierta. Tu espíritu ha aceptado la llamada a las armas.\n\nAhora formas parte de los Lobo Gélido, &lt;nombre&gt;. Enhorabuena.\n\nLleva siempre contigo esta insignia. A medida que subes de rango entre los Lobo Gélido, sustituiré tu insignia por otras, otras más poderosas.\n\nQuizás algún día se exalten tus hazañas entre el clan Lobo Gélido. Si tal día llega, se te concederá el Ojo del Mando.", rewards = "Recibirás:\n\nInsignia Lobo Gélido Rango 1Pelando la cebolla"},
[7162] = {title = "Terreno de pruebas", objective = "Viaja hasta la cueva Ala Gélida ubicada al suroeste de Dun Baldar en el Valle de Alterac y recupera el estandarte de Pico Tormenta. Devuélveselo al teniente Haggerdin en las Montañas de Alterac.", description = "Has llegado justo a tiempo, <clase>. ¡Estos salvajes Lobo Gélidos no se rinden! Tenemos que demostrarles el poder de los Pico Tormenta. Deben inclinarse ante nuestra superioridad.\n\nAntes de que te dirijas al campo de batalla, deberías unirte a las filas de los Pico Tormenta.\n\nAl suroeste de Dun Baldar encontrarás la Cueva Ala Gélida. En lo más profundo de las cavernas hay clavado un estandarte de los Pico Tormenta. Adéntrate hasta ese lugar y recupera ese estandarte. Devuélvemelo y te ganarás una insignia de Iniciado de los Pico Tormenta.", progress = "Cuidado con las arpías de la región, <clase>. ¡No se lo pensarán dos veces antes de cortarte el cuello!", completion = "Toma esta insignia y llévala con orgullo, &lt;nombre&gt;. A medida que subes de rango entre los Pico Tormenta, recibirás la insignia correspondiente. Vuelve aquí conforme adquieres más honores con los Pico Tormenta y sustituiré tu insignia por otras, otras más poderosas.\n\nQuizás algún día llegues a crecer tanto como para convertirte en una leyenda en el campo de batalla. Si tal día llega, se te concederá el Ojo del Mando.", rewards = "Recibirás:\n\nInsignia de Pico Tormenta Rango 1La alcachofa Lobo Gélido"},
[7163] = {title = "Ascender y darse a conocer", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nInsignia Lobo Gélido Rango 2"},
[7164] = {title = "Honrado en el clan", objective = "¡Cómo me alegra verte de nuevo, <clase>! No esperaba que regresaras. No obstante, has demostrado ser un valiente y honorable soldado Lobo Gélido.\n\nHa llegado de nuevo tu hora, <nombre>. Te has ganado un nuevo rango y con ello, una nueva insignia que refleje este aumento de importancia.\n\nPresenta tu insignia.", description = nil, progress = "¡Cómo me alegra verte de nuevo, <clase>! No esperaba que regresaras. No obstante, has demostrado ser un valiente y honorable soldado Lobo Gélido.\n\nHa llegado de nuevo tu hora, <nombre>. Te has ganado un nuevo rango y con ello, una nueva insignia que refleje este aumento de importancia.\n\nPresenta tu insignia.", completion = "&lt;El maestro de guerra Laggrond hace el saludo.&gt;\n\n¡Vete, &lt;nombre&gt;! Sigue con tu defensa de los Lobo Gélido. ¡Hay que ganar esta guerra! ¡Por Drek'Thar! ¡Por el Jefe de Guerra!", rewards = "Recibirás:\n\nInsignia Lobo Gélido Rango 3"},
[7165] = {title = "Ganarse una reverencia", objective = "Tantos muertos. La sangre empapa el campo. Y a pesar de ello… estás viva… Has perseverado. Has conducido a nuestros ejércitos a muchas victorias. Por ello, soldado, te has ganado un nuevo rango.\n\nPresenta tu insignia.", description = nil, progress = "Tantos muertos. La sangre empapa el campo. Y a pesar de ello… estás viva… Has perseverado. Has conducido a nuestros ejércitos a muchas victorias. Por ello, soldado, te has ganado un nuevo rango.\n\nPresenta tu insignia.", completion = "&lt;El maestro de guerra Laggrond emite un rugido.&gt;\n\nYa empieza a correrse la voz sobre tus hazañas, soldado. Los Pico Tormenta se estremecen de miedo con solo oír tu nombre. ¡Sigue así!", rewards = "Recibirás:\n\nInsignia Lobo Gélido Rango 4"},
[7166] = {title = "Héroes legendarios", objective = "Irradias mando y poder, <clase>. Exaltado a los ojos de los Lobo Gélido, el enemigo se encoge ante la simple mención de tu nombre.\n\nEn pie, héroe de los Lobo Gélido. ¡Levántate y recibe los honores!\n\nPresenta tu insignia.", description = nil, progress = "Irradias mando y poder, <clase>. Exaltado a los ojos de los Lobo Gélido, el enemigo se encoge ante la simple mención de tu nombre.\n\nEn pie, héroe de los Lobo Gélido. ¡Levántate y recibe los honores!\n\nPresenta tu insignia.", completion = "Pronto, todos los que estén en el campo de batalla estarán bajo tu ojo vigilante. ¡Tendrás al valle y a sus soldados a tus órdenes!", rewards = "Recibirás:\n\nInsignia Lobo Gélido Rango 5"},
[7167] = {title = "El ojo del orden", objective = "Drek'Thar no tiene más que alabanzas para ti. Por todo Kalimdor se cuentan tus hazañas. El Jefe de Guerra está radiante, tu inquebrantable defensa de nuestro clan le ha complacido sobremanera.\n\nTe has ganado el Ojo del Mando.\n\nPresenta tu insignia, comandante <nombre>.", description = nil, progress = "Drek'Thar no tiene más que alabanzas para ti. Por todo Kalimdor se cuentan tus hazañas. El Jefe de Guerra está radiante, tu inquebrantable defensa de nuestro clan le ha complacido sobremanera.\n\nTe has ganado el Ojo del Mando.\n\nPresenta tu insignia, comandante <nombre>.", completion = "Canaliza el poder de la insignia. Deja que los enemigos oigan tus palabras. El poder de la Horda es incuestionable!", rewards = "Recibirás:\n\nInsignia Lobo Gélido Rango 6"},
[7168] = {title = "Ascender y darse a conocer", objective = "La mayoría no vive lo suficiente para superar su propia mediocridad. Has demostrado ser un soldado ejemplar, <clase>. Ha llegado la hora.\n\nPresenta tu insignia.", description = nil, progress = "La mayoría no vive lo suficiente para superar su propia mediocridad. Has demostrado ser un soldado ejemplar, <clase>. Ha llegado la hora.\n\nPresenta tu insignia.", completion = "¡Que nuestros enemigos sepan que un soldado de rango los ha atacado! ¡Lucha por la gloria de los Pico Tormenta!", rewards = "Recibirás:\n\nInsignia de Pico Tormenta Rango 2"},
[7169] = {title = "Honrado en la guardia", objective = "¡Por toda la base se habla de tus hazañas en la Tierra de Disputa! Le has asestado golpes contundentes a nuestro enemigo, ¡aplastándoles la moral! Por eso, te has ganado un rango de honor entre los Pico Tormenta.\n\nPresenta tu insignia.", description = nil, progress = "¡Por toda la base se habla de tus hazañas en la Tierra de Disputa! Le has asestado golpes contundentes a nuestro enemigo, ¡aplastándoles la moral! Por eso, te has ganado un rango de honor entre los Pico Tormenta.\n\nPresenta tu insignia.", completion = "El Rey está al corriente de tus heroicos actos en el frente, &lt;clase&gt;. ¡Sigue con tu inquebrantable defensa de la Guardia de Pico Tormenta!", rewards = "Recibirás:\n\nInsignia de Pico Tormenta Rango 3"},
[7170] = {title = "Ganarse una reverencia", objective = "Tengo que saberlo, <nombre> Cuando miras al enemigo directamente a los ojos, ¿ves miedo? ¿Se encogen ahora en tu presencia? ¡Deben de darse cuenta de que están vencidos!\n\nTe has ganado el respeto de la Guardia. ¡Presenta tu insignia!", description = nil, progress = "Tengo que saberlo, <nombre> Cuando miras al enemigo directamente a los ojos, ¿ves miedo? ¿Se encogen ahora en tu presencia? ¡Deben de darse cuenta de que están vencidos!\n\nTe has ganado el respeto de la Guardia. ¡Presenta tu insignia!", completion = "Cuando te conocí, nunca habría pensado que llegarías a ser una heroína. ¡Sigue así, soldado!", rewards = "Recibirás:\n\nInsignia de Pico Tormenta Rango 4"},
[7171] = {title = "Héroes legendarios", objective = "Ante mí veo a un héroe exaltado de la Alianza.\n\n<El teniente Haggerdin hace el saludo.>\n\nSon pocos los que han alcanzado tal rango entre los Pico Tormenta. He visto como el enemigo caía ante ti. He visto cómo su determinación se doblegaba en tu presencia. Cuando saltas a la palestra, ¡te conviertes en la luz de la esperanza para nuestras tropas!\n\nPresenta tu insignia.", description = nil, progress = "Ante mí veo a un héroe exaltado de la Alianza.\n\n<El teniente Haggerdin hace el saludo.>\n\nSon pocos los que han alcanzado tal rango entre los Pico Tormenta. He visto como el enemigo caía ante ti. He visto cómo su determinación se doblegaba en tu presencia. Cuando saltas a la palestra, ¡te conviertes en la luz de la esperanza para nuestras tropas!\n\nPresenta tu insignia.", completion = "¡Seguro que serás tú quien lleve el Ojo del Mando!", rewards = "Recibirás:\n\nInsignia de Pico Tormenta Rango 5"},
[7172] = {title = "El ojo del orden", objective = "¡Que oigan tu voz, comandante <nombre>! Que sepan lo que es el miedo. ¡Enséñales el poder del que disponen los Pico Tormenta en sus filas!\n\nPresenta tu insignia.", description = nil, progress = "¡Que oigan tu voz, comandante <nombre>! Que sepan lo que es el miedo. ¡Enséñales el poder del que disponen los Pico Tormenta en sus filas!\n\nPresenta tu insignia.", completion = "¡Tú eres quien ha de llevar a nuestros soldados a la victoria, Comandante! Los soldados están a tus órdenes. Seguirán directamente lo que les ordenes. Dirígelos… Aplasta a los Lobo Gélido.", rewards = "Recibirás:\n\nInsignia de Pico Tormenta Rango 6"},
[7181] = {title = "La leyenda de Korrak", objective = "Según cuenta la leyenda, el jefe de los poderosos trols Hacha Invernal parece dispuesto a sembrar el caos entre los habitantes del Valle de Alterac.\n\nSi Korrak se digna aparecer, destrúyelo y vuelve con el maestro de guerra Laggrond.", description = "Los invasores Pico Tormenta no son la única amenaza de la región, soldado. La guerra en el valle se está librando en dos frentes. Los trols caníbales Hacha Invernal también rivalizan por el poder.\n\nSu líder es Korrak el Sanguinario, una cruel y taimada fiera.\n\nUn buen golpe sobre Korrak podría ser aplastante para el clan Hacha Invernal. Ejecuta a la fiera ¡y recibirás tu recompensa!Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nBuscasangreLanza con punta de hieloVarita de Frío cortante\n\nTambién recibirás:\n\nRuna de RecuerdoLanza con punta de hieloBuscasangreVarita de Frío cortante"},
[7201] = {title = "El último elemento", objective = "Viaja a las Profundidades de Roca Negra y recupera 10 esencias de los elementos. Tu primer impulso es buscar los golems y a los creadores de golems. Recuerdas que Vivian Latumba también murmuró entre dientes algo sobre los elementales.", description = "Hay trabajo a mansalva para quien se aventure en las profundidades, <nombre>.\n\nLos Hierro Negro han perfeccionado la creación de golems extremadamente poderosos.\n\nLos primeros informes de nuestros espías indican que los enanos usan una fuente de poder única para conferir a sus creaciones una potencia destructora sin igual.\n\n¡Tan solo imagínate lo que podríamos hacer con nuestras abominaciones si nos hiciéramos con esta esencia de los elementos! No dejes piedra por remover en esa ciudad si es necesario, ¡pero no vuelvas hasta haber encontrado la esencia! Recibirás un justo pago por el riesgo.", progress = "<Se frota las manos.>\n\n¡Enséñamelas!", completion = "¡Estupendo! ¡Haré que las envíen inmediatamente por mensajero a Entrañas!\n\nEn cuanto a ti, aquí tienes el pago, como te prometí. ¡Quédate con el cambio, bestia asquerosa!|n", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nLacre de Lagrave"},
[7202] = {title = "Korrak el Sanguinario", objective = "Según cuenta la leyenda, el jefe de los poderosos trols Hacha Invernal parece dispuesto a sembrar el caos entre los habitantes del Valle de Alterac.\n\nSi Korrak se digna aparecer, destrúyelo y vuelve con el teniente Haggerdin en las Montañas de Alterac.", description = "Los trols indígenas Hacha Invernal de esa región son salvajes despiadados a los que nada complacería más que añadir nuestros huesos a su hediondo estofado.\n\n¡Debemos demostrarles nuestro poder!\n\nHemos recuperado unos libros de sus cuevas donde se describe su jerarquía de poder. Los artefactos apuntan a que su líder, Korrak el Sanguinario, tiende a permanecer oculto hasta que surge un motivo para manifestarse.\n\n¡La muerte de Korrak significaría el fin del clan Hacha Invernal! Mátalo y vuelve aquí.Podrás elegir una de estas recompensas:\n\n", progress = nil, completion = nil, rewards = "Podrás elegir una de estas recompensas:\n\nBuscasangreLanza con punta de hieloVarita de Frío cortante\n\nTambién recibirás:\n\nRuna de RecuerdoLanza con punta de hieloBuscasangreVarita de Frío cortante"},
[7221] = {title = "Habla con la prospectora Tallapiedra", objective = "Habla con la prospectora Tallapiedra en la plaza fuerte de Dun Baldar.Ah, <nombre>, ¿traes un informe de tu misión? Fantástico, ¡todos los que logremos reunir serán pocos para los Pico Tormenta!\n\nHabla con la prospectora Tallapiedra de Dun Baldar. Está reclutando para una misión vital en territorio enemigo. Encontrarás a Tallapiedra al final del cuartel de Dun Baldar.\n\nY mantén los ojos bien abiertos al cruzar Dun Baldar. Hay mucho trabajo para un esforzado <clase> que no tema ensuciarse las manos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", description = "Ah, <nombre>, ¿traes un informe de tu misión? Fantástico, ¡todos los que logremos reunir serán pocos para los Pico Tormenta!\n\nHabla con la prospectora Tallapiedra de Dun Baldar. Está reclutando para una misión vital en territorio enemigo. Encontrarás a Tallapiedra al final del cuartel de Dun Baldar.\n\nY mantén los ojos bien abiertos al cruzar Dun Baldar. Hay mucho trabajo para un esforzado <clase> que no tema ensuciarse las manos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7222] = {title = "Habla con Voggah Presaletal", objective = "Habla con Voggah en el Bastión Lobo Gélido.¿Un <clase>? ¡Bien! Es una alegría verte en Alterac, <nombre>. Unos enanos intrusos y sus aliados han invadido nuestro valle y recibimos con los brazos abiertos a todo el que se una a la Horda¡en legítima masacre!\n\nHabla con Voggah Presaletal. Está buscando agentes para una importante misión en el bastión de la Alianza en el valle de Alterac Valley!\n\nEncuentra a Voggah en el Bastión Lobo Gélido.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", description = "¿Un <clase>? ¡Bien! Es una alegría verte en Alterac, <nombre>. Unos enanos intrusos y sus aliados han invadido nuestro valle y recibimos con los brazos abiertos a todo el que se una a la Horda¡en legítima masacre!\n\nHabla con Voggah Presaletal. Está buscando agentes para una importante misión en el bastión de la Alianza en el valle de Alterac Valley!\n\nEncuentra a Voggah en el Bastión Lobo Gélido.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7223] = {title = "Los restos de armadura", objective = "Llévale 20 piezas de restos de armadura a Murgot Forjahonda en Dun Baldar.", description = "La batalla es encarnizada en el Valle de Alterac y los suministros que conseguimos de Forjaz no bastan para vestir a nuestros expertos soldados.\n\nSi encuentras restos de armadura en el campo de batalla, ¡contribuye a nuestro esfuerzo bélico y tráemelos!", progress = "¿Me has traído algún resto de armadura, <nombre>?", completion = "Estas piezas son muy buenas, &lt;nombre&gt;. Puedo usarlas para confeccionar nuevos equipos para nuestros soldados.\n\nSi mi nivel de suministro llega a ser suficientemente alto, ¡a lo mejor nuestras avezadas unidades de combate irán al campo de batalla!", rewards = "Te van a lanzar el siguiente hechizo:\n\nArmadura lustrosa\n\nRecibirás:\n\n"},
[7224] = {title = "Botín enemigo", objective = "Llévale 20 piezas de restos de armadura al herrero Regzar en Aldea Lobo Gélido.", description = "Cuando te enfrentes al enemigo, reúne los restos de sus armaduras y tráemelos. ¡Los usaré para hacer armaduras para nuestros soldados!", progress = "¿Cómo va la caza, <nombre>? ¿Tienes algún resto de armadura para mí?", completion = "Estos restos nos servirán. Los fundiré y los remendaré ¡y nadie se enterará de que proceden de esos cobardes Pico Tormenta!\n\nGracias, &lt;nombre&gt;. Y permíteme pulir tu armadura...", rewards = "Te van a lanzar el siguiente hechizo:\n\nArmadura lustrosa\n\nRecibirás:\n\n"},
[7241] = {title = "En defensa de Lobo Gélido", objective = "Dirígete al Valle de Alterac en las Montañas de Alterac. Encuentra al maestro de guerra Laggrond y habla con él para empezar tu carrera como soldado Lobo Gélido; lo encontrarás en la entrada del túnel. El Valle de Alterac se sitúa al norte de Molino Tarren en la falda de las Montañas de Alterac.", description = "Retumban los tambores de guerra en una tierra distante, <clase>. La abominable guardia de los Pico Tormenta de Forjaz está atacando al clan partidario de tu Jefe de Guerra.\n\nSolo con verte, deduzco que tienes resistencia para el combate y que estás <preparado/preparada> para hacer tuya la causa de los Lobo Gélido.\n\nTú eres quien debe ahora dar el paso siguiente en tu evolución.\n\nAl norte del Molino Tarren, en Alterac, hallarás la entrada del Valle de Alterac. Allí encontrarás al maestro de guerra Laggrond. ¡Date prisa, <clase>, que la batalla está próxima!", progress = nil, completion = "Para convertirte en una soldado Lobo Gélido, hay que ponerte a prueba...", rewards = "Recibirás:\n\n"},
[7261] = {title = "El imperativo soberano", objective = "Dirígete al Valle de Alterac en las Laderas de Trabalomas. Delante de la entrada del túnel, encuentra al teniente Haggerdin y habla con él.\n\n¡Por la gloria de Barbabronce!", description = "Estamos librando una brutal batalla en los valles de Alterac. Los trols caníbales Hacha Invernal nos atacan por un lado y el salvaje clan Lobo Gélido, por otro. ¡Ambos deben ser exterminados en el nombre del rey Magni Barbabronce! La toma, sacrificio y devolución de esa tierra es un imperativo soberano y territorial para el Reino de Forjaz.\n\n¡Por desgracia, los soldados no crecen en los árboles! Ponte a las órdenes del teniente Haggerdin en las afueras del valle Alterac para empezar tu recorrido de deber y honor entre los Pico Tormenta.", progress = nil, completion = "¡A trabajar!", rewards = "Recibirás:\n\n"},
[7281] = {title = "Amor fraternal", objective = "Viaja hasta el territorio de los Pico Tormenta y mata al comandante Karl Filis. Vuelve con el comandante Luis Filis cuando hayas completado la tarea.", description = "Una tragedia... Mi hermano y yo enfrentados en el campo de batalla: un comandante de los Pico Tormenta.\n\nDe los cuatro comandantes de los Pico Tormenta, es sin duda alguna el más peligroso. Quizás si lo ejecutan, la reina alma en pena podría convertirlo como hizo conmigo, demostrarle cuán equivocado está...\n\nEn lo más profundo del territorio enemigo encontrarás a mi hermano. Ejecútalo y vuelve aquí. Confío en que serás capaz de cumplir esta misión, soldado.\n\nAh, soldado, no vayas en solitario.", progress = nil, completion = "Tus proezas no pasarán desapercibidos, &lt;nombre&gt;.\n\nNo debo perder el tiempo si hay que salvarlo. ¡Hay que ponerse en contacto con Sylvanas!", rewards = "Recibirás:\n\n"},
[7282] = {title = "Amor fraternal", objective = "Viaja hasta el territorio de los Lobo Gélido y mata al comandante Luis Filis. Vuelve con el comandante Karl Filis cuando hayas completado la tarea.", description = "¡Hay que acabar con su sufrimiento, soldado! No sabe lo que hace Se ha convertido en un sirviente ciego de la temida reina alma en pena.\n\nRealiza una incursión por la Tierra de Disputa y hasta lo más recóndito del territorio de los Lobo Gélido. Encuentra a mi hermano y acaba con él. Hay que poner fin a su tormento.", progress = nil, completion = "Te acordaste de incinerar el cuerpo, ¿verdad?", rewards = "Recibirás:\n\n"},
[7301] = {title = "Señores del Cielo caídos", objective = "Viaja hasta el territorio de los Lobo Gélido y busca al comandante del aire Slidore, al comandante del aire Vipore y al comandante del aire Ichman. Vuelve con el comandante Duffy cuando hayas completado la misión.", description = "Los tenemos prisioneros, <raza>. Me refiero a sus comandantes del aire. Fue una batalla encarnizada en los cielos pero ¡al final cayeron! Desgraciadamente, abatieron a nuestros mejores pilotos en un contraataque sobre el Bastión Lobo Gélido. Tienes que averiguar qué ha sido de ellos.\n\nBusca al oeste de Punta de la Torre y por el Bastión Lobo Gélido. Allí es donde se les vio por última vez. No dejes piedra por remover en estas zonas.", progress = nil, completion = "¡Increíble! Los has liberado, ¿verdad? ¿¿¿No??? Bueno, al menos sabemos dónde están retenidos", rewards = "Recibirás:\n\n"},
[7302] = {title = "Señores del Cielo caídos", objective = "Averigua qué fue del comandante del aire Guse, el comandante del aire Jeztor y el comandante del aire Mulverick. Vuelve con el comandante Mulfort cuando hayas completado la misión.", description = "Nuestro apoyo aéreo es un caos, <nombre>. Han abatido a los comandantes del aire sobre territorio enemigo. ¡Tienes que averiguar qué ha sido de ellos para que podamos intentar rescatarlos!\n\nSe vio por última vez a Guse sobrevolando el Búnker de Ala Gélida. Al alas de guerra de Jeztor se le avistó cerca de la Serrería de Pico Tormenta. Mulverick... ese chalado temerario se estrelló cerca del Búnker Norte de Dun'Baldar.\n\nEso es todo lo que sé. ¡Vamos! No dejes piedra por remover en estas zonas.", progress = nil, completion = "¡Muy bien! La información que has conseguido es muy valiosa para nuestra causa.", rewards = "Recibirás:\n\n"},
[7321] = {title = "Sopa balsámica de tortuga", objective = "Llévale 10 trozos de carne de tortuga y algunas especias calmantes a Christoph Yabrigo a Molino Tarren.", description = "¿Quién dijo que los no-muertos no tenemos ni idea de cocinar? Mira mi receta secreta de sopa de tortuga, por ejemplo. ¡Sé de algunos que incluso se levantarían de su tumba solo para probarla! Je, je...\n\nPor cierto, hace mucho que no la hago. Antes solía ir yo mismo al Lago Lordamere a cazar Quijafortes, esas cosas repelentes, pero ahora no puedo dejar mi trabajo.\n\nSi me traes carne de tortuga Quijaforte y especias calmantes, ¡te enseñaré a prepararla!", progress = "¿Has encontrado los ingredientes? Debo advertirte de que los gusanos de la Alianza son muy numerosos en esta zona... En mi opinión, creo que ya están demasiado cerca...", completion = "Mi sopa de tortuga calmante te mantendrá caliente en una noche fría... por si te interesa saberlo.", rewards = "Recibirás:\n\nReceta: sopa de tortuga reconfortanteSopa de tortuga reconfortante"},
[7341] = {title = "Un trato justo", objective = "Saludos, <raza>. Yo soy Kolark, un <clase> de tesoros de Cima del Trueno. He viajado mucho por el mundo y he visto también mucho.\n\nMis viajes por los Mares del Sur me permitieron descubrir la existencia de estas flechas con cabeza de torio. Tengo un sinfín de ellas, pero ya no tengo balas. Y bueno, ningún tauren que se precie andaría por ahí con un endeble arco. ¡Las flechas no sirven para nada! \n\nTe voy a ofrecer un trato: si me traes proyectiles de torio te las cambio por mis cabezales de flecha de torio. ¿Hecho?", description = nil, progress = "Saludos, <raza>. Yo soy Kolark, un <clase> de tesoros de Cima del Trueno. He viajado mucho por el mundo y he visto también mucho.\n\nMis viajes por los Mares del Sur me permitieron descubrir la existencia de estas flechas con cabeza de torio. Tengo un sinfín de ellas, pero ya no tengo balas. Y bueno, ningún tauren que se precie andaría por ahí con un endeble arco. ¡Las flechas no sirven para nada! \n\nTe voy a ofrecer un trato: si me traes proyectiles de torio te las cambio por mis cabezales de flecha de torio. ¿Hecho?", completion = "¡Excelente! Soy un tauren de palabra, &lt;raza&gt;; aquí tienes las flechas.", rewards = "Recibirás:\n\nEspada con punta de torio"},
[7342] = {title = "Las flechas no valen para nada", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nEspada con punta de torio"},
[7361] = {title = "Favor entre los Lanza Negra", objective = "Se te asignado la tarea de matar a los jugadores elfos de la noche en el Valle de Alterac.\n\nMata a un elfo de la noche y llévale su cabeza decapitada a Najak Malditor en el Bastión Lobo Gélido.", description = "<Najak gruñe>.\n\nAntes de que los elfos de la noche existieran había trols. Es en los trols donde radica el origen de los elfos de la noche.\n\nNo toleraré sus ataques rebeldes contra mi pueblo. El objetivo de Corzocelada es aniquilarnos para no dejar rastro de los que lo concibieron ni de su clase. ¡No ocurrirá!\n\nPor cada elfo de la noche que mates y decapites, Najak te concederá el favor entre los Lanza Negra. Se correrá la voz de tus actos por todo Kalimdor y los Reinos del Este. ¡Márchate ahora y sirve a la Lanza Negra!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7362] = {title = "Aliado de los Tauren", objective = "Se te asignado la tarea de matar a los jugadores gnomos en el Valle de Alterac.\n\nMata a un gnomo y llévale un mechón de la víctima a Ravak Tótem Siniestro en el Bastión Lobo Gélido.", description = "La bruja más anciana me envió a esta zona de guerra para investigar sobre otras razas. Más concretamente, estoy recolectando muestras de gnomo. Lo que más interesa a Magatha es su capacidad de supervivencia. Sí, los gnomos, aunque lamentablemente débiles y minúsculos, exudan gran resistencia en el combate y fuera de él.\n\nPor suerte para ti, ando buscando a rufianes para cumplir ese encargo de Magatha. Regresa con mechones de los gnomos y los tauren reconocerán en ti a un aliado.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7363] = {title = "La condición humana", objective = "Se te asignado la tarea de matar a los jugadores humanos en el Valle de Alterac.\n\nMata a un humano y vuelve con el comandante Luis Philips (que deambula entre el frente y el Bastión Lobo Gélido) con un hueso de tu víctima.\n\n¡Al fin se avecina una cura para la condición humana!", description = "Los humanos son absolutamente detestables.\n\n<el comandante Philips esboza una mueca>.\n\nAunque es cierto que soy un soldado de los Lobo Gélido, siempre seré un sirviente de Sylvanas.\n\nCuando estoy de servicio, me ocupo de tareas adicionales para los boticarios oscuros de Entrañas.\n\nMe han encargado que recolecte esquirlas de huesos humanos que se usarán para curar a la condición humana.\n\nTengo autorización para delegar esa tarea en mis subordinados.\n\nEncuéntralos. Mátalos. Tráeme sus esquirlas de huesos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7364] = {title = "La recompensa de Gnomeregan", objective = "Se te asignado la tarea de matar a los jugadores adversarios tauren en el Valle de Alterac.\n\nMata a un tauren y vuelve con Dirk Estafa a Dun'Baldar con una pezuña tauren.", description = "El Gran Manitas en persona me ha enviado a este campo de batalla para defender a mis hermanas y hermanos gnomos. Según nos cuentan, los tauren están señalando y masacrando a los gnomos. Y lo que es peor, están arrebatando gloriosos mechones gnomos de los cadáveres caídos. Estas atrocidades deben cesar. ¡Debemos responder con la misma moneda!\n\nQuiero verlos sin pezuñas, <clase>. ¡Tráeme sus pezuñas! Demuestra que eres un aliado de los gnomos.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7365] = {title = "Réquiem de Corzocelada", objective = "Se te asignado la tarea de matar a los jugadores trol en el Valle de Alterac.\n\nMata a un trol y vuelve con Athramanis a Dun'Baldar con el mojo de un trol Lanza Negra.", description = "Corzocelada ha dado la orden. Hay que exterminar a todos los trols en el campo de batalla. Sus absurdas pretensiones según las cuales dan a luz a elfos de la noche se debe contrarrestar con la fuerza y el debido perjuicio.\n\nAsesínalos y tráeme su mojo. Insisto: quiero que les robes el mojo.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7366] = {title = "La misericordia del arzobispo", objective = "Se te asignado la tarea de matar a los jugadores renegados en el Valle de Alterac.\n\nMata a un renegado y vuelve con el comandante Karl Filis (que deambula entre la Serrería y la Mina) con el corazón de tu víctima.", description = "El arzobispo es tan amable como sabio. Para que un hombre mire con compasión a ese grupo de desgraciados...\n\n<el comandante Philips se sacude la cabeza incrédulo.\n\nDemasiado amable... Sencillamente demasiado amable.\n\nEl arzobispo nos pide que compadezcamos a los no-muertos y les concedamos muertes rápidas y permanentes. Para tal muestra de misericordia, tan solo necesitamos sus ennegrecidos corazones dominados por el mal.\n\nTráeme los corazones de los Renegados para poder incinerarlos con la ceremonia debida en la Catedral de la Luz. ¡Apúrate, <nombre>!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7367] = {title = "Acabar con la amenaza", objective = "Encuentra al experto en explosivos de los Lobo Gélido y mátalo. Vuelve con el experto en explosivos de los Pico Tormenta cuando hayas acabado este trabajo.", description = "Las bases y las líneas más avanzadas de los Lobo Gélido han caído en una trampa, <clase>. Tienes que mirar donde pisas al invadir territorio enemigo.\n\nSi quieres dar un golpe contundente contra ellos, debes destruir su fuente de municiones antipersona, es decir su especialista en explosivos. Si lo matas, estarás a salvo para desarmar las minas. Mientras él esté bajo control, también lo estarán las minas.\n\nEs probable que lo encuentres en una torre en las cercanías del Baluarte Sangre Fría. ¡Suerte!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7368] = {title = "Acabar con la amenaza", objective = "Encuentra al experto en explosivos de los Pico Tormenta y mátalo. Vuelve con el experto en explosivos de los Lobo Gélido cuando hayas acabado este trabajo.", description = "Conozco bien las minas. Al fin y al cabo, soy el que las coloca todas en primera línea de frente.\n\nSi quieres desactivar los campos de minas de los Pico Tormenta, solo tienes que matar a su especialista en explosivos. Seguro que ese enano cobarde se oculta en la primera torre de la Tierra de Disputa. Sácalo de allí y esas minas no volverán después de haber sido desactivadas.\n\nSi resulta que lo matas, vuelve para hacérmelo saber. Toda buena acción merece su recompensa.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7381] = {title = "El regreso de Korrak", objective = "Si Korrak el Sanguinario vuelve por la Tierra de Disputa, encuéntralo y acaba con él.\n\nLlévale la calavera de Korrak al maestro de guerra Laggrond en las Laderas de Trabalomas.", description = "Aunque es cierto que Korrak ha sido ejecutado, queda pendiente un asunto: ¿permanecerá muerto? Los trols son famosos por su magia tribal de otro mundo. No me sorprendería que el líder caído resurgiera de entre los muertos para volver a gobernar su clan.\n\nSabes qué hacer si tal cosa ocurriera.\n\n<El maestro de guerra Laggrond se pasa el índice por el cuello como si lo degollaran.>\n\nY esta vez, quiero su calavera. Abre bien los ojos, soldado.\n\n¡Retírese!Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nRuna de Recuerdo"},
[7382] = {title = "Korrak el Eterno", objective = "Si Korrak el Sanguinario vuelve por la Tierra de Disputa, encuéntralo y acaba con él.\n\nLlévale la calavera de Korrak al teniente Haggerdin en las Laderas de Trabalomas.|n", description = "Hay algo que no encaja en todo esto... ¿Ha pensado alguien en quemar el cadáver de Korrak? Esos trols no se detendrán ante nada para resucitar a su líder caído <como a menudo hacen los paganos semihombres-semibestias.\n\nAbre bien los ojos, <nombre>. No te asustes si Korrak nos sorprende con una reaparición y, si lo hace, quiero que sea ejecutado y que su cuerpo sea reducido a cenizas.\n\nPara mayor seguridad, córtale la cabeza y tráeme su calavera antes de prenderle fuego al cuerpo sin vida.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nRuna de Recuerdo"},
[7383] = {title = "La corona de la Tierra", objective = "Llena la ampolla de amatista y devuélvesela a Corithras Furia Lunar, que está en Dolanaar.", description = "Sin embargo, no todo iba bien en Teldrassil. Corzocelada lo había planeado todo meticulosamente ya que el Árbol del Mundo había brotado según lo previsto, pero ocurrió un pequeño desajuste, desajuste al que se pueden atribuir muchos de los conflictos de Teldrassil.\n\nPero no entraré en detalles por ahora. Debes ir a la última poza de la luna, al noroeste de El Claro del Oráculo. Debajo de las ramas del Árbol del Oráculo se encuentra la primera y más poderosa de nuestras pozas. Llena una ampolla con agua de esta poza y tráemela.", progress = "Los druidas, junto con el Árbol del Oráculo y el Archidruida han estado vigilando con suma dedicación el crecimiento de Teldrassil. Pero aun teniendo un nuevo hogar, no hemos recuperado nuestra inmortalidad.", completion = "Estar en presencia del Árbol del Oráculo es casi como ver a la sabiduría tomar forma. Continuaré con mi relato...\n\nCuando Teldrassil ya había crecido, el Archidruida pidió a los dragones su bendición, al igual que habían hecho con Nordrassil en la antigüedad. Sin embargo Nozdormu, el Señor del Tiempo, le negó su bendición como repulsa a la arrogancia del druida. Al igual que Nozdormu, Alexstrasza también se negó a satisfacer la petición de Corzocelada por lo que el crecimiento de Teldrassil ha sido irregular e impredecible...", rewards = "Recibirás:\n\n"},
[7385] = {title = "Un galón de sangre", objective = "Tienes la posibilidad de ofrecer cantidades mayores de sangre de nuestros enemigos. Será un placer aceptar ofrendas de mayor tamaño, <nombre>.", description = nil, progress = "Tienes la posibilidad de ofrecer cantidades mayores de sangre de nuestros enemigos. Será un placer aceptar ofrendas de mayor tamaño, <nombre>.", completion = "Se ha derramado sangre enemiga. Mezclaré esta sangre con el resto de elementos que hemos recolectado. Cuando la ofrenda esté completa, el Señor del Hielo enseñará a esos perros Pico Tormenta ¡toda la ira del clan Lobo Gélido!", rewards = nil},
[7386] = {title = "Recogida de cristal", objective = "A veces te puedes encontrar atrincherado en la batalla durante varios días o incluso varias semanas. En esos períodos más largos de actividad, puedes acabar recogiendo grandes bloques de cristales de tormenta de los Lobo Gélido.\n\nEl Círculo acepta tales ofrendas, <nombre>.", description = nil, progress = "A veces te puedes encontrar atrincherado en la batalla durante varios días o incluso varias semanas. En esos períodos más largos de actividad, puedes acabar recogiendo grandes bloques de cristales de tormenta de los Lobo Gélido.\n\nEl Círculo acepta tales ofrendas, <nombre>.", completion = "Muy bien, &lt;nombre&gt;. Usaré el poder de estos talismanes para aumentar la magia que ya se ha reunido. Cuando hayas acumulado suficiente poder, concentraré las energías reunidas e invocaré al Señor del Bosque para que nos ayude en nuestra causa.", rewards = nil},
[7401] = {title = "SE BUSCA: ¡enanos!", objective = "Se te asignado la tarea de matar a los jugadores enanos en el Valle de Alterac.\n\nMata a un enano y sácale la espina dorsal. Llévasela al sargento Yazra Gruñido Sangriento en el Bastión Lobo Gélido.", description = "Por orden del sargento Yazra Gruñido Sangriento, todos los enanos del campo de batalla han de ser ejecutados en el acto.\n\nSi das muerte a un enano en combate, arráncale la espina dorsal, dejando un putrefacto y hediondo montón sin espina que sirva de advertencia a todo aquel que ose oponerse al poder de los Lobo Gélido.\n\nTráele al sargento Yazra Gruñido Sangriento la prueba de tu hazaña.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7402] = {title = "Se buscan: ¡ORCOS!", objective = "Se te asignado la tarea de matar a los jugadores orcos en el Valle de Alterac.\n\nMata a un orco y llévale un colmillo de la víctima al cabo Noreg Pico Tormenta en Dun Baldar.", description = "Por orden del cabo Noreg Pico Tormenta, se exige de todos los soldados Pico Tormenta sanos que maten a todo orco que muestre oposición en el campo de batalla.\n\nLos soldados deberán llevarse un colmillo o un diente de las fauces del enemigo y presentar dicho diente como prueba de haber cumplido.\n\nLlevar guantes protectores.Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
