[7652] = {title = "Una ganga azulada", objective = "Muy bien, señor, te voy a ofrecer un buen trato.\n\nQuiero deshacerme de estos diseños de esta armadura de placas imperiales y necesito a alguien que cargue con ellos. Todo lo que quiero a cambio es algunas barras de torio. ¿Te parece bien?", description = nil, progress = nil, completion = nil, rewards = nil},
