[7802] = {title = "Un donativo de lana", objective = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de Forjaz por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", description = nil, progress = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de Forjaz por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7803] = {title = "Un donativo de seda", objective = "Estamos muy escasos de seda. Nos ayudaría mucho que donaras 60 piezas de seda; conseguiremos más por otros medios.\n\nAdemás, una donación tan generosa mejorará tu reputación entre nosotros. Si tienes las 60 piezas de seda y quieres donarlas, puedo cogerlas ahora.", description = nil, progress = "Estamos muy escasos de seda. Nos ayudaría mucho que donaras 60 piezas de seda; conseguiremos más por otros medios.\n\nAdemás, una donación tan generosa mejorará tu reputación entre nosotros. Si tienes las 60 piezas de seda y quieres donarlas, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
