[7806] = {title = "Un donativo extra de paño rúnico", objective = "¡Saludos, <nombre>! Seguimos necesitando paño rúnico; si quieres seguir ayudándonos, te lo agradeceremos. Una donación de 20 piezas te valdrá la admiración y el respeto de Forjaz.\n\nPuedes darme el paño rúnico cuando quieras.", description = nil, progress = "¡Saludos, <nombre>! Seguimos necesitando paño rúnico; si quieres seguir ayudándonos, te lo agradeceremos. Una donación de 20 piezas te valdrá la admiración y el respeto de Forjaz.\n\nPuedes darme el paño rúnico cuando quieras.", completion = "&lt;nombre&gt;, Forjaz estaría perdida sin valientes como tú. ¡Gracias!", rewards = nil},
[7807] = {title = "Un donativo de lana", objective = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de los Exiliados de Gnomeregan por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", description = nil, progress = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de los Exiliados de Gnomeregan por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
