[7810] = {title = "El maestro de la arena", objective = "Habla con Corto John Mitril en la Arena de la Vega de Tuercespina.\n\nObjeto provisto: \n", description = "¡Ser un maestro de la arena tiene que ser algo más que esto! ¡Quizás lo sepa el pirata que lleva el evento en Gurubashi!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
