[7812] = {title = "Un donativo extra de paño rúnico", objective = "¡Saludos, <nombre>! Seguimos necesitando paño rúnico; si quieres seguir ayudándonos, te lo agradeceremos. Una donación de 20 piezas te valdrá la admiración y el respeto de los Exiliados de Gnomeregan.\n\nPuedes darme el paño rúnico cuando quieras.", description = nil, progress = "¡Saludos, <nombre>! Seguimos necesitando paño rúnico; si quieres seguir ayudándonos, te lo agradeceremos. Una donación de 20 piezas te valdrá la admiración y el respeto de los Exiliados de Gnomeregan.\n\nPuedes darme el paño rúnico cuando quieras.", completion = "&lt;nombre&gt;, los gnomos estaríamos perdidos sin valientes como tú. ¡Gracias!", rewards = nil},
[7813] = {title = "Un donativo de lana", objective = "Estamos recogiendo donaciones de paño de lana. Una donación de 60 retales de paño de lana te hará ganar el reconocimiento de Entrañas por tu generosidad. Tal y como está nuestro almacén, solo necesitamos que nos entregues 60 retales, pues las otras gentes del reino nos ayudarán a conseguir nuestro objetivo.\n\nSi tienes los 60 retales de paño de lana y estás dispuesta a donarlos, puedo aceptar tu donativo ahora mismo.", description = nil, progress = "Estamos recogiendo donaciones de paño de lana. Una donación de 60 retales de paño de lana te hará ganar el reconocimiento de Entrañas por tu generosidad. Tal y como está nuestro almacén, solo necesitamos que nos entregues 60 retales, pues las otras gentes del reino nos ayudarán a conseguir nuestro objetivo.\n\nSi tienes los 60 retales de paño de lana y estás dispuesta a donarlos, puedo aceptar tu donativo ahora mismo.", completion = "¡Estupendo! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7814] = {title = "Un donativo de seda", objective = "Nuestras reservas de seda, como de los demás tejidos, están casi bajo mínimos. Tal y como está nuestro almacén, solo necesitamos que nos entregues 60 retales de seda pues con la ayuda de los demás lograríamos cumplir nuestro objetivo.\n\n\n\nUn regalo tan generoso como la seda aumentaría sin duda tu reputación en la comunidad… Si tienes los 60 retales de paño de seda y estás dispuesto a donarlos, puedo aceptarlos ahora mismo.", description = nil, progress = "Nuestras reservas de seda, como de los demás tejidos, están casi bajo mínimos. Tal y como está nuestro almacén, solo necesitamos que nos entregues 60 retales de seda pues con la ayuda de los demás lograríamos cumplir nuestro objetivo.\n\n\n\nUn regalo tan generoso como la seda aumentaría sin duda tu reputación en la comunidad… Si tienes los 60 retales de paño de seda y estás dispuesto a donarlos, puedo aceptarlos ahora mismo.", completion = "¡Estupendo! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7815] = {title = "Los quijafortes", objective = "Katoom el Pescador, del Poblado Sañadiente en las Tierras del Interior, quiere que mates 15 quijafortes de agua salada. Ve a verlo cuando hayas terminado.", description = "¡No te fíes de su jeta! Los quijafortes de agua salada son peligrosos. Cada cinco minutos viene uno de esos aspiradores acuáticos y me arranca el cebo.\n\nHaz un favor a todo el Poblado Sañadiente y acaba con ellos.\n\n¡No dejes ni uno!", progress = nil, completion = "No sabes cuánto te lo agradezco. Espero que esta caña de pescar sea justa recompensa.", rewards = "Recibirás:\n\nPescador extremo de Nat Pagle FC-5000"},
[7816] = {title = "Gammerita", objective = "Katoom el Pescador, del Poblado Sañadiente en las Tierras del Interior, quiere que mates a Gammerita y que le saques el cebo del estómago.\n\nDijo que Gammerita está en la costa con las otras tortugas.", description = "¡Eh! Hay una tortuga que no es como las otras. Es Gammerita, la jefa de los quijafortes de agua salada; una condenada ladrona también es.\n\nSe llevó mi mejor cebo. ¡Lo mordió y se lo llevó! Desde entonces, pican menos.\n\nBusca a Gammerita y sácale mi cebo del estómago.", progress = "¿Ya has acabado con Gammerita?", completion = "¡Genial! Con el cebo y muchos quijafortes muertos, tendré muchos muchos peces.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
