[7826] = {title = "Un donativo de lana", objective = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de Orgrimmar por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", description = nil, progress = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de Orgrimmar por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7827] = {title = "Un donativo de seda", objective = "Estamos muy escasos de seda. Nos ayudaría mucho que donaras 60 piezas de seda; conseguiremos más por otros medios.\n\nAdemás, una donación tan generosa mejorará tu reputación entre nosotros. Si tienes las 60 piezas de seda y quieres donarlas, puedo cogerlas ahora.", description = nil, progress = "Estamos muy escasos de seda. Nos ayudaría mucho que donaras 60 piezas de seda; conseguiremos más por otros medios.\n\nAdemás, una donación tan generosa mejorará tu reputación entre nosotros. Si tienes las 60 piezas de seda y quieres donarlas, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7828] = {title = "Acechar a los acechadores", objective = "El cazador Markhor, del Poblado Sañadiente en las Tierras del Interior, quiere que mates 15 acechadores y 15 aulladores Melenargenta. Vuelve a verlo cuando hayas terminado.\n\nDijo que los lobos se ocultan entre la maleza en las Tierras del Interior.", description = "Muchos trols han perdido la vida en las duras Tierras del Interior, <raza>. Los lobos Melenargenta son uno de los mayores peligros para nuestros cazadores. Atacan por emboscada, escondiéndose entre la vegetación.\n\n¡No vamos a permitirlo!\n\nVamos a darles su propia medicina. ¡Los acecharemos! ¡Y acabaremos con ellos!", progress = nil, completion = "Buen trabajo, &lt;raza&gt;. Tienes la gratitud de los Sañadiente.", rewards = "Recibirás:\n\n"},
[7829] = {title = "Cazar a los salvajes", objective = "El cazador Markhor, del Poblado Sañadiente en las Tierras del Interior, quiere que mates 20 lechúcicos salvajes. Vuelve a verlo cuando hayas terminado.\n\nDijo que viven por toda la región.", description = "Una de las criaturas más desagradables de las Tierras del Interior es el lechúcico. Más de un buen guerrero Sañadiente ha caído bajo sus feroces picos y garras.\n\nMata todos los lechúcicos que encuentres, <raza>. Vuelve cuando te hayas saciado de sangre. Tu contribución se ponderará y juzgará convenientemente.", progress = nil, completion = "Has demostrado tu valor ante los Sañadiente, &lt;nombre&gt;. Bien hecho.", rewards = "Recibirás:\n\n"},
[7830] = {title = "Vengar a los Caídos", objective = "El cazador Markhor, del Poblado Sañadiente en las Tierras del Interior, quiere que mates a un Señor del Cielo y le lleves el penacho.\n\nDijo que son una raza poco común que se extiende por toda la región.", description = "Esos desgraciados de los Martillo Salvaje adiestran a los grifos para matar trols. Es horrible, pero pronto se hará justicia.\n\nPor ahora, concéntrate en exterminar a los grifos más viejos de la región. Se los conoce como los Señores del Cielo.\n\nMata uno y tráeme su penacho.", progress = "El Señor del Cielo Picovaja pertenece a una raza poco común de grifos cuya función es criar asesinos alados.", completion = "&lt;Markhor observa la péndola.&gt;\n\nMe pregunto a cuántos cazadores habrá devorado esta bestia.", rewards = "Recibirás:\n\n"},
[7831] = {title = "Un donativo de paño mágico", objective = "¡Nos estamos quedando sin paño mágico! Si nos donaras 60 piezas de paño mágico, alcanzaríamos el objetivo fijado. Tu generosidad te valdría la admiración de Orgrimmar.\n\nSi tienes las 60 piezas de paño mágico y las quieres donar, puedo cogerlas ahora.", description = nil, progress = "¡Nos estamos quedando sin paño mágico! Si nos donaras 60 piezas de paño mágico, alcanzaríamos el objetivo fijado. Tu generosidad te valdría la admiración de Orgrimmar.\n\nSi tienes las 60 piezas de paño mágico y las quieres donar, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7832] = {title = "Un donativo extra de paño rúnico", objective = nil, description = nil, progress = nil, completion = nil, rewards = nil},
[7833] = {title = "Un donativo de lana", objective = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de los trol Lanza Negra por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", description = nil, progress = "Estamos haciendo una colecta de paño de lana. Si nos das 60 piezas de paño de lana, ganarás el reconocimiento de los trol Lanza Negra por tu generosidad. Con 60 piezas por tu parte es suficiente; conseguiremos más por otros medios.\n\nSi tienes las 60 piezas de paño de lana y las quieres donar, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7834] = {title = "Un donativo de seda", objective = "Estamos muy escasos de seda. Nos ayudaría mucho que donaras 60 piezas de seda; conseguiremos más por otros medios.\n\nAdemás, una donación tan generosa mejorará tu reputación entre nosotros. Si tienes las 60 piezas de seda y quieres donarlas, puedo cogerlas ahora.", description = nil, progress = "Estamos muy escasos de seda. Nos ayudaría mucho que donaras 60 piezas de seda; conseguiremos más por otros medios.\n\nAdemás, una donación tan generosa mejorará tu reputación entre nosotros. Si tienes las 60 piezas de seda y quieres donarlas, puedo cogerlas ahora.", completion = "¡Magnífico! ¡Gracias por tu generosa donación, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7835] = {title = "Un donativo de paño mágico", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7836] = {title = "Un donativo de paño rúnico", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
[7837] = {title = "Un donativo extra de paño rúnico", objective = "¡Saludos, <nombre>! Seguimos necesitando paño rúnico; si quieres seguir ayudándonos, te lo agradeceremos. Una donación de 20 piezas te valdrá la admiración y el respeto de los trols Lanza Negra.\n\nPuedes darme el paño rúnico cuando quieras.", description = nil, progress = "¡Saludos, <nombre>! Seguimos necesitando paño rúnico; si quieres seguir ayudándonos, te lo agradeceremos. Una donación de 20 piezas te valdrá la admiración y el respeto de los trols Lanza Negra.\n\nPuedes darme el paño rúnico cuando quieras.", completion = "&lt;nombre&gt;, los trols Lanza Negra estarían perdidos sin valientes como tú. ¡Gracias!", rewards = nil},
[7838] = {title = "El maestro mayor de la arena", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nGran maestro de arena"},
[7839] = {title = "Los vándalos de Vilrama", objective = "El herrero Esco de Poblado Sañadiente, en las Tierras del Interior, quiere que encuentres y le lleves sus herramientas perdidas.\n\nDijo que las herramientas pueden estar en uno de los templos Vilrama en la zona noreste de las Tierras del Interior. Mira también en Roca Oculta.", description = "¡Esos rufianes Vilrama han invadido el poblado y se han llevado mis herramientas! Tengo herramientas de repuesto, pero las que se llevaron eran un regalo de un gnomo bondadoso que me salvó la vida cuando era jovencito.\n\nQuiero recuperarlas, <nombre>; son importantísimas para mí.\n\nMarkhor informó que se han visto cerca de los templos de Agol'watha y Shaol'watha.\n\nEstán en la región norte de las Tierras del Interior.\n\n¡Tráeme mis herramientas!", progress = "Cuidado con los Vilrama, <nombre>, son unos asesinos despiadados.", completion = "¡Mis herramientas! ¡Excelente! ¡Gracias, muchas gracias, &lt;nombre&gt;!", rewards = "Recibirás:\n\n"},
[7840] = {title = "Lard perdió su comida", objective = "Lard, del Poblado Sañadiente en las Tierras del Interior, quiere que vayas a buscar su comida. Llévasela cuando la encuentres.\n\nDijo que se la dejó en la isla del norte. Ten cuidado con los trols Vilrama.", description = "Lard estaba comiendo en un sitio secreto cuando el feo Vilrama le dio con su porra. Lard fue corriendo a Sañadiente pero se dejó la comida. Lard tiene hambre. ¿Quieres ir al sitio secreto a buscar la comida de Lard? Lard tiene miedo. Ve al norte. El sitio secreto está en la isla del norte.", progress = "<Lard se frota la panza.>\n\nLard mucha hambre.", completion = "&lt;Lard coge la carne y empieza a devorarla.&gt;\n\nQué rico. Gracias.\n\nLard te hace un regalo por tu ayuda.", rewards = "Recibirás:\n\nCesta de picnic especial de Lard"},
[7841] = {title = "Un mensaje para los Martillo Salvaje", objective = "Otho Moji'ko del Poblado Sañadiente, en las Tierras del Interior, quiere que mates a 15 avanzados, 15 rastreadores, 15 tiradores y 15 exploradores Vallealto. Vuelve a verlo cuando hayas terminado.\n\nLos elfos Vallealto están en la Avanzada Quel'Danil, en la zona noroeste de las Tierras del Interior.", description = "Los Martillo Salvaje negocian con nuestros enemigos, establecen alianzas. Ahora andan detrás de los altos elfos.\n\n<Escupe.>\n\nHay que mandar un mensaje a los Martillo Salvaje: un mensaje con sangre de nuestros enemigos. Asesina a todos los altos elfos de Vallealto que encuentres y deja sus cuerpos como advertencia.\n\n¡Con los trols no se juega!", progress = nil, completion = "Parece que los Sañadiente están sellando nuevas alianzas. Buen trabajo.", rewards = "Recibirás:\n\n"},
[7842] = {title = "Un nuevo mensaje para los Martillo Salvaje", objective = "Otho Moji'ko del Poblado Sañadiente, en las Tierras del Interior, quiere que le lleves 10 plumas elegantes de los grifos de las Tierras del Interior.\n\nPuedes encontrarlos por toda la región.", description = "Dicen que los Martillo Salvaje y los grifos se quieren tanto como el trol y su trolita. Qué asco. Pero podemos aprovecharnos de eso. Les mandaremos otro mensaje.\n\nMata a todos los grifos que veas en las Tierras del Interior y recoge las plumas. Con eso mandaremos el último mensaje.", progress = "El último mensaje los asustará. O eso, o los pondrá furiosos.", completion = "¡Solo una cosa más y los Martillo Salvaje aprenderán que con los trols no se juega!", rewards = "Recibirás:\n\n"},
[7843] = {title = "Un mensaje final para los Martillo Salvaje", objective = "Otho Moji'ko del Poblado Sañadiente, en las Tierras del Interior, quiere que vayas a Pico Nidal, al oeste de la región, y coloques el mensaje final junto al pozo del centro del pueblo. Vuelve a verlo cuando hayas terminado.\n\nTen cuidado con los belicosos enanos Martillo Salvaje y los miembros indisciplinados de la Alianza.", description = "<Te da una lanza manchada de sangre y envuelta en plumas de grifo.>\n\nToma. Llévatela a Pico Nidal, en medio del pueblo, junto al pozo. ¡Clávala en el suelo! ¡Con poderío! ¡Así aprenderán los enanos a tomar en serio a los trols! Vuelve después. Esperemos que vuelvas con vida.", progress = nil, completion = "Me habría gustado verles las caras. ¿Ya están haciendo las maletas?\n\nMuy buen trabajo, &lt;nombre&gt;. Te has ganado un mojo grande.", rewards = "Recibirás:\n\nGuantes de pellejo de lechubestia"},
[7844] = {title = "Unos primos caníbales", objective = "La mística Yayo'jin del Poblado Sañadiente, en las Tierras del Interior, quiere que mates a 30 arrancabelleras y 15 Veritas Vilrama. Vuelve a verla cuando hayas terminado.\n\nDijo que suelen estar cerca de los templos de Shaol'watha y Agol'watha, en la zona noreste de las Tierras del Interior.", description = "En las Tierras del Interior viven tres tribus de trols, <nombre>. Solo nos parecemos en eso: en que somos trols y vivimos aquí.\n\nLos Vilrama (mira que les queda bien el nombre) son unos depravados que comen otros trols y humanoides.\n\nSi Zul'jin estuviera aquí, los eliminaría de un solo golpe, pero como aún no ha vuelto, tenemos que apañárnoslas solos.\n\n¡Te toca a ti limpiar la región de chusma!", progress = nil, completion = "Buen trabajo, &lt;clase&gt;. El propio Zul'jin no lo habría hecho mejor.|n", rewards = "Recibirás:\n\n"},
[7845] = {title = "El secuestro del anciano Cuernorroto", objective = "Primigenia Cuernorroto, del Poblado Sañadiente en las Tierras del Interior, quieres que rescates a su compañero, el anciano Cuernorroto, que está en Jintha'alor.\n\nEstá prisionero en la cima de Jintha'alor, la capital Vilrama al sur de las Tierras del Interior.", description = "Esos salvajes Vilrama se llevaron a mi amigo en un ataque nocturno. Nuestros espías me dicen que está vivo y prisionero en Jintha'alor. Se dice que la Sacerdotisa Vil Hexx lo está preparando para sacrificarlo al ignoto dios de la sangre, Hakkar.\n\n¡Tienes que salvarlo, <nombre>! El pueblo no podría enfrentarse a tan gran pérdida.", progress = nil, completion = "Apresúrate, &lt;nombre&gt;. Seguro que los guardias han detectado tu presencia.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[7846] = {title = "¡Recupera la llave!", objective = "Elder Cuernorroto de Jintha'alor quiere que mates a Hitah'ya la Guardiana para quitarle la llave de los grilletes. Vuelve a ver a Elder cuando hayas conseguido la llave.", description = "Hitah'ya, la guardiana, tiene la llave de mis cadenas. Está en la cueva que está cerca de aquí. Ten cuidado con ella, <clase>; está bien protegida.\n\nMátala y trae la llave para quitarme los grilletes. Solo entonces podré volver a casa.", progress = "¡Apresúrate!", completion = "¡Al fin libre! Gracias, &lt;nombre&gt;. ¡Sañadiente sabrá de esta proeza!", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\n"},
[7847] = {title = "Vuelve con Primigenia Cuernorroto", objective = "El anciano Cuernorroto de Jintha'alor quiere que informes de su rescate a Primigenia Cuernorroto en el Poblado Sañadiente.", description = "Ve al Poblado Sañadiente y avisa a mi compañera, Primigenia Cuernorroto. ¡Dile que voy para allá! Seguro que te recompensará.", progress = nil, completion = "¡Gracias en nombre de los Sañadiente, &lt;nombre&gt;! Tienes toda nuestra gratitud.", rewards = "Podrás elegir una de estas recompensas:\n\nArco de tierra altaFrasco de mojo del bosque\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nArco de tierra altaFrasco de mojo del bosque"},
[7848] = {title = "Armonización con el Núcleo", objective = "Entra en el Núcleo de Magma de las Profundidades de Roca Negra y recoge un fragmento de núcleo. LLévaselo a Lothos Levantagrietas a la Montaña Roca Negra.", description = "Se abren y cierran grietas por todas partes, <raza>. A dos pasos de aquí hay una grieta que va a las profundidades de la montaña Roca Negra, a las fauces mismas del Señor del Fuego.\n\n¿Te sorprende? Lástima... Las razas mortales no pueden comprender lo que no pueden tocar, ver o sentir.\n\nPero el portal está aquí y el acceso es posible.\n\n¿He despertado tu curiosidad? La Armonización es muy sencilla. Aventúrate en las Profundidades de Roca Negra y consigue un fragmento del núcleo. Tráemelo y armonizaré tu esencia con el portal.", progress = "Debes sintonizar tu alma con el Núcleo de Magma antes de poder entrar.", completion = "El Núcleo de Magma está justo pasada la entrada a mi derecha.", rewards = "Te van a lanzar el siguiente hechizo:\n\nArmonización con el Núcleo\n\nRecibirás:\n\n"},
[7849] = {title = "Una separación angustiosa", objective = "El cazador Markhor de Poblado Sañadiente, en las Tierras del Interior, quiere que le lleves los restos de su hermano gemelo. Llévale la calavera y los huesos de Malkhor.\n\nDijo que los Vilrama habían arrojado los restos de su hermano a los mocos y los lobos. También dijo que hay una fosa llena de mocos en Jintha'alor, además de una guarida de lobos, ambas cerca de la parte alta del templo.", description = "Como muchos en el poblado, yo también perdí a un ser querido a manos de los salvajes. Mi gemelo, Malkhor, fue apresado por los Vilrama en el mismo ataque que el compañero de Cuernorroto.\n\nFue ejecutado brutalmente y sus restos arrojados a los lobos y mocos de los Vilrama.\n\nYa es tarde para salvar su vida, pero no para salvar su alma. Recupera sus restos, <nombre>.", progress = "Llevaré los restos a Yayo'jin y los enterraré como es debido.", completion = "Cómo echo de menos a mi hermano...\n\nGracias por tu ayuda, &lt;nombre&gt;. Cuenta con mi gratitud eterna.", rewards = "Podrás elegir una de estas recompensas:\n\nQuijotes de placas de madera lacadasMitones hojaverde\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nMitones hojaverdeQuijotes de placas de madera lacadas"},
[7850] = {title = "Vasijas oscuras", objective = "Colmillopartido Primigenia, del Poblado Sañadiente en las Tierras del Interior, quieres que recojas 10 vasijas de sangre corrupta en Jintha'alor. Vuelve a verla cuando hayas terminado la tarea.", description = "Los Vilrama luchan con una ferocidad sobrenatural. Se debe a los inmundos trucos mágicos de la Sacerdotisa Vil Hexx.\n\nEncontrarás vasijas oscuras con sangre corrupta por todo Jintha'alor. Emanan la nauseabunda magia del ignoto dios de la sangre, que da fuerzas y pone frenéticos a los Vilrama.\n\nTráeme las vasijas para que pueda limpiar la corrupción y debilitar el poder del dios de la sangre.", progress = "Ten cuidado con los Vilrama; están obcecados y lucharán a muerte.", completion = "Se ha eliminado la energía maléfica que controlaba a los Vilrama, pero solo por el momento. No podemos bajar la guardia; aunque destruyamos a la Sacerdotisa Vil Hexx, otra ocupará su lugar.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nAliento de la Naturaleza"},
[7861] = {title = "Se buscan: Sacerdotisa Vil Hexx y sus esbirros", objective = "Te han ordenado que mates a la Sacerdotisa Vil Hexx y a 20 guardas Vilrama de Aman'zasi. Ve a ver a Colmillopartido Primigenia al Poblado Sañadiente cuando hayas terminado.\n\nLos encontrarás en la cima de Jintha'alor, en las Tierras del Interior.", description = "Por orden de Primal Torntusk se busca a la Sacerdotisa Vil Hexx y sus esbirros.\n\nSe ofrece generosa recompensa a quien elimine a Hexx y su guardia de élite Aman'zasi.", progress = nil, completion = "Este será un duro golpe para los Vilrama. Te has ganado una recompensa magnífica, &lt;nombre&gt;.", rewards = "Podrás elegir una de estas recompensas:\n\nCollar de tejido de hiedraCapa del bosque profundo\n\nTe van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nTambién recibirás:\n\nCapa del bosque profundoCollar de tejido de hiedra"},
[7862] = {title = "Oferta de empleo: capitán de la guardia de Poblado Sañadiente", objective = "Debes aniquilar a 20 frenéticos, 20 cazadores oscuros, 20 chupasangres y 20 devoralmas Vilrama.\n\nCuando hayas terminado, ve a ver a Colmillopartido Primigenia al Poblado Sañadiente.", description = "Tras la pérdida de Malkhor, el puesto de Capitán de la guardia de Sañadiente está disponible. Si quieres ser Capitán de la guardia de Sañadiente, completa las siguientes tareas y envía tu solicitud a Primal Torntusk:\n\nExterminar a los frenéticos Vilrama.\n\nExterminar a los cazadores invisibles Vilrama.\n\nExterminar a los chupasangre Vilrama.\n\nExterminar a los devoralmas Vilrama.", progress = nil, completion = "Desde luego, tienes muchos méritos para el puesto, &lt;nombre&gt;. Acepta esto como pago por tus servicios; revisaré las candidaturas el año próximo.", rewards = "Te van a lanzar el siguiente hechizo:\n\nExplosión de experiencia\n\nRecibirás:\n\nRuna del Capitán guardia"},
