[7866] = {title = "Paquete de curación elemental de escolta", objective = "Sin duda nos has demostrado lo que vales, <nombre>. Como recompensa, acepta este paquete de curación. En el interior encontrarás raciones y vendas que te serán útiles solo en la Garganta Grito de Guerra.\n\nConforme sigues demostrando tu valor en la Garganta, podrás adquirir estos artículos. Las raciones estarán disponible cuando seas amistoso para con nosotros y las vendas cuando seas honrado.", description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nPaquete de curación elemental de escolta"},
