[7907] = {title = "La baraja de las Bestias de la Luna Negra", objective = "Lleva la baraja de las Bestias a la Feria de la Luna Negra cuando pase por la ciudad.", description = "Ahora que has reunido todas las cartas de bestias en una baraja, aparece misteriosamente una novena carta con el mismo reverso que las ocho primeras.  Esa carta tiene una inscripción: \"Lleva estas cartas a la Feria de la Luna Negra y recibe tu recompensa.\"  Has oído que esa feria aparece cada pocos meses y que se alterna entre Mulgore y Villadorada. Tendrás que esperar a que aparezca de nuevo.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nCarta de la Luna Negra: Dragón azul"},
[7908] = {title = "El maestro de la arena", objective = "Habla con Corto John Mitril en la Arena de la Vega de Tuercespina.\n\nObjeto provisto: \n", description = "¡Ser un maestro de la arena debe ser mucho más que esto! ¡Tal vez lo sepa el pirata que dirige la competición en Gurubashi!Recibirás:\n\nCuando completes esta misión ganarás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\n"},
