[7934] = {title = "50 vales: caja de almacenamiento de la Luna Negra", objective = "¡Acércate, acércate! Si tienes vales de la Feria de la Luna Negra que quieres canjear, ¡solo tienes que decirlo! Puedes canjear vales de diferente valor por premios fantásticos y maravillosos. ¡Venga, no seas tímida, anímate!", description = nil, progress = "¡Acércate, acércate! Si tienes vales de la Feria de la Luna Negra que quieres canjear, ¡solo tienes que decirlo! Puedes canjear vales de diferente valor por premios fantásticos y maravillosos. ¡Venga, no seas tímida, anímate!", completion = "¡Aquí tienes, aquí tienes! ¡Un premio digno de una reina! ¡Enhorabuena, amiga!\n\nSi tienes más vales que quieras canjear, ¡házmelo saber! Mientras la Feria de la Luna Negra esté aquí, canjearé todos tus vales.", rewards = "Recibirás:\n\nCaja para guardar de la Luna Negra"},
[7935] = {title = "10 vales: oveja del mes pasado", objective = nil, description = nil, progress = nil, completion = nil, rewards = "Recibirás:\n\nAñojo del mes pasado"},
