[7937] = {title = "La Fortuna te espera...", objective = "Ve al Aserradero de la Vega del Este del Bosque de Elwynn y busca tu fortuna.", description = "\"La Fortuna te espera en la Vega del Este.\"\n\nEl papelito de la Fortuna que te dio Sayge está caliente. Cuando lo aprietas, ves una imagen del Aserradero de la Vega del Este, en el Bosque de Elwynn. Te acercas más y ves un almiar junto a un establo.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nArcón misterioso"},
[7938] = {title = "La Fortuna te espera...", objective = "Ve a Las Minas de la Muerte de los Páramos de Poniente y busca tu fortuna.", description = "\"La Fortuna te espera en las Minas de la Muerte.\"\n\nEl papelito de la Fortuna que te dio Sayge está caliente. Cuando lo aprietas, ves una imagen de las Minas de la Muerte entre las ruinas de la aldea de Arroyo de la Luna, en los Páramos de Poniente. Te acercas más y te encuentras moviéndote a gran velocidad por unos enrevesados pasadizos hasta que llegas a un resplandeciente portal que lleva al mismísimo corazón de las minas. Cuando lo cruzas, aparece de la nada un extraño cofre.Recibirás:\n\n", progress = nil, completion = nil, rewards = "Recibirás:\n\nArcón misterioso"},
